import{_ as o,o as p,c as t,e as u}from"./app.6bf529a6.js";const i={},n=u('<h1 id="unix\u57FA\u4E8E\u6F14\u5316\u7684\u54F2\u5B66" tabindex="-1"><a class="header-anchor" href="#unix\u57FA\u4E8E\u6F14\u5316\u7684\u54F2\u5B66" aria-hidden="true">#</a> Unix\u57FA\u4E8E\u6F14\u5316\u7684\u54F2\u5B66</h1><h2 id="\u5173\u4E8E-unix-\u54F2\u5B66-\u4E00\u8BCD\u7684\u4E00\u4E9B\u57FA\u672C\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E-unix-\u54F2\u5B66-\u4E00\u8BCD\u7684\u4E00\u4E9B\u57FA\u672C\u95EE\u9898" aria-hidden="true">#</a> \u5173\u4E8E &quot;Unix \u54F2\u5B66&quot; \u4E00\u8BCD\u7684\u4E00\u4E9B\u57FA\u672C\u95EE\u9898</h2><p>\u5F53\u6211\u4EEC\u8981\u5F00\u53E3\u8BF4 &quot;Unix \u54F2\u5B66&quot; \u7684\u65F6\u5019, \u7ACB\u523B\u5C31\u4F1A\u9047\u5230\u95EE\u9898.</p><h3 id="\u54F2\u5B66-\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u54F2\u5B66-\u7684\u95EE\u9898" aria-hidden="true">#</a> &quot;\u54F2\u5B66&quot; \u7684\u95EE\u9898</h3><p>\u9996\u5148\u662F &quot;\u54F2\u5B66&quot; \u7684\u95EE\u9898. \u4EC0\u4E48\u662F &quot;\u54F2\u5B66&quot;? \u6211\u4EEC\u80FD\u4E0D\u80FD\u8BF4 &quot;Unix \u54F2\u5B66&quot;.</p><p>\u8981\u77E5\u9053, \u5982\u679C\u6211\u8BF4 &quot;\u4E2D\u56FD\u5148\u79E6\u54F2\u5B66&quot;, \u4E00\u5B9A\u4F1A\u6709\u4EBA\u8BF4\u8FD9\u662F\u5BF9 &quot;\u54F2\u5B66&quot; \u4E00\u8BCD\u7684\u8BEF\u7528. \u56E0\u4E3A\u4E2D\u56FD\u5148\u79E6\u662F\u6CA1\u6709\u4E00\u95E8\u5B66\u672F\u53EB\u505A &quot;\u54F2\u5B66&quot; \u7684. \u8FD9\u4E0D\u662F\u4EC0\u4E48\u94BB\u725B\u89D2\u5C16\u7684\u5947\u8C08\u602A\u8BBA, \u751A\u81F3\u51AF\u53CB\u5170\u4E5F\u8BA4\u4E3A\u4E2D\u56FD\u6CA1\u6709\u54F2\u5B66. \u4ED6\u5728\u300A\u4E2D\u56FD\u54F2\u5B66\u53F2\u300B\u4E2D\u8BF4, \u4E2D\u56FD\u53EA\u6709\u8BBA\u7406\u5B66\u800C\u65E0\u54F2\u5B66, \u4F46\u8BBA\u7406\u5B66\u4E2D\u662F\u53EF\u4EE5\u6709\u54F2\u5B66\u601D\u60F3\u7684, \u6545\u800C\u53EF\u4EE5\u5C06\u4E4B\u6574\u7406, \u5199\u4E00\u90E8\u54F2\u5B66\u53F2\u51FA\u6765.</p><p>\u6216\u8005\u6211\u4EEC\u53EF\u4EE5\u8FD9\u60F3, \u4E00\u4E2A\u8BDD\u5373\u7136\u8BF4\u4E86\u51FA\u6765\u800C\u6CA1\u6709\u8BED\u75C5, \u90A3\u8FD9\u4E2A\u8BDD\u4E00\u5B9A\u662F\u53EF\u4EE5\u8FD9\u6837\u8BF4\u7684. \u6211\u4EEC\u8981\u5206\u8FA9\u7684, \u53EA\u662F\u6211\u4EEC\u5728\u4EC0\u4E48\u610F\u4E49\u4E0A\u4F7F\u7528\u8FD9\u4E2A\u8BCD.</p><p>\u6BD4\u5982, \u6309\u51AF\u53CB\u5170\u7684\u610F\u601D, \u6211\u4EEC\u5F53\u7136\u662F\u53EF\u4EE5\u8BF4 &quot;\u4E2D\u56FD\u5148\u79E6\u54F2\u5B66&quot; \u7684, \u4F46\u6211\u4EEC\u8BF4 &quot;\u4E2D\u56FD\u5148\u79E6\u54F2\u5B66&quot; \u65F6, \u6211\u4EEC\u8BF4\u7684\u4E0D\u662F\u4E2D\u56FD\u5148\u79E6\u7684\u4E00\u95E8\u7CFB\u7EDF\u5B66\u672F, \u8FD9\u4E2A\u5B66\u672F\u53EB\u54F2\u5B66; \u800C\u662F\u4E2D\u56FD\u5148\u79E6\u7684\u601D\u60F3\u4E2D, \u96F6\u661F\u5305\u542B\u7684\u54F2\u5B66\u601D\u60F3.</p><p>\u6240\u4EE5, \u5F53\u6211\u4EEC\u8BF4 &quot;Unix \u54F2\u5B66&quot; \u7684\u65F6\u5019, \u6211\u4EEC\u5728\u4EC0\u4E48\u610F\u4E49\u4E0A\u4F7F\u7528 &quot;\u54F2\u5B66&quot; \u8FD9\u4E2A\u8BCD.</p><p>\u5F53\u6211\u4EEC\u5728\u8BF4 &quot;Unix \u54F2\u5B66&quot; \u65F6, \u660E\u663E\u4E0D\u662F\u5728 &quot;\u9A6C\u514B\u601D\u4E3B\u4E49\u54F2\u5B66&quot; \u6216 &quot;\u540E\u73B0\u4EE3\u54F2\u5B66&quot; \u7684\u610F\u4E49\u4E0A\u5728\u4F7F\u7528 &quot;\u54F2\u5B66&quot; \u4E00\u8BCD.</p><p>\u56E0\u4E3A &quot;\u9A6C\u514B\u601D\u4E3B\u4E49\u54F2\u5B66&quot; \u548C &quot;\u540E\u73B0\u4EE3\u54F2\u5B66&quot; \u5FC5\u987B\u8981\u5305\u542B\u4E00\u6574\u5957\u7684\u7CFB\u7EDF\u8BF4\u7406, \u4F46 &quot;Unix \u54F2\u5B66&quot; \u660E\u663E\u5305\u542B\u4EC0\u4E48\u7CFB\u7EDF\u8BF4\u7406.</p><p>\u5F53\u6211\u4EEC\u5728\u8BF4 &quot;Unix \u54F2\u5B66&quot; \u65F6, \u5927\u6982\u662F\u5728 &quot;\u6469\u6258\u8F66\u4FEE\u7406\u54F2\u5B66&quot; \u6216 &quot;\u70E4\u86CB\u631E\u7684\u54F2\u5B66&quot; \u7684\u610F\u4E49\u4E0A\u5728\u4F7F\u7528 &quot;\u54F2\u5B66&quot; \u4E00\u8BCD.</p><p>\u5F53\u6211\u4EEC\u8FD9\u6837\u8BF4\u65F6, \u5927\u6982\u662F\u4E00\u4E2A\u56FA\u5B9A\u7684\u7528\u6CD5: \u4E00\u79CD\u5177\u4F53\u7684\u6280\u672F + &quot;\u54F2\u5B66&quot;. \u5B83\u5927\u6982\u7684\u610F\u601D\u662F, \u5728\u8FDB\u884C\u67D0\u79CD\u6280\u672F\u64CD\u4F5C\u65F6, \u751F\u53D1\u51FA\u67D0\u79CD\u54F2\u601D, \u8FDB\u800C\u4EA7\u751F\u67D0\u79CD\u667A\u6167.</p><blockquote><p>\u4ECE\u7ECF\u9A8C\u6240\u5F97\u8BDD\u591A\u8981\u70B9\u4F7F\u4EBA\u4EA7\u751F\u5BF9\u4E00\u7C7B\u4E8B\u7269\u7684\u666E\u904D\u5224\u65AD, \u800C\u6280\u672F\u5C31\u7531\u6B64\u5174\u8D77 ... \u51ED\u7ECF\u9A8C\u7684, \u77E5\u4E8B\u7269\u4E4B\u6240\u7136\u800C\u4E0D\u77E5\u5176\u6240\u4EE5\u7136, \u6280\u672F\u5BB6\u5219\u517C\u77E5\u5176\u6240\u4EE5\u7136\u4E4B\u6545. \u6211\u4EEC\u4E5F\u8BA4\u4E3A\u6BCF\u4E00\u884C\u4E2D\u7684\u5927\u5E08\u5320\u5E94\u66F4\u53D7\u5C0A\u656C, \u4ED6\u4EEC\u6BD4\u4E00\u822C\u7684\u5DE5\u5320\u77E5\u9053\u7684\u66F4\u771F\u5207, \u4E5F\u66F4\u806A\u660E, \u4ED6\u4EEC\u77E5\u9053\u81EA\u5DF1\u4E00\u4E3E\u624B\u4E00\u62AC\u8DB3\u7684\u539F\u56E0.</p></blockquote><blockquote><p>----- \u300A\u5F62\u800C\u4E0A\u5B66 \xB7 \u5377\u4E00 \xB7 \u7AE0\u4E00\u300B</p></blockquote><p>Unix \u54F2\u5B66\u5927\u6982\u662F\u80FD\u7B26\u5408\u4E9A\u91CC\u58EB\u591A\u5FB7\u8BF4\u7684: \u7ECF\u7531\u7ECF\u9A8C\u800C\u4EA7\u751F\u666E\u904D\u5224\u65AD, \u8FDB\u800C\u53EF\u4EE5\u4F7F\u638C\u63E1\u5B83\u7684\u4EBA\u5F97\u4EE5\u77E5\u9053\u4E3E\u624B\u62AC\u8DB3\u7684\u539F\u56E0.</p><blockquote><p>\u5728\u6240\u6709\u8FD9\u4E9B\u53D1\u660E\u76F8\u7EE7\u5EFA\u7ACB\u4EE5\u540E, \u53C8\u51FA\u73B0\u4E86\u5373\u4E0D\u4E3A\u751F\u6D3B\u6240\u9700, \u4E5F\u4E0D\u4E3A\u4EBA\u4E16\u5FEB\u4E50\u4E3A\u76EE\u7684\u7684\u4E00\u4E9B\u77E5\u8BC6, \u8FD9\u4E9B\u77E5\u8BC6\u51FA\u73B0\u5728\u4EBA\u4EEC\u5F00\u59CB\u6709\u95F2\u7771\u7684\u5730\u65B9.</p></blockquote><blockquote><p>----- \u300A\u5F62\u800C\u4E0A\u5B66 \xB7 \u5377\u4E00 \xB7 \u7AE0\u4E00\u300B</p></blockquote><p>\u5F53\u7136, \u4E9A\u91CC\u58EB\u591A\u5FB7\u8BA4\u4E3A\u667A\u6167\u6709\u9AD8\u4E0B\u4E4B\u5206, \u4EE5\u5B9E\u7528\u4E3A\u76EE\u7684\u667A\u6167\u6700\u4F4E, \u4EE5\u5FEB\u4E50\u4E3A\u76EE\u7684\u667A\u6167\u6B21\u4E4B, \u5565\u76EE\u7684\u4E5F\u4E0D\u4E3A\u7684\u667A\u6167\u6700\u9AD8. Unix \u54F2\u5B66\u663E\u7136\u662F\u6700\u4F4E\u7684\u667A\u6167.</p><h3 id="\u4E3A\u4EC0\u4E48\u6CA1\u6709\u5176\u5B83\u8BA1\u7B97\u673A\u54F2\u5B66" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u6CA1\u6709\u5176\u5B83\u8BA1\u7B97\u673A\u54F2\u5B66" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u6CA1\u6709\u5176\u5B83\u8BA1\u7B97\u673A\u54F2\u5B66</h3><p>\u4F17\u6240\u5468\u77E5, Unix \u662F\u4E00\u6B3E\u8BA1\u7B97\u673A\u64CD\u4F5C\u7CFB\u7EDF, \u4E00\u5982 windows \u64CD\u4F5C\u7CFB\u7EDF.</p><p>\u90A3\u4E48\u4E3A\u4EC0\u4E48\u6CA1\u6709\u8BA1\u7B97\u673A\u54F2\u5B66, \u64CD\u4F5C\u7CFB\u7EDF\u54F2\u5B66, windows \u54F2\u5B66\u5462?</p><blockquote><p>\u52A8\u7269\u51ED\u73B0\u8C61\u4E0E\u8BB0\u5FC6\u800C\u751F\u6D3B ...... \u4EBA\u4ECE\u8BB0\u5FC6\u79EF\u7D2F\u7ECF\u9A8C ...... \u4ECE\u7ECF\u9A8C\u6240\u5F97\u8BB8\u591A\u8981\u70B9\u4F7F\u4EBA\u4EA7\u751F\u5BF9\u4E00\u7C7B\u4E8B\u7279\u7684\u666E\u904D\u5224\u65AD, \u800C\u6280\u672F\u5C31\u7531\u6B64\u5174\u8D77.</p></blockquote><blockquote><p>---- \u300A\u5F62\u800C\u4E0A\u5B66 \xB7 \u5377\u4E00 \xB7 \u7AE0\u4E00\u300B</p></blockquote><blockquote><p>\u5DE5\u7A0B\u548C\u8BBE\u8BA1\u7684\u6BCF\u4E2A\u5206\u652F\u90FD\u6709\u81EA\u5DF1\u7684\u6280\u672F\u6587\u5316 ...... \u8D44\u6DF1\u5DE5\u7A0B\u5E08\u4EEC\u5728\u5DE5\u4F5C\u4E2D\u4F1A\u79EF\u7D2F\u5927\u91CF\u7684\u9690\u6027\u77E5\u8BC6, \u4ED6\u4EEC\u7528\u7C7B\u4F3C\u7985\u5B97 &quot;\u6559\u5916\u522B\u4F20&quot; \u7684\u65B9\u5F0F, \u901A\u8FC7\u8A00\u4F20\u8EAB\u6559\u4F20\u6388\u7ED9\u540E\u8F88.</p></blockquote><blockquote><p>\u8F6F\u4EF6\u5DE5\u7A0B\u7B97\u662F\u6B64\u89C4\u5219\u7684\u4E00\u4E2A\u4F8B\u5916: \u6280\u672F\u53D8\u9769\u5982\u6B64\u4E4B\u5FEB, \u8F6F\u4EF6\u73AF\u5883\u65E5\u65B0\u6708\u5F02, \u8F6F\u4EF6\u6280\u672F\u6587\u5316\u6682\u5982\u671D\u9732.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 1 \u7AE0 \u54F2\u5B66\u300B</p></blockquote><p>\u6211\u4EEC\u524D\u8FB9\u8BF4\u4E86, \u8FD9\u91CC\u6240\u8BF4\u7684 &quot;\u54F2\u5B66&quot; \u662F\u6307\u7684, \u4ECE\u4E00\u79CD\u65E5\u5E38\u7684\u64CD\u4F5C\u7684\u79EF\u7D2F\u4E2D\u6D8C\u73B0\u51FA\u7684\u666E\u904D\u6027\u7684\u667A\u6167. \u90A3\u4EC0\u4E48\u6837\u65E5\u5E38\u64CD\u4F5C\u7684\u79EF\u7D2F\u53EF\u4EE5\u6D8C\u73B0\u51FA\u8FD9\u79CD\u53EF\u4EE5\u88AB\u79F0\u4E3A &quot;\u54F2\u5B66&quot; \u7684\u667A\u6167\u5462?</p><p><strong>\u653E\u76D0 3 \u514B\u7684\u64CD\u4F5C\u4E0D\u884C, \u4F46\u76D0\u5C11\u8BB8\u7684\u64CD\u4F5C\u6216\u8BB8\u53EF\u4EE5.</strong></p><p>\u653E\u76D0 3 \u514B\u53EA\u662F\u4E00\u4E2A\u79F0\u91CF\u7684\u52A8\u4F5C, \u800C\u653E\u76D0\u5C11\u8BB8\u5219\u5C06\u8FD9\u4E2A\u64CD\u4F5C\u901A\u8FBE\u5230\u4E86 &quot;\u54B8\u6DE1&quot; \u7684\u5473\u9053\u4E0A.</p><p>\u8FD9\u79CD &quot;\u5C11\u8BB8&quot; \u5C31\u662F\u6240\u8C13\u7684 <strong>\u5728\u5DE5\u4F5C\u4E2D\u79EF\u7D2F\u7684\u5927\u91CF\u7684\u9690\u6027\u77E5\u8BC6</strong>, \u8FD9\u79CD\u77E5\u8BC6\u4E0D\u662F\u901A\u8FC7\u7EDF\u8BA1\u4E00\u4E0B\u4E00\u4E07\u4E2A\u5403\u8FC7\u8FD9\u9053\u83DC\u7684\u4EBA, \u53D1\u73B0\u5927\u591A\u6570\u4EBA\u8BA4\u4E3A 3 \u514B\u76D0\u662F\u6700\u5408\u9002\u5F97\u6765\u7684. \u800C\u662F\u5728\u8001\u5E08\u5085\u5E26\u7740\u5C0F\u5E08\u5085\u505A\u4E86\u4E00\u4E07\u6B21\u4E4B\u540E, \u5C0F\u5E08\u5085\u81EA\u5DF1\u9886\u609F\u5230\u7684.</p><p>\u800C\u8FD9\u79CD\u8001\u5E08\u5085\u5E26\u7740\u5C0F\u5E08\u5085\u505A\u4E00\u4E07\u6B21, \u5C31\u662F\u6240\u8C13 <strong>&quot;\u4ED6\u4EEC\u7528\u7C7B\u4F3C\u7985\u5B97 &#39;\u6559\u5916\u522B\u4F20&#39; \u7684\u65B9\u5F0F, \u901A\u8FC7\u8A00\u4F20\u8EAB\u6559\u4F20\u6388\u7ED9\u540E\u8F88&quot;</strong>.</p><p>\u7136\u800C\u8F6F\u4EF6\u5DE5\u7A0B\u7684\u95EE\u9898\u5728\u4E8E, \u8F6F\u4EF6\u5DE5\u7A0B\u7684\u6280\u672F\u53D1\u5C55\u5982\u6B64\u4E4B\u5FEB, \u4EE5\u81F3\u4E8E\u6211\u4EEC\u5728\u8FD9\u79CD\u5DE5\u7A0B\u64CD\u4F5C\u7684\u6D3B\u52A8\u4E2D, \u6765\u4E0D\u53CA\u79EF\u7D2F\u51FA\u53EF\u4EE5\u79F0\u4E4B\u4E3A &quot;\u54F2\u5B66&quot; \u7684\u667A\u6167, \u64CD\u4F5C\u672C\u8EAB\u5C31\u88AB\u65B0\u7684\u6280\u672F\u66FF\u4EE3\u4E86.</p><p>\u5F53\u8001\u5E08\u5085\u8FD8\u5728\u8BF4, \u76D0\u5C11\u8BB8\u7684\u65F6\u5019, \u5C0F\u5E08\u5085\u5DF2\u7ECF\u62FF\u51FA\u9E21\u7CBE\u6765\u4E86.</p><p>\u6240\u4EE5, \u8F6F\u4EF6\u5DE5\u7A0B\u5F88\u5C11\u4F1A\u51FA\u73B0\u8FD9\u79CD\u6280\u672F\u79EF\u7D2F\u51FA\u7684 &quot;\u54F2\u5B66&quot;. \u56E0\u6B64\u4E5F\u5C31\u4E0D\u4F1A\u6709\u4EC0\u4E48\u8BA1\u7B97\u673A\u54F2\u5B66, \u64CD\u4F5C\u7CFB\u7EDF\u54F2\u5B66\u6216 windows \u54F2\u5B66\u4E86.</p><p>\u6B63\u5982\u6211\u4EEC\u4E0D\u5E94\u8BE5\u95EE\u4E3A\u4EC0\u4E48\u4E2D\u56FD\u6CA1\u6709\u53D1\u5C55\u51FA\u8D44\u672C\u4E3B\u4E49, \u800C\u5E94\u8BE5\u95EE\u4E3A\u4EC0\u4E48\u82F1\u56FD\u53D1\u5C55\u51FA\u8D44\u672C\u4E3B\u4E49\u4E00\u6837, \u6211\u4EEC\u4E0D\u5E94\u8BE5\u95EE\u4E3A\u4EC0\u4E48\u6CA1\u6709\u5176\u5B83\u8BA1\u7B97\u673A\u54F2\u5B66, \u800C\u5E94\u8BE5\u95EE, \u4E3A\u4EC0\u4E48\u6709 Unix \u54F2\u5B66.</p><p>Unix \u4F5C\u4E3A\u4E00\u4E2A\u64CD\u4F5C\u7CFB\u7EDF\u6709\u4EC0\u4E48\u6837\u7684\u7279\u6B8A\u6027, \u4F7F\u5176\u5728\u6682\u5982\u671D\u9732\u4E2D\u7684\u8F6F\u4EF6\u6280\u672F\u6587\u5316\u4E2D\u6D8C\u73B0\u51FA\u4E00\u5957\u53EF\u4EE5\u4F20\u7EDF\u7684 &quot;\u54F2\u5B66&quot;.</p><p>\u8FD9\u4E2A\u95EE\u9898\u7684\u7B54\u6848, \u5C31\u662F Unix \u54F2\u5B66\u672C\u8EAB\u4E86.</p><h2 id="unix-\u7684\u8C31\u7CFB" tabindex="-1"><a class="header-anchor" href="#unix-\u7684\u8C31\u7CFB" aria-hidden="true">#</a> Unix \u7684\u8C31\u7CFB</h2><blockquote><p>\u5C0F\u578B\u5B9E\u9A8C\u539F\u578B\u7CFB\u7EDF\u7684\u540E\u7EE7\u4EA7\u54C1\u5F80\u5F80\u52A1\u53D7\u4EE4\u4EBA\u8BA8\u538C\u7684 &quot;\u7B2C\u4E8C\u7248\u6548\u5E94&quot; \u6298\u78E8. \u7531\u4E8E\u8FEB\u5207\u5E0C\u671B\u628A\u6240\u6709\u9996\u6B21\u5F00\u53D1\u8757\u9057\u6F0F\u7684\u529F\u80FD\u90FD\u6DFB\u52A0\u8FDB\u53BB, \u5F80\u5F80\u5BFC\u8BBE\u8BA1\u5341\u5206\u5E9E\u5927, \u8FC7\u4E8E\u590D\u6742. \u5176\u5B9E, \u8FD8\u6709\u4E00\u4E2A\u56E0\u4E0D\u5E38\u9047\u5230\u800C\u9C9C\u4E3A\u4EBA\u77E5\u7684 &quot;\u7B2C\u4E09\u7248\u6548\u5E94&quot;: \u6709\u65F6\u5019, \u5728\u7B2C\u4E8C\u7CFB\u7EDF\u4E0D\u582A\u81EA\u8EAB\u91CD\u8D1F\u800C\u5D29\u6E83\u4E4B\u540E, \u6709\u53EF\u80FD\u8FD4\u749E\u5F52\u771F, \u8D70\u4E0A\u6B63\u9053.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 2 \u7AE0 \u5386\u53F2 -- \u53CC\u6D41\u8BB0\u300B</p></blockquote><h3 id="\u8D77\u6E90" tabindex="-1"><a class="header-anchor" href="#\u8D77\u6E90" aria-hidden="true">#</a> \u8D77\u6E90</h3><p>\u5728\u8BA1\u7B97\u673A\u7684\u4E0A\u53E4\u65F6\u4EE3, \u8BA1\u7B97\u673A\u90FD\u662F\u4EE5 &quot;\u6279\u5904\u7406&quot; \u65B9\u5F0F\u8FDB\u884C\u5DE5\u4F5C.</p><p>\u6240\u8C13\u6279\u5904\u7406\u7684\u5DE5\u4F5C\u65B9\u5F0F\u6307\u7684\u662F, \u5148\u628A\u7F16\u597D\u7684\u7A0B\u5E8F\u8F93\u5165\u7ED9\u8BA1\u7B97\u673A, \u800C\u540E\u8BA1\u7B97\u673A\u8FD0\u884C\u7A0B\u5E8F; \u8BA1\u7B97\u673A\u8FD0\u884C\u5B8C\u4E00\u6279\u7A0B\u5E8F\u540E, \u518D\u628A\u7B2C\u4E8C\u6279\u7F16\u597D\u7684\u7A0B\u5E8F\u8F93\u5165\u7ED9\u8BA1\u7B97\u673A, \u8BA1\u7B97\u673A\u518D\u8FD0\u884C\u7A0B\u5E8F.</p><p>\u56E0\u4E3A\u8BA1\u7B97\u673A\u6BD4\u4EBA\u5FEB, \u6240\u4EE5\u91C7\u7528\u8FD9\u79CD\u5DE5\u4F5C\u65B9\u5F0F\u7684\u7ED3\u679C\u5C31\u662F, \u7A0B\u5E8F\u8F93\u5165\u7684\u901F\u5EA6\u8DDF\u4E0D\u4E0A\u8BA1\u7B97\u673A\u8FD0\u884C\u7684\u901F\u5EA6. \u6700\u540E, \u5927\u90E8\u5206\u65F6\u95F4, \u8BA1\u7B97\u673A\u90FD\u5728\u95F2\u7F6E\u7740\u7B49\u5E72\u6D3B.</p><p>\u4E8E\u662F\u5F53\u65F6\u4EBA\u4EEC\u5C31\u60F3, \u80FD\u4E0D\u80FD\u8BA9\u8BA1\u7B97\u673A\u540C\u65F6\u5904\u7406\u591A\u4E2A\u7528\u6237\u7684\u7A0B\u5E8F, \u7531\u4E8E\u8BA1\u7B97\u673A\u8DB3\u591F\u5FEB, \u8BA9\u6BCF\u4E2A\u7528\u6237\u90FD\u89C9\u5F97\u81EA\u5DF1\u72EC\u5360\u8BA1\u7B97\u673A\u4E00\u6837.</p><p>\u90A3\u4E48\u5982\u4F55\u8BA9\u8BA1\u7B97\u673A\u540C\u65F6\u5904\u7406\u591A\u4E2A\u7528\u6237\u7684\u7A0B\u5E8F\u5462?</p><p>\u7FFB\u7535\u7684\u5C0F\u674E\u8001\u5E08\u5728\u590F\u4EE4\u8425\u91CC\u8BF4\u8FC7\u4ED6\u8BFB\u4E66\u7684\u4E60\u60EF: \u540C\u65F6\u8BFB\u516D\u672C\u4E66. \u5982\u4F55\u540C\u65F6\u8BFB\u516D\u672C\u4E66\u5462? \u5148\u8BFB\u7B2C\u4E00\u672C\u7684\u7B2C\u4E00\u7AE0, \u518D\u8BFB\u7B2C\u4E8C\u672C\u7684\u7B2C\u4E00\u7AE0, \u76F4\u5230\u8BFB\u5B8C\u7B2C\u516D\u672C\u4E66\u7684\u7B2C\u4E00\u7AE0, \u800C\u540E\u91CD\u65B0\u62FF\u8D77\u7B2C\u4E8C\u672C\u4E66\u6765\u8BFB\u7B2C\u4E8C\u7AE0.</p><p>\u5F53\u65F6\u8BA9\u8BA1\u7B97\u673A\u540C\u65F6\u5904\u7406\u591A\u4E2A\u7528\u6237\u7684\u7A0B\u5E8F\u4E5F\u91C7\u7528\u8FD9\u6837\u7684\u601D\u8DEF.</p><p>\u8BA9\u8BA1\u7B97\u673A\u5148\u5904\u7406\u7B2C\u4E00\u4E2A\u7A0B\u5E8F\u4E00\u6BB5\u65F6\u95F4, \u518D\u5904\u7406\u7B2C\u4E8C\u4E2A\u7A0B\u5E8F\u4E00\u6BB5\u65F6\u95F4, \u76F4\u5230\u5904\u7406\u5B8C\u6700\u540E\u4E00\u4E2A\u7A0B\u5E8F\u4E00\u6BB5\u65F6\u95F4, \u800C\u540E\u91CD\u65B0\u5904\u7406\u7B2C\u4E00\u4E2A\u7A0B\u5E8F\u4E00\u6BB5\u65F6\u95F4.</p><p>\u56E0\u4E3A\u8BA1\u7B97\u673A\u8DB3\u591F\u5FEB, \u800C\u6BCF\u6B21\u5904\u7406\u7A0B\u5E8F\u7684\u8FD9\u4E00\u6BB5\u65F6\u95F4\u8DB3\u591F\u77ED, \u6240\u4EE5\u611F\u89C9\u4E0A, \u8BA1\u7B97\u673A\u5728\u540C\u65F6\u5904\u7406\u591A\u4E2A\u7A0B\u5E8F.</p><p>1961 \u7B2C\u4E00\u4E2A\u5206\u65F6\u7CFB\u7EDF TCSS \u88AB\u7814\u53D1\u51FA\u6765. \u6240\u8C13\u5206\u65F6\u7CFB\u7EDF\u6307\u7684\u662F, \u628A cpu \u7684\u5904\u7406\u80FD\u529B\u6309\u65F6\u95F4\u5206\u6210 &quot;\u65F6\u95F4\u7247&quot;, \u518D\u628A\u8FD9\u4E2A\u65F6\u95F4\u5206\u914D\u7ED9\u4E0D\u540C\u7A0B\u5E8F\u4F7F\u7528.</p><p>TCSS \u7684\u6210\u529F\u7814\u5236\u88AB\u7F8E\u56FD\u56FD\u9632\u90E8\u9AD8\u5EA6\u91CD\u89C6. 1962 \u5E74\u7531 DARPA ( \u56FD\u9632\u90E8\u9AD8\u7EA7\u7814\u7A76\u8BA1\u5212\u5C40 ) \u51FA\u8D44 300 \u4E07\u7F8E\u5143\u542F\u52A8 MAC \u9879\u76EE, \u5B8C\u5584 TCSS.</p><p>1964 \u5E74\u8D1D\u5C14\u5B9E\u9A8C\u5BA4, \u9EBB\u7701\u7406\u5DE5\u5B66\u9662\u548C\u901A\u7528\u516C\u53F8\u627F\u62C5\u4E86\u7814\u5236\u4EFB\u52A1, \u5F00\u59CB\u4E86 Multics \u9879\u76EE, \u8BE5\u9879\u76EE\u8BD5\u56FE\u5EFA\u7ACB\u8FD0\u884C\u5728\u5927\u578B\u673A\u4E0A\u7684\u4E00\u4E2A\u5177\u5907\u4F17\u591A\u529F\u80FD\u7684, \u80FD\u591F\u5F88\u6F02\u4EAE\u5730\u652F\u6301\u5927\u7FA4\u7528\u6237\u7684\u5206\u65F6\u64CD\u4F5C\u7CFB\u7EDF.</p><p>Multics \u5C31\u662F &quot;\u7B2C\u4E8C\u7248\u6548\u5E94&quot; \u4E2D\u7684\u90A3\u4E2A &quot;\u7B2C\u4E8C\u7248&quot;. \u6700\u540E\u5B83\u56E0\u4E0D\u582A\u81EA\u8EAB\u91CD\u8D1F\u800C\u5D29\u6E83.</p><h3 id="\u8BDE\u751F" tabindex="-1"><a class="header-anchor" href="#\u8BDE\u751F" aria-hidden="true">#</a> \u8BDE\u751F</h3><p>\u8D1D\u5C14\u5B9E\u9A8C\u5BA4\u6709\u4E00\u4E2A\u5DE5\u7A0B\u5E08\u53EB <strong>\u80AF \xB7 \u6C64\u666E\u900A</strong> \u53C2\u4E8E\u4E86 Multics. \u4ED6\u5728 Mutics \u4E0A\u7F16\u5199\u4E00\u4E2A\u6E38\u620F\u540D\u53EB &quot;\u661F\u9645\u65C5\u884C&quot; \u7684\u6E38\u620F.</p><p>1969 \u5E74\u8D1D\u5C14\u5B9E\u9A8C\u5BA4\u7EC8\u4E8E\u56E0\u4E3A\u7684\u7814\u53D1\u8FDB\u5EA6\u592A\u6162\u9000\u51FA\u4E86 Multics \u9879\u76EE.</p><p>\u56DE\u5230\u8D1D\u5C14\u5B9E\u9A8C\u5BA4\u7684\u6C64\u666E\u900A\u56E0\u4E3A\u518D\u6CA1\u6709\u53EF\u4EE5\u8FD0\u884C\u4ED6\u7684 &quot;\u661F\u9645\u65C5\u884C&quot; \u800C\u6574\u65E5\u5931\u9B42\u843D\u9B44\u7684\u6E38\u8361\u5728\u529E\u516C\u5BA4\u91CC. \u76F4\u5230\u4ED6\u627E\u5230\u4E86\u4E00\u53F0\u5E9F\u5F03\u7684 PDP -- 7 \u5C0F\u578B\u673A.</p><p>\u4ED6\u4E3A\u4E86\u80FD\u8FD0\u884C\u4ED6\u7684 &quot;\u661F\u9645\u65C5\u884C&quot;, \u5728\u8FD9\u53F0 PDP - 7 \u4E0A\u5EF6\u7740 Multics \u7684\u601D\u8DEF, \u5F00\u53D1\u51FA\u4E00\u4E2A\u66F4\u8F7B\u7684\u64CD\u4F5C\u7CFB\u7EDF, \u5E76\u628A\u5B83\u620F\u79F0\u4E3A Unix. Multics \u4E2D multi \u662F &quot;\u590D\u6742, \u591A&quot;, \u800C unit \u5219\u662F &quot;\u7EDF\u4E00\u7684\u610F\u601D.&quot;</p><p>\u5F53\u65F6\u4E0E\u6C64\u666E\u900A\u4E00\u8D77\u53C2\u4E8E\u8FC7 Multics \u7684\u540C\u4E8B\u4EEC, \u5DF2\u7ECF\u4E60\u60EF\u4E86 Multics \u73AF\u5883\u4E0B\u7684\u4EA4\u4E92\u8BA1\u7B97\u65B9\u5F0F, \u770B\u5230 Unix \u540E\u5927\u559C\u8FC7\u671B.</p><p>\u53D1\u660E C \u8BED\u8A00\u7684 <strong>\u4E39\u5C3C\u65AF \xB7 \u91CC\u5947</strong> \u540E\u6765\u8BC4\u4EF7\u9053:</p><blockquote><p>\u6211\u4EEC\u5E0C\u671B\u4FDD\u7559\u7684\u4E0D\u4EC5\u4EC5\u662F\u4E00\u4E2A\u826F\u597D\u7684\u7F16\u7A0B\u73AF\u5883, \u8FD8\u5305\u62EC\u4E00\u79CD\u80FD\u591F\u5F62\u6210\u4F19\u4F34\u5173\u7CFB\u7684\u7CFB\u7EDF. \u7ECF\u9A8C\u544A\u8BC9\u6211\u4EEC, \u8FDC\u7A0B\u8BBF\u95EE ( remote-access ) \u548C\u5206\u65F6\u7CFB\u7EDF\u652F\u6301\u7684\u516C\u7528\u8BA1\u7B97, \u5176\u672C\u8D28\u4E0D\u662F\u7528\u7EC8\u7AEF\u673A\u4EE3\u66FF\u6253\u5B54\u673A\u6765\u8F93\u5165\u7A0B\u5E8F, \u800C\u662F\u9F13\u52B1\u9891\u7E41\u7684\u4EA4\u6D41.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 2 \u7AE0 \u5386\u53F2 -- \u53CC\u6D41\u8BB0\u300B</p></blockquote><p><strong>\u8BA1\u7B97\u673A\u4E0D\u4EC5\u5E94\u88AB\u89C6\u4E3A\u4E00\u79CD\u903B\u8F91\u8BBE\u5907\u800C\u66F4\u5E94\u89C6\u4E3A\u793E\u7FA4\u7684\u7ACB\u8DB3\u70B9, \u8FD9\u79CD\u89C2\u5FF5\u6DF1\u5165\u4EBA\u5FC3.</strong></p><p>Unix \u88AB\u5F00\u53D1\u51FA\u6765\u540E, \u9664\u4E86\u8FD0\u884C\u6C64\u666E\u900A\u7684\u661F\u9645\u65C5\u884C\u5916, \u4E3B\u8981\u627F\u62C5\u7684\u5DE5\u4F5C\u662F\u4E3A\u8D1D\u5C14\u5BA4\u9A8C\u5BA4\u7684\u4E13\u5229\u90E8\u95E8\u8FDB\u884C &quot;\u6587\u5B57\u5904\u7406&quot; \u7684\u652F\u6301\u5DE5\u4F5C. \u800C\u8FD9\u8BA9\u6C64\u666E\u900A\u548C\u4ED6\u7684\u4F19\u4F34\u4EEC\u6210\u540D\u6B63\u8A00\u987A\u8D2D\u4E70\u4E86\u4E00\u53F0\u529F\u80FD\u5F3A\u5927\u591A\u7684 PDP - 11 \u5C0F\u578B\u673A.</p><p>\u76F4\u5230\u6B64\u65F6, \u8D1D\u5C14\u5B9E\u9A8C\u5BA4\u7684\u9AD8\u5C42\u4EEC\u8FD8\u6CA1\u6709\u610F\u8BC6\u5230\u4ED6\u4EEC\u7F16\u5199\u7684\u5B57\u5904\u7406\u7CFB\u7EDF\u5C31\u5FEB\u5B75\u5316\u51FA\u4E00\u4E2A\u54F2\u4F5C\u7CFB\u7EDF\u6765\u4E86. \u76F4\u5230 1972 \u5E74, Unix \u7684\u88C5\u673A\u91CF\u53EA\u8FBE\u5230 10 \u53F0.</p><p>\u53D1\u660E &quot;\u7BA1\u9053&quot; \u5DE5\u5177\u7684\u9053\u683C\u62C9\u65AF \xB7 \u9EA6\u514B\u7F57\u4F0A\u63CF\u8FF0\u90A3\u4E2A\u65F6\u4EE3:</p><blockquote><p>\u4E0A\u754C\u7684\u538B\u529B\u548C\u7EAF\u7CB9\u51FA\u4E8E\u5BF9\u6280\u827A\u7684\u8363\u8A89\u611F, \u4FC3\u4F7F\u4EBA\u4EEC\u5728\u6709\u4E86\u66F4\u597D\u66F4\u591A\u7684\u521D\u6B65\u601D\u8DEF\u540E, \u53BB\u91CD\u5199\u6216\u629B\u5F00\u5DF1\u6709\u7684\u5927\u91CF\u4EE3\u7801. \u4ECE\u6765\u6CA1\u542C\u8BF4\u8FC7\u7ADE\u4E89\u548C\u52BF\u529B\u8303\u56F4\u4F55\u62A4: \u597D\u4E1C\u897F\u592A\u591A\u4E86, \u6CA1\u6709\u4EBA\u9700\u8981\u628A\u8FD9\u4E9B\u521B\u65B0\u5360\u4E3A\u5DF1\u6709.</p></blockquote><h3 id="\u5F00\u653E\u4E0E\u5185\u6218" tabindex="-1"><a class="header-anchor" href="#\u5F00\u653E\u4E0E\u5185\u6218" aria-hidden="true">#</a> \u5F00\u653E\u4E0E\u5185\u6218</h3><blockquote><p>\u5C31\u8FD9\u6837, \u7F57\u9A6C\u5728\u71C3\u70E7, \u800C\u6211\u4EEC\u8FD8\u5728\u62C9\u5C0F\u63D0\u7434.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 2 \u7AE0 \u5386\u53F2 -- \u53CC\u6D41\u8BB0\u300B</p></blockquote><p>1958 \u5E74\u4E3A\u4E86\u89E3\u51B3\u53CD\u6258\u62C9\u65AF\u6848\u4F8B\u8FBE\u6210\u7684\u548C\u89E3, AT&amp;T ( \u8D1D\u5C14\u5BA4\u9A8C\u5BA4\u7684\u6BCD\u516C\u53F8 ) \u88AB\u7981\u6B62\u8FDB\u5165\u8BA1\u7B97\u673A\u673A\u5173\u7684\u5546\u4E1A\u9886\u57DF. \u6839\u636E\u534F\u8BAE\u89C4\u5B9A, \u8D1D\u5C14\u5BA4\u9A8C\u5BA4\u5FC5\u987B\u5C06\u975E\u7535\u8BDD\u4E1A\u52A1\u7684\u6280\u672F\u8BB8\u53EF\u7ED9\u4EFB\u4F55\u63D0\u51FA\u8981\u6C42\u7684\u4EBA.</p><p>1971 \u5E74\u4E3A\u4E86\u66F4\u597D\u7684\u5C06 Unix \u79FB\u690D\u5230\u5176\u5B83\u673A\u578B\u4E0A, \u4E39\u5C3C\u65AF \xB7 \u91CC\u5947\u53D1\u660E\u4E86 C \u8BED\u8A00, 1973 \u5E74\u91CC\u5947\u548C\u6C64\u666E\u900A\u91CD\u5199\u4E86 Unix \u7CFB\u7EDF.</p><p>1974 \u5E74\u5728\u300A\u7F8E\u56FD\u8BA1\u7B97\u673A\u901A\u4FE1\u300B\u4E0A\u53D1\u8868\u7684\u4E00\u7BC7\u8BBA\u6587\u4E2D\u7B2C\u4E00\u6B21\u516C\u5F00\u5C55\u793A Unix. \u5168\u6C42\u5404\u4E2A\u7814\u7A76\u5B9E\u9A8C\u5BA4\u548C\u5927\u5B66\u90FD\u8981\u6C42\u4F53\u9A8C Unix. \u4E8E\u662F\u6C64\u666E\u900A\u5C06\u88C5\u6709 Unix \u7684\u78C1\u5E26\u548C\u78C1\u5E26\u76D8\u4E00\u5305\u5305\u5730\u5BC4\u7ED9\u4ED6\u4EEC.</p><p>Unix \u56E0\u5176\u5C0F\u5DE7\u800C\u53EF\u4EE5\u8FD0\u884C\u5728\u5C0F\u578B\u673A\u4E0A. \u5728\u90A3\u4E2A\u6CA1\u6709\u4E2A\u4EBA\u673A\u7684\u65F6\u4EE3, \u5927\u578B\u673A\u548C\u5C0F\u578B\u673A\u4EE3\u8868\u4E86\u4E24\u79CD\u6587\u5316. \u5927\u578B\u673A\u4EE3\u8868\u7740\u62E5\u6709\u4E2D\u5FC3\u5316\u6743\u529B\u7684\u653F\u5E9C\u673A\u6784, \u516C\u53F8\u548C\u9AD8\u6821. \u800C\u76F8\u5BF9\u7BA1\u5236\u8F83\u677E\u7684\u5C0F\u578B\u673A\u5219\u4EE3\u8868\u4E86\u81EA\u7531\u548C\u53CD\u4F20\u7EDF, Unix \u9ED1\u5BA2\u4EEC\u53EF\u4EE5\u7EB5\u60C5\u6325\u6D12\u7684\u53BB\u63A8\u7FFB\u4F34\u968F &quot;\u5927\u8BA1\u7B97&quot; \u7684\u6240\u6709\u6280\u672F\u5047\u5B9A\u548C\u5546\u4E1A\u5B9E\u8DF5.</p><p>1976 \u5E74\u81F3 1976 \u5E74, \u6C64\u666E\u900A\u5229\u7528\u5E74\u4F11\u5728\u52A0\u5DDE\u5728\u5B66\u4F2F\u514B\u5229\u5206\u6821\u6559\u5B66. \u8FD9\u4F7F\u4F2F\u514B\u5229\u6210\u4E3A\u4E86 Unix \u7814\u7A76\u7684\u7684\u5B66\u672F\u91CD\u9547. 1977 \u5E74\u4F2F\u514B\u5229\u53D1\u5E03\u4E86\u7B2C\u4E00\u7248 BSD ( Berkeley Software Distribution ).</p><p>1980 \u5E74, DARPA ( \u56FD\u9632\u90E8\u9AD8\u7EA7\u7814\u7A76\u8BA1\u5212\u5C40 ) \u59D4\u6258\u4F2F\u514B\u5229\u4E3B\u673A\u79D1\u5B66\u7814\u7A76\u7EC4\u5B9E\u73B0\u5168\u65B0\u7684 TCP/IP \u534F\u8BAE\u6808. \u4ECE\u6B64\u4E92\u8054\u7F51\u6587\u5316\u5F00\u59CB\u4E0E Unix \u6587\u5316\u81EA\u8FB9\u7F18\u5F00\u59CB\u878D\u5408.</p><p>1982 \u5E74, SUN \u5FAE\u7CFB\u7EDF\u516C\u53F8\u51FA\u4E16, \u4ED6\u4EEC\u7EFC\u5408\u4E86\u65AF\u5766\u798F\u5927\u5B66\u8BBE\u8BA1\u7684\u786C\u4EF6\u548C\u4F2F\u514B\u5229\u5206\u6821\u5F00\u53D1\u7684 Unix, \u53D6\u5F97\u4E86\u8F89\u714C\u7684\u6210\u529F, \u5F00\u521B\u4E86\u5DE5\u4F5C\u7AD9\u4EA7\u4E1A.</p><p>1983 \u5E74, IBM PC \u53EF\u4F7F\u7528\u4E0D\u4E0B\u516D\u79CD\u7684 Unix \u901A\u7528\u64CD\u4F5C\u7CFB\u7EDF.</p><p>\u540C\u5E74, \u7F8E\u56FD\u53F8\u6CD5\u90E8\u5728\u9488\u5BF9 AT&amp;T \u7684\u7B2C\u4E8C\u8D77\u53CD\u6258\u62C9\u65AF\u8BC9\u8BBC\u4E2D\u83B7\u80DC, \u5E76\u62C6\u5206\u4E86\u8D1D\u5C14\u7CFB\u7EDF. \u8FD9\u6B21\u5224\u51B3\u5C06 AT&amp;T \u4ECE 1958 \u5E74\u7684\u7981\u6B62\u5C06 Unix \u4EA7\u54C1\u5316\u7684\u548C\u89E3\u534F\u8BAE\u4E2D\u89E3\u8131\u51FA\u6765. \u800C\u8FD9\u5C06 Unix \u7CFB\u7EDF\u62D6\u5165\u5546\u4E1A\u516C\u53F8\u7684\u5185\u6218\u4E2D.</p><p>\u9996\u5148 Unix \u7684\u4EA7\u4E1A\u5316\u7834\u574F\u4E86 Unix \u7684\u6E90\u7801\u81EA\u7531\u4EA4\u6D41, \u8FDB\u800C\u5927\u516C\u53F8\u7684\u4E3A\u4E86\u8FFD\u6C42\u5DEE\u5F02\u5316\u5E26\u6765\u7684\u5229\u76CA\u800C\u629B\u5F03\u4E86 Unix \u7684\u8DE8\u5E73\u53F0\u517C\u5BB9\u6027.</p><p>\u4E0E\u662F Unix \u793E\u533A\u5206\u4E3A\u4E86\u4E24\u6D3E, \u4EE5\u7A0B\u7A0B\u5E8F\u5458\u548C\u5DE5\u7A0B\u5E08\u4E3A\u4EE3\u8868\u7684\u957F\u53D1\u6D3E\u7AD9\u5728\u4F2F\u514B\u5229\u5206\u6821\u4E00\u8FB9, \u800C\u5546\u4E1A\u4E3A\u76EE\u6807\u7684\u4EBA\u5219\u503E\u5411\u4E8E AT&amp;T.</p><blockquote><p>\u957F\u53D1\u6D3E, \u91CD\u5531\u7740\u5341\u5E74\u524D\u7684 Unix \u65E9\u671F\u4E3B\u9898, \u559C\u6B22\u6807\u699C\u4E3A\u4F01\u4E1A\u5546\u56FD\u7684\u53DB\u9006\u8005. \u6BD4\u5982\u4E00\u5BB6\u5C0F\u516C\u53F8\u8D34\u7684\u6D77\u62A5\u90A3\u6837, \u4E0A\u9762\u753B\u7740\u4E00\u4E2A\u6807\u7740 &quot;BSD&quot; \u7684 X \u7FFC\u661F\u9645\u6218\u673A\u5FEB\u901F\u98DE\u79BB\u5DE8\u5927\u7684 AT&amp;T \u6B7B\u661F, \u540E\u8005\u5728\u718A\u718A\u7684\u70C8\u706B\u4E2D\u7C89\u8EAB\u788E\u9AA8. \u5C31\u8FD9\u6837, \u7F57\u9A6C\u5728\u71C3\u70E7, \u800C\u6211\u4EEC\u8FD8\u5728\u62C9\u5C0F\u63D0\u7434.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 2 \u7AE0 \u5386\u53F2 -- \u53CC\u6D41\u8BB0\u300B</p></blockquote><p>\u800C\u6B64\u65F6\u5FAE\u8F6F\u6B63\u5728\u4E2A\u4EBA\u7535\u8111\u7684\u9886\u57DF\u6084\u7136\u5D1B\u8D77. 1990 \u5E74, \u5FAE\u8F6F\u7684\u7B2C\u4E00\u4E2A\u6210\u529F\u7684\u56FE\u5F62\u64CD\u4F5C\u7CFB\u7EDF Windows 3.0 \u5DE9\u56FA\u4E86\u5FAE\u8F6F\u7684\u7EDF\u6CBB\u5730\u4F4D, \u5E76\u4E3A\u5FAE\u8F6F\u5728\u4E5D\u5341\u5E74\u4EE3\u6700\u7EC8\u5784\u65AD\u684C\u9762\u5E94\u7528\u5E02\u573A\u521B\u9020\u4E86\u6761\u4EF6.</p><blockquote><p>1989 \u5E74\u5230 1993 \u5E74\u662F Unix \u7684\u4E2D\u4E16\u7EAA. \u5F53\u65F6, \u4F3C\u4E4E Unix \u793E\u7FA4\u6240\u6709\u7684\u68A6\u60F3\u90FD\u7834\u706D\u4E86.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 2 \u7AE0 \u5386\u53F2 -- \u53CC\u6D41\u8BB0\u300B</p></blockquote><h3 id="\u91CD\u751F" tabindex="-1"><a class="header-anchor" href="#\u91CD\u751F" aria-hidden="true">#</a> \u91CD\u751F</h3><p>\u5854\u80FD\u9C8D\u59C6\u6559\u6388\u662F\u8377\u5170\u963F\u59C6\u65AF\u7279\u4E39\u81EA\u7531\u5927\u5B66\u7684\u6559\u6388. \u4ED6\u7ED9\u5B66\u751F\u6559 Unix \u76F8\u5173\u7684\u77E5\u8BC6.</p><p>\u4F46 AT&amp;T \u5C06 Unix \u4EA7\u4E1A\u5316\u540E, \u5C06 Unix \u7684\u6E90\u7801\u79C1\u6709\u5316, \u5927\u5B66\u65E0\u6CD5\u518D\u4F7F\u7528 Unix \u6E90\u7801\u4E86.</p><p>\u5854\u80FD\u9C8D\u59C6\u6559\u6388\u4E3A\u4E86\u80FD\u591F\u7EE7\u7EED\u6559 Unix, \u624B\u52A8\u7F16\u5199\u4E86\u4E00\u4E2A\u7C7B Unix \u7684\u6838\u5FC3\u7A0B\u5E8F, \u4ED6\u79F0\u8FD9\u4E2A\u7A0B\u5E8F\u4E3A Minix.Minix \u610F\u4E3A &quot;\u5C0F\u578B Unix&quot;.</p><blockquote><p>\u5728\u7F16\u5199\u8FC7\u7A0B\u4E2D, \u4E3A\u4E86\u907F\u514D\u7248\u6743\u7EA0\u7EB7, \u8C2D\u90A6\u5B81 ( \u5373\u5854\u80FD\u9C8D\u59C6\u6559\u6388 ) \u5B8C\u5168\u4E0D\u770B Unix \u6838\u5FC3\u6E90\u7801, \u5E76\u4E14\u5F3A\u8C03\u4ED6\u7684 Minix \u5FC5\u987B\u80FD\u591F\u4E0E Unix \u517C\u5BB9.</p></blockquote><blockquote><p>---- \u300A\u9E1F\u54E5\u7684 Linux \u79C1\u623F\u83DC\u300B</p></blockquote><p>1991 \u5E74, \u82AC\u5170\u8D6B\u5C14\u5E78\u57FA\u5927\u5B66\u8BA1\u7B97\u673A\u79D1\u5B66\u7CFB\u7684\u5B66\u751F\u674E\u7EB3\u65AF\u5F00\u5BA3\u5E03\u4E86 Linux \u8BA1\u5212.</p><p>\u674E\u7EB3\u65AF\u56E0\u5B66\u4E60\u7684\u4E13\u4E1A\u548C\u5174\u8DA3, \u9700\u8981\u7ECF\u5E38\u4F7F\u7528 Unix \u7CFB\u7EDF, \u800C\u6574\u4E2A\u8D6B\u5C14\u5E78\u57FA\u5927\u5B66\u53EA\u6709\u4E00\u4E2A Unix \u7CFB\u7EDF, \u540C\u65F6\u4EC5\u63D0\u4F9B 16 \u4E2A\u7EC8\u7AEF.\u4E3A\u4E86\u4E0D\u7528\u6392\u961F\u7B49\u5F85\u4F7F\u7528 Unix \u7684\u65F6\u95F4, \u674E\u7EB3\u65AF\u8D2D\u4E70\u4E86 Minix \u7CFB\u7EDF, \u88C5\u5728\u81EA\u5DF1\u7684\u7535\u8111\u4E2D.</p><p>\u4F46\u662F Minix \u662F\u4E00\u4E2A\u4EC5\u4F9B\u6559\u5B66\u7528\u7684\u64CD\u4F5C\u7CFB\u7EDF, \u5F88\u591A\u529F\u80FD\u5E76\u4E0D\u5341\u5206\u5B8C\u5584. \u800C\u5854\u80FD\u9C8D\u59C6\u6559\u6388\u4E5F\u5BF9\u5B8C\u5584 Minix \u7CFB\u7EDF\u5174\u8DA3\u4E0D\u5927. \u4E8E\u662F\u674E\u7EB3\u65AF\u7D22\u6027\u51B3\u5B9A\u81EA\u5DF1\u5199\u4E00\u4E2A\u81EA\u5DF1\u7528\u7684 Minix.</p><p>\u4E8E\u662F\u6700\u521D\u7248\u672C\u7684 Linux \u7CFB\u7EDF\u5C31\u6B64\u8BDE\u751F\u4E86.</p><p>\u4E3A\u4E86\u8FDB\u4E00\u6B65\u5B8C\u5584 Linux \u7CFB\u7EDF, \u674E\u7EB3\u65AF\u5C06 Linux \u7CFB\u7EDF\u7684\u6E90\u7801\u653E\u5728\u7F51\u7EDC\u4E0A\u514D\u8D39\u8BA9\u5927\u5BB6\u4E0B\u8F7D\u4F7F\u7528, \u540C\u65F6 Linux \u5F00\u653E\u6E90\u4EE3\u7801, \u9ED1\u5BA2\u5F88\u5BB9\u6613\u53C2\u8003\u5176\u6E90\u7801\u4E3A Linux \u5199\u51FA\u9A71\u52A8\u7A0B\u5E8F\u548C\u8F6F\u4EF6.</p><p>\u5F53\u5FD7\u613F\u8005\u5B8C\u6210\u4E86\u9A71\u52A8\u7A0B\u5E8F\u6216\u8F6F\u4EF6\u7684\u5F00\u53D1, \u674E\u7EB3\u65AF\u4F1A\u5BF9\u5176\u6D4B\u8BD5, \u5982\u679C\u6CA1\u6709\u5927\u7684\u95EE\u9898, \u8FD9\u4E2A\u9A71\u52A8\u6216\u8F6F\u4EF6\u5C31\u4F1A\u88AB\u52A0\u5165\u5230 Linux \u7CFB\u7EDF\u6838\u5FC3\u5F53\u4E2D.</p><p>\u540E\u6765, \u968F\u7740 Linux \u6838\u5FC3\u7684\u529F\u80FD\u8D8A\u6765\u8D8A\u591A, \u66F4\u591A\u7684\u5FD7\u613F\u8005\u52A0\u5165\u4E86\u6D4B\u8BD5\u7A0B\u5E8F\u7684\u961F\u4F0D\u4E2D\u6765.</p><p>\u8FD9\u7FA4\u7D20\u672A\u8C0B\u9762\u7684\u865A\u62DF\u56E2\u961F, \u5728 1994 \u5E74\u7EC8\u4E8E\u5B8C\u6210\u4E86 Linux \u6838\u5FC3\u6B63\u5F0F\u7248 1.0.</p><blockquote><p>\u4E0E\u6B64\u524D\u5404\u79CD\u7248\u672C\u7684 Unix \u4E0D\u540C, Linux \u548C BSD \u7684\u5F00\u53D1\u76F8\u5F53\u4E0D\u540C, \u5B83\u4EEC\u6839\u503C\u4E8E\u4E92\u8054\u7F51.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 2 \u7AE0 \u5386\u53F2 -- \u53CC\u6D41\u8BB0\u300B</p></blockquote><blockquote><p>\u9ED1\u5BA2\u793E\u533A\u7684\u7684\u8BB8\u591A\u9AD8\u7EA7\u9886\u5BFC\u4EBA\u4E5F\u90FD\u662F Unix \u7684\u8001\u524D\u8F88, \u516B\u5341\u5E74\u4EE3\u5206\u62C6\u540E\u7684\u4F24\u75DB\u5C24\u5728, \u4ED6\u4EEC\u5C06 Linux \u4F5C\u4E3A\u5B9E\u73B0 Unix \u65E9\u671F\u5224\u9006\u68A6\u60F3\u6700\u540E\u548C\u6700\u5927\u7684\u5E0C\u671B, \u800C\u6C47\u805A\u5728 Linux \u65D7\u4E0B.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 2 \u7AE0 \u5386\u53F2 -- \u53CC\u6D41\u8BB0\u300B</p></blockquote><p>1998 \u5E74, \u5404\u4E2A\u9ED1\u5BA2\u793E\u533A\u7684\u9886\u5BFC\u4EEC\u53EC\u5F00\u4E86\u4E00\u6B21\u5CF0\u4F1A, \u8FD9\u6B21\u4F1A\u8BA9\u4E3A\u793E\u533A\u5F00\u53D1\u7684\u65B9\u5F0F\u786E\u7ACB\u4E86\u4E00\u4E2A\u65B0\u7684\u6807\u8BB0: \u5F00\u6E90.</p><hr><p>Unix \u8D77\u6E90\u4E8E Multics, \u5B83\u662F\u6743\u529B\u673A\u6784\u5BF9\u4E8E\u673A\u5668\u9AD8\u6548\u5229\u7528\u7684\u8BC9\u6C42\u7684\u4F53\u73B0.</p><p>\u8FD9\u79CD\u8BC9\u6C42\u50AC\u751F\u4E86\u4EBA\u4EEC\u4EE5\u6280\u672F\u548C\u7CFB\u7EDF\u4F5C\u4E3A\u793E\u533A\u8FDB\u884C\u5408\u4F5C\u7684\u51B2\u52A8.</p><p>\u800C Multics \u7684\u5931\u8D25\u548C Unix \u7684\u8BDE\u751F, \u4F7F\u5F97 Unix \u8D70\u5411\u5176\u521D\u8877\u7684\u53CD\u9762: \u81EA\u7531\u548C\u53CD\u53DB.</p><blockquote><p>Unix \u7684\u5386\u53F2\u4E2D, \u6700\u5927\u7684\u89C4\u5F8B\u5C31\u662F: \u8DDD\u5F00\u6E90\u8D8A\u8FD1\u5C31\u8D8A\u7E41\u8363. \u4EFB\u4F55\u5C06 Unix \u4E13\u6709\u5316\u7684\u4F01\u56FE, \u53EA\u80FD\u9677\u5165\u505C\u6EDE\u548C\u8870\u8D25.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 2 \u7AE0 \u5386\u53F2 -- \u53CC\u6D41\u8BB0\u300B</p></blockquote><p>\u5728 Unix \u5386\u53F2\u4E2D\u6700\u8BE1\u5F02\u4F46\u975E\u5E38\u5408\u903B\u8F91\u7684\u4E8B\u60C5\u662F, 1958 \u5E74 AT&amp;T \u7684\u4E0E\u53CD\u6258\u62C9\u65AF\u6848\u4F8B\u8FBE\u6210\u7684\u548C\u89E3\u534F\u8BAE, \u4F7F Unix \u66B4\u9732\u5728\u77E5\u8BC6\u4EA7\u6743\u7684\u4FDD\u62A4\u4E4B\u5916, \u8FD9\u53CD\u800C\u4E3A Unix \u5E26\u6765\u4E86\u5F00\u653E\u548C\u7E41\u8363; \u800C 1983 \u5E74 AT&amp;T \u89E3\u9664\u4E86\u548C\u89E3\u534F\u8BAE\u7684\u7981\u4EE4, \u5C06 Unix \u91CD\u65B0\u7F6E\u5165\u77E5\u8BC6\u4EA7\u6743\u7684\u4FDD\u62A4\u4E2D, \u53CD\u800C\u4F7F Unix \u62D6\u5165\u9ED1\u6697\u7684\u4E2D\u4E16\u7EAA.</p><h2 id="unix-\u57FA\u4E8E\u6F14\u5316\u7684\u54F2\u5B66" tabindex="-1"><a class="header-anchor" href="#unix-\u57FA\u4E8E\u6F14\u5316\u7684\u54F2\u5B66" aria-hidden="true">#</a> Unix \u57FA\u4E8E\u6F14\u5316\u7684\u54F2\u5B66</h2><blockquote><p>Unix \u54F2\u5B66 ( \u540C\u5176\u5B83\u5DE5\u7A0B\u9886\u57DF\u7684\u4F20\u7EDF\u4E00\u6837 ) \u662F\u81EA\u4E0B\u800C\u4E0A\u7684, \u800C\u4E0D\u662F\u81EA\u4E0A\u800C\u4E0B\u7684. Unix \u54F2\u5B66\u6CE8\u91CD\u5B9E\u6548, \u7ACB\u8DB3\u4E8E\u4E30\u5BCC\u7684\u7ECF\u9A8C. \u4F60\u4E0D\u4F1A\u5728\u6B63\u89C4\u65B9\u6CD5\u5B66\u4E2D\u627E\u5230\u5B83, \u5B83\u66F4\u63A5\u8FD1\u4E8E\u66F4\u672C\u80FD\u7684\u534A\u9690\u6027\u77E5\u8BC6, \u5373 Unix \u6240\u4F20\u64AD\u7684\u4E13\u4E1A\u7ECF\u9A8C. \u5B83\u9F13\u52B1\u90A3\u79CD\u5206\u6E05\u8F7B\u91CD\u7F13\u6025\u7684\u611F\u89C9, \u4EE5\u53CA\u6000\u7591\u4E00\u5207\u7684\u6001\u5EA6, \u5E76\u9F13\u52B1\u4F60\u4EE5\u5E7D\u9ED8\u8FBE\u89C2\u7684\u6001\u5EA6\u5BF9\u5F85\u8FD9\u4E9B.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F\xB7 \u7B2C 1 \u7AE0 \u54F2\u5B66\u300B</p></blockquote><h3 id="\u63D0\u4F9B\u673A\u5236-\u800C\u975E\u7B56\u7565" tabindex="-1"><a class="header-anchor" href="#\u63D0\u4F9B\u673A\u5236-\u800C\u975E\u7B56\u7565" aria-hidden="true">#</a> \u63D0\u4F9B\u673A\u5236, \u800C\u975E\u7B56\u7565</h3><p>\u5173\u4E8E Unix \u54F2\u5B66\u662F\u4EC0\u4E48, \u6709\u65E0\u6570\u7684\u793E\u533A\u5927\u4F6C\u4EEC\u8BF4\u8FC7\u65E0\u6570\u4E2A\u7248\u672C.</p><p>\u4F46\u6211\u8BA4\u4E3A, \u53EF\u80FD\u91CC\u8FB9\u6700\u91CD\u8981\u7684\u662F x Window \u7684\u8BBE\u8BA1\u8005\u63D0\u51FA\u7684 &quot;\u63D0\u4F9B\u673A\u5236, \u800C\u975E\u7B56\u7565&quot; .</p><p>X Window \u662F\u4E00\u4E2A\u57FA\u4E8E Unix \u7684\u56FE\u5F62\u754C\u9762\u8F6F\u4EF6. \u5F53\u65F6\u56FE\u5F62\u754C\u9762\u8FD8\u662F\u4E00\u4E2A\u65B0\u73A9\u610F. \u9762\u5BF9\u5982\u4F55\u8BBE\u8BA1\u4E00\u79CD\u56FE\u5F62\u754C\u9762, \u5F53\u65F6\u6709\u4E24\u4E2A\u9009\u62E9: \u4E00\u79CD\u662F\u628A\u7CFB\u7EDF\u884C\u4E3A\u7684\u903B\u8F91\u5C3D\u53EF\u80FD\u7684\u63A8\u5230\u4F7F\u7528\u7AEF, \u8FD8\u662F\u628A\u8FD9\u4E9B\u903B\u8F91\u6DF1\u85CF\u4E8E\u5E95\u5C42.</p><p>\u4E3A\u4E86\u652F\u6301\u66F4\u591A\u7684 Unix \u7684\u7CFB\u7EDF\u800C\u6210\u4E3A\u4E00\u4E2A\u6781\u7AEF\u901A\u7528\u7684\u56FE\u5F62\u754C\u9762, X Window \u9009\u62E9\u4E86\u540E\u8005. \u5C06\u7CFB\u7EDF\u5E95\u5C42\u7684\u884C\u4E3A\u903B\u8F91\u66B4\u9732\u51FA\u6765, \u63D0\u4F9B\u5176\u8FD0\u884C\u7684\u673A\u5236, \u4F7F\u7528\u8005\u53EF\u4EE5\u6839\u636E\u81EA\u5DF1\u7684\u73AF\u5883\u8BA2\u5236\u81EA\u5DF1\u7684\u7B56\u7565.</p><p>\u800C\u6211\u4EEC\u77E5\u9053, \u5FAE\u8F6F\u7684 Windows \u9009\u62E9\u4E86\u524D\u8005, \u4ED6\u628A\u7CFB\u7EDF\u7684\u5E95\u5C42\u7684\u884C\u4E3A\u903B\u8F91\u5305\u88F9\u7684\u5F88\u4E25, \u7528\u6237\u53EA\u80FD\u5B8C\u6210\u88AB\u8BBE\u8BA1\u597D\u7684\u64CD\u4F5C. \u4F46\u8FD9\u4E5F\u6709\u660E\u663E\u7684\u597D\u5904, \u5C31\u662F\u5BF9\u4E8E\u7528\u6237\u6765\u8BF4, \u7CFB\u7EDF\u6210\u4E86\u4E00\u4E2A\u4E0D\u7528\u64CD\u5FC3\u7684\u9ED1\u7BB1, <strong>\u663E\u5F97</strong> \u975E\u5E38\u7684\u7B80\u5355.</p><p>&quot;\u63D0\u4F9B\u673A\u5236, \u800C\u975E\u7B56\u7565&quot; \u8868\u8FBE\u4E86\u4E00\u4E2A\u975E\u5E38\u91CD\u8981\u7684\u4FE1\u5FF5: \u6700\u7EC8\u7528\u6237\u6C38\u8FDC\u6BD4\u64CD\u4F5C\u7CFB\u7EDF\u8BBE\u8BA1\u4EBA\u5458\u66F4\u6E05\u695A\u4ED6\u4EEC\u9700\u8981\u4EC0\u4E48, \u800C\u4ED6\u4EEC\u4E5F\u6709\u80FD\u529B\u4E3A\u81EA\u5DF1\u7684\u884C\u4E3A\u8D1F\u8D23.</p><blockquote><p>\u8D1D\u5C14\u5B9E\u9A8C\u5BA4\u7684 Dick Mamming \u5728 1950 \u5E74\u4EE3\u4FBF\u6811\u7ACB\u4E86\u6B64\u4FE1\u6761: \u5C3D\u7BA1\u8BA1\u7B97\u673A\u7A00\u7F3A\u6602\u8D35, \u4F46\u662F\u5F00\u653E\u5F0F\u7684\u8BA1\u7B97\u6A21\u5F0F, \u5373\u5BA2\u6237\u53EF\u4EE5\u4E3A\u81EA\u5DF1\u5199\u51FA\u5E94\u7528\u7A0B\u5E8F, \u8FD9\u4E00\u70B9\u52BF\u5728\u5FC5\u884C, \u56E0\u4E3A &quot;\u7528\u9519\u8BEF\u7684\u65B9\u5F0F\u89E3\u51B3\u6B63\u786E\u7684\u95EE\u9898\u603B\u6BD4\u7528\u6B63\u786E\u7684\u65B9\u5F0F\u89E3\u51B3\u9519\u8BEF\u7684\u95EE\u9898\u597D&quot;.</p></blockquote><blockquote><p>---- \u9053\u683C\u62C9\u65AF \xB7 \u9EA6\u514B\u7F57\u4F0A</p></blockquote><p>&quot;\u63D0\u4F9B\u673A\u5236, \u800C\u975E\u7B56\u7565&quot; \u7684\u539F\u5219\u7684\u4EE3\u4EF7\u662F, \u7528\u6237\u5C06\u9762\u5BF9\u770B\u8D77\u6765\u66F4\u590D\u6742\u7684\u7CFB\u7EDF\u903B\u8F91, \u8FD9\u4FC3\u4F7F\u975E\u4E13\u4E1A\u7684\u7528\u6237\u53BB\u9009\u62E9\u770B\u8D77\u6765\u66F4\u7B80\u7ED3\u7684\u64CD\u4F5C\u7CFB\u7EDF.</p><p>( \u8FD9\u8BA9\u6211\u83AB\u660E\u7684\u60F3\u8D77\u7F57\u632F\u5B87\u8BF4\u7F57\u8F91\u601D\u7EF4\u8981\u505A &quot;\u77E5\u8BC6\u7684\u8FD0\u8425\u5546&quot;, \u4EC0\u4E48\u662F &quot;\u8FD0\u8425\u5546&quot; \u5462, \u5C31\u662F\u628A\u5E95\u5C42\u590D\u6742\u7684\u8FC7\u7A0B\u90FD\u6253\u5305\u5904\u7406\u6389, \u800C\u7ED9\u5BA2\u6237\u63D0\u4F9B\u6700\u7EC8\u7B80\u5355\u7684\u670D\u52A1. \u8FD9\u662F\u5178\u578B\u7684 &quot;\u63D0\u4F9B\u7B56\u7565, \u800C\u975E\u673A\u5236&quot; \u7684\u903B\u8F91. )</p><p>\u4F46\u8FD9\u4E2A\u539F\u5219\u4E5F\u6709\u81F3\u5173\u91CD\u8981\u7684\u4F18\u52BF: \u673A\u5236\u662F\u957F\u4E45\u6709\u6548\u7684, \u800C\u7B56\u7565\u5219\u975E\u5E38\u77ED\u6682.</p><p>\u7B56\u7565\u6709\u7740\u5F88\u5F3A\u7684 &quot;\u8BED\u5883&quot;: \u5B83\u662F\u4E3A\u4E86\u89E3\u51B3\u7279\u5B9A\u7684\u95EE\u9898\u6216\u6EE1\u8DB3\u7279\u5B9A\u7684\u8BC9\u6C42\u800C\u5BF9\u673A\u5236\u7684\u5E94\u7528. \u4ECA\u65E5\u6D41\u884C\u7684\u7B56\u7565\u660E\u65E5\u53EF\u80FD\u8D70\u5165\u6B7B\u80E1\u540C.</p><p>\u673A\u5236\u5219\u4E0D\u540C, \u5B83\u662F\u6F14\u5316\u7684\u57FA\u7840. \u4E00\u65B9\u9762\u7CFB\u7EDF\u5728\u9762\u5BF9\u4E0D\u540C\u7684\u60C5\u5883\u91C7\u65F6\u6839\u636E\u5176\u673A\u5236\u91C7\u7528\u4E0D\u540C\u7684\u7B56\u7565, \u53E6\u4E00\u65B9\u9762, \u5C06\u673A\u5236\u66B4\u9732\u51FA\u6765, \u4E5F\u4F7F\u673A\u5236\u5728\u4E0D\u9002\u5C31\u65F6\u53EF\u4EE5\u8FDB\u884C\u8FED\u4EE3.</p><p>\u6B63\u662F\u6F14\u5316\u4F7F\u673A\u5236\u6210\u4E3A\u957F\u4E45\u6709\u6548\u7684.</p><h3 id="\u5DE5\u5177\u54F2\u5B66" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u5177\u54F2\u5B66" aria-hidden="true">#</a> \u5DE5\u5177\u54F2\u5B66</h3><blockquote><p>\u62FF\u4E0D\u51C6, \u5C31\u7A77\u4E3E.</p></blockquote><blockquote><p>---- \u80AF \xB7 \u6C64\u666E\u900A</p></blockquote><blockquote><p>\u745E\u58EB\u519B\u5200\u662F\u5F88\u591A\u4EBA\u53E3\u888B\u91CC\u7684\u597D\u5E2E\u624B. \u5B83\u6709\u5200\u5203, \u87BA\u4E1D\u8D77\u5B50, \u5F00\u7F50\u5668, \u7259\u7B7E\u7B49\u5DE5\u5177. \u529F\u80FD\u66F4\u9F50\u5907\u7684, \u8FD8\u6709\u50CF\u62E8\u585E\u94BB, \u653E\u5927\u955C\u7B49\u5DE5\u5177.</p></blockquote><blockquote><p>---- \u300AShell \u811A\u672C\u5B66\u4E60\u6307\u5357 \xB7 \u524D\u8A00\u300B</p></blockquote><p>\u5982\u679C\u63D0\u4F9B\u673A\u5236, \u90A3\u4E48\u8981\u4EE5\u4EC0\u4E48\u5F62\u5F0F\u5C06\u673A\u5236\u63D0\u4F9B\u51FA\u6765\u5462?</p><p><strong>\u7528\u5DE5\u5177\u6765\u63D0\u4F9B.</strong></p><p>\u5DE5\u5177\u53EF\u4EE5\u770B\u4F5C\u662F\u4F7F\u7528\u8005\u4E0E\u63D0\u4F9B\u8005\u4E4B\u95F4\u7684\u6865\u6881. \u4E00\u65B9\u9762\u5DE5\u5177\u7684\u8BBE\u8BA1\u8005\u901A\u8FC7\u5DE5\u5177\u5C06\u673A\u5668\u7684\u80FD\u529B\u63D0\u4F9B\u51FA\u6765, \u53E6\u4E00\u65B9\u9762\u5DE5\u5177\u7684\u4F7F\u7528\u8005\u901A\u8FC7\u5DE5\u5177\u6765\u4F7F\u7528\u673A\u5668\u7684\u80FD\u529B.</p><p>\u4F7F\u7528\u8005\u81EA\u7136\u662F\u6709\u81EA\u5DF1\u4F7F\u7528\u5DE5\u5177\u7684\u4E60\u60EF, \u800C\u8BBE\u8BA1\u8005\u4E5F\u5E94\u8BE5\u5C3D\u91CF\u6EE1\u8DB3\u4F7F\u7528\u8005\u7684\u4E60\u60EF. \u4F46\u662F\u4F5C\u4E3A\u8BBE\u8BA1\u8005\u5982\u4F55\u80FD\u591F\u731C\u4E2D\u7528\u6237\u7684\u4F7F\u7528\u4E60\u60EF\u5462?</p><p>\u7B54\u6848\u662F\u4E0D\u7528\u731C\u4E2D, Unix \u91C7\u7528\u4E00\u79CD\u7B80\u5355\u7C97\u66B4\u7684\u65B9\u5F0F\u6765\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898: <strong>\u62FF\u4E0D\u51C6, \u5C31\u7A77\u4E3E</strong>.</p><p>\u8BBE\u8BA1\u8005\u5E94\u8BE5\u5C3D\u53EF\u80FD\u7684\u5DE5\u5177\u6240\u63D0\u4F9B\u80FD\u529B\u7684\u4F7F\u7528\u65B9\u5F0F, \u5E76\u8BA9\u4F7F\u7528\u8005\u901A\u8FC7\u9009\u9879\u6765\u9009\u62E9\u81EA\u5DF1\u7684\u4F7F\u7528\u65B9\u5F0F. <strong>\u8FD9\u4E5F\u662F &quot;\u63D0\u4F9B\u673A\u5236, \u800C\u975E\u7B56\u7565&quot; \u7684\u6781\u597D\u4F53\u73B0: \u5DE5\u5177\u63D0\u4F9B\u89E3\u51B3\u95EE\u9898\u7684\u6240\u6709\u53EF\u80FD, \u800C\u4F7F\u7528\u8005\u51B3\u5B9A\u5982\u4F55\u4F7F\u7528\u5DE5\u5177\u6765\u51B3\u5B9A\u95EE\u9898.</strong></p><p>\u8FD9\u79CD\u5DE5\u5177\u54F2\u5B66\u7684\u8BBE\u8BA1\u601D\u7ECF\u5E38\u88AB\u6070\u5F53\u7684\u6BD4\u55BB\u4E3A <strong>&quot;\u745E\u58EB\u519B\u5200&quot;</strong>.</p><p>\u745E\u58EB\u519B\u5200\u7684\u9690\u55BB\u5305\u542B\u7740\u4E24\u5C42\u610F\u601D: \u4ECE\u5DE5\u5177\u5185\u90E8\u6765\u8BB2, \u5B83\u5E94\u5F53\u63D0\u4F9B\u8DB3\u591F\u591A\u7684\u4F7F\u7528\u65B9\u5F0F, \u540C\u65F6\u4ECE\u5904\u90E8\u8BB2, \u5B83\u4E0D\u5E94\u5F53\u4E5F\u4E0D\u53EF\u80FD\u662F\u5200\u80FD\u7684. <strong>\u65E0\u8BBA\u745E\u58EB\u519B\u5200\u5982\u4F55\u65B9\u4FBF, \u780D\u6811\u8BF7\u4F7F\u7528\u7535\u952F.</strong></p><p>\u8FD9\u662F\u5DE5\u5177\u54F2\u5B66\u7684\u53E6\u4E00\u9762: <strong>\u6A21\u5757\u539F\u5219</strong>:</p><ul><li>\u9664\u975E\u522B\u65E0\u5B83\u6CD5, \u4E0D\u8981\u7F16\u5199\u5E9E\u5927\u7A0B\u5E8F;</li><li>\u7EC4\u5408\u5C0F\u5DE5\u5177, \u5B8C\u6210\u5927\u4EFB\u52A1, \u56E0\u6B64\u5728\u8BBE\u8BA1\u5DE5\u5177\u65F6, \u5C31\u8981\u8003\u8651\u5DE5\u5177\u7684\u62FC\u63A5\u7EC4\u5408;</li><li>\u4E3A\u4E86\u8BA9\u5DE5\u5177\u5177\u6709\u8DB3\u591F\u7684\u901A\u7528\u6027, \u5C31\u5F53\u8BA9 <strong>\u7B56\u7565\u4E0E\u673A\u5236\u5206\u79BB, \u8BA9\u63A5\u53E3\u4E0E\u5F15\u64CE\u5206\u79BB</strong>;</li><li>\u5DE5\u5177\u5E94\u5F53\u7F04\u9ED8, \u4E00\u65B9\u9762\u5B83\u76F8\u4FE1\u7528\u8005\u6572\u4E0B\u547D\u4EE4\u65F6\u77E5\u9053\u81EA\u5DF1\u5728\u505A\u4EC0\u4E48, \u53E6\u4E00\u65B9\u9762\u4F5C\u4E3A\u4E00\u4E2A\u534F\u4F5C\u7684\u73AF\u8282, \u5982\u679C\u8FD0\u884C\u6B63\u5E38\u5C31\u4E0D\u5E94\u8BE5\u6253\u6270\u7528\u6237;</li><li>\u5DE5\u5177\u4EA7\u751F\u7684\u4E00\u5207\u77E5\u8BC6,\u5E94\u5F53\u4EE5\u6570\u636E\u7684\u5F62\u5F0F\u4FDD\u5B58, \u4EE5\u4F9B\u534F\u4F5C\u7684\u5176\u5B83\u5DE5\u5177\u4F7F\u7528.</li></ul><p>\u5DE5\u5177\u54F2\u5B66\u7684\u5B9E\u8D28\u662F\u6307\u5411\u534F\u4F5C\u7684. \u8FD9\u79CD\u534F\u4F5C\u4E0D\u662F\u5176\u4E8E\u4E2D\u5FC3\u5316\u7BA1\u63A7\u7684\u5206\u5DE5\u534F\u4F5C, \u800C\u662F\u5176\u4E8E\u5206\u5E03\u5F0F\u7684\u4E3A\u81EA\u5DF1\u72EC\u7ACB\u8D1F\u8D23\u7684\u5320\u4EBA\u7684\u534F\u4F5C.</p><p>\u5728 Unix \u54F2\u5B66\u4E2D, <strong>\u4F7F\u7528\u8005\u662F\u5168\u5B8C\u4E3A\u81EA\u5DF1\u884C\u4E3A\u8D1F\u8D23\u7684</strong> \u662F\u4E00\u4E2A\u6781\u4E3A\u91CD\u8981\u7684\u5047\u8BBE.</p><h3 id="\u7A0B\u5E8F\u662F\u5199\u7ED9\u4EBA\u770B\u7684" tabindex="-1"><a class="header-anchor" href="#\u7A0B\u5E8F\u662F\u5199\u7ED9\u4EBA\u770B\u7684" aria-hidden="true">#</a> \u7A0B\u5E8F\u662F\u5199\u7ED9\u4EBA\u770B\u7684</h3><p>\u56E0\u4E3A\u534F\u4F5C, \u7A0B\u5E8F\u4E0D\u662F\u5199\u7ED9\u673A\u5668\u770B\u7684, \u800C\u662F\u5199\u7ED9\u4EBA\u770B\u7684. \u8FD9\u8981\u6C42\u4E24\u70B9:</p><ul><li><strong>\u900F\u660E</strong>, \u53EA\u6709\u5DE5\u5177\u5185\u90E8\u662F\u900F\u660E\u7684, \u4F7F\u7528\u8005\u624D\u80FD\u7406\u89E3\u5176\u673A\u5236;</li><li><strong>\u7B80\u6D01</strong>, \u7B80\u7684\u903B\u8F91\u53EF\u4EE5\u8BA9\u4EBA\u4EEC\u5FEB\u901F\u7406\u89E3;</li></ul><p>\u6B63\u662F\u56E0\u6B64, Unix \u4E2D\u51E0\u4E4E\u6240\u6709\u4E1C\u897F\u90FD\u4EE5\u6587\u672C\u6587\u4EF6\u7684\u5F62\u5F0F\u4F53\u73B0\u51FA\u6765. \u751A\u81F3\u786C\u4EF6\u8BBE\u5907\u90FD\u8981\u4EE5\u6587\u4EF6\u7684\u5F62\u5F0F\u5448\u73B0.</p><p>\u56E0\u4E3A\u6587\u672C\u6587\u4EF6\u662F\u4EBA\u7C7B\u53EF\u8BFB\u7684 ( \u800C\u4E8C\u8FDB\u5236\u6587\u4EF6\u5219\u662F\u4EBA\u7C7B\u4E0D\u53EF\u8BFB\u7684 ).</p><p>\u800C\u900F\u660E\u4E0E\u7B80\u6D01\u53C8\u4F7F\u5F97\u7A0B\u5E8F\u672C\u8EAB\u53D8\u5F97\u66F4\u52A0\u5EFA\u58EE:</p><blockquote><p>\u53EA\u8981\u6C42\u8DB3\u591F\u591A\u7684\u773C\u7403\u5173\u6CE8, \u5C31\u53EF\u4EE5\u8BA9\u6240\u6709\u8F6F\u4EF6\u7684\u7F3A\u9677\u6D6E\u73B0.</p></blockquote><blockquote><p>---- \u300A\u5927\u6559\u5802\u4E0E\u96C6\u5E02\u300B</p></blockquote><h3 id="\u8FED\u4EE3\u4E0E\u6F14\u5316" tabindex="-1"><a class="header-anchor" href="#\u8FED\u4EE3\u4E0E\u6F14\u5316" aria-hidden="true">#</a> \u8FED\u4EE3\u4E0E\u6F14\u5316</h3><blockquote><p>\u5BF9\u4E8E\u8F6F\u4EF6\u8BBE\u8BA1\u548C\u5B9E\u73B0\u6765\u8BF4, Unix \u6709\u4E00\u70B9\u5F88\u597D, \u5373\u4ECE\u4E0D\u76F8\u4FE1\u4EFB\u4F55 &quot;\u4E0D\u4E8C\u6CD5\u95E8&quot; ...... \u5982\u679C\u8BF4\u76F8\u4FE1\u522B\u4EBA\u5BA3\u79F0\u7684 &quot;\u4E0D\u4E8C\u6CD5\u95E8&quot; \u662F\u4E0D\u660E\u667A\u7684\u8BDD, \u90A3\u4E48\u575A\u4FE1\u81EA\u5DF1\u7684\u8BBE\u8BA1\u662F &quot;\u4E0D\u4E8C\u6CD5\u95E8&quot; \u7B80\u76F4\u5C31\u662F\u611A\u8822\u4E86.</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 1 \u7AE0 \u54F2\u5B66\u300B</p></blockquote><p>\u6700\u7EC8\u6307\u5411\u534F\u4F5C\u7684 Unix \u54F2\u5B66\u8BF8\u539F\u5219, \u4F7F\u5F97\u5176\u5177\u6709\u4E86\u5206\u5E03\u5F0F\u8FED\u4EE3\u7684\u53EF\u80FD.</p><p>\u4E0D\u540C\u7684\u4EBA\u4EE5\u4E0D\u540C\u7684\u76EE\u7684, \u4F7F\u7528 Unix \u63D0\u4F9B\u7684\u5DE5\u5177, \u5E76\u5728\u4F7F\u7528\u4E2D\u53D1\u73B0\u66F4\u597D\u7684\u673A\u5236.</p><p>\u800C\u900F\u660E\u548C\u7B80\u6D01\u7684\u539F\u5219\u4F7F\u5F97\u5728\u5DE5\u5177\u4E2D\u589E\u52A0\u8FD9\u4E9B\u66F4\u597D\u7684\u673A\u5236\u53D8\u5F97\u53EF\u80FD.</p><p>\u800C\u66F4\u597D\u7684\u673A\u5236\u53C8\u80FD\u5E26\u6765\u66F4\u597D\u7684\u7B56\u7565, \u4F7F\u66F4\u591A\u7684\u4EBA\u53EF\u4EE5\u4F7F\u7528 Unix \u63D0\u4F9B\u7684\u5DE5\u5177.</p><p>\u5982\u6B64, \u4E00\u79CD\u5206\u5E03\u5F0F\u7684\u8FED\u4EE3\u6A21\u5F0F\u5C31\u6B64\u5F62\u6210. \u60F3\u60F3 Linux \u7684\u865A\u62DF\u56E2\u961F\u5C31\u662F\u5EFA\u57FA\u5728\u8FD9\u79CD\u8FED\u4EE3\u6A21\u5F0F\u4E0B\u7684.</p><p>\u6B63\u662F\u5728\u8FD9\u4E2A\u610F\u4E49\u4E0A, \u6211\u4EEC\u624D\u771F\u6B63\u80FD\u8BF4 <strong>\u673A\u5236\u662F\u957F\u4E45\u6709\u6548\u7684, \u800C\u7B56\u7565\u975E\u5E38\u77ED\u6682</strong>.</p><p><strong>\u8FD9\u79CD\u8FED\u4EE3\u5C31\u662F\u6F14\u5316</strong>.</p><p><strong>\u76F8\u4FE1\u6F14\u5316\u7684\u529B\u91CF, \u800C\u4E0D\u662F\u7CBE\u5DE7\u7684\u8BBE\u8BA1</strong> \u662F Unix \u54F2\u5B66\u7684\u57FA\u672C\u6C14\u8D28:</p><blockquote><p>&quot;\u8FC7\u65E9\u4F18\u5316\u662F\u4E07\u6076\u4E4B\u6E90.&quot;</p></blockquote><blockquote><p>---- \u300AUnix \u7F16\u7A0B\u827A\u672F \xB7 \u7B2C 1 \u7AE0 \u54F2\u5B66\u300B</p></blockquote><p>Unix \u54F2\u5B66\u7684\u8FD9\u79CD\u6C14\u8D28\u5373\u6839\u690D\u4E8E\u5B83\u8BDE\u751F\u4E8E\u4E2D\u5FC3\u5316\u5927\u7A0B\u5E8F Multics \u9879\u76EE\u7684\u5E9F\u589F\u8FD9\u4E0A\u8FD9\u4E00\u51FA\u751F, \u4E5F\u6839\u690D\u4E8E\u5F53\u65F6 Unix \u6240\u57FA\u4E8E\u7684\u5C0F\u578B\u673A\u672C\u8EAB\u81EA\u7531\u548C\u53CD\u4F20\u7EDF\u7684\u6587\u5316\u9690\u55BB\u4E2D.</p><p>\u800C Unix \u5F00\u653E\u4E0E\u5206\u88C2\u7684\u5386\u53F2\u548C\u5176\u5728 Linux \u4E0A\u7684\u91CD\u751F, \u4E5F\u4E00\u518D\u8BC1\u660E, \u8FD9\u79CD <strong>\u76F8\u4FE1\u6F14\u5316</strong> \u7684\u6C14\u8D28\u6B63\u662F Unix \u4F5C\u4E3A\u4E00\u4E2A\u64CD\u4F5C\u7CFB\u7EDF\u53EF\u4EE5\u5728\u6682\u5165\u671D\u9732\u7684\u8F6F\u4EF6\u6587\u5316\u4E2D\u6D8C\u73B0\u51FA\u53EF\u4EE5\u4F20\u627F\u7684 &quot;\u54F2\u5B66&quot; \u7684\u539F\u56E0.</p><h3 id="\u53E6\u4E00\u6761\u8DEF" tabindex="-1"><a class="header-anchor" href="#\u53E6\u4E00\u6761\u8DEF" aria-hidden="true">#</a> \u53E6\u4E00\u6761\u8DEF</h3><p>\u7FFB\u7535\u7684\u5C0F\u674E\u8001\u5E08\u5728 \u300A\u7FFB\u8F6C\u95EE\u7B54 \xB7 VOL 9 | \u4E92\u8054\u7F51\u5728\u8D8A\u6765\u8D8A\u7CDF\u4E48? \u600E\u4E48\u529E\u300B\u4E00\u671F\u8282\u76EE\u4E2D, \u63D0\u5230\u4ECE\u8C31\u7CFB\u5B66\u7684\u89D2\u5EA6\u63D0\u5230\u4E92\u8054\u7F51\u5176\u5B9E\u662F\u6765\u81EA\u7531\u7F8E\u519B\u5BF9\u4E8E\u63A7\u5236\u7684\u8BC9\u6C42.</p><p>\u8FD9\u5F53\u7136\u662F\u5BF9\u7684\u5566. \u4F46\u662F\u6211\u60F3\u8865\u5145\u7684\u662F, \u540C\u65F6, \u5728\u4E92\u8054\u7F51\u7684\u8BDE\u751F\u4E2D, \u8FD8\u6709\u8FD9\u6837\u4E00\u5C42\u8C31\u7CFB, \u5C31\u662F\u9ED1\u5BA2\u6587\u5316\u548C Unix \u54F2\u5B66.</p><p>\u5B83\u4EEC\u4EE3\u8868\u7740\u53E6\u5916\u4E00\u79CD\u529B\u91CF, \u5851\u9020\u7740\u4ECA\u5929\u6837\u8C8C.</p><p>\u800C\u8FD9\u5E76\u4E0D\u662F\u6211\u4EEC\u7ECF\u5E38\u60F3\u8C61\u7684, \u5DE5\u7A0B\u5E08\u4EEC\u901A\u8FC7\u6280\u672F\u65E0\u610F\u8BC6\u7684\u5F71\u54CD\u7740\u4E16\u754C. \u800C\u662F\u4ED6\u4EEC\u6709\u610F\u8BC6\u7684\u8BA9\u8FD9\u79CD\u4E16\u754C\u4E0D\u843D\u5165\u8D5B\u535A\u670B\u514B\u5C0F\u8BF4\u6240\u63CF\u8FF0\u7684\u7CDF\u7CD5\u7684\u4E16\u754C.</p><p>\u6280\u672F\u6587\u5316\u672C\u8EAB\u662F\u6709\u653F\u6CBB\u9690\u55BB\u7684.</p><blockquote><p>\u4E16\u4E0A\u6709\u4E24\u79CD\u5BC6\u7801, \u4E00\u79CD\u662F\u9632\u6B62\u4F60\u7684\u5C0F\u59B9\u59B9\u770B\u4F60\u7684\u6587\u4EF6; \u53E6\u4E00\u79CD\u662F\u9632\u6B62\u5F53\u5C40\u8005\u9605\u8BFB\u4F60\u7684\u6587\u4EF6\u8D44\u6599. \u672C\u4E66\u5199\u7684\u662F\u540E\u4E00\u79CD\u60C5\u51B5.</p></blockquote><blockquote><p>---- \u300A\u5E94\u7528\u5BC6\u7801\u5B66\u300B</p></blockquote><p>\u5DE5\u7A0B\u5E08\u4EEC, \u4F5C\u4E3A\u77E5\u8BC6\u5206\u5B50, \u6709\u610F\u8BC6\u7684\u7528\u81EA\u5DF1\u6240\u638C\u63E1\u7684\u6280\u672F\u62B5\u6297\u7740\u8BA9\u4E16\u754C\u53D8\u63D0\u66F4\u7CDF\u7684\u529B\u91CF, \u5BC6\u7801\u5B66\u662F\u5176\u4E00, Unix \u54F2\u5B66\u4EA6\u662F\u5176\u4E00.</p><p>\u5C0F\u674E\u8001\u5E08\u5728\u540C\u4E00\u671F\u8282\u76EE\u4E2D\u4E5F\u63D0\u5230, \u4E92\u8054\u7F51\u7684\u95EE\u9898\u5176\u5B9E\u662F &quot;\u7CFB\u7EDF ( \u6280\u672F ) \u5165\u4FB5\u751F\u6D3B&quot; \u7684\u95EE\u9898.</p><p>\u8FD9\u4E5F\u662F\u6211\u4EEC\u8FD9\u4E00\u4EE3\u4EBA\u6240\u5FC5\u9700\u8981\u9762\u5BF9\u7684\u95EE\u9898. \u4F60\u4E0D\u5173\u5FC3\u6280\u672F, \u4F46\u6280\u672F\u5173\u5FC3\u4F60.</p><p>\u6240\u4EE5\u6211\u4EEC\u8981\u901A\u8FC7\u79EF\u6781\u7684\u81EA\u6211\u6280\u672F\u5EFA\u7ACB\u4E00\u5957\u7CFB\u7EDF, \u7528\u6280\u672F\u5BF9\u6297\u6280\u672F, \u7528\u7CFB\u7EDF\u5BF9\u6297\u7CFB\u7EDF.</p><p>( \u5982\u679C\u5173\u6CE8\u6211\u65E9\u7684\u670B\u53CB\u53EF\u80FD\u4F1A\u53D1\u73B0\u516C\u4F17\u53F7\u7684\u7B7E\u540D\u6539\u4E86. )</p><p>\u90A3\u6211\u4EEC\u5982\u4F55\u901A\u8FC7\u79EF\u6781\u7684\u81EA\u6211\u6280\u672F\u5EFA\u7ACB\u4E00\u5957\u7CFB\u7EDF, \u4E86\u89E3 Unix \u54F2\u5B66, \u6216\u8BB8\u4F1A\u6709\u70B9\u542F\u53D1\u5427.</p>',186),q=[n];function e(c,x){return p(),t("div",null,q)}var a=o(i,[["render",e],["__file","Unix\u57FA\u4E8E\u6F14\u5316\u7684\u54F2\u5B66.html.vue"]]);export{a as default};
