import{_ as r,o as p,c as a,e as i}from"./app.efeddd24.js";var e="/blog/img/project/qishilu/01.jpeg",t="/blog/img/project/qishilu/02.jpeg",s="/blog/img/project/qishilu/03.jpeg",n="/blog/img/project/qishilu/04.jpeg",o="/blog/img/project/qishilu/05.jpeg",h="/blog/img/project/qishilu/06.jpeg",l="/blog/img/project/qishilu/07.jpeg";const d={},g=i('<h1 id="\u300A\u542F\u793A\u5F55\u300B\u8BF4\u4E86\u5565" tabindex="-1"><a class="header-anchor" href="#\u300A\u542F\u793A\u5F55\u300B\u8BF4\u4E86\u5565" aria-hidden="true">#</a> \u300A\u542F\u793A\u5F55\u300B\u8BF4\u4E86\u5565</h1><h2 id="\u7F18\u8D77" tabindex="-1"><a class="header-anchor" href="#\u7F18\u8D77" aria-hidden="true">#</a> \u7F18\u8D77</h2><p>\u8FD9\u662F\u53E6\u4E00\u4E2A\u7CFB\u5217\u6587\u7AE0\u7684\u7B2C\u4E00\u7BC7. \u6240\u4EE5, \u6211\u8BF4\u7684\u8FD9\u4E2A \u201C\u7F18\u8D77\u201D \u4E0D\u4EC5\u662F\u6211\u4E3A\u4EC0\u4E48\u5199\u8FD9\u7BC7\u6587\u7AE0\u7684\u7F18\u8D77, \u4E5F\u662F\u6211\u4E3A\u4EC0\u4E48\u5199\u8FD9\u4E2A\u7CFB\u5217\u7684\u7F18\u8D77.</p><p>2015 \u5E74\u7684\u65F6\u5019, \u6211\u5F00\u59CB\u4E86\u6211\u7684\u4EA7\u54C1\u751F\u6DAF. \u73B0\u5728\u770B\u6765\u662F\u4E00\u4EF6\u975E\u5E38\u5076\u7136\u7684\u4E8B\u60C5: \u5F53\u65F6\u516C\u53F8\u7684\u4E00\u4E2A\u4EA7\u54C1\u7ECF\u7406\u751F\u75C5\u4E86, \u800C\u4ED6\u6240\u5728\u7684\u9879\u76EE\u53C8\u5F88\u6025, \u6240\u4EE5\u5FC5\u987B\u8981\u5728\u4E1A\u52A1\u90E8\u5206\u4E2D\u9009\u4E00\u4E2A\u76F8\u5BF9\u6BD4\u8F83\u61C2\u6280\u672F\u7684\u4EBA\u9876\u4E0A\u53BB. \u8FD9\u4E2A\u4EBA\u5C31\u662F\u6211.</p><p>\u6240\u4EE5\u6211\u5E72\u4EA7\u54C1\u7ECF\u7406\u7B97\u662F\u534A\u8DEF\u51FA\u5BB6.</p><p>\u800C\u4E14, \u5728\u6211\u505A\u4EA7\u54C1\u7ECF\u7406\u7684\u8FD9\u51E0\u5E74\u6765, \u5E26\u8FC7\u6211\u7684\u5E08\u5085\u4E3B\u8981\u6709\u8FD9\u4E48\u51E0\u4F4D:</p><ul><li>\u7B2C\u4E00\u4E2A\u6559\u6211\u7684, \u662F\u4E00\u4E2A\u505A\u9879\u76EE\u7BA1\u7406\u7684\u5927\u54E5, \u59D3\u674E. \u624B\u628A\u624B\u7684\u6559\u6211, \u4E00\u4E2A\u91C7\u7528\u7011\u5E03\u6A21\u578B\u7684\u9879\u76EE\u5E94\u8BE5\u600E\u4E48\u4F5C. \u73B0\u5728\u8FD9\u4E2A\u5927\u54E5\u8FD8\u5728\u6D77\u822A\u505A\u5176\u4ED6\u9879\u76EE.</li><li>\u7B2C\u4E8C\u4E2A\u6559\u6211\u7684, \u662F\u4E00\u4E2A\u505A\u6280\u672F\u7684\u8001\u677F, \u59D3\u5468. \u6211\u4E00\u76F4\u4F7F\u7528 UML \u505A\u6D41\u7A0B\u8BBE\u8BA1, \u5C31\u662F\u53D7\u4ED6\u7684\u5F71\u54CD. \u73B0\u5728\u8FD9\u4E2A\u5927\u54E5\u81EA\u5DF1\u521B\u4E1A\u4E86.</li><li>\u7B2C\u4E09\u4E2A\u6559\u6211\u7684, \u662F\u4E00\u4E2A\u505A\u54A8\u8BE2\u7684\u535A\u58EB, \u59D3\u5E94. \u6211\u5BF9\u4E8E\u5546\u4E1A\u6A21\u5F0F, \u5206\u5DE5\u754C\u9762, \u4E1A\u52A1\u67B6\u6784\u7684\u89C2\u70B9, \u51E0\u4E4E\u90FD\u662F\u4ED6\u57F9\u517B\u8D77\u6765\u7684. \u73B0\u5728\u8FD9\u4E2A\u5927\u54E5\u5728\u5B66\u6821\u91CC\u6559\u4E66.</li></ul><p>\u53EF\u4EE5\u770B\u5230, \u5728\u6211\u505A\u4EA7\u54C1\u7ECF\u7406\u7684\u751F\u6DAF\u4E2D, \u6CA1\u6709\u4E00\u4E2A\u771F\u6B63\u5E72\u4EA7\u54C1\u7684\u4EBA, \u544A\u8BC9\u8FC7\u6211\u4EA7\u54C1\u7ECF\u7406\u662F\u4E2A\u4EC0\u4E48\u5C97\u4EEC, \u5E94\u8BE5\u5E72\u4E9B\u5565.</p><p>\u800C\u4E14\u81EA\u6211\u505A\u4EA7\u54C1\u7ECF\u7406\u4EE5\u6765, \u6211\u5C31\u4E00\u76F4\u5728\u505A\u533A\u5757\u94FE\u5E94\u7528.</p><p>\u6240\u4EE5\u5728\u6211\u7684\u5FC3\u7406\u5C31\u6709\u4E00\u4E2A\u975E\u5E38\u5927\u7684\u7126\u8651: \u6211\u6240\u79EF\u7D2F\u7684\u7ECF\u9A8C, \u6280\u80FD\u548C\u4EA7\u54C1\u8BBE\u8BA1\u65B9\u6CD5\u8BBA\u662F\u53EA\u80FD\u5728\u533A\u5757\u94FE\u884C\u4E1A\u91CC\u7528, \u8FD8\u662F\u666E\u9002\u7684.</p><p>\u8FD9\u4E2A\u7126\u8651\u5728 2018 \u5E74\u521D, \u8FBE\u5230\u4E86\u9876\u5CF0. \u6211\u4E0B\u5B9A\u51B3\u5FC3\u79BB\u5F00\u533A\u5757\u94FE\u884C\u4E1A, \u8981\u5728\u5176\u884C\u4E1A\u4E2D\u8BD5\u4E00\u8BD5.</p><p>\u800C\u300A\u542F\u793A\u5F55\u300B\u8FD9\u672C\u4E66, \u5C31\u662F\u5F53\u65F6\u9762\u8BD5\u6211\u7684\u4E00\u4F4D\u5927\u59D0\u63A8\u8350\u7684.</p><p>\u5F53\u65F6\u6211\u53BB\u9762\u8BD5, \u548C\u8FD9\u4E2A\u5927\u59D0\u804A\u4E86\u4E0D\u5230 5 \u5206\u949F, \u5C31\u5DF2\u7ECF\u786E\u5B9A\u6211\u4E0D\u9002\u5408\u4ED6\u4EEC\u6240\u62DB\u7684\u5C97\u4F4D\u4E86. \u6240\u4EE5\u540E\u9762\u5C31\u804A\u7684\u6BD4\u8F83\u653E\u98DE\u81EA\u6211.</p><p>\u6211\u63D0\u5230\u4E86\u6211\u7684\u7126\u8651\u548C\u56F0\u60D1. \u5927\u59D0\u8350\u8BAE\u6211\u53BB\u8BFB\u8FD9\u672C\u300A\u542F\u793A\u5F55\u300B.</p><p>\u6211\u4E70\u5230\u8FD9\u672C\u4E66\u540E, \u5C06\u4E66\u4E2D\u6240\u8BF4\u4E0E\u81EA\u5DF1\u7684\u7ECF\u5386\u4E00\u4E00\u6BD4\u5BF9, \u7EC8\u4E8E\u628A\u4E00\u9897\u7126\u8651\u7684\u5FC3\u653E\u56DE\u4E86\u8154\u5B50\u91CC.</p><p>\u6240\u4EE5\u8FD9\u672C\u4E66\u7B97\u662F\u6211\u7684 <strong>\u5B89\u5FC3\u4E4B\u7269</strong>. \u6240\u4EE5\u6211\u4E5F\u628A\u8FD9\u7BC7\u6587\u7AE0\u4F5C\u4E3A\u8FD9\u4E2A\u7CFB\u5217\u7684\u5F00\u7AEF.</p><hr><p>\u5982\u679C\u53EA\u770B\u300A\u542F\u793A\u5F55\u300B\u7684\u76EE\u5F55, \u4F60\u53EF\u80FD\u89C9\u5F97\u8FD9\u672C\u4E66\u7CFB\u7EDF\u7684\u8BB2\u4E86\u4E00\u4E2A\u4EA7\u54C1\u7ECF\u7406\u5982\u4F55\u505A\u51FA\u4E00\u4E2A\u4EA7\u54C1\u6765. \u4F46\u662F\u7B49\u4F60\u770B\u5230\u6B63\u6587\u7684\u65F6\u5019\u4F60\u4F1A\u53D1\u73B0, \u8FD9\u672C\u4E66\u7684\u4F53\u7CFB\u6027\u6CA1\u6709\u90A3\u4E48\u7684\u5F3A. \u5B83\u66F4\u8C61\u662F\u4E00\u90E8\u6587\u96C6: \u72EC\u7ACB\u5199\u4F5C\u7684\u6587\u7AE0, \u6309\u7167\u4E3B\u9898\u88AB\u7EC4\u7EC7\u8D77\u6765.</p><p>\u53E6\u5916, \u5F88\u663E\u7136\u4F5C\u8005\u559C\u6B22\u51D1\u6574. \u4EC0\u4E48\u5341\u6761\u89C4\u5F8B\u554A, \u5341\u4E2A\u5EFA\u8BAE\u554A. \u4E0D\u8981\u5F53\u771F, \u4E00\u770B\u5C31\u51D1\u51FA\u6765\u7684: \u6709\u7684\u4E24\u4E09\u6761\u8BF4\u7684\u662F\u4E00\u4E2A\u4E8B, \u6709\u7684\u53C8\u662F\u5BF9\u524D\u8FB9\u51E0\u6761\u7684\u603B\u7ED3.</p><h2 id="\u4EA7\u54C1\u8BBE\u8BA1\u7684\u5BF9\u8C61\u662F\u7528\u6237\u7684\u60C5\u611F" tabindex="-1"><a class="header-anchor" href="#\u4EA7\u54C1\u8BBE\u8BA1\u7684\u5BF9\u8C61\u662F\u7528\u6237\u7684\u60C5\u611F" aria-hidden="true">#</a> \u4EA7\u54C1\u8BBE\u8BA1\u7684\u5BF9\u8C61\u662F\u7528\u6237\u7684\u60C5\u611F</h2><p>\u4F5C\u4E3A\u4FE1\u606F\u4EA7\u4E1A, \u4E92\u8054\u7F51\u884C\u4E1A\u8131\u80CE\u4E8E IT \u884C\u4E1A\u800C\u4E0E IT \u884C\u4E1A\u6709\u7740\u672C\u8D28\u7684\u4E0D\u540C. IT \u884C\u4E1A\u63D0\u4F9B\u7684\u662F\u4FE1\u606F\u5316\u7CFB\u7EDF, \u800C\u4E92\u8054\u7F51\u63D0\u4F9B\u7684\u662F\u670D\u52A1.</p><p>\u6B63\u662F\u8FD9\u70B9\u4E0D\u540C, \u4F7F\u8BDE\u751F\u4E8E\u4E92\u8054\u7F51\u65F6\u4EE3\u7684\u4EA7\u54C1\u7ECF\u7406\u5177\u5907\u72EC\u6709\u7684\u7279\u8D28.</p><p>\u56E0\u4E3A IT \u884C\u4E1A\u63D0\u4F9B\u7684\u662F\u4FE1\u606F\u5316\u7CFB\u7EDF, \u6240\u4EE5\u5728 IT \u884C\u4E1A\u4E2D, \u901A\u5E38\u7684\u60C5\u51B5\u662F\u6211\u6709\u4E00\u4E2A\u4E1A\u52A1\u6A21\u5F0F, \u73B0\u5728\u53EA\u662F\u9700\u8981\u628A\u4ED6\u5B9E\u73B0\u51FA\u6765. \u6240\u4EE5\u5728 IT \u884C\u4E1A\u4E2D, \u8BBE\u8BA1\u5DE5\u4F5C\u6700\u7EC8\u662F\u8981\u7684\u662F\u63D0\u4F9B\u4E00\u4E2A\u5B9E\u73B0\u6A21\u578B: \u8BA9\u673A\u5668\u7406\u89E3\u4EBA\u8981\u505A\u7684\u4E8B.</p><p>\u800C\u5728\u4E92\u8054\u7F51\u65F6\u4EE3, \u5219\u53D1\u751F\u7740\u660E\u663E\u7684\u4E0D\u540C: \u6280\u672F\u65B9\u6848\u6781\u5927\u7684\u4E30\u5BCC\u4E86, \u6211\u4EEC\u7684\u4EFB\u52A1\u662F\u5229\u7528\u8FD9\u4E9B\u6280\u672F\u65B9\u6848\u4E3A\u4E0D\u7279\u5B9A\u7684\u5927\u591A\u6570\u63D0\u4F9B\u670D\u52A1. \u6240\u4EE5\u8BBE\u8BA1\u95EE\u9898\u7684\u6838\u5FC3\u79BB\u5F00\u4E86\u5B9E\u73B0\u6A21\u578B, \u53D8\u6210\u4E86\u6982\u5FF5\u6A21\u578B \u2014 \u4E5F\u5C31\u662F\u5E94\u7528\u573A\u666F. \u56E0\u4E3A\u53EA\u6709\u5728\u5B9E\u9645\u7684\u573A\u666F\u4E2D, \u4EBA\u624D\u53EF\u4EE5\u7406\u89E3\u7CFB\u7EDF\u80FD\u505A\u4EC0\u4E48.</p><p>\u800C\u5728\u300A\u542F\u793A\u5F55\u300B\u4E2D\u628A\u8FD9\u4E2A\u95EE\u9898\u6316\u7684\u66F4\u6DF1, \u4ED6\u4EE5 IPHONE \u4E3A\u4F8B, \u8C08\u5230\u82F9\u679C\u516C\u53F8\u7ED9\u4ED6\u7684\u56DB\u70B9\u542F\u793A:</p><ul><li>\u786C\u4EF6\u4E3A\u8F6F\u4EF6\u670D\u52A1;</li><li>\u8F6F\u4EF6\u4E3A\u7528\u6237\u4F53\u9A8C\u670D\u52A1;</li><li>\u7528\u6237\u4F53\u9A8C\u4E3A\u60C5\u611F\u670D\u52A1;</li><li>\u4EA7\u54C1\u4E3A\u771F\u6B63\u7684\u7528\u6237\u9700\u6C42\u670D\u52A1;</li></ul><p>\u4EA7\u54C1\u9762\u5BF9\u7684\u4E0D\u662F\u4E00\u4E2A\u6743\u529B\u4F53\u7CFB (\u5982\u6743\u529B\u7ED3\u6784\u5B8C\u5907\u7684\u516C\u53F8), \u800C\u662F\u9762\u5BF9\u7684\u6D3B\u751F\u751F\u7684\u4EBA. \u56E0\u6B64\u4EA7\u54C1\u8BBE\u8BA1\u662F\u8BD5\u56FE\u56DE\u7B54\u8FD9\u6837\u4E00\u7CFB\u5217\u7684\u95EE\u9898: \u6211\u4EEC\u8981\u5728\u4EC0\u4E48\u6837\u7684\u573A\u666F\u4E0B, \u901A\u8FC7\u4EC0\u4E48\u6837\u7684\u624B\u6BB5, \u4F7F\u90A3\u4E9B\u4EBA, \u4EA7\u751F\u4EC0\u4E48\u6837\u7684\u60C5\u611F, \u4EE5\u6B64\u8FBE\u5230\u4EC0\u4E48\u6837\u7684\u76EE\u7684.</p><p>\u300A\u542F\u793A\u5F55\u300B\u4EE5\u65B0\u80FD\u6E90\u6C7D\u8F66\u4E3A\u4F8B\u4F5C\u4E86\u7CBE\u91C7\u7684\u8BF4\u660E:</p><p>\u5728\u300A\u542F\u793A\u5F55\u300B\u6210\u4E66\u7684\u5E74\u4EE3\u91CC, \u65B0\u901A\u6E90\u6C7D\u8F66\u7684\u6280\u672F\u8FDC\u6CA1\u6709\u73B0\u5728\u6210\u719F, \u662F\u4E00\u79CD\u6210\u672C\u6781\u9AD8\u7684\u89E3\u51B3\u65B9\u6848, \u90A3\u4E3A\u4EC0\u4E48\u8FD8\u6709\u4EBA\u4F1A\u4E3A\u8FD9\u79CD\u89E3\u51B3\u65B9\u6848\u4E70\u5355\u5462?</p><blockquote><p>\u975E\u7406\u6027\u6D88\u8D39\u8005\u4E5F\u7518\u613F\u82B1\u4E24\u4E07\u7F8E\u91D1\u8D2D\u4E70\u666E\u9510\u65AF, \u56E0\u4E3A\u4ED6\u4EEC\u662F\u72C2\u70ED\u7684\u73AF\u4FDD\u4E3B\u4E49\u8005. \u4ED6\u4EEC\u5B8C\u5168\u53EF\u4EE5\u628A\u8D2D\u4E70\u666E\u9510\u65AF\u7684\u94B1\u7528\u5728\u522B\u7684\u5730\u65B9, \u6BD4\u5982\u4E70\u4E00\u8F86\u6469\u6258\u8F66, \u4E00\u6837\u53EF\u4EE5\u51CF\u5C11\u6E29\u5BA4\u6C14\u4F53\u6392\u653E\u505A\u8D21\u732E. \u4ED6\u4EEC\u6025\u4E8E\u89E3\u51B3\u95EE\u9898\u7684\u5FC3\u60C5\u4FC3\u4F7F\u4ED6\u4EEC\u4E3A\u6602\u8D35\u800C\u4E0D\u5B9E\u9645\u7684\u89E3\u51B3\u65B9\u6848\u4E70\u5355.</p></blockquote><p>\u6240\u4EE5:</p><ul><li>\u9996\u5148\u662F\u4E00\u79CD\u8EAB\u4EFD\u8BA4\u540C (\u5982 \u201C\u72C2\u70ED\u7684\u73AF\u4FDD\u4E3B\u4E49\u8005\u201D);</li><li>\u7136\u540E\u57FA\u4E8E\u8FD9\u79CD\u8EAB\u4EFD\u8BA4\u540C\u4EA7\u751F\u67D0\u79CD\u60C5\u611F\u8BC9\u6C42 (\u5982 \u201C\u6025\u4E8E\u89E3\u51B3\u95EE\u9898\u7684\u5FC3\u60C5\u201D)</li><li>\u6700\u540E\u5728\u8FD9\u79CD\u60C5\u611F\u8BC9\u6C42\u7684\u9A71\u52A8\u4E0B\u7528\u6237\u4F1A\u8FBE\u6210\u6211\u4EEC\u7684\u4EA7\u54C1\u76EE\u7684 (\u5982 \u201C\u4E3A\u6602\u8D35\u800C\u4E0D\u5B9E\u9645\u7684\u89E3\u51B3\u65B9\u6848\u4E70\u5355\u201D)</li></ul><p>\u4E3A\u4EC0\u4E48\u67D0\u77E5\u8BC6\u4ED8\u8D39\u7684\u4EA7\u54C1, \u7231\u79F0\u5176\u7528\u6237\u4E3A \u201C\u7231\u667A\u6C42\u771F\u7684\u5C0F\u4F19\u4F34\u201D, \u56E0\u4E3A\u4ED6\u4EEC\u7684\u4EA7\u54C1\u76EE\u6807, \u9700\u8981\u5728\u8FD9\u6837\u4E00\u79CD\u8EAB\u4EFD\u8BA4\u540C\u6240\u4EA7\u751F\u7684\u60C5\u611F\u8BC9\u6C42\u4E0B\u8FBE\u6210.</p><h2 id="\u652F\u6491\u4E0E\u5E72\u6270-\u5206\u5DE5\u754C\u9762" tabindex="-1"><a class="header-anchor" href="#\u652F\u6491\u4E0E\u5E72\u6270-\u5206\u5DE5\u754C\u9762" aria-hidden="true">#</a> \u652F\u6491\u4E0E\u5E72\u6270: \u5206\u5DE5\u754C\u9762</h2><p>\u4EA7\u54C1\u8BBE\u8BA1\u4EE5\u60C5\u611F\u4E3A\u5BF9\u8C61, \u4E5F\u88AB\u5176\u5B83\u8981\u7D20\u6240\u652F\u6491: \u4EBA\u4E0E\u6280\u672F.</p><p><strong>\u53EA\u6709\u771F\u5B9E\u7684\u4EBA, \u5728\u771F\u5B9E\u7684\u573A\u666F\u4E0B, \u4F7F\u7528\u771F\u5B9E\u7684\u6280\u672F, \u624D\u80FD\u4EA7\u751F\u771F\u5B9E\u7684\u60C5\u611F, \u5E76\u4EE5\u6B64\u771F\u6B63\u8FBE\u6210\u4EA7\u54C1\u7684\u76EE\u6807.</strong></p><ul><li>\u4F53\u73B0 \u201C\u4EBA\u201D \u7684\u8981\u7D20\u7684\u90E8\u95E8\u6709: \u5BA2\u670D, \u8FD0\u8425, \u9500\u552E\u7B49\u90E8\u95E8.</li><li>\u4F53\u73B0 \u201C\u6280\u672F\u201D \u7684\u8981\u7D20\u7684\u90E8\u95E8\u6709: \u6280\u672F, \u7814\u53D1, \u5F00\u53D1, \u9879\u76EE\u7B49\u90E8\u95E8.</li></ul><p>\u800C\u8FD9\u4E9B\u652F\u6491\u8981\u7D20, \u540C\u6837\u4E5F\u662F\u5E72\u6270\u8981\u7D20.</p><h3 id="\u4EBA\u5BF9\u4E8E\u4EA7\u54C1\u7684\u5E72\u6270" tabindex="-1"><a class="header-anchor" href="#\u4EBA\u5BF9\u4E8E\u4EA7\u54C1\u7684\u5E72\u6270" aria-hidden="true">#</a> \u4EBA\u5BF9\u4E8E\u4EA7\u54C1\u7684\u5E72\u6270</h3><h4 id="\u6DF7\u6DC6\u5BA2\u6237\u9700\u6C42\u548C\u4EA7\u54C1\u76EE\u6807" tabindex="-1"><a class="header-anchor" href="#\u6DF7\u6DC6\u5BA2\u6237\u9700\u6C42\u548C\u4EA7\u54C1\u76EE\u6807" aria-hidden="true">#</a> \u6DF7\u6DC6\u5BA2\u6237\u9700\u6C42\u548C\u4EA7\u54C1\u76EE\u6807</h4><p>\u6700\u5E38\u89C1\u7684\u4F8B\u5B50\u662F OA \u7CFB\u7EDF. \u8FD9\u4E2A\u4E16\u754C\u4E0A\u6CA1\u6709\u4E00\u4E2A OA \u7CFB\u7EDF\u662F\u88AB\u4ED6\u7684\u7528\u6237 (\u516C\u53F8\u5458\u5DE5) \u6240\u559C\u7231\u7684.</p><p>\u539F\u56E0\u5F88\u7B80\u5355: \u5458\u5DE5\u4F5C\u618E\u6068\u7684\u4E0D\u662F <strong>\u7528\u6307\u7EB9, \u5730\u7406\u4FE1\u606F, \u9762\u90E8\u8BA4\u522B\u6253\u5361</strong> , \u4ED6\u4EEC\u618E\u6068\u7684\u662F <strong>\u6253\u5361\u5236\u5EA6\u672C\u8EAB</strong> . \u4F46\u4ED6\u4EEC\u53C8\u4E0D\u80FD\u8BCB\u6BC1\u516C\u53F8, \u5492\u9A82\u8001\u677F, \u6240\u4EE5\u53EA\u80FD\u5C06\u6012\u6C14\u53D1\u6CC4\u5728 OA \u7CFB\u7EDF\u4E0A.</p><p>\u6240\u4EE5\u6211\u4EEC\u5982\u679C\u628A\u63D0\u5347\u4F7F\u7528\u8005\u7684\u5E78\u798F\u611F\u4F5C\u4E3A\u4EA7\u54C1\u76EE\u6807, \u6211\u4EEC\u7684\u4EA7\u54C1\u4F1A\u9677\u5165\u5230\u4E00\u4E2A\u75DB\u82E6\u7684\u5931\u8D25\u5FAA\u73AF\u4E2D\u53BB.</p><h3 id="\u4E3A\u4EC0\u4E48\u4EA7\u54C1\u9700\u6C42\u4E0D\u80FD\u7528\u6237\u8BF4\u4E86\u7B97" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u4EA7\u54C1\u9700\u6C42\u4E0D\u80FD\u7528\u6237\u8BF4\u4E86\u7B97" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u4EA7\u54C1\u9700\u6C42\u4E0D\u80FD\u7528\u6237\u8BF4\u4E86\u7B97</h3><p>\u5373\u4F7F\u7528\u6237\u9700\u6C42\u548C\u4EA7\u54C1\u76EE\u6807\u662F\u4E00\u81F4\u7684, \u7528\u6237\u9700\u6C42\u4E5F\u4E0D\u80FD\u7531\u7528\u6237\u8BF4\u4E86\u7B97. \u300A\u542F\u793A\u5F55\u300B\u5BF9\u6B64\u89E3\u91CA\u9053:</p><ul><li>\u5728\u770B\u5230\u4EA7\u54C1\u524D, \u7528\u6237\u5F88\u96BE\u77E5\u9053\u81EA\u5DF1\u9700\u8981\u4EC0\u4E48;</li><li>\u7528\u6237\u4E0D\u77E5\u9053\u4EC0\u4E48\u4EA7\u54C1\u662F\u53EF\u884C\u7684;</li><li>\u7528\u6237\u4E4B\u95F4\u7F3A\u5C11\u6C9F\u901A, \u5F88\u96BE\u8FBE\u6210\u7EDF\u4E00.</li></ul><p>\u8FD9\u4E2A\u9053\u7406\u5C31\u548C\u533B\u751F\u8981 <strong>\u6839\u636E\u75C5\u4EBA\u7684\u5177\u4F53\u60C5\u51B5\u8FDB\u884C\u6CBB\u7597, \u4F46\u4E0D\u80FD\u6839\u636E\u75C5\u4EBA\u7684\u63D0\u7684\u65B9\u6848\u8FDB\u884C\u6CBB\u7597</strong> \u4E00\u6837; \u53EA\u8981\u6211\u4EEC\u627F\u8BA4 <strong>\u4EA7\u54C1\u5DE5\u4F5C\u662F\u4E00\u4E2A\u4E13\u4E1A\u5DE5\u4F5C</strong> , \u4EA7\u54C1\u5DE5\u4F5C\u5C31\u5E94\u8BE5 <strong>\u4F9D\u9760\u4E13\u4E1A\u6280\u80FD</strong> \u800C\u4E0D\u662F\u7531\u7528\u6237\u8BF4\u4E86\u7B97.</p><h3 id="\u6280\u672F\u5BF9\u4E8E\u4EA7\u54C1\u7684\u5E72\u6270" tabindex="-1"><a class="header-anchor" href="#\u6280\u672F\u5BF9\u4E8E\u4EA7\u54C1\u7684\u5E72\u6270" aria-hidden="true">#</a> \u6280\u672F\u5BF9\u4E8E\u4EA7\u54C1\u7684\u5E72\u6270</h3><p>\u5C24\u5176\u662F\u6280\u672F\u9A71\u52A8\u7684\u516C\u53F8, \u4EA7\u54C1\u5F88\u5BB9\u6613\u88AB\u6280\u672F\u6240\u5E72\u6270. \u8FD9\u79CD\u5E72\u6270\u4E3B\u8981\u6765\u81EA\u4E8E <strong>\u201C\u53D1\u73B0\u65B0\u70ED\u70B9, \u5F00\u53D1\u65B0\u5E02\u573A\u7684\u51B2\u52A8\u201C</strong>.</p><p>\u8FD9\u91CC\u6211\u4EEC\u4E3E\u4E24\u4E2A\u516C\u53F8\u7684\u4F8B\u5B50\u6765\u8BF4\u660E.</p><h4 id="\u4EFB\u5929\u5802" tabindex="-1"><a class="header-anchor" href="#\u4EFB\u5929\u5802" aria-hidden="true">#</a> \u4EFB\u5929\u5802</h4><p>\u4EFB\u5929\u5802\u5BF9\u4E8E\u81EA\u5DF1\u7684\u5B9A\u4F4D\u662F\u73A9\u5177\u516C\u53F8, \u56E0\u6B64\u4EFB\u5929\u5802\u5F88\u5C11\u523B\u610F\u7684\u8FDB\u884C\u6280\u672F\u521B\u65B0.</p><blockquote><p>\u6211\u4EEC\u7684\u6307\u5BFC\u601D\u8DEF\u662F, \u600E\u6837\u53EF\u4EE5\u4E3A\u73A9\u5BB6\u5E26\u6765\u6109\u60A6\u7684\u60CA\u559C. \u6211\u4EEC\u5E76\u4E0D\u523B\u610F\u7684\u505A\u521B\u65B0, \u800C\u53EA\u662F\u5C1D\u8BD5\u627E\u5230\u8BA9\u4EBA\u4EEC\u5F00\u5FC3\u7684\u65B9\u6CD5, \u6240\u4EE5\u5F80\u5F80\u505A\u51FA\u5176\u4ED6\u4EEC\u6CA1\u6709\u505A\u51FA\u8FC7\u7684\u4E1C\u897F.</p><p>\u2014\u2014 \u4EFB\u5929\u5802\u7814\u53D1\u90E8\u603B\u7ECF\u7406</p></blockquote><p>\u5C31\u662F\u5728\u8FD9\u6837\u7684\u6307\u5BFC\u601D\u8DEF\u4E0B, \u4EFB\u5929\u5802\u7528\u786C\u7EB8\u76D2\u548C Switch \u521B\u9020\u51FA\u4E86 Labo \u2014 \u6CA1\u6709\u4EFB\u4F55\u7684\u521B\u65B0, \u4F46\u5E26\u6765\u524D\u6240\u672A\u6709\u7684\u521B\u65B0\u6E38\u620F\u4F53\u9A8C.</p><p>\u6240\u4EE5, \u521B\u65B0, \u5E76\u4E0D\u9700\u8981\u4EC0\u4E48 \u201C\u5413\u4EBA\u79D1\u6280\u201D.</p><h4 id="ibm" tabindex="-1"><a class="header-anchor" href="#ibm" aria-hidden="true">#</a> IBM</h4><p>\u5728\u524D\u6587\u63D0\u5230\u7684\u5E94\u535A\u58EB\u5E26\u7740\u6211\u4EEC\u505A\u8C03\u7814\u7684\u65F6\u5019, IBM \u51E0\u4E4E\u6210\u4E3A\u4E86\u6211\u4EEC\u4EA7\u54C1\u90E8\u7684\u5F00\u5FC3\u679C.</p><p>\u65E0\u8BBA\u6211\u4EEC\u5728\u8C03\u7814\u4EC0\u4E48\u65B0\u5174\u6280\u672F, \u90FD\u4F1A\u770B\u5230 IBM \u5728\u8BE5\u6280\u672F\u8BDE\u751F\u9636\u6BB5\u5C31\u51B2\u5165\u8FD9\u4E2A\u884C\u4E1A, <strong>\u6559\u80B2\u4E86\u5E02\u573A, \u57F9\u517B\u4E86\u4EBA\u624D, \u4F46\u5728\u5E02\u573A\u6210\u719F\u540E\u6D88\u58F0\u533F\u8FF9</strong>.</p><p>\u636E\u8BF4\u5728\u4E91\u8BA1\u7B97\u9886\u57DF, \u5728 PlayStation 2 \u7684\u65F6\u4EE3, IBM \u63D0\u51FA\u8FC7\u8FD9\u6837\u4E00\u4E2A\u8DEF\u7EBF:</p><p>\u5F53\u65F6\u7684\u56FE\u5F62\u6280\u672F\u5DF1\u7ECF\u975E\u5E38\u6210\u719F\u4E86, \u53EF\u4EE5\u4E3A\u6E38\u620F\u63D0\u4F9B\u66F4\u597D\u7684\u753B\u9762, \u4F46\u662F\u56FE\u5F62\u6280\u672F\u53D7\u5236\u4E8E\u786C\u4EF6\u7684\u8BA1\u7B97\u6027\u80FD. \u56E0\u6B64\u4ED6\u4EEC\u63D0\u51FA, \u670D\u52A1\u5668\u7684\u8BA1\u7B97\u80FD\u529B\u662F\u975E\u5E38\u5F3A\u5927\u7684, \u53EF\u4EE5\u5C06\u56FE\u5F62\u8BA1\u7B97\u7684\u529F\u80FD\u653E\u5230\u670D\u52A1\u5668, \u901A\u8FC7\u4E91\u8BA1\u7B97\u7684\u65B9\u6CD5\u6765\u8FDB\u884C.</p><p>\u60F3\u6CD5\u5F88\u597D, \u6280\u672F\u4E5F\u4E0D\u9519, \u4F46\u662F\u5F53\u65F6\u7F51\u7EDC\u73AF\u5883\u5E76\u4E0D\u80FD\u652F\u6491\u8FD9\u6837\u7684\u6280\u672F\u8DEF\u7EBF. \u6240\u4EE5\u8FD9\u6761\u6280\u672F\u8DEF\u7EBF\u81F3\u4ECA\u4E5F\u6CA1\u6709\u5B9E\u73B0.</p><h4 id="\u5206\u5DE5\u754C\u9762" tabindex="-1"><a class="header-anchor" href="#\u5206\u5DE5\u754C\u9762" aria-hidden="true">#</a> \u5206\u5DE5\u754C\u9762</h4><p>\u9053\u7406\u5927\u5BB6\u90FD\u61C2, \u4F46 <strong>\u4E3A\u4EC0\u4E48\u6709\u7684\u516C\u53F8\u5C31\u53EF\u4EE5\u907F\u514D\u4EBA\u4E0E\u6280\u672F\u5BF9\u4E8E\u4EA7\u54C1\u7684\u5E72\u6270, \u800C\u6709\u7684\u5206\u53F8\u5C31\u907F\u514D\u4E0D\u4E86\u5462?</strong></p><p>\u662F\u56E0\u4E3A\u5206\u5DE5\u754C\u9762.</p><p><strong>\u5982\u679C\u8BBE\u7F6E\u4E00\u4E2A\u72EC\u7ACB\u7684\u5C97\u4F4D\u6CA1\u6709\u4EC0\u4E48\u9053\u7406, \u90A3\u4E2A\u5C97\u4F4D\u5C31\u4E0D\u4F1A\u88AB\u72EC\u7ACB\u7684\u8BBE\u7F6E\u51FA\u6765.</strong></p><p>\u300A\u542F\u793A\u5F55\u300B\u4E00\u4E66, \u7EC6\u7EC6\u7684\u5206\u6790\u4E86, \u5982\u679C\u4EA7\u54C1\u5C97\u4F4D\u548C\u8425\u9500\u90E8\u5206\u6DF7\u6DC6\u4F1A\u4EA7\u751F\u4EC0\u4E48\u95EE\u9898:</p><p><img src="'+e+'" alt="\u4EA7\u54C1\u7BA1\u7406\u4E0E\u4EA7\u54C1\u8425\u9500\u4E0D\u662F\u4E00\u56DE\u4E8B"></p><p>\u4E5F\u5206\u6790\u4E86, \u5982\u67E5\u4EA7\u54C1\u5C97\u4F4D\u548C\u9879\u76EE/\u6280\u672F\u90E8\u95E8\u6DF7\u6DC6\u4F1A\u4EA7\u751F\u4EC0\u4E48\u95EE\u9898.</p><p><img src="'+t+'" alt="\u4EA7\u54C1\u7BA1\u7406\u4E0E\u9879\u76EE\u7BA1\u7406"></p><p>\u800C\u6B63\u662F\u5728\u4E0E\u5176\u5B83\u5C97\u4F4D\u7684\u5BF9\u6BD4\u4E2D, \u624D\u5B9A\u4E49\u4E86\u4EA7\u54C1\u5C97\u4F4D\u5230\u5E95\u662F\u4EC0\u4E48.</p><h2 id="\u8BBE\u8BA1\u4E00\u4E2A\u4EA7\u54C1" tabindex="-1"><a class="header-anchor" href="#\u8BBE\u8BA1\u4E00\u4E2A\u4EA7\u54C1" aria-hidden="true">#</a> \u8BBE\u8BA1\u4E00\u4E2A\u4EA7\u54C1</h2><p>\u597D\u4E86, \u6211\u4EEC\u77E5\u9053\u4E86\u4EA7\u54C1\u662F <strong>\u57FA\u4E8E\u771F\u5B9E\u7684\u9700\u6C42, \u5229\u7528\u7528\u6237\u7684\u60C5\u611F, \u5B9E\u73B0\u4EA7\u54C1\u76EE\u6807</strong>.</p><p>\u90A3\u4E48\u6211\u4EEC\u600E\u4E48\u4FDD\u8BC1\u6211\u4EEC\u7684\u8BBE\u8BA1\u662F <strong>\u57FA\u4E8E\u771F\u5B9E\u7684\u9700\u6C42\u5462</strong>?</p><p>\u300A\u542F\u793A\u5F55\u300B\u63D0\u51FA\u4E86\u4E00\u6574\u5957\u6D41\u7A0B. \u4F46\u628A\u6700\u91CD\u8981\u7684\u6982\u62EC\u8D77\u6765\u8BF4: <strong>\u524D\u671F\u505A\u597D\u7528\u6237\u7814\u7A76, \u540E\u671F\u505A\u597D\u539F\u578B\u6D4B\u8BD5</strong>.</p><h3 id="\u524D\u671F\u505A\u597D\u7528\u6237\u4F53\u9A8C" tabindex="-1"><a class="header-anchor" href="#\u524D\u671F\u505A\u597D\u7528\u6237\u4F53\u9A8C" aria-hidden="true">#</a> \u524D\u671F\u505A\u597D\u7528\u6237\u4F53\u9A8C</h3><p>\u524D\u671F\u7528\u6237\u8C03\u7814\u7684\u65B9\u6CD5\u4E3B\u8981\u5206\u4E3A\u4E09\u6B65:</p><ul><li>\u7EA6\u8C08\u7279\u7EA6\u7528\u6237</li><li>\u8FDB\u884C\u5E02\u573A\u8C03\u7814</li><li>\u5EFA\u7ACB\u7528\u6237\u753B\u8C61</li></ul><h4 id="\u7EA6\u8C08\u7279\u7EA6\u7528\u6237" tabindex="-1"><a class="header-anchor" href="#\u7EA6\u8C08\u7279\u7EA6\u7528\u6237" aria-hidden="true">#</a> \u7EA6\u8C08\u7279\u7EA6\u7528\u6237</h4><p><img src="'+s+'" alt="\u7279\u7EA6\u7528\u6237"></p><h4 id="\u8FDB\u884C\u5E02\u573A\u8C03\u7814" tabindex="-1"><a class="header-anchor" href="#\u8FDB\u884C\u5E02\u573A\u8C03\u7814" aria-hidden="true">#</a> \u8FDB\u884C\u5E02\u573A\u8C03\u7814</h4><p><img src="'+n+'" alt="\u5E02\u573A\u8C03\u7814"></p><h4 id="\u5EFA\u7ACB\u7528\u6237\u753B\u8C61" tabindex="-1"><a class="header-anchor" href="#\u5EFA\u7ACB\u7528\u6237\u753B\u8C61" aria-hidden="true">#</a> \u5EFA\u7ACB\u7528\u6237\u753B\u8C61</h4><p><img src="'+o+'" alt="\u4EA7\u54C1\u4EBA\u7269\u89D2\u8272"></p><hr><p>\u7528\u6237\u8C03\u7814\u662F\u4E3A\u4E86\u627E\u5230\u5E94\u7528\u573A\u666F.</p><p>\u5E94\u7528\u573A\u666F\u5C31\u597D\u8C61\u4EA7\u54C1\u529F\u80FD\u7684\u4E0A\u4E0B\u6587. \u4EA7\u54C1\u529F\u80FD\u662F\u6709\u5728\u5E94\u7528\u573A\u666F\u4E2D\u624D\u662F\u53EF\u4EE5\u7406\u89E3\u7684.</p><p>\u5BF9\u4E8E 2B \u7684\u4EA7\u54C1\u6211\u4EEC\u53EF\u4EE5\u627E\u5230\u5BA2\u6237\u7684 <strong>\u4E1A\u52A1\u5173\u952E\u4EBA</strong>, \u5BF9\u4E8E\u5DF2\u7ECF\u4E0A\u7EBF\u7684\u4EA7\u54C1, \u6216\u5DF2\u7ECF\u8FDB\u884C\u8FC7\u521D\u90E8\u52A8\u8425\u7684\u4EA7\u54C1, \u6211\u4EEC\u53EF\u4EE5\u627E\u5230 <strong>\u79CD\u5B50\u7528\u6237</strong>.</p><p>\u4F46\u66F4\u591A\u7684\u65F6\u5019, \u5E94\u7528\u573A\u666F\u662F\u901A\u8FC7\u8C03\u7814 <strong>\u884C\u4E1A\u524D\u8F88\u548C\u7ADE\u4E89\u5BF9\u624B\u83B7\u5F97\u7684</strong>. \u6559\u80B2\u7528\u6237\u6210\u672C\u662F\u5F88\u9AD8\u7684, \u6240\u4EE5\u5982\u679C\u6709\u4E0E\u4EA7\u54C1\u7684\u5E94\u7528\u573A\u666F\u76F8\u4F3C\u7684\u524D\u8F88, \u6700\u597D\u7684\u65B9\u5F0F\u5B66\u662F\u76F4\u63A5\u5411\u524D\u8F88\u5B66\u4E60. <strong>\u4E0D\u8981\u91CD\u65B0\u53D1\u660E\u8F66\u8F6E</strong>, \u5BF9\u4E8E\u4EA7\u54C1\u5DE5\u4F5C\u662F\u4E00\u6837\u7684.</p><h4 id="\u540E\u671F\u505A\u597D\u7528\u6237\u6D4B\u8BD5" tabindex="-1"><a class="header-anchor" href="#\u540E\u671F\u505A\u597D\u7528\u6237\u6D4B\u8BD5" aria-hidden="true">#</a> \u540E\u671F\u505A\u597D\u7528\u6237\u6D4B\u8BD5</h4><p>\u66FE\u7ECF\u5728\u7F51\u7EDC\u4E0A\u76DB\u4F20\u7740\u4E00\u79CD \u201C<strong>\u9AD8\u4FDD\u771F\u539F\u578B\u65E0\u7528\u8BBA</strong>\u201C \u8BA4\u4E3A, \u539F\u578B\u8BF4\u5230\u5E95\u662F\u4E00\u79CD\u5DE5\u5177. \u4E3B\u8981\u662F\u4E3A\u4E86\u5411\u5F00\u53D1\u4EBA\u5458\u8BF4\u660E, \u4EA7\u54C1\u662F\u4EC0\u4E48\u6837\u7684. \u56E0\u6B64\u53EA\u9700\u8981\u80FD\u8BF4\u660E\u95EE\u9898\u7684 <strong>\u7EBF\u6846\u56FE</strong> \u5C31\u53EF\u4EE5\u6EE1\u8DB3\u8FD9\u4E2A\u9700\u6C42. <strong>\u9AD8\u4FDD\u771F\u539F\u578B\u5403\u529B\u4E0D\u8BA8\u597D, \u8FD8\u53EF\u80FD\u4F1A\u8BEF\u5BFC UI \u8BBE\u8BA1\u4EBA\u5458</strong>.</p><p>\u66FE\u7ECF\u6211\u4E5F\u6DF1\u4EE5\u4E3A\u7136.</p><p>\u4F46\u8FD9\u4E2A\u7ED3\u8BBA\u662F\u4EE5 <strong>\u539F\u578B\u662F\u7ED9\u5F00\u53D1\u4EBA\u5458\u770B\u7684</strong> \u4E3A\u524D\u63D0\u7684.</p><p>\u300A\u542F\u793A\u5F55\u300B\u4E3B\u5F20\u4EA7\u54C1\u7ECF\u7406 <strong>\u4E00\u5B9A\u8981\u51FA\u9AD8\u4FDD\u771F\u539F\u578B</strong>. \u800C <strong>\u9AD8\u4FDD\u771F\u539F\u578B\u662F\u7528\u6765\u505A\u539F\u578B\u6D4B\u8BD5</strong> \u7684.</p><p><strong>\u539F\u578B\u6D4B\u8BD5</strong> \u662F\u7528\u6765\u6D4B\u8BD5\u6211\u4EEC\u4EA7\u54C1\u6240\u63D0\u4F9B\u7684\u5E94\u7528\u573A\u666F\u662F\u5426\u771F\u7684\u53EF\u4EE5\u88AB\u7528\u6237\u7406\u89E3: \u4E5F\u5373\u662F <strong>\u53EF\u7528\u6027\u6D4B\u8BD5</strong>.</p><p><strong>\u5982\u679C\u6211\u4EEC\u4E0D\u4F7F\u7528\u9AD8\u4FDD\u771F\u539F\u578B\u6765\u4F5C\u539F\u578B\u6D4B\u8BD5, \u90A3\u6211\u4EEC\u5B9E\u9645\u4E0A\u662F\u5728\u4F7F\u7528\u6211\u4EEC\u7684\u7B2C\u4E00\u7248\u6B63\u5F0F\u4E0A\u7EBF\u7684\u4EA7\u54C1\u5728\u4F5C\u53EF\u7528\u6027\u6D4B\u8BD5</strong>.</p><p>\u800C\u5982\u679C\u6211\u4EEC\u7528\u6B63\u5F0F\u4E0A\u7EBF\u7684\u4EA7\u54C1\u4F5C\u53EF\u7528\u6027\u6D4B\u8BD5, <strong>\u5373\u4F7F\u6211\u4EEC\u53D1\u73B0\u6211\u4EEC\u63D0\u4F9B\u4E86\u9519\u8BEF\u7684\u5E94\u7528\u573A\u666F, \u8003\u8651\u5230\u516C\u53F8\u653F\u6CBB, \u6211\u4EEC\u4E5F\u6CA1\u6709\u52C7\u6C14\u8FDB\u884C\u6539\u6B63, \u7ED3\u679C\u5C31\u4F1A\u662F\u5C06\u9519\u5C31\u9519\u7684\u5C06\u5C31\u7740</strong>.</p><p><img src="'+h+'" alt="\u4EA7\u54C1\u9A8C\u8BC1"></p><p><img src="'+l+'" alt="\u539F\u578B\u6D4B\u8BD5"></p>',98),c=[g];function u(_,b){return p(),a("div",null,c)}var f=r(d,[["render",u],["__file","\u300A\u542F\u793A\u5F55\u300B\u8BF4\u4E86\u5565.html.vue"]]);export{f as default};
