import{_ as r,o as p,c as a,e as o}from"./app.efeddd24.js";var t="/blog/img/project/shangyemoshi00/01.jpeg",i="/blog/img/project/shangyemoshi00/02.jpeg",s="/blog/img/project/shangyemoshi00/03.jpeg",e="/blog/img/project/shangyemoshi00/04.jpeg",n="/blog/img/project/shangyemoshi00/05.jpeg",h="/blog/img/project/shangyemoshi00/06.jpeg",g="/blog/img/project/shangyemoshi00/07.jpeg";const l={},d=o('<h1 id="\u300A\u5546\u4E1A\u6A21\u5F0F\u65B0\u751F\u4EE3\u300B\u8BF4\u4E86\u5565" tabindex="-1"><a class="header-anchor" href="#\u300A\u5546\u4E1A\u6A21\u5F0F\u65B0\u751F\u4EE3\u300B\u8BF4\u4E86\u5565" aria-hidden="true">#</a> \u300A\u5546\u4E1A\u6A21\u5F0F\u65B0\u751F\u4EE3\u300B\u8BF4\u4E86\u5565</h1><h2 id="\u4ECE\u654F\u6377\u65B9\u6CD5\u8BF4\u8D77" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u654F\u6377\u65B9\u6CD5\u8BF4\u8D77" aria-hidden="true">#</a> \u4ECE\u654F\u6377\u65B9\u6CD5\u8BF4\u8D77</h2><p><img src="'+t+'" alt="\u5C01\u9762"></p><p>2017 \u5E74\u7684\u65F6\u5019, \u6211\u6709\u5E78\u548C ThoughtWorks \u7684\u54A8\u8BE2\u56E2\u961F\u5728\u4E00\u4E2A\u9879\u76EE\u4E0A\u5408\u4F5C\u8FC7\u4E00\u6BB5\u65F6\u95F4. ThoughtWorks \u662F\u4E00\u5BB6\u505A\u654F\u6377\u65B9\u6CD5\u54A8\u8BE2\u548C\u654F\u6377\u5F00\u53D1\u7684\u516C\u53F8.</p><p>\u6B64\u524D, \u6211\u4E00\u76F4\u89C9\u5F97\u654F\u6377\u65B9\u6CD5\u662F\u90A3\u4E9B <strong>\u4E0D\u60F3\u5199\u6587\u6863</strong> \u7684\u61D2\u6C49\u53D1\u660E\u7684 <strong>\u4F4E\u914D\u7248\u7011\u5E03\u6A21\u578B</strong>. \u4F46\u662F\u548C ThoughtWork \u5408\u4F5C\u7684\u7ECF\u5386, \u5F7B\u5E95\u6539\u53D8\u4E86\u6211\u5BF9\u4E8E\u654F\u6377\u65B9\u6CD5\u7684\u770B\u6CD5.</p><p><strong>\u7011\u5E03\u6A21\u578B\u8FFD\u6C42\u6548\u7387, \u654F\u6377\u65B9\u6CD5\u8FFD\u6C42\u7075\u6D3B</strong>.</p><p>\u654F\u6377\u65B9\u6CD5\u662F\u7528\u727A\u7272\u4E00\u5B9A\u7684\u6548\u7387\u4E3A\u4EE3\u4EF7, \u6765\u6362\u53D6\u9762\u5BF9 <strong>\u4E0D\u786E\u5B9A\u9700\u6C42</strong> \u65F6\u7684\u7075\u6D3B\u6027.</p><p><strong>\u6240\u4EE5\u7011\u5E03\u6A21\u578B\u8FFD\u6C42\u5F62\u5F0F\u4E0A\u7684\u5171\u8BC6, \u654F\u6377\u65B9\u6CD5\u8FFD\u6C42\u5B9E\u8D28\u4E0A\u7684\u5171\u8BC6</strong>.</p><p>\u6BD4\u5982\u5728\u9762\u5BF9\u9700\u6C42\u7684\u65F6\u5019, \u7011\u5E03\u6A21\u578B\u7684\u601D\u8DEF\u662F, \u5728\u9700\u6C42\u5206\u6790\u9636\u6BB5\u5C31\u628A\u9700\u6C42\u5361\u6B7B, \u7136\u540E\u51FA\u4E2A\u6587\u6863, \u5927\u5BB6\u90FD\u5728\u6587\u6863\u4E0A\u7B7E\u5B57. \u7B49\u5230\u5B9E\u73B0\u7684\u65F6\u5019, \u8C01\u4E5F\u522B\u8DF3\u51FA\u6765\u8BF4, \u5F53\u65F6\u6211\u63D0\u7684\u9700\u6C42\u4E0D\u662F\u8FD9\u6837\u7684. \u800C\u654F\u6377\u65B9\u6CD5\u7684\u601D\u8DEF\u7684\u662F, \u5EFA\u7ACB\u4E00\u4E2A\u7D27\u5BC6\u548C\u9AD8\u9891\u6C9F\u901A\u7684\u5C0F\u56E2\u961F, \u8BA9\u5C0F\u56E2\u961F\u4E2D\u6240\u6709\u7684\u4EBA\u90FD\u771F\u6B63\u7684, \u4ECE\u5B9E\u8D28\u4E0A\u4E86\u89E3\u6211\u4EEC\u8981\u505A\u4EC0\u4E48. \u6240\u4EE5\u5BF9\u4E8E\u654F\u6377\u65B9\u6CD5, \u4E00\u4EFD\u5927\u5BB6\u90FD\u7B7E\u5B9A\u7684\u6587\u6863\u4E0D\u91CD\u8981, \u91CD\u8981\u7684\u662F\u5927\u5BB6\u662F\u4E0D\u662F\u4ECE\u5B9E\u8D28\u4E0A\u5F62\u6210\u4E86\u5171\u8BC6.</p><p>\u6240\u4EE5\u5BF9\u4E8E\u654F\u6377\u65B9\u6CD5, \u6709\u4E24\u70B9\u975E\u5E38\u91CD\u8981:</p><ul><li><strong>\u6240\u6709\u4EBA\u90FD\u771F\u6B63\u7684\u4E3A\u4E00\u4EF6\u4E8B\u8D1F\u8D23</strong>, \u5982\u679C\u56E2\u961F\u4E2D\u5927\u91CF\u5B58\u5728\u4EE5\u660E\u54F2\u4FDD\u8EAB\u4E3A\u7B2C\u4E00\u8BC9\u6C42\u7684\u4EBA, \u90A3\u4E48\u654F\u6377\u65B9\u6CD5\u53EA\u4F1A\u5E26\u6765\u65E0\u5C3D\u7684\u4E89\u5435\u548C\u56E2\u961F\u7684\u5206\u88C2;</li><li><strong>\u5FC5\u987B\u8981\u6709\u4E00\u79CD\u9AD8\u6548\u7684\u6C9F\u901A\u5DE5\u5177, \u8BA9\u56E2\u961F\u4E2D\u4E0D\u540C\u4E13\u4E1A\u7684\u4EBA, \u53EF\u4EE5\u5BF9\u540C\u4E00\u4EF6\u4E8B, \u8FBE\u6210\u5171\u901A\u7684\u8BA4\u77E5.</strong></li></ul><p>\u4E0A\u8FF0\u7B2C\u4E8C\u70B9, \u5C31\u8981\u6C42\u654F\u6377\u65B9\u6CD5\u5FC5\u987B\u6709\u4E00\u7CFB\u5217\u5C06\u62BD\u8C61\u6982\u5FF5\u53EF\u89C6\u5316\u7684\u6280\u672F.</p><p><strong>\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03</strong> \u5C31\u662F\u6211\u548C ThoughtWorks \u5408\u4F5C\u65F6\u5B66\u5230\u7684\u4E00\u79CD <strong>\u5BF9\u4E8E\u5546\u4E1A\u6A21\u5F0F\u5206\u6790\u7684\u53EF\u89C6\u5316\u6280\u672F</strong>. \u4ECE\u6B64\u4E3A\u6211\u6253\u5F00\u4E86\u65B0\u4E16\u754C\u7684\u5927\u95E8.</p><h2 id="\u5206\u6790\u800C\u975E\u521B\u9020\u5546\u4E1A\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u5206\u6790\u800C\u975E\u521B\u9020\u5546\u4E1A\u6A21\u5F0F" aria-hidden="true">#</a> \u5206\u6790\u800C\u975E\u521B\u9020\u5546\u4E1A\u6A21\u5F0F</h2><p>\u5173\u4E8E\u5546\u4E1A\u6A21\u5F0F, \u6211\u4ECE\u4E8B\u4EA7\u54C1\u8BBE\u8BA1\u5C97\u4F4D\u8FD9\u51E0\u5E74\u6765, \u6709\u4E00\u4E2A\u975E\u5E38\u6DF1\u523B\u7684\u4F53\u4F1A: <strong>\u9664\u975E\u4EA7\u54C1\u7ECF\u7406\u5C31\u662F\u516C\u53F8\u8001\u677F, \u5426\u5219\u522B\u7279\u4E48\u7684\u778E\u8BBE\u8BA1\u5546\u4E1A\u6A21\u5F0F</strong>.</p><p>\u56E0\u4E3A\u4E00\u4E2A\u5546\u4E1A\u6A21\u5F0F\u662F\u5426\u53EF\u4EE5\u6210\u529F, \u5E76\u4E0D\u5728\u4E8E\u6A21\u5F0F\u672C\u8EAB, \u800C\u5728\u4E8E <strong>\u5BF9\u8D44\u6E90\u7684\u63A7\u5236, \u5206\u914D\u548C\u7EC4\u5408</strong>. \u800C\u5BF9\u4E8E\u8FD9\u4E9B, \u9664\u975E\u662F\u8D44\u6E90\u7684\u638C\u63E1\u8005\u6216\u63D0\u4F9B\u8005, \u5426\u5219\u5F88\u83B7\u5F97\u771F\u5B9E\u7684\u5168\u91CF\u4FE1\u606F, \u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B, \u5BF9\u5546\u4E1A\u6A21\u5F0F\u8FDB\u884C\u8BBE\u8BA1, \u5F88\u5BB9\u6613\u8BBE\u8BA1\u51FA\u4E00\u4E2A\u770B\u7740\u6CA1\u4EC0\u4E48\u6BDB\u75C5, \u4F46\u5C31\u662F\u8FD0\u884C\u4E0D\u4E86\u7684\u5546\u4E1A\u6A21\u5F0F.</p><p>\u6211\u4E3E\u4E2A\u4F8B\u5B50:</p><p><img src="'+i+'" alt="\u6295\u878D\u754C"></p><blockquote><p>\u6211\u4ECE\u505A\u533A\u5757\u94FE\u7684\u8001\u4E1C\u5BB6\u51FA\u6765\u540E, \u505A\u8FC7\u4E00\u6BB5\u65F6\u95F4\u7684\u4F9B\u5E94\u94FE\u91D1\u878D\u9879\u76EE. \u4E3A\u6B64\u6211\u8C03\u7814\u4E86\u4E00\u4E9B\u884C\u4E1A\u524D\u8F88. \u5176\u4E2D\u6709\u4E00\u5BB6\u53EB\u505A\u878D\u6295\u754C\u7684\u7F51\u7AD9.</p><p>\u8FD9\u4E2A\u7F51\u7AD9\u5BF9\u6240\u6709\u7684\u4E1A\u52A1\u51E0\u4E4E\u4E0D\u505A\u63A7\u5236. \u4F60\u60F3\u770B\u8C01\u7684\u4FE1\u606F, \u5C31\u53EF\u4EE5\u770B\u8C01\u7684\u4FE1\u606F, \u60F3\u548C\u8C01\u8054\u7CFB, \u5C31\u53EF\u4EE5\u548C\u8C01\u8054\u7CFB.</p><p>\u8FD9\u5BF9\u4E8E\u4E00\u4E2A\u505A\u4FE1\u606F\u64AE\u5408\u7684\u7F51\u7AD9\u6765\u8BF4, \u662F\u975E\u5E38\u5947\u602A\u7684. \u56E0\u4E3A\u5982\u679C\u7528\u6237\u53EF\u4EE5\u4E0D\u82B1\u4E00\u5206\u94B1\u5728\u7F51\u7AD9\u4E0A\u83B7\u5F97\u4ED6\u4EEC\u9700\u8981\u7684\u4FE1\u606F, \u4F60\u5C31\u5F88\u96BE\u8BBE\u7F6E\u6536\u8D39\u70B9. \u90A3\u7F51\u7AD9\u8981\u9760\u4EC0\u4E48\u76C8\u5229\u5462? (\u4E0D\u662F\u9760\u5E7F\u544A\u554A, \u878D\u6295\u754C\u662F\u4E00\u975E\u5E38\u5E72\u51C0\u7684\u7F51\u7AD9, \u51E0\u4E4E\u770B\u4E0D\u5230\u4E0D\u76F8\u5E72\u7684\u4FE1\u606F.)</p><p>\u5F53\u6211\u8C03\u7814\u4ED6\u4EEC\u7684\u76C8\u5229\u6A21\u5F0F\u65F6, \u60CA\u8BB6\u7684\u53D1\u73B0\u4ED6\u4EEC\u4E3B\u8981\u7684\u76C8\u5229\u6A21\u5F0F\u662F\u505A\u4F1A: \u4ED6\u4EEC\u4F1A\u5B9A\u671F\u62DB\u96C6\u6295\u8D44\u8005\u5927\u4F1A, \u5982\u679C\u4F60\u6709\u8D44\u91D1\u9700\u6C42, \u8981\u60F3\u53C2\u52A0\u4F1A\u8BAE, \u4F60\u9700\u8981\u7F34\u4E00\u5B9A\u7684\u8D39\u7528.</p><p>\u4E8E\u662F\u67E5\u4E86\u8FD9\u4E2A\u7F51\u7AD9\u7684\u8FD0\u8425\u65B9, \u53D1\u73B0\u8FD0\u8425\u5546\u7684\u524D\u8EAB\u662F\u6D59\u6C5F\u7701\u6295\u8D44\u8005\u534F\u4F1A. \u81F3\u6B64\u6211\u624D\u604D\u7136\u5927\u609F.</p><p>\u6211\u5AB3\u5987\u66FE\u7ECF\u5728\u67D0\u5730\u65B9\u534F\u4F1A\u5446\u8FC7, \u4ED6\u4EEC\u7684\u65E5\u5E38\u5DE5\u4F5C\u662F\u529E\u6742\u5FD7, \u4F46\u6742\u5FD7\u672C\u8EAB\u4E0D\u8D5A\u94B1, \u800C\u662F\u4E3A\u4E86\u5EFA\u7ACB\u4E00\u79CD\u516C\u4FE1\u529B. \u771F\u6B63\u6323\u94B1\u7684\u5730\u65B9, \u662F\u5BF9\u516C\u4FE1\u529B\u7684\u53D8\u73B0, \u6BD4\u5982\u5236\u5B9A\u4F01\u4E1A\u6807\u51C6\u8FDB\u884C\u8BC4\u7EA7\u7B49.</p><p>\u6240\u4EE5\u878D\u6295\u754C\u7684\u7F51\u7AD9, \u5176\u5B9E\u662F\u76F8\u5F53\u4E8E\u4EE5\u524D\u534F\u4F1A\u529E\u7684\u6742\u5FD7, \u4ED6\u4EEC\u8DDF\u672C\u4E0D\u9760\u8FD9\u4E2A\u8D5A\u94B1.</p><p>\u540C\u65F6\u6211\u5728\u8C03\u7814\u7684\u65F6\u5019, \u4E5F\u770B\u5230\u65E0\u6570\u5B8C\u5168\u501F\u8350\u548C\u81F4\u656C\u878D\u6295\u754C\u7684\u7F51\u7AD9. \u4F46\u5176\u5B9E\u8FD9\u4E2A\u5546\u4E1A\u6A21\u5F0F\u662F\u6284\u4E0D\u8FC7\u53BB\u7684, \u9664\u975E\u4F60\u4E5F\u662F\u53EF\u4EE5\u901A\u8FC7\u53D8\u73B0\u516C\u4FE1\u529B\u6323\u94B1\u7684\u673A\u6784.</p></blockquote><p>\u6240\u4EE5, \u4EA7\u54C1\u7ECF\u7406\u9762\u5BF9\u5546\u4E1A\u6A21\u5F0F\u65F6, \u4E0D\u662F\u8981\u505A\u521B\u65B0, \u800C\u662F\u8981 <strong>\u80FD\u542C\u660E\u767D\u8001\u677F</strong>, \u8425\u9500\u4EBA\u5458\u6216\u5546\u52A1\u90E8\u95E8\u6240\u63CF\u7ED8\u7684\u5546\u4E1A\u6A21\u5F0F: <strong>\u5B83\u7531\u4EC0\u4E48\u8981\u7D20\u6784\u6210, \u8FD9\u6B64\u8981\u7D20\u5982\u4F55\u7EC4\u7EC7\u53C8\u5982\u4F55\u8FD0\u8F6C</strong>, \u800C\u4F60\u53C8\u8BE5\u5982\u4F55\u5C06\u8FD9\u4E9B\u5546\u4E1A\u6A21\u5F0F\u7684\u8981\u7D20\u8F6C\u5316\u6210\u4EA7\u54C1\u8BBE\u8BA1\u7684\u8981\u7D20.</p><p>\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03\u5C31\u662F\u4E00\u4E2A\u5206\u6790\u5546\u4E1A\u6A21\u5F0F\u7684\u53EF\u89C6\u5316\u5DE5\u5177. \u5F53\u7136\u5B83\u7684\u529F\u80FD\u4E0D\u53EA\u662F\u5206\u6790, \u4F46\u6211\u4EEC\u4E3B\u8981\u7528\u5B83\u6765\u5206\u6790.</p><h2 id="\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03" tabindex="-1"><a class="header-anchor" href="#\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03" aria-hidden="true">#</a> \u5546\u4E1A\u6A21\u5F0F\u753B\u5E03</h2><p><img src="'+s+'" alt="\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03"></p><p>\u8FD9\u5C31\u662F\u4E00\u4E2A\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03, \u5B83\u6709\u4E5D\u4E2A\u90E8\u5206\u6784\u6210:</p><ul><li><strong>\u4EF7\u503C\u4E3B\u5F20</strong>: \u4EF7\u503C\u4E3B\u5F20\u662F\u5546\u4E1A\u6A21\u5F0F\u7684\u6838\u5FC3, \u4EFB\u4F55\u4E00\u4E2A\u5546\u4E1A\u6A21\u5F0F, \u672C\u8D28\u4E0A\u90FD\u662F <strong>\u63D0\u4F9B\u4E00\u79CD\u4EF7\u503C</strong>, \u8FD9\u79CD\u4EF7\u503C\u53EF\u80FD\u662F <strong>\u89E3\u51B3\u67D0\u4E9B\u95EE\u9898, \u4F9B\u63D0\u67D0\u79CD\u670D\u52A1\u6216\u8282\u7EA6\u67D0\u4E9B\u6210\u672C</strong> \u7B49\u7B49. \u4EFB\u4F55\u5546\u4E1A\u6A21\u5F0F, \u9996\u5148\u5FC5\u987B\u662F\u6709\u7528\u7684.</li><li><strong>\u5BA2\u6237\u7EC6\u5206</strong>: \u6211\u4EEC\u63D0\u4F9B\u7684\u4EF7\u503C\u4E00\u5B9A\u662F\u6709\u5BF9\u8C61\u7684, \u4ED6\u4EEC\u5C31\u6211\u4EEC\u7684\u5BA2\u6237. \u4F46\u662F <strong>\u8BD5\u56FE\u8BA8\u597D\u6240\u6709\u4EBA, \u4E00\u5B9A\u8C01\u4E5F\u8BA8\u597D\u4E0D\u4E86</strong>. \u6240\u4EE5\u6211\u9700\u8981\u5C06\u6211\u4EEC\u7684\u4EF7\u503C\u4E3B\u5F20\u6240\u9488\u5BF9\u7684\u7279\u5B9A\u4EBA\u7FA4\u533A\u5206\u51FA\u6765, \u8FD9\u5C31\u662F\u5BA2\u6237\u7EC6\u5206.</li><li><strong>\u5BA2\u6237\u5173\u7CFB</strong> \u548C <strong>\u6E20\u9053\u901A\u8DEF</strong> \u662F\u4EF7\u503C\u4E3B\u5F20\u5230\u8FBE\u5BA2\u6237\u7684\u9014\u5F84, \u5BA2\u6237\u5173\u7CFB\u662F\u6211\u4EEC\u9700\u8981\u505A\u7684\u4E8B, \u6E20\u9053\u901A\u8DEF\u662F\u6211\u4EEC\u8981\u627E\u7684\u4EBA.</li><li>\u5F53\u4EF7\u503C\u4E3B\u5F20\u8FBE\u5230\u4E86\u5BA2\u6237\u7EC6\u5206, \u5C31\u4F1A\u4EA7\u751F <strong>\u6536\u5165\u6765\u6E90</strong>;</li><li><strong>\u91CD\u8981\u5408\u4F5C</strong>: \u662F\u4E0E\u6211\u4EEC\u5171\u540C\u4EA7\u751F\u4EF7\u503C\u4E3B\u5F20\u7684\u4F19\u4F34, \u901A\u5E38\u662F\u6211\u4EEC\u7684\u4F9B\u5E94\u5546\u6216\u5176\u5B83\u6211\u4EEC\u8981\u7ED9\u4ED6\u4EEC\u94B1\u7684 <strong>\u4E0A\u6E38\u4F01\u4E1A</strong>;</li><li><strong>\u6838\u5FC3\u8D44\u6E90</strong>: \u662F\u6211\u4EEC\u81EA\u5DF1\u638C\u63E1\u7684, \u53EF\u4EE5\u4EA7\u751F\u4EF7\u503C\u4E3B\u5F20\u7684\u8D44\u6E90;</li><li><strong>\u5173\u952E\u4E1A\u52A1</strong>: \u662F\u6211\u4EEC\u4E3A\u4E86\u4EA7\u751F\u4EF7\u503C\u4E3B\u5F20\u9700\u8981\u505A\u7684\u4E8B;</li><li>\u4E3A\u4E86\u7EF4\u7CFB\u91CD\u8981\u5408\u4F5C, \u6838\u5FC3\u8D44\u6E90\u548C\u5173\u952E\u4E1A\u52A1, \u5FC5\u7136\u4F1A\u6D88\u8017\u6210\u672C, \u8FD9\u5C31\u662F <strong>\u6210\u672C\u7ED3\u6784</strong>.</li></ul><p>\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03\u4E2D\u6240\u6709\u65E0\u7D20\u5FC5\u987B\u5F62\u6210\u4E00\u4E2A\u95ED\u73AF. \u6BD4\u5982:</p><ul><li>\u5982\u679C\u6211\u4EEC\u53D1\u73B0\u4E00\u4E2A\u4EF7\u503C\u4E3B\u5F20\u6CA1\u6709\u5BF9\u5E94\u7684\u7EC6\u5206\u5BA2\u6237, \u6216\u8005\u6709\u5BF9\u5E94\u7684\u7EC6\u5206\u5BA2\u6237, \u4F46\u662F\u6CA1\u6709\u5BA2\u6237\u5173\u7CFB\u548C\u6E20\u9053\u901A\u8DEF\u53EF\u4EE5\u8FBE\u5230\u7EC6\u5206\u5BA2\u6237. \u90A3\u4E48\u8BF4\u660E\u8FD9\u4E2A\u4EF7\u503C\u4E3B\u5F20\u662F\u4E00\u4E2A\u4F2A\u4EF7\u503C;</li><li>\u5982\u679C\u6211\u4EEC\u53D1\u73B0\u5408\u4F5C\u4F19\u4F34, \u6216\u5173\u952E\u4E1A\u52A1\u6D3B\u52A8\u4E0D\u652F\u6491\u4EFB\u4F55\u4EF7\u503C\u4E3B\u5F20, \u90A3\u4E48\u8FD9\u4E2A\u5408\u4F5C\u4F19\u4F34\u6216\u5173\u952E\u4E1A\u52A1\u5C31\u662F\u5728\u767D\u767D\u7684\u6D88\u8017\u6210\u672C.</li></ul><h2 id="\u64CD\u4F5C\u4E0E\u5B9E\u4F8B" tabindex="-1"><a class="header-anchor" href="#\u64CD\u4F5C\u4E0E\u5B9E\u4F8B" aria-hidden="true">#</a> \u64CD\u4F5C\u4E0E\u5B9E\u4F8B</h2><h3 id="\u57FA\u672C\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u57FA\u672C\u64CD\u4F5C" aria-hidden="true">#</a> \u57FA\u672C\u64CD\u4F5C</h3><p>\u90A3\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03\u662F\u5982\u4F55\u753B\u51FA\u6765\u7684?</p><p>\u5728\u6211\u4E0E ThoughtWorks \u5408\u4F5C\u7684\u8FC7\u7A0B\u4E2D, \u6211\u89C2\u5BDF\u5230\u4ED6\u4EEC\u8FD9\u6837\u505A\u7684:</p><ul><li>\u4E0E\u53EC\u96C6\u6240\u6709\u4EA7\u54C1\u5229\u76CA\u5173\u7CFB\u4EBA, \u5BF9\u5546\u4E1A\u6A21\u5F0F\u8FDB\u884C\u5934\u8111\u98CE\u66B4;</li><li>\u5728\u5934\u8111\u98CE\u66B4\u7684\u8FC7\u7A0B\u4E2D, \u5C06\u5173\u952E\u5B57\u5199\u5728\u4FBF\u5229\u8D34\u4E0A;</li><li>\u5728\u5934\u8111\u98CE\u66B4\u7ED3\u675F\u540E, \u753B\u51FA\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03;</li><li>\u5C06\u5199\u6709\u5173\u952E\u5B57\u7684\u5229\u8D34\u79FB\u5165\u753B\u5E03;</li><li>\u8FDB\u884C\u5206\u6790.</li></ul><h3 id="\u6211\u7684\u53D8\u901A" tabindex="-1"><a class="header-anchor" href="#\u6211\u7684\u53D8\u901A" aria-hidden="true">#</a> \u6211\u7684\u53D8\u901A</h3><p>\u4F46\u662F, \u540E\u6765\u6211\u5728\u5B9E\u9645\u4F7F\u7528\u8FD9\u4E2A\u5DE5\u5177\u7684\u65F6\u5019, \u5E76\u6CA1\u6709\u6309\u7167\u4E0A\u8FF0\u65B9\u5F0F\u8FDB\u884C, \u539F\u56E0\u662F:</p><ul><li>\u56E0\u4E3A\u5546\u4E1A\u6A21\u5F0F\u7684\u539F\u56E0\u7EC4\u7EC7\u5934\u8111\u98CE\u4EA7\u54C1\u5229\u76CA\u5173\u7CFB\u4EBA\u8FDB\u884C\u5934\u8111\u98CE\u66B4\u5B9E\u5728\u592A\u96BE\u4E86, (\u6211\u89C1\u8FC7\u7684\u6240\u6709\u8001\u677F\u51E0\u4E4E\u90FD\u8BA4\u4E3A\u81EA\u5DF1\u7684\u5546\u4E1A\u6A21\u5F0F\u7B80\u7B80\u5355\u5355, \u660E\u660E\u767D\u767D\u6839\u672C\u4E0D\u7528\u5206\u6790. \u4EC0\u4E48, \u4F60\u60F3\u5206\u6790\u6211\u4EEC\u7684\u5546\u4E1A\u6A21\u5F0F, \u4F60\u662F\u60F3\u8D28\u7591\u9886\u5BFC\u7684\u51B3\u7B56\u4E48.)</li><li>\u800C\u4E14, \u524D\u9762\u6211\u4E5F\u8BF4\u4E86, \u6211\u6839\u672C\u4E0D\u8BA4\u4E3A\u4EA7\u54C1\u7ECF\u7406\u5E94\u8BE5\u53BB\u8BBE\u8BA1\u5546\u4E1A\u6A21\u5F0F, \u5BF9\u4E8E\u6211\u4EEC\u6765\u8BF4, \u81EA\u5DF1\u641E\u6E05\u695A, \u5E76\u53EF\u4EE5\u6307\u5BFC\u6211\u4EEC\u7684\u8BBE\u8BA1\u5C31 OK \u4E86.</li></ul><h3 id="\u5B9E\u4F8B" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u4F8B" aria-hidden="true">#</a> \u5B9E\u4F8B</h3><p>\u4E0B\u9762\u6211\u5C31\u7528\u6211\u505A\u8FC7\u7684\u4E00\u4E2A\u4F9B\u5E94\u94FE\u91D1\u878D\u9879\u76EE\u4E3A\u4F8B, \u8BF4\u4E00\u4E0B\u6211\u662F\u5982\u4F55\u8FD0\u7528\u8FD9\u4E2A\u5DE5\u5177\u7684:</p><p>\u516C\u53F8\u5BF9\u4E8E\u8FD9\u4E2A\u9879\u76EE\u7684\u63CF\u8FF0\u662F\u8FD9\u6837\u7684:</p><blockquote><p>\u6211\u4EEC\u516C\u53F8\u7684\u6BCD\u516C\u53F8\u6709\u4F9B\u5E94\u94FE\u4E1A\u52A1, \u4E5F\u6709\u57FA\u4E8E\u4F9B\u5E94\u94FE\u4E1A\u52A1\u7684\u4FDD\u7406\u56E2\u961F. \u4F46\u8FD9\u4E9B\u4E1A\u52A1\u76EE\u524D\u90FD\u5728\u7EBF\u4E0B\u505A. \u6240\u4EE5\u73B0\u5728\u60F3\u628A\u4E1A\u52A1\u822C\u5230\u7EBF\u4E0A\u6765. \u8FD9\u6837\u9996\u5148\u53EF\u4EE5\u4F18\u5316\u4F9B\u5E94\u94FE\u4E1A\u52A1, \u63D0\u9AD8\u4F9B\u5E94\u94FE\u4E1A\u52A1\u6548\u7387; \u5176\u6B21\u53EF\u4EE5\u7559\u5B58\u4F9B\u5E94\u94FE\u6570\u636E, \u4E3A\u4E2D\u5C0F\u4F01\u4E1A\u589E\u4FE1. \u6211\u4EEC\u53EF\u901A\u8FC7\u5E94\u6536\u8D26\u6B3E\u878D\u8D44\u6216\u4FDD\u7406\u7684\u5F62\u5F0F\u4E3A\u4F9B\u5E94\u94FE\u4E2D\u7684\u4E2D\u5C0F\u4F01\u4E1A\u878D\u8D44, \u5E76\u4E14\u6536\u53D6\u878D\u8D44\u670D\u52A1\u8D39. \u5982\u679C\u53EF\u80FD, \u6211\u4EEC\u8FD8\u53EF\u4EE5\u5F00\u901A\u4F1A\u5458, \u4E3A\u4F1A\u5458\u63D0\u4F9B\u5176\u5B83\u7684\u878D\u8D44\u76F8\u5173\u7684\u7EBF\u4E0B\u670D\u52A1.</p><p>\u4EA7\u54C1\u7684\u524D\u671F\u7684\u8D44\u4EA7\u7AEF\u7528\u6237, \u5C31\u662F\u6BCD\u516C\u53F8\u65D7\u4E0B\u7684\u4F9B\u5E94\u94FE\u4F01\u4E1A; \u8D44\u91D1\u7AEF\u7528\u6237\u662F\u5DF1\u6709\u5408\u4F5C\u7684\u94F6\u884C. \u4F46\u672A\u6765\u4F1A\u8003\u8651\u5F00\u653E\u4F01\u4E1A\u6CE8\u518C, \u5E76\u4E14\u5F15\u5165\u5176\u5B83\u91D1\u878D\u673A\u6784.</p></blockquote><h3 id="\u5236\u4F5C\u753B\u5E03" tabindex="-1"><a class="header-anchor" href="#\u5236\u4F5C\u753B\u5E03" aria-hidden="true">#</a> \u5236\u4F5C\u753B\u5E03</h3><p>\u4E0E\u662F\u6211\u4EEC\u53EF\u4EE5\u4E0A\u8FF0\u63CF\u8FF0\u4E2D, \u63D0\u53D6\u51FA\u4E0B\u9762\u8FD9\u4E9B\u5173\u952E\u8BCD.</p><p><img src="'+e+'" alt="\u5173\u952E\u8BCD"></p><p>\u8BA9\u6211\u4EEC\u628A\u8FD9\u4E9B\u5173\u952E\u8BCD\u653E\u5165\u5230\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03\u4E2D:</p><p><img src="'+n+'" alt="\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03"></p><p>\u8BA9\u6211\u4EEC\u628A\u8FD9\u4E9B\u8981\u7D20\u8FDE\u8D77\u6765:</p><p><img src="'+h+'" alt="\u8FDE\u63A5\u5546\u8981\u8981\u7D20"></p><p>\u6211\u4EEC\u53EF\u4EE5\u770B\u5230</p><ul><li>\u6211\u4EEC\u770B\u5230\u4F9B\u5E94\u94FE\u4E1A\u52A1\u4F18\u5316\u5176\u5B9E\u662F\u4E00\u4E2A\u4F2A\u9700\u6C42, \u6211\u4EEC\u5B9E\u9645\u80FD\u63D0\u4F9B\u7684\u670D\u52A1\u5E76\u4E0D\u80FD\u8D77\u5230\u4F18\u5316\u4F9B\u5E94\u94FE\u4E1A\u52A1\u7684\u76EE\u7684.</li><li>\u4E1A\u52A1\u8FD0\u8425\u7684\u8D39\u7528\u4E0D\u5E94\u5F53\u7B97\u5230\u4EA7\u54C1\u6210\u672C\u7406, \u56E0\u4E3A\u4E0D\u505A\u8FD9\u4E2A\u4EA7\u54C1, \u6BCD\u516C\u53F8\u4E5F\u4E0D\u4F1A\u505C\u6389\u4F9B\u5E94\u94FE\u4E1A\u52A1\u548C\u4FDD\u7406\u4E1A\u52A1.</li></ul><p>\u6240\u4EE5\u753B\u5E03\u6700\u7EC8\u662F\u8FD9\u6837\u7684:</p><p><img src="'+g+'" alt="\u5546\u4E1A\u6A21\u5F0F\u5206\u6790\u7ED3\u679C"></p><p>\u8FD9\u6837\u7684\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03\u5BF9\u4E8E\u4EA7\u54C1\u8BBE\u8BA1\u6709\u4EC0\u4E48\u7528\u5462? \u8FD9\u4E2A\u753B\u5E03\u5728\u5B9E\u9645\u4E2D\u4EA7\u751F\u4E86\u8FD9\u6837\u4E24\u4E2A\u91CD\u8981\u7684\u4F5C\u7528:</p><h4 id="\u4E0D\u7528\u505A\u4F9B\u5E94\u94FE\u4E1A\u52A1\u4F18\u5316\u4E86" tabindex="-1"><a class="header-anchor" href="#\u4E0D\u7528\u505A\u4F9B\u5E94\u94FE\u4E1A\u52A1\u4F18\u5316\u4E86" aria-hidden="true">#</a> \u4E0D\u7528\u505A\u4F9B\u5E94\u94FE\u4E1A\u52A1\u4F18\u5316\u4E86</h4><p>\u9700\u6C42\u8C03\u7814\u9636\u6BB5, \u4E1A\u52A1\u90E8\u5206\u7ED9\u5230\u6211\u5341\u51E0\u4EFD\u4E0D\u540C\u884C\u4E1A\u7684\u4F9B\u9500\u5408\u540C, \u4E0D\u540C\u7684\u4F9B\u9500\u5408\u540C\u7684\u6240\u91C7\u7528\u7684\u4ED8\u6B3E\u4E0E\u4EA4\u8D27\u6D41\u7A0B\u90FD\u4E0D\u5C3D\u76F8\u540C.</p><p>\u5982\u679C\u6211\u4EEC\u8981\u505A\u4F9B\u5E94\u94FE\u4E1A\u52A1\u4F18\u5316, \u6211\u7684\u8BA2\u5355\u6A21\u5757\u8981\u652F\u6301\u6240\u6709\u7684\u4ED8\u6B3E\u548C\u4EA4\u8D27\u6D41\u7A0B. \u641E\u4E0D\u597D\u8FD8\u8981\u53BB\u5BF9\u63A5\u7A0E\u52A1\u7CFB\u7EDF. \u8FD9\u4E2A\u5DE5\u7A0B\u53EF\u5C31\u5927\u4E86\u53BB\u4E86.</p><p>\u4F46\u662F\u6211\u4EEC\u770B\u5230, \u5176\u5B9E\u8FD9\u4E2A\u8BC9\u6C42, \u5BF9\u4E8E\u6574\u4E2A\u5546\u4E1A\u6A21\u5F0F\u6CA1\u6709\u4EC0\u4E48\u5375\u7528.</p><p>\u6240\u4EE5\u6211\u7684\u4EFB\u52A1\u662F, \u8BF4\u670D\u9886\u5BFC\u653E\u5F03\u4F9B\u5E94\u94FE\u4E1A\u52A1\u4F18\u5316\u7684\u9700\u6C42, \u6216\u8005\u628A\u8FD9\u4E2A\u9700\u6C42\u653E\u5230\u672A\u6765\u6765\u5B9E\u73B0. \u6211\u4EEC\u5148\u96C6\u4E2D\u7CBE\u529B, \u8BA9\u4F9B\u5E94\u94FE\u516C\u53F8\u53EF\u4EE5\u628A\u4F9B\u5E94\u94FE\u6570\u636E\u653E\u5230\u5E73\u53F0\u4E0A\u6765.</p><p>\u6700\u540E\u7684\u7ED3\u679C\u662F, \u6211\u8BF4\u670D\u9886\u5BFC\u4E86.</p><h4 id="\u6211\u4EEC\u5176\u5B9E\u8FDE\u878D\u8D44\u4E1A\u52A1\u90FD\u4E0D\u9700\u8981\u8FDB\u884C\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#\u6211\u4EEC\u5176\u5B9E\u8FDE\u878D\u8D44\u4E1A\u52A1\u90FD\u4E0D\u9700\u8981\u8FDB\u884C\u63A7\u5236" aria-hidden="true">#</a> \u6211\u4EEC\u5176\u5B9E\u8FDE\u878D\u8D44\u4E1A\u52A1\u90FD\u4E0D\u9700\u8981\u8FDB\u884C\u63A7\u5236</h4><p>\u524D\u9762\u6211\u5728\u8BF4\u5BF9\u878D\u6295\u754C\u7684\u8C03\u7814\u7684\u65F6\u5019, \u6211\u8BF4\u8FC7, \u4F5C\u4E3A\u4E00\u4E2A\u4FE1\u606F\u64AE\u5408\u5E73\u53F0, \u5982\u679C\u4E0D\u5BF9\u4FE1\u606F\u8FDB\u884C\u63A7\u5236, \u8BA9\u53CC\u65B9\u53EF\u4EE5\u968F\u610F\u83B7\u53D6\u4FE1\u606F, \u90A3\u4E48\u5F88\u96BE\u8BBE\u7F6E\u6536\u8D39\u70B9: \u56E0\u4E3A\u7528\u6237\u5B8C\u5168\u53EF\u4EE5\u83B7\u53D6\u4E86\u4FE1\u606F\u540E, \u8DF3\u8FC7\u5E73\u53F0\u81EA\u5DF1\u4EA4\u6613.</p><p>\u4F46\u6CE8\u610F\u6211\u4EEC\u7684\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03, \u6211\u4EEC\u4E00\u70B9\u90FD\u4E0D\u62C5\u5FC3\u4F9B\u5E94\u94FE\u4F01\u4E1A\u8DF3\u8FC7\u6211\u4EEC\u53BB\u63A5\u89E6\u94F6\u884C. \u56E0\u4E3A\u4ED6\u4EEC\u5C31\u662F\u56E0\u4E3A\u81EA\u8EAB\u7684\u6761\u4EF6\u5230\u4E0D\u5230\u94F6\u884C\u7684\u653E\u6B3E\u6761\u4EF6\u624D\u6765\u505A\u4F9B\u5E94\u94FE\u91D1\u878D\u7684, \u6240\u4EE5\u4ED6\u4EEC\u8DF3\u8FC7\u6211\u4EEC\u81EA\u5DF1\u53BB\u627E\u94F6\u884C\u4E00\u70B9\u610F\u4E49\u90FD\u6CA1\u6709.</p><p>\u90A3\u8FD9\u610F\u5473\u7740\u4EC0\u4E48\u5462?</p><p>\u8FD9\u610F\u5473\u7740\u6211\u53EF\u4EE5\u5927\u80C6\u7684\u5411\u878D\u6295\u754C\u501F\u8350\u548C\u81F4\u656C\u4E86.</p><h4 id="\u662F\u5426\u5C06\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03\u4F5C\u4E3A\u5DE5\u4F5C\u4EA7\u51FA" tabindex="-1"><a class="header-anchor" href="#\u662F\u5426\u5C06\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03\u4F5C\u4E3A\u5DE5\u4F5C\u4EA7\u51FA" aria-hidden="true">#</a> \u662F\u5426\u5C06\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03\u4F5C\u4E3A\u5DE5\u4F5C\u4EA7\u51FA</h4><p>\u5728\u548C ThoughtWorks \u5408\u4F5C\u65F6, \u4ED6\u4EEC\u662F\u5C06\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03\u4F5C\u4E3A\u5DE5\u4F5C\u4EA7\u51FA\u63D0\u4EA4\u7684.</p><p>\u4F46\u662F\u6211\u4E0D\u5EFA\u8BAE\u8FD9\u6837, \u5C24\u5176\u662F\u6CA1\u6709\u505A\u96C6\u4F53\u7684\u5934\u8111\u98CE\u66B4, \u53EA\u662F\u628A\u8FD9\u5B83\u5F53\u6210\u4E2A\u4EBA\u5206\u6790\u5546\u4E1A\u6A21\u5F0F\u7684\u7684\u5DE5\u5177\u65F6.</p><p>\u56E0\u4E3A\u4E2A\u5DE5\u5177\u672C\u8D28\u4E0A\u662F\u4E00\u4E2A\u5206\u6790\u5DE5\u5177, \u800C\u4E0D\u662F\u6C47\u62A5\u5DE5\u5177. \u6C47\u62A5\u5DE5\u5177\u6700\u597D\u662F\u4E00\u4E2A\u9759\u6001\u7684\u7ED3\u679C. \u800C\u5546\u4E1A\u6A21\u5F0F\u753B\u5E03, \u53EA\u6709\u5728\u753B\u7684\u8FC7\u7A0B\u4E2D, \u624D\u80FD\u611F\u53D7\u5230\u5B83\u7684\u610F\u4E49.</p><p>\u5982\u679C\u8D38\u7136\u628A\u753B\u5E03\u653E\u8FDB\u4F60\u7684\u6587\u6863\u4E2D, \u53EA\u4F1A\u8BA9\u8BFB\u8005\u89C9\u5F97\u83AB\u660E\u5176\u5999.</p><p>\u4F46\u662F\u5982\u679C\u4F60\u6309\u7167 ThoughtWorks \u4E00\u6837\u505A\u4E86\u5934\u8111\u98CE\u66B4, \u800C\u6587\u6863\u7684\u8BFB\u8005\u53C8\u53C2\u52A0\u8FC7, \u90A3\u4E48\u628A\u753B\u5E03\u653E\u5165\u6587\u6863\u4F1A\u662F\u4E00\u4E2A\u597D\u4E3B\u610F, \u56E0\u4E3A\u8BFB\u8005\u5728\u770B\u5230\u753B\u5E03\u65F6\u53EF\u4EE5\u56DE\u60F3\u8D77\u521B\u9020\u753B\u5E03\u7684\u8FC7\u7A0B.</p>',68),c=[d];function _(u,m){return p(),a("div",null,c)}var f=r(l,[["render",_],["__file","\u300A\u5546\u4E1A\u6A21\u5F0F\u65B0\u751F\u4EE3\u300B\u8BF4\u4E86\u5565.html.vue"]]);export{f as default};
