import{_ as p,o as a,c as e,e as t}from"./app.6bf529a6.js";const o={},r=t('<h1 id="\u4E00" tabindex="-1"><a class="header-anchor" href="#\u4E00" aria-hidden="true">#</a> \u4E00</h1><p>\u6211\u5988\u5C0F\u65F6\u5019\u5F97\u8FC7\u80BA\u708E. \u5F53\u65F6\u5170\u5DDE\u9644\u8FD1\u7684\u5C71\u533A, \u6709\u975E\u5E38\u5389\u5BB3\u4E13\u7528\u9488\u7078\u6CBB\u80BA\u708E\u7684\u4E2D\u533B. \u4E8E\u662F\u5916\u516C\u4FBF\u80CC\u7740\u5979\u53BB\u6C42\u533B.</p><p>\u4E34\u6668\u65E2\u53BB, \u873F\u5EF6\u7684\u5C71\u8DEF\u4E0A\u5DF2\u6392\u6EE1\u4E86\u95EE\u8BCA\u7684\u75C5\u60A3. \u5F80\u5F80\u6392\u4E0A\u4E00\u5929, \u4E5F\u672A\u5FC5\u89C1\u5F97\u7740\u90A3\u795E\u533B.</p><p>\u5916\u516C\u662F\u65E0\u4EA7\u9636\u7EA7\u6218\u58EB, \u5411\u6765\u4FE1\u5949\u4E3B\u5E2D\u81EA\u5DF1\u52A8\u624B\u4E30\u8863\u8DB3\u98DF\u7684\u7CBE\u795E. \u4E00\u6012\u4E4B\u4E0B\u4FBF\u81EA\u5B66\u9488\u7078. \u4ECE\u81EA\u5DF1\u624B\u811A\u4E0A\u4E0D\u5F88\u8981\u7D27\u7684\u7A74\u4F4D\u5F00\u59CB, \u76F4\u5230\u624E\u4FBF\u5468\u8EAB, \u7EC8\u4E8E\u81EA\u5B66\u6210\u624D, \u81EA\u5DF1\u7528\u9488\u7099\u624E\u597D\u4E86\u6211\u5988\u7684\u80BA\u708E.</p><p>\u81EA\u6B64, \u5BB6\u91CC\u4EBA\u8981\u662F\u6709\u5934\u75BC\u8111\u70ED\u7684\u5C0F\u75C5, \u90FD\u662F\u4ED6\u7528\u81EA\u5B66\u7684\u9488\u7078\u624E\u597D\u7684. \u751A\u81F3\u90BB\u91CC\u8981\u662F\u6709\u75C5, \u5927\u90FD\u5148\u6765\u627E\u4ED6. \u5982\u679C\u771F\u662F\u624E\u4E0D\u597D, \u518D\u53BB\u6B63\u89C4\u533B\u9662.</p><p>\u76F4\u5230\u524D\u4E24\u5E74, \u4ED6\u7ED9\u6211\u5916\u5A46\u624E\u9488, \u4E0D\u614E\u628A\u80BA\u6CE1\u624E\u7834\u4E86. \u6B64\u540E\u5C31\u518D\u4E5F\u4E0D\u7ED9\u522B\u4EBA\u624E\u9488\u4E86.</p><p>\u5916\u516C\u66FE\u60F3\u628A\u8FD9\u624B\u827A\u4F20\u7ED9\u6211\u5988. \u4F46\u5979\u5B9E\u5728\u4E0B\u4E0D\u53BB\u624B\u628A\u94F6\u9488\u624E\u8FDB\u8089\u91CC, \u6240\u4EE5\u81F3\u4ECA\u4E5F\u6CA1\u5B66\u6210.</p><p>\u4F46\u6211\u4EEC\u5BF9\u4E8E\u4E2D\u533B\u5374\u5EFA\u7ACB\u4E86\u57FA\u672C\u7684\u4FE1\u5FF5. \u867D\u7136\u624E\u9488\u662F\u4E0D\u6562\u624E, \u4F46\u5BB6\u4E2D\u5374\u5E38\u5907\u7740\u827E\u8349, \u4E0D\u8212\u670D\u4E86\u968F\u65F6\u90FD\u80FD\u7099\u4E00\u7099.</p><h1 id="\u4E8C" tabindex="-1"><a class="header-anchor" href="#\u4E8C" aria-hidden="true">#</a> \u4E8C</h1><p>\u5176\u5B9E\u8BF4\u6765, &quot;\u5BF9\u4E8E\u4E2D\u533B\u5EFA\u7ACB\u4E86\u57FA\u672C\u7684\u4FE1\u5FF5&quot;, \u8FD9\u53E5\u8BDD\u672C\u8EAB\u5176\u5B9E\u5C31\u5F88\u5947\u602A.</p><p>\u5728\u6211\u5AB3\u5987\u5BB6, \u7518\u8083\u7701\u767D\u94F6\u5E02\u5317\u6E7E\u9547\u9648\u5BB6\u6751, \u9AA8\u4F24\u627E\u767D\u5BB6, \u70E7\u4F24\u627E\u9B4F\u5BB6, \u8FD9\u662F\u60F3\u90FD\u4E0D\u8981\u60F3\u7684\u4E8B, \u54EA\u9700\u8981\u4EC0\u4E48 &quot;\u57FA\u672C\u7684\u4FE1\u5FF5&quot;.</p><p>\u767D\u5BB6\u6B63\u9AA8\u7684\u624B\u827A\u4F20\u4E86\u4E09\u4E94\u4EE3, \u8001\u5934\u5B50\u5E74\u7EAA\u5927\u4E86, \u4E0D\u8F7B\u6613\u51FA\u624B. \u513F\u5B50\u4E0D\u60F3\u63A5\u73ED, \u597D\u5728\u513F\u5AB3\u80AF\u5B66, \u4E0D\u81F3\u4E8E\u4F7F\u624B\u827A\u5931\u4F20.</p><p>\u9B4F\u5BB6\u8893\u4F20\u7684\u6CBB\u70EB\u4F24\u7684\u65B9\u5B50, \u636E\u8BF4\u662F\u62FF\u737E\u6CB9\u71AC\u5236\u7684. \u867D\u7136\u4E5F\u4E0D\u77E5\u9053\u4ED6\u4EEC\u4ECE\u54EA\u91CC\u641E\u5230\u7684\u737E\u6CB9, \u4F46\u5C31\u662F\u51FA\u5947\u7684\u6709\u6548. \u6211\u5927\u95F0\u5973\u4E0D\u6EE1\u4E00\u5C81\u65F6\u6253\u7FFB\u521A\u76DB\u7684\u7A00\u996D, \u817F\u4E0A\u70EB\u7684\u60E8\u4E0D\u5FCD\u7779. \u4E08\u6BCD\u5A18\u5E26\u7740\u6211\u4EEC, \u53BB 50 \u5757\u94B1\u4E70\u4E86\u4E00\u5C0F\u74F6. \u800C\u4ECA\u5927\u95F0\u5973\u817F\u4E0A\u5DF2\u5B8C\u5168\u770B\u4E0D\u51FA\u70EB\u4F24\u7684\u75D5\u8FF9.</p><p>\u6751\u4EBA\u8BF4\u8D77\u8FD9\u4E9B\u4E8B\u60C5, \u5C31\u50CF\u8BF4\u8D77\u5C0F\u533A\u91CC\u8C01\u7684\u714E\u997C\u679C\u5B50\u91CF\u8DB3, \u8857\u5BF9\u9762\u54EA\u5BB6\u6DEE\u5357\u725B\u8089\u6C64\u5473\u6B63\u4E00\u6837\u81EA\u7136. \u54EA\u91CC\u9700\u8981\u4EC0\u4E48 &quot;\u57FA\u7840\u4FE1\u5FF5&quot;.</p><p>\u5BF9\u4E8E\u4ED6\u4EEC\u6765\u8BF4, \u5927\u6982\u8C01\u751F\u75C5\u4E86\u90FD\u4E0D\u4F1A\u53BB\u60F3\u4EC0\u4E48\u4E2D\u897F\u533B\u4E4B\u4E89, \u4EC0\u4E48\u75C5\u5C31\u627E\u54EA\u5BB6\u4EBA, \u5B9E\u5728\u6CA1\u64A4\u4E86\u624D\u53BB\u7701\u57CE\u5927\u533B\u9662\u770B.</p><p>\u8FD9 &quot;\u5B9E\u5728\u6CA1\u64A4\u4E86&quot; \u7684\u52B2\u50CF\u6781\u4E86 &quot;\u4E0D\u884C\u7684\u8BDD\u53BB\u8BB2\u4E2A\u8FF7\u4FE1&quot;.</p><h1 id="\u4E09" tabindex="-1"><a class="header-anchor" href="#\u4E09" aria-hidden="true">#</a> \u4E09</h1><p>\u6240\u4EE5, \u6BCF\u5F53\u6211\u7ED9\u6211\u5988\u8BB2\u8D77\u6D41\u884C\u7684\u53CD\u5BF9\u4E2D\u533B\u7684\u6D41\u884C\u89C2\u70B9\u65F6, \u603B\u80FD\u6536\u83B7\u4E00\u53E5 &quot;\u653E\u5C41&quot;.</p><p>\u662F\u7684, \u5982\u679C\u6CA1\u6709\u5BF9\u4E2D\u897F\u533B\u4E4B\u8FA9\u4ECE\u6C11\u56FD\u4EE5\u6765\u7684\u5386\u53F2\u6709\u6240\u4E86\u89E3, \u5E73\u767D\u542C\u5230\u8FD9\u79CD\u4E89\u8BBA, \u5927\u6982\u90FD\u4F1A\u89C9\u5F97\u8FD9\u662F\u95F2\u7684\u86CB\u75DB\u5427.</p><p>\u7136\u800C\u65E2\u4FBF\u5982\u6B64, \u6211\u5988\u4E5F\u8BA4\u540C\u8FD9\u4E2A\u89C2\u70B9: \u4E2D\u533B\u91CC\u4E5F\u592A\u5BB9\u6613\u51FA\u9A97\u5B50\u4E86.</p><p>\u4E14\u4E0D\u8BF4\u6743\u5065, \u836F\u9152, \u4E00\u65F6\u662F\u82D7\u533B\u4E00\u65F6\u662F\u85CF\u836F\u7684\u4EBA\u6C11\u544A\u827A\u672F\u5BB6, \u5C31\u8BF4\u6700\u8FD1\u5728\u6B66\u6C49\u53F7\u79F0 &quot;\u7A74\u4F4D\u6CE8\u5C04\u82EF\u915A&quot; \u5305\u6CBB\u767E\u75C5\u7684\u795E\u533B\u674E\u8DC3\u534E\u90FD\u532A\u5937\u6240\u601D\u7684\u81EA\u79F0\u662F\u4E2D\u533B.</p><p>\u867D\u7136\u5916\u516C\u7684\u9488\u7078\u624B\u827A\u6CA1\u6709\u4F20\u5230\u6211\u624B\u91CC, \u4F46\u662F\u6211\u4E5F\u770B\u7684\u51FA\u6765 &quot;\u7A74\u4F4D\u6CE8\u5C04\u82EF\u915A&quot; \u8FD9\u73A9\u610F\u4E0D\u662F\u4E2D\u533B.</p><p>\u56FA\u7136\u7A74\u4F4D\u662F\u4E2D\u533B\u91CC\u7684\u6982\u5FF5, \u4F46\u5F80\u91CC\u8FB9\u6CE8\u5C04\u82EF\u915A, \u65E2\u4E0D\u662F\u4E2D\u533B\u7684\u7597\u6CD5, \u4E5F\u4E0D\u662F\u4E2D\u533B\u7684\u4F20\u7EDF, \u4E5F\u6CA1\u6709\u4E2D\u533B\u7684\u7406\u8BBA\u4F9D\u636E.</p><p>\u6240\u4EE5\u6211\u4EA7\u751F\u4E86\u8FD9\u6837\u7684\u7591\u95EE: \u4E3A\u4EC0\u4E48\u8FD9\u4E48\u591A\u9A97\u5B50\u90FD\u8981\u53F7\u79F0\u81EA\u5DF1\u662F\u4E2D\u533B\u5462?</p><h1 id="\u56DB" tabindex="-1"><a class="header-anchor" href="#\u56DB" aria-hidden="true">#</a> \u56DB</h1><p>\u6216\u8BB8\u6700\u8FD1\u7A81\u7136\u6D8C\u73B0\u7684\u53F7\u79F0\u4E2D\u533B\u7684\u602A\u4E8B, \u5982\u7A74\u4F4D\u6CE8\u5C04\u82EF\u915A, \u5F3A\u8FEB\u4E2D\u5C0F\u5B66\u751F\u559D\u5927\u9505\u836F\u7B49, \u786E\u5B9E\u4E0E\u695A\u738B\u53F7\u7EC6\u8170\u5BAB\u4E2D\u591A\u997F\u6B7B\u6709\u5173. \u4F46\u8FD9\u89E3\u91CA\u4E0D\u4E86\u6743\u5065, \u836F\u9152, \u4E00\u65F6\u662F\u82D7\u533B\u4E00\u65F6\u662F\u85CF\u836F\u7684\u4EBA\u6C11\u544A\u827A\u672F\u5BB6\u7B49\u7531\u6765\u4EE5\u4E45\u7684\u73B0\u8C61.</p><p>\u6211\u4EEC\u5FC5\u987B\u627F\u8BA4, \u4E2D\u533B\u6709\u67D0\u79CD\u7279\u8D28, \u4F7F\u5F97\u90A3\u4E9B\u4E2A\u9A97\u5B50, \u683C\u5916\u559C\u6B22\u81EA\u79F0\u4E2D\u533B.</p><p>\u8FD8\u662F\u56DE\u5230 &quot;\u7A74\u4F4D\u6CE8\u5C04\u82EF\u915A&quot; \u7684\u4F8B\u5B50\u4E0A. \u5373\u7136\u90FD\u7528\u4E86\u6CE8\u5C04\u8FD9\u79CD\u5178\u578B\u7684\u897F\u533B\u6CBB\u7597\u624B\u6BB5, \u5B83\u4E3A\u4EC0\u4E48\u4E0D\u53F7\u79F0\u81EA\u5DF1\u662F\u897F\u533B.</p><p>\u5927\u6982\u662F\u56E0\u4E3A, \u897F\u533B\u5EFA\u7ACB\u5728\u73B0\u4EE3\u79D1\u5B66\u5B9E\u8BC1\u7684\u57FA\u7840\u4E0A. \u4E00\u79CD\u6CBB\u7597\u624B\u6BB5\u60F3\u8981\u6210\u4E3A\u88AB\u897F\u533B\u8BA4\u53EF\u7684\u6CBB\u7597\u65B9\u6848, \u9700\u8981\u7ECF\u8FC7\u4E00\u5957\u7CFB\u7EDF\u7684\u8BC1\u5B9E\u8FC7\u7A0B. \u50CF &quot;\u7A74\u4F4D\u6CE8\u5C04\u82EF\u915A&quot; \u8FD9\u79CD\u5947\u5947\u602A\u602A\u7684\u4E1C\u897F\u5B8C\u5168\u65E0\u6CD5\u901A\u8FC7\u90A3\u5957\u8BC1\u5B9E\u7684\u8FC7\u7A0B.</p><p>\u8FD9\u4E5F\u662F\u6743\u5065\u7684\u706B\u7597\u8FDB\u4E0D\u4E86\u533B\u9662, \u67D0\u8305\u7684\u836F\u9152\u4E0A\u4E0D\u4E86\u836F\u67B6\u7684\u539F\u56E0.</p><p>\u4F46\u95EE\u9898\u662F, \u4E3A\u4EC0\u4E48 &quot;\u7A74\u4F4D\u6CE8\u5C04\u82EF\u915A&quot; \u4E4B\u4E00, \u65E2\u4E0D\u662F\u4E2D\u533B\u7684\u7597\u6CD5, \u4E5F\u4E0D\u662F\u4E2D\u533B\u7684\u4F20\u7EDF, \u4E5F\u6CA1\u6709\u4E2D\u533B\u7684\u7406\u8BBA\u4F9D\u636E, \u4ED6\u81EA\u79F0\u662F\u4E2D\u533B, \u5C31\u6709\u4EBA\u4FE1\u5462?</p><p>\u5927\u6982\u662F\u56E0\u4E3A\u4E2D\u533B\u5E76\u4E0D\u662F\u79D1\u5B66\u7F62. \u81F3\u5C11\u4E0D\u662F\u73B0\u4EE3\u79D1\u5B66.</p><h1 id="\u4E94" tabindex="-1"><a class="header-anchor" href="#\u4E94" aria-hidden="true">#</a> \u4E94</h1><p>\u4E25\u683C\u8BF4\u6765\u6CA1\u6709\u4EC0\u4E48\u4E2D\u533B\u897F\u533B, \u53EA\u6709\u73B0\u4EE3\u533B\u5B66\u548C\u4F20\u7EDF\u533B\u5B66. \u73B0\u4EE3\u533B\u5B66\u5EFA\u7ACB\u5728\u73B0\u4EE3\u79D1\u5B66\u7684\u57FA\u7840\u4E0A, \u4F20\u7EDF\u533B\u5B66\u5EFA\u7ACB\u5728\u4F20\u7EDF\u8BA4\u77E5\u7ED3\u6784\u4E0A.</p>',34),h=[r];function d(u,i){return a(),e("div",null,h)}var c=p(o,[["render",d],["__file","\u4E2D\u533B.html.vue"]]);export{c as default};
