import{_ as t,r as o,o as r,c as h,a as d,b as i,w as n,e as p,d as e}from"./app.6bf529a6.js";const s={},c=p('<h1 id="\u4E3A\u4EC0\u4E48\u4E0D\u542C-\u5F97\u5230-\u4E86" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u4E0D\u542C-\u5F97\u5230-\u4E86" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u4E0D\u542C &quot;\u5F97\u5230&quot; \u4E86</h1><p>\u6211\u8FD8\u662F\u4E0D\u662F\u975E\u5E38\u503E\u5411\u4E8E\u8BA4\u4E3A\u77E5\u8BC6\u8BA8\u8D39\u80CC\u7684\u90A3\u4E2A\u9700\u6C42, \u8FD8\u4E0D\u662F\u5B8C\u5168\u88AB\u5546\u4EBA\u4EEC\u6240\u521B\u9020\u51FA\u6765\u7684\u9700\u6C42.</p><p>\u6211\u503E\u5411\u4E8E\u8BA4\u4E3A, \u77E5\u8BC6\u8BA8\u8D39\u80CC\u540E\u7684\u9700\u6C42\u662F\u5EFA\u7ACB\u79EF\u7EA7\u7684\u81EA\u6211\u6280\u672F\u7684\u9700\u6C42. \u8FD9\u4E2A\u9700\u6C42\u662F\u771F\u5B9E\u7684 ( \u4E0D\u77E5\u6211\u8D39\u52B2\u5DF4\u62C9\u7684\u4F5C\u8FD9\u4E2A\u77E5\u8BC6\u5E93\u5E72\u561B ).</p><p>\u8FD9\u6837, \u6211\u4EEC\u7684\u8BDD\u5C31\u8981\u8BF4\u5230, \u4E3A\u4EC0\u4E48\u8981\u5EFA\u7ACB\u79EF\u6781\u7684\u81EA\u6211\u6280\u672F.</p><p>\u5F88\u7B80\u5355, \u65E2\u4F7F\u6211\u4EEC\u4E0D\u5173\u5FC3\u6280\u672F, \u6280\u672F\u4E5F\u5173\u5FC3\u6211\u4EEC\u554A. \u524D\u8FB9\u8BF4\u5230\u5546\u4EBA\u4EEC\u4F1A\u4E3A\u6211\u4EEC\u521B\u9020\u9700\u6C42, \u4ED6\u4EEC\u53EF\u662F\u6709\u6574\u5957\u65B9\u6CD5\u8BBA\u4F5C\u4E3A\u6280\u672F\u6765\u4E3A\u6211\u4EEC\u521B\u9020\u9700\u6C42\u7684.</p><p>\u8089\u8EAB\u662F\u65E0\u6CD5\u5BF9\u6297\u6280\u672F\u7684. \u6240\u4EE5\u6211\u4EEC\u53EA\u80FD\u4E3A\u81EA\u5DF1\u5EFA\u7ACB\u4E00\u5957\u81EA\u6211\u6280\u672F\u6765\u5BF9\u6297\u8FD9\u4E2A\u4E16\u754C\u7684\u6280\u672F\u7684.</p><p>\u8FD9\u662F\u4E3A\u4EC0\u4E48\u77E5\u8BC6\u8BA8\u8D39\u80FD\u5438\u5F15\u4EBA\u4EEC\u7684\u5E95\u5C42\u9700\u6C42.</p><p>\u6211\u4E0D\u542C &quot;\u5F97\u5230&quot; \u4E86, \u9053\u4E0D\u662F\u6211\u89C9\u5F97\u8FD9\u4E2A\u9700\u6C42\u662F\u4F2A\u9700\u6C42, \u800C\u662F\u56E0\u4E3A, \u6211\u89C9\u5F97\u77E5\u8BC6\u8BA8\u8D39\u7684\u8FD9\u79CD\u5F62\u5F0F, \u662F\u4E0D\u80FD\u8BA9\u6211\u4EEC\u6EE1\u8DB3\u8FD9\u4E2A\u9700\u6C42\u7684. \u4E0D\u662F\u4ED6\u4EEC\u4E0D\u52AA\u529B, \u800C\u662F\u8FD9\u4E2A\u6A21\u5F0F, \u6253\u6839\u4E0A, \u5C31\u65E0\u6CD5\u771F\u7684\u6EE1\u8DB3\u8FD9\u4E2A\u9700\u6C42.</p><h2 id="_2019-\u5E74\u521D\u7834\u9F13\u4E07\u4EBA\u9524\u7684\u77E5\u8BC6\u4ED8\u8D39" tabindex="-1"><a class="header-anchor" href="#_2019-\u5E74\u521D\u7834\u9F13\u4E07\u4EBA\u9524\u7684\u77E5\u8BC6\u4ED8\u8D39" aria-hidden="true">#</a> 2019 \u5E74\u521D\u7834\u9F13\u4E07\u4EBA\u9524\u7684\u77E5\u8BC6\u4ED8\u8D39</h2><p>\u5728 2018 \u5E74\u672A\u81F3 2019 \u5E74\u521D, \u670B\u53CB\u5708\u548C\u516C\u4F17\u53F7\u91CC\u6D8C\u73B0\u51FA\u4E00\u5927\u6CE2\u6279\u5224\u77E5\u8BC6\u4ED8\u8D39\u7684\u6587\u7AE0.</p><p>\u5927\u6982\u591A\u5C11\u662F\u53D7\u4E86\u4E92\u8054\u7F51\u5BD2\u51AC\u7684\u5F71\u54CD ( \u4E0D\u5F97\u4E0D\u8BF4, \u6211\u5BF9\u4E8E\u4E92\u8054\u7F51\u5546\u4E1A\u6A21\u5F0F\u672C\u8EAB\u7684\u53CD\u601D, \u591A\u591A\u5C11\u5C11\u4E5F\u662F\u53D7\u4E86\u4E92\u8054\u7F51\u5BD2\u51AC\u7684\u5F71\u54CD ). \u4EE5\u524D\u6B23\u6B23\u5411\u8363\u65F6\u7684 &quot;\u5C0F\u751C\u751C&quot;, \u800C\u4ECA\u5728\u8270\u96BE\u65F6\u65E5\u4E2D\u53D8\u6210\u4E86 &quot;\u725B\u592B\u4EBA&quot;.</p><p>\u7531\u6B64\u53EF\u89C1, \u9000\u4E00\u4E07\u6B65\u8BB2, \u77E5\u8BC6\u8BA8\u8D39\u5C31\u7B97\u662F\u6709\u4F5C\u7528, \u4E5F\u9876\u591A\u9526\u4E0A\u6DFB\u82B1\u7684\u88C5\u5178, \u800C\u6CA1\u6709\u96EA\u4E2D\u9001\u78B3\u7684\u5B9E\u7528.</p><p>\u7FFB\u7535\u672C\u8EAB\u5BF9\u4E8E\u77E5\u8BC6\u8BA8\u8D39\u662F\u62B1\u6301\u6279\u5224\u7684\u6001\u5EA6\u7684. \u5177\u4F53\u53EF\u542C\u76F8\u5E94\u8282\u76EE.</p><p>\u8BF4\u5B9E\u8BDD, \u4E00\u76F4\u4EE5\u6765\u6211\u5BF9\u4E8E\u77E5\u8BC6\u8BA8\u8D39\u4E0D\u8C61\u662F\u7FFB\u7535\u6709\u7740\u4E25\u683C\u7684\u6279\u5224. \u4F46\u6211\u4E5F\u5F88\u4E45\u6CA1\u6709\u8D2D\u4E70\u8FC7\u77E5\u8BC6\u8BA8\u8D39\u7684\u670D\u52A1\u6216\u63A5\u6536\u8FC7\u77E5\u8BC6\u8BA8\u8D39\u7684\u4FE1\u606F\u4E86. \u56E0\u6B64, \u5927\u4F17\u591A\u516C\u4F17\u53F7\u6587\u7AE0\u8FDB\u884C\u7834\u9F13\u4E07\u4EBA\u9524\u7684\u65F6\u5019, \u6211\u8FD8\u771F\u6709\u70B9\u4E0D\u592A\u6E05\u695A\u4E3A\u4EC0\u4E48\u4ED6\u4EEC\u5177\u4F53\u5728\u8BF4\u5565.</p><h2 id="\u4E24\u4E2A\u9519\u8BEF" tabindex="-1"><a class="header-anchor" href="#\u4E24\u4E2A\u9519\u8BEF" aria-hidden="true">#</a> \u4E24\u4E2A\u9519\u8BEF</h2><p>\u6211\u4EE5\u524D\u662F\u8D2D\u4E70\u4E86\u77E5\u8BC6\u8BA8\u8D39\u670D\u52A1\u7684. \u786E\u5207\u7684\u8BF4, \u662F\u5728\u5F97\u5230\u4E0A\u8D2D\u4E70\u8FC7\u4E07\u7EF4\u521A\u548C\u718A\u9038\u7684\u670D\u52A1.</p><p>\u4F46\u662F\u5927\u6982\u5728 17 \u5E74\u521D\u7684\u65F6\u5019, \u6211\u5C31\u518D\u4E5F\u4E0D\u6253\u5F00\u5F97\u5230\u4E86.</p><p>\u771F\u63A5\u7684\u539F\u56E0\u662F, \u5F53\u65F6\u53D1\u73B0\u4E24\u4E2A\u9519\u8BEF.</p><p>\u7B2C\u4E00\u4E2A\u662F\u7F57\u632F\u5B87\u5728\u7F57\u8F91\u601D\u7EF4\u7684\u516C\u4F17\u53F7\u4E0A\u8BF4\u7684\u4E00\u6BB5\u5173\u4E8E\u98CE\u9669\u5B9A\u4EF7\u7684\u89C2\u70B9. \u5F53\u65F6\u8FD8\u5728\u60E0\u667A, \u4E8E\u662F\u5C31\u590D\u5236\u4E86\u8FD9\u6BB5\u8BF4\u8BCD\u7ED9\u5B66\u91D1\u878D\u51FA\u751F, \u5E72\u4E86\u597D\u51E0\u5E74\u94F6\u884C\u98CE\u63A7\u7684\u9093\u7267\u770B.</p><p>\u9093\u7267\u7684\u770B\u6CD5\u662F: \u8FD9\u4E2A\u89C2\u70B9\u4E00\u770B\u5C31\u662F\u6CA1\u6B63\u7ECF\u5B66\u8FC7\u91D1\u878D, \u4F46\u770B\u8FC7\u51E0\u672C\u91D1\u878D\u79D1\u666E\u4E66\u7684\u4EBA\u63D0\u7684, \u56E0\u4E3A\u8FD9\u4E2A\u89C2\u70B9\u67B6\u52BF\u5F88\u8DB3, \u4F46\u57FA\u7840\u6982\u5FF5\u7528\u9519\u4E86. ( \u8FD9\u4E2A\u8BC4\u4EF7\u5F88\u8C61\u662F\u5BF9\u6C11\u54F2\u548C\u6C11\u79D1\u7684\u8BC4\u4EF7 ). \u9093\u7267\u8FD8\u82E6\u53E3\u5A46\u5FC3\u7684\u8BA9\u6211\u522B\u770B\u8FD9\u7C7B\u4E1C\u897F.</p>',20),_=e('\u56E0\u4E3A\u6BD5\u7ADF\u6211\u4E5F\u4E0D\u662F\u5B66\u91D1\u878D\u51FA\u751F\u7684, \u800C\u4E14\u5F53\u65F6\u591A\u591A\u5C11\u5C11\u6709\u70B9 "\u4E92\u8054\u7F51\u4EBA" \u5BF9\u4E8E "\u4F20\u7EDF\u5B66\u672F" \u7684\u53CD\u667A\u6001\u5EA6, \u6240\u4EE5\u4E5F\u6CA1\u592A\u4E0A\u5FC3. '),u=e("\u4E92\u8054\u7F51\u65F6\u4EE3\u7684\u53CD\u667A\u4E3B\u4E49"),l=p('<p>\u4F46\u7B2C\u4E8C\u4E2A\u9519\u8BEF\u5374\u8BA9\u6211\u7559\u4E86\u610F.</p><p>\u7B2C\u4E8C\u4E2A\u9519\u8BEF\u6211\u662F\u4ECE\u4E07\u7EF4\u94A2\u7684\u4E13\u680F\u91CC\u542C\u5230\u7684. \u91CC\u8FB9\u6709\u4E00\u671F\u63D0\u5230\u53E4\u5E0C\u814A\u54F2\u5B66. \u8BA4\u4E3A\u4ECE\u79D1\u5B66\u7684\u89D2\u5EA6\u4E0A\u8BB2, \u4E0D\u4F46\u53E4\u5E0C\u814A\u4EBA\u7684\u8BBA\u65AD\u662F\u9519\u7684, \u800C\u4E14\u53E4\u5E0C\u814A\u4EBA\u7814\u7A76\u4E16\u754C\u7684\u65B9\u6CD5\u90FD\u662F\u9519\u7684, \u6240\u4EE5\u53E4\u5E0C\u814A\u7684\u8457\u4F5C\u53EA\u80FD\u5F53\u8BD7\u6765\u8BFB.</p><p>\u8FD9\u4E0B\u8BF4\u5230\u6211\u7565\u7565\u7EC4\u901A\u7684\u9886\u57DF\u4E86. \u6211\u5BF9\u54F2\u5B66\u53F2\u6709\u5174\u8DA3, \u5BF9\u53E4\u5E0C\u814A\u54F2\u5B66\u53F2\u5C24\u6709\u5174\u8DA3.</p><p>\u6211\u4EEC\u5148\u4E0D\u8BA8\u8BBA\u79D1\u5B66\u9738\u6743\u4E3B\u4E49, \u4EC5\u5C31\u62FF\u73B0\u5728\u79D1\u5B66\u7814\u7A76\u7684\u65B9\u6CD5\u6765\u8BF4, \u6240\u6709\u79D1\u5B66\u65B9\u6CD5\u7684\u57FA\u7840 -- \u6570\u5B66\u548C\u903B\u8F91 -- \u5C31\u662F\u5728\u53E4\u5E0C\u814A\u4EBA\u78D5\u78D5\u7ECA\u7ECA\u7684\u54F2\u5B66\u7814\u7A76\u4E2D\u6765\u7684. \u5982\u679C\u5F53\u65F6\u7684\u54F2\u5B66\u53EA\u80FD\u5F53\u8BD7\u6765\u8BFB, \u90A3\u4E48\u53EA\u6015, \u73B0\u5728\u6240\u6709\u7684\u79D1\u5B66\u90FD\u53EA\u80FD\u5F53\u8BD7\u6765\u8BFB\u4E86.</p><p>\u8FD9\u4E2A\u89C2\u70B9\u5C31\u5982 &quot;\u79E6\u59CB\u7687\u5F31\u66B4\u4E86, \u56E0\u4E3A\u4ED6\u5373\u6CA1\u6709\u9020\u51FA\u539F\u5B50\u5F39, \u800C\u4E14\u8FDE\u9020\u539F\u5B50\u5F39\u7684\u60F3\u6CD5\u90FD\u6CA1\u6709, \u6240\u4EE5\u79E6\u7EDF\u4E00\u516D\u56FD\u5C31\u6839\u672C\u4E0D\u662F\u6218\u4E89, \u53EA\u662F\u6751\u53E3\u6253\u7FA4\u67B6&quot; \u4E00\u6837.</p><h2 id="\u4E24\u4E2A\u9519\u8BEF\u610F\u5473\u7740\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u4E24\u4E2A\u9519\u8BEF\u610F\u5473\u7740\u4EC0\u4E48" aria-hidden="true">#</a> \u4E24\u4E2A\u9519\u8BEF\u610F\u5473\u7740\u4EC0\u4E48?</h2><p>\u4E16\u4E0A\u8C01\u4EBA\u4E0D\u72AF\u9519, \u72AF\u9519\u53C8\u80FD\u600E\u4E48\u6837. \u4F55\u51B5\u6211\u4E5F\u53EA\u53D1\u73B0\u4E86\u4E24\u4E2A\u9519\u8BEF.</p><p>\u9996\u5148\u8BF4 &quot;\u4E24\u4E2A&quot; \u8FD9\u4E2A\u6570\u91CF\u95EE\u9898.</p><p>\u4E07\u7EF4\u521A\u5C31\u8BF4\u4E86\u4E00\u6B21\u53E4\u5E0C\u814A\u54F2\u5B66, \u5C31\u662F\u9519\u7684. \u7F57\u632F\u5B87\u867D\u7136\u8BF4\u8FC7\u5F88\u591A\u6B21\u91D1\u878D, \u4F46\u6211\u53EA\u627E\u4E13\u4E1A\u4EBA\u58EB\u9A8C\u8BC1\u8FC7\u4E00\u6B21, \u5C31\u662F\u9519\u7684.</p><p>\u4EC0\u4E48\u610F\u601D\u5462? \u5728\u6211\u56E0\u4E3A\u4E0D\u61C2\u800C\u65E0\u6CD5\u9A8C\u8BC1\u7684\u9886\u57DF\u6211\u4E0D\u77E5\u9053\u4ED6\u4EEC\u8BF4\u7684\u5BF9\u4E0D\u5BF9, \u4F46\u662F\u5728\u6240\u6709\u6211\u53EF\u4EE5\u9A8C\u8BC1\u7684\u5730\u65B9, \u4ED6\u4EEC\u90FD\u9519\u4E86.</p><p>\u8FD9\u6837\u60F3\u4E00\u60F3, \u771F\u7684\u5F88\u53EF\u6015, \u5728\u90A3\u4E9B\u6211\u4E0D\u77E5\u9053\u7684\u5730\u65B9, \u4ED6\u4EEC\u7ED9\u6211\u704C\u8F93\u4E86\u4E0D\u77E5\u9053\u591A\u5C11\u4E0D\u5BF9\u7684\u4E1C\u897F.</p><h2 id="\u77E5\u8BC6\u8BA8\u8D39\u5FC5\u7136\u662F\u8981\u7ED9\u51FA\u9519\u8BEF\u77E5\u8BC6\u7684" tabindex="-1"><a class="header-anchor" href="#\u77E5\u8BC6\u8BA8\u8D39\u5FC5\u7136\u662F\u8981\u7ED9\u51FA\u9519\u8BEF\u77E5\u8BC6\u7684" aria-hidden="true">#</a> \u77E5\u8BC6\u8BA8\u8D39\u5FC5\u7136\u662F\u8981\u7ED9\u51FA\u9519\u8BEF\u77E5\u8BC6\u7684</h2><p>\u7EC6\u7EC6\u60F3\u6765, \u7F57\u7EF4\u521A\u548C\u7F57\u632F\u5B87\u4E4B\u6240\u4EE5\u4F1A\u72AF\u9519, \u4E0D\u662F\u56E0\u4E3A\u4ED6\u4EEC\u7B28. \u800C\u662F\u8FD9\u4E2A\u5546\u4E1A\u6A21\u5F0F, \u4F1A\u903C\u7740\u4ED6\u4EEC\u72AF\u8FD9\u6837\u7684\u9519.</p><p>\u77E5\u8BC6\u8BA8\u8D39\u4F5C\u4E3A\u4E00\u4E2A\u4EA7\u4E1A, \u5FC5\u7136\u8981\u8FFD\u6C42\u89C4\u6A21. \u4E00\u65E6\u8FFD\u6C42\u89C4\u6A21, \u5C31\u8981\u9762\u5BF9\u4E24\u4E2A\u95EE\u9898:</p><ul><li>1 \u5982\u4F55\u8BA9\u66F4\u591A\u7684\u4EBA\u53EF\u4EE5\u6D88\u8D39\u8FD9\u4E2A\u77E5\u8BC6;</li><li>2 \u5982\u4F55\u8BA9\u66F4\u591A\u7684\u4EBA\u613F\u610F\u6D88\u8D39\u8FD9\u4E2A\u77E5\u8BC6;</li></ul><p>\u4E3A\u4E86\u8BA9\u66F4\u591A\u7684\u4EBA\u53EF\u4EE5\u6D88\u8D39\u77E5\u8BC6, \u5FC5\u7136\u4F1A\u5BF9\u77E5\u8BC6\u8FDB\u884C\u8FC7\u5EA6\u7684\u7B80\u5316. \u5BF9\u77E5\u8BC6\u8FC7\u5EA6\u7684\u7B80\u5316\u6700\u597D\u7684\u7ED3\u679C\u662F\u628A\u77E5\u8BC6\u53D8\u6210\u6D77\u5FB7\u683C\u5C14\u610F\u4E49\u4E0A\u7684\u95F2\u8C08, \u800C\u66F4\u66F4\u574F\u7684\u60C5\u51B5\u662F, \u7559\u4E0B\u6613\u4E8E\u8BA4\u77E5\u7684\u90E8\u5206 ( \u5982\u67B6\u52BF ), \u800C\u820D\u5F03\u5176\u6DF1\u523B\u96BE\u61C2\u7684\u90E8\u5206 ( \u5982\u6982\u5FF5 ).</p><p>\u800C\u4E3A\u4E86\u8BA9\u66F4\u591A\u7684\u613F\u610F\u6D88\u8D39\u77E5\u8BC6, \u5FC5\u7136\u4F1A\u57FA\u4E8E\u77E5\u8BC6\u5FEB\u901F\u5BF9\u4E8E\u5F53\u4E0B\u70ED\u70B9\u8FDB\u884C\u8868\u6001. \u800C\u4EFB\u4F55\u77E5\u8BC6, ( \u4E07\u5176\u662F\u73B0\u4EE3\u7684\u5206\u79D1\u77E5\u8BC6 ), \u5F80\u5F80\u662F\u6709\u975E\u5E38\u5F3A\u7684\u4E13\u4E1A\u6027, \u8FD9\u79CD\u4E13\u4E1A\u6027\u4F7F\u77E5\u8BC6\u672C\u8EAB\u662F\u6709\u9002\u7528\u8FB9\u754C\u7684. \u5982\u679C\u8981\u6C42\u5FC5\u987B\u57FA\u4E8E\u4E00\u7C7B\u77E5\u8BC6, \u5BF9\u6240\u6709\u70ED\u70B9\u5FEB\u901F\u505A\u51FA\u56DE\u7B54, \u8FDF\u65E9\u662F\u4F1A\u51FA\u9519\u7684.</p><p>\u800C\u66F4\u53EF\u6015\u7684\u662F, \u57FA\u4E8E\u77E5\u8BC6\u8BA8\u8D39\u7684\u903B\u8F91, \u6709\u9519\u5C31\u6539\u7684\u60C5\u51B5\u662F\u4E0D\u4F1A\u51FA\u73B0\u7684.</p><p>\u56E0\u4E3A\u77E5\u8BC6\u8BA8\u8D39\u8BA8\u8D39\u7684\u5BF9\u8C61\u5176\u5B9E\u4E0D\u662F\u77E5\u8BC6\u672C\u8EAB, \u800C\u662F\u77E5\u8BC6\u7684\u8BB2\u6388\u4EBA. \u5177\u4F53\u8BF4\u6765, \u4E0D\u662F\u8BB2\u6388\u4EBA\u672C\u8EAB, \u800C\u662F\u8BB2\u6388\u4EBA\u7684\u4EBA\u8BBE. \u518D\u5177\u4F53\u8BF4, \u4E0D\u662F\u8BB2\u6388\u4EBA\u7684\u4EBA\u8BBE\u672C\u8EAB, \u800C\u662F\u8BB2\u6388\u4EBA\u77E5\u6167\u7684\u4EBA\u8BBE.</p><p>\u4E00\u65E6\u8BB2\u6388\u4EBA\u88AB\u4E00\u4E2A\u667A\u6167\u7684\u4EBA\u8BBE\u6240\u7ED1\u67B6, \u4ED6\u5C31\u5F88\u6539\u6B63\u5176\u5DF2\u72AF\u7684\u9519\u8BEF.</p><hr><p>\u6240\u4EE5, \u6211\u540E\u6765\u5C31\u4E0D\u542C &quot;\u5F97\u5230&quot; \u4E86.</p>',22);function q(f,B){const a=o("RouterLink");return r(),h("div",null,[c,d("p",null,[_,i(a,{to:"/write/%E4%BA%92%E8%81%94%E7%BD%91%E6%97%B6%E4%BB%A3%E7%9A%84%E5%8F%8D%E6%99%BA%E4%B8%BB%E4%B9%89.html"},{default:n(()=>[u]),_:1})]),l])}var x=t(s,[["render",q],["__file","\u4E3A\u4EC0\u4E48\u4E0D\u542C\u5F97\u5230\u4E86.html.vue"]]);export{x as default};
