import{_ as p,o as e,c as a,e as r}from"./app.6bf529a6.js";const t={},o=r('<h1 id="\u4E89\u7AEF\u7684\u8D77\u6E90\u5230\u7D22\u4F26\u6E38\u5386\u524D" tabindex="-1"><a class="header-anchor" href="#\u4E89\u7AEF\u7684\u8D77\u6E90\u5230\u7D22\u4F26\u6E38\u5386\u524D" aria-hidden="true">#</a> \u4E89\u7AEF\u7684\u8D77\u6E90\u5230\u7D22\u4F26\u6E38\u5386\u524D</h1><h2 id="\u4E89\u7AEF\u7684\u8D77\u6E90" tabindex="-1"><a class="header-anchor" href="#\u4E89\u7AEF\u7684\u8D77\u6E90" aria-hidden="true">#</a> \u4E89\u7AEF\u7684\u8D77\u6E90</h2><p>\u5E0C\u7F57\u591A\u5FB7\u63D0\u5230\u4E86\u591A\u65B9\u7684\u8BF4\u6CD5, \u4F46\u4F3C\u4E4E\u6CE2\u65AF\u4EBA\u548C\u5E0C\u814A\u4EBA\u4E89\u7AEF\u7684\u8D77\u6E90\u90FD\u662F\u4ECE\u76F8\u4E92\u52AB\u63A0 ( \u4E5F\u6709\u53EF\u80FD\u662F\u8BF1\u9A97 ) \u5987\u5973\u5F00\u59CB\u7684.</p><p>\u636E\u6CE2\u65AF\u4EBA\u8BF4, \u8FC7\u7A0B\u5927\u6982\u662F\u8FD9\u6837\u7684.</p><p>\u8153\u5C3C\u57FA\u4EBA\u53BB\u5E0C\u814A\u505A\u751F\u610F, \u63A0\u8D70\u4E86\u5F53\u5730\u56FD\u738B\u7684\u5973\u513F\u4F0A\u5965.</p><p>\u5E0C\u814A\u4EBA\u4E3A\u4E86\u62A5\u590D, \u53BB\u8153\u5C3C\u57FA\u4EBA\u7684\u56FD\u5BB6\u63A0\u8D70\u4E86\u56FD\u738B\u7684\u5973\u513F\u6B27\u7F57\u5DF4, \u4E0D\u53EA\u5982\u6B64, \u4ED6\u4EEC\u8FD8\u8DD1\u53BB\u79D1\u542F\u65AF\u63A0\u8D70\u4E86\u56FD\u738B\u7684\u5973\u513F\u7F8E\u5730\u4E9A. ( \u5E0C\u814A\u4EBA\u52AB\u63A0\u516C\u4E3B\u4E0A\u763E\u4E86\u4E48? )</p><p>\u79D1\u542F\u65AF\u8981\u6C42\u5E0C\u814A\u4EBA\u966A\u507F, \u4F46\u662F\u5E0C\u814A\u4EBA\u4EE5\u8153\u5C3C\u57FA\u4EBA\u6CA1\u6709\u8D54\u507F\u8FC7\u4E3A\u7531\u62D2\u7EDD\u966A\u507F.</p><p>\u4E0E\u662F\u5728\u4E0B\u4E00\u4EE3, \u4E9A\u91CC\u5C71\u5927\u5C31\u4ECE\u5E0C\u814A\u63A0\u8D70\u4E86\u6D77\u4F26. \u5F15\u53D1\u4E86\u7279\u6D1B\u4F0A\u6218\u4E89.</p><p>\u7167\u7740\u6CE2\u65AF\u4EBA\u7684\u8BF4\u6CD5, \u5728\u6B64\u4E4B\u524D\u4ED6\u4EEC\u8BA4\u4E3A\u4E8B\u60C5\u7684\u6027\u8D28\u90FD\u662F\u76F8\u4E92\u95F4\u52AB\u63A0\u5987\u5973\u800C\u5DF2, \u800C\u5987\u5973\u88AB\u52AB\u63A0\u591A\u534A\u662F\u5987\u5973\u81EA\u5DF1\u8868\u793A\u613F\u610F. \u56E0\u6B64\u4ED6\u4EEC\u5BF9\u6B64\u5E76\u4E0D\u653E\u5728\u5FC3\u4E0A, \u4F46\u6CA1\u60F3\u5230\u5E0C\u814A\u4E0A\u8FC7\u6765\u628A\u4ED6\u4EEC\u7ED9\u6253\u4E86.</p><blockquote><p>\u4ECE\u6B64\u4EE5\u540E, \u4ED6\u4EEC\u5C31\u628A\u5E0C\u814A\u4EBA\u770B\u6210\u662F\u81EA\u5DF1\u7684\u4EC7\u654C\u4E86.</p></blockquote><p>\u7136\u800C\u5173\u4E8E\u4E8B\u60C5\u6700\u521D\u7684\u8D77\u70B9 -- \u4F0A\u5965\u88AB\u52AB\u4E8B\u4EF6 -- \u8153\u5C3C\u57FA\u4EBA\u7684\u7248\u672C\u662F\u8FD9\u6837\u7684: \u4F0A\u5965\u5F53\u65F6\u4E0E\u4E00\u4E2A\u8153\u5C3C\u57FA\u8239\u4E3B\u6709\u67D3, \u4EE5\u81F3\u6000\u5B55, \u5979\u5DEE\u4E8E\u628A\u8FD9\u4E8B\u544A\u8BC9\u81EA\u5DF1\u7684\u7236\u4EB2, \u4E0E\u662F\u5728\u8153\u5C3C\u57FA\u4EBA\u79BB\u5F00\u7684\u65F6\u5019, \u5FC3\u7518\u60C5\u613F\u7684\u968F\u4ED6\u4EEC\u4E00\u540C\u4E58\u8239\u8D70\u4E86.</p><h2 id="\u514B\u6D1B\u4F0A\u7D22\u65AF\u4E00\u5BB6\u7684\u5F00\u7AEF-\u5DE8\u5409\u65AF\u7684\u6545\u4E8B" tabindex="-1"><a class="header-anchor" href="#\u514B\u6D1B\u4F0A\u7D22\u65AF\u4E00\u5BB6\u7684\u5F00\u7AEF-\u5DE8\u5409\u65AF\u7684\u6545\u4E8B" aria-hidden="true">#</a> \u514B\u6D1B\u4F0A\u7D22\u65AF\u4E00\u5BB6\u7684\u5F00\u7AEF: \u5DE8\u5409\u65AF\u7684\u6545\u4E8B</h2><p>\u88AB\u79F0\u4E3A\u7B2C\u4E00\u4E2A\u5236\u670D\u5E0C\u814A\u7684\u5916\u90A6\u4EBA\u662F\u514B\u6D1B\u4F0A\u7D22\u65AF, \u4ED6\u7684\u5BB6\u65CF\u662F\u8FD9\u6837\u6210\u4E3A\u5415\u5E95\u4E9A\u7684\u50ED\u4E3B\u7684:</p><p>\u5415\u5E95\u4E9A\u7684\u7B2C\u4E8C\u4E2A\u5974\u96B6\u5236\u738B\u671D\u6D77\u62C9\u514B\u5217\u8FBE\u4F0A\u738B\u671D, \u4F20\u4E86\u4E94\u767E\u96F6\u4E94\u5E74, \u5B83\u7684\u6700\u540E\u4E00\u4EFB\u50ED\u4E3B\u662F\u574E\u9053\u5217\u65AF.</p><p>\u574E\u9053\u5217\u65AF\u975E\u5E38\u5BA0\u7231\u4ED6\u7684\u59BB\u5B50, \u8BA4\u4E3A\u5979\u662F\u4E16\u4E0A\u6700\u7F8E\u7684\u5973\u4EBA. \u4ED6\u6709\u4E00\u4E2A\u975E\u5E38\u4EB2\u8FD1\u7684\u4F8D\u536B, \u53EB\u505A\u5DE8\u5409\u65AF. \u4ED6\u5E38\u5E38\u5411\u5DE8\u5409\u65AF\u8D5E\u7F8E\u81EA\u5DF1\u7684\u59BB\u5B50.</p><p>\u7EC8\u4E8E\u6709\u4E00\u5929</p><blockquote><p>\u547D\u4E2D\u6CE8\u5B9A\u8981\u906D\u5230\u4E0D\u5E78\u7684\u574E\u9053\u5217\u65AF\u5411\u5DE8\u5409\u65AF\u8FD9\u6837\u8BF4: &quot;\u5DE8\u5409\u65AF, \u6211\u770B\u6211\u5355\u662F\u5411\u4F60\u8BF4\u6211\u7684\u59BB\u5B50\u7F8E\u4E3D, \u90A3\u4F60\u662F\u4E0D\u4F1A\u76F8\u4FE1\u7684. \u4F60\u60F3\u4E2A\u529E\u6CD5\u6765\u770B\u770B\u5979\u797C\u4F53\u65F6\u7684\u6837\u5B50\u7F62.&quot;</p></blockquote><p>note: \u6211\u5B9E\u5728\u4E0D\u80FD\u7406\u89E3\u53E4\u5E0C\u814A\u8FD9\u91CC\u7684\u5BA0\u7231\u662F\u4EC0\u4E48\u610F\u601D.</p><p>\u5DE8\u5409\u65AF\u5F00\u59CB\u4E0D\u540C\u610F, \u4F46\u574E\u9053\u5217\u65AF\u8FD8\u662F\u5B89\u6392\u8FD9\u6837\u505A\u4E86. \u5DE8\u5409\u65AF\u8EB2\u5728\u574E\u9053\u5217\u65AF\u7684\u5367\u5BA4\u91CC\u770B\u5230\u4E86\u574E\u9053\u5217\u65AF\u59BB\u5B50\u7684\u797C\u4F53, \u5F53\u4ED6\u79BB\u5F00\u65F6, \u88AB\u5979\u53D1\u73B0\u4E86.</p><p>\u7531\u4E8E\u5BB3\u5DEE\u7684\u7F18\u6545, \u574E\u9053\u5217\u65AF\u7684\u59BB\u5B50\u5E76\u6CA1\u6709\u53EB\u4E86\u51FA\u6765, \u800C\u662F\u6697\u4E2D\u76D8\u7B97\u5982\u4F55\u5411\u5979\u7684\u4E08\u592B\u62A5\u590D. \u4E8E\u662F\u6B21\u65E5\u4ED6\u903C\u8FEB\u5DE8\u5409\u65AF\u6740\u6B7B\u574E\u9053\u65AF, \u5426\u5219\u5979\u5C31\u4F1A\u6740\u6B7B\u5DE8\u5409\u65AF.</p><p>\u5DE8\u5409\u65AF\u5F00\u59CB\u4E5F\u662F\u62D2\u7EDD\u7684, \u4F46\u6700\u540E\u8FD8\u662F\u6740\u6B7B\u4E86\u574E\u9053\u65AF.</p><p>note: \u7C7B\u4F3C\u7684\u6545\u4E8B\u5728\u4E2D\u56FD\u5386\u53F2\u4E0A\u597D\u8C61\u4E5F\u6709\u8FC7, \u603B\u662F\u4F3C\u66FE\u76F8\u8BC6\u7684\u6837\u5B50. \u8FD9\u6837\u7684\u6545\u4E8B\u591A\u534A\u662F\u7BE1\u6743\u8005\u4E3A\u4E86\u81EA\u5DF1\u7684\u5408\u6CD5\u6027\u800C\u6784\u9020\u51FA\u6765\u7684. \u8FD9\u6837\u7684\u6545\u4E8B, \u6BD4\u8F83\u53EF\u80FD\u7684\u771F\u5B9E\u60C5\u51B5\u5E94\u8BE5\u662F\u5DE8\u5409\u65AF\u4E0E\u574E\u9053\u5217\u65AF\u7684\u59BB\u5B50\u6709\u67D3, \u6700\u540E\u4E8B\u60C5\u8D25\u6F0F, \u4E8E\u662F\u6740\u6B7B\u4E86\u574E\u9053\u5217\u65AF. \u751A\u81F3\u6709\u53EF\u80FD\u662F\u8FD9\u6837, \u5DE8\u5409\u65AF\u6740\u4E86\u574E\u9053\u65AF\u5E76\u5F3A\u5360\u4E86\u5176\u59BB\u5B50. \u8981\u77E5\u9053, \u6545\u4E8B\u7684\u4E66\u5199\u6743\u6491\u636E\u5728\u6D3B\u4E0B\u6765\u7684\u4EBA\u4E2D.</p><p>\u5415\u5E95\u4E9A\u4EBA\u5F00\u59CB\u8FD8\u6FC0\u6124\u4E8E\u4ED6\u4EEC\u7684\u56FD\u738B\u88AB\u6740\u800C\u53CD\u6297\u5DE8\u5409\u65AF, \u4F46\u4ED6\u4F9D\u9760\u795E\u6258, \u6700\u7EC8\u5750\u7A33\u4E86\u90A3\u4E2A\u4F4D\u7F6E. \u4F46\u4F20\u8FBE\u795E\u6258\u7684\u5973\u5DEB\u53C8\u8BF4: \u5DE8\u5409\u65AF\u7684\u7B2C\u4E94\u4EE3\u7684\u5B50\u5B59\u5C06\u8981\u53D7\u5230\u6D77\u62C9\u514B\u5217\u8FBE\u4F0A\u5BB6\u7684\u62A5\u590D.</p><p>note: \u4E0D\u77E5\u9053 <code>\u795E\u6258</code> \u662F\u4E0D\u662F\u5C31\u662F <code>\u795E\u8C15</code>. \u4F46\u5728\u5E0C\u7F57\u591A\u5FB7\u7684\u5386\u53F2\u4E2D, \u5145\u6EE1\u4E86\u8FD9\u79CD\u547D\u8FD0\u611F. \u53EF\u80FD\u662F\u56E0\u4E3A\u8FD9\u672C\u4E66\u591A\u534A\u5728\u5F53\u65F6\u662F\u5927\u4F17\u6587\u827A ( \u636E\u8BF4\u662F\u5728\u5E7F\u573A\u4E0A\u5411\u5927\u4F17\u8BF5\u8BFB\u7684 ) \u6240\u4EE5\u591A\u591A\u5C11\u5C11\u6709\u4E9B\u82B1\u8FB9\u65B0\u95FB\u7684\u611F\u89C9.</p><p>\u5DE8\u5409\u65AF\u5728\u4F4D\u4E09\u5341\u516B\u5E74, \u4E3B\u8981\u7684\u529F\u7EE9\u662F\u5411\u7C73\u5229\u90FD\u548C\u58EB\u9EA6\u62FF\u8FDB\u72AF, \u653B\u9677\u4E86\u79D1\u6D1B\u5F6D\u57CE.</p><h2 id="\u7EE7\u627F\u56DB\u4EE3\u4EBA\u7684\u6218\u4E89" tabindex="-1"><a class="header-anchor" href="#\u7EE7\u627F\u56DB\u4EE3\u4EBA\u7684\u6218\u4E89" aria-hidden="true">#</a> \u7EE7\u627F\u56DB\u4EE3\u4EBA\u7684\u6218\u4E89</h2><p>\u5DE8\u5409\u65AF\u7684\u513F\u5B50\u662F\u963F\u675C\u65AF, \u963F\u5C14\u675C\u65AF\u7684\u513F\u5B50\u662F\u96A1\u675C\u963F\u94C1\u65AF, \u96A1\u675C\u963F\u94C1\u65AF\u7684\u513F\u5B50\u662F\u963F\u5F8B\u963F\u94C1\u65AF. \u963F\u5F8B\u963F\u94C1\u65AF\u5C31\u662F\u514B\u6D1B\u4F0A\u7D22\u65AF\u7684\u7236\u4EB2.</p><p>\u5230\u963F\u5F8B\u963F\u94C1\u65AF, \u5415\u5E95\u4E9A\u5BF9\u4E8E\u7C73\u5229\u90FD\u7684\u4F5C\u6218\u5DF2\u7ECF\u7EE7\u627F\u4E86\u56DB\u4EE3\u4EBA. \u4ED6\u91C7\u7528\u4E86\u8FD9\u6837\u4E00\u79CD\u6218\u7565: \u8FDB\u653B\u65F6\u4EE5\u519B\u4E50\u4F34\u594F, \u5360\u9886\u571F\u5730\u65F6, \u4E0D\u6BC1\u623F\u5C4B, \u53EA\u70E7\u6811\u6728\u548C\u5E84\u7A3C.</p><p>\u8FD9\u6837\u505A\u662F\u56E0\u4E3A, \u7C73\u5229\u90FD\u662F\u6D77\u4E0A\u9738\u4E3B, \u5415\u5E95\u65AF\u4EBA\u6253\u8FC7\u6765, \u4ED6\u4EEC\u5C31\u8DD1\u5230\u6D77\u4E0A, \u7B49\u7740\u5415\u5E95\u65AF\u4EBA\u4E00\u8D70, \u7C73\u5229\u90FD\u4EBA\u5C31\u518D\u56DE\u6765.</p><p>\u800C\u963F\u5F8B\u94C1\u65AF\u7684\u505A\u6CD5, \u4F7F\u7C73\u5229\u90FD\u4EBA\u6BCF\u6B21\u56DE\u6765\u90FD\u53EF\u4EE5\u7EED\u7EE7\u751F\u4EA7, \u800C\u4ED6\u4E0B\u6B21\u6253\u8FC7\u6765\u65F6, \u5C31\u53C8\u6709\u94B1\u7CAE\u53EF\u4EE5\u63A0\u593A\u4E86.</p><p>\u4F46\u662F\u5C31\u662F\u70E7\u5E84\u7A3C\u7684\u65F6\u5019, \u51FA\u4E86\u4E8B. \u5728\u6218\u4E89\u7684\u7B2C\u5341\u4E8C\u4E2A\u5E74\u5934\u91CC, \u5415\u5E95\u4E9A\u7684\u519B\u961F\u70E7\u7CAE\u98DF\u7684\u65F6\u5019, \u4E00\u573A\u5F3A\u98CE\u628A\u706B\u5F15\u5411\u4E86\u96C5\u5178\u5A1C\u7684\u795E\u795E\u5E99, \u7ED3\u679C\u628A\u5E99\u7ED9\u70E7\u4E86. \u963F\u5F8B\u963F\u94C1\u65AF\u56DE\u53BB\u540E\u5C31\u75C5\u5012\u4E86.</p><p>\u7531\u4E8E\u75C5\u603B\u662F\u4E0D\u597D, \u4ED6\u5C31\u6D3E\u4EBA\u53BB\u8BF7\u793A\u795E\u8C15, \u4F46\u5973\u5DEB\u8BF4, \u4ED6\u4EEC\u4E0D\u628A\u5415\u5E95\u4E9A\u4EBA\u5728\u7C73\u5229\u90FD\u70E7\u6389\u7684\u795E\u5E99\u91CD\u5EFA\u8D77\u6765, \u662F\u4E0D\u80FD\u5F97\u5230\u795E\u8C15\u7684. \u4E8E\u662F\u963F\u5F8B\u963F\u94C1\u65AF\u5C31\u6D3E\u4EBA\u53BB\u7C73\u5229\u90FD\u8BB2\u548C.</p><p>\u7C73\u5229\u90FD\u7684\u7EDF\u6CBB\u8005\u7279\u62C9\u53D9\u5E03\u6D1B\u65AF\u5F97\u77E5\u6B64\u4E8B\u540E, \u4FBF\u96C6\u5168\u57CE\u4E4B\u8D22\u529B\u7CAE\u98DF, \u5728\u4F7F\u8005\u5230\u6765\u65F6\u5C3D\u60C5\u996E\u9152\u72C2\u6B22.</p><p>\u963F\u5F8B\u963F\u94C1\u65AF\u4EE5\u4E3A\u7C73\u5229\u8005\u4EBA\u5DF2\u56F0\u82E6\u4E0D\u582A, \u4F46\u5F97\u5230\u7684\u6D88\u606F\u5374\u6B63\u597D\u76F8\u53CD. \u4E8E\u662F\u4F7F\u548C\u7C73\u5229\u5730\u90FD\u4EBA\u7F14\u7ED3\u4E86\u4E00\u9879\u6761\u7EA6, \u4F7F\u4E24\u56FD\u6210\u4E86\u76DF\u53CB.</p><p>\u800C\u6700\u540E, \u963F\u5F8B\u963F\u94C1\u65AF\u5728\u7C73\u5229\u90FD\u91CD\u5EFA\u4E86\u4E24\u5EA7\u795E\u6BBF.</p><h2 id="\u4E58\u7740\u6D77\u8C5A\u6765\u5854\u4F0A\u90A3\u9686\u7684\u963F\u5229\u6634" tabindex="-1"><a class="header-anchor" href="#\u4E58\u7740\u6D77\u8C5A\u6765\u5854\u4F0A\u90A3\u9686\u7684\u963F\u5229\u6634" aria-hidden="true">#</a> \u4E58\u7740\u6D77\u8C5A\u6765\u5854\u4F0A\u90A3\u9686\u7684\u963F\u5229\u6634</h2><p>\u8FD9\u662F\u5B8C\u5168\u63D2\u8FDB\u6765\u7684\u5947\u95FB\u5F02\u4E8B.</p><p>\u5C06\u4E0A\u8FF0\u795E\u8C15\u544A\u8BC9\u7C73\u5229\u591A\u7684\u7279\u62C9\u5E03\u6D1B\u65AF\u7684\u4EBA\u662F\u79D1\u6797\u65AF\u7684\u50ED\u4E3B\u57F9\u5229\u5B89\u591A\u6D1B\u65AF. \u5728\u8FD9\u4E2A\u4EBA\u6D3B\u7740\u7684\u65F6\u5019\u53D1\u751F\u4E86\u4E00\u4EF6\u5947\u95FB\u5F02\u4E8B.</p><p>\u963F\u5229\u6634\u662F\u4E3E\u4E16\u65E0\u53CC\u7684\u7AD6\u7434\u624B, \u5728\u610F\u5927\u5229\u548C\u897F\u91CC\u53D1\u4E86\u5927\u8D22, \u53C8\u56DE\u60F3\u56DE\u5230\u79D1\u6797\u65AF\u6765. \u5728\u56DE\u6765\u8DEF\u4E0A, \u8239\u5458\u60F3\u8981\u62A2\u52AB\u4ED6\u7684\u8D22\u4EA7, \u5C31\u903C\u4ED6\u8DF3\u4E86\u6D77.</p><p>\u4ED6\u5728\u6D77\u4E2D\u88AB\u6D77\u8C5A\u6240\u6551, \u5E26\u4ED6\u5230\u4E86\u5854\u4F0A\u90A3\u9686. \u57F9\u5229\u5B89\u591A\u6D1B\u65AF\u5F00\u59CB\u4E0D\u76F8\u4FE1\u4ED6\u7684\u8BDD, \u771F\u5230\u62A2\u52AB\u4ED6\u7684\u6C34\u624B\u56DE\u5230\u79D1\u6797\u65AF, \u8FD9\u4E8B\u624D\u5F97\u5230\u8BC1\u5B9E.</p>',40),d=[o];function h(c,n){return e(),a("div",null,d)}var _=p(t,[["render",h],["__file","\u4E89\u7AEF\u7684\u8D77\u6E90\u5230\u7D22\u4F26\u6E38\u5386\u524D.html.vue"]]);export{_ as default};
