import{_ as p,o as r,c as e,e as t}from"./app.efeddd24.js";var i="/blog/img/project/mingjie/01.jpeg",a="/blog/img/project/mingjie/02.jpeg";const o={},n=t('<h1 id="\u4EA7\u54C1\u7ECF\u7406\u548C\u5F00\u53D1\u4EBA\u5458\u7684\u6218\u4E89-\u8EAB\u4EFD\u8BA4\u8BC1-\u5974\u96B6\u9053\u5FB7\u4EE5\u53CA\u654F\u6377\u5F00\u53D1\u4F55\u4EE5\u53EF\u80FD" tabindex="-1"><a class="header-anchor" href="#\u4EA7\u54C1\u7ECF\u7406\u548C\u5F00\u53D1\u4EBA\u5458\u7684\u6218\u4E89-\u8EAB\u4EFD\u8BA4\u8BC1-\u5974\u96B6\u9053\u5FB7\u4EE5\u53CA\u654F\u6377\u5F00\u53D1\u4F55\u4EE5\u53EF\u80FD" aria-hidden="true">#</a> \u4EA7\u54C1\u7ECF\u7406\u548C\u5F00\u53D1\u4EBA\u5458\u7684\u6218\u4E89\uFF1A\u8EAB\u4EFD\u8BA4\u8BC1\uFF0C\u5974\u96B6\u9053\u5FB7\u4EE5\u53CA\u654F\u6377\u5F00\u53D1\u4F55\u4EE5\u53EF\u80FD</h1><h2 id="\u4E00-\u8EAB\u4EFD\u8BA4\u540C" tabindex="-1"><a class="header-anchor" href="#\u4E00-\u8EAB\u4EFD\u8BA4\u540C" aria-hidden="true">#</a> \u4E00 \u8EAB\u4EFD\u8BA4\u540C</h2><p>\u4E0A\u5B66\u7684\u65F6\u5019, \u8001\u5E08\u4E0A\u8BFE\u65F6, \u8BB2\u4E86\u4E00\u4E2A\u4ED6\u4EB2\u8EAB\u7ECF\u5386\u7684\u6545\u4E8B.</p><p>\u5927\u6982\u662F\u4ED6\u53BB\u519C\u6751\u505A\u666E\u6CD5\u5DE5\u4F5C\u7684\u65F6\u5019. \u548C\u6751\u6C11\u804A\u5929\u804A\u7684\u6E34\u4E86, \u5C31\u8BF4: \u201C\u80FD\u7ED9\u6211\u4E00\u676F\u6C34 (shui) \u559D\u4E48?\u201D</p><p>\u6751\u6C11\u4EEC\u5148\u662F\u4E00\u6123, \u7EE7\u800C\u54C4\u5802\u5927\u7B11: \u201C\u559D\u6C34 (fei) \u5C31\u559D\u6C34 (fei) \u561B, \u8FD8\u559D\u6C34 (shui).\u201D</p><p>\u897F\u5317\u65B9\u8A00\u4E2D, \u201C\u6C34\u201D \u5B57\u53D1 \u201Cfei\u201D \u7684\u97F3, \u8001\u5E08\u662F\u77E5\u8BC6\u5206\u5B50, \u81EA\u7136\u64CD\u4E00\u53E3\u6807\u51C6\u7684\u666E\u901A\u8BDD. \u6751\u6C11\u4EEC\u4FBF\u5632\u7B11\u4ED6\u88C5\u8154\u4F5C\u52BF.</p><p>\u8001\u5E08\u8BB2\u8FD9\u4E2A\u6545\u4E8B, \u672C\u610F\u662F\u8981\u8BB2, \u6CD5\u5F8B\u662F\u4E00\u95E8\u6781\u52A1\u5B9E\u7684\u5B66\u95EE, \u6240\u4EE5\u5728\u73B0\u5B9E\u4E2D, \u4E00\u5B9A\u8981\u4E0D\u8981\u4E00\u5929\u5230\u665A\u628A\u6CD5\u5F8B\u672F\u8BED\u6302\u5728\u5634\u4E0A.</p><p>\u800C\u6211\u542C\u5230\u4E2A\u8FD9\u6545\u4E8B, \u5374\u65E0\u7AEF\u60F3\u8D77\u963F Q \u6765:</p><blockquote><p>\u52A0\u4EE5\u8FDB\u4E86\u51E0\u56DE\u57CE, \u963F Q \u81EA\u7136\u66F4\u81EA\u8D1F, \u7136\u800C\u4ED6\u53C8\u5F88\u9119\u8584\u57CE\u91CC\u4EBA, \u8B6C\u5982\u7528\u4E09\u5C3A\u4E09\u5BF8\u5BBD\u7684\u6728\u677F\u505A\u7684\u51F3\u5B50, \u672A\u5E84\u4EBA\u53EB \u201C\u957F\u51F3\u201D, \u57CE\u91CC\u4EBA\u5374\u53EB \u201C\u6761\u51F3\u201D, \u4ED6\u60F3: \u8FD9\u662F\u9519\u7684, \u53EF\u7B11! \u6CB9\u714E\u5927\u5934\u9C7C, \u672A\u5E84\u90FD\u52A0\u4E0A\u534A\u5BF8\u957F\u7684\u8471\u53F6, \u57CE\u91CC\u5374\u90FD\u52A0\u4E0A\u5207\u7EC6\u7684\u8471\u4E1D, \u4ED6\u60F3: \u8FD9\u4E5F\u662F\u9519\u7684, \u53EF\u7B11! \u7136\u800C\u672A\u5E84\u4EBA\u771F\u662F\u4E0D\u89C1\u4E16\u9762\u7684\u53EF\u7B11\u4E61\u4E0B\u4EBA\u5475, \u4ED6\u4EEC\u6CA1\u6709\u89C1\u8FC7\u57CE\u91CC\u7684\u714E\u9C7C!</p><p>\u2014\u2014 \u300A\u963F Q \u6B63\u4F20\u300B</p></blockquote><p>\u201Cfei\u201D \u4E0D\u4EC5\u4EC5\u662F\u4E00\u4E2A\u53D1\u97F3, \u800C \u201C\u957F\u51F3\u201D \u4E5F\u4E0D\u4EC5\u4EC5\u662F\u4E00\u4E2A\u53EB\u6CD5, \u5B83\u4EEC\u80CC\u540E\u662F\u4E00\u5957\u8EAB\u4EFD\u8BA4\u540C\u673A\u5236. \u6751\u6C11\u548C\u963F Q \u7684\u8EAB\u4EFD\u8BA4\u540C\u5EFA\u7ACB\u4E8E\u5176\u4E0A.</p><p>\u4E3A\u4E86\u4FDD\u8BC1\u8FD9\u79CD\u8EAB\u4EFD\u7684\u6B63\u5F53\u6027, \u6C34\u53EA\u80FD\u5FF5 (fei), \u5426\u5219\u4FBF\u662F\u88C5\u8154\u4F5C\u52BF, \u800C\u4E09\u5C3A\u957F\u4E09\u5BF8\u5BBD\u7684\u6728\u677F\u505A\u7684\u51F3\u5B50, \u5FC5\u987B\u53EB \u201C\u957F\u51F3\u201D \u5426\u5219\u4FBF \u201C\u662F\u9519\u7684, \u53EF\u7B11\u201D.</p><p>\u800C\u963F Q \u8FD8\u591A\u4E00\u79CD\u8EAB\u4EFD\u8BA4\u540C, \u4ED6\u662F \u201C\u8FDB\u8FC7\u51E0\u56DE\u57CE\u201D \u7684, \u56E0\u6B64\u4ED6\u4E0D\u4F46\u80FD\u5632\u7B11\u57CE\u91CC\u4EBA\u7684 \u201C\u6761\u51F3\u201D, \u8FD8\u80FD\u5632\u7B11\u4E0D\u77E5\u9053 \u201C\u6761\u51F3\u201D \u7684\u672A\u5E84\u4EBA.</p><h2 id="\u4E8C-\u4E3B\u5974\u9053\u5FB7\u8BF4" tabindex="-1"><a class="header-anchor" href="#\u4E8C-\u4E3B\u5974\u9053\u5FB7\u8BF4" aria-hidden="true">#</a> \u4E8C \u4E3B\u5974\u9053\u5FB7\u8BF4</h2><p>\u5173\u4E8E\u9053\u5FB7, \u5C3C\u91C7\u6709\u4E00\u4E2A \u201C\u4E3B\u5974\u9053\u5FB7\u201D \u8BF4.</p><p>\u4ED6\u8BA4\u4E3A\u6700\u57FA\u672C\u7684\u9053\u5FB7\u5F62\u6001\u6709\u4E24\u79CD: \u201C\u4E3B\u4EBA\u7684\u9053\u5FB7\u201D \u548C \u201C\u5974\u96B6\u7684\u9053\u5FB7\u201D.</p><p>\u4E3B\u4EBA\u7684\u9053\u5FB7\u6765\u6E90\u4E8E\u80DC\u5229\u8005\u5BF9\u4E8E\u5F3A\u8005\u7684\u6B4C\u9882, \u56E0\u6B64, \u4E3B\u4EBA\u7684\u9053\u5FB7\u4EE5 <strong>\u81EA\u6211\u80AF\u5B9A, \u8363\u8A89\u548C\u4E3B\u52A8\u4E3A\u7279\u5F81</strong>.</p><p>\u800C\u5974\u96B6\u7684\u9053\u5FB7\u5219\u662F\u6765\u6E90\u4E8E\u5974\u96B6\u4EEC\u5BF9\u4E8E\u4E3B\u4EBA\u7684\u53CD\u6297, \u56E0\u6B64\u51E1\u662F\u4E3B\u4EBA\u8BA4\u4E3A\u662F\u597D\u7684, \u4ED6\u4EEC\u4FBF\u8BA4\u4E3A\u662F\u6076, \u5E76\u4ECE\u6B64\u627E\u51FA\u4E00\u4E2A\u5BF9\u7ACB\u9762\u6765, \u79F0\u4E4B\u4E3A\u5584.</p><p>\u6240\u4EE5\u4E3B\u4EBA\u8BF4 <strong>\u81EA\u6211\u80AF\u5B9A</strong>, \u4ED6\u4EEC\u4FBF\u8BB2 <strong>\u81EA\u6211\u5426\u5B9A</strong>; \u4E3B\u4EBA\u8BB2 <strong>\u8363\u8A89</strong>, \u4ED6\u4EEC\u4FBF\u8BB2 <strong>\u8C26\u5351</strong>; \u4E3B\u4EBA\u8BB2 <strong>\u4E3B\u52A8</strong>, \u4ED6\u4EEC\u4FBF\u8BB2 <strong>\u88AB\u52A8</strong>.</p><p>\u5974\u96B6\u7684\u9053\u5FB7, \u5148\u8981\u6709\u4E00\u4E2A\u8EAB\u4EFD\u8BA4\u540C, \u4E0E\u8FD9\u4E2A\u8EAB\u4EFD\u8BA4\u540C\u76F8\u540C\u7684, \u5C31\u662F\u5584\u7684, \u5BF9\u7684; \u4E0E\u8FD9\u4E2A\u8EAB\u4EFD\u8BA4\u540C\u76F8\u5F02\u7684, \u5C31\u662F\u6076\u7684, \u9519\u7684.</p><p>\u4E0E\u662F, \u628A\u6C34\u5FF5\u4F5C \u201Cshui\u201D \u6216 \u201Cfei\u201D, \u628A\u4E09\u5C3A\u957F\u4E09\u5BF8\u5BBD\u7684\u6728\u677F, \u79F0\u4F5C \u201C\u957F\u51F3\u201D \u6216 \u201C\u6761\u51F3\u201D, \u4FBF\u56E0\u4E3A\u8EAB\u4EFD\u8BA4\u540C\u800C\u6709\u4E86\u9053\u5FB7\u4E0A\u7684\u4EF7\u503C.</p><p><strong>\u201C\u975E\u6211\u65CF\u7C7B, \u5176\u5FC3\u5FC5\u5F02\u201D, \u8BF4\u7684\u4FBF\u662F\u8FD9\u4E8B.</strong></p><p>\u5728\u8FD9\u79CD\u9053\u5FB7\u8BA4\u77E5\u4E2D, \u53EA\u6709\u4E24\u5957\u8BDD\u8BED: \u201C\u6211\u662F\u8C01\u201D \u548C \u201C\u6240\u4EE5\u4F60\u4EEC\u90FD\u662F\u9519\u7684\u201D.</p><p>\u5F53\u7136, \u5974\u96B6\u7684\u9053\u5FB7\u4E0D\u4EC5\u5B58\u5728\u4E8E\u9065\u8FDC\u7684\u897F\u5317\u519C\u6751\u548C\u8FC7\u53BB\u7684\u672A\u5E84\u963F Q \u8EAB\u4E0A.</p><p>\u4E5F\u5B58\u5728\u4E8E, \u5973\u6743\u4E3B\u4E49\u548C\u76F4\u7537\u764C\u7684\u5BF9\u7ACB\u4E2D; \u7231\u72D7\u4EBA\u58EB\u548C\u7981\u72D7\u4EBA\u58EB\u7684\u5BF9\u7ACB\u4E2D; \u201C\u4E0D\u5B5D\u5B50\u5973\u201C \u548C \u201C\u7236\u6BCD\u7686\u7978\u5BB3\u201C \u7684\u5BF9\u7ACB\u4E2D.</p><p>\u751A\u81F3\u66F4\u5E7F\u6CDB\u7684\u5B58\u5728\u4E8E, \u751C\u515A\u4E0E\u54B8\u515A, \u732B\u515A\u4E0E\u72D7\u515A, \u7C89\u4E1D\u4E0E\u975E\u7C89\u4E1D, \u4E0D\u540C\u5076\u50CF\u7684\u7C89\u4E1D\u4E4B\u95F4.</p><p>\u81EA\u7136, \u5728\u5B58\u5728\u4E8E\u4EA7\u54C1\u7ECF\u7406\u4E0E\u7A0B\u5E8F\u5458\u7684\u6218\u4E89\u4E2D.</p><h2 id="\u4E09-\u4EA7\u54C1\u7ECF\u7406\u548C\u7A0B\u5E8F\u5458\u7684\u6218\u4E89" tabindex="-1"><a class="header-anchor" href="#\u4E09-\u4EA7\u54C1\u7ECF\u7406\u548C\u7A0B\u5E8F\u5458\u7684\u6218\u4E89" aria-hidden="true">#</a> \u4E09 \u4EA7\u54C1\u7ECF\u7406\u548C\u7A0B\u5E8F\u5458\u7684\u6218\u4E89</h2><p>\u4EA7\u54C1\u7ECF\u7406\u548C\u7A0B\u5E8F\u5458\u7684\u77DB\u76FE\u4F3C\u4E4E\u7531\u6765\u5DF2\u4E45, \u4F46\u4ECE\u672A\u8C61\u73B0\u5728\u8FD9\u4E48\u7D27\u5F20.</p><p>\u51E0\u4E4E\u6240\u6709\u4E0E\u6280\u672F\u6709\u5173\u7684\u5A92\u4F53\u591A\u591A\u5C11\u5C11\u90FD\u8C03\u4F83\u8FC7\u4EA7\u54C1\u7ECF\u7406\u548C\u5F00\u53D1\u4EBA\u5458\u7684\u77DB\u76FE.</p><p>\u8FD9\u79CD\u8C03\u4F83\u662F\u53EF\u4EE5\u5FEB\u901F\u57FA\u4E8E\u8EAB\u4EFD\u8BA4\u540C\u4EA7\u751F\u60C5\u611F\u5171\u9E23\u7684. \u5F53\u7136, \u5982\u679C\u6CA1\u6709\u8FD9\u79CD\u5FEB\u901F\u4EA7\u751F\u60C5\u611F\u5171\u9E23\u7684\u6548\u7387, \u5B83\u53EF\u80FD\u4E5F\u4E0D\u4F1A\u5982\u6B64\u5FEB\u901F\u4F20\u64AD\u8D77\u6765.</p><p>\u800C\u8FD9\u6F2B\u753B\u4E5F\u9887\u5F97\u963F Q \u771F\u4F20. \u5982\u5176 <strong>\u65E2\u5632\u7B11\u57CE\u91CC\u4EBA\u7684 \u201C\u6761\u51F3\u201D \u53C8\u5632\u7B11\u672A\u5E84\u4EBA\u7684\u4E0D\u77E5\u9053 \u201C\u6761\u51F3\u201D</strong> \u4E00\u822C, \u65E2\u8C03\u4F83\u4E86\u4EA7\u54C1\u7ECF\u7406, \u4E5F\u8C03\u4F83\u4E86\u7A0B\u5E8F\u5458.</p><p>\u4E0E\u662F\u4E4E\u8BFB\u8005\u4EEC\u4FBF\u5728\u563B\u7B11\u4E2D, \u5BF9\u53F7\u5165\u5750, \u5404\u81EA\u52A0\u6DF1\u4E86\u8EAB\u4EFD\u7684\u8BA4\u540C\u548C\u5BF9\u6297\u7684\u60C5\u7EEA.</p><p>\u8FD9\u4E2A\u95EE\u9898\u662F\u65E0\u89E3\u7684.</p><p><strong>\u56E0\u4E3A\u4E92\u8054\u7F51\u5A92\u4F53\u4E3A\u4E86\u80FD\u591F\u5FEB\u901F\u5F62\u6210\u4F20\u64AD, \u4E00\u5B9A\u4F1A\u8BD5\u56FE\u5236\u9020\u5F3A\u70C8\u7684\u60C5\u611F\u51B2\u7A81; \u800C\u4E3A\u4E86\u5236\u9020\u60C5\u611F\u51B2\u7A81\u4E00\u5B9A\u4F1A\u8BD5\u7740\u5851\u9020\u8EAB\u4EFD\u8BA4\u540C; \u800C\u5229\u7528\u5974\u96B6\u7684\u9053\u5FB7\u5236\u9020\u5BF9\u7ACB, \u5219\u662F\u5FEB\u901F\u5EFA\u7ACB\u8EAB\u4EFD\u8BA4\u540C\u6700\u6709\u6548\u7684\u624B\u6BB5.</strong></p><p>\u56E0\u6B64, \u6211\u4EEC\u53EF\u4EE5\u60F3\u5230, \u672A\u6765\u4E00\u5B9A\u4F1A\u6709\u4EBA\u7EE7\u7EED\u8D29\u5356\u4EA7\u54C1\u7ECF\u7406\u548C\u7A0B\u5E8F\u5458\u7684\u77DB\u76FE, \u5E76\u4E14\u5FC5\u7136\u7684\u4F1A\u8FDB\u4E00\u6B65\u52A0\u6DF1\u4ED6\u4EEC\u4E4B\u95F4\u7684\u5BF9\u6297.</p><p>\u90A3\u4E48\u4EA7\u54C1\u7ECF\u7406\u548C\u7A0B\u5E8F\u5458\u53EA\u6709\u4E92\u76F8\u4F24\u5BB3\u4E00\u6761\u8DEF\u53EF\u4EE5\u8D70\u4E48?</p><p>\u5F53\u7136\u4E0D\u662F.</p><h2 id="\u56DB-\u7011\u5E03\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u56DB-\u7011\u5E03\u6A21\u578B" aria-hidden="true">#</a> \u56DB \u7011\u5E03\u6A21\u578B</h2><p>\u4F5C\u4E3A\u7ECF\u5178\u7684\u8F6F\u4EF6\u5F00\u53D1\u751F\u547D\u5468\u671F\u6A21\u578B, \u7011\u5E03\u6A21\u578B\u5F53\u7136\u5BF9\u4E8E\u8FD9\u79CD\u57FA\u4E8E\u5C97\u4F4D\u8EAB\u4EFD\u8BA4\u540C\u7684\u5BF9\u6297\u6709\u4E00\u4E2A\u89E3\u51B3\u65B9\u6848.</p><p>\u7011\u5E03\u6A21\u578B\u5927\u4F53\u5982\u4E0B\u56FE\u6240\u793A.</p><p><img src="'+i+'" alt="\u7011\u5E03\u6A21\u578B"></p><p>\u7B80\u5355\u8BF4\u6765, \u5176\u6D41\u7A0B\u5982\u4E0B:</p><ul><li>\u9700\u6C42\u5206\u6790\u9636\u6BB5: <ul><li>\u9700\u6C42\u4EBA\u5458\u8FDB\u573A, \u8FDB\u884C\u9700\u6C42\u5206\u6790, \u5F62\u6210\u6587\u6863;</li><li>\u6240\u6709\u5229\u76CA\u5173\u7CFB\u4EBA\u5F00\u4F1A, \u786E\u8BA4\u5927\u5BB6\u4E0D\u4F1A\u6539\u4E86, \u7136\u540E\u7B7E\u5B57;</li><li>\u8FDB\u5165\u4E0B\u4E00\u9636\u6BB5;</li></ul></li><li>\u8BBE\u8BA1\u9636\u6BB5 <ul><li>\u8BBE\u8BA1\u4EBA\u5458\u8FDB\u573A, \u8FDB\u884C\u8BBE\u8BA1, \u5F62\u6210\u6587\u6863;</li><li>\u6240\u6709\u5229\u76CA\u5173\u7CFB\u4EBA\u5F00\u4F1A, \u786E\u8BA4\u5927\u5BB6\u4E0D\u4F1A\u6539\u4E86, \u7136\u540E\u7B7E\u5B57;</li><li>\u8FDB\u5165\u4E0B\u4E00\u9636\u6BB5;</li></ul></li><li>\u5F00\u53D1\u9636\u6BB5 <ul><li>\u5F00\u53D1\u4EBA\u5458\u8FDB\u573A, \u8FDB\u884C\u5F00\u53D1, \u5F62\u6210\u4EE3\u7801;</li><li>\u5F00\u53D1\u7ED3\u675F, \u8FDB\u5165\u5230\u6D4B\u8BD5\u9636\u6BB5;</li></ul></li><li>\u6D4B\u8BD5\u9636\u6BB5 <ul><li>\u6D4B\u8BD5\u4EBA\u5458\u8FDB\u573A, \u8FDB\u884C\u6D4B\u8BD5, \u5F00\u53D1\u4EBA\u5458\u6539 BUG, \u6700\u7EC8\u5F62\u6210\u6D4B\u8BD5\u62A5\u544A;</li><li>\u6240\u6709\u5229\u76CA\u5173\u7CFB\u4EBA\u5F00\u4F1A, \u786E\u8BA4\u5F00\u53D1\u7ED3\u679C\u6CA1\u95EE\u9898, \u7136\u540E\u7B7E\u5B57;</li><li>\u5E03\u7F72\u5230\u6B63\u5F0F\u73AF\u5883, \u8FDB\u5165\u8FD0\u7EF4\u9636\u6BB5.</li></ul></li></ul><p>\u5982\u679C\u6211\u4EEC\u628A\u8FD9\u4E2A\u6D41\u7A0B\u7528\u65F6\u5E8F\u56FE\u6765\u8868\u793A:</p><p><img src="'+a+'" alt="\u7011\u5E03\u6A21\u578B\u65F6\u5E8F\u56FE"></p><p>\u7011\u5E03\u6A21\u578B\u5176\u5B9E\u5E76\u4E0D\u662F\u89E3\u51B3\u4E86\u4E0D\u540C\u5C97\u4F4D\u4E4B\u95F4\u7684\u77DB\u76FE, \u800C\u662F\u907F\u5F00\u4E86\u77DB\u76FE: \u5728\u8FD9\u4E2A\u6A21\u578B\u4E2D, \u7406\u60F3\u72B6\u6001\u4E0B, \u4EBA\u4E0E\u4EBA\u4E4B\u95F4\u662F\u4E0D\u7528\u6C9F\u901A\u7684, \u6240\u6709\u4EBA\u90FD\u53EA\u548C\u6587\u6863\u7CFB\u7EDF\u6C9F\u901A\u5C31\u884C\u4E86.</p><p>\u8FD9\u4E2A\u89E3\u51B3\u65B9\u6848\u5F53\u7136\u662F\u6709\u6548\u7684. \u751A\u81F3, \u5982\u679C\u4EA7\u54C1\u7ECF\u7406\u548C\u7A0B\u5E8F\u5458\u7684\u77DB\u76FE\u8FBE\u5230\u53CC\u62D2\u7EDD\u6C9F\u901A\u7684\u7A0B\u5EA6, \u8BC9\u8BF8\u6587\u6863\u4F53\u7CFB\u662F\u6700\u540E\u7684\u624B\u6BB5:</p><blockquote><p>\u7A0B\u5E8F\u5458\u8BF4: \u201C\u4F60\u4E0D\u8981\u7ED9\u6211\u8BB2\u4EC0\u4E48\u9700\u6C42. \u4F60\u53BB\u5199\u6210\u6587\u6863, \u53D1\u90AE\u4EF6\u7ED9\u6211, \u5E76\u4E14\u6284\u9001\u9886\u5BFC. \u5982\u679C\u9886\u5BFC\u540C\u610F\u4E86, \u6211\u5C31\u5F00\u53D1.\u201D</p><p>\u4EA7\u54C1\u7ECF\u7406\u8BF4: \u201C\u4F60\u4E0D\u8981\u7ED9\u6211\u8BB2\u4E3A\u4EC0\u4E48\u5B9E\u73B0\u4E0D\u4E86. \u4F60\u628A\u60C5\u51B5\u5199\u6210\u90AE\u4EF6\u53D1\u6211, \u5E76\u4E14\u6284\u9001\u9886\u5BFC, \u5982\u679C\u9886\u5BFC\u8BF4\u8FD9\u4E8B\u4E0D\u505A\u4E86, \u6211\u5C31\u628A\u9700\u6C42\u53D6\u6389.\u201D</p></blockquote><p>\u4F46\u662F\u8FD9\u79CD\u89E3\u51B3\u65B9\u6848\u6709\u4E24\u4E2A\u5F88\u9EBB\u70E6\u7684\u95EE\u9898:</p><h3 id="_1-\u6240\u6709\u4EBA\u4E0D\u5F97\u8D8A\u754C" tabindex="-1"><a class="header-anchor" href="#_1-\u6240\u6709\u4EBA\u4E0D\u5F97\u8D8A\u754C" aria-hidden="true">#</a> 1 \u6240\u6709\u4EBA\u4E0D\u5F97\u8D8A\u754C</h3><p>\u7011\u5E03\u6A21\u578B\u7684\u6587\u6863\u7CFB\u7EDF\u80CC\u540E\u662F\u4E00\u6574\u5957\u6743\u8D23\u7CFB\u7EDF. <strong>\u6240\u6709\u4EBA\u57FA\u4E8E\u6587\u6863\u8FDB\u884C\u5206\u5DE5\u65F6, \u5176\u5B9E\u4E5F\u662F\u5728\u5212\u5206\u6743\u8D23\u7684\u754C\u9650</strong>.</p><p><strong>\u6240\u4EE5\u4EBA\u5FC5\u987B\u53EA\u5BF9\u81EA\u5DF1\u7684\u5206\u5DE5\u5177\u6709\u6743\u8D23, \u800C\u4E0D\u80FD\u6709\u4EFB\u4F55\u8D8A\u754C.</strong></p><p>\u5982\u679C\u6709\u4EBA\u5728\u67D0\u4E2A\u5206\u5DE5\u4E0A\u8D8A\u754C, \u8FD9\u4E2A\u5DE5\u4F5C\u4E00\u5B9A\u51FA\u95EE\u9898. \u56E0\u4E3A\u4E00\u65E6\u8D8A\u754C, \u5C31\u7ACB\u523B\u4F1A\u5F62\u6743\u8D23\u6DF7\u6DC6, \u6700\u540E\u5C31\u5F62\u6210\u6CA1\u6709\u4EBA\u5BF9\u6B64\u4E8B\u8D1F\u8D23\u7684\u5C40\u9762.</p><p>\u6BD4\u5982: \u5982\u679C\u4EA7\u54C1\u7ECF\u7406\u5C31\u67D0\u4E2A\u529F\u80FD\u63D0\u51FA\u4E00\u4E2A\u5982\u4F55\u5B9E\u73B0\u7684\u6280\u672F\u65B9\u6848, \u8FD9\u4E2A\u529F\u80FD\u4E00\u65E6\u51FA\u4E86\u95EE\u9898, \u5927\u5BB6\u5C31\u4F1A\u5F00\u59CB\u626F\u8FD9\u6837\u7684\u76AE:</p><blockquote><p>\u5F00\u53D1: \u201C\u662F\u4EA7\u54C1\u7ECF\u7406\u8BA9\u6211\u8FD9\u6837\u505A\u7684\u554A.\u201D</p><p>\u4EA7\u54C1\u7ECF\u7406: \u201C\u4F60\u662F\u4E13\u4E1A\u7684\u6280\u672F\u4EBA\u5458, \u4F60\u5E94\u8BE5\u5BF9\u6B64\u6709\u8D77\u7801\u7684\u5224\u65AD\u554A.\u201D</p></blockquote><p><strong>\u65E0\u8BBA\u6700\u540E, \u677F\u5B50\u6253\u5230\u8C01\u7684\u5C41\u80A1\u4E0A, \u53CD\u6B63\u4E8B\u513F\u662F\u9EC4\u4E86</strong>.</p><p>\u6240\u4EE5\u5BF9\u4E8E\u6BCF\u4E2A\u4EBA\u6765\u8BF4, \u6700\u8D1F\u8D23\u7684\u884C\u4E3A\u662F, <strong>\u4E0D\u5BF9\u81EA\u5DF1\u5206\u5DE5\u4EE5\u5916\u7684\u4EFB\u4F55\u4E8B\u60C5\u63D0\u51FA\u4EFB\u4F55\u5EFA\u8BAE.</strong></p><h3 id="_2-\u7CFB\u7EDF\u6027\u7529\u9505" tabindex="-1"><a class="header-anchor" href="#_2-\u7CFB\u7EDF\u6027\u7529\u9505" aria-hidden="true">#</a> 2 \u7CFB\u7EDF\u6027\u7529\u9505</h3><p>\u7CFB\u7EDF\u6027\u7529\u9505\u662F\u6211\u675C\u64B0\u51FA\u6765\u7684\u4E00\u4E2A\u8BCD. \u5B83\u6307\u7684\u662F\u8FD9\u6837\u4E00\u79CD\u60C5\u51B5:</p><p>\u5F53\u9879\u76EE\u8FDB\u884C\u8FC7\u7A0B\u4E2D, \u51FA\u73B0\u4E00\u4E2A\u4EE5\u524D\u6CA1\u6709\u9884\u6599\u5230\u7684\u95EE\u9898\u65F6, \u53EA\u8981\u4E2A\u8FD9\u95EE\u9898\u8FD8\u4E0D\u81F3\u4E8E\u8BA9\u9879\u76EE\u73B0\u573A\u5B8C\u86CB, \u5BF9\u6240\u6709\u4EBA\u6765\u8BF4, \u6700\u7406\u6027\u7684\u9009\u62E9\u662F\u88C5\u4F5C\u770B\u4E0D\u89C1\u8FD9\u4E2A\u95EE\u9898.</p><p>\u56E0\u4E3A\u6240\u6709\u4EBA\u53EA\u5BF9\u81EA\u5DF1\u5177\u4F53\u7684\u5206\u5DE5\u8D1F\u8D23, \u6240\u4EE5\u4E8B\u5B9E\u4E0A\u6CA1\u6709\u4EBA\u5BF9\u9879\u76EE\u672C\u8EAB\u8D1F\u8D23.</p><p>\u800C\u5F53\u8FD9\u4E2A\u95EE\u9898\u5BFC\u81F4\u9879\u76EE\u5931\u8D25\u540E, \u6211\u4EEC\u8FFD\u67E5\u8D23\u4EFB\u65F6, \u4F1A\u53D1\u73B0\u6CA1\u6709\u4EBA\u5E94\u8BE5\u4E3A\u6B64\u8D1F\u8D23. \u9505\u5C31\u8FD9\u6837\u88AB\u7CFB\u7EDF\u6027\u7684\u7529\u51FA\u53BB\u4E86.</p><p>\u5F53\u7136, \u8FD9\u79CD\u60C5\u51B5\u4E0B, \u5927\u5BB6\u53D1\u6325\u66B4\u6C11\u653F\u6CBB, \u627E\u4E2A\u66FF\u7F6A\u7F8A\u6765\u625B\u9505\u4E5F\u4E0D\u662F\u4E0D\u53EF\u80FD\u7684.</p><p>\u6240\u4EE5\u5728\u8FD9\u79CD\u7CFB\u7EDF\u4E2D, \u660E\u54F2\u4FDD\u8EAB\u548C\u4EBA\u9645\u5173\u7CFB\u662F\u8981\u6BD4\u6280\u672F, \u80FD\u529B\u91CD\u8981\u767E\u500D\u7684\u4E8B\u60C5.</p><hr><p>\u65E2\u7136\u7011\u5E03\u6A21\u578B\u5E26\u6765\u7684\u5206\u5DE5\u6A21\u5F0F\u6709\u4E9B\u8D1F\u4F5C\u7528, \u90A3\u6211\u4EEC\u662F\u4E0D\u662F\u53EF\u4EE5\u4E0D\u5206\u5DE5, \u8BA9\u6240\u6709\u4EBA\u4E3A\u6240\u6709\u4E8B\u8D1F\u8D23\u5462?</p><p>\u4E0D\u884C. \u56E0\u4E3A\u5982\u679C\u4E0D\u8FD9\u6837, \u56E2\u961F\u4E2D\u4E0D\u540C\u5C97\u4F4D\u7684\u4EBA, \u4F1A\u56E0\u4E3A\u8EAB\u4EFD\u8BA4\u540C\u548C\u5974\u96B6\u9053\u5FB7\u5435\u6210\u4E00\u9505, \u7136\u540E\u8FC5\u901F\u5206\u88C2.</p><p>\u90A3\u4E48\u8FD8\u6709\u522B\u7684\u51FA\u8DEF\u4E48? \u6709\u7684.</p><h2 id="\u4E94-\u4E24\u79CD\u8BED\u8A00" tabindex="-1"><a class="header-anchor" href="#\u4E94-\u4E24\u79CD\u8BED\u8A00" aria-hidden="true">#</a> \u4E94 \u4E24\u79CD\u8BED\u8A00</h2><p>\u8BF4\u4E2A\u516B\u5366\u554A? \u4E3A\u4EC0\u4E48\u556A\u556A\u7684\u65F6\u5019\u8981\u53EB\u5E8A?</p><p>\u7B54\u6848\u662F: \u53EB\u5E8A\u5176\u5B9E\u8D77\u7684\u662F\u52B3\u52A8\u53F7\u5B50\u7684\u4F5C\u7528. \u56E0\u4E3A\u8FD9\u4E2A\u6D3B\u52A8, \u5982\u679C\u53CC\u65B9\u7684\u8282\u594F\u4E0D\u4E00\u81F4, \u8F7B\u5219\u6548\u7387\u4F4E\u4E0B, \u91CD\u5219\u5BB9\u6613\u53D7\u4F24.</p><p>\u6240\u4EE5\u8FD9\u662F\u57FA\u4E8E\u5206\u5DE5\u7684\u534F\u8C03\u673A\u5236.</p><p>\u597D, \u95EE\u9898\u6765\u4E86, <strong>\u4E3A\u4EC0\u4E48\u556A\u556A\u556A\u4E4B\u524D, \u5927\u5BB6\u4E0D\u9700\u8981\u5C31\u53EB\u5E8A\u4E00\u4E8B\u5199\u6587\u6863, \u89C4\u5B9A\u4E00\u4E0B\u8FD9\u4E2A\u8282\u594F\u662F\u4EC0\u4E48\u6837\u7684, \u7136\u540E\u7B7E\u5B57.</strong></p><p>\u56E0\u4E3A: <strong>\u8BED\u8A00\u7684\u76EE\u7684\u662F\u4E3A\u4E86\u8FBE\u6210\u5171\u8BC6, \u800C\u8FBE\u6210\u5171\u8BC6\u6709\u4E24\u79CD\u65B9\u5F0F, \u4E00\u79CD\u65B9\u5F0F\u662F\u901A\u8FC7\u610F\u4E49\u6765\u5171\u8BC6, \u4E00\u79CD\u65B9\u5F0F\u662F\u901A\u8FC7\u60C5\u5883\u6765\u5171\u8BC6</strong>. \u8FD9\u4E24\u79CD\u5171\u8BC6\u5BF9\u5E94\u4E24\u79CD\u8BED\u8A00, \u4E00\u79CD\u53EB\u610F\u4E49\u7684\u8BED\u8A00, \u4E00\u79CD\u53EB\u60C5\u5883\u7684\u8BED\u8A00.</p><p>\u6587\u6863\u662F\u4E00\u79CD\u5178\u578B\u7684\u610F\u4E49\u7684\u8BED\u8A00. \u901A\u8FC7\u4E66\u9762\u7684\u5F62\u5F0F, \u628A\u4E8B\u60C5\u8BF4\u6E05\u695A, \u9053\u660E\u767D, \u6CA1\u6709\u4EFB\u4F55\u6B67\u610F, \u5927\u5BB6\u57FA\u4E8E\u6B64, \u5F62\u6210\u5171\u8BC6. \u800C\u7B7E\u5B57, \u662F\u5BF9\u5171\u8BC6\u7684\u786E\u8BA4.</p><p>\u53EB\u5E8A\u662F\u4E00\u79CD\u5178\u578B\u7684\u60C5\u5883\u7684\u8BED\u8A00, \u5728\u53EB\u5E8A\u4E2D, \u6CA1\u6709\u4EFB\u4F55\u6709\u610F\u4E49\u7684\u8BCD\u6C47. \u5982\u679C\u628A\u5B83\u4ECE\u60C5\u5883\u4E2D\u62FF\u51FA\u6765, \u6211\u4EEC\u5B8C\u5168\u4E0D\u6CD5\u77E5\u9053\u5B83\u5728\u8BF4\u4EC0\u4E48, \u4F46\u5982\u679C\u628A\u5B83\u4EEC\u653E\u56DE\u5230\u60C5\u5883\u4E2D, \u6211\u4EEC\u60F3\u90FD\u4E0D\u7528\u60F3, \u5C31\u77E5\u9053\u662F\u4EC0\u4E48\u610F\u601D\u4E86.</p><p>\u6240\u4EE5\u8981\u8BA9\u4EA7\u54C1\u7ECF\u7406\u548C\u7A0B\u5E8F\u5458\u629B\u5F00\u8EAB\u4EFD\u8BA4\u540C\u548C\u5974\u96B6\u9053\u5FB7, \u9664\u4E86\u5927\u5BB6\u53EA\u5199\u6587\u6863\u4E0D\u8981\u6C9F\u901A\u4EE5\u5916\u8FD8\u6709\u4E00\u79CD\u529E\u6CD5, \u5C31\u662F\u5EFA\u7ACB\u4E00\u79CD\u60C5\u5883\u7684\u8BED\u8A00, \u8BA9\u5927\u5BB6\u5728\u60C5\u5883\u4E2D\u8FBE\u6210\u5171\u8BC6.</p><h2 id="\u516D-\u654F\u6377\u65B9\u6CD5\u4E3A\u4EC0\u4E48\u6709\u6548" tabindex="-1"><a class="header-anchor" href="#\u516D-\u654F\u6377\u65B9\u6CD5\u4E3A\u4EC0\u4E48\u6709\u6548" aria-hidden="true">#</a> \u516D \u654F\u6377\u65B9\u6CD5\u4E3A\u4EC0\u4E48\u6709\u6548</h2><p>\u654F\u6377\u65B9\u6CD5\u63D0\u51FA\u4E00\u5957\u5EFA\u7ACB\u5728\u60C5\u5883\u7684\u8BED\u8A00\u4E4B\u4E0A\u7684\u5F00\u53D1\u65B9\u6CD5, \u5176\u57FA\u672C\u4EF7\u503C\u89C2\u662F:</p><blockquote><p>\u4E2A\u4F53\u548C\u4E92\u52A8: \u9AD8\u4E8E\u6D41\u7A0B\u548C\u5DE5\u5177.</p><p>\u5DE5\u4F5C\u7684\u8F6F\u4EF6: \u9AD8\u4E8E\u8BE6\u5C3D\u7684\u6587\u6863.</p><p>\u5BA2\u6237\u5408\u4F5C: \u9AD8\u4E8E\u5408\u540C\u8C08\u5224.</p><p>\u54CD\u5E94\u53D8\u5316: \u9AD8\u4E8E\u9075\u5FAA\u8BA1\u5212.</p></blockquote><p>\u654F\u6377\u65B9\u6CD5\u4E3B\u5F20, <strong>\u5EFA\u7ACB\u5173\u7CFB\u7D27\u5BC6\u7684\u5C0F\u56E2\u961F, \u5728\u4E00\u4E2A\u8F83\u77ED\u7684\u8FED\u4EE3\u4E2D, \u5B8C\u6210\u4E00\u4E2A\u76F8\u5BF9\u72EC\u7ACB\u7684\u5C0F\u529F\u80FD.</strong></p><p>\u5C0F\u56E2\u961F\u4F7F\u56E2\u961F\u6210\u5458\u5F62\u6210\u57FA\u4E8E\u60C5\u5883\u7684\u975E\u6B63\u5F0F\u8BDD\u8BED\u4F53\u7CFB, \u8F83\u77ED\u7684\u8FED\u4EE3\u4F7F\u56E2\u961F\u6210\u5458\u5BF9\u4E8E\u9879\u76EE\u7684\u8D70\u5411\u6709\u4E00\u79CD\u628A\u63A7\u611F, \u5B8C\u6210\u4E00\u4E2A\u76F8\u5BF9\u72EC\u7ACB\u7684\u5C0F\u529F\u80FD\u53EF\u4EE5\u4F7F\u56E2\u961F\u6210\u5458\u8BA9\u5BF9\u9879\u76EE\u7684\u6240\u6709\u5DE5\u4F5C\u6709\u4E00\u4E2A\u5177\u4F53, \u5B8C\u6574\u7684\u8BA4\u77E5.</p><p>\u8FD9\u79CD\u7EC4\u7EC7\u8BBE\u8BA1, \u4F7F\u6BCF\u4E00\u4E2A\u56E2\u961F\u6210\u5458\u6709\u4E00\u79CD <strong>\u201C\u8FD9\u662F\u6211\u7684\u9879\u76EE\u201C</strong> \u7684\u4E3B\u4EBA\u516C\u822C\u7684\u63A7\u5236\u611F.</p><p>\u8FD9\u79CD\u7EC4\u7EC7\u8BBE\u8BA1, \u4FDD\u8BC1\u4E86\u56E2\u961F\u5728 <strong>\u201C\u505A\u4E8B\u201C</strong> \u8FD9\u4E2A\u60C5\u666F\u4E2D\u8FBE\u5230\u4E86\u5171\u8BC6. \u800C\u5728\u8FD9\u79CD\u60C5\u666F\u4E2D, \u56E2\u961F\u6210\u5458\u57FA\u4E8E\u5C97\u4F4D\u5206\u5DE5\u7684\u8EAB\u4EFD\u8BA4\u540C\u548C\u5BF9\u6297\u610F\u8BC6\u5C31\u4F1A\u76F8\u5BF9\u5F31\u5316, \u53D6\u800C\u4EE3\u4E4B\u7684\u662F\u4E92\u8865\u4E0E\u534F\u4F5C.</p><h2 id="\u4E03-\u4EBA\u683C\u9B45\u529B" tabindex="-1"><a class="header-anchor" href="#\u4E03-\u4EBA\u683C\u9B45\u529B" aria-hidden="true">#</a> \u4E03 \u4EBA\u683C\u9B45\u529B</h2><p>\u654F\u6377\u65B9\u6CD5\u662F\u4E00\u5957\u975E\u5E38\u4F9D\u8D56\u89C4\u6A21\u63A7\u5236\u7684\u65B9\u6CD5. \u4E00\u65E6\u9879\u76EE\u89C4\u6A21\u81A8\u80C0\u8D77\u6765, \u654F\u6377\u65B9\u6CD5\u6240\u4F9D\u8D56\u7684, <strong>\u6240\u6709\u4EBA\u4E3A\u4E00\u4E2A\u53EF\u63A7\u7684\u76EE\u6807\u52AA\u529B\u7684\u60C5\u666F</strong> \u5C31\u4F1A\u6DE1\u5316. \u56E2\u961F\u6210\u5458\u4F1A\u53C8\u4F1A\u6309\u90E8\u5C31\u73ED\u7684\u56DE\u5230\u8EAB\u4EFD\u8BA4\u540C\u4E2D.</p><p>\u96BE\u9053\u5927\u9879\u76EE\u5C31\u53EA\u80FD\u9677\u5165\u5BF9\u6297\u548C\u56E2\u961F\u5206\u88C2\u4E48? \u5F53\u7136\u4E0D\u662F, \u6709\u5F88\u591A\u6210\u529F\u7684\u5927\u9879\u76EE, \u6210\u529F\u7684\u843D\u5730\u800C\u6CA1\u6709\u9677\u5165 \u201C\u8D8A\u754C\u201D \u548C \u201C\u7CFB\u7EDF\u6027\u7529\u9505\u201D \u7684\u9EBB\u70E6\u4E2D.</p><p>\u8FD9\u4E9B\u5927\u9879\u76EE\u662F\u901A\u8FC7\u4EC0\u4E48\u5F62\u6210\u60C5\u666F\u7684\u8BED\u8A00\u5462?</p><p>\u5C31\u6211\u7684\u7ECF\u9A8C\u800C\u8A00, \u90A3\u4E9B\u6210\u529F\u7684\u5927\u9879\u76EE, \u80CC\u540E\u90FD\u6709\u4E00\u4E2A\u975E\u5E38\u5177\u6709 <strong>\u4EBA\u683C\u9B45\u529B</strong> \u7684\u9879\u76EE\u7ECF\u7406.</p><p>\u9664\u4E86\u901A\u8FC7 <strong>\u5177\u4F53\u7684\u4E8B</strong> \u5F62\u6210\u60C5\u5883, \u8FD8\u53EF\u4EE5\u901A\u8FC7 <strong>\u5177\u4F53\u7684\u4EBA</strong> \u5F62\u6210\u60C5\u5883.</p><p>\u6240\u4EE5, \u5BF9\u4E8E\u9879\u76EE\u7ECF\u7406, <strong>\u6700\u91CD\u8981\u7684\u5012\u4E0D\u662F\u4E13\u4E1A\u7A0B\u5EA6\u548C\u7ECF\u9A8C, \u800C\u662F\u80FD\u8BA9\u4EBA\u4EEC\u5F62\u6210\u51DD\u805A\u529B\u7684\u4EBA\u683C\u9B45\u529B</strong>.</p><hr><p>\u5173\u4E8E\u4EA7\u54C1\u7ECF\u7406\u548C\u7A0B\u5E8F\u5458\u7684\u6218\u4E89, \u4ECE\u8EAB\u4EFD\u8BA4\u540C\u548C\u5974\u96B6\u9053\u5FB7\u7684\u89D2\u5EA6\u6765\u8BB2, \u662F\u4E0D\u4F1A\u6709\u6240\u7F13\u89E3\u4E86.</p><p>\u800C\u4E14\u8003\u8651\u5230\u4E92\u8054\u7F51\u5A92\u4F53\u770B\u70ED\u95F9\u4E0D\u5ACC\u4E8B\u5927\u7684\u5FC3\u6001, \u8FD9\u4E24\u4E2A\u5C97\u4F4D\u7684\u5BF9\u6297\u548C\u4E0D\u7406\u89E3\u53EA\u4F1A\u8D8A\u6765\u8D8A\u4E25\u91CD.</p><p>\u6211\u4EEC\u4F5C\u4E3A\u5F53\u4E8B\u4EBA\u548C\u53D7\u5BB3\u8005, \u80FD\u505A\u7684\u4E5F\u80FD\u662F\u5728\u6C9F\u901A\u4E2D\u5C3D\u53EF\u80FD\u5EFA\u6784\u60C5\u5883\u7684\u8BED\u8A00, \u8BA9\u56E2\u961F\u5728\u5177\u4F53\u7684\u4E8B\u6216\u5177\u4F53\u7684\u4EBA\u7684\u89D2\u5EA6\u4E0A\u8FBE\u6210\u5171\u8BC6.</p>',95),s=[n];function l(h,g){return r(),e("div",null,s)}var _=p(o,[["render",l],["__file","\u4EA7\u54C1\u7ECF\u7406\u548C\u5F00\u53D1\u4EBA\u5458\u7684\u6218\u4E89\uFF1A\u8EAB\u4EFD\u8BA4\u540C\uFF0C\u5974\u96B6\u9053\u5FB7\u4EE5\u53CA\u654F\u6377\u5F00\u53D1\u4F55\u4EE5\u6709\u6548.html.vue"]]);export{_ as default};
