import{_ as o,o as e,c as t,e as c}from"./app.efeddd24.js";const d={},a=c('<h1 id="\u4ECA\u65E5\u7ECF\u6587\u542F\u53D1" tabindex="-1"><a class="header-anchor" href="#\u4ECA\u65E5\u7ECF\u6587\u542F\u53D1" aria-hidden="true">#</a> \u4ECA\u65E5\u7ECF\u6587\u542F\u53D1</h1><h2 id="\u4E00" tabindex="-1"><a class="header-anchor" href="#\u4E00" aria-hidden="true">#</a> \u4E00</h2><p>\u4ECA\u5929\u7684 <code>\u5929\u4E3B\u6559\u4E2D\u6587\u7F51</code> \u516C\u4F17\u53F7\u7684\u4ECA\u65E5\u8BFB\u7ECF\u63A8\u5173\u4E86\u4E24\u7BC7\u7ECF\u6587. \u5176\u4E2D\u7B2C\u4E8C\u7BC7\u7ECF\u6587\u51FA\u81EA <code>\u739B\u7AA6\u798F\u97F3\u7B2C 25 \u7AE0\u7B2C 1 \u8282\u81F3\u7B2C 13 \u8282</code>. \u7ECF\u6587\u5185\u5BB9\u662F:</p><blockquote><p><code>\u8036\u7A23</code> \u5BF9\u95E8\u5F92\u8BF4: &quot;\u5929\u56FD\u597D\u6BD4\u5341\u4E2A\u7AE5\u5973\u62FF\u7740\u81EA\u5DF1\u7684\u706F, \u51FA\u53BB\u8FCE\u63A5\u65B0\uF92C. \u4ED6\u4EEC\u4E2D\u4E94\u4E2A\u662F\u80E1\u6D82\u7684, \u4E94\u4E2A\u662F\u660E\u667A\u7684. \u80E1\u6D82\u7684\u62FF\u4E86\u706F, \u5374\u6CA1\u6709\u968F\u8EAB\u5E26\u6CB9; \u800C\u660E\u667A\u7684\u62FF\u4E86\u706F, \u5E76\u4E14\u5728\u58F6\u91CC\u5E26\u4E86\u6CB9. \u56E0\u4E3A\u65B0\uF92C\u8FDF\u5EF6, \u5979\u4EEC\u90FD\u6253\u76F9\u7761\u7740\u4E86, \u88C5\u5907\u5979\u4EEC\u7684\u706F. \u534A\u591C\u6709\u4EBA\u558A\u8BF4: \u65B0\uF92C\u6765\u4E86, \u4F60\u4EEC\u51FA\u6765\u8FCE\u63A5\u5427! \u90A3\u4E9B\u7AE5\u5973\u9042\u90FD\u8D77\u6765, \u88C5\u5907\u5979\u4EEC\u7684\u706F. \u80E1\u6D82\u7684\u5BF9\u660E\u667A\u7684\u8BF4: \u628A\u4F60\u4EEC\u7684\u6CB9, \u5206\u4E9B\u7ED9\u6211\u4EEC\u5427! \u56E0\u4E3A\u6211\u4EEC\u7684\u706F\u8981\u706D\u4E86! \u660E\u667A\u7684\u7B54\u8BF4: \u6015\u4E3A\u6211\u4EEC\u548C\u4F60\u4EEC\u90FD\u4E0D\u591F, \u66F4\u597D\u4F60\u4EEC\u5230\u5356\u6CB9\u7684\u90A3\u91CC\u53BB, \u4E3A\u81EA\u5DF1\u4E70\u5427! \u5979\u4EEC\u53BB\u4E70\u7684\u65F6\u5019, \u65B0\uF92C\u5230\u4E86, \u90A3\u51C6\u5907\u597D\u4E86\u7684, \u5C31\u540C\u4ED6\u8FDB\u53BB, \u5171\u8D74\u5A5A\u5BB4; \u95E8\u9042\u5173\u4E0A\u4E86. \u672B\u540E, \u5176\u4F59\u7684\u7AE5\u5973\u4E5F\u6765\u4E86, \u8BF4: \u4E3B\u554A! \u4E3B\u554A! \u7ED9\u6211\u4EEC\u5F00\u95E8\u5427! \u4ED6\u5374\u7B54\u8BF4: \u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC: \u6211\u4E0D\u8BA4\u8BC6\u4F60\u4EEC. \u6240\u4EE5, \u4F60\u4EEC\u8BE5\u9192\u5BE4, \u56E0\u4E3A\u4F60\u4EEC\u4E0D\u77E5\u9053\u90A3\u65E5\u5B50, \u4E5F\u4E0D\u77E5\u9053\u90A3\u65F6\u8FB0.&quot;</p></blockquote><p>\u56E0\u4E3A\u672C\u5468\u4E09 FSTD \u8BFB\u7ECF\u5C0F\u7EC4, \u8BFB\u5230 <code>\u300A\u739B\u7AA6\u798F\u97F3\u300B\u7B2C\u4E03\u7AE0</code>, \u6B63\u597D\u8BA8\u8BBA\u5230\u5173\u4E8E\u7A84\u95E8, \u6263\u95E8\u7684\u6BD4\u55BB\u7684\u6BD4\u55BB, \u5C31\u5BF9\u8FD9\u6BB5\u7ECF\u6587\u7279\u522B\u7559\u5FC3\u4E86\u8D77\u6765.</p><blockquote><p>\u4F60\u4EEC\u6C42, \u5FC5\u8981\u7ED9\u4F60\u4EEC; \u4F60\u4EEC\u627E, \u5FC5\u8981\u627E\u7740; \u4F60\u4EEC\u6572, \u5FC5\u8981\u7ED9\u4F60\u4EEC\u5F00, \u56E0\u4E3A\u51E1\u662F\u6C42\u7684, \u5C31\u5FC5\u5F97\u5230; \u627E\u7684, \u5C31\u5FC5\u627E\u5230; \u6572\u7684, \u5C31\u5FC5\u7ED9\u4ED6\u5F00. ( <code>\u300A\u739B\u7AA6\u798F\u97F3\u300B\u7B2C 7 \u8282\u81F3\u7B2C 8 \u8282</code> )</p></blockquote><blockquote><p>\u4F60\u4EEC\u8981\u4ECE\u7A84\u95E8\u8FDB\u53BB, \u56E0\u4E3A\u5BBD\u95E8\u548C\u5927\u8DEF\u5BFC\u5165\u4E27\u4EA1; \u4F46\u6709\u8BB8\u591A\u7684\u4EBA\u4ECE\u90A3\u91CC\u8FDB\u53BB. \u90A3\u5BFC\u5165\u751F\u547D\u7684\u95E8\u662F\u591A\u4E48\u7A84, \u8DEF\u662F\u591A\u4E48\u72ED! \u627E\u5230\u5B83\u7684\u4EBA\u7684\u786E\u4E0D\u591A. ( <code>\u300A\u739B\u7AA6\u798F\u97F3</code>\u7B2C 13 \u8282\u81F3\u7B2C 14 \u8282 )</p></blockquote><p>\u5176\u5B9E\u60F3\u6765, <strong>&quot;\u95E8&quot;</strong> \u7684\u610F\u8C61\u5728\u5723\u7ECF\u91CC\u5E76\u4E0D\u5C11\u89C1, \u591A\u662F\u6307\u672A\u4E16. \u8036\u7A23\u6765\u4E4B\u524D, \u95E8\u662F\u5173\u7740\u7684, \u8036\u7A23\u6765\u4E86, \u95E8\u4FBF\u5F00\u4E86. \u627E\u5230\u7684\u4EBA, \u4ECE\u95E8\u91CC\u8FDB\u53BB, \u5C31\u5F97\u4E86\u6551\u8D4E. \u4E00\u65E6\u5927\u5BA1\u5224\u6765\u4E34, \u95E8\u53C8\u5173\u4E0A\u4E86, \u90A3\u4E9B\u6CA1\u8FDB\u53BB\u95E8\u7684\u4EBA, \u53EA\u80FD\u5728\u95E8\u5916\u54C0\u6C42.</p><p>\u56E0\u4E3A\u4E8E\u672A\u4E16\u76F8\u5173\u8054, \u6240\u4EE5\u8FD9\u4E2A\u610F\u8C61\u53C8\u4E0E\u767D\u663C\u5173\u8054\u8D77\u6765.</p><blockquote><p>\u9ED1\u591C\u6DF1\u4E86, \u767D\u65E5\u5DF2\u8FD1, \u6240\u4EE5\u6211\u4EEC\u8BE5\u8131\u53BB\u9ED1\u6697\u7684\u884C\u4E3A, \u4F69\u6234\u5149\u660E\u7684\u6B66\u5668. ( \u300A\u7F57\u9A6C\u4E66\u300B\u7B2C 13 \u7AE0\u7B2C 12 \u8282 )</p></blockquote><p>\u56E0\u4E3A\u672A\u4E16, \u6240\u4EE5 &quot;\u9ED1\u591C\u6DF1\u4E86&quot;; \u4E00\u65E6\u5BA1\u5224\u6765\u4E34, \u5929\u56FD\u5C31\u4F1A\u964D\u4E34, \u4E5F\u5C31\u662F &quot;\u767D\u663C\u5DF2\u8FD1&quot;.</p>',11),r=[a];function u(p,_){return e(),t("div",null,r)}var l=o(d,[["render",u],["__file","\u4ECA\u65E5\u7ECF\u6587\u542F\u53D1.html.vue"]]);export{l as default};
