import{_ as e,o,c as d,e as c}from"./app.6bf529a6.js";const p={},a=c('<h1 id="\u4F0A\u5B89\u7BC7\u8BF4\u4E86\u5565" tabindex="-1"><a class="header-anchor" href="#\u4F0A\u5B89\u7BC7\u8BF4\u4E86\u5565" aria-hidden="true">#</a> \u4F0A\u5B89\u7BC7\u8BF4\u4E86\u5565</h1><p><code>\u67CF\u62C9\u56FE</code> \u7684\u300A\u4F0A\u5B89\u7BC7\u300B\u662F\u4ED6\u7684\u65E9\u671F\u5BF9\u8BDD\u5F55. \u5728\u8FD9\u7BC7\u5BF9\u8BDD\u5F55\u4E2D, <code>\u67CF\u62C9\u56FE</code> \u501F <code>\u82CF\u683C\u62C9\u5E95</code> \u4E4B\u53E3\u8BF4\u51FA\u4E86\u4ED6\u7684 <strong>\u7075\u611F\u89C2</strong>.</p><p><strong>\u4ED6\u8BA4\u4E3A\u827A\u672F\u5BB6\u7684\u7075\u611F\u4E0D\u5355\u7EAF\u662F\u4EEC\u6280\u827A, \u800C\u662F\u6765\u81EA\u4E8E\u795E\u7684\u542F\u793A, \u662F\u4E00\u79CD\u72C2\u8FF7</strong>.</p><h2 id="\u6545\u4E8B\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#\u6545\u4E8B\u80CC\u666F" aria-hidden="true">#</a> \u6545\u4E8B\u80CC\u666F</h2><p>\u8BF5\u8BD7\u8005 <code>\u4F0A\u5B89</code> \u4ECE <code>\u57C3\u76AE\u9053\u4F26</code> \u6765\u5230 <code>\u96C5\u5178</code>, \u88AB <code>\u82CF\u683C\u62C9\u5E95</code> \u53EB\u4F4F. <code>\u4F0A\u5B89</code> \u521A\u53C2\u52A0\u4E86\u5E86\u795D <code>\u963F\u65AF\u514B\u52D2\u5E87\u4FC4\u65AF</code> \u8282\u65E5\u7684\u8BF5\u8BD7\u6BD4\u8D5B, <code>\u82CF\u683C\u62C9\u5E95</code> \u5411\u4ED6\u8BE2\u95EE\u6BD4\u8D5B\u7ED3\u679C, \u5F97\u77E5\u4ED6\u5F97\u4E86\u7B2C\u4E00\u4E2A\u5956.</p><p><code>\u82CF\u683C\u62C9\u5E95</code> \u8868\u793A\u4E86\u5BF9 <code>\u4F0A\u5B89</code> \u8BF5\u8BD7\u4EBA\u7684\u7FA1\u6155, \u4E0D\u4EC5\u662F\u56E0\u4E3A\u8FD9\u884C\u8981\u7A7F\u7740\u6F02\u4EAE\u7684\u8863\u670D, \u8FD8\u56E0\u4E3A\u8FD9\u884C\u9700\u8981\u5BF9\u6240\u8BF5\u8BD7\u4EBA\u8BD7\u53E5\u6709\u6DF1\u523B\u7684\u7406\u89E3.</p><p><code>\u4F0A\u5B89</code> \u975E\u5E38\u540C\u610F <code>\u82CF\u683C\u62C9\u5E95</code> \u7684\u8BF4\u6CD5, \u5E76\u4E14\u8868\u793A\u6CA1\u6709\u4EBA\u6BD4\u4ED6\u66F4\u4E86\u89E3 <code>\u8377\u9A6C</code>.</p><h2 id="\u5173\u4E8E\u8BF5\u8BD7\u4EBA-\u7406\u89E3-\u8BD7\u4EBA\u7591\u95EE" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u8BF5\u8BD7\u4EBA-\u7406\u89E3-\u8BD7\u4EBA\u7591\u95EE" aria-hidden="true">#</a> \u5173\u4E8E\u8BF5\u8BD7\u4EBA &quot;\u7406\u89E3&quot; \u8BD7\u4EBA\u7591\u95EE</h2><p>\u4E8E\u662F <code>\u82CF\u683C\u62C9\u5E95</code> \u95EE <code>\u4F0A\u5B89</code>, \u4ED6\u662F\u53EA\u4E86\u89E3 <code>\u8377\u9A6C</code>, \u8FD8\u662F\u4E86\u89E3\u6240\u6709\u7684\u8BD7\u4EBA.</p><p><code>\u4F0A\u5B89</code> \u8868\u793A\u4ED6\u53EA\u4E86\u89E3 <code>\u8377\u9A6C</code>, \u5E76\u4E14\u89C9\u5F97\u53EA\u4E86\u89E3 <code>\u8377\u9A6C</code> \u5C31\u591F\u4E86.</p><p><code>\u4F0A\u5B89</code> \u8BA4\u4E3A, \u5982\u679C <code>\u8377\u9A6C</code> \u4E0E\u5176\u4ED6\u8BD7\u4EBA\u5BF9\u540C\u4E00\u4EF6\u4E8B\u8BF4\u4E86\u540C\u6837\u7684\u8BDD, \u4ED6\u5C06\u4F5C\u51FA\u540C\u6837\u7684\u89E3\u91CA; \u5982\u679C <code>\u8377\u9A6C</code> \u4E5F\u5176\u4ED6\u7684\u8BD7\u4EBA\u5BF9\u540C\u4E00\u4EF6\u4E8B\u8BF4\u4E86\u4E0D\u540C\u7684\u8BDD, \u90A3\u4E48 <code>\u8377\u9A6C</code> \u8BF4\u7684\u8981\u6BD4\u5176\u4ED6\u4EBA\u8BF4\u7684\u597D.</p><p><code>\u82CF\u683C\u62C9\u5E95</code> \u8BF4, \u5982\u679C\u4E00\u4E2A\u4EBA\u80FD\u4ECE\u4EBA\u4EEC\u5BF9\u67D0\u4E00\u4EF6\u4E8B\u7684\u8C08\u8BBA\u4E2D\u5206\u522B\u51FA\u597D\u574F\u6765, \u90A3\u4ED6\u4E00\u5B9A\u6709\u8FD9\u4EF6\u4E8B\u60C5\u7684\u77E5\u8BC6. \u5982\u4E00\u4E2A\u4EBA\u80FD\u5206\u8FA9\u51FA\u8C01\u8BBA\u8C08\u8BBA\u6570\u5B66\u6BD4\u8F83\u597D, \u90A3\u4ED6\u4E00\u5B9A\u80FD\u6709\u6570\u5B66\u77E5\u8BC6; \u5982\u679C\u4EBA\u4EEC\u80FD\u533A\u522B\u8C01\u8C08\u8282\u98DF\u8C08\u7684\u597D, \u90A3\u4ED6\u4E00\u5B9A\u662F\u533B\u751F. \u6240\u4EE5\u8BC6\u522B\u8C08\u8BBA\u7684\u597D\u7684\u4EBA, \u548C\u8BC6\u522B\u8C08\u8BBA\u7684\u574F\u7684\u4EBA, \u662F\u540C\u4E00\u4E2A\u4EBA.</p><p>\u6240\u4EE5, \u5982\u679C <code>\u4F0A\u5B89</code> \u80FD\u5224\u65AD, <code>\u8377\u9A6C</code> \u5728\u4E00\u5207\u4E8B\u60C5\u4E0A\u8BF4\u7684\u90FD\u6BD4\u522B\u4EBA\u597D, \u90A3\u4ED6\u4E00\u5B9A\u62E5\u6709\u4E00\u5207\u77E5\u8BC6.</p><p><code>\u4F0A\u5B89</code> \u8BF4, \u4ED6\u4E4B\u6240\u4EE5\u8FD9\u6837\u8BF4, \u662F\u56E0\u4E3A\u522B\u4EBA\u63D0\u5230\u522B\u7684\u8BD7\u4EBA, \u4ED6\u5168\u7136\u4E0D\u80FD\u63D0\u8D77\u5174\u8DA3; \u4F46\u662F\u5982\u679C\u63D0\u5230 <code>\u8377\u9A6C</code>, \u4ED6\u5C31\u7ACB\u523B\u4EA7\u751F\u51FA\u5174\u8DA3\u6765. \u4ED6\u8BF7 <code>\u82CF\u683C\u62C9\u5E95</code> \u6765\u89E3\u91CA\u8FD9\u4E2A\u95EE\u9898.</p><h2 id="\u7075\u611F\u8BF4" tabindex="-1"><a class="header-anchor" href="#\u7075\u611F\u8BF4" aria-hidden="true">#</a> \u7075\u611F\u8BF4</h2><p><code>\u82CF\u683C\u62C9\u5E95</code> \u8BF4, \u5982\u679C\u8C08\u8BBA <code>\u8377\u9A6C</code> \u7684\u80FD\u529B\u6765\u81EA\u77E5\u8BC6\u548C\u6280\u827A, <strong>\u90A3\u4E48\u5C31\u5E94\u8BE5\u5B58\u5728\u4E00\u95E8\u6574\u4F53\u7684\u8BD7\u5B66</strong>. \u800C\u5982\u679C\u5B58\u5728\u4E00\u95E8\u6574\u4F53\u7684\u8BD7\u5B66, \u90A3\u4E48\u5BF9\u4E8E\u4E0D\u540C\u8BD7\u4EBA\u7684\u7406\u89E3\u5C31\u4F1A\u662F\u4E00\u6837\u7684\u6709\u6548\u7684. \u8FD9\u5C31\u548C\u5B58\u4E00\u95E8\u7ED8\u753B\u827A\u672F, \u6491\u63E1\u8FD9\u95E8\u827A\u672F\u7684\u4EBA, \u5C31\u4F1A\u5BF9\u6240\u6709\u7684\u7ED8\u753B\u4F5C\u54C1\u6709\u5224\u65AD\u529B, \u5B58\u5728\u4E00\u4E2A\u96D5\u523B\u827A\u672F, \u6491\u63E1\u8FD9\u95E8\u827A\u672F, \u5C31\u4F1A\u5BF9\u4E00\u5207\u96D5\u50CF\u827A\u672F\u6709\u5224\u65AD\u529B.</p><p><code>\u4F0A\u5B89</code> \u5BF9\u6B64\u65E0\u6CD5\u53CD\u9A73, \u4F46\u662F\u4ED6\u65E0\u6CD5\u89E3\u91CA\u51FA\u73B0\u5728\u4ED6\u8EAB\u4E0A\u7684\u73B0\u8C61.</p><p>\u4E8E\u662F <code>\u82CF\u683C\u62C9\u5E95</code> \u63D0\u51FA\u4E00\u4E2A\u89E3\u91CA: \u89E3\u8BF4 <code>\u8377\u9A6C</code> \u7684\u80FD\u529B\u662F\u4E00\u79CD\u5982\u540C\u78C1\u77F3\u7684\u795E\u5723\u80FD\u529B. \u78C1\u77F3\u4E0D\u4F46\u53EF\u4EE5\u5438\u5F15\u94C1\u73AF, \u8FD8\u80FD\u628A\u78C1\u529B\u4F20\u7ED9\u94C1\u73AF. <code>\u7F2A\u65AF</code> \u7684\u7075\u611F\u4E5F\u5982\u78C1\u529B\u4E00\u6837, \u53EF\u4EE5\u901A\u8FC7\u6709\u7075\u611F\u7684\u4EBA\u4F20\u9012\u51FA\u53BB, \u5F62\u6210\u4E00\u4E2A\u94FE\u6761. \u6240\u4EE5\u8FD9\u79CD\u8BD7\u4EBA\u7684\u80FD\u529B\u4E0D\u662F\u6280\u672F, \u800C\u662F\u6765\u81EA\u7075\u611F. \u8FD9\u79CD\u7075\u611F\u5C31\u50CF\u9152\u795E\u8282\u7684\u5973\u4FE1\u5F92, \u53EF\u4EE5\u4ECE\u6CB3\u4E2D\u6C72\u53D6\u4E73\u548C\u871C, \u4F46\u600E\u4E48\u505A\u5230\u7684, \u5979\u4EEC\u4E00\u70B9\u4E5F\u4E0D\u77E5\u9053. \u8BD7\u4EBA\u4E5F\u662F\u5982\u6B64, \u50CF\u662F\u5728 <code>\u7F2A\u65AF</code> \u82B1\u56ED\u91CC\u6C72\u53D6\u871C\u7684\u871C\u8702. \u8BD7\u6B4C\u5C31\u50CF\u662F\u5149\u548C\u957F\u7740\u7FC5\u8180\u7684\u4E1C\u897F.</p><p>\u8FD9\u5C31\u89E3\u91CA\u4E86\u4E3A\u4EC0\u4E48\u8BD7\u4EBA\u53EA\u64C5\u957F\u4E00\u79CD\u4F53\u88C1\u7684\u8BD7. \u56E0\u4E3A\u8BD7\u4EBA\u4F5C\u8BD7\u51ED\u7684\u662F\u795E\u529B, \u5C31\u5982\u9884\u8A00\u5BB6\u4EE3\u795E\u8BF4\u8BDD\u4E00\u6837, \u4EE3\u795E\u8BF4\u8BDD. \u8BD7\u4EBA\u5728\u8BF4\u8BDD\u65F6, \u662F\u5931\u53BB\u7406\u667A\u7684, \u771F\u6B63\u8BF4\u8BDD\u7684\u662F\u795E, \u901A\u8FC7\u8BD7\u4EBA, \u6211\u4EEC\u624D\u80FD\u66F4\u6E05\u6670\u7684\u8046\u542C\u795E\u7684\u8BDD\u8BED.</p><p><code>\u4F0A\u5B89</code> \u8868\u793A, \u5BF9\u4E8E <code>\u82CF\u683C\u62C9\u5E95</code> \u7684\u89E3\u91CA\u5F88\u52A8\u5FC3.</p><p><code>\u82CF\u683C\u62C9\u5E95</code> \u8FDB\u4E00\u6B65\u89E3\u91CA, \u8BF5\u8BD7\u4EBA\u5728\u8BF5\u8BD7\u65F6\u56E0\u9996\u8BD7\u6B4C\u7684\u5185\u5BB9, \u65F6\u800C\u60B2\u4F24, \u65F6\u800C\u6050\u60E7, \u5982\u540C\u4E27\u5931\u7406\u667A\u4E00\u6837; \u800C\u542C\u4F17\u4E5F\u53D7\u5230\u611F\u67D3, \u4E00\u540C\u60B2\u4F24\u6050\u60E7. \u8FD9\u5C31\u50CF\u662F\u78C1\u77F3\u5F62\u6210\u7684\u94FE\u6761. \u8BD7\u4EBA\u5982\u94C1\u73AF\u4E00\u6837\u60AC\u6302\u5728\u8BD7\u795E\u8EAB\u4E0A, \u800C\u8BF5\u8BD7\u8005\u53C8\u88AB\u8BD7\u4EBA\u9644\u8EAB. \u6240\u4EE5 <code>\u4F0A\u5B89</code> \u624D\u4F1A\u53EA\u5BF9 <code>\u8377\u9A6C</code> \u611F\u5174\u8DA3, \u800C\u5BF9\u5176\u4ED6\u8BD7\u4EBA\u611F\u5174\u8DA3. \u56E0\u4E3A\u4ED6\u662F\u88AB <code>\u8377\u9A6C</code> \u9644\u8EAB\u7684\u8BD7\u4EBA.</p><h2 id="\u5BF9\u4E8E\u7075\u611F\u8BF4\u7684\u8BBA\u8BC1" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u4E8E\u7075\u611F\u8BF4\u7684\u8BBA\u8BC1" aria-hidden="true">#</a> \u5BF9\u4E8E\u7075\u611F\u8BF4\u7684\u8BBA\u8BC1</h2><p><code>\u4F0A\u5B89</code> \u5E76\u4E0D\u8BA4\u4E3A\u81EA\u5DF1\u5728\u8BF5\u8BD7\u65F6\u795E\u667A\u4E0D\u6E05, \u56E0\u6B64\u5E0C\u671B <code>\u82CF\u683C\u62C9\u5E95</code> \u5BF9\u81EA\u5DF1\u7684\u8BF4\u6CD5\u8FDB\u884C\u8BBA\u8BC1.</p><p><code>\u82CF\u683C\u62C9\u5E95</code> \u95EE <code>\u4F0A\u5B89</code>, \u5BF9 <code>\u8377\u9A6C</code> \u7684\u5168\u90E8\u8BD7\u90FD\u719F\u6089, \u8FD8\u662F\u53EA\u5BF9\u4E00\u90E8\u5206\u719F\u6089. <code>\u4F0A\u5B89</code> \u8868\u793A, \u4ED6\u5BF9\u6240\u6709\u7684\u90E8\u5206\u90FD\u719F\u6089.</p><p><code>\u82CF\u683C\u62C9\u5E95</code> \u4E3E\u51FA <code>\u8377\u9A6C</code> \u8BD7\u4E2D\u5BF9\u4E0D\u540C\u4E3B\u9898\u7684\u63CF\u5199, \u5305\u62EC\u9A7E\u8F66. \u5BF9\u4E8E\u9A7E\u8F66\u63CF\u8FF0\u6765\u8BF4, \u9A6D\u624B\u4E00\u5B9A\u662F\u6700\u624B\u8BC4\u5224\u8005. \u800C\u9A6D\u624B\u80FD\u8BC4\u5224, \u6240\u4F9D\u8D56\u7684\u4E0D\u662F\u522B\u7684, \u6B63\u662F\u4ED6\u7684\u6280\u827A. \u800C\u6BCF\u884C\u6280\u827A\u90FD\u6709\u5176\u5BF9\u5E94\u7684\u884C\u4E1A, \u8DE8\u8D8A\u884C\u4E1A\u7684\u6280\u827A, \u5E76\u4E0D\u76F8\u901A. \u5982\u533B\u751F\u7684\u6280\u827A\u4E0D\u540C\u4E8E\u5EFA\u7B51\u7684\u6280\u827A. \u533A\u5206\u4E00\u7C7B\u6280\u672F\u7684\u533A\u522B\u5728\u4E8E, \u5B83\u662F\u5173\u4E8E\u67D0\u4E00\u4E8B\u7279\u7684\u77E5\u8BC6. \u56E0\u6B64\u4E00\u7C7B\u6280\u827A\u53EF\u4EE5\u4F7F\u6211\u4EEC\u77E5\u9053\u4E00\u7C7B\u4E8B\u7269\u7684\u77E5\u8BC6, \u800C\u4E0D\u80FD\u544A\u8BC9\u6211\u4EEC\u53E6\u4E00\u7C7B\u4E8B\u7269\u7684\u77E5\u8BC6.</p><p>\u5982\u679C\u8BD7\u4EBA\u9760\u6280\u827A\u5199\u8BD7, \u90A3\u4ED6\u5199\u9A7E\u8F66, \u5C31\u5F97\u6709\u9A6D\u624B\u7684\u6280\u827A; \u5199\u8282\u98DF, \u5C31\u8981\u6709\u533B\u751F\u7684\u6280\u827A; \u5199\u9493\u9C7C, \u5C31\u8981\u6709\u9493\u9C7C\u7684\u6280\u827A; \u5199\u5360\u535C, \u5C31\u8981\u6709\u5360\u535C\u7684\u6280\u827A.</p><p>\u800C\u8BD7\u4EBA\u8981\u662F\u8981\u80FD\u89E3\u91CA\u8BD7\u4EBA, \u4E5F\u5C31\u8981\u6709\u540C\u6837\u7684\u6280\u827A\u4E86.</p><p><code>\u4F0A\u5B89</code> \u63D0\u51FA\u53CD\u9A73, \u8BD7\u4EBA\u7684\u6280\u827A\u662F, \u77E5\u9053\u4E0D\u540C\u600E\u4E48\u8BF4\u8BDD, \u5982\u7537\u4EBA\u548C\u5973\u4EBA, \u81EA\u7531\u4EBA\u548C\u5974\u96B6, \u88AB\u7EDF\u6CBB\u8005\u548C\u7EDF\u6CBB\u8005. \u4F46 <code>\u82CF\u683C\u62C9\u5E95</code> \u5374\u8BF4, <strong>\u4EBA\u5728\u5177\u4F53\u60C5\u51B5 ( \u8EAB\u4EFD ) \u4E0B\u6240\u8BF4\u4E4B\u8BDD, \u662F\u8981\u4F9D\u636E\u5177\u4F53\u6280\u827A\u7684</strong>. \u5982\u6D77\u4E0A\u9047\u66B4\u98CE\u96E8, \u8235\u624B\u8981\u6BD4\u8BF5\u8BD7\u4EBA\u66F4\u77E5\u9053\u8981\u8BF4\u4EC0\u4E48; \u5728\u9A6F\u670D\u75AF\u725B\u65F6, \u7267\u725B\u4EBA\u8981\u6BD4\u8BF5\u8BD7\u4EBA\u66F4\u77E5\u9053\u8981\u8BF4\u4EC0\u4E48. \u4F5C\u4E3A\u8BAD\u8BEB\u58EB\u5175\u7684\u5C06\u519B, \u5C06\u519B\u4E00\u5B9A\u6BD4\u8BF5\u8BD7\u4EBA\u66F4\u77E5\u9053\u8981\u8BF4\u4EC0\u4E48.</p><p>\u8FD9\u4E2A\u9053\u7406\u5C31\u597D\u6BD4, \u4F60\u5373\u61C2\u9A91\u9A6C, \u53C8\u61C2\u97F3\u4E50, \u90A3\u4E48\u5F97\u77E5\u9A6C\u600E\u4E48\u8DD1\u7684\u5FEB\u7684\u77E5\u8BC6, \u4E00\u5B9A\u662F\u9A91\u9A6C\u7684\u77E5\u8BC6. <strong>( \u56E0\u4E3A\u4E0D\u540C\u4E8B\u7269\u7684\u77E5\u8BC6\u662F\u76F8\u9694\u7684. )</strong> \u82E5\u975E\u5982\u6B64, \u5C06\u519B\u5C31\u4F1A\u662F\u6700\u80FD\u5E72\u7684\u8BF5\u8BD7\u4EBA, \u800C\u8BF5\u8BD7\u4EBA\u4E5F\u662F\u6700\u80FD\u5E72\u7684\u5C06\u519B\u4E86. \u82E5\u6700\u597D\u7684\u8BF5\u8BD7\u4EBA\u540C\u6837\u4E5F\u662F\u6700\u597D\u7684\u5C06\u519B, \u90A3\u4E3A\u4EC0\u4E48\u4F0A\u5B89\u4F60\u5F80\u6765\u96C5\u5178\u591A\u6B21, \u6CA1\u88AB\u96C5\u5178\u9009\u4F5C\u5C06\u519B.</p><p>\u6700\u540E <code>\u82CF\u683C\u62C9\u5E95</code> \u8BF4, **&quot;\u8BB0\u5F97\u5728\u8D5E\u7F8E<code>\u8377\u9A6C</code>\u65F6, \u8981\u7528\u6211\u4EEC\u795E\u5723\u7684\u5FC3\u7075, \u800C\u4E0D\u8981\u7528\u827A\u672F\u5BB6\u7684\u5FC3\u7075.&quot;</p>',30),r=[a];function t(n,h){return o(),d("div",null,r)}var i=e(p,[["render",t],["__file","\u4F0A\u5B89\u7BC7\u8BF4\u4E86\u5565.html.vue"]]);export{i as default};
