import{_ as o,o as t,c as r,e as n}from"./app.efeddd24.js";const s={},g=n('<h1 id="\u4F2F\u91CC\u514B\u5229\u9635\u4EA1\u5C06\u58EB\u6F14\u6F6E" tabindex="-1"><a class="header-anchor" href="#\u4F2F\u91CC\u514B\u5229\u9635\u4EA1\u5C06\u58EB\u6F14\u6F6E" aria-hidden="true">#</a> \u4F2F\u91CC\u514B\u5229\u9635\u4EA1\u5C06\u58EB\u6F14\u6F6E</h1><p>\u8FC7\u53BB\u8BB8\u591A\u5728\u6B64\u5730\u8BF4\u8FC7\u8BDD\u7684\u4EBA, \u603B\u662F\u8D5E\u7F8E\u6211\u4EEC\u5728\u846C\u793C\u5C06\u5B8C\u6210\u65F6\u53D1\u8868\u6F14\u8BF4\u7684\u8FD9\u79CD\u5236\u5EA6. \u5728\u4ED6\u4EEC\u770B\u6765, \u5BF9\u4E8E\u9635\u4EA1\u5C06\u58EB\u53D1\u8868\u6F14\u8BF4\u4F3C\u4E4E\u662F\u5BF9\u9635\u4EA1\u6218\u58EB\u4E00\u79CD\u5149\u8363\u7684\u8868\u793A. \u8FD9\u4E00\u70B9, \u6211\u4E0D\u540C\u610F. \u6211\u8BA4\u4E3A, \u5728\u8FD9\u4E9B\u884C\u52A8\u4E2D\u8868\u73B0\u81EA\u5DF1\u52C7\u6562\u7684\u4EBA, <strong>\u4ED6\u4EEC\u7684\u884C\u4E3A\u5C31\u5145\u5206\u5BA3\u5E03\u4E86\u4ED6\u4EEC\u7684\u5149\u8363\u4E86</strong>, \u6B63\u5982\u4F60\u4EEC\u521A\u624D\u4ECE\u8FD9\u6B21\u56FD\u846C\u5178\u793C\u4E2D\u6240\u770B\u89C1\u7684\u4E00\u6837. \u6211\u4EEC\u76F8\u4FE1, \u8FD9\u8BB8\u591A\u4EBA\u7684\u52C7\u6562\u548C\u82F1\u96C4\u6C14\u6982 <strong>\u6BEB\u4E0D\u56E0\u4E3A\u4E00\u4E2A\u4EBA\u5BF9\u4ED6\u8BF4\u597D\u6216\u8BF4\u6B79\u800C\u6709\u6240\u53D8\u66F4</strong>.</p><p><strong>\u9996\u5148\u6211\u8981\u8BF4\u5230\u6211\u4EEC\u7684\u7956\u5148\u4EEC</strong>. \u56E0\u4E3A\u5728\u8FD9\u6837\u7684\u5178\u793C\u4E0A, \u56DE\u5FC6\u4ED6\u4EEC\u7684\u4F5C\u4E3A, \u4EE5\u8868\u793A\u4ED6\u4EEC\u7684\u656C\u610F, \u8FD9\u662F\u9002\u5F53\u7684. \u6211\u4EEC\u5728\u8FD9\u5757\u571F\u5730\u4E0A, \u540C\u4E00\u4E2A\u6C11\u65CF\u7684\u4EBA\u4E16\u4E16\u4EE3\u4EE3\u4F4F\u5728\u8FD9\u91CC, \u76F4\u5230\u73B0\u5728; \u56E0\u4E3A\u4ED6\u4EEC\u7684\u52C7\u6562\u548C\u7F8E\u5FB7, \u4ED6\u4EEC\u628A\u8FD9\u5757\u571F\u5730 <strong>\u5F53\u4F5C\u4E00\u4E2A\u81EA\u7531\u56FD\u5BB6\u4F20\u7ED9\u4E86\u6211\u4EEC</strong>. \u4ED6\u4EEC\u65E0\u7591\u662F\u503C\u5F97\u6211\u4EEC\u6B4C\u9882\u7684. \u5C24\u5176\u662F\u6211\u4EEC\u7684\u7236\u8F88, \u66F4\u52A0\u503C\u5F97\u6211\u4EEC\u6B4C\u9882, \u56E0\u4E3A\u9664\u4E86\u4ED6\u4EEC\u6240\u7EE7\u627F\u7684\u571F\u5730\u4E4B\u5916, \u4ED6\u4EEC\u8FD8\u6269\u5C55\u4E86\u6211\u4EEC\u7684\u9886\u571F; \u4ED6\u4EEC\u628A\u8FD9\u4E2A\u56FD\u5BB6\u4F20\u7ED9\u6211\u4EEC\u8FD9\u4E00\u4EE3, \u4E0D\u662F\u6CA1\u6709\u7ECF\u8FC7\u6D41\u8840\u548C\u8F9B\u52E4\u52B3\u52A8\u7684. \u4ECA\u5929\u6211\u4EEC\u5728\u8FD9\u91CC\u96C6\u5408\u7684\u4EBA, \u7EDD\u5927\u591A\u6570\u6B63\u5F53\u76DB\u5E74, \u6211\u4EEC\u5DF2\u7ECF\u5728\u5404\u65B9\u9762\u6269\u5145\u4E86\u6211\u4EEC\u56FD\u5BB6\u7684\u52BF\u529B, \u6211\u4EEC\u65E0\u8BBA\u5728\u5E73\u65F6\u6216\u6218\u65F6, <strong>\u90FD\u80FD\u5B8C\u5168\u7167\u987E\u81EA\u5DF1</strong>.</p><p>\u6211\u4E0D\u60F3\u4F5C\u4E00\u7BC7\u5197\u957F\u7684\u6F14\u8BF4\u6765\u8BC4\u8FF0\u4E00\u4E9B\u4F60\u4EEC\u90FD\u5F88\u719F\u6089\u7684\u95EE\u9898, \u6240\u4EE5\u6211\u4EEC\u4E0D\u8C08\u6211\u4EEC\u7528\u4EE5\u53D6\u5F97\u6211\u4EEC\u52BF\u529B\u7684\u4E00\u4E9B\u8FD9\u4E8B\u884C\u52A8, \u4E5F\u4E0D\u8C08\u6211\u4EEC\u7684\u7236\u8F88\u82F1\u52C7\u5730\u62B5\u641E\u6211\u4EEC <strong>\u5E0C\u814A\u5185\u90E8\u548C\u5916\u90E8\u7684\u654C\u4EBA</strong> \u7684\u6218\u5F79. \u6211\u6240\u8981\u8BF4\u7684, <strong>\u9996\u5148\u662F\u8BA8\u8BBA\u6211\u4EEC\u66FE\u7ECF\u53D7\u5230\u8003\u9A8C\u7684\u7CBE\u795E, \u6211\u4EEC\u7684\u5BAA\u6CD5\u548C\u4F7F\u6211\u4EEC\u4F1F\u5927\u7684\u751F\u65B9\u5F0F</strong>. \u8BF4\u4E86\u8FD9\u4E9B\u4E4B\u540E, \u6211\u60F3\u6B4C\u9882\u9635\u4EA1\u6218\u58EB. \u6211\u8BA4\u4E3A\u8FD9\u79CD\u6F14\u8BF4, \u5728\u76EE\u524D\u60C5\u51B5\u4E0B, \u4E0D\u4F1A\u662F\u4E0D\u9002\u5F53\u7684; \u540C\u65F6\u5728\u8FD9\u91CC\u96C6\u4F1A\u7684\u5168\u4F53\u4EBA\u5458, \u5305\u62EC <strong>\u516C\u6C11\u548C\u5916\u56FD\u4EBA\u5728\u5185</strong>, \u542C\u4E86\u8FD9\u7BC7\u6F14\u8BF4, \u4E5F\u4F1A\u611F\u5230\u6709\u610F\u4E49.</p><p>\u6211\u8981\u8BF4, \u6211\u4EEC\u7684\u653F\u6CBB\u5236\u5EA6\u4E0D\u662F\u4ECE\u6211\u4EEC\u90BB\u4EBA\u7684\u5236\u5EA6\u4E2D\u6A21\u4EFF\u6765\u7684. <strong>\u6211\u4EEC\u7684\u5236\u5EA6\u662F\u522B\u4EBA\u7684\u6A21\u8303</strong>, \u800C\u4E0D\u662F\u6211\u4EEC\u6A21\u4EFF\u4EFB\u4F55\u5176\u4ED6\u4EBA. \u6211\u4EEC\u7684\u5236\u5EA6\u4E4B\u6240\u4EE5\u88AB\u79F0\u4E3A\u6C11\u4E3B\u653F\u6CBB, <strong>\u662F\u56E0\u4E3A\u653F\u6743\u5728\u5168\u4F53\u516C\u6C11\u624B\u4E2D</strong>, \u800C\u4E0D\u662F\u5728\u5C11\u6570\u4EBA\u624B\u4E2D. \u89E3\u51B3\u79C1\u4EBA\u4E89\u6267\u7684\u65F6\u5019, <strong>\u6BCF\u4E2A\u4EBA\u5728\u6CD5\u5F8B\u4E0A\u90FD\u662F\u5E73\u7B49\u7684</strong>; \u8BA9\u4E00\u4E2A\u4EBA\u62C5\u4EFB\u516C\u804C\u4F18\u5148\u4E8E\u4ED6\u4EBA\u7684\u65F6\u5019, \u6240\u8003\u8651\u7684\u4E0D\u662F\u67D0\u4E00\u4E2A\u7279\u6B8A\u9636\u7EA7\u7684\u6210\u5458, \u800C\u662F\u4ED6\u5177\u6709\u771F\u6B63\u7684\u624D\u80FD. \u4EFB\u4F55\u4EBA, \u53EA\u8981\u4ED6\u80FD\u591F\u5BF9\u56FD\u5BB6\u6709\u6240\u8D21\u732E, \u5C31\u7EDD\u5BF9\u4E0D\u4F1A\u56E0\u4E3A\u8D2B\u7A77\u800C\u5728\u653F\u6CBB\u4E0A\u6E6E\u6CA1\u65E0\u95FB. <strong>\u6B63\u56E0\u4E3A\u6211\u4EEC\u7684\u653F\u6CBB\u751F\u6D3B\u662F\u81EA\u7531\u800C\u516C\u5F00\u7684</strong>, \u6211\u4EEC\u5F7C\u6B64\u95F4\u7684\u65E5\u5E38\u751F\u6D3B\u4E5F\u662F\u8FD9\u6837. \u5F53\u6211\u4EEC\u9694\u58C1\u90BB\u4EBA\u4E3A\u6240\u6B32\u4E3A\u7684\u65F6\u5019\u6211\u4EEC\u4E0D\u81F4\u4E8E\u56E0\u6B64\u800C\u751F\u6C14; \u6211\u4EEC\u4E5F\u4E0D\u4F1A\u56E0\u6B64\u800C\u7ED9\u4ED6\u4EE5\u96BE\u770B\u7684\u989C\u8272\u4EE5\u4F24\u4ED6\u7684\u60C5\u611F, \u5C3D\u7BA1\u8FD9\u79CD\u989C\u8272\u5BF9\u4ED6\u6CA1\u6709\u5B9E\u9645\u7684\u635F\u5BB3. \u5728\u6211\u4EEC\u79C1\u4EBA\u751F\u6D3B\u4E2D, \u6211\u4EEC\u662F\u81EA\u7531\u800C\u5BBD\u5BB9\u7684; \u4F46\u662F\u5728\u516C\u5BB6\u7684\u4E8B\u52A1\u4E2D, \u6211\u4EEC\u9075\u5B88\u6CD5\u5F8B. <strong>\u8FD9\u662F\u56E0\u4E3A\u8FD9\u79CD\u6CD5\u5F8B\u4F7F\u6211\u4EEC\u5FC3\u60A6\u8BDA\u670D</strong>.</p><p>\u53E6\u5916\u8FD8\u6709\u4E00\u70B9. \u5F53\u6211\u4EEC\u5DE5\u4F5C\u5B8C\u6BD5\u7684\u65F6\u5019, <strong>\u6211\u4EEC\u53EF\u4EE5\u4EAB\u53D7\u5404\u79CD\u5A31\u4E50</strong>, \u4EE5\u63D0\u9AD8\u6211\u4EEC\u7684\u60C5\u8DA3. \u6574\u4E2A\u4E00\u5E74\u4E4B\u4E2D, \u6709\u5404\u79CD\u5B9A\u671F\u7684\u8D5B\u4F1A\u548C\u796D\u7940; \u5728\u6211\u4EEC\u7684\u5BB6\u5EAD\u4E2D, \u6211\u4EEC\u6709\u534E\u4E3D\u800C\u98CE\u96C5\u7684\u8BBE\u5907, \u6BCF\u5929\u6021\u5A31\u5FC3\u76EE, \u4F7F\u6211\u4EEC\u5FD8\u8BB0\u4E86\u6211\u4EEC\u7684\u5FE7\u8651. \u6211\u4EEC\u7684\u57CE\u90A6\u8FD9\u6837\u4F1F\u5927, \u5B83\u5145\u5206\u5730\u7ED9\u4E88\u6211\u4EEC\u4E16\u754C\u5404\u5730\u4E00\u5207\u7F8E\u597D\u7684\u4E1C\u897F, \u4F7F\u6211\u4EEC\u4EAB\u53D7\u5916\u56FD\u7684\u4E1C\u897F, \u5C31\u597D\u50CF\u662F\u6211\u4EEC\u672C\u5730\u7684\u51FA\u4EA7\u54C1\u4E00\u6837.</p><p>\u6211\u4EEC\u662F\u81EA\u613F\u5730 <strong>\u4EE5\u8F7B\u677E\u7684\u60C5\u7EEA\u6765\u5E94\u4ED8\u5371\u9669\u7684</strong>, \u800C\u4E0D\u662F\u7528\u90A3\u79CD\u8270\u82E6\u7684\u8BAD\u7EC3. \u6211\u4EEC\u7684\u52C7\u6562\u662F <strong>\u4ECE\u6211\u4EEC\u7684\u751F\u6D3B\u65B9\u5F0F\u4E2D\u81EA\u7136\u4EA7\u751F\u7684</strong>, \u800C\u4E0D\u662F\u56E0\u4E3A\u56FD\u5BB6\u7684\u6CD5\u5F8B\u7684\u5F3A\u8FEB; \u6211\u8BA4\u4E3A\u8FD9\u4E9B\u662F\u6211\u4EEC\u7684\u4F18\u70B9. \u6211\u4EEC\u4E0D\u82B1\u8D39\u65F6\u95F4\u6765\u8BAD\u7EC3\u81EA\u5DF1\u5FCD\u53D7\u90A3\u4E9B\u5C1A\u672A\u6765\u5230\u7684\u75DB\u82E6, \u4F46\u662F\u5F53\u6211\u4EEC\u771F\u7684\u9047\u5230\u75DB\u82E6\u7684\u65F6\u5019, \u6211\u4EEC\u8868\u73B0\u51FA\u6211\u4EEC\u81EA\u5DF1\u6B63\u548C\u90A3\u4E9B\u7ECF\u5E38\u53D7\u5230\u8BAD\u7EC3\u7684\u4E00\u6837\u52C7\u6562. \u6211\u8BA4\u4E3A\u8FD9\u662F\u6211\u4EEC\u7684\u57CE\u90A6\u503C\u5F97\u5D07\u62DC\u4E4B\u5904. \u5F53\u7136\u8FD8\u6709\u5176\u4ED6\u4F18\u70B9.</p><p>\u6211\u4EEC\u7231\u597D\u7F8E\u4E3D, \u4F46\u662F\u6CA1\u6709\u56E0\u6B64\u800C\u53D8\u5F97\u5962\u4F88; \u6211\u4EEC\u7231\u597D\u667A\u6167, \u4F46\u662F\u6CA1\u6709\u56E0\u6B64\u800C\u53D8\u5F97\u67D4\u5F31. \u6211\u4EEC\u628A\u8D22\u5BCC\u5F53\u4F5C\u53EF\u4EE5\u9002\u5F53\u5229\u7528\u7684\u4E1C\u897F, \u800C\u6CA1\u6709\u628A\u5B83\u5F53\u4F5C\u53EF\u4EE5\u5938\u8000\u81EA\u5DF1\u7684\u672C\u94B1. \u81F3\u4E8E\u8D2B\u7A77, \u8C01\u4E5F\u4E0D\u5FC5\u627F\u8BA4\u81EA\u5DF1\u7684\u8D2B\u7A77\u4E3A\u803B, \u771F\u6B63\u7684\u803B\u8FB1\u662F\u4E3A\u907F\u514D\u8D2B\u7A77\u800C\u4E0D\u62E9\u624B\u6BB5. \u5728\u6211\u4EEC\u8FD9\u91CC, \u6BCF\u4E00\u4E2A\u4EBA\u6240\u5173\u5FC3\u7684, \u4E0D\u4EC5\u662F\u4ED6\u81EA\u5DF1\u7684\u4E8B\u52A1, \u800C\u4E14\u4E5F\u5173\u5FC3\u56FD\u5BB6\u7684\u4E8B\u52A1; \u8FD9\u5C31\u662F\u90A3\u4E9B\u6700\u5FD9\u4E8E\u4ED6\u4EEC\u81EA\u5DF1\u7684\u4E8B\u52A1\u7684\u4EBA, \u5BF9\u4E8E\u4E00\u822C\u653F\u6CBB\u4E5F\u5F88\u719F\u6089 -- \u8FD9\u662F\u6211\u4EEC\u7684\u4E00\u4E2A\u7279: <strong>\u4E00\u4E2A\u4E0D\u5173\u5FC3\u653F\u6CBB\u7684\u4EBA, \u6211\u4EEC\u4E0D\u8BF4\u4ED6\u662F\u4E00\u4E2A\u53EA\u91CD\u6CE8\u81EA\u5DF1\u7684\u4E8B\u52A1\u7684\u4EBA, \u800C\u662F\u8BF4\u4ED6\u6839\u672C\u6CA1\u6709\u4E8B\u52A1</strong>. \u6211\u4EEC\u516C\u6C11\u81EA\u5DF1\u51B3\u5B9A\u6211\u4EEC\u7684\u653F\u7B56, \u6211\u4EEC\u7684\u653F\u7B56\u4E5F\u5C06\u5F97\u5230\u5E7F\u6CDB\u7684\u8BA8\u8BBA. \u6211\u4EEC\u8BA4\u4E3A\u8A00\u8BBA\u548C\u884C\u52A8\u4E4B\u95F4\u6CA1\u6709\u77DB\u76FE, \u6700\u574F\u7684\u4E8B\u60C5\u5C31\u662F <strong>\u6CA1\u6709\u7ECF\u8FC7\u6070\u5F53\u7684\u8BA8\u8BBA</strong>, \u5C31\u8D38\u7136\u5F00\u59CB\u884C\u52A8; \u8FD9\u4E00\u70B9\u53C8\u662F\u6211\u4EEC\u548C\u5176\u4ED6\u5730\u65B9\u7684\u4EBA\u6C11\u4E0D\u540C\u7684\u5730\u65B9. \u6211\u4EEC\u6562\u4E8E\u5192\u9669, \u540C\u65F6\u53C8\u80FD\u591F\u5728\u8FDB\u884C\u8FD9\u4E00\u5192\u9669\u4E4B\u524D\u6DF1\u601D\u719F\u8651. \u4ED6\u4EBA\u7684\u52C7\u6562, \u662F\u7531\u4E8E\u65E0\u77E5; \u5F53\u4ED6\u4EEC\u505C\u4E0B\u6765\u601D\u8003\u7684\u65F6\u5019, \u4ED6\u4EEC\u5C31\u5F00\u59CB\u7591\u60E7\u4E86. <strong>\u4F46\u662F\u771F\u6B63\u7B97\u5F97\u4E0A\u52C7\u6562\u7684\u4EBA\u662F\u90A3\u4E2A\u6700\u4E86\u89E3\u4EBA\u751F\u7684\u8F9B\u798F\u548C\u707E\u60A3, \u7136\u540E\u52C7\u5F80\u76F4\u524D, \u62C5\u5F53\u8D77\u5C06\u6765\u4F1A\u53D1\u751F\u7684\u4E8B\u53D8\u4E4B\u7ED3\u679C\u4E4B\u4EBA</strong>.</p><p>\u518D\u8005, <strong>\u5728\u5173\u4E8E\u53CB\u8C0A\u7684\u95EE\u9898\u4E0A</strong>, \u6211\u4EEC\u548C\u5176\u4ED6\u5927\u591A\u6570\u4EBA\u4E5F\u5F62\u6210\u4E00\u4E2A\u660E\u663E\u7684\u5BF9\u6BD4. <strong>\u6211\u4EEC\u7ED3\u4EA4\u670B\u53CB\u7684\u65B9\u6CD5\u662F\u7ED9\u4ED6\u4EBA\u4EE5\u597D\u5904</strong>, \u800C\u4E0D\u662F\u4ECE\u4ED6\u4EEC\u65B9\u9762\u5F97\u5230\u597D\u5904. \u8FD9\u5C31\u4F7F\u6211\u4EEC\u7684\u53CB\u8C0A\u66F4\u4E3A\u53EF\u9760, \u56E0\u4E3A\u6211\u4EEC\u8981\u7EE7\u7EED\u5BF9\u4ED6\u4EEC\u8868\u793A\u597D\u610F, \u4F7F\u53D7\u60E0\u4E8E\u6211\u4EEC\u7684\u4EBA\u6C38\u8FDC\u611F\u6FC0\u6211\u4EEC. \u4F46\u662F, \u4E00\u4E9B\u53D7\u4E86\u6211\u4EEC\u6069\u60E0\u7684\u4EBA, \u5728\u611F\u60C5\u4E0A\u7F3A\u5C11\u540C\u6837\u7684\u70ED\u5FF1; \u56E0\u4E3A\u4ED6\u4EEC\u89C9\u5F97, \u5728\u4ED6\u4EEC\u62A5\u7B54\u6211\u4EEC\u7684\u65F6\u5019, \u5C31\u597D\u50CF\u507F\u8FD8\u4E00\u7B14\u503A\u52A1, \u800C\u4E0D\u662F\u81EA\u89C9\u7684\u7ED9\u4E88\u6069\u60E0. <strong>\u5728\u8FD9\u65B9\u9762, \u6211\u4EEC\u662F\u72EC\u7279\u7684</strong>. \u5F53\u6211\u4EEC\u771F\u6B63\u7ED9\u4E88\u4ED6\u4EBA\u7684\u6069\u60E0\u65F6, \u6211\u4EEC\u4E0D\u662F\u56E0\u4E3A\u8003\u8651\u6211\u4EEC\u7684\u5F97\u5931\u624D\u8FD9\u6837\u505A\u7684, \u800C\u662F\u7531\u4E8E\u6211\u4EEC\u7684\u6177\u6168, \u6211\u4EEC\u4E0D\u4F1A\u56E0\u4E3A\u8FD9\u6837\u505A\u800C\u540E\u6094. \u56E0\u6B64, \u5982\u679C\u628A\u4E00\u5207\u8054\u7CFB\u8D77\u6765\u8003\u8651\u7684\u8BDD, \u6211\u53EF\u4EE5\u65AD\u8A00, <strong>\u6211\u4EEC\u7684\u57CE\u90A6\u662F\u5168\u5E0C\u814A\u7684\u5B66\u6821</strong>; \u6211\u4EEC\u53EF\u65AD\u8A00, \u6211\u4EEC\u6BCF\u4E2A\u516C\u6C11, \u5728\u751F\u6D3B\u7684\u5404\u4E2A\u65B9\u9762, \u90FD\u80FD\u72EC\u7ACB\u81EA\u4E3B; \u5E76\u4E14\u5728\u8868\u73B0\u72EC\u7ACB\u81EA\u4E3B\u7684\u65F6\u5019, <strong>\u80FD\u591F\u7279\u522B\u5730\u8868\u73B0\u6E29\u6587\u5C14\u96C5\u548C\u591A\u624D\u591A\u827A</strong>. \u4E3A\u4E86\u8BF4\u660E\u8FD9\u5E76\u4E0D\u662F\u5728\u8FD9\u4E2A\u5178\u793C\u4E0A\u7684\u7A7A\u6D1E\u81EA\u6211\u5439\u5618, \u800C\u662F\u771F\u6B63\u7684\u5177\u4F53\u4E8B\u5B9E, \u4F60\u4EEC\u53EA\u8981\u8003\u8651\u4E00\u4E0B: \u6B63\u56E0\u4E3A\u6211\u5728\u4E0A\u9762\u6240\u8BF4\u7684\u4F18\u826F\u54C1\u8D28, \u6211\u4EEC\u57CE\u90A6\u624D\u83B7\u5F97\u4E86\u5B83\u73B0\u5728\u7684\u52BF\u529B. \u6211\u4EEC\u6240\u77E5\u9053\u7684\u56FD\u5BB6\u4E2D, \u53EA\u6709 <code>\u96C5\u5178</code> \u5728\u9047\u5230\u6218\u4E89\u7684\u65F6\u5019, \u53EF\u4EE5\u8BC1\u660E\u5B83\u6BD4\u4E00\u822C\u4EBA\u6240\u60F3\u8C61\u7684\u66F4\u4E3A\u4F1F\u5927. \u5728 <code>\u96C5\u5178</code>\uFF0C \u4E5F\u53EA\u6709\u5728 <code>\u96C5\u5178</code>, \u5165\u4FB5\u7684\u654C\u4EBA\u4E0D\u4EE5\u6218\u8D25\u4E3A\u803B\u8FB1; \u53D7\u5B83\u7EDF\u6CBB\u7684\u5C5E\u6C11\u4E5F\u4E0D\u56E0\u7EDF\u6CBB\u8005\u4E0D\u591F\u683C\u800C\u62A5\u6028. \u771F\u7684, \u6211\u4EEC\u7956\u5148\u6240\u9057\u7559\u4E0B\u7684\u56FD\u5BB6\u6807\u5FD7\u548C\u7EAA\u5FF5\u7269\u662F\u5DE8\u5927\u7684, \u4E0D\u4F46\u73B0\u5728, \u800C\u4E14\u540E\u4E16\u4E5F\u4F1A\u5BF9\u6211\u4EEC\u8868\u793A\u8D5E\u53F9. \u6211\u4EEC\u4E0D\u9700\u8981\u8377\u9A6C\u7684\u6B4C\u9882, \u56E0\u4E3A\u4ED6\u4EEC\u7684\u6B4C\u9882\u53EA\u80FD\u4F7F\u6211\u4EEC\u5A31\u4E50\u4E00\u65F6, \u800C\u4ED6\u4EEC\u5BF9\u4E8E\u4E8B\u5B9E\u7684\u4F30\u8BA1\u4E5F\u4E0D\u8DB3\u4EE5\u4EE3\u8868\u771F\u5B9E\u7684\u60C5\u51B5. \u56E0\u4E3A\u6211\u4EEC\u5192\u9669\u7CBE\u795E\u5145\u6EA2\u7740\u6BCF\u4E2A\u6D77\u6D0B\u548C\u6BCF\u5757\u9646\u5730; \u6211\u4EEC\u5230\u5904\u5BF9\u6211\u4EEC\u7684\u670B\u53CB\u65BD\u4EE5\u6069\u5FB7, \u5BF9\u6211\u4EEC\u7684\u654C\u4EBA\u654C\u4EBA\u7ED9\u4E88\u51FB; \u5173\u4E8E\u8FD9\u4E9B\u4E8B\u60C5, \u6211\u4EEC\u5C06\u9057\u7559\u6C38\u4E45\u7EAA\u5FF5\u4E8E\u540E\u4E16.</p><p>\u8FD9\u5C31\u662F\u8FD9\u4E9B\u70C8\u58EB\u4E3A\u5B83\u6177\u6168\u800C\u6218, \u6177\u6168\u800C\u6B7B\u7684\u4E00\u4E2A\u57CE\u90A6, \u56E0\u4E3A\u4ED6\u4EEC\u53EA\u8981\u60F3\u5230\u4E27\u5931\u8FD9\u4E2A\u57CE\u90A6, \u5C31\u4F1A\u4E0D\u5BD2\u800C\u6817. \u5341\u5206\u81EA\u7136, \u6211\u4EEC\u751F\u4E8E\u4ED6\u4EEC\u4E4B\u540E\u7684\u4EBA, \u6BCF\u4E2A\u4EBA\u90FD\u5E94\u5F53\u5FCD\u53D7\u4E00\u5207\u75DB\u82E6\u4E3A\u5B83\u670D\u52A1. \u56E0\u4E3A\u8FD9\u4E2A\u7F18\u6545, \u6211\u8BF4\u4E86\u8FD9\u4E48\u591A\u8BDD\u6765\u8BA8\u8BBA\u6211\u4EEC\u7684\u57CE\u90A6, \u56E0\u4E3A\u6211\u8981\u5F88\u6E05\u695A\u7684\u8BF4\u660E, \u6211\u4EEC\u6240\u4E89\u53D6\u7684\u76EE\u6807\u6BD4\u8D77\u5176\u4ED6\u90A3\u4E9B\u6CA1\u6709\u6211\u4EEC\u5FE7\u70B9\u7684\u4EBA\u6240\u4E89\u53D6\u76EE\u6807\u8FDC\u5927. \u56E0\u6B64, \u6211\u60F3\u7528\u5B9E\u8BC1\u6765\u66F4\u6E05\u695A\u5730\u8868\u8FBE\u6211\u5BF9\u9635\u4EA1\u5C06\u58EB\u4EEC\u7684\u6B4C\u9882. \u73B0\u5728\u5BF9\u4E8E\u4ED6\u4EEC\u7684\u6B4C\u9882\u7684\u6700\u8981\u7684\u90E8\u5206, \u6211\u5DF2\u7ECF\u8BF4\u5B8C\u4E86. \u6211\u5DF2\u7ECF\u6B4C\u9882\u4E86\u6211\u4EEC\u7684\u57CE\u90A6, \u4F46\u662F, \u4F7F\u6211\u4EEC\u7684\u57CE\u90A6\u5149\u660E\u707F\u70C2\u7684\u662F\u8FD9\u4E9B\u4EBA\u548C\u7C7B\u4F3C\u4ED6\u4EEC\u7684\u4EBA\u7684\u52C7\u6562\u548C\u82F1\u96C4\u6C14\u6982. \u540C\u65F6, \u4F60\u4EEC\u4E5F\u4F1A\u53D1\u73B0, \u8A00\u8BCD\u662F\u4E0D\u80FD\u514D\u516C\u5141\u5730\u8868\u8FBE\u4ED6\u4EEC\u7684\u884C\u4E3A\u7684. \u5728\u6240\u7684\u5E0C\u814A\u4EBA\u4E2D\u95F4, \u6709\u4ED6\u4EEC\u8FD9\u79CD\u52C7\u6C14\u7684\u4EBA\u4E5F\u4E0D\u4F1A\u5F88\u591A.</p><p>\u5728\u6211\u770B\u6765, \u4ED6\u4EEC\u7684\u90A3\u79CD\u58EE\u70C8\u732E\u8EAB, \u5411\u6211\u4EEC\u8868\u73B0\u4E86\u975E\u51E1\u7684\u82F1\u96C4\u6C14\u6982, \u4E0D\u7BA1\u5B83\u662F\u521D\u6B21\u8868\u73B0\u7684\u4E5F\u597D, \u6216\u8005\u662F\u6700\u540E\u8BC1\u5B9E\u7684\u4E5F\u597D. \u4ED6\u4EEC\u4E2D\u95F4\u6709\u4E9B\u4EBA\u65E0\u7591\u662F\u6709\u7F3A\u70B9\u7684, \u4F46\u662F\u6211\u4EEC\u6240\u5E94\u5F53\u8BB0\u7740\u7684, \u9996\u5148\u662F\u4ED6\u4EEC\u62B5\u6297\u654C\u4EBA, \u634D\u536B\u7956\u56FD\u7684\u82F1\u52C7\u884C\u4E3A. \u4ED6\u4EEC\u7684\u4F18\u70B9\u62B5\u6D88\u4E86\u4ED6\u4EEC\u7684\u7F3A\u70B9, \u4ED6\u4EEC\u5BF9\u56FD\u5BB6\u7684\u8D21\u732E\u591A\u4E8E\u4ED6\u4EEC\u5728\u79C1\u4EBA\u751F\u6D3B\u4E2D\u6240\u4F5C\u7684\u7978\u5BB3. \u4ED6\u4EEC\u8FD9\u4E9B\u4EBA\u4E2D\u95F4, \u6CA1\u6709\u4EBA\u56E0\u4E3A\u60F3\u7EE7\u7EED\u4EAB\u53D7\u4ED6\u4EEC\u7684\u8D22\u5BCC\u800C\u53D8\u4E3A\u5112\u592B, \u4E5F\u6CA1\u6709\u4EBA\u9003\u907F\u8FD9\u4E2A\u5371\u96BE\u7684\u65E5\u5B50, \u4EE5\u56FE\u5077\u751F\u8131\u79BB\u7A77\u56F0\u800C\u83B7\u5F97\u5BCC\u88D5. \u4ED6\u4EEC\u6240\u9700\u8981\u7684\u662F\u4E0D\u662F\u8FD9\u4E9B\u4E1C\u897F, \u800C\u662F\u8981\u632B\u8D25\u654C\u4EBA\u7684\u9A84\u6C14. \u5728\u4ED6\u4EEC\u770B\u6765, \u8FD9\u662F\u6700\u5149\u8363\u7684\u5192\u9669. \u4ED6\u4EEC\u62C5\u5F53\u4E86\u8FD9\u4E2A\u5192\u9669, \u613F\u610F\u51FB\u6E83\u654C\u4EBA, \u800C\u653E\u5F03\u4E86\u5176\u4ED6\u4E00\u5207. \u81F3\u4E8E\u6210\u8D25, \u4ED6\u4EEC\u8BA9\u5B83\u7559\u5728\u4E0D\u53EF\u9884\u6D4B\u7684\u5E0C\u671B\u5973\u795E\u540E\u4E2D. \u5F53\u4ED6\u4EEC\u771F\u7684\u9762\u4E34\u6218\u6597\u7684\u65F6\u5019, \u4ED6\u4EEC\u4FE1\u8D56\u81EA\u5DF1. \u5728\u6218\u6597\u4E2D, \u4ED6\u4EEC\u8BA4\u4E3A\u4FDD\u6301\u81EA\u5DF1\u7684\u5C97\u4F4D\u800C\u6218\u6B7B\u6BD4\u5C48\u670D\u800C\u9003\u751F\u66F4\u5149\u8363. \u6240\u4EE5\u4ED6\u4EEC\u6CA1\u53D7\u5230\u522B\u4EBA\u7684\u8D23\u96BE, \u800C\u662F\u4EE5\u81EA\u5DF1\u7684\u8840\u8089\u4E4B\u9A71\u62B5\u6321\u4E86\u654C\u4EBA\u7684\u51B2\u950B. \u9877\u523B\u95F4, \u5728\u4ED6\u4EEC\u751F\u547D\u7684\u9876\u70B9, \u4E5F\u662F\u5149\u8363\u7684\u9876\u70B9, \u800C\u53CC\u662F\u6050\u60E7\u7684\u9876\u70B9, \u4ED6\u4EEC\u5C31\u79BB\u5F00\u6211\u4EEC\u957F\u901D\u4E86.</p><p>\u4ED6\u4EEC\u7684\u884C\u4E3A\u662F\u8FD9\u6837\u7684\u52C7\u6562, \u8FD9\u4E9B\u4EBA\u65E0\u6127\u4E8E\u4ED6\u4EEC\u7684\u57CE\u90A6. \u6211\u4EEC\u8FD9\u4E9B\u5C1A\u8FD8\u751F\u5B58\u7684\u4EBA\u4EEC\u53EF\u4EE5\u5E0C\u671B\u4E0D\u4F1A\u906D\u9047\u548C\u4ED6\u4EEC\u540C\u6837\u7684\u547D\u8FD0, \u4F46\u662F\u5728\u5BF9\u6297\u654C\u4EBA\u7684\u65F6\u5019, \u6211\u4EEC\u4E00\u5B9A\u8981\u6709\u540C\u6837\u7684\u52C7\u6562\u7CBE\u795E. \u8FD9\u4E0D\u662F\u5355\u7EAF\u4ECE\u7406\u8BBA\u4E0A\u4F30\u8BA1\u7684\u4F18\u70B9. \u5173\u4E8E\u51FB\u8D25\u654C\u4EBA\u7684\u597D\u5904, \u6211\u53EF\u4EE5\u8BF4\u5F97\u5F88\u591A ( \u8FD9\u4E9B, \u4F60\u4EEC\u548C\u6211\u4E00\u6837\u90FD\u662F\u77E5\u9053\u7684 ). <strong>\u6211\u5B81\u613F\u4F60\u4EEC\u6BCF\u5929\u628A\u773C\u5149\u96C6\u4E2D\u5230\u96C5\u5178\u7684\u4F1F\u5927\u4E0A</strong>. \u5B83\u771F\u6B63\u662F\u4F1F\u5927\u7684, \u4F60\u4EEC\u5E94\u5F53\u70ED\u7231\u5B83. \u5F53\u4F60\u8BA4\u8BC6\u5230\u5B83\u7684\u4F1F\u5927\u65F6, \u518D\u56DE\u5FC6\u4E00\u4E0B, \u4F7F\u5B83\u4F1F\u5927\u7684\u662F\u6709\u6700\u5192\u9669\u7CBE\u795E\u7684\u4EBA\u4EEC, \u77E5\u9053\u4ED6\u4EEC\u7684\u8D23\u4EFB\u7684\u4EBA\u4EEC, \u6DF1\u4EE5\u4E0D\u8FBE\u5230\u67D0\u79CD\u76EE\u6807\u4E3A\u803B\u8FB1\u7684\u4EBA\u4EEC. \u5982\u679C\u4ED6\u4EEC\u5728\u4E00\u9879\u4E8B\u4E1A\u4E0A\u5931\u8D25\u4E86, \u4ED6\u4EEC\u4E5F\u4F1A\u4E0B\u5B9A\u51B3\u5FC3, \u4E0D\u8BA9\u4ED6\u4EEC\u7684\u57CE\u90A6\u53D1\u73B0\u4ED6\u4EEC\u7F3A\u4E4F\u52C7\u6562, \u4ED6\u4EEC\u5C3D\u53EF\u80FD\u628A\u6700\u597D\u7684\u4E1C\u897F\u8D21\u732E\u7ED9\u4E86\u56FD\u5BB6. \u4ED6\u4EEC\u8D21\u732E\u4E86\u4ED6\u4EEC\u7684\u751F\u547D\u7ED9\u56FD\u5BB6\u548C\u6211\u4EEC\u5168\u4F53, \u81F3\u4E8E\u4ED6\u81EA\u5DF1, \u5219\u83B7\u5F97\u4E86\u6C38\u8FDC\u5E38\u9752\u7684\u8D5E\u7F8E, \u6700\u5149\u8F89\u707F\u6599\u7684\u575F\u5893 -- \u4E0D\u662F\u4ED6\u4EEC\u9057\u4F53\u6240\u5B89\u846C\u7684\u575F\u5893, <strong>\u800C\u662F\u4ED6\u4EEC\u5149\u8363\u6C38\u8FDC\u7559\u5728\u4EBA\u4EEC\u7684\u5FC3\u4E2D</strong>, \u6BCF\u5230\u9002\u5F53\u7684\u65F6\u673A, \u603B\u4F1A\u6FC0\u52A8\u7740\u4ED6\u4EBA\u7684\u8A00\u8BBA\u6216\u884C\u52A8. \u6574\u4E2A\u5730\u7403\u5176\u5B9E\u90FD\u662F\u4ED6\u4EEC\u7684\u7EAA\u5FF5\u7269; \u4ED6\u4EEC\u7EAA\u5FF5\u7269\u4E0D\u4EC5\u662F\u5728\u81EA\u5DF1\u7684\u7956\u56FD, \u800C\u4E14\u4E5F\u5728\u5916\u56FD; \u4ED6\u4EEC\u7684\u82F1\u540D\u5DF2\u7ECF\u751F\u6839\u5728\u4EBA\u4EEC\u5FC3\u4E2D, \u800C\u4E0D\u662F\u51FF\u523B\u5728\u6709\u5F62\u7684\u77F3\u7891\u4E0A. \u4F60\u4EEC\u5E94\u8BE5\u52AA\u529B\u5B66\u4E60\u4ED6\u4EEC\u7684\u699C\u6837. \u4F60\u4EEC\u8981\u4E0B\u51B3\u5FC3: <strong>\u8981\u81EA\u7531, \u624D\u80FD\u6709\u5E78\u798F; \u8981\u52C7\u6562,\u624D\u80FD\u6709\u81EA\u7531</strong>.</p><p>\u73B0\u5728\u4F9D\u7167\u6CD5\u5F8B\u4E0A\u7684\u8981\u6C42, \u6211\u5DF2\u7ECF\u8BF4\u4E86\u6211\u6240\u5E94\u5F53\u8BF4\u7684\u8BDD. \u521A\u624D\u6211\u4EEC\u5DF2\u5BF9\u6B7B\u8005\u4F5C\u4E86\u796D\u732E. \u5C06\u6765\u4ED6\u4EEC\u7684\u513F\u5973\u4EEC\u5C06\u7531\u516C\u8D39\u629A\u517B, \u76F4\u5230\u4ED6\u4EEC\u8FBE\u5230\u6210\u5E74\u4E3A\u6B62. \u8FD9\u662F\u56FD\u5BB6\u7ED9\u4E88\u6B7B\u8005\u548C\u4ED6\u4EEC\u513F\u5973\u4EEC\u7684\u82B1\u51A0\u548C\u5956\u54C1, \u4F5C\u4E3A\u4ED6\u4EEC\u7ECF\u5F97\u4F4F\u8003\u9A8C\u7684\u916C\u8C22. \u51E1\u662F\u5BF9\u4E8E\u52C7\u6562\u6709\u6700\u5927\u7684\u5956\u8D4F\u7684\u5730\u65B9, \u5C31\u53EF\u4EE5\u627E\u5230\u4EBA\u6C11\u4E2D\u95F4\u6700\u4F18\u79C0\u548C\u6700\u52C7\u6562\u7684\u7CBE\u795E. \u73B0\u5728\u4F60\u4EEC\u5BF9\u4E8E\u9635\u4EA1\u7684\u4EB2\u5C5E\u5DF2\u81F4\u54C0\u540A, \u4F60\u4EEC\u53EF\u4EE5\u6563\u5F00\u4E86.</p>',13),e=[g];function p(_,c){return t(),r("div",null,e)}var d=o(s,[["render",p],["__file","\u4F2F\u5229\u514B\u5229\u9635\u4EA1\u5C06\u58EB\u6F14\u8BB2.html.vue"]]);export{d as default};
