import{_ as e,o as p,c as a,e as d}from"./app.6bf529a6.js";const c={},r=d('<h1 id="\u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66" tabindex="-1"><a class="header-anchor" href="#\u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66" aria-hidden="true">#</a> \u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66</h1><h2 id="\u7F18\u8D77" tabindex="-1"><a class="header-anchor" href="#\u7F18\u8D77" aria-hidden="true">#</a> \u7F18\u8D77</h2><p>\u81EA\u4ECE\u5728\u624B\u673A\u4E0A\u5199\u968F\u7B14\u5F00\u59CB, \u6709\u4E00\u79CD\u5F3A\u5217\u7684\u628A\u6240\u6709\u8FD9\u79CD\u968F\u65F6\u60F3\u8D77\u6765\u5199\u4E00\u4E0B\u7684\u4E1C\u897F\u90FD\u8D4B\u4E88\u76F8\u4F3C\u7ED3\u6784\u7684\u51B2\u52A8.</p><p>\u60F3\u6765\u4E4B\u6240\u4EE5\u5982\u6B64, \u5927\u534A\u662F\u4E3A\u4E86\u4F7F\u6211\u5728\u5199\u4F5C\u65F6\u5019, \u628A\u73B0\u5728\u8FD9\u6837\u7684\u5E9F\u8BDD\u548C\u6709\u7528\u7684\u4E1C\u897F\u5206\u5F00.</p><p>\u6765\u8BF4\u8BF4\u8FD9\u4E2A\u60F3\u6CD5\u7684\u7F18\u8D77\u5427.</p><p>\u8FD9\u4E2A\u60F3\u6CD5\u662F\u5728\u67E5\u9605 <code>\u666F\u89C2\u793E\u4F1A</code> \u65F6\u60F3\u5230\u7684.</p><p>\u5728\u67E5\u9605 <code>\u666F\u89C2\u793E\u4F1A</code> \u65F6, \u6211\u53D1\u73B0\u8FD9\u4E2A\u89C2\u70B9\u662F\u5728\u4E00\u4E2A\u8109\u843D\u4E2D\u7684. \u5B83\u4E0A\u627F\u9A6C\u514B\u601D, \u4E0B\u542F\u9C8D\u5FB7\u91CC\u4E9A, \u662F\u73B0\u4EE3\u9A6C\u514B\u601D\u4E3B\u4E49\u7684\u4E00\u652F.</p><p>\u8FD9\u65F6\u6211\u5B9E\u7136\u610F\u5230, \u9664\u4E86\u6211\u4EEC\u4E00\u76F4\u4EE5\u6765\u6240\u8BF4\u7684\u6D88\u5F25\u4E86\u7684\u54F2\u5B66\u4E4B\u5916, \u4E00\u76F4\u6709\u4E00\u652F\u54F2\u5B66\u652F\u6D41\u8FD8\u5728\u8D77\u7740\u91CD\u8981\u7684\u4F5C\u7528, \u8FD9\u5C31\u662F\u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66.</p><h2 id="\u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66-1" tabindex="-1"><a class="header-anchor" href="#\u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66-1" aria-hidden="true">#</a> \u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66</h2><p>\u4E00\u76F4\u4EE5\u6765, \u5BF9\u4E8E\u73B0\u4EE3\u54F2\u5B66\u7684\u547D\u8FD0, \u6211\u90FD\u91C7\u53D6 <code>\u54F2\u5B66\u5DF2\u6B7B</code> \u7684\u89C2\u70B9.</p><p>\u8FD9\u4E2A\u89C2\u70B9\u88AB\u73B0\u4EE3\u601D\u60F3\u5BB6\u4E00\u518D\u63D0\u8D77. \u800C\u6211\u4EEC\u6240\u8BA4\u4E3A\u7684\u73B0\u4EE3\u54F2\u5B66\u7684\u4E24\u4E2A\u6700\u6709\u4EF7\u503C\u7684\u652F\u8109\u90FD\u4EE5\u6B64\u89C2\u70B9\u4E3A\u57FA\u7840:</p><ul><li>\u5206\u6790\u54F2\u5B66: \u7EF4\u7279\u6839\u65AF\u5766\u8BA4\u4E3A, \u73B0\u5728\u54F2\u5B66\u5DF2\u7ECF\u5931\u53BB\u4E86\u4F20\u7EDF\u7684\u4EF7\u503C, \u800C\u53EA\u80FD\u542F\u5230\u533B\u751F\u7684\u8B66\u5BDF\u7684\u4EF7\u503C, \u4E5F\u5373\u53EA\u80FD\u7528\u4E8E\u5206\u6790, \u4EC0\u4E48\u54EA\u4E9B\u6982\u5FF5\u662F\u9519\u7684, \u9762\u5BF9\u8FD9\u4E9B\u6982\u9519\u7684\u6982\u5FF5\u6211\u4EEC\u600E\u4E48\u529E.</li><li>\u5B58\u5728\u4E3B\u4E49: \u6D77\u5FB7\u683C\u5C14, \u6D77\u5FB7\u683C\u5C14\u8BA4\u4E3A\u73B0\u5728\u54F2\u5B66\u5DF2\u7ECF\u6D88\u4EA1\u4E86, \u6211\u4EEC\u80FD\u4F5C\u7684\u53EA\u662F <code>\u8BD7\u4E0E\u601D</code>.</li></ul><p>\u7136\u800C, \u660E\u660E\u8FD8\u6709\u4E00\u652F\u54F2\u5B66\u652F\u6D41, \u4E00\u76F4\u88AB\u6211\u4EEC\u8C08\u8BBA, \u5B83\u4EEC\u81EA\u8EAB\u4E5F\u5E76\u6CA1\u6709\u8981\u6D88\u4EA1\u7684\u6837\u5B50, \u4F46\u662F\u6211\u5C31\u662F\u5BF9\u5176\u89C6\u800C\u4E0D\u89C1, \u575A\u6301\u7740 <code>\u54F2\u5B66\u6D88\u4EA1\u8BBA</code>.</p><p>\u8FD9\u4E00\u652F\u54F2\u5B66\u652F\u6D41\u5C31\u662F\u4EE5\u793E\u4F1A\u6279\u5224\u4E3A\u4E3B\u8981\u76EE\u6807\u7684\u4E00\u7CFB\u5217\u54F2\u5B66\u5BB6: \u897F\u65B9\u9A6C\u514B\u601D\u4E3B\u4E49\u4EE5\u53CA\u6700\u8457\u540D\u7684\u4EE3\u8868 -- \u6CD5\u5170\u514B\u798F\u5B66\u6D3E.</p><p>\u8FD9\u4E00\u652F\u6D41\u76F4\u5230\u73B0\u5728\u8FD8\u6D3B\u8DC3\u7740, \u63D0\u4F9B\u7740\u5BF9\u4E8E\u73B0\u4EE3\u8D44\u672C\u4E3B\u4E49\u6279\u5224\u7684\u53CD\u601D\u5DE5\u5177.</p><p>\u73B0\u4EE3\u793E\u4F1A\u662F\u4E00\u6574\u5957\u5F02\u5316\u548C\u89C4\u8BAD\u7684\u4F53\u7CFB, \u5728\u8FD9\u4E00\u4F53\u7CFB\u4E2D, \u4E00\u5207\u4EBA\u90FD\u65E0\u4ECE\u9003\u907F. \u800C\u4E14, \u56E0\u4E3A\u542F\u8499, \u53CD\u601D\u672C\u8EAB\u6210\u4E3A\u4E86\u8D44\u672C\u4E3B\u4E49\u793E\u4F1A\u6D17\u6211\u4EEC\u8111\u7684\u5DE5\u5177: \u591A\u5143\u4EF7\u503C, \u76F8\u5BF9\u4E3B\u4E49, \u6D88\u8D39\u4E3B\u4E49\u7B49\u7B49, \u6210\u4E3A\u6211\u4EEC\u7684\u53CD\u601D\u7684\u8BA4\u5E95\u5C42.</p><p>\u800C\u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66, \u5C31\u662F\u4E3A\u6211\u4EEC\u63D0\u4F9B\u4E00\u4E2A\u6279\u5224\u548C\u53CD\u5BF9\u8FD9\u4E9B\u8D44\u672C\u4E3B\u4E49\u4EF7\u503C\u89C2\u7684\u5DE5\u5177.</p><p>\u800C\u8FD8\u4E0D\u662F\u5C31\u662F\u8BBA\u4E8B\u7684\u7B80\u5355\u53CD\u5BF9\u548C\u6279\u5224, \u800C\u662F\u627F\u63A5\u4E86\u897F\u65B9\u54F2\u5B66\u7684\u8109\u843D, \u63A5\u7EE7\u4E86\u5F62\u800C\u4E0A\u5B66\u7684\u4EF7\u503C, \u5E76\u4EE5\u6B64\u4E3A\u57FA\u7840\u6765\u53CD\u5BF9\u548C\u6279\u5224\u73B0\u4EE3\u7684\u8D44\u672C\u4E3B\u4E49.</p><p>\u56E0\u6B64, \u8FD9\u4E00\u652F\u6D41\u5B8C\u5168\u5E94\u8BE5\u88AB\u770B\u4EE3, \u897F\u65B9\u54F2\u5B66\u4EF7\u503C\u5728\u5F53\u4EE3\u7684\u6CBF\u7EED.</p><h2 id="\u4E3A\u4EC0\u4E48\u88AB\u6211\u4EEC\u89C6\u800C\u4E0D\u89C1" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u88AB\u6211\u4EEC\u89C6\u800C\u4E0D\u89C1" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u88AB\u6211\u4EEC\u89C6\u800C\u4E0D\u89C1</h2><p>\u56E0\u4E3A\u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66, \u5FC5\u7136\u8981\u5BF9\u653F\u6CBB\u548C\u73B0\u5B9E\u516C\u5171\u751F\u6D3B\u8FDB\u884C\u8C08\u8BBA.</p><p>\u9996\u5148, \u73B0\u4EE3\u793E\u4F1A\u7684\u4E2A\u4EBA\u4E3B\u4E49\u548C\u5E73\u6C11\u793E\u4F1A\u5C31\u662F\u628A\u4E00\u4E2A\u628A\u4EBA\u4ECE\u516C\u5171\u751F\u6D3B\u4E2D\u5265\u79BB\u7684\u8FC7\u7A0B. \u5305\u62EC\u4E92\u8054\u7F51\u6240\u5F00\u542F\u7684\u793E\u4F1A\u89C2\u5FF5\u548C\u751F\u6D3B\u65B9\u5F0F, \u5C31\u662F\u5728\u4E0D\u65AD\u628A\u4EBA\u4EEC\u53D8\u6210\u4E00\u4E2A\u4E2A\u5206\u79BB\u7684\u4E2A\u4F53.</p><p>\u800C\u4E14, \u6211\u4EEC\u5C24\u5176\u5904\u4E8E\u4E00\u4E2A\u653F\u6CBB\u548C\u516C\u5171\u8A00\u8BBA\u7A7A\u95F4\u88AB\u4E25\u91CD\u538B\u7F29\u7684\u5730\u65B9.</p><p>\u897F\u65B9\u9A6C\u514B\u601D\u4E3B\u4E49, \u5BF9\u4E8E\u6211\u4EEC\u53C8\u662F\u4E00\u4E2A\u683C\u5916\u5C34\u5C2C\u7684\u5B58\u5728.</p><p>\u6240\u4EE5\u8FD9\u4E00\u6574\u5957\u7CFB\u7EDF\u7684\u54F2\u5B66\u4F53\u7CFB, \u6211\u4EEC\u53EA\u662F\u96F6\u661F\u7684\u63D0\u5230, \u800C\u4ECE\u6765\u6CA1\u6709\u771F\u6B63\u610F\u8BC6\u5B83\u4EEC\u7684\u5728\u573A.</p><p>\u800C\u54F2\u5B66\u8FD9\u79CD\u6D3B\u52A8, \u5B83\u7684\u4E3B\u8981\u5B9E\u8DF5\u65B9\u5F0F\u5C31\u662F\u8A00\u8BBA, \u5C31\u5BF9\u6709\u5BF9\u8C61\u7684\u8A00\u8BF4.</p><p>\u6240\u4EE5\u5F53\u6211\u4EEC\u63D0\u5230\u54F2\u5B66\u65F6, \u7406\u6240\u5F53\u7136\u7684\u628A\u8FD9\u79CD\u6279\u5224\u793E\u4F1A\u73B0\u5B9E\u7684\u54F2\u5B66\u5FFD\u7565\u4E86.</p>',27),h=[r];function o(i,t){return p(),a("div",null,h)}var n=e(c,[["render",o],["__file","\u4F5C\u4E3A\u793E\u4F1A\u6279\u5224\u7684\u54F2\u5B66.html.vue"]]);export{n as default};
