import{_ as e,o as r,c as p,e as l}from"./app.6bf529a6.js";var h="/blog/img/project/shangyemoshi04/01.jpeg",d="/blog/img/project/shangyemoshi04/02.jpeg",s="/blog/img/project/shangyemoshi04/03.jpeg",o="/blog/img/project/shangyemoshi04/04.jpeg",t="/blog/img/project/shangyemoshi04/05.jpeg",c="/blog/img/project/shangyemoshi04/06.jpeg",n="/blog/img/project/shangyemoshi04/07.jpeg",g="/blog/img/project/shangyemoshi04/08.jpeg",_="/blog/img/project/shangyemoshi04/09.jpeg",m="/blog/img/project/shangyemoshi04/10.jpeg",u="/blog/img/project/shangyemoshi04/11.jpeg",b="/blog/img/project/shangyemoshi04/12.jpeg",a="/blog/img/project/shangyemoshi04/13.jpeg",i="/blog/img/project/shangyemoshi04/14.jpeg";const O={},f=l('<h1 id="\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F-o2o\u96F7\u533A\u6307\u5357" tabindex="-1"><a class="header-anchor" href="#\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F-o2o\u96F7\u533A\u6307\u5357" aria-hidden="true">#</a> \u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\uFF1AO2O\u96F7\u533A\u6307\u5357</h1><p><img src="'+h+'" alt="\u5C01\u9762"></p><h2 id="\u4E00-\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u4E00-\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\u662F\u4EC0\u4E48" aria-hidden="true">#</a> \u4E00 \u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\u662F\u4EC0\u4E48</h2><p>\u5728\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\u4E2D, \u81F3\u5C11\u6709\u4E00\u4E2A\u5173\u952E\u5BA2\u6237\u7FA4\u4F53\u662F \u53EF\u4EE5\u6301\u7EED\u514D\u8D39\u5730\u4EAB\u53D7\u670D\u52A1 \u7684. \u4E0D\u4ED8\u8D39\u7684\u5BA2\u6237\u6240\u5F97\u5230\u7684\u670D\u52A1, \u5176\u8D22\u52A1\u652F\u6301\u6765\u5904\u5546\u4E1A\u6A21\u5F0F\u4E2D\u7684\u53E6\u4E00\u5BA2\u6237\u7FA4</p><p>\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\u6709\u4E09\u79CD: \u5E7F\u544A, \u514D\u8D39\u589E\u503C, \u9493\u9C7C.</p><h3 id="_1-\u5E7F\u544A-\u4E00\u4E2A\u591A\u5E73\u53F0\u5546\u4E1A\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#_1-\u5E7F\u544A-\u4E00\u4E2A\u591A\u5E73\u53F0\u5546\u4E1A\u6A21\u5F0F" aria-hidden="true">#</a> 1 \u5E7F\u544A: \u4E00\u4E2A\u591A\u5E73\u53F0\u5546\u4E1A\u6A21\u5F0F</h3><h4 id="\u514D\u8D39\u62A5\u7EB8-metro" tabindex="-1"><a class="header-anchor" href="#\u514D\u8D39\u62A5\u7EB8-metro" aria-hidden="true">#</a> \u514D\u8D39\u62A5\u7EB8 Metro</h4><p>Metro \u662F\u53D1\u884C\u4E8E\u65AF\u5FB7\u54E5\u5C14\u6469\u7684\u514D\u8D39\u62A5\u7EB8, \u76EE\u524D\u5B83\u51ED\u501F\u514D\u8D39\u6A21\u5F0F\u5C06\u4E1A\u52A1\u6269\u5C55\u5230\u6B27\u6D32\u4E3B\u8981\u5927\u57CE\u5E02\u4E86.</p><p><img src="'+d+'" alt="\u514D\u8D39\u62A5\u7EB8"></p><p>\u5B83\u7684\u5546\u4E1A\u6A21\u5F0F\u53EF\u4EE5\u6982\u62EC\u4E3A:</p><ul><li>\u62A5\u7EB8\u662F\u514D\u8D39\u7684</li><li>\u6D3E\u53D1\u5728\u4EBA\u6D41\u5BC6\u96C6\u7684\u529E\u516C\u533A\u4EE5\u53CA\u516C\u5171\u4EA4\u901A\u7F51\u70B9, \u91C7\u7528\u4EBA\u5DE5\u53D1\u653E\u6216\u9648\u5217\u5C55\u793A\u4EE5\u4F9B\u81EA\u53D6\u7684\u65B9\u5F0F\u53D1\u653E</li><li>\u8D28\u91CF\u4EC5\u6EE1\u8DB3\u4E8E\u4E0A\u73ED\u901A\u52E4\u5F80\u8FD4\u9014\u4E2D\u6D88\u78E8\u65F6\u95F4, \u56E0\u6B64\u6240\u9700\u7F16\u8F91\u6210\u672C\u5F88\u4F4E</li><li></li></ul><h4 id="\u88AB\u514D\u8D39\u6A21\u5F0F\u7C89\u788E\u7684\u884C\u4E1A" tabindex="-1"><a class="header-anchor" href="#\u88AB\u514D\u8D39\u6A21\u5F0F\u7C89\u788E\u7684\u884C\u4E1A" aria-hidden="true">#</a> \u88AB\u514D\u8D39\u6A21\u5F0F\u7C89\u788E\u7684\u884C\u4E1A</h4><p>\u4F20\u7EDF\u62A5\u4E1A\u6709\u4E09\u4E2A\u6536\u5165\u6765\u6E90:</p><ul><li>\u62A5\u520A\u4EAD\u7684\u9500\u552E</li><li>\u8BA2\u9605\u8D39</li><li>\u5E7F\u544A</li></ul><p>\u524D\u4E24\u4E2A\u6536\u5165\u5728\u514D\u8D39\u62A5\u6A21\u5F0F\u7684\u51B2\u51FB\u4E0B, \u8FC5\u901F\u51CF\u5C0F, \u800C\u7B2C\u4E09\u4E2A\u6536\u5165\u6765\u6E90\u6CA1\u6709\u660E\u663E\u589E\u52A0.</p><p>\u5728\u7EBF\u671F\u520A\u662F\u4F20\u7EDF\u62A5\u520A\u4F01\u4E1A\u8FDB\u884C\u9769\u65B0\u7684\u5C1D\u8BD5, \u4F46\u7ED3\u679C\u5E76\u6CA1\u6709\u660E\u663E\u6210\u6548: \u5F88\u96BE\u4F7F\u5728\u7EBF\u7528\u6237\u4ED8\u8D39.</p><h4 id="\u62A5\u520A\u884C\u4E1A\u7684\u65B0\u5C1D\u8BD5" tabindex="-1"><a class="header-anchor" href="#\u62A5\u520A\u884C\u4E1A\u7684\u65B0\u5C1D\u8BD5" aria-hidden="true">#</a> \u62A5\u520A\u884C\u4E1A\u7684\u65B0\u5C1D\u8BD5</h4><p>\u88AB\u514D\u8D39\u6A21\u5F0F\u51B2\u7684\u4F20\u7EDF\u62A5\u520A\u884C\u4E1A, \u5728\u8FDB\u884C\u65B0\u7684\u5C1D\u8BD5:</p><ul><li>\u805A\u96C6\u884C\u4E1A\u4E13\u5BB6\u9488\u5BF9\u4E13\u4E1A\u95EE\u9898\u64B0\u5199\u6587\u7AE0;</li><li>\u8BE5\u6587\u7AE0\u4E2D\u5E7F\u544A\u83B7\u5F97\u7684\u6536\u76CA\u5411\u4F5C\u8005\u5206\u6210;</li><li>\u5BF9\u6587\u7AE0\u548C\u5E7F\u544A\u5546\u7684\u5E7F\u544A\u8FDB\u884C\u914D\u5BF9.</li></ul><h4 id="\u514D\u8D39\u589E\u503C-\u57FA\u7840\u90E8\u5206\u514D\u8D39-\u589E\u503C\u90E8\u5206\u6536\u8D39" tabindex="-1"><a class="header-anchor" href="#\u514D\u8D39\u589E\u503C-\u57FA\u7840\u90E8\u5206\u514D\u8D39-\u589E\u503C\u90E8\u5206\u6536\u8D39" aria-hidden="true">#</a> \u514D\u8D39\u589E\u503C: \u57FA\u7840\u90E8\u5206\u514D\u8D39, \u589E\u503C\u90E8\u5206\u6536\u8D39</h4><h5 id="flckr" tabindex="-1"><a class="header-anchor" href="#flckr" aria-hidden="true">#</a> Flckr</h5><p>Flckr \u662F\u4E00\u4E2A\u56FE\u7247\u5206\u4EAB\u7F51\u7AD9. \u7528\u6237\u53EF\u4EE5\u5F97\u5230\u4E00\u4E2A\u514D\u8D39\u7684\u57FA\u672C\u8D26\u6237, \u4F46\u5B58\u50A8\u7A7A\u95F4\u548C\u4E0A\u4F20\u56FE\u7247\u6570\u91CF\u6709\u9650.</p><p><img src="'+s+'" alt="flckr"></p><p>\u5982\u679C\u7528\u6237\u652F\u4ED8\u5C11\u91CF\u5E74\u8D39\u53EF\u4EE5\u5F97\u5230\u4E00\u4E2A\u4E13\u4E1A\u8D26\u6237, \u6709\u65E0\u9650\u91CF\u7684\u4E0A\u4F20\u6570\u91CF\u548C\u5B58\u50A8\u7A7A\u95F4, \u4EE5\u53CA\u4E00\u4E9B\u989D\u5916\u529F\u80FD.</p><h5 id="\u5F00\u6E90-redhad" tabindex="-1"><a class="header-anchor" href="#\u5F00\u6E90-redhad" aria-hidden="true">#</a> \u5F00\u6E90: redhad</h5><p>\u4F01\u4E1A\u8F6F\u4EF6\u884C\u4E1A\u6709\u4E24\u4E2A\u7279\u70B9:</p><ul><li>\u7B2C\u4E00, \u4E3A\u4FDD\u6709\u4E00\u6279\u8D1F\u8D23\u8F6F\u4EF6\u5F00\u53D1\u7684\u4EA7\u54C1\u4E13\u5BB6\u800C\u652F\u4ED8\u7684\u9AD8\u6602\u56FA\u5B9A\u6210\u672C</li><li>\u7B2C\u4E8C, \u5176\u6536\u76CA\u6A21\u5F0F\u662F\u9500\u552E\u591A\u79CD\u57FA\u4E8E\u7528\u6237\u7684\u8F6F\u4EF6\u4F7F\u7528\u8BB8\u53EF, \u4EE5\u53CA\u8F6F\u4EF6\u5E38\u89C1\u5347\u7EA7.</li></ul><p>\u800C redhat \u91C7\u7528\u4E86\u5F00\u6E90\u7684\u5546\u4E1A\u6A21\u5F0F:</p><ul><li>\u4F7F\u7528\u5F00\u6E90\u8F6F\u4EF6</li><li>\u63D0\u4F9B\u7EF4\u62A4\u670D\u52A1</li><li>\u5BA2\u6237\u901A\u8FC7\u4ED8\u8D39, \u907F\u514D\u5F00\u6E90\u8F6F\u4EF6 \u201C\u65E0\u6B63\u5F0F\u5F52\u5C5E\u201D \u5E26\u6765\u7684\u4E0D\u786E\u5B9A\u6027, \u540C\u65F6\u53EF\u4EE5\u4EAB\u53D7\u5F00\u6E90\u8F6F\u4EF6\u7684\u6210\u672C\u4F18\u52BF</li></ul><p><img src="'+o+'" alt="redhat"></p><p>redhat \u7684\u4E3B\u8981\u6536\u76CA\u6A21\u5F0F\u4E3A\u4F1A\u5458\u5E74\u8D39.</p><h5 id="skype-\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#skype-\u6A21\u5F0F" aria-hidden="true">#</a> Skype \u6A21\u5F0F</h5><p>Skype \u4EE5\u7F51\u7EDC\u5B9E\u73B0\u514D\u8D39\u901A\u8BDD\u670D\u52A1.</p><p>\u56E0\u4E3A Skype \u4F7F\u7528\u7528\u6237\u786C\u4EF6\u548C\u4E92\u7F51\u4F5C\u4E3A\u57FA\u7840\u8BBE\u65BD, \u56E0\u6B64\u5B83\u4E0D\u9700\u8981\u50CF\u7535\u4FE1\u8FD0\u8425\u5546\u4E00\u6837\u7BA1\u7406\u4E00\u5957\u81EA\u6709\u7684\u7F51\u7EDC\u8BBE\u5907. \u56E0\u6B64\u4ED6\u7684\u6210\u672C\u975E\u5E38\u4F4E\u5EC9.</p><p><img src="'+t+'" alt="skype"></p><p>\u6B63\u5982\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\u5BF9\u4E8E\u62A5\u520A\u884C\u4E1A\u7684\u7834\u574F, Skype \u7684\u6A21\u5F0F\u4E5F\u7834\u574F\u4E86\u4F20\u7EDF\u901A\u4FE1\u884C\u4E1A: \u56E0\u4E3A\u514D\u8D39, \u5B83\u8FC5\u901F\u6389\u4E86\u8FD0\u8425\u5546\u6700\u53EF\u89C2\u7684\u90A3\u90E8\u5206\u6536\u76CA\u6765\u6E90.</p><p><img src="'+c+'" alt="skype"></p><h5 id="\u5012\u8F6C\u7684\u514D\u8D39\u589E\u503C\u6A21\u5F0F-\u4FDD\u9669\u884C\u4E1A" tabindex="-1"><a class="header-anchor" href="#\u5012\u8F6C\u7684\u514D\u8D39\u589E\u503C\u6A21\u5F0F-\u4FDD\u9669\u884C\u4E1A" aria-hidden="true">#</a> \u5012\u8F6C\u7684\u514D\u8D39\u589E\u503C\u6A21\u5F0F: \u4FDD\u9669\u884C\u4E1A</h5><p>REGA \u662F\u4E00\u5BB6\u745E\u58EB\u7684\u975E\u76C8\u5229\u673A\u6784, \u5B83\u4E3B\u8981\u7684\u4E1A\u52A1\u662F\u5728\u4E8B\u6545\u53D1\u65F6, \u4F7F\u7528\u76F4\u5347\u673A\u9001\u533B\u62A4\u4EBA\u5458\u5230\u4E8B\u6545\u5730.</p><p><img src="'+n+'" alt="\u4FDD\u9669\u884C\u4E1A"></p><p>REGA \u6A21\u5F0F\u907F\u514D\u4E86\u5728\u6ED1\u96EA\u5047\u671F, \u590F\u65E5\u5F92\u6B65\u6216\u8005\u5C71\u95F4\u9A7E\u9A76\u8FC7\u7A0B\u4E2D, \u56E0\u53EF\u80FD\u53D1\u751F\u7684\u610F\u5904\u800C\u652F\u4ED8\u9AD8\u6602\u7684\u6551\u63F4\u82B1\u8D39.</p><h3 id="_3-\u9493\u9C7C\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#_3-\u9493\u9C7C\u6A21\u5F0F" aria-hidden="true">#</a> 3 \u9493\u9C7C\u6A21\u5F0F</h3><p>\u9493\u9C7C\u6A21\u5F0F\u662F\u6307, \u671F\u521D\u4EE5\u4E0D\u8D35\u7684\u4EF7\u683C\u6216\u514D\u8D39\u7684\u4EF7\u683C\u63D0\u4F9B\u6709\u5438\u5F15\u529B\u7684\u5546\u54C1, \u4E14\u8BE5\u5546\u54C1\u8FD8\u5C06\u8FDB\u4E00\u6B65\u5730\u9F13\u52B1\u5BF9\u76F8\u5173\u4EA7\u54C1\u6216\u670D\u52A1\u7684\u4E0D\u65AD\u6D88\u8D39.</p><p>\u53D1\u660E\u8FD9\u79CD\u6A21\u5F0F\u7684\u5409\u5217, \u4ED6\u53D1\u660E\u4E86 \u201C\u5243\u5200 &amp; \u5200\u7247\u201D \u6A21\u5F0F: \u9001\u5200\u67B6, \u5356\u5200\u7247.</p><p><img src="'+g+'" alt="\u9493\u9C7C\u6A21\u5F0F"></p><p>\u73B0\u5728\u514D\u8D39\u7ED1\u5B9A\u670D\u52A1\u54A8\u8D39\u7684\u514D\u8D39\u624B\u673A\u662F\u4E00\u79CD\u6807\u51C6\u64CD\u4F5C.</p><p><img src="'+_+'" alt="\u9493\u9C7C\u6A21\u5F0F"></p><h2 id="\u4E8C-\u514D\u8D39\u6A21\u5F0F\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#\u4E8C-\u514D\u8D39\u6A21\u5F0F\u539F\u7406" aria-hidden="true">#</a> \u4E8C \u514D\u8D39\u6A21\u5F0F\u539F\u7406</h2><h3 id="_1-\u514D\u8D39\u5E7F\u544A\u6A21\u5F0F\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#_1-\u514D\u8D39\u5E7F\u544A\u6A21\u5F0F\u539F\u7406" aria-hidden="true">#</a> 1 \u514D\u8D39\u5E7F\u544A\u6A21\u5F0F\u539F\u7406</h3><p>\u514D\u8D39\u5E7F\u544A\u6A21\u5F0F\u672C\u8D28\u4E0A\u662F\u4E00\u4E2A\u591A\u5E73\u65B9\u5E73\u53F0\u6A21\u5F0F:</p><ul><li>\u514D\u8D39\u4EA7\u54C1\u6216\u670D\u52A1\u5E26\u6765\u4E86\u5E73\u53F0\u7684\u9AD8\u6D41\u91CF, \u589E\u52A0\u4E86\u5BF9\u5E7F\u544A\u5546\u7684\u5438\u5F15\u529B</li><li>\u9AD8\u6D41\u91CF\u6765\u4E86\u5E7F\u6536\u76CA, \u5E7F\u544A\u6536\u76CA\u8865\u8D34\u514D\u8D39\u4EA7\u54C1\u53CA\u670D\u52A1</li><li>\u4E3B\u8981\u6210\u672C\u6765\u81EA\u5E73\u53F0\u7684\u5F00\u53D1\u548C\u7EF4\u62A4: \u6D41\u91CF\u589E\u52A0\u53CA\u7EF4\u6301\u7684\u6210\u672C\u4E5F\u53EF\u80FD\u63D0\u9AD8</li></ul><p><img src="'+m+'" alt="\u514D\u8D39\u6A21\u5F0F"></p><h3 id="_2-\u514D\u8D39\u589E\u503C\u6A21\u5F0F\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#_2-\u514D\u8D39\u589E\u503C\u6A21\u5F0F\u539F\u7406" aria-hidden="true">#</a> 2 \u514D\u8D39\u589E\u503C\u6A21\u5F0F\u539F\u7406</h3><p>\u514D\u8D39\u589E\u503C\u6A21\u5F0F\u4E0E\u514D\u8D39\u5E7F\u544A\u6A21\u5F0F\u76F8\u6BD4, \u5BA2\u6237\u7FA4\u4F53\u53EA\u6709\u4E00\u4E2A, \u4F46\u8FD9\u4E2A\u7FA4\u4F53\u4E2D\u901A\u5E38\u53EA\u6709\u4E00\u90E8\u5206, \u901A\u5E38\u4E0D\u5230 10% \u7684\u7528\u6237\u4F1A\u4E3A\u589E\u503C\u670D\u52A1\u4ED8\u8D39. \u5927\u91CF\u7684\u7528\u6237\u4ECE\u514D\u8D39\u7684, \u65E0\u9644\u52A0\u6761\u4EF6\u7684\u670D\u52A1\u4E2D\u83B7\u76CA. \u800C\u5927\u90E8\u5206\u7528\u6237\u4E0D\u4F1A\u53D8\u6210\u4ED8\u8D39\u7528\u6237. \u800C\u514D\u8D39\u7528\u6237\u4E4B\u6240\u4EE5\u53EF\u4EE5\u83B7\u5F97\u514D\u8D39\u7528\u6237, \u662F\u56E0\u4E3A\u4ED8\u8D39\u7528\u6237\u7684\u8425\u6536\u8865\u8D34\u4E86\u514D\u8D39\u7528\u6237.</p><p>\u8FD9\u79CD\u6A21\u5F0F\u4E4B\u6240\u4EE5\u662F\u53EF\u80FD\u7684, \u53EF\u80FD\u7684\u539F\u56E0\u662F: \u670D\u52A1\u7684\u8FB9\u9645\u6210\u672C\u5F88\u4F4E.</p><p>\u5728\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\u4E2D, \u6700\u91CD\u8981\u7684\u8D44\u4EA7\u662F\u5E73\u53F0\u672C\u8EAB. \u56E0\u4E3A, \u4E4B\u6240\u4EE5 \u670D\u52A1\u7684\u8FB9\u9645\u6210\u672C\u5F88\u4F4E, \u662F\u56E0\u4E3A\u5E73\u53F0\u4F5C\u4E3A\u57FA\u7840\u8BBE\u8BBE, \u5177\u6709\u4E00\u6B21\u5EFA\u6210, \u91CD\u590D\u4F7F\u7528 \u7684\u7279\u70B9. \u800C\u53EA\u8981\u4ED8\u8D39\u7528\u6237\u7684\u8425\u6536\u53EF\u4EE5\u8986\u76D6\u5E73\u53F0\u7684\u5EFA\u8BBE\u548C\u7EF4\u62A4\u6210\u672C, \u514D\u8D39\u7528\u6237\u5C31\u53EF\u4EE5 \u642D\u4FBF\u8F66.</p><p>\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\u7684\u5E73\u53F0\u5FC5\u9700\u5177\u5907\u63D0\u4F9B \u81EA\u52A8\u5316 + \u5927\u89C4\u6A21\u5B9A\u5236 \u7684\u83B7\u5BA2\u80FD\u529B. \u56E0\u4E3A\u53EA\u6709\u5177\u5907\u8FD9\u79CD\u4F4E\u6210\u672C\u7684\u83B7\u5BA2\u65B9\u5F0F\u53EF\u4EE5\u4FDD\u8BC1\u5176\u62E5\u6709\u8DB3\u591F\u57FA\u6570\u7684\u7528\u6237.</p><p><img src="'+u+'" alt="\u514D\u8D39\u589E\u503C\u6A21\u5F0F\u539F\u7406"></p><p>\u514D\u8D39\u589E\u503C\u6A21\u5F0F\u7684\u8425\u5229\u516C\u5F0F\u5982\u4E0B:</p><ul><li>\u6536\u5165 = {\u7528\u6237\u6570\u91CF * \u589E\u503C\u7528\u6237\u767E\u5206\u6BD4 * \u589E\u503C\u670D\u52A1\u7684\u4EF7\u683C} * \u589E\u957F\u7387 * \u987E\u5BA2\u6D41\u5931\u7387</li><li>\u670D\u52A1\u6210\u672C = {\u7528\u6237\u6570\u91CF * \u589E\u503C\u7528\u6237\u767E\u5206\u6BD4 * \u514D\u8D39\u7528\u6237\u670D\u52A1\u6210\u672C} + [\u7528\u6237\u6570\u91CF * \u589E\u503C\u7528\u6237\u767E\u5206\u6BD4 * \u589E\u503C\u7528\u6237\u670D\u52A1\u6210\u672C]</li><li>\u8FD0\u8425\u5229\u6DA6 = \u6536\u5165 - \u670D\u52A1\u6210\u672C - \u56FA\u5B9A\u6210\u672C - \u5BA2\u6237\u83B7\u53D6\u6210\u672C</li></ul><h3 id="_3-\u9493\u9C7C\u6A21\u5F0F\u7684\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#_3-\u9493\u9C7C\u6A21\u5F0F\u7684\u539F\u7406" aria-hidden="true">#</a> 3 \u9493\u9C7C\u6A21\u5F0F\u7684\u539F\u7406</h3><p>\u9493\u9C7C\u6A21\u5F0F\u4E0E\u514D\u8D39\u589E\u503C\u6A21\u5F0F\u76F8\u6BD4, \u4EAB\u53D7\u514D\u8D39\u670D\u52A1\u7684\u7528\u6237\u548C\u4ED8\u8D39\u7528\u6237\u662F\u540C\u4E00\u4E2A\u7528\u6237, \u53EA\u662F\u4ED6\u4EEC\u4EAB\u53D7\u4E86\u4E0D\u540C\u7684\u670D\u52A1. \u5BF9\u4E8E\u4E00\u4E2A\u9493\u9C7C\u6A21\u5F0F, \u6700\u91CD\u8981\u6709\u4E24\u70B9:</p><ul><li>\u63D0\u4F9B\u7684\u514D\u8D39\u4F4E\u4EF7\u6216\u514D\u8D39\u7684\u521D\u59CB\u5546\u54C1\u662F\u5426\u7D27\u5BC6\u8FDE\u63A5\u540E\u7EED\u6D88\u8D39\u54C1</li><li>\u63A7\u5236\u9501\u5B9A\u5173\u7CFB</li></ul><p><img src="'+b+'" alt="\u9493\u9C7C\u6A21\u5F0F\u539F\u7406"></p><p>\u5728\u7535\u4FE1\u8FD0\u8425\u5546\u7684\u4F8B\u5B50\u91CC, \u670D\u52A1\u8D44\u8D39\u662F\u514D\u8D39\u7535\u8BDD\u540E\u7EED\u6D88\u8D39\u54C1, \u63A7\u5236\u9500\u5B9A\u5173\u7CFB\u7684\u65B9\u5F0F\u5408\u540C; \u5728\u5409\u5217\u7684\u4F8B\u5B50\u4E2D, \u5200\u7247\u662F\u5200\u67B6\u7684\u540E\u7EED\u6D88\u8D39\u54C1, \u63A7\u5236\u9501\u5B9A\u5173\u7CFB\u7684\u65B9\u5F0F\u662F\u4E13\u5229\u963B\u65AD.</p><h3 id="_4-\u6210\u529F\u514D\u8D39\u6A21\u5F0F\u7684\u5171\u6027" tabindex="-1"><a class="header-anchor" href="#_4-\u6210\u529F\u514D\u8D39\u6A21\u5F0F\u7684\u5171\u6027" aria-hidden="true">#</a> 4 \u6210\u529F\u514D\u8D39\u6A21\u5F0F\u7684\u5171\u6027</h3><p>\u4ECE\u4E0A\u9762\u4E09\u79CD\u514D\u8D39\u6A21\u5F0F, \u6211\u4EEC\u770B\u5230\u4E00\u4E2A\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F, \u5FC5\u987B\u8981\u5177\u6709\u8FD9\u6837\u51E0\u4E2A\u8981\u7D20:</p><ul><li>\u514D\u8D39\u6A21\u5F0F\u4E4B\u6240\u4EE5\u53EF\u80FD\u662F\u56E0\u4E3A\u67D0\u79CD\u6210\u672C\u7684\u964D\u4F4E <ul><li>\u5728\u514D\u8D39\u5E7F\u544A\u6A21\u5F0F\u7684\u6848\u4F8B\u91CC, \u662F\u901A\u627E\u5230\u4E00\u4E2A\u5BF9\u4E8E\u8D28\u91CF\u8981\u6C42\u4E0D\u9AD8\u7684\u7FA4\u4F53\u964D\u4F4E\u4E86\u7F16\u8F91\u8D28\u91CF;</li><li>\u5728\u514D\u8D39\u589E\u503C\u6A21\u5F0F\u7684\u6848\u4F8B\u91CC, \u662F\u901A\u8FC7\u5EFA\u8BBE\u4E00\u4E2A\u81EA\u52A8\u5316\u5E73\u53F0, \u964D\u4F4E\u4E86\u670D\u52A1\u7684\u8FB9\u9645\u6210\u672C;</li><li>\u5728\u9493\u9C7C\u6A21\u5F0F\u91CC, \u867D\u7136\u6CA1\u6709\u964D\u4F4E\u6210\u672C, \u4F46\u5B83\u63D0\u4F9B\u4E86 \u6210\u672C\u6709\u9650\u7684 \u7684\u514D\u8D39\u670D\u52A1, \u800C\u540E\u7EED\u8DDF\u7740\u4E00\u4E2A \u5229\u6DA6\u65E0\u9650\u7684 \u540E\u7EED\u670D\u52A1.</li></ul></li><li>\u514D\u8D39\u6A21\u5F0F\u4E2D\u4E00\u5B9A\u5B58\u5728\u591A\u4E2A\u4E1A\u52A1\u95ED\u73AF <ul><li>\u5728\u514D\u8D39\u5E7F\u544A\u6A21\u5F0F\u4E2D, \u6709\u514D\u8D39\u62A5\u7EB8\u4E1A\u52A1\u548C\u5E7F\u544A\u4E1A\u52A1;</li><li>\u5728\u514D\u8D39\u589E\u503C\u6A21\u5F0F\u4E2D, \u6709\u57FA\u7840\u514D\u8D39\u4E1A\u52A1\u548C\u589E\u503C\u4E1A\u52A1;</li><li>\u5728\u9493\u9C7C\u6A21\u5F0F\u4E2D, \u6709\u8BF1\u94D2\u4E1A\u52A1\u548C\u9677\u9631\u4E1A\u52A1;</li></ul></li><li>\u514D\u8D39\u6A21\u5F0F\u4E2D\u591A\u4E2A\u4E1A\u52A1\u4E4B\u95F4\u5B58\u5728\u5F3A\u5173\u8054: <ul><li>\u5728\u514D\u8D39\u5E7F\u544A\u6A21\u5F0F\u4E2D, \u514D\u8D39\u62A5\u7EB8\u4E1A\u52A1\u4EA7\u751F\u7684\u6D41\u91CF\u662F\u5176\u5E7F\u544A\u4E1A\u52A1\u7684\u57FA\u7840;</li><li>\u5728\u514D\u8D39\u589E\u503C\u6A21\u5F0F\u4E2D, \u57FA\u7840\u514D\u8D39\u4E1A\u52A1\u548C\u589E\u503C\u4E1A\u52A1\u5FC5\u987B\u662F\u540C\u4E00\u4E2A\u8FB9\u9645\u6548\u76CA\u9012\u51CF\u7684\u5E73\u53F0\u4E0A;</li><li>\u5728\u9493\u9C7C\u6A21\u5F0F\u4E2D, \u8BF1\u94D2\u4E1A\u52A1\u548C\u9677\u9631\u4E1A\u52A1\u4E4B\u95F4\u5FC5\u987B\u5B58\u5728\u9501\u5B9A\u5173\u7CFB.</li></ul></li></ul><h3 id="_5-\u514D\u8D39\u6A21\u5F0F\u7684\u7834\u574F\u6027" tabindex="-1"><a class="header-anchor" href="#_5-\u514D\u8D39\u6A21\u5F0F\u7684\u7834\u574F\u6027" aria-hidden="true">#</a> 5 \u514D\u8D39\u6A21\u5F0F\u7684\u7834\u574F\u6027</h3><p>\u6709\u65F6\u514D\u8D39\u6A21\u5F0F\u5177\u6709\u4E00\u5B9A\u7684\u7834\u574F\u6027. \u6BD4\u5982</p><ul><li>\u514D\u8D39\u62A5\u520A\u7684\u6848\u4F8B\u4E2D, \u5BF9\u4F20\u7EDF\u62A5\u520A\u884C\u4E1A\u548C\u51B2\u51FB;</li><li>Skype \u6848\u4F8B\u4E2D, \u5BF9\u4F20\u7EDF\u901A\u4FE1\u884C\u4E1A\u7684\u51B2\u51FB;</li></ul><p>\u8FD9\u4E9B\u6848\u4F8B\u4E4B\u6240\u4EE5\u4EA7\u751F\u51B2\u51FB\u4E3B\u8981\u662F\u56E0\u4E3A, \u4ED6\u4EEC\u7684\u514D\u8D39\u6A21\u5F0F\u5E76\u6CA1\u6709\u4EA7\u751F\u65B0\u7684\u4EF7\u503C, \u800C\u53EA\u662F\u901A\u8FC7\u67D0\u79CD\u624B\u6BB5\u5C06\u81EA\u8EAB\u7684\u6210\u672C\u964D\u4F4E, \u800C\u4F7F\u5176\u53EF\u4EE5\u5FEB\u901F\u8FDB\u5165\u5E02\u573A.</p><p>google \u540C\u6837\u91C7\u7528\u4E86 \u514D\u8D39\u670D\u52A1 + \u5E7F\u544A\u6536\u76CA \u7684\u6A21\u5F0F, \u4F46 google \u5E76\u6CA1\u6709\u5BF9\u4EC0\u4E48\u884C\u4E1A\u9020\u6210\u7834\u574F\u6027\u7684\u51B2\u51FB. \u539F\u56E0\u5728\u4E8E, \u4ED6\u4EEC\u7684\u521B\u65B0\u4E86\u65B0\u7684\u76CA\u6536\u6A21\u5F0F: \u5173\u952E\u8BCD\u7ADE\u62CD, \u800C\u975E\u7B80\u5355\u7684\u552E\u9500\u5E7F\u544A\u4F4D.</p><p>\u800C\u6709\u7684\u65F6\u5019, \u8FD9\u79CD\u51B2\u51FB\u5BF9\u4E8E\u6574\u4E2A\u5E02\u573A\u6765\u8BF4\u5E76\u4E0D\u662F\u4EC0\u4E48\u597D\u4E8B.</p><p>\u6BD4\u5982\u5728\u514D\u8D39\u62A5\u520A\u7684\u6848\u4F8B\u4E2D, \u4F20\u7EDF\u62A5\u520A\u884C\u4E1A\u56E0\u4E3A\u575A\u6301\u5176\u5185\u5BB9\u8D28\u91CF\u800C\u65E0\u6CD5\u964D\u4F4E\u5176\u7F16\u8F91\u6210\u672C, \u81F3\u4F7F\u5176\u5E02\u573A\u7F29\u5C0F. \u957F\u6B64\u4EE5\u5F80\u53EF\u80FD\u4EA7\u751F\u7684\u7ED3\u679C\u662F, \u4E0D\u518D\u6709\u4EBA\u63D0\u4F9B\u9AD8\u8D28\u91CF\u7684\u5185\u5BB9.</p><h2 id="\u4E09-o2o-\u96F7\u533A\u6307\u5357" tabindex="-1"><a class="header-anchor" href="#\u4E09-o2o-\u96F7\u533A\u6307\u5357" aria-hidden="true">#</a> \u4E09 O2O \u96F7\u533A\u6307\u5357</h2><h3 id="_1-\u53F7\u79F0\u514D\u8D39\u6A21\u5F0F\u7684-o2o" tabindex="-1"><a class="header-anchor" href="#_1-\u53F7\u79F0\u514D\u8D39\u6A21\u5F0F\u7684-o2o" aria-hidden="true">#</a> 1 \u53F7\u79F0\u514D\u8D39\u6A21\u5F0F\u7684 O2O</h3><p>\u6211\u505A O2O \u884C\u4E1A\u8C03\u7814\u662F\u5728 2016 \u5E74\u4E0B\u534A\u5E74. \u5F53\u65F6 O2O \u884C\u4E1A\u5DF2\u7136\u88AB\u79F0\u4E3A \u201C\u8870\u9E3F\u904D\u91CE\u201C \u4E86.</p><p>\u5F53\u65F6\u6709\u4E00\u4E2A\u5F62\u5BB9 O2O \u9879\u76EE\u5931\u8D25\u7684\u8BCD\u53EB\u505A C \u8F6E\u6B7B. \u5B83\u6307\u8FD9\u6837\u4E00\u79CD\u60C5\u51B5:</p><ul><li>\u9879\u76EE\u5728 A \u8F6E\u548C B \u8F6E\u7684\u65F6\u5019\u90FD\u5BA3\u79F0\u81EA\u5DF1\u524D\u671F\u9700\u8981 \u8865\u8D34\u7528\u6237, \u5728\u83B7\u5F97\u8DB3\u591F\u6D41\u91CF\u540E\u624D\u80FD\u5B9E\u73B0\u76C8\u5229.</li><li>\u800C\u76F4\u5230\u9700\u8981\u8FDB\u884C C \u8F6E\u7684\u65F6\u5019, \u975E\u4F46\u65E0\u6CD5\u76C8\u5229, \u751A\u81F3\u5982\u4F55\u8FDB\u884C\u76C8\u5229\u90FD\u8BF4\u4E0D\u6E05\u695A.</li><li>\u7EC8\u4E8E\u6D88\u78E8\u4E86\u6295\u8D44\u4EBA\u7684\u4FE1\u5FC3, \u5BFC\u81F4\u8D44\u91D1\u65AD\u88C2, \u5F15\u8D77\u9879\u76EE\u5931\u8D25.</li></ul><p><img src="'+a+'" alt="\u94FE"></p><p>\u201C\u901A\u8FC7\u524D\u671F\u8865\u8D34\u7528\u6237, \u83B7\u5F97\u6D41\u91CF\u4E4B\u540E\u624D\u80FD\u5B9E\u73B0\u76C8\u5229\u201D \u4F3C\u4E4E\u5728\u8868\u660E, \u8FD9\u662F\u4E00\u4E2A\u514D\u8D39\u7684\u5546\u4E1A\u6A21\u5F0F.</p><p>\u4F46\u5982\u679C\u6211\u4EEC\u7A0D\u52A0\u5206\u6790, \u6211\u4EEC\u5C31\u4F1A\u53D1\u73B0, \u591A\u6570 O2O \u662F\u4E0D\u5177\u5907\u5B9E\u73B0\u4E00\u4E2A\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\u7684\u6761\u4EF6\u7684.</p><h3 id="_2-o2o-\u7684\u7279\u70B9\u4F7F\u5176\u65E0\u6CD5\u5B9E\u73B0\u4E00\u4E2A\u6210\u529F\u80FD\u514D\u8D39\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#_2-o2o-\u7684\u7279\u70B9\u4F7F\u5176\u65E0\u6CD5\u5B9E\u73B0\u4E00\u4E2A\u6210\u529F\u80FD\u514D\u8D39\u6A21\u5F0F" aria-hidden="true">#</a> 2 O2O \u7684\u7279\u70B9\u4F7F\u5176\u65E0\u6CD5\u5B9E\u73B0\u4E00\u4E2A\u6210\u529F\u80FD\u514D\u8D39\u6A21\u5F0F</h3><p>O2O \u6A21\u5F0F\u4E0E\u4E4B\u524D\u7EAF\u7EBF\u4E0A\u7684\u4E92\u8054\u7F51\u9879\u76EE\u76F8\u6BD4, \u6709\u4E00\u4E2A\u975E\u5E38\u5927\u7684\u4E0D\u540C, \u5C31\u662F\u5B83\u5FC5\u987B\u638C\u63A7\u7EBF\u4E0B\u7684\u4E1A\u52A1.</p><p>\u6BD4\u5982\u5916\u5356\u884C\u4E1A, \u7ADE\u4E89\u5230\u6700\u540E, \u7ADE\u4E89\u7684\u5176\u5B9E\u662F\u914D\u9001\u901F\u5EA6: \u5927\u5BB6\u5BF9\u63A5\u7684\u5546\u5BB6\u662F\u4E00\u6837\u7684, \u8865\u8D34\u7684\u529B\u5EA6\u4E5F\u662F\u4E00\u6837\u7684, \u56E0\u6B64\u5230\u6700\u540E, \u5927\u5BB6\u80FD\u6BD4\u62FC\u7684, \u53EA\u6709\u914D\u9001\u7684\u901F\u5EA6.</p><p><img src="'+i+'" alt="\u7279\u70B9"></p><p>\u800C\u914D\u9001\u901F\u5EA6\u7684\u63D0\u5347, \u53D6\u51B3\u4E8E\u5BF9\u4E8E\u7EBF\u4E0B\u914D\u9001\u4EBA\u5458, \u8BBE\u5907\u7684\u6295\u5165.</p><p>\u8FD9\u5BFC\u81F4 O2O \u884C\u4E1A\u7684\u8FB9\u9645\u6210\u672C\u662F\u9012\u589E\u7684. \u4F60\u7684\u4E1A\u52A1\u505A\u5230\u54EA\u91CC, \u4F60\u7684\u6210\u672C\u5C31\u8981\u6295\u5230\u54EA\u91CC.</p><p>\u8FD9\u4E2A\u7279\u70B9\u7684\u5BFC\u81F4, O2O \u9879\u76EE\u5FC5\u9700\u5728\u4E00\u4E2A \u7EC6\u5206\u9886\u57DF\u8FDB\u884C\u6DF1\u8015. \u56E0\u4E3A\u5176\u6210\u672C\u7ED3\u6784\u4E0D\u652F\u6301\u5B83\u4EEC\u8FDB\u884C\u591A\u7EBF\u4F5C\u6218.</p><p><img src="'+a+'" alt="\u6DF1\u8015"></p><p>\u8FD9\u4E9B\u7279\u5F81\u4F7F O2O \u9879\u76EE\u6839\u672C\u65E0\u6CD5\u5B9E\u73B0\u4E00\u4E2A\u6210\u529F\u7684\u514D\u8D39\u6A21\u5F0F:</p><ul><li>\u514D\u8D39\u6A21\u5F0F\u8981\u6C42\u901A\u8FC7\u67D0\u79CD\u624B\u6BB5 (\u901A\u5E38\u662F\u4FE1\u606F\u6280\u672F\u7684\u5E94\u7528) \u5207\u5B9E\u7684\u964D\u4F4E\u6210\u672C, \u800C O2O \u7684\u6210\u672C\u7ED3\u6784\u662F \u8FB9\u9645\u6210\u672C\u9012\u589E\u7684.</li><li>\u514D\u8D39\u6A21\u5F0F\u8981\u6C42\u5177\u6709\u5F3A\u8054\u7CFB\u7684\u591A\u6761\u4E1A\u52A1\u7EBF, \u5E76\u5728\u591A\u6761\u4E1A\u52A1\u7EBF\u4E2D\u5F62\u6210 \u76C8\u5229 - \u8865\u8D34 \u7684\u5173\u7CFB, \u800C O2O \u9879\u76EE\u7684\u6210\u672C\u7ED3\u6784\u51B3\u5B9A\u4E86\u5B83\u53EA\u80FD\u5728\u4E00\u4E2A \u7EC6\u5206\u9886\u57DF\u8FDB\u884C\u6DF1\u8015.</li></ul><h3 id="_3-\u901A\u8FC7\u88AB\u8865\u8D34\u5F3A\u884C\u8FDB\u884C\u514D\u8D39\u6A21\u5F0F\u7684\u8D1F\u4F5C\u7528" tabindex="-1"><a class="header-anchor" href="#_3-\u901A\u8FC7\u88AB\u8865\u8D34\u5F3A\u884C\u8FDB\u884C\u514D\u8D39\u6A21\u5F0F\u7684\u8D1F\u4F5C\u7528" aria-hidden="true">#</a> 3 \u901A\u8FC7\u88AB\u8865\u8D34\u5F3A\u884C\u8FDB\u884C\u514D\u8D39\u6A21\u5F0F\u7684\u8D1F\u4F5C\u7528</h3><p>\u6211\u4EEC\u77E5\u9053, \u6CA1\u6709\u76C8\u6A21\u5F0F\u521B\u65B0\u7684\u514D\u8D39\u6A21\u5F0F, \u662F\u4F1A\u5BF9\u5E02\u573A\u8D77\u5230\u7834\u574F\u4F5C\u7528\u7684.</p><p>\u6BD4\u8F83\u5178\u578B\u7684\u662F\u5A5A\u5E86 O2O.</p><p>\u5A5A\u5E86\u884C\u4E1A\u5C5E\u4E8E\u5229\u6DA6\u5F88\u9AD8, \u4F46\u4F7F\u7528\u7387\u5374\u884C\u4F4E\u7684\u884C\u4E1A. \u5F53\u5A5A\u5E86 O2O \u4F01\u4E1A\u8FDB\u884C\u8865\u8D34\u65F6, \u7528\u6237\u53EA\u4F1A\u56E0\u4E3A\u8865\u8D34\u800C\u63D0\u524D\u4E3E\u884C\u5A5A\u793C, \u800C\u4E0D\u53EF\u80FD\u56E0\u4E3A\u6709\u8865\u8D34\u7ED3\u4E24\u6B21\u5A5A.</p><p><img src="'+i+'" alt="\u9700\u6C42"></p><p>\u4E5F\u5C31\u662F\u8BF4, \u8865\u8D34\u53EA\u80FD\u900F\u652F\u7528\u6237\u672A\u6765\u7684\u9700\u6C42, \u800C\u4E0D\u4F1A\u56E0\u6B64\u521B\u9020\u51FA\u65B0\u7684\u9700\u6C42.</p><p>\u7528\u6237\u7684\u9700\u6C42\u4E00\u65E6\u900F\u652F\u6389, \u5176\u7C98\u6027\u4E5F\u4F1A\u6D88\u5931.</p>',100),j=[f];function x(y,v){return r(),p("div",null,j)}var S=e(O,[["render",x],["__file","\u514D\u8D39\u5546\u4E1A\u6A21\u5F0F\uFF1AO2O\u96F7\u533A\u6307\u5357.html.vue"]]);export{S as default};
