import{_ as t,o,c as u,e as q}from"./app.6bf529a6.js";const p={},e=q('<h1 id="\u5170\u5DDE\u5730\u540D\u8003" tabindex="-1"><a class="header-anchor" href="#\u5170\u5DDE\u5730\u540D\u8003" aria-hidden="true">#</a> \u5170\u5DDE\u5730\u540D\u8003</h1><h2 id="\u57CE-\u4E0E-\u5173" tabindex="-1"><a class="header-anchor" href="#\u57CE-\u4E0E-\u5173" aria-hidden="true">#</a> &quot;\u57CE&quot; \u4E0E &quot;\u5173&quot;</h2><p>&quot;\u7518\u8083&quot; \u662F\u7518\u5DDE\u4E0E\u8083\u5DDE\u7684\u5408\u79F0. &quot;\u8083\u5DDE&quot; \u662F\u9152\u6CC9. \u6C49\u6B66\u5E1D\u65F6\u970D\u53BB\u75C5\u7ED9\u5308\u5974 &quot;\u53BB\u75C5&quot; \u7684\u65F6\u5019 ( \u897F\u5317\u8BDD\u91CC, &quot;\u53BB\u75C5&quot; \u4E00\u8BCD\u6709\u6536\u62FE\u574F\u6BDB\u75C5\u7684\u610F\u601D, \u6BD4\u5982\u5927\u5B9D\u5982\u679C\u8001\u662F\u8BA1\u7B97\u51FA\u9519, \u6211\u5C31\u53EF\u4EE5\u5BF9\u5979\u8BF4: &quot;\u4F60\u662F\u4E0D\u662F\u60F3\u8BA9\u6211\u7ED9\u4F60\u53BB\u53BB\u75C5.&quot; ), \u5728\u6B64\u5730\u5927\u80DC. \u4E8E\u662F\u6B66\u5E1D\u8D50\u9152\u4EE5\u8D4F. \u970D\u53BB\u75C5\u4EE5\u529F\u5728\u5168\u519B, \u4EBA\u591A\u9152\u5C11, \u9042\u503E\u9152\u4E8E\u6CC9\u4E2D, \u4E0E\u5C06\u58EB\u5171\u996E, \u81EA\u6B64\u4FBF\u6709\u4E86 &quot;\u9152\u6CC9&quot; \u4E4B\u540D. \u970D\u53BB\u75C5\u63A5\u7740\u5F80\u897F\u6253, \u5728\u4E00\u5904\u53EB &quot;\u6D41\u6C99&quot; \u7684\u5730\u65B9\u5927\u7834\u5308\u5974, \u6B66\u5E1D\u5728\u6B64\u7F6E\u90E1, \u53D6\u540D &quot;\u5F20\u6396&quot;, \u662F &quot;\u5F20\u56FD\u4E4B\u6396, \u4EE5\u901A\u897F\u57DF&quot; \u7684\u610F\u601D. \u5927\u6982\u662F\u8BF4, \u5728\u6B64\u4E4B\u524D, \u6C49\u671D\u53EA\u80FD\u5939\u7740\u80F3\u818A\u505A\u4EBA, \u754F\u624B\u754F\u811A; \u6B64\u540E\u7EC8\u4E8E\u80FD\u628A\u624B\u81C2\u4F38\u5C55, \u5F20\u5F00\u6396\u7A9D\u4E86.</p><p>\u7518\u8083\u8FD9\u4E2A\u5730\u65B9, \u81EA\u53E4\u5C31\u4E0E\u6253\u4ED7\u5C11\u4E0D\u4E86\u5E72\u7CFB. \u5170\u5DDE\u505A\u4E3A\u7518\u8083\u7684\u7701\u4F1A, \u867D\u5728\u7518\u5DDE, \u8083\u5DDE\u4EE5\u4E1C, \u4F46\u4E5F\u662F\u519B\u4E8B\u91CD\u9547. \u5170\u5DDE\u65E9\u5148\u53EB &quot;\u91D1\u57CE\u90E1&quot;. \u4E3A\u4F55\u5F97\u540D &quot;\u91D1\u57CE&quot;, \u6211\u542C\u8FC7\u4E24\u79CD\u8BF4\u6CD5, \u4E00\u79CD\u662F\u8BF4 &quot;\u7B51\u57CE\u5F97\u91D1, \u6545\u66F0\u91D1\u57CE&quot;, \u4E00\u8BF4\u662F &quot;\u79F0\u91D1, \u53D6\u5176\u56FA\u575A\u56FA\u4E5F&quot;. \u603B\u4E4B, \u6700\u521D\u662F\u4E00\u4E2A\u519B\u4E8B\u8981\u585E, \u56FA\u82E5\u91D1\u6C64. \u636E\u8BF4\u5728\u738B\u83BD\u65F6\u671F, \u9752\u6D77\u6E56\u9644\u8FD1\u7684\u897F\u6D77\u90E1\u88AB\u7F8C\u4EBA\u653B\u5360, \u738B\u83BD\u4E3A\u4E86\u4E0E\u4E1C\u6D77\u90E1, \u5317\u6D77\u90E1, \u5357\u6D77\u90E1\u51D1\u8DB3 &quot;\u56DB\u6D77\u4E4B\u6570&quot;, \u91D1\u57CE\u90E1\u4E5F\u88AB\u6539\u6210\u8FC7\u897F\u6D77\u90E1. \u540E\u6765\u968F\u7740\u4E1C\u6C49\u548C\u7F8C\u4EBA\u7684\u4F5C\u6218, \u91D1\u57CE\u90E1\u88AB\u4E89\u6765\u593A\u53BB. \u76F4\u5230\u9B4F\u664B\u65F6\u671F, \u624D\u5927\u6982\u7B97\u662F\u6D88\u505C. \u76F4\u5230\u968B\u671D\u91D1\u57CE\u90E1\u624D\u6539\u4E3A &quot;\u5170\u5DDE\u603B\u7BA1\u5E9C&quot;.</p><p>&quot;\u5148\u6709\u91D1\u57CE\u5173, \u540E\u6709\u5170\u5DDE\u57CE.&quot; \u91D1\u57CE\u5173\u636E\u8BF4\u5C31\u662F\u91D1\u57CE\u90E1\u65F6\u671F\u7559\u4E0B\u7684\u5730\u540D. \u5F53\u5E74\u91D1\u57CE\u5173\u5728\u9EC4\u6CB3\u4EE5\u5317\u4F9D\u5C71\u800C\u5EFA, \u5C71\u6CB3\u4E4B\u95F4\u53EA\u6709\u4E00\u6761\u5927\u8DEF. \u53EA\u8981\u56FA\u5B88\u6B64\u5904, \u771F\u662F\u4E00\u592B\u5F53\u5173\u4E07\u592B\u83AB\u5F00. \u5F53\u7136, \u73B0\u5728\u7684\u91D1\u57CE\u5173\u65E9\u5DF2\u6CA1\u4E86\u5173\u9698\u57CE\u5899, \u53EA\u6709\u73B0\u4EE3\u4FEE\u7684\u4EFF\u53E4\u5EFA\u7B51, \u4F5C\u4E3A\u6587\u4EF6\u98CE\u60C5\u533A. \u73B0\u5728\u91D1\u57CE\u5173\u4E00\u5E26\u6709\u5F88\u591A\u56DE\u65CF\u5144\u5F1F\u5C45\u4F4F, \u6CBF\u6CB3\u6709\u4E00\u5EA7\u6C34\u4E0A\u6E05\u771F\u5BFA, \u5E95\u90E8\u9020\u5F62\u5982\u8239, \u4E0A\u6709\u963F\u62C9\u4F2F\u62F1\u9876\u6E05\u771F\u5BFA.</p><p>\u91D1\u57CE\u5173\u5F80\u4E1C\u4E0D\u5230\u4E00\u91CC, \u5C31\u662F\u53F7\u79F0 &quot;\u9EC4\u6CB3\u7B2C\u4E00\u6865&quot; \u7684 &quot;\u4E2D\u5C71\u6865&quot;. \u4E2D\u5C71\u6865\u7684\u524D\u8EAB\u662F &quot;\u9547\u8FDC\u6D6E\u6865&quot;, \u5EFA\u4E8E\u660E\u671D\u6D2A\u6B66\u5E74\u95F4. \u5149\u7EEA\u4E09\u5341\u4E00\u5E74, \u7518\u8083\u6D0B\u52A1\u603B\u5C40\u4E0E\u5FB7\u5546\u7B7E\u5B9A\u5408\u540C, \u5728\u6B64\u5EFA\u4E86\u94C1\u6865. \u6C11\u56FD\u65F6, \u4E3A\u4E86\u7EAA\u5FF5\u5B59\u4E2D\u5C71, \u6539\u540D\u4E3A &quot;\u4E2D\u5C71\u6865&quot;. \u4E2D\u5C71\u6865\u901A\u4F53\u94C5\u7070\u8272, \u662F\u6297\u65E5\u6218\u4E89\u65F6\u4E3A\u4E86\u8EB2\u907F\u65E5\u519B\u8F70\u70B8\u6D82\u7684. \u5728\u6B64\u5904\u5EFA\u6865, \u662F\u56E0\u4E3A\u6B64\u5904\u6CB3\u8C37\u5BBD\u9614 &quot;\u6CB3\u6C34\u5C11\u7F13&quot;. \u6211\u5C0F\u7684\u65F6\u5019\u5E38\u5E38\u8DB4\u5728\u4E2D\u5C71\u6865\u7684\u680F\u6746\u4E0A\u4F4E\u5934\u770B\u6CB3\u6C34, \u76EF\u5F97\u4E45\u4E86, \u5C31\u6709\u6865\u5728\u52A8\u7684\u9519\u89C9.</p><p>\u8FC7\u4E86\u6865\u662F\u8001\u5170\u5DDE\u57CE\u7684\u6865\u95E8. \u8FDB\u4E86\u6865\u95E8\u5C31\u8FDB\u4E86\u8001\u5170\u5DDE\u57CE. \u4F46\u73B0\u65E9\u6CA1\u4E86\u57CE\u5899, \u4E5F\u6CA1\u4E86\u4EC0\u4E48\u6865\u95E8. \u751A\u81F3\u8FDE\u6865\u95E8\u8FD9\u4E2A\u5730\u540D\u90FD\u90FD\u6CA1\u7559\u4E0B. \u53D6\u800C\u4EE3\u4E4B\u7684\u662F\u4E2D\u5C71\u8DEF, \u5E94\u8BE5\u662F\u7531\u4E2D\u5C71\u6865\u800C\u5F97\u540D.</p><p>\u6CBF\u4E2D\u5C71\u8DEF\u5F80\u5357\u662F\u897F\u5173\u4EC0\u5B57. \u897F\u5173\u5341\u5B57\u5E94\u8BE5\u662F\u8001\u5170\u5DDE\u57CE\u7684\u897F\u95E8, \u51FA\u4E86\u897F\u5173\u5341\u5B57\u518D\u5F80\u897F\u662F\u4E03\u91CC\u6CB3\u533A, \u5F80\u4E1C\u662F\u57CE\u5173\u533A. \u6211\u8FD8\u4F4F\u5728\u5170\u5DDE\u65F6\u897F\u5173\u5341\u5B57\u662F\u4E2A\u516C\u4EA4\u67A2\u7EBD, \u73B0\u5728\u53EF\u80FD\u6539\u6210\u5730\u94C1\u7AD9\u4E86\u5427. 2000 \u5E74\u524D, \u897F\u5173\u5341\u5B57\u516C\u4EA4\u67A2\u7EBD\u767D\u5929\u505C\u516C\u4EA4\u8F66, \u665A\u4E0A\u516C\u4EA4\u8F66\u90FD\u52A0\u5382\u4E86, \u5C31\u4F1A\u6446\u51FA\u591C\u5E02\u6765, \u5168\u662F\u5404\u79CD\u5C0F\u5403. \u90A3\u65F6\u6211\u8FD8\u5728\u4E0A\u521D\u4E2D, \u665A\u4E0A\u5076\u5C14\u53BB\u5403. \u8BB0\u5F97\u6709\u4E00\u5BB6\u7C73\u7EBF\u975E\u5E38\u597D\u5403, \u7279\u522B\u662F\u91CC\u8FB9\u7684\u9178\u83DC\u683C\u5916\u7684\u9999. \u540E\u6765\u591C\u5E02\u88AB\u64A4\u9500\u4E86, \u597D\u50CF\u662F\u56E0\u4E3A\u51FA\u4E86\u6CBB\u5B89\u4E8B\u4EF6.</p><p>\u897F\u5173\u5341\u5B57\u516C\u4EA4\u67A2\u94AE\u8FB9\u4E0A\u5C31\u662F\u897F\u5173\u6E05\u771F\u5927\u5BFA, \u5170\u5DDE\u6700\u5927\u7684\u6E05\u771F\u5BFA. \u897F\u5173\u6E05\u771F\u5927\u5BFA\u518D\u5F80\u897F, \u53EB\u505A\u8403\u82F1\u95E8, \u4EE5\u524D\u662F\u4E3E\u9662, \u8003\u4E3E\u4EBA\u7684\u5730\u65B9. \u4EE5\u524D\u7518\u8083\u4EBA\u8981\u8003\u4E3E\u4EBA, \u8981\u5230\u9655\u897F\u8003. \u5DE6\u5B97\u68E0\u4EFB\u9655\u7518\u603B\u7763\u65F6, \u770B\u5230\u7518\u8083\u4EBA\u53BB\u9655\u897F\u8003\u8BD5\u8D39\u65F6\u8D39\u529B, \u5C31\u5411\u671D\u5EAD\u594F\u8BF7 &quot;\u9655\u7518\u5206\u95F1&quot;. \u51C6\u594F\u540E\u5C31\u5728\u6B64\u5EFA\u4E86\u4E3E\u9662. \u636E\u8BF4\u8001\u5170\u5927\u4E5F\u5728\u6B64\u5904. \u8403\u82F1\u95E8\u5C31\u662F\u53D6 &quot;\u835F\u8403\u7CBE\u82F1&quot; \u7684\u610F\u601D.</p><p>\u8403\u82F1\u95E8\u65C1\u8FB9\u662F\u897F\u7A0D\u95E8, \u89E3\u653E\u5170\u5DDE\u7684\u65F6\u5019, \u89E3\u653E\u519B\u4ECE\u8FD9\u91CC\u6253\u8FDB\u7684\u5170\u5DDE, \u6240\u4EE5\u73B0\u5728\u6539\u540D\u53EB\u89E3\u653E\u95E8\u4E86. \u6211\u5728\u5170\u5DDE\u65F6, \u5BB6\u5C31\u4F4F\u5728\u89E3\u653E\u95E8. \u53EF\u60DC\u4F4F\u5728\u4E3E\u9662\u548C\u5170\u5927\u65E7\u5740\u5904\u5E76\u6CA1\u6709\u4F7F\u6211\u5B66\u4E60\u53D8\u597D. \u4F46\u4E5F\u4E0D\u80FD\u602A\u6211, \u89E3\u653E\u95E8\u65E9\u5DF2\u662F\u4E00\u4E2A\u5927\u6742\u9662, \u6709\u623F\u7BA1\u6240, \u4EA4\u901A\u5C40\u7B49\u597D\u51E0\u4E2A\u5355\u4F4D\u7684\u4EBA\u4F4F\u5728\u91CC\u8FB9, \u4E00\u70B9\u4E5F\u6CA1\u6709 &quot;\u835F\u8403\u7CBE\u82F1&quot; \u7684\u611F\u89C9.</p><p>\u8FC7\u897F\u5173\u5341\u5B57\u6CBF\u4E2D\u5C71\u8DEF\u5F80\u5357\u8D70\u4E00\u7AD9\u516C\u4EA4\u8F66\u7684\u8DEF, \u5C31\u5230\u4E86\u53CC\u57CE\u95E8. \u5F53\u5E74\u6297\u65E5\u6218\u4E89\u65F6\u671F, \u65E5\u519B\u7ECF\u5E38\u8F70\u70B8\u5170\u5DDE, \u4E3A\u4E86\u758F\u6563\u7FA4\u4F17, \u5728\u6B64\u5904\u57CE\u5899\u5F00\u4E86\u4E24\u4E2A\u7B80\u6613\u57CE\u95E8, \u56E0\u6B64\u5F97\u540D\u53CC\u57CE\u95E8. \u81F3\u6B64\u4E2D\u5C71\u8DEF\u5C31\u5230\u4E86\u5934, \u518D\u5F80\u897F\u5357\u53BB\u662F\u767D\u94F6\u8DEF, \u5F80\u4E1C\u53BB\u53EB\u5E86\u9633\u8DEF. \u987A\u767D\u94F6\u8DEF\u800C\u4E0A\u5C31\u5230\u5B89\u5B9A\u95E8. \u5B89\u5B9A\u95E8\u65E7\u79F0\u6C38\u5EB7\u95E8, \u5BD3\u610F\u5B89\u5B81\u5EB7\u5B9A, \u662F\u8001\u5170\u5DDE\u57CE\u897F\u5357\u89D2\u7684\u57CE\u95E8. \u51FA\u5B89\u5B9A\u95E8\u518D\u5411\u5357, \u662F\u5C31\u4E86\u768B\u5170\u5C71. \u987A\u7740\u5E86\u9633\u8DEF\u518D\u5F80\u4E1C\u8FC7\u4E24\u4E2A\u7EA2\u7EFF\u706F\u5C31\u5230\u4E86\u5357\u5173\u5341\u5B57. \u4E0E\u897F\u5173\u5341\u5B57\u7C7B\u4F3C, \u5357\u5173\u5341\u5B57\u662F\u8001\u5170\u5DDE\u57CE\u7684\u5357\u95E8.</p><p>\u81EA\u53CC\u57CE\u95E8\u5230\u5357\u5173\u5341\u5B57\u4E00\u7247\u662F\u53E4\u65F6\u7684\u5170\u6CC9\u9A7F. \u5170\u6CC9\u9A7F\u5185\u5EFA\u6709\u4F4F\u623F\u548C\u9A6C\u53A9, \u4E13\u4F9B\u8FC7\u5F80\u6295\u9012\u516C\u6587\u7684\u9A7F\u5352\u4F4F\u5BBF, \u6362\u9A6C. \u800C\u4ECA\u6B64\u5904\u65E9\u662F\u6C38\u660C\u8DEF\u767E\u8D27\u548C\u91D1\u57CE\u5267\u9662.</p><p>\u897F\u5173\u5341\u5B57\u5F80\u4E1C\u662F\u5F20\u6396\u8DEF\u5546\u4E1A\u6B65\u884C\u8857, \u6CBF\u8857\u5C3D\u662F\u5546\u94FA\u9910\u5385. \u6B65\u884C\u8857\u5230\u5C3D\u5934\u662F\u4E2D\u592E\u5E7F\u573A. \u6709\u4F20\u8BF4\u5170\u5DDE\u662F\u4E2D\u56FD\u7684\u51E0\u4F55\u4E2D\u5FC3, \u4E2D\u592E\u5E7F\u573A\u662F\u5170\u5DDE\u7684\u51E0\u4F55\u4E2D\u5FC3, \u4E0D\u77E5\u4F9D\u636E\u4F55\u5728. \u4F46\u5728\u67D0\u79CD\u610F\u4E49\u4E0A, \u4E2D\u592E\u5E7F\u573A\u786E\u5B9E\u662F\u5170\u5DDE\u7684\u653F\u6CBB\u4E2D\u5FC3, \u56E0\u4E3A\u6B64\u5904\u662F\u7701\u653F\u5E9C\u7684\u6240\u5728\u5730. \u7701\u653F\u5E9C\u7684\u524D\u8EAB\u662F\u660E\u8083\u738B\u5E9C, \u5EFA\u4E8E\u660E\u60E0\u5E1D\u5EFA\u6587\u5143\u5E74, \u81EA\u90A3\u65F6\u8D77, \u6B64\u5730\u5C31\u662F\u5C31\u662F\u7518\u8083\u7701\u6700\u9AD8\u884C\u653F\u6743\u529B\u673A\u6784\u6240\u5728\u5730. \u6E05\u671D\u65F6, \u6B64\u5730\u6539\u4E3A\u9655\u9655\u7518\u603B\u7763\u5E9C, \u6C11\u56FD\u65F6\u6539\u4E3A\u7518\u8083\u7701\u653F\u5E9C.</p><p>\u7701\u653F\u5E9C\u524D\u6709\u5C0F\u82B1\u575B, \u82B1\u575B\u4E2D\u5FC3\u662F\u56FD\u65D7\u6746. \u636E\u8BF4\u8FD9\u65D7\u6746\u5C31\u662F\u90A3\u51E0\u4F55\u4E2D\u5FC3\u7684\u51E0\u4F55\u4E2D\u5FC3 -- \u90FD\u5E02\u4F20\u8BF4\u603B\u662F\u8FD9\u6837\u6709\u9F3B\u5B50\u6709\u773C\u7684. \u6211\u521D\u9AD8\u4E2D\u4E0A\u5B66\u8981\u7ECF\u8FC7\u6B64\u5730, \u65E9\u4E0A\u8981\u8D76\u4E0A\u5347\u65D7, \u5C31\u80FD\u770B\u5230\u8DEF\u4E0A\u6C7D\u8F66\u505C\u4E0B\u89C2\u793C.</p><p>\u9664\u4E86\u7701\u653F\u5E9C, \u8FD9\u91CC\u8FD8\u6709\u4E09\u4E2A\u4E66\u5E97. \u4E24\u4E2A\u56FD\u8425\u7684: \u65B0\u534E\u4E66\u5E97\u548C\u53E4\u85C9\u4E66\u5E97. \u4E00\u4E2A\u79C1\u8425\u7684: \u5E7F\u573A\u4E66\u57CE. \u6211\u7684\u7B2C\u4E00\u4EFD\u5DE5\u4F5C\u5C31\u662F\u5728\u5E7F\u573A\u4E66\u5E97\u5F53\u5E97\u5458.</p><p>\u7701\u653F\u5E9C\u5411\u5357\u53BB\u7684\u9053\u8DEF\u53EB\u9152\u6CC9\u8DEF, \u987A\u9152\u6CC9\u8DEF\u5411\u5357\u5C31\u5230\u4E86\u524D\u8FB9\u8BF4\u7684\u5357\u5173\u5341\u5B57. \u65B0\u534E\u4E66\u5E97\u8FB9\u5411\u5317\u7684\u9053\u8DEF\u53EB\u901A\u6E2D\u8DEF, \u901A\u6E2D\u8DEF\u5411\u5317\u5C31\u5230\u4E86\u9EC4\u6CB3. \u8FD9\u91CC\u6709\u4E00\u5EA7\u65B0\u4FEE\u7684\u5927\u6865\u53EB\u901A\u5143\u5927\u6865.</p><p>\u7701\u653F\u5E9C\u518D\u5F80\u4E1C\u5F20\u6396\u8DEF\u5C31\u5230\u4E86\u5934, \u518D\u5411\u4E1C\u7684\u8DEF\u53EB\u79E6\u5B89\u8DEF, \u5357\u5317\u5411\u4E0E\u4E4B\u76F8\u5173\u7684\u53EB\u9759\u5B81\u8DEF. \u5F20\u6396\u8DEF\u4E0E\u9759\u5B81\u8DEF\u76F8\u4EA4\u4E4B\u5904\u5C31\u662F\u8001\u5170\u5DDE\u57CE\u5317\u95E8\u4E4B\u6240\u5728.</p><p>\u6211\u521D\u9AD8\u4E2D\u4E0A\u5B66\u7684\u5170\u5DDE\u4E09\u4E2D\u5C31\u5728\u79E6\u5B89\u8DEF. \u79E6\u5B89\u8DEF\u518D\u5411\u4E1C\u5C31\u5230\u4E86\u5E7F\u6B66\u95E8. \u5E7F\u6B66\u95E8\u53E4\u65F6\u53C8\u79F0\u65B0\u5173\u95E8. \u6240\u8C13\u65B0\u5173\u95E8, \u662F\u6307\u6B64\u5173\u95E8\u662F\u5728\u5EFA\u57CE\u4EE5\u540E\u6240\u5EFA, \u76F8\u8F83\u57CE\u95E8\u4E3A\u65B0\u5173\u95E8. \u5170\u5DDE\u7684\u7EC3\u5175\u573A, \u6559\u573A\u8BBE\u5728\u6B64\u5904. \u56E0\u6B64\u5230\u4E86\u6E05\u671D\u91CD\u4FEE\u6B64\u95E8\u65F6\u5C31\u6B63\u5F0F\u5B9A\u540D\u4E3A\u5E7F\u6B66\u95E8\u4E86.</p><p>\u4EC5\u4ECE\u5730\u540D\u6765\u770B, \u8001\u5170\u5DDE\u57CE\u5C31\u4E5F\u5C31\u8FD9\u4E48\u5927\u4E86: \u4E1C\u8D77\u5E7F\u6B66\u95E8, \u897F\u81F3\u897F\u5173\u5341\u5B57, \u5317\u4E34\u9EC4\u6CB3, \u5357\u5230\u5357\u5173\u5341\u5B57. \u8BF4\u6765\u6709\u8DA3\u7684\u662F, \u6211\u521D\u9AD8\u4E2D\u65F6, \u5BB6\u4F4F\u897F\u5173\u4EE5\u5916\u7684\u89E3\u653E\u95E8, \u4E0A\u5B66\u5728\u5E7F\u6B66\u95E8\u7684\u4E09\u4E2D. \u6BCF\u5929\u4E0A\u4E0B\u5B66\u6B63\u597D\u6A2A\u7A7F\u8001\u5170\u5DDE\u57CE. \u6211\u6BCF\u5929\u6B65\u884C\u4E0A\u4E0B\u5B66, \u8DEF\u4E0A\u5927\u6982\u4E5F\u5C31\u9700\u8981\u4E09\u5341\u5206\u949F\u5DE6\u53F3.</p><p>\u5982\u4ECA\u5170\u5DDE\u65E9\u5DF2\u4E0D\u89C1\u4E86\u57CE\u5899\u4E0E\u5173\u95E8, \u53EA\u80FD\u4F9D\u7A00\u9760\u7740\u5730\u540D\u6765\u60F3\u8C61\u5F53\u5E74\u5170\u5DDE\u57CE\u5343\u767E\u5E74\u95F4\u7684\u751F\u6D3B: \u7EDC\u7ECE\u4E0D\u7EDD\u7684\u4F7F\u56E2\u5546\u65C5\u81EA\u897F\u57DF\u6216\u4E2D\u539F\u800C\u6765, \u7ECF\u91D1\u57CE\u5173\u8FC7\u9547\u8FDC\u6865, \u8FDB\u6865\u95E8\u800C\u5165; \u6216\u7531\u5B89\u5B9A\u95E8\u5E7F\u6B66\u95E8\u8FDB\u5170\u5DDE\u57CE; \u5148\u53BB\u5170\u6CC9\u9A7F\u653E\u4E0B\u884C\u674E, \u5B89\u987F\u8D27\u7269, \u62F4\u597D\u9AA1\u9A6C, \u518D\u53BB\u8083\u738B\u5E9C\u529E\u597D\u516C\u4E8B, \u5982\u8FD8\u6709\u4F59\u88D5\u4FBF\u547C\u670B\u5F15\u4F34\u5230\u7E41\u534E\u7684\u5927\u8857\u6E38\u73A9; \u76F4\u5230\u591C\u5E55\u964D\u4E34\u5357\u6765\u5317\u5F80\u7684\u65C5\u4EBA\u4EEC\u56DE\u5230\u5170\u6CC9\u9A7F, \u76F8\u805A\u800C\u996E, \u628A\u9152\u8A00\u6B22.</p><p>\u5170\u6CC9\u9A7F\u65E7\u5740\u6240\u5728\u7684\u6C38\u660C\u8DEF, \u73B0\u5728\u662F\u4E00\u6761\u591C\u5E02. \u6BCF\u5230\u5929\u9ED1\u81EA\u9EC4\u6CB3\u8FB9\u76F4\u5230\u768B\u5170\u5C71\u4E0B, \u4E00\u8DEF\u4E0A\u5168\u662F\u5356\u8863\u5E3D\u978B\u889C\u548C\u5404\u79CD\u5C0F\u73A9\u610F\u7684\u5C0F\u644A, \u901B\u591C\u5E02\u7684\u4EBA\u7199\u7199\u6518\u6518\u6469\u80A9\u64E6\u8E35. \u5357\u5173\u5341\u5B57\u66FE\u7ECF\u6709\u4E2A\u5564\u9152\u5E7F\u573A, \u4EBA\u4EEC\u805A\u5728\u6B64\u5904\u559D\u9152\u9635\u52BF\u751A\u5927, \u88AB\u4EBA\u620F\u79F0\u4E3A &quot;\u4E07\u4EBA\u5751&quot;. \u5F53\u5E74\u5728\u4E07\u4EBA\u5751\u4E2D\u559D\u9152\u7684\u4EBA\u4EEC, \u5728\u89E5\u7B79\u4EA4\u9519\u95F4, \u7528\u9189\u773C\u671B\u5411\u4EE5\u524D\u7684\u5170\u6CC9\u9A7F\u73B0\u5728\u7684\u6C38\u660C\u8DEF\u591C\u5E02, \u90A3\u4EBA\u5934\u6512\u52A8\u7684\u76DB\u666F\u4E2D, \u604D\u5FFD\u4E0D\u77E5\u51E0\u4EBA\u5728\u4ECA\u51E0\u4EBA\u4E3A\u53E4, \u53E4\u4ECA\u4EA4\u9519\u4F3C\u5728\u4E00\u77AC\u77A5\u89C1\u4E86\u6C38\u6052.</p>',21),a=[e];function r(_,c){return o(),u("div",null,a)}var d=t(p,[["render",r],["__file","\u5170\u5DDE\u5730\u540D\u8003.html.vue"]]);export{d as default};
