import{_ as p,o as r,c as t,e}from"./app.6bf529a6.js";const i={},a=e('<h1 id="\u5173\u4E8E\u6709\u6548\u8FDB\u6B65\u7684\u76F8\u5173\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u6709\u6548\u8FDB\u6B65\u7684\u76F8\u5173\u95EE\u9898" aria-hidden="true">#</a> \u5173\u4E8E\u6709\u6548\u8FDB\u6B65\u7684\u76F8\u5173\u95EE\u9898</h1><h2 id="\u6709\u6548\u8FDB\u6B65" tabindex="-1"><a class="header-anchor" href="#\u6709\u6548\u8FDB\u6B65" aria-hidden="true">#</a> \u6709\u6548\u8FDB\u6B65</h2><p>\u5F53\u6211\u8981\u8BF4\u8FD9\u4E2A\u8BDD\u9898\u7684\u65F6\u5019, \u4E00\u65F6\u95F4, \u7ADF\u4E0D\u77E5\u9053\u8981\u5982\u4F55\u6765\u8868\u8FBE.</p><p>\u6240\u4EE5\u4F30\u4E14\u8FD9\u4E48\u8BF4\u5427, <strong>&quot;\u6709\u6548\u8FDB\u6B65&quot;</strong>.</p><p>\u8981\u771F\u8BF4\u8D77\u6765, \u5F88\u660E\u663E, <strong>&quot;\u6709\u6548\u8FDB\u6B65&quot;</strong> \u6765\u6E90\u4E8E\u5F3A\u5217\u7684\u4E0D\u5B89\u5168\u611F:</p><ul><li>\u53EF\u4EE5\u5F88\u786E\u5207\u7684\u8BF4, \u76EE\u524D\u6240\u6709\u7684\u751F\u6D3B\u5EFA\u57FA\u4E8E\u4E92\u8054\u7F51\u4EA7\u54C1\u7ECF\u7406\u7684\u5DE5\u4F5C\u4E4B\u4E0A;</li><li>\u800C\u4E92\u8054\u7F51\u884C\u4E1A\u56E0\u5176\u672C\u8EAB\u7684\u6027\u8D28\u5177\u6709\u975E\u5E38\u4E0D\u7A33\u5B9A\u548C\u6027\u8D28: \u8FD9\u662F\u4E00\u4E2A\u5B8C\u5168\u5EFA\u7ACB\u5728\u4F20\u9012\u4E0D\u5E73\u7B49\u6027\u6765\u83B7\u76CA\u7684\u884C\u4E1A;</li><li>\u800C\u4EA7\u54C1\u7ECF\u7406\u672C\u8EAB\u53C8\u662F\u4E92\u8054\u7F51\u5F80\u4E1A\u4E2D\u6700\u4E0D\u7A33\u5B9A\u7684\u5C97\u4F4D: \u8FD9\u662F\u4E00\u4E2A\u5B8C\u5168\u4E3A\u4E86\u4F20\u9012\u4E0D\u5E73\u7B49\u6027\u800C\u521B\u5EFA\u7684\u5C97\u4F4D;</li><li>\u8FD9\u4E2A\u7ED3\u679C\u662F, \u8FD9\u4E2A\u5C97\u4F4D\u4E0E\u521B\u9020\u771F\u5B9E\u4EF7\u503C\u79BB\u7684\u975E\u5E38\u7684\u7684\u8FDC. \u4E0D\u80FD\u521B\u5EFA\u771F\u5B9E\u4EF7\u503C\u7684\u5DE5\u4F5C, \u5F80\u5F80\u6CA1\u6709\u786E\u5B9A\u7684\u6807\u51C6, \u8868\u73B0\u51FA\u6765\u5C31\u662F: \u8FD9\u4E2A\u5C97\u4F4D\u7684\u968F\u610F\u6027\u975E\u5E38\u7684\u5927;</li><li>\u6240\u4EE5\u5728\u8FD9\u4E2A\u5C97\u4F4D\u4E0A\u5F88\u96BE\u6709\u786C\u6027\u7684\u79EF\u7D2F.\u4E5F\u5C31\u662F\u8BF4, \u4E00\u4E2A\u4EA7\u54C1\u7ECF\u7406, \u5728\u4E00\u4E2A\u516C\u53F8\u6240\u79EF\u7D2F\u7684\u7ECF\u9A8C, \u5F88\u96BE\u4F20\u9012\u5230\u522B\u4E0B\u4E00\u516C\u53F8.</li><li>\u800C\u975E\u5E38\u4E0D\u5DE7\u7684\u662F, \u4E92\u8054\u7F51\u516C\u53F8\u7684\u4E0D\u7A33\u5B9A\u6027\u8D28, \u4F7F\u7684\u5DE5\u4F5C\u53D8\u52A8\u975E\u5E38\u5927: B \u8F6E\u6B7B\u5DF2\u4E0D\u662F\u4EC0\u4E48\u8038\u4EBA\u542C\u95FB\u7684\u4E8B\u4E86.</li></ul><p>\u6240\u4EE5\u6211\u7684\u7126\u8651\u53EF\u60F3\u800C\u77E5.</p><p>\u8FD9\u4E5F\u662F\u6211\u5F53\u521D\u4ECE\u60E0\u667A\u51FA\u6765\u7684\u4E3B\u8981\u539F\u56E0.</p><p>\u867D\u7136\u5F53\u65F6\u5BF9\u4E8E\u4E92\u8054\u7F51\u884C\u4E1A\u548C\u4EA7\u54C1\u7ECF\u7406\u5C97\u4F4D\u8FD8\u6CA1\u6709\u4EA7\u751F\u73B0\u5728\u8FD9\u6837\u60B2\u89C2\u7684\u770B\u6CD5, \u4F46\u662F\u6709\u4E00\u70B9, \u4ECE\u5F53\u65F6\u770B\u6765\u5C31\u662F\u975E\u5E38\u660E\u786E\u7684, \u90A3\u4E2A\u516C\u53F8\u662F\u4E0D\u53EF\u80FD\u8D70\u5F88\u8FDC\u7684.</p><p>\u603B\u6709\u4E00\u5929, \u6211\u6240\u9700\u8981\u9762\u5BF9\u7684\u6574\u4E2A\u5E02\u4E92\u8054\u7F51\u4EBA\u529B\u8D44\u6E90\u5E02\u573A. \u5728\u90A3\u4E00\u5929, \u5982\u679C\u6211\u6240\u62B1\u6301\u7684\u8FD8\u662F\u60E0\u667A\u7684\u90A3\u4E00\u5957, \u975E\u5E38\u6709\u53EF\u80FD\u4F1A\u6CA1\u6709\u51FA\u8DEF.</p><p>\u6240\u4EE5\u5F53\u65F6\u505A\u51FA\u4E86\u53BB\u4E0A\u6D77\u7684\u51B3\u5B9A. \u603B\u7684\u8BF4\u6765, \u8FD9\u4E2A\u51B3\u5B9A\u7684\u662F\u76F8\u5F53\u6B63\u786E\u7684.\u81F3\u5C11\u5BF9\u4E8E\u5F53\u65F6\u6240\u79EF\u7D2F\u7684\u6280\u80FD\u548C\u601D\u8DEF\u8FDB\u884C\u4E86\u9A8C\u8BC1.</p><p>\u4F46\u662F\u63A5\u8E35\u800C\u81F3\u7684 p2p \u66B4\u96F7\u548C\u9519\u8BEF\u7684\u4F11\u606F\u4E09\u4E2A\u6708\u7684\u51B3\u5B9A, \u4F7F\u6211\u4EEC\u6297\u98CE\u9669\u80FD\u529B\u4E00\u4E0B\u5B50\u8DCC\u5230\u5E95\u7EBF\u4E86.</p><p>\u800C\u540E\u7684\u51E0\u6B21\u9965\u4E0D\u62E9\u98DF\u7684\u6C42\u804C, \u4F7F\u7A33\u5B9A\u6027\u6BCF\u51B5\u903E\u4E0B. \u800C\u5BF9\u4E8E\u672A\u6765, \u662F\u5426\u53EF\u4EE5\u6297\u5230\u4E00\u4E2A\u76F8\u5BF9\u7A33\u5B9A\u548C\u7ADF\u51B5, \u5DF2\u7ECF\u53D8\u5F97\u975E\u5E38\u4E0D\u786E\u5B9A\u4E86.</p><p>\u6240\u4EE5, \u5FC5\u987B\u8981\u5728\u6709\u9650\u7684\u65F6\u95F4\u5185 <strong>\u6709\u6548\u7684\u8FDB\u6B65</strong> \u5C31\u4E3A\u5F97\u975E\u5E38\u8FEB\u5207. \u53EA\u6709\u8FDB\u6B65\u624D\u662F\u5BF9\u6297\u98CE\u9669\u7684\u6700\u597D\u624B\u6BB5.</p><p>\u8BBE\u60F3, \u5982\u679C\u5F53\u65F6\u4E00\u80A1\u4F5C\u6C14, \u628A\u9879\u7BA1\u8003\u4E0B\u6765, \u73B0\u5728\u8FD8\u771F\u4E0D\u81F3\u4E8E\u5982\u6B64\u56E7\u8FEB. \u591A\u6837\u6027\u7684\u6280\u80FD, \u53EF\u4EE5\u4FDD\u8BC1\u6211\u4EEC\u5728\u590D\u6742\u7684\u5E02\u573A\u73AF\u5883\u4FDD\u8BC1\u7A33\u5B9A\u6027.</p><h2 id="\u6709\u6548\u8FDB\u6B65\u4E4B\u6210\u4E3A\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u6709\u6548\u8FDB\u6B65\u4E4B\u6210\u4E3A\u95EE\u9898" aria-hidden="true">#</a> \u6709\u6548\u8FDB\u6B65\u4E4B\u6210\u4E3A\u95EE\u9898</h2><p>\u6709\u6548\u8FDB\u6B65\u4E4B\u6210\u4E3A\u95EE\u9898, \u662F\u56E0\u4E3A, \u4E8B\u5B9E\u4E0A\u6CA1\u80FD\u505A\u5230\u6709\u6548\u8FDB\u6B65.</p><p>\u5E94\u8BE5\u662F\u6709\u5404\u79CD\u5177\u4F53\u7684\u7EC6\u8282\u7EC4\u6210\u4E86\u8FD9\u4E2A\u95EE\u9898:</p><p>\u9996\u5148, \u4EC0\u4E48\u624D\u662F\u6709\u6548\u8FDB\u6B65, \u591A\u770B\u4E86\u54F2\u5B66\u4E66, \u591A\u5199\u4E86\u516C\u4F17\u53F7\u6587\u7AE0, \u7B97\u4E0D\u7B97\u6709\u6548\u8FDB\u6B65? \u5199\u4E86\u811A\u672C, \u914D\u7F6E\u4E86 vim \u7B97\u4E0D\u7B97\u6709\u6548\u8FDB\u6B65\uFF1F \u5982\u679C\u7B97, \u5B83\u5E26\u6765\u4EC0\u4E48\u6837\u7684\u8FDB\u6B65. \u8FD9\u6837\u7684\u8FDB\u6B65\u6709\u4EC0\u4E48\u610F\u4E49, \u5982\u679C\u4E0D\u7B97, \u90A3\u5B83\u4EEC\u662F\u4E0D\u591F\u6709\u6548, \u8FD8\u662F\u4E0D\u591F\u8FDB\u6B65? \u90A3\u4E48\u505A\u4EC0\u4E48\u624D\u7B97\u5F97\u4E0A\u662F\u6709\u6548\u8FDB\u6B65?</p><p>\u6216\u8005\u6211\u4EEC\u5E94\u5F53\u4ECE\u4EC0\u4E48\u662F\u8FDB\u6B65\u5F00\u59CB\u8BA8\u8BBA. \u53EA\u6709\u77E5\u9053\u4E86\u4EC0\u4E48\u662F\u8FDB\u6B65, \u6211\u4EEC\u624D\u80FD\u8FDB\u4E00\u6B65\u5224\u65AD, \u4EC0\u4E48\u662F\u6709\u6548\u7684\u8FDB\u6B65, \u4EC0\u4E48\u7684\u8FDB\u6B65\u4E0D\u591F\u6709\u6548.</p><p>\u5982\u679C\u6211\u4EEC\u56DE\u5230\u4E3A\u4EC0\u4E48\u8981\u6709 <strong>\u6709\u6548\u8FDB\u6B65</strong> \u8BA8\u8BBA\u4E0A, \u6211\u4EEC\u53EF\u4EE5\u5F97\u5230\u8FD9\u6837\u7684\u7ED3\u8BBA. <strong>&quot;\u6709\u6548\u8FDB\u6B65&quot;</strong> \u662F\u4E3A\u4E86\u901A\u8FC7\u589E\u52A0\u81EA\u8EAB\u7684\u77E5\u8BC6\u6216\u6280\u80FD\u6216\u7ECF\u9A8C, \u800C\u83B7\u5F97\u4E00\u5B9A\u7A0B\u5EA6\u7684\u7A33\u5B9A\u6027, \u4EE5\u5BF9\u6297\u53EF\u80FD\u51FA\u73B0\u7684\u98CE\u9669.</p><p>\u90A3\u4E48\u6211\u4EEC\u5728\u8BA8\u8BBA\u662F\u5426 <strong>\u8FDB\u6B65</strong> \u65F6, \u6211\u4EEC\u5176\u5B9E\u662F\u5728\u8BA8\u8BBA, \u6211\u4EEC\u662F\u5426\u771F\u6B63\u7684\u589E\u4E86\u7A33\u5B9A\u6027.</p><p>\u597D\u6211\u4EEC\u8FDB\u4E00\u6B65\u53BB\u60F3\u8FD9\u4E2A\u95EE\u9898, \u4EC0\u4E48\u6837\u7684\u4E8B\u60C5\u4EA7\u751F\u7A33\u5B9A\u6027. \u6216\u8005\u66F4\u5F62\u8C61\u7684\u8BF4, \u4EC0\u4E48\u7684\u4E8B\u60C5\u4F7F\u6211\u4EEC\u79EF\u7D2F\u7A33\u5B9A\u6027.</p><p>\u786C\u6280\u80FD\u4EA7\u751F\u7A33\u5B9A\u6027. \u4EC0\u4E48\u662F\u786C\u6280\u80FD. \u5927\u6982\u6709\u4E24\u4E2A\u6807\u51C6: \u5F62\u6210\u95E8\u69DB\u548C\u5177\u6709\u5BA2\u89C2\u6807\u51C6.</p><p>\u5F62\u6210\u95E8\u69DB\u662F\u6307: \u6491\u63E1\u8FD9\u4E2A\u6280\u80FD, \u5219\u53EF\u4EE5\u591A\u505A\u4E00\u4E9B\u4E8B. \u6BD4\u5982, \u8BF4\u8BDD\u7684\u6280\u80FD, \u638C\u63E1\u4E86, \u53EF\u4EE5\u8BF4\u7684\u597D\u4E00\u4E9B, \u4E0D\u638C\u63E1, \u4E5F\u4E0D\u803D\u8BEF\u8BF4\u8BDD. \u8FD9\u5C31\u4E0D\u662F\u786C\u6280\u80FD. \u8F6F\u4EF6\u64CD\u4F5C, \u4E13\u95E8\u5B66\u4E60\u4E86, \u53EF\u4EE5\u7528\u7684\u597D\u4E00\u70B9, \u4E0D\u5B66\u4E60, \u81EA\u5DF1\u6478\u7D22\u4E5F\u5C06\u5C31\u7528, \u8FD9\u5C31\u4E0D\u662F\u4EC0\u4E48\u786C\u6280\u80FD.</p><p>\u5177\u6709\u5BA2\u89C2\u6027\u662F\u6307: \u8FD9\u4E2A\u6280\u80FD, \u638C\u63E1\u7684\u597D\u4E0D\u597D, \u662F\u53EF\u4EE5\u7528\u901A\u5BA2\u89C2\u7684\u65B9\u5F0F\u663E\u5F0F\u4E86\u6765\u7684. \u6BD4\u5982, \u5982\u679C\u5B66\u4E60\u4E00\u95E8\u7F16\u7A0B\u8BED\u8A00, \u5B66\u5230\u4EC0\u4E48\u7A0B\u5EA6, \u638C\u63E1\u591A\u5C11, \u8FD9\u662F\u53EF\u4EE5\u91CF\u5316\u7684. \u4F46\u662F, \u8C61\u662F\u601D\u7EF4\u65B9\u5F0F, \u5E7F\u800C\u8A00\u4E4B\u7684\u65B9\u6CD5\u8BBA, \u8FD9\u79CD\u4E1C\u897F, \u5176\u5B9E\u5C31\u6CA1\u4EC0\u4E48\u5BA2\u89C2\u6807\u51C6. \u4EFB\u4F55\u4E00\u4E2A\u4EBA\u90FD\u53EF\u4EE5\u5BF9\u81EA\u5DF1\u7684\u4E00\u5957\u65B9\u6CD5\u8BBA\u8BB2\u51FA\u4E00\u4E2A\u8BF4\u6CD5\u6765.</p><p>\u590D\u6742\u6027\u4EA7\u751F\u7A33\u5B9A\u6027. \u5176\u5B9E\u66F4\u786E\u5207\u7684\u8BF4, \u5197\u4F59\u4EA7\u751F\u7A0B\u5B9A\u6027, \u800C\u590D\u6742\u6027\u4EA7\u751F\u5197\u4F59. \u65E2\u7136\u6211\u4EEC\u9762\u5BF9\u7684\u662F\u672A\u6765\u4E0D\u53EF\u77E5\u7684\u98CE\u9669, \u90A3\u4E48\u80FD\u591F\u5E94\u5BF9\u66F4\u591A\u60C5\u51B5, \u5C31\u6BD4\u53EA\u80FD\u5728\u4E00\u5904\u4E13\u7CBE\u66F4\u5177\u6709\u7A0B\u5B9A\u6027.</p><p>\u5F53\u7136, \u5BF9\u51B2\u662F\u4E00\u4E2A\u597D\u6CE8\u610F, \u4F46\u662F, \u627E\u5230\u4E00\u4E2A\u597D\u5BF9\u5B50\u53EF\u80FD\u662F\u56F0\u96BE\u7684, \u800C\u80FD\u591F\u5F62\u6210\u5BF9\u51B2, \u5C31\u66F4\u96BE\u4E86. \u6BD4\u5982, \u5982\u679C\u5B66\u4E60\u673A\u68B0\u52A0\u5DE5\u4E00\u5B9A\u662F\u53EF\u4EE5\u5F62\u6210\u5BF9\u51B2\u7684. \u4F46\u662F\u76EE\u524D\u7684\u73AF\u5883\u5374\u5F88\u96BE\u5B66\u4E60\u673A\u68B0\u52A0\u5DE5.</p><p>\u53E6\u5916, \u5BF9\u5B50\u90FD\u662F\u4EBA\u5728\u53EF\u77E5\u7684\u60C5\u51B5\u4E0B\u5F62\u6210\u7684\u8BA4\u77E5\u65B9\u5F0F. \u4F46\u4E16\u754C\u4E0D\u662F\u8FD9\u6837\u7684. \u6240\u4EE5\u5982\u679C\u771F\u7684\u51B2\u7740\u5BF9\u5B50\u53BB\u4E86, \u4F46\u6709\u53EF\u80FD\u843D\u4E2A\u4E24\u5934\u7A7A.</p><p>\u5728\u8FD9\u4E00\u70B9\u4E0A, \u53EF\u4EE5\u5EF6\u4F38\u51FA\u7684\u662F, \u57FA\u7840\u6027\u4EA7\u751F\u7A33\u5B9A\u6027. \u56E0\u4E3A\u57FA\u7840\u6027\u7684, \u6216\u4EE5\u5728\u66F4\u591A\u7684\u5730\u65B9\u88AB\u5E94\u7528. \u8FD9\u5C31\u662F\u4E00\u79CD\u4EA7\u751F\u5197\u4F59\u7684\u60C5\u51B5.</p><p>\u5916\u90E8\u6027\u4E5F\u53EF\u4EE5\u4EA7\u751F\u7A33\u6027\u5B9A. \u8FD9\u4E2A\u6765\u81EA\u4E8E\u8D1F\u5892. \u53EA\u6709\u5728\u5916\u90E8\u4EA4\u6D41\u7684\u60C5\u51B5\u4E0B, \u5185\u90E8\u624D\u80FD\u4EA7\u751F\u79E9\u5E8F.</p><p>\u8BFB\u54F2\u5B66\u4E66, \u53EF\u4EE5\u7B97\u5230\u57FA\u7840\u6027\u4E0A, \u5199\u516C\u4F17\u53F7, \u53EF\u4EE5\u7B97\u5230\u5916\u90E8\u6027\u4E0A. \u5B66\u4E60 vimScript \u53EF\u4EE5\u7B97\u5230\u786C\u6280\u80FD\u4E0A. ( \u4F46\u5176\u5B9E\u4F60\u60F3\u4E00\u4E0B, \u8FD9\u4E2A\u5176\u5B9E\u4E5F\u4E0D\u90A3\u4E48\u786C. \u9664\u975E\u771F\u80FD\u7528 vimScript \u505A\u5230\u4E00\u4E9B\u522B\u4EBA\u505A\u4E0D\u5230\u7684\u4E8B\u60C5. )</p><p>\u90A3\u4E48\u4EC0\u4E48\u662F\u6709\u6548. \u8BFB\u54F2\u5B66\u4E66\u66F4\u6709\u6548, \u8FD8\u662F\u5199\u516C\u4F17\u53F7\u66F4\u6709\u6548. \u6211\u4EEC\u5E94\u8BE5\u628A\u66F4\u591A\u7684\u65F6\u95F4\u653E\u5728\u914D\u7F6E vimrc \u4E0A, \u8FD8\u662F\u628A\u66F4\u591A\u7684\u65F6\u95F4, \u7528\u6765\u6458\u5F55\u8D6B\u897F\u4FC4\u5FB7\u7684\u795E\u8C31?</p><p>\u4EC5\u8FD9\u6837\u6A2A\u5411\u6765\u6BD4\u8F83, \u6211\u4EEC\u4EC0\u4E48\u90FD\u6BD4\u8F83\u4E0D\u51FA\u6765. \u56E0\u4E3A,</p><ul><li>\u9996\u5148, \u5982\u679C\u6211\u4EEC\u6A2A\u5411\u6765\u8FDB\u884C\u6BD4\u8F83, \u6211\u4EEC\u5176\u5B9E\u662F\u5728\u5BF9\u5B83\u4EEC\u8FDB\u884C\u91CF\u5316. \u4F46\u5B83\u4EEC\u672C\u8D28\u4E0A, \u5C31\u662F\u6027\u8D28\u4E0D\u540C\u7684\u4E8B\u60C5, \u6027\u8D28\u4E0D\u540C\u7684\u4E8B\u60C5, \u8FDB\u884C\u91CF\u5316, \u5FC5\u7136\u4F1A\u8FDB\u884C\u8FC7\u5EA6\u7B80\u5316. \u5728\u8FD9\u79CD\u4E8B\u60C5\u8FC7\u5EA6\u7B80\u5316, \u90A3\u4F55\u82E6\u8FD8\u505A\u5462?</li><li>\u5176\u6B21, \u5982\u679C\u6211\u4EEC\u4EE5\u8FD9\u79CD\u786E\u77E5\u7684\u7406\u6027\u884C\u4E8B, \u5C31\u5F88\u96BE\u53BB\u5E94\u5BF9\u4EE5\u4E0D\u53EF\u77E5\u4E3A\u57FA\u7840\u7684\u98CE\u9669\u4E86.</li></ul><p>\u53EF\u80FD, \u5982\u679C\u975E\u5361\u5728\u65F6\u95F4\u6709\u9650\u4E0A, \u6211\u4EEC\u771F\u7684\u65E0\u6CD5\u5B9A\u4E49\u51FA\u4E00\u4E2A\u5224\u65AD <strong>\u6709\u6548</strong> \u7684\u65B9\u6CD5.</p><h2 id="\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#\u600E\u4E48\u529E" aria-hidden="true">#</a> \u600E\u4E48\u529E</h2><p>\u90A3\u4E48\u600E\u4E48\u529E? \u5982\u4F55\u4F7F\u6211\u4EEC\u7684\u80FD\u591F\u6709\u6548\u7684\u8FDB\u6B65, \u4EE5\u4FDD\u6B63, \u5728\u771F\u8981\u9762\u5BF9\u98CE\u9669\u7684\u90A3\u4E00\u5929\u91CC, \u5177\u6709\u771F\u6B63\u7684\u62B5\u6297\u80FD\u529B.</p><p>\u60F3\u6765\u60F3\u53BB, \u4F3C\u4E4E\u53EA\u6709 <strong>\u505A\u4E8B</strong> \u4E00\u4E2A\u529E\u6CD5.</p><p>\u90A3\u95EE\u9898\u5C31\u53D8\u6210, \u505A\u4EC0\u4E48\u4E8B. \u5F53\u7136\u53EF\u4EE5\u8BF4, \u4F7F\u81EA\u5DF1\u662F\u5176\u6240\u662F\u7684\u4E8B. \u4F46\u662F, \u5728\u8FD9\u91CC\u8BF4\u8FD9\u6837\u7684\u653E\u4E4B\u56DB\u6D77\u7686\u51C6\u7684\u5E9F\u8BDD, \u5176\u5B9E\u6CA1\u4EC0\u4E48\u610F\u601D. \u56E0\u4E3A, \u53EA\u8BF4\u4F7F\u81EA\u5DF1\u662F\u5176\u6240\u4E8B, \u6211\u4EEC\u4F9D\u7136\u4E0D\u77E5\u9053\u8BE5\u505A\u4EC0\u4E48.</p><p>\u66FE\u7ECF\u6709\u8FC7\u8FD9\u6837\u7684\u60F3\u6CD5, \u5C31\u662F\u8BBE\u8BA1\u4E00\u4E2A\u4F9B\u5E94\u94FE\u91D1\u878D\u7CFB\u7EDF, \u7136\u540E\u81EA\u5DF1\u5B9E\u73B0\u4E4B. \u4F46\u662F, \u8FD9\u4E2A\u60F3\u6CD5\u5230\u73B0\u5728\u8FD8\u505C\u7559\u5728\u60F3\u6CD5\u4E0A.</p><p>\u540C\u6837\u7684\u60F3\u6CD5, \u8FD8\u53EF\u4EE5\u662F\u8BBE\u8BA1\u4E00\u4E2A\u901A\u7528\u79EF\u5206\u7CFB\u7EDF, \u7136\u540E\u81EA\u5DF1\u5B9E\u73B0\u4E4B.</p><p>\u6216\u8005, \u8FD8\u53EF, \u9762\u5411\u8003\u8BD5. \u6BD4\u5982\u518D\u8BD5\u7740\u8003\u8F6F\u8003. \u4F46\u662F, \u6211\u4EEC\u771F\u7684\u6709\u8FD9\u6837\u7684\u80FD\u529B\u548C\u5FC3\u6C14\u5B8C\u6210\u8FD9\u6837\u7684\u4E00\u4E2A\u4EFB\u52A1\u4E48? \u4F60\u8981\u77E5\u9053, \u6211\u4EEC\u7684\u5BA2\u89C2\u73AF\u5883\u662F\u56DE\u5230\u65E0\u9521\u7684\u5BB6\u91CC, \u53EA\u80FD\u5077\u6478\u7684\u5B66\u4E60.</p><p>\u53E6\u4E00\u4E2A\u6BD4\u8F83\u53EF\u884C\u7684\u60F3\u6CD5, \u5C31\u662F\u770B\u4E66. \u4EE5\u770B\u4E66\u7684\u4EFB\u52A1\u4E3A\u4E3B, \u65E5\u62F1\u4E00\u5352\u7684\u624E\u624E\u5B9E\u5B9E\u7684\u770B\u4E9B\u4E66. \u4F46\u662F, \u53EF\u80FD\u5C31\u53C8\u4F1A\u9677\u5165\u5230\u539F\u6765\u90A3\u79CD\u4E0D\u77E5\u9053\u8BE5\u5982\u4F55\u5224\u65AD\u662F\u5426\u5728 <strong>\u6709\u6548\u8FDB\u6B65</strong> \u7684\u72B6\u51B5\u4E2D\u4E86.</p><p>\u90A3\u6240\u4EE5,</p><p>\u600E\u4E48\u529E?</p><p>\u600E\u4E48\u529E?</p><p>\u6216\u8005\u771F\u6B63\u53EF\u884C\u662F, \u8BA1\u5212\u5C0F\u4EFB\u52A1, \u9010\u6B65\u5B9E\u73B0.</p><p>\u96BE\u9053, \u73B0\u5728\u771F\u6B63\u7684\u95EE\u9898, \u4E0D\u80FD\u884C\u4E4B\u6709\u6548\u7684\u5C06\u6211\u4EEC\u7684\u884C\u4E3A\u53D8\u6210\u53EF\u89C1\u7684\u7ED3\u679C\u4E48?</p>',49),o=[a];function n(s,l){return r(),t("div",null,o)}var c=p(i,[["render",n],["__file","\u5173\u4E8E\u6709\u6548\u8FDB\u6B65\u7684\u76F8\u5173\u95EE\u9898.html.vue"]]);export{c as default};
