import{_ as p,o as e,c as o,e as r}from"./app.6bf529a6.js";const t={},l=r('<p>\u518D\u8BBA\u300A\u6CA1\u6709\u94F6\u5F39\u300B ( \u201C No Silver Bullet \u201D Refired)</p><blockquote><p>\u751F\u6B7B\u6709\u547D,\u5BCC\u8D35\u5728\u5929</p><ul><li>\u5A01\u5EC9\u4E09\u4E16,\u5965\u4F26\u6CBB\u738B\u5B50</li></ul></blockquote><blockquote><p>\u90A3\u4E9B\u60F3\u770B\u5230\u5B8C\u7F8E\u65B9\u6848\u7684\u4EBA,\u5176\u5B9E\u5728\u5FC3\u5E95\u91CC\u5C31\u8BA4\u4E3A\u5B83\u4EEC\u4EE5\u524D\u4E0D\u5B58\u5728,\u4EE5\u540E\u4E5F\u4E0D\u53EF\u80FD\u51FA\u73B0\u3002</p><ul><li>\u4E9A\u5386\u5C71\u5927\xB7\u6CE2\u666E,\u6279\u5224\u6563\u6587</li></ul></blockquote><blockquote><p>Every bullet has its billet.</p><ul><li>WILLIAM III OF ENGLAND, PRINCE OF ORANGE</li></ul></blockquote><blockquote><p>Whoever thinks a faultless piece to see, thinks what ne\u2019er was, nor e\u2019er shall be.</p><ul><li>ALEXANDER POPE, AN ESSAY ON CRITISIM</li></ul></blockquote><h2 id="\u4EBA\u72FC\u548C\u5176\u4ED6\u6050\u6016\u4F20\u8BF4" tabindex="-1"><a class="header-anchor" href="#\u4EBA\u72FC\u548C\u5176\u4ED6\u6050\u6016\u4F20\u8BF4" aria-hidden="true">#</a> \u4EBA\u72FC\u548C\u5176\u4ED6\u6050\u6016\u4F20\u8BF4</h2><p>\u300A\u6CA1\u6709\u94F6\u5F39-\u8F6F\u4EF6\u5DE5\u7A0B\u4E2D\u7684\u6839\u672C\u548C\u6B21\u8981\u95EE\u9898\u300B\u6700\u521D\u662F\u5728 IFIP\u201986 \u5E74\u90FD\u67CF\u6797\u5927\u4F1A\u7684\u7EA6\u7A3F,\u63A5\u7740\u5728\u4E00\u7CFB\u5217\u7684\u520A\u7269\u4E0A\u53D1\u8868 \u3002\u300A \u8BA1\u7B97\u673A\u300B \u6742\u5FD7\u4E0A\u7FFB\u5370\u4E86\u8BE5\u6587\u7AE0,\u5C01\u9762\u662F\u4E00\u526F\u7C7B\u4F3C\u4E8E\u300A \u4F26\u6566\u4EBA\u72FC\u300B \u5F71\u7247\u7684\u6050\u6016\u5267\u7167\u3002\u540C\u65F6,\u8FD8\u6709\u4E00\u680F\u8865\u5145\u62A5\u9053\u300A\u6740\u6B7B\u4EBA\u72FC\u300B,\u63CF\u8FF0\u4E86\u94F6\u5F39\u5C06\u8981\u5B8C\u6210\u7684(\u73B0\u4EE3)\u795E\u8BDD\u3002\u5728\u51FA\u7248\u4EE5\u524D,\u6211\u5E76\u672A\u6CE8\u610F\u5230\u8865\u5145\u62A5\u9053\u548C\u6587\u5B57,\u4E5F\u6CA1\u6709\u6599\u5230\u4E00\u7BC7\u4E25\u8083\u7684\u6280\u672F\u6027\u6587\u5B57\u4F1A\u88AB\u8FD9\u6837\u6DA6\u8272\u3002</p><p>Computer \u6742\u5FD7\u7684\u7F16\u8F91\u4EEC\u662F\u53D6\u5F97\u4ED6\u4EEC\u60F3\u8981\u7684\u6548\u679C\u7684\u4E13\u5BB6,\u4E0D\u8FC7,\u4F3C\u4E4E\u6709\u5F88\u591A\u4EBA\u9605\u8BFB\u4E86\u90A3\u7BC7\u6587\u7AE0\u3002\u56E0\u6B64,\u6211\u4E3A\u90A3\u4E00\u7AE0\u9009\u62E9\u4E86\u53E6\u4E00\u5E45\u4EBA\u72FC\u63D2\u56FE,\u4E00\u5E45\u5BF9\u8FD9\u79CD\u8FD1\u4E4E\u6ED1\u7A3D\u7269\u79CD\u7684\u53E4\u8001\u7D20\u63CF\u3002\u6211\u5E0C\u671B\u8FD9\u526F\u5E76\u4E0D\u523A\u773C\u7684\u56FE\u6848\u6709\u76F8\u540C\u7684\u6B63\u9762\u6548\u679C\u3002</p><h2 id="\u5B58\u5728\u7740\u94F6\u5F39-\u5C31\u5728\u8FD9\u91CC" tabindex="-1"><a class="header-anchor" href="#\u5B58\u5728\u7740\u94F6\u5F39-\u5C31\u5728\u8FD9\u91CC" aria-hidden="true">#</a> \u5B58\u5728\u7740\u94F6\u5F39-\u5C31\u5728\u8FD9\u91CC!</h2><p>\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u4E2D\u58F0\u79F0\u548C\u65AD\u5B9A,\u5728\u8FD1\u5341\u5E74\u5185,\u6CA1\u6709\u4EFB\u4F55\u5355\u72EC\u7684\u8F6F\u4EF6\u5DE5\u7A0B\u8FDB\u5C55\u53EF\u4EE5\u4F7F\u8F6F\u4EF6\u751F\u4EA7\u7387\u6709\u6570\u91CF\u7EA7\u7684\u63D0\u9AD8(\u5F15\u81EA 1986 \u5E74\u7684\u7248\u672C)\u3002\u73B0\u5728\u5DF2\u7ECF\u662F\u7B2C\u4E5D\u4E2A\u5E74\u5934,\u56E0\u6B64\u4E5F\u8BE5\u770B\u770B\u662F\u5426\u8FD9\u4E9B\u9884\u8A00\u5F97\u5230\u4E86\u5E94\u9A8C\u3002</p><p>\u300A\u4EBA\u6708\u795E\u8BDD\u300B \u4E00\u6587\u88AB\u5927\u91CF\u5730\u5F15\u7528,\u5F88\u5C11\u5B58\u5728\u5F02\u8BAE;\u76F8\u6BD4\u4E4B\u4E0B,\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u5374\u5F15\u53D1\u4E86\u4F17\u591A\u7684\u8FA9\u8BBA,\u7F16\u8F91\u6536\u5230\u4E86\u5F88\u591A\u6587\u7AE0\u548C\u4FE1\u4EF6,\u81F3\u4ECA\u8FD8\u5728\u5EF6\u7EED \u3002\u4ED6\u4EEC\u4E2D\u7684\u5927\u591A\u6570\u653B\u51FB\u5176\u6838\u5FC3\u8BBA\u70B9\u548C\u6211\u7684\u89C2\u70B9\u2014\u2014\u6CA1\u6709\u795E\u8BDD\u822C\u7684\u89E3\u51B3\u65B9\u6848,\u4EE5\u53CA\u5C06\u6765\u4E5F\u4E0D\u4F1A\u6709\u3002\u4ED6\u4EEC\u5927\u90FD\u540C\u610F\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u4E00\u6587\u4E2D\u7684\u591A\u6570\u89C2\u70B9,\u4F46\u63A5\u7740\u65AD\u5B9A\u5B9E\u9645\u5B58\u5728\u7740\u6740\u6B7B\u8F6F\u4EF6\u602A\u517D\u7684\u94F6\u5F39\u2014\u2014\u7531\u4ED6\u4EEC\u6240\u53D1\u660E\u7684\u94F6\u5F39\u3002\u4ECA\u5929,\u5F53\u6211\u91CD\u65B0\u9605\u8BFB\u4E00\u4E9B\u65E9\u671F\u7684\u53CD\u9988,\u6211\u4E0D\u7981\u53D1\u73B0\u5728 1986 \u5E74~1987 \u5E74\u671F\u95F4,\u66FE\u88AB\u5F3A\u70C8\u63A8\u5D07\u7684\u79D8\u65B9\u5E76\u6CA1\u6709\u51FA\u73B0\u6240\u58F0\u79F0\u7684\u620F\u5267\u6027\u6548\u679C\u3002</p><p>\u5728\u8D2D\u4E70\u8BA1\u7B97\u673A\u8F6F\u4EF6\u548C\u786C\u4EF6\u65F6,\u6211\u559C\u6B22\u542C\u53D6\u90A3\u4E9B\u771F\u6B63\u4F7F\u7528\u8FC7\u4EA7\u54C1\u5E76\u611F\u5230\u6EE1\u610F\u7684\u7528\u6237\u7684\u63A8\u8350\u3002\u7C7B\u4F3C\u7684,\u6211\u5F88\u4E50\u610F\u63A5\u53D7\u94F6\u5F39\u5DF2\u7ECF\u51FA\u73B0\u7684\u89C2\u70B9,\u4F8B\u5982,\u67D0\u4E2A\u540D\u526F\u5176\u5B9E\u7684\u4E2D\u7ACB\u5BA2\u6237\u8D70\u5230\u9762\u524D,\u5E76\u58F0\u79F0,\u201C\u6211\u4F7F\u7528\u4E86\u8FD9\u79CD\u65B9\u6CD5\u3001\u5DE5\u5177\u6216\u8005\u4EA7\u54C1,\u5B83\u4F7F\u6211\u7684\u8F6F\u4EF6\u751F\u4EA7\u7387\u63D0\u9AD8\u4E86\u5341\u500D\u3002\u201D</p><p>\u5F88\u591A\u4E66\u4FE1\u4F5C\u8005\u8FDB\u884C\u4E86\u82E5\u5E72\u6B63\u786E\u7684\u4FEE\u8BA2\u548C\u6F84\u6E05,\u5176\u4E2D\u4E00\u4E9B\u8FD8\u63D0\u4F9B\u4E86\u5F88\u6709\u9488\u5BF9\u6027\u7684\u5206\u6790\u548C\u8FA9\u9A73,\u5BF9\u6B64\u6211\u975E\u5E38\u611F\u6FC0\u3002\u672C\u7AE0\u4E2D,\u6211\u5C06\u540C\u5927\u5BB6\u5206\u4EAB\u8FD9\u4E9B\u6539\u8FDB,\u4EE5\u53CA\u5BF9\u53CD\u9762\u610F\u89C1\u8FDB\u884C\u8BA8\u8BBA\u3002</p><h2 id="\u542B\u7CCA\u7684\u8868\u8FBE\u5C06\u4F1A\u5BFC\u81F4\u8BEF\u89E3" tabindex="-1"><a class="header-anchor" href="#\u542B\u7CCA\u7684\u8868\u8FBE\u5C06\u4F1A\u5BFC\u81F4\u8BEF\u89E3" aria-hidden="true">#</a> \u542B\u7CCA\u7684\u8868\u8FBE\u5C06\u4F1A\u5BFC\u81F4\u8BEF\u89E3</h2><p>\u67D0\u4E9B\u4F5C\u8005\u6307\u51FA\u6211\u6CA1\u6709\u5C06\u4E00\u4E9B\u89C2\u70B9\u8868\u8FBE\u6E05\u695A\u3002</p><p><strong>\u6B21\u8981(Accident)</strong> \u5728\u7B2C 16 \u7AE0\u7684\u6458\u8981\u4E2D,\u6211\u5DF2\u7ECF\u5C3D\u6211\u6240\u80FD\u5730\u6E05\u6670\u8868\u8FBE\u4E86\u300A\u6CA1\u6709\u94F6\u5F39\u300B \u4E00\u6587\u7684\u4E3B\u8981\u89C2\u70B9\u3002\u7136\u800C,\u4ECD\u6709\u4E9B\u89C2\u70B9\u7531\u4E8E\u672F\u8BED \u201Caccident(\u5076\u7136)\u201D \u548C \u201Caccidental(\u6B21\u8981)\u201D\u800C\u88AB\u6DF7\u6DC6,\u8FD9\u4E9B\u672F\u8BED\u6765\u81EA\u4E9A\u91CC\u65AF\u591A\u5FB7\u7684\u53E4\u8001\u7528\u6CD5 \u3002\u672F\u8BED\u201Caccidental\u201D,\u6211\u4E0D\u662F\u6307\u201C\u5076\u7136\u53D1\u751F\u201D,\u4E5F\u4E0D\u662F\u6307\u201C\u4E0D\u5E78\u7684\u201D,\u800C\u662F\u66F4\u63A5\u8FD1\u4E8E\u201C\u9644\u5E26\u7684\u201D\u6216\u8005\u201C\u4ECE\u5C5E\u7684\u201D\u3002</p><p>\u6211\u5E76\u4E0D\u662F\u8D2C\u4F4E\u8F6F\u4EF6\u6784\u5EFA\u4E2D\u7684\u6B21\u8981\u90E8\u5206,\u76F8\u53CD,\u6211\u8BA4\u540C\u82F1\u56FD\u5267\u4F5C\u5BB6\u3001\u4FA6\u63A2\u5C0F\u8BF4\u4F5C\u8005\u548C\u795E\u5B66\u5BB6\u6843\u4E50\u4E1D\xB7\u8D5B\u5C14\u4E1D\u770B\u5F85\u521B\u9020\u6027\u6D3B\u52A8\u7684\u89C2\u70B9,\u521B\u9020\u6027\u6D3B\u52A8\u5305\u62EC(1)\u6982\u5FF5\u6027\u7ED3\u6784\u7684\u5F62\u5F0F\u89C4\u683C\u5316,(2)\u4F7F\u7528\u73B0\u5B9E\u7684\u4ECB\u8D28\u6765\u5B9E\u73B0,(3)\u5728\u5B9E\u9645\u7684\u4F7F\u7528\u4E2D,\u4E0E\u7528\u6237\u4EA4\u4E92 \u3002\u5728\u8F6F\u4EF6\u5F00\u53D1\u4E2D,\u6211\u79F0\u4E3A\u201C\u5FC5\u8981(essence)\u201D\u7684\u90E8\u5206\u662F\u6784\u601D\u8FD9\u4E9B\u6982\u5FF5\u4E0A\u7684\u7ED3\u6784;\u6211\u79F0\u4E3A\u201C\u6B21\u8981(accident)\u201D\u7684\u90E8\u5206\u6307\u5B83\u7684\u5B9E\u73B0\u8FC7\u7A0B\u3002</p><p><strong>\u73B0\u5B9E\u95EE\u9898</strong> \u5BF9\u6211\u800C\u8A00(\u5C3D\u7BA1\u4E0D\u662F\u6240\u6709\u4EBA),\u5173\u952E\u8BBA\u70B9\u7684\u6B63\u786E\u4E0E\u5426\u5F52\u7ED3\u4E3A\u4E00\u4E2A\u73B0\u5B9E\u95EE\u9898:\u6574\u4E2A\u8F6F\u4EF6\u5F00\u53D1\u5DE5\u4F5C\u4E2D\u7684\u54EA\u4E9B\u90E8\u5206\u4E0E\u6982\u5FF5\u6027\u7ED3\u6784\u7684\u7CBE\u786E\u548C\u6709\u5E8F\u8868\u8FBE\u76F8\u5173,\u54EA\u4E9B\u90E8\u5206\u662F\u521B\u9020\u90A3\u4E9B\u7ED3\u6784\u7684\u601D\u7EF4\u6D3B\u52A8?\u6839\u636E\u7F3A\u9677\u662F\u6982\u5FF5\u6027\u7684(\u4F8B\u5982\u672A\u80FD\u8BC6\u522B\u67D0\u4E9B\u5F02\u5E38),\u6216\u8005\u662F\u8868\u8FBE\u4E0A\u7684\u95EE\u9898(\u4F8B\u5982\u6307\u9488\u9519\u8BEF\u6216\u8005\u5185\u5B58\u5206\u914D\u9519\u8BEF)\u7B49,\u53EF\u4EE5\u5C06\u8FD9\u4E9B\u7F3A\u9677\u7684\u5BFB\u627E\u548C\u4FEE\u590D\u5DE5\u4F5C\u8FDB\u884C\u76F8\u5E94\u7684\u5212\u5206\u3002</p><p>\u5728\u6211\u770B\u6765,\u5F00\u53D1\u7684\u6B21\u8981\u6216\u8005\u8868\u8FBE\u90E8\u5206\u73B0\u5728\u5DF2\u7ECF\u4E0B\u964D\u5230\u6574\u4E2A\u5DE5\u4F5C\u7684\u4E00\u534A\u6216\u4E00\u534A\u4EE5\u4E0B\u3002\u7531\u4E8E\u8FD9\u90E8\u5206\u662F\u73B0\u5B9E\u7684\u95EE\u9898,\u6240\u4EE5\u539F\u5219\u4E0A\u53EF\u4EE5\u5E94\u7528\u6D4B\u91CF\u6280\u672F\u6765\u7814\u7A76 \u3002\u8FD9\u6837,\u6211\u7684\u89C2\u70B9\u4E5F\u53EF\u4EE5\u901A\u8FC7\u6765\u66F4\u79D1\u5B66\u548C\u66F4\u65B0\u7684\u4F30\u8BA1\u6765\u7EA0\u6B63\u3002\u503C\u5F97\u6CE8\u610F\u7684\u662F,\u8FD8\u6CA1\u6709\u4EBA\u516C\u5F00\u53D1\u8868\u6216\u8005\u5199\u4FE1\u544A\u8BC9\u6211,\u6B21\u8981\u90E8\u5206\u7684\u4EFB\u52A1\u5360\u636E\u4E86\u5DE5\u4F5C\u7684 9/10\u3002</p><p>\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u65E0\u53EF\u4E89\u8FA9\u5730\u6307\u51FA,\u5982\u679C\u5F00\u53D1\u7684\u6B21\u8981\u90E8\u5206\u5C11\u4E8E\u6574\u4E2A\u5DE5\u4F5C\u7684 9/10,\u90A3\u4E48\u5373\u4F7F\u4E0D\u5360\u7528\u4EFB\u4F55\u65F6\u95F4(\u9664\u975E\u51FA\u73B0 \u5947\u8FF9 ),\u4E5F\u4E0D\u4F1A\u7ED9\u751F\u4EA7\u7387\u5E26\u6765\u6570\u91CF\u7EA7\u7684\u63D0\u9AD8\u3002\u56E0\u6B64, \u5FC5\u987B \u7740\u624B\u89E3\u51B3\u5F00\u53D1\u7684\u6839\u672C\u95EE\u9898\u3002</p><p>\u7531\u4E8E\u300A\u6CA1\u6709\u94F6\u5F39\u300B,Bruce Blum \u628A\u6211\u7684\u6CE8\u610F\u529B\u5F15\u5411 Herzberg\u3001Mausner \u548C Sayderman\u7B49\u4EBA\u5728 1959 \u5E74\u7684\u7814\u7A76\u3002\u4ED6\u4EEC\u53D1\u73B0\u52A8\u673A\u56E0\u7D20 \u53EF\u4EE5 \u63D0\u9AD8\u751F\u4EA7\u7387\u3002\u53E6\u4E00\u65B9\u9762,\u73AF\u5883\u548C\u6B21\u8981\u56E0\u7D20,\u65E0\u8BBA\u8D77\u5230\u591A\u4E48\u79EF\u6781\u7684\u4F5C\u7528,\u4ECD\u65E0\u6CD5\u63D0\u9AD8\u751F\u4EA7\u7387\u3002\u4F46\u662F\u5728\u4EA7\u751F\u8D1F\u9762\u5F71\u54CD\u65F6,\u5B83\u4EEC\u4F1A\u4F7F\u751F\u4EA7\u7387\u964D\u4F4E\u3002\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u8BA4\u4E3A\u5F88\u591A\u8F6F\u4EF6\u5F00\u53D1\u8FC7\u7A0B\u5DF2\u7ECF\u6D88\u9664\u4E86\u4EE5\u4E0B\u8D1F\u9762\u56E0\u7D20:\u5341\u5206\u7B28\u62D9\u7684\u673A\u5668\u8BED\u8A00\u3001\u6F2B\u957F\u7684\u6279\u5904\u7406\u5468\u8F6C\u65F6\u95F4\u4EE5\u53CA\u65E0\u6CD5\u5FCD\u53D7\u7684\u5185\u5B58\u9650\u5236\u3002</p><p><strong>\u56E0\u4E3A\u662F\u6839\u672C\u56F0\u96BE\u6240\u4EE5\u6CA1\u6709\u5E0C\u671B</strong>? 1990 \u5E74 Brad Cox \u7684\u4E00\u7BC7\u975E\u5E38\u51FA\u8272\u7684\u8BBA\u6587\u300A\u8FD9\u5C31\u662F\u94F6\u5F39\u300B(There Is a Silver Bullet),\u6709\u8BF4\u670D\u529B\u5730\u6307\u51FA\u91CD\u7528\u548C\u4EA4\u4E92\u7684\u6784\u4EF6\u5F00\u53D1\u662F\u89E3\u51B3\u8F6F\u4EF6\u6839\u672C\u56F0\u96BE\u7684\u4E00\u79CD\u65B9\u6CD5 \u3002\u6211\u7531\u8877\u5730\u8868\u793A\u8D5E\u540C\u3002</p><p>\u4E0D\u8FC7,Cox \u5728\u4E24\u70B9\u4E0A\u8BEF\u89E3\u4E86\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u3002\u9996\u5148,\u4ED6\u65AD\u5B9A\u8F6F\u4EF6\u56F0\u96BE\u6765\u81EA\u201C\u7F16\u7A0B\u4EBA\u5458\u7F3A\u4E4F\u6784\u5EFA\u5F53\u4ECA\u8F6F\u4EF6\u7684\u6280\u672F\u201D\u3002\u800C\u6211\u8BA4\u4E3A\u6839\u672C\u56F0\u96BE\u662F\u56FA\u6709\u7684\u6982\u5FF5\u590D\u6742\u6027,\u65E0\u8BBA\u662F\u4EFB\u4F55\u65F6\u95F4,\u4F7F\u7528\u4EFB\u4F55\u65B9\u6CD5\u8BBE\u8BA1\u548C\u5B9E\u73B0\u8F6F\u4EF6\u7684\u529F\u80FD,\u5B83\u90FD\u5B58\u5728\u3002\u5176\u6B21,\u4ED6(\u4EE5\u53CA\u5176\u4ED6\u4EBA)\u9605\u8BFB\u300A\u6CA1\u6709\u94F6\u5F39\u300B,\u5E76\u8BA4\u5B9A\u6587\u4E2D\u7684\u89C2\u70B9\u662F\u6CA1\u6709\u4EFB\u4F55\u5904\u7406\u8F6F\u4EF6\u5F00\u53D1\u4E2D\u6839\u672C\u56F0\u96BE\u7684\u5E0C\u671B\u2014\u2014\u8FD9\u4E0D\u662F\u6211\u7684\u672C\u610F\u3002\u4F5C\u4E3A\u672C\u8D28\u4E0A\u7684\u56F0\u96BE,\u6784\u601D\u8F6F\u4EF6\u6982\u5FF5\u6027\u7684\u7ED3\u6784\u672C\u8EAB\u5C31\u6709\u590D\u6742\u6027\u3001\u4E00\u81F4\u6027\u3001\u53EF\u53D8\u6027\u53CA\u4E0D\u53EF\u89C1\u6027\u7684\u7279\u70B9\u3002\u4E0D\u8FC7\u5B9E\u9645\u4E0A,\u6BCF\u4E00\u79CD\u56F0\u96BE\u4EA7\u751F\u7684\u9EBB\u70E6\u90FD\u662F\u53EF\u4EE5\u6539\u5584\u7684\u3002</p><p><strong>\u590D\u6742\u6027\u662F\u5C42\u6B21\u5316\u7684</strong>\u3002\u4F8B\u5982,\u590D\u6742\u6027\u662F\u6700\u4E25\u91CD\u7684\u5185\u5728\u56F0\u96BE,\u5E76\u4E0D\u662F\u6240\u6709\u7684\u590D\u6742\u6027\u90FD\u662F\u4E0D\u53EF\u907F\u514D\u7684\u3002\u6211\u4EEC\u7684\u5F88\u591A\u8F6F\u4EF6,\u4F46\u4E0D\u662F\u5168\u90E8,\u6765\u81EA\u5E94\u7528\u672C\u8EAB\u968F\u610F\u7684\u590D\u6742\u7279\u6027\u3002\u6765\u81EA\u4E00\u5BB6\u56FD\u9645\u7BA1\u7406\u54A8\u8BE2\u516C\u53F8,MYSIGMA Lars Sodahl \u7684 Lars Sodahl \u548C\u5408\u4F5C\u4F19\u4F34\u66FE\u5199\u9053:</p><blockquote><p>\u5C31\u6211\u7684\u7ECF\u9A8C\u800C\u8A00,\u5728\u7CFB\u7EDF\u5DE5\u4F5C\u4E2D\u6240\u9047\u5230\u7684\u5927\u591A\u6570\u56F0\u96BE\u662F\u7EC4\u7EC7\u7ED3\u6784\u4E0A\u7684\u4E00\u4E9B\u5931\u8BEF\u5F81\u5146\u3002\u8BD5\u56FE\u4E3A\u8FD9\u4E9B\u73B0\u5B9E\u5EFA\u6A21,\u5EFA\u7ACB\u540C\u7B49\u590D\u6742\u7684\u7A0B\u5E8F,\u5B9E\u9645\u4E0A\u662F\u9690\u85CF,\u800C\u4E0D\u662F\u89E3\u51B3\u8FD9\u4E9B\u6742\u4E71\u65E0\u7AE0\u7684\u60C5\u51B5\u3002</p><p>Northrop \u7684 Steve Lukasik \u8BA4\u4E3A\u5373\u4F7F\u662F\u7EC4\u7EC7\u673A\u6784\u4E0A\u7684\u590D\u6742\u6027\u4E5F\u4E0D\u662F\u4EFB\u610F\u7684,\u53EF\u80FD\u5BB9\u6613\u53D7\u5230\u7B56\u7565\u8C03\u6574\u7684\u5F71\u54CD\u3002</p><p>\u6211\u66FE\u4F5C\u4E3A\u7269\u7406\u5B66\u5BB6\u63A5\u53D7\u8FC7\u57F9\u8BAD,\u56E0\u6B64\u503E\u5411\u4E8E\u7528\u66F4\u7B80\u5355\u7684\u6982\u5FF5\u6765\u63CF\u8FF0\u201C\u590D\u6742\u201D\u4E8B\u7269\u3002\u73B0\u5728\u4F60\u53EF\u80FD\u662F\u6B63\u786E\u7684,\u6211\u65E0\u6CD5\u65AD\u5B9A\u6240\u6709\u7684\u590D\u6742\u4E8B\u7269\u90FD\u5BB9\u6613\u7528\u6709\u5E8F\u7684\u89C4\u5F8B\u8868\u8FBE......\u540C\u6837\u7684\u9053\u7406,\u4F60\u4E0D\u80FD\u65AD\u5B9A\u5B83\u4EEC\u4E0D\u80FD \u3002</p><p>......\u6628\u5929\u7684\u590D\u6742\u6027\u662F\u4ECA\u5929\u7684\u89C4\u5F8B\u3002\u5206\u5B50\u7684\u65E0\u5E8F\u6027\u542F\u8FEA\u4E86\u6C14\u4F53\u52A8\u529B\u5B66\u7406\u8BBA\u548C\u70ED\u529B\u5B66\u7684\u4E09\u5927\u5B9A\u5F8B\u3002\u73B0\u5728,\u8F6F\u4EF6\u6CA1\u6709\u63ED\u793A\u7C7B\u4F3C\u7684\u89C4\u5F8B\u6027\u539F\u7406,\u4F46\u662F\u89E3\u91CA\u4E3A\u4EC0\u4E48\u6CA1\u6709\u7684\u91CD\u62C5\u5728\u4F60\u7684\u8EAB\u4E0A\u3002\u6211\u4E0D\u662F\u8FDF\u949D\u548C\u597D\u8FA9\u7684\u3002\u6211\u76F8\u4FE1\u6709\u4E00\u5929\u8F6F\u4EF6\u7684\u201C\u590D\u6742\u6027\u201D\u5C06\u4EE5\u67D0\u79CD\u66F4\u9AD8\u7EA7\u7684\u89C4\u5F8B\u6027\u6982\u5FF5\u6765\u8868\u8FBE(\u5C31\u50CF\u7269\u7406\u5B66\u5BB6\u7684\u4E0D\u53D8\u5F0F)\u3002</p></blockquote><p>\u6211\u5E76\u6CA1\u6709\u7740\u624B\u4E8E Lukasik \u63D0\u5021\u7684\u66F4\u6DF1\u5C42\u6B21\u7684\u5206\u6790\u3002\u4F5C\u4E3A\u4E00\u4E2A\u5B66\u79D1,\u6211\u4EEC\u9700\u8981\u66F4\u5E7F\u6CDB\u7684\u4FE1\u606F\u7406\u8BBA,\u5B83\u80FD\u591F\u91CF\u5316\u9759\u6001\u7ED3\u6784\u7684\u4FE1\u606F\u5185\u5BB9,\u5C31\u50CF\u9488\u5BF9\u4EA4\u4E92\u6D41\u7684\u9999\u519C\u4FE1\u606F\u8BBA\u4E00\u6837\u3002\u8FD9\u5DF2\u7ECF\u8D85\u8D8A\u4E86\u6211\u7684\u80FD\u529B\u3002\u4F5C\u4E3A\u5BF9 Lukasik \u7684\u7B80\u5355\u56DE\u5E94,\u6211\u8BA4\u4E3A\u7CFB\u7EDF\u590D\u6742\u6027\u662F\u65E0\u6570\u7EC6\u8282\u7684\u51FD\u6570,\u8FD9\u4E9B\u7EC6\u8282\u5FC5\u987B\u7CBE\u786E\u800C\u4E14\u8BE6\u7EC6\u5730\u8BF4\u660E\u2014\u2014\u6216\u8005\u662F\u501F\u52A9\u67D0\u79CD\u901A\u7528\u89C4\u5219,\u6216\u8005\u662F\u9010\u4E00\u9610\u8FF0,\u4F46\u51B3\u4E0D\u4EC5\u4EC5\u662F\u7EDF\u8BA1\u8BF4\u660E\u3002\u4EC5\u9760\u82E5\u5E72\u4EBA\u4E0D\u76F8\u5E72\u7684\u5DE5\u4F5C,\u662F\u4E0D\u5927\u53EF\u80FD\u4EA7\u751F\u8DB3\u591F\u7684\u4E00\u81F4\u6027,\u80FD\u7528\u901A\u7528\u89C4\u5F8B\u8FDB\u884C\u7CBE\u786E\u63CF\u8FF0\u3002</p><p>\u4E0D\u8FC7,\u5F88\u591A\u590D\u6742\u6027\u5E76\u4E0D\u5B8C\u5168\u662F\u56E0\u4E3A\u548C\u5916\u90E8\u4E16\u754C\u4FDD\u6301\u4E00\u81F4,\u800C\u662F\u56E0\u4E3A\u5B9E\u73B0\u7684\u672C\u8EAB,\u4F8B\u5982\u6570\u636E\u7ED3\u6784\u3001\u7B97\u6CD5\u3001\u4E92\u8054\u6027\u7B49\u3002\u800C\u5728\u66F4\u9AD8\u7684\u7EA7\u522B\u5F00\u53D1(\u53D1\u5C55)\u8F6F\u4EF6,\u4F7F\u7528\u5176\u4ED6\u4EBA\u7684\u6210\u679C,\u6216\u8005\u91CD\u7528\u81EA\u5DF1\u7684\u7A0B\u5E8F\u2014\u2014\u90FD\u80FD\u907F\u514D\u9762\u5BF9\u6574\u4E2A\u5C42\u6B21\u7684\u590D\u6742\u6027\u3002\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u63D0\u51FA\u4E86\u5168\u529B\u89E3\u51B3\u590D\u6742\u6027\u95EE\u9898\u7684\u65B9\u6CD5,\u8FD9\u79CD\u65B9\u6CD5\u53EF\u4EE5\u5728\u73B0\u5B9E\u4E2D\u53D6\u5F97\u5341\u5206\u4E50\u89C2\u7684\u8FDB\u5C55\u3002\u5B83\u5021\u5BFC\u5411\u8F6F\u4EF6\u7CFB\u7EDF\u589E\u52A0\u5FC5\u8981\u7684\u590D\u6742\u6027:</p><ul><li>\u5C42\u6B21\u5316,\u901A\u8FC7\u5206\u5C42\u7684\u6A21\u5757\u6216\u8005\u5BF9\u8C61\u3002</li><li>\u589E\u91CF\u5316,\u4ECE\u800C\u7CFB\u7EDF\u53EF\u4EE5\u6301\u7EED\u5730\u8FD0\u884C\u3002</li></ul><h2 id="harel-\u7684\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#harel-\u7684\u5206\u6790" aria-hidden="true">#</a> Harel \u7684\u5206\u6790</h2><p>David Harel,\u5728 1992 \u5E74\u7684\u8BBA\u6587\u300A\u6279\u8BC4\u94F6\u5F39\u300B(Biting the Silver Bullet)\u4E2D,\u5BF9\u5DF2\u51FA\u7248\u7684\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u8FDB\u884C\u4E86\u5F88\u591A\u6700\u4ED4\u7EC6\u7684\u5206\u6790\u3002</p><p><strong>\u60B2\u89C2\u4E3B\u4E49 vs. \u4E50\u89C2\u4E3B\u4E49 vs. \u73B0\u5B9E\u4E3B\u4E49\u3002</strong> Harel \u540C\u65F6\u9605\u8BFB\u4E86\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u548C 1984 \u5E74 Parnas \u7684\u6587\u7AE0\u300A\u6218\u7565\u9632\u536B\u7CFB\u7EDF\u7684\u8F6F\u4EF6\u95EE\u9898\u300B(Software Aspects of Strategic Defense (Toward Systems ),\u8BA4\u4E3A\u5B83\u4EEC\u201C\u592A\u8FC7\u9EEF\u6DE1\u201D\u3002\u56E0\u6B64,\u4ED6\u8BD5\u56FE\u5728\u8BBA\u6587\u300A\u8D70\u5411\u7CFB\u7EDF\u5F00\u53D1\u7684\u5149\u660E\u672A\u6765\u300Ba Brighter Future for System Development)\u4E2D\u5C55\u73B0\u5176\u660E\u4EAE\u7684\u4E00\u9762\u3002Cox \u540C Halel \u4E00\u6837\u8BA4\u4E3A\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u4E00\u6587\u8FC7\u4E8E\u60B2\u89C2,\u4ECE\u800C\u4ED6\u63D0\u51FA\u201C\u4F46\u662F,\u5982\u679C\u4ECE\u4E00\u4E2A\u65B0\u89C6\u70B9\u53BB\u89C2\u5BDF\u76F8\u540C\u7684\u4E8B\u60C5,\u4F60\u4F1A\u5F97\u5230\u4E00\u4E2A\u66F4\u52A0\u4E50\u89C2\u7684\u7ED3\u8BBA\u201D\u3002\u4ED6\u4EEC\u7684\u8BBA\u8C03\u90FD\u6709\u4E9B\u95EE\u9898\u3002</p><p>\u9996\u5148,\u6211\u7684\u59BB\u5B50\u3001\u540C\u4E8B\u548C\u6211\u7684\u7F16\u8F91\u53D1\u73B0\u6211\u72AF\u4E50\u89C2\u4E3B\u4E49\u9519\u8BEF\u7684\u51E0\u7387\u8FDC\u8FDC\u5927\u4E8E\u60B2\u89C2\u4E3B\u4E49\u3002\u6BD5\u7ADF,\u6211\u7684\u4ECE\u4E1A\u80CC\u666F\u662F\u7A0B\u5E8F\u5458,\u4E50\u89C2\u4E3B\u4E49\u662F\u8FD9\u4E2A\u884C\u4E1A\u7684\u804C\u4E1A\u75C5\u3002\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u4E00\u6587\u660E\u786E\u5730\u6307\u51FA\u201C\u6211\u4EEC\u770B\u770B\u8FD1\u5341\u5E74\u6765\u7684\u60C5\u51B5,\u6CA1\u6709\u94F6\u5F39\u7684\u8E2A\u8FF9......\u6000\u7591\u8BBA\u8005\u5E76\u4E0D\u662F\u60B2\u89C2\u4E3B\u4E49\u8005......\u867D\u7136\u6CA1\u6709\u901A\u5929\u5927\u9053,\u4F46\u662F\u8DEF\u5C31\u5728\u811A\u4E0B\u3002\u201D\u5B83\u9884\u8A00\u4E86\u5982\u679C 1986 \u5E74\u7684\u5F88\u591A\u521B\u65B0\u80FD\u6301\u7EED\u5F00\u62D3\u548C\u53D1\u5C55,\u90A3\u4E48\u5B9E\u9645\u4E0A\u5B83\u4EEC\u7684 \u5171\u540C \u4F5C\u7528\u80FD\u4F7F\u751F\u4EA7\u7387\u83B7\u5F97\u6570\u91CF\u7EA7\u7684\u63D0\u9AD8\u3002\u968F\u7740 1986~1996 \u5341\u5E74\u8FC7\u53BB\u4E86,\u8FD9\u4E2A\u9884\u8A00\u5373\u4F7F\u8BF4\u660E\u4E86\u4EC0\u4E48,\u90A3\u4E5F\u662F\u8FC7\u4E8E\u4E50\u89C2,\u800C\u4E0D\u662F\u8FC7\u4E8E\u60B2\u89C2\u3002</p><p>\u5C31\u7B97\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u603B\u4F53\u770B\u6765\u6709\u4E9B\u60B2\u89C2,\u90A3\u4E48\u5230\u5E95\u5B58\u5728\u4EC0\u4E48\u95EE\u9898?\u662F\u5426\u7231\u56E0\u65AF\u5766\u5173\u4E8E\u4EFB\u4F55\u7269\u4F53\u8FD0\u52A8\u7684\u901F\u5EA6\u65E0\u6CD5\u8D85\u8FC7\u5149\u901F\u7684\u8BBA\u65AD\u8FC7\u4E8E\u201C\u9EEF\u6DE1\u201D\u6216\u8005\u201C\u4EE4\u4EBA\u6CAE\u4E27\u201D\u5462?\u90A3\u4E48\u54E5\u5FB7\u5C14\u5173\u4E8E\u67D0\u4E9B\u4E8B\u7269\u65E0\u6CD5\u8BA1\u7B97\u7684\u7ED3\u8BBA,\u53C8\u5982\u4F55\u5462?\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u4E00\u6587\u8BA4\u4E3A\u201C\u8F6F\u4EF6\u7684\u7279\u6027\u672C\u8EAB\u5BFC\u81F4\u4E86\u4E0D\u5927\u53EF\u80FD\u6709\u4EFB\u4F55\u7684\u94F6\u5F39\u201D\u3002Tuski \u5728 IFIP \u5927\u4F1A\u4E0A\u53D1\u8868\u4E86\u4E00\u7BC7\u8BBA\u6587\u4F5C\u4E3A\u51FA\u8272\u7684\u56DE\u5E94,\u6587\u4E2D\u6307\u51FA:</p><blockquote><p>\u5728\u6240\u6709\u88AB\u8BEF\u5BFC\u7684\u79D1\u5B66\u63A2\u7D22\u4E2D,\u6700\u60B2\u60E8\u7684\u83AB\u8FC7\u4E8E\u5BF9\u4E00\u79CD\u80FD\u591F\u5C06\u4E00\u822C\u91D1\u5C5E\u53D8\u6210\u91D1\u5B50\u7684\u7269\u8D28,\u5373\u70B9\u91D1\u77F3\u7684\u7814\u7A76\u3002\u8FD9\u4E2A\u7531\u7EDF\u6CBB\u8005\u4E0D\u65AD\u5730\u6295\u5165\u91D1\u94B1,\u88AB\u4E00\u4EE3\u4EE3\u7684\u7814\u7A76\u8005\u4E0D\u61C8\u8FFD\u6C42\u7684\u3001\u70BC\u91D1\u672F\u4E2D\u81F3\u9AD8\u65E0\u4E0A\u7684\u6CD5\u5B9D,\u662F\u4E00\u79CD\u4ECE\u7406\u60F3\u5316\u60F3\u8C61\u548C\u666E\u904D\u5047\u8BBE\u4E2D\u2014\u2014\u4EE5\u4E3A\u4E8B\u60C5\u4F1A\u50CF\u6211\u4EEC\u6240\u8BA4\u4E3A\u7684\u90A3\u6837\u2014\u2014\u63D0\u53D6\u51FA\u7684\u7CBE\u534E\u3002\u5B83\u662F\u4EBA\u7C7B\u7EAF\u7CB9\u4FE1\u4EF0\u7684\u4F53\u73B0,\u4EBA\u4EEC\u82B1\u8D39\u4E86\u5927\u91CF\u7684\u65F6\u95F4\u548C\u7CBE\u529B\u6765\u8BA4\u53EF\u548C\u63A5\u53D7\u8FD9\u4E2A\u65E0\u6CD5\u89E3\u51B3\u7684\u95EE\u9898\u3002\u5373\u4F7F\u88AB\u8BC1\u660E\u662F\u4E0D\u5B58\u5728,\u90A3\u79CD\u5BFB\u627E\u51FA\u8DEF\u548C\u5E0C\u671B\u80FD\u4E00\u52B3\u6C38\u9038\u7684\u613F\u671B,\u4F9D\u7136\u5341\u5206\u7684\u5F3A\u70C8\u3002\u800C\u6211\u4EEC\u4E2D\u7684\u7EDD\u5927\u591A\u6570\u603B\u662F\u5F88\u540C\u60C5\u8FD9\u4E9B\u660E\u77E5\u4E0D\u53EF\u4E3A\u800C\u4E3A\u4E4B\u7684\u4EBA,\u56E0\u6B64\u5B83\u4EEC\u603B\u662F\u5F97\u4EE5\u5EF6\u7EED\u3002\u6240\u4EE5,\u5C06\u5706\u5F62\u53D8\u65B9\u7684\u8BBA\u6587\u88AB\u53D1\u8868,\u6062\u590D\u8131\u53D1\u7684\u6D17\u6DB2\u88AB\u7814\u5236\u548C\u51FA\u552E,\u63D0\u9AD8\u8F6F\u4EF6\u751F\u4EA7\u7387\u7684\u65B9\u6CD5\u88AB\u63D0\u51FA\u5E76\u6210\u529F\u5730\u63A8\u9500\u3002</p><p>\u6211\u4EEC\u592A\u8FC7\u503E\u5411\u4E8E\u9075\u5FAA\u6211\u4EEC\u81EA\u5DF1\u7684\u4E50\u89C2\u4E3B\u4E49(\u6216\u8005\u662F\u53D1\u6398\u6211\u4EEC\u51FA\u8D44\u4EBA\u7684\u4E50\u89C2\u4E3B\u4E49)\u3002\u6211\u4EEC\u592A\u559C\u6B22\u5FFD\u89C6\u771F\u7406\u7684\u58F0\u97F3,\u800C\u53BB\u542C\u4ECE\u4E07\u7075\u836F\u8D29\u5356\u8005\u7684\u8BF1\u60D1</p></blockquote><p>\u6211\u548C Turski \u90FD\u575A\u6301\u8BA4\u4E3A\u8FD9\u4E2A\u767D\u65E5\u68A6 \u9650\u5236\u4E86\u5411\u524D\u7684\u53D1\u5C55,\u6D6A\u8D39\u4E86\u7CBE\u529B\u3002</p><p><strong>\u201C\u6D88\u6781\u201D\u4E3B\u9898\u3002</strong> Harel \u8BA4\u8BC6\u5230\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u4E2D\u7684\u6D88\u6781\u6765\u81EA\u4E09\u4E2A\u4E3B\u9898:</p><ul><li>\u6839\u672C\u548C\u6B21\u8981\u95EE\u9898\u7684\u6E05\u6670\u5212\u5206</li><li>\u72EC\u7ACB\u5730\u8BC4\u4EF7\u6BCF\u4E2A\u5019\u9009\u94F6\u5F39</li><li>\u4EC5\u4EC5\u9884\u8A00\u4E86\u5341\u5E74,\u800C\u4E0D\u662F\u8DB3\u591F\u957F\u7684\u65F6\u95F4\u201C\u51FA\u73B0\u4EFB\u4F55\u91CD\u5927\u7684\u8FDB\u6B65\u3002\u201D</li></ul><p>\u7B2C\u4E00\u4E2A\u4E3B\u9898,\u5B83\u662F\u6574\u7BC7\u6587\u7AE0\u7684\u4E3B\u8981\u89C2\u70B9\u3002\u6211\u4ECD\u7136\u8BA4\u4E3A\u4E0A\u8FF0\u5212\u5206\u5BF9\u4E8E\u7406\u89E3\u4E3A\u4EC0\u4E48\u8F6F\u4EF6\u96BE\u4EE5\u5F00\u53D1\u662F\u7EDD\u5BF9\u5173\u952E\u7684\u3002\u5BF9\u4E8E\u5E94\u8BE5\u505A\u51FA\u54EA\u4E9B\u65B9\u9762\u7684\u6539\u8FDB,\u5B83\u4E5F\u662F\u5341\u5206\u660E\u786E\u7684\u6307\u5357\u3002\u81F3\u4E8E\u72EC\u7ACB\u5730\u8003\u8651\u4E0D\u540C\u7684\u5019\u9009\u94F6\u5F39,\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u5E76\u975E\u5982\u6B64\u3002\u5404\u79CD\u5404\u6837\u7684\u6280\u672F\u4E00\u4E2A\u63A5\u4E00\u4E2A\u5730\u88AB\u63D0\u51FA,\u6BCF\u4E00\u79CD\u90FD\u8FC7\u5206\u5BA3\u626C \u81EA\u8EAB \u7684\u6548\u679C\u3002\u56E0\u6B64,\u4F9D\u6B21\u72EC\u7ACB\u7684\u8BC4\u4F30\u5B83\u4EEC\u662F\u975E\u5E38\u516C\u5E73\u7684\u3002\u6211\u6301\u53CD\u5BF9\u6001\u5EA6\u7684\u5E76\u4E0D\u662F\u8FD9\u4E9B\u6280\u672F,\u800C\u662F\u90A3\u79CD\u5B83\u4EEC\u80FD\u8D77\u5230\u9B54\u672F\u822C\u4F5C\u7528\u7684\u89C2\u70B9\u3002Glass\u3001Vessey \u548C Conger \u5728\u4ED6\u4EEC 1992 \u5E74\u7684\u8BBA\u6587\u4E2D\u63D0\u4F9B\u4E86\u5145\u8DB3\u7684\u8BC1\u636E,\u6307\u51FA\u5BF9\u94F6\u5F39\u7684\u65E0\u8C13\u7814\u7A76\u4ECD\u672A\u7ED3\u675F \u3002</p><p>\u5173\u4E8E\u9009\u62E9 10 \u5E74\u8FD8\u662F 40 \u5E74\u4F5C\u4E3A\u9884\u8A00\u7684\u671F\u9650,\u9009\u62E9\u8F83\u77ED\u7684\u65F6\u95F4\u662F\u627F\u8BA4\u6211\u4EEC\u5E76\u6CA1\u6709\u8DB3\u591F\u5F3A\u7684\u80FD\u529B\u53EF\u4EE5\u9884\u89C1\u5230\u5341\u5E74\u4EE5\u540E\u7684\u4E8B\u60C5\u3002\u6211\u4EEC\u4E2D\u95F4\u6709\u8C01\u53EF\u4EE5\u5728 1975 \u5E74\u9884\u89C1\u5230 80 \u5E74\u4EE3\u7684\u5FAE\u578B\u8BA1\u7B97\u673A\u9769\u547D\u5417?</p><p>\u5BF9\u4E8E\u5341\u5E74\u7684\u671F\u9650,\u8FD8\u6709\u5176\u4ED6\u7684\u4E00\u4E9B\u539F\u56E0:\u5404\u79CD\u94F6\u5F39\u90FD\u5BA3\u79F0\u5B83\u4EEC\u80FD\u591F\u7ACB\u523B\u53D6\u5F97\u6548\u679C\u3002\u6211\u56DE\u987E\u4E86\u4E00\u4E0B,\u53D1\u73B0\u6CA1\u6709\u4EFB\u4F55\u4E00\u79CD\u94F6\u5F39\u58F0\u79F0\u201C\u5411\u6211\u7684\u79D8\u65B9\u6295\u8D44,\u5728\u5341\u5E74\u540E\u4F60\u5C06\u83B7\u5F97\u6210\u529F\u201D\u3002\u53E6\u5916,\u786C\u4EF6\u7684\u6027\u80FD/\u4EF7\u683C\u6BD4\u53EF\u80FD\u6BCF\u5341\u5E74\u5C31\u4F1A\u6709\u6210\u767E\u500D\u7684\u589E\u957F,\u5C3D\u7BA1\u8FD9\u79CD\u6BD4\u8F83\u4E0D\u5F88\u5408\u9002,\u4F46\u662F\u76F4\u89C9\u4E0A\u7684\u786E\u5982\u6B64\u3002\u6211\u4EEC\u786E\u4FE1\u4F1A\u5728\u4E0B\u4E00\u4E2A 40 \u5E74\u4E2D\u53D6\u5F97\u7A33\u6B65\u7684\u53D1\u5C55\u3002\u4E0D\u8FC7,\u4EE5 40 \u5E74\u4EE3\u4EF7\u53D6\u5F97\u6570\u91CF\u7EA7\u7684\u8FDB\u5C55,\u5F88\u96BE\u88AB\u8BA4\u4E3A\u662F\u4E0D\u53EF\u601D\u8BAE\u7684\u8FDB\u6B65\u3002</p><p><strong>\u60F3\u8C61\u7684\u8BD5\u9A8C\u3002</strong> Harel \u5EFA\u8BAE\u4E86\u4E00\u79CD\u60F3\u8C61\u7684\u8BD5\u9A8C,\u4ED6\u5047\u8BBE\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u662F\u53D1\u8868\u5728 1952 \u5E74,\u800C\u4E0D\u662F 1986 \u5E74,\u4E0D\u8FC7\u8868\u8FBE\u7684\u8BBA\u65AD\u76F8\u540C\u3002\u4ED6\u4F7F\u7528 \u53CD\u8BC1\u6CD5 \u6765\u8BC1\u660E\u5C06\u6839\u672C\u548C\u6B21\u8981\u95EE\u9898\u5206\u5F00\u662F\u4E0D\u6070\u5F53\u7684\u3002</p><p>\u8FD9\u79CD\u89C2\u70B9\u7AD9\u4E0D\u4F4F\u811A\u3002\u9996\u5148,\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u4E00\u5F00\u59CB\u5C31\u58F0\u79F0,50 \u5E74\u4EE3\u7684\u7A0B\u5E8F\u5F00\u53D1\u4E2D\u66FE\u5360\u652F\u914D\u5730\u4F4D\u7684\u6B21\u8981\u56F0\u96BE,\u5982\u4ECA\u5DF2\u7ECF\u4E0D\u5B58\u5728\u4E86,\u5E76\u4E14\u6D88\u9664\u8FD9\u4E9B\u56F0\u96BE\u5DF2\u7ECF\u4EA7\u751F\u4E86\u63D0\u9AD8\u82E5\u5E72\u6570\u91CF\u7EA7\u7684\u6548\u679C\u3002\u5C06\u8FA9\u8BBA\u63A8\u56DE\u5230 40 \u5E74\u524D\u662F\u4E0D\u5408\u7406\u7684,\u5728 1952 \u5E74,\u751A\u81F3\u5F88\u96BE\u60F3\u8C61\u5F00\u53D1\u7684\u6B21\u8981\u95EE\u9898\u4E0D\u4F1A\u5360\u636E\u5F00\u53D1\u5DE5\u4F5C\u7684\u4E3B\u8981\u90E8\u5206\u3002</p><p>\u5176\u6B21,Harel \u6240\u8BBE\u60F3 50 \u5E74\u4EE3\u884C\u4E1A\u6240\u5904\u72B6\u6001\u662F\u4E0D\u51C6\u786E\u7684:</p><blockquote><p>\u5F53\u65F6\u5DF2\u7ECF\u4E0D\u662F\u6784\u5EFA\u5927\u578B\u590D\u6742\u7CFB\u7EDF\u7684\u65F6\u4EE3,\u7A0B\u5E8F\u5458\u7684\u5DE5\u4F5C\u6A21\u5F0F\u5DF2\u7ECF\u6210\u4E3A\u5E38\u89C4\u4E2A\u4EBA\u7A0B\u5E8F\u7684\u5F00\u53D1(\u5728\u73B0\u4EE3\u7684\u7F16\u7A0B\u8BED\u8A00\u4E2D,\u5927\u6982\u662F 100~200 \u884C\u4EE3\u7801)\u3002\u5728\u5DF2\u6709\u6280\u672F\u548C\u65B9\u6CD5\u5B66\u7684\u524D\u63D0\u4E0B,\u8FD9\u4E9B\u4EFB\u52A1\u662F\u4EE4\u4EBA\u6050\u6016\u7684,\u5904\u5904\u90FD\u662F\u9519\u8BEF\u3001\u6545\u969C\u548C\u843D\u540E\u7684\u5B8C\u6210\u671F\u9650\u3002</p></blockquote><p>\u63A5\u7740,\u4ED6\u9610\u8FF0\u4E86\u5728\u4F20\u7EDF\u7684\u5C0F\u578B\u4E2A\u4EBA\u7A0B\u5E8F\u4E2D,\u90A3\u4E9B\u5047\u8BBE\u7684\u9519\u8BEF\u3001\u6545\u969C\u548C\u843D\u540E\u7684\u6700\u7EC8\u671F\u9650\u5982\u4F55\u5728\u63A5\u4E0B\u6765\u7684 25 \u5E74\u4E2D,\u5F97\u5230\u6570\u91CF\u7EA7\u7684\u6539\u8FDB\u548C\u63D0\u9AD8\u3002</p><p>\u4F46\u662F,50 \u5E74\u4EE3\u8BE5\u9886\u57DF\u7684\u5B9E\u9645\u60C5\u51B5\u5E76\u4E0D\u662F\u5C0F\u578B\u4E2A\u4EBA\u7A0B\u5E8F\u3002\u5728 1952 \u5E74,Univac \u8FD8\u5728\u4F7F\u7528\u5927\u7EA6 8 \u4EBA\u5F00\u53D1\u7684\u590D\u6742\u7A0B\u5E8F\u5904\u7406 1950 \u5E74\u7684\u4EBA\u53E3\u666E\u67E5 \u3002\u5176\u4ED6\u673A\u5668\u5219\u7528\u4E8E\u5316\u5B66\u52A8\u529B\u5B66\u3001\u4E2D\u5B50\u6F2B\u5C04\u8BA1\u7B97\u3001\u5BFC\u5F39\u6027\u80FD\u8BA1\u7B97\u7B49\u7B49 \u3002\u6C47\u7F16\u8BED\u8A00\u3001\u91CD\u5B9A\u4F4D\u7684\u94FE\u63A5\u548C\u88C5\u8F7D\u7A0B\u5E8F\u3001\u6D6E\u70B9\u89E3\u91CA\u7CFB\u7EDF\u7B49,\u8FD8\u7ECF\u5E38\u88AB\u4F7F\u7528 \u30021955 \u5E74,\u4EBA\u4EEC\u5F00\u53D1 50~100 \u4EBA\u5E74\u7684\u5546\u7528\u7A0B\u5E8F \u30021956 \u5E74,\u901A\u7528\u7535\u6C14\u5728\u8DEF\u6613\u65AF\u7EF4\u5C14\u7684\u8BBE\u5907\u8F66\u95F4\u4F7F\u7528\u7740\u8D85\u8FC7 80,000 \u6307\u4EE4\u7684\u85AA\u8D44\u7CFB\u7EDF\u30021957 \u5E74,SAGE ANFSQ/7 \u9632\u7A7A\u8BA1\u7B97\u673A\u7CFB\u7EDF\u5DF2\u7ECF\u8FD0\u8F6C\u4E86\u4E24\u5E74,\u8FD9\u4E2A\u7CFB\u7EDF\u5206\u5E03\u5728 30 \u4E2A\u4E0D\u540C\u7684\u5730\u70B9,\u662F\u57FA\u4E8E\u901A\u8BAF\u3001\u81EA\u6D88\u9664\u6545\u969C\u7684\u70ED\u5907\u5B9E\u65F6\u7CFB\u7EDF \u3002\u56E0\u6B64,\u51E0\u4E4E\u65E0\u6CD5\u575A\u6301\u8BF4\u4E2A\u4EBA\u7A0B\u5E8F\u7684\u6280\u672F\u9769\u547D,\u80FD\u591F\u7528\u6765\u63CF\u8FF0 1952 \u5E74\u4EE5\u6765\u7684\u8F6F\u4EF6\u5DE5\u7A0B\u4E0A\u7684\u52AA\u529B\u3002</p><p><strong>\u94F6\u5F39\u5C31\u5728\u8FD9\u91CC\u3002</strong> Harel \u63A5\u7740\u63D0\u51FA\u4E86\u4ED6\u81EA\u5DF1\u7684\u94F6\u5F39,\u4E00\u79CD\u79F0\u4E3A\u201C\u9999\u8349(Vanilla)\u6846\u67B6\u201D\u7684\u5EFA\u6A21\u6280\u672F\u3002\u6587\u4E2D\u5E76\u6CA1\u6709\u5BF9\u65B9\u6CD5\u63D0\u4F9B\u8DB3\u591F\u8BC4\u4F30\u7684\u8BE6\u7EC6\u63CF\u8FF0,\u4E0D\u8FC7\u7ED9\u51FA\u4E86\u4E00\u4E9B\u8BBA\u6587\u548C\u53C2\u8003\u8D44\u6599\u3002\u5EFA\u6A21\u6240\u9488\u5BF9\u7684\u786E\u5B9E\u662F\u8F6F\u4EF6\u5F00\u53D1\u7684\u6839\u672C\u56F0\u96BE,\u5373\u6982\u5FF5\u6027\u8981\u7D20\u7684\u8BBE\u8BA1\u548C\u8C03\u8BD5,\u56E0\u6B64 Vanilla \u6846\u67B6\u6709\u53EF\u80FD\u662F\u9769\u547D\u6027\u7684\u3002\u6211\u4E5F\u5E0C\u671B\u5982\u6B64\u3002Ken Brooks \u5728\u62A5\u544A\u4E2D\u63D0\u5230,\u5728\u5B9E\u9645\u5DE5\u4F5C\u4E2D\u5E94\u7528\u65F6,\u5B83\u7684\u786E\u662F\u4E00\u79CD\u9887\u6709\u5E2E\u52A9\u7684\u65B9\u6CD5\u5B66\u3002</p><p><strong>\u4E0D\u53EF\u89C1\u6027\u3002</strong> Harel \u5F3A\u70C8\u5730\u4E3B\u5F20\u8F6F\u4EF6\u7684\u6982\u5FF5\u6027\u8981\u7D20\u672C\u8D28\u4E0A\u662F\u62D3\u6251\u7684,\u8FD9\u4E9B\u5173\u7CFB\u53EF\u4EE5\u7528\u7A7A\u95F4/\u56FE\u5F62\u65B9\u5F0F\u6765\u81EA\u7136\u5730\u8868\u8FBE:</p><blockquote><p>\u9002\u5F53\u4F7F\u7528\u53EF\u89C6\u5316\u56FE\u5F62\u53EF\u4EE5\u7ED9\u5DE5\u7A0B\u5E08\u548C\u7A0B\u5E8F\u5458\u5E26\u6765\u53EF\u89C2\u7684\u6210\u6548\u3002\u800C\u4E14,\u8FD9\u79CD\u6548\u679C\u5E76\u4E0D\u4EC5\u4EC5\u5C40\u9650\u4E8E\u6B21\u8981\u95EE\u9898,\u5F00\u53D1\u4EBA\u5458\u601D\u8003\u548C\u63A2\u7D22\u7684\u8D28\u91CF\u4E5F\u5F97\u5230\u4E86\u6539\u8FDB\u3002\u672A\u6765\u7684\u6210\u529F\u7CFB\u7EDF\u7684\u5F00\u53D1\u5C06\u56F4\u7ED5\u5728\u53EF\u89C6\u5316\u56FE\u5F62\u8868\u8FBE\u65B9\u5F0F\u7684\u5468\u56F4\u3002\u9996\u5148,\u6211\u4EEC\u4F1A\u4F7F\u7528\u201C\u5408\u9002\u7684\u201D\u5B9E\u4F53\u548C\u5173\u7CFB\u6765\u5F62\u6210\u6982\u5FF5,\u7136\u540E\u8868\u8FBE\u6210\u4E00\u7CFB\u5217\u9010\u6B65\u5B8C\u5584\u7684\u6A21\u578B,\u4E0D\u65AD\u5730\u7CFB\u7EDF\u5316\u9610\u660E\u548C\u7CBE\u5316\u8BBE\u8BA1\u6982\u5FF5\u3002\u6A21\u578B\u7528\u82E5\u5E72\u53EF\u89C6\u5316\u8BED\u8A00\u7684\u9002\u5F53\u7EC4\u5408\u6765\u63CF\u8FF0,\u5B83\u5FC5\u987B\u662F\u591A\u79CD\u8BED\u8A00\u7684\u7EC4\u5408,\u56E0\u4E3A\u7CFB\u7EDF\u6A21\u578B\u5177\u6709\u82E5\u5E72\u65B9\u9762\u7684\u5185\u5BB9,\u6BCF\u65B9\u9762\u8C61\u53D8\u620F\u6CD5\u822C\u4EA7\u751F\u4E0D\u540C\u7C7B\u578B\u7684\u601D\u7EF4\u56FE\u50CF\u3002</p><p>......\u5C31\u4F7F\u81EA\u5DF1\u6210\u4E3A\u826F\u597D\u53EF\u89C6\u5316\u8868\u8FBE\u65B9\u5F0F\u800C\u8A00,\u5EFA\u6A21\u8FC7\u7A0B\u7684\u67D0\u4E9B\u65B9\u9762\u5E76\u4E0D\u4F1A\u7ACB\u523B\u51FA\u73B0\u6539\u89C2\u3002\u4F8B\u5982,\u53D8\u91CF\u548C\u6570\u636E\u7ED3\u6784\u4E0A\u7684\u7B97\u6CD5\u64CD\u4F5C\u53EF\u80FD\u8FD8\u662F\u4F1A\u91C7\u7528\u6587\u5B57\u6027\u63CF\u8FF0\u3002</p></blockquote><p>\u6211\u548C Harel \u9887\u4E3A\u4E00\u81F4\u3002\u6211\u8BA4\u4E3A\u8F6F\u4EF6\u8981\u7D20\u5E76\u4E0D\u5B58\u5728\u4E8E\u4E09\u7EF4\u7A7A\u95F4\u4E2D,\u56E0\u6B64\u5E76\u4E0D\u5B58\u5728\u6982\u5FF5\u6027\u8BBE\u8BA1\u5230\u56FE\u5F62\u7B80\u5355\u4E24\u7EF4\u6216\u4E09\u7EF4\u4E0A\u7684\u6620\u5C04\u3002\u4ED6\u627F\u8BA4,\u6211\u4E5F\u540C\u610F\u2014\u2014\u8FD9\u9700\u8981\u591A\u79CD\u56FE\u5F62,\u6BCF\u79CD\u56FE\u5F62\u8986\u76D6\u67D0\u4E2A\u7279\u5B9A\u7684\u65B9\u9762,\u800C\u4E14\u6709\u4E9B\u65B9\u9762\u65E0\u6CD5\u7528\u56FE\u5F62\u6765\u8868\u8FBE\u3002</p><p>Harel \u91C7\u7528\u56FE\u5F62\u6765\u8F85\u52A9\u601D\u8003\u548C\u8BBE\u8BA1\u7684\u70ED\u60C5\u5F7B\u5E95\u5730\u611F\u67D3\u4E86\u6211\u3002\u6211\u4E00\u76F4\u559C\u6B22\u5411\u51C6\u7A0B\u5E8F\u5458\u63D0\u95EE,\u201C\u4E0B\u4E2A\u5341\u4E00\u6708\u5728\u54EA?\u201D\u5982\u679C\u89C9\u5F97\u95EE\u9898\u8FC7\u4E8E\u6A21\u7CCA,\u63A5\u7740\u6211\u4F1A\u95EE,\u201C\u544A\u8BC9\u6211,\u4F60\u81EA\u5DF1\u5173\u4E8E\u65F6\u95F4\u5386\u6CD5\u7684\u6A21\u578B\u3002\u201D\u4F18\u79C0\u7A0B\u5E8F\u5458\u5177\u6709\u5F88\u5F3A\u7684\u7A7A\u95F4\u60F3\u8C61\u80FD\u529B,\u4ED6\u4EEC\u5E38\u5E38\u6709\u65F6\u95F4\u7684\u51E0\u4F55\u6A21\u578B,\u800C\u4E14\u65E0\u9700\u8003\u8651,\u5C31\u80FD\u7406\u89E3\u7B2C\u4E00\u4E2A\u95EE\u9898\u3002\u4ED6\u4EEC\u5F80\u5F80\u62E5\u6709\u9AD8\u5EA6\u4E2A\u6027\u5316\u7684\u6A21\u578B\u3002</p><p><strong>Jone \u7684\u89C2\u70B9\u2014\u2014\u8D28\u91CF\u5E26\u6765\u751F\u4EA7\u7387</strong></p><p>Capers Jones \u6700\u5F00\u59CB\u5728\u4E00\u7CFB\u5217\u5907\u5FD8\u5F55\u91CC,\u800C\u540E\u5728\u4E00\u672C\u4E66\u91CC,\u63D0\u51FA\u4E86\u9887\u6709\u6D1E\u5BDF\u529B\u7684\u89C2\u70B9\u3002\u5F88\u591A\u548C\u6211\u6709\u4E66\u4FE1\u5F80\u6765\u7684\u4EBA\u5411\u6211\u63D0\u5230\u4E86\u4ED6\u7684\u89C2\u70B9,\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u5982\u540C\u5F53\u65F6\u7684\u5F88\u591A\u6587\u7AE0,\u5173\u6CE8\u4E8E\u751F\u4EA7\u7387\u2014\u2014\u5355\u4F4D\u8F93\u5165\u5BF9\u5E94\u7684\u8F6F\u4EF6\u4EA7\u51FA\u3002Jone \u63D0\u51FA,\u201C\u4E0D\u3002\u5173\u6CE8\u8D28\u91CF,\u751F\u4EA7\u7387\u81EA\u7136\u4F1A\u968F\u7740\u63D0\u9AD8.\u201D\u4ED6\u8BA4\u4E3A,\u5F88\u591A\u4EE3\u4EF7\u9AD8\u6602\u7684\u540E\u7EED\u9879\u76EE\u6295\u5165\u4E86\u5927\u91CF\u7684\u65F6\u95F4\u548C\u7CBE\u529B\u6765\u5BFB\u627E\u548C\u4FEE\u590D\u89C4\u683C\u8BF4\u660E\u4E2D\u3001\u8BBE\u8BA1\u548C\u5B9E\u73B0\u4E0A\u7684\u9519\u8BEF\u3002\u4ED6\u63D0\u4F9B\u7684\u6570\u636E\u663E\u793A\u4E86\u7F3A\u4E4F\u7CFB\u7EDF\u5316\u8D28\u91CF\u63A7\u5236\u548C\u8FDB\u5EA6\u707E\u96BE\u4E4B\u95F4\u7684\u5BC6\u5207\u5173\u7CFB\u3002\u6211\u8BA4\u540C\u8FD9\u4E9B\u6570\u636E\u3002\u4E0D\u8FC7,Boehm \u6307\u51FA,\u5982\u679C\u4E00\u5473\u8FFD\u6C42\u5B8C\u7F8E\u8D28\u91CF,\u751F\u4EA7\u7387\u4F1A\u50CF IBM \u7684\u822A\u5929\u8F6F\u4EF6\u4E00\u6837\u518D\u6B21\u4E0B\u964D\u3002</p><p>Coqui \u4E5F\u63D0\u51FA\u76F8\u4F3C\u7684\u4E3B\u5F20:\u7CFB\u7EDF\u5316\u8F6F\u4EF6\u5F00\u53D1\u65B9\u6CD5\u7684\u53D1\u5C55\u662F\u4E3A\u4E86\u89E3\u51B3\u8D28\u91CF\u95EE\u9898(\u7279\u522B\u662F\u907F\u514D\u5927\u578B\u7684\u707E\u96BE),\u800C\u4E0D\u662F\u51FA\u4E8E\u751F\u4EA7\u7387\u65B9\u9762\u7684\u8003\u8651\u3002</p><blockquote><p>\u4F46\u662F\u6CE8\u610F:70 \u5E74\u4EE3,\u5728\u8F6F\u4EF6\u751F\u4EA7\u4E0A\u5E94\u7528\u5DE5\u7A0B\u539F\u7406\u7684\u76EE\u6807\u662F\u63D0\u9AD8\u8F6F\u4EF6\u4EA7\u54C1\u7684\u8D28\u91CF\u3001\u53EF\u6D4B\u8BD5\u6027\u3001\u7A33\u5B9A\u6027\u4EE5\u53CA\u53EF\u9884\u89C1\u6027\u2014\u2014\u800C\u4E0D\u662F\u8F6F\u4EF6\u4EA7\u54C1\u7684\u5F00\u53D1\u6548\u7387\u3002</p><p>\u5728\u8F6F\u4EF6\u751F\u4EA7\u4E0A\u5E94\u7528\u5DE5\u7A0B\u539F\u7406\u7684\u9A71\u52A8\u529B\u662F\u62C5\u5FC3\u62E5\u6709\u65E0\u6CD5\u63A7\u5236\u7684\u201C\u827A\u672F\u5BB6\u4EEC\u201D\u800C\u53EF\u80FD\u5BFC\u81F4\u7684\u5DE8\u5927\u707E\u96BE,\u4ED6\u4EEC\u5F80\u5F80\u5BF9\u5F02\u5E38\u590D\u6742\u7CFB\u7EDF\u5F00\u53D1\u627F\u62C5\u8D23\u4EFB</p></blockquote><p><strong>\u90A3\u4E48,\u751F\u4EA7\u7387\u7684\u60C5\u5F62\u5982\u4F55?</strong></p><p><strong>\u751F\u4EA7\u7387\u6570\u636E\u3002</strong> \u751F\u4EA7\u7387\u7684\u6570\u636E\u975E\u5E38\u96BE\u4EE5\u5B9A\u4E49\u3001\u6D4B\u91CF\u548C\u5BFB\u627E\u3002Capers Jones \u76F8\u4FE1\u4E24\u4E2A\u76F8\u9694\u5341\u5E74\u3001\u5B8C\u5168\u7B49\u540C\u7684 COBOL \u7A0B\u5E8F,\u4E00\u4E2A\u91C7\u7528\u7ED3\u6784\u5316\u65B9\u6CD5\u5F00\u53D1,\u53E6\u4E00\u4E2A\u4E0D\u4F7F\u7528\u7ED3\u6784\u5316\u65B9\u6CD5,\u5B83\u4EEC\u4E4B\u95F4\u7684\u5DEE\u8DDD\u662F 3 \u500D\u3002</p><p>Ed Yourdon \u8BF4,\u201C\u7531\u4E8E\u5DE5\u4F5C\u7AD9\u548C\u8F6F\u4EF6\u5DE5\u5177,\u6211\u770B\u5230\u4E86\u4EBA\u4EEC\u7684\u5DE5\u4F5C\u83B7\u5F97\u4E86 5 \u500D\u7684\u63D0\u9AD8\u3002\u201DTom DeMarco \u8BA4\u4E3A\u201C\u4F60\u7684\u671F\u671B\u2014\u2014\u5341\u5E74\u5185,\u7531\u4E8E\u6240\u6709\u7684\u6280\u672F\u800C\u4F7F\u751F\u4EA7\u7387\u5F97\u5230\u6570\u91CF\u7EA7\u7684\u63D0\u9AD8\u2014\u2014\u592A\u4E50\u89C2\u4E86\u3002\u6211\u6CA1\u6709\u770B\u5230\u4EFB\u4F55\u673A\u6784\u53D6\u5F97\u6570\u91CF\u7EA7\u7684\u8FDB\u6B65\u3002\u201D</p><p><strong>\u5851\u6599\u8584\u819C\u5305\u88C5\u7684\u6210\u54C1\u8F6F\u4EF6\u2014\u2014\u8D2D\u4E70,\u800C\u975E\u5F00\u53D1\u3002</strong> \u6211\u8BA4\u4E3A 1986 \u5E74\u300A\u6CA1\u6709\u94F6\u5F39\u300B\u4E2D\u7684\u4E00\u4E2A\u4F30\u8BA1\u88AB\u8BC1\u5B9E\u662F\u6B63\u786E\u7684:\u201C\u6211\u76F8\u4FE1,\u8FD9\u4E2A\u5927\u4F17\u5E02\u573A\u662F......\u8F6F\u4EF6\u5DE5\u7A0B\u9886\u57DF\u610F\u4E49\u6700\u6DF1\u8FDC\u7684\u5F00\u53D1\u65B9\u5411\u3002\u201D\u4ECE\u5B66\u79D1\u7684\u89D2\u5EA6\u8BF4,\u4E0D\u7BA1\u548C\u5185\u90E8\u8FD8\u662F\u5916\u90E8\u5BA2\u6237\u8F6F\u4EF6\u7684\u5F00\u53D1\u76F8\u6BD4,\u5927\u4F17\u5E02\u573A\u8F6F\u4EF6\u90FD\u51E0\u4E4E\u662F\u4E00\u4E2A\u5D2D\u65B0\u7684\u9886\u57DF\u3002\u5F53\u8F6F\u4EF6\u5305\u7684\u9500\u91CF\u4E00\u65E6\u8FBE\u5230\u767E\u4E07\u6216\u8005\u5373\u4F7F\u53EA\u662F\u51E0\u5343,\u8FD9\u65F6\u5173\u952E\u7684\u652F\u914D\u6027\u95EE\u9898\u5C31\u53D8\u6210\u4E86\u8D28\u91CF\u3001\u65F6\u673A\u3001\u4EA7\u54C1\u6027\u80FD\u548C\u652F\u6491\u6210\u672C,\u800C\u4E0D\u518D\u662F\u5BF9\u4E8E\u5BA2\u6237\u7CFB\u7EDF\u5F02\u5E38\u5173\u952E\u7684\u5F00\u53D1\u6210\u672C\u3002</p><p><strong>\u521B\u9020\u6027\u6D3B\u52A8\u7684\u5F3A\u5927\u5DE5\u5177\u3002</strong> \u63D0\u9AD8\u4FE1\u606F\u7BA1\u7406\u7CFB\u7EDF(MIS)\u7F16\u7A0B\u4EBA\u5458\u751F\u4EA7\u7387\u6700\u620F\u5267\u5316\u7684\u65B9\u6CD5\u662F\u5230\u4E00\u5BB6\u8BA1\u7B97\u673A\u5546\u5E97\u53BB,\u8D2D\u4E70\u7406\u5E94\u7531\u4ED6\u4EEC\u5F00\u53D1\u7684\u5546\u4E1A\u6210\u54C1\u3002\u8FD9\u5E76\u4E0D\u8352\u5510\u53EF\u7B11\u3002\u4EF7\u683C\u4F4E\u5EC9\u3001\u529F\u80FD\u5F3A\u5927\u7684\u8584\u819C\u5305\u88C5\u8F6F\u4EF6\u5DF2\u7ECF\u80FD\u6EE1\u8DB3\u8981\u6C42,\u800C\u4EE5\u524D\u8FD9\u4F1A\u8981\u6C42\u8FDB\u884C\u5B9A\u5236\u8F6F\u4EF6\u5305\u7684\u5F00\u53D1\u3002\u6BD4\u8D77\u590D\u6742\u7684\u5927\u578B\u4EA7\u54C1\u5DE5\u5177,\u5B83\u4EEC\u66F4\u52A0\u50CF\u7535\u952F\u3001\u7535\u8F6C\u548C\u7802\u78E8\u673A\u3002\u628A\u5B83\u4EEC\u7EC4\u5408\u6210\u517C\u5BB9\u4E92\u8865\u7684\u96C6\u5408,\u50CF Microsoft Works \u548C\u96C6\u6210\u66F4\u597D\u7684 Claris Works \u4E00\u6837,\u80FD\u591F\u5E26\u6765\u5DE8\u5927\u7684\u7075\u6D3B\u6027\u3002\u53E6\u5916,\u8C61\u4F9B\u4EBA\u4EEC\u4F7F\u7528\u7684\u7EC4\u5408\u5DE5\u5177\u7BB1,\u5176\u4E2D\u7684\u67D0\u4E9B\u5DE5\u5177\u4F1A\u7ECF\u5E38\u88AB\u4F7F\u7528,\u4EE5\u81F4\u4E8E\u719F\u80FD\u751F\u5DE7\u3002\u8FD9\u79CD\u5DE5\u5177\u5FC5\u987B\u6CE8\u91CD\u5E38\u4EBA\u4F7F\u7528\u7684\u65B9\u4FBF,\u800C\u4E0D\u662F\u4E13\u4E1A\u3002</p><p>Ivan Selin,\u7F8E\u56FD\u7BA1\u7406\u7CFB\u7EDF\u516C\u53F8\u4E3B\u5E2D,\u5728 1987 \u5E74\u66FE\u5199\u4FE1\u7ED9\u6211:</p><blockquote><p>\u6211\u6709\u4E9B\u6000\u7591\u4F60\u7684\u5173\u4E8E\u8F6F\u4EF6\u5305\u6CA1\u6709\u771F\u6B63\u5730\u6539\u53D8\u5F88\u591A......\u7684\u89C2\u70B9\u3002\u6211\u89C9\u5F97\u4F60\u592A\u8FC7\u8F7B\u6613\u5730\u629B\u5F00\u4E86\u4F60\u7684\u89C2\u5BDF\u6240\u8574\u6DB5\u7684\u4E8B\u5B9E;\u4F60\u89C2\u5BDF\u5230\u2014\u2014[\u8F6F\u4EF6\u5305]\u201C\u53EF\u80FD\u6BD4\u4EE5\u524D\u66F4\u52A0\u901A\u7528\u548C\u66F4\u52A0\u5BB9\u6613\u5B9A\u5236\u4E00\u4E9B,\u4F46\u5E76\u4E0D\u592A\u591A\u3002\u201D\u5373\u4F7F\u5728\u8868\u9762\u4E0A\u63A5\u53D7\u4E86\u8FD9\u79CD\u8BBA\u8FF0,\u6211\u76F8\u4FE1\u7528\u6237\u5BDF\u89C9\u5230\u8F6F\u4EF6\u5305\u66F4\u52A0\u901A\u7528\u548C\u6613\u4E8E\u672C\u5730\u5316,\u8FD9\u79CD\u611F\u89C9\u4F7F\u7528\u6237\u66F4\u5BB9\u6613\u63A5\u53D7\u8F6F\u4EF6\u5305\u3002\u5728\u6211\u516C\u53F8\u53D1\u73B0\u7684\u5927\u591A\u6570\u60C5\u51B5\u4E2D,\u662F[\u6700\u7EC8\u7528\u6237],\u800C\u4E0D\u662F\u8F6F\u4EF6\u4EBA\u5458,\u4E0D\u613F\u610F\u4F7F\u7528\u8F6F\u4EF6\u5305,\u56E0\u4E3A\u4ED6\u4EEC\u8BA4\u4E3A\u4F1A\u5931\u53BB\u5FC5\u8981\u7684\u7279\u6027\u6216\u529F\u80FD\u3002\u56E0\u6B64,\u5BF9\u4ED6\u4EEC\u800C\u8A00,\u6613\u4E8E\u5B9A\u5236\u662F\u4E00\u4E2A\u975E\u5E38\u5927\u7684\u5356\u70B9\u3002</p></blockquote><p>\u6211\u8BA4\u4E3A Selin \u662F\u5341\u5206\u6B63\u786E\u7684\u2014\u2014\u6211\u4F4E\u4F30\u4E86\u8F6F\u4EF6\u5305\u5BA2\u6237\u5316\u7684\u7A0B\u5EA6\u548C\u5B83\u7684\u91CD\u8981\u6027\u3002</p><p><strong>\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u2014\u2014\u8FD9\u9897\u94DC\u8D28\u5B50\u5F39\u53EF\u4EE5\u5417?</strong></p><p>\u672C\u7AE0\u4E00\u5F00\u59CB\u7684\u63CF\u8FF0\u63D0\u9192\u6211\u4EEC,\u5F53\u5F88\u591A\u96F6\u4EF6\u9700\u8981\u88C5\u914D,\u800C\u4E14\u6BCF\u4E2A\u96F6\u4EF6\u53EF\u80FD\u5F88\u590D\u6742\u65F6,\u5982\u679C\u5B83\u4EEC\u7684\u63A5\u53E3\u8BBE\u8BA1\u5F97\u5F88\u6D41\u7545,\u5927\u91CF\u4E30\u5BCC\u7684\u7ED3\u6784\u5C31\u80FD\u5FEB\u901F\u5730\u7EC4\u5408\u5728\u4E00\u8D77\u3002</p><p><strong>\u4F7F\u7528\u66F4\u5927\u7684\u96F6\u4EF6\u6765\u6784\u5EFA\u3002</strong> \u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u7684\u7B2C\u4E00\u4E2A\u7279\u5F81\u662F,\u5B83\u5F3A\u5236\u7684 \u6A21\u5757\u5316 \u548C\u6E05\u6670\u7684\u63A5\u53E3\u3002\u5176\u6B21,\u5B83\u5F3A\u8C03\u4E86 \u5C01\u88C5 ,\u5373\u5916\u754C\u65E0\u6CD5\u770B\u5230\u7EC4\u4EF6\u7684\u5185\u90E8\u7ED3\u6784;\u5B83\u8FD8\u5F3A\u8C03\u4E86 \u7EE7\u627F \u548C \u5C42\u6B21\u5316 \u7C7B\u7ED3\u6784\u4EE5\u53CA\u865A\u51FD\u6570\u3002\u9762\u5411\u5BF9\u8C61\u8FD8\u5F3A\u8C03\u4E86 \u5F3A\u62BD\u8C61\u6570\u636E\u7C7B\u578B\u5316 ,\u5B83\u786E\u4FDD\u67D0\u79CD\u7279\u5B9A\u7684\u6570\u636E\u7C7B\u578B\u53EA\u80FD\u7531\u5B83\u81EA\u8EAB\u7684\u76F8\u5E94\u51FD\u6570\u6765\u64CD\u4F5C\u3002</p><p>\u73B0\u5728,\u65E0\u9700\u4F7F\u7528\u6574\u4E2A Smalltalk \u6216\u8005 C++\u7684\u8F6F\u4EF6\u5305,\u5C31\u53EF\u4EE5\u83B7\u5F97\u8FD9\u4E9B\u7279\u70B9\u4E2D\u7684\u4EFB\u610F\u4E00\u4E2A\u2014\u2014\u5176\u4E2D\u4E00\u4E9B\u751A\u81F3\u51FA\u73B0\u5728\u9762\u5411\u5BF9\u8C61\u6280\u672F\u4E4B\u524D\u3002\u9762\u5411\u5BF9\u8C61\u65B9\u6CD5\u5438\u5F15\u4EBA\u7684\u5730\u65B9\u7C7B\u4F3C\u4E8E\u590D\u5408\u7EF4\u4ED6\u547D\u836F\u4E38:\u4E00\u6B21\u6027(\u5373\u7F16\u7A0B\u4EBA\u5458\u7684\u57F9\u8BAD)\u5F97\u5230\u6240\u6709\u7684\u597D\u5904\u3002\u9762\u5411\u5BF9\u8C61\u662F\u4E00\u79CD\u975E\u5E38\u6709\u524D\u9014\u7684\u6982\u5FF5\u3002</p><p><strong>\u9762\u5411\u5BF9\u8C61\u6280\u672F\u4E3A\u4EC0\u4E48\u53D1\u5C55\u7F13\u6162?</strong> \u300A\u6CA1\u6709\u94F6\u5F39\u300B\u540E\u7684\u4E5D\u5E74\u4E2D,\u5BF9\u9762\u5411\u5BF9\u8C61\u6280\u672F\u7684\u671F\u671B\u7A33\u6B65\u589E\u957F\u3002\u4E3A\u4EC0\u4E48\u589E\u957F\u5982\u6B64\u7F13\u6162?\u7406\u8BBA\u8FC7\u591A\u3002James Coggins,\u5DF2\u7ECF\u5728 The C++ Report \u505A\u4E86\u56DB\u5E74 \u201CThe best of comp.lang.c++\u201D\u4E13\u680F\u7684\u4F5C\u8005,\u4ED6\u63D0\u51FA\u4E86\u8FD9\u6837\u7684\u89E3\u91CA:</p><blockquote><p>\u95EE\u9898\u662F O-O \u7A0B\u5E8F\u5458\u7ECF\u5386\u4E86\u5F88\u591A\u9519\u7EFC\u590D\u6742\u6DF7\u4E71\u7684\u5E94\u7528,\u4ED6\u4EEC\u6240\u5173\u6CE8\u7684\u662F\u4F4E\u5C42\u6B21,\u800C\u4E0D\u662F\u9AD8\u5C42\u6B21\u7684\u62BD\u8C61\u3002\u4F8B\u5982,\u4ED6\u4EEC\u5F00\u53D1\u4E86\u5F88\u591A\u8C61\u94FE\u8868\u6216\u96C6\u5408\u8FD9\u6837\u7684\u7C7B,\u800C\u4E0D\u662F \u7528\u6237\u63A5\u53E3 \u3001 \u5C04\u7EBF\u675F\u6A21\u578B \u6216\u8005 \u6709\u9650\u5143\u7D20\u6A21\u578B \u3002\u4E0D\u5E78\u7684\u662F,C++\u4E2D\u5E2E\u52A9\u7A0B\u5E8F\u5458\u907F\u514D\u9519\u8BEF\u7684\u5F3A\u7C7B\u578B\u68C0\u67E5,\u4F7F\u5F97\u4ECE\u5C0F\u578B\u4E8B\u7269\u4E2D\u6784\u5EFA\u5927\u578B\u7269\u4F53\u975E\u5E38\u56F0\u96BE\u3002</p></blockquote><p>\u4ED6\u56DE\u5F52\u5230\u57FA\u672C\u7684\u8F6F\u4EF6\u95EE\u9898,\u4E3B\u5F20\u4E00\u79CD\u89E3\u51B3\u8F6F\u4EF6\u4E0D\u80FD\u6EE1\u8DB3\u8981\u6C42\u7684\u65B9\u6CD5,\u5373\u901A\u8FC7\u5BA2\u6237\u7684\u53C2\u4E0E\u548C\u534F\u4F5C\u6765\u63D0\u9AD8\u8111\u529B\u52B3\u52A8\u7684\u89C4\u6A21\u3002\u4ED6\u8D5E\u540C\u81EA\u9876\u5411\u4E0B\u7684\u8BBE\u8BA1:</p><blockquote><p>\u5982\u679C\u6211\u4EEC\u8BBE\u8BA1\u5927\u7C92\u5EA6\u7684\u7C7B,\u5173\u6CE8\u7528\u6237\u5DF2\u7ECF\u63A5\u89E6\u7684\u6982\u5FF5,\u5219\u5728\u8FDB\u884C\u8BBE\u8BA1\u7684\u65F6\u5019,\u4ED6\u4EEC\u80FD\u591F\u7406\u89E3\u8BBE\u8BA1\u5E76\u63D0\u51FA\u95EE\u9898,\u5E76\u4E14\u53EF\u4EE5\u5E2E\u52A9\u8BBE\u8BA1\u6D4B\u8BD5\u7528\u4F8B\u3002\u6211\u7684\u773C\u79D1\u5BA2\u6237\u5E76\u4E0D\u5173\u5FC3\u5806\u6808,\u4ED6\u4EEC\u5173\u5FC3\u63CF\u8FF0\u773C\u89D2\u819C\u5F62\u72B6\u7684\u52D2\u8BA9\u5FB7\u591A\u9879\u5F0F\u3002\u5728\u8FD9\u65B9\u9762,\u5C0F\u89C4\u6A21\u7684\u5C01\u88C5\u5E26\u6765\u7684\u597D\u5904\u6BD4\u8F83\u5C11\u3002</p></blockquote><p>David Parnas \u7684\u8BBA\u6587\u662F\u9762\u5411\u5BF9\u8C61\u6982\u5FF5\u7684\u8D77\u6E90\u4E4B\u4E00,\u4ED6\u7528\u4E0D\u540C\u7684\u89C2\u70B9\u770B\u8FD9\u4E2A\u95EE\u9898\u3002\u4ED6\u5199\u4FE1\u7ED9\u6211:</p><blockquote><p>\u7B54\u6848\u5F88\u7B80\u5355\u3002\u56E0\u4E3A[O-O]\u548C\u5404\u79CD\u590D\u6742\u8BED\u8A00\u7684\u8054\u7CFB\u5DF2\u7ECF\u5F88\u7D27\u5BC6\u3002\u4EBA\u4EEC\u5E76\u6CA1\u6709\u88AB\u544A\u8BC9 O-O \u662F\u4E00\u79CD\u8BBE\u8BA1\u7684\u65B9\u6CD5,\u5E76\u5411\u4ED6\u4EEC\u8BB2\u6388\u8BBE\u8BA1\u65B9\u6CD5\u548C\u539F\u7406,\u5927\u5BB6\u53EA\u662F\u88AB\u544A\u77E5 O-O \u662F\u4E00\u79CD\u7279\u6B8A\u5DE5\u5177\u3002\u800C\u6211\u4EEC\u53EF\u4EE5\u7528\u4EFB\u4F55\u5DE5\u5177\u5199\u51FA\u4F18\u8D28\u6216\u4F4E\u52A3\u7684\u4EE3\u7801\u3002\u9664\u975E\u6211\u4EEC\u7ED9\u4EBA\u4EEC\u8BB2\u89E3\u5982\u4F55\u8BBE\u8BA1,\u5426\u5219\u8BED\u8A00\u6240\u8D77\u7684\u4F5C\u7528\u975E\u5E38\u5C0F\u3002\u7ED3\u679C\u662F\u4EBA\u4EEC\u4F7F\u7528\u8FD9\u79CD\u8BED\u8A00\u505A\u51FA\u4E0D\u597D\u7684\u8BBE\u8BA1,\u6CA1\u6709\u4ECE\u4E2D\u83B7\u5F97\u4EC0\u4E48\u4EF7\u503C\u3002\u800C\u4E00\u65E6\u83B7\u5F97\u7684\u4EF7\u503C\u5C11,\u5B83\u5C31\u4E0D\u4F1A\u6D41\u884C\u3002</p></blockquote><p><strong>\u8D44\u91D1\u7684\u5148\u884C\u6295\u5165,\u6536\u76CA\u7684\u540E\u671F\u83B7\u5F97\u3002</strong> \u9762\u5411\u5BF9\u8C61\u6280\u672F\u5305\u542B\u4E86\u5F88\u591A\u65B9\u6CD5\u5B66\u4E0A\u7684\u8FDB\u6B65\u3002\u9762\u5411\u5BF9\u8C61\u6280\u672F\u7684\u524D\u671F\u6295\u5165\u5F88\u591A\u2014\u2014\u4E3B\u8981\u662F\u57F9\u8BAD\u7A0B\u5E8F\u5458\u7528\u5F88\u65B0\u7684\u65B9\u6CD5\u601D\u8003,\u540C\u65F6\u8FD8\u8981\u628A\u51FD\u6570\u6253\u9020\u6210\u901A\u7528\u7684\u7C7B\u3002\u6211\u8BA4\u4E3A\u5B83\u7684\u597D\u5904\u662F\u5BA2\u89C2\u5B9E\u5728\u7684,\u5E76\u975E\u4EC5\u4EC5\u662F\u63A8\u6D4B\u3002\u9762\u5411\u5BF9\u8C61\u5E94\u7528\u5728\u6574\u4E2A\u5F00\u53D1\u5468\u671F\u4E2D,\u4F46\u662F\u771F\u6B63\u7684\u83B7\u76CA\u53EA\u6709\u5728\u540E\u7EED\u5F00\u53D1\u3001\u6269\u5C55\u548C\u7EF4\u62A4\u6D3B\u52A8\u4E2D\u624D\u80FD\u4F53\u73B0\u51FA\u6765\u3002Coggin \u8BF4:</p><blockquote><p>\u201C\u9762\u5411\u5BF9\u8C61\u6280\u672F\u4E0D\u4F1A\u52A0\u5FEB\u9996\u6B21\u6216\u7B2C\u4E8C\u6B21\u7684\u5F00\u53D1,\u4EA7\u54C1\u65CF\u4E2D\u7B2C\u4E94\u4E2A\u9879\u76EE\u7684\u5F00\u53D1\u624D\u4F1A\u5F02\u4E4E\u5BFB\u5E38\u7684\u8FC5\u901F\u3002\u201D</p></blockquote><p>\u4E3A\u4E86\u9884\u671F\u4E2D\u7684,\u4F46\u662F\u6709\u4E9B\u4E0D\u786E\u5B9A\u7684\u6536\u76CA,\u5192\u7740\u98CE\u9669\u6295\u5165\u91D1\u94B1\u662F\u6295\u8D44\u4EBA\u6BCF\u5929\u5728\u505A\u7684\u4E8B\u60C5\u3002\u4E0D\u8FC7,\u5728\u5F88\u591A\u8F6F\u4EF6\u516C\u53F8\u4E2D,\u8FD9\u9700\u8981\u771F\u6B63\u7684\u7BA1\u7406\u52C7\u6C14,\u4E00\u79CD\u6BD4\u6280\u672F\u7ADE\u4E89\u529B\u6216\u8005\u4F18\u79C0\u7BA1\u7406\u80FD\u529B\u66F4\u5C11\u6709\u7684\u7CBE\u795E\u3002\u6211\u8BA4\u4E3A\u6781\u5EA6\u7684\u524D\u671F\u6295\u5165\u548C\u6536\u76CA\u7684\u63A8\u540E\u662F\u4F7F O-O \u6280\u672F\u5E94\u7528\u8FDF\u7F13\u7684\u6700\u5927\u539F\u56E0\u3002\u5373\u4F7F\u5982\u6B64,\u5728\u5F88\u591A\u673A\u6784\u4E2D,C++\u4ECD\u6BEB\u65E0\u7591\u95EE\u5730\u53D6\u4EE3\u4E86 C\u3002</p><h2 id="\u91CD\u7528\u7684\u60C5\u51B5\u600E\u6837" tabindex="-1"><a class="header-anchor" href="#\u91CD\u7528\u7684\u60C5\u51B5\u600E\u6837" aria-hidden="true">#</a> \u91CD\u7528\u7684\u60C5\u51B5\u600E\u6837?</h2><p>\u89E3\u51B3\u8F6F\u4EF6\u6784\u5EFA\u6839\u672C\u56F0\u96BE\u7684\u6700\u4F73\u65B9\u6CD5\u662F\u4E0D\u8FDB\u884C\u4EFB\u4F55\u5F00\u53D1\u3002\u8F6F\u4EF6\u5305\u53EA\u662F\u8FBE\u5230\u4E0A\u8FF0\u76EE\u6807\u7684\u65B9\u6CD5\u4E4B\u4E00,\u53E6\u5916\u7684\u65B9\u6CD5\u662F\u7A0B\u5E8F\u91CD\u7528\u3002\u5B9E\u9645\u4E0A,\u7C7B\u7684\u5BB9\u6613\u91CD\u7528\u548C\u901A\u8FC7\u7EE7\u627F\u65B9\u4FBF\u5730\u5B9A\u5236\u662F\u9762\u5411\u5BF9\u8C61\u6280\u672F\u6700\u5438\u5F15\u4EBA\u7684\u5730\u65B9\u3002</p><p>\u4E8B\u60C5\u5E38\u5E38\u5C31\u662F\u8FD9\u6837\u3002\u5F53\u67D0\u4EBA\u5728\u65B0\u7684\u505A\u4E8B\u65B9\u6CD5\u4E0A\u53D6\u5F97\u4E86\u4E00\u4E9B\u7ECF\u9A8C,\u65B0\u6A21\u5F0F\u5C31\u4E0D\u518D\u8C61\u4E00\u5F00\u59CB\u90A3\u4E48\u7B80\u5355\u3002</p><p>\u5F53\u7136,\u7A0B\u5E8F\u5458\u7ECF\u5E38\u91CD\u7528\u4ED6\u4EEC\u81EA\u5DF1\u7684\u624B\u5934\u5DE5\u4F5C\u3002Jone \u63D0\u5230:</p><blockquote><p>\u5927\u591A\u6570\u6709\u4E30\u5BCC\u7ECF\u9A8C\u7684\u7A0B\u5E8F\u5458\u62E5\u6709\u81EA\u5DF1\u7684\u79C1\u4EBA\u5F00\u53D1\u5E93,\u53EF\u4EE5\u4F7F\u4ED6\u4EEC\u4F7F\u7528\u5927\u7EA6 30%\u7684\u91CD\u7528\u4EE3\u7801\u6765\u5F00\u53D1\u8F6F\u4EF6\u3002\u516C\u53F8\u7EA7\u522B\u7684\u91CD\u7528\u80FD\u63D0\u4F9B 70%\u7684\u91CD\u7528\u4EE3\u7801\u91CF,\u5B83\u9700\u8981\u7279\u6B8A\u7684\u5F00\u53D1\u5E93\u548C\u7BA1\u7406\u652F\u6301\u3002\u516C\u53F8\u7EA7\u522B\u7684\u91CD\u7528\u4EE3\u7801\u4E5F\u610F\u5473\u7740\u9700\u8981\u5BF9\u9879\u76EE\u4E2D\u7684\u53D8\u66F4\u8FDB\u884C\u7EDF\u8BA1\u548C\u5EA6\u91CF,\u4ECE\u800C\u63D0\u9AD8\u91CD\u7528\u7684\u53EF\u4FE1\u7A0B\u5EA6 \u3002</p></blockquote><p>W.Huang \u5EFA\u8BAE\u7528\u8D23\u4EFB\u4E13\u5BB6\u7684\u77E9\u9635\u7BA1\u7406\u6765\u7EC4\u7EC7\u8F6F\u4EF6\u5DE5\u5382,\u4ECE\u800C\u57F9\u517B\u91CD\u7528\u81EA\u8EAB\u4EE3\u7801\u7684\u65E5\u5E38\u5DE5\u4F5C\u4E60\u60EF \u3002</p><p>JPL \u7684 Van Snyder \u5411\u6211\u6307\u51FA,\u6570\u5B66\u8F6F\u4EF6\u9886\u57DF\u6709\u7740\u8F6F\u4EF6\u91CD\u7528\u7684\u957F\u671F\u4F20\u7EDF:</p><blockquote><p>\u6211\u4EEC\u63A8\u6D4B\u91CD\u7528\u7684\u969C\u788D\u4E0D\u5728\u751F\u4EA7\u8005\u4E00\u8FB9,\u800C\u5728\u6D88\u8D39\u8005\u4E00\u8FB9\u3002\u5982\u679C\u4E00\u4E2A\u8F6F\u4EF6\u5DE5\u7A0B\u5E08,\u6F5C\u5728\u7684\u6807\u51C6\u5316\u8F6F\u4EF6\u6784\u4EF6\u6D88\u8D39\u8005,\u89C9\u5F97\u5BFB\u627E\u80FD\u6EE1\u8DB3\u4ED6\u9700\u8981\u7684\u6784\u4EF6,\u8FDB\u884C\u9A8C\u8BC1,\u6BD4\u81EA\u884C\u7F16\u5199\u7684\u4EE3\u4EF7\u66F4\u52A0\u6602\u8D35\u65F6,\u91CD\u590D\u7684\u6784\u4EF6\u5C31\u4F1A\u4EA7\u751F\u3002\u6CE8\u610F\u6211\u4EEC\u4E0A\u9762\u63D0\u5230\u7684\u201C\u89C9\u5F97\u201D\u3002\u5B83\u548C\u91CD\u65B0\u5F00\u53D1\u7684\u771F\u6B63\u6295\u5165\u65E0\u5173\u3002</p><p>\u6570\u5B66\u8F6F\u4EF6\u4E0A\u91CD\u7528\u6210\u529F\u7684\u539F\u56E0\u6709\u4E24\u4E2A:(1)\u5B83\u5F88\u6666\u6DA9\u96BE\u61C2,\u6BCF\u884C\u4EE3\u7801\u9700\u8981\u5927\u91CF\u9AD8\u667A\u5546\u7684\u8F93\u5165;(2)\u5B58\u5728\u4E30\u5BCC\u7684\u6807\u51C6\u672F\u8BED,\u4E5F\u5C31\u662F\u7528\u6570\u5B66\u6765\u63CF\u8FF0\u6BCF\u4E2A\u6784\u4EF6\u7684\u529F\u80FD\u3002\u56E0\u6B64,\u91CD\u65B0\u5F00\u53D1\u6570\u5B66\u8F6F\u4EF6\u6784\u4EF6\u7684\u6210\u672C\u5F88\u9AD8,\u800C\u67E5\u627E\u73B0\u6709\u6784\u4EF6\u529F\u80FD\u7684\u6210\u672C\u5F88\u4F4E\u3002\u6570\u5B66\u8F6F\u4EF6\u754C\u5B58\u5728\u4E00\u4E9B\u957F\u671F\u7684\u4F20\u7EDF\u2014\u2014\u4F8B\u5982,\u4E13\u4E1A\u671F\u520A\u548C\u7B97\u6CD5\u641C\u96C6,\u7528\u9002\u5EA6\u6210\u672C\u63D0\u4F9B\u7B97\u6CD5,\u51FA\u4E8E\u5546\u4E1A\u8003\u8651\u5F00\u53D1\u7684\u9AD8\u8D28\u91CF\u7B97\u6CD5(\u5C3D\u7BA1\u6210\u672C\u6709\u4E9B\u9AD8,\u4F46\u4F9D\u65E7\u9002\u5EA6)\u7B49\u2014\u2014\u4F7F\u67E5\u627E\u548C\u53D1\u73B0\u6EE1\u8DB3\u67D0\u4EBA\u9700\u8981\u7684\u6784\u4EF6\u6BD4\u5176\u4ED6\u5F88\u591A\u9886\u57DF\u8981\u5BB9\u6613\u3002\u5176\u4ED6\u9886\u57DF\u4E2D,\u6709\u65F6\u751A\u81F3\u4E0D\u53EF\u80FD\u7B80\u6D01\u5730\u63D0\u51FA\u660E\u786E\u7684\u8981\u6C42\u3002\u8FD9\u4E9B\u56E0\u7D20\u5408\u5728\u4E00\u8D77,\u4F7F\u6570\u5B66\u8F6F\u4EF6\u7684\u91CD\u7528\u6BD4\u91CD\u65B0\u5F00\u53D1\u66F4\u6709\u5438\u5F15\u529B\u3002</p></blockquote><p>\u540C\u6837\u7684\u539F\u56E0,\u5728\u5F88\u591A\u5176\u4ED6\u9886\u57DF\u4E2D\u4E5F\u53EF\u4EE5\u53D1\u73B0\u76F8\u540C\u7684\u91CD\u7528\u73B0\u8C61,\u5982\u90A3\u4E9B\u4E3A\u6838\u53CD\u5E94\u3001\u5929\u6C14\u6A21\u578B\u3001\u6D77\u6D0B\u6A21\u578B\u5F00\u53D1\u8F6F\u4EF6\u7684\u4EE3\u7801\u7F16\u5236\u5DE5\u4F5C\u3002\u8FD9\u4E9B\u9886\u57DF\u90FD\u662F\u5728\u76F8\u540C\u7684\u8BFE\u672C\u548C\u6807\u51C6\u6982\u5FF5\u4E0B\u9010\u6B65\u5730\u53D1\u5C55\u8D77\u6765\u7684\u3002</p><p><strong>\u73B0\u5728\u516C\u53F8\u7EA7\u522B\u7684\u91CD\u7528\u60C5\u51B5\u5982\u4F55?</strong> \u5B58\u5728\u7740\u5927\u91CF\u7684\u7814\u7A76\u3002\u7F8E\u56FD\u56FD\u5185\u7684\u5B9E\u8DF5\u76F8\u5BF9\u8F83\u5C11,\u6709\u62A5\u9053\u58F0\u79F0\u56FD\u5916\u91CD\u7528\u8F83\u591A \u3002</p><p>Jones \u62A5\u544A,\u5728\u4ED6\u516C\u53F8\u7684\u5BA2\u6237\u4E2D,\u6240\u6709\u62E5\u6709 5000 \u540D\u4EE5\u4E0A\u7A0B\u5E8F\u5458\u7684\u673A\u6784\u90FD\u8FDB\u884C\u6B63\u5F0F\u7684\u91CD\u7528\u7814\u7A76,\u800C 500 \u540D\u4EE5\u4E0B\u7A0B\u5E8F\u5458\u7684\u7EC4\u7EC7,\u53EA\u6709\u4E0D\u5230 10%\u7740\u624B\u91CD\u7528\u7814\u7A76 \u3002\u62A5\u544A\u6307\u51FA,\u6700\u5177\u6709\u91CD\u7528\u6F5C\u8D28\u7684\u4F01\u4E1A\u4E2D,\u91CD\u7528\u6027\u7814\u7A76(\u800C\u975E\u90E8\u7F72)\u201C\u662F\u6D3B\u8DC3\u548C\u79EF\u6781\u7684,\u5373\u4F7F\u6CA1\u6709\u5B8C\u5168\u6210\u529F\u3002\u201D Ed Yourdon \u62A5\u544A,\u6709\u4E00\u5BB6\u9A6C\u5C3C\u62C9\u7684\u8F6F\u4EF6\u516C\u53F8,200 \u540D\u7A0B\u5E8F\u5458\u4E2D\u6709 50 \u540D\u4ECE\u4E8B\u4F9B\u5176\u4ED6\u4EBA\u4F7F\u7528\u7684\u91CD\u7528\u6A21\u5757\u7684\u5F00\u53D1,\u201C\u6211\u6240\u89C1\u5230\u7684\u4E2A\u6848\u975E\u5E38\u5C11\u2014\u2014\u662F\u7531\u4E8E \u673A\u6784\u4E0A \u56E0\u7D20\u800C\u8FDB\u884C\u91CD\u7528\u7814\u7A76,\u800C\u4E0D\u662F\u6280\u672F\u4E0A\u7684\u539F\u56E0\u201D\u3002</p><p>DeMarco \u544A\u8BC9\u6211,\u5927\u4F17\u5E02\u573A\u8F6F\u4EF6\u5305\u63D0\u4F9B\u4E86\u6570\u636E\u5E93\u7CFB\u7EDF\u7B49\u901A\u7528\u529F\u80FD,\u5145\u5206\u5730\u51CF\u8F7B\u4E86\u538B\u529B,\u51CF\u5C11\u4E86\u5904\u5728\u91CD\u7528\u6A21\u5757\u8FB9\u7F18\u7684\u5F00\u53D1\u3002 \u201C\u4E0D\u7BA1\u600E\u6837,\u91CD\u7528\u7684\u6A21\u5757\u4E00\u822C\u662F\u4E00\u4E9B\u901A\u7528\u529F\u80FD\u3002\u201D</p><p>Parnas \u5199\u9053:</p><blockquote><p>\u91CD\u7528\u662F\u4E00\u4EF6\u8BF4\u8D77\u6765\u5BB9\u6613,\u505A\u8D77\u6765\u96BE\u7684\u4E8B\u60C5\u3002\u5B83\u540C\u65F6\u9700\u8981\u826F\u597D\u7684\u8BBE\u8BA1\u548C\u6587\u6863\u3002\u5373\u4F7F\u6211\u4EEC\u770B\u5230\u4E86\u5E76\u4E0D\u5341\u5206\u5E38\u89C1\u7684\u4F18\u79C0\u8BBE\u8BA1,\u4F46\u5982\u679C\u6CA1\u6709\u597D\u7684\u6587\u6863,\u6211\u4EEC\u4E5F\u4E0D\u4F1A\u770B\u5230\u80FD\u91CD\u7528\u7684\u6784\u4EF6\u3002</p></blockquote><p>Ken Brooks \u5173\u4E8E\u9884\u6D4B\u4EA7\u54C1\u901A\u7528\u5316\u7684\u4E00\u4E9B\u56F0\u96BE\u7684\u8BC4\u8BBA:\u201C\u6211\u4E0D\u65AD\u5730\u8FDB\u884C\u4FEE\u6539,\u5373\u4F7F\u5728\u7B2C\u4E94\u6B21\u4F7F\u7528\u6211\u81EA\u5DF1\u7684\u4E2A\u4EBA\u7528\u6237\u754C\u9762\u5E93\u7684\u65F6\u5019\u3002\u201D</p><p>\u771F\u6B63\u7684\u91CD\u7528\u4F3C\u4E4E\u624D\u521A\u521A\u5F00\u59CB\u3002Jones \u62A5\u544A,\u5728\u5F00\u653E\u5E02\u573A\u4E0A\u4EC5\u6709\u5C11\u91CF\u7684\u91CD\u7528\u4EE3\u7801\u6A21\u5757,\u5B83\u4EEC\u7684\u4EF7\u683C\u5728\u5E38\u89C4\u5F00\u53D1\u6210\u672C\u7684 1%~20% \u3002DeMacro \u8BF4:</p><blockquote><p>\u5BF9\u6574\u4E2A\u91CD\u7528\u73B0\u8C61,\u6211\u53D8\u5F97\u6709\u4E9B\u6C14\u9981\u3002\u5BF9\u4E8E\u91CD\u7528,\u73B0\u6709\u7406\u8BBA\u51E0\u4E4E\u662F\u6574\u4F53\u7F3A\u4E4F\u3002\u65F6\u95F4\u8BC1\u660E\u4E86\u4F7F\u6A21\u5757\u80FD\u591F\u91CD\u7528\u7684\u6210\u672C\u975E\u5E38\u9AD8\u3002</p></blockquote><p>Yourdon \u4F30\u8BA1\u4E86\u8FD9\u4E2A\u9AD8\u6602\u7684\u8D39\u7528: \u201C\u4E00\u4E2A\u826F\u597D\u7684\u7ECF\u9A8C\u6CD5\u5219\u662F\u53EF\u91CD\u7528\u7684\u6784\u4EF6\u7684\u5DE5\u4F5C\u91CF\u662F\u2018\u4E00\u6B21\u6027\u2019\u6784\u4EF6\u7684\u4E24\u500D\u3002 \u201D\u5728\u7B2C\u4E00\u7AE0\u7684\u8BA8\u8BBA\u4E2D,\u6211\u89C2\u5BDF\u5230\u4E86\u771F\u6B63\u4EA7\u54C1\u5316\u6784\u4EF6\u6240\u9700\u7684\u6210\u672C\u3002\u56E0\u6B64,\u6211\u5BF9\u5DE5\u4F5C\u91CF\u6BD4\u7387\u7684\u4F30\u8BA1\u662F\u4E09\u500D\u3002</p><p>\u663E\u7136,\u6211\u4EEC\u6B63\u5728\u770B\u5230\u5F88\u591A\u91CD\u7528\u7684\u5F62\u5F0F\u548C\u53D8\u5316,\u4F46\u79BB\u6211\u4EEC\u6240\u671F\u671B\u7684\u8FD8\u8FDC,\u8FD8\u6709\u5F88\u591A\u9700\u8981\u5B66\u4E60\u7684\u5730\u65B9\u3002</p><h2 id="\u5B66\u4E60\u5927\u91CF\u7684\u8BCD\u6C47\u2014\u2014\u5BF9\u8F6F\u4EF6\u91CD\u7528\u7684\u4E00\u4E2A\u53EF\u9884\u89C1-\u4F46\u8FD8\u6CA1\u6709\u88AB\u9884\u8A00\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5B66\u4E60\u5927\u91CF\u7684\u8BCD\u6C47\u2014\u2014\u5BF9\u8F6F\u4EF6\u91CD\u7528\u7684\u4E00\u4E2A\u53EF\u9884\u89C1-\u4F46\u8FD8\u6CA1\u6709\u88AB\u9884\u8A00\u7684\u95EE\u9898" aria-hidden="true">#</a> \u5B66\u4E60\u5927\u91CF\u7684\u8BCD\u6C47\u2014\u2014\u5BF9\u8F6F\u4EF6\u91CD\u7528\u7684\u4E00\u4E2A\u53EF\u9884\u89C1,\u4F46\u8FD8\u6CA1\u6709\u88AB\u9884\u8A00\u7684\u95EE\u9898</h2><p>\u601D\u7D22\u7684\u5C42\u6B21\u8D8A\u9AD8,\u6240\u9700\u8981\u5904\u7406\u7684\u57FA\u672C\u601D\u8003\u8981\u7D20\u4E5F\u5C31\u8D8A\u591A\u3002\u56E0\u6B64,\u7F16\u7A0B\u8BED\u8A00\u6BD4\u673A\u5668\u8BED\u8A00\u66F4\u52A0\u590D\u6742,\u800C\u81EA\u7136\u8BED\u8A00\u7684\u590D\u6742\u7A0B\u5EA6\u66F4\u9AD8\u3002\u9AD8\u7EA7\u8BED\u8A00\u6709\u66F4\u5E7F\u6CDB\u7684\u8BCD\u6C47\u91CF\u3001\u66F4\u590D\u6742\u7684\u8BED\u6CD5\u4EE5\u53CA\u66F4\u52A0\u4E30\u5BCC\u7684\u8BED\u4E49\u3002</p><p>\u4F5C\u4E3A\u4E00\u4E2A\u79D1\u76EE,\u6211\u4EEC\u5E76\u6CA1\u6709\u5C31\u7A0B\u5E8F\u91CD\u7528\u7684\u5B9E\u9645\u60C5\u51B5,\u4ED4\u7EC6\u8003\u8651\u5B83\u8574\u6DB5\u7684\u610F\u4E49\u3002\u4E3A\u4E86\u63D0\u9AD8\u8D28\u91CF\u548C\u751F\u4EA7\u7387,\u6211\u4EEC\u9700\u8981\u901A\u8FC7\u7ECF\u8FC7\u8C03\u8BD5\u7684\u5927\u578B\u8981\u7D20\u6765\u6784\u5EFA\u7CFB\u7EDF,\u5728\u7F16\u7A0B\u8BED\u8A00\u4E2D,\u8FD9\u4E9B\u51FD\u6570\u7684\u7EA7\u522B\u8FDC\u8FDC\u9AD8\u4E8E\u8BED\u53E5\u3002\u6240\u4EE5,\u65E0\u8BBA\u91C7\u7528\u5BF9\u8C61\u7C7B\u5E93\u8FD8\u662F\u51FD\u6570\u5E93\u7684\u65B9\u5F0F,\u6211\u4EEC\u5FC5\u987B\u9762\u5BF9\u6211\u4EEC\u7F16\u7A0B\u8BCD\u6C47\u89C4\u6A21\u5F7B\u5E95\u6269\u5927\u7684\u4E8B\u5B9E\u3002\u5BF9\u4E8E\u91CD\u7528,\u8BCD\u6C47\u5B66\u4E60\u5E76\u4E0D\u662F\u601D\u7EF4\u969C\u788D\u4E2D\u7684\u4E00\u5C0F\u90E8\u5206\u3002</p><p>\u73B0\u5728\u4EBA\u4EEC\u62E5\u6709\u6210\u5458\u8D85\u8FC7 3000 \u4E2A\u7684\u7C7B\u5E93\u3002\u5F88\u591A\u5BF9\u8C61\u9700\u8981 10 \u5230 20 \u4E2A\u53C2\u6570\u548C\u53EF\u9009\u53D8\u91CF\u7684\u8BF4\u660E\u3002\u5982\u679C\u60F3\u83B7\u5F97\u6240\u6709\u6F5C\u5728\u7684\u91CD\u7528,\u4EFB\u4F55\u4F7F\u7528\u7C7B\u5E93\u7F16\u7A0B\u7684\u4EBA\u5458\u5FC5\u987B\u5B66\u4E60\u5176\u6210\u5458\u7684\u8BED\u6CD5(\u5916\u90E8\u63A5\u53E3)\u548C\u8BED\u4E49(\u8BE6\u7EC6\u7684\u529F\u80FD\u884C\u4E3A)\u3002</p><p>\u8FD9\u9879\u5DE5\u4F5C\u5E76\u4E0D\u662F\u6CA1\u6709\u5E0C\u671B\u7684\u3002\u4E00\u822C\u4EBA\u65E5\u5E38\u4F7F\u7528\u7684\u8BCD\u6C47\u8D85\u8FC7 10,000 \u4E2A,\u53D7\u8FC7\u6559\u80B2\u7684\u4EBA\u8FDC\u591A\u4E8E\u8FD9\u4E2A\u6570\u76EE\u3002\u53E6\u5916,\u6211\u4EEC\u5728\u81EA\u7136\u800C\u7136\u5730\u5B66\u4E60\u7740\u8BED\u6CD5\u548C\u975E\u5E38\u5FAE\u5999\u7684\u8BED\u4E49\u3002\u6211\u4EEC\u53EF\u4EE5\u6B63\u786E\u5730\u533A\u5206 \u5DE8\u5927\u3001\u5927\u3001\u8FBD\u9614\u3001\u5927\u91CF\u548C\u5E9E\u5927 \u3002\u4EBA\u4EEC\u4E0D\u4F1A\u8BF4:\u5E9E\u5927\u7684\u6C99\u6F20\u6216\u8005\u8FBD\u9614\u7684\u5927\u8C61\u3002</p><p>\u5BF9\u8F6F\u4EF6\u91CD\u7528\u95EE\u9898,\u6211\u4EEC\u9700\u8981\u7814\u7A76\u9002\u5F53\u7684\u5B66\u95EE,\u4E86\u89E3\u4EBA\u4EEC\u5982\u4F55\u62E5\u6709\u8BED\u8A00\u3002\u4E00\u4E9B\u7ECF\u9A8C\u6559\u8BAD\u662F\u663E\u800C\u6613\u89C1\u7684:</p><ul><li>\u4EBA\u4EEC\u5728\u4E0A\u4E0B\u6587\u4E2D\u5B66\u4E60,\u6240\u4EE5\u6211\u4EEC\u9700\u8981\u51FA\u7248\u4E00\u4E9B\u590D\u5408\u4EA7\u54C1\u7684\u4F8B\u5B50,\u800C\u4E0D\u4EC5\u4EC5\u662F\u96F6\u90E8\u4EF6\u7684\u5E93\u3002</li><li>\u4EBA\u4EEC\u53EA\u4F1A\u8BB0\u5FC6\u80CC\u8BF5\u5355\u8BCD\u3002\u8BED\u6CD5\u548C\u8BED\u4E49\u662F\u5728\u4E0A\u4E0B\u6587\u4E2D,\u901A\u8FC7\u4F7F\u7528\u9010\u6E10\u5730\u5B66\u4E60\u3002</li><li>\u4EBA\u4EEC\u6839\u636E\u8BED\u4E49\u4E0A\u7684\u5206\u7C7B\u5BF9\u8BCD\u6C47\u7EC4\u5408\u89C4\u5219\u8FDB\u884C\u5206\u7EC4,\u800C\u4E0D\u662F\u901A\u8FC7\u6BD4\u8F83\u5BF9\u8C61\u5B50\u96C6\u3002</li></ul><h2 id="\u5B50\u5F39\u7684\u672C\u8D28\u2014\u2014\u5F62\u52BF\u6CA1\u6709\u53D1\u751F\u6539\u53D8" tabindex="-1"><a class="header-anchor" href="#\u5B50\u5F39\u7684\u672C\u8D28\u2014\u2014\u5F62\u52BF\u6CA1\u6709\u53D1\u751F\u6539\u53D8" aria-hidden="true">#</a> \u5B50\u5F39\u7684\u672C\u8D28\u2014\u2014\u5F62\u52BF\u6CA1\u6709\u53D1\u751F\u6539\u53D8</h2><p>\u73B0\u5728,\u6211\u4EEC\u56DE\u5230\u57FA\u672C\u95EE\u9898\u3002\u590D\u6742\u6027 \u662F \u6211\u4EEC\u8FD9\u4E2A\u884C\u4E1A\u7684\u5C5E\u6027,\u800C\u4E14\u590D\u6742\u6027\u662F\u6211\u4EEC\u4E3B\u8981\u7684\u9650\u5236\u3002R.L.Glass \u5728 1988 \u5E74\u7684\u6587\u5B57\u7CBE\u786E\u5730\u603B\u7ED3\u4E86\u6211\u5728 1995 \u5E74\u7684\u770B\u6CD5:</p><blockquote><p>\u53C8\u600E\u4E48\u6837\u5462?Parnas \u548C Brooks \u4E0D\u662F\u5DF2\u7ECF\u544A\u8BC9\u6211\u4EEC\u4E86\u5417?\u8F6F\u4EF6\u5F00\u53D1\u662F\u4E00\u4EF6\u68D8\u624B\u7684\u4E8B\u60C5,\u524D\u65B9\u5E76\u4E0D\u4F1A\u6709\u9B54\u672F\u822C\u7684\u89E3\u51B3\u65B9\u6848\u3002\u73B0\u5728\u662F\u4ECE\u4E1A\u8005\u7814\u7A76\u548C\u5206\u6790\u9769\u547D\u6027\u8FDB\u5C55\u7684\u65F6\u523B,\u800C\u4E0D\u662F\u7B49\u5F85\u6216\u5E0C\u671B\u5B83\u7684\u51FA\u73B0\u3002</p><p>\u8F6F\u4EF6\u9886\u57DF\u4E2D\u7684\u4E00\u4E9B\u4EBA\u53D1\u73B0\u8FD9\u662F\u4E00\u5E45\u4F7F\u4EBA\u6CC4\u6C14\u7684\u56FE\u753B\u3002\u4ED6\u4EEC\u662F\u90A3\u4E9B\u4F9D\u7136\u8BA4\u4E3A\u7A81\u7834\u8FD1\u5728\u773C\u524D\u7684\u4EBA\u4EEC\u3002</p><p>\u4F46\u662F\u6211\u4EEC\u4E2D\u7684\u4E00\u4E9B\u2014\u2014\u90A3\u4E9B\u975E\u5E38\u56FA\u6267,\u4EE5\u81F4\u4E8E\u53EF\u4EE5\u8BA4\u4E3A\u662F\u73B0\u5B9E\u4E3B\u4E49\u8005\u7684\u4EBA\u2014\u2014\u628A\u5B83\u770B\u6210\u662F\u6E05\u65B0\u7684\u7A7A\u6C14\u3002\u6211\u4EEC\u7EC8\u4E8E\u53EF\u4EE5\u5C06\u7126\u70B9\u96C6\u4E2D\u5728\u66F4\u52A0\u53EF\u884C\u7684\u4E8B\u60C5\u4E0A,\u800C\u4E0D\u662F\u7A7A\u4E2D\u7684\u9985\u997C\u3002\u73B0\u5728,\u6709\u53EF\u80FD,\u6211\u4EEC\u53EF\u4EE5\u5728\u8F6F\u4EF6\u751F\u4EA7\u7387\u4E0A\u53D6\u5F97\u9010\u6B65\u7684\u8FDB\u5C55,\u800C\u4E0D\u662F\u7B49\u5F85\u4E0D\u5927\u53EF\u80FD\u5230\u6765\u7684\u7A81\u7834\u3002</p></blockquote>',105),a=[l];function s(n,i){return e(),o("div",null,a)}var u=p(t,[["render",s],["__file","\u518D\u8BBA\u6CA1\u6709\u94F6\u5F39.html.vue"]]);export{u as default};
