import{_ as p,o as t,c as o,e as u}from"./app.6bf529a6.js";const q={},a=u('<h1 id="\u52A8\u6F2B-\u5927\u5218-\u51AF\u53CB\u5170-\u6211\u4EEC\u4E3A\u4EC0\u4E48\u8BFB\u54F2\u5B66" tabindex="-1"><a class="header-anchor" href="#\u52A8\u6F2B-\u5927\u5218-\u51AF\u53CB\u5170-\u6211\u4EEC\u4E3A\u4EC0\u4E48\u8BFB\u54F2\u5B66" aria-hidden="true">#</a> \u52A8\u6F2B, \u5927\u5218, \u51AF\u53CB\u5170: \u6211\u4EEC\u4E3A\u4EC0\u4E48\u8BFB\u54F2\u5B66</h1><h1 id="\u4ECE\u5927\u5218\u5230\u52A8\u6F2B" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u5927\u5218\u5230\u52A8\u6F2B" aria-hidden="true">#</a> \u4ECE\u5927\u5218\u5230\u52A8\u6F2B</h1><p>\u7FFB\u8F6C\u7535\u53F0\u300A\u4E2A\u4EBA\u4E3B\u4E49\u4E0E\u5E73\u6C11\u793E\u4F1A -- \u81EA\u7136\u6210\u4E3A\u8BC5\u5492\u300B\u4E00\u671F\u91CC, \u63D0\u5230\u4E86\u5218\u6148\u6B23\u7684\u673A\u68B0\u5B87\u5B99\u89C2.</p><p>\u987A\u7740\u5927\u5218\u8FD9\u4E2A\u8BDD\u9898, \u5C0F\u674E\u8001\u5E08\u53C8\u63D0\u5230, \u73B0\u5728\u4E4B\u6240\u4EE5\u6709\u5F88\u591A\u4EBA\u89C9\u5F97\u5927\u5218\u7684\u300A\u4E09\u4F53\u300B\u662F\u4E00\u90E8\u975E\u5E38\u6DF1\u523B\u7684\u4F5C\u54C1, \u5F88\u6709\u53EF\u80FD\u662F\u56E0\u4E3A\u951A\u5B9A\u6548\u5E94: \u5BF9\u4E8E\u4ED6\u4EEC\u6765\u8BF4, \u300A\u4E09\u4F53\u300B\u53EF\u80FD\u662F\u4ED6\u4EEC\u8FD1\u4E09\u5E74\u6765\u770B\u8FC7\u7684\u552F\u4E00\u4E00\u672C &quot;\u6B63\u7ECF&quot; \u7684\u6587\u5B66\u4F5C\u54C1.</p><p>\u7D27\u63A5\u7740, \u5C0F\u674E\u8001\u5E08\u53C8\u987A\u7740\u951A\u5B9A\u6548\u5E94\u8FD9\u4E2A\u8BDD\u9898, \u8BF4\u5230\u52A8\u6F2B\u4F5C\u54C1\u6DF1\u5EA6\u7684\u95EE\u9898. \u4ED6\u8BF4, \u603B\u662F\u6709\u4EBA\u95EE\u4ED6, \u5173\u4E8E\u67D0\u90E8\u52A8\u6F2B\u4F5C\u54C1\u601D\u60F3\u6DF1\u5EA6\u7684\u95EE\u9898, \u4ED6\u8BA4\u4E3A\u52A8\u6F2B\u4F5C\u54C1\u5176\u5B9E\u6CA1\u4EC0\u4E48\u6DF1\u5EA6 ( \u9664\u300A\u5251\u98CE\u4F20\u5947\u300B \u4E4B\u5916 ).</p><p>\u542C\u5230\u90A3\u91CC\u65F6, \u6211\u4FBF\u8D77\u4E86\u4EE5\u5C0F\u4EBA\u4E4B\u5FC3\u5EA6\u8BF8\u541B\u4E4B\u8179\u7684\u60F3\u6CD5. &quot;\u5927\u6982\u4F1A\u6709\u4EBA\u63D0\u51FA\u8FD9\u6837\u7684\u5F02\u8BAE\u5427: \u6CA1\u4EC0\u4E48\u601D\u60F3\u6DF1\u5EA6\u7684\u4F5C\u54C1\u5C31\u4E0D\u503C\u5F97\u53BB\u8BFB\u4E48?&quot;</p><p>\u7ED3\u679C\u53D1\u73B0, \u6211\u679C\u7136\u662F\u5C0F\u4EBA\u800C\u8BF8\u541B\u679C\u7136\u4E0D\u662F. \u56E0\u4E3A\u5E76\u6CA1\u6709\u4EBA\u63D0\u51FA\u8FD9\u6837\u7684\u5F02\u8BAE.</p><p>\u4F46\u6211\u8D77\u5C0F\u4EBA\u4E4B\u5FC3, \u5012\u662F\u4E8B\u51FA\u6709\u56E0\u7684: \u6CA1\u4EC0\u4E48\u601D\u60F3\u6DF1\u5EA6\u7684\u4F5C\u54C1\u662F\u4E0D\u662F\u503C\u5F97\u53BB\u8BFB, \u786E\u5B9E\u56F0\u6270\u8FC7\u6211\u5F88\u957F\u65F6\u95F4.</p><h1 id="\u51AF\u53CB\u5170\u4E0E\u725F\u5B97\u4E09" tabindex="-1"><a class="header-anchor" href="#\u51AF\u53CB\u5170\u4E0E\u725F\u5B97\u4E09" aria-hidden="true">#</a> \u51AF\u53CB\u5170\u4E0E\u725F\u5B97\u4E09</h1><p>\u9886\u6211\u5165\u5112\u5B66\u95E8\u7684\u8BB8\u8001\u5E08, \u5173\u4E8E\u65B0\u5112\u5BB6\u7684\u4E66\u5E94\u8BE5\u8BFB\u90A3\u4E9B, \u6709\u8FD9\u6837\u4E00\u4E2A\u8BF4\u6CD5: \u65B0\u5112\u5BB6\u7684\u4E66\u53EA\u6709\u725F\u5B97\u4E09\u7684\u6700\u503C\u5F97\u8BFB, \u50CF\u4EC0\u4E48\u51AF\u53CB\u5170\u4E4B\u7C7B\u7684\u4E66, \u6DF1\u5EA6\u90FD\u662F\u4E0D\u591F\u7684.</p><p>\u5F53\u6211\u542C\u5230\u8FD9\u53E5\u8BDD\u65F6, \u6211\u53EA\u80FD &quot;\u5475\u5475&quot; \u4EE5\u5BF9. \u56E0\u4E3A\u5173\u4E8E\u5112\u5BB6, \u6211\u8BFB\u7684\u6700\u591A\u7684, \u5BF9\u6211\u5F71\u54CD\u6700\u5927\u7684\u5012\u8FD8\u6B63\u662F\u51AF\u53CB\u5170.</p><p>\u8BF4\u8D77\u539F\u56E0\u6765, \u5927\u6982\u6709\u4E24\u4E2A:</p><ul><li>\u51AF\u53CB\u5170\u6BD4\u8F83\u81EA\u5DF1\u4E0E\u91D1\u5CB3\u9716\u8BF4, \u4ED6\u64C5\u957F\u4E8E\u5C06\u590D\u6742\u7684\u4E8B\u60C5\u8BF4\u7B80\u5355, \u800C\u91D1\u5CB3\u9716\u64C5\u957F\u628A\u7B80\u5355\u7684\u4E8B\u60C5\u8BF4\u590D\u6742. \u51AF\u53CB\u5170\u5BF9\u81EA\u5DF1\u7684\u8FD9\u4E2A\u8BC4\u4EF7\u662F\u5BF9\u7684, \u4ED6\u7684\u4E66\u786E\u5B9E\u8BFB\u7684\u4E0D\u5403\u529B.</li><li>\u65B0\u5112\u5BB6\u591A\u662F\u8981\u5C06\u5112\u5B66\u4E0E\u54F2\u5B66\u8FDB\u884C\u7ED3\u5408, \u725F\u5B97\u4E09\u8BD5\u56FE\u7ED3\u5408\u5112\u5B66\u548C\u5EB7\u5FB7\u54F2\u5B66, \u5510\u541B\u6BC5\u8BD5\u56FE\u7ED3\u5408\u5112\u5B66\u4E0E\u67CF\u683C\u68EE\u54F2\u5B66, \u800C\u51AF\u53CB\u5170\u5219\u8BD5\u56FE\u7ED3\u5408\u5112\u5B66\u548C\u53E4\u5E0C\u814A\u54F2\u5B66.\u800C\u6211\u6B63\u597D\u5BF9\u53E4\u5E0C\u814A\u54F2\u5B66\u683C\u5916\u611F\u5174\u8DA3.</li></ul><p>\u4ECE\u601D\u60F3\u6DF1\u5EA6\u4E0A\u8BB2\u6700\u503C\u5F97\u8BFB\u7684\u725F\u5B97\u4E09\u7684\u4E66\u6211\u5012\u4E0D\u662F\u4E0D\u53BB\u8BFB, \u6211\u4E5F\u8BD5\u7740\u60F3\u8BFB\u8BFB\u300A\u4E2D\u897F\u54F2\u5B66\u4E4B\u4F1A\u901A\u5341\u56DB\u8BB2\u300B, \u4F46\u662F\u771F\u7684\u8BFB\u4E0D\u4E86.</p><p>\u4E66\u91CC\u6BCF\u4E2A\u5B57\u90FD\u8BA4\u8BC6, \u7EC4\u6210\u53E5\u5B50\u4E5F\u80FD\u77E5\u9053\u662F\u4EC0\u4E48\u610F\u601D, \u4F46\u4E32\u6210\u7684\u903B\u8F91\u6211\u5374\u603B\u662F\u8DDF\u4E0D\u4E0A. \u8BFB\u8FC7\u4E09\u4E94\u9875\u540E, \u611F\u89C9\u5C31\u662F\u5728\u8BFB\u5929\u4E66\u4E86.</p><p>\u8BDD\u8BF4\u6211\u8BFB\u5EB7\u5FB7\u4E5F\u662F\u5982\u6B64, \u51E0\u6B21\u60F3\u8BFB\u300A\u7EAF\u7CB9\u7406\u6027\u6279\u5224\u300B, \u90FD\u662F\u8BFB\u4E86\u4E09\u4E94\u9875\u5C31\u653E\u5F03\u4E86.</p><p>\u6240\u4EE5\u4E00\u4E2A\u95EE\u9898\u5C31\u6A2A\u4E98\u5728\u6211\u9762\u524D: \u5982\u679C\u601D\u60F3\u6DF1\u5EA6\u8DB3\u591F\u7684\u6211\u8BFB\u4E0D\u4E86, \u800C\u6DF1\u5EA6\u4E0D\u591F\u7684\u53C8\u4E0D\u503C\u5F97\u53BB\u8BFB, \u90A3\u6211\u8BE5\u600E\u4E48\u529E\u5462?</p><h1 id="\u4E3A\u4EC0\u4E48\u8BFB\u54F2\u5B66\u4E66" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u8BFB\u54F2\u5B66\u4E66" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u8BFB\u54F2\u5B66\u4E66</h1><p>\u5F53\u7136\u6211\u4EEC\u5E94\u8BE5\u56DE\u5230\u90A3\u4E2A\u95EE\u9898\u53BB, \u6211\u4EEC\u4E3A\u4EC0\u4E48\u8981\u53BB\u8BFB\u8FD9\u4E9B\u4F5C\u54C1.</p><p>\u5982\u679C\u771F\u6709\u5C0F\u4EBA\u5982\u6211\u8005, \u63D0\u51FA\u5F02\u8BAE: \u6CA1\u4EC0\u4E48\u601D\u60F3\u6DF1\u5EA6\u7684\u52A8\u6F2B\u4F5C\u54C1\u5C31\u4E0D\u503C\u5F97\u53BB\u770B\u4E48? \u5927\u53EF\u8FD9\u6837\u6765\u56DE\u590D: \u52A8\u6F2B\u672C\u8EAB\u5C31\u4E0D\u662F\u8981\u63D0\u4F9B\u601D\u60F3\u6DF1\u5EA6\u7684, \u5B83\u662F\u5546\u4E1A\u4F5C\u54C1, \u672C\u6765\u7684\u76EE\u7684\u662F\u4E3A\u4E86\u770B\u4E86\u4EA7\u751F\u6109\u5FEB, \u5982\u679C\u6709\u4EC0\u4E48\u601D\u60F3\u6DF1\u5EA6\u53CD\u800C\u662F\u4E00\u79CD\u526F\u4EA7\u54C1.</p><p>\u4F46\u5BF9\u5BF9\u4E8E\u51AF\u53CB\u5170\u7684\u8FD9\u4E2A\u95EE\u9898, \u5C31\u4E0D\u80FD\u8FD9\u6837\u6765\u56DE\u590D\u4E86. \u867D\u7136\u6211\u4EEC\u4E5F\u80FD\u8BF4, \u6211\u4EEC\u8BFB\u54F2\u5B66\u4E66\u662F\u4E3A\u4E86 &quot;\u83B7\u5F97\u4E00\u79CD\u601D\u7EF4\u7684\u5FEB\u611F&quot;, \u4F46\u5982\u679C\u6211\u4EEC\u8BDA\u5B9E\u7684\u60F3, \u8BFB\u54F2\u5B66\u80FD\u6709 &quot;\u601D\u7EF4\u7684\u5FEB\u611F&quot; \u5012\u5982\u770B\u52A8\u6F2B\u800C\u611F\u5230\u601D\u60F3\u6DF1\u5EA6\u4E00\u6837, \u662F\u79CD\u526F\u4EA7\u54C1. \u56E0\u4E3A\u8BFB\u54F2\u5B66\u4E66\u5E76\u4E0D\u5FC5\u7136\u6709\u8FD9\u79CD\u5FEB\u611F.</p><p>\u6216\u8005\u6211\u4EEC\u8BF4, \u8BFB\u54F2\u5B66\u4E66, \u6211\u4EEC\u6B63\u662F\u8981\u83B7\u5F97\u4E00\u79CD\u601D\u60F3\u7684\u6DF1\u5EA6. \u5373\u662F\u5982\u6B64, \u662F\u4E0D\u662F\u610F\u5473\u7740\u5BF9\u4E8E\u54F2\u5B66\u4E66\u6765\u8BF4, \u660E\u77E5\u6709\u725F\u5B97\u4E09\u8FD8\u53BB\u8BFB\u51AF\u53CB\u5170\u662F\u4EF6\u6781\u6CA1\u6709\u9053\u7406\u7684\u4E8B\u5462?</p><p>\u5373\u7136\u6211\u4EEC\u662F\u8981\u83B7\u5F97\u601D\u60F3\u6DF1\u5EA6, \u627E\u4E2A\u6700\u6DF1\u7684, \u83B7\u5F97\u4E4B\u5C31\u597D\u4E86, \u4F55\u82E6\u660E\u77E5\u6DF1\u7684\u5728\u90A3\u91CC, \u8FD8\u8981\u627E\u4E2A\u6D45\u7684\u6765\u83B7\u5F97\u5462?</p><p>\u5C31\u5982\u79D1\u5B66, \u73B0\u4EE3\u79D1\u5B66\u662F\u4ECE\u53E4\u4EE3\u79D1\u5B66\u53D1\u5C55\u6765\u7684, \u4F46\u662F\u9664\u975E\u8981\u6CBB\u79D1\u5B66\u53F2, \u5B66\u4E60\u79D1\u5B66\u771F\u63A5\u5B66\u73B0\u4EE3\u79D1\u5B66\u5C31\u597D\u4E86, \u4E0D\u5FC5\u4ECE\u4E9A\u91CC\u58EB\u591A\u5FB7\u5F00\u59CB\u5B66\u7269\u7406\u5B66.</p><h1 id="\u638C\u63E1\u4E00\u79CD\u610F\u4E49" tabindex="-1"><a class="header-anchor" href="#\u638C\u63E1\u4E00\u79CD\u610F\u4E49" aria-hidden="true">#</a> \u638C\u63E1\u4E00\u79CD\u610F\u4E49</h1><p>\u6BD5\u7ADF\u4EBA\u5FC3\u4E0D\u662F\u4E00\u56E2\u8840\u8089, \u601D\u60F3\u7684\u6DF1\u5EA6\u4E5F\u4E0D\u80FD\u7528\u8111\u6C9F\u7684\u6DF1\u5EA6\u6765\u4E08\u91CF.</p><p>\u7136\u800C &quot;\u83B7\u5F97\u4E00\u79CD\u601D\u60F3\u6DF1\u5EA6&quot; \u53C8\u662F\u4EC0\u4E48\u610F\u601D\u5462?</p><p>\u5F53\u6211\u4EEC\u8BF4\u5927\u5218\u7684\u300A\u4E09\u4F53\u300B\u7684\u601D\u60F3\u6DF1\u5EA6, \u52A8\u6F2B\u7684\u601D\u60F3\u6DF1\u5EA6, \u51AF\u53CB\u5170\u548C\u725F\u5B97\u4E09\u54F2\u5B66\u7684\u601D\u60F3\u6DF1\u5EA6, \u591A\u591A\u5C11\u5C11\u90FD\u662F\u5728\u8BF4\u4E00\u79CD\u610F\u4E49, \u90A3\u4E9B\u6587\u5B57, \u753B\u9762, \u903B\u8F91\u80CC\u540E\u7684\u610F\u4E49.</p><p>\u5F53\u6211\u4EEC\u83B7\u5F97\u4E00\u79CD\u601D\u60F3\u6DF1\u5EA6\u65F6, \u5E76\u4E0D\u5982\u83B7\u5F97\u5DE5\u8D44\u4E00\u6837, \u62FF\u6765\u63E3\u515C\u91CC, \u800C\u662F\u638C\u63E1\u4E00\u79CD\u610F\u4E49.</p><p>\u4F46\u662F, \u8BF4\u638C\u63E1\u610F\u4E49\u662F\u5408\u9002\u7684\u4E48? \u9648\u5609\u6620\u4E0D\u662F\u8BF4, &quot;\u5E76\u4E0D\u662F\u6211\u4EEC\u638C\u63E1\u771F\u7406, \u800C\u662F\u771F\u7406\u638C\u63E1\u6211\u4EEC&quot;.</p><p>\u9648\u5609\u6620\u4E3E\u4F8B\u8BF4, \u4E24\u4EBA\u4E89\u8BBA\u4E00\u4E2A\u95EE\u9898, \u5F00\u59CB\u4E24\u4EBA\u90FD\u89C9\u5F97 &quot;\u6211\u662F\u5BF9\u7684&quot;, \u800C\u7EC8\u4E8E\u4E00\u4E2A\u89C9\u5F97\u81EA\u5DF1\u7684\u60F3\u6CD5\u662F\u9519\u7684, \u8FD9\u65F6\u4E0E\u5176\u8BF4 &quot;\u4E00\u4EBA\u901A\u8FC7\u9053\u7406\u6218\u80DC\u4E86\u53E6\u4E00\u4EBA&quot;, \u66F4\u597D\u7684\u8BF4\u6CD5\u662F &quot;\u771F\u7406\u5728\u4E24\u4EBA\u8BDA\u6073\u7684\u8BA8\u8BBA\u4E2D\u663E\u73B0&quot;.</p><p>\u6CA1\u6709\u8C2C\u8BEF\u5C31\u6CA1\u6709\u771F\u7406, \u771F\u7406\u662F\u5BF9\u6210\u89C1\u7684\u514B\u670D. \u56E0\u6B64\u8BDA\u6073\u7684\u8BA8\u8BBA\u4E2D\u6CA1\u6709\u8C01\u8D62\u4E86\u8C01, \u800C\u662F\u771F\u7406\u5728\u514B\u670D\u6210\u89C1\u4E2D\u663E\u73B0\u4E86\u51FA\u6765.</p><p>\u6D77\u5FB7\u683C\u5C14\u8BF4: \u4EBA\u4E4B\u4E3A\u4EBA, \u5FC5\u9700\u5C06\u771F\u7406\u8BBE\u4E3A\u524D\u63D0, \u8FD9\u5E76\u4E0D\u662F\u6211\u4E00\u5F00\u59CB\u5C31\u8BA4\u8BC6\u4E86\u771F\u7406, \u800C\u662F\u8BF4, \u5C3D\u7BA1\u6211\u8BA4\u4E3A\u6211\u662F\u5BF9\u7684, \u4F46\u6211\u627F\u8BA4\u6211\u53EF\u80FD\u662F\u9519\u7684\u6216\u9700\u8981\u4FEE\u6B63\u7684.</p><p>&quot;\u6211\u662F\u5BF9\u7684&quot; \u91CC, &quot;\u5BF9&quot; \u662F\u6838\u5FC3, &quot;\u6211&quot; \u662F\u9644\u5C5E. \u5728\u8FD9\u4E2A\u610F\u4E49\u4E0A, \u6211\u4EEC\u624D\u8BF4 &quot;\u5E76\u4E0D\u662F\u6211\u4EEC\u638C\u63E1\u771F\u7406, \u800C\u662F\u771F\u7406\u638C\u63E1\u6211\u4EEC&quot;.</p><p>\u6CA1\u6709\u8C2C\u8BEF\u5C31\u6CA1\u6709\u771F\u7406, \u4E5F\u662F\u8BF4, \u771F\u7406\u548C\u6210\u89C1\u662F\u540C\u6839\u7684. \u8FD9\u4E2A\u6839\u5C31\u690D\u5728\u8BDA\u6073\u4E2D. \u6210\u89C1\u4E0D\u6B63\u662F\u5C06\u8C2C\u8BEF\u60F3\u6CD5\u5F53\u6210 &quot;\u6211\u662F\u5BF9\u7684&quot; \u7684\u4E48?</p><p>\u8BDA\u6073\u662F\u4E00\u79CD\u89C2\u6CE8. \u5982\u679C\u6211\u4EEC\u4E0D\u5173\u6CE8\u4E00\u4EF6\u4E8B\u7269, \u6211\u4EEC\u8FDE\u6210\u89C1\u90FD\u4E0D\u4F1A\u6709. &quot;\u7741\u5F00\u773C\u5C31\u80FD\u770B, \u800C\u6211\u4EEC\u6709\u65F6\u5374\u89C6\u800C\u4E0D\u89C1.&quot;</p><p>\u5F53\u6211\u4EEC\u4E0D\u89C2\u6CE8\u65F6, \u6211\u4EEC\u4E0D\u662F\u4E5F\u4F1A\u8FD9\u6837\u8BF4\u4E48: &quot;\u8FD9\u4EF6\u4E8B\u5BF9\u6211\u6CA1\u6709\u610F\u4E49.&quot;</p><p>\u610F\u4E49\u4E5F\u4E0E\u89C2\u6CE8\u76F8\u5173, \u53EA\u662F\u6216\u8BB8\u5B83\u8FD8\u6CA1\u6709\u6210\u4E3A\u771F\u7406\u6216\u6210\u89C1.</p><p>\u89C2\u6CE8\u662F\u4E00\u79CD &quot;\u770B&quot;, \u610F\u4E49\u4E5F\u5C31\u662F\u4E00\u79CD &quot;\u770B\u6CD5&quot;. &quot;\u770B\u6CD5&quot; \u4E0D\u540C, \u6240\u89C1\u4E5F\u5C31\u4E0D\u540C, \u4EE5\u6240\u89C1\u4E3A\u771F\u4FBF\u662F &quot;\u6210\u89C1&quot; \u4E86.</p><p>\u5728\u300A\u4F55\u4E3A\u826F\u597D\u751F\u6D3B\u300B\u4E2D\u9648\u5609\u6620\u4E3E\u753B\u5BB6\u7684\u4F8B\u5B50\u8BB2\u4EBA\u884C\u4E3A\u7684\u76EE\u7684. \u4ED6\u8BF4\u6709\u7684\u4E8B\u60C5\u76EE\u5730\u5C31\u662F\u5176\u81EA\u8EAB, \u5982\u4E0B\u68CB; \u6709\u7684\u4E8B\u60C5\u76EE\u7684\u5219\u5728\u5176\u5916, \u5982\u6316\u6C9F. \u4F46\u753B\u753B\u4E0D\u7EAF\u662F\u4E0B\u68CB\u4E00\u6837, \u76EE\u7684\u662F\u5176\u884C\u4E3A\u672C\u8EAB; \u4E5F\u4E0D\u662F\u6316\u6C9F, \u884C\u4E3A\u53EA\u662F\u6323\u5DE5\u94B1\u7684\u5DE5\u5177. \u753B\u753B\u7684\u76EE\u7684\u662F\u6DF7\u6742\u4E24\u8005\u7684, \u65E2\u6709\u5176\u81EA\u8EAB\u7684\u90E8\u5206, \u4E5F\u6709\u5728\u5176\u5916\u7684\u90E8\u5206.</p><p>\u4F46\u6211\u4EEC\u4E0D\u4E5F\u8BF4 &quot;\u4E3A\u4E86\u827A\u672F\u800C\u827A\u672F&quot; \u4E48? \u8FD9\u662F\u8BF4\u6709\u7684\u753B\u5BB6\u5177\u6709\u5F00\u521B\u6027, \u4ED6\u4EEC\u7684\u4F5C\u54C1\u6539\u53D8\u4E86\u4EBA\u4EEC\u770B\u5F85\u4E8B\u7269\u7684\u65B9\u6CD5. \u4ED6\u4EEC\u7684\u521B\u4F5C\u6210\u4E3A\u4E86\u7ED8\u753B\u7684\u5178\u8303, \u4ED6\u4EEC\u7684\u827A\u672F\u4E3A\u5176\u4ED6\u4EBA\u827A\u672F\u7684\u8303\u672C.</p><p>\u8FD9\u4E0D\u4E5F\u5C31\u662F\u610F\u4E49\u7684\u4F5C\u7528\u4E48? \u5B83\u63D0\u4F9B\u4E00\u79CD &quot;\u770B\u6CD5&quot;, \u4F7F\u4E16\u754C\u5728\u6211\u4EEC\u773C\u4E2D\u5448\u73B0\u51FA\u4E0D\u540C\u7684\u6837\u5F0F. \u5F53\u6211\u4EEC\u4EE5\u8FD9\u79CD\u6837\u5F0F\u7684 &quot;\u770B\u6CD5&quot; \u53BB\u770B\u4E16\u754C\u65F6, \u610F\u4E49\u624D\u5728\u6211\u4EEC\u7684 &quot;\u770B&quot; \u4E2D\u663E\u73B0\u51FA\u6765.</p><p>\u6240\u4EE5\u6211\u4EEC\u6216\u8BB8\u4E5F\u5E94\u5F53\u8BF4 &quot;\u5E76\u4E0D\u662F\u6211\u4EEC\u638C\u63E1\u610F\u4E49, \u800C\u662F\u610F\u4E49\u638C\u63E1\u6211\u4EEC&quot;.</p><h1 id="\u6DF1\u4E0E\u6D45" tabindex="-1"><a class="header-anchor" href="#\u6DF1\u4E0E\u6D45" aria-hidden="true">#</a> \u6DF1\u4E0E\u6D45</h1><p>\u6211\u4EEC\u56DE\u8FC7\u5934\u53BB\u770B\u770B\u5927\u5218, \u52A8\u6F2B, \u51AF\u53CB\u5170\u7684\u95EE\u9898.</p><p>\u601D\u60F3\u7684\u6DF1\u5EA6\u662F\u6709\u6DF1\u6D45, \u4F46\u552F\u5176\u4F7F\u6211\u4EEC\u6240\u89C1\u4E0D\u540C, \u624D\u771F\u7684\u6709\u610F\u4E49. \u610F\u4E49\u5728\u6211\u4EEC\u901A\u8FC7\u4F5C\u54C1\u7ED9\u4E88\u6211\u7684, \u770B\u5F85\u4E16\u754C\u7684\u4E0D\u540C\u65B9\u5F0F\u4E2D\u5448\u73B0\u51FA\u6765.</p><p>\u5F53\u7136\u6709\u66F4\u6DF1\u5EA6\u7684\u601D\u60F3, \u4F46\u5982\u679C\u6211\u4EEC\u73B0\u5728\u4E0D\u80FD\u4ECE\u4E2D\u627E\u5230 &quot;\u770B\u6CD5&quot;, \u90A3\u5B83\u7684\u6DF1\u5EA6\u5BF9\u4E8E\u6211\u4EEC\u4E5F\u6CA1\u6709\u610F\u4E49.</p><p>\u4F46\u662F\u4E5F\u4E0D\u5FC5\u60F6\u6050, \u6BD5\u7ADF\u771F\u7406\u638C\u63E1\u6211\u4EEC, \u6211\u4EEC\u8BDA\u6073\u7684\u53BB\u89C2\u6CE8, \u53BB &quot;\u770B&quot;, \u5B83\u603B\u4F1A\u5448\u73B0\u51FA\u6765,\u5C31\u5982\u4E0A\u5E1D\u5448\u73B0\u5728\u7948\u7977\u8005\u773C\u524D.</p><hr><p>\u53E6\u5916\u60F3\u5230\u7684. \u5C0F\u674E\u8001\u5E08\u5E38\u6279\u5224: \u8FD9\u662F\u975E\u5E38\u80A4\u6D45\u7684\u521D\u7EA7\u53CD\u601D.</p><p>\u4E0D\u77E5\u9053\u6709\u6CA1\u6709\u5C0F\u4EBA\u5982\u6211\u8005, \u60F3\u8FC7\u8FD9\u6837\u4E00\u4E2A\u95EE\u9898, \u80A4\u6D45\u7684\u521D\u7EA7\u53CD\u601D\u603B\u8FD8\u662F\u53CD\u601D, \u96BE\u9053\u8FD9\u6BD4\u6D51\u6D51\u5669\u5669\u65E0\u6240\u53CD\u601D\u8FD8\u7CDF\u4E48.</p><p>\u4F46\u6216\u8BB8\u8FD9\u79CD\u80A4\u6D45\u7684\u521D\u7EA7\u53CD\u601D, \u95EE\u9898\u8FD8\u4E0D\u51FA\u5728\u80A4\u6D45\u548C\u521D\u7EA7\u4E0A. \u5728\u8FD9\u91CC\u80A4\u6D45\u548C\u521D\u7EA7\u8FD8\u771F\u4E0D\u662F\u6DF1\u523B\u548C\u9AD8\u7EA7 &quot;\u521D\u7EA7\u9636\u6BB5&quot;.</p><p>\u771F\u6B63\u7684\u6709\u610F\u4E49\u7684\u53CD\u601D, \u5FC5\u987B\u4FDD\u6301\u89C2\u6CE8, \u5FC5\u987B\u662F\u5728\u7528\u53CD\u601D\u53BB &quot;\u770B&quot; \u8FD9\u4E2A\u4E16\u754C.</p><p>\u800C\u80A4\u6D45\u7684\u521D\u7EA7\u53CD\u601D\u5374\u8BA9\u4EBA\u4EEC\u628A\u8138\u4ECE\u95EE\u9898\u4E0A\u522B\u5F00. \u6BD4\u5982\u628A\u95EE\u9898\u6254\u5230 &quot;\u539F\u751F\u5BB6\u5EAD&quot; \u6216 &quot;\u5DE8\u5A74\u56FD&quot; \u7684\u5927\u5751\u91CC, \u7136\u540E\u5C31\u5982\u7529\u4E86\u9505\u4E00\u6837, \u4E0D\u518D\u53BB\u6B63\u773C\u770B\u8FD9\u4E2A\u95EE\u9898.</p><p>\u8FD9\u624D\u662F\u771F\u6B63\u53EF\u6015\u7684.</p>',55),e=[a];function r(h,d){return t(),o("div",null,e)}var n=p(q,[["render",r],["__file","\u52A8\u6F2B\u5927\u5218\u51AF\u53CB\u5170.html.vue"]]);export{n as default};
