import{_ as e,o,c,e as d}from"./app.efeddd24.js";const a={},t=d('<blockquote><p>\u533A\u5757\u94FE\u6280\u672F\u5929\u751F\u53CD\u9AA8.</p></blockquote><p>\u6709\u4E2A\u6BB5\u5B50, \u8BF4: &quot;\u7F8E\u56FD\u662F\u4E00\u4E2A\u5EFA\u7ACB\u5728\u8F66\u8F6E, \u67AA\u652F\u548C\u9634\u8C0B\u8BBA\u4E0A\u7684\u56FD\u5BB6.&quot;</p><p>\u9634\u8C0B\u8BBA\u662F\u4E00\u79CD\u6709\u7740\u72EC\u7279\u5BA1\u7F8E\u7684\u6587\u5316\u6A21\u5F0F, <code>\u533A\u5757\u94FE\u6280\u672F</code> \u7684\u8BDE\u751F\u5C31\u6DF1\u53D7\u5176\u5F71\u54CD. \u4EE5\u81F3\u4E8E <code>\u533A\u5757\u94FE</code> \u81EA\u59CB\u5C31\u6709\u4E00\u79CD\u53CD\u4F20\u7EDF\u7684\u670B\u514B\u6C14\u8D28.</p><p>\u786E\u5207\u8BF4\u6765, \u4ECE\u6587\u5316\u57FA\u56E0\u4E0A\u8BB2, <code>\u533A\u5757\u94FE</code> \u6765\u6E90\u4E8E <code>\u8D5B\u535A\u670B\u514B</code> \u7684\u4E00\u652F, \u53EB <code>\u5BC6\u7801\u670B\u514B</code> .</p><blockquote><p>\u6211\u8BA4\u8BC6\u4E00\u4E2A\u641E\u533A\u5757\u94FE\u5E95\u5C42\u6280\u672F\u7684\u54E5\u4EEC, \u5728\u6211\u770B\u6765\u5C31\u86EE\u8D5B\u535A\u670B\u514B\u7684, \u4E0D\u4F46\u8D77\u7684\u7F51\u540D\u5F71\u5C04 <code>\u4E8C\u8FDB\u5236</code>, \u8FD8\u7ECF\u5E38\u5728\u670B\u53CB\u5708\u91CC\u89C1\u4ED6\u603C\u5929\u603C\u5730\u603C\u7A7A\u6C14.</p></blockquote><p>\u8FD9\u7BC7\u6587\u7AE0\u5C31\u6765\u8BB2\u8BB2, <code>\u8D5B\u535A\u670B\u514B</code> \u662F\u600E\u4E48\u56DE\u4E8B, \u4EE5\u53CA\u8FD9\u79CD\u6587\u5316\u57FA\u56E0\u5BF9\u533A\u5757\u94FE\u6280\u672F\u6709\u4EC0\u4E48\u5F71\u54CD.</p><h2 id="\u8981\u8BF4-\u8D5B\u535A\u670B\u514B-\u5C31\u8981\u804A\u804A\u7F8E\u56FD\u79D1\u5E7B\u53F2" tabindex="-1"><a class="header-anchor" href="#\u8981\u8BF4-\u8D5B\u535A\u670B\u514B-\u5C31\u8981\u804A\u804A\u7F8E\u56FD\u79D1\u5E7B\u53F2" aria-hidden="true">#</a> \u8981\u8BF4 <code>\u8D5B\u535A\u670B\u514B</code> \u5C31\u8981\u804A\u804A\u7F8E\u56FD\u79D1\u5E7B\u53F2</h2><blockquote><p>\u4E3A\u4EC0\u4E48? \u56E0\u4E3A <code>\u8D5B\u535A\u670B\u514B</code> \u672C\u6765\u5C31\u662F\u4E00\u79CD\u79D1\u5E7B\u98CE\u683C.</p></blockquote><h3 id="\u8D77\u521D-\u7F8E\u56FD\u79D1\u5E7B\u4E00\u70B9\u4E0D\u79D1\u5E7B" tabindex="-1"><a class="header-anchor" href="#\u8D77\u521D-\u7F8E\u56FD\u79D1\u5E7B\u4E00\u70B9\u4E0D\u79D1\u5E7B" aria-hidden="true">#</a> \u8D77\u521D, \u7F8E\u56FD\u79D1\u5E7B\u4E00\u70B9\u4E0D\u79D1\u5E7B</h3><p>\u7F8E\u56FD\u6700\u65E9\u7684\u79D1\u5E7B\u53EB\u505A\u79D1\u5B66\u4F20\u5947. \u901A\u5E38\u90FD\u662F\u62AB\u7740\u79D1\u5E7B\u5916\u8863\u7684\u5192\u9669\u6545\u4E8B.</p><blockquote><p>\u6BD4\u5982: \u4E00\u4E2A\u767D\u4EBA\u7537\u6027\u706B\u661F\u63A2\u9669, \u7ECF\u5386\u4E00\u7CFB\u7EDF\u5947\u9047, \u6253\u8D25\u4E11\u964B\u7684\u539F\u4F4F\u6C11, \u83B7\u5F97\u540D\u58F0\u548C\u8D22\u5BCC, \u8FD8\u52FE\u642D\u4E0A\u4E00\u4E2A\u706B\u661F\u516C\u4E3B\u4E4B\u7C7B.</p></blockquote><p>\u8FD9\u79CD\u6545\u4E8B\u520A\u767B\u5728\u5EC9\u4EF7\u6742\u5FD7\u4E0A, \u7ED9\u52B3\u7D2F\u4E86\u4E00\u5929\u7684\u52B3\u82E6\u5927\u4F17\u770B. \u55EF, \u53EF\u4EE5\u628A\u8FD9\u79CD\u6545\u4E8B\u5F53\u6210\u73B0\u5728\u7684\u7F51\u7EDC\u723D\u6587.</p><blockquote><p>\u8001\u5B50\u5DF2\u7ECF\u5728\u516C\u53F8\u5199\u4E86\u4E00\u5929\u6587\u6863\u4E86, \u4E0D\u7ED9\u70B9\u4E3B\u4EBA\u516C\u7A7F\u8D8A\u91CD\u751F\u5F00\u6302\u7684\u5C0F\u8BF4, \u8FD8\u8BA9\u4E0D\u8BA9\u4EBA\u6D3B\u4E86.</p></blockquote><h3 id="\u574E\u8D1D\u5C14\u4E0E\u79D1\u5E7B\u9EC4\u91D1\u65F6\u4EE3" tabindex="-1"><a class="header-anchor" href="#\u574E\u8D1D\u5C14\u4E0E\u79D1\u5E7B\u9EC4\u91D1\u65F6\u4EE3" aria-hidden="true">#</a> \u574E\u8D1D\u5C14\u4E0E\u79D1\u5E7B\u9EC4\u91D1\u65F6\u4EE3</h3><p>\u7EC8\u7ED3\u79D1\u5B66\u4F20\u5947\u65F6\u4EE3\u7684\u7537\u4EBA, \u53EB\u505A <strong>\u574E\u8D1D\u5C14</strong>.</p><p>\u4ED6\u662F\u5F53\u65F6\u79D1\u5E7B\u5C0F\u8BF4\u4E3B\u8981\u9635\u5730 \u300A\u60CA\u5947\u79D1\u5E7B\u300B \u7684\u4E3B\u7F16. \u4ED6\u63D0\u51FA\u4E86\u4E00\u5957 <strong>\u574E\u8D1D\u5C14</strong> \u539F\u5219, \u4E0D\u7B26\u5408\u539F\u5219\u7684\u5C0F\u8BF4\u4E00\u5F8B\u4E0D\u4E88\u520A\u767B. \u539F\u5219\u5185\u5BB9\u5982\u4E0B :</p><ul><li>\u5199\u771F\u6B63\u7684\u79D1\u5B66;</li><li>\u7528\u73B0\u5B9E\u624B\u6CD5\u63CF\u5199\u8D85\u73B0\u5B9E\u7684\u9898\u6750;</li><li>\u5BF9\u79D1\u6280\u548C\u8FDB\u6B65\u4FDD\u6301\u4E50\u89C2\u6001\u5EA6;</li><li>\u4EE5\u7406\u6027\u6001\u5EA6\u63CF\u5199\u8D85\u73B0\u5B9E\u60C5\u8282.</li></ul><p>\u5728\u4ED6\u7684\u8FD9\u4E9B\u539F\u5219\u7684\u6307\u5BFC\u4E0B, \u5F00\u542F\u4E86 <strong>\u79D1\u5E7B\u5C0F\u8BF4\u9EC4\u91D1\u65F6\u4EE3</strong>. \u79D1\u5E7B\u5C0F\u8BF4\u4E09\u5DE8\u5934\u90FD\u662F <strong>\u574E\u8D1D\u5C14</strong> \u57F9\u517B\u51FA\u6765\u7684.</p><p>\u65F6\u503C\u4E8C\u6218\u7ED3\u675F, \u79D1\u5E7B\u5C0F\u8BF4\u5145\u6EE1\u4E86\u6602\u626C\u5411\u4E0A\u7684\u4E50\u89C2\u7CBE\u795E.</p><blockquote><p>\u4EBA\u7C7B\u8FDE\u7EB3\u7CB9\u90FD\u5E72\u7FFB\u4E86, \u8FD8\u6709\u4EC0\u4E48\u56F0\u96BE\u80FD\u963B\u6B62\u4EBA\u7C7B.</p></blockquote><h3 id="\u53CD\u4F20\u7EDF\u7684-\u65B0\u6D6A\u6F6E" tabindex="-1"><a class="header-anchor" href="#\u53CD\u4F20\u7EDF\u7684-\u65B0\u6D6A\u6F6E" aria-hidden="true">#</a> \u53CD\u4F20\u7EDF\u7684 <code>\u65B0\u6D6A\u6F6E</code></h3><p>\u65B0\u6D6A\u6F6E\u4F5C\u5BB6\u53CD\u5BF9\u574E\u8D1D\u5C14\u7684\u6559\u6761\u548C\u7EA6\u675F, \u4ED6\u4EEC\u6BD4\u9EC4\u91D1\u65F6\u4EE3\u7684\u4F5C\u5BB6\u66F4\u5173\u6CE8\u4EBA\u6587\u7684\u4E1C\u897F, \u5E76\u5438\u7EB3\u8C61\u5F81\u4E3B\u4E49, \u8868\u73B0\u4E3B\u4E49, \u5B58\u5728\u4E3B\u4E49\u7684\u5199\u4F5C\u6280\u6CD5.</p><blockquote><p>\u4F46\u636E\u8BF4, \u4ED6\u4EEC\u662F\u56E0\u4E3A\u7ED9\u574E\u8D1D\u5C14\u7684\u6742\u5FD7\u6295\u7A3F\u90FD\u88AB\u9000\u7A3F, \u6240\u4EE5\u51B3\u5B9A\u81EA\u5DF1\u73A9\u81EA\u5DF1\u7684.</p></blockquote><h3 id="\u8D5B\u535A\u670B\u514B" tabindex="-1"><a class="header-anchor" href="#\u8D5B\u535A\u670B\u514B" aria-hidden="true">#</a> <code>\u8D5B\u535A\u670B\u514B</code></h3><p><code>\u8D5B\u535A\u670B\u514B</code> \u4EA7\u751F\u7684\u539F\u56E0:</p><ul><li>\u4EBA\u7C7B\u767B\u4E0A\u6708\u7403\u540E, \u8FC8\u5411\u661F\u6668\u5927\u6D77\u7684\u6B65\u5B50\u5C31\u6162\u4E86;</li><li>\u51B7\u6218\u7ED3\u675F\u540E, \u4EBA\u4EEC\u5BF9\u672A\u6765\u793E\u4F1A\u7684\u56FE\u666F\u6709\u4E86\u65B0\u53CD\u601D;</li><li>\u6700\u91CD\u8981\u7684\u539F\u56E0\u662F, \u4FE1\u606F\u6280\u672F\u7684\u53D1\u5C55\u8BA9\u5F53\u65F6\u7684\u79D1\u5E7B\u4F5C\u5BB6\u731C\u60F3, \u4FE1\u606F\u6280\u672F\u624D\u662F\u672A\u6765\u53D1\u5C55\u7684\u4E3B\u6D41. (\u5F53\u7136, \u4ED6\u4EEC\u731C\u5BF9\u4E86.)</li></ul><p><code>\u8D5B\u535A\u670B\u514B</code> \u8FD9\u4E2A\u8BCD\u6765\u6E90\u4E8E <code>\u8D5B\u535A\u683C</code>. <code>\u8D5B\u535A\u683C</code>\u7684\u82F1\u6587\u5355\u8BCD\u662F <code>\u63A7\u5236\u8BBA</code> \u548C <code>\u80A2\u4F53</code> \u4E24\u4E2A\u82F1\u6587\u5355\u8BCD\u7684\u5408\u5E76. (\u4E0D\u8981\u95EE\u6211\u8FD9\u51E0\u4E2A\u82F1\u6587\u5355\u8BCD\u600E\u4E48\u5199, <strong>\u4E0D\u77E5\u9053!!</strong> \u8C22\u8C22)</p><p>\u5728 <code>\u8D5B\u535A\u670B\u514B</code> \u7684\u79D1\u5E7B\u8BF4\u91CC, \u4E16\u754C\u4E0A\u7684\u4E00\u5207\u90FD\u8FDE\u4E0A\u4E86\u7F51\u7EDC, \u4EBA\u7684\u96F6\u90E8\u4EF6\u90FD\u53EF\u4EE5\u4F7F\u7528\u673A\u5668\u4EE3\u66FF, \u56FD\u5BB6\u3001\u653F\u5E9C\u3001\u5BB6\u5EAD\u7B49\u4E00\u5207\u4EBA\u7C7B\u7EC4\u7EC7\u5F62\u5F0F\u8981\u4E0D\u7136\u5DF2\u7ECF\u6D88\u89E3, \u8981\u4E0D\u7136\u6BEB\u65E0\u610F\u4E49. \u4EBA\u7C7B\u9762\u4E34\u4E00\u4E2A\u7EC8\u6781\u7684\u95EE\u9898: \u4EC0\u4E48\u662F\u4EBA?</p><blockquote><p><code>\u653B\u58F3\u673A\u52A8\u961F</code> \u5C31\u662F\u8D5B\u535A\u670B\u514B\u98CE\u683C\u7684\u4F5C\u54C1.</p></blockquote><h2 id="\u91D1\u878D\u9634\u8C0B\u8BBA\u4E0E\u5BC6\u7801\u670B\u514B" tabindex="-1"><a class="header-anchor" href="#\u91D1\u878D\u9634\u8C0B\u8BBA\u4E0E\u5BC6\u7801\u670B\u514B" aria-hidden="true">#</a> \u91D1\u878D\u9634\u8C0B\u8BBA\u4E0E\u5BC6\u7801\u670B\u514B</h2><blockquote><p>\u8BF4\u5230\u670B\u514B, \u5C31\u4E00\u5B9A\u8981\u6709 &quot;\u6211\u6CA1\u6709\u9519, \u9519\u7684\u90FD\u662F\u4E16\u754C&quot; \u7684\u4E2D\u4E8C\u6C14\u8D28\u90FD\u5BF9. \u5B83\u603B\u5F97\u53CD\u5BF9\u70B9\u4EC0\u4E48\u624D\u884C, \u6069, \u6211\u4EEC\u5C31\u8BF4\u8BF4, \u8BDE\u751F\u533A\u5757\u94FE\u7684 <code>\u5BC6\u7801\u670B\u514B</code> \u4ED6\u4EEC\u5728\u53CD\u5BF9\u4EC0\u4E48.</p></blockquote><h3 id="\u91D1\u878D\u9634\u8C0B\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u91D1\u878D\u9634\u8C0B\u8BBA" aria-hidden="true">#</a> \u91D1\u878D\u9634\u8C0B\u8BBA</h3><blockquote><p>\u770B\u8FC7 \u300A\u8D27\u5E01\u6218\u4E89\u300B \u4E48? \u770B\u8FC7\u7684\u8BDD, \u8FD9\u4E00\u8282\u5C31\u4E0D\u7528\u8BF4\u4E86.</p></blockquote><p>\u5728\u8FD9\u4E2A\u4E16\u754C\u4E0A, \u6709\u4E00\u4E2A\u795E\u79D8\u7EC4\u7EC7, \u4ED6\u4EEC\u79D8\u5BC6\u7684\u63A7\u5236\u7740\u6574\u4E2A\u4E16\u754C\u7684\u8FD0\u884C, \u6240\u6709\u4E16\u754C\u5927\u4E8B\u7684\u80CC\u540E\u90FD\u6709\u4ED6\u4EEC\u7684\u5F71\u5B50. \u4ED6\u4EEC\u63A7\u5236\u4E16\u754C\u7684\u5DE5\u5177\u53EB\u505A</p><p><code>\u7F8E\u8054\u50A8</code>!!!</p><p>\u4EE5\u4E0A\u5C31\u662F\u91D1\u878D\u9634\u8C0B\u8BBA.</p>',36),p=[t];function r(l,i){return o(),c("div",null,p)}var n=e(a,[["render",r],["__file","\u533A\u5757\u94FE\u4E0E\u5BC6\u7801\u670B\u514B.html.vue"]]);export{n as default};
