import{_ as a,o as e,c as h,e as i}from"./app.6bf529a6.js";const r={},d=i('<h1 id="\u533A\u5757\u94FE\u5171\u8BC6\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#\u533A\u5757\u94FE\u5171\u8BC6\u673A\u5236" aria-hidden="true">#</a> \u533A\u5757\u94FE\u5171\u8BC6\u673A\u5236</h1><h2 id="\u5DE5\u4F5C\u91CF\u8BC1\u660E-pow" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F5C\u91CF\u8BC1\u660E-pow" aria-hidden="true">#</a> \u5DE5\u4F5C\u91CF\u8BC1\u660E ( PoW )</h2><h3 id="\u7279\u70B9" tabindex="-1"><a class="header-anchor" href="#\u7279\u70B9" aria-hidden="true">#</a> \u7279\u70B9</h3><p>\u4F9D\u8D56\u673A\u5668\u8FDB\u884C\u6570\u5B66\u8FD0\u7B97\u6765\u83B7\u53D6\u8BB0\u8D26\u6743, \u8D44\u6E90\u6D88\u8017\u76F8\u6BD4\u5176\u4ED6\u5171\u8BC6\u673A\u5236\u9AD8, \u53EF\u76D1\u7BA1\u6027\u5F31, \u540C\u65F6\u6BCF\u6B21\u8FBE\u6210\u5171\u8BC6\u9700\u8981\u5168\u7F51\u5171\u540C\u53C2\u4E0E\u8FD0\u7B97, \u6027\u80FD\u6548\u7387\u6BD4\u8F83\u4F4E, \u5BB9\u9519\u6027\u65B9\u9762\u5141\u8BB8\u5168\u7F51 50% \u8282\u70B9\u51FA\u9519.</p><h3 id="\u4F18\u7F3A\u70B9" tabindex="-1"><a class="header-anchor" href="#\u4F18\u7F3A\u70B9" aria-hidden="true">#</a> \u4F18\u7F3A\u70B9</h3><p>\u4F18\u70B9:</p><ul><li>\u5B8C\u5168\u53BB\u4E2D\u5FC3\u5316, \u8282\u70B9\u81EA\u7531\u8FDB\u51FA</li></ul><p>\u7F3A\u70B9:</p><ul><li>\u76EE\u524D bitcoin \u5DF1\u7ECF\u5438\u5F15\u5168\u7403\u5927\u90E8\u5206\u7B97\u529B, \u5176\u5B83\u518D\u7528 PoW \u5171\u8BC6\u673A\u5236\u7684\u533A\u5757\u94FE\u5E94\u7528\u5F88\u96BE\u83B7\u5F97\u76F8\u540C\u7684\u7B97\u529B\u6765\u4FDD\u969C\u81EA\u8EAB\u7684\u5B89\u5168.</li><li>\u6316\u77FF\u9020\u6210\u5927\u91CF\u7684\u8D44\u6E90\u6D6A\u8D39</li><li>\u9700\u5168\u7F51\u9A8C\u8BC1, \u5171\u8BC6\u8FBE\u6210\u7684\u5468\u671F\u8F83\u957F</li><li>\u53EF\u76D1\u7BA1\u6027\u5F31</li></ul><h3 id="\u76F8\u5173\u9879\u76EE" tabindex="-1"><a class="header-anchor" href="#\u76F8\u5173\u9879\u76EE" aria-hidden="true">#</a> \u76F8\u5173\u9879\u76EE</h3><ul><li>\u6BD4\u7279\u5E01</li><li>\u4EE5\u592A\u574A\u524D\u4E09\u4E2A\u9636\u6BB5 <ul><li>Frontier ( \u524D\u6CBF )</li><li>Homestead ( \u5BB6\u56ED )</li><li>Metropolis ( \u5927\u90FD\u4F1A )</li></ul></li></ul><h3 id="\u7C7B\u6BD4" tabindex="-1"><a class="header-anchor" href="#\u7C7B\u6BD4" aria-hidden="true">#</a> \u7C7B\u6BD4</h3><p>\u5B66\u4E1A\u8BC1\u660E\u8003\u8BD5</p><h2 id="\u6743\u76CA\u8BC1\u660E-pos" tabindex="-1"><a class="header-anchor" href="#\u6743\u76CA\u8BC1\u660E-pos" aria-hidden="true">#</a> \u6743\u76CA\u8BC1\u660E ( PoS )</h2><h3 id="\u7279\u70B9-1" tabindex="-1"><a class="header-anchor" href="#\u7279\u70B9-1" aria-hidden="true">#</a> \u7279\u70B9</h3><p>\u6982\u636E\u6301\u6709\u8D27\u5E01\u7684\u91CF\u548C\u65F6\u95F4, \u8FDB\u884C\u5229\u606F\u5FC5\u653E\u548C\u533A\u5757\u4EA7\u751F\u7684\u673A\u5236.</p><p>\u8282\u70B9\u8BB0\u8D26\u6743\u7684\u83B7\u5F97\u96BE\u5EA6\u4E0E\u8282\u70B9\u6301\u6709\u7684\u6743\u76CA\u6210\u53CD\u6BD4, \u76F8\u5BF9\u4E8E PoW, \u4E00\u5B9A\u7A0B\u5EA6\u51CF\u5C11\u4E86\u6570\u5B66\u8FD0\u7B97\u5E26\u6765\u7684\u8D44\u6E90\u6D88\u8017, \u6027\u80FD\u4E5F\u5F97\u5230\u4E86\u76F8\u5E94\u7684\u63D0\u5347, \u4F46\u4F9D\u7136\u662F\u57FA\u4E8E\u54C8\u5E0C\u8FD0\u7B97\u7ADE\u4E89\u83B7\u5F97\u8BB0\u8D26\u6743\u7684\u65B9\u5F0F, \u53EF\u76D1\u7BA1\u6027\u5F31. \u8BE5\u5171\u8BC6\u673A\u5236\u5BB9\u9519\u6027\u548C PoW \u76F8\u540C. \u5B83\u662F PoW \u7684\u4E00\u79CD\u5347\u7EA7\u5171\u8BC6\u673A\u5236, \u6839\u636E\u6BCF\u4E2A\u8282\u70B9\u6240\u5360\u4EE3\u5E01\u7684\u6BD4\u4F8B\u548C\u65F6\u95F4, \u7B49\u6BD4\u4F8B\u7684\u964D\u4F4E\u6316\u77FF\u7684\u96BE\u5EA6, \u4ECE\u800C\u52A0\u5FEB\u627E\u968F\u673A\u6570\u7684\u901F\u5EA6.</p><h3 id="\u4F18\u7F3A\u70B9-1" tabindex="-1"><a class="header-anchor" href="#\u4F18\u7F3A\u70B9-1" aria-hidden="true">#</a> \u4F18\u7F3A\u70B9</h3><p>\u4F18\u70B9:</p><ul><li>\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u7F29\u77ED\u4E86\u5171\u8BC6\u8FBE\u6210\u7684\u65F6\u95F4, \u4E0D\u518D\u9700\u8981\u5927\u91CF\u6D88\u8017\u80FD\u6E90\u6316\u77FF</li></ul><p>\u7F3A\u70B9:</p><ul><li>\u8FD8\u662F\u9700\u8981\u6316\u77FF, \u672C\u8D28\u4E0A\u6CA1\u6709\u89E3\u51B3\u5546\u4E1A\u5E94\u7528\u7684\u75DB\u70B9; \u6240\u6709\u7684\u786E\u8BA4\u90FD\u53EA\u662F\u4E00\u4E2A\u6982\u7387\u4E0A\u7684\u8868\u8FBE, \u800C\u4E0D\u662F\u4E00\u4E2A\u786E\u5B9A\u6027\u7684\u4E8B\u60C5, \u7406\u8BBA\u4E0A\u6709\u53EF\u80FD\u5B58\u5728\u5176\u4ED6\u653B\u51FB\u5F71\u54CD, \u4F8B\u5982, \u4EE5\u592A\u574A\u7684 DAO \u653B\u51FB\u4E8B\u4EF6\u9020\u6210\u4EE5\u592A\u574A\u786C\u5206\u53C9, \u800C ETC \u7531\u6B64\u51FA\u73B0, \u4E8B\u5B9E\u4E0A\u8BC1\u660E\u4E86\u6B64\u6B21\u786C\u5206\u53C9\u7684\u5931\u8D25.</li></ul><h3 id="\u76F8\u5173\u9879\u76EE-1" tabindex="-1"><a class="header-anchor" href="#\u76F8\u5173\u9879\u76EE-1" aria-hidden="true">#</a> \u76F8\u5173\u9879\u76EE</h3><p>\u672A\u6765\u5E01</p><h3 id="\u7C7B\u6BD4-1" tabindex="-1"><a class="header-anchor" href="#\u7C7B\u6BD4-1" aria-hidden="true">#</a> \u7C7B\u6BD4</h3><p>\u80A1\u7968\u673A\u5236 (\u6301\u6709\u80A1\u7968\u8005\u4E0D\u4F1A\u653B\u51FB\u516C\u53F8)</p><h2 id="\u6743\u76CA\u8BC1\u660E-\u5DE5\u4F5C\u91CF\u8BC1\u660E-pow-pos" tabindex="-1"><a class="header-anchor" href="#\u6743\u76CA\u8BC1\u660E-\u5DE5\u4F5C\u91CF\u8BC1\u660E-pow-pos" aria-hidden="true">#</a> \u6743\u76CA\u8BC1\u660E + \u5DE5\u4F5C\u91CF\u8BC1\u660E ( PoW + PoS )</h2><h3 id="\u7279\u70B9-2" tabindex="-1"><a class="header-anchor" href="#\u7279\u70B9-2" aria-hidden="true">#</a> \u7279\u70B9</h3><p>PoW \u53D1\u884C\u8D27\u5E01, PoS \u7EF4\u62A4\u7F51\u7EDC\u5B89\u5168.</p><p>PoS \u533A\u5757\u4E2D, \u8981\u8FDB\u884C 51% \u653B\u51FB, \u9996\u5148\u8981\u63A7\u5236\u4F17\u591A\u7684\u5E01, \u8FD9\u6210\u672C\u4F1A\u9AD8\u4E8E 51% \u7684\u7B97\u529B, \u63D0\u9AD8\u4E86\u653B\u51FB\u6210\u672C, \u540C\u65F6\u53D1\u8D77\u653B\u51FB\u65F6, \u653B\u51FB\u8005\u4E5F\u4F1A\u6D88\u8017\u5E01, \u4F7F\u5176\u60F3\u8FDB\u5165\u4E3B\u94FE\u66F4\u56F0\u96BE.</p><h2 id="\u80A1\u4EFD\u6388\u6743\u8BC1\u660E" tabindex="-1"><a class="header-anchor" href="#\u80A1\u4EFD\u6388\u6743\u8BC1\u660E" aria-hidden="true">#</a> \u80A1\u4EFD\u6388\u6743\u8BC1\u660E</h2><h3 id="\u7279\u70B9-3" tabindex="-1"><a class="header-anchor" href="#\u7279\u70B9-3" aria-hidden="true">#</a> \u7279\u70B9</h3><p>PoS \u9762\u4E34\u7684\u6311\u6218\u662F: \u5982\u4F55\u901A\u8FC7\u53CA\u65F6\u800C\u9AD8\u6548\u7684\u65B9\u6CD5\u8FBE\u6210\u5171\u8BC6?</p><p>DPoS \u4E0E PoS \u7684\u4E3B\u8981\u533A\u522B\u5728\u4E8E\u8282\u70B9, \u9009\u4E3E\u82E5\u5E72\u4EE3\u7406\u4EBA, \u7531\u4EE3\u7406\u4EBA\u9A8C\u8BC1\u548C\u8BB0\u8D26, \u5176\u5408\u89C4\u76D1\u7BA1, \u6027\u80FD, \u8D44\u6E90\u6D88\u8017\u548C\u5BB9\u9519\u80FD\u6027\u4E0E PoS \u76F8\u4F3C.</p><h3 id="\u4F18\u7F3A\u70B9-2" tabindex="-1"><a class="header-anchor" href="#\u4F18\u7F3A\u70B9-2" aria-hidden="true">#</a> \u4F18\u7F3A\u70B9</h3><p>\u4F18\u70B9:</p><ul><li>\u5927\u5E45\u7F29\u5C0F\u53C2\u4E0E\u9A8C\u8BC1\u548C\u8BB0\u8D26\u8282\u70B9\u7684\u6570\u91CF, \u53EF\u4EE5\u8FBE\u5230\u79D2\u7EA7\u7684\u5171\u8BC6\u9A8C\u8BC1.</li></ul><p>\u7F3A\u70B9:</p><ul><li>\u6574\u4E2A\u5171\u8BC6\u673A\u5236\u8FD8\u662F\u4F9D\u8D56\u4E8E\u4EE3\u5E01, \u5F88\u591A\u5546\u4E1A\u5E94\u7528\u662F\u4E0D\u9700\u8981\u4EE3\u5E01\u5B58\u5728\u7684.</li></ul><h3 id="\u7C7B\u6BD4-2" tabindex="-1"><a class="header-anchor" href="#\u7C7B\u6BD4-2" aria-hidden="true">#</a> \u7C7B\u6BD4</h3><p>\u4EBA\u6C11\u4EE3\u8868\u5927\u4F1A\u5236\u5EA6</p><p>\u8463\u4E8B\u4F1A\u6295\u7968\u5236</p><h2 id="\u745E\u6CE2\u5171\u8BC6\u534F\u8BAE-rcp" tabindex="-1"><a class="header-anchor" href="#\u745E\u6CE2\u5171\u8BC6\u534F\u8BAE-rcp" aria-hidden="true">#</a> \u745E\u6CE2\u5171\u8BC6\u534F\u8BAE ( RCP )</h2><h3 id="\u4F18\u7F3A\u70B9-3" tabindex="-1"><a class="header-anchor" href="#\u4F18\u7F3A\u70B9-3" aria-hidden="true">#</a> \u4F18\u7F3A\u70B9</h3><p>\u4E0E\u6BD4\u7279\u5E01\u53CA\u70B9\u70B9\u5E01\u4E00\u6837, \u745E\u6CE2\u7CFB\u7EDF\u5C06\u80A1\u4E1C\u4EEC\u4E0E\u5176\u6295\u7968\u6743\u9694\u5F00, \u5E76\u56E0\u6B64\u6BD4\u5176\u4ED6\u7CFB\u7EDF\u66F4\u4E2D\u5FC3\u5316.</p><h3 id="\u7C7B\u6BD4-3" tabindex="-1"><a class="header-anchor" href="#\u7C7B\u6BD4-3" aria-hidden="true">#</a> \u7C7B\u6BD4</h3><p>\u4FE1\u4EFB\u5217\u8868</p><p>\u5408\u683C\u4F9B\u65B9</p><h2 id="pool-\u9A8C\u8BC1\u6C60" tabindex="-1"><a class="header-anchor" href="#pool-\u9A8C\u8BC1\u6C60" aria-hidden="true">#</a> Pool \u9A8C\u8BC1\u6C60</h2><h3 id="\u7279\u70B9-4" tabindex="-1"><a class="header-anchor" href="#\u7279\u70B9-4" aria-hidden="true">#</a> \u7279\u70B9</h3><p>\u57FA\u4E8E\u4F20\u7EDF\u7684\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u6280\u672F, \u52A0\u4E0A\u6570\u636E\u9A8C\u8BC1\u673A\u5236: \u662F\u76EE\u524D\u884C\u4E1A\u94FE\u5927\u8303\u56F4\u5728\u4F7F\u7528\u7684\u5171\u8BC6\u673A\u5236.</p><h3 id="\u4F18\u7F3A\u70B9-4" tabindex="-1"><a class="header-anchor" href="#\u4F18\u7F3A\u70B9-4" aria-hidden="true">#</a> \u4F18\u7F3A\u70B9</h3><p>\u4F18\u70B9:</p><ul><li>\u4E0D\u9700\u8981\u4EE3\u5E01\u4E5F\u53EF\u4EE5\u5DE5\u4F5C, \u5728\u6210\u719F\u7684\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u7B97\u6CD5 ( Pasox, Raft ) \u57FA\u7840\u4E0A, \u5B9E\u73B0\u79D2\u7EA7\u5171\u8BC6\u9A8C\u8BC1.</li></ul><p>\u7F3A\u70B9:</p><ul><li>\u53BB\u4E2D\u5FC3\u5316\u7A0B\u5EA6\u4E0D\u5982 bitcoin; \u66F4\u9002\u5408\u591A\u65B9\u53C2\u4E0E\u7684\u591A\u4E2D\u5FC3\u5546\u4E1A\u6A21\u5F0F.</li></ul>',56),l=[d];function n(o,t){return e(),h("div",null,l)}var s=a(r,[["render",n],["__file","\u533A\u5757\u94FE\u5171\u8BC6\u673A\u5236.html.vue"]]);export{s as default};
