import{_ as p,o,c as t,e as u}from"./app.6bf529a6.js";const e={},q=u('<h1 id="\u5361\u5C14\u7C73\u5FB7\u6700\u540E\u600E\u6837\u4E86" tabindex="-1"><a class="header-anchor" href="#\u5361\u5C14\u7C73\u5FB7\u6700\u540E\u600E\u6837\u4E86" aria-hidden="true">#</a> \u5361\u5C14\u7C73\u5FB7\u6700\u540E\u600E\u6837\u4E86</h1><p>\u56E0\u4E3A\u6700\u8FD1\u5728\u8BA1\u5212\u4E00\u4EF6\u4E8B, \u6240\u4EE5\u4E94\u4E00\u5168\u90E8\u7684\u95F2\u6687\u65F6\u95F4\u90FD\u5728\u5543\u67CF\u62C9\u56FE\u7684\u300A\u5361\u5C14\u7C73\u5FB7\u7BC7\u300B.</p><p>\u56E0\u6B64\u4E5F\u5C31\u5E76\u6CA1\u6709\u770B\u5230 B \u7AD9\u53D1\u5E03\u7684\u89C6\u9891. \u53EA\u662F\u6628\u5929\u665A\u4E0A\u534A\u591C\u8D77\u5E8A\u7ED9\u4E8C\u5B9D\u628A\u5C3F\u65F6, \u53D1\u73B0\u7535\u53F0\u66F4\u65B0\u4E86, \u624D\u5927\u6982\u77E5\u9053\u53D1\u751F\u4E86\u4EC0\u4E48.</p><p>\u7136\u800C\u6211\u80FD\u8BF4\u4EC0\u4E48\u5462? \u6211\u8FD8\u662F\u8BB2\u8BB2\u67CF\u62C9\u56FE\u7684\u300A\u5361\u5C14\u7C73\u5FB7\u7BC7\u300B\u5427.</p><hr><p>\u516C\u5143\u524D 429 \u5E74 5 \u6708\u672B, \u82CF\u683C\u62C9\u5E95\u521A\u6253\u5B8C\u6CE2\u63D0\u72C4\u4E9A\u6218\u4E89, \u4ECE\u519B\u8425\u8FD4\u56DE\u96C5\u5178.</p><p>\u6CE2\u63D0\u72C4\u4E9A\u662F\u79D1\u6797\u65AF\u4EBA\u7684\u6B96\u6C11\u5730, \u53C8\u662F\u63D0\u6D1B\u540C\u76DF\u7684\u6210\u5458. \u96C5\u5178\u4E0E\u79D1\u6797\u65AF\u4EBA\u6709\u4EC7\u9699, \u4E8E\u662F\u8981\u6C42\u6CE2\u63D0\u72C4\u4E9A\u62C6\u6BC1\u57CE\u5899, \u4EA4\u7EB3\u4EBA\u8D28, \u9A71\u9010\u79D1\u6797\u65AF\u4E13\u5458. \u4E8E\u662F\u6CE2\u63D0\u72C4\u4E9A\u5728\u516C\u5143\u524D 432 \u5E74\u5BA3\u5E03\u8131\u79BB\u96C5\u5178. \u4E8E\u662F\u96C5\u5178\u56F4\u653B\u6CE2\u63D0\u72C4\u4E9A. \u8FD9\u4E00\u4ED7\u4E00\u76F4\u6253\u5230 429 \u5E74, \u624D\u4EE5\u96C5\u5178\u80DC\u5229\u544A\u7EC8.</p><p>\u7ECF\u5386\u4E86\u6F2B\u957F\u6218\u4E89\u5C81\u6708\u7684\u82CF\u683C\u62C9\u5E95, \u56DE\u5230\u96C5\u5178\u540E, \u76F4\u63A5\u6765\u5230\u4E86\u738B\u5BAB\u795E\u5E99\u5BF9\u9762\u7684\u9676\u745E\u4E9A\u65AF\u4F53\u80B2\u573A.</p><p>\u8FD9\u91CC\u6240\u8BF4\u7684 &quot;\u738B\u5BAB\u795E\u5E99&quot; \u662F\u4EA1\u7075\u5973\u795E\u7684\u795E\u5E99, \u800C\u9676\u745E\u4E9A\u65AF\u4F53\u80B2\u573A\u662F\u4E00\u4E2A\u6454\u8DE4\u573A, \u662F\u4E13\u95E8\u8BAD\u7EC3\u89D2\u529B\u7684\u5730\u65B9. \u4E24\u8005\u76F8\u5BF9, \u662F\u8981\u6697\u793A\u89D2\u529B\u4E8E\u6B7B\u4EA1\u7684\u5173\u7CFB. \u4ECE\u82CF\u683C\u62C9\u5E95\u5BF9\u4E8E\u6CE2\u63D0\u72C4\u4E9A\u6218\u4E89\u7684\u8F6C\u8FF0\u6765\u770B, \u8FD9\u4E00\u573A\u4ED7\u4E2D, \u5E94\u8BE5\u662F\u6B7B\u4EBA\u65E0\u7B97\u7684. \u89C1\u60EF\u6218\u4E89\u4E2D\u6B7B\u4EA1\u7684\u4ED6, \u6765\u5230\u4EA1\u7075\u5973\u795E\u795E\u5E99\u5BF9\u9762\u7684\u6454\u8DE4\u573A\u65F6, \u662F\u5426\u4F1A\u6709\u611F\u6168, \u6211\u4EEC\u4E0D\u5F97\u800C\u77E5, \u4F46\u6B64\u8BAD\u7EC3\u8EAB\u4F53\u4E4B\u6240\u6B64\u65F6\u5DF2\u53D8\u6210\u4E86\u516C\u4F17\u4EA4\u8C08\u7684\u5730\u65B9.</p><p>\u82CF\u683C\u62C9\u5E95\u5728\u6B64\u5904\u770B\u5230\u4E86 &quot;\u75AF\u5B50\u4E00\u6837&quot; \u7684\u51EF\u52D2\u4E30. \u51EF\u52D2\u4E30\u5411\u82CF\u683C\u62C9\u5E95\u8BE2\u95EE\u6218\u573A\u4E0A\u7684\u4E8B, \u5E76\u628A\u4ED6\u62C9\u5230\u4E86\u514B\u91CC\u5E95\u4E9A\u7684\u8EAB\u8FB9\u5750\u4E0B.</p><p>\u82CF\u683C\u62C9\u5E95\u5E73\u9759\u7684\u56DE\u7B54\u4E86\u5173\u4E8E\u6218\u4E89\u7684\u4E8B. \u4ED6\u7684\u5E73\u9759\u751A\u81F3\u8BA9\u51EF\u52D2\u4E30\u6000\u7591\u82CF\u683C\u62C9\u5E95\u662F\u5426\u53C2\u52A0\u8FC7\u6218\u6597.</p><blockquote><p>&quot;\u6211\u5047\u5B9A&quot; \u4ED6\u8BF4 &quot;\u4F60\u5F53\u65F6\u5728\u573A.&quot; &quot;\u662F\u7684&quot; \u6211\u8BF4.</p></blockquote><p>\u5728\u82CF\u683C\u62C9\u5E95\u8BB2\u5B8C\u4E86\u5173\u4E8E\u6218\u4E89\u7684\u4E8B\u540E, \u4ED6\u8BE2\u95EE\u5BB6\u91CC\u7684\u60C5\u51B5, \u4E3B\u8981\u6D89\u53CA\u54F2\u5B66\u7684\u5B66\u4E60\u548C\u9752\u5E74\u60C5\u51B5, \u5C24\u5176\u662F\u6709\u6CA1\u6709\u6770\u51FA\u7684\u9752\u5E74\u624D\u4FCA.</p><p>\u4E8E\u662F\u53D1\u751F\u4E86\u4E00\u573A\u5927\u578B\u7684\u5BF9\u5E74\u9752\u7684\u4EBA &quot;\u8DEA\u8214&quot;.</p><hr><p>\u8DEA\u8214\u7684\u4E3B\u529B\u662F\u514B\u91CC\u5E95\u4E9A, \u88AB\u8DEA\u8214\u7684\u5BF9\u8C61\u662F\u4ED6\u7684\u5802\u5F1F\u5361\u5C14\u7C73\u5FB7.</p><p>\u5F53\u7136\u5361\u5C14\u7C73\u5FB7\u88AB\u8DEA\u8214\u4E5F\u4E0D\u662F\u6CA1\u6709\u9053\u7406\u7684, \u8FDE\u82CF\u683C\u62C9\u5E95\u90FD\u627F\u8BA4, \u4ED6\u5BF9\u5361\u5C14\u7C73\u5FB7\u7684\u4FCA\u7F8E\u548C\u5F62\u4F53\u611F\u5230\u9707\u60CA.</p><p>\u800C\u5361\u5C14\u7C73\u5FB7\u56E0\u5176\u4FCA\u7F8E, \u5DF2\u6210\u4E3A\u4E86\u5F53\u65F6\u5E74\u9752\u4EBA\u7684\u9886\u8896:</p><blockquote><p>\u90A3\u4E9B\u5B69\u5B50, \u4ECE\u6700\u5927\u7684\u5230\u6700\u5C0F\u7684, \u5168\u90FD\u8F6C\u8FC7\u5934\u770B\u7740\u5361\u5C14\u7C73\u5FB7, \u5C31\u597D\u50CF\u4ED6\u662F\u4E00\u5C0A\u96D5\u50CF.</p></blockquote><p>\u5361\u5C14\u7C73\u5FB7\u4E0D\u4EC5\u76F8\u8C8C\u7F8E\u6781\u4E86, \u51EF\u52D2\u4E30\u8FD8\u8BF4: &quot;\u5982\u679C\u4F60\u770B\u89C1\u4ED6\u7684\u797C\u4F53, \u4F60\u5C31\u4E0D\u4F1A\u8BF4\u4ED6\u7684\u8138\u86CB\u6F02\u4EAE\u4E86, \u4ED6\u786E\u5B9E\u662F\u7EDD\u5BF9\u5B8C\u7F8E\u7684.&quot;</p><p>\u4F46\u82CF\u683C\u62C9\u5E95\u662F\u4F55\u7B49\u667A\u6167\u548C\u8282\u5236, \u4ED6\u867D\u627F\u8BA4\u5361\u5C14\u7C73\u5FB7\u7684\u8EAB\u4F53\u662F\u5B8C\u7F8E\u7684, \u4F46\u8FD8\u8981\u8981\u6C42\u4ED6\u6709\u5B8C\u7F8E\u7684\u7075\u9B42\u624D\u80AF\u8BF4\u4ED6\u662F\u5C3D\u5584\u5C3D\u7F8E\u7684.</p><p>\u8FD9\u514B\u91CC\u5E95\u4E9A\u7ACB\u65F6\u4FBF\u51FA\u6765, \u4E3A\u5361\u5C14\u7C73\u5FB7\u7075\u9B42\u7684\u7F8E\u597D\u505A\u4FDD\u8BC1.</p><p>\u9996\u5148\u662F\u6538\u4E45\u800C\u7F8E\u597D\u7684\u5386\u53F2\u5728\u5361\u5C14\u7C73\u5FB7\u7684\u7075\u9B42\u4E2D\u6D41\u8D9F. \u8FD9\u5C31\u662F\u4ED6\u7684\u5BB6\u65CF. \u5361\u5C14\u7C73\u5FB7\u6709\u7740\u6538\u4E45\u800C\u9AD8\u5C1A\u7684\u5BB6\u65CF, \u5728\u4ED6\u7684\u65CF\u8C31\u4E2D, \u6700\u6709\u540D\u7684\u4FBF\u662F\u667A\u6167\u7684\u68AD\u4F26.</p><p>\u5176\u6B21\u514B\u91CC\u5E95\u4E9A\u6781\u529B\u4FDD\u8BC1\u5361\u5C14\u7C73\u5FB7 &quot;\u786E\u5B9E\u662F\u4E00\u540D\u54F2\u5B66\u5BB6, \u4E5F\u662F\u4E00\u540D\u91CD\u8981\u7684\u8BD7\u4EBA&quot;.</p><p>\u5F53\u7136, \u5982\u679C\u6211\u4EEC\u770B\u5230\u540E\u9762, \u5C31\u4F1A\u77E5\u9053\u4E3A\u4EC0\u4E48\u514B\u91CC\u5E95\u4E9A\u5982\u6B64\u6781\u529B\u7684\u4FDD\u8BC1\u4E86.</p><hr><p>\u5982\u679C\u53EA\u770B\u672C\u7BC7, \u5927\u6982\u4F1A\u4EE5\u4E3A\u514B\u91CC\u5E95\u4E9A\u662F\u4F4D\u70ED\u5FC3\u9752\u5E74\u7684\u826F\u597D\u5BFC\u5E08.</p><p>\u53EF\u60DC\u4ED6\u5E76\u4E0D\u662F. \u514B\u91CC\u5E95\u4E9A\u5411\u6765\u4EE5\u8D2A\u5A6A\u548C\u65E0\u6240\u4E0D\u7528\u5176\u6781\u8457\u79F0.</p><p>\u4ED6\u66FE\u4EE5\u6076\u52A3\u7684\u65B9\u5F0F\u8FFD\u6C42\u8FC7\u53E6\u4E00\u4E2A\u53EB\u505A\u6B27\u897F\u5FB7\u8C1F\u65AF\u7684\u9752\u5E74. \u5176\u624B\u6BB5\u4E4B\u80AE\u810F, \u4EE5\u81F3\u4E8E\u82CF\u683C\u62C9\u5E95\u5F53\u7740\u4ED6\u548C\u6B27\u897F\u5FB7\u8C1F\u65AF\u7684\u9762\u8BF4\u4E86\u7C97\u8BDD:</p><blockquote><p>&quot;\u514B\u91CC\u5E95\u4E9A\u5C31\u662F\u4E00\u4E2A\u88AB\u8D2A\u5A6A\u63A7\u5236\u7684\u4EBA, \u4ED6\u60F3\u8981\u597D\u6B27\u897F\u5FB7\u8C1F\u65AF\u4EA4\u5BC6, \u5C31\u50CF\u732A\u60F3\u8981\u6469\u64E6\u77F3\u5934\u4E00\u6837.&quot;</p></blockquote><hr><p>\u7136\u800C\u5361\u5C14\u7C73\u5FB7\u4E5F\u5E76\u4E0D\u5982\u5176\u8868\u5B8C\u7F8E. \u5728\u5176\u770B\u8D77\u6765\u5B8C\u7F8E\u7684\u8EAB\u4F53\u4E0B, \u5374\u6709\u7740\u75C5\u60A3. \u4ED6\u60A3\u6709\u5934\u75BC.</p><p>\u82CF\u683C\u62C9\u5E95\u8BF4\u4ED6\u6709\u6CBB\u5934\u75BC\u7684\u529E\u6CD5, \u4F46\u8981\u5728\u6CBB\u4E4B\u524D\u5148\u7528\u4E00\u4E2A\u5492\u8BED\u5C06\u8282\u5236\u7684\u5FB7\u6027\u6CE8\u5165\u75C5\u60A3\u7684\u7075\u9B42. \u5F53\u7136, \u5982\u679C\u5361\u5C14\u7C73\u5FB7\u672C\u8EAB\u5177\u6709\u8282\u5236\u7684\u5FB7\u6027, \u90A3\u6CBB\u5934\u75BC\u4E5F\u5C31\u4E0D\u5FC5\u8981\u7528\u5492\u8BED\u4E86.</p><p>\u90A3\u5982\u4F55\u786E\u5B9A\u5361\u5C14\u7C73\u5FB7\u662F\u5426\u5177\u6709\u8282\u5236\u7684\u5FB7\u6027\u5462? \u82CF\u683C\u62C9\u5E95\u8BF4, \u5982\u679C\u4E00\u4E2A\u4EBA\u62E5\u6709\u8282\u5236\u7684\u5FB7\u6027, \u90A3\u4ED6\u4E00\u5B9A\u80FD\u8BF4\u51FA\u8282\u5236\u662F\u4EC0\u4E48. \u56E0\u6B64\u5982\u679C\u5361\u5C14\u7C73\u5FB7\u53EF\u4EE5\u544A\u8BC9\u4ED6\u8282\u5236\u662F\u4EC0\u4E48, \u5C31\u53EF\u4EE5\u786E\u5B9A\u4ED6\u662F\u5426\u5177\u6709\u8282\u5236\u7684\u5FB7\u6027\u4E86.</p><p>\u4E8E\u662F\u4FBF\u5F00\u59CB\u4E86\u672C\u7BC7\u7684\u4E3B\u4F53\u5BF9\u8BDD, \u5BF9\u4E8E\u8282\u5236\u5B9A\u4E49\u7684\u8BA8\u8BBA.</p><p>\u4F5C\u4E3A &quot;\u4E00\u540D\u54F2\u5B66\u5BB6&quot; \u548C &quot;\u91CD\u8981\u7684\u8BD7\u4EBA&quot;, \u5361\u5C14\u7C73\u5FB7\u5BF9\u4E8E\u8282\u5236\u7684\u8BA4\u8BC6\u53EA\u505C\u7559\u5728\u5BF9\u4E8E\u8282\u5236\u7684\u5F62\u6001\u6216\u8868\u5F81\u4E0A.</p><p>\u4ED6\u5148\u8BF4\u8282\u5236\u5C31\u662F\u5E73\u9759\u7684\u505A\u4E8B. \u800C\u540E\u4ED6\u53C8\u8BF4\u8282\u5236\u5C31\u662F\u8C26\u900A. \u5BF9\u4E8E\u8FD9\u79CD\u6D6E\u4E8E\u8868\u9762\u7684\u5B9A\u4E49, \u82CF\u683C\u62C9\u5E95\u5F88\u5FEB\u5C31\u53CD\u9A73\u4E86.</p><p>\u4E8E\u662F\u4ED6\u53C8\u63D0\u51FA: &quot;\u8282\u5236\u5C31\u662F\u505A\u81EA\u5DF1\u7684\u4E8B&quot;. \u8FD9\u662F\u4ED6\u7684\u5802\u5144\u514B\u91CC\u5E95\u4E9A\u6559\u7ED9\u4ED6\u7684\u8BF4\u6CD5. \u4F46\u4ED6\u53EA\u80FD\u91CC\u89E3\u8FD9\u53E5\u8BDD\u7684\u81EA\u9762\u610F\u601D, \u5374\u4E0D\u80FD\u7406\u89E3\u5176\u771F\u6B63\u7684\u542B\u4E49.</p><p>\u82CF\u683C\u62C9\u5E95\u4E5F\u8F7B\u677E\u7684\u8FDB\u884C\u4E86\u53CD\u9A73.</p><p>\u8FD9\u65F6, \u521A\u521A\u8FD8\u70ED\u60C5\u7684\u8D5E\u7F8E\u7684\u5361\u5C14\u7C73\u5FB7\u7684\u514B\u91CC\u5E95\u4E9A\u5374\u53D1\u8D77\u706B\u6765:</p><blockquote><p>\u597D\u50CF\u8981\u548C\u5361\u5C14\u7C73\u5FB7\u5435\u67B6\u4F3C\u7684, \u5C31\u50CF\u4E00\u540D\u8BD7\u4EBA\u8981\u548C\u4E00\u4E2A\u80CC\u8BF5\u8BD7\u6B4C\u65F6\u7CDF\u8E4B\u4E86\u4ED6\u7684\u8BD7\u6B4C\u7684\u6F14\u5458\u5435\u67B6.</p></blockquote><p>\u514B\u91CC\u5E95\u4E9A\u8D5E\u7F8E\u5361\u5C14\u7C73\u5FB7\u662F\u54F2\u5B66\u5BB6\u548C\u8BD7\u4EBA, \u662F\u56E0\u4E3A\u5361\u5C14\u7C73\u5FB7\u7684\u54F2\u5B66\u548C\u8BD7\u662F\u4ED6\u6559\u51FA\u6765\u7684.</p><p>\u800C\u4E00\u65E6\u5361\u5C14\u7C73\u5FB7\u4E0D\u80FD\u8BC1\u660E\u5176\u6559\u80B2\u4E4B\u826F\u597D, \u4ED6\u7ACB\u65F6\u5C31\u4ECE\u8D5E\u7F8E\u7684\u8DEA\u8214, \u53D8\u6210\u4E86\u6076\u6068\u6068\u7684\u53D1\u706B\u4E86.</p><hr><p>\u81EA\u6B64, \u300A\u5361\u5C14\u7C73\u5FB7\u7BC7\u300B\u4E2D\u4E0E\u82CF\u683C\u62C9\u5E95\u5BF9\u8BDD\u7684\u4EBA\u4FBF\u4E0D\u518D\u662F\u5361\u5C14\u7C73\u5FB7, \u800C\u662F\u514B\u91CC\u5E95\u4E9A\u4E86.</p><p>\u514B\u91CC\u5E95\u4E9A\u540E\u6765\u53C8\u63D0\u51FA\u4E86\u56DB\u79CD\u8282\u5236\u7684\u5B9A\u4E49. \u4F46\u6BCF\u4E00\u79CD\u5B9A\u4E49\u90FD\u4E0D\u80FD\u901A\u8FC7\u82CF\u683C\u62C9\u5E95\u7684\u5BA1\u89C6.</p><p>\u6700\u7EC8\u4ED6\u4EEC\u975E\u4F46\u6CA1\u6709\u627E\u5230\u8282\u5236\u7684\u5B9A\u4E49\u662F\u4EC0\u4E48, \u53CD\u800C\u53D1\u73B0\u6211\u4EEC\u5E76\u4E0D\u80FD\u4ECE\u8282\u5236\u4E2D\u83B7\u5F97\u76CA\u5904.</p><p>\u6709\u5B66\u8005\u8BA4\u4E3A\u5728\u672C\u7BC7\u4E2D\u5F97\u5230\u8FD9\u4E2A\u7B54\u6848\u662F\u56E0\u4E3A\u6B64\u65F6\u8FD8\u672A\u63D0\u51FA\u67CF\u62C9\u56FE\u7684 &quot;\u5FB7\u6027\u7EDF\u4E00\u8BBA&quot;. \u800C\u5FB7\u6027\u7EDF\u4E00\u8BBA\u7684\u63D0\u51FA\u8981\u7B49\u5230\u8BE5\u7BC7\u5BF9\u8BDD\u6545\u4E8B\u7684\u51E0\u5468\u540E, \u82CF\u683C\u62C9\u5E95\u8FDB\u884C\u7684\u53E6\u4E00\u573A\u5BF9\u8BDD\u4E2D\u624D\u88AB\u63D0\u51FA. \u90A3\u573A\u5BF9\u8BDD\u5373\u662F \u300A\u7406\u60F3\u56FD\u300B.</p><p>\u7136\u800C\u8FD9\u5E76\u4E0D\u662F\u6211\u5728\u8FD9\u91CC\u60F3\u8BF4\u7684. \u6211\u8FD9\u91CC\u60F3\u8BF4\u7684\u5176\u5B9E\u662F, \u88AB\u8DEA\u8214\u7684\u8FD9\u4E2A\u5361\u5C14\u7C73\u5FB7, \u6700\u540E\u7ED3\u5C40\u600E\u4E48\u6837\u4E86.</p><hr><p>\u6CE2\u63D0\u72C4\u4E9A\u6218\u4E89\u662F\u4F2F\u7F57\u5954\u5C3C\u6218\u4E89\u7684\u524D\u594F. \u5728\u8FD9\u573A\u6218\u4E89\u4E2D\u96C5\u5178\u56F4\u653B\u4E86\u6CE2\u63D0\u72C4\u4E9A, \u4ECE\u516C\u5143\u524D 432 \u5E74\u4E00\u76F4\u6253\u5230\u516C\u5143\u524D 429 \u5E74\u624D\u653B\u514B.</p><p>\u4E8C\u5341\u5E74\u540E, \u96C5\u5178\u56E0\u897F\u897F\u91CC\u8FDC\u5F81\u5931\u8D25\u800C\u8F93\u6389\u4E86\u8FD9\u573A\u6218\u4E89. \u5C31\u5728\u96C5\u5178\u6218\u8D25\u540E\u4E0D\u4E45, \u4E5F\u5373\u516C\u5143\u524D 404 \u5E74, \u4E09\u5341\u50ED\u4E3B\u63A8\u7FFB\u4E86\u96C5\u5178\u7684\u6C11\u4E3B\u5236, \u5EFA\u7ACB\u4E86\u5BE1\u5934\u5236. \u8FD9\u573A\u653F\u53D8\u7684\u5934\u76EE\u4FBF\u662F\u514B\u91CC\u5E95\u4E9A, \u800C\u5361\u5C14\u7C73\u5FB7\u5728\u662F\u6210\u5458\u4E4B\u4E00.</p><p>\u514B\u91CC\u5E95\u4E9A\u5BF9\u9752\u5E74\u5361\u5C14\u7C73\u5FB7\u7684\u8D5E\u7F8E, \u662F\u4E00\u79CD\u83B7\u53D6\u653F\u6CBB\u8D44\u6E90\u7684\u624B\u6BB5. \u800C\u9752\u5E74\u7684\u5361\u5C14\u7C73\u5FB7\u56E0\u5176\u7F3A\u5C11\u5BF9\u4E8E\u8282\u5236\u5FB7\u6027\u7684\u8BA4\u8BC6, \u6CA1\u6709\u80FD\u529B\u4ECE\u8282\u5236\u4E2D\u83B7\u76CA, \u4ED6\u7684\u4E00\u5207\u7F8E\u597D\u6700\u7EC8\u53D8\u6210\u4E86\u50ED\u653F\u7684\u8D70\u72D7.</p><p>\u50CF\u75AF\u5B50\u4E00\u6837\u7684\u51EF\u52D2\u4E30\u662F\u4E2A\u6C11\u4E3B\u6D3E, \u5F53\u65F6\u5E76\u6CA1\u6709\u770B\u51FA\u514B\u91CC\u5E95\u4E9A\u7684\u91CE\u5FC3, \u8FD8\u56F4\u7ED5\u5728\u4ED6\u8EAB\u8FB9\u4E3A\u5176\u5FD9\u524D\u5FD9\u540E. \u5728\u6545\u4E8B\u4E2D, \u6B63\u662F\u4ED6\u628A\u82CF\u683C\u62C9\u5E95\u62C9\u5230\u514B\u91CC\u5E95\u4E9A\u8EAB\u8FB9\u5750\u4E0B. \u5728\u4E09\u5341\u50ED\u4E3B\u65F6\u671F\u51EF\u52D2\u4E30\u9003\u4EA1\u56FD\u5916. \u516D\u4E2A\u6708\u540E\u56DE\u5230\u96C5\u5178, \u4E0E\u6C11\u4E3B\u6D3E\u63A8\u7FFB\u4E86\u4E09\u5341\u50ED\u4E3B\u7684\u7EDF\u6CBB. \u5728\u6B64\u590D\u8F9F\u8FC7\u7A0B\u4E2D, \u51EF\u52D2\u4E30\u8EAB\u6B7B. \u800C\u514B\u91CC\u5E95\u4E9A\u548C\u5361\u5C14\u7C73\u5FB7\u4E5F\u5728\u5BE1\u5934\u5236\u88AB\u63A8\u7FFB\u540E\u60E8\u6B7B.</p><p>\u50ED\u653F\u4E0D\u4EC5\u4EC5\u8D25\u574F\u4E86\u5361\u5C14\u7C73\u5FB7, \u4E5F\u8D25\u574F\u4E86\u96C5\u5178\u6574\u6574\u7684\u5E74\u9752\u4E00\u4EE3.</p><p>\u56DB\u5E74\u4E4B\u540E, \u4E5F\u5373\u516C\u5143\u524D 399 \u5E74, \u82CF\u683C\u62C9\u5E95\u88AB\u544A\u4E0A\u6CD5\u5EAD, \u5E76\u88AB\u5224\u5904\u6B7B\u5211.</p><p>\u8FD9\u5C31\u662F\u516C\u5143\u524D 429 \u5E74, \u90A3\u4E2A\u88AB\u8DEA\u8214\u7684\u9752\u5E74\u6700\u540E\u7684\u7ED3\u5C40. \u4E5F\u662F\u662F\u76DB\u65F6\u671F, \u88AB\u9AD8\u9AD8\u6367\u8D77\u7684\u4E00\u4EE3\u96C5\u5178\u9752\u5E74, \u548C\u6367\u8D77\u4ED6\u4EEC\u7684\u96C5\u5178\u7684\u6700\u540E\u7684\u7ED3\u5C40.</p><hr><p>\u7136\u800C, \u4E5F\u4E0D\u5FC5\u8FD9\u4E48\u60B2\u89C2. \u6070\u5982\u5F80\u5F80\u8150\u673D\u4E2D\u6700\u80FD\u751F\u51FA\u751F\u673A, \u5199\u4F5C\u8FD9\u7BC7\u5BF9\u8BDD\u7684, \u5374\u6B63\u662F\u514B\u91CC\u5E95\u4E9A\u548C\u5361\u5C14\u7C73\u5FB7\u7684\u4EB2\u4F84\u5B50, \u540E\u4E16\u79F0\u4E3A &quot;\u795E\u5723\u7684&quot; \u67CF\u62C9\u56FE.</p>',59),c=[q];function r(_,a){return o(),t("div",null,c)}var l=p(e,[["render",r],["__file","\u5361\u5C14\u7C73\u5FB7\u6700\u540E\u600E\u6837\u4E86.html.vue"]]);export{l as default};
