import{_ as t,o as e,c as o,e as _}from"./app.efeddd24.js";const p={},a=_('<h1 id="\u5361\u5C14\u7C73\u5FB7\u7BC7\u95EE\u9898\u56DE\u7B54" tabindex="-1"><a class="header-anchor" href="#\u5361\u5C14\u7C73\u5FB7\u7BC7\u95EE\u9898\u56DE\u7B54" aria-hidden="true">#</a> \u5361\u5C14\u7C73\u5FB7\u7BC7\u95EE\u9898\u56DE\u7B54</h1><p>1 \u4E3A\u4EC0\u4E48\u67CF\u62C9\u56FE\u4E00\u63D0\u5230\u51EF\u52D2\u4E30, \u5C31\u8BF4\u4ED6 &quot;\u50CF\u4E2A\u75AF\u5B50\u4E00\u6837&quot; \u8FD9\u548C\u4ED6\u6C11\u4E3B\u6D3E\u7684\u8EAB\u4EFD\u6709\u6CA1\u6709\u5173\u7CFB?</p><p>\u5728\u300A\u7533\u8FA9\u7BC7\u300B\u91CC\u4E5F\u63D0\u5230\u4E86\u51EF\u52D2\u4E30, \u8BF4\u6B63\u662F\u56E0\u4E3A\u4ED6\u8DD1\u5230\u5FB7\u5C14\u6590\u795E\u5E99\u53BB\u95EE &quot;\u82CF\u683C\u62C9\u5E95\u662F\u4E0D\u662F\u6700\u806A\u660E\u7684\u4EBA&quot;, \u624D\u4F7F\u5F97\u82CF\u683C\u62C9\u5E95\u6210\u4E3A\u4E86\u54F2\u5B66\u5BB6. \u5728\u672C\u7BC7\u5BF9\u8BDD\u91CC, \u51EF\u52D2\u4E30\u4E5F\u8D77\u5230 &quot;\u63A5\u5F15&quot; \u7684\u4F5C\u7528. \u5BF9\u8BDD\u7684\u4E3B\u8981\u5185\u5BB9\u662F\u82CF\u683C\u62C9\u5E95\u4E0E\u514B\u91CC\u5E95\u4E9A\u53CA\u5361\u5C14\u7C73\u5FB7\u7684\u5BF9\u8BDD, \u4F46\u6B63\u662F\u51EF\u52D2\u4E30\u628A\u82CF\u683C\u62C9\u5E95\u5F15\u5411\u4E86\u91CC\u514B\u5E95\u4E9A.</p><p>\u6211\u4EEC\u77E5\u9053\u51EF\u52D2\u4E30\u662F\u6C11\u4E3B\u6D3E\u7684\u4EE3\u7406, \u800C\u514B\u91CC\u5E95\u4E9A\u662F\u50ED\u653F\u7684\u4EE3\u8868. \u5728\u7406\u60F3\u56FD\u4E2D\u6709\u4E94\u79CD\u653F\u4F53, \u6700\u7CDF\u7CD5\u7684\u662F\u6C11\u4E3B\u653F\u6CBB\u548C\u50ED\u4E3B\u653F\u6CBB. \u6C11\u4E3B\u653F\u6CBB\u7684\u7279\u70B9\u662F\u653E\u4EFB, \u50ED\u4E3B\u653F\u6CBB\u662F\u5229\u7528\u4E86\u6743\u7AEF\u81EA\u7531\u5BFC\u81F4\u4E86\u6743\u7AEF\u5974\u5F79.</p><p>\u6240\u4EE5\u5728\u8FD9\u91CC\u53EF\u4EE5\u5F71\u5C04\u6C11\u4E3B\u7F3A\u5C11\u8282\u5236, \u800C\u50ED\u4E3B\u6B63\u662F\u901A\u8FC7\u5229\u7528\u6C11\u4E3B\u7F3A\u5C11\u8282\u5236\u6765\u5B9E\u73B0\u5974\u5F79.</p><p>2 \u6545\u4E8B\u5730\u70B9\u662F &quot;\u738B\u5BAB\u5BF9\u9762\u7684\u9676\u745E\u4E9A\u4F53\u80B2\u573A&quot;. \u738B\u5BAB\u795E\u5E99\u662F\u51A5\u754C\u5973\u795E\u7684\u795E\u5E99, \u800C\u9676\u745E\u4E9A\u4F53\u80B2\u573A\u662F\u7EC3\u4E60\u6454\u8DE4\u7684\u573A\u6240. \u8FD9\u4E2A\u5730\u70B9\u7684\u8BBE\u7F6E\u662F\u968F\u610F\u7684, \u8FD8\u662F\u53E6\u6709\u6DF1\u610F? \u662F\u4E0D\u662F\u548C\u672C\u7BC7\u4E3B\u65E8 &quot;\u8282\u5236&quot; \u6709\u5173.</p><p>\u795E\u5E99\u5BF9\u7740\u6454\u8DE4\u573A\u7684\u610F\u601D\u5F88\u660E\u663E, \u4E0D\u597D\u597D\u7EC3\u4E60\u640F\u6597\u7684\u6280\u5DE7, \u5C31\u4F1A\u89C1\u51A5\u754C\u5973\u795E. \u4F53\u80B2\u573A\u53D8\u6210\u4E3A\u8C08\u8BDD\u7684\u573A\u6240. \u82CF\u683C\u62C9\u5E95\u7684\u8C08\u8BDD\u4E5F\u662F\u4E00\u79CD\u640F\u6597. \u5728\u672C\u7BC7\u4E2D\u8FD8\u6709\u4E00\u4E2A\u7C7B\u4F3C\u7684\u60C5\u666F\u8BBE\u7F6E. \u82CF\u683C\u62C9\u5E95\u53F7\u79F0\u5B66\u5230\u5492\u8BED\u7684\u6765\u6E90, \u8272\u96F7\u65AF\u56FD\u738B\u672D\u8033\u8C1F\u514B\u897F\u4E5F\u662F\u8272\u96F7\u65AF\u4EBA\u7684\u51A5\u795E. \u4F46\u5173\u4E8E\u8C08\u8BDD\u7684\u640F\u6597\u5374\u4E00\u8DEF\u653E\u6C34, \u7ED3\u679C\u4E5F\u5E76\u672A\u627E\u5230\u8282\u5236\u662F\u4EC0\u4E48. \u8FD9\u4E2A\u6545\u4E8B\u60C5\u8282\u7684\u8BBE\u7F6E\u591A\u591A\u5C11\u5C11\u5728\u8BF4, \u96C5\u5178\u4ECE\u6B66\u529B\u5230\u6587\u5316\u7684\u8D25\u843D. \u6709\u4E00\u79CD\u82CF\u683C\u62C9\u5E95\u6218\u540E\u54F2\u5B66\u8BF4\u6CD5, \u8BF4\u82CF\u683C\u62C9\u5E95\u5728\u6B64\u6B21\u6218\u6597\u540E\u56DE\u5230\u96C5\u5178, \u53D1\u73B0\u9752\u5E74\u5DF2\u7ECF\u88AB\u8D25\u574F\u4E86, \u56E0\u6B64\u5BF9\u4ED6\u81EA\u5DF1\u7684\u54F2\u5B66\u8FDB\u884C\u4E86\u53CD\u601D, \u624D\u6709\u4E86\u540E\u9762\u300A\u7406\u60F3\u56FD\u300B\u5BF9\u4E8E\u653F\u4F53\u7684\u5168\u9762\u8BA8\u8BBA.</p><p>3 \u5BF9\u6218\u4E89\u6545\u4E8B\u7684\u51B7\u6F20</p><p>\u8FD9\u91CC\u5BF9\u6218\u4E89 ( \u52C7\u6562 ) \u7684\u51B7\u6F20\u4E0E\u540E\u9762\u7684\u6B32\u671B\u7684\u63A7\u5236, \u90FD\u662F\u7528\u6765\u8868\u73B0\u82CF\u683C\u62C9\u5E95\u7684\u8282\u5236\u7684. \u5728\u6590\u591A\u7BC7\u4E2D, \u82CF\u683C\u62C9\u5E95\u8BF4, \u8282\u5236\u5C31\u662F\u5BF9\u6B32\u671B\u7684\u4F53\u9762\u7684\u51B7\u6F20.</p><p>4 \u5176\u4ED6\u542C\u4F17</p>',10),c=[a];function r(u,n){return e(),o("div",null,c)}var d=t(p,[["render",r],["__file","\u5361\u5C14\u7C73\u5FB7\u7BC7\u56DE\u7B54.html.vue"]]);export{d as default};
