import{_ as t,o,c as e,e as _}from"./app.6bf529a6.js";const u={},p=_('<h1 id="\u300A\u4F0A\u5B89\u7BC7\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u4F0A\u5B89\u7BC7\u300B" aria-hidden="true">#</a> \u300A\u4F0A\u5B89\u7BC7\u300B</h1><p>\u300A\u4F0A\u5B89\u7BC7\u300B\u662F\u67CF\u62C9\u56FE\u65E9\u671F\u5199\u7684\u77ED\u7BC7\u5BF9\u8BDD, \u5B83\u5199\u82CF\u683C\u62C9\u5E95\u548C\u4E00\u4F4D\u804C\u4E1A\u8BF5\u8BD7\u4EBA\u4F0A\u5B89\u8BA8\u8BBA\u8BD7\u6B4C\u521B\u4F5C\u662F\u51ED\u4E13\u95E8\u7684\u6280\u827A\u5373\u77E5\u8BC6, \u8FD8\u662F\u51ED\u7075\u611F, \u5B83\u4F53\u73B0\u82CF\u683C\u62C9\u5E95\u5173\u4E8E\u827A\u672F\u521B\u4F5C\u548C\u9274\u8D4F\u7684\u5BA1\u7F8E\u601D\u60F3. \u53E4\u5E0C\u814A\u7684\u6587\u4EF6\u4F5C\u54C1\u7C7B\u578B\u4E3B\u8981\u662F\u53F2\u8BD7, \u6292\u60C5\u8BD7, \u60B2\u5267\u548C\u559C\u5267. \u5267\u4F5C\u4E00\u822C\u662F\u8BD7\u4F53, \u7531\u6F14\u5458\u4EEC\u5728\u5267\u573A\u8868\u73B0, \u5728\u96C5\u5178\u6C11\u4E3B\u5236\u6D3B\u4E2D\u8D77\u6709\u91CD\u8981\u7684\u793E\u4F1A\u653F\u6CBB\u4F5C\u7528. \u53F2\u8BD7\u548C\u6292\u60C5\u8BD7\u5219\u7531\u80B2\u8BD7\u4EBA\u5728\u796D\u5178\u548C\u5BB4\u4F1A\u573A\u5408\u6717\u8BF5, \u6709\u65F6\u8FD8\u8FDB\u884C\u7ADE\u8D5B\u548C\u9881\u5956. \u8377\u9A6C\u548C\u8D6B\u897F\u4FC4\u5FB7\u5C31\u662F\u4F1F\u5927\u7684\u8BD7\u6B4C\u521B\u4F5C\u8005, \u4E5F\u662F\u884C\u541F\u8BD7\u4EBA. \u8BF5\u8BD7\u4EBA\u4E3B\u8981\u8BF5\u8FF0\u8377\u9A6C\u7684\u53F2\u8BD7, \u68AD\u4F26\u66FE\u9881\u4EE4\u5728\u96C5\u5178\u7684\u8BF5\u8BD7\u4EBA\u5E94\u80FD\u80CC\u8BF5\u5168\u90E8\u300A\u4F0A\u5229\u6602\u8BB0\u300B\u548C\u300A\u5965\u5FB7\u4FEE\u7EAA\u300B. \u540E\u6765\u7684\u8BF5\u8BD7\u4EBA\u4E0D\u4EC5\u80CC\u8BF5\u4E5F\u5BF9\u8377\u9A6C\u53F2\u8BD7\u7684\u5185\u5BB9\u81EA\u51FA\u5FC3\u88C1\u5730\u4F5C\u89E3\u8BF4\u548C\u8BC4\u8BBA, \u50CF\u662F\u4E2D\u56FD\u7684 &quot;\u8BC4\u4E66&quot;; \u4ED6\u4EEC\u53EF\u4EE5\u8BF4\u662F &quot;\u8377\u9A6C\u5B66&quot; \u7684\u59CB\u4F5C\u4FD1\u8005, \u4F0A\u5B89\u5C31\u662F\u8FD9\u7C7B\u8BF5\u8BD7\u4EBA.</p><p>\u8FD9\u7BC7\u5BF9\u8BDD\u662F\u53E4\u4EE3\u897F\u65B9\u6700\u65E9\u8BBA\u8FF0\u827A\u672F\u7075\u611F\u7684\u6587\u732E, \u5728\u8FD9\u4EE5\u524D\u5E0C\u814A\u4EBA\u901A\u5E38\u8BA4\u4E3A\u8BD7\u6B4C\u4E5F\u662F\u4E00\u79CD\u6280\u827A\u77E5\u8BC6, \u540C\u5176\u4ED6\u5DE5\u827A, \u533B\u672F\u7B49\u76F8\u4EFF, \u8BD7\u4EBA\u7684\u5730\u4F4D\u7C7B\u4F3C\u5DE5\u5320\u548C\u533B\u751F, \u4ED6\u4EEC\u53EA\u662F\u4EE5\u541F\u8BF5\u7684\u65B9\u5F0F\u4F20\u6388\u6280\u827A\u77E5\u8BC6. \u5728\u82CF\u683C\u62C9\u5E95\u4EE5\u524D\u53EA\u6709\u897F\u897F\u91CC\u5C9B\u8457\u540D\u8BD7\u4EBA\u54C1\u8FBE\u666E\u65AD\u8A00\u8BD7\u4EBA\u4F18\u4E8E\u6280\u5320, \u56E0\u4E3A\u8BD7\u4EBA\u51ED\u501F &quot;\u5929\u751F\u7684\u624D\u667A&quot; \u5373\u67D0\u79CD\u5929\u8D4B\u7684\u6FC0\u60C5\u8FDB\u884C\u521B\u4F5C, \u4F46\u54C1\u8FBE\u5E76\u6CA1\u6709\u5C55\u5F00\u8BBA\u8FF0. \u5728\u300A\u4F0A\u5B89\u7BC7\u300B\u4E2D\u82CF\u683C\u62C9\u5E95\u6700\u65E9\u63D0\u51FA\u8BD7\u6B4C\u521B\u4F5C\u548C\u9274\u8D4F\u7684\u672C\u6027\u4E0D\u662F\u6280\u827A\u77E5\u8BC6\u800C\u662F\u7075\u611F\u5373\u6FC0\u60C5, \u8FD9\u540C\u300A\u7533\u8FA9\u7BC7\u300B\u4E2D\u82CF\u683C\u62C9\u5E95\u8BF4\u4ED6\u8D70\u8BBF\u8BD7\u4EBA, \u53D1\u73B0 &quot;\u4E0D\u662F\u667A\u6167\u800C\u662F\u4E00\u79CD\u672C\u80FD\u6216\u7075\u611F\u4F7F\u4ED6\u4EEC\u80FD\u5199\u8BD7&quot;\u7684\u89C2\u70B9\u662F\u4E00\u81F4\u7684. \u4ED6\u63D0\u51FA\u7684\u827A\u672F\u7075\u611F\u8BF4\u4ECE\u4ECA\u4EBA\u7684\u773C\u5149\u770B\u5E76\u4E0D\u65B0\u9C9C, \u5728\u5F53\u65F6\u5374\u662F\u4E00\u79CD\u65B0\u9896\u7684\u89C1\u89E3; \u8868\u660E\u82CF\u683C\u62C9\u5E95\u867D\u7136\u4E3B\u5F20\u4EE5\u7406\u6027\u4E3B\u4E49\u54F2\u5B66\u8BF4\u660E\u4EBA\u548C\u793E\u4F1A\u751F\u6D3B\u7684\u5404\u79CD\u95EE\u9898, \u4F46\u4ED6\u5DF2\u6CE8\u610F\u5230\u827A\u672F\u521B\u4F5C\u6709\u72EC\u7279\u7684\u6027\u8D28\u548C\u529F\u80FD, \u5BF9\u6B64\u4F5C\u4E86\u521D\u6B65\u63A7\u8BA8.</p><p>\u5C0F\u4E9A\u7EC6\u4E9A\u57CE\u90A6\u7231\u83F2\u7D22\u4EBA\u4F0A\u5B89\u5174\u9AD8\u91C7\u70C8\u5730\u5BF9\u82CF\u683C\u62C9\u5E95\u8BF4, \u4ED6\u53C2\u52A0\u4E86\u5E0C\u814A\u5357\u90E8\u57C3\u76AE\u9053\u4F26\u6BCF\u56DB\u5E74\u4E3E\u884C\u4E00\u6B21\u7684\u533B\u795E\u796D\u5178, \u5E76\u5728\u8BF5\u8BD7\u6BD4\u8D5B\u4E2D\u83B7\u5F97\u5934\u5956. \u82CF\u683C\u62C9\u5E95\u79F0\u9882\u8377\u9A6C\u662F\u4E00\u4F4D\u6700\u4F1F\u5927\u6700\u795E\u5723\u7684\u8BD7\u4EBA, \u5E76\u8D5E\u626C\u8BF5\u8BD7\u4EBA\u4F0A\u5B89\u80FD\u900F\u5F7B\u7406\u89E3\u548C\u89E3\u8BF4\u8377\u9A6C\u601D\u60F3\u7684\u672C\u9886\u4EE4\u4EBA\u7FA1\u6155. \u4F46\u662F\u4F0A\u5B89\u65E0\u6CD5\u8BF4\u660E\u4ED6\u4E3A\u4EC0\u4E48\u5C24\u5176\u80FD\u8BF4\u660E\u8377\u9A6C\u7684\u53F2\u8BD7, \u4E00\u8C08\u5230\u8377\u9A6C\u987F\u751F\u60C5\u8DA3, \u6587\u601D\u6E90\u6E90\u4E0D\u65AD; \u867D\u7136\u8D6B\u897F\u5965\u5FB7\u53CA\u5176\u4ED6\u8BD7\u4EBA\u7684\u4F5C\u54C1\u4E5F\u548C\u8377\u9A6C\u76F8\u4F3C\u5730\u5199\u6218\u4E89, \u4EBA\u795E\u5173\u7CFB\u7B49\u9898\u6750, \u4ED6\u5374\u89E3\u8BF4\u4E0D\u597D, \u8C08\u5230\u5B83\u4EEC\u65F6\u987F\u751F\u5026\u610F\u8981\u6253\u778C\u7761. \u82CF\u683C\u62C9\u5E95\u89E3\u91CA\u8FD9\u79CD\u60C5\u51B5\u7684\u539F\u56E0: \u4F0A\u5B89\u89E3\u8BF4\u8377\u9A6C\u7684\u80FD\u529B\u4E0D\u662F\u51ED\u6280\u827A\u77E5\u8BC6, \u8981\u662F\u51ED\u6280\u827A\u5C31\u5E94\u8BE5\u80FD\u89E3\u8BF4\u5176\u4ED6\u4E00\u5207\u8BD7\u4EBA: \u56E0\u4E3A\u4F5C\u4E3A\u6574\u4F53\u7684\u8BD7\u7684\u6280\u827A\u540C\u5176\u4ED6\u4EFB\u4F55\u5176\u4ED6\u4EFB\u4F55\u6280\u827A\u4E00\u6837, \u5404\u6709\u540C\u6837\u7684\u7814\u8BA8\u65B9\u6CD5\u53EF\u4EE5\u7528\u6765\u8BC4\u9274\u4E00\u5207\u8BD7\u4EBA\u7684\u4F5C\u54C1; \u7ED8\u753B\u548C\u96D5\u523B\u7684\u6280\u827A\u4F5C\u4E3A\u6574\u4F53\u4E5F\u6709\u76F8\u540C\u7684\u7814\u8BA8\u65B9\u6CD5, \u4E0D\u4F1A\u4F7F\u4EBA\u53EA\u80FD\u5224\u522B\u6CE2\u5415\u683C\u8BFA\u56FE ( \u516C\u5143\u4E94\u4E16\u7EAA\u5E0C\u814A\u5927\u753B\u5BB6 ) \u4F5C\u54C1\u7684\u597D\u574F, \u800C\u5BF9\u5176\u4ED6\u753B\u5BB6\u7684\u4F5C\u54C1\u5C31\u4F1A\u6253\u778C\u7761, \u832B\u7136\u65E0\u7DD2; \u5BF9\u96D5\u523B, \u7B1B\u5E08, \u7434\u5E08\u548C\u8BF5\u8BD7\u4EBA\u7684\u9274\u8BC4\u4E5F\u662F\u5982\u6B64. \u8FD9\u91CC\u82CF\u683C\u62C9\u5E95\u5E76\u6CA1\u6709\u5426\u8BA4\u827A\u672F\u662F\u6709\u77E5\u8BC6\u5185\u5BB9\u7684\u6280\u827A, \u5404\u79CD\u827A\u672F\u5F62\u5F0F\u7684\u5168\u4F53\u4F5C\u4E3A\u4E00\u79CD\u6280\u827A\u77E5\u8BC6\u6709\u5171\u540C\u6027, \u6709\u540C\u6837\u7684\u7814\u8BA8\u6CD5\u5219, \u6709\u9274\u522B\u597D\u574F\u7684\u5171\u540C\u6807\u51C6. \u4F46\u662F\u4ED6\u6839\u636E\u4F0A\u5B89\u53EA\u64C5\u957F\u5E76\u9676\u9189\u4E8E\u89E3\u8BF4\u8377\u9A6C\u53F2\u8BD7\u8FD9\u79CD\u8BF7\u51B5, \u6307\u51FA\u827A\u672F\u54C1\u521B\u4F5C\u548C\u9274\u8BC4\u6709\u4E0D\u540C\u4E8E\u4E00\u822C\u6280\u827A\u77E5\u8BC6\u7684\u7279\u6027\u548C\u9B45\u529B, \u90A3\u5C31\u662F\u5176\u4E2D\u6709\u7075\u611F\u5373\u6FC0\u60C5\u8D77\u91CD\u8981\u4F5C\u7528.</p><p>\u82CF\u683C\u62C9\u5E95\u8BA4\u4E3A\u53EF\u4EE5\u89E3\u5F00\u4F0A\u5B89\u4E4B\u8FF7; \u4F0A\u5B89\u64C5\u957F\u89E3\u8BF4\u8377\u9A6C\u7684\u53C2\u529B\u6765\u81EA\u4E00\u79CD\u795E\u5723\u7684\u529B\u91CF, \u5C31\u662F\u7075\u611F. \u5B83\u50CF\u6B27\u91CC\u5E87\u5F97\u65AF\u6240\u8BF4\u7684\u77F3\u5934\u5373\u4E00\u822C\u4EBA\u6240\u8BF4\u7684\u78C1\u77F3, \u5B83\u4E0D\u4EC5\u80FD\u5438\u5F15\u94C1\u73AF, \u8FD8\u80FD\u5C06\u5438\u5F15\u529B\u4F20\u7ED9\u94C1\u73AF, \u4F7F\u5B83\u4EEC\u4E5F\u80FD\u50CF\u78C1\u77F3\u4E00\u6837\u5438\u5F15\u5176\u4ED6\u94C1\u73AF, \u5F62\u6210\u4E00\u6761\u957F\u7684\u9501\u94FE, \u5168\u90E8\u5438\u5F15\u529B\u90FD\u6765\u81EA\u8FD9\u5757\u78C1\u77F3\u5373\u7075\u611F. \u8BD7\u795E\u5C31\u50CF\u8FD9\u5757\u78C1\u77F3, \u9996\u5148\u7ED9\u8BD7\u4EBA\u4EE5\u7075\u611F, \u7136\u540E\u4F7F\u5176\u4ED6\u4EBA\u5206\u6709\u6FC0\u60C5\u4E5F\u9F13\u8D77\u7075\u611F, \u5F62\u6210\u827A\u672F\u611F\u67D3\u7684\u9501\u94FE. \u4ED6\u8BA4\u4E3A\u51E1\u662F\u9AD8\u660E\u7684\u8BD7\u4EBA\u90FD\u4E0D\u662F\u51ED\u6280\u672F, \u800C\u662F\u56E0\u4E3A\u62E5\u6709\u7075\u611F\u624D\u80FD\u521B\u9020\u51FA\u4F18\u7F8E\u7684\u8BD7\u54E5. \u8FD9\u79CD\u7075\u611F\u662F\u4E00\u79CD\u6C89\u6EBA\u4E8E\u6FC0\u60C5\u7684\u4F3C\u9189\u5982\u75F4\u7684\u60C5\u611F\u5FC3\u7406, \u5C31\u597D\u50CF\u5DEB\u5E08\u796D\u9152\u72C2\u821E\u65F6\u7684\u8FF7\u72C2, \u6292\u60C5\u505A\u8BD7\u65F6\u7684\u5FC3\u7406\u4E5F\u662F\u5982\u6B64. \u8FD9\u91CC\u82CF\u683C\u62C9\u5E95\u4E3B\u5F20\u827A\u672F\u4F5C\u54C1\u7684\u521B\u4F5C\u548C\u9274\u8D4F\u53D7\u7075\u611F\u652F\u914D, \u4E0D\u80FD\u5C06\u5B83\u5F52\u7ED3\u4E3A\u7406\u6027\u77E5\u8BC6, \u7075\u611F\u662F\u4E00\u79CD\u827A\u672F\u7684\u6FC0\u60C5, \u6709\u5F3A\u70C8\u7684\u827A\u672F\u611F\u67D3\u9B45\u529B, \u80FD\u53D1\u751F\u9501\u94FE\u5F0F\u53CD\u5E94.</p><p>\u82CF\u683C\u62C9\u5E95\u63A5\u7740\u4ECE\u4E0D\u540C\u65B9\u9762\u8BBA\u8FF0\u827A\u672F\u4F5C\u54C1\u4E2D\u8FD9\u79CD\u7075\u611F\u7684\u652F\u914D\u529B, \u4ED6\u8BA4\u4E3A\u8BD7\u6B4C\u521B\u4F5C\u662F\u51ED\u7075\u611F\u7684, \u6B63\u5982\u5973\u4FE1\u5F92\u51ED\u7740\u5BF9\u9152\u795E\u7684\u8FF7\u72C2\u5FC3\u7406\u7ADF\u4F1A\u4ECE\u6CB3\u6C34\u4E2D\u6C72\u53D6\u4E73\u548C\u871C, \u6292\u60C5\u8BD7\u4EBA\u521B\u4F5C\u65F6\u4E5F\u540C\u6837\u53D7\u97F5\u5F8B\u652F\u914D, \u7075\u9B42\u50CF\u5BC6\u8702\u90A3\u6837\u98DE\u5230\u8BD7\u795E\u7684\u82B1\u56ED\u548C\u5E7D\u8C37\u91CC\u53BB\u917F\u871C. \u8BD7\u4EBA\u50CF\u662F\u8F7B\u9038, \u957F\u6709\u7FBD\u7FFC\u7684\u795E, \u6CA1\u6709\u7075\u611F, \u4E0D\u5931\u53BB\u5E73\u5E38\u7684\u7406\u6027\u5C31\u6CA1\u6709\u80FD\u529B\u505A\u8BD7, \u541F\u5531\u51FA\u4F18\u7F8E\u7684\u8BD7\u53E5. \u8BD7\u4EBA\u541F\u5531\u8BB8\u591A\u795E\u548C\u4EBA\u7684\u7F8E\u597D\u4E1A\u7EE9, \u90FD\u4E0D\u662F\u9760\u6280\u672F\u77E5\u8BC6, \u800C\u662F\u9760\u7075\u611F\u8FD9\u79CD\u8BD7\u795E\u9A71\u9063\u7684\u795E\u529B. \u56E0\u6B64\u8BD7\u4EBA\u4EEC\u90FD\u5404\u6709\u6240\u957F\u5584\u4E8E\u505A\u67D0\u7C7B\u4F53\u88C1\u6216\u9898\u6750\u7684\u8BD7; \u8981\u662F\u505A\u8BD7\u51ED\u6280\u672F, \u8BD7\u4EBA\u5C31\u5E94\u8BE5\u4F1A\u4F5C\u4EFB\u4F55\u9898\u76EE\u6216\u4F53\u88C1\u7684\u8BD7\u4E86. \u82CF\u683C\u62C9\u5E95\u5C06\u8BD7\u4EBA\u5931\u53BB\u5E73\u5E38\u7406\u6027, \u4EFB\u7075\u611F\u521B\u4F5C\u8BD7\u8BF4\u6210\u662F\u597D\u50CF\u795E\u7ED9\u4E88\u7684\u529B\u91CF, \u4F7F\u8BD7\u4EBA\u6210\u4E3A\u795E\u7684\u4EE3\u8A00\u4EBA.</p><p>\u8BF5\u8BD7\u4EBA\u5219\u662F\u4EE3\u8A00\u4EBA\u7684\u4EE3\u8A00\u4EBA. \u4ED6\u4EEC\u5BF9\u8BD7\u7684\u89E3\u8BF4\u548C\u9274\u8D4F\u4E5F\u662F\u51ED\u9501\u94FE\u5F0F\u53CD\u5E94\u7684\u7075\u611F\u7684\u611F\u67D3\u529B, \u800C\u4E0D\u662F\u51ED\u6280\u827A. \u82CF\u683C\u62C9\u5E95\u6307\u51FA, \u4F0A\u5B89\u5728\u6717\u8BF5\u8377\u9A6C\u53F2\u8BD7\u7684\u4E00\u4E9B\u7CBE\u5F69\u6BB5\u843D\u65F6, &quot;\u5931\u53BB\u81EA\u4E3B, \u9677\u5165\u8FF7\u72C2, \u597D\u50CF\u8EAB\u4E34\u8BD7\u6240\u63CF\u8FF0\u7684\u5883\u754C&quot;, \u54C0\u601C\u65F6 &quot;\u6EE1\u773C\u662F\u6CEA&quot;, \u6050\u6016\u65F6 &quot;\u6BDB\u9AA8\u609A\u7136&quot;; \u542C\u4F17\u540C\u6837\u5982\u9189\u4F3C\u8FF7, \u4EFF\u4F5B\u5931\u53BB\u6E05\u9192\u7684\u795E\u667A. \u8FD9\u79CD\u611F\u67D3\u529B\u6765\u81EA\u7075\u611F, \u8BD7\u4EBA\u7684\u7075\u611F\u662F\u539F\u59CB\u78C1\u77F3, \u662F\u6700\u521D\u7684\u4E00\u73AF, \u8BF5\u8BD7\u4EBA\u548C\u6F14\u5458\u662F\u4E2D\u95F4\u73AF\u8282, \u542C\u4F17\u548C\u89C2\u4F17\u662F\u6700\u540E\u4E00\u73AF. \u4F0A\u5B89\u64C5\u957F\u89E3\u8BF4\u8377\u9A6C, \u4E43\u662F\u56E0\u4E3A\u8377\u9A6C\u7684\u7075\u611F\u6240\u6FC0\u594B, \u800C\u4E0D\u662F\u51ED\u6280\u827A\u77E5\u8BC6.</p><p>\u82CF\u683C\u62C9\u5E95\u5E76\u4E0D\u5426\u8BA4\u8377\u9A6C\u53F2\u8BD7\u4E2D\u6709\u4E0D\u5C11\u5177\u4F53\u6280\u827A\u77E5\u8BC6\u5185\u5BB9, \u5BF9\u5B83\u4EEC\u53EA\u80FD\u7531\u4E13\u95E8\u7684\u6280\u827A\u77E5\u8BC6\u53BB\u8BC4\u4EF7; \u4F46\u4ED6\u8BA4\u4E3A\u8BF5\u8BD7\u4EBA\u80FD\u541F\u8BF4\u548C\u9274\u8D4F\u53F2\u8BD7\u5E76\u4E0D\u662F\u9760\u8FD9\u7C7B\u77E5\u8BC6, \u800C\u53EA\u80FD\u9760\u8BD7\u4EBA\u8D4B\u4E88\u7684\u7075\u611F. \u82CF\u683C\u62C9\u5E95\u4E3E\u4F8B\u8BF4: \u8377\u9A6C\u300A\u4F0A\u5229\u6602\u7EAA\u300B\u4E2D\u5199\u5230\u9A7E\u8F66, \u533B\u7597, \u6E14\u592B, \u730E\u4EBA\u624D\u80FD\u6B63\u786E\u8BC4\u5224\u8FD9\u4E9B\u6709\u5173\u6280\u827A\u7684\u63CF\u8FF0, \u9760\u8BF5\u8BD7\u7684\u6280\u827A\u4E0D\u80FD\u5BF9\u5B83\u4EEC\u6709\u6B63\u786E\u7684\u5224\u65AD. \u4F0A\u5B89\u8FA9\u89E3\u8BF4: \u8BF5\u8BD7\u4EBA\u56FA\u7136\u4E0D\u61C2\u8FD9\u4E9B\u6280\u827A, \u4F46\u61C2\u5F97\u7537\u4EBA\u548C\u5973\u4EBA, \u81EA\u7531\u6C11\u548C\u5974\u96B6, \u7EDF\u6CBB\u8005\u548C\u88AB\u7EDF\u6CBB\u8005\u600E\u6837\u8BF4\u8BDD, \u5728\u8FD9\u65B9\u9762\u6709\u4E13\u957F\u662F\u884C\u5BB6. \u82CF\u683C\u62C9\u5E95\u53CD\u9A73\u8BF4: \u8BF5\u8BD7\u4EBA\u5E76\u4E0D\u61C2\u5F97\u7267\u725B\u5974\u96B6\u5728\u9A6F\u670D\u72C2\u725B\u65F6\u5982\u4F55\u8BF4\u8BDD, \u4E0D\u61C2\u5F97\u7EBA\u7EC7\u5987\u5973\u5728\u7EC7\u7F8A\u6BDB\u65F6\u8BF4\u4EC0\u4E48\u8BF4, \u4E5F\u4E0D\u61C2\u5F97\u5C06\u519B\u7684\u6280\u827A, \u5426\u5219\u4F0A\u5B89\u4F5C\u4E3A\u6700\u9AD8\u660E\u7684\u8BF5\u8BD7\u4EBA\u4E5F\u5C31\u662F\u6700\u9AD8\u660E\u7684\u5C06\u519B\u4E86, \u800C\u4F0A\u5B89\u5728\u5E0C\u814A\u5230\u5904\u541F\u8BF5, \u603B\u662F\u5F53\u4E0D\u4E0A\u5C06\u519B. \u8FD9\u4E00\u5207\u90FD\u53EA\u80FD\u8BF4\u660E\u4F0A\u5B89\u8BF5\u9882\u8377\u9A6C\u7684\u53F2\u8BD7, \u4E0D\u662F\u51ED\u6280\u827A\u800C\u662F\u9760\u7075\u611F. \u8FD9\u4E9B\u8BBA\u8FF0\u8868\u660E, \u82CF\u683C\u62C9\u5E95\u8BA4\u4E3A\u827A\u672F\u4F5C\u54C1\u7684\u5185\u5BB9\u548C\u5BF9\u5B83\u7684\u89E3\u8BF4\u4E5F\u7275\u6D89\u5230\u5404\u79CD\u6280\u827A\u77E5\u8BC6, \u5BF9\u5B83\u4EEC\u7684\u7406\u89E3\u548C\u8BC4\u5224\u4E5F\u5F97\u9760\u5404\u79CD\u4E13\u95E8\u77E5\u8BC6, \u8BD7\u4EBA\u548C\u8BF5\u8BD7\u4EBA\u5E76\u4E0D\u662F\u8FD9\u7C7B\u4E13\u95E8\u6280\u827A\u77E5\u8BC6\u7684\u884C\u5BB6. \u4F46\u4ED6\u8BA4\u4E3A, \u6280\u827A\u77E5\u8BC6\u4E0D\u662F\u827A\u672F\u4F5C\u54C1\u7684\u7279\u6027\u548C\u9B45\u529B\u6240\u5728, \u7075\u611F\u5373\u975E\u7406\u6027\u77E5\u8BC6\u7684\u6FC0\u60C5\u624D\u4F53\u73B0\u827A\u672F\u4F5C\u54C1\u7684\u7279\u6B8A\u6027.</p><p>\u300A\u4F0A\u5B89\u7BC7\u300B\u8868\u8FF0\u4E86\u82CF\u683C\u62C9\u5E95\u5173\u4E8E\u827A\u672F\u521B\u4F5C\u548C\u9274\u8D4F\u7684\u5BA1\u7F8E\u601D\u60F3. \u5F53\u65F6\u5E0C\u814A\u4EBA\u8FD8\u6CA1\u6709\u5C06\u6587\u827A\u4F5C\u54C1\u540C\u4E00\u822C\u6280\u672F\u77E5\u8BC6\u533A\u522B\u5F00\u6765, \u8BD7\u4EBA\u7684\u5730\u4F4D\u7C7B\u4F3C\u6280\u5320; \u82CF\u683C\u62C9\u5E95\u4E5F\u4E0D\u5426\u8BA4\u6587\u5B66\u827A\u672F\u662F\u4E00\u79CD\u6280\u827A\u77E5\u8BC6, \u5728\u300A\u5927\u5E0C\u5E87\u4E9A\u7BC7\u300B\u4E2D\u4ED6\u63A2\u8BA8\u7F8E\u7684\u672C\u8D28\u4E5F\u662F\u8981\u5728\u4E2A\u522B\u4E2D\u5BFB\u6C42\u4E00\u822C. \u4F46\u662F\u4ED6\u4E5F\u770B\u5230\u6587\u5B66\u4F5C\u54C1\u6709\u5B83\u72EC\u7279\u7684\u9B45\u529B, \u4E3A\u4EC0\u4E48\u4F1F\u5927\u7684\u4F5C\u5BB6\u53EF\u4EE5\u521B\u9020\u51FA\u6263\u4EBA\u5FC3\u5F26\u7684\u4F5C\u54C1? \u4E3A\u4EC0\u4E48\u5728\u6717\u8BF5\u548C\u6B23\u8D4F\u53F2\u8BD7\u65F6\u4F1A\u5BF9\u8377\u9A6C\u7684\u4F5C\u54C1\u5982\u75F4\u4F3C\u72C2, \u800C\u5BF9\u522B\u4EBA\u7684\u4F5C\u54C1\u7D22\u7136\u65E0\u5473\u5462? \u4ED6\u611F\u5230\u8FD9\u79CD\u60C5\u51B5\u4E0D\u80FD\u7528\u666E\u901A\u7684\u4E00\u822C\u77E5\u8BC6\u53BB\u89E3\u91CA, \u53EA\u80FD\u5C06\u5B83\u4EEC\u5F52\u4E8E\u7075\u611F. \u7075\u611F\u662F\u4E00\u79CD\u6FC0\u60C5, \u5B83\u5177\u6709\u6FC0\u52A8\u4EBA\u5FC3\u7684\u611F\u67D3\u529B, \u5C31\u50CF\u78C1\u77F3\u4E00\u6837. \u8BD7\u4EBA\u4ECE\u795E\u90A3\u91CC\u5F97\u5230\u7075\u611F\uFF0C \u7531\u6B64\u611F\u67D3\u4E86\u8BF5\u8BD7\u4EBA\u53C8\u611F\u67D3\u542C\u4F17, \u6210\u4E3A\u4E00\u6761\u6FC0\u60C5\u7684\u9501\u94FE, \u5B83\u4E0D\u540C\u4E8E\u4E00\u822C\u7684\u7406\u6027\u77E5\u8BC6, \u8FD9\u5C31\u662F\u827A\u672F\u4F5C\u54C1\u7684\u7279\u6027\u7684\u9B45\u529B. \u7528\u4ECA\u5929\u7684\u8BDD\u8BF4, \u4ED6\u5DF2\u7ECF\u770B\u5230\u827A\u672F\u521B\u4F5C\u548C\u9274\u8D4F\u4E0D\u662F\u51ED\u62BD\u8C61\u7684\u903B\u8F91\u601D\u7EF4, \u800C\u662F\u5C06\u6FC0\u60C5\u878D\u6CE8\u5165\u827A\u672F\u5F62\u8C61, \u521B\u9020\u51FA\u7F8E\u7684\u610F\u5883, \u4ECE\u800C\u4EA7\u751F\u78C1\u77F3\u822C\u7684\u827A\u672F\u5438\u5F15\u529B. \u56E0\u800C\u82CF\u683C\u62C9\u5E95\u7684\u770B\u6CD5\u5E94\u8BE5\u8BF4\u662F\u4ECE\u5B9E\u9645\u51FA\u53D1, \u4F01\u56FE\u5BF9\u4E8B\u5B9E\u4F5C\u51FA\u5408\u7406\u7684\u89E3\u91CA; \u5C3D\u7BA1\u4ED6\u6240\u8BF4\u7684\u7075\u611F\u5E26\u6709\u67D0\u79CD\u795E\u79D8\u6210\u5206, \u4F46\u4E0D\u80FD\u8BF4\u5B83\u662F\u8FDD\u80CC\u7406\u6027\u539F\u5219, \u662F\u53CD\u7406\u6027\u4E3B\u4E49\u7684. \u73B0\u4EE3\u7F8E\u5B66\u5BF9\u7075\u611F\u53EF\u4EE5\u6709\u5404\u79CD\u8D5E\u6210\u6216\u53CD\u5BF9\u7684\u610F\u89C1, \u4F46\u5927\u591A\u6570\u7F8E\u5B66\u5BB6\u603B\u4F1A\u627F\u8BA4\u6587\u5B66\u827A\u672F\u6709\u4E0D\u540C\u4E8E\u4E00\u822C\u7406\u6027\u7684\u903B\u8F91\u601D\u7EF4\u7684\u7279\u70B9. \u56E0\u6B64\u6211\u4EEC\u5728\u8BA8\u8BBA\u8FD9\u79CD\u7075\u611F\u8BF4\u65F6, \u4E0D\u5E94\u8FC7\u5206\u5938\u5927\u5B83\u7684\u795E\u79D8\u6027\u548C\u53CD\u7406\u6027\u7684\u65B9\u9762. \u6731\u5149\u6F5C\u5148\u751F 1963 \u5E74\u51FA\u7248\u7684 \u300A\u67CF\u62C9\u56FE\u6587\u827A\u5BF9\u8BDD\u96C6\u300B\u7FFB\u8BD1\u4E86\u67CF\u62C9\u56FE\u6240\u6709\u6709\u5173\u6587\u827A\u7684\u5BF9\u8BDD, \u5E76\u4F5C\u4E86\u89E3\u91CA\u548C\u8BC4\u8FF0, \u5BF9\u8FD9\u65B9\u9762\u7684\u5B66\u672F\u7814\u7A76\u4F5C\u51FA\u4E86\u91CD\u8981\u8D21\u732E, \u6211\u4EEC\u5BF9\u8FD9\u4E9B\u5BF9\u8BDD\u7684\u8BBA\u8FF0\u90FD\u53C2\u8003\u4E86\u6731\u5148\u751F\u7684\u8BD1\u6587\u548C\u610F\u89C1. \u4F46\u662F\u7531\u4E8E\u5F53\u65F6\u65F6\u4EE3\u7684\u70D9\u5370, \u6731\u5148\u751F\u6240\u4F5C\u7684\u8BC4\u8FF0\u4ECE\u73B0\u5728\u6765\u770B\u662F\u5E26\u6709\u7247\u9762\u6027\u7684, \u4ED6\u4ECE &quot;\u67CF\u62C9\u56FE\u662F\u7AD9\u5728\u8D35\u65CF\u9636\u7EA7\u53CD\u5BF9\u7684\u7ACB\u573A\u4E0A\u7684&quot; \u8FD9\u4E2A\u603B\u7684\u8BC4\u4EF7\u51FA\u53D1, \u8BF4 \u300A\u4F0A\u5B89\u7BC7\u300B \u4E2D\u7684 &quot;\u7075\u611F\u8BF4\u57FA\u672C\u4E0A\u662F\u795E\u79D8\u548C\u53CD\u5BF9\u7684&quot;, &quot;\u6B64\u540E\u5C3C\u91C7\u7684 &#39;\u9152\u795E\u7CBE\u795E&#39; \u8BF4, \u67CF\u683C\u68EE\u7684\u76F4\u89C9\u8BF4\u548C\u827A\u672F\u50AC\u7720\u72B6\u6001\u8BF4, \u5F17\u6D1B\u4F0A\u5FB7\u7684\u827A\u672F\u8D77\u6E90\u4E0B\u610F\u8BC6\u8BF4, \u514B\u7F57\u9F50\u7684\u76F4\u89C9\u8868\u73B0\u8BF4\u4EE5\u53CA\u8428\u7279\u7684\u5B58\u5728\u4E3B\u4E49, \u867D\u7136\u51FA\u53D1\u70B9\u4E0D\u540C, \u63A8\u7406\u7684\u65B9\u5F0F\u4E5F\u4E0D\u540C, \u4F46\u662F\u5728\u53CD\u7406\u6027\u4E00\u70B9\u4E0A\u90FD\u548C\u67CF\u62C9\u56FE\u662F\u4E00\u9F3B\u5B54\u51FA\u6C14\u7684.&quot; \u6211\u4EEC\u4EE5\u4E3A\u8BC4\u4EF7\u4E00\u4F4D\u54F2\u5B66\u5BB6\u7684\u67D0\u4E00\u65B9\u9762\u7684\u601D\u60F3\u65F6, \u9996\u5148\u8981\u786E\u5B9A\u4ED6\u6574\u4F53\u601D\u60F3\u503E\u5411, \u5E94\u8BE5\u627F\u8BA4\u82CF\u683C\u62C9\u5E95\u548C\u67CF\u62C9\u56FE\u7684\u601D\u60F3\u90FD\u5341\u5206\u5F3A\u8C03\u7406\u6027\u7684\u4F5C\u7528, \u4ED6\u4EEC\u662F\u897F\u65B9\u7406\u6027\u4E3B\u4E49\u601D\u60F3\u7684\u521B\u59CB\u4EBA\u548C\u6700\u65E9\u7684\u4EE3\u8868. \u4ED6\u4EEC\u6700\u65E9\u63D0\u51FA\u7075\u611F\u8FD9\u79CD\u827A\u672F\u7279\u5F81\u8BD5\u56FE\u7ED9\u4EE5\u89E3\u91CA, \u5F53\u6CA1\u6709\u529E\u6CD5\u5B8C\u5168\u7528\u7406\u6027\u8BF4\u660E\u4ED6\u4EEC\u501F\u7528\u4E86\u5F53\u65F6\u4E00\u822C\u6D41\u884C\u7684\u795E\u7684\u89C2\u5FF5\u4F5C\u4E86\u4E00\u70B9\u795E\u79D8\u7684\u89E3\u91CA; \u540E\u6765\u67CF\u62C9\u56FE\u5728\u300A\u4F1A\u996E\u7BC7\u300B\u548C\u300A\u6590\u5FB7\u7F57\u7BC7\u300B\u4E2D\u4E5F\u8BB2\u5230\u8FF7\u72C2\u7B49\u95EE\u9898, \u4F46\u662F\u5C06\u5B83\u548C\u77E5\u8BC6, \u7F8E\u5FB7\u8054\u7CFB\u8D77\u6765\u63A2\u8BA8\u771F\u5584\u7F8E\u7684\u7EDF\u4E00\u95EE\u9898, \u4E5F\u5C31\u662F\u4F01\u56FE\u4EE5\u7406\u6027\u53BB\u89E3\u91CA\u827A\u672F\u6FC0\u60C5. \u600E\u4E48\u80FD\u5C06\u67CF\u62C9\u56FE\u63A8\u5230\u8FD1\u73B0\u4EE3\u7684\u53CD\u7406\u6027\u4E3B\u4E49\u884C\u5217\u4E2D\u53BB\u5462? \u6211\u4EEC\u4EE5\u4E3A\u82CF\u683C\u62C9\u5E95\u7684\u5BA1\u7F8E\u601D\u60F3\u672C\u8D28\u662F\u4ECD\u662F\u7406\u6027\u4E3B\u4E49\u7684, \u6B63\u56E0\u6B64, \u5C3C\u91C7\u5728\u300A\u5E0C\u814A\u60B2\u5267\u7684\u8BDE\u751F\u300B\u4E2D\u5C06\u82CF\u683C\u62C9\u5E95\u548C\u6B27\u91CC\u5E87\u5F97\u65AF\u4F5C\u4E3A\u5D07\u626C\u7406\u6027\u7684\u963F\u6CE2\u7F57\u7CBE\u795E\u4EE3\u8868\u800C\u5927\u52A0\u62A8\u51FB.</p><p>\u6211\u4EEC\u4EE5\u4E3A\u5728\u300A\u4F0A\u5B89\u7BC7\u300B\u4E2D\u82CF\u683C\u62C9\u5E95\u5C06\u6587\u5B66\u827A\u672F\u540C\u4E00\u822C\u7684\u6280\u827A\u77E5\u8BC6\u533A\u522B\u5F00\u6765, \u6700\u65E9\u8BBA\u8FF0\u4E86\u827A\u672F\u521B\u4F5C\u4E0D\u540C\u4E8E\u4E00\u822C\u7406\u667A\u6D3B\u52A8, \u6307\u51FA\u4E86\u827A\u672F\u4F5C\u54C1\u7684\u60C5\u611F\u6027, \u5F62\u8C61\u6027\u548C\u610F\u5883\u7F8E; \u8FD9\u5BF9\u4E8E\u4FC3\u4F7F\u53E4\u4EE3\u5E0C\u814A\u4EBA\u53BB\u8BA4\u8BC6\u6587\u5B66\u827A\u672F\u7684\u7279\u6B8A\u6027\u662F\u6709\u79EF\u6781\u610F\u4E49\u7684.</p>',10),a=[p];function q(c,r){return o(),e("div",null,a)}var s=t(u,[["render",q],["__file","\u53E4\u5E0C\u814A\u54F2\u5B66\u53F2_\u4F0A\u5B89\u7BC7.html.vue"]]);export{s as default};
