import{_ as t,o,c as u,e as q}from"./app.efeddd24.js";const p={},e=q('<h1 id="\u7231\u4E0E\u6B32-\u300A\u5415\u897F\u65AF\u7BC7\u300B" tabindex="-1"><a class="header-anchor" href="#\u7231\u4E0E\u6B32-\u300A\u5415\u897F\u65AF\u7BC7\u300B" aria-hidden="true">#</a> \u7231\u4E0E\u6B32: \u300A\u5415\u897F\u65AF\u7BC7\u300B</h1><p>\u300A\u5415\u897F\u65AF\u7BC7\u300B\u8BA8\u8BBA\u7684\u4E3B\u9898\u662F &quot;philia&quot;. &quot;philia&quot; \u4E00\u8BCD\u5728\u53E4\u5E0C\u814A\u8BED\u4E2D, \u6709\u60C5\u7231, \u53CB\u7231, \u670B\u53CB\u7B49\u591A\u79CD\u610F\u601D, \u5305\u542B\u4E86\u6211\u4EEC\u73B0\u4EE3\u610F\u4E49\u4E0A\u7684\u7231\u60C5, \u4EB2\u60C5\u548C\u53CB\u60C5\u7684\u610F\u601D. \u5728\u5BF9\u8BDD\u4E2D, \u4E0A\u8FF0\u51E0\u79CD\u610F\u5473\u5747\u6709\u51FA\u73B0. \u5415\u897F\u65AF\u662F\u53C2\u4E0E\u5BF9\u8BDD\u7684\u4EBA\u7269\u4E4B\u4E00. \u800C\u5728\u53E4\u5E0C\u814A\u8BED\u4E2D, \u5415\u897F\u65AF ( lysis ) \u4E00\u8BCD\u53C8\u6709\u89E3\u8131\u8D4E\u7F6A, \u89E3\u653E\u79BB\u5A5A\u7684\u610F\u601D. \u56E0\u6B64, \u8FD9\u662F\u4E00\u7BC7\u9876\u7740\u89E3\u8131\u4E4B\u540D, \u8BA8\u8BBA\u7231\u6B32\u7684\u5BF9\u8BDD.</p><p>\u8FD9\u7BC7\u5BF9\u8BDD\u4EE5\u82CF\u683C\u62C9\u5E95\u7B2C\u4E00\u4EBA\u79F0\u53D9\u8FF0. \u8FD9\u79CD\u53D9\u8FF0\u65B9\u5F0F, \u5728\u67CF\u62C9\u56FE\u7684\u5BF9\u8BDD\u5F55\u91CC\u53EA\u6709\u56DB\u7BC7: \u300A\u7406\u60F3\u56FD\u300B, \u300A\u5361\u5C14\u7C73\u5FB7\u300B,\u300A\u5415\u897F\u65AF\u300B,\u300A\u60C5\u654C\u300B.</p><p>\u5BF9\u8BDD\u59CB\u4E8E\u82CF\u683C\u62C9\u5E95\u4ECE\u963F\u5361\u7C73\u5FB7\u5B66\u56ED\u524D\u5F80\u5415\u514B\u6602, \u8DEF\u9047\u5E0C\u6CE2\u6CF0\u52D2\u548C\u514B\u7279\u897F\u666E\u4E00\u884C\u9752\u5E74. \u4ED6\u4EEC\u53F7\u79F0\u6B63\u5728 &quot;\u6253\u53D1\u65F6\u95F4&quot;. \u5E0C\u6CE2\u6CF0\u52D2\u9080\u8BF7\u82CF\u683C\u62C9\u5E95\u4E0E\u4ED6\u4EEC\u4E00\u8D77\u53BB\u9644\u8FD1\u65B0\u5EFA\u7684\u4F53\u80B2\u573A. \u5E0C\u6CE2\u6CF0\u52D2\u58F0\u79F0\u91CC\u8FB9\u6709\u8BB8\u591A\u6F02\u4EAE\u7684\u4EBA. \u8003\u8651\u5230\u82CF\u683C\u62C9\u5E95\u597D\u8272\u4E4B\u540D, \u5E0C\u6CE2\u6CF0\u52D2\u6B64\u4E3E\u9887\u6709\u4EE5\u8272\u5F15\u8BF1\u7684\u5ACC\u7591.</p><p>\u4F46\u82CF\u683C\u62C9\u5E95\u4E0D\u80AF\u8F7B\u6613\u4E0A\u5F53, \u975E\u8981\u95EE\u6E05\u695A, \u4ED6\u6240\u7684\u6F02\u4EAE\u4E4B\u4EBA\u662F\u8C01. \u5E0C\u6CE2\u6CF0\u52D2\u5374\u5BB3\u7F9E\u8D77\u6765. \u82CF\u683C\u62C9\u5E95\u770B\u51FA\u4ED6\u9677\u5165\u604B\u7231\u4E86. \u514B\u7279\u897F\u666E\u63ED\u5F00\u4E86\u8FF7\u5E95, \u5E0C\u6CE2\u6CF0\u52D2\u7231\u4E0A\u4E86\u4E00\u4F4D\u540D\u4E3A\u5415\u897F\u65AF\u7684\u5C11\u5E74. \u8FD9\u4F4D\u5C11\u5E74\u5BB6\u52BF\u663E\u8D35, \u662F\u5FB7\u8C1F\u514B\u62C9\u7279\u4E4B\u5B50.</p><p>\u8FD9\u91CC\u53C8\u663E\u51FA\u67CF\u62C9\u56FE\u559C\u6B22\u7528\u4EBA\u540D\u73A9\u8BED\u8A00\u6897\u7684\u6076\u8DA3\u5473\u4E86: \u5FB7\u8C1F\u514B\u62C9\u7279 ( Demokratous ) \u662F\u6C11\u4E3B ( Demokratia ) \u4E00\u8BED\u7684\u53D8\u4F53. \u5415\u897F\u65AF\u4E3A\u5FB7\u8C1F\u514B\u62C9\u7279\u4E4B\u5B50, \u4E5F\u5373 &quot;\u89E3\u653E&quot; \u662F &quot;\u6C11\u4E3B&quot; \u4E4B\u5B50.</p><p>\u514B\u7279\u897F\u666E\u5BF9\u5E0C\u6CE2\u6CF0\u52D2\u7684\u604B\u5BB6\u5927\u5BB6\u5632\u8BBD. \u4ED6\u8BF4\u5E0C\u6CE2\u6CF0\u52D2\u4E00\u5929\u5230\u665A\u628A\u5415\u897F\u65AF\u6302\u5728\u5634\u4E0A, \u4F5C\u4E86\u4E0D\u5C11\u8D5E\u7F8E\u5415\u897F\u65AF\u7684\u8BD7\u6B4C; \u6700\u8981\u547D\u7684\u662F, \u4ED6\u8FD8\u7528\u8E69\u811A\u7684\u6280\u5DE7\u5927\u58F0\u8BF5\u8BFB\u5B83\u4EEC. \u81F3\u6B64, \u6211\u4EEC\u5927\u6982\u4E5F\u80FD\u77E5\u9053, \u8FD9\u7FA4\u9752\u5E74\u4E00\u5929\u662F\u9760\u662F\u4EC0\u4E48 &quot;\u6253\u53D1\u65F6\u95F4&quot; \u7684\u4E86.</p><p>\u4F5C\u4E3A &quot;\u597D\u8272\u4E4B\u5F92&quot;, \u82CF\u683C\u62C9\u5E95\u8BA4\u53EF\u4E86\u5E0C\u6CE2\u6CF0\u52D2\u7684\u604B\u7231, \u4F46\u5374\u4E0D\u8BA4\u53EF\u4ED6\u7684\u65B9\u6CD5. \u7ED9\u7231\u4EBA\u5199\u8D5E\u7F8E\u8BD7\u5176\u5B9E\u662F\u5728\u8D5E\u7F8E\u81EA\u5DF1, \u8054\u7CFB\u5230\u540E\u9762\u7684\u730E\u4EBA\u6BD4\u55BB, \u6211\u4EEC\u53EF\u4EE5\u8BF4\u730E\u4EBA\u5938\u8000\u730E\u7269\u7684\u5F3A\u58EE, \u65E0\u8FC7\u662F\u60F3\u8BF4\u81EA\u5DF1\u7684\u5389\u5BB3. \u5C31\u5982\u5927\u68CB\u8BBA\u7684\u4F5C\u8005, \u7F16\u9020\u9650\u7535\u5927\u68CB\u7684\u6DF1\u8C0B\u8FDC\u8651, \u65E0\u8FC7\u662F\u60F3\u5C55\u793A\u81EA\u5DF1\u6709\u591A\u4E48\u4F1A\u6D1E\u5BDF\u9886\u5BFC\u7684\u610F\u56FE. \u4F46\u95EE\u9898\u5728\u4E8E, \u5E0C\u6CE2\u6CF0\u52D2\u73B0\u5728\u5C1A\u672A\u83B7\u5F97\u5415\u897F\u65AF\u7684\u82B3\u5FC3, \u6B64\u65F6\u4F5C\u8BD7\u8D5E\u7F8E, \u53EA\u4F1A\u4F7F\u4ED6\u66F4\u52A0\u9A84\u50B2, \u8FDB\u800C\u4F7F\u81EA\u5DF1\u66F4\u96BE\u5F97\u624B. \u8FD9\u79CD\u884C\u4E3A\u5C31\u5982\u730E\u4EBA\u5728\u83B7\u5F97\u730E\u7269\u4E4B\u524D, \u5C31\u66B4\u9732\u81EA\u5DF1\u60CA\u52A8\u730E\u7269\u4E00\u6837. \u82CF\u683C\u62C9\u5E95\u8BF4, \u5982\u679C\u80FD\u89C1\u5230\u5415\u897F\u65AF, \u4ED6\u53EF\u4EE5\u5411\u5E0C\u6CE2\u6CF0\u52D2\u5C55\u793A\u6B63\u786E\u7684 &quot;\u730E\u7231&quot; \u624B\u6BB5.</p><p>\u5E0C\u6CE2\u6CF0\u52D2\u4E0D\u77E5\u662F\u4F55\u76EE\u7684\u8981\u5F15\u8BF1\u82CF\u683C\u62C9\u5E95\u53BB\u4F53\u80B2\u573A. \u4F46\u6B63\u5982\u82CF\u683C\u62C9\u5E95\u6240\u8BF4, \u4ED6\u5B9E\u5728\u662F\u4E00\u4E2A\u5DEE\u52B2\u7684\u730E\u624B. \u73B0\u5728\u4ED6\u4E0E\u82CF\u683C\u62C9\u5E95\u4F4D\u7F6E\u8C03\u6362, \u6210\u4E86\u88AB &quot;\u730E\u7231\u672F&quot; \u8BF1\u60D1\u7684\u730E\u7269. \u4ED6\u7ACB\u523B\u5BF9\u82CF\u683C\u62C9\u5E95\u8BF4, \u5415\u897F\u65AF\u5C31\u5728\u4F53\u80B2\u573A\u5185, \u800C\u514B\u7279\u897F\u666E\u7684\u4F84\u5B50\u6B63\u662F\u5415\u897F\u65AF\u7684\u597D\u53CB. \u4ECA\u5929\u6B63\u662F\u8D6B\u5C14\u58A8\u65AF\u8282, \u6210\u5E74\u4EBA\u53EF\u4EE5\u8FDB\u5165\u4F53\u80B2\u573A\u5185, \u514B\u7279\u897F\u666E\u53EF\u4EE5\u53EB\u6765\u4ED6\u7684\u4F84\u5B50\u548C\u5415\u897F\u65AF. \u90A3\u65F6\u82CF\u683C\u62C9\u5E95\u5C31\u53EF\u5C55\u793A\u4ED6\u7684 &quot;\u730E\u7231\u672F&quot; \u4E86.</p><p>\u636E\u8BF4\u8D6B\u5C14\u58A8\u65AF\u66FE\u4F7F\u5854\u7EB3\u683C\u62C9\u57CE\u8EB2\u8FC7\u4E00\u573A\u761F\u75AB. \u8D6B\u5C14\u58A8\u65AF\u8282\u5373\u662F\u7EAA\u5FF5\u6B64\u4E8B. \u5728\u8D6B\u5C14\u58A8\u65AF\u8282\u8FD9\u4E00\u5929, \u4E3B\u5974\u5173\u7CFB\u4F1A\u98A0\u5012, \u5974\u96B6\u53EF\u4EE5\u4E3A\u6240\u6B32\u4E3A, \u751A\u81F3\u6253\u9A82\u4E3B\u4EBA; \u800C\u5176\u5B83\u4E0D\u5408\u793C\u6CD5\u7684\u4E8B, \u4E5F\u53EF\u4EE5\u53D1\u751F. \u6BD4\u5982, \u6210\u5E74\u4EBA\u53EF\u4EE5\u8FDB\u5165\u90A3\u4E2A\u65B0\u5EFA\u7684\u4F53\u80B2\u573A. \u8FD9\u4E2A\u4F53\u80B2\u573A\u662F\u5C11\u5E74\u7EC3\u4E60\u6454\u8DE4\u7684\u6240\u5728. \u53E4\u5E0C\u814A\u4EE5\u5341\u56DB\u5C81\u4E3A\u9650, \u8FC7\u4E86\u5341\u56DB\u5C81\u5C31\u7531\u7B97\u9752\u5E74\u4E86. \u8FD9\u662F\u6309\u6027\u542F\u8499\u4E3A\u6807\u51C6\u7684. \u5C11\u5E74\u4EEC\u8D64\u8EAB\u88F8\u4F53\u7EC3\u4E60\u6454\u8DE4\u7684\u5730\u65B9, \u81EA\u7136\u4E0D\u80FD\u8BA9\u5DF2\u6709\u6027\u610F\u8BC6\u7684\u9752\u5E74\u8FDB\u5165, \u4EE5\u907F\u514D\u53D1\u751F\u6027\u9A9A\u6270\u7684\u4E8B\u60C5. \u800C\u8D6B\u5C14\u58A8\u65AF\u8282\u662F\u552F\u4E00\u7684\u4F8B\u5916, \u8FD9\u4E00\u5929\u82CF\u683C\u62C9\u5E95\u8FD9\u4E2A\u597D\u8272\u8001\u5934, \u53EF\u4EE5\u5E26\u7740\u4E00\u7FA4\u7231\u6B32\u52C3\u53D1\u7684\u9752\u5E74\u8FDB\u5165\u8FD9\u91CC. \u60F3\u50CF\u8FD9\u4E2A\u573A\u666F, \u6211\u7A81\u7136\u6709\u70B9\u660E\u767D, \u4E3A\u4EC0\u4E48\u96C5\u5178\u4EBA\u8981\u4EE5\u8D25\u574F\u9752\u5E74\u7684\u7F6A\u540D\u5904\u6B7B\u82CF\u683C\u62C9\u5E95\u4E86.</p><p>\u82CF\u683C\u62C9\u5E95\u4E00\u884C\u4EBA\u8FDB\u5165\u4F53\u80B2\u573A, \u514B\u7279\u897F\u666E\u53EB\u6765\u4ED6\u7684\u4F84\u5B50\u7F8E\u6D85\u514B\u585E\u52AA\u548C\u5415\u897F\u65AF. \u5415\u897F\u65AF\u9762\u5BB9\u59E3\u597D, \u4F53\u5F62\u4FCA\u7F8E, \u751A\u81F3\u8FD8\u6709\u4E9B\u9AD8\u8D35\u7684\u6C14\u8D28. \u82CF\u683C\u62C9\u5E95\u95EE\u7F8E\u6D85\u514B\u585E\u52AA\u548C\u5415\u897F\u65AF\u4E24\u4EBA, \u8C01\u5E74\u9F84\u5927, \u8C01\u66F4\u5584\u826F. \u4E24\u4EBA\u5BF9\u4E9B\u95EE\u9898\u4E89\u6267\u4E0D\u4E0B. \u53C8\u95EE\u8D77\u8C01\u66F4\u66F4\u6F02\u4EAE. \u4E24\u4E2A\u5C0F\u5B69\u7B11\u4E86\u8D77\u6765. \u81F3\u4E8E\u8C01\u66F4\u5BCC\u6709\u7684\u95EE\u9898, \u4E24\u4EBA\u6839\u672C\u4E0D\u60F3\u6BD4\u8F83. \u53EF\u4EE5\u60F3\u89C1, \u8FD9\u662F\u4E24\u4E2A\u65F6\u800C\u5435\u95F9\u76F8\u4E92\u4E0D\u8BA9, \u4F46\u53C8\u53CB\u8C0A\u7EAF\u6D01\u7262\u56FA\u7684\u73A9\u4F34.</p><p>\u8FD9\u65F6\u6709\u4EBA\u5C06\u7F8E\u6D85\u514B\u585E\u52AA\u88AB\u4EBA\u53EB\u8D70. \u8FD9\u662F\u4E0D\u662F\u514B\u7279\u897F\u666E\u7684\u5B89\u6392, \u597D\u4F7F\u82CF\u683C\u62C9\u5E95\u5C55\u793A &quot;\u730E\u7231\u672F&quot; \u6211\u4EEC\u4E0D\u5F97\u800C\u77E5. \u4F46\u6B64\u65F6\u786E\u5B9E\u65F6\u673A\u5927\u597D, \u82CF\u683C\u62C9\u5E95\u5F00\u59CB\u5C55\u793A\u624B\u6BB5\u4E86.</p><p>\u82CF\u683C\u62C9\u5E95\u95EE\u5415\u897F\u65AF\u4ED6\u7684\u7236\u6BCD\u662F\u5426\u7231\u4ED6. \u4ECE\u524D\u8FB9\u6211\u4EEC\u6240\u77E5, \u5415\u897F\u65AF\u6700\u4E3A\u4F18\u8D8A\u4E4B\u5904\u662F\u4ED6\u7684\u5BB6\u52BF. \u800C\u5BF9\u4E8E\u5B69\u5B50, \u7236\u6BCD\u4E4B\u7231\u4E5F\u5374\u5B9E\u662F\u751F\u6D3B\u4E2D\u6700\u4E3A\u91CD\u8981\u7684\u90E8\u5206. \u4ED6\u81EA\u7136\u5982\u6240\u6709\u5B69\u7AE5\u4E00\u822C\u56DE\u7B54\u7236\u6BCD\u7231\u6211. \u82CF\u683C\u62C9\u5E95\u63A5\u7740\u95EE, \u65E2\u7136\u7236\u6BCD\u7231\u4F60, \u4ED6\u4EEC\u4F1A\u7ED9\u4F60\u81EA\u7531, \u8BA9\u4F60\u505A\u81EA\u5DF1\u559C\u7684\u4E8B\u4E48? \u6BD4\u5982\u9A7E\u9A76\u9A6C\u8F66, \u6446\u5F04\u7EC7\u5E03\u673A? \u5415\u897F\u65AF\u56DE\u7B54, \u5F53\u7136\u4E0D\u4F1A, \u4ED6\u4EEC\u53EA\u4F1A\u628A\u8FD9\u4E9B\u4E8B\u52A1\u4EA4\u7ED9\u76F8\u5E94\u7684\u5974\u96B6. \u82CF\u683C\u62C9\u5E95\u8FDB\u800C\u8BF4, \u4E0D\u4F46\u8FD9\u4E9B\u4E8B\u52A1\u4F60\u7684\u7236\u6BCD\u4E0D\u4F1A\u4EA4\u7ED9\u4F60, \u751A\u81F3\u6709\u4E9B\u65F6\u5019, \u8FD8\u4F1A\u628A\u4F60\u4EA4\u7ED9\u5974\u96B6\u7BA1\u675F, \u96BE\u9053\u4F60\u7684\u7236\u6BCD\u4E0D\u7231\u4F60\u4E48? \u82CF\u683C\u62C9\u5E95\u5728\u8FD9\u91CC\u628A\u7231\u4E0E\u652F\u914D\u548C\u81EA\u7531\u7ED3\u5408\u5728\u4E00\u8D77. \u8BBA\u8BC1\u9690\u542B\u7684\u524D\u9898\u662F, \u5982\u679C\u7236\u6BCD\u7231\u4F60, \u5C31\u4F1A\u7ED9\u4F60\u81EA\u5DF1, \u8BA9\u4F60\u7531\u6709\u652F\u914D\u7684\u6743\u529B.</p><p>\u5415\u897F\u65AF\u56DE\u7B54\u8BF4: \u8FD9\u53EF\u80FD\u662F\u56E0\u4E3A\u6211\u8FD8\u672A\u6210\u5E74\u7F62. \u5415\u897F\u65AF\u6B64\u65F6\u8FD8\u505C\u7559\u5728\u4E00\u79CD\u5BB6\u65CF\u7684\u8FF7\u68A6\u4E2D, \u8BA4\u4E3A\u81EA\u7531\u4E0E\u652F\u914D\u662F\u7531\u5E74\u9F84\u51B3\u5B9A, \u53EA\u8981\u5E74\u9F84\u5230\u8FBE, \u81EA\u7136\u53EF\u4EE5\u83B7\u5F97\u8FD9\u4E9B. \u8FD9\u4E5F\u662F\u6240\u6709\u5B69\u7AE5\u7684\u903B\u8F91, \u53EA\u8981\u6B32\u6C42\u5F97\u4E0D\u5230\u6EE1\u8DB3, \u5C31\u4F1A\u8BC9\u8BF8\u5E74\u9F84: \u53EA\u8981\u6211\u957F\u5927, \u5C31\u53EF\u5982\u4F55\u5982\u4F55.....</p><p>\u82CF\u683C\u62C9\u5E95\u5374\u6307\u51FA, \u663E\u7136\u95EE\u9898\u4E0D\u51FA\u5728\u5E74\u9F84\u4E0A: \u4F60\u7684\u7236\u6BCD\u4E0D\u662F\u4E5F\u4F1A\u5728\u67D0\u4E9B\u4E8B\u4E0A\u4EFB\u4F60\u5904\u7F6E\u4E48? \u6BD4\u5982\u5199\u4F5C, \u9605\u8BFB, \u5F39\u7434\u4E4B\u7C7B. \u5415\u897F\u65AF\u604D\u7136, \u8BA9\u4ED6\u505A\u4E8B\u662F\u4ED6\u61C2\u7684, \u800C\u4E0D\u8BA9\u4ED6\u505A\u7684\u4E8B\u662F\u4ED6\u4E0D\u61C2\u7684. \u8FD9\u5C31\u8FDB\u5165\u4E86\u82CF\u683C\u62C9\u5E95\u5177\u6709\u4F18\u52BF\u7684\u6218\u573A: \u77E5\u8BC6. \u82CF\u683C\u62C9\u5E95\u8BF4, \u53EA\u8981\u4F60\u5177\u6709\u77E5\u8BC6, \u4F60\u7684\u7236\u6BCD\u5C31\u4F1A\u628A\u5BB6\u4E2D\u7684\u4E8B\u52A1\u4EA4\u7ED9\u4F60, \u800C\u53EA\u8981\u4F60\u8DB3\u591F\u4F18\u79C0, \u4F60\u7684\u90BB\u5C45, \u751A\u81F3\u6CE2\u65AF\u7684\u5927\u738B\u90FD\u4F1A\u628A\u81EA\u5DF1\u7684\u4E8B\u52A1\u4EA4\u7ED9\u4F60; \u4F46\u5982\u679C\u6CA1\u6709\u77E5\u8BC6, \u8FDE\u4F60\u7684\u7236\u6BCD\u90FD\u4E0D\u4F1A\u7231\u4F60. \u6240\u4EE5\u83B7\u5F97\u77E5\u8BC6, \u5C31\u53EF\u4EE5\u83B7\u5F97\u7231, \u83B7\u5F97\u7231\u5C31\u53EF\u4EE5\u83B7\u5F97\u81EA\u7531\u548C\u652F\u914D\u529B.</p><p>\u82CF\u683C\u62C9\u5E95\u6240\u5EFA\u7ACB\u7684\u903B\u8F91\u94FE\u6761\u5F53\u7136\u662F\u5BF9\u7684, \u9996\u5148, \u7231\u6B32\u786E\u5B9E\u8BC9\u6C42\u5BF9\u5BB6\u65CF\u5173\u7CFB\u7684\u7A81\u7834, \u6700\u660E\u663E\u7684\u4F8B\u5B50\u5373\u662F\u5A5A\u59FB\u5173\u7CFB; \u5176\u6B21, \u4EE5\u7231\u4E4B\u540D\u884C\u652F\u914D\u4E4B\u5B9E\u7684\u4E8B\u5B9E\u4E5F\u4E0D\u5728\u5C11\u6570, \u73B0\u5728\u5E38\u8BF4 PUA \u5373\u662F\u5982\u6B64. \u82CF\u683C\u62C9\u5E95\u6240\u5C55\u793A\u7684 &quot;\u730E\u7231\u672F&quot; \u4E5F\u65E2\u662F\u4E00\u6B21 PUA \u6280\u672F\u7684\u5B9E\u8DF5: \u901A\u8FC7 &quot;\u730E\u7231\u672F&quot; \u7684\u77E5\u8BC6\u53EF\u4EE5\u83B7\u5F97\u5415\u897F\u65AF\u7684\u7231\u6B32, \u8FDB\u800C\u53EF\u4EE5\u83B7\u5F97\u5415\u897F\u65AF\u7684\u670D\u4ECE. \u4F46\u8FD9\u6761\u903B\u8F91\u91CC\u53C8\u523B\u610F\u9690\u85CF\u4E86\u5F88\u591A\u4E8B\u5B9E: \u6BD4\u5982, \u7236\u6BCD\u7684\u9650\u5236\u5F80\u5F80\u4E0D\u662F\u51FA\u4E8E &quot;\u4E0D\u7231&quot;, \u800C\u662F\u51FA\u4E8E\u4FDD\u62A4; \u800C\u613F\u610F\u5C06\u4E8B\u52A1\u4EA4\u7531\u4ED6\u4EBA, \u5F80\u5F80\u4E5F\u4E0D\u662F\u51FA\u4E8E &quot;\u7231&quot;, \u800C\u662F\u51FA\u4E8E\u5BF9\u5229\u5BB3\u7684\u7B97\u8BA1. \u4F46\u5BF9\u4E8E\u4E00\u4E2A\u4E0D\u6EE1\u5341\u56DB\u5B69\u5B50\u6765\u8BF4, \u8FD9\u6761\u903B\u8F91\u94FE\u6761\u5DF2\u7ECF\u8DB3\u591F\u628A\u4ED6\u4ECE\u5BB6\u65CF\u7684\u8FF7\u68A6\u62C9\u5165\u5230\u77E5\u8BC6\u7684\u8FF7\u68A6\u4E2D.</p><p>\u5F97\u901E\u4E4B\u540E\u7684\u82CF\u683C\u62C9\u5E95\u770B\u5411\u5E0C\u6CE2\u6CF0\u52D2, \u60F3\u5411\u4ED6\u8BF4: \u4F60\u770B, \u8FD9\u624D\u662F\u6B63\u786E\u7684 &quot;\u730E\u7231&quot; \u65B9\u6CD5. \u4F46\u4ED6\u770B\u5230\u7684\u5374\u662F\u5E0C\u6CE2\u6CF0\u52D2\u8EB2\u5728\u4EBA\u7FA4\u4E2D\u5F20\u7687\u5931\u63AA. \u5E0C\u6CE2\u6CF0\u52D2\u53EA\u662F\u5E0C\u671B\u8EB2\u5728\u4EBA\u7FA4\u4E2D\u5077\u770B\u81EA\u5DF1\u7684\u7231\u6B32\u5BF9\u50CF, \u800C\u4E0D\u6562\u88AB\u4ED6\u7684\u7231\u6B32\u5BF9\u50CF\u770B\u5230. \u4EE5\u8D5E\u7F8E\u7684\u65B9\u5F0F\u5B9E\u73B0\u7231\u6B32\u662F\u4E0D\u9700\u8981\u52C7\u6C14\u7684, \u4F46\u8981\u901A\u8FC7\u5F71\u54CD\u5B9E\u73B0\u7231\u6B32\u5374\u9700\u8981\u52C7\u6C14. \u4F46\u8BDA\u5982\u4E4B\u524D\u82CF\u683C\u62C9\u5E95\u6240\u8BF4\u7684, \u8D5E\u7F8E\u53EA\u80FD\u5728\u5B9E\u73B0\u7231\u6B32\u4E4B\u540E\u53BB\u505A, \u800C\u6CA1\u6709\u5B9E\u73B0\u7231\u6B32\u7684\u80FD\u529B. \u95EE\u9898\u5728\u4E8E, \u4E3A\u4EC0\u4E48\u7231\u6B32\u52C3\u53D1\u7684\u5E0C\u6CE2\u6CF0\u52D2\u4F1A\u6EE1\u8DB3\u4E8E\u901A\u8FC7\u8D5E\u7F8E\u5B9E\u73B0\u7231\u6B32. \u5927\u6982\u56E0\u4E3A, \u901A\u8FC7\u8FD9\u79CD\u65B9\u5F0F\u5B9E\u73B0\u7231\u6B32, \u53EA\u662F\u8FD9\u7FA4\u9752\u5E74 &quot;\u6253\u53D1\u65F6\u95F4&quot; \u7684\u65B9\u5F0F. \u5E0C\u6CE2\u6CF0\u52D2\u53EA\u662F\u88AB\u7231\u6B32\u6240\u9F13\u52A8. \u6216\u8005\u4E5F\u53EF\u4EE5\u8FD9\u6837\u8BF4: \u5E0C\u6CE2\u6CF0\u52D2\u53EA\u6709\u6D88\u8D39\u4ED6\u4ECE\u5415\u897F\u65AF\u8EAB\u4E0A\u611F\u5230\u7684\u7231\u6B32\u7684\u51B2\u52A8.</p><p>\u8FD9\u65F6, \u7F8E\u6D85\u514B\u585E\u52AA\u56DE\u6765\u4E86. \u5415\u897F\u65AF\u6084\u6084\u5730\u8BA9\u82CF\u683C\u62C9\u5E95\u628A\u521A\u624D\u7684\u8BDD\u5411\u7F8E\u6D85\u514B\u585E\u52AA\u91CD\u590D\u4E00\u904D. \u8FD9\u53EF\u80FD\u662F\u5415\u897F\u65AF\u611F\u53D7\u5230\u82CF\u683C\u62C9\u5E95\u8BBA\u8BC1\u7684\u4E50\u8DA3, \u50CF\u662F\u60F3\u8981\u5206\u4EAB\u597D\u7535\u5F71\u4E00\u6837, \u5411\u81EA\u5DF1\u7684\u597D\u53CB\u5206\u4EAB\u8FD9\u7F8E\u5999\u7684\u8BBA\u8BC1. \u867D\u7136\u82CF\u683C\u62C9\u5E95\u7684 &quot;\u730E\u7231&quot; \u662F\u4E3A\u4E86\u4FD8\u83B7\u5415\u897F\u65AF, \u5415\u897F\u65AF\u5374\u4ECE\u4E2D\u611F\u5230\u4E86\u4E50\u8DA3. \u8FD9\u662F\u56E0\u4E3A\u7231\u6B32\u7684\u751F\u53D1\u672C\u8EAB\u5C31\u5305\u542B\u7740\u5BF9\u652F\u914D\u7684\u627F\u8BA4. \u300A\u963F\u7518\u6B63\u4F20\u300B\u4E2D\u4E39\u4E2D\u5C09\u56E0\u4E3A\u5728\u6218\u4E89\u4E2D\u6B8B\u75BE\u800C\u52A8\u63FA\u4E86\u5BF9\u4E0A\u5E1D\u7684\u4FE1\u5FC3, \u540E\u6765\u4ED6\u4E0E\u963F\u7518\u4E00\u8D77\u505A\u6355\u867E\u7684\u751F\u610F, \u5728\u6355\u867E\u65F6\u9047\u5230\u98CE\u66B4; \u98CE\u66B4\u4E2D\u4ED6\u60F3\u50CF\u81EA\u5DF1\u4E8E\u4E0A\u5E1D\u640F\u6597, \u800C\u98CE\u66B4\u505C\u606F\u540E, \u4ED6\u5374\u4E0D\u4F46\u6062\u590D\u4E86\u4FE1\u4EF0, \u800C\u4E14\u8FD8\u66F4\u575A\u5B9A\u4E86. \u4E0E\u652F\u914D\u8005\u640F\u6597\u800C\u7EC8\u4E8E\u627F\u8BA4\u652F\u914D, \u662F\u53EF\u4EE5\u4EA7\u751F\u5F3A\u5217\u7231\u6B32\u7684. \u6240\u4EE5\u604B\u7231\u7684\u8FC7\u7A0B\u5E38\u88AB\u6BD4\u4F5C\u72E9\u730E.</p><p>\u82CF\u683C\u62C9\u5E95\u5374\u4E0D\u613F\u5411\u7F8E\u6D85\u514B\u585E\u52AA\u91CD\u590D\u8BBA\u8BC1. \u4ED6\u8BA9\u5415\u897F\u65AF\u4EE5\u540E\u6309\u81EA\u5DF1\u7684\u56DE\u5FC6\u91CD\u590D\u8FD9\u4E9B\u8BBA\u8BC1. \u514B\u7279\u897F\u666E\u770B\u5230\u82CF\u683C\u62C9\u5E95\u548C\u5415\u897F\u65AF\u4EA4\u5934\u63A5\u8033\u7684\u8BF4\u6084\u6084\u8BDD, \u5C31\u51FA\u58F0\u8BE2\u95EE. \u82CF\u683C\u62C9\u5E95\u8BF4\u4ED6\u5728\u53CB\u7231\u95EE\u9898\u4E0A\u6709\u4E9B\u7591\u95EE, \u6B63\u60F3\u8BF7\u6559\u7F8E\u6D85\u514B\u585E\u52AA. \u4E8E\u662F\u82CF\u683C\u62C9\u5E95\u5BF9\u8BDD\u7684\u5BF9\u8C61\u8F6C\u5230\u4E86\u7F8E\u6D85\u514B\u585E\u52AA, \u8BDD\u9898\u4E5F\u4ECE &quot;\u730E\u7231\u672F&quot; &quot;\u89E3\u8131&quot; \u51FA\u6765.</p><p>\u82CF\u683C\u62C9\u5E95\u8BF4\u4ED6\u4E00\u751F\u552F\u6C42\u627E\u5230\u4E00\u4E2A\u597D\u670B\u53CB. \u4F46\u5173\u4E8E\u53CB\u7231\u5982\u4F55\u4EA7\u751F\u4ED6\u5374\u6709\u7591\u60D1: \u5047\u82E5\u5F20\u4E09\u8FFD\u6C42\u674E\u56DB, \u4E24\u4EBA\u7EC8\u6210\u670B\u53CB, \u90A3\u662F\u5F20\u4E09\u6210\u4E86\u674E\u56DB\u7684\u670B\u53CB, \u8FD8\u662F\u674E\u56DB\u6210\u4E86\u5F20\u4E09\u7684\u670B\u53CB? \u53C8\u6216\u90FD\u662F\u8FD9\u4E24\u79CD\u8BF4\u6CD5\u662F\u4E00\u56DE\u4E8B ( \u4ED6\u4EEC\u6210\u4E86\u5BF9\u65B9\u7684\u670B\u53CB )? \u7F8E\u6D85\u514B\u585E\u52AA\u56DE\u7B54, \u4E24\u79CD\u8BF4\u6CD5\u662F\u4E00\u56DE\u4E8B. \u82CF\u683C\u62C9\u5E95\u8BF4, \u600E\u4E48\u4F1A\u8FD9\u6837\u5462? \u4E00\u4E2A\u4EBA\u6709\u7231\u6B32\u5374\u4F7F\u4E24\u4EBA\u6210\u4E86\u670B\u53CB ( \u4E24\u4EBA\u83B7\u5F97\u53CB\u7231 ). \u5047\u82E5\u5F20\u4E09\u4E0E\u674E\u56DB\u672C\u6765\u662F\u654C\u4EBA, \u96BE\u9053\u5C31\u56E0\u4E3A\u5F20\u4E09\u5BF9\u674E\u56DB\u6709\u7231\u6B32, \u4E24\u4EBA\u5C31\u53D8\u53CB\u4E3A\u654C\u4E86? \u751A\u81F3\u4E8E\u56DB\u674E\u56E0\u5F20\u4E09\u7684\u8FFD\u6C42\u800C\u6068\u4E0A\u4E86\u5F20\u4E09, \u54EA\u6015\u5F20\u4E09\u8BF4 &quot;\u6211\u7231\u4F60\u4E0E\u4F60\u4F55\u5E72&quot;, \u6211\u4EEC\u4E5F\u4E0D\u80FD\u7167\u5F20\u4E09\u7684\u4E00\u53A2\u60C5\u613F\u5C31\u8BF4\u8FD9\u4E24\u4EBA\u6210\u4E86\u670B\u53CB.</p><p>\u8FD9\u662F\u4ECE &quot;\u730E\u7231&quot; \u7684\u89C6\u89D2 &quot;\u89E3\u8131&quot; \u51FA\u6765, \u5FC5\u7136\u4EA7\u751F\u7684\u7591\u95EE. &quot;\u730E\u7231&quot; \u662F\u4EE5\u8FFD\u6C42\u7684\u624B\u6BB5\u5B9E\u73B0\u7231\u6B32\u7684\u65B9\u6CD5, \u4F46\u662F\u8FFD\u6C42\u7684\u624B\u6BB5\u662F\u7231\u6B32\u4EA7\u751F\u7684\u539F\u56E0\u4E48? \u6211\u4EE5\u524D\u5E38\u4EE5\u4E3A, \u7231\u4E0E\u610F\u5FD7\u76F8\u5173. \u4F46\u4EA7\u751F\u7231\u7684, \u662F\u7231\u7684\u610F\u5FD7\u4E48?</p><p>\u4F3C\u4E4E\u53EA\u6709\u83B7\u5F97\u56DE\u5E94\u7684\u8FFD\u6C42\u624D\u4EA7\u751F\u53CB\u7231; \u5355\u5411\u5EA6\u7684\u7231\u6B32, \u5E76\u4E0D\u4EA7\u751F\u53CB\u7231. \u4F46\u82CF\u683C\u62C9\u5E95\u53C8\u8BF4, \u4EBA\u7231\u9A6C, \u9E1F, \u72D7, \u4F53\u80B2\u548C\u667A\u6167, \u8FD9\u4E9B\u975E\u4EBA\u4E4B\u7269\u4E0D\u4F1A\u56DE\u5E94\u7231, \u96BE\u9053\u56E0\u6B64, \u5B83\u4EEC\u5C31\u4E0D\u80FD\u6210\u4E3A\u4EBA\u7684\u670B\u53CB\u4E48? \u90A3\u8BD7\u4E2D\u4E3A\u4EC0\u4E48\u53C8\u8BF4: &quot;\u53EF\u7231\u7684\u9A6C\u4E0E\u72D7.&quot; \u82CF\u683C\u62C9\u5E95\u5728\u8FD9\u91CC\u662F\u628A\u53EF\u7231\u7684 ( te philia ) \u5F53\u4F5C &quot;\u53CB\u7231&quot; \u7684. \u5F53\u7136\u8FD9\u4E2A\u662F\u53E4\u5E0C\u814A\u7684\u8BED\u8A00\u6897. \u4F46\u8054\u7CFB\u751F\u6D3B\u7ECF\u9A8C, \u53CB\u5584\u4E4B\u7269\u96BE\u9053\u4E0D\u90FD\u770B\u7740\u53EF\u7231\u4E48? \u6768\u8FC7\u89C9\u5F97\u5C0F\u9F99\u5973\u751F\u6C14\u53EF\u7231, \u4E0D\u6B63\u662F\u56E0\u4E3A\u5C0F\u9F99\u5973\u7684\u751F\u6C14\u9971\u542B\u7740\u7231\u6B32\u4E48? \u5426\u5219\u8D75\u5FD7\u656C\u4E5F\u751F\u6C14, \u4ED6\u600E\u4E48\u4E0D\u89C9\u5F97\u53EF\u7231\u5462?</p><p>\u4E9A\u91CC\u58EB\u591A\u5FB7\u5728\u300A\u5C3C\u5404\u9A6C\u53EF\u4F26\u7406\u5B66\u300B\u91CC\u6279\u5224\u300A\u5415\u897F\u65AF\u7BC7\u300B\u8FD9\u6BB5: \u53EA\u6709\u4EBA\u4E0E\u4EBA\u624D\u8C08\u5F97\u4E0A\u670B\u53CB, \u4EBA\u518D\u7231\u975E\u4EBA\u4E4B\u7269, \u4E5F\u8C08\u4E0D\u4E0A\u4E0E\u4E4B\u6210\u4E3A\u670B\u53CB. \u4E8B\u5B9E\u5C42\u9762\u4E0A\u4E9A\u91CC\u58EB\u591A\u5FB7\u5F53\u7136\u8BF4\u7684\u662F\u5BF9\u7684. \u4F46\u67CF\u62C9\u56FE\u8FD9\u91CC\u8BB2\u7684\u662F\u4E00\u79CD\u60F3\u8C61\u7684\u53CB\u7231, \u4EBA\u4E0E\u5BA0\u7269, \u4EBA\u4E0E\u4F53\u80B2\u667A\u6167\u7B49\u62BD\u8C61\u4E4B\u7269, \u4EE5\u53CA\u5927\u68CB\u8BBA\u7684\u4F5C\u8005\u603B\u5728\u8111\u4E2D\u60F3\u8C61\u51FA\u4E00\u79CD\u4E0E\u4ED6\u4EA7\u751F\u4EB2\u5BC6\u5173\u7CFB\u7684\u4EBA\u683C\u4F53.</p><p>\u53E6\u5916\u5355\u5411\u5EA6\u7684\u7231\u8FD8\u6709\u4EB2\u60C5, \u7236\u6BCD\u7231\u5B50\u5973, \u5374\u6709\u53EF\u80FD\u56E0\u6B64\u62DB\u81F3\u5B50\u5973\u7684\u6068\u610F. \u4F46\u8FD9\u5E76\u9632\u788D\u7236\u6BCD\u603B\u662F\u5B50\u5973\u7684\u670B\u53CB. \u5728\u4E0D\u8FDC\u7684\u524D\u8FB9, \u82CF\u683C\u62C9\u5E95\u521A\u8BF4\u8FC7, \u7236\u6BCD\u53EF\u80FD\u56E0\u4E3A\u5B50\u5973\u7684\u65E0\u77E5\u800C\u4E0D\u7231\u4ED6\u4EEC, \u8FD9\u91CC\u53C8\u53CD\u8FC7\u6765\u8BF4, \u7236\u6BCD\u603B\u662F\u5B50\u5973\u7684\u670B\u53CB. \u8FD9\u662F\u56E0\u4E3A\u4ED6\u4EEC\u5DF2\u4ECE &quot;\u730E\u7231&quot; \u7684\u4E3B\u9898\u4E2D &quot;\u89E3\u8131&quot; \u51FA\u6765, \u56E0\u6B64\u88AB\u9690\u85CF\u7684, \u5C31\u663E\u793A\u4E86\u51FA\u6765.</p><p>\u81F3\u6B64, \u7531\u610F\u5FD7\u6765\u8BA8\u8BBA\u7231\u6B32\u7684\u53D1\u751F\u5B8C\u5168\u5931\u8D25: \u6211\u4EEC\u65E2\u4E0D\u80FD\u8BF4\u6709\u7231\u7684\u610F\u5FD7\u5C31\u80FD\u4EA7\u751F\u53CB\u7231, \u4E5F\u4E0D\u80FD\u8BF4\u552F\u6709\u56DE\u5E94\u7231\u7684\u610F\u5FD7\u624D\u80FD\u4EA7\u751F\u53CB\u7231. \u82CF\u683C\u62C9\u5E95\u611F\u6982\u9053, \u6211\u4EEC\u4E00\u5B9A\u6709\u54EA\u91CC\u5F04\u9519\u4E86.</p><p>\u8FD9\u65F6\u5415\u897F\u65AF\u7A81\u7136\u63D2\u5634\u9053, \u662F\u7684, \u6211\u4EEC\u4E00\u5B9A\u5728\u54EA\u91CC\u5F04\u9519\u4E86! \u82CF\u683C\u62C9\u5E95\u770B\u51FA\u4ED6\u8138\u7EA2\u4E86. \u8FD9\u53EF\u80FD\u662F\u56E0\u4E3A\u63D2\u5634\u800C\u5BB3\u7F9E, \u4E5F\u53EF\u80FD\u662F\u56E0\u4E3A\u542C\u5F97\u592A\u4E13\u6CE8\u800C\u6FC0\u52A8. \u4E0E\u662F\u82CF\u683C\u62C9\u5E95\u5C06\u8C08\u8BDD\u7684\u5BF9\u8C61\u53C8\u8F6C\u5411\u5415\u897F\u65AF.</p><p>\u65E2\u7136\u53CB\u7231\u7684\u8BBA\u8BC1\u4ECE &quot;\u4EBA\u7684\u610F\u5FD7&quot; \u8FD9\u9762\u8D70\u4E0D\u901A, \u90A3\u4E48\u6765\u8BD5\u8BD5 &quot;\u81EA\u7136\u7684\u751F\u6210&quot; \u5427. \u82CF\u683C\u62C9\u5E95\u5F15\u7528\u8BD7\u4EBA\u7684\u8BDD, \u63D0\u51FA &quot;\u540C\u7C7B\u76F8\u805A&quot; \u7684\u89C2\u70B9. \u8FD9\u4E2A\u89C2\u70B9\u4E5F\u662F\u4E3B\u5F20\u5143\u7D20\u8BF4\u7684\u81EA\u7136\u54F2\u5B66\u5BB6\u6069\u57F9\u591A\u514B\u52D2\u7684\u89C2\u70B9. \u90A3\u4E48\u662F\u4E0D\u662F\u5177\u6709\u76F8\u540C\u6027\u8D28\u7684\u4EBA\u4F1A\u6210\u4E3A\u670B\u53CB?</p><p>\u82CF\u683C\u62C9\u5E95\u8BF4, \u65E0\u8BBA\u5982\u4F55, &quot;\u540C\u7C7B\u76F8\u805A&quot; \u4E0D\u4F1A\u6307\u5F97\u662F\u574F\u4EBA\u548C\u574F\u4EBA\u6210\u4E3A\u670B\u53CB. \u56E0\u4E3A\u574F\u4EBA\u4E4B\u95F4\u76F8\u4E92\u635F\u5BB3, \u56E0\u6B64\u574F\u4EBA\u4E4B\u95F4\u4E0D\u4F1A\u6709\u53CB\u8C0A\u5B58\u5728. \u90A3\u4E48\u597D\u4EBA\u4E4B\u95F4\u5462? \u597D\u4EBA\u5DF2\u7ECF\u662F\u4E00\u5207\u90FD\u662F\u597D\u7684\u4E86, \u4E00\u4E2A\u4E00\u5207\u90FD\u662F\u597D\u7684\u4EBA, \u4E0D\u4F1A\u5173\u6CE8\u4EFB\u4F55\u4E8B, \u800C\u670B\u53CB\u4E4B\u95F4\u9700\u8981\u76F8\u4E92\u5173\u6CE8, \u56E0\u6B64\u597D\u4EBA\u4E5F\u4E0D\u4F1A\u548C\u597D\u4EBA\u662F\u670B\u53CB.</p><p>\u5BF9\u4E8E\u4E00\u4E2A\u73B0\u4EE3\u4EBA\u6765\u8BF4, \u8FD9\u662F\u4E00\u4E2A\u975E\u5E38\u53EF\u7591\u7684\u8BBA\u8BC1. \u524D\u8FB9\u8BB2\u7684\u574F\u4EBA, \u662F\u76F8\u4E92\u4E3A\u5BB3\u7684\u4EBA. \u5982\u679C\u597D\u4EBA\u4E0E\u574F\u4EBA\u505A\u5BF9\u5B50, \u96BE\u9053\u4E0D\u5E94\u8BE5\u662F\u76F8\u4E92\u5E2E\u52A9\u7684\u4EBA\u4E48? \u600E\u4E48\u7A81\u7136\u53D8\u6210\u4E86 &quot;\u4E00\u5207\u90FD\u662F\u597D\u7684\u4EBA&quot; \u4E86? \u4E3A\u4E86\u4F7F\u8FD9\u4E2A\u8BBA\u8BC1\u6210\u7ACB, \u6211\u4EEC\u4F3C\u4E4E\u5E94\u8BE5\u628A\u597D\u4EBA\u7406\u89E3\u4E3A\u7CBE\u795E\u5065\u5168\u5B8C\u5907\u4E4B\u4EBA, \u574F\u4EBA\u7406\u89E3\u4E3A\u7CBE\u795E\u6B8B\u7F3A\u7834\u635F\u4E4B\u4EBA. \u6211\u4EEC\u5E38\u542C\u8BF4, \u5C11\u63A5\u89E6\u7CBE\u795E\u6B8B\u7F3A\u7834\u635F\u7684\u4EBA, \u4ED6\u4EEC\u7684\u7262\u9A9A\u62A5\u6028\u4F1A\u6210\u4E3A\u62D6\u7D2F\u4F60\u7684\u8D1F\u80FD\u91CF; \u800C\u4E00\u4E2A\u7CBE\u795E\u5065\u5168\u5B8C\u5907\u7684\u4F3C\u4E4E\u4E5F\u786E\u5B9E\u5E94\u4EE5\u4E0D\u7ED9\u4ED6\u4EBA\u6DFB\u9EBB\u70E6\u4E3A\u5DF2\u4EFB. \u4EE5\u6B64\u4E3A\u7406\u60F3, \u8981\u6C42\u4EBA\u4EEC\u76F8\u4E92\u5173\u6CE8\u7684\u7231\u6B32\u5F53\u7136\u6210\u4E86\u9EBB\u70E6\u7684\u7D2F\u8D58.</p><p>\u53CB\u7231\u4E0D\u662F\u56E0\u4E3A &quot;\u540C\u7C7B\u76F8\u805A&quot;, \u90A3\u4F1A\u4E0D\u4F1A\u662F\u56E0\u4E3A &quot;\u5F02\u7C7B\u76F8\u6C42&quot; \u5462? \u82CF\u683C\u62C9\u5E95\u5F15\u7528\u8D6B\u897F\u4FC4\u5FB7\u7684\u300A\u5DE5\u4F5C\u4E0E\u65F6\u65E5\u300B: &quot;\u9676\u5DE5\u4E0E\u9676\u5DE5\u7ADE\u4E89, \u5320\u4EBA\u4E0E\u5320\u4EBA\u7ADE\u4E89; \u4E5E\u4E10\u5AC9\u5992\u4E5E\u4E10, \u8BD7\u4EBA\u5AC9\u5992\u8BD7\u4EBA.&quot; \u800C\u81EA\u7136\u754C\u4E2D, \u51B7\u8981\u70ED, \u5E72\u8981\u6E7F, \u865A\u8981\u76C8, \u76C8\u8981\u865A. \u90A3\u4E48\u53CB\u7231\u662F\u4E0D\u662F\u4E5F\u662F\u5982\u6B64, \u4EA7\u751F\u4E8E\u4EBA\u7684\u76F8\u5F02\u5462? \u597D\u8FA9\u7684\u7F8E\u6D85\u514B\u585E\u52AA\u542C\u5230\u8FD9\u91CC, \u7ACB\u523B\u6765\u4E86\u7CBE\u795E, \u8BF4, \u8FD9\u542C\u4E0A\u53BB\u76F8\u5F53\u6B63\u786E. \u800C\u82CF\u683C\u62C9\u5E95\u5374\u8BF4, \u5982\u679C\u8FD9\u65F6\u6709\u4E00\u4E2A\u597D\u8FA9\u7684\u4EBA\u6765\u95EE\u4F60, \u670B\u53CB\u4E0E\u654C\u4EBA\u76F8\u5F02, \u90A3\u670B\u53CB\u662F\u5426\u80FD\u548C\u654C\u4EBA\u4EA7\u751F\u53CB\u7231? \u7F8E\u6D85\u514B\u585E\u52AA\u53EA\u80FD\u627F\u8BA4, \u8FD9\u662F\u4E0D\u53EF\u80FD\u7684.</p><p>\u7528 &quot;\u4EBA\u7684\u610F\u5FD7&quot; \u89E3\u91CA\u4E00\u5207, \u662F\u667A\u672F\u5E08 &quot;\u4EBA\u662F\u4E07\u7269\u5C3A\u5EA6&quot; \u7684\u4E3B\u5F20, &quot;\u540C\u7C7B\u76F8\u805A&quot; \u548C &quot;\u5F02\u7C7B\u76F8\u6C42&quot; \u662F\u81EA\u7136\u54F2\u5B66\u751F\u6210\u8BBA\u7684\u4E24\u7C7B\u89C2\u70B9. \u81F3\u6B64, \u82CF\u683C\u62C9\u5E95\u5DF2\u7A77\u5C3D\u4E86\u5F53\u65F6\u6240\u6709\u77E5\u8BC6\u6846\u67B6. \u5728 &quot;\u730E\u7231\u672F&quot; \u4E2D\u4E3A\u5415\u897F\u65AF\u5851\u9020\u4E00\u4E2A\u77E5\u8BC6\u8FF7\u68A6\u7684\u82CF\u683C\u62C9\u5E95, \u53C8\u4EB2\u624B\u6253\u788E\u4E86\u5B83.</p><p>\u5728\u904D\u5386\u4E86\u6240\u6709\u77E5\u8BC6\u6846\u67B6\u540E, \u82CF\u683C\u62C9\u5E95\u63D0\u51FA\u4E86\u81EA\u5DF1\u7684\u770B\u6CD5. \u65E2\u7136\u597D\u7684\u4E8B\u7269\u4E4B\u95F4\u4E0D\u4F1A\u4EA7\u751F\u53CB\u7231, \u574F\u7684\u4E8B\u7269\u4E4B\u95F4\u4E5F\u4E0D\u4F1A\u4EA7\u751F\u53CB\u7231, \u597D\u7684\u4E8B\u7269\u548C\u574F\u7684\u4E8B\u7269\u4E4B\u95F4\u4E5F\u4E0D\u4F1A\u4EA7\u751F\u53CB\u7231, \u90A3\u53CB\u7231\u4E00\u5B9A\u53D1\u751F\u4E0D\u597D\u4E0D\u574F\u7684\u4E8B\u7269\u4E0A. \u4EC0\u4E48\u662F\u4E0D\u597D\u4E0D\u574F\u7684\u4E8B\u7269\u5462? \u6BD4\u5982, \u5065\u5EB7\u662F\u597D\u7684, \u75BE\u75C5\u662F\u574F\u7684, \u8EAB\u4F53\u662F\u4E0D\u597D\u4E0D\u574F\u7684. \u8EAB\u4F53\u53D7\u4E86\u75BE\u75C5\u7684\u574F\u5F71\u54CD, \u800C\u6B32\u6C42\u5065\u5EB7, \u56E0\u6B64\u548C\u533B\u672F\u6210\u4E86\u670B\u53CB. \u8FD9\u91CC\u4E0D\u597D\u4E0D\u574F\u4E4B\u7269\u53EA\u80FD\u662F\u53D7\u4E86\u574F\u5F71\u54CD, \u800C\u4E0D\u80FD\u662F\u53D8\u574F. \u90A3 &quot;\u574F\u5F71\u54CD&quot; \u548C &quot;\u53D8\u574F&quot; \u7684\u533A\u522B\u53C8\u5728\u54EA\u5462? \u82CF\u683C\u62C9\u5E95\u4E3E\u4F8B\u8BF4: \u91D1\u53D1\u53EF\u4EE5\u56E0\u4E3A\u62BA\u7C89\u800C\u663E\u767D, \u4E5F\u53EF\u4EE5\u56E0\u4E3A\u5E74\u8001\u800C\u663E\u767D, \u62BA\u7C89\u663E\u767D\u662F\u53D7\u4E86\u767D\u8272\u7684\u5F71\u54CD, \u5E74\u8001\u663E\u767D\u662F\u771F\u7684\u53D8\u767D\u4E86. \u667A\u6167\u4E0E\u611A\u8822\u4E5F\u662F\u5982\u6B64 -- \u4E86\u89E3\u82CF\u683C\u62C9\u5E95\u540C\u5B66\u4E86, \u4ED6\u90FD\u8FD9\u6837\u62AC\u5C3B\u5B50\u4E86, \u6211\u4EEC\u8FD8\u731C\u4E0D\u51FA\u4ED6\u8981\u653E\u4EC0\u4E48\u5C41\u4E48 -- \u667A\u6167\u662F\u597D\u7684, \u611A\u8822\u662F\u574F\u7684; \u771F\u6B63\u667A\u6167\u7684\u4EBA\u4E0D\u4F1A\u8FFD\u6C42\u77E5\u8BC6, \u56E0\u4E3A\u4ED6\u5DF2\u7ECF\u667A\u6167\u4E86; \u611A\u8822\u7684\u4EBA\u4E86\u4E0D\u4F1A\u8FFD\u6C42\u77E5\u8BC6, \u56E0\u4E3A\u4ED6\u4E0D\u6B32\u6C42\u667A\u6167; \u53EA\u6709\u81EA\u77E5\u65E0\u77E5\u7684\u4EBA, \u5BB3\u6015\u53D7\u5230\u611A\u8822\u7684\u5F71\u54CD, \u624D\u4F1A\u548C\u667A\u6167\u6210\u4E3A\u670B\u53CB ( \u7231\u667A\u6167, \u4E5F\u5373\u54F2\u5B66 ).</p><p>\u4E9A\u91CC\u58EB\u591A\u5FB7\u8BC4\u8BBA\u82CF\u683C\u62C9\u5E95\u6B64\u5916\u8BBA\u53CB\u7231\u592A\u8FC7\u529F\u5229. \u4E8B\u5B9E\u4E0A\u4EE5\u8FD9\u4E2A\u7ED3\u6784\u6765\u770B\u5F85\u7231\u6B32\u7684\u53D1\u751F\u786E\u5B9E\u975E\u5E38\u5177\u6709\u6548\u529B. \u6BD4\u5982, \u7F8E\u5143\u9738\u6743\u662F\u574F\u7684, \u4E2D\u534E\u6C11\u65CF\u590D\u5174\u662F\u597D\u7684, \u6211\u4EEC\u7684\u826F\u597D\u751F\u6D3B\u56E0\u4E3A\u53D7\u5230\u7F8E\u5143\u9738\u6743\u7684\u574F\u5F71\u54CD, \u5C31\u4F1A\u6B32\u6C42\u4E2D\u534E\u6C11\u65CF\u590D\u5174, \u8FDB\u800C\u4EA7\u751F\u51FA\u6F8E\u52C3\u7684\u7231\u6B32. \u6B64\u65F6, \u542C\u5230\u8FD9\u7FFB\u8BBA\u8BC1\u7684\u4E24\u4E2A\u5C0F\u5B69\u4E5F\u5FC3\u60A6\u81E3\u670D\u4E86. \u4ED6\u4FE9\u540C\u65F6\u53EB\u8D77\u6765, \u8FD9\u4E2A\u8BBA\u8BC1\u592A\u7CBE\u5F69\u4E86. \u82CF\u683C\u62C9\u5E95\u8BF4, \u4ED6\u4E5F\u5174\u9AD8\u5F69\u70C8, \u5C31\u5982\u72E9\u730E\u5DF2\u4E45\u7684\u730E\u4EBA\u7EC8\u4E8E\u6355\u83B7\u4E86\u730E\u7269.</p><p>\u4F46\u5F88\u5FEB\u4ED6\u5C31\u5BF9\u81EA\u5DF1\u7684\u8BBA\u8BC1\u4EA7\u751F\u4E86\u6000\u7591. \u4ED6\u62C5\u5FC3\u4ED6\u4EEC\u53EA\u662F\u5728\u68A6\u4E2D\u53D1\u73B0\u4E86\u5B9D\u85CF. \u5584\u9020\u8FF7\u68A6\u7684\u4EBA, \u6C38\u8FDC\u6000\u7591\u81EA\u5DF1\u8EAB\u5904\u8FF7\u68A6\u4E2D.</p><p>\u9996\u5148\u662F &quot;\u7B2C\u4E00\u7231\u4EBA&quot; \u7684\u95EE\u9898. \u6839\u636E\u4EE5\u4E0A\u7684\u8BBA\u8BC1, \u6211\u4EEC\u77E5\u9053\u53CB\u7231\u7684\u4EA7\u751F\u662F\u6709\u624B\u6BB5\u548C\u76EE\u7684. \u6BD4\u5982\u8EAB\u4F53\u4EE5\u533B\u672F\u4E3A\u53CB\u8FFD\u6C42\u5065\u5EB7, \u5C31\u662F\u4EE5\u5065\u5EB7\u4E3A\u76EE\u7684\u800C\u4EE5\u533B\u672F\u4E3A\u624B\u6BB5. \u624B\u6BB5\u56E0\u76EE\u7684\u800C\u6210\u4E3A\u670B\u53CB. \u90A3\u4E48\u662F\u5426\u6709\u6BD4\u5065\u5EB7\u66F4\u9AD8\u7684\u76EE\u7684, \u4F7F\u5065\u5EB7\u6210\u4E3A\u5176\u624B\u6BB5, \u8FDB\u800C\u4F7F\u5176\u6210\u4E3A\u8EAB\u4F53\u7684\u670B\u53CB\u5462? \u6BD4\u5982\u751F\u5B58. \u90A3\u4E48\u662F\u5426\u6709\u6BD4\u751F\u5B58\u66F4\u9AD8\u7684\u76EE\u7684, \u4F7F\u751F\u547D\u6210\u4E3A\u624B\u6BB5, \u8FDB\u800C\u4F7F\u5176\u6210\u4E3A\u8EAB\u4F53\u7684\u670B\u53CB\u5462? \u6BD4\u5982\u7E41\u884D. \u6211\u4EEC\u5927\u53EF\u8FD9\u6837\u4E00\u76F4\u8FFD\u95EE, \u771F\u5230\u8FFD\u95EE\u51FA\u4E00\u4E2A\u7EC8\u7EA7\u76EE\u7684, \u800C\u8FBE\u5230\u8FD9\u4E2A\u7EC8\u7EA7\u76EE\u7684\u624B\u6BB5\u5C31\u53EF\u4EE5\u6210\u4E3A &quot;\u7B2C\u4E00\u7231\u4EBA&quot;. \u4E00\u4F46\u627E\u5230 &quot;\u7B2C\u4E00\u7231\u4EBA&quot;, \u6211\u4EEC\u5C31\u53EF\u5177\u6B64\u63A8\u51FA\u4E00\u7CFB\u5217\u7684\u670B\u53CB.</p><p>\u82CF\u683C\u62C9\u5E95\u4E3E\u4E86\u4E00\u4E2A\u4F8B\u5B50: \u5047\u5982\u4E00\u4E2A\u4EBA\u7684\u513F\u5B50\u559D\u4E86\u6BD2\u82B9\u6C41, \u800C\u4ED6\u4EE5\u4E3A\u9152\u53EF\u89E3\u6BD2, \u90A3\u4ED6\u5C82\u4E0D\u4F1A\u5C06\u9152\u4F5C\u4E3A\u6700\u597D\u7684\u670B\u53CB; \u4EE5\u9152\u4E3A\u53CB, \u5C82\u4E0D\u4F1A\u4EE5\u88C5\u9152\u7684\u5668\u76BF\u4E3A\u53CB. \u5668\u76BF\u56E0\u9152\u800C\u4E3A\u53CB, \u9152\u56E0\u513F\u5B50\u800C\u4E3A\u53CB. \u4F3C\u4E4E\u7231\u6B32\u7684\u53D1\u751F\u662F\u56E0\u4E3A\u90A3\u4E2A &quot;\u7B2C\u4E00\u7231\u4EBA&quot;. \u4F46\u5728\u8FD9\u4E2A\u4F8B\u5B50\u4E2D, \u9690\u9690\u7684\u6307\u51FA, \u77E5\u8BC6\u51B3\u5B9A\u4E86\u7231\u6B32\u53D1\u751F\u7684\u5BF9\u8C61, \u9152\u6210\u4E3A\u670B\u53CB, \u662F\u56E0\u4E3A &quot;\u9152\u53EF\u89E3\u6BD2&quot; \u7684\u77E5\u8BC6. \u5982\u679C\u7406\u89E3\u7684\u77E5\u8BC6\u6709\u6240\u4E0D\u540C, \u53CB\u7231\u751F\u53D1\u7684\u8FC7\u7A0B\u5C31\u4F1A\u66F4\u6539.</p><p>\u5982\u679C\u6211\u4EEC\u518D\u60F3\u7684\u8FDC\u70B9, \u82CF\u683C\u62C9\u5E95\u5373\u662F\u559D\u6BD2\u82B9\u6C41\u800C\u6B7B. \u5BF9\u4E8E\u57CE\u90A6\u6765\u8BF4, \u6700\u9AD8\u7684\u4EF7\u503C\u662F\u751F\u5B58. \u800C\u4FDD\u62A4\u5C11\u5E74\u662F\u57CE\u90A6\u751F\u5B58\u7684\u6761\u4EF6, \u6240\u4EE5\u57CE\u90A6\u7684\u793C\u6CD5\u4E0D\u8BB8\u6210\u5E74\u4EBA\u8FDB\u5165\u5C11\u5E74\u6454\u8DE4\u7684\u4F53\u80B2\u573A; \u800C\u54F2\u5B66\u7684\u6700\u9AD8\u4EF7\u503C\u662F\u6C42\u77E5, \u82CF\u683C\u62C9\u5E95\u4EE5\u6C42\u77E5\u7684\u76EE\u7684\u7834\u574F\u793C\u6CD5\u5E26\u7740\u7231\u6B32\u52C3\u53D1\u7684\u9752\u5E74\u8FDB\u5165\u4E86\u793C\u6CD5\u4E0D\u5141\u8BB8\u5176\u8FDB\u5165\u7684\u4F53\u80B2\u573A. \u56E0\u6B64\u5F53\u57CE\u90A6\u7684 &quot;\u7B2C\u4E00\u7231\u4EBA&quot; \u4E0E\u54F2\u5B66\u7684 &quot;\u7B2C\u4E00\u7231\u4EBA&quot; \u51B2\u7A81\u7684\u65F6\u5019, \u57CE\u90A6\u5C31\u5904\u6B7B\u4E86\u82CF\u683C\u62C9\u5E95. &quot;\u7B2C\u4E00\u7231\u4EBA&quot; \u4E4B\u4E89\u662F\u65E0\u6CD5\u901A\u8FC7\u7406\u6027\u89E3\u51B3\u7684\u95EE\u9898. \u5B58\u5728\u4E4B\u4E89\u662F\u6CF0\u5766\u4E4B\u4E89; &quot;\u7B2C\u4E00\u7231\u4EBA&quot; \u4E4B\u4E89\u5373\u662F &quot;\u5B58\u5728\u4E4B\u4E89&quot;.</p><p>\u63A5\u7740, \u82CF\u683C\u62C9\u5E95\u53C8\u63D0\u51FA\u7B2C\u4E8C\u4E2A\u53EF\u7591\u4E4B\u5904. \u5728\u4E0A\u8FF0\u7684\u53CB\u7231\u7ED3\u6784\u4E2D, \u5BF9\u5584\u597D\u4E4B\u7231\u6E90\u4E8E\u574F, \u90A3\u4E48\u5982\u679C\u574F\u706D\u7EDD\u4E86, \u662F\u4E0D\u662F\u53CB\u7231\u4E5F\u5C31\u706D\u7EDD\u4E86? \u8003\u8651\u574F\u7684\u706D\u7EDD\u5E76\u4E0D\u662F\u94BB\u725B\u89D2\u5C16, \u5982\u679C\u6211\u4EEC\u4EE5\u8FFD\u6C42\u81F3\u5584\u4E3A\u76EE\u6807, \u4E5F\u5373\u662F\u8FFD\u6C42\u574F\u7684\u706D\u7EDD; \u5982\u679C\u574F\u7684\u706D\u7EDD\u5C31\u662F\u7231\u7684\u706D\u7EDD, \u90A3\u4E48\u6211\u4EEC\u8FFD\u6C42\u7684\u76EE\u6807\u5C82\u4E0D\u662F\u4E5F\u662F\u7231\u7684\u706D\u7EDD.</p><p>\u4E3A\u4E86\u5E94\u5BF9\u8FD9\u4E2A\u96BE\u9898, \u82CF\u683C\u62C9\u5E95\u63D0\u51FA\u7528\u6B32\u671B\u6765\u66FF\u4EE3\u574F. \u6BD4\u5982\u9965\u6E34, \u9965\u6E34\u4F7F\u6211\u4EEC\u6B32\u6C42\u996E\u98DF, \u4F46\u9965\u6E34\u5E76\u4E0D\u603B\u662F\u574F\u7684. \u81F3\u5C11\u4E0D\u4F1A\u6709\u4EFB\u4F55\u8FFD\u6C42\u81F3\u5584\u7684\u4EBA\u6216\u57CE\u90A6\u60F3\u8981\u706D\u7EDD\u9965\u6E34. \u5982\u679C\u6211\u4EEC\u7528\u6B32\u671B\u6765\u8003\u5BDF\u53CB\u7231, \u5C31\u4F1A\u53D1\u73B0, \u6B32\u671B\u4EA7\u751F\u53CB\u7231\u7684\u539F\u56E0\u662F\u7F3A\u4E4F. \u82CF\u683C\u62C9\u5E95\u7528\u4E00\u79CD\u8BD7\u610F\u7684\u8BED\u8A00\u8BF4: \u5982\u679C\u4E24\u4E2A\u4EBA\u6210\u4E3A\u670B\u53CB, \u90A3\u4E00\u5B9A\u662F\u4E24\u4E2A\u4EBA\u8EAB\u4E0A\u6709\u5C5E\u4E8E\u5BF9\u65B9\u7684\u4E1C\u897F ( \u4E5F\u65E2\u662F\u5BF9\u65B9\u6240\u7F3A\u4E4F\u4E4B\u7269 ).</p><p>\u4F46\u4EE5\u6B32\u671B\u653E\u5165\u53CB\u7231\u7684\u7ED3\u6784\u4E2D\u53C8\u4F1A\u4EA7\u751F\u4E00\u4E2A\u96BE\u9898, \u5728\u53CB\u7231\u7684\u7ED3\u6784\u4E2D, \u8FFD\u6C42\u53CB\u7231\u7684\u4E00\u65B9\u662F\u4E0D\u597D\u4E0D\u574F\u7684, \u6BD4\u5982\u5728\u5065\u5EB7, \u75BE\u75C5, \u8EAB\u4F53\u7684\u7ED3\u6784\u4E2D, \u8EAB\u4F53\u662F\u4E0D\u597D\u4E0D\u574F\u7684. \u800C\u6B32\u671B\u672C\u8EAB\u4E5F\u662F\u4E0D\u597D\u4E0D\u574F\u7684. \u4E5F\u5C31\u662F\u8BF4, \u6B32\u671B\u548C\u8FFD\u6C42\u53CB\u7231\u8005\u662F\u540C\u7C7B\u4E8B\u7269. \u4F46\u540C\u7C7B\u4E8B\u7269\u662F\u4E0D\u4F1A\u4F7F\u540C\u7C7B\u4E8B\u7269\u4EA7\u751F\u53CB\u7231\u7684, \u8FD9\u662F\u524D\u8FB9\u5DF2\u7136\u8BA8\u8BBA\u51FA\u7684\u7ED3\u679C.</p><p>\u8FD9\u4E2A\u96BE\u9898\u96BE\u4F4F\u4E86\u6240\u6709\u7684\u4EBA. \u82CF\u683C\u62C9\u5E95\u63D0\u610F\u518D\u53BB\u627E\u4E9B\u6210\u5E74\u4EBA\u6765\u7EE7\u7EED\u8BA8\u8BBA. \u4F46\u76D1\u62A4\u4E24\u4E2A\u5C11\u5E74\u5974\u96B6\u4EEC\u5374\u7A81\u7136\u51FA\u73B0, \u6293\u4F4F\u4E24\u4EBA\u7684\u624B\u8981\u5E26\u4ED6\u4EEC\u56DE\u5BB6. \u56E0\u4E3A\u6B64\u65F6\u5929\u8272\u5DF2\u665A. \u56F4\u7ED5\u5728\u6B64\u5904\u7684\u9752\u5E74\u5C11\u5E74\u4EEC\u60F3\u8981\u963B\u6B62\u8FD9\u4E9B\u5974\u96B6. \u4F46\u5974\u96B6\u4EEC\u5DF2\u7ECF\u559D\u9189, \u8BF4\u7740\u7C97\u4FD7\u7684\u5E0C\u814A\u8BED, \u5F88\u96BE\u8FDB\u884C\u6C9F\u901A. \u5BF9\u8BDD\u5C31\u5728\u8FD9\u6DF7\u4E71\u7684\u573A\u666F\u4E2D\u8FDB\u5165\u4E86\u5C3E\u58F0. \u82CF\u683C\u62C9\u5E95\u5728\u4E24\u4E2A\u5C11\u5E74\u5C06\u8981\u79BB\u5F00\u65F6, \u5411\u4E24\u4E2A\u5C11\u5E74\u558A\u9053: \u6211\u4EEC\u5C06\u6210\u4E3A\u7B11\u67C4, \u56E0\u4E3A\u4ECA\u5929\u6211\u4EEC\u867D\u6210\u4E3A\u4E86\u670B\u53CB, \u5374\u4F9D\u7136\u8FDE\u670B\u53CB\u662F\u4EC0\u4E48\u4E5F\u4E0D\u77E5\u9053.</p><p>\u5728\u4E0E\u5415\u897F\u65AF\u5206\u522B\u4E4B\u65F6, \u82CF\u683C\u62C9\u5E95\u518D\u6B21\u6253\u7834\u4ED6\u5728 &quot;\u730E\u7231\u672F&quot; \u4E2D\u4E3A\u5415\u897F\u65AF\u5851\u9020\u7684\u77E5\u8BC6\u8FF7\u68A6. \u7F3A\u5C11\u670B\u53CB\u7684\u77E5\u8BC6, \u5E76\u4E0D\u9632\u788D\u4ED6\u4EEC\u6210\u4E3A\u670B\u53CB. \u8FD9\u5927\u6982\u4E5F\u662F\u5E94 &quot;\u89E3\u8131&quot; \u4E4B\u540D\u5427. &quot;\u730E\u7231\u672F&quot; \u662F\u7528\u77E5\u8BC6\u8FF7\u68A6\u89E3\u8131\u5BB6\u5EAD\u7684\u8FF7\u68A6, \u8BA8\u8BBA\u53CB\u7231\u5982\u4F55\u4EA7\u751F\u662F\u5BF9\u730E\u7231\u672F\u7684\u89E3\u8131, \u82CF\u683C\u62C9\u5E95\u7684\u53CB\u7231\u7ED3\u6784\u662F\u5BF9\u53E4\u5E0C\u814A\u54F2\u5B66\u77E5\u8BC6\u6846\u67B6\u89E3\u8131, \u6700\u540E\u4E09\u4E2A\u8BBA\u8BC1 -- &quot;\u7B2C\u4E00\u7231\u4EBA&quot;, &quot;\u574F\u7684\u706D\u7EDD&quot;, &quot;\u6B32\u671B\u751F\u7231&quot; -- \u662F\u5BF9\u53CB\u7231\u7ED3\u6784\u7684\u89E3\u8131.</p><p>\u9664\u6B64\u4E4B\u5916, \u7ED3\u5C3E\u4E2D\u503C\u5F97\u4E00\u8BF4\u7684\u662F &quot;\u9189\u9152\u7684\u5974\u96B6&quot;. \u5974\u96B6\u672C\u662F\u4E00\u65E0\u6240\u6709\u7684\u4EBA. \u9189\u9152\u7684\u8FF7\u72C2\u5374\u7ED9\u5974\u96B6\u4E00\u79CD\u5B9E\u73B0\u7231\u6B32\u7684\u5E7B\u89C9, \u5C31\u5982\u8D6B\u5C14\u58A8\u65AF\u8282\u7ED9\u4EC0\u4E48\u4E5F\u652F\u914D\u4E0D\u4E86\u7684\u5974\u96B6\u4E00\u79CD\u53EF\u4EE5\u652F\u914D\u4E3B\u4EBA\u7684\u5E7B\u89C9\u4E00\u6837. \u66F4\u6709\u8DA3\u7684\u662F, \u62E5\u6709\u7231\u6B32\u5E7B\u89C9\u7684\u5974\u96B6\u5374\u80DC\u8FC7\u4E86\u5BF9\u77E5\u8BC6\u4EA7\u751F\u771F\u5B9E\u7231\u6B32\u9752\u5E74. \u6240\u4EE5\u6D41\u8A00\u6B62\u4E8E\u667A\u8005, \u53EA\u662F\u6709\u77E5\u8BC6\u7684\u4EBA\u7684\u4E00\u53A2\u60C5\u613F; \u6253\u7834\u5927\u68CB\u8BBA\u7684\u4E0D\u662F\u4EC0\u4E48\u9053\u7406, \u800C\u662F\u7231\u6B32\u5E7B\u89C9\u7684\u7834\u706D.</p>',43),_=[e];function a(i,r){return o(),u("div",null,_)}var s=t(p,[["render",a],["__file","\u5415\u897F\u65AF\u7BC7_\u7231\u6B32\u4F55\u4EE5\u53EF\u80FD.html.vue"]]);export{s as default};
