import{_ as a,o as e,c as h,e as r}from"./app.efeddd24.js";const d={},t=r('<h1 id="\u5415\u897F\u65AF\u7BC7\u8BF4\u4E86\u5565" tabindex="-1"><a class="header-anchor" href="#\u5415\u897F\u65AF\u7BC7\u8BF4\u4E86\u5565" aria-hidden="true">#</a> \u5415\u897F\u65AF\u7BC7\u8BF4\u4E86\u5565</h1><h2 id="\u8BA8\u8BBA\u7684\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#\u8BA8\u8BBA\u7684\u80CC\u666F" aria-hidden="true">#</a> \u8BA8\u8BBA\u7684\u80CC\u666F</h2><h3 id="\u5E0C\u6CE2\u6CF0\u52D2\u7684\u9519\u8BEF\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u5E0C\u6CE2\u6CF0\u52D2\u7684\u9519\u8BEF\u65B9\u5F0F" aria-hidden="true">#</a> \u5E0C\u6CE2\u6CF0\u52D2\u7684\u9519\u8BEF\u65B9\u5F0F</h3><p>\u82CF\u683C\u62C9\u5E95\u4ECE\u5B66\u56ED\u53BB\u5415\u514B\u6602\u7684\u8DEF\u4E0A\uFF0C\u78B0\u5230\u4E86\u5E0C\u6CE2\u6CF0\u52D2\u548C\u514B\u7279\u897F\u666E\u3002\u4ED6\u4EEC\u9080\u8BF7\u82CF\u683C\u62C9\u5E95\u53BB\u65B0\u5EFA\u7684\u4F53\u80B2\u573A\u8C08\u5929\u3002\u82CF\u683C\u62C9\u5E95\u53D1\u73B0\u5E0C\u6CE2\u514B\u6CF0\u52D2\u6B63\u5728\u604B\u7231\u4E2D\u3002\u4ED6\u7231\u4E0A\u4E86\u4E00\u4E2A\u53EB\u5415\u897F\u65AF\u7684\u9752\u5E74\u3002\u514B\u7279\u897F\u666E\u8BF4\u5E0C\u6CE2\u6CF0\u7528\u4EE5\u8868\u8FBE\u7231\u610F\u7684\u65B9\u5F0F\u5C31\u662F\u901A\u8FC7\u5199\u548C\u5531\u8D5E\u7F8E\u5415\u897F\u65AF\u7684\u9882\u6B4C\u3002</p><p>\u82CF\u683C\u62C9\u5E95\u8BA4\u4E3A\u5E0C\u6CE2\u6CF0\u52D2\u8FD9\u6837\u505A\u53EA\u662F\u5728\u8D5E\u7F8E\u81EA\u5DF1\u3002\u56E0\u4E3A\u4E00\u4F46\u5415\u897F\u65AF\u56DE\u5E94\u4ED6\u7684\u7231\u610F\uFF0C\u90A3\u4E48\u8FD9\u4E9B\u8D5E\u7F8E\u90FD\u53EF\u4EE5\u5F52\u5230\u4ED6\u81EA\u5DF1\u8EAB\u4E0A\u3002\u4F46\u82CF\u683C\u62C9\u5E95\u4E5F\u8BA4\u4E3A\u8FD9\u662F\u4E00\u79CD\u4E0D\u660E\u667A\u7684\u505A\u6CD5\uFF0C\u56E0\u4E3A\uFF1A</p><p>1 \u5982\u679C\u5415\u897F\u65AF\u4E0D\u56DE\u5E94\u4ED6\u7684\u7231\u610F\uFF0C\u4ED6\u73B0\u5728\u8D5E\u7F8E\u8D8A\u70ED\u5217\uFF0C\u4EE5\u540E\u5931\u53BB\u7684\u5E78\u798F\u5C31\u8D8A\u5927 2 \u8FD9\u6837\u70ED\u5230\u7684\u8D5E\u7F8E\u4F1A\u4F7F\u5415\u897F\u65AF\u53D8\u5F97\u9A84\u50B2\u3002\u800C\u5415\u897F\u65AF\u8D8A\u9A84\u50B2\uFF0C\u8FFD\u6C42\u8D77\u6765\u5C31\u8D8A\u56F0\u96BE\u3002</p><p>\u5E0C\u6CE2\u6CF0\u52D2\u540C\u610F\u82CF\u683C\u62C9\u5E95\u7684\u8BF4\u6CD5\u3002\u5E76\u5E0C\u671B\u82CF\u683C\u62C9\u5E95\u53EF\u4EE5\u63D0\u4F9B\u5EFA\u8BAE\u3002\u82CF\u683C\u62C9\u5E95\u8BF4\uFF0C\uFF0C\u5982\u679C\u4ED6\u80FD\u548C\u5415\u897F\u65AF\u8C08\u8BDD\uFF0C\u4ED6\u53EF\u4EE5\u4E3A\u5E0C\u6CE2\u6CF0\u52D2\u505A\u51FA\u793A\u8303\u3002</p><p>\u4E0E\u662F\u82CF\u683C\u62C9\u5E95\u4E0E\u4F17\u4EBA\u8D70\u8FDB\u5E7F\u573A\u3002\u5415\u897F\u65AF\u8DDF\u968F\u7F8E\u6D85\u514B\u585E\u52AA\u6765\u5230\u82CF\u683C\u62C9\u5E95\u8EAB\u8FB9\u3002</p><h3 id="\u77E5\u8BC6\u4E0E\u7231" tabindex="-1"><a class="header-anchor" href="#\u77E5\u8BC6\u4E0E\u7231" aria-hidden="true">#</a> \u77E5\u8BC6\u4E0E\u7231</h3><p>\u4E8E\u662F\u82CF\u683C\u62C9\u5E95\u95EE\u4E24\u4EBA\u5E74\u9F84\uFF0C\u5BB9\u8C8C\uFF0C\u8D22\u5BCC\u548C\u5FB7\u6027\u65B9\u9762\u7684\u95EE\u9898\u3002\u6B63\u5728\u8FD9\u65F6\u5E7F\u573A\u7684\u4E3B\u4EBA\u53EB\u8D70\u4E86\u7F8E\u6D85\u514B\u585E\u52AA\u3002\u8FD9\u65F6\u82CF\u683C\u62C9\u5E95\u5F00\u59CB\u4E86\u4E0E\u5415\u897F\u65AF\u7684\u8C08\u8BDD\u3002</p><p>\u82CF\u683C\u62C9\u5E95\u5148\u4E0E\u5415\u897F\u65AF\u8C08\u8BBA\u4E86\u77E5\u8BC6\u4E0E\u53CB\u8C0A\u7684\u5173\u7CFB\uFF1A\u5982\u679C\u4E00\u4E2A\u4EBA\u83B7\u5F97\u77E5\u8BC6\uFF0C\u5C31\u53EF\u4EE5\u83B7\u5F97\u522B\u4EBA\u7684\u53CB\u8C0A\uFF0C\u5426\u5219\u8FDE\u4ED6\u7684\u5BB6\u4EBA\u4E5F\u4E0D\u4F1A\u6210\u4E3A\u4ED6\u7684\u670B\u53CB\uFF1A</p><p>1 \u7236\u6BCD\u867D\u7231\u4ED6\u5374\u4E0D\u4F1A\u8BA9\u4ED6\u4EFB\u6027\u800C\u4E3A\uFF0C\u5982\uFF1A 1.1 \u4E0D\u8BA9\u4ED6\u67B6\u8F66\u800C\u8BA9\u8F66\u592B\u67B6\u8F66 1.2 \u4E0D\u8BA9\u4ED6\u97AD\u6253\u9AA1\u5B50\u5374\u8BA9\u7BA1\u7272\u53E3\u7684\u5974\u96B6\u97AD\u6253 1.3 \u8BA9\u5974\u96B6\u7BA1\u6559\u4ED6\u53BB\u4E0A\u5B66 1.4 \u4E0D\u8BA9\u4ED6\u4E71\u52A8\u7EC7\u673A\u5DE5\u5177 2 \u4F46\u5BF9\u4E8E\u67D0\u4E9B\u4E8B\uFF0C\u5374\u4E0D\u7B49\u4ED6\u6210\u5E74\u5C31\u4F1A\u8BA9\u4ED6\u968F\u610F\u53BB\u505A, \u5982: 2.1 \u8BFB\u4E66 2.2 \u5199\u4F5C 2.3 \u5F39\u7434 3 \u7531\u6B64\u53EF\u89C1,\u53EA\u8981\u662F\u4ED6\u61C2\u7684\u4E8B\u60C5,\u7236\u6BCD\u5C31\u4F1A\u4EA4\u7ED9\u4ED6\u7531\u4ED6\u5904\u7F6E. 4 \u53EA\u8981\u5BF9\u76F8\u5173\u4E8B\u52A1\u8DB3\u591F\u806A\u660E,\u8FDE\u5916\u4EBA\u4E5F\u4F1A\u5C06\u4E8B\u52A1\u4EA4\u7531\u4ED6\u505A\uFF0C\u5982 4.1 \u5982\u679C\u6211\u4EEC\u6BD4\u90BB\u5C45\u5728\u7406\u8D22\u4E0A\u66F4\u806A\u660E\uFF0C\u90BB\u5C45\u4F1A\u628A\u8D22\u4EA7\u4EA7\u7ED9\u6211\u4EEC\u5904\u7406 4.2 \u5982\u679C\u6211\u4EEC\u5728\u505A\u83DC\u4E0A\u66F4\u62FF\u624B\uFF0C\u56FD\u738B\u4F1A\u628A\u505A\u83DC\u7684\u4E8B\u4EA4\u6258\u7ED9\u6211\u4EEC\uFF0C\u800C\u4E0D\u662F\u81EA\u5DF1\u7684\u738B\u5B50 4.3 \u5982\u679C\u56FD\u738B\u513F\u5B50\u60A3\u6709\u773C\u75BE\uFF0C\u800C\u6211\u4EEC\u61C2\u533B\u5B66\uFF0C\u4ED6\u4F1A\u8BA9\u6211\u4EEC\u5904\u7406\u4ED6\u513F\u5B50\u7684\u773C\u775B\uFF0C\u800C\u4E0D\u662F\u8BA9\u4ED6\u81EA\u5DF1\u5904\u7406\u4ED6\u7684\u513F\u5B50</p><h2 id="\u5173\u4E8E\u53CB\u8C0A\u7684\u8BBA\u8BC1" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u53CB\u8C0A\u7684\u8BBA\u8BC1" aria-hidden="true">#</a> \u5173\u4E8E\u53CB\u8C0A\u7684\u8BBA\u8BC1</h2><h3 id="\u4ECE\u7231\u8005\u4E0E\u88AB\u7231\u8005\u7684\u89D2\u8BA8\u8BBA\u53CB\u8C0A" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u7231\u8005\u4E0E\u88AB\u7231\u8005\u7684\u89D2\u8BA8\u8BBA\u53CB\u8C0A" aria-hidden="true">#</a> \u4ECE\u7231\u8005\u4E0E\u88AB\u7231\u8005\u7684\u89D2\u8BA8\u8BBA\u53CB\u8C0A</h3><p>\u8FD9\u65F6\u5019\u7F8E\u6D85\u514B\u585E\u52AA\u56DE\u6765\u4E86. \u4E8E\u662F\u4E24\u4EBA\u5F00\u59CB\u8BA8\u5173\u4E8E\u53CB\u8C0A\u7684\u95EE\u9898\u3002</p><p>\u82CF\u683C\u62C9\u5E95\u9996\u5148\u8BD5\u7740\u4ECE\u7231\u8005\u548C\u88AB\u7231\u8005\u7684\u89D2\u5EA6\u8C08\u8BBA\u53CB\u8C0A\u3002\u4ED6\u63D0\u51FA\u8FD9\u6837\u7684\u95EE\u9898\uFF1A</p><blockquote><p>\u4E00\u4E2A\u4EBA\u4E00\u65E6\u7231\u4E0A\u53E6\u4E00\u4E2A\u4EBA\uFF0C\u90A3\u4E48\u4ED6\u4EEC\u4E2D\u95F4\u54EA\u4E00\u4E2A\u6210\u4E3A\u670B\u53CB\uFF1F\u662F\u90A3\u4F4D\u88AB\u7231\u8005\u6210\u4E3A\u7231\u8005\u7684\u670B\u53CB\uFF0C\u8FD8\u662F\u7231\u8005\u6210\u4E3A\u88AB\u7231\u8005\u7684\u670B\u53CB\uFF1F\u6216\u8005\u8BF4\u4E24\u79CD\u8BF4\u6CD5\u662F\u4E00\u56DE\u4E8B</p></blockquote><p>\u4F46\u6700\u7EC8\u53D1\u73B0\uFF0C\u4ECE\u8FD9\u4E2A\u89D2\u5EA6\u65E0\u6CD5\u627E\u5230\u53CB\u8C0A\u662F\u4EC0\u4E48\u3002</p><p>1 \u4E24\u79CD\u8BF4\u6CD5\u662F\u4E00\u56DE\u4E8B\uFF08\u53EA\u8981\u6709\u4E00\u4E2A\u7231\u8005\uFF0C\u7231\u8005\u548C\u88AB\u7231\u8005\u90FD\u4F1A\u6210\u4E3A\u670B\u53CB\uFF09\u3002\u56E0\u4E3A\u6709\u53EF\u80FD\u88AB\u7231\u8005\u4E0D\u7231\u7231\u8005\u3002 2 \u53EA\u6709\u4E24\u4EBA\u5F7C\u6B64\u76F8\u7231\u624D\u662F\u670B\u53CB\u3002\u800C\u7231\u8005\u5F97\u4E0D\u5230\u88AB\u7231\u8005\u7684\u56DE\u5E94\u5C31\u4E0D\u80FD\u6210\u4E3A\u670B\u53CB\u3002 3 \u7531\u6B64\u53EF\u89C1\u88AB\u7231\u7684\u5BF9\u8C61\u4E0D\u662F\u670B\u53CB\uFF0C\u53EA\u6709\u7231\u8005\u624D\u662F\u670B\u53CB 4 \u540C\u7406\u6068\u8005\u624D\u662F\u654C\u4EBA\u3002 5 \u4F46\u5B58\u5728\u7231\u8005\u6240\u7231\u4E4B\u4EBA\u6068\u7231\u8005\uFF0C\u6068\u8005\u6240\u6068\u4E4B\u4EBA\u7231\u6068\u8005\u7684\u60C5\u51B5\u5B58\u5728 6 \u5982\u6B64\u5C31\u4F1A\u51FA\u73B0\u6709\u4EBA\u662F\u670B\u53CB\u7684\u654C\u4EBA\uFF0C\u6216\u6709\u4EBA\u662F\u654C\u4EBA\u6709\u670B\u53CB\u3002 7 \u800C\u8FD9\u662F\u4E0D\u53EF\u80FD\u7684\u3002\u56E0\u6B64\u4E0D\u80FD\u4ECE\u7231\u8005\u548C\u88AB\u7231\u8005\u7684\u89D2\u5EA6\u6765\u8BA8\u8BBA\u53CB\u8C0A\u3002</p><h3 id="\u4ECE-\u540C\u7C7B\u76F8\u805A-\u8BA8\u8BBA\u53CB\u8C0A" tabindex="-1"><a class="header-anchor" href="#\u4ECE-\u540C\u7C7B\u76F8\u805A-\u8BA8\u8BBA\u53CB\u8C0A" aria-hidden="true">#</a> \u4ECE &quot;\u540C\u7C7B\u76F8\u805A&quot; \u8BA8\u8BBA\u53CB\u8C0A</h3><p>\u8FD9\u65F6\u5415\u897F\u65AF\u63D2\u5165\u8BA8\u8BBA\u3002\u4E0E\u662F\u82CF\u683C\u62C9\u5E95\u8F6C\u5411\u5415\u897F\u65AF\u8BA8\u8BBA\u53CB\u8C0A\u3002\u8FD9\u6B21\u8BA8\u8BBA\u7684\u51FA\u53D1\u70B9\u662F &quot;\u540C\u7C7B\u76F8\u805A&quot;. \u4F46\u8FD9\u6B21\u4F9D\u7136\u6CA1\u6709\u901A\u8FC7 &quot;\u540C\u7C7B\u76F8\u805A&quot; \u4E2D\u627E\u5230\u53CB\u8C0A\u662F\u4EC0\u4E48\u3002</p><p>1 \u574F\u4EBA\u4E4B\u95F4\u4E0D\u4F1A\u6210\u4E3A\u670B\u53CB, \u56E0\u4E3A 1.1 \u574F\u4EBA\u4E4B\u95F4\u76F8\u4E92\u4F24\u5BB3 1.2 \u574F\u4EBA\u4E0D\u65AD\u53D8\u5316\uFF0C\u4ED6\u4EEC\u751A\u81F3\u4E0E\u81EA\u5DF1\u4E0D\u540C 2 \u5982\u6B64\u8BF4\u6765, \u53EA\u6709\u597D\u4EBA\u53EF\u4EE5\u6210\u4E3A\u670B\u53CB. \u4F46\u8FD9\u4E5F\u4E0D\u53EF\u80FD, \u56E0\u4E3A 2.1 \u4EBA\u4EEC\u76F8\u4E92\u53CB\u597D\u4E0D\u662F\u56E0\u4E3A\u76F8\u540C\uFF0C\u800C\u662F\u56E0\u4E3A\u76F8\u4E92\u4E4B\u95F4\u662F\u6709\u7528\u7684\uFF0C\u6709\u7528\u4F7F\u4ED6\u4EEC\u76F8\u4E92\u5173\u6CE8, \u56E0\u6B64\u4EA7\u751F\u53CB\u8C0A 2.2 \u597D\u4EBA\u4EC0\u4E48\u90FD\u4E0D\u7F3A\uFF0C\u56E0\u6B64\u4ED6\u4EEC\u4EC0\u4E48\u90FD\u4E0D\u5173\u6CE8\uFF0C\u4E0D\u5173\u6CE8\u5C31\u4E0D\u559C\u7231\uFF0C\u4E0D\u559C\u7231\u5C31\u65E0\u4ECE\u4F1A\u6709\u53CB\u8C0A</p><h3 id="\u4ECE-\u5F02\u7C7B\u76F8\u5438-\u8BA8\u8BBA\u53CB\u8C0A" tabindex="-1"><a class="header-anchor" href="#\u4ECE-\u5F02\u7C7B\u76F8\u5438-\u8BA8\u8BBA\u53CB\u8C0A" aria-hidden="true">#</a> \u4ECE &quot;\u5F02\u7C7B\u76F8\u5438&quot; \u8BA8\u8BBA\u53CB\u8C0A</h3><p>\u4E0E\u662F\u82CF\u683C\u62C9\u5E95\u518D\u6B21\u6539\u53D8\u601D\u8DEF, \u8BD5\u56FE\u901A\u8FC7 &quot;\u540C\u7C7B\u76F8\u65A5, \u5F02\u7C7B\u76F8\u5438&quot; \u7684\u89D2\u5EA6\u8BD5\u56FE\u627E\u5230\u53CB\u8C0A\u662F\u4EC0\u4E48.</p><p>1 \u76F8\u5F02\u8005\u662F\u76F8\u5F02\u8005\u7684\u98DF\u7CAE, \u76F8\u540C\u8005\u4E0D\u540C\u4ECE\u76F8\u540C\u8005\u90A3\u91CC\u5F97\u5230\u76CA\u5904. 2 \u4F46\u7EDD\u5BF9\u76F8\u53CD\u7684\u662F\u654C\u4EBA, \u800C\u4E0D\u662F\u670B\u53CB. \u5982\u6B63\u4E49\u548C\u975E\u6B63\u4E49, \u8282\u5236\u548C\u975E\u8282\u5236. 3 \u6211\u4EEC\u628A\u4E8B\u7269\u5206\u4E3A\u4E09\u7C7B: \u597D\u7684\u4E8B\u7269, \u574F\u7684\u4E8B\u7269\u548C\u4E0D\u597D\u4E0D\u597D\u7684\u4E8B\u7269 3.1 \u574F\u7684\u4E8B\u7269\u4E0D\u4F1A\u4E0E\u4EFB\u4F55\u4E8B\u7269\u662F\u670B\u53CB 3.2 \u597D\u7684\u4E8B\u7269\u4E0D\u4F1A\u548C\u597D\u7684\u4E8B\u7269\u662F\u670B\u53CB 3.3 \u4E0D\u597D\u4E0D\u574F\u7684\u4E8B\u7269\u4E0D\u4F1A\u548C\u4E0D\u597D\u4E0D\u574F\u7684\u4E8B\u7269\u662F\u670B\u53CB 3.4 \u56E0\u6B64\u53CB\u8C0A\u7684\u552F\u4E00\u53EF\u80FD\u662F, \u4E0D\u597D\u4E0D\u574F\u7684\u4E8B\u7269\u53EF\u4EE5\u548C\u597D\u7684\u4E8B\u7269\u662F\u670B\u53CB 4 \u5173\u4E8E\u4E0D\u597D\u4E0D\u574F\u7684\u4E8B\u7269\u53EF\u4EE5\u548C\u597D\u7684\u4E8B\u7269\u6210\u4E3A\u670B\u53CB\u7684\u8BBA\u8BC1: 4.1 \u8EAB\u4F53\u662F\u4E0D\u597D\u4E0D\u574F\u7684 4.2 \u533B\u672F\u662F\u597D\u7684 4.3 \u5065\u5EB7 ( \u597D\u7684\u4E8B\u7269 ) \u4F7F\u8EAB\u4F53\u4E0D\u9700\u8981\u533B\u672F, \u800C\u75BE\u75C5 ( \u574F\u7684\u4E8B\u7269 ) \u4F7F\u8EAB\u4F53\u9700\u8981\u533B\u672F 4.4 \u4E8B\u7269\u5448\u73B0\u67D0\u6027\u8D28, \u53EF\u80FD\u6709\u4E24\u79CD\u60C5\u51B5: 4.4.1 \u4E00\u79CD\u60C5\u51B5\u662F, \u4E8B\u7269\u5E76\u4E0D\u53D8\u6210\u6240\u5448\u73B0\u7684\u6027\u8D28, \u5982\u91D1\u9501\u9540\u767D\u94C5, \u91D1\u9501\u867D\u5448\u73B0\u51FA\u767D, \u4F46\u5176\u672C\u8EAB\u5E76\u4E0D\u53D8\u767D 4.4.2 \u4E00\u79CD\u60C5\u51B5\u662F, \u4E8B\u7269\u53D8\u6210\u4E86\u6240\u5448\u73B0\u7684\u6027\u8D28, \u5982\u5E74\u9F84\u751F\u767D\u53D1, \u5934\u53D1\u53D8\u6210\u4E86\u767D\u8272 4.5 \u56E0\u6B64\u4E0D\u597D\u4E0D\u574F\u7684\u4E8B\u7269\u5448\u73B0\u4E3A\u574F\u800C\u4E0D\u53D8\u4E3A\u574F\u7684\u65F6, \u5B83\u4F1A\u548C\u597D\u7684\u4E8B\u7269\u6210\u4E3A\u670B\u53CB; \u4F46\u5982\u679C\u4E0D\u597D\u4E0D\u574F\u7684\u4E8B\u7269\u53D8\u6210\u4E86\u574F\u7684\u4E8B\u7269, \u5B83\u5C31\u4E0D\u4F1A\u4E0E\u597D\u7684\u4E8B\u7269\u6210\u4E3A\u670B\u53CB. \u56E0\u6B64 4.5.1 \u5DF2\u7ECF\u806A\u660E\u7684\u4E0D\u518D\u662F\u667A\u6167\u7684\u670B\u53CB 4.5.2 \u611A\u8822\u7684\u4E5F\u4E0D\u4F1A\u6210\u4E3A\u667A\u6167\u7684\u670B\u53CB 4.5.3 \u56E0\u6B64\u53EA\u6709\u90A3\u6B64\u611A\u8822\u65E0\u77E5\u800C\u81EA\u77E5\u65E0\u77E5\u7684\u4EBA\u4F1A\u6210\u4E3A\u667A\u6167\u7684\u670B\u53CB</p><h3 id="\u4ECE\u6B32\u671B\u4E0E\u4ECE\u5C5E\u7684\u89D2\u5EA6\u8BA8\u8BBA\u53CB\u8C0A" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u6B32\u671B\u4E0E\u4ECE\u5C5E\u7684\u89D2\u5EA6\u8BA8\u8BBA\u53CB\u8C0A" aria-hidden="true">#</a> \u4ECE\u6B32\u671B\u4E0E\u4ECE\u5C5E\u7684\u89D2\u5EA6\u8BA8\u8BBA\u53CB\u8C0A</h3><p>\u8FD9\u4E2A\u8BBA\u8BC1\u4F7F\u5415\u897F\u65AF\u548C\u7F8E\u6D85\u514B\u585E\u52AA\u5FC3\u60A6\u8BDA\u670D. \u4F46\u82CF\u683C\u62C9\u5E95\u5374\u62C5\u5FC3\u5BF9\u53CB\u8C0A\u7684\u63A2\u8BA8\u4F1A\u683D\u5728\u9519\u8BEF\u7684\u63A8\u7406\u4E0A.</p><p>1 \u4F7F\u67D0\u4EBA\u6210\u4E3A\u4ED6\u4EBA\u670B\u53CB\u7684\u539F\u56E0\u662F\u56E0\u4E3A\u4ED6\u662F\u4ED6\u670B\u53CB\u7684\u670B\u53CB 1.1 \u67D0\u4EBA\u505A\u4ED6\u4EBA\u7684\u670B\u53CB\u662F\u6709\u52A8\u673A\u6709\u539F\u56E0\u7684 1.2 \u75C5\u4EBA\u662F\u533B\u751F\u7684\u670B\u53CB, \u539F\u56E0\u662F\u75BE\u75C5, \u52A8\u673A\u662F\u5065\u5EB7 1.3 \u75BE\u75C5\u662F\u574F\u7684, \u5065\u5EB7\u662F\u597D\u7684, \u8EAB\u4F53\u662F\u4E0D\u597D\u4E0D\u574F\u7684 1.4 \u533B\u672F\u56E0\u5065\u5EB7\u5F97\u5230\u8EAB\u4F53\u7684\u53CB\u8C0A, \u800C\u8EAB\u4F53\u548C\u5065\u5EB7\u662F\u670B\u53CB, \u548C\u75BE\u75C5\u662F\u654C\u4EBA 1.5 \u56E0\u6B64\u8EAB\u4F53\u4E0E\u533B\u672F\u4E3A\u53CB, \u539F\u56E0\u662F\u533B\u672F\u5BF9\u4E8E\u8EAB\u4F53\u7684\u654C\u4EBA ( \u75BE\u75C5 ) \u662F\u4E00\u79CD\u574F, \u800C\u5176\u52A8\u673A\u662F\u533B\u672F\u5BF9\u4E8E\u8EAB\u4F53\u7684\u670B\u53CB ( \u5065\u5EB7 ) \u662F\u4E00\u79CD\u597D 2 \u5B58\u5728\u4E00\u4E2A\u4F7F\u6211\u4EEC\u4E0E\u4E00\u5207\u4E8B\u7269\u6210\u4E3A\u670B\u53CB\u7684\u6700\u7EC8\u539F\u56E0 2.1 \u67D0\u4EBA\u56E0\u6B64\u67D0\u4E2A\u539F\u56E0\u5BF9\u67D0\u4E2A\u4E8B\u7269\u53CB\u597D, \u90A3\u4E48\u4ED6\u4E5F\u4F1A\u5BF9\u4F5C\u4E3A\u539F\u56E0\u7684\u4E8B\u7269\u53CB\u597D 2.2 \u56E0\u6B64\u53EA\u6211\u4EEC\u4E00\u76F4\u8FFD\u95EE, \u5C31\u53EF\u627E\u5230\u4F5C\u4E3A\u6700\u540E\u90A3\u4E2A\u539F\u56E0\u7684\u4E8E\u4E8B\u7269. 2.3 \u53EA\u6709\u8FD9\u4E2A\u7EC8\u6781\u7684\u53CB\u8C0A\u624D\u4F7F\u6211\u4EEC\u5BF9\u5176\u4ED6\u4E8B\u7269\u662F\u53CB\u597D\u7684 3 \u574F\u4E0D\u662F\u8FD9\u4E2A\u6700\u7EC8\u53CB\u8C0A\u7684\u539F\u56E0, \u800C\u6B32\u671B\u662F 3.1 \u524D\u8FB9\u8BBA\u8BC1\u8FC7, \u7531\u4E8E\u574F\u7684\u5B58\u5728, \u597D\u624D\u88AB\u6211\u4EEC\u8FD9\u4E9B\u5904\u4E8E\u597D\u4E8E\u574F\u4E4B\u95F4\u7684\u4EBA\u6240\u7231. 3.2 \u6B32\u671B\u662F\u4E0D\u597D\u4E0D\u574F\u7684 3.3 \u5047\u8BBE\u574F\u706D\u7EDD\u4E86, \u90A3\u4E9B\u4E0D\u597D\u4E0D\u574F\u7684\u6B32\u671B\u4ECD\u65E7\u5B58\u5728 3.4 \u53EA\u4EBA\u6B32\u671B\u5B58\u5728, \u4EBA\u5C31\u4F1A\u7231\u6155\u4E8B\u7269 3.5 \u56E0\u6B64\u574F\u706D\u7EDD\u4E86, \u53CB\u8C0A\u8FD8\u5B58\u5728 3.6 \u56E0\u6B64\u574F\u4E0D\u662F\u6700\u7EC8\u53CB\u8C0A\u7684\u539F\u56E0 4 \u53CB\u8C0A\u662F\u7531\u4E8E\u67D0\u79CD\u672C\u6027\u7684\u8054\u7CFB\u800C\u76F8\u4E92\u5C5E\u4E8E\u5BF9\u65B9 4.1 \u6B32\u671B\u610F\u5473\u7740\u5FC5\u5B9A\u6709\u60F3\u8981\u5F97\u5230\u7F3A\u4E4F\u7684\u4E1C\u897F 4.2 \u7F3A\u4E4F\u8005\u5BF9\u60F3\u8981\u7684\u4E1C\u897F\u8868\u793A\u53CB\u597D 4.3 \u7231\u60C5, \u53CB\u8C0A, \u6B32\u671B\u7684\u5BF9\u8C61\u8005\u5C5E\u4E8E\u4EBA 4.4 \u56E0\u6B64\u53CB\u7231\u7684\u53CC\u65B9\u7531\u4E8E\u67D0\u79CD\u672C\u6027\u800C\u6709\u5BF9\u65B9\u6240\u7F3A\u4E4F\u7684\u4E1C\u897F 4.5 \u56E0\u6B64\u53EA\u6709\u771F\u6B63\u7684\u7231\u4EBA\u624D\u4F1A\u5F97\u5230\u4ED6\u7231\u86EE\u7684\u5BF9\u8C61\u7684\u56DE\u7231\u662F\u4E0D\u53EF\u80FD\u7684. 5 \u5982\u679C\u597D\u5C5E\u4E8E\u597D, \u574F\u5C5E\u4E8E\u574F, \u4E0D\u597D\u4E0D\u574F\u5C5E\u4E8E\u4E0D\u597D 5.1 \u76F8\u5C5E\u7684\u4E8B\u7269\u4E0E\u76F8\u540C\u7684\u4E8B\u7269\u6709\u533A\u522B 5.1.1 \u76F8\u540C\u7684\u4E8B\u7269\u76F8\u4E92\u603B\u662F\u65E0\u7528 5.1.2 \u76F8\u5C5E\u7684\u4E8B\u7269\u53EF\u4EE5\u5F15\u8D77\u6B32\u671B 5.2 \u5982\u679C\u597D\u5C5E\u4E8E\u597D, \u574F\u5C5E\u4E8E\u574F, \u4E0D\u597D\u4E0D\u574F\u5C5E\u4E8E\u4E0D\u597D\u4E0D\u574F, \u53CB\u8C0A\u5C31\u4E0D\u4F1A\u5B58\u5728 5.3 \u56E0\u4E3A\u540C\u4E8B\u7269\u4E4B\u95F4\u4E0D\u5B58\u5728\u53CB\u8C0A</p><p>\u5230\u6B64, \u5173\u4E8E\u53CB\u8C0A\u7684\u8BBA\u8BC1\u53C8\u56DE\u5230\u4E86\u539F\u70B9. \u82CF\u683C\u62C9\u5E95\u60F3\u8BF7\u4ED6\u8FD9\u6837\u5E74\u7EAA\u7684\u957F\u8005\u6765\u5E2E\u5FD9, \u4F46\u7F8E\u6D85\u514B\u585E\u52AA\u548C\u5415\u897F\u65AF\u7684\u8DDF\u73ED\u6765\u5374\u8981\u62C9\u7740\u4E24\u4EBA\u56DE\u5BB6. \u5BF9\u8BDD\u53EA\u80FD\u5728\u6B64\u6253\u4F4F.</p>',29),i=[t];function p(n,o){return e(),h("div",null,i)}var s=a(d,[["render",p],["__file","\u5415\u897F\u65AF\u7BC7\u8BF4\u4E86\u5565.html.vue"]]);export{s as default};
