import{_ as u,o as t,c as p,e as o}from"./app.6bf529a6.js";const e={},s=o('<h2 id="\u53EF\u4EE5\u7528\u771F\u76F8\u8BF1\u60D1\u4E48" tabindex="-1"><a class="header-anchor" href="#\u53EF\u4EE5\u7528\u771F\u76F8\u8BF1\u60D1\u4E48" aria-hidden="true">#</a> \u53EF\u4EE5\u7528\u771F\u76F8\u8BF1\u60D1\u4E48?</h2><blockquote><p><sup>15</sup>\u4E0A\u4E3B\u5929\u4E3B\u5C06\u4EBA\u5B89\u7F6E\u5728\u4F0A\u7538\u7684\u4E50\u56ED\u5185\uFF0C\u53EB\u4ED6\u8015\u79CD\uFF0C\u770B\u5B88\u4E50\u56ED\u3002 <sup>16</sup>\u4E0A\u4E3B\u5929\u4E3B\u7ED9\u4EBA\u4E0B\u4EE4\u8BF4\uFF1A&quot;\u4E50\u56ED\u4E2D\u5404\u6811\u4E0A\u7684\u679C\u5B50\uFF0C\u4F60\u90FD\u53EF\u5403\uFF0C<sup>17</sup>\u53EA\u6709\u77E5\u5584\u6076\u6811\u4E0A\u7684\u679C\u5B50\u4F60\u4E0D\u53EF\u5403\uFF0C\u56E0\u4E3A\u90A3\u4E00\u5929\u4F60\u5403\u4E86\uFF0C\u5FC5\u5B9A\u8981\u6B7B\u3002&quot; --\u300A\u521B\u4E16\u7EAA \u7B2C\u4E8C\u7AE0\u300B</p></blockquote><blockquote><p><sup>1</sup>\u5728\u4E0A\u4E3B\u5929\u4E3B\u6240\u9020\u7684\u4E00\u5207\u91CE\u517D\u4E2D\uFF0C\u86C7\u662F\u6700\u72E1\u733E\u7684\u3002\u86C7\u5BF9\u5973\u4EBA\u8BF4\uFF1A&quot;\u5929\u4E3B\u771F\u8BF4\u4E86\uFF0C\u4F60\u4EEC\u4E0D\u53EF\u5403\u4E50\u56ED\u4E2D\u4EFB\u4F55\u6811\u4E0A\u7684\u679C\u5B50\u5417\uFF1F&quot;<sup>2</sup>\u5973\u4EBA\u5BF9\u86C7\u8BF4\uFF1A&quot;\u4E50\u56ED\u4E2D\u6811\u4E0A\u7684\u679C\u5B50\uFF0C\u6211\u4EEC\u90FD\u53EF\u5403\uFF1B<sup>3</sup>\u53EA\u6709\u4E50\u56ED\u4E2D\u592E\u90A3\u68F5\u6811\u4E0A\u7684\u679C\u5B50\uFF0C\u5929\u4E3B\u8BF4\u8FC7\uFF0C\u4F60\u4EEC\u4E0D\u53EF\u4EE5\u5403\uFF0C\u4E5F\u4E0D\u53EF\u6478\uFF0C\u514D\u5F97\u6B7B\u4EA1\u3002&quot;<sup>4</sup>\u86C7\u5BF9\u5973\u4EBA\u8BF4\uFF1A&quot;\u4F60\u4EEC\u51B3\u4E0D\u4F1A\u6B7B\uFF01<sup>5</sup>\u56E0\u4E3A\u5929\u4E3B\u77E5\u9053\uFF0C\u4F60\u4EEC\u90A3\u5929\u5403\u4E86\u8FD9\u679C\u5B50\uFF0C\u4F60\u4EEC\u7684\u773C\u5C31\u4F1A\u5F00\u4E86\uFF0C\u5C06\u5982\u540C\u5929\u4E3B\u4E00\u6837\u77E5\u9053\u5584\u6076\u3002&quot;<sup>6</sup>\u5973\u4EBA\u770B\u90A3\u68F5\u679C\u6811\u5B9E\u5728\u597D\u5403\u597D\u770B\uFF0C\u4EE4\u4EBA\u7FA1\u6155\uFF0C\u4E14\u80FD\u589E\u52A0\u667A\u6167\uFF0C\u9042\u6458\u4E0B\u4E00\u4E2A\u679C\u5B50\u5403\u4E86\uFF0C\u53C8\u7ED9\u4E86\u5979\u7684\u7537\u4EBA\u4E00\u4E2A\uFF0C\u4ED6\u4E5F\u5403\u4E86\u3002 --\u300A\u521B\u4E16\u7EAA \u7B2C\u4E09\u7AE0\u300B</p></blockquote><p>\u5495\u54D2\u771F\u8BA8\u538C, \u51C0\u95EE\u4E9B\u83AB\u660E\u5176\u5999\u7684\u95EE\u9898.</p><p>&quot;\u4F60\u770B, \u8FD9\u91CC\u795E\u4E0D\u662F\u9A97\u4EBA\u4E48? \u4ED6\u7ED9\u4E9A\u5F53\u8BF4\u5403\u4E86\u679C\u5B50\u5FC5\u6B7B, \u7ED3\u679C\u4ED6\u4EEC\u5403\u4E86\u679C\u5B50\u4E5F\u6CA1\u6B7B.&quot;</p><p>\u8FD9\u4E2A\u95EE\u9898\u6211\u8FD8\u771F\u60F3\u4E86\u4E00\u4E9B\u5929: \u8BF6, \u5047\u5982\u6211\u5BB6\u5927\u5B9D\u8003\u8BD5\u524D\u4E0D\u597D\u590D\u4E60, \u6211\u7ED9\u5979\u8BF4, \u4F60\u8981\u4E0D\u597D\u597D\u590D\u4E60, \u4E00\u5B9A\u8003\u4E0D\u597D; \u5F53\u7136\u5979\u4E0D\u597D\u597D\u590D\u4E60\u7684\u65F6\u5019, \u8FD8\u6CA1\u8003\u8BD5, \u90A3\u8FD9\u7B97\u6211\u9A97\u5979\u4E48? \u5982\u679C\u8FD9\u91CC\u4E0D\u7B97\u6211\u9A97\u5927\u5B9D, \u90A3\u795E\u5728\u8FD9\u91CC\u4E5F\u4E0D\u7B97\u9A97\u4EBA\u5427.</p><p>\u7ED3\u679C\u51E0\u5929\u65F6\u95F4, \u5495\u54D2\u5DF2\u7ECF\u5728\u548C\u522B\u4EBA\u8BA8\u8BBA\u4E2D\u8F6C\u79FB\u9635\u5730\u4E86. &quot;\u6211\u6CA1\u8BF4\u795E\u9A97\u4EBA, \u6211\u4EEC\u73B0\u5728\u5728\u8BA8\u8BBA, \u8FD9\u91CC\u86C7\u6709\u6CA1\u6709\u9A97\u4EBA. \u4F60\u770B, \u86C7\u5728\u8FD9\u91CC\u9610\u660E\u7684\u662F\u667A\u6167\u679C\u7684\u672C\u8D28\u554A.&quot;</p><p>\u771F\u8BA8\u538C, \u4E0D\u7136\u8BA9\u4ED6\u5AB3\u5987\u95EE\u4E00\u4E0B\u90A3\u4E2A\u5B66\u98CE\u6C34\u7684\u540C\u5B66\u7684\u5E08\u5085, \u82B1\u4E94\u4E07\u5757\u94B1\u8FD9\u4E2A\u75C5\u80FD\u65AD\u6839\u4E48? \u8981\u662F\u80FD\u65AD\u6839\u4E0D, \u4E0D\u7136\u5927\u5BB6\u51D1\u51D1\u94B1\u7ED9\u770B\u770B. ( \u4E0D\u61C2\u8FD9\u4E2A\u6897\u7684, \u5C31\u5F53\u6CA1\u8FD9\u53E5\u5427. )</p><p>\u4F46\u4E3A\u4E86\u7EF4\u6301\u6211\u5728\u8BA8\u8BBA\u7EC4\u7684\u5FE0\u539A\u5F62\u8C61, \u6211\u8BF4\u8FD8\u5F97\u597D\u597D\u8BF4\u8BDD: \u8FD9\u91CC\u86C7\u6709\u6CA1\u6709\u9A97\u4EBA\u53C8\u6709\u4EC0\u4E48\u91CD\u8981\u6027\u5462? \u5047\u5982\u6211\u53BB ktv, \u8FB9\u4E0A\u6709\u4E2A\u533B\u9662\u7684\u54E5\u4EEC, \u62FF\u51FA\u4ECE\u533B\u9662\u91CC\u62FF\u51FA\u6765\u7684\u7CBE\u795E\u7C7B\u5174\u594B\u7C7B\u836F\u54C1, \u529D\u6211\u5403. \u6211\u8BF4, \u8BF6, \u5403\u8FD9\u4E2A\u4E0D\u592A\u597D\u5427. \u4ED6\u8BF4, \u8FD9\u6709\u4EC0\u4E48\u7684, \u8FD9\u4E2A\u4E1C\u897F\u672C\u6765\u8BF4\u662F\u62FF\u6765\u6CBB\u75C5\u7684. \u4ECE\u67D0\u79CD\u610F\u4E49\u4E0A\u8BB2, \u8FD9\u4E2A\u54E5\u4EEC\u4E5F\u4E0D\u7B97\u9A97\u6211. \u4F46\u8FD9\u91CC\u4ED6\u662F\u4E0D\u662F\u5728\u9A97\u6211, \u6709\u4EC0\u4E48\u91CD\u8981\u7684\u4E48?</p><p>\u5495\u54D2\u8BF4, \u8FD9\u4E2A\u95EE\u9898\u5F53\u7136\u91CD\u8981, \u5982\u679C\u86C7\u662F\u5728\u9A97\u4EBA, \u90A3\u4E48\u53EF\u4E0D\u53EF\u4EE5\u7528\u771F\u76F8\u8BF1\u60D1\u4EBA.</p><p>\u554A, \u8981\u662F\u8981\u8BA8\u8BBA\u8FD9\u4E2A\u95EE\u9898, \u90A3\u5495\u54D2\u5C31\u4E0D\u90A3\u4E48\u8BA8\u538C\u4E86.</p><h2 id="\u8FF7\u4E8E\u5883\u754C\u548C\u6D6A\u6F2B\u4E3B\u4E49" tabindex="-1"><a class="header-anchor" href="#\u8FF7\u4E8E\u5883\u754C\u548C\u6D6A\u6F2B\u4E3B\u4E49" aria-hidden="true">#</a> \u8FF7\u4E8E\u5883\u754C\u548C\u6D6A\u6F2B\u4E3B\u4E49</h2><p>&quot;\u8BF1\u60D1&quot; \u8FD9\u4E2A\u6982\u5FF5, \u5C31\u5305\u542B\u4E86 &quot;\u771F&quot;. \u5219\u6709\u80FD\u8BA9\u6211\u611F\u53D7\u7684 &quot;\u771F&quot;, \u5C31\u8C08\u4E0D\u4E0A\u8BF1\u60D1.</p><p>\u597D\u6BD4\u6211\u662F\u654C\u540E\u5DE5\u4F5C\u8005, \u88AB\u654C\u4EBA\u6293\u4F4F\u4E86. \u654C\u4EBA\u8981\u7528\u7F8E\u4EBA\u8BA1\u6765\u8BF1\u60D1\u6211. \u90A3\u7F8E\u4EBA\u597D\u6B79\u5F97 &quot;\u771F&quot; \u662F\u7F8E\u4EBA\u5427. \u5982\u679C\u6765\u8BF1\u60D1\u6211\u7684\u957F\u5F97\u548C\u5F20\u98DE\u4F3C\u7684, \u4E00\u6839\u6839\u817F\u6BDB\u4ECE\u4E1D\u889C\u91CC\u624E\u51FA\u6765, \u90A3\u4E48\u4E5F\u8C08\u4E0A\u662F\u8BF1\u60D1\u4E86.</p>',14),a=[s];function c(q,r){return t(),p("div",null,a)}var n=u(e,[["render",c],["__file","\u5584\u597D\u7684\u8BF1\u60D1.html.vue"]]);export{n as default};
