import{_ as t,o,c as u,e}from"./app.efeddd24.js";const q={},a=e('<h1 id="\u56FE\u5BBE\u6839\u5B66\u6D3E\u4E0E\u5723\u7ECF\u6548\u529B\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u56FE\u5BBE\u6839\u5B66\u6D3E\u4E0E\u5723\u7ECF\u6548\u529B\u95EE\u9898" aria-hidden="true">#</a> \u56FE\u5BBE\u6839\u5B66\u6D3E\u4E0E\u5723\u7ECF\u6548\u529B\u95EE\u9898</h1><h2 id="\u56FE\u5BBE\u6839\u5B66\u6D3E" tabindex="-1"><a class="header-anchor" href="#\u56FE\u5BBE\u6839\u5B66\u6D3E" aria-hidden="true">#</a> \u56FE\u5BBE\u6839\u5B66\u6D3E</h2><p>\u6700\u8FD1\u5728\u8BFB\u300A\u8BFB\u67CF\u62C9\u56FE\u300B\u4E00\u4E66. \u8FD9\u662F\u4E00\u672C\u56FE\u5BBE\u6839\u5B66\u6D3E\u7684\u4E66.</p><p>20 \u4E16\u7EAA 50 \u5E74\u4EE3\u4E2D\u8D77, \u5FB7\u56FD\u56FE\u5BBE\u6839\u5927\u5B66\u54F2\u5B66\u7CFB\u548C\u53E4\u5178\u8BED\u6587\u7684\u4E00\u6279\u5B66\u8005\u63D0\u51FA\u4E00\u79CD\u65B0\u7684\u8BE0\u91CA\u67CF\u62C9\u54F2\u5B66\u7684\u8303\u5F0F, \u56E0\u4E3A\u72EC\u6811\u4E00\u5E1C, \u56E0\u6B64\u6210\u4E3A\u4E00\u4E2A\u5B66\u6D3E.</p><p>\u67CF\u62C9\u56FE\u7684\u5BF9\u8BDD\u5F55\u6709\u975E\u5E38\u591A\u5BF9\u4E8E &quot;\u53E3\u4F20&quot; \u548C &quot;\u4E66\u5199&quot; \u7684\u533A\u5206, \u5C24\u5176\u662F\u5728\u300A\u6590\u5FB7\u82E5\u300B\u7BC7\u4E2D, \u5BF9\u4E66\u5199\u6280\u672F\u8FDB\u884C\u4E86\u5F88\u660E\u786E\u7684\u6279\u5224; \u800C\u4E14\u5728\u67CF\u62C9\u56FE\u7684\u4E66\u4FE1\u4E2D, \u67CF\u62C9\u56FE\u4E5F\u975E\u5E38\u660E\u786E\u7684\u8BDD, \u4ED6\u4E0D\u4F1A\u5C06\u5176\u54F2\u5B66\u4E66\u5199\u51FA\u6765. \u6BD4\u5982, \u67CF\u62C9\u56FE\u5BF9\u4E8E\u4E66\u5199\u6709\u4E00\u4E2A\u91CD\u8981\u7684\u6279\u8BC4: \u601D\u60F3\u4E00\u65E6\u88AB\u4E66\u5199\u51FA\u6765\u540E, \u5C31\u4E0D\u80FD\u56DE\u7B54\u548C\u53CD\u9A73\u8BFB\u8005\u7684\u8D28\u7591. \u6240\u4EE5\u81EA\u53E4\u4EE5\u6765\u5C31\u8BA4\u4E3A\u67CF\u62C9\u56FE\u54F2\u5B66\u5B58\u5728 &quot;\u5185\u4F20&quot; \u548C &quot;\u5916\u4F20&quot; \u7684\u533A\u522B. \u6211\u4EEC\u770B\u5230\u7684\u67CF\u62C9\u56FE\u54F2\u5B66\u5BF9\u8BDD\u5F55\u4EC5\u4EC5\u662F\u5176 &quot;\u5916\u4F20&quot; \u54F2\u5B66, \u800C\u5176\u5B66\u56ED\u91CC\u8FD8\u6709\u53E6\u4E00\u5957\u53E3\u5934\u4F20\u6388\u7684 &quot;\u5185\u4F20&quot; \u54F2\u5B66. \u8FD9\u5957\u5185\u4F20\u54F2\u5B66\u5C31\u662F\u4E9A\u91CC\u58EB\u591A\u5FB7\u6240\u8BB0\u8FF0\u7684 &quot;\u67CF\u62C9\u56FE\u7684\u672C\u539F\u5B66\u8BF4&quot;. \u800C\u540E\u6765\u7684 &quot;\u65B0\u67CF\u62C9\u56FE\u4E3B\u4E49&quot; \u5C31\u662F\u8FD9\u79CD\u5185\u4F20\u5B66\u8BF4\u7684\u7EE7\u627F\u548C\u53D1\u5C55.</p><p>\u7136\u800C\u5230\u4E86\u8FD1\u4EE3, \u67CF\u62C9\u56FE\u54F2\u5B66\u7814\u7A76\u57FA\u672C\u91C7\u53D6\u4E00\u79CD\u6392\u65A5\u5185\u4F20\u5B66\u8BF4\u7684\u6001\u5EA6. \u65BD\u83B1\u5C14\u9A6C\u8D6B\u8BA4\u4E3A, \u67CF\u62C9\u56FE\u4EE5 &quot;\u5BF9\u8BDD\u5F55&quot; \u7684\u5F62\u5F0F\u6210\u529F\u7684\u514B\u670D\u4E86\u4E66\u7684\u7F3A\u9677, \u8FBE\u5230\u4E86\u5BF9\u4E8E\u53E3\u4F20\u7684\u5B8C\u7F8E\u6A21\u4EFF\u548C\u66FF\u4EE3. \u6240\u8C13\u7684 &quot;\u5185\u4F20&quot; \u548C &quot;\u5916\u4F20&quot; \u7684\u533A\u5206\u4EC5\u5728\u4E8E\u8BFB\u8005\u7684\u6C34\u5E73. \u4F46\u65BD\u83B1\u5C14\u9A6C\u8D6B\u5219\u8BA4\u4E3A, \u67CF\u62C9\u56FE\u5728\u5176\u5BF9\u8BDD\u5F55\u4E2D\u57CB\u4E0B\u4E86\u8BF8\u591A\u7EBF\u7D22, \u5F53\u6211\u4EEC\u901A\u7BC7\u8BFB\u5B8C\u5BF9\u8BDD, \u6536\u96C6\u5230\u8DB3\u591F\u7684\u7EBF\u7D22\u540E, \u518D\u56DE\u8FC7\u5934\u91CD\u65B0\u9605\u8BFB, \u5C31\u4F1A\u4EA7\u751F\u65B0\u7684\u8BA4\u8BC6\u548C\u770B\u6CD5, \u8FD9\u4E9B\u65B0\u7684\u770B\u6CD5\u5C31\u4F1A\u5F62\u6210\u5BF9\u4EE5\u524D\u9605\u8BFB\u4E2D\u4EA7\u751F\u7684\u8D28\u7591\u7684\u89E3\u7B54. \u901A\u8FC7\u8FD9\u6837 &quot;\u591A\u58F0\u90E8&quot; \u7684\u8BBE\u8BA1, \u67CF\u62C9\u56FE\u7684\u5BF9\u8BDD\u5F55\u5C31\u5177\u5907\u4E86\u4E00\u79CD\u81EA\u6211\u89E3\u91CA\u7684\u80FD\u529B. \u5C31\u50CF\u5F88\u591A\u7535\u5B50\u6E38\u620F\u7684\u8BBE\u8BA1\u4E00\u6837, \u901A\u5173\u4E4B\u540E, \u6E38\u620F\u624D\u771F\u6B63\u5F00\u59CB. \u56E0\u6B64\u65BD\u83B1\u5C14\u9A6C\u8D6B\u8BA4\u4E3A, \u67CF\u62C9\u56FE\u7684\u5168\u90E8\u5B66\u8BF4\u90FD\u5305\u542B\u5728\u4ED6\u7684\u5BF9\u8BDD\u5F55\u4E2D, \u6211\u4EEC\u6CA1\u6709\u5FC5\u8981\u518D\u53BB &quot;\u62B1\u6028\u548C\u68A6\u60F3\u67CF\u62C9\u56FE\u667A\u6167\u53E6\u5916\u4E00\u4E9B\u5931\u53BB\u7684\u5B9D\u85CF&quot;. \u4E9A\u91CC\u58EB\u591A\u5FB7\u6240\u8BB0\u8FF0\u7684 &quot;\u67CF\u62C9\u56FE\u7684\u672C\u539F\u5B66\u8BF4&quot;, \u53EA\u662F\u67CF\u62C9\u56FE\u5B66\u56ED\u91CC\u7684\u5B66\u751F\u5BF9\u4E8E\u67CF\u62C9\u56FE\u54F2\u5B66\u7684\u8BEF\u89E3, \u5426\u5219\u4E3A\u4EC0\u4E48\u67CF\u62C9\u56FE\u5BF9\u8BDD\u91CC\u4E00\u70B9\u4E5F\u770B &quot;\u67CF\u62C9\u56FE\u7684\u672C\u539F\u5B66\u8BF4&quot; \u7684\u5F71\u5B50\u5462?</p><p>\u800C\u56FE\u5BBE\u6839\u5B66\u6D3E\u5219\u901A\u8FC7\u5927\u91CF\u7684\u6587\u732E\u5B66\u548C\u8BCD\u6E90\u5B66\u65B9\u6CD5, \u8BC1\u660E\u67CF\u62C9\u56FE\u5185\u4F20\u5B66\u6D3E\u7684\u5B58\u5728. \u9996\u5148\u6211\u4EEC\u53EF\u4EE5\u5728\u67CF\u62C9\u56FE\u7684\u5BF9\u8BDD\u5F55\u4E2D\u770B\u5230\u5927\u91CF\u5173\u4E8E &quot;\u5185\u4F20\u5B66\u8BF4&quot; \u7684\u63D0\u793A. \u6BD4\u5982, \u5BF9\u8BDD\u5F55\u4E2D\u7ECF\u5E38\u63D0\u793A, \u903B\u8F91\u81EA\u8EAB\u4E0D\u80FD\u5B8C\u6210\u8BBA\u8BC1, \u9700\u8981\u5916\u90E8\u7684\u5E2E\u52A9; \u5BF9\u8BDD\u4E2D\u7ECF\u5E38\u51FA\u73B0\u4E00\u4E9B\u7F3A\u5931, \u8BB2\u5230\u4E00\u534A\u5C31\u8DF3\u8FC7\u7684\u8BBA\u8BC1; \u5BF9\u8BDD\u4E2D\u7ECF\u5E38\u63D0\u793A\u5BF9\u8BDD\u5185\u5BB9\u672C\u8EAB\u662F\u4E00\u79CD\u8BAD\u7EC3\u7B49. \u66F4\u4E0D\u8981\u8BF4, \u67CF\u62C9\u56FE\u5BF9\u8BDD\u4E2D\u975E\u5E38\u660E\u786E\u7684\u63D0\u5230\u7684\u5BF9\u4E8E\u4E66\u5199\u7684\u6279\u5224. \u5176\u6B21, \u5BF9\u5185\u4F20\u5B66\u8BF4\u7684\u5426\u5B9A, \u4E5F\u662F\u5BF9\u5927\u91CF\u53E4\u4EE3\u6587\u732E\u7684\u6F20\u89C6. \u5728\u67CF\u62C9\u56FE\u5B66\u56ED\u5446\u8FC7 13 \u5E74\u7684\u4E9A\u91CC\u58EB\u591A\u5FB7\u5728\u8BB0\u5F55\u4E2D\u7ADF\u7136\u4E0D\u533A\u5206 &quot;\u67CF\u62C9\u56FE\u7684\u5B66\u8BF4&quot; \u548C &quot;\u67CF\u62C9\u56FE\u5B66\u56ED\u7684\u5B66\u8BF4&quot;? \u6574\u4E2A\u53E4\u4EE3\u90FD\u8BEF\u89E3\u4E86\u67CF\u62C9\u56FE\u7684\u5929\u624D, \u975E\u5F97\u7B49\u5230\u8FD1\u73B0\u4EE3\u624D\u6211\u4EEC\u624D\u91CD\u65B0\u53D1\u73B0\u4E86\u67CF\u62C9\u56FE? \u8FD9\u6837\u60F3\u6765, \u8FD1\u4EE3\u67CF\u62C9\u56FE\u7814\u7A76\u6F20\u89C6\u9664\u4E86\u67CF\u62C9\u56FE\u5BF9\u8BDD\u5F55\u4EE5\u5916\u7684\u5176\u4ED6\u6587\u732E\u7684\u505A\u6CD5\u662F\u6709\u4E9B\u5947\u602A.</p><p>\u56FE\u5BBE\u6839\u5B66\u6D3E\u8BA4\u4E3A\u67CF\u62C9\u56FE\u7684\u5BF9\u8BDD\u5F55\u662F\u5199\u7ED9\u96C5\u5178\u5927\u4F17\u548C\u5B66\u56ED\u5B66\u751F\u7684\u4F5C\u54C1, \u8FD9\u4E9B\u4F5C\u54C1\u7684\u7528\u9014\u662F\u68C0\u9A8C\u8BFB\u8005\u4E2D\u6709\u6CA1\u6709\u80FD\u529B\u5B66\u4E60\u5185\u4F20\u5B66\u8BF4, \u5E76\u63D0\u4F9B\u7ED9\u5B66\u56ED\u5B66\u751F\u8FDB\u884C\u57FA\u672C\u7684\u54F2\u5B66\u7EC3\u4E60. \u800C\u67CF\u62C9\u56FE\u771F\u6B63\u7684\u54F2\u5B66\u601D\u60F3\u6B63\u662F\u4ED6\u7684\u5185\u4F20\u5B66\u8BF4.</p><p>\u56FE\u5BBE\u6839\u5B66\u6D3E\u8BA4\u4E3A\u4ED6\u4EEC\u7684\u5F00\u542F\u4E86\u7B2C\u56DB\u79CD\u7814\u7A76\u7684\u8303\u5F0F, \u524D\u4E09\u79CD\u67CF\u62C9\u56FE\u7814\u7A76\u7684\u8303\u5F0F\u5206\u522B\u662F: \u4E9A\u91CC\u58EB\u591A\u5FB7\u7684\u4EB2\u7099\u5F1F\u5B50, \u65B0\u67CF\u62C9\u56FE\u4E3B\u4E49, \u65BD\u83B1\u5C14\u9A6C\u8D6B\u7684\u6D6A\u6F2B\u6D3E\u8BE0\u91CA. \u4F46\u636E\u8BF4\u56FE\u5BBE\u6839\u5B66\u6D3E\u7684\u89C2\u70B9\u8FD8\u6CA1\u6709\u88AB\u666E\u904D\u63A5\u53D7, \u56F4\u7ED5\u7740\u67CF\u62C9\u56FE\u7684\u5185\u4F20\u5B66\u8BF4\u4EE5\u53CA\u5185\u4F20\u5B66\u8BF4\u4EF7\u503C\u8FD8\u6709\u5F88\u591A\u4E89\u8BBA.</p><h2 id="\u5047\u8BBE\u56FE\u5BBE\u6839\u5B66\u6D3E\u7684\u89C2\u70B9\u662F\u5BF9\u7684" tabindex="-1"><a class="header-anchor" href="#\u5047\u8BBE\u56FE\u5BBE\u6839\u5B66\u6D3E\u7684\u89C2\u70B9\u662F\u5BF9\u7684" aria-hidden="true">#</a> \u5047\u8BBE\u56FE\u5BBE\u6839\u5B66\u6D3E\u7684\u89C2\u70B9\u662F\u5BF9\u7684</h2><p>\u6211\u5728\u4E86\u89E3\u56FE\u5BBE\u6839\u5B66\u6D3E\u65F6, \u5FC3\u91CC\u4EA7\u751F\u4E86\u4E00\u4E2A\u60F3\u6CD5: \u5047\u8BBE\u56FE\u5BBE\u6839\u5B66\u6D3E\u7684\u89C2\u70B9\u662F\u5BF9\u7684, \u90A3\u4E48\u662F\u4E0D\u662F\u771F\u7684\u5BF9\u67CF\u62C9\u56FE\u54F2\u5B66\u7684\u7814\u7A76\u4F1A\u53D1\u751F\u4E00\u573A\u6839\u672C\u7684\u53D8\u5316, \u5C31\u5982\u4ED6\u4EEC\u81EA\u5DF1\u8BF4\u7684\u90A3\u6837, \u5F00\u542F\u7814\u7A76\u67CF\u62C9\u56FE\u7684\u7B2C\u56DB\u79CD\u8303\u5F0F.</p><p>\u5176\u5B9E\u6211\u89C9\u5F97\u4E0D\u5927\u53EF\u80FD. \u5047\u8BBE\u56FE\u5BBE\u6839\u5B66\u6D3E\u7684\u89C2\u70B9\u662F\u5BF9\u7684, \u67CF\u62C9\u56FE\u771F\u6709\u53EA\u901A\u8FC7\u53E3\u5934\u4F20\u6388\u7684\u5185\u4F20\u5B66\u8BF4, \u800C\u4E14\u56FE\u5BBE\u6839\u5B66\u6D3E\u4E5F\u771F\u7684\u628A\u8FD9\u5957\u5185\u4F20\u5B66\u6D3E\u7ED9\u91CD\u65B0\u53D1\u73B0\u4E86, \u90A3\u4E5F\u53EA\u8BC1\u660E\u4E86, \u67CF\u62C9\u56FE\u7684\u5185\u4F20\u5B66\u8BF4\u8FDC\u6CA1\u6709\u5176\u5BF9\u8BDD\u5F55\u4E2D\u4F53\u73B0\u7684\u601D\u60F3\u66F4\u91CD\u8981, \u6216\u8005\u81F3\u5C11\u53EF\u4EE5\u8BF4\u5185\u4F20\u5B66\u8BF4\u7684\u751F\u547D\u529B\u8FDC\u6CA1\u6709\u5BF9\u8BDD\u5F55\u91CC\u601D\u60F3\u66F4\u6709\u751F\u547D\u529B.</p><p>\u5373\u4F7F\u771F\u6709\u67CF\u62C9\u56FE\u5185\u4F20\u5B66\u8BF4, \u5728\u5B66\u56ED\u4E2D\u4F20\u64AD, \u5E76\u7531\u65B0\u67CF\u62C9\u56FE\u4E3B\u4E49\u7EE7\u627F, \u4E5F\u5E76\u4E0D\u4F7F\u6240\u8C13\u7684 &quot;\u67CF\u62C9\u56FE\u672C\u539F\u5B66\u8BF4&quot; \u548C\u65B0\u67CF\u62C9\u56FE\u4E3B\u4E49\u7684\u54F2\u5B66\u589E\u8272, \u5BF9\u4E8E\u6211\u4EEC\u6765\u8BF4, \u5F71\u54CD\u66F4\u5927\u7684\u4F9D\u7136\u662F\u67CF\u62C9\u56FE\u5BF9\u8BDD\u5F55\u4E2D\u7684\u5916\u4F20\u5B66\u8BF4. \u800C\u4E14, \u67CF\u62C9\u56FE\u5BF9\u4E8E\u6211\u4EEC\u4E4B\u6240\u4EE5\u5982\u6B64\u91CD\u8981, \u662F\u51E0\u5343\u5E74\u67CF\u62C9\u56FE\u54F2\u5B66\u53D1\u5C55\u548C\u5BF9\u67CF\u62C9\u56FE\u54F2\u5B66\u7684\u7814\u7A76\u5171\u540C\u7684\u4F5C\u7528, \u67CF\u62C9\u56FE\u672C\u4EBA\u7684\u601D\u60F3\u56FA\u7136\u91CD\u8981, \u4F46\u66F4\u91CD\u8981\u7684\u662F\u51E0\u5343\u5E74\u6765\u4E0D\u540C\u5B66\u8005\u5BF9\u4E8E\u67CF\u62C9\u56FE\u5BF9\u8BDD\u7684\u7814\u7A76, \u4EE5\u53CA\u4ED6\u4EEC\u5728.</p>',13),r=[a];function _(p,h){return o(),u("div",null,r)}var d=t(q,[["render",_],["__file","\u56FE\u5BBE\u6839\u5B66\u6D3E\u4E0E\u5723\u7ECF\u6548\u529B\u95EE\u9898.html.vue"]]);export{d as default};
