import{_ as e,o as c,c as a,e as d}from"./app.efeddd24.js";const o={},p=d(`<h1 id="\u5728ubuntu\u4E0A\u8BBE\u7F6Eapache2\u7684\u7528\u6237\u8BBF\u95EE\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#\u5728ubuntu\u4E0A\u8BBE\u7F6Eapache2\u7684\u7528\u6237\u8BBF\u95EE\u63A7\u5236" aria-hidden="true">#</a> \u5728ubuntu\u4E0A\u8BBE\u7F6Eapache2\u7684\u7528\u6237\u8BBF\u95EE\u63A7\u5236</h1><h2 id="\u9700\u6C42" tabindex="-1"><a class="header-anchor" href="#\u9700\u6C42" aria-hidden="true">#</a> \u9700\u6C42</h2><p>\u516C\u53F8\u7684\u4EA7\u54C1\u6587\u6863, \u6211\u901A\u8FC7 markdown \u8FDB\u884C\u64B0\u5199, \u5E76\u4F7F\u7528 mdwiki \u6765\u67E5\u770B\u548C\u7BA1\u7406.</p><p>\u800C\u4E00\u76F4\u4EE5\u6765, \u4E5F\u662F\u901A\u8FC7\u5E03\u5728\u516C\u7F51\u4E0A\u7684\u4E00\u5957 mdwiki \u53D1\u5E03\u7ED9\u5F00\u53D1\u540C\u4E8B\u770B.</p><p>\u56E0\u4E3A mdwiki \u662F\u4E00\u4E2A\u7EAF\u9759\u6001 wiki, \u4E5F\u6CA1\u6709\u4EC0\u4E48\u8BBF\u95EE\u63A7\u5236\u7684\u9AD8\u7EA7\u529F\u80FD. \u800C\u4E14\u6211\u4EEC\u7684\u4EA7\u54C1\u4E5F\u5927\u4F53\u4E0A\u5904\u4E8E\u521D\u671F, \u6240\u4EE5, \u4E5F\u5C31\u655E\u5F00\u4E86\u653E\u5728\u516C\u7F51\u4E0A, \u6CA1\u6709\u505A\u4EC0\u4E48\u63A7\u5236.</p><p>\u540E\u6765\u9886\u5BFC\u4E86\u89E3\u6B64\u4E8B\u540E, \u8981\u6C42\u628A\u516C\u7F51\u7684 wiki \u4E0B\u4E86, \u4E3B\u8981\u8003\u8651\u7684\u5C31\u662F\u8BBF\u95EE\u63A7\u5236\u7684\u95EE\u9898.</p><p>\u4E3A\u4E86\u4EE5\u540E\u53EF\u4EE5\u7EE7\u7EED\u4F7F\u7528 mdwiki \u53D1\u5E03\u6587\u6863, \u6211\u5F00\u59CB\u5BFB\u627E\u8BBF\u95EE\u63A7\u5236\u7684\u65B9\u6848. \u540E\u6765\u53D1\u73B0 apache \u672C\u8EAB\u5C31\u6709\u8FD9\u4E2A\u529F\u80FD. \u8BBE\u7F6E\u4E00\u4E0B\u5C31\u53EF\u4EE5\u7528.</p><h2 id="ubuntu-\u4E0A\u7684-apache2-\u8BBF\u95EE\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#ubuntu-\u4E0A\u7684-apache2-\u8BBF\u95EE\u63A7\u5236" aria-hidden="true">#</a> ubuntu \u4E0A\u7684 apache2 \u8BBF\u95EE\u63A7\u5236</h2><h3 id="\u914D\u7F6E\u914D\u7F6E\u6587\u4EF6" tabindex="-1"><a class="header-anchor" href="#\u914D\u7F6E\u914D\u7F6E\u6587\u4EF6" aria-hidden="true">#</a> \u914D\u7F6E\u914D\u7F6E\u6587\u4EF6</h3><p>\u56E0\u4E3A\u6211\u7684\u672C\u5730\u73AF\u5883\u662F <code>ubuntu 18</code>, \u516C\u7F51\u73AF\u5883\u662F <code>ubuntu 16</code>, \u6240\u4EE5\u8FD9\u91CC\u8BF4\u7684\u8BBF\u95EE\u63A7\u5236\u8BBE\u7F6E\u4E3B\u8981\u662F\u8FD9\u4E2A\u73AF\u5883\u7684\u8BE5\u95EE\u63A7\u5236.</p><p><code>ubuntu 18</code> \u4E0E <code>ubuntu 16</code> \u7684 <code>apache2</code> \u914D\u7F6E\u6587\u4EF6\u7684\u76EE\u5F55\u662F <code>/etc/apache2</code>. \u4E3B\u8981\u7684\u914D\u7F6E\u6587\u4EF6\u662F <code>/etc/apache2/apache2.conf</code>.</p><p>\u6253\u5F00 <code>apache2.conf</code> \u6587\u4EF6, \u627E\u5230\u4EE5 <code>&lt;Directory \u8DEF\u5F84\u540D&gt; &lt;/Directory&gt;</code> \u5305\u56F4\u7684\u914D\u7F6E\u9879. \u901A\u5E38\u5728 160 \u5DE6\u53F3.</p><p><code>&lt;Directory /var/www/html/test&gt;</code> \u4E2D\u7684\u8DEF\u5F84 <code>/var/www/html/test</code> \u662F\u8981\u8BBE\u7F6E\u8BBF\u95EE\u63A7\u5236\u7684\u8DEF\u5F84.</p><p><code>Options</code> \u548C <code>AllowOverride</code> \u662F\u5E72\u4EC0\u4E48\u7684, \u4E0D\u6E05\u695A. \u4F46\u53CD\u6B63\u6211\u662F\u7167\u9ED8\u8BA4\u7684\u590D\u5236\u8FC7\u6765\u4E86.</p><p><code>authtype</code> \u662F\u8BA4\u8BC1\u7684\u7C7B\u578B. \u8FD9\u91CC\u586B <code>Basic</code>.</p><p><code>authname</code> \u662F\u5145\u8BB8\u54EA\u4E2A\u7528\u6237\u767B\u5F55. \u8FD9\u4E2A\u7528\u6237, \u5FC5\u987B\u662F\u4E0B\u9762\u7528\u6237\u6587\u4EF6\u4E2D\u7684\u7528\u6237.</p><p><code>authuserfile</code> \u662F\u7528\u6237\u6587\u4EF6. \u91CC\u8FB9\u4EE5 <code>\u7528\u6237\u540D:\u5BC6\u7801\u5BC6\u6587</code> \u7684\u5F62\u5F0F\u8BB0\u7740\u7528\u6237\u4FE1\u606F.</p><p><code>Require valid-user</code> \u662F\u5F00\u542F\u7528\u6237\u8EAB\u4EFD\u9A8C\u8BC1.</p><p>\u8BBE\u7F6E\u597D\u4EE5\u4E0B\u5185\u5BB9, \u5C31\u53EF\u4EE5\u9000\u51FA\u4E86.</p><h3 id="\u751F\u6210\u7528\u6237\u6587\u4EF6" tabindex="-1"><a class="header-anchor" href="#\u751F\u6210\u7528\u6237\u6587\u4EF6" aria-hidden="true">#</a> \u751F\u6210\u7528\u6237\u6587\u4EF6</h3><p>\u901A\u8FC7\u4EE5\u4E0B\u547D\u4EE4\u53EF\u4EE5\u751F\u6210\u7528\u6237\u6587\u4EF6</p><pre><code>    htpasswd -c /etc/apache2/user \u7528\u6237\u540D
</code></pre><p>\u5176\u4E2D <code>htpasswd</code> \u662F\u521B\u5EFA apache \u7528\u6237\u7684\u547D\u4EE4, <code>-c</code> \u9009\u9879\u8868\u793A\u8981\u521B\u5EFA\u4E00\u4E2A\u7528\u6237\u6587\u4EF6, \u5982\u679C\u6587\u4EF6\u5B58\u5728\u5C31\u91CD\u65B0\u5199\u5165\u5185\u5BB9.</p><p>warring: \u5982\u679C\u91CD\u65B0\u5199\u5165, \u4F1A\u590D\u76D6\u539F\u6709\u5185\u5BB9. \u4E5F\u5C31\u662F\u8BF4, \u5982\u679C\u60F3\u8981\u6DFB\u52A0\u4E00\u4E2A\u7528\u6237, \u800C\u4E0D\u662F\u521B\u5EFA\u4E00\u4E2A\u6587\u4EF6, \u8BF7\u4E0D\u8981\u5E26 <code>-c</code> \u9009\u9879.</p><p><code>/etc/apache2/user</code> \u662F\u6587\u4EF6\u8DEF\u5F84. \u6CE8\u610F\u8FD9\u4E2A\u6587\u4EF6\u8DEF\u5F84\u8981\u548C <code>apache2.conf</code> \u4E2D <code>authuserfile</code> \u4E00\u81F4.</p><p>\u6700\u540E\u8DDF\u7684\u662F\u7528\u6237\u540D, \u5C31\u662F\u8981\u521B\u5EFA\u7684\u7528\u6237\u540D.</p><p>\u952E\u5165\u8FD9\u4E2A\u540D\u4EE4\u4E4B\u540E, \u8F93\u5165\u4E24\u6B21\u5BC6\u7801, \u6587\u4EF6\u5C31\u521B\u5EFA\u6210\u529F\u4E86.</p><p><code>cat</code> \u8BE5\u6587\u4EF6\u53EF\u4EE5\u770B\u5230\u6DFB\u52A0\u7684\u7528\u6237.</p><p>\u800C\u540E\u7528 <code>/etc/init.d/apache2 restart</code> \u547D\u4EE4\u91CD\u542F <code>apache2</code> \u670D\u52A1\u5C31\u53EF\u4EE5\u4E86.</p><h2 id="\u8E29\u8FC7\u7684\u5751" tabindex="-1"><a class="header-anchor" href="#\u8E29\u8FC7\u7684\u5751" aria-hidden="true">#</a> \u8E29\u8FC7\u7684\u5751</h2><p>\u4E00\u5B9A\u8981\u5E26\u4E0A sudo \u554A! \u65E0\u8BBA\u662F\u6539\u914D\u7F6E\u6587\u4EF6, \u8FD8\u662F\u521B\u5EFA\u7528\u6237\u6587\u4EF6.</p><hr><p>\u6539\u914D\u7F6E\u6587\u4EF6\u7684\u65F6\u5019, \u6700\u597D\u662F\u4ECE\u4E0A\u9762\u590D\u5236\u4E00\u4E2A\u8FC7\u6765. \u6309\u9700\u6DFB\u52A0\u548C\u4FEE\u6539\u4E0D\u540C\u7684. \u5176\u5B83\u7684\u80FD\u4E0D\u52A8\u5C31\u4E0D\u80FD.</p><p>\u5982\u679C\u914D\u7F6E\u6587\u4EF6\u6539\u9519\u4E86, \u53EF\u80FD\u4F1A\u51FA\u73B0 apache2 \u670D\u52A1\u91CD\u542F\u5931\u8D25\u7684\u60C5\u51B5.</p><p>\u5982\u679C\u51FA\u73B0\u8FD9\u6837\u7684\u60C5\u51B5, \u5C31\u4E00\u4E2A\u914D\u7F6E\u914D\u7F6E\u7684\u8BD5\u5427.</p><hr><p>\u636E\u8BF4\u6539 <code>sites-enabled/000-default.conf </code> \u8FD9\u4E2A\u6587\u4EF6\u4E5F\u53EF\u914D\u7F6E\u8BE5\u95EE\u63A7\u5236, \u6CA1\u8BD5\u8FC7.</p>`,37),t=[p];function h(r,u){return c(),a("div",null,t)}var n=e(o,[["render",h],["__file","\u5728ubuntu\u4E0A\u8BBE\u7F6Eapache2\u7684\u7528\u6237\u8BBF\u95EE\u63A7\u5236.html.vue"]]);export{n as default};
