import{_ as p,o as e,c as a,e as t}from"./app.6bf529a6.js";const o={},d=t('<h1 id="\u8FC7\u8DEF\u541B\u5B50\u8BFB\u4E09\u904D-\u4E00\u89C9\u7761\u5230\u5929\u5149\u4EAE" tabindex="-1"><a class="header-anchor" href="#\u8FC7\u8DEF\u541B\u5B50\u8BFB\u4E09\u904D-\u4E00\u89C9\u7761\u5230\u5929\u5149\u4EAE" aria-hidden="true">#</a> \u8FC7\u8DEF\u541B\u5B50\u8BFB\u4E09\u904D, \u4E00\u89C9\u7761\u5230\u5929\u5149\u4EAE</h1><h2 id="\u4E00-\u54ED\u5E16" tabindex="-1"><a class="header-anchor" href="#\u4E00-\u54ED\u5E16" aria-hidden="true">#</a> \u4E00 \u54ED\u5E16</h2><blockquote><p>\u5929\u60F6\u60F6, \u5730\u60F6\u60F6; \u6211\u5BB6\u6709\u4E2A\u591C\u54ED\uF92C; \u8FC7\u8DEF\u541B\u5B50\u8BFB\u4E09\u904D; \u4E00\u89C9\u7761\u5230\u5230\u5149\u4EAE.</p></blockquote><p>\u8FD9\u53EB <code>\u54ED\u5E16</code>.</p><p>\u636E\u8BF4\u5C0F\u5B69\u773C\u5E72\u51C0, \u80FD\u770B\u89C1\u4E0D\u5E72\u51C0\u7684\u4E1C\u897F. \u6240\u4EE5\u5076\u5C14\u5C0F\u5B69\u770B\u5230\u4E0D\u8BE5\u770B\u5230\u7684\u4E1C\u897F, \u5C31\u4F1A\u72AF\u591C\u60CA\u6014, \u5F7B\u591C\u557C\u54ED.</p><p>\u89E3\u51B3\u7684\u529E\u6CD5, \u5C31\u662F\u62FF\u5F20\u7EB8\u6765, \u5199\u4E0A <code>\u54ED\u5E16</code>, \u5F20\u5E16\u5728\u8DEF\u505C\u7535\u6746\u6216\u6811\u6728\u4E0A. \u8FC7\u5F80\u884C\u4EBA\u770B\u5230, \u5FF5\u4E0A\u4E00\u5FF5, \u4FBF\u5982\u5B5F\u5B50\u6240\u8BF4\u7684 &quot;\u96C6\u4E49\u751F\u6C14&quot; \u4E00\u822C, \u6C47\u96C6\u8FC7\u8DEF\u541B\u5B50\u7684\u610F\u5FF5, \u751F\u51FA\u6D69\u7136\u6B63\u6C14\u6765, \u9A71\u6563\u5B69\u5B50\u6240\u89C1\u90AA\u79FD. \u4E8E\u662F\u5B69\u5B50\u7684\u591C\u6014\u4FBF\u4F1A\u6CBB\u597D.</p><p>\u6211\u5C0F\u65F6\u5019, \u4E5F\u6709\u5F7B\u591C\u557C\u54ED\u7684\u6BDB\u75C5. \u6211\u5988\u548C\u5916\u5A46\u4E5F\u5728\u8DEF\u8FB9\u7684\u8D34\u8FC7\u54ED\u5E16. \u4F46\u8FD9\u6CD5\u5B50\u7EC8\u7A76\u4E0D\u8FC7\u662F\u60E8\u6742\u4E86\u8FF7\u4FE1\u7684\u6D6A\u6F2B\u60F3\u8C61. \u6700\u540E, \u8FD8\u662F\u770B\u4E86\u513F\u79D1, \u53D1\u73B0\u662F\u751F\u5929\u7684\u813E\u80C3\u4E0A\u706B, \u5403\u4E86\u51E0\u526F\u836F\u624D\u597D.</p><h2 id="\u4E8C-\u5173\u95E8" tabindex="-1"><a class="header-anchor" href="#\u4E8C-\u5173\u95E8" aria-hidden="true">#</a> \u4E8C \u5173\u95E8</h2><p>\u6211\u505A\u4EA7\u54C1\u7ECF\u7406\u505A\u7684\u7B2C\u4E00\u4E2A\u9879\u76EE\u5728\u6D77\u5357\u7684\u6D77\u53E3. \u56E0\u4E3A\u8981\u9A7B\u573A\u5F00\u53D1, \u9879\u76EE\u7EC4\u5341\u6765\u4E2A\u4EBA\u90FD\u5728\u6D77\u53E3\u51FA\u5DEE.</p><p>\u6709\u4E00\u5929, \u8BBE\u8BA1\u59B9\u5B50\u8EAB\u4F53\u4E0D\u8212\u670D, \u8BF7\u5047\u6CA1\u6709\u53BB\u5F00\u53D1\u73B0\u573A. \u4F5C\u4E3A\u4EA7\u54C1\u7ECF\u7406, \u6211\u89C9\u5F97\u6211\u6709\u8D23\u4EFB\u53BB\u5979\u4F4F\u7684\u9152\u5E97\u4EBA\u6587\u5173\u6000\u4E00\u4E0B.</p><p>\u5F53\u65F6\u6B63\u503C\u6B63\u5348, \u8273\u9633\u9AD8\u7167. \u4E0D, \u5BF9\u4E8E\u6D77\u53E3\u7684\u6B63\u5348, \u5E94\u8BE5\u53EB\u6BD2\u65E5\u5F53\u7A7A.</p><p>\u5F53\u6211\u8FDB\u4E86\u8BBE\u8BA1\u59B9\u5B50\u5F00\u4E86\u7A7A\u8C03\u7684\u5BA2\u623F\u65F6, \u6E05\u51C9\u7684\u5BA4\u6E29\u8BA9\u6211\u89C9\u5F97\u597D\u8C61\u4ECE\u5730\u72F1\u91CC\u91CD\u65B0\u6D3B\u8FC7\u6765\u4E00\u6837.</p><p>\u4E8E\u662F\u4E00\u8FDB\u5C4B, \u6211\u5C31\u628A\u623F\u95E8\u5173\u4E0A\u4E86.</p><p>\u8BBE\u8BA1\u59B9\u5B50\u7684\u8138\u8272\u96BE\u6781\u4E86. \u5F53\u65F6\u6211\u53EA\u89C9\u5F97, \u5979\u53EF\u4EE5\u771F\u7684\u8EAB\u4F53\u5F88\u4E0D\u8212\u670D. \u5979\u8D70\u5230\u95E8\u8FB9, \u628A\u95E8\u5F00.</p><p>\u5F53\u65F6\u6211\u5E76\u6CA1\u6709\u53CD\u5E94\u8FC7\u6765, \u53EA\u662F\u89C9\u5F97\u70ED\u6D6A\u4ECE\u95E8\u5916\u6D8C\u5165, \u4E8E\u662F\u7ADF\u7136\u561F\u56D4\u7740 &quot;\u4E0D\u70ED\u4E48?&quot; \u53C8\u628A\u95E8\u7ED9\u5173\u4E0A\u4E86.</p><p>\u8BBE\u8BA1\u59B9\u5B50\u8138\u8272\u66F4\u96BE\u770B\u4E86. \u5979\u6C14\u9F13\u9F13\u7684\u518D\u6B21\u628A\u95E8\u6253\u5F00.</p><p>\u597D\u5728\u6211\u867D\u7136\u611A\u7B28, \u4F46\u5E76\u6CA1\u6709\u7B28\u5230\u5BB6, \u7EC8\u4E8E\u4E5F\u77E5\u9053\u4E0D\u5E94\u8BE5\u518D\u628A\u95E8\u5173\u4E0A.</p><p>\u8FC7\u4E86\u5927\u6982\u4E00\u4E24\u5E74\u4E4B\u540E, \u6211\u624D\u77E5\u9053, \u4E0E\u5973\u540C\u4E8B\u5728\u9152\u5E97\u91CC\u72EC\u5904, \u628A\u95E8\u5173\u4E0A\u662F\u4E00\u4EF6\u591A\u4E0D\u5F97\u4F53\u7684\u4E8B\u60C5.</p><h2 id="\u4E09-\u6B67\u89C6\u662F\u900F\u660E\u7684" tabindex="-1"><a class="header-anchor" href="#\u4E09-\u6B67\u89C6\u662F\u900F\u660E\u7684" aria-hidden="true">#</a> \u4E09 \u6B67\u89C6\u662F\u900F\u660E\u7684</h2><p>\u8BB0\u5F97\u5728\u540D\u4E3A <code>\u5229\u7EF4\u5766</code> \u7684\u516C\u4F17\u53F7\u4E0A, \u770B\u5230\u8FC7\u4E00\u7BC7\u5173\u4E8E\u6B67\u89C6\u7684\u6587\u7AE0, \u8BF4\u6B67\u89C6\u662F\u900F\u660E\u7684.</p><p>\u539F\u6587\u5B9E\u5728\u627E\u4E0D\u5230\u4E86, \u4F46\u5927\u6982\u7684\u610F\u601D\u8FD8\u8BB0\u5F97. \u662F\u8BF4\u53EA\u6709\u8EAB\u53D7\u6B67\u89C6\u7684\u4EBA, \u624D\u80FD\u771F\u6B63\u611F\u53D7\u5230\u6B67\u89C6\u7684\u5B58\u5728. \u6587\u7AE0\u4E3E\u4E86\u4F8B\u5B50, \u53EA\u6709\u9ED1\u4EBA\u624D\u80FD\u771F\u6B63\u7406\u89E3\u79CD\u65CF\u6B67\u89C6, \u53EA\u6709\u5973\u6027\u624D\u80FD\u771F\u6B63\u7406\u89E3\u6027\u522B\u6B67\u89C6; \u4F5C\u8005\u662F\u4E00\u4E2A\u767D\u4EBA\u5973\u6027, \u5979\u63D0\u5230\u81EA\u5DF1\u91C7\u8BBF\u4E00\u4F4D\u9ED1\u4EBA\u5973\u6027\u65F6\u8BF4: \u5979\u672C\u4EE5\u4E3A\u81EA\u5DF2\u4F5C\u4E3A\u5973\u6027\u56E0\u8BE5\u80FD\u5BF9\u91C7\u8BBF\u5BF9\u8C61\u4EA7\u751F\u540C\u60C5, \u4F46\u7ED3\u679C\u53D1\u73B0, \u5979\u5B8C\u5168\u65E0\u6CD5\u771F\u6B63\u7406\u89E3\u4E00\u4F4D\u9ED1\u4EBA\u5973\u6027\u53D7\u5230\u6B67\u89C6.</p><p>\u6240\u4EE5\u5979\u7684\u7ED3\u8BBA\u662F: \u6B67\u89C6\u662F\u900F\u660E\u7684.</p><p>\u5BF9, \u6B67\u89C6\u662F\u900F\u660E\u7684. \u4F5C\u4E3A\u7537\u6027\u7684\u6211, \u662F\u5B8C\u5168\u4E0D\u80FD\u4ECE\u611F\u53D7\u4E0A\u7406\u89E3\u4E0E\u5F02\u6027\u540C\u5904\u4E00\u4E2A\u5BC6\u95ED\u573A\u6240\u7684\u611F\u53D7\u7684. \u8FD9\u4E2A\u4E8B\u5B9E\u51FA\u5356\u5728 &quot;\u5F97\u4F53&quot; \u4E00\u8BCD\u4E0A.</p><p>\u6211\u4EEC\u901A\u5E38\u8FD9\u6837\u4F7F\u7528 &quot;\u5F97\u4F53&quot; \u4E00\u8BCD: \u6BD4\u5982\u6211\u4EEC\u7A7F\u7740\u62D6\u978B\u53BB\u4E86\u67D0\u4E2A\u6B63\u5E38\u7684\u573A\u6240, \u6211\u4EEC\u4F1A\u8BF4\u8FD9\u6837\u7A7F\u7740\u662F\u4E0D\u5F97\u4F53\u7684; \u6216\u8005\u6211\u4EEC\u5728\u9519\u8BEF\u7684\u573A\u7684\u573A\u5408\u8BB2\u4E86\u4E00\u4E2A\u4E0D\u5408\u5B9C\u7684\u8E69\u811A\u7B11\u8BDD.</p><p>\u5F53\u6211\u8BA4\u4E3A\u8FD9\u4EF6\u4E8B\u4EC5\u4EC5\u662F &quot;\u4E0D\u5F97\u4F53&quot; \u65F6, \u6211\u662F\u5728\u60F3: \u5373\u7136\u6211\u5F53\u65F6\u5E76\u65E0\u4EFB\u4F55\u90AA\u5FF5, \u90A3\u4E48\u6211\u5F53\u65F6\u7684\u505A\u6CD5\u4EC5\u4EC5\u662F\u4E00\u79CD\u4E0D\u597D\u7684\u59FF\u6001, \u6216\u8005\u9876\u591A\u662F\u4E00\u79CD\u4E0D\u793C\u8C8C\u7684\u5192\u72AF; \u603B\u5F52\u662F\u4EF6\u65E0\u5173\u7D27\u8981\u7684\u5C0F\u4E8B.</p><p>\u662F\u7684, \u5BF9\u4E8E\u5F53\u65F6\u5E76\u6CA1\u6709\u4EFB\u4F55\u6076\u610F\u7684\u6211\u6765\u8BF4, \u8FD9\u6837\u4E00\u4EF6\u4E8B, \u8FD8\u80FD\u610F\u5473\u7740\u4EC0\u4E48\u5462?</p><p>\u4E8E\u662F, \u8FD9\u4EF6\u4E2D\u7684\u6B67\u89C6\u610F\u5473, \u5C31\u5728\u6211\u773C\u4E2D &quot;\u900F\u660E&quot; \u4E86.</p><p>\u7136\u800C, \u6B67\u89C6\u662F\u900F\u660E\u7684, \u4E0D\u4EE3\u8868\u6B67\u89C6\u5C31\u4E0D\u5B58\u5728. \u8FD9\u6837\u4E00\u4EF6\u5C0F\u4E8B, \u4ECE\u5973\u6027\u7684\u89D2\u5EA6\u6765\u770B\u5219\u5B8C\u5168\u662F\u53E6\u4E00\u4E2A\u6837\u5B50.</p><h2 id="\u4E09-\u4E0D\u5BF9\u7B49\u7684\u4E0D\u786E\u5B9A\u6027" tabindex="-1"><a class="header-anchor" href="#\u4E09-\u4E0D\u5BF9\u7B49\u7684\u4E0D\u786E\u5B9A\u6027" aria-hidden="true">#</a> \u4E09 \u4E0D\u5BF9\u7B49\u7684\u4E0D\u786E\u5B9A\u6027</h2><p>\u90A3\u4E48, \u4E3A\u4EC0\u4E48\u7537\u6027\u4E0E\u5973\u6027\u5728\u5BC6\u95ED\u73AF\u5883\u4E0B\u72EC\u5904, \u4E0D\u80FD\u5173\u4E0A\u95E8\u5462?</p><p>\u56E0\u4E3A\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B, \u5982\u679C\u7537\u6027\u5BF9\u5973\u6027\u8FDB\u884C\u4FB5\u72AF, \u5173\u95ED\u7684\u623F\u95E8\u4F1A\u5BF9\u5973\u6027\u7684\u9003\u79BB\u7684\u963B\u788D.</p><p>\u90A3\u4E48\u662F\u4E0D\u662F\u5982\u679C\u8FD9\u4E2A\u7537\u6027, \u5982\u6211\u4E00\u6837\u6CA1\u6709\u4FB5\u72AF\u5973\u6027\u7684\u6076\u610F, \u5173\u4E0D\u5173\u623F\u95E8\u5C31\u53D8\u6210\u4E00\u4EF6\u65E0\u5173\u7D27\u8981\u7684\u5C0F\u4E8B\u5462?</p><p>\u5F53\u7136\u4E0D\u662F. \u56E0\u4E3A\u867D\u7136\u5BF9\u4E8E\u6211\u6765\u8BF4, \u4E0D\u53D1\u751F\u4FB5\u72AF\u884C\u4E3A\u662F\u786E\u5B9A\u7684, \u4F46\u5BF9\u4E8E\u540C\u5BA4\u7684\u5973\u6027\u6765\u8BF4, \u5374\u662F\u4E0D\u786E\u5B9A\u7684. \u5979\u53C8\u4E0D\u77E5\u9053\u6211\u7684\u60F3\u6CD5. \u800C\u4E14\u5BF9\u4E8E\u5979\u6765\u8BF4, \u5979\u751A\u81F3\u65E0\u4ECE\u5224\u65AD\u6211\u662F\u5426\u6709\u4E3B\u89C2\u7684\u6076\u610F.u \u56FA\u7136\u5BF9\u6211\u6765\u8BF4, \u5728\u4EFB\u4F55\u4E00\u79CD\u60C5\u51B5\u4E0B, \u5F53\u6211\u9762\u5BF9\u4EFB\u4F55\u4E0D\u7279\u5B9A\u7684\u5973\u6027\u65F6, \u90FD\u786E\u5B9A\u7684\u4E0D\u4F1A\u53D1\u751F\u4FB5\u72AF\u7684\u884C\u4E3A; \u4F46\u56E0\u4E3A\u7537\u6027\u7684\u5929\u7136\u4F18\u52BF, \u53D1\u8D77\u4FB5\u72AF\u7684\u4E3B\u52A8\u6743\u5728\u7537\u6027\u624B\u4E2D, \u5728\u4EFB\u4F55\u4E00\u79CD\u60C5\u8A4B\u4E0B, \u5F53\u5973\u6027\u9762\u5BF9\u4EFB\u4F55\u4E0D\u7279\u5B9A\u7684\u7684\u7537\u6027\u65F6, \u662F\u5426\u53D1\u751F\u4FB5\u72AF\u5374\u90FD\u662F\u4E0D\u786E\u5B9A\u7684.</p><p>\u800C\u5982\u679C\u6211\u4EEC\u8BF4, &quot;\u53EA\u6709\u5F53\u7537\u6027\u6709\u4FB5\u72AF\u610F\u56FE\u65F6, \u5173\u95E8\u624D\u662F\u5FC5\u8981\u7684&quot; \u5176\u5B9E\u628A\u662F\u5426\u53D1\u884C\u4FB5\u72AF\u7684\u4E0D\u786E\u5B9A\u6027\u98CE\u9669\u5B8C\u5168\u538B\u5728\u5973\u6027\u8EAB\u4E0A.</p><p>\u56E0\u6B64, \u4E0D\u8BBA\u7537\u6027\u662F\u5426\u6709\u4FB5\u72AF\u7684\u6076\u610F, \u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B, \u90FD\u5E94\u8BE5\u4FDD\u6301\u623F\u95E8\u7684\u655E\u5F00.</p>',35),r=[d];function h(c,i){return e(),a("div",null,r)}var u=p(o,[["render",h],["__file","\u591C\u54ED\uF92C.html.vue"]]);export{u as default};
