import{_ as l,o as i,c as e,e as o}from"./app.efeddd24.js";const t={},u=o('<h1 id="\u5927\u5B66\u7AE0\u53E5\u5E8F" tabindex="-1"><a class="header-anchor" href="#\u5927\u5B66\u7AE0\u53E5\u5E8F" aria-hidden="true">#</a> \u5927\u5B66\u7AE0\u53E5\u5E8F</h1><blockquote><p>\u5927\u5B66\u4E4B\u4E66, \u53E4\u4E4B\u5927\u5B66\u6240\u4EE5\u6559\u4EBA\u4E4B\u6CD5\u4E5F. \u76D6\u81EA\u5929\u964D\u751F\u6C11, \u5219\u65E2\u83AB\u4E0D\u4E0E\u4E4B\u4EE5\u4EC1\u4E49\u793C\u667A\u4E4B\u6027\u77E3. \u7136\u5176\u6C14\u8D28\u4E4B\u7A1F\u6216\u4E0D\u80FD\u9F50, \u662F\u4EE5\u4E0D\u80FD\u7686\u6709\u4EE5\u77E5\u5176\u6027\u4E4B\u6240\u6709\u800C\u5168\u4E4B\u4E5F. \u4E00\u6709\u806A\u660E\u777F\u667A\u80FD\u5C3D\u5176\u6027\u8005\u51FA\u4E8E\u5176\u95F4, \u5219\u5929\u5FC5\u547D\u4E4B\u4EE5\u4E3A\u4EBF\u5146\u4E4B\u541B\u5E08, \u4F7F\u4E4B\u51B6\u800C\u6559\u4E4B, \u4EE5\u590D\u5176\u6027. \u6B64\u4F0F\u7FB2, \u795E\u519C, \u9EC4\u5E1D, \u5C27, \u821C, \u6240\u4EE5\u7EE7\u5929\u7ACB\u6781, \u800C\u53F8\u5F92\u4E4B\u804C, \u5178\u4E50\u4E4B\u5B98\u6240\u7531\u8BBE\u4E5F.</p></blockquote><ul><li>\u672C\u6BB5\u6240\u5199\u5185\u5BB9\u4E3A: \u5927\u5B66\u4E4B\u540D\u7684\u7531\u6765\u53CA\u653F\u6CBB\u7684\u610F\u4E49.</li><li>\u4EBA\u7684\u5929\u6027\u53CA\u4EBA\u7684\u5929\u6027\u56E0\u4E2A\u6027\u800C\u4E0D\u5F70;</li><li>\u9053\u5FB7\u8D85\u4EBA\u7684\u6743\u529B\u8D77\u6E90\u53CA\u5176\u6559\u5316\u610F\u4E49: \u653F\u6CBB\u7684\u6559\u5316\u610F\u4E49\u548C\u5B98\u804C\u7684\u8D77\u6E90.</li></ul><blockquote><p>\u4E09\u4EE3\u4E4B\u9686, \u5176\u6CD5\u6D78\u5907, \u7136\u540E\u738B\u5BAB, \u56FD\u90FD\u4EE5\u53CA\u95FE\u5DF7, \u83AB\u4E0D\u6709\u5B66, \u4EBA\u751F\u516B\u5C81, \u5219\u81EA\u738B\u516C\u4EE5\u4E0B, \u81F3\u4E8E\u5EB6\u4EBA\u4E4B\u5B50\u5F1F, \u7686\u5165\u5C0F\u5B66, \u800C\u6559\u4E4B\u4EE5\u6492\u626B, \u5E94\u5BF9, \u8FDB\u9000\u4E4B\u8282, \u793C\u4E50, \u5C04\u5FA1, \u4E66\u6570\u4E4B\u6587; \u53CA\u5341\u6709\u4E94\u5E74, \u5219\u5929\u5B50\u4E4B\u5143\u5B50, \u4F17\u5B50, \u4EE5\u81F3\u516C, \u537F, \u5927\u592B, \u5143\u5B50\u4E4B\u9002\u5B50, \u4E0E\u51E1\u6C11\u4E4B\u4FCA\u79C0, \u7686\u5165\u5927\u5B66, \u800C\u6559\u4E4B\u4EE5\u7A77\u7406, \u6B63\u5FC3, \u4FEE\u5DF1, \u6CBB\u4EBA\u4E4B\u9053. \u6B64\u53C8\u5B66\u6821\u4E4B\u6559, \u5927\u5C0F\u4E4B\u8282\u6240\u4EE5\u5206\u4E5F.</p></blockquote><ul><li>\u4E09\u4EE3\u7684\u60C5\u51B5, \u5C0F\u5B66\u4E0E\u5927\u6D53\u7684\u533A\u5206</li><li>\u5C0F\u5B66\u7684\u5185\u5BB9\u591A\u4E3A\u6280\u672F\u6027, \u77E5\u8BC6\u6027\u7684.</li><li>\u5927\u5BB6\u4E0D\u4EC5\u662F\u5E74\u9F84\u4E0A\u7684, \u540C\u65F6\u4E5F\u662F\u8EAB\u4EFD\u548C\u80FD\u529B\u4E0A\u7684.</li></ul><blockquote><p>\u592B\u5B66\u6821\u4E4B\u8BBE, \u5176\u5E7F\u5982\u6B64, \u6559\u4E4B\u4E4B\u672F, \u5176\u6B21\u9012\u8282\u76EE\u4E4B\u8BE6\u53C8\u5982\u6B64. \u800C\u5176\u6240\u4EE5\u4E3A\u6559, \u5219\u53C8\u672C\u4E4B\u4EBA\u541B\u8EAC\u884C\u5FC3\u5F97\u4E4B\u4F59, \u4E0D\u5F85\u6C42\u4E4B\u6C11\u751F\u65E5\u7528\u5F5D\u4F26\u4E4B\u5916. \u662F\u4EE5\u5F53\u4E16\u4E4B\u4EBA\u65E0\u4E0D\u5B66, \u5176\u5B66\u7109\u8005, \u65E0\u6709\u4EE5\u77E5\u5176\u6027\u5206\u4E4B\u6240\u56E0\u6709, \u804C\u5206\u4E4B\u6240\u5F53\u4E3A, \u800C\u5404\u4FDB\u7109\u4EE5\u5C3D\u5176\u529B. \u6B64\u53E4\u6614\u76DB\u65F6\u6240\u4EE5\u6CBB\u9686\u4E8E\u4E0A, \u4FD7\u7F8E\u4E8E\u4E0B, \u800C\u975E\u540E\u4E16\u4E4B\u6240\u80FD\u53CA\u4E5F.</p></blockquote><ul><li>\u5BF9\u4E09\u4EE3\u7684\u8D5E\u7F8E. <ul><li>\u5B66\u4F55\u4EE5\u5E7F\u8303\u800C\u65E0\u6240\u4E0D\u5305\u7684\u539F\u56E0: <ul><li>\u53D7\u4F17\u591A</li><li>\u7ED3\u6784\u5168</li><li>\u4F5C\u7528\u57FA\u7840</li></ul></li><li>\u4E3A\u5B66\u662F\u77E5\u6027\u4E0E\u804C\u5206\u7684\u7EDF\u4E00.</li><li>\u4E09\u4EE3\u4E4B\u6240\u4EE5\u6210\u4E3A\u5178\u8303\u662F\u56E0\u4E3A\u5176\u653F\u6CBB\u6559\u5316\u4E0E\u9053\u5FB7\u7684\u5408\u4E00</li><li>\u4E3A\u5B66\u662F\u77E5\u6027\u4E0E\u804C\u5206\u7684\u7EDF\u4E00.</li></ul></li></ul><blockquote><p>\u53CA\u5468\u4E4B\u8870, \u8D24\u5723\u4E4B\u541B\u4E0D\u4F5C, \u5B66\u6821\u4E4B\u653F\u4E0D\u4FEE, \u6559\u5316\u9675\u5937, \u98CE\u4FD7\u9893\u8D25, \u65F6\u5219\u6709\u82E5\u5B54\u5B50\u4E4B\u5723, \u800C\u4E0D\u5F97\u4EE5\u541B\u5E08\u4E4B\u4F4D\u4EE5\u884C\u5176\u653F\u6559, \u4E8E\u662F\u72EC\u53D6\u5148\u738B\u4E4B\u6CD5, \u8BF5\u800C\u4F20\u4E4B\u4EE5\u8BCF\u540E\u4E16. \u82E5\u66F2\u793C, \u5C11\u4EEA, \u5185\u5219, \u5F1F\u5B50\u804C\u8BF8\u7BC7, \u56FA\u5C0F\u5B66\u4E4B\u652F\u6D41\u4F59\u88D4, \u800C\u6B64\u7BC7\u8005, \u5219\u56E0\u5C0F\u5B66\u4E4B\u6210\u529F, \u4EE5\u8457\u5927\u5B66\u4E4B\u660E\u6CD5, \u5916\u6709\u4EE5\u6781\u5176\u89C4\u6A21\u4E4B\u5927, \u800C\u5185\u6709\u4EE5\u5C3D\u5176\u8282\u76EE\u4E4B\u8BE6\u8005\u4E5F. \u4E09\u5343\u4E4B\u5F92, \u76D6\u83AB\u4E0D\u95FB\u5176\u8BF4, \u800C\u66FE\u6C0F\u4E4B\u4F20\u72EC\u5F97\u5176\u5B97, \u4E8E\u662F\u4F5C\u4E3A\u4F20\u4E49, \u4EE5\u53D1\u5176\u610F. \u53CA\u5B5F\u5B50\u6CA1\u800C\u5176\u4F20\u6CEF\u7109, \u5219\u5176\u4E66\u867D\u5B58\u800C\u77E5\u8005\u9C9C\u77E3.</p></blockquote><ul><li>\u5927\u5B66\u6210\u4E66\u7684\u539F\u56E0 <ul><li>\u5468\u7684\u8870\u843D\u540C\u6837\u662F\u56E0\u653F\u6CBB\u6559\u5316\u53CA\u9053\u5FB7\u7684\u5206\u79BB</li><li>\u5B54\u5B50\u4EE5\u5723\u4EBA\u4E4B\u8EAB\u800C\u4E0D\u5F97\u541B\u5E08\u4E4B\u4F4D, \u56E0\u6B64\u53EA\u80FD\u4EE5\u6587\u5316\u6D3B\u52A8\u4EE3\u884C\u6CBB\u4E4B\u529F.</li></ul></li></ul><blockquote><p>\u81EA\u662F\u4EE5\u6765, \u4FD7\u5112\u8BB0\u8BF5\u8BCD\u7AE0\u4E4B\u4E60, \u5176\u529F\u500D\u4E8E\u5C0F\u5B66\u800C\u65E0\u7528; \u5F02\u7AEF\u865A\u65E0\u5BC2\u706D\u4E4B\u6559, \u5176\u9AD8\u8FC7\u4E8E\u5927\u5B66\u800C\u65E0\u5B9E. \u5176\u4ED6\u6743\u8C0B\u672F\u6570, \u4E00\u5207\u4EE5\u5C31\u529F\u540D\u4E4B\u8BF4, \u4E0E\u592B\u767E\u5BB6\u4F17\u6280\u4E4B\u6D41, \u6240\u4EE5\u60D1\u4E16\u8BEC\u6C11, \u5145\u585E\u4EC1\u4E49\u8005, \u53C8\u7EB7\u6742\u51FA\u4E4E\u5176\u95F4. \u4F7F\u5176\u541B\u5B50\u4E0D\u5E78\u800C\u4E0D\u5F97\u95FB\u5927\u9053\u4E4B\u8981, \u5176\u5C0F\u4EBA\u4E0D\u5E78\u800C\u4E0D\u5F97\u8499\u81F3\u6CBB\u4E4B\u6CFD, \u6666\u76F2\u5426\u585E, \u53CD\u8986\u6C89\u75FC, \u4EE5\u53CA\u4E94\u5B63\u4E4B\u8870, \u800C\u574F\u4E71\u6781\u77E3\u3002</p></blockquote><blockquote><p>\u5929\u8FD0\u5FAA\u73AF, \u65E0\u5F80\u4E0D\u590D. \u5B8B\u5FB7\u9686\u76DB, \u6CBB\u6559\u4F11\u660E. \u4E8E\u662F\u6CB3\u5357\u7A0B\u6C0F\u4E24\u5B50\u51FA, \u800C\u6709\u4EE5\u63A5\u4E4E\u5B5F\u6C0F\u4E4B\u4F20. \u5B9E\u59CB\u5C0A\u4FE1\u6B64\u7BC7\u800C\u8868\u7AE0\u4E4B. \u5373\u4E3A\u4E4B\u6B21\u5176\u7B80\u7F16 \u53D1\u5176\u5F52\u8DA3, \u7136\u540E\u53E4\u8005\u5927\u5B66\u6559\u4EBA\u4E4B\u6CD5, \u5723\u7ECF\u8D24\u4F20\u4E4B\u6307, \u7CB2\u7136\u590D\u660E\u4E8E\u4E16. \u867D\u4EE5\u71B9\u4E4B\u4E0D\u654F, \u4EA6\u5E78\u79C1\u6DD1\u800C\u4E0E\u5176\u6709\u95FB\u7109. \u987E\u5176\u4E3A\u4E66\u72B9\u9887\u653E\u5931, \u662F\u4EE5\u5FD8\u5176\u56FA\u964B, \u91C7\u800C\u8F91\u4E4B, \u95F2\u4EA6\u7A83\u9644\u5DF2\u610F, \u8865\u5176\u9619\u7567, \u4EE5\u4FDF\u540E\u4E4B\u541B\u5B50, \u6781\u77E5\u50ED\u8E30, \u65E0\u6240\u9003\u7F6A, \u7136\u4E8E\u56FD\u5BB6\u5316\u6C11\u6210\u4FD7\u4E4B\u610F, \u5B66\u8005\u4FEE\u5DF1\u6CBB\u4EBA\u4E4B\u65B9, \u5219\u672A\u5FC5\u65E0\u5C0F\u8865\u4E91.</p></blockquote>',11),c=[u];function _(a,r){return i(),e("div",null,c)}var n=l(t,[["render",_],["__file","\u5927\u5B66\u7AE0\u53E5\u5E8F.html.vue"]]);export{n as default};
