import{_ as e,o as t,c as o,e as a}from"./app.6bf529a6.js";const c={},_=a('<h1 id="\u5982\u679C\u4ED6\u4EEC\u6CA1\u6709\u72B9\u8C6B" tabindex="-1"><a class="header-anchor" href="#\u5982\u679C\u4ED6\u4EEC\u6CA1\u6709\u72B9\u8C6B" aria-hidden="true">#</a> \u5982\u679C\u4ED6\u4EEC\u6CA1\u6709\u72B9\u8C6B</h1><h2 id="\u4E00\u79CD\u65B0\u7684\u5FD8\u6069" tabindex="-1"><a class="header-anchor" href="#\u4E00\u79CD\u65B0\u7684\u5FD8\u6069" aria-hidden="true">#</a> \u4E00\u79CD\u65B0\u7684\u5FD8\u6069</h2><p>\u5854\u52D2\u5E03\u5728\u300A\u9ED1\u5929\u9E45\u300B\u7684\u5E8F\u8A00\u91CC, \u505A\u4E86\u8FD9\u6837\u4E00\u4E2A\u601D\u60F3\u5B9E\u9A8C.</p><p>\u5047\u8BBE\u4E00\u540D\u5BCC\u6709\u52C7\u6C14, \u5F71\u54CD\u529B, \u667A\u6167, \u8FDC\u89C1\u548C\u6BC5\u529B\u7684\u7ACB\u6CD5\u8005\u6210\u529F\u9881\u5E03\u4E86\u4E00\u9879\u6CD5\u5F8B, \u5728 2001 \u5E74 9 \u6708 11 \u65E5\u666E\u904D\u751F\u6548\u548C\u5B9E\u65BD, \u6CD5\u5F8B\u5F3A\u8981\u6C42\u6BCF\u4E00\u4E2A\u98DE\u884C\u5458\u9A7E\u9A76\u8231\u5FC5\u987B\u5B89\u88C5\u9632\u5F39\u95E8\u5E76\u4E0A\u9501 ( \u7ED9\u6323\u624E\u4E2D\u7684\u822A\u7A7A\u516C\u53F8\u5E26\u6765\u5DE8\u5927\u6210\u672C ), \u4EE5\u9632\u6050\u6016\u4E3B\u4E49\u8005\u7528\u98DE\u673A\u88AD\u51FB\u4E16\u8D38\u4E2D\u5FC3.</p><p>\u4EBA\u4EEC\u4F1A\u56E0\u4E3A\u8FD9\u9879\u6CD5\u5F8B\u907F\u514D\u4E86 911 \u4E8B\u4EF6\u800C\u8D5E\u626C\u8FD9\u4F4D\u7ACB\u6CD5\u8005\u4E48?</p><p>\u5E76\u4E0D\u4F1A. \u56E0\u4E3A, \u5982\u679C\u8FD9\u9879\u6CD5\u5F8B\u5F97\u4EE5\u5B9E\u65BD, 911 \u8FD9\u573A\u707E\u96BE\u5C31\u4E0D\u4F1A\u53D1\u751F; \u5982\u679C\u8FD9\u573A\u707E\u96BE\u4E0D\u53D1\u751F, \u4EBA\u4EEC\u5C31\u610F\u8BC6\u4E0D\u5230\u8FD9\u9879\u6CD5\u5F8B\u4F7F\u4E00\u573A\u707E\u96BE\u5F97\u4EE5\u907F\u514D.</p><p>\u4ED6\u5F97\u5230\u7ED3\u679C, \u5F88\u6709\u53EF\u80FD\u662F:</p><blockquote><p>\u7531\u4E8E\u8BA4\u4E3A\u4ED6\u7684\u6CD5\u5F8B\u65E2\u591A\u4F59\u53C8\u6D6A\u8D39\u8D44\u6E90, \u516C\u4F17\u5728\u822A\u7A7A\u516C\u53F8\u98DE\u884C\u5458\u7684\u5E2E\u52A9\u4E0B, \u5F88\u53EF\u80FD\u628A\u4ED6\u8F70\u51FA\u529E\u516C\u5BA4. \u4ED6\u4F1A\u5728\u5DE8\u5927\u5931\u8D25\u611F\u4E2D\u6291\u90C1\u5730\u9000\u4F11, \u4ED6\u4F1A\u5728\u4E00\u4E8B\u65E0\u6210\u7684\u6CAE\u4E27\u4E2D\u6B7B\u53BB.</p></blockquote><p>\u751A\u81F3\u4ED6\u81EA\u5DF1\u90FD\u610F\u8BC6\u4E0D\u5230\u81EA\u5DF1\u7684\u727A\u7272\u907F\u514D\u4E86\u4E00\u573A\u707E\u96BE, \u62EF\u6551\u4E86\u65E0\u6570\u751F\u547D. \u4ED6\u53EA\u4F1A\u89C9\u5F97\u81EA\u5DF1\u5E72\u4E86\u4E00\u4EF6\u8822\u4E8B\u800C\u5DF2.</p><p>\u5854\u52D2\u5E03\u79F0\u8FD9\u79CD\u73B0\u8C61\u4E3A &quot;\u4E00\u79CD\u65B0\u7684\u5FD8\u6069&quot;, \u4ED6\u95EE\u9053:</p><blockquote><p>\u8C01\u5F97\u5230\u4E86\u5956\u8D4F? \u662F\u907F\u514D\u4E86\u7ECF\u6D4E\u8870\u9000\u7684\u4E2D\u592E\u94F6\u884C\u884C\u957F, \u8FD8\u662F &quot;\u7EA0\u6B63&quot; \u4E86\u524D\u4EFB\u7684\u9519\u8BEF, \u5E76\u6070\u597D\u5728\u67D0\u6B21\u590D\u82CF\u4E2D\u5728\u4F4D\u7684\u90A3\u4E2A\u4EBA? \u8C01\u66F4\u6709\u4EF7\u503C? \u662F\u907F\u514D\u4E86\u4E00\u573A\u6218\u4E89\u7684\u653F\u6CBB\u5BB6, \u8FD8\u662F\u53D1\u52A8\u4E86\u4E00\u573A\u65B0\u6218\u4E89 ( \u5E76\u4FA5\u5E78\u8D62 ) \u7684\u4EBA.</p></blockquote>',11),r=[_];function p(n,d){return t(),o("div",null,r)}var s=e(c,[["render",p],["__file","\u5982\u679C\u4ED6\u4EEC\u6CA1\u6709\u72B9\u8C6B.html.vue"]]);export{s as default};
