import{_ as p,o,c as t,e as u}from"./app.6bf529a6.js";const e={},q=u('<h1 id="\u5B50\u4F5C\u6625\u79CB\u800C\u4E71\u81E3\u8D3C\u5B50\u60E7" tabindex="-1"><a class="header-anchor" href="#\u5B50\u4F5C\u6625\u79CB\u800C\u4E71\u81E3\u8D3C\u5B50\u60E7" aria-hidden="true">#</a> \u5B50\u4F5C\u6625\u79CB\u800C\u4E71\u81E3\u8D3C\u5B50\u60E7</h1><h2 id="\u4E00" tabindex="-1"><a class="header-anchor" href="#\u4E00" aria-hidden="true">#</a> \u4E00</h2><p>\u5B5F\u5B50\u8BF4: \u5B50\u4F5C\u6625\u79CB\u800C\u4E71\u81E3\u8D3C\u5B50\u60E7.</p><p>\u63E1\u7740\u6743\u67C4\u7684\u4E71\u81E3\u8D3C\u5B50\u771F\u7684\u60E7\u6015\u4E86\u4E48? \u6211\u4E0D\u77E5\u9053. \u4F46\u5B5F\u5B50\u5374\u5DEE\u70B9\u88AB\u63E1\u7740\u6743\u67C4\u7684\u7F62\u4E86\u914D\u4EAB, \u5403\u4E0D\u5F97\u592A\u5E99\u7684\u90A3\u4E00\u53E3\u51B7\u732A\u5934\u8089.</p><p>\u300A\u660E\u53F2\u300B\u5377 139 \u300A\u94B1\u5510\u4F20\u300B\u4E2D\u8BF4:</p><blockquote><p>\u5E1D\u5C1D\u89C8\u300A\u5B5F\u5B50\u300B, \u81F3 &quot;\u571F\u82A5&quot; &quot;\u5BC7\u96E0&quot; \u8BED, \u8C13\u975E\u81E3\u5B50\u6240\u5B9C\u8A00, \u8BAE\u7F62\u5176\u914D\u4EAB, \u8BCF\u6709\u8C0F\u8005\u4EE5\u5927\u4E0D\u656C\u8BBA.</p></blockquote><p>\u8BF4\u662F\u5F53\u4E86\u7687\u5E1D\u7684\u6731\u5143\u748B, \u6709\u4E00\u5929\u8BFB\u300A\u5B5F\u5B50\u300B, \u8BFB\u5230\u300A\u79BB\u5A04\u4E0B\u300B\u4E2D\u8BF4: &quot;\u541B\u5B50\u89C6\u81E3\u5982\u624B\u8DB3, \u5219\u81E3\u89C6\u541B\u5982\u8179\u5FC3; \u541B\u4E4B\u89C6\u81E3\u5982\u72AC\u9A6C, \u5219\u81E3\u89C6\u7FA4\u5982\u56FD\u4EBA; \u541B\u4E4B\u89C6\u81E3\u5982\u571F\u82A5, \u5219\u81E3\u89C6\u541B\u5982\u5BC7\u96E0.&quot;</p><p>\u5B5F\u8001\u5148\u751F\u4E0D\u5982\u5B54\u8001\u5148\u751F &quot;\u8C26\u8C26\u541B\u5B50&quot;, \u4ED6\u662F\u6709\u813E\u6C14\u7684 &quot;\u5927\u4E08\u592B&quot;. \u89C1\u8FC7\u4E86\u4E0D\u9760\u8C31\u7684\u541B\u738B, \u51FA\u95E8\u5C31\u9A82 &quot;\u671B\u4E4B\u4E0D\u4F3C\u4EBA\u541B&quot;.</p><p>\u5173\u4E8E\u541B\u81E3\u5173\u7CFB, \u4ED6\u8BF4: \u541B\u738B\u5982\u4F55\u5F85\u81E3\u4E0B, \u81E3\u4E0B\u5C31\u4F1A\u5982\u4F55\u5F85\u541B\u738B, \u5982\u679C\u541B\u738B\u628A\u81E3\u4E0B\u770B\u4F5C\u6CE5\u571F\u8349\u82A5\u4E00\u822C, \u90A3\u81E3\u4E0B\u5C31\u5E94\u8BE5\u628A\u541B\u738B\u5F53\u6210\u4EC7\u654C\u4E00\u6837.</p><p>\u9020\u4E86\u4E00\u8F88\u5B50\u53CD, \u9A6C\u4E0A\u5F97\u5929\u4E0B\u7684\u592A\u7956\u7687\u5E1D, \u90A3\u542C\u5F97\u4E86\u4F60\u4EEC\u8FD9\u5E2E\u81ED\u77E5\u8BC6\u5206\u5B50, \u8BF4\u8FD9\u6837\u7684\u8BDD. \u4E8E\u662F\u4FBF\u8981\u7F62\u514D\u5B5F\u5B50\u914D\u4EAB\u592A\u4EA9\u5E99\u7684\u5F85\u9047. \u5982\u679C\u6709\u4EBA\u53CD\u5BF9\u8FD9\u4EF6\u4E8B, \u5C31\u6309 &quot;\u5927\u4E0D\u656C&quot; \u8BBA\u7F6A.</p><h2 id="\u4E8C" tabindex="-1"><a class="header-anchor" href="#\u4E8C" aria-hidden="true">#</a> \u4E8C</h2><p>\u9648\u5609\u6620\u8001\u5E08\u8BF4\u4E2D\u897F\u6587\u5316\u7684\u5DEE\u5F02\u5728\u4E8E\u8BA4\u77E5\u539F\u578B\u7684\u4E0D\u540C. \u8FD9\u4E00\u70B9\u4F53\u73B0\u5728\u4E2D\u897F\u6587\u5316\u4E2D, \u8BA9\u4EBA\u4EEC\u4EA7\u751F\u611F\u52A8\u7684\u4E1C\u897F\u4E0D\u540C.</p><p>\u4ED6\u4E3E\u4E86\u4E24\u4E2A\u4F8B\u5B50, \u4E1C\u897F\u65B9\u5404\u4E00\u4E2A.</p><p>\u636E\u8BF4, \u7B2C\u4E8C\u6B21\u5E03\u533F\u6218\u4E89\u65F6, \u4E00\u7FA4\u7F57\u9A6C\u58EB\u5175\u51B2\u8FDB\u4E86\u963F\u57FA\u7C73\u5FB7\u7684\u5BB6\u4E2D, \u5176\u65F6\u963F\u57FA\u7C73\u5FB7\u6B63\u5728\u8BC1\u660E\u4E00\u4E2A\u753B\u56FE\u95EE\u9898. \u963F\u57FA\u7C73\u5FB7\u9762\u5BF9\u654C\u519B\u7684\u5200\u67AA, \u4EE5\u53CC\u624B\u62A4\u4F4F\u6C99\u76D8, \u9AD8\u547C\u9053: &quot;\u4E0D\u8981\u52A8\u6211\u7684\u5706.&quot;</p><p>\u897F\u65B9\u4EBA\u542C\u5230\u8FD9\u4E2A\u6545\u4E8B, \u611F\u52A8\u7684\u4E0D\u5F97\u4E86. \u4E2D\u56FD\u4EBA\u542C\u4E86\u5374\u5E76\u6CA1\u6709\u90A3\u4E48\u611F\u52A8. \u540C\u6837\u4E5F\u6709\u4E00\u4E2A\u4E1C\u65B9\u7684\u6545\u4E8B, \u4E2D\u56FD\u4EBA\u542C\u4E86\u611F\u52A8\u7684\u4E0D\u5F97\u4E86, \u800C\u897F\u65B9\u4EBA\u5374\u5F88\u96BE\u53D7\u5176\u611F\u52A8.</p><p>\u8FD9\u5C31\u662F\u5D14\u677C\u5F11\u541B\u7684\u6545\u4E8B.</p><blockquote><p>\u5D14\u677C\u6740\u4E86\u9F50\u5E84\u516C. \u9F50\u56FD\u592A\u53F2\u5199\u4E0B &quot;\u5D14\u677C\u5F11\u5E84\u516C&quot;. \u5D14\u677C\u6740\u6B7B\u4E86\u592A\u53F2, \u592A\u53F2\u7684\u5927\u5F1F\u7EE7\u7EED\u8FD9\u6837\u5199, \u5D14\u677C\u53C8\u6740\u4E86\u5927\u5F1F. \u592A\u53F2\u7684\u4E8C\u5F1F\u4E5F\u8FD9\u6837\u8BB0\u8F7D, \u5D14\u677C\u53C8\u6740\u4E86\u4E8C\u5F1F. \u6700\u540E\u592A\u53F2\u7684\u4E09\u5F1F\u4F9D\u7136\u8FD9\u6837\u5199, \u5D14\u677C\u53EA\u597D\u653E\u8FC7\u4ED6. \u592A\u53F2\u7684\u65CF\u4EBA\u4EE5\u4E3A\u592A\u53F2\u5168\u5BB6\u90FD\u88AB\u6740\u4E86, \u5E26\u7740\u7AF9\u7B80\u8981\u53BB\u5199 &quot;\u5D14\u677C\u5F11\u5E84\u516C&quot; \u8FD9\u51E0\u4E2A\u5B57, \u7ED3\u679C\u53D1\u73B0\u5DF2\u5982\u5B9E\u8BB0\u5F55, \u4E8E\u662F\u79BB\u53BB</p></blockquote><p>\u5927\u6982\u8BF4\u6765, \u897F\u65B9\u4EBA\u5BF9\u4E8E\u4E16\u754C\u7684\u771F\u76F8\u6709\u4E00\u79CD\u72EC\u7279\u7684\u656C\u610F, \u800C\u4E2D\u56FD\u4EBA\u5BF9\u4E8E\u5386\u53F2\u7684\u771F\u76F8\u4E5F\u6709\u4E00\u79CD\u72EC\u7279\u7684\u656C\u610F. \u4E3A\u4E86\u8FD9\u79CD\u656C\u610F, \u6574\u6574\u4E00\u65CF\u4EBA\u53EF\u4EE5\u5B89\u7136\u8D74\u6B7B.</p><p>\u5728\u6709\u8FD9\u6837 &quot;\u8BA4\u77E5\u539F\u578B&quot; \u7684\u4E2D\u56FD, \u4E00\u4E2A\u7687\u5E1D\u600E\u80FD\u52A8\u7684\u4E86\u5723\u4EBA\u7684\u5F85\u9047.</p><p>\u300A\u660E\u53F2 \u94B1\u5510\u4F20\u300B\u63A5\u7740\u5199\u9053:</p><blockquote><p>\u5510\u6297\u758F\u8C0F\u66F0: &quot;\u81E3\u5F97\u4E3A\u5B5F\u67EF\u6B7B, \u6B7B\u6709\u9980\u8363&quot;. \u65F6\u5EF7\u81E3\u65E0\u4E0D\u4E3A\u5510\u5371. \u5E1D\u9274\u5176\u8BDA\u6073, \u4E0D\u4E4B\u7F6A. \u5B5F\u5B50\u914D\u4EAB\u4EA6\u65CB\u590D.</p></blockquote><p>\u5728\u6B63\u53F2\u91CC, \u5199\u7684\u51A0\u5195\u5802\u7687, \u4F46\u5728\u91CE\u53F2\u91CC, \u53EF\u5C31\u5199\u7684\u8840\u8165\u591A\u4E86. \u300A\u5B81\u6CE2\u5E9C\u65B0\u5FD7\u300B\u8BF4:</p><blockquote><p>\u4E0A\u89C8\u300A\u5B5F\u5B50\u300B\u571F\u82A5\u5BC7\u4EC7\u4E4B\u8BF4, \u8C13\u975E\u81E3\u5B50\u6240\u5B9C\u8A00, \u8BAE\u6B32\u53BB\u5176\u914D\u4EAB, \u8BCF\u6562\u8C0F\u8005, \u7F6A\u4EE5\u4E0D\u656C, \u4E14\u547D\u91D1\u543E\u5C04\u4E4B, \u5211\u90E8\u5C1A\u4E66\u8C61\u5C71\u94B1\u5510, \u6297\u758F\u5165\u8C0F, \u8206\u6987\u81EA\u968F, \u8892\u80F8\u5F53\u7BAD, \u66F0, \u81E3\u5F97\u4E3A\u5B5F\u67EF\u6B7B, \u6B7B\u6709\u9980\u8363. \u4E0A\u89C1\u5176\u8BDA\u6073, \u547D\u592A\u533B\u7597\u6CBB, \u5B5F\u5B50\u914D\u4EAB\u5F97\u4E0D\u5E9F.</p></blockquote><p>\u94B1\u5510\u662F\u5E26\u7740\u68FA\u6750, \u9876\u7740\u7BAD\u53E3\u53BB\u4E3A\u5B5F\u5B50\u6B7B\u8C0F, \u624D\u62E6\u4E0B\u4E86 &quot;\u5B5F\u5B50\u7F62\u4EAB&quot;.</p><p>\u7136\u800C\u8FD9\u624D\u662F\u6743\u529B\u4E0E\u6587\u5316\u8FC7\u62DB\u7684\u7B2C\u4E00\u4E2A\u56DE\u5408.</p><h2 id="\u4E09" tabindex="-1"><a class="header-anchor" href="#\u4E09" aria-hidden="true">#</a> \u4E09</h2><p>\u7136\u800C\u9F50\u56FD\u592A\u53F2\u4EE5\u4E00\u65CF\u4E4B\u8840\u8109\u6240\u7EF4\u62A4\u7684\u5386\u53F2, \u81F3\u5510, \u5176\u9762\u76EE\u4E5F\u5F00\u59CB\u53D8\u5F97\u9762\u76EE\u4E0D\u6E05\u8D77\u6765. \u5510\u592A\u5B97\u786C\u662F\u770B\u4E86\u8BB0\u5F55\u81EA\u8EAB\u529F\u8FC7\u7684\u300A\u8D77\u5C45\u6CE8\u300B, \u81EA\u6B64\u5F00\u4E86\u541B\u738B\u76F4\u63A5\u5220\u6539\u5386\u53F2\u7684\u5148\u4F8B.</p><p>\u5220\u6539\u662F\u6743\u529B\u5BF9\u6587\u5316\u7684\u5E38\u89C4\u64CD\u4F5C. \u4E0D\u4EC5\u4EC5\u662F\u8FC7\u5F80, \u4E0D\u4EC5\u4EC5\u662F\u4E2D\u56FD. \u751A\u81F3\u8FDE\u300A\u661F\u9645\u7A7F\u8D8A\u300B\u91CC, \u53EA\u8981\u60F3\u4E00\u7FA4\u4F1A\u79CD\u5730\u7684\u987A\u6C11\u7684\u7F8E\u56FD\u653F\u5E9C, \u4E5F\u5220\u6539\u4E86\u767B\u6708\u7684\u5386\u53F2.</p><p>\u592A\u7956\u7687\u5E1D\u672A\u80FD\u7F62\u4EAB\u5B5F\u5B50, \u4FBF\u60F3\u8981\u5220\u6539\u300A\u5B5F\u5B50\u300B\u4E00\u4E66.</p><p>\u63A5\u7740\u4E0A\u9762\u7684\u6545\u4E8B, \u300A\u53F2\u8BB0 \u94B1\u5510\u4F20\u300B\u4E2D\u7740\u5199\u9053:</p><blockquote><p>\u7136\u5352\u547D\u5112\u81E3\u4FEE\u300A\u5B5F\u5B50\u8282\u6587\u300B\u4E91.</p></blockquote><p>\u8FD9\u91CC\u7684\u8FD9\u4E2A &quot;\u5112\u81E3&quot;, \u53EB\u505A\u5218\u4E09\u543E.</p><p>\u6D2A\u6B66\u5143\u5E74, 56 \u5C81\u7684\u5218\u4E09\u543E\u5DF1\u9887\u6709\u6587\u540D. \u81F3\u6D2A\u6B66\u5341\u516B\u5E74, \u5218\u4E09\u543E 73 \u5C81, \u88AB\u8861\u5C71\u77E5\u5E9C\u8339\u747A\u63A8\u8350\u81F3\u671D\u5EF7, \u594F\u5BF9\u79F0\u65E8, \u6388\u5B98\u5DE6\u6625\u574A\u5DE6\u8D5E\u5584.</p><p>\u5218\u4E09\u543E\u6DF1\u5F97\u592A\u7956\u5668\u91CD, \u4E3A\u592A\u7956\u7684\u300A\u5927\u8BF0\u300B\u53CA\u300A\u6D2A\u8303\u6CE8\u300B\u5199\u8FC7\u5E8F. \u5E76\u4FEE\u8BA2\u300A\u7701\u8EAC\u5F55\u300B, \u300A\u4E66\u4F20\u4F1A\u9009\u300B, \u300A\u5BF0\u5B87\u901A\u5FD7\u300B, \u300A\u793C\u5236\u96C6\u8981\u300B\u8BF8\u4E66.</p><p>\u5176\u54C1\u683C, \u300A\u660E\u53F2\u300B\u4E2D\u8BF4:</p><blockquote><p>\u4E09\u4E94\u4E3A\u4EBA\u6177\u6168, \u4E0D\u8BBE\u57CE\u5E9C, \u81EA\u53F7 &quot;\u5766\u5766\u7FC1&quot;. \u81F3\u4E34\u5927\u8282, \u5C79\u7136\u4E0D\u53EF\u593A.</p></blockquote><p>\u81F3\u4FEE\u300A\u5B5F\u5B50\u8282\u6587\u300B\u65F6, \u5218\u4E09\u543E\u5DF2\u5E74\u81F3 82 \u5C81. \u8FD9\u4F4D &quot;\u81F3\u4E34\u5927\u8282, \u5C79\u7136\u4E0D\u53EF\u593A&quot; \u7684 &quot;\u5766\u5766\u7FC1&quot; \u7AD9\u5728\u4E86\u8981\u4E48\u9762\u5BF9\u86EE\u6A2A\u7684\u6743\u529B, \u8981\u4E48\u9762\u5BF9\u5929\u4E0B\u7684\u9A82\u540D\u7684\u5341\u5B57\u8DEF\u53E3.</p><p>\u4ED6\u6700\u540E\u7684\u9009\u62E9\u662F, \u4FEE\u4E86\u90A3\u90E8 \u300A\u5B5F\u5B50\u8282\u6587\u300B. \u636E\u5218\u4E09\u543E\u6240\u64B0\u300A\u5B5F\u5B50\u8282\u6587\u9898\u8F9E\u300B\u4E2D\u6240\u8BF4:</p><blockquote><p>\u300A\u5B5F\u5B50\u300B\u4E00\u4E66, \u4E2D\u95F4\u8BCD\u6C14\u4E4B\u95F4, \u6291\u626C\u592A\u8FC7\u8005\u516B\u5341\u4E94\u6761, \u5176\u4F59\u4E00\u767E\u4E03\u5341\u4F59\u6761, \u6089\u9881\u4E4B\u4E2D\u5916\u6821\u5B98, \u4FFE\u662F\u4E66\u8005, \u77E5\u6240\u672C\u65E8.</p></blockquote><p>\u300A\u5B5F\u5B50\u300B\u4E00\u4E66, \u88AB\u4ED6\u5220\u4E8E\u4E86\u516B\u5341\u4E94\u6761, \u5269\u4E0B\u4E00\u767E\u4E03\u5341\u4F59\u6761.</p><p>\u5728\u4FEE\u4E86\u300A\u5B5F\u5B50\u8282\u6587\u300B\u7684\u4E09\u5E74\u540E, \u5218\u4E09\u543E\u56E0\u8457\u540D\u7684\u5357\u5317\u699C\u6848\u53D1\u914D\u8FB9\u7F30.</p><h2 id="\u56DB" tabindex="-1"><a class="header-anchor" href="#\u56DB" aria-hidden="true">#</a> \u56DB</h2><p>\u7136\u800C\u518D\u5F3A\u5927\u7684\u4EBA\u6CBB, \u4E5F\u4E0D\u8FC7\u4EBA\u6CBB\u800C\u5DF2. \u5F3A\u5982\u592A\u7956\u8005, \u7EC8\u4E0D\u80FD &quot;\u4E07\u5C81\u4E07\u5C81\u4E07\u4E07\u5C81&quot;, \u8EB2\u4E0D\u8FC7 &quot;\u4EBA\u4EA1\u653F\u606F&quot; \u7684\u547D\u8FD0.</p><p>\u300A\u56FD\u53F2\u8003\u5F02\u300B\u4E2D\u8FD9\u6837\u5199\u9053:</p><blockquote><p>\u8FD1\u89C1\u8463\u5E94\u4E3E\u64B0\u8FDE\u6C5F\u5B59\u829D\u4F20\u4E91\uFF1A\u6C38\u4E50\u8F9B\u536F\uFF08\u4E5D\u5E74\uFF09\uFF0C\u594F\u590D\u300A\u5B5F\u5B50\u300B\u5168\u4E66\uFF0C\u7565\u8A00\u9006\u81E3\u5218\u4E09\u543E\u6B32\u53BB\u516B\u5341\u4E94\u6761\uFF0C\u5176\u4E2D\u517B\u6C14\u4E00\u7AE0\uFF0C\u6B64\u7A0B\u5B50\u6240\u8C13\u6269\u524D\u5723\u6240\u672A\u53D1\uFF0C\u5927\u6709\u529F\u4E8E\u4E16\u6559\u8005\u3002\u53C8\u6B32\u8BFE\u8BD5\u4E0D\u4EE5\u547D\u9898\uFF0C\u79D1\u4E3E\u4E0D\u4EE5\u53D6\u58EB\uFF0C\u5219\u8C2C\u5984\u76CA\u751A\u3002\u4E5E\u4E0B\u90E8\u8BAE\u6536\u590D\u5168\u4E66\uFF0C\u5EB6\u4F7F\u4E07\u4E16\u77E5\u6240\u8BF5\u6155\u3002\u758F\u8349\u4E3A\u866B\u9F20\u6240\u8680\uFF0C\u4E0D\u80FD\u8BE6\uFF0C\u7136\u300A\u5B5F\u5B50\u300B\u4E66\uFF0C\u4EE5\u516C\u8A00\u590D\u5168\u3002</p></blockquote><p>\u6C38\u4E50\u4E5D\u5E74\u65F6, \u798F\u5EFA\u8FDE\u6C5F\u53BF\u7684\u5B59\u829D\u636E\u7406\u529B\u4E89, \u88AB\u5218\u4E09\u543E\u5220\u5F97\u9762\u76EE\u5168\u975E\u7684\u300A\u5B5F\u5B50\u300B\u4E00\u4E66, \u624D\u5F97\u4EE5\u6062\u590D\u539F\u8C8C.</p><p>\u81F3\u6D2A\u6B66\u4E8C\u5341\u4E03\u5E74\u81F3\u6C38\u4E50\u4E5D\u5E74, \u300A\u5B5F\u5B50\u8282\u6587\u300B\u4EC5\u4EC5\u5B58\u4E16 17 \u5E74. \u800C\u5F53\u65F6, \u660E\u6210\u8893\u6731\u68E3\u5C1A\u672A\u5B9A\u90FD\u5317\u4EAC. \u56E0\u6B64, \u6574\u4E2A\u300A\u5B5F\u5B50\u8282\u6587\u300B\u81EA\u59CB\u81F3\u7EC8\u4EE5\u5357\u4EAC\u4E3A\u4E2D\u5FC3.</p><p>\u81F3\u6E05\u521D\u65F6, \u300A\u5B5F\u5B50\u8282\u6587\u300B\u4E00\u4E66\u5DF2\u4E0D\u590D\u5F97\u89C1. \u5927\u540D\u9F0E\u9F0E\u7684\u6731\u5F5D\u5C0A\uFF081629\u20141709\uFF09\u5C31\u6CA1\u6709\u89C1\u8FC7\u300A\u5B5F\u5B50\u8282\u6587\u300B\u4E00\u4E66\uFF0C\u5176\u540D\u8457\u300A\u7ECF\u4E49\u8003\u300B\u5377235\u6307\u51FA\uFF1A\u201C\u5218\u6C0F\uFF08\u4E09\u543E\uFF09\u7B49\u300A\u5B5F\u5B50\u8282\u6587\u300B\u4E8C\u5377\uFF0C\u672A\u89C1\u3002\u201D</p><p>\u4EBA\u6CBB\u77ED\u6682, \u800C\u6587\u5316\u957F\u5B58.</p><h2 id="\u4E94" tabindex="-1"><a class="header-anchor" href="#\u4E94" aria-hidden="true">#</a> \u4E94</h2><p>\u7136\u800C\u6211\u60F3\u8BB2\u7684, \u662F\u6545\u4E8B\u7684\u53E6\u4E00\u9762.</p><p>\u8C61\u5B5F\u5B50\u7F62\u4EAB, \u5B5F\u5B50\u8282\u6587\u8FD9\u6837\u7684\u91CD\u5927\u4E8B\u4EF6, \u5728\u660E\u53F2\u7684\u300A\u827A\u6587\u5FD7\u300B\u548C\u300A\u793C\u5FD7\u300B\u7ADF\u6CA1\u6709\u8BB0\u8F7D\u6B64\u4E8B.</p><p>\u5F53\u7136, \u62FF\u7740\u5200\u67AA\u7684\u5F53\u6743\u8005, \u5F53\u7136\u53EF\u4EE5\u903C\u7740\u62FF\u7740\u7B14\u6746\u7684\u4E66\u751F, \u5199\u4E0B\u4EFB\u4F55\u81EA\u5DF1\u60F3\u7559\u4E0B\u7684\u5386\u53F2.</p><p>\u7136\u800C &quot;\u5B50\u505A\u6625\u79CB, \u800C\u4E71\u81E3\u8D3C\u5B50\u60E7&quot;. &quot;\u6625\u79CB\u7B14\u6CD5&quot; \u7684\u5A01\u529B\u4E0D\u4EC5\u5728\u5176\u8912\u8D2C\u4E0A. \u800C\u5728\u5176\u5F00\u521B\u4E86\u4E00\u79CD\u4EE5\u9690\u8BF2\u7684\u65B9\u5F0F\u6765\u8C08\u8BBA\u7684\u6280\u672F.</p><p>\u8BA9\u5B5F\u5B50\u7F62\u4EAB\u4E00\u4E8B, \u6D41\u4F20\u81F3\u4ECA\u7684, \u662F \u300A\u94B1\u5510\u4F20\u300B. \u8BA9\u5B59\u829D\u6062\u590D\u300A\u5B5F\u5B50\u300B\u4E00\u4E66\u7684\u6545\u4E8B\u5F97\u4EE5\u6D41\u4F20\u81F3\u4ECA\u7684, \u662F\u88AB\u8463\u5E94\u4E3E\u6240\u53D1\u73B0\u7684 &quot;\u4E3A\u866B\u9F20\u6240\u8680&quot; \u7684\u594F\u758F.</p><p>\u65E0\u8BBA\u5F53\u6743\u8005\u5982\u4F55\u5835\u4E0A\u6587\u4EBA\u5634, \u77E5\u8BC6\u5206\u5B50\u603B\u80FD\u901A\u8FC7 &quot;\u6625\u79CB\u7B14\u6CD5&quot; \u7684\u6280\u672F, \u5728\u8FB9\u89D2\u7EC6\u8282\u4E0A\u7559\u4E0B\u7EBF\u7D22, \u8BA9\u5386\u53F2\u7684\u771F\u76F8\u4FDD\u5B58\u4E0B\u6765.</p><p>\u8BA9\u6211\u4EEC\u56DE\u5230\u6D2A\u6B66\u4E8C\u5341\u4E03\u5E74, \u5218\u4E09\u543E\u8001\u5148\u751F\u9886\u4E86\u5220\u8282\u5B5F\u5B50\u7684\u4EFB\u52A1\u56DE\u5230\u5BB6\u4E2D. \u662F\u8981\u7528\u4E00\u5BB6\u4EBA\u7684\u6027\u547D\u6765\u6297\u547D\u5462, \u8FD8\u662F\u8981\u843D\u4E2A\u4E07\u53E4\u9A82\u540D\u5462?</p><p>\u8001\u5148\u751F\u7ED9\u51FA\u8FD9\u6837\u4E00\u4E2A\u7B54\u6848: \u300A\u5B5F\u5B50\u300B\u539F\u6587\u5220\u4E86 85 \u6761.</p><blockquote><p>\u6240\u8C13\u201C\u516B\u5341\u4E94\u6761\u201D\u4E4B\u8BF4\uFF0C\u5C31\u9690\u542B\u4E86\u8010\u4EBA\u5BFB\u5473\u7684\u4E09\u4E2A\u7384\u673A\u3002</p><p>\u7B2C\u4E00\u4E2A\u7384\u673A\u662F\u201C\u6240\u7528\u91CF\u8BCD\u4E0D\u59A5\u201D\u3002\u4EBA\u4EEC\u5BF9\u300A\u5B5F\u5B50\u300B\u4E66\u4E2D\u5404\u7BC7\u4E4B\u4E0B\u90A3\u4E9B\u76F8\u5BF9\u72EC\u7ACB\u7684\u6BB5\u843D\uFF0C\u5386\u6765\u79F0\u4E4B\u4E3A\u201C\u7AE0\u201D\uFF0C\u800C\u4E0D\u662F\u201C\u6761\u201D\u3002\u5218\u4E09\u543E\u5728\u5143\u671D\u672B\u5E74\u505A\u8FC7\u9759\u6C5F\u8DEF\u7684\u5112\u5B66\u526F\u63D0\u4E3E\uFF0C\u53C8\u4EE5\u7FF0\u6797\u5B66\u58EB\u3001\u5949\u8BAE\u5927\u592B\u7684\u8EAB\u4EFD\u4FEE\u300A\u5B5F\u5B50\u8282\u6587\u300B\uFF0C\u4F55\u4EE5\u4F1A\u72AF\u8FD9\u6837\u7684\u5E38\u8BC6\u9519\u8BEF\u5462\uFF1F</p><p>\u7B2C\u4E8C\u4E2A\u7384\u673A\u662F\u201C\u6240\u4F9D\u5E95\u672C\u4E0D\u8BE6\u201D\u3002\u6211\u4EEC\u6240\u89C1\u7684\u300A\u5B5F\u5B50\u8282\u6587\u300B\u660E\u521D\u523B\u672C\uFF0C\u5404\u5377\u5747\u6709\u7AE0\u6570\u63D0\u793A\uFF0C\u5171172\u7AE0\u3002\u8FD9\u91CC\u8981\u7279\u522B\u6307\u51FA\uFF1A\u901A\u884C\u672C\u300A\u516C\u5B59\u4E11\u4E0B\u300B\u7B2C8\u7AE0\uFF084\xB78\uFF09\uFF0C\u88AB\u300A\u5B5F\u5B50\u8282\u6587\u300B\u5206\u4E3A\u4E24\u7AE0\uFF0C\u4EA6\u5373\u4EE5\u201C\u6C88\u540C\u4EE5\u5176\u79C1\u95EE\u66F0\u201D\u4EE5\u4E0B\u4E3A\u4E00\u7AE0\uFF0C\u4EE5\u201C\u9F50\u4EBA\u4F10\u71D5\u201D\u4EE5\u4E0B\u4E3A\u4E00\u7AE0\uFF0C\u56E0\u6B64\uFF0C\u4ECE\u901A\u884C\u672C\u7684\u89D2\u5EA6\u770B\uFF0C\u300A\u5B5F\u5B50\u8282\u6587\u300B\u5B9E\u9645\u53EA\u6709171\u7AE0\u3002\u5218\u4E09\u543E\u65F6\u671F\u6700\u6743\u5A01\u3001\u4E5F\u6700\u666E\u53CA\u7684\u300A\u5B5F\u5B50\u300B\u7248\u672C\uFF0C\u4E00\u662F\u4E1C\u6C49\u8D75\u5C90\u6CE8\u3001\u5317\u5B8B\u5B59\u596D\u758F\u7684\u300A\u5B5F\u5B50\u6CE8\u758F\u300B\uFF0C\u4E00\u662F\u5357\u5B8B\u6731\u71B9\u7684\u300A\u5B5F\u5B50\u96C6\u6CE8\u300B\uFF0C\u524D\u8005\u4E3A258\u7AE0\uFF08\u300A\u5341\u4E09\u7ECF\u6CE8\u758F\u300B\u672C\u8BEF\u4E3A259\u7AE0\uFF09\uFF0C\u540E\u8005\u4E3A260\u7AE0\uFF0C\u5747\u975E257\u7AE0\u2014\u2014172\uFF08\u5B58\uFF09+85\uFF08\u5220\uFF09=257\u3002\u4E8E\u662F\uFF0C\u5218\u4E09\u543E\u7A76\u7ADF\u62FF\u54EA\u4E2A\u5E95\u672C\u6765\u4FEE\u300A\u5B5F\u5B50\u8282\u6587\u300B\uFF0C\u4E5F\u5C31\u6210\u4E86\u95EE\u9898\u3002</p><p>\u7B2C\u4E09\u4E2A\u7384\u673A\u662F\u201C\u6240\u8282\u7AE0\u6570\u4E0D\u51C6\u201D\u3002\u4EE5\u6731\u71B9\u7684\u300A\u5B5F\u5B50\u96C6\u6CE8\u300B\u548C\u6768\u4F2F\u5CFB\u7684\u300A\u5B5F\u5B50\u8BD1\u6CE8\u300B\u4E3A\u636E\uFF0C\u6211\u4EEC\u5C06\u770B\u5230\uFF1A\u88AB\u300A\u5B5F\u5B50\u8282\u6587\u300B\u5220\u9664\u7684\u7AE0\u6570\u4E0D\u662F85\u7AE0\uFF0C\u800C\u662F89\u7AE0\u3002</p></blockquote><p>\u5F53\u7136, \u6211\u4EEC\u53EF\u4EE5\u8BA4\u4E3A\u8FD9\u662F\u8001\u5148\u751F &quot;\u65F6\u95F4\u7D27, \u4EFB\u52A1\u91CD&quot; \u5FD9\u4E2D\u51FA\u9519\u7684\u7ED3\u679C.</p><p>\u4F46\u6211\u66F4\u613F\u610F\u76F8\u4FE1\u53E6\u4E00\u4E2A\u6545\u4E8B. \u4E00\u4E2A\u6587\u4EBA\u7528 &quot;\u6625\u79CB\u7B14\u6CD5&quot; \u6765\u53CD\u6297\u6743\u529B\u7684\u6545\u4E8B.</p><p>\u6240\u4EE5, \u5B5F\u5B50\u8BF4: &quot;\u5B50\u4F5C\u6625\u79CB, \u800C\u4E71\u81E3\u8D3C\u5B50\u60E7.&quot;</p><hr><p>\u6587\u7AE0\u7D20\u6750\u6765\u6E90\u4E8E:</p><p>\u300A\u6731\u5143\u748B\u65F6\u671F\u7684\u300A\u5B5F\u5B50\u8282\u6587\u300B\u4E8B\u4EF6\u300B. \u539F\u8F7D\u5218\u5C0F\u67AB\u3001\u9648\u5C11\u660E\u4E3B\u7F16\u300A\u67CF\u62C9\u56FE\u7684\u54F2\u5B66\u620F\u5267\u300B\uFF08\u300A\u7ECF\u5178\u4E0E\u89E3\u91CA\u300B\u7B2C2\u8F91\uFF09\uFF0C\u4E0A\u6D77\u4E09\u8054\u4E66\u5E972003\u5E74\u7248\uFF0C\u7B2C259~296\u9875\uFF1B\u7B2C5\u8282\u4EE5\u300A&lt;\u5B5F\u5B50\u8282\u6587&gt;\u7684\u6587\u5316\u7701\u601D\u300B\u4E3A\u9898\uFF0C\u520A\u300A\u4E2D\u56FD\u54F2\u5B66\u53F2\u300B2002\u5E74\u7B2C\uFF12\u671F\u3002</p>',65),a=[q];function c(r,h){return o(),t("div",null,a)}var l=p(e,[["render",c],["__file","\u5B50\u4F5C\u6625\u79CB\u800C\u4E71\u81E3\u8D3C\u5B50\u60E7.html.vue"]]);export{l as default};
