import{_ as o,o as t,c as u,e as q}from"./app.6bf529a6.js";const p={},e=q('<h1 id="\u8BBA\u8BED" tabindex="-1"><a class="header-anchor" href="#\u8BBA\u8BED" aria-hidden="true">#</a> \u8BBA\u8BED</h1><blockquote><p>\u5B50\u66F0: \u5B66\u800C\u65F6\u4E60\u4E4B, \u4E0D\u4EA6\u8BF4\u4E4E? \u6709\u670B\u81EA\u8FDC\u65B9\u6765, \u4E0D\u4EA6\u4E50\u4E4E? \u4EBA\u4E0D\u77E5\u800C\u4E0D\u6120, \u4E0D\u4EA6\u541B\u5B50\u4E4E?</p></blockquote><p>\u5728\u6211\u8FD8\u5728\u4E0A\u5B66\u7684\u65F6\u5019, \u8FD8\u6709\u4E9B\u8BFB\u53E4\u4E66\u7684\u5174\u8DA3, \u5E38\u53BB\u4E2D\u592E\u5E7F\u573A\u65C1\u7684\u53E4\u85C9\u4E66\u5E97. \u53E4\u85C9\u4E66\u5E97, \u987E\u540D\u601D\u4E49\u662F\u4E13\u505A\u53E4\u85C9\u518D\u7248\u7684, \u591A\u7684\u662F\u4E9B\u518D\u7248\u7684\u53E4\u4EE3\u5178\u85C9.</p><p>\u8BB0\u5FC6\u5C24\u5176\u6DF1\u7684\u662F, \u6709\u4E00\u6B21\u53BB\u53E4\u85C9\u4E66\u5E97, \u968F\u624B\u62FF\u8D77\u4E00\u672C\u539A\u539A\u7684\u5173\u4E8E\u8BBA\u8BED\u7684\u6CE8\u758F. \u4ECE\u7B2C\u4E00\u9875\u7FFB\u8D77, \u6B63\u662F\u5173\u4E8E\u300A\u8BBA\u8BED. \u5B66\u800C\u7B2C\u4E00\u300B\u9610\u91CA.</p><p>\u9996\u5148\u4ECE\u7B2C\u4E00\u53E5\u8BB2\u8D77: &quot;\u5B66\u800C\u65F6\u4E60\u4E4B ......&quot;. \u5723\u4EBA\u5373\u7136\u8BF4 &quot;\u65F6\u4E60&quot;, \u90A3\u4F55\u8C13 <strong>\u65F6</strong> \u5462? \u6240\u8C13\u65F6, \u5E38\u6709\u4E09\u79CD\u610F\u601D, \u9996\u5148\u662F\u4E00\u5E74\u4E4B\u65F6, \u5982\u6625\u590F\u79CB\u51AC, \u81EA\u7136\u8981\u5F15\u7ECF\u636E\u5178\u6765\u8BF4\u4E00\u5E74\u4E4B\u65F6\u7684\u4E0D\u540C\u7528\u6CD5; \u5176\u5B9E\u53C8\u6709\u4E00\u6708\u4E4B\u65F6, \u5982\u6708\u4E4B\u76C8\u4E8F, \u81EA\u7136\u4E5F\u8981\u5F15\u636E\u5178\u6765\u8BF4\u4E00\u6708\u4E4B\u65F6\u7684\u4E0D\u540C\u7528\u6CD5; \u6700\u540E\u662F\u4E00\u65E5\u4E4B\u65F6, \u5982\u671D\u5347\u5915\u843D, \u81EA\u7136\u8FD8\u8981\u5F15\u7ECF\u636E\u5178\u7684\u6765\u8BF4\u4E00\u65E5\u4E4B\u65F6\u7684\u4E0D\u540C\u7528\u6CD5. \u5927\u6982\u7528\u4E86\u4E24\u6574\u9875, \u7EC6\u7EC6\u5206\u8FA8\u4E86\u4E09\u79CD &quot;\u65F6&quot; \u7684\u7528\u6CD5\u540E, \u4F5C\u8005\u7EC8\u4E8E\u53D1\u51FA\u4E86\u7075\u9B42\u7684\u62F7\u95EE: \u90A3\u4E48\u8FD9\u91CC\u7684 &quot;\u5B66\u800C\u65F6\u4E60\u4E4B&quot; \u7684\u65F6\u662F\u54EA\u4E2A &quot;\u65F6&quot; \u5462? \u53C8\u7EC6\u7EC6\u5206\u8FA9\u4E86\u8FD9\u91CC\u7684\u8BED\u610F\u548C\u7528\u6CD5, \u6700\u7EC8\u5F97\u51FA\u7B54\u6848\u6765: \u8FD9\u91CC\u7684 &quot;\u65F6&quot; \u4E0D\u662F\u4E0A\u8FF0\u4E09\u79CD &quot;\u65F6&quot; \u7684\u4EFB\u4F55\u4E00\u79CD, \u800C\u662F &quot;\u65F6\u65F6&quot; \u7684\u610F\u601D: \u65E0\u8BBA\u662F\u6625\u590F\u79CB\u51AC, \u6708\u76C8\u6708\u4E8F, \u65E5\u5347\u65E5\u843D, \u4EBA\u4E4B\u6240\u5B66, \u603B\u8981\u65F6\u65F6\u4E60\u4E4B\u4E3A\u5999.</p><p>\u770B\u5230\u6B64\u5904, \u5DF2\u6709\u4E94\u9875\u5DE6\u53F3. \u518D\u52A0\u4E0A\u8FD9\u4E66\u540A\u4E66\u888B\u540A\u7684\u4E25\u91CD, \u8BFB\u8D77\u6765\u5F88\u4E0D\u6D41\u7545, \u867D\u6CA1\u6709\u4F53\u4F1A\u5230\u4E00\u5E74\u4E4B\u65F6\u548C\u4E00\u6708\u4E4B\u65F6, \u5927\u6982\u4E5F\u77E5\u9053\u4E86\u4EC0\u4E48\u53EB\u505A\u4E00\u65E5\u4E4B\u65F6. \u4E0E\u662F\u6211\u957F\u957F\u51FA\u4E86\u4E00\u53E3\u6C14, \u7740\u4E86\u7740\u8FD9\u4E66\u7684\u539A\u5EA6.\u628A\u8FD9\u672C\u6CE8\u758F\u653E\u4E0B, \u8F6C\u8EAB\u79BB\u5F00\u4E86\u53E4\u8024\u4E66\u5E97.</p><p>\u5F53\u7136, \u8BB2\u8FD9\u4E8B\u591A\u591A\u5C11\u5C11\u6709\u8BB2\u7B11\u8BDD\u7684\u610F\u5473. \u4F46\u8BF4\u5230\u5E95, \u4E5F\u662F\u60F3\u8BF4, \u4E2D\u56FD\u7684\u77E5\u8BC6\u5206\u5B50, \u591A\u4E48\u6709\u51B2\u52A8\u4ECE\u672C\u5B54\u5B50\u7684\u8A00\u884C\u5F55\u4E2D\u627E\u5230\u5B89\u8EAB\u7ACB\u547D\u7684\u672C\u94B1. \u300A\u8BBA\u8BED\u300B\u4E00\u4E66\u5171 20 \u7BC7, \u6BCF\u7BC7\u4EE5\u5F00\u7BC7\u7684\u524D\u4E24\u4E2A\u5B57\u4E3A\u9898. \u6BD4\u5982\u4ECA\u5929\u6240\u8BB2\u7684, \u4FBF\u662F\u300A\u8BBA\u8BED. \u5B66\u800C\u7B2C\u4E00\u300B. \u6240\u8C13 &quot;\u5B66\u800C\u7B2C\u4E00&quot; \u7684 &quot;\u5B66\u800C&quot; \u662F\u7BC7\u540D, \u6765\u81EA\u4E8E &quot;\u5B66\u800C\u65F6\u4E4B&quot; \u7684\u524D\u4E24\u4E2A\u5B57; &quot;\u7B2C\u4E00&quot; \u6307\u7684\u662F\u8BE5\u6761\u662F\u672C\u7BC7\u6761\u4E00\u6761.</p><p>\u5982\u6B64\u60F3\u6765, \u4E13\u7528\u4E94\u9875\u7EB8\u5C31\u6765\u8BF4\u8FD9\u534A\u53E5\u90FD\u4E0D\u5230\u7684\u8BDD, \u4E5F\u5C31\u60C5\u53EF\u539F\u4E86: \u6BD5\u7ADF\u8FD9\u662F\u5723\u4EBA\u6240\u7ACB\u7684\u7B2C\u4E00\u53E5\u8BDD, \u65E0\u8BBA\u6295\u6CE8\u591A\u5C11\u4F3C\u4E4E\u90FD\u65E0\u4E3A\u8FC7\u4EFD. \u7136\u800C\u8FD9\u5168\u4E66\u7684\u7B2C\u4E00\u53E5\u8BDD\u8BB2\u7684\u662F\u4EC0\u4E48\u5462? \u901A\u5E38\u8BF4\u6765\u5927\u6982\u662F\u8FD9\u6837:</p><blockquote><p>\u5B54\u5B50\u8BF4, \u5B66\u4E60\u540E\u65F6\u5E38\u590D\u4E60, \u4E0D\u4E5F\u5F88\u5FEB\u4E50\u4E48? \u6709\u670B\u53CB\u4ECE\u5916\u5730\u6765, \u4E0D\u4E5F\u662F\u975E\u5E38\u5FEB\u4E50\u7684\u95EE\u9898\u4E48? \u4EBA\u4E0D\u77E5\u9053\u6211\u800C\u6211\u53BB\u4E0D\u6C14\u6C14, \u4E0D\u4E5F\u662F\u975E\u5E38\u541B\u5B50\u7684\u4E8B\u60C5\u8272?</p></blockquote><p>\u636E\u8BF4\u5357\u6000\u8C28\u5BF9\u8FD9\u79CD\u89E3\u91CA\u65B9\u6CD5\u8868\u793A\u4E0D\u6EE1. \u4ED6\u4ECE\u65E5\u5E38\u7ECF\u9A8C\u51FA\u6765, \u8BF4\u4EBA\u5B66\u4E60\u5411\u6765\u4E0D\u5FEB\u4E50\u7684\u4E8B\u60C5. \u4F55\u51B5\u8FD8\u8981 &quot;\u65F6\u65F6&quot; \u7684\u53CD\u590D\u5B66\u4E60\u5462. \u5982\u771F\u8981\u6C42\u4EBA\u5B66\u4E86\u4E4B\u540E\u8FD8\u8981\u662F &quot;\u65F6\u65F6&quot; \u4E60\u4E4B, \u90A3\u975E\u4F46\u4E0D\u662F\u5FEB\u4E4B\u4E8B, \u53CD\u800C\u6781\u62C6\u78E8\u7684\u4E8B\u60C5. \u4E8E\u662F\u4ED6\u8BF4: \u6240\u8C13 &quot;\u5B66\u800C\u65F6\u4E60\u4E4B\u4E0D\u4EA6\u4E50\u4E4E&quot; \u7684\u4E60, \u5927\u6982\u4E0D\u662F\u590D\u4E60\u7684\u610F\u601D, \u800C\u662F\u5B9E\u4E60\u7684\u610F\u601D. &quot;\u5B66\u800C\u5B9E\u4E60\u4E4B&quot; \u4E0D\u662F\u5B66\u4E86\u5B66\u95EE\u4E4B\u540E, \u8981\u65F6\u5E38 &quot;\u5B9E\u4E60&quot; \u5B66\u5230\u7684\u77E5\u8BC6\u6280\u80FD. \u5728\u8FD9\u6837\u7684\u5B9E\u8DF5\u4E2D, \u4FBF\u80FD\u6709\u5FEB\u4E50\u4ECE\u65E0\u4E2D\u751F\u51FA\u6765.</p><p>\u5357\u6000\u8C28\u4E0A\u672F\u7684\u5BF9\u8FD9\u53E5\u8BDD\u7684\u7406\u89E3, \u867D\u80FD\u5C06\u5B83\u4E0E\u65E5\u5E38\u7ECF\u9A8C\u8054\u7CFB\u8D77\u6765, \u4F46\u7EC8\u4E0D\u80FD\u628A\u4E09\u53E5\u4E32\u8D77\u6765, \u4F7F\u5B83\u4EEC\u5408\u8D77\u6765\u8BB2\u4E00\u4E2A\u9053\u7406. \u7136\u800C\u8FD9\u79CD\u89E3\u6CD5\u5374\u7ED9\u6211\u4EEC\u5E26\u6765\u975E\u5E38\u4E0D\u540C\u89C6\u89D2: \u5B66\u4E0E\u4E60\u7684\u5173\u7CFB. \u6240\u5B66\u6240\u77E5\u4E00\u5B9A\u8981\u4E8E\u5B9E\u8DF5\u7ED3\u5408\u8D77\u6765, \u624D\u80FD\u771F\u6B63\u4EA7\u751F\u5FEB\u4E50. \u8FD9\u4E5F\u4E0D\u662F\u4EC0\u4E48\u65B0\u9C9C\u7684\u4E3B\u5F20: \u5112\u5BB6\u5411\u6765\u4E3B\u5F20\u771F\u6B63\u7684\u5B66\u95EE\u90FD\u5728\u4F26\u7406\u4E4B\u4E2D, \u4E00\u5207\u4F26\u7406\u4E4B\u4E2D\u7684\u4E1C\u897F\u4E00\u5B9A\u662F\u53EF\u4EE5\u884C\u51FA\u6765\u7684. \u751A\u81F3\u53EF\u4EE5\u8BF4, \u884C\u51FA\u6765\u7684\u4F18\u5148\u6027\u5176\u5B9E\u662F\u8FDC\u9AD8\u4E8E\u77E5\u8BC6\u672C\u8EAB\u7684. \u6240\u8C13 &quot;\u884C\u6709\u4F59\u529B\u4E43\u5B66\u6587&quot;, \u77E5\u8BC6\u6027\u7684\u5B66\u4E60\u5176\u5B9E\u53EA\u662F\u5B9E\u8DF5\u7684\u6709\u76CA\u8865\u5145\u800C\u5DF2.</p><p>\u4ECE\u8FD9\u4E2A\u89D2\u5EA6\u6765\u8BB2, \u4F5C\u4E3A\u5112\u5BB6\u6700\u4E3B\u8981\u7ECF\u5178\u7684\u7B2C\u4E00\u7AE0\u7B2C\u4E00\u8282 -- &quot;\u5B66\u800C\u7B2C\u4E00&quot; -- \u8BD5\u56FE\u8BD7\u8BBA\u5B66\u95EE\u548C\u5FEB\u4E50\u7684\u5173\u7CFB, \u600E\u4E48\u80FD\u4E0D\u5173\u6D89\u5B9E\u8DF5\u5462? \u5C24\u5176\u662F\u8FD9\u91CC\u6240\u8BB2\u4E09\u4EF6\u4E8B, \u5B66\u800C\u5B9E\u4E60, \u6709\u670B\u8FDC\u6765, \u4E0D\u77E5\u4E0D\u6120\u4E09\u4E8B, \u4F55\u4EE5\u4F7F\u5FEB\u4E50\u4E0E\u5B66\u95EE\u5173\u8054\u8D77\u6765, \u4FBF\u662F\u6700\u5173\u952E\u7684\u95EE\u9898.</p><p>\u6211\u4EEC\u5F53\u7136\u5E94\u5F53\u5047\u8BBE\u5B54\u95E8\u5F1F\u5B50\u628A &quot;\u5B66\u800C\u7B2C\u4E00&quot; \u4F5C\u4E3A\u5112\u5BB6\u6700\u91CD\u8981\u7ECF\u5178\u7684\u7B2C\u4E00\u90E8\u5206\u4E0D\u662F\u968F\u610F\u800C\u4E3A. \u6731\u71B9\u5C31\u8BF4, \u8FD9\u53E5\u8BDD\u662F &quot;\u5165\u9053\u4E4B\u95E8, \u79EF\u5FB7\u4E4B\u57FA&quot;. \u6211\u4EEC\u6709\u7406\u7531\u76F8\u4FE1\u5B83\u662F\u6709\u63D0\u7EB2\u643A\u9886\u7684\u610F\u5473\u5728\u91CC\u8FB9.</p><p>\u7136\u800C\u56DE\u5230\u7BC7\u5934\u6240\u8BB2\u7684\u5173\u4E8E &quot;\u65F6&quot; \u7684\u6545\u4E8B, \u4ECE &quot;\u6B64\u8282\u6240\u8BB2\u8FD9\u65F6\u4E43 &#39;\u65F6\u65F6&#39; \u8FD9\u65F6&quot; \u7684\u89D2\u5EA6\u8BB2, \u6211\u5012\u662F\u8BA4\u4E3A\u6709\u4E00\u7EC4\u9012\u8FDB\u7684\u5173\u7CFB\u53EF\u4EE5\u6765\u8BB2: \u65F6\u65F6 -- \u8FDC\u670B -- \u4E0D\u77E5. \u8FD9\u65F6\u4E00\u7EC4\u660E\u663E\u7684\u6709\u8FD1\u53CA\u8FDC\u7684\u8FC7\u6E21\u5173\u7CFB:</p><blockquote><p>\u4EBA\u6709\u5B66\u95EE\u800C\u4F7F\u5176\u5F97\u5230\u4E60\u7528 ( \u5F97\u4EE5\u5B9E\u8DF5, \u65BD\u5C55\u62B1\u8D1F ), \u90A3\u662F\u6700\u5FEB\u4E50\u7684\u4E8B\u4E86 ( \u6B64\u662F\u7ACB\u529F ); \u9000\u800C\u6C42\u5176\u6B21, \u5982\u679C\u6709\u4EBA\u80FD\u8BA4\u540C\u6211\u7684\u5B66\u4E60, \u7531\u8FDC\u65B9\u6765\u8FFD\u968F\u6211, \u90A3\u4E5F\u975E\u5E38\u8BA9\u4EBA\u5FEB\u4E50 ( \u6B64\u662F\u7ACB\u8A00 ); \u5C31\u662F\u6700\u574F\u7684\u60C5\u51B5, \u6211\u7684\u5B66\u95EE\u60F3\u6CD5, \u65E2\u65E0\u4ECE\u65BD\u5C55, \u4E5F\u65E0\u4EBA\u8BA4\u540C, \u4F46\u6211\u575A\u4FE1\u6B64\u9053, \u4E0D\u6539\u521D\u5FC3, \u8FD9\u4E0D\u5E94\u5F53\u662F\u541B\u5B50\u7684\u4F5C\u4E3A\u5417 ( \u6B64\u662F\u7ACB\u5FB7 )?</p></blockquote><p>\u6240\u8C13\u7ACB\u5FB7, \u7ACB\u8A00, \u7ACB\u529F, \u662F\u4E2D\u56FD\u5112\u751F\u7684\u6700\u9AD8\u8FFD\u6C42, \u53F7\u79F0 &quot;\u771F\u4E09\u4E0D\u673D&quot;. \u636E\u8BF4\u6574\u4E2A\u4E2D\u56FD\u5386\u53F2\u4E5F\u53EA\u6709\u4E24\u4E2A\u534A\u4EBA\u505A\u5230\u4E86: \u8BF8\u845B\u4EAE, \u738B\u9633\u660E\u7B97\u662F\u4E24\u4E2A, \u66FE\u56FD\u85E9\u7B97\u662F\u534A\u4E2A.</p><p>\u6211\u4EEC\u5047\u8BBE &quot;\u5B66\u800C\u7B2C\u4E00&quot; \u662F\u5112\u95E8\u63D0\u7EB2\u643A\u9886\u7684 &quot;\u5165\u9053\u4E4B\u95E8, \u79EF\u5FB7\u4E4B\u57FA&quot;, \u90A3\u4E48\u6211\u8BA4\u4E3A, \u8FD9 &quot;\u771F\u4E09\u4E0D\u673D&quot; \u5C31\u662F\u300A\u8BBA\u8BED\u300B\u4E00\u4E66\u8981\u6211\u4EEC\u53BB\u8DF5\u884C\u7684\u9053\u7406. \u8FD9\u9053\u7406\u8981\u518D\u51DD\u7EC3\u4E9B\u7684\u8BF4\u51FA\u6765, \u5C31\u662F: \u8010\u4E0B\u6027\u5B50\u6765 ( \u7ACB\u5FB7 ) \u505A\u4E8B ( \u7ACB\u529F ) \u548C\u8BF4\u7406 ( \u7ACB\u8A00 ).</p><p>\u4ECA\u65E5\u6B63\u662F\u5B54\u5B50\u7684\u751F\u8FB0, \u6765\u5199\u8FD9\u6837\u4E00\u7BC7\u6587\u7AE0, \u65E2\u662F\u4E3A\u4E86\u7EAA\u5FF5, \u4E5F\u662F\u4E3A\u4E86\u505A\u70B9\u4E8B\u60C5, \u5373\u4E3A\u4E86\u5B54\u5B50, \u4E5F\u4E3A\u4E86\u6211\u4EEC.</p><p>\u8FD9\u4E2A\u60F3\u6CD5\u6765\u81EA DTH \u7684 EC. \u4ED6\u8BF4\u6211\u4EEC\u4F5C\u4E3A\u4E1C\u4E9A\u6587\u5316\u5708\u7684\u4E00\u4EFD\u5B50, \u5176\u5B9E\u5E94\u8BE5\u82B1\u4E9B\u5FC3\u601D\u6765\u5B66\u4E60\u4E00\u4E0B\u5112\u5BB6\u7684\u6559\u5BFC. \u90A3\u5112\u5BB6\u7684\u6559\u5BFC\u5373\u662F \u7ACB\u5FB7 \u7ACB\u8A00 \u7ACB\u529F, \u6211\u4FBF\u60F3\u505A\u4EF6\u4E0E\u8BF4\u7406\u76F8\u5173\u7684\u4E8B\u6765:</p><p>\u8FD9\u5176\u662F\u4E00\u4E2A\u5F81\u6587\u7684\u9080\u8BF7: \u4ECE\u4ECA\u5929\u5F00\u59CB, \u6211\u4F1A\u6309\u7167\u8BBA\u8BED\u7684\u987A\u5E8F\u6BCF\u5929\u5199\u4E00\u7BC7\u77ED\u6587, \u5E76\u5411\u5927\u5BB6\u4EE5\u540C\u6837\u7684\u9898\u76EE\u5F81\u6587, \u5E76\u53D1\u5728\u8FD9\u4E2A\u516C\u4F17\u53F7\u4E0A\uFF0C\u6BCF\u5199\u8FC7\u4E00\u7BC7, \u4FBF\u7EC4\u7EC7\u53C2\u52A0\u5F81\u6587\u7684\u540C\u5B66\u8FDB\u884C\u4E00\u6B21\u7EBF\u4E0A\u8BA8\u8BBA, \u5E76\u5C06\u8BA8\u8BBA\u5F55\u97F3\u4EE5\u7535\u53F0\u8282\u76EE\u7684\u65B9\u5F0F\u653E\u51FA.</p><p>\u5F53\u7136, \u6700\u574F\u7684\u60C5\u51B5\u662F &quot;\u4EBA\u4E0D\u77E5\u800C\u4E0D\u6120&quot;, \u4F46\u6211\u8FD8\u662F\u5E0C\u671B\u5927\u5BB6\u80FD &quot;\u6709\u670B\u81EA\u8FDC\u65B9\u6765&quot;, \u4E0E\u6211\u4E00\u9053 &quot;\u5B66\u800C\u5B9E\u4E60\u4E4B&quot;.</p>',21),c=[e];function _(a,r){return t(),u("div",null,c)}var s=o(p,[["render",_],["__file","\u5B66\u800C\u7B2C\u4E00\u7B2C\u4E00\u7AE0.html.vue"]]);export{s as default};
