import{_ as t,o,c as u,e as q}from"./app.6bf529a6.js";const e={},_=q('<h1 id="\u5B66\u800C\u7B2C\u4E00\u7B2C\u4E8C\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u5B66\u800C\u7B2C\u4E00\u7B2C\u4E8C\u7AE0" aria-hidden="true">#</a> \u5B66\u800C\u7B2C\u4E00\u7B2C\u4E8C\u7AE0</h1><p>\u53F2\u8BB0\u4E0A\u8BB2, \u5B54\u5B50\u6B7B\u540E\u5B66\u751F\u4EEC\u601D\u5FF5\u8001\u5E08, \u770B\u6709\u5B50 &quot;\u72B6\u4F3C\u5B54\u5B50&quot;, \u5C31\u7ACB\u6709\u5B50\u4E3A\u8001\u5E08, \u5E76\u4EE5\u5BF9\u5F85\u8001\u5E08\u7684\u65B9\u5F0F\u6765\u5BF9\u5F85\u6709\u5B50. \u53F2\u8BB0\u91CC\u6CA1\u6709\u5177\u4F53\u8BF4, \u8FD9\u4E2A &quot;\u72B6\u4F3C&quot; \u5B54\u5B50\u662F\u600E\u4E48\u4E2A\u4F3C\u6CD5: \u5012\u5E95\u662F\u957F\u7684\u5F97\u50CF, \u8FD8\u662F\u8A00\u884C\u50CF. \u4F46\u60F3\u6765\u5B54\u95E8\u90FD\u662F\u5112\u751F, \u56E0\u7740\u6709\u4EBA\u957F\u5F97\u50CF\u5B54\u5B50, \u5C31\u5728\u5B54\u5B50\u6B7B\u540E\u628A\u4ED6\u7ACB\u4E3A\u8001\u5E08, \u597D\u50CF\u8BF4\u4E0D\u592A\u8FC7\u53BB. \u4F46\u662F\u770B\u300A\u53F2\u8BB0\u300B\u6240\u8BF4, \u5F1F\u5B50\u4EEC\u662F\u56E0\u601D\u5FF5\u8001\u5E08, \u624D\u628A\u6709\u5B50\u7ACB\u4E3A\u8001\u5E08, \u60F3\u6765\u8FD9\u91CC\u7684 &quot;\u72B6\u4F3C&quot; \u5C31\u5E94\u8BE5\u4E5F\u4E0D\u662F\u4E00\u79CD\u975E\u5E38\u62BD\u8C61\u7684\u76F8\u4F3C. \u8FD9\u6837\u8BF4\u6765, \u5012\u8BA9\u6211\u60F3\u5230\u4E00\u4E2A\u8BCD\u6765: \u5723\u4EBA\u6C14\u8C61. \u5723\u4EBA\u4E4B\u4E3A\u5723\u4EBA, \u603B\u6709\u4E00\u79CD\u6C14\u8C61\u5728\u91CC\u8FB9, \u8FD9\u79CD\u6C14\u8C61\u4E0D\u662F\u67D0\u5177\u4F53\u76F8\u4F3C\u4E4B\u5904\u7684\u96C6\u5408, \u800C\u662F\u4E00\u79CD\u6C1B\u56F4\u6C14\u573A\u4E0A\u7684\u76F8\u8FD1.</p><p>\u300A\u8BBA\u8BED\u300B\u5B66\u800C\u7B2C\u4E8C, \u63A5\u7740\u5B54\u5B50\u63D0\u51FA\u7684 &quot;\u5165\u5FB7\u4E4B\u95E8&quot;, \u9A6C\u4E0A\u5C31\u63A5\u7740\u6709\u5B50\u7684\u8BDD, \u5927\u6982\u4E5F\u662F\u56E0\u4E3A\u6709\u5B50\u7684\u8BDD\u91CC\u6709\u7740\u5723\u4EBA\u6C14\u8C61\u5427.</p><p>\u5B54\u5B50\u8BB2\u5B66\u95EE\u4E0E\u5FEB\u4E50\u7684\u5173\u7CFB, \u6700\u7EC8\u628A\u5B66\u95EE\u843D\u5230\u4E86\u5B9E\u8DF5\u4E0A. \u7136\u800C\u5728\u90A3\u4E2A\u65F6\u4EE3, \u6625\u79CB\u6218\u56FD\u90A3\u4E2A\u7EB7\u4E71\u7684\u5E74\u4EE3, \u65F6\u4EE3\u7ED9\u77E5\u8BC6\u5206\u5B50\u63D0\u51FA\u7684\u6700\u5927\u7684\u5B9E\u8DF5\u95EE\u9898\u662F\u4EC0\u4E48\u5462? \u5C31\u662F\u5982\u4F55\u7ED3\u675F\u7EB7\u4E71\u7684\u793E\u4F1A\u72B6\u6001. \u4E8E\u662F\u6709\u5B50\u4FBF\u63A5\u7740\u5B54\u5B50\u5B66\u95EE\u4E0E\u5B9E\u8DF5\u7684\u5173\u7CFB, \u6765\u8BB2\u8BB2\u5B9E\u8DF5\u7684\u95EE\u9898.</p><p>\u6709\u5B50\u5BF9\u4E8E\u65F6\u4EE3\u63D0\u7ED9\u4ED6\u7684\u95EE\u9898, \u4ED6\u7684\u56DE\u7B54\u662F: &quot;\u541B\u5B50\u52A1\u672C&quot;. &quot;\u52A1&quot; \u8FD9\u4E2A\u5B57\u5F88\u6709\u610F\u601D, \u6211\u4EEC\u8BB2\u5177\u4F53\u8981\u529E\u7684\u4E8B\u53EB &quot;\u4E8B\u52A1&quot;, \u628A\u5B89\u6392\u597D\u8981\u505A\u7684\u5DE5\u4F5C\u53EB &quot;\u4EFB\u52A1&quot;. \u51E1\u5E26\u4E2A &quot;\u52A1&quot; \u5B57\u7684, \u90FD\u5F97\u662F\u5177\u4F53\u7684, \u5B89\u6392\u597D\u7684. \u53E6\u5916, \u4E0E &quot;\u52A1\u672C&quot; \u76F8\u4F3C\u7684\u6784\u8BCD\u6CD5, \u6211\u4EEC\u8BF4 &quot;\u52A1\u519C&quot; &quot;\u52A1\u5DE5&quot;. \u8FD9\u5F53\u7136\u6709\u4ECE\u4E8B\u7684\u610F\u601D ( \u4ECE\u4E8B\u519C\u4E1A\u751F\u4EA7\u6216\u5DE5\u4E1A\u6D3B\u52A8 ), \u4F46\u7ED3\u5408\u5B83\u7684\u5177\u4F53\u7684, \u5B89\u6392\u597D\u7684\u8FD9\u5C42\u610F\u601D, \u6211\u66F4\u613F\u610F\u8BF4, \u8FD9\u91CC\u5E26\u7740 &quot;\u64CD\u5FC3&quot; \u7684\u610F\u601D, ( \u5728\u519C\u4E8B\u4E0A\u64CD\u5FC3, \u5728\u5DE5\u4F5C\u4E0A\u64CD\u5FC3 ). \u6709\u5B50\u8BF4, \u8981\u89E3\u51B3\u793E\u4F1A\u7EB7\u4E71, \u4F5C\u4E3A\u541B\u5B50, \u5C31\u662F\u8981\u5728\u90A3\u6839\u672C\u7684\u4E8B\u60C5\u4E0A\u64CD\u5FC3. \u6240\u8C13 &quot;\u672C\u7ACB\u800C\u9053\u751F&quot; \u5C31\u662F\u628A\u90A3\u6839\u672C\u7684\u4E8B\u60C5\u64CD\u5FC3\u597D\u4E86, \u65B9\u6CD5\u548C\u8DEF\u5F84\u7684\u95EE\u9898\u4E5F\u5C31\u6E05\u695A\u4E86.</p><p>\u90A3\u4E48\u4E2A\u8981\u64CD\u5FC3\u7684\u6839\u672C\u662F\u4EC0\u4E48\u5462? &quot;\u5B5D\u5F1F\u4E5F\u8005, \u5176\u4E3A\u4EBA\u4E4B\u672C\u4E5F&quot;. \u4EBA\u7EC4\u7EC7\u793E\u4F1A\u7684\u6700\u57FA\u672C\u7684\u5355\u4F4D\u662F\u5BB6\u5EAD, \u5728\u5BB6\u5EAD\u4E2D, \u5B5D\u662F\u7236\u6BCD\u5B50\u5973\u7684\u5173\u7CFB, \u608C\u662F\u5144\u5F1F\u957F\u5E7C\u7684\u5173\u7CFB. \u5728\u90A3\u4E2A\u65F6\u4EE3\u91CC, \u8FD9\u5C31\u662F\u4EBA\u6700\u6838\u5FC3\u548C\u6700\u5177\u4F53\u7684\u5173\u7CFB\u4E86. \u6211\u4EEC\u5927\u6982\u7C97\u7565\u7684\u8BF4, \u793E\u4F1A\u662F\u4EBA\u7684\u5173\u7CFB\u7684\u603B\u548C, \u90A3\u4E48\u89E3\u51B3\u793E\u4F1A\u95EE\u9898\u7684\u6839\u672C, \u8FD8\u662F\u8981\u53BB\u89E3\u51B3\u5177\u4F53\u7684, \u6700\u771F\u5B9E\u7684\u4EBA\u7684\u5173\u7CFB\u7684\u95EE\u9898.</p><p>\u4E00\u4E2A\u4EBA\u5982\u679C\u80FD\u5728\u5BB6\u5EAD\u7684\u5173\u7CFB\u4E0A\u5904\u7406\u597D\u4E86, \u90A3\u4ED6\u5927\u6982\u7387\u5728\u793E\u4F1A\u7684\u5173\u7CFB\u4E0A\u5904\u7406\u7684\u4E5F\u4E0D\u9519 -- &quot;\u5176\u4E3A\u4EBA\u4E5F\u5B5D\u5F1F, \u800C\u597D\u72AF\u4E0A\u8005, \u9C9C\u77E3.&quot; \u793E\u4F1A\u5173\u7CFB\u5904\u7406\u597D\u4E86, \u793E\u4F1A\u7EB7\u4E71\u7684\u95EE\u9898\u4E5F\u5C31\u81EA\u7136\u89E3\u51B3\u4E86 -- &quot;\u4E0D\u597D\u72AF\u4E0A, \u800C\u597D\u4F5C\u4E71\u8005, \u672A\u4E4B\u6709\u4E5F&quot;. \u4E8E\u662F\u4E00\u6761\u7531\u4E2A\u4EBA\u800C\u793E\u4F1A\u7684\u793E\u4F1A\u95EE\u9898\u6CBB\u7406\u601D\u8DEF, \u5C31\u6E05\u6670\u4E86.</p><p>\u5F53\u7136, \u8FD9\u91CC\u5BE5\u5BE5\u6570\u8BED, \u80FD\u7ED9\u51FA\u7684\u9876\u591A\u662F\u4E2A\u7A7A\u6846\u67B6. \u8FD9\u6837\u6765\u8BF4\u793E\u4F1A\u95EE\u9898, \u4E0D\u6BD4\u8BF4\u628A\u5927\u8C61\u88C5\u51B0\u7BB1\u4E00\u5171\u5206\u4E09\u6B65\u66F4\u6709\u6307\u5BFC\u5B9E\u8DF5\u7684\u4EF7\u503C. \u6240\u4EE5\u6211\u8981\u5F3A\u8C03\u7684\u90A3\u4E2A &quot;\u52A1\u672C&quot; \u7684 &quot;\u52A1&quot; \u5B57\u7528\u7684\u597D. \u5E76\u4E0D\u662F\u6211\u4EEC\u77E5\u9053\u4E86 &quot;\u672C&quot; \u662F\u4EC0\u4E48\u4E86, \u5C31\u80FD\u77E5\u9053\u89E3\u51B3\u7684\u65B9\u6CD5\u548C\u8DEF\u5F84, \u800C\u662F\u53BB &quot;\u52A1&quot;: \u53BB\u5230\u771F\u5B9E\u7684, \u5177\u4F53\u7684\u4EBA\u4E0E\u4EBA\u5173\u7CFB\u7684\u7EC6\u8282\u4E0A\u53BB\u64CD\u5FC3, \u624D\u80FD &quot;\u672C\u7ACB\u800C\u9053\u751F&quot;.</p><p>\u7136\u800C\u8BF4 &quot;\u72B6\u4F3C\u5723\u4EBA&quot; , \u4E5F\u5C31\u662F\u8BF4 &quot;\u8FD8\u4E0D\u662F\u5723\u4EBA&quot;, \u4E0D\u7136\u4E5F\u5C31\u6709\u7528 &quot;\u72B6\u4F3C&quot; \u4E86. \u4ECE\u6709\u5B50\u540C\u5B66\u7ED9\u51FA\u7684\u89E3\u51B3\u601D\u8DEF\u770B, \u4F3C\u4E4E\u793E\u4F1A\u7EB7\u4E71\u7684\u95EE\u9898\u5F52\u6839\u5230\u5E95\u53EA\u662F\u72AF\u4E0A\u4F5C\u4E71\u7684\u95EE\u9898. \u597D\u50CF\u53EA\u8981\u89E3\u51B3\u4E86\u72AF\u4E0A\u4F5C\u4E71\u7684\u95EE\u9898, \u5C31\u4E07\u4E8B\u5927\u5409\u4E86. \u4F46\u5C31\u662F\u4E0D\u662F\u5723\u4EBA\u7684\u6211\u4EEC, \u4E5F\u80FD\u60F3\u5230, \u8981\u662F &quot;\u4E0A&quot; \u672A\u88AB &quot;\u72AF&quot; \u81EA\u5DF1\u4E71\u6389\u4E86\u600E\u4E48\u529E? \u5728\u90A3\u6837\u7684\u60C5\u51B5\u4E0B, \u541B\u5B50\u53C8\u5F53\u5982\u4F55\u52A1\u672C?</p><p>\u8FD9\u5927\u6982\u662F\u6211\u4EEC\u4EE5\u540E\u7684\u5B66\u4E60, \u8981\u53BB\u63A2\u8BA8\u7684\u95EE\u9898\u4E86.</p>',10),p=[_];function a(c,r){return o(),u("div",null,p)}var s=t(e,[["render",a],["__file","\u5B66\u800C\u7B2C\u4E00\u7B2C\u4E8C\u7AE0.html.vue"]]);export{s as default};
