import{_ as l,o as e,c as i,e as o}from"./app.6bf529a6.js";const c={},d=o('<h1 id="\u5C0F\u5E0C\u5E87\u4E9A\u7BC7\u8BF4\u4E86\u5565" tabindex="-1"><a class="header-anchor" href="#\u5C0F\u5E0C\u5E87\u4E9A\u7BC7\u8BF4\u4E86\u5565" aria-hidden="true">#</a> \u5C0F\u5E0C\u5E87\u4E9A\u7BC7\u8BF4\u4E86\u5565</h1><h2 id="\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#\u80CC\u666F" aria-hidden="true">#</a> \u80CC\u666F</h2><p><code>\u5E0C\u5E87\u4E9A</code> \u662F\u4E00\u540D\u667A\u8005. \u5728\u4E00\u6B21\u6F14\u8BB2\u8868\u6F14\u540E, <code>\u6B27\u72C4\u5E93</code> \u9080\u8BF7 <code>\u82CF\u683C\u62C9\u5E95</code> \u5BF9\u4ED6\u7684\u6F14\u8BB2\u8868\u6F14\u8FDB\u884C\u8BC4\u8BBA.</p><p>\u4E8E\u662F <code>\u82CF\u683C\u62C9\u5E95</code> \u5411 <code>\u5E0C\u5E87\u4E9A</code> \u95EE\u4E86\u5173\u4E8E <code>\u963F\u5580\u7409\u65AF</code> \u548C <code>\u5965\u5FB7\u4FEE\u65AF</code> \u4E24\u4F4D\u82F1\u96C4\u54EA\u4E2A\u66F4\u597D\u7684\u95EE\u9898.</p><p><code>\u5E0C\u5E87\u4E9A</code> \u8BA4\u4E3A <code>\u8377\u9A6C</code> \u60F3\u628A <code>\u963F\u5580\u7409\u65AF</code> \u8BF4\u6210\u662F\u6700\u52C7\u6562\u7684\u4EBA, \u628A <code>\u6D85\u65AF\u6258\u8033</code> \u8BF4\u6210\u662F\u6700\u806A\u660E\u7684\u4EBA, \u628A <code>\u5965\u5FB7\u4FEE\u65AF</code> \u8BF4\u6210\u662F\u6700\u72E1\u733E\u7684\u4EBA. \u4ED6\u8FD8\u4E3E\u4E86\u8377\u9A6C\u7684 <code>\u7948\u7977\u8BCD</code> \u6BB5\u843D\u6765\u8BC1\u660E, <code>\u5965\u5FB7\u4FEE\u65AF</code> \u662F\u6700\u72E1\u733E\u7684\u4EBA:</p><blockquote><p>\u963F\u5580\u7409\u65AF\u5BF9\u5965\u5FB7\u4FEE\u65AF\u8BF4: &quot;\u62C9\u57C3\u5C14\u7279\u4E4B\u5B50, \u5929\u795E\u7684\u540E\u88D4, \u8DB3\u667A\u591A\u8C0B\u7684\u5965\u5FB7\u4FEE, \u6211\u4F1A\u628A\u5FC3\u7406\u60F3\u8981\u7684\u505A\u7684\u4E8B\u660E\u660E\u767D\u767D\u5730\u8BF4\u51FA\u6765, \u6211\u76F8\u4FE1\u6211\u4E00\u5B9A\u4F1A\u8FD9\u6837\u505A. \u6709\u4E9B\u4EBA\u5FC3\u7406\u60F3\u7684\u662F\u4E00\u56DE\u4E8B, \u5634\u4E0A\u8BF4\u7684\u662F\u53E6\u4E00\u56DE\u4E8B, \u8FD9\u79CD\u4EBA\u5C31\u50CF\u51A5\u738B\u7684\u5927\u95E8\u90A3\u6837\u53EF\u6068. \u800C\u6211\u5FC3\u91CC\u600E\u4E48\u60F3, \u5634\u4E0A\u5C31\u600E\u4E48\u8BF4.&quot;</p></blockquote><h2 id="\u8BDA\u5B9E\u7684\u4EBA\u548C\u865A\u5047\u7684\u4EBA\u662F\u4E00\u6837\u7684" tabindex="-1"><a class="header-anchor" href="#\u8BDA\u5B9E\u7684\u4EBA\u548C\u865A\u5047\u7684\u4EBA\u662F\u4E00\u6837\u7684" aria-hidden="true">#</a> \u8BDA\u5B9E\u7684\u4EBA\u548C\u865A\u5047\u7684\u4EBA\u662F\u4E00\u6837\u7684</h2><p>\u4F46\u82CF\u683C\u62C9\u5E95\u8BA4\u4E3A\u8BDA\u5B9E\u7684\u4EBA\u548C\u865A\u5047\u7684\u4EBA\u662F\u4E00\u6837\u7684. \u4ED6\u7684\u8BBA\u8BC1\u5982\u4E0B:</p><ul><li>\u9996\u5148\u8BBA\u8BC1\u4E86, \u865A\u4F2A\u4E4B\u4EBA\u662F\u6709\u77E5\u8BC6\u7684, \u806A\u660E\u7684\u4EBA <ul><li>\u865A\u4F2A\u4E4B\u4EBA\u6709\u80FD\u529B\u6B3A\u9A97, \u56E0\u6B64\u865A\u4F2A\u4E4B\u4EBA\u662F\u6709\u80FD\u529B\u7684</li><li>\u865A\u4F2A\u4E4B\u4EBA\u7528\u4EE5\u6B3A\u9A97\u7684, \u662F\u4ED6\u7684\u7CBE\u660E, \u56E0\u6B64\u865A\u4F2A\u4E4B\u4EBA\u662F\u7CBE\u660E\u7684</li><li>\u865A\u4F2A\u4E4B\u4EBA\u7528\u662F\u660E\u77E5\u81EA\u5DF1\u5728\u6B3A\u9A97\u800C\u5728\u6B3A\u9A97\u7684, \u56E0\u6B64\u865A\u4F2A\u4E4B\u4EBA\u662F\u6709\u77E5\u8BC6\u7684.</li></ul></li><li>\u63A5\u7740\u8BBA\u8BC1\u4E86, \u6B3A\u9A97\u662F\u79CD\u80FD\u529B, \u6709\u8FD9\u79CD\u80FD\u529B\u7684\u4EBA\u662F\u806A\u660E\u7684, \u5426\u5219\u662F\u611A\u8822\u7684. <ul><li>\u865A\u4F2A\u4E4B\u4EBA\u62E5\u6709\u628A\u865A\u5047\u7684\u4E8B\u60C5\u8BF4\u51FA\u6765\u7684\u80FD\u529B</li><li>\u56E0\u6B64\u865A\u4F2A\u4E4B\u4EBA\u7684\u806A\u660E\u5728\u4E8E\u5176\u8BF4\u5047\u8BDD\u7684\u80FD\u529B</li><li>\u800C\u6CA1\u6709\u8FD9\u79CD\u80FD\u529B\u7684\u4EBA\u662F\u611A\u8822\u7684.</li></ul></li><li>\u800C\u540E\u8BBA\u8BC1\u4E86, \u8BF4\u865A\u5047\u4E4B\u4E8B\u7684\u80FD\u529B\u548C\u8BF4\u771F\u5B9E\u4E4B\u4E8B\u7684\u80FD\u529B\u662F\u4E00\u4E2A\u80FD\u529B <ul><li>\u6709\u80FD\u529B\u4E4B\u4EBA\u624D\u80FD\u6309\u81EA\u5DF1\u7684\u5E0C\u671B\u505A\u4E8B, \u5982\u5199\u51FA\u540D\u5B57\u6216\u505A\u7B97\u672F\u9898.</li><li>\u53EA\u6709\u6709\u80FD\u529B\u8BF4\u771F\u8BDD, \u624D\u6709\u80FD\u529B\u8BF4\u5047\u8BDD <ul><li>\u56E0\u4E3A\u5982\u679C\u6CA1\u6709\u80FD\u529B\u8BF4\u771F\u8BDD, \u5728\u8BF4\u5047\u8BDD\u7684\u65F6\u5019, \u5C31\u6709\u53EF\u80FD\u5076\u7136\u5730\u78B0\u5230\u771F\u76F8.</li></ul></li><li>\u6240\u4EE5\u4E00\u4E2A\u4EBA\u5728\u54EA\u4E2A\u65B9\u9762\u8BF4\u771F\u8BDD, \u4ED6\u5C31\u6709\u80FD\u529B\u5728\u54EA\u4E2A\u65B9\u9762\u8BF4\u5047\u8BDD.</li></ul></li></ul><p>\u5728\u8BF4\u5B8C\u4E0A\u8FF0\u8BBA\u8BC1\u540E, <code>\u82CF\u683C\u62C9\u5E95</code> \u53C8\u4E3E\u4E86\u5F88\u591A\u8865\u5145\u7684\u4F8B\u5B50\u6765\u8BF4\u660E\u8FD9\u4E8B, \u6BD4\u5982\u51E0\u4F55\u5B66, \u5929\u6587\u5B66, \u548C <code>\u5E0C\u5E87\u4E9A</code> \u6240\u4F1A\u4E00\u5207\u5176\u5B83\u6280\u80FD.</p><p>\u56E0\u6B64 <code>\u82CF\u683C\u62C9\u5E95</code> \u5F97\u51FA\u7ED3\u8BBA, \u5982\u679C <code>\u5965\u5FB7\u4FEE\u65AF</code> \u662F\u865A\u4F2A\u7684, \u90A3\u4E48\u4ED6\u4E5F\u662F\u8BDA\u5B9E\u7684, \u5982\u679C <code>\u963F\u5580\u7409\u65AF</code> \u662F\u8BDA\u5B9E\u7684, \u90A3\u4E48\u4ED6\u4E5F\u662F\u865A\u4F2A\u7684. \u82CF\u683C\u62C9\u5E95\u8FD8\u4E3E\u51FA\u4E86\u4F8B\u5B50:</p><blockquote><p>\u7A0D\u540E, \u963F\u5580\u7409\u65AF\u8BF4\u81EA\u5DF1\u4E0D\u4F1A\u542C\u4ECE\u5965\u5FB7\u4FEE\u65AF\u548C\u963F\u4F3D\u95E8\u519C\u7684\u529D\u544A, \u4E5F\u4E0D\u4F1A\u7559\u5728\u7279\u6D1B\u4F0A, \u800C\u662F\u5982\u4ED6\u81EA\u5DF1\u8BF4\u7684: &quot;\u660E\u5929\u6211\u4F1A\u5411\u5B99\u65AF\u548C\u5168\u4F53\u5929\u795E\u732E\u796D, \u7136\u540E\u6211\u4F1A\u628A\u8239\u53EA\u88C5\u4E0A\u8D27\u7269, \u62D6\u5230\u6D77\u4E0A, \u53EA\u8981\u4F60\u613F\u610F, \u8FD9\u6837\u7684\u4E8B\u8FD8\u503C\u5F97\u4F60\u5173\u5FC3, \u90A3\u4E48\u4F60\u5C31\u4F1A\u770B\u5230, \u62C2\u6653\u65F6\u6211\u7684\u8239\u5C31\u4F1A\u822A\u884C\u5728\u591A\u9C7C\u7684\u8D6B\u52D2\u65AF\u65C1\u7684\u6D77\u4E0A, \u6211\u7684\u4EBA\u70ED\u5FC3\u5212\u6868; \u8981\u662F\u90A3\u4F4D\u95FB\u540D\u7684\u9707\u64BC\u5927\u5730\u7684\u6D77\u795E\u8D50\u6211\u987A\u5229\u7684\u822A\u884C, \u7B2C\u4E09\u5929\u6211\u5C31\u80FD\u5230\u8FBE\u571F\u5730\u80A5\u6D3B\u7684\u5F17\u63D0\u4E9A.&quot;</p></blockquote><blockquote><p>\u8FD8\u6709, \u5728\u6B64\u4E4B\u524D, \u4ED6\u5728\u8FB1\u9A82\u52A0\u4F3D\u95E8\u519C\u65F6\u8BF4: &quot;\u6211\u73B0\u5728\u8981\u56DE\u5F17\u63D0\u4E9A, \u5E26\u7740\u6211\u90A3\u6709\u7740\u9E1F\u5634\u5F62\u7684\u8239\u5934\u7684\u6218\u8239, \u90A3\u6837\u8981\u597D\u5F97\u591A, \u6211\u53EF\u4E0D\u60F3\u5728\u8FD9\u91CC\u5FCD\u53D7\u4F60\u4EEC\u7684\u4FAE\u8FB1, \u4E3A\u4F60\u6323\u5F97\u8D22\u4EA7\u548C\u91D1\u94B1.&quot;</p></blockquote><p><code>\u5E0C\u5E87\u4E9A</code> \u63D0\u51FA\u53CD\u5BF9\u610F\u89C1, \u8BA4\u4E3A <code>\u963F\u5580\u7409\u65AF</code> \u8BF4\u5047\u8BDD\u5E76\u975E\u6545\u610F, \u800C <code>\u5965\u5FB7\u4FEE\u65AF</code> \u8BF4\u5047\u8BDD\u5374\u662F\u6545\u610F\u7684.</p><p><code>\u82CF\u683C\u62C9\u5E95</code> \u8BF4, \u8FD9\u6837\u770B\u6765, <code>\u5965\u5FB7\u4FEE\u65AF</code> \u8981\u6BD4 <code>\u963F\u5580\u7409\u65AF</code> \u8981\u597D\u4E86.</p><h2 id="\u6709\u610F\u8BC6\u72AF\u9519\u597D\u8FC7\u65E0\u610F\u8BC6\u72AF\u9519" tabindex="-1"><a class="header-anchor" href="#\u6709\u610F\u8BC6\u72AF\u9519\u597D\u8FC7\u65E0\u610F\u8BC6\u72AF\u9519" aria-hidden="true">#</a> \u6709\u610F\u8BC6\u72AF\u9519\u597D\u8FC7\u65E0\u610F\u8BC6\u72AF\u9519</h2><p><code>\u82CF\u683C\u62C9\u5E95</code> \u4E8E\u662F\u4E3E\u51FA\u4E00\u7CFB\u5217\u4F8B\u5B50\u8BC1\u660E\u6709\u610F\u8BC6\u7684\u72AF\u9519\u597D\u8FC7\u65E0\u610F\u8BC6\u72AF\u9519:</p><ul><li>\u5173\u4E8E\u4EBA\u7684\u6D3B\u52A8\u7684\u4F8B\u5B50 <ul><li>\u8DD1\u6B65: \u80FD\u8DD1\u5FEB\u800C\u6545\u610F\u8DD1\u6162\u8981\u597D\u8FC7\u56E0\u8DD1\u4E0D\u5FEB\u800C\u8DD1\u6162</li><li>\u6454\u8DE4: \u80FD\u6454\u5012\u522B\u4EBA\u800C\u6545\u610F\u88AB\u522B\u4EBA\u6454\u5012, \u8981\u597D\u8FC7\u672C\u6765\u8BF4\u6454\u4E0D\u8FC7\u522B\u4EBA\u88AB\u522B\u4EBA\u6454\u5012.</li></ul></li><li>\u5173\u4E8E\u4EBA\u7684\u8EAB\u4F53\u7684\u4F8B\u5B50 <ul><li>\u5F62\u4F53: \u6545\u610F\u505A\u51FA\u4E0D\u4F18\u96C5\u7684\u52A8\u4F5C\u597D\u8FC7\u8EAB\u4F53\u672C\u8EAB\u7F3A\u9677\u800C\u663E\u51FA\u4E0D\u4F18\u96C5\u6765</li><li>\u58F0\u97F3: \u6545\u610F\u5531\u6B4C\u8DD1\u8C03\u597D\u8FC7\u672C\u6765\u5C31\u5531\u6B4C\u4E0D\u5728\u8C03\u4E0A</li><li>\u7638\u817F: \u6545\u610F\u7638\u7684\u817F\u597D\u8FC7\u5929\u751F\u7638\u7684\u817F.</li><li>\u7728\u773C: \u6709\u610F\u8BC6\u7684\u7728\u773C\u597D\u8FC7\u65E0\u610F\u8BC6\u4E0D\u81EA\u89C9\u7684\u7728\u773C.</li></ul></li><li>\u5173\u4E8E\u5668\u5177\u7684\u4F8B\u5B50 <ul><li>\u8235: \u505A\u7528\u8FC7\u7A0B\u6545\u610F\u5F04\u504F\u65B9\u5411\u7684\u8235\u597D\u8FC7\u5173\u975E\u6545\u610F\u5F04\u504F\u65B9\u5411\u7684\u8235.</li><li>\u5F13, \u7434, \u7B1B\u4EE5\u53CA\u5176\u4ED6\u4E1C\u897F</li><li>\u9A6C, \u72D7, \u5176\u5B83\u52A8\u7269</li></ul></li><li>\u5173\u4E8E\u6280\u827A\u7684\u4F8B\u5B50 <ul><li>\u5C04\u7BAD: \u6545\u610F\u5C04\u4E0D\u4E2D\u9776\u5B50\u7684\u5F13\u7BAD\u624B\u8981\u6BD4\u672C\u6765\u5C31\u5C04\u4E0D\u4E2D\u9776\u5B50\u7684\u5F13\u7BAD\u624B\u597D.</li><li>\u533B\u672F: \u6545\u610F\u4F24\u5BB3\u8EAB\u4F53\u7684\u533B\u672F\u8981\u6BD4\u5E76\u975E\u6545\u610F\u800C\u4F24\u5BB3\u8EAB\u4F53\u8981\u597D</li><li>\u5974\u96B6\u7684\u54C1\u6027: \u6545\u610F\u72AF\u9519\u7684\u5974\u96B6\u8981\u597D\u8FC7\u5173\u975E\u72AF\u9519\u7684\u5974\u96B6</li></ul></li><li>\u5173\u4E8E\u5FC3\u7075\u7684\u4F8B\u5B50</li></ul><p>\u5728\u8BF4\u5230\u5173\u4E8E\u5FC3\u7075\u7684\u4F8B\u5B50\u65F6, <code>\u5E0C\u5E87\u4E9A</code> \u7EC8\u4E8E\u63D0\u51FA\u4E86\u53CD\u5BF9\u7684\u610F\u89C1, \u4ED6\u4E0D\u80FD\u8BA4\u540C\u6545\u610F\u4F5C\u6076\u7684\u5FC3\u7075\u8981\u6BD4\u5173\u975E\u6545\u610F\u4F5C\u6076\u7684\u5FC3\u7075\u8981\u597D.</p><h2 id="\u7075\u9B42\u662F\u4E0D\u662F\u4E5F\u662F\u6545\u610F\u4F5C\u6076\u597D\u8FC7\u65E0\u610F\u4F5C\u6076" tabindex="-1"><a class="header-anchor" href="#\u7075\u9B42\u662F\u4E0D\u662F\u4E5F\u662F\u6545\u610F\u4F5C\u6076\u597D\u8FC7\u65E0\u610F\u4F5C\u6076" aria-hidden="true">#</a> \u7075\u9B42\u662F\u4E0D\u662F\u4E5F\u662F\u6545\u610F\u4F5C\u6076\u597D\u8FC7\u65E0\u610F\u4F5C\u6076</h2><ul><li>\u6B63\u4E49\u662F\u7075\u9B42\u7684\u529B\u91CF\u548C\u77E5\u8BC6</li><li>\u7075\u9B42\u7684\u529B\u91CF\u8D8A\u5927, \u8D8A\u667A\u6167, \u5C31\u8D8A\u6B63\u4E49.</li><li>\u7075\u9B42\u8D8A\u6709\u529B\u91CF\u548C\u667A\u6167, \u5C31\u66F4\u52A0\u80FD\u5E72\u7684\u884C\u5584\u548C\u4F5C\u6076.</li><li>\u4F5C\u6076\u7684\u7075\u9B42\u51ED\u529B\u91CF\u548C\u6280\u827A\u4F5C\u6076.</li><li>\u505A\u4E0D\u6B63\u4E49\u7684\u4E8B\u5C31\u662F\u4F5C\u6076</li><li>\u6545\u610F\u4F5C\u6076\u7684\u7075\u9B42\u6709\u6B63\u4E49\u7684\u80FD\u529B, \u662F\u597D\u7684\u7075\u9B42, \u800C\u5E76\u975E\u6545\u610F\u4F5C\u6076\u7684\u7075\u9B42\u6CA1\u6709\u6B63\u4E49\u7684\u80FD\u529B, \u662F\u574F\u7684\u7075\u9B42</li><li>\u6709\u597D\u7075\u9B42\u7684\u662F\u597D\u4EBA, \u6CA1\u6709\u597D\u7075\u9B42\u7684\u662F\u574F\u4EBA.</li><li>\u6240\u4EE5\u6545\u610F\u4F5C\u6076\u7684\u4EBA\u662F\u597D\u4EBA, \u800C\u5E76\u975E\u6545\u610F\u4F5C\u6076\u7684\u4EBA\u662F\u574F\u4EBA.</li></ul><p>\u8BBA\u8BC1\u5230\u8FD9\u91CC, <code>\u5E0C\u5E87\u4E9A</code> \u65E0\u6CD5\u63A5\u53D7\u8FD9\u4E2A\u8BBA\u8BC1. \u8BBA\u8BC1\u5230\u8FD9\u91CC\u7ED3\u675F. \u5168\u7BC7\u5B8C.</p>',22),a=[d];function u(t,r){return e(),i("div",null,a)}var p=l(c,[["render",u],["__file","\u5C0F\u5E0C\u5E87\u4E9A\u7BC7\u8BF4\u4E86\u5565.html.vue"]]);export{p as default};
