import{_ as p,o,c as t,e}from"./app.efeddd24.js";const u={},a=e('<h1 id="\u5C0F\u767D\u5199\u7ED9\u5C0F\u767D\u7684\u533A\u5757\u94FE\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#\u5C0F\u767D\u5199\u7ED9\u5C0F\u767D\u7684\u533A\u5757\u94FE\u7B80\u4ECB" aria-hidden="true">#</a> \u5C0F\u767D\u5199\u7ED9\u5C0F\u767D\u7684\u533A\u5757\u94FE\u7B80\u4ECB</h1><p>\u672C\u6587\u662F\u4E00\u7BC7\u6587\u79D1\u751F\u5199\u7684\u6280\u672F\u666E\u53CA\u6587.</p><p>...</p><p>...</p><p>\u5982\u679C\u4F60\u73B0\u5728\u8FD8\u6CA1\u6709\u70B9\u51FB\u5DE6\u4E0A\u89D2\u7684\u5173\u95ED\u6309\u94AE, \u8BF4\u660E\u4F60\u662F\u4E2A\u5F88\u53CD\u667A\u7684\u4EBA\u54E6~~</p><p>\u4F5C\u4E3A\u6587\u79D1\u751F\u5199\u7684\u6587\u7AE0, \u90A3\u6211\u4EEC\u4E0D\u59A8\u5E26\u7740\u5386\u53F2\u7684\u773C\u5149\u6765\u770B\u8FD9\u4E2A\u8BDD\u9898.</p><blockquote><p>\u8FD9\u4F4D\u8BFB\u8005\u8001\u7237, \u8BF7\u653E\u4E0B\u624B\u4E2D\u7684\u677F\u7816, \u8FD9\u6B21\u6211\u4EEC\u4E0D\u7528\u4ECE\u4E8C\u6218\u524D\u540E\u8BB2\u8D77....</p></blockquote><p>\u8BA9\u6211\u4EEC\u56DE\u5230\u533A\u5757\u94FE\u8BDE\u751F\u4E4B\u521D, \u770B\u4E00\u4E0B <strong>\u4E2D\u672C\u806A</strong> \u5927\u795E\u7684 <strong>\u6BD4\u7279\u5E01\u767D\u76AE\u4E66</strong>, \u5E76\u4E14\u8BD5\u56FE\u56DE\u7B54\u4E24\u4E2A\u95EE\u9898:</p><ol><li>\u4ED6\u6574\u4E2A\u6BD4\u7279\u5E01\u51FA\u6765\u662F\u4E3A\u4E86\u89E3\u51B3\u4EC0\u4E48\u95EE\u9898;</li><li>\u4ED6\u662F\u5982\u4F55\u89E3\u51B3\u7684.</li></ol><blockquote><p>\u65E2\u7136\u672C\u6587\u5B9A\u4F4D\u662F\u5C0F\u767D\u8BB2\u7ED9\u5C0F\u767D, \u6211\u5C31\u4E0D\u8D34\u90A3\u4E9B\u7B14\u8005\u548C\u8BFB\u8005\u90FD\u770B\u4E0D\u61C2\u7684\u539F\u6587\u4E86.</p></blockquote><h2 id="\u675C\u7EDD\u56DE\u6EDA" tabindex="-1"><a class="header-anchor" href="#\u675C\u7EDD\u56DE\u6EDA" aria-hidden="true">#</a> \u675C\u7EDD\u56DE\u6EDA</h2><p>\u5F53\u7136, \u767D\u76AE\u4E66\u6CA1\u6709\u660E\u786E\u5199 &quot;\u6253\u5012\u7F8E\u8054\u50A8&quot; \u7684\u9769\u547D\u53E3\u53F7.</p><p>\u5728\u7B80\u4ECB\u90E8\u5206, \u767D\u76AE\u4E66\u8BF4, \u4E3A\u4EC0\u4E48\u9700\u8981\u4E00\u79CD &quot;\u70B9\u5BF9\u70B9\u7684\u7535\u5B50\u73B0\u91D1\u7CFB\u7EDF&quot;? \u662F\u56E0\u4E3A &quot;\u675C\u7EDD\u56DE\u6EDA&quot;. \u675C\u7EDD\u597D\u8BF4, \u4EC0\u4E48\u662F &quot;\u56DE\u6EDA&quot; \u5462? \u8BF7\u4E0D\u8981\u591A\u60F3, \u5C31\u662F &quot;\u6EDA\u56DE\u53BB&quot; \u7684\u610F\u601D.</p><p>\u8BF7\u628A\u652F\u4ED8\u4E1A\u52A1\u60F3\u8C61\u6210\u5411\u524D\u6EDA\u52A8\u7684\u8F66\u8F6E, \u6709\u7684\u65F6\u5019\u6D41\u7A0B\u8D70\u5230\u4E00\u534A, \u63A8\u8F66\u7684\u8001\u6C49\u7A81\u7136\u89C9\u5F97\u4E0D\u5408\u9002, \u53C8\u628A\u8F66\u7ED9\u62C9\u56DE\u6765\u4E86, \u5C31\u662F\u56DE\u6EDA.</p><p>\u8BF4\u767D\u4E86, \u6240\u8C13 &quot;\u675C\u7EDD\u56DE\u6EDA&quot; \u5C31\u662F\u7981\u6B62\u64A4\u9500\u652F\u4ED8\u884C\u4E3A.</p><p>\u54EA\u4E3A\u4EC0\u4E48\u8981 &quot;\u675C\u7EDD\u56DE\u6EDA&quot; \u5462, \u767D\u76AE\u4E66\u8BF4\u4E86, \u4E92\u8054\u7F51\u4E0A\u7684\u5546\u54C1\u6709\u5F88\u591A\u662F\u6570\u5B57\u5546\u54C1\u6216\u670D\u52A1, \u8FD9\u4E9B\u4E1C\u897F\u8005\u5F88\u96BE\u9000\u8D27, \u5982\u679C\u652F\u4ED8\u53EF\u4EE5\u64A4\u9500, \u90A3\u4E48 &quot;\u4E92\u8054\u7F51\u7684\u8D38\u6613\u4F1A\u5927\u5927\u53D7\u9650&quot;.</p><blockquote><p>\u4E3E\u4E2A\u4F8B\u5B50, \u4F60\u62FF 10 \u5757\u94B1\u5411\u6211\u4E70\u4E86\u4E00\u90E8\u82CD\u8001\u5E08\u7684\u6559\u5B66\u7247, \u6211\u628A\u7247\u5B50\u53D1\u7ED9\u4F60\u4E86, \u4F60\u8BF4\u8981\u9000\u8D27, \u628A\u7247\u5B50\u53C8\u7ED9\u6211\u53D1\u56DE\u6765, \u6211\u77E5\u9053\u4F60\u6709\u6CA1\u6709\u518D\u62F7\u8D1D.</p></blockquote><p>\u4F30\u8BA1\u8BFB\u5230\u8FD9\u4E2A\u65F6\u5019, \u61C2\u6280\u672F\u7684\u8BFB\u8005\u8001\u7237\u5C31\u8BE5\u6380\u684C\u5B50\u4E86: \u9632\u6B62\u64A4\u9500\u652F\u4ED8\u7684\u6280\u672F\u5728\u533A\u5757\u94FE\u4EA7\u751F\u524D, \u65E9\u5C31\u89E3\u51B3\u4E86.</p><p>\u9996\u5148, \u8BFB\u8005\u8001\u7237\u8BF4\u7684\u5BF9!! \u4F46\u662F\u767D\u76AE\u4E66\u7684\u4E3B\u5F20\u8981\u66F4\u6FC0\u8FDB\u4E00\u4E9B.</p><p>\u767D\u76AE\u4E66\u8BF4, \u76EE\u524D\u4E3B\u8981\u7684\u7EBF\u4E0A\u652F\u4ED8\u7684\u624B\u6BB5, \u4E3A\u4E86\u907F\u514D &quot;\u53CC\u91CD\u4EA4\u6613&quot; \u7684\u95EE\u9898, \u5FC5\u987B\u5F15\u5165\u4E00\u4E2A\u4E2D\u5FC3\u5316\u7684\u7B2C\u4E09\u65B9\u7684\u4FE1\u7528\u673A\u6784\u624D\u884C. \u53EA\u8981\u5B58\u5728\u4E00\u4E2A\u4E2D\u5FC3\u5316\u7684\u4FE1\u7528\u673A\u6784\u63A7\u5236\u652F\u4ED8, \u522B\u8BF4\u56DE\u6EDA\u4E86, \u4ED6\u60F3\u8BA9\u4F60\u600E\u4E48\u6EDA, \u4F60\u5C31\u5F97\u600E\u4E48\u6EDA, \u60F3\u8BA9\u4F60\u6EDA\u591A\u8FDC, \u4F60\u5C31\u5F97\u6EDA\u591A\u8FDC.</p><h2 id="\u53CC\u91CD\u4EA4\u6613" tabindex="-1"><a class="header-anchor" href="#\u53CC\u91CD\u4EA4\u6613" aria-hidden="true">#</a> \u53CC\u91CD\u4EA4\u6613</h2><p>\u4E3E\u4E2A\u5927\u5BB6\u559C\u95FB\u4E50\u89C1\u7684\u4F8B\u5B50, \u6BD4\u5982\u5356\u623F\u5B50, \u5C31\u662F\u4E00\u4E2A\u7ECF\u5E38\u51FA\u73B0 &quot;\u53CC\u91CD\u4EA4\u6613&quot; \u7684\u4E8B\u60C5.</p><p>\u4ECA\u5929\u5F20\u4E09\u51FA 300 \u4E07, \u6211\u628A\u623F\u5B50\u5356\u7ED9\u5F20\u4E09, \u660E\u5929\u674E\u56DB\u51FA 400 \u4E07, \u6211\u518D\u628A\u540C\u4E00\u4E2A\u623F\u5B50\u5356\u7ED9\u674E\u56DB. \u65E0\u8BBA\u5F20\u4E09\u8FD8\u662F\u674E\u56DB\u90FD\u65E0\u6CD5\u5728\u4EA4\u6613\u65F6\u786E\u8BA4\u8FD9\u4E2A\u623F\u5B50\u53EA\u5356\u7ED9\u4ED6\u4E86.</p><p>\u73B0\u5B9E\u751F\u6D3B\u4E2D\u8FD9\u4E2A\u95EE\u9898\u600E\u4E48\u89E3\u51B3\u7684\u5462? <strong>\u6240\u6709\u6743\u767B\u8BB0\u5236\u5EA6</strong>, \u4E0D\u7BA1\u6211\u7B7E\u591A\u5C11\u4E2A\u5356\u623F\u5408\u540C, \u771F\u6B63\u8F6C\u79FB\u623F\u5C4B\u6240\u6709\u6743\u7684\u884C\u4E3A\u662F\u5230\u623F\u7BA1\u6240\u53D8\u66F4\u6240\u6709\u6743\u767B\u8BB0.</p><p>\u5728\u652F\u4ED8\u4E2D\u89E3\u51B3\u53CC\u91CD\u4EA4\u6613, \u770B\u8D77\u6765\u53EF\u80FD\u6709\u70B9\u590D\u6742:</p><p>\u5728\u7EBF\u4E0A\u652F\u4ED8\u4E2D, \u7532\u5411\u4E59\u652F\u4ED8 10 \u5143\u94B1\u65F6, \u7532\u5E76\u4E0D\u662F\u628A 10 \u5143\u94B1\u7ED9\u5230\u4E86\u4E59, \u800C\u662F\u53D1\u884C\u8FD9 10 \u5143\u94B1\u7684\u673A\u6784 (\u4E5F\u5C31\u662F\u524D\u6587\u4E66\u8BF4\u7684 &quot;\u4E2D\u5FC3\u5316\u7684\u7B2C\u4E09\u65B9\u4FE1\u7528\u673A\u6784&quot;) \u628A\u8FD9 10 \u5143\u94B1\u56DE\u6536, \u518D\u53D1\u7ED9\u4E59\u5D2D\u65B0\u7684 10 \u5143\u94B1.</p><p>\u4E8E\u662F\u5F53\u4E59\u518D\u8981\u7ED9\u4E19\u4ED8\u8FD9 10 \u5143\u94B1\u7684\u65F6\u5019, \u4E19\u4E00\u770B\u8FD9\u94B1\u662F\u4E0D\u662F\u5D2D\u65B0\u7684, (\u67E5\u8BE2\u8FD9\u679A\u6570\u5B57\u8D27\u5E01\u662F\u4E0D\u662F\u65B0\u53D1\u884C\u7684), \u5C31\u53EF\u4EE5\u77E5\u9053\u4E59\u662F\u4E0D\u662F\u5408\u6CD5\u6301\u6709\u8FD9 10 \u5143\u94B1\u4E86.</p><p>\u770B\u8D77\u6709\u70B9\u590D\u6742, \u4F46\u662F\u5982\u679C\u4F60\u628A\u8FD9 10 \u5143\u94B1\u60F3\u8C61\u6210\u623F\u4EA7\u8BC1, \u4F60\u770B\u6574\u4E2A\u8FC7\u7A0B\u662F\u4E0D\u662F\u5C31\u662F\u623F\u7BA1\u6240\u56DE\u6536\u65E7\u8BC1, \u53D1\u884C\u65B0\u8BC1\u7684\u8FC7\u7A0B?</p><p>\u8FD9\u79CD\u89E3\u51B3\u65B9\u6848\u4E2D, \u4EA4\u6613\u53CC\u65B9\u662F\u4E0D\u80FD\u968F\u610F\u64A4\u9500\u652F\u4ED8\u4E86, \u4F46\u662F, \u4E2D\u5FC3\u5316\u7684\u4FE1\u7528\u673A\u6784\u786E\u662F\u53EF\u4EE5\u968F\u610F\u64A4\u9500\u4EFB\u610F\u4E00\u7B14\u652F\u4ED8\u7684. \u6BD4\u7279\u5E01\u60F3\u7684, \u5C31\u662F\u8981\u5728\u4E0D\u5F15\u5165\u8FD9\u4E2A\u4E2D\u5FC3\u5316\u7684\u7B2C\u4E09\u65B9\u4FE1\u7528\u673A\u6784\u7684\u524D\u63D0\u4E0B, \u89E3\u51B3\u53CC\u91CD\u4EA4\u6613\u7684\u95EE\u9898.</p><h2 id="\u6D41\u4F20\u6709\u5E8F-\u7684\u4EA4\u6613\u94FE\u6761" tabindex="-1"><a class="header-anchor" href="#\u6D41\u4F20\u6709\u5E8F-\u7684\u4EA4\u6613\u94FE\u6761" aria-hidden="true">#</a> &quot;\u6D41\u4F20\u6709\u5E8F&quot; \u7684\u4EA4\u6613\u94FE\u6761</h2><p>\u5148\u626F\u70B9\u95F2\u8BDD, \u8BFB\u8005\u8001\u7237, \u4F60\u4EEC\u77E5\u9053\u76EE\u524D\u4E3A\u6B62\u6700\u9760\u8C31\u7684\u53E4\u73A9\u9274\u5B9A\u65B9\u6CD5\u662F\u4EC0\u4E48?</p><p>\u78B3\u5143\u7D20\u9274\u5B9A? \u5236\u4F5C\u5DE5\u827A\u9274\u5B9A? \u827A\u672F\u98CE\u683C\u9274\u5B9A? \u8FD9\u4E9B\u90FD\u4E0D\u662F\u6700\u9760\u8C31\u7684, \u56E0\u4E3A\u53EA\u8981\u6709\u4E00\u79CD\u9274\u5B9A\u65B9\u6CD5\u88AB\u53D1\u660E\u51FA\u6765, \u5C31\u4F1A\u6709\u9488\u5BF9\u8FD9\u79CD\u9274\u5B9A\u65B9\u6CD5\u4EFF\u5236\u6280\u672F\u88AB\u53D1\u660E\u51FA\u6765.</p><p>\u76EE\u524D\u6700\u9760\u8C31\u7684\u9274\u5B9A\u65B9\u6CD5\u53EB\u505A: &quot;\u6D41\u4F20\u6709\u5E8F&quot;. \u4EC0\u4E48\u610F\u601D\u5462? \u4E3E\u4E2A\u4F8B\u5B50:</p><p>\u8FD9\u4E2A\u9752\u82B1\u74F6\u5B50\u662F\u4E7E\u9686\u7237\u8D4F\u7ED9\u7532\u7684, \u7532\u4F20\u7ED9\u4E59, \u4E59\u4F20\u7ED9\u4E19, \u8FD9\u79CD\u4F20\u4E86\u5341\u4EE3\u540E, \u7532\u7684\u4F20\u4EBA\u8F9B\u628A\u8FD9\u4E2A\u74F6\u5B50\u4E0A\u4EA4\u7ED9\u56FD\u5BB6, \u653E\u5728\u67D0\u67D0\u535A\u7269\u9986\u91CC. \u6574\u4E2A\u8FD9\u4E2A\u6D41\u4F20\u8FC7\u7A0B\u6CA1\u6709\u65AD, \u90A3\u4E48\u6211\u4EEC\u8BA4\u4E3A\u8FD9\u4E2A\u67D0\u67D0\u535A\u7269\u9986\u7684\u74F6\u5B50\u662F\u771F\u7684.</p><p>\u8FD9\u5C31\u662F &quot;\u6D41\u4F20\u6709\u5E8F&quot; .</p><p>\u6211\u4EEC\u56DE\u5230\u623F\u7BA1\u6240\u7684\u4F8B\u5B50, \u6211\u4EEC\u4E3A\u4EC0\u4E48\u76F8\u4FE1\u623F\u7BA1\u6240\u7684\u767B\u8BB0? \u56E0\u4E3A\u623F\u7BA1\u6240\u7684\u767B\u8BB0\u662F &quot;\u6D41\u4F20\u6709\u5E8F&quot; \u7684. \u4ECE\u623F\u5B50\u9020\u51FA\u6765\u7684\u65F6\u5019, \u8FD9\u4E2A\u623F\u5B50\u7684\u6240\u6709\u6240\u6709\u6743\u6D41\u8F6C\u5173\u7CFB\u90FD\u8BB0\u5F55\u5728\u6848, \u6240\u4EE5\u53EF\u4EE5\u4FDD\u8BC1, \u5230\u76EE\u524D\u4E3A\u6B62, \u8FD9\u4E2A\u623F\u5B50\u767B\u8BB0\u7684\u6240\u6709\u6743\u5F52\u5C5E\u662F\u6E05\u6670\u7684.</p><p>\u540C\u6837, \u6211\u4EEC\u4FE1\u606F\u4E2D\u5FC3\u5316\u4FE1\u7528\u673A\u6784, \u662F\u56E0\u4E3A\u4ED6\u90A3\u91CC\u7684\u6709\u5173\u4E8E\u6570\u5B57\u8D27\u5E01 &quot;\u6D41\u4F20\u6709\u5E8F&quot; \u7684\u6240\u6709\u8BB0\u5F55.</p><p>\u4E0E\u662F\u767D\u76AE\u4E66\u63D0\u51FA\u4E00\u4E2A\u6784\u60F3, \u6211\u4EEC\u4E0D\u8981\u53BB\u4E2D\u5FC3\u5316\u7684\u4FE1\u7528\u673A\u6784\u767B\u8BB0 &quot;\u6D41\u4F20\u6709\u5E8F&quot; \u7684\u6D41\u8F6C\u5173\u7CFB, \u6211\u4EEC\u76F4\u63A5\u628A\u8FD9\u79CD\u6D41\u8F6C\u5173\u7CFB\u8BB0\u5F55\u5728\u8D27\u5E01\u4E0A\u884C\u4E0D\u884C?</p><p>\u5F53\u7532\u7ED9\u4E59\u8FDB\u884C\u652F\u4ED8\u7ED9\u4E59 10 \u5143\u94B1\u7684\u65F6\u5019, \u7532\u7528\u81EA\u5DF1\u7684\u6570\u5B57\u7B7E\u540D\u5728 10 \u5143\u94B1\u4E0A\u7B7E\u4E00\u4E0B\u540D, \u4E59\u4E5F\u7528\u81EA\u5DF1\u7684\u6570\u5B57\u7B7E\u540D\u5728\u8FD9 10 \u5143\u94B1\u4E0A\u7B7E\u4E00\u4E0B\u540D. \u5F53\u4E59\u518D\u5C06\u8FD9 10 \u5143\u94B1\u652F\u4ED8\u7ED9\u4E19\u7684\u65F6\u5019, \u4E19\u62FF\u8FC7\u94B1\u6765\u4E00\u770B, \u54E6, \u4E59\u62E5\u6709\u8FD9 10 \u5143\u94B1\u662F\u901A\u8FC7\u7532\u7684\u5408\u6CD5\u8F6C\u8BA9, \u6211\u53EA\u8981\u5728\u4E2A 10 \u5143\u94B1\u4E0A\u7B7E\u4E00\u4E0B\u540D, \u8FD9 10 \u5143\u94B1\u5C31\u5408\u6CD5\u8F6C\u8BA9\u7ED9\u6211\u4E86.</p><blockquote><p>\u5F53\u7136\u5177\u4F53\u6280\u672F\u5B9E\u73B0\u8981\u6BD4\u8FD9\u590D\u6742\u7684\u591A, \u6BD4\u5982\u7B7E\u540D\u90FD\u7B7E\u5728\u4E86\u94B1\u4E0A, \u8981\u627E\u96F6\u600E\u4E48\u529E? \u4F5C\u4E3A\u5C0F\u767D\u8BFB\u8005\u4F60\u53EA\u9700\u8981\u77E5\u9053, \u7CFB\u7EDF\u641E\u5F97\u5B9A\u5C31 ok \u4E86. \u4EC0\u4E48, \u4F60\u95EE\u6211\u77E5\u4E0D\u77E5\u9053\u5B9E\u73B0\u7EC6\u8282? \u6211\u5F53\u7136\u662F\u77E5\u9053\u7684\u4E86.(\u4E0D\u7BA1\u4F60\u4FE1\u4E0D\u4FE1, \u6211\u53CD\u6B63\u4FE1\u4E86)</p></blockquote><p>\u8FD9\u6837\u4E00\u6765, \u5373\u901A\u8FC7 &quot;\u6D41\u4F20\u6709\u5E8F&quot; \u7684\u4EA4\u6613\u94FE\u6761\u907F\u514D\u4E86\u53CC\u91CD\u652F\u4ED8, \u53C8\u7529\u6389\u4E86\u4E2D\u5FC3\u5316\u4FE1\u7528\u673A\u6784, \u5C82\u4E0D\u7F8E\u54C9.</p><p>\u4F46\u662F\u6211\u4EEC\u77E5\u9053, \u767B\u8BB0\u673A\u6784\u4E0D\u662F\u767D\u5F53\u7684, \u5B83\u4EEC\u8FD8\u6709\u4E00\u4E2A\u91CD\u8981\u5DE5\u4F5C, \u5C31\u662F\u9A8C\u8BC1\u4EA4\u6613. \u73B0\u5728\u628A\u767B\u8BB0\u673A\u6784\u7529\u6389\u4E86, \u8C01\u6765\u505A\u9A8C\u8BC1\u4EA4\u6613\u7684\u5DE5\u4F5C?</p><p>\u7B54\u6848\u662F\u4EA4\u7ED9\u77FF\u5DE5.</p><h2 id="\u77FF\u5DE5\u7684\u5DE5\u4F5C\u4E0E\u5DE5\u4F5C\u91CF\u8BC1\u660E\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#\u77FF\u5DE5\u7684\u5DE5\u4F5C\u4E0E\u5DE5\u4F5C\u91CF\u8BC1\u660E\u673A\u5236" aria-hidden="true">#</a> \u77FF\u5DE5\u7684\u5DE5\u4F5C\u4E0E\u5DE5\u4F5C\u91CF\u8BC1\u660E\u673A\u5236</h2><p>\u597D, \u73B0\u5728\u7532\u7ED9\u4E59\u652F\u4ED8 1 \u4E2A\u6BD4\u7279\u5E01, \u6BD4\u7279\u5E01\u7CFB\u7EDF\u4F1A\u505A\u4E9B\u4EC0\u4E48\u5462?</p><p>\u6BD4\u7279\u5E01\u4F1A\u5411\u6240\u6709\u4F7F\u7528\u6BD4\u7279\u5E01\u7684\u4EBA\u5E7F\u64AD: \u7532\u7ED9\u4E86\u4E59 1 \u4E2A\u6BD4\u7279\u5E01, \u8C01\u6765\u5E2E\u5FD9\u9A8C\u8BC1\u4E00\u4E0B. \u7136\u540E\u628A\u8FD9\u4EA4\u6613\u7684\u4FE1\u606F\u653E\u5728\u4E00\u4E2A\u4EA4\u6613\u6C60\u91CC.</p><p>\u8FD9\u65F6\u5019\u6709\u4E00\u4E2A\u597D\u5FC3\u4EBA\u5F20\u4E09\u6253\u7B97\u5E2E\u5FD9, \u4ED6\u4ECE\u4EA4\u6613\u6C60\u4E2D\u53D6\u51FA\u8FD9\u4E2A\u4EA4\u6613\u4FE1\u606F, \u9A8C\u8BC1\u4EA4\u6613\u7684\u5408\u6CD5\u6027, \u7136\u540E\u628A\u8FD9\u4E2A\u4EA4\u6613\u548C\u5176\u5B83\u5F20\u4E09\u9A8C\u8BC1\u7684\u4EA4\u6613\u653E\u5728\u4E00\u4E2A\u4FE1\u5C01\u91CC, \u76D6\u4E0A\u4E00\u4E2A\u7AE0\u5B50: \u67D0\u5E74\u67D0\u6708\u9A8C\u8BC1\u901A\u8FC7. \u7136\u540E\u4ED6\u628A\u8FD9\u4E2A\u4FE1\u5C01\u5728\u4F7F\u7528\u6BD4\u7279\u5E01\u7684\u4EBA\u7684\u7F51\u7EDC\u4E2D\u516C\u5F00. \u6BD4\u7279\u5E01\u7CFB\u7EDF\u4E3A\u4E86\u9F13\u52B1\u8FD9\u79CD\u4E50\u4E8E\u52A9\u4EBA\u7684\u884C\u4E3A, \u5956\u52B1\u5F20\u4E09\u4E00\u4E9B\u6BD4\u7279\u5E01.</p><p>\u597D\u5FC3\u4EBA\u5F20\u4E09\u662F\u77FF\u5DE5, \u5C06\u7740\u4EA4\u6613\u4FE1\u606F\u7684\u4FE1\u5C01\u53EB\u505A\u533A\u5757, \u67D0\u5E74\u67D0\u6708\u9A8C\u8BC1\u901A\u8FC7\u7684\u7AE0\u5B50\u53EB\u505A\u65F6\u95F4\u6233.</p><p>\u5047\u8BBE\u8FD9\u65F6\u5019\u6709\u53C8\u6709\u4E00\u4E2A\u597D\u5FC3\u4EBA\u674E\u56DB\u4E5F\u6253\u7B97\u5E2E\u5FD9, \u4ED6\u628A\u8FD9\u7B14\u4EA4\u6613\u9A8C\u8BC1\u540E\u653E\u5230\u53E6\u4E00\u4E2A\u4FE1\u5C01\u4E2D, \u8FD9\u4E2A\u65F6\u5019\u7CFB\u7EDF\u4E2D\u5C31\u540C\u65F6\u5B58\u5728\u4E24\u4E2A\u540C\u6837\u6709\u6548\u7684\u533A\u5757, \u8FD9\u79CD\u60C5\u51B5\u5C31\u53EB\u5206\u53C9.</p><p>\u51FA\u73B0\u5206\u53C9\u600E\u4E48\u5904\u7406\u5462? \u6BD4\u7279\u5E01\u7CFB\u7EDF\u8981\u6C42, \u6BCF\u4E00\u4E2A\u533A\u5757\u5FC5\u987B\u53E0\u52A0\u5728\u524D\u4E00\u4E2A\u533A\u5757\u4E0A. \u56DE\u5230\u4FE1\u5C01\u7684\u4F8B\u5B50, \u6BD4\u7279\u5E01\u7CFB\u7EDF\u8981\u6C42\u6BCF\u4E2A\u4FE1\u5C01\u8981\u6709\u4E00\u4E2A\u76EE\u5F55, \u8FD9\u4E2A\u76EE\u5F55\u8BB0\u7740\u5728\u6211\u8FD9\u4E2A\u4FE1\u5C01\u4E4B\u524D\u6709\u54EA\u4E9B\u8BB0\u5F55\u4EA4\u6613\u7684\u4FE1\u606F, \u5E76\u4E14\u6BD4\u7279\u5E01\u7CFB\u7EDF\u8BF4, \u8C01\u7684\u76EE\u5F55\u957F, \u8C01\u725B B. \u6240\u4EE5, \u6240\u6709\u9A8C\u8BC1\u4EA4\u6613\u7684\u597D\u5FC3\u4EBA, \u90FD\u60F3\u5728\u5C3D\u91CF\u957F\u7684\u76EE\u5F55\u540E\u9762\u653E\u81EA\u5DF1\u7684\u4FE1\u5C01.</p><p>\u5F53\u5F20\u4E09\u548C\u674E\u56DB\u90FD\u6709\u4E00\u5408\u6CD5\u7684\u4FE1\u5C01\u7684\u65F6\u5019, \u5982\u679C\u6709\u4E00\u4E2A\u4EBA\u62FF\u4E86\u5F20\u4E09\u7684\u76EE\u5F55\u6240\u4E3A\u81EA\u5DF1\u7684\u4FE1\u5C01\u7684\u57FA\u7840, \u7ACB\u523B\u6240\u6709\u7684\u4EBA, \u90FD\u4F1A\u7EE7\u7EED\u4F7F\u7528\u6709\u5F20\u4E09\u4FE1\u5C01\u7684\u76EE\u5F55, \u800C\u89C6\u674E\u56DB\u7684\u4FE1\u5C01\u5982\u65E0\u7269, \u56E0\u4E3A\u8FD9\u76EE\u5F55\u66F4\u957F\u4E00\u4E9B.</p><p>\u8FD9\u4E2A\u673A\u5236\u5C31\u53EB\u505A\u5DE5\u4F5C\u91CF\u8BC1\u660E, \u56E0\u4E3A\u66F4\u957F\u7684\u76EE\u5F55\u610F\u5473\u7740\u66F4\u591A\u7684\u9A8C\u8BC1\u5DE5\u4F5C\u91CF.</p><p>\u90A3\u4E48, \u5982\u679C\u6709\u4E00\u4E2A\u90AA\u6076\u7EC4\u7EC7\u60F3\u8981\u63A7\u5236\u6BD4\u7279\u5E01\u7CFB\u7EDF, \u4ED6\u8981\u600E\u4E48\u505A\u5462? \u6069, \u4ED6\u6240\u8981\u505A\u7684\u5C31\u662F\u6D3E\u81EA\u5DF1\u7684\u624B\u4E0B\u6DF7\u5165\u597D\u5FC3\u4EBA\u7684\u961F\u4F0D, \u5E76\u4E14\u5360\u5230\u597D\u5FC3\u4EBA\u7684\u4E00\u534A\u4EE5\u4E0A. \u8FD9\u5C31\u662F\u6240\u8C13 &quot;51% \u653B\u51FB&quot;.</p><p>\u6BD4\u7279\u5E01\u5982\u4F55\u963B\u6B62\u90AA\u6076\u7EC4\u7EC7\u5462? \u5B83\u505A\u4E86\u4E2A\u5047\u8BBE, \u505A\u574F\u4E8B\u662F\u8981\u7B97\u6210\u672C\u7684. \u6BD4\u7279\u5E01\u7CFB\u7EDF\u901A\u8FC7\u6280\u672F\u624B\u6BB5, \u8BA9\u81EA\u5DF1\u7684\u4EA4\u6613\u9A8C\u8BC1\u8D8A\u6765\u8D8A\u96BE, \u9A8C\u8BC1\u8D8A\u96BE, \u9A8C\u8BC1\u65F6\u7684\u6240\u9700\u8981\u7684\u7535\u91CF\u5C31\u8D8A\u5927. \u5F53\u6BD4\u7279\u5E01\u7CFB\u7EDF\u8DB3\u591F\u5927\u7684\u65F6\u5019, \u60F3\u8981\u505A\u51FA &quot;51% \u653B\u51FB&quot; \u6240\u8981\u8981\u7684\u7535\u8D39, \u5C31\u4E0D\u662F\u4EC0\u4E48\u968F\u4FBF &quot;\u90AA\u6076\u7EC4\u7EC7&quot; \u53EF\u4EE5\u4EA4\u7684\u8D77\u7684\u4E86.</p><h2 id="\u5199\u5728\u6700\u540E\u7684\u8BDD" tabindex="-1"><a class="header-anchor" href="#\u5199\u5728\u6700\u540E\u7684\u8BDD" aria-hidden="true">#</a> \u5199\u5728\u6700\u540E\u7684\u8BDD</h2><blockquote><p>\u5982\u679C\u73B0\u5728\u4F60\u8FD8\u6CA1\u6709\u70B9\u51FB\u5DE6\u4E0A\u89D2\u7684\u5173\u95ED\u6309\u94AE, \u8BF4\u660E\u4F60\u771F\u662F\u4E2A\u5C0F\u767D\u8BFB\u8005\u4E86.....</p></blockquote><p>\u770B\u5230\u8FD9\u91CC\u4F60\u53EF\u80FD\u89C9\u5F97, \u4F60\u5927\u6982\u4E86\u89E3\u533A\u5757\u94FE\u662F\u600E\u4E48\u56DE\u4E8B\u4E86. \u4F46\u662F\u8BFB\u8005\u8001\u7237, \u53EF\u80FD\u4F60\u8981\u5931\u671B\u4E86. \u4EE5\u4E0A\u6240\u8BF4, \u4EE5\u4E0A\u6240\u8BF4, \u4EC5\u4EC5\u662F\u6BD4\u7279\u5E01\u533A\u5757\u94FE\u7684\u4E00\u4E9B\u5185\u5BB9.</p><p>\u540E\u6765\u6709\u5F88\u591A\u4EBA\u8BA4\u4E3A\u6BD4\u7279\u5E01\u7684\u8BBE\u8BA1\u662F\u6709\u95EE\u9898\u7684, \u6BD4\u5982, \u77FF\u5DE5\u9A8C\u8BC1\u4EA4\u6613\u592A\u8D39\u7535\u4E86; \u6BD4\u5982\u5B83\u5B8C\u5168\u6392\u65A5\u4E2D\u5FC3\u5316\u7CFB\u7EDF\u7684\u5E72\u9884\u5C31\u4E00\u7247\u91D1\u878D\u9886\u57DF\u7684\u6CD5\u5916\u4E4B\u5730\u7B49. \u4E0E\u662F\u51FA\u73B0\u5F88\u591A, \u91C7\u7528\u90E8\u5206\u6BD4\u7279\u5E01\u7406\u5FF5\u7684\u533A\u5757\u94FE\u7CFB\u7EDF.</p><p>\u6BD4\u5982\u6709\u7684\u4EBA\u89C9\u5F97\u5DE5\u4F5C\u91CF\u8BC1\u660E\u592A\u8D39\u7535\u4E86, \u54B1\u4EEC\u7528\u6240\u6709\u4EBA\u6295\u7968\u89E3\u51B3\u5206\u53C9\u597D\u4E0D\u597D? \u6240\u6709\u4EBA\u6295\u7968\u4E5F\u8D39\u52B2, \u90A3\u6539\u6210\u90E8\u5206\u8282\u70B9\u6295\u7968, \u5176\u5B83\u8282\u70B9\u4FE1\u606F\u8FD9\u4E9B\u6295\u7968\u8282\u70B9\u597D\u4E0D\u597D;</p><p>\u6BD4\u5982\u6709\u7684\u4EBA\u89C9\u5F97, \u628A\u9A8C\u8BC1\u8BB0\u5F55\u5728\u6570\u5B57\u8D27\u5E01\u4E0A\u592A\u9EBB\u70E6\u4E86, \u6211\u4EEC\u628A\u4EA4\u6613\u8BB0\u5F55\u5728\u4E00\u4E2A\u8D26\u672C\u4E2D, \u901A\u8FC7\u5BF9\u8D26\u672C\u63D0\u53D6\u5FEB\u7167\u6765\u8FDB\u884C\u9A8C\u8BC1\u884C\u4E0D\u884C.</p><p>\u800C\u4E14\u8D8A\u6765\u8D8A\u7684\u4EBA\u628A\u533A\u5757\u94FE\u5E94\u7528\u7684\u9886\u57DF\u653E\u5728\u6570\u5B57\u8D27\u5E01\u4EE5\u5916\u7684\u5730\u65B9, \u751A\u81F3\u4E8E\u653E\u5728\u975E\u91D1\u878D\u9886\u57DF.</p><p>\u90A3\u4E48\u662F\u4EC0\u4E48\u8BA9\u8FD9\u4E9B\u9762\u76EE\u4E0D\u540C\u7684\u7CFB\u7EDF\u90FD\u53EB\u505A\u533A\u5757\u94FE\u5462?</p><p>\u8BA9\u6211\u4EEC\u56DE\u5230\u672C\u6587\u6700\u521D\u7684\u95EE\u9898: \u533A\u5757\u94FE\u8BBE\u8BA1\u51FA\u6765\u662F\u8981\u89E3\u51B3\u4EC0\u4E48\u95EE\u9898? \u5176\u5B9E\u6211\u4EEC\u770B\u5230, \u65E0\u8BBA\u4EC0\u4E48\u533A\u5757\u94FE\u7CFB\u7EDF\u6700\u7EC8\u60F3\u89E3\u51B3\u7684\u95EE\u9898\u662F: \u5982\u4F55\u5728\u6CA1\u6709\u4E2D\u5FC3\u5316\u4FE1\u7528\u673A\u6784\u7684\u60C5\u51B5\u4E0B\u5EFA\u7ACB\u4E00\u4E2A\u4FE1\u7528\u4F53\u7CFB.</p><p>\u56E0\u6B64\u533A\u5757\u94FE\u4E0D\u662F\u4EC0\u4E48\u573A\u666F\u90FD\u9002\u5408\u7684, \u751A\u81F3\u53EF\u80FD\u533A\u5757\u94FE\u9002\u7528\u7684\u8303\u56F4\u8FD8\u633A\u7A84: \u533A\u5757\u94FE\u9002\u7528\u4E8E\u9700\u8981\u4FE1\u7528\u4F53\u7CFB\u4F46\u4E0D\u5EFA\u7ACB\u4E2D\u5FC3\u5316\u4FE1\u7528\u7CFB\u7EDF\u53C8\u4E0D\u65B9\u4FBF\u6216\u4E0D\u5212\u7B97\u7684\u573A\u666F.</p><blockquote><p>\u5F53\u7136\u8BA4\u53EF\u8FD9\u70B9\u7684\u524D\u63D0\u662F, \u6211\u4EEC\u4E0D\u8BA4\u4E3A\u7F8E\u8054\u50A8\u662F\u4E07\u6076\u4E4B\u6E90.</p></blockquote>',65),q=[a];function r(h,c){return o(),t("div",null,q)}var i=p(u,[["render",r],["__file","\u5C0F\u767D\u8BB2\u7ED9\u5C0F\u767D\u7684\u533A\u5757\u94FE\u7B80\u4ECB.html.vue"]]);export{i as default};
