import{_ as e,o as a,c as t,e as r}from"./app.6bf529a6.js";const o={},h=r('<h1 id="\u67CF\u62C9\u56FE\u5BF9\u8BDD\u5F55\u5B66\u4E60\u5C0F\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u67CF\u62C9\u56FE\u5BF9\u8BDD\u5F55\u5B66\u4E60\u5C0F\u7EC4" aria-hidden="true">#</a> \u67CF\u62C9\u56FE\u5BF9\u8BDD\u5F55\u5B66\u4E60\u5C0F\u7EC4</h1><h2 id="\u4E3A\u4F55\u505A\u8FD9\u4E2A\u5C0F\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4F55\u505A\u8FD9\u4E2A\u5C0F\u7EC4" aria-hidden="true">#</a> \u4E3A\u4F55\u505A\u8FD9\u4E2A\u5C0F\u7EC4</h2><p>\u8BDA\u5982\u4E13\u6CBB\u53E4\u5E0C\u814A\u54F2\u5B66\u53F2\u7684\u54F2\u5B66\u53F2\u5BB6\u9648\u5EB7\u6240\u8BF4: &quot;\u67CF\u62C9\u56FE\u7684\u8457\u4F5C\u5DF2\u51E0\u4E4E\u6BCF\u7BC7\u662F\u4E00\u4E2A\u8FF7, \u6216\u6BCF\u7BC7\u81F3\u5C11\u5305\u542B\u4E00\u4E2A\u8FF7\u4E86&quot;.</p><p>\u8FD9\u9996\u5148\u662F\u8BF4\u67CF\u62C9\u56FE\u5BF9\u8BDD\u5F55\u96BE\u8BFB, \u4F46\u540C\u65F6\u8868\u660E\u4E86\u9605\u8BFB\u67CF\u62C9\u56FE\u8457\u4F5C\u7684\u4E50\u8DA3\u4E4B\u6240\u5728: \u53EA\u8981\u80FD\u5728\u5176\u4E2D\u628A\u63E1\u4F4F\u4E00\u4E2A\u8FF7\u56E2, \u5E76\u8010\u5FC3\u987A\u7740\u5B83\u8D70\u4E0B\u53BB, \u6211\u4EEC\u5C31\u4F1A\u770B\u5230\u4E00\u7BC7\u6734\u7D20\u7684\u5BF9\u8BDD, \u5982\u9C9C\u82B1\u822C\u5C42\u5C42\u7EFD\u5F00, \u7F8E\u4E0D\u80DC\u6536.</p><p>\u6240\u4EE5, \u95E8\u69DB\u6709\u4E24\u4E2A: \u80FD\u628A\u63E1\u4F4F\u8FF7\u56E2, \u8FD8\u5F97\u6709\u8FD9\u4E2A\u8010\u5FC3\u987A\u7740\u8D70\u4E0B\u53BB.</p><p>\u6240\u4EE5, \u8BF7\u628A\u8FD9\u4E2A\u5C0F\u7EC4\u770B\u4F5C\u662F\u4E00\u4E2A\u88C5\u7F6E, \u4E0D\u4EC5\u662F\u5F20\u542F\u9707\u7684\u81EA\u6211\u88C5\u7F6E, \u800C\u662F\u4E00\u4E2A\u901A\u8FC7\u4E00\u7CFB\u5217\u8BBE\u7F6E, \u5E2E\u52A9\u4F60\u628A\u63E1\u4F4F\u8FF7\u56E2, \u5E76\u9F13\u5229\u4F60, \u7763\u4FC3\u4F60, \u966A\u4F34\u4F60\u8D70\u4E0B\u53BB, \u770B\u5230\u90A3\u7F8E\u4E3D\u80DC\u666F\u7684\u88C5\u7F6E.</p><h2 id="\u5982\u4F55\u505A\u8FD9\u4E2A\u5C0F\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u505A\u8FD9\u4E2A\u5C0F\u7EC4" aria-hidden="true">#</a> \u5982\u4F55\u505A\u8FD9\u4E2A\u5C0F\u7EC4</h2><p>\u6211\u4EEC\u5171\u8BFB\u7684\u662F\u738B\u6653\u671D\u8BD1\u7684\u300A\u67CF\u62C9\u56FE\u5168\u96C6\u300B. \u6BCF\u5468\u4F1A\u9009\u4E00\u7BC7\u5BF9\u8BDD\u6765\u8BFB. \u5F53\u7136, \u5982\u679C\u662F\u5927\u90E8\u5934, \u5982\u300A\u7406\u60F3\u56FD\u300B, \u6211\u4EEC\u4F1A\u62C6\u6210\u90E8\u5206\u4F9D\u6B21\u6765\u8BFB.</p><p>\u6BCF\u5468\u6211\u4F1A\u4E3A\u5927\u5BB6\u63D0\u4F9B\u4E00\u4E2A\u7BC7\u5BFC\u8BFB\u6587\u7AE0, \u6765\u5E2E\u52A9\u5927\u5BB6\u5FEB\u901F\u68B3\u7406\u6587\u4E49. \u5E76\u4E3A\u5927\u5BB6\u521B\u5EFA\u4E00\u4E2A\u5171\u4EAB\u6587\u6863, \u5927\u5BB6\u53EF\u4EE5\u5728\u5176\u4E2D\u8BB0\u4E0B\u81EA\u5DF1\u9605\u8BFB\u65F6\u7684\u95EE\u9898. \u901A\u8FC7\u8BB0\u5F55\u95EE\u9898, \u6211\u4EEC\u53EF\u4EE5\u628A\u8FD9\u7BC7\u5BF9\u8BDD\u7684 &quot;\u8FF7\u56E2&quot; \u5448\u73B0\u51FA\u6765.</p><p>\u6BCF\u5468\u516D\u665A\u4E0A\u516B\u70B9, \u4F1A\u6709\u4E00\u4E2A\u7EBF\u4E0A\u8BA8\u8BBA\u4F1A. \u5728\u8BA8\u8BBA\u4F1A\u4E0A, \u5927\u5BB6\u53EF\u4EE5\u9488\u5BF9\u5171\u4EAB\u6587\u6863\u4E2D\u7684\u95EE\u9898\u8FDB\u884C\u8BA8\u8BBA.</p><p>\u5F53\u7136, \u6211\u662F\u5E0C\u671B\u5927\u5BB6\u90FD\u80FD\u53C2\u52A0\u8BA8\u8BBA. \u4F46\u5982\u679C\u4E0D\u80FD\u53C2\u52A0, \u4E5F\u6CA1\u6709\u5173\u7CFB. \u56E0\u4E3A\u6211\u4EEC\u6BCF\u6B21\u7684\u8BA8\u8BBA\u90FD\u4F1A\u6709\u5F55\u97F3.</p><p>\u5C31\u76EE\u524D\u8BF4, \u5F55\u97F3\u4F1A\u653E\u5728\u6211\u7684\u516C\u4F17\u53F7\u548C\u7F51\u6613\u4E91\u7535\u53F0\u4E0A. \u516C\u4F17\u53F7\u548C\u7535\u53F0\u7684\u540D\u5B57\u90FD\u662F &quot;\u5F20\u542F\u9707&quot;.</p>',12),c=[h];function d(p,n){return a(),t("div",null,c)}var _=e(o,[["render",d],["__file","\u5C0F\u7EC4\u4ECB\u7ECD.html.vue"]]);export{_ as default};
