import{_ as p,o as t,c as o,e as u}from"./app.6bf529a6.js";const r={},e=u('<h1 id="\u5DE5\u4F5C\u5341\u5E74" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F5C\u5341\u5E74" aria-hidden="true">#</a> \u5DE5\u4F5C\u5341\u5E74</h1><hr><p>\u5931\u4E1A\u6709\u4E00\u4E2A\u6708\u6709\u4F59\u4E86. \u501F\u7740\u75AB\u60C5\u4E0D\u597D\u627E\u5DE5\u4F5C\u7684\u501F\u53E3, \u8D4B\u95F2\u5728\u5BB6\u5B8C\u6210\u5919\u613F, \u5B66\u5B8C\u4E86 vue \u524D\u7AEF\u6846\u67B6. ( \u8BDD\u8BF4, \u4E00\u4E2A\u4EA7\u54C1\u7ECF\u7406, \u4E3A\u4EC0\u4E48\u6709\u5B66\u4E60\u524D\u7AEF\u6280\u672F\u7684\u5919\u613F, \u4EE5\u540E\u6709\u673A\u4F1A\u8BD5\u7740\u804A\u804A. )</p><p>\u5B66\u4E86\u524D\u7AEF\u6846\u67B6, \u603B\u5F97\u505A\u70B9\u4EC0\u4E48\u9879\u76EE\u624D\u597D. \u4E8E\u662F\u5199\u4E86\u4E2A\u4EBA\u4E3B\u9875. \u53D7 <strong>css \u7985\u610F\u82B1\u56ED</strong> \u7684\u542F\u53D1, \u628A\u8FD9\u4E2A\u4E2A\u4EBA\u4E3B\u9875\u9879\u76EE\u505A\u6210\u4E00\u4E2A\u5B66\u4E60\u524D\u7AEF\u7684\u5F00\u6E90\u9879\u76EE.</p><p>\u65E2\u7136\u5199\u4E86\u4E2A\u4EBA\u4E3B\u9875, \u603B\u5F97\u5173\u8054\u5230\u4E2A\u4EBA\u535A\u5BA2\u624D\u597D. \u4E8E\u662F\u53C8\u7814\u7A76\u4E86 vuepress, \u5728 github pages \u4E0A\u5E03\u7F72\u4E86\u4E2A\u4EBA\u535A\u5BA2. ( \u4E3A\u4EC0\u4E48\u9009\u62E9 vuepress \u505A\u4E3A\u535A\u5BA2\u7CFB\u7EDF, \u4EE5\u540E\u6709\u673A\u4F1A\u6211\u4F1A\u8BD5\u7740\u804A\u804A. )</p><p>\u4E3A\u4E86\u628A\u535A\u5BA2\u586B\u6EE1, \u5C06\u7535\u8111\u91CC\u5B58\u7684\u4EE5\u524D\u5199\u7684\u4E1C\u897F\u90FD\u7FFB\u51FA\u6765. \u624D\u53D1\u73B0, \u6709\u597D\u591A &quot;\u6211\u712F, \u6211\u8FD8\u5199\u8FC7\u8FD9\u73A9\u827A&quot; \u7684\u4E1C\u897F. \u8FD9\u7BC7\u4FBF\u662F\u4E00\u4F8B.</p><p>\u65F6\u503C 19 \u5E74, \u5F53\u65F6\u6211\u6B63\u5728\u53CD\u601D, \u662F\u4E0D\u662F\u975E\u5F97\u4EE5\u4EA7\u54C1\u7ECF\u7406\u7EC8\u4E86\u4E00\u751F -- \u6216\u8005, \u66F4\u786E\u5207\u7684\u8BF4, \u6211\u662F\u4E0D\u662F\u8F6C\u505A\u6587\u5B57\u5DE5\u4F5C\u8005\u66F4\u597D. -- \u7136\u800C\u5F88\u5FEB\u53D1\u73B0, \u6211\u8FD9\u4E2A\u4EBA\u5C31\u662F\u60F3\u505A\u4EA7\u54C1\u8BBE\u8BA1\u5DE5\u4F5C. \u5728\u6B64\u5176\u95F4, \u6211\u5199\u4E86\u8FD9\u6837\u4E00\u7BC7\u6587\u5B57. \u5F53\u65F6\u8FD8\u5BC4\u7ED9\u6211\u94A6\u4F69\u7684\u8001\u5E08, \u611F\u8C22\u4ED6\u4EE5\u73B0\u5B9E\u4E3B\u4E49\u7ACB\u573A\u63D0\u51FA\u7684\u5EFA\u8BAE, \u4F7F\u6211\u4E0D\u81F3\u56E0\u6D6A\u6F2B\u4E3B\u4E49\u8D70\u4E0A\u5947\u602A\u7684\u9053\u8DEF.</p><p>22 \u5E74\u518D\u8BFB, \u603B\u6709\u4E9B\u5F02\u6837\u7684\u611F\u89C9. \u4FBF\u60F3\u7740\u53D1\u5230\u516C\u4F17\u53F7\u91CC, \u5982\u80FD\u7EE7\u5F80, \u4EE5\u5176\u5F00\u6765\u5427.</p><p>\u6587\u7AE0\u672A\u5C3E, \u5220\u53BB\u4E86\u4E9B\u8BAE\u8BBA\u7684\u5185\u5BB9. \u5982\u679C\u6709\u673A\u4F1A, \u63A5\u7740\u8FD9\u4E2A\u7ED3\u5C3E, \u518D\u6765\u8BF4\u8BF4\u8FDB\u4E24\u5E74\u7684\u6545\u4E8B\u5427.</p><hr><p>\u4ECE 2009 \u5230 2019, \u8F6C\u77AC\u5341\u5E74\u5C31\u8FD9\u4E48\u8FC7\u53BB\u4E86.</p><p>\u56DE\u671B\u8FD9\u5341\u5E74, \u603B\u8BA9\u6211\u6709\u4E00\u79CD\u9519\u89C9: \u4F3C\u4E4E\u6211\u603B\u8E29\u5728\u65F6\u4EE3\u7684\u8109\u535A\u4E0A. \u4ECE\u623F\u4EA7\u70ED\u5230\u827A\u672F\u54C1\u70ED, \u4ECE\u5BCC\u5A92\u4F53\u5230\u533A\u5757\u94FE, \u4F3C\u4E4E\u6211\u7684\u6BCF\u4E00\u6B21\u547D\u8FD0\u7684\u8F6C\u8EAB, \u90FD\u591A\u591A\u5C11\u5C11\u4E0E\u65F6\u4EE3\u53D8\u5316\u6709\u5173.</p><p>\u4F46\u53EA\u8981\u8BDA\u5B9E\u7684\u9762\u5BF9\u81EA\u5DF1, \u5C31\u4F1A\u53D1\u73B0\u66F4\u51C6\u786E\u7684\u8868\u8FF0\u5E94\u8BE5\u662F, \u65F6\u4EE3\u7684\u6D6A\u6F6E\u63A8\u52A8\u6211\u6D6E\u840D\u822C\u7684\u751F\u547D, \u8BA9\u6211\u5982\u4E00\u7247\u5927\u6D77\u4E0A\u7684\u6811\u53F6, \u968F\u6CE2\u9010\u6D41.</p><p>\u96BE\u514D, \u6709\u65F6\u6211\u4F1A\u8FD9\u6837\u53CD\u95EE\u81EA\u5DF1:</p><p>\u5728\u6211\u7684\u8FC7\u5F80\u4E2D, \u6211\u5E94\u8BE5\u505C\u7559\u5728\u67D0\u4E00\u4E2A\u9636\u6BB5\u4E48? \u6211\u53EF\u4EE5\u505C\u7559\u5728\u67D0\u4E00\u9636\u6BB5\u4E48?</p><h2 id="_2009-\u5E74-\u4E66\u5E97\u5E97\u5458" tabindex="-1"><a class="header-anchor" href="#_2009-\u5E74-\u4E66\u5E97\u5E97\u5458" aria-hidden="true">#</a> 2009 \u5E74: \u4E66\u5E97\u5E97\u5458</h2><p>\u5173\u4E8E\u5170\u5DDE\u6709\u8FD9\u6837\u4E00\u4E2A\u4F20\u8BF4: \u5170\u5DDE\u662F\u4E2D\u56FD\u7684\u51E0\u4F55\u4E2D\u5FC3, \u800C\u5170\u5DDE\u8FD9\u4E2A\u4E2D\u5FC3\u7684\u4E2D\u5FC3\u5219\u662F\u7518\u8083\u7701\u653F\u5E9C\u524D\u7684\u5C0F\u5E7F\u573A. \u8FD9\u4E2A\u5E7F\u573A\u53EB\u505A\u4E2D\u592E\u5E7F\u573A.</p><p>\u8BF4\u662F\u5E7F\u573A, \u5176\u5B9E\u4E0D\u8FC7\u662F\u7EFF\u690D\u56F4\u6210\u7684\u73AF\u5C9B. \u73AF\u5C9B\u4E2D\u5FC3\u662F\u56FD\u65D7\u6746. \u6211\u9AD8\u4E2D\u65F6\u8981\u8DEF\u8FC7\u6B64\u5730, \u5982\u679C\u51FA\u95E8\u65E9, \u80FD\u78B0\u4E0A\u5347\u65D7\u4EEA\u5F0F.</p><p>\u4E2D\u592E\u5E7F\u573A\u5468\u56F4\u6709\u4E09\u5BB6\u4E66\u5E97, \u4E24\u5BB6\u56FD\u8425\u7684: \u65B0\u534E\u4E66\u5E97\u548C\u53E4\u85C9\u4E66\u5E97. \u8FD8\u6709\u4E00\u5BB6\u79C1\u8425\u7684, \u56E0\u5730\u800C\u540D, \u53EB\u5E7F\u573A\u4E66\u57CE.</p><p>\u5E7F\u573A\u4E66\u57CE\u76F8\u8F83\u4E24\u5BB6\u56FD\u8425\u7684\u4E66\u5E97, \u4E66\u85C9\u54C1\u79CD\u66F4\u4E30\u5BCC, \u73AF\u5883\u4E5F\u66F4\u597D. \u5728\u4E66\u5E97\u4E2D\u95F4\u7684\u4F4D\u7F6E\u4E13\u95E8\u6709\u4F11\u606F\u533A, \u6446\u7740\u5E72\u51C0\u7684\u684C\u6905, \u653E\u7740\u8F7B\u67D4\u7684\u97F3\u4E50.</p><p>\u9AD8\u4E2D\u65F6\u7684\u6211, \u6BCF\u5929\u90FD\u4F1A\u6765\u8FD9\u91CC\u8E6D\u4E66\u770B. \u5927\u5B66\u6BD5\u4E1A\u540E, \u4E00\u65F6\u4E0D\u77E5\u8981\u505A\u4EC0\u4E48\u7684\u6211, \u4FBF\u6765\u8FD9\u91CC\u5E94\u8058\u5F53\u4E86\u5E97\u5458.</p><hr><p>\u4E66\u5E97\u5E97\u5458\u7684\u65E5\u5E38\u5DE5\u4F5C\u5206\u4E3A\u4E24\u7C7B: <strong>\u7801\u4E66\u548C\u5F15\u5BFC</strong> .</p><p><strong>\u7801\u4E66</strong> \u662F\u6307\u628A\u987E\u5BA2\u62C9\u4E71\u7684\u4E66\u653E\u56DE\u539F\u4F4D, \u6216\u8005\u6839\u636E\u8425\u9500\u90E8\u95E8\u7684\u6307\u793A\u628A\u7279\u5B9A\u7684\u4E66\u5728\u663E\u8457\u4F4D\u7F6E\u4EE5\u663E\u773C\u7684\u65B9\u5F0F\u7801\u51FA\u82B1\u6765.</p><p><strong>\u5F15\u5BFC</strong> \u662F\u6307\u5F53\u987E\u5BA2\u54A8\u8BE2\u65F6, \u5BF9\u987E\u5BA2\u8FDB\u884C\u56DE\u7B54, \u5E76\u5F15\u5BFC\u987E\u5BA2\u4E70\u4E66.</p><p>\u4E66\u5E97\u6BCF\u5929\u5DE5\u4F5C\u7684\u9AD8\u5CF0\u671F\u662F\u4E0B\u5348\u5B66\u751F\u653E\u5B66\u540E\u7684\u4E00\u6BB5\u65F6\u95F4. \u718A\u5B69\u5B50\u4EEC\u4E00\u62E5\u800C\u5165, \u628A\u5404\u79CD\u4E66\u626F\u7684\u5230\u5904\u90FD\u662F. \u6211\u4EEC\u8981\u8DDF\u5728\u718A\u5B69\u5B50\u5C41\u80A1\u540E\u9762, \u628A\u4E66\u4E00\u904D\u904D\u7684\u7801\u56DE\u53BB.</p><p>\u9664\u6B64\u4E4B\u5916\u7684\u65F6\u95F4, \u76F8\u5BF9\u6BD4\u8F83\u8F7B\u95F2. \u6B64\u65F6\u4FBF\u770B\u51FA\u5E97\u5458\u7684\u4E0D\u540C\u6765.</p><p>\u6709\u4E13\u4EE5\u6B64\u4E3A\u7CCA\u53E3\u7684\u5DE5\u4F5C\u7684\u5458\u5DE5, \u5DE5\u4F5C\u95F2\u6687\u65F6, \u4FBF\u805A\u5728\u4E00\u8D77\u95F2\u804A. \u7AD9\u5728\u60CA\u609A\u5C0F\u8BF4\u533A, \u5C31\u804A\u5BB6\u4E61\u7684\u9B3C\u6545\u4E8B; \u7AD9\u5728\u8A00\u60C5\u5C0F\u8BF4\u533A, \u5C31\u804A\u5404\u79CD\u7537\u5973\u516B\u5366.</p><p>\u800C\u8FD8\u6709\u4E00\u7C7B\u5458\u5DE5, \u7EAF\u662F\u56E0\u4E3A\u559C\u6B22\u4E66\u624D\u6765\u8FD9\u91CC\u4E0A\u73ED. \u4E00\u65E6\u95F2\u4E0B\u6765\u4FBF\u4ECE\u4E66\u67B6\u4E0A\u62FF\u4E66\u6765\u8BFB.</p><p>\u5F53\u65F6\u5E97\u91CC\u6709\u4E00\u4F4D\u51E0\u4E2D\u5E74\u7684\u5355\u8EAB\u5927\u53D4\u662F\u6B64\u7C7B\u5E97\u5458\u7684\u4EE3\u8868. \u4ED6\u662F\u56FD\u4F01\u7684\u5458\u5DE5, \u5DE5\u4F5C\u662F\u5012\u73ED\u5236\u7684. \u5012\u73ED\u65F6, \u5C31\u4F1A\u6765\u4E66\u5E97\u517C\u804C. \u6211\u5F53\u5E97\u5458\u65F6, \u4ED6\u5DF2\u5728\u5E97\u91CC\u5E72\u4E86\u5F88\u591A\u5E74\u4E86, \u5BF9\u5E97\u91CC\u7684\u4E66\u4E86\u5982\u6307\u638C. \u968F\u4FBF\u4ECE\u54EA\u4E2A\u7284\u89D2\u65EE\u65EF\u91CC\u62FF\u51FA\u4E00\u672C\u4E66\u6765, \u4ED6\u90FD\u80FD\u804A\u4E0A\u534A\u5929.</p><p>\u5982\u679C\u6709\u987E\u5BA2\u4E0D\u77E5\u9053\u54EA\u672C\u4E66\u5728\u54EA\u91CC, \u95EE\u5230\u8FD9\u4F4D\u56FD\u4F01\u5927\u53D4, \u901A\u5E38\u7684\u7ED3\u679C\u662F: \u672C\u6765\u8BA1\u5212 30 \u5757\u94B1\u4E70\u4E00\u672C\u4E66, \u4F46\u7ED3\u679C\u5374\u82B1\u4E86 300 \u5757\u94B1\u4E70\u4E00\u645E\u4E66\u56DE\u53BB.</p><p>\u6211\u7B97\u662F\u4ECB\u4E8E\u4E24\u79CD\u5E97\u5458\u4E4B\u95F4. \u65E2\u548C\u5927\u59D0\u5C0F\u59B9\u8001\u963F\u59E8\u804A\u7684\u4E0D\u4EA6\u4E50\u4E4E, \u4E5F\u5E38\u62FF\u4E66\u7EC6\u8BFB. \u56E0\u4E3A\u5728\u5B66\u6821\u91CC\u5C31\u662F\u5E7F\u64AD\u7AD9\u7684, \u6709\u70B9\u64AD\u97F3\u7684\u5E95\u5B50, \u8FD8\u5F55\u4E86\u4E00\u4E9B\u4E66\u8BC4\u5728\u5E97\u91CC\u7684\u5E7F\u64AD\u91CC\u64AD. \u4E3A\u6B64\u5E97\u91CC\u8FD8\u53D1\u4E86 50 \u5143\u7684\u7231\u5C97\u656C\u4E1A\u5956\u91D1.</p><p>\u4F46\u4E66\u5E97\u7684\u5DE5\u8D44\u5B9E\u5728\u592A\u4F4E, \u5C31\u7B97\u662F\u52A0\u4E0A\u7231\u5C97\u656C\u4E1A\u7684\u5956\u91D1, \u4E5F\u4EC5\u591F\u4E00\u4E2A\u4EBA\u7684\u6E29\u9971. \u66F4\u91CD\u8981\u7684\u662F, \u5E97\u5458\u5DE5\u4F5C\u4F3C\u4E4E\u662F\u4E00\u79CD\u4E00\u773C\u53EF\u4EE5\u671B\u4E00\u5934\u7684\u5DE5\u4F5C, \u6CA1\u6709\u76FC\u5934, \u6CA1\u6709\u79EF\u7D2F, \u4E5F\u6CA1\u6709\u8FDB\u6B65.</p><p>\u6211\u7684\u4E00\u4E2A\u53D1\u5C0F\u5B9E\u5728\u770B\u4E0D\u4E0B\u53BB\u6211\u5728\u4E66\u5E97 &quot;\u6C89\u6CA6&quot;, \u5C31\u4ECB\u7ECD\u6211\u53BB\u623F\u4EA7\u4E2D\u4ECB\u5356\u623F\u5B50\u4E86.</p><h2 id="_2009-2011-\u623F\u4EA7\u4E2D\u4ECB" tabindex="-1"><a class="header-anchor" href="#_2009-2011-\u623F\u4EA7\u4E2D\u4ECB" aria-hidden="true">#</a> 2009 - 2011\uFF1A \u623F\u4EA7\u4E2D\u4ECB</h2><p>2002 \u5E74\u571F\u5730\u62DB\u62CD\u6301\u5236\u5EA6\u5B9E\u65BD. \u540C\u5E74, \u6765\u81EA\u53F0\u6E7E\u7701\u7684\u5E84\u5EAD\u796F\u5728\u5170\u5DDE\u5F00\u529E\u4E86\u7B2C\u4E00\u5BB6 <strong>\u739B\u96C5\u623F\u5C4B</strong>. \u706B\u70ED\u7684\u623F\u5730\u4EA7\u5E02\u573A\u4E0E\u6765\u81EA\u53F0\u6E7E\u7684\u5148\u8FDB\u7ECF\u9A8C\u7ED3\u5408, \u5F88\u5FEB\u6559\u4F1A\u4E86\u5170\u5DDE\u4EBA\u6C11, \u5982\u4F55\u73A9\u4E8C\u624B\u623F\u5C4B\u7ECF\u7EAA\u8FD9\u4E2A\u6E38\u620F.</p><p>\u4E00\u5207\u7684\u5173\u952E\u5728\u4E8E\u5229\u7528\u4E70\u5BB6\u548C\u5356\u5BB6\u7684\u4FE1\u606F\u5DEE.</p><p>\u5728\u53D1\u73B0\u623F\u6E90\u4FE1\u606F\u540E, \u5148\u4EE5\u4E70\u5BB6\u7684\u8EAB\u4EFD\u8FDB\u884C\u63A5\u89E6, \u5982\u679C\u4EF7\u683C\u4F4E\u4E8E\u5E02\u573A\u4EF7 ( \u5728\u623F\u4EF7\u65E5\u65E5\u7FFB\u65B0\u7684\u5E74\u4EE3, \u90A3\u6015\u7B49\u4E8E\u5E02\u573A\u4EF7\u4E5F\u662F\u53EF\u4EE5\u63A5\u53D7\u7684 ), \u5C31\u76F4\u63A5\u4E70\u4E0B, \u7B49\u5E02\u573A\u4EF7\u6DA8\u4E0A\u53BB\u518D\u8F6C\u624B\u5356\u51FA.</p><p>\u5982\u679C\u4EF7\u683C\u504F\u9AD8, \u5219\u9000\u800C\u6C42\u5176\u6B21, \u7B7E\u8BA2\u4E00\u4E2A\u76F8\u5BF9\u8F83\u4F4E\u7684\u4EE3\u7406\u4EF7, \u800C\u540E\u9AD8\u4EF7\u6302\u5356, \u5982\u679C\u6709\u4E70\u5BB6\u6709\u610F\u5411, \u518D\u4EE5\u4F4E\u4EF7\u4ECE\u5356\u5BB6\u4E70\u5165, \u9AD8\u4EF7\u8F6C\u624B\u5356\u51FA. \u5F53\u7136\u6240\u8C13\u4E70\u5165, \u4E0D\u5FC5\u771F\u5B8C\u6210\u8FC7\u6237\u624B\u7EED, \u53EA\u8981\u62FF\u5230\u516C\u8BC1\u65E2\u53EF.</p><p>\u5176\u95F4\u81EA\u6709\u79CD\u79CD\u538B\u4EF7\u62AC\u4EF7\u7684\u624B\u6BB5. \u5F53\u7136\u4E5F\u6709\u4E00\u5207\u624B\u6BB5\u90FD\u6CA1\u7528\u7684\u65F6\u5019. \u8FD9\u65F6\u624D\u4F1A\u771F\u7684\u8001\u8001\u5B9E\u5B9E\u8D5A\u4E2D\u4ECB\u8D39.</p><p>02 \u5230 08 \u5E74, \u5170\u5DDE\u4E8C\u624B\u623F\u5E02\u573A\u5728\u623F\u5730\u4EA7\u53D1\u5C55\u7684\u5927\u6F6E\u7684\u80CC\u666F\u4E0B, \u706B\u70ED\u5F02\u5E38. \u4E2D\u4ECB\u5C0F\u4F19\u65E9\u4E0A\u62FF\u7740\u4E00\u6C93\u5370\u7740 &quot;\u73B0\u91D1\u6025\u8D2D\u623F&quot; \u7684\u5C0F\u5E7F\u544A\u51FA\u95E8, \u4E0B\u5348\u5C31\u80FD\u627E\u5230\u623F\u6E90. \u52A8\u4F5C\u5FEB\u7684\u8BDD, \u665A\u4E0A\u623F\u866B\u5C31\u80FD\u628A\u623F\u5B50\u62FF\u4E0B. \u7B2C\u4E8C\u5929\u5446\u5728\u623F\u5B50\u7B49\u7740\u5BA2\u6237\u6765\u770B\u623F, \u901A\u5E38\u4E00\u5929\u5C31\u80FD\u628A\u623F\u5B50\u5356\u6389.</p><p>\u542C\u8001\u524D\u8F88\u8BF4, \u5F53\u65F6\u4E70\u623F\u5356\u623F\u7684\u7535\u8BDD\u592A\u591A, \u4E00\u5929\u8981\u5907\u4E24\u5757\u624B\u673A\u7535\u6C60\u624D\u591F\u7528.</p><p>\u52E4\u52B3\u80AF\u5E72\u7684\u5C0F\u4F19\u53EA\u8981\u7A0D\u7A0D\u6709\u70B9\u5FC3, \u5F88\u5FEB\u5C31\u80FD\u8DFB\u8EAB\u623F\u866B\u7684\u884C\u5217, \u6216\u8005\u53E6\u7ACB\u5C71\u5934\u81EA\u5DF1\u5F00\u4E00\u5BB6\u623F\u4EA7\u4E2D\u4ECB\u516C\u53F8.</p><p>\u6211\u6240\u5165\u804C\u7684\u8FD9\u5BB6\u623F\u4EA7\u4E2D\u4ECB\u5C31\u662F\u8FD9\u6837\u4E00\u4E2A &quot;\u8D5A\u4E86\u94B1\u7684\u5C0F\u4F19\u53E6\u7ACB\u5C71\u5934\u81EA\u5DF1\u5F00\u7684\u623F\u4EA7\u4E2D\u4ECB\u516C\u53F8&quot;.</p><hr><p>\u8FD9\u662F\u4E00\u5BB6\u6CA1\u6709\u95E8\u9762\u7684\u8896\u73CD\u5C0F\u516C\u53F8. \u529E\u516C\u5BA4\u5728\u4E00\u680B\u8001\u65E7\u9AD8\u5C42\u4E2D, \u53EA\u6709\u4E0D\u5230 10 \u5E73\u7C73.</p><p>\u516C\u53F8\u4E2D\u5E26\u4E0A\u6211\u4E00\u5171\u6709\u4E09\u4E2A\u4EBA. \u4E00\u4E2A\u662F\u8001\u677F, \u4E00\u4E2A\u662F\u80A1\u4E1C. \u4ED6\u4EEC\u4E24\u4E2A\u5728\u524D\u51E0\u5E74\u7684\u623F\u5730\u4EA7\u5927\u6F6E\u4E2D\u6323\u4E86\u94B1, \u5408\u80A1\u538B\u4E86\u51E0\u5957\u623F\u5B50, \u5F00\u4E86\u8FD9\u4E2A\u4E2D\u4ECB\u516C\u53F8.</p><p>\u56E0\u4E3A\u516C\u53F8\u592A\u8896\u73CD\u4E86, \u6240\u4EE5\u516C\u53F8\u7684\u4E3B\u8981\u4E1A\u52A1\u662F\u4E70\u719F\u6089\u7684\u623F\u866B\u5B50\u7684\u623F\u5B50.</p><p>2009 \u5E74\u7684\u5170\u5DDE\u4E8C\u624B\u623F\u5E02\u573A\u5DF2\u7ECF\u8FDC\u6CA1\u524D\u51E0\u5E74\u706B\u70ED\u4E86. \u90A3\u79CD\u4F20\u5947\u6545\u4E8B\u822C\u7684\u9500\u552E\u6545\u4E8B\u4E0D\u518D\u51FA\u73B0, \u66F4\u591A\u662F\u5FD9\u788C\u7684\u65E5\u5E38.</p><p>\u626B\u697C\u662F\u5F00\u53D1\u623F\u6E90\u7684\u4E3B\u8981\u65B9\u5F0F, \u62FF\u7740 &quot;\u7ED3\u5A5A\u6025\u8D2D\u623F&quot; \u548C &quot;\u73B0\u91D1\u6025\u8D2D\u623F&quot; \u7684\u5C0F\u7EB8\u6761, \u6328\u5BB6\u6328\u6237\u7684\u63D2\u5728\u95E8\u7F1D\u4E2D.</p><p>\u800C\u623F\u6E90\u7684\u53D1\u5E03\u7684\u65B9\u5F0F, \u4E3B\u8981\u7684\u4F9D\u9760\u7684\u662F\u5728\u62A5\u7EB8\u4E0A\u767B\u5E7F\u544A. \u7F51\u4E0A\u7684\u6E20\u9053, \u5F53\u65F6\u53EA\u6709 58 \u540C\u57CE\u548C\u8D76\u96C6\u7F51.</p><p>\u5982\u679C\u6709\u4EBA\u6253\u6765\u7535\u54A8\u8BE2, \u4FBF\u7EA6\u7740\u53BB\u770B\u623F\u5B50. \u770B\u623F\u65F6\u81EA\u7136\u6709\u4E00\u5957\u63A8\u9500\u7684\u8BDD\u672F, \u4F46\u6700\u6709\u6548\u7684\u5F80\u5F80\u662F\u5E2E\u7740\u89C4\u5219\u8FD9\u4E2A\u623F\u5B50\u5982\u4F55\u5E03\u7F6E: \u8FD9\u91CC\u505A\u9694\u65AD, \u90A3\u91CC\u6302\u7535\u89C6, \u8FB9\u8FB9\u89D2\u843D\u585E\u51B0\u7BB1.</p><p>\u5982\u679C\u6709\u4EBA\u770B\u4E0A\u623F\u5B50, \u4FBF\u5E26\u56DE\u516C\u53F8, \u8001\u677F\u7EA6\u4E86\u623F\u4E1C\u6765\u8C08 -- \u90FD\u662F\u957F\u671F\u5408\u4F5C\u7684\u623F\u866B\u5B50.</p><p>\u5170\u5DDE\u6709\u4E00\u53E5\u571F\u8BDD, \u53EB\u505A &quot;\u62FE\u8DCC\u679C&quot;, \u6361\u8DCC\u843D\u679C\u5B9E\u4E4B\u610F. \u5927\u6982\u662F\u6307 &quot;\u7A83\u53D6\u4ED6\u4EBA\u52B3\u52A8\u679C\u5B9E&quot; \u7684\u610F\u601D. \u6211\u5728\u516C\u53F8\u4FBF\u6709 &quot;\u8DCC\u679C\u5C0F\u738B\u5B50&quot; \u4E4B\u79F0.</p><p>\u5012\u4E0D\u662F\u8BF4\u6211\u4E13\u6361\u522B\u4EBA\u7684\u5355\u5B50\u505A, \u800C\u662F\u56E0\u4E3A\u4E24\u4E2A\u8001\u677F\u7ECF\u8FC7\u90A3\u4E2A\u70ED\u706B\u671D\u5929\u7684\u65E5\u5B50, \u89C1\u4E0D\u5F97\u4E00\u904D\u904D\u770B\u623F\u5B50\u800C\u5C31\u662F\u4E0D\u5B9A\u7684\u5BA2\u6237. \u4E8E\u662F\u4ED6\u4EEC\u628A\u8FD9\u4E9B\u5BA2\u6237\u90FD\u7529\u7ED9\u6211. \u6211\u662F\u65B0\u4EBA, \u81EA\u7136\u6765\u8005\u4E0D\u62D2. \u800C\u5C31\u662F\u4E9B\u8001\u677F\u4EEC\u770B\u4E0D\u4E0A\u7684\u5BA2\u6237\u6210\u4E86\u6211\u6210\u5355\u7684\u4E3B\u8981\u6765\u6E90.</p><p>\u5728 2009 \u5230 2011 \u5E74\u5170\u5DDE\u4E8C\u624B\u623F\u7684\u51B7\u6DE1\u671F, \u6211\u9760\u7740 &quot;\u62FE\u8DCC\u679C&quot; \u5B9E\u73B0\u4E86\u51E0\u4E4E\u6BCF\u4E2A\u6708\u90FD\u6709\u5F00\u5355\u7684\u4E1A\u7EE9.</p><hr><p>\u65E0\u9521\u7684 118 \u8DEF\u516C\u4EA4\u8F66\u665A\u70B9\u975E\u5E38\u975E\u5E38\u4E25\u91CD. \u5C24\u5176\u662F\u65E9\u665A\u9AD8\u5CF0\u65F6\u6BB5, \u5E38\u5E38\u665A\u70B9\u5230\u534A\u4E2A\u5C0F\u65F6\u4EE5\u4E0A.</p><p>\u6709\u4E00\u6BB5\u65F6\u95F4\u6211\u4E0A\u73ED\u5929\u5929\u8981\u505A 118 \u8DEF\u8F66. \u5F00\u59CB\u81EA\u7136\u5BF9\u8FD9\u6837\u7684\u4E8B\u60C5\u975E\u5E38\u6C14\u6124. \u4F46\u65F6\u95F4\u957F\u4E86, \u6162\u6162\u9002\u5E94\u4E86\u8D77\u6765.</p><p>\u751A\u81F3\u6709\u65F6, \u770B\u5230\u5176\u5B83\u7B49\u8F66\u7684\u4EBA\u6025\u5F97\u8C61\u70ED\u9505\u4E0A\u8682\u8681, \u6211\u5FC3\u4E2D\u7ADF\u9690\u9690\u751F\u51FA\u4E00\u4E1D\u4F18\u8D8A\u611F: \u8C01\u8BA9\u4F60\u4E0D\u65E9\u65E9\u51FA\u95E8\u5462?</p><p>\u4EBA\u7684\u5FC3\u7406\u771F\u662F\u6709\u975E\u5E38\u5F3A\u70C8\u7684\u5408\u7406\u5316\u503E\u5411, \u957F\u671F\u9762\u5BF9\u4E0D\u53EF\u6539\u53D8\u7684\u4E0D\u5408\u7406\u7684\u73B0\u5B9E, \u4EBA\u90FD\u80FD\u5728\u5FC3\u7406\u627E\u5230\u7406\u7531\u4F7F\u5176\u5408\u7406\u5316. \u975E\u4F46\u80FD\u628A\u4E0D\u5408\u7406\u7684\u5408\u7406\u5316, \u8FD8\u80FD\u56E0\u81EA\u5DF1 <strong>\u80FD\u628A\u4E0D\u5408\u7406\u7684\u5408\u7406\u5316</strong> \u751F\u51FA\u4F18\u8D8A\u611F\u6765.</p><p>\u7B49\u516C\u4EA4\u8F66\u662F\u4E00\u4F8B, \u5632\u7B11\u7A0B\u5E8F\u5458\u7684 996.icu \u662F\u4E00\u4F8B, \u53EA\u5356\u623F\u866B\u7684\u623F\u5B50\u800C\u4E0D\u5185\u759A\u4E5F\u662F\u4E00\u4F8B.</p><p>\u53EA\u5356\u623F\u866B\u7684\u623F\u5B50\u662F\u5408\u7406\u7684\u4E48? \u4E0D\u5408\u7406, \u4F46\u8FD8\u6709\u5176\u5B83\u9009\u62E9\u4E48? \u6CA1\u6709\u4E86.</p><p>\u56E0\u4E3A\u4E13\u4E1A\u5316\u7684\u4E2D\u4ECB, \u5B8C\u5168\u5784\u65AD\u4E86\u6240\u6709\u4FE1\u606F\u6E20\u9053. \u53EA\u8981\u5356\u4E70\u53CC\u65B9\u60F3\u8981\u5411\u81EA\u7531\u5E02\u573A\u4EA4\u6362\u4FE1\u606F, \u4ED6\u7684\u58F0\u97F3\u53EA\u4F1A\u88AB\u4E13\u4E1A\u7684\u4E2D\u4ECB\u542C\u5230, \u5E76\u88AB\u4E2D\u4ECB\u7684\u58F0\u97F3\u6240\u8986\u76D6. \u4EFB\u4F55\u53EF\u4EE5\u5411\u6574\u4E2A\u5E02\u573A\u4F20\u9012\u4FE1\u606F\u7684\u6E20\u9053\u90FD\u662F\u4E2D\u4ECB\u4EEC\u7684\u9635\u5730: \u62A5\u7EB8, \u7F51\u7EDC, \u751A\u81F3\u4F60\u4EEC\u5BB6\u95E8\u53E3\u7684\u5C0F\u5E7F\u544A.</p><p>\u56E0\u6B64\u4E2D\u4ECB\u5B8C\u5168\u53EF\u4EE5\u9009\u62E9\u5BF9\u4ED6\u4EEC\u6700\u6709\u5229\u7684\u4EA4\u6613\u65B9\u5F0F. \u800C\u4E00\u65E6\u8FD9\u662F\u53EF\u80FD\u7684, \u5728\u7ADE\u4E89\u7684\u673A\u5236\u4E0B, \u8FD9\u79CD\u6700\u6709\u5229\u7684\u4EA4\u6613\u65B9\u5F0F\u5C31\u4F1A\u53D8\u6210\u552F\u4E00\u7684\u4EA4\u6613\u65B9\u5F0F.</p><p>\u56E0\u6B64\u5E76\u4E0D\u662F\u4E2D\u4ECB\u53EA\u5356\u623F\u866B\u7684\u623F\u5B50, \u800C\u662F\u5E02\u573A\u4E0A\u53EA\u6709\u623F\u866B\u5B50\u7684\u623F\u5B50\u5728\u5356.</p><p>\u5F53\u7136, 2009 \u5E74\u521A\u8FDB\u5165\u793E\u4F1A\u7684\u6211, \u5E76\u6CA1\u6709\u8FD9\u6837\u7684\u53CD\u601D\u80FD\u529B. \u6211\u53EA\u662F\u9690\u9690\u7684\u89C9\u5F97\u4E0D\u59A5, \u4F46\u53C8\u5F88\u5FEB\u8FD9\u79CD\u4E0D\u59A5\u7684\u611F\u89C9\u4FBF\u88AB\u5408\u7406\u5316\u7684\u503E\u5411\u7ED9\u62BA\u5E73, \u751A\u81F3\u8FD8\u56E0\u4E3A\u80FD\u514D\u63A5\u53D7\u8FD9\u79CD\u5408\u7406\u800C\u4EA7\u751F\u4E86\u4E00\u4E1D\u4F18\u8D8A\u611F.</p><p>\u6211\u5728\u623F\u5C4B\u4E2D\u4ECB\u516C\u53F8\u5E72\u4E86 3 \u5E74\u623F\u4EA7\u7ECF\u7EAA\u4EBA. \u6211\u4E8E 2011 \u5E74\u7ED3\u5A5A. \u5A5A\u540E, \u6211\u51B3\u5B9A\u5230\u65E0\u9521\u53D1\u5C55.</p><h2 id="_2011-\u4E0A\u534A\u5E74-\u62CD\u5356\u516C\u53F8" tabindex="-1"><a class="header-anchor" href="#_2011-\u4E0A\u534A\u5E74-\u62CD\u5356\u516C\u53F8" aria-hidden="true">#</a> 2011 \u4E0A\u534A\u5E74: \u62CD\u5356\u516C\u53F8</h2><p>2011 \u5E74\u6211\u7B2C\u4E00\u6B21\u6765\u5230\u65E0\u9521\u53D1\u5C55, \u5165\u804C\u4E86\u4E00\u5BB6\u62CD\u5356\u516C\u53F8.</p><p>2011 \u5E74\u5DE6\u53F3, \u827A\u672F\u54C1\u5E02\u573A\u5448\u73B0\u51FA\u4E00\u79CD\u5947\u602A\u7684\u706B\u70ED\u72B6\u6001: \u7535\u89C6\u4E0A, \u4ECE\u4E2D\u592E\u53F0\u5230\u5730\u65B9\u53F0\u5145\u6EE1\u4E86\u5404\u7C7B\u9274\u5B9D\u8282\u76EE, \u4E0D\u5C11\u641E\u9274\u8BA2\u7684\u8001\u5E08\u8C61\u5076\u8C61\u4E00\u6837\u88AB\u70ED\u6367.</p><p>\u5F53\u65F6\u6D41\u4F20\u8FD9\u6837\u4E00\u4E2A\u8BF4\u6CD5: \u653F\u5E9C\u4E3A\u4E86\u524C\u6FC0\u7ECF\u6D4E\u4E00\u76F4\u5728\u8D85\u53D1\u8D27\u5E01, \u623F\u5730\u4EA7\u5E02\u573A\u5C31\u662F\u4E3A\u4E86\u5438\u53EB\u8D85\u53D1\u8D27\u5E01\u5EFA\u7ACB\u7684\u84C4\u6C34\u6C60. \u800C\u73B0\u5728\u623F\u5730\u4EA7\u8FD9\u4E2A\u84C4\u6C34\u6C60\u5DF2\u7ECF\u6EE1\u4E86, \u9700\u8981\u627E\u5230\u4E00\u4E2A\u65B0\u7684\u84C4\u6C34\u6C60, \u8FD9\u4E2A\u84C4\u6C34\u6C60\u5C31\u662F\u827A\u672F\u54C1\u5E02\u573A.</p><p>\u800C\u53E6\u4E00\u5219\u6545\u4E8B, \u5219\u4EE5\u66F4\u4F20\u5947\u7684\u65B9\u5F0F\u8BBA\u8BC1\u7740\u827A\u672F\u54C1\u5E02\u573A\u7684 &quot;\u521A\u9700&quot;: \u636E\u8BF4\u5F53\u65F6\u6709\u4E00\u4E2A\u5927\u8D2A\u5B98\u88AB\u6293\u4E86\u540E\u67E5\u51FA\u4E00\u5C4B\u5B50\u7684\u53E4\u8463, \u5982\u679C\u8FD9\u4E9B\u4E1C\u897F\u90FD\u662F\u771F\u7684, \u90A3\u8FD9\u7B14\u6765\u5386\u4E0D\u660E\u7684\u8D44\u4EA7, \u8BA9\u4ED6\u6B7B\u4E00\u4E07\u6B21\u90FD\u591F\u4E86, \u4F46\u6CA1\u60F3\u5230\u8FD9\u4E9B\u53E4\u8463\u9274\u5B9A\u540E\u53D1\u73B0\u6CA1\u6709\u4E00\u4EF6\u662F\u771F\u7684, \u8FD9\u4F4D\u5DE8\u8D2A\u7ADF\u5F97\u8131\u6B7B\u7F6A. \u4EF7\u503C\u7684\u6A21\u7CCA\u6027\u4F7F\u827A\u672F\u54C1\u4F5C\u4E3A &quot;\u793C\u54C1&quot; \u7684\u7279\u6B8A\u610F\u4E49.</p><p>\u4E0D\u7BA1\u6545\u4E8B\u8BB2\u7684\u591A\u4E48\u5929\u82B1\u4E71\u5815, \u505A\u7684\u4E8B\u60C5\u5374\u4E0E\u623F\u4EA7\u4E2D\u4ECB\u6709\u7740\u5F02\u66F2\u540C\u5DE5\u4E4B\u5999.</p><p>\u9996\u5148\u662F\u5F00\u53D1\u85CF\u5BB6. \u4E3B\u8981\u7684\u65B9\u5F0F\u662F\u8D76\u9274\u5B9D\u6D3B\u52A8. \u65E0\u8BBA\u662F\u7535\u89C6\u53F0\u7684\u9274\u5B9D\u6D3B\u52A8\u8FD8\u662F\u5176\u5B83\u62CD\u5356\u516C\u53F8\u7684\u9274\u5B9A\u4F1A, \u516C\u53F8\u90FD\u4F1A\u628A\u6211\u4EEC\u8FD9\u4E9B\u4E1A\u52A1\u4EBA\u5458\u62C9\u5230\u73B0\u573A\u53BB\u5411\u53C2\u52A0\u9274\u5B9A\u4F1A\u7684\u85CF\u5BB6\u53D1\u8D44\u6599. \u6240\u8C13\u7684\u8D44\u6599\u662F\u516C\u53F8\u81EA\u5DF1\u529E\u7684\u4E00\u4E2A\u5C0F\u6742\u5FD7. \u5F53\u7136\u53D1\u8D44\u6599\u53EA\u662F\u4E00\u4E2A\u7531\u5934, \u771F\u6B63\u7684\u76EE\u7684\u662F\u95EE\u85CF\u5BB6\u8981\u7535\u8BDD.</p><p>\u56DE\u5230\u516C\u53F8\u540E, \u4FBF\u7ED9\u7559\u4E0B\u7535\u8BDD\u7684\u85CF\u5BB6\u4E00\u4E00\u6253\u53BB\u7535\u8BDD, \u95EE\u4ED6\u4EEC\u662F\u5426\u6709\u610F\u5411\u53C2\u52A0\u516C\u53F8\u7684\u6625\u5B63\u62CD\u5356\u4F1A.</p><p>\u8FD9\u662F\u6807\u51C6\u7684\u7535\u8BDD\u8F93\u51FA, \u5982\u679C\u53D1\u6325\u6B63\u5E38\u4E14\u5BA2\u6237\u5145\u8DB3, \u4E00\u5929\u53EF\u4EE5\u8F93\u51FA 300 \u4E2A\u7535\u8BDD. \u6CA1\u6709\u4EFB\u4F55\u7684\u6280\u672F\u542B\u91CF, \u53EA\u662F\u8868\u660E\u8EAB\u4EFD, \u8BF4\u6E05\u610F\u56FE, \u7B49\u5F85\u53CD\u6620. \u5927\u6982 300 \u767E\u4E2A\u7535\u8BDD\u4E2D, 200 \u4E2A\u4F1A\u76F4\u63A5\u6302\u65AD, 80 \u4E2A\u4F1A\u5BA2\u5957\u4E00\u4E0B\u540E\u8868\u793A\u6CA1\u5174\u8DA3, \u5269\u4E0B 20 \u4E2A\u8868\u793A\u611F\u5174\u8DA3, \u5230\u65F6\u5019\u771F\u6B63\u6765\u516C\u53F8\u7684\u5927\u6982\u53EA\u6709 2 \u4EBA.</p><p>\u7136\u800C\u5982\u679C\u6BCF\u5929\u80FD\u6709\u4E24\u4E2A\u5BA2\u6237\u6765\u516C\u53F8, \u8FD9\u5DF2\u7ECF\u662F\u5353\u6709\u6210\u6548\u7684\u7535\u8BDD\u8F93\u51FA\u4E86.</p><p>\u5BA2\u6237\u6765\u516C\u53F8\u4E4B\u540E, \u81EA\u7136\u6709\u4E00\u5957\u7EA6\u8C08\u7684\u6D41\u7A0B,</p><p>\u9996\u5148\u662F\u6211\u81EA\u5DF1\u5F97\u63A5\u5F85, \u867D\u7136\u4E0D\u7528\u591A\u804A, \u4F46 &quot;\u5F00\u4E0D\u5F00\u773C, \u5230\u4E0D\u5230\u4EE3&quot; \u4E4B\u7C7B\u7684 &quot;\u9ED1\u8BDD&quot; \u591A\u591A\u5C11\u5C11\u5F97\u4F1A\u4E24\u53E5.</p><p>\u800C\u540E\u662F\u9274\u5B9A\u8001\u5E08\u6765\u9274\u5B9A, \u8FD9\u5C31\u6709\u4E00\u7FFB\u975E\u5E38\u4E13\u4E1A\u7684\u8BDD\u672F, \u65E8\u5728\u8BA9\u5BA2\u6237\u5FC3\u60A6\u8BDA\u670D.</p><p>\u6700\u540E\u662F\u4E1A\u52A1\u90E8\u95E8\u7684\u7ECF\u7406, \u7ECF\u7406\u4E3B\u8981\u7684\u8BA9\u5BA2\u6237\u638F\u94B1\u7684: \u53C2\u52A0\u62CD\u5356\u672C\u8EAB\u4E0D\u6536\u94B1, \u4F46\u53C2\u52A0\u62CD\u5356\u9700\u8981\u5236\u4F5C\u56FE\u5F55, \u5236\u4F5C\u56FE\u5F55\u662F\u9700\u8981\u8D39\u7528\u7684.</p><p>\u6240\u4EE5, \u5BA2\u6237\u53EA\u8981\u6765\u4E86\u516C\u53F8, \u4E00\u5957\u6D41\u7A0B\u4E0B\u6765, \u591A\u534A\u4F1A\u638F\u51FA\u94B1\u6765.</p><p>&quot;\u5F00\u53D1\u85CF\u5BB6 -- \u7535\u8BDD\u7EA6\u8C08 -- \u516C\u53F8\u9762\u8C08&quot; &quot;\u5F00\u53D1\u623F\u6E90 -- \u7EA6\u770B\u623F\u5B50 -- \u516C\u53F8\u9762\u8C08&quot; \u4E24\u5957\u6D41\u7A0B\u4F55\u5176\u76F8\u4F3C. \u4F46\u623F\u5B50\u5927\u4F53\u4E0A\u90FD\u662F\u771F\u623F\u5B50, \u85CF\u54C1\u5374\u5927\u4F53\u4E0A\u90FD\u662F\u5047\u85CF\u54C1.</p><p>\u5927\u85CF\u5BB6\u90FD\u53BB\u9009\u62E9\u5609\u5FB7\u4FDD\u5229\u7B49\u5927\u7684\u62CD\u5356\u516C\u53F8, \u80FD\u6765\u6211\u4EEC\u65B0\u6210\u7ACB\u7684\u5C0F\u516C\u53F8\u7684, \u90FD\u53EA\u80FD\u662F\u90A3\u4E9B\u6307\u7740\u80FD\u5728\u6C11\u95F4\u6361\u6F0F\u7684\u5C0F\u85CF\u5BB6.</p><p>\u827A\u672F\u54C1\u5E02\u573A\u90FD\u706B\u6210\u8FD9\u6837\u4E86, \u8FD8\u80FD\u8BA9\u4F60\u5728\u6C11\u95F4\u6361\u7740\u6F0F?</p><p>\u5F53\u7136\u8FD9\u90FD\u662F\u540C\u4E8B\u4EEC\u79C1\u4E0B\u7684\u5600\u5495. \u516C\u53F8\u53F0\u9762\u4E0A\u8FD8\u662F\u8C61\u6A21\u8C61\u6837\u7684. \u57FA\u81F3\u6211\u4EEC\u7684\u6625\u62CD\u7684\u4FE1\u606F\u8FD8\u5728\u592E\u89C6\u7ECF\u6D4E\u9891\u9053\u4E0A\u4EE5\u6EDA\u52A8\u5B57\u5E55\u7684\u65B9\u5F0F\u51FA\u73B0.</p><p>\u4F46\u4E0D\u5B89\u7684\u56E0\u7D20\u8FD8\u662F\u4E00\u70B9\u70B9\u6E17\u900F\u51FA\u6765.</p><p>\u6BD4\u5982\u8001\u677F\u5728\u4E00\u6B21\u5927\u4F1A\u4E0A\u81EA\u79F0\u662F &quot;\u8D29\u5356\u4EBA\u53E3&quot; \u8D77\u5BB6: \u4ED6\u65E9\u5148\u662F\u505A\u4E3A\u6280\u672F\u9662\u6821\u8054\u7CFB\u5B9E\u4E60\u5355\u4F4D\u7684\u4E1A\u52A1\u7684, \u6BCF\u5E74\u65F6\u4E60\u7684\u65F6\u5019\u6210\u8F66\u6210\u8F66\u7684\u628A\u6280\u6821\u7684\u5B66\u4E60\u62C9\u5230\u5DE5\u5382. \u4ED6\u505A\u8FD9\u4E2A\u8D5A\u4E86\u94B1, \u79EF\u7D2F\u4E86\u4EBA\u8109, \u6B63\u597D\u5BF9\u827A\u672F\u54C1\u611F\u5174\u8DA3, \u624D\u5F00\u4E86\u8FD9\u4E2A\u62CD\u5356\u516C\u53F8.</p><p>\u6BD4\u5982\u636E\u8BF4\u516C\u53F8\u7684\u8D44\u8D28\u4E00\u76F4\u6CA1\u6709\u529E\u4E0B\u6765.</p><p>\u7136\u800C\u6211\u5E76\u4E0D\u5173\u5FC3\u8FD9\u4E00\u5207\u7684\u771F\u5047, \u6211\u53EA\u662F\u6309\u6D41\u7A0B\u505A\u5355. \u6280\u672F\u5316\u7684\u6D41\u7A0B\u4F7F\u7535\u8BDD, \u8336\u51E0\u5BF9\u9762\u7684\u6D3B\u4EBA, \u53D8\u6210\u4E86\u540D\u4E3A\u5BA2\u6237\u7684\u5BA2\u4F53.</p><p>\u76F4\u5230\u90A3\u4E2A\u8001\u5934\u51FA\u73B0\u4E86.</p><p>\u90A3\u662F\u4E2A\u5E72\u7626\u7684\u8001\u5934, \u8138\u8272\u8721\u9EC4\u8721\u9EC4\u7684. \u8BF4\u8BDD\u58F0\u97F3\u53C8\u8F7B\u53C8\u6162. \u4ED6\u60F3\u8981\u4E0A\u62CD\u7684\u85CF\u54C1\u662F\u4E00\u4E2A\u6E05\u4E09\u4EE3\u7684\u9752\u82B1\u74F6. \u6E05\u4E09\u4EE3\u7684\u9752\u82B1\u74F6\u662F\u8D5D\u54C1\u7684\u91CD\u707E\u533A. \u4F46\u662F\u8981\u62FF\u6765\u4E0A\u62CD\u7684\u4E1C\u897F, \u5728\u6211\u4EEC\u516C\u53F8\u600E\u4E48\u4F1A\u662F\u5047\u7684\u5462?</p><p>\u4ED6\u6CA1\u6709\u94B1\u4ED8\u56FE\u5F55\u8D39, \u6700\u540E\u7528\u4E24\u4E2A\u6C11\u56FD\u65F6\u7684\u9F3B\u70DF\u58F6\u62B5\u4E86\u56FE\u5F55\u8D39.</p><p>\u8001\u5934\u7684\u53E3\u5934\u7985\u662F, &quot;\u5509, \u6211\u8EAB\u4F53\u4E0D\u597D.&quot; &quot;\u5509, \u6211\u8EAB\u4F53\u4E0D\u597D, \u8BA9\u4F60\u4EEC\u4E45\u7B49\u4E86.&quot; &quot;\u5509, \u6211\u8EAB\u4F53\u4E0D\u597D, \u4E0D\u80FD\u6765\u6625\u62CD\u73B0\u573A.&quot;</p><p>\u5728\u6574\u4E2A\u7EA6\u8C08\u7684\u8FC7\u7A0B\u4E2D, \u6211\u4E00\u76F4\u5904\u5728\u8981\u6210\u5355\u7684\u5174\u594B\u4E2D, \u5B8C\u5168\u6CA1\u660E\u767D\u8FD9\u53E5\u8BDD\u7684\u610F\u601D.</p><p>\u7B49\u5230\u7B7E\u4E86\u5408\u540C, \u4ED6\u62FF\u9F3B\u70DF\u58F6\u62B5\u4E86\u56FE\u5F55\u8D39, \u6211\u4FE9\u5750\u5728\u516C\u53F8\u4F1A\u5BA2\u5385\u559D\u6C34. \u6211\u5BA2\u6C14\u9053: &quot;\u60A8\u8001\u8FD8\u662F\u8981\u6CE8\u610F\u8EAB\u4F53.&quot; \u8FD9\u65F6\u4ED6\u624D\u53F9\u6C14\u9053: &quot;\u6211\u662F\u809D\u764C, \u7B49\u5356\u4E86\u90A3\u4E2A\u74F6\u5B50\u6CBB\u75C5.&quot;</p><p>\u5F53\u65F6\u53EA\u662F\u4E00\u6014, \u4E0D\u77E5\u8BE5\u8BF4\u4EC0\u4E48\u597D. \u8FC7\u4E86\u5F88\u4E45\u6211\u624D\u611F\u53D7\u5230\u8FD9\u4EF6\u4E8B\u7684\u91CD\u91CF. \u8FD9\u91CD\u91CF\u8D8A\u6765\u8D8A\u91CD, \u91CD\u5230\u6211\u5598\u4E0D\u4E0A\u6C14\u6765.</p><p>\u6211\u5F00\u59CB\u771F\u7684\u5173\u5FC3\u516C\u53F8\u7684\u6625\u62CD, \u5E0C\u671B\u8FD9\u662F\u4E00\u573A\u771F\u7684\u62CD\u5356, \u54EA\u6015\u4E1C\u897F\u6CA1\u6709\u88AB\u62CD\u6389, \u4F46\u597D\u6B79\u88AB\u771F\u7684\u62CD\u5356\u8FC7.</p><p>\u4F46\u600E\u4E48\u53EF\u80FD\u662F\u771F\u7684\u62CD\u5356\u5462?</p><p>\u6625\u62CD\u5F53\u5929, \u53C2\u52A0\u62CD\u5356\u7684\u90FD\u662F\u8BF7\u7684\u6258: \u516C\u53F8\u9AD8\u5C42\u7684\u4EB2\u53CB\u4EEC. \u4ED6\u4EEC\u5173\u6B7B\u4E86\u62CD\u5356\u73B0\u573A\u7684\u5927\u95E8, \u8C01\u4E5F\u4E0D\u8BA9\u8FDB, \u8C01\u4E5F\u4E0D\u8BA9\u51FA, \u4E00\u7FA4\u89C1\u90FD\u6CA1\u89C1\u8FC7\u62CD\u5356\u7684\u4EBA\u5728\u91CC\u8FB9\u778E\u80E1\u62CD.</p><p>\u636E\u8BF4\u516C\u53F8\u8FD8\u8BF7\u4E86\u5317\u5C31\u4FDD\u5229\u7684\u62CD\u5356\u5E08. \u5728\u62CD\u5356\u8FC7\u7A0B\u4E2D, \u62CD\u5356\u5E08\u51E0\u6B32\u6124\u7136\u79BB\u53BB: \u4ED6\u4ECE\u6765\u6CA1\u89C1\u8FC7\u5982\u6B64\u80E1\u6574\u7684\u62CD\u5356\u4F1A.</p><p>\u62CD\u5356\u4F1A\u540E, \u516C\u53F8\u53D1\u653E\u4E1A\u52A1\u63D0\u6210. \u6211\u627E\u5230\u8001\u677F, \u8BF4: \u63D0\u6210\u6211\u4E0D\u8981\u4E86, \u80FD\u628A\u9F3B\u70DF\u58F6\u8FD8\u7ED9\u4EBA\u5BB6\u4E48?</p><p>\u8001\u677F\u76EF\u7740\u6211, \u6211\u4E5F\u76EF\u7740\u8001\u677F. \u826F\u4E45, \u4ED6\u8BF4, \u597D\u554A.</p><p>&quot;\u4ED6\u7ED9\u4F60\u63D0\u6210\u4E86\u4E48?&quot; \u540E\u6765\u54CD\u9A6C\u95EE\u6211.</p><p>&quot;\u6CA1\u6709.&quot;</p><p>&quot;\u90A3\u4ED6\u628A\u9F3B\u70DF\u58F6\u8FD8\u7ED9\u8001\u5934\u4E86\u4E48?&quot;</p><p>&quot;\u4E0D\u77E5\u9053.&quot;</p><p>&quot;\u5982\u679C\u6211\u662F\u90A3\u4E2A\u8001\u677F, \u5C31\u65E2\u4E0D\u7ED9\u4F60\u53D1\u63D0\u6210, \u4E5F\u4E0D\u8FD8\u9F3B\u70DF\u58F6.&quot; \u54CD\u9A6C\u8BF4, &quot;\u4F60\u53EA\u662F\u60F3\u9003\u907F\u7F62\u4E86.&quot;</p><p>\u6216\u8BB8\u6211\u771F\u7684\u53EA\u662F\u60F3\u9003\u907F\u7F62\u4E86. \u5F88\u5FEB\u6211\u5C31\u8F9E\u4E86\u8FD9\u4EFD\u5DE5\u4F5C, \u7070\u6E9C\u6E9C\u7684\u56DE\u4E86\u5170\u5DDE.</p><h2 id="_2011-\u4E0B\u534A\u5E74-2012-\u4E0A\u534A\u5E74-\u5E93\u7BA1" tabindex="-1"><a class="header-anchor" href="#_2011-\u4E0B\u534A\u5E74-2012-\u4E0A\u534A\u5E74-\u5E93\u7BA1" aria-hidden="true">#</a> 2011 \u4E0B\u534A\u5E74 - 2012 \u4E0A\u534A\u5E74: \u5E93\u7BA1</h2><p>2011 \u5E74\u4E0B\u534A\u5E74, \u6211\u4ECE\u65E0\u9521\u56DE\u5230\u5170\u5DDE. \u56E0\u4E3A\u62CD\u5356\u516C\u53F8\u7684\u7ECF\u5386, \u4F7F\u6211\u60F3\u627E\u4E00\u4EFD\u51ED\u529B\u6C14\u5403\u996D\u7684\u5DE5\u4F5C. \u5F53\u7136\u4E5F\u522B\u5168\u662F\u51FA\u529B\u6C14. \u6700\u597D\u8FD8\u80FD\u79EF\u7D2F\u70B9\u6280\u672F. \u4E8E\u662F\u6211\u5E72\u4E86\u5E93\u7BA1.</p><p>\u90A3\u662F\u4E00\u5BB6\u5BA0\u7269\u7528\u54C1\u98DF\u54C1\u516C\u53F8. \u7236\u8F88\u7ECF\u8425\u4E00\u5BB6\u5B9E\u4F53\u5E97, \u513F\u5B50\u4F9D\u9760\u7236\u8F88\u79EF\u7D2F\u7684\u8D44\u6E90\u505A\u4E86\u4E00\u95F4\u7F51\u5E97.</p><p>\u6211\u6240\u8D1F\u8D23\u7684\u5E93\u623F\u662F\u7F51\u5E97\u7684\u5E93\u623F. \u4E3B\u8981\u7684\u5DE5\u4F5C\u9664\u4E86\u51FA\u5165\u5E93, \u5E93\u623F\u76D8\u70B9\u5916, \u8FD8\u8D1F\u8D23\u53D1\u8D27.</p><p>\u5927\u6982\u5728\u6211\u4E4B\u524D\u7684\u5E93\u7BA1\u6587\u5316\u7A0B\u5EA6\u4E0D\u9AD8\u5427, \u6CA1\u6709\u5EFA\u7ACB\u4EFB\u4F55\u5E93\u623F\u5236\u5EA6\u548C\u8D26\u76EE. \u5E93\u623F\u91CC\u6709\u4E9B\u4EC0\u4E48\u4E1C\u897F, \u7ADF\u7136\u4E00\u70B9\u6570\u90FD\u6CA1\u6709.</p><p>\u597D\u5728\u5E93\u623F\u7684\u5DE5\u4F5C\u867D\u7136\u7E41\u91CD, \u4F46\u5E76\u4E0D\u590D\u6742. \u4E8E\u662F\u5728\u65E5\u5E38\u5DE5\u4F5C\u4E4B\u4F59, \u6211\u4E0E\u516C\u53F8\u8001\u677F\u91CD\u65B0\u5EFA\u7ACB\u4E86\u5E93\u623F\u8D26\u6237\u548C\u5E93\u623F\u5236\u5EA6.</p><p>\u7B2C\u4E00\u4E2A\u6708\u7ED3\u675F\u65F6, \u5F53\u6211\u62FF\u7740\u5E93\u623F\u8D26\u672C\u7ED9\u8001\u677F\u770B\u65F6, \u8001\u677F\u6B23\u559C\u7684\u8BF4, \u4ECE\u6765\u6CA1\u6709\u770B\u8FC7\u5982\u4E9B\u6E05\u6670\u5E72\u51C0\u7684\u8D26\u76EE.</p><p>\u6211\u5728\u8FD9\u5BB6\u5BA0\u7269\u5E97\u5E72\u4E86\u534A\u5E74, \u8001\u677F\u56E0\u4E3A\u628A\u7F51\u5E97\u6253\u7406\u7684\u4E0D\u9519, \u800C\u83B7\u5F97\u4E86\u7236\u8F88\u7684\u8BA4\u53EF, \u4ECE\u800C\u63A5\u624B\u4E86\u5B9E\u4F53\u5E97\u7684\u7ECF\u8425. \u6211\u7684\u5934\u8854\u4E5F\u4ECE\u5E93\u623F\u5F20\u5E08\u5085\u5347\u6210\u4E86 &quot;\u7269\u6D41\u7ECF\u7406&quot;.</p><p>\u4F46\u6211\u4E00\u65B9\u9762\u4E0D\u7518\u5FC3\u505A\u4E00\u4E2A &quot;\u770B\u72D7\u7CAE&quot; \u7684, \u4E00\u65B9\u9762\u4E5F\u975E\u5E38\u60F3\u4E86\u89E3\u4E00\u4E0B\u6B63\u89C4\u7684\u5E93\u623F\u5236\u5EA6\u662F\u5982\u4F55\u8FD0\u884C\u7684.</p><p>\u4E8E\u662F\u6211\u8F9E\u53BB\u4E86\u5BA0\u7269\u7528\u54C1\u98DF\u54C1\u516C\u53F8\u7684\u5DE5\u4F5C, \u53BB\u5E94\u8058\u4E86\u5F20\u88D5\u96C6\u56E2\u897F\u5317\u5206\u516C\u53F8\u7684\u5E93\u7BA1\u5C97\u4F4D.</p><hr><p>\u5728\u5BA0\u7269\u7528\u54C1\u98DF\u54C1\u5E97\u65F6, \u6211\u5E38\u5E38\u81EA\u8C6A\u4E8E\u81EA\u5DF1\u4E00\u4E2A\u4EBA\u53EF\u4EE5\u628A 300 \u5E73\u7C73\u7684\u5E93\u623F\u6253\u7406\u7684\u6709\u6761\u4E0D\u7D0A. \u4F46\u770B\u5230\u5F20\u88D5\u4E24\u5343\u5E73\u7C73\u7684\u5E93\u623F\u65F6, \u6211\u624D\u77E5\u9053\u6211\u4EE5\u524D\u4E0D\u8FC7\u662F\u8FDB\u5E95\u4E4B\u86D9.</p><p>\u5728\u5F20\u88D5\u6211\u624D\u771F\u7684\u89C1\u5230\u771F\u6B63\u7684\u5E93\u623F\u8D26\u76EE\u662F\u4EC0\u4E48\u6837, \u4E5F\u8BA9\u6211\u89C1\u8BC6\u5230\u5982\u4F55\u901A\u8FC7\u5C97\u4F4D\u5206\u5DE5\u6765\u5B9E\u73B0\u90E8\u95E8\u95F4\u76F8\u4E92\u5236\u7EA6\u76F8\u4E92\u76D1\u7763.</p><p>\u5F20\u88D5\u96C6\u56E2\u5C06\u5E93\u623F\u7BA1\u7406\u7684\u4E1A\u52A1\u5916\u5305\u7ED9\u5F53\u5730\u7684\u4ED3\u50A8\u516C\u53F8, \u5E76\u7531\u96C6\u56E2\u6D3E\u51FA\u5E93\u7BA1\u6765\u7BA1\u7406\u4ED3\u50A8\u516C\u53F8 ( \u6211\u5C31\u662F\u8FD9\u4E2A\u5C97\u4F4D ), \u4F46\u771F\u6B63\u6709\u6743\u529B\u5BF9\u5E93\u5B58\u7684\u662F\u5730\u533A\u5206\u516C\u53F8.</p><p>\u7531\u6B64\u5C31\u5F62\u6210\u4E86\u5E93\u7BA1\u770B\u7740\u4ED3\u50A8\u516C\u53F8, \u4ED3\u50A8\u516C\u53F8\u770B\u7740\u5730\u533A\u5206\u516C\u53F8, \u5730\u533A\u5206\u516C\u53F8\u770B\u7740\u5E93\u7BA1\u7684\u76D1\u7763\u7ED3\u6784.</p><p>\u5728\u5F20\u88D5\u9664\u4E86\u770B\u5230\u4E00\u4E2A\u771F\u6B63\u7684\u5927\u516C\u53F8\u5982\u4F55\u7BA1\u7406\u5E93\u623F\u5916, \u5BF9\u540E\u6765\u5DE5\u4F5C\u66F4\u91CD\u8981\u7684\u662F, \u5728\u5F20\u88D5\u628A excel \u73A9\u6E9C\u4E86.</p><hr><p>\u5728\u8FD9\u4E00\u6BB5\u65F6\u95F4\u5185, \u6211\u7684\u5927\u5973\u513F\u51FA\u751F\u4E86. \u5E93\u7BA1\u7684\u5FAE\u8584\u5DE5\u8D44, \u5B8C\u5168\u4E0D\u80FD\u652F\u6491\u4E00\u4E2A\u4E09\u53E3\u4E4B\u524D\u7684\u65E5\u5E38\u751F\u6D3B, \u6211\u4E0D\u5F97\u4E0D\u91CD\u65B0\u8003\u8651\u51FA\u8DEF.</p><p>\u800C\u8FD9\u65F6, \u54CD\u9A6C\u56DE\u5230\u4E86\u5170\u5DDE.</p><h2 id="_2012-2015-\u5BCC\u5A92\u4F53" tabindex="-1"><a class="header-anchor" href="#_2012-2015-\u5BCC\u5A92\u4F53" aria-hidden="true">#</a> 2012 - 2015 \u5BCC\u5A92\u4F53</h2><p>2012 \u5E74, \u54CD\u9A6C\u5E26\u7740\u5BCC\u5A92\u4F53\u7684\u751F\u610F\u56DE\u5230\u4E86\u5170\u5DDE. \u800C\u540E, \u6211\u4E0E\u54CD\u9A6C\u4E00\u8D77\u505A\u4E86\u4E09\u5E74\u5BCC\u5A92\u4F53.</p><p>\u6211\u4E00\u76F4\u6709\u4E00\u79CD\u5F3A\u70C8\u7684\u611F\u89C9: \u8FD9\u4E09\u5E74\u6240\u6709\u7684\u6545\u4E8B, \u90FD\u662F\u54CD\u9A6C\u7684\u6545\u4E8B, \u800C\u6211\u53EA\u662F\u6709\u5E78\u53C2\u4E88\u4E86.</p><hr><p>\u54CD\u9A6C\u662F\u6211\u7684\u9AD8\u4E2D\u540C\u5B66, \u4ED6\u5728\u5317\u4EAC\u8BFB\u7684\u5927\u5B66. \u6BD5\u4E1A\u540E, \u4ED6\u7559\u5728\u5317\u4EAC\u5DE5\u4F5C, \u6362\u8FC7\u51E0\u5BB6\u516C\u53F8, \u6700\u540E\u9009\u62E9\u4E86\u5BCC\u5A92\u4F53\u884C\u4E1A.</p><p>\u5BCC\u5A92\u4F53\u662F\u7528\u4E8E\u5C55\u89C8\u5C55\u793A\u7684\u6570\u5B57\u4E92\u52A8\u6280\u672F, \u4E3B\u8981\u5305\u62EC\u591A\u70B9\u89E6\u63A7, \u4F53\u611F\u8BC6\u522B, \u865A\u62DF\u73B0\u5B9E, \u589E\u5F3A\u73B0\u5B9E, \u4E92\u52A8\u6295\u5F71\u7B49\u6280\u672F.</p><p>2012 \u5E74\u4ED6\u56E0\u4E3A\u4E00\u4E9B\u5BB6\u5EAD\u53D8\u6545\u9700\u8981\u56DE\u5230\u5170\u5DDE, \u516C\u53F8\u8001\u677F\u89C9\u5F97\u4ED6\u662F\u4E2A\u53EF\u7528\u7684\u4EBA\u624D, \u4FBF\u8BA9\u4ED6\u5728\u5170\u5DDE\u5EFA\u7ACB\u529E\u4E8B\u5904, \u5F00\u62D3\u897F\u5317\u5E02\u573A.</p><p>\u4ED6\u4E00\u4E2A\u4EBA\u5728\u5170\u5DDE\u505A\u5E02\u573A, \u592A\u8FC7\u5B64\u72EC, \u6B63\u78B0\u5DE7\u6211\u5728\u6362\u5DE5\u4F5C, \u4E0E\u662F\u4ED6\u9080\u8BF7\u6211\u4E00\u8D77\u548C\u4ED6\u505A\u5BCC\u5A92\u4F53.</p><p>\u4F46\u8FD9\u5176\u5B9E\u4E0E\u516C\u53F8\u7684\u89C4\u5212\u5E76\u4E0D\u76F8\u7B26: \u516C\u53F8\u672C\u6765\u7684\u9884\u671F\u662F, \u4ED6\u6765\u5170\u5DDE\u7684\u7B2C\u4E00\u5E74, \u53EF\u80FD\u53EA\u80FD\u662F\u4E86\u89E3\u5E02\u573A, \u5E76\u4E0D\u4F1A\u5F00\u5355, \u4E5F\u5C31\u6CA1\u6709\u6536\u5165. \u8FD9\u79CD\u60C5\u51B5\u4E0B, \u516C\u53F8\u662F\u4E0D\u53EF\u80FD\u589E\u52A0\u4EBA\u5458\u7684.</p><p>\u56E0\u6B64, \u6211\u53EA\u80FD\u5148\u4E0D\u4EE5\u6B63\u5F0F\u5458\u5DE5\u7684\u8EAB\u4EFD\u53C2\u4E88\u5230\u8FD9\u4EF6\u4E8B\u4E2D. \u7B49\u5230\u5F00\u5355\u540E\u518D\u6B63\u5F0F\u5411\u516C\u53F8\u63D0\u51FA\u8BA9\u6211\u5165\u804C\u7684\u8981\u6C42.</p><p>\u9002\u9022\u5170\u5DDE\u4E3E\u529E\u7B2C\u4E00\u5C4A\u56FD\u9645\u8F66\u5C55, \u6211\u4EEC\u5C31\u5C06\u76EE\u6807\u5BF9\u51C6\u5170\u5DDE\u7684 4s \u5E97.</p><p>\u5170\u5DDE\u4E00\u5171\u6709\u4E00\u767E\u56DB\u5341\u591A\u5BB6 4s \u5E97, \u79BB\u56FD\u9645\u8F66\u5C55\u5F00\u5E55\u5927\u6982\u8FD8\u8981\u4E00\u4E2A\u534A\u6708\u7684\u65F6\u95F4, \u5982\u679C\u6211\u4EEC\u60F3\u8DD1\u5B8C\u6240\u6709\u7684 4s \u5E97, \u7B97\u4E0B\u6765\u6211\u4EEC\u4E00\u5929\u9700\u8981\u8DD1\u4E09\u5230\u56DB\u5BB6.</p><p>\u54CD\u9A6C\u6709\u4E00\u8F86\u957F\u5B89\u4E4B\u661F\u7684\u5C0F\u9762\u5305, \u4E0E\u662F\u4ED6\u6BCF\u5929\u65E9\u4E0A\u5F00\u8F66\u63A5\u4E0A\u6211, \u6211\u5728\u540E\u6392\u6253\u7535\u8BDD\u7EA6\u5BA2\u6237, \u4ED6\u5F00\u8F66\u89C1\u5BA2\u6237. \u65E9\u4E0A\u89C1\u4E24\u4E2A\u5BA2\u6237, \u4E2D\u5348\u5728\u8F66\u4E0A\u4F11\u606F\u4E00\u4E0B, \u4E0B\u5348\u518D\u89C1\u4E24\u4E2A\u5BA2\u6237.</p><p>\u5F53\u7136\u65E2\u4F7F\u8FD9\u6837\u6211\u4EEC\u4E5F\u6CA1\u6709\u8DD1\u5B8C\u4E00\u767E\u56DB\u5341\u5BB6 4s \u5E97. \u4E00\u65B9\u9762\u662F\u5F53\u6211\u4EEC\u8DD1\u5230\u516B\u5341\u591A\u5BB6\u7684\u65F6\u5019, \u53D1\u73B0\u5269\u4E0B\u7684\u5E97\u548C\u4E4B\u524D\u7684\u5E97\u8001\u677F\u662F\u4E00\u6837\u7684\u4E86; \u53E6\u4E00\u4E2A\u66F4\u91CD\u8981\u7684\u539F\u56E0\u662F, \u6211\u4EEC\u5DF2\u7ECF\u5F00\u5355\u4E86.</p><p>\u867D\u7136\u53EA\u662F\u4E00\u4E2A\u79DF\u8D41\u7684\u5C0F\u5355, \u4F46\u5B83\u7684\u610F\u4E49\u975E\u51E1; \u9996\u5148\u8D85\u8FC7\u4E86\u516C\u53F8\u7684\u9884\u671F, \u5728\u5170\u5DDE\u529E\u4E8B\u5904\u8BBE\u7ACB\u7684\u5934\u4E00\u5E74\u91CC\u5F00\u4E86\u5355; \u800C\u6211\u4E5F\u80FD\u591F\u6B63\u5F0F\u5165\u804C\u4E86.</p><hr><p>\u5728\u8FD9\u4E2A\u9879\u76EE\u5B9E\u65BD\u7684\u65F6\u5019, \u516C\u53F8\u7684\u8001\u677F\u4EB2\u81EA\u5230\u73B0\u573A\u6307\u5BFC\u5B89\u88C5\u5DE5\u4F5C. \u987A\u4FBF\u4EB2\u81EA\u9762\u8BD5\u4E86\u6211.</p><p>\u9762\u8BD5\u7684\u7ED3\u679C\u5176\u5B9E\u5E76\u4E0D\u8BA9\u4ED6\u6EE1\u610F. \u539F\u56E0\u5F88\u591A: \u6CA1\u6709\u63A5\u89E6\u8FC7 IT \u884C\u4E1A, \u4E5F\u6CA1\u6709\u63A5\u89E6\u8FC7\u5927\u5BA2\u6237\u9500\u552E, \u6700\u91CD\u8981\u7684\u662F\u4ED6\u5224\u65AD\u6211\u7684\u6027\u683C\u5E76\u4E0D\u9002\u5408\u4F5C\u9500\u552E.</p><p>\u4F46\u56E0\u4E3A\u54CD\u9A6C\u7684\u575A\u6301, \u52A0\u4E0A\u6BD5\u7ADF\u6709\u4E00\u5355\u5728\u524D, \u6240\u4EE5\u6700\u540E\u8FD8\u662F\u8BA9\u6211\u5165\u804C\u4E86.</p><p>\u6211\u5165\u804C\u540E\u7684\u5C97\u4F4D\u4E0E\u54CD\u9A6C\u4E00\u6837, \u9879\u76EE\u7ECF\u7406, \u4E3B\u8981\u5DE5\u4F5C\u662F\u5927\u5BA2\u6237\u9500\u552E.</p><p>\u679C\u4E0D\u51FA\u8001\u677F\u6240\u6599, \u6211\u5E76\u4E0D\u9002\u5408\u505A\u5927\u5BA2\u6237\u9500\u552E: \u505A\u4E86\u51E0\u4E4E\u4E00\u5E74, \u5374\u8FDE\u6709\u610F\u5411\u7684\u5BA2\u6237\u90FD\u6CA1\u804A\u51FA\u6765.</p><p>\u800C\u54CD\u9A6C\u5728\u8FD9\u6BB5\u65F6\u95F4\u91CC, \u62FF\u4E0B\u4E86\u4E00\u4E2A\u7518\u8083\u7701\u56FE\u4E66\u9986\u7684\u9879\u76EE. \u6211\u5728\u8DD1\u5BA2\u6237\u4E4B\u4F59, \u8FD8\u5E2E\u54CD\u9A6C\u8FDB\u884C\u4E00\u4E9B\u9879\u76EE\u7BA1\u7406\u7684\u5DE5\u4F5C.</p><p>\u516C\u53F8\u53D1\u73B0, \u4E0E\u5176\u8BA9\u6211\u8C6A\u65E0\u6548\u7387\u7684\u8DD1\u4E1A\u52A1, \u4E0D\u5982\u8BA9\u6211\u5E2E\u52A9\u54CD\u9A6C\u505A\u4E00\u4E9B\u7BA1\u7406\u7C7B\u7684\u8F85\u52A9\u7C7B\u7684\u5DE5\u4F5C, \u8BA9\u54CD\u9A6C\u817E\u51FA\u624B\u6765\u4E13\u5FC3\u8DD1\u5BA2\u6237.</p><p>\u4E0E\u662F\u6211\u7684\u5C97\u4F4D\u8C03\u6574\u4E3A\u9879\u76EE\u52A9\u7406, \u5DE5\u4F5C\u5185\u5BB9\u4E3A\u9500\u552E\u4E4B\u5916\u7684\u4E00\u5207\u4E8B\u52A1\u6027\u5DE5\u4F5C.</p><hr><p>\u5176\u65F6\u54CD\u9A6C\u56DE\u5230\u5170\u5DDE\u5DF2\u8FD1\u4E24\u5E74\u65F6\u95F4, \u6211\u4EEC\u53EA\u505A\u4E86\u4E24\u4E2A\u9879\u76EE: \u8F66\u5C55\u4E0A\u7684\u8BBE\u5907\u79DF\u8D41\u548C\u7701\u56FE\u4E66\u9986\u7684\u955C\u9762\u6210\u8C61\u9879\u76EE.</p><p>\u800C\u771F\u6B63\u7684\u5927\u5355 -- \u6574\u4F53\u6570\u5B57\u5316\u5C55\u5385 -- \u8FD8\u4E00\u70B9\u5F71\u5B50\u4E5F\u6CA1\u6709.</p><p>\u516C\u53F8\u6E10\u6E10\u5F00\u59CB\u5411\u6211\u4EEC\u4F20\u9012\u4E00\u4E9B\u538B\u529B, \u7126\u8651\u7684\u60C5\u7EEA\u6F2B\u5EF6\u5728\u5170\u5DDE\u529E\u4E8B\u5904.</p><p>\u6211\u4EEC\u8BD5\u56FE\u6062\u590D\u5230\u5F53\u521D\u8C08 4s \u5E97\u65F6\u7684\u72B6\u6001. \u6211\u4EEC\u627E\u6765\u4E00\u672C\u9EC4\u9875, \u8BA1\u5212\u4ECE\u7B2C\u4E00\u9875, \u4E00\u4E2A\u5355\u4F4D\u4E00\u4E2A\u5355\u4F4D\u7EA6\u8FC7\u53BB.</p><p>\u5F53\u65F6\u5170\u5DDE\u529E\u4E8B\u5904\u6CA1\u6709\u79DF\u529E\u516C\u5BA4, \u6211\u4EEC\u5404\u81EA\u5728\u5BB6\u529E\u516C. \u4F46\u6211\u4F9D\u7136\u6BCF\u5929\u8D77\u5E8A\u540E\u6362\u4E0A\u4E0A\u73ED\u7A7F\u7684\u6B63\u88C5, \u5371\u895F\u6B63\u5750\u5728\u81EA\u5BB6\u7535\u8111\u684C\u524D, \u62FF\u51FA\u9EC4\u9875\u4F9D\u6B21\u7EA6\u5BA2\u6237.</p><p>\u8FD9\u662F\u5728\u62CD\u5356\u516C\u53F8\u7EC3\u5C31\u7684\u7535\u8BDD\u8F93\u51FA\u7684\u8001\u628A\u620F\u4E86. \u4F46\u51E1\u7EA6\u5B9A\u7684, \u4FBF\u8BB0\u901A\u77E5\u54CD\u9A6C\u53BB\u8C08. \u54CD\u9A6C\u8C08\u5B8C, \u4E00\u51FA\u5BA2\u6237\u529E\u516C\u5BA4, \u4FBF\u7ED9\u6211\u6253\u7535\u8BDD, \u8BB2\u8FF0\u5BA2\u6237\u60C5\u51B5. \u6211\u5C06\u5BA2\u6237\u60C5\u51B5\u4E00\u4E00\u8BB0\u4E0B, \u6839\u636E\u9700\u6C42\u5236\u4F5C\u6587\u6863, \u518D\u8BA9\u54CD\u9A6C\u9001\u53BB.</p><p>\u54CD\u9A6C\u540E\u6765\u8BF4\u8D77, \u90A3\u4E00\u6BB5\u65F6\u95F4\u662F\u611F\u89C9\u6700\u8F7B\u677E\u7684\u65F6\u5019: \u6BCF\u5929\u65E9\u4E0A\u4F1A\u6536\u5230\u6211\u7684\u77ED\u4FE1, \u4E0A\u9762\u5199\u8981\u89C1\u7684\u5BA2\u6237\u60C5\u51B5, \u7EA6\u5B9A\u7684\u65F6\u95F4\u5730\u70B9, \u5982\u679C\u4ED6\u7684\u8F66\u9650\u53F7\u8FD8\u6709\u5982\u4F55\u5750\u516C\u5171\u4EA4\u901A\u53BB\u7EA6\u4F1A\u5730\u70B9\u7684\u4FE1\u606F. \u4ED6\u6839\u672C\u4E0D\u8981\u591A\u60F3, \u53EA\u8981\u6309\u7EA6\u5B9A\u53BB\u8C08\u5C31\u597D\u4E86.</p><p>\u5F53\u7136\u4E5F\u6709\u4E0D\u5C3D\u5982\u4EBA\u610F\u7684\u5730\u65B9. \u6211\u65E0\u6CD5\u4ECE\u7535\u8BDD\u9884\u7EA6\u4E2D\u4E86\u89E3\u5BA2\u6237\u610F\u5411\u7684\u5F3A\u5EA6 -- \u786E\u5207\u7684\u8BF4, \u6211\u6839\u672C\u4E0D\u53BB\u5224\u65AD\u8FD9\u4E2A, \u53EA\u8981\u5BF9\u65B9\u80AF\u89C1\u9762, \u54EA\u8FD8\u6709\u653E\u8FC7\u7684?</p><p>\u56E0\u6B64\u4E0D\u514D\u63A5\u5230\u54CD\u9A6C\u7684\u7535\u8BDD: &quot;\u8FD9\u79CD\u53EA\u662F\u9001\u4E00\u4E0B\u8D44\u6599\u7684\u5BA2\u6237, \u4EE5\u540E\u80FD\u4E0D\u80FD\u4E0D\u8981\u7EA6\u4E86??!!&quot;</p><p>&quot;\u597D\u597D\u597D&quot; \u6211\u4E00\u8FB9\u5728\u7535\u8BDD\u4E2D\u6EE1\u53E3\u5E94\u7740, \u4E00\u8FB9\u628A\u9001\u8D44\u6599\u7684\u5BA2\u6237\u653E\u5230\u7EA6\u8C08\u7684\u5B89\u6392\u4E2D\u53BB.</p><p>\u800C\u6070\u6070\u5C31\u662F\u8FD9\u6837\u9001\u8D44\u6599, \u9001\u51FA\u4E00\u4E2A\u5927\u9879\u76EE.</p><p>\u5BA2\u6237\u662F\u7518\u8083\u7701\u9AD8\u6CD5. \u7B2C\u4E00\u6B21\u53BB\u7EA6\u8C08, \u53EA\u542C\u8BF4\u4ED6\u4EEC\u8981\u5EFA\u4E00\u4E2A\u6587\u5316\u957F\u5ECA. \u4F46\u4ED6\u4EEC\u5BF9\u4E8E\u6211\u4EEC\u7684\u6280\u672F\u4F3C\u4E4E\u5E76\u4E0D\u611F\u5174\u8DA3, \u4E0D\u54B8\u4E0D\u6DE1\u7684\u7559\u4E86\u8D44\u6599, \u5C31\u8BA9\u54CD\u9A6C\u56DE\u53BB\u4E86.</p><p>\u672C\u6765\u662F\u4E0D\u62B1\u4EC0\u4E48\u5E0C\u671B\u7684, \u4F46\u8FD8\u662F\u505A\u4E86\u65B9\u6848, \u54CD\u9A6C\u9001\u53BB\u65F6, \u6B63\u78B0\u4E0A\u9886\u5BFC\u5728\u4E0E\u8BBE\u8BA1\u5355\u4F4D\u5F00\u8BC4\u5BA1\u4F1A, \u9886\u5BFC\u770B\u5230\u54CD\u9A6C\u5F88\u9AD8\u5174, \u8BA9\u4ED6\u5750\u5728\u8EAB\u8FB9\u63D0\u63D0\u610F\u89C1. \u54CD\u9A6C\u6839\u636E\u8BBE\u8BA1\u5355\u4F4D\u7684\u8BBE\u8BA1, \u63D0\u4E86\u4E0D\u5C11\u6570\u5B57\u5316\u4E92\u52A8\u5C55\u793A\u7684\u5EFA\u8BAE.</p><p>\u53EF\u80FD\u662F\u54CD\u9A6C\u7684\u5EFA\u8BAE\u7ED3\u5408\u8BBE\u8BA1\u65B9\u7684\u8BBE\u8BA1\u5F62\u6210\u4E86\u771F\u5B9E\u7684\u8BED\u5883, \u7ADF\u4E00\u4E3E\u83B7\u5F97\u4E86\u9886\u5BFC\u7684\u8BA4\u53EF, \u6210\u529F\u7684\u8BA9\u6211\u4EEC\u53C2\u4E8E\u5230\u6587\u957F\u5316\u5ECA\u7684\u5EFA\u8BBE\u4E2D.</p><hr><p>\u7518\u8083\u7701\u9AD8\u6CD5\u9879\u76EE\u6210\u529F\u6253\u5F00\u4E86\u5170\u5DDE\u529E\u4E8B\u5904\u7684\u5C40\u9762. \u5BF9\u4E8E\u7518\u8083\u7684\u653F\u6CD5\u53E3\u5355\u4F4D, \u8FD9\u662F\u4E00\u4E2A\u975E\u5E38\u6709\u8BF4\u670D\u529B\u7684\u6848\u4F8B.</p><p>\u968F\u540E, \u6211\u4EEC\u53C8\u505A\u4E86\u7518\u8083\u7701\u56FE\u4E66\u9986\u56DB\u5E93\u5168\u4E66\u5C55\u5385\u9879\u76EE.</p><p>\u8FD9\u4E2A\u9879\u76EE\u6572\u5B9A\u4E4B\u540E, \u516C\u53F8\u540C\u610F\u5728\u5170\u5DDE\u79DF\u529E\u516C\u5BA4, \u62DB\u65B0\u5458\u5DE5.</p><p>\u5170\u5DDE\u529E\u4E8B\u5904\u8C61\u6A21\u8C61\u6837\u7684\u529E\u8D77\u6765\u4E86.</p><p>\u4F46\u8FD9\u65F6, \u6211\u56E0\u4E3A\u4E00\u4E9B\u5BB6\u5EAD\u539F\u56E0, -- \u5176\u5B9E\u662F\u56E0\u4E3A\u5170\u5DDE\u7684\u623F\u4EF7\u592A\u9AD8, \u6211\u5B9E\u5728\u4E70\u4E0D\u8D77\u623F\u5B50 -- \u6211\u51B3\u5B9A\u518D\u6B21\u6765\u65E0\u9521\u53D1\u5C55. \u5F53\u65F6\u6211\u4EEC\u5BB6\u5DF2\u7ECF\u5728\u65E0\u9521\u7ED9\u6211\u4E70\u4E86\u5957\u623F\u4E86.</p><p>2015 \u5E74\u521D, \u6211\u7B2C\u4E8C\u6B21\u79BB\u5F00\u5170\u5DDE, \u6765\u5230\u65E0\u9521.</p><h2 id="_2015-2018-\u533A\u5757\u94FE\u5E94\u7528" tabindex="-1"><a class="header-anchor" href="#_2015-2018-\u533A\u5757\u94FE\u5E94\u7528" aria-hidden="true">#</a> 2015 -- 2018: \u533A\u5757\u94FE\u5E94\u7528</h2><p>2015 \u5E74, \u6211\u7B2C\u4E8C\u6B21\u6765\u5230\u65E0\u9521. \u8FD9\u6B21\u6211\u9009\u62E9\u4E86\u4E00\u5BB6\u505A\u533A\u5757\u94FE\u6280\u672F\u7814\u53D1\u548C\u5E94\u7528\u7684\u516C\u53F8\u5C31\u804C.</p><p>\u8BF4\u8D77\u6765, \u8FD9\u5E94\u8BE5\u7B97\u662F\u4E2A\u96C6\u56E2\u516C\u53F8. \u8BF4\u8D77\u516C\u53F8\u7684\u5386\u53F2\u6765, \u5145\u6EE1\u4E86\u7406\u60F3\u4E3B\u4E49\u7684\u8272\u5F69.</p><p>\u96C6\u56E2\u6700\u65E9\u8D77\u6E90\u4E00\u4E2A\u8BBA\u575B\u7684\u4F5C\u8005, \u4ED6\u5199\u4E86\u4E00\u7CFB\u5217\u5173\u4E8E\u4E16\u754C\u653F\u6CBB, \u7ECF\u6D4E, \u79D1\u6280\u53D1\u5C55\u65B9\u5411\u7684\u6587\u7AE0. \u8FD9\u4E00\u7CFB\u5217\u6587\u7AE0\u5438\u5F15\u4E86\u4E00\u6279\u793E\u4F1A\u7CBE\u82F1.</p><p>\u540E\u6765\u8FD9\u7FA4\u793E\u4F1A\u7CBE\u82F1\u51B3\u5B9A\u56F4\u7ED5\u7740\u8FD9\u4E2A\u4F5C\u8005\u505A\u70B9\u4EC0\u4E48, \u4E8E\u662F\u6709\u4E86\u8FD9\u4E2A\u96C6\u56E2\u516C\u53F8.</p><p>\u5728\u6BD4\u7279\u5E01\u521A\u5174\u8D77\u7684\u5E74\u4EE3\u91CC, \u4ED6\u4EEC\u6295\u8D44\u6BD4\u7279\u5E01\u77FF\u673A\u6323\u4E86\u94B1. \u540E\u6765\u6295\u8D44\u77FF\u673A\u7684\u5229\u6DA6\u8D8A\u6765\u8D8A\u4F4E, \u4ED6\u4EEC\u4FBF\u8F6C\u5411\u533A\u5757\u94FE\u6280\u672F\u7814\u53D1\u548C\u5E94\u7528, \u8FD9\u5C31\u662F\u6211\u6240\u5165\u804C\u7684\u516C\u53F8.</p><p>\u5F53\u7136\u636E\u8BF4, \u96C6\u56E2\u9664\u4E86\u533A\u5757\u94FE\u516C\u53F8, \u8FD8\u6709\u673A\u5668\u4EBA\u516C\u53F8\u548C\u53D1\u52A8\u673A\u516C\u53F8.</p><hr><p>\u5F53\u65F6\u516C\u53F8\u5904\u4E8E\u521D\u521B\u9636\u6BB5, \u521A\u4E0A\u7EBF\u4E86\u4E00\u4E2A\u4EA4\u6613\u5E73\u53F0, \u7528\u6765\u4EA4\u6613\u516C\u53F8\u7684\u533A\u5757\u94FE\u53D1\u884C\u7684\u865A\u62DF\u51ED\u8BC1.</p><p>\u6211\u5165\u804C\u7684\u5C97\u4F4D\u662F\u5BA2\u670D\u52A9\u7406, \u4E3B\u8981\u5DE5\u4F5C\u5C31\u662F\u670D\u52A1\u8FD9\u4E2A\u4EA4\u6613\u5E73\u53F0\u7684\u5BA2\u6237. \u540C\u65F6, \u516C\u53F8\u8FD8\u6D3D\u8C08\u4E00\u4E9B\u533A\u5757\u94FE\u7CFB\u7EDF\u5F00\u53D1\u7684\u9879\u76EE, \u6211\u4E5F\u5E2E\u52A9\u5236\u4F5C\u4E00\u4E9B\u65B9\u6848.</p><p>\u5982\u6B64\u6253\u6742\u4E86\u4E24\u4E2A\u591A\u6708, \u5FFD\u7136\u4E00\u5929\u665A\u4E0A, \u63A5\u5230\u516C\u53F8\u4EA7\u54C1\u603B\u76D1\u7684\u7535\u8BDD, \u95EE\u6211\u60F3\u4E0D\u60F3\u53BB\u6D77\u822A\u505A\u4EA7\u54C1\u7ECF\u7406.</p><p>\u6211\u53D7\u5BA0\u82E5\u60CA: \u5F53\u7136\u613F\u610F\u5566, \u4F46\u6211\u4EE5\u524D\u4ECE\u6765\u6CA1\u505A\u8FC7\u554A. \u4EA7\u54C1\u603B\u76D1\u8BF4, \u6CA1\u505A\u8FC7\u53EF\u4EE5\u5B66\u554A, \u5982\u679C\u4F60\u613F\u610F\u7684\u8BDD, \u628A\u5BB6\u91CC\u5B89\u6392\u4E00\u4E0B, \u51C6\u5907\u51FA\u5DEE\u5427.</p><p>\u7B2C\u4E8C\u5929\u5230\u4E86\u516C\u53F8, \u6211\u624D\u77E5\u9053\u4E8B\u60C5\u7684\u6765\u9F99\u53BB\u8109:</p><p>\u6211\u4EEC\u516C\u53F8\u6709\u4E00\u4E2A\u6D77\u822A\u7684\u9879\u76EE, \u516C\u53F8\u4EC5\u6709\u7684\u4EA7\u54C1\u7ECF\u7406, \u4E5F\u5C31\u662F\u6211\u4EEC\u7684\u4EA7\u54C1\u603B\u76D1\u88AB\u59D4\u6D3E\u5230\u6D77\u53E3\u9A7B\u573A. \u6D77\u53E3\u6C14\u5019\u6076\u52A3, \u52A0\u4E0A\u9879\u76EE\u5DE5\u4F5C\u7E41\u5FD9, \u4EA7\u54C1\u603B\u76D1\u7ADF\u88AB\u7D2F\u51FA\u4E86\u80BA\u7ED3\u6838.</p><p>\u6211\u4EEC\u516C\u53F8\u662F\u4E00\u4E2A\u521D\u521B\u516C\u53F8, \u5C31\u8FD9\u4E48\u4E00\u4E2A\u4EA7\u54C1\u7ECF\u7406. \u6240\u4EE5\u5F53\u65F6, \u5FC5\u987B\u8981\u5728\u4E1A\u52A1\u4EBA\u5458\u4E2D\u627E\u4E00\u4E2A\u61C2\u70B9\u6280\u672F\u7684\u9876\u5230\u8FD9\u4E2A\u5C97\u4F4D\u4E0A\u53BB. \u800C\u6211\u5728\u505A\u5BCC\u5A92\u4F53\u65F6, \u4E3A\u4E86\u505A\u9879\u76EE\u5B9E\u65BD, \u591A\u591A\u5C11\u5C11\u5B66\u4E86\u4E00\u4E9B\u6280\u672F\u77E5\u8BC6, \u56E0\u6B64\u88AB\u516C\u53F8\u9009\u4E2D.</p><hr><p>\u6D77\u822A\u96C6\u56E2\u91C7\u8D2D\u90E8\u5EFA\u6709\u4E00\u5957 SRM \u7CFB\u7EDF ( \u4F9B\u5E94\u5546\u7BA1\u7406\u7CFB\u7EDF ). \u8FD9\u5957 SRM \u7CFB\u7EDF\u4E3B\u8981\u7684\u529F\u80FD\u662F\u91C7\u8D2D\u5BFB\u6E90. \u800C\u6211\u4EEC\u7684\u6D77\u822A\u9879\u76EE, \u5C31\u662F\u5728\u8FD9\u5957 SRM \u7CFB\u7EDF\u4E4B\u4E0A, \u5EFA\u8BBE\u4E00\u5957\u91C7\u8D2D\u652F\u4ED8\u7CFB\u7EDF.</p><p>\u4E4B\u6240\u4EE5\u91C7\u7528\u6211\u4EEC\u516C\u53F8\u7684\u533A\u5757\u94FE\u6280\u672F, \u662F\u51FA\u4E8E\u5F53\u65F6\u91C7\u8D2D\u90E8\u9886\u5BFC\u7684\u524D\u77BB\u6027\u8003\u8651. \u5728\u5F53\u65F6, \u533A\u5757\u94FE\u6280\u672F\u8FD8\u88AB\u4E3A\u662F\u4E00\u79CD\u6700\u5148\u8FDB\u7684\u652F\u4ED8\u6280\u672F, \u662F\u672A\u6765\u652F\u4ED8\u6280\u672F\u7684\u53D1\u5C55\u65B9\u5411.</p><p>\u4F5C\u4E3A\u4E34\u65F6\u66FF\u8865\u4E0A\u573A\u7684\u65B0\u624B, \u8FD8\u6CA1\u6709\u7ECF\u63A5\u53D7\u4EFB\u4F55\u7684\u8BAD\u7EC3, \u5C31\u88AB\u4E22\u5230\u6700\u524D\u7EBF\u7684\u9879\u76EE\u4E2D, \u4E00\u8138\u61F5\u903C\u7684\u6211, \u80FD\u505A\u7684\u53EA\u662F\u4E8B\u4E8B\u8BB0\u5F55, \u65E5\u65E5\u6C47\u62A5.</p><p>\u597D\u5728\u5F53\u65F6\u7532\u65B9\u7684\u9879\u76EE\u7ECF\u7406\u5F88\u6709\u7ECF\u9A8C, \u4E5F\u613F\u610F\u6559\u6211. \u800C\u4E0E\u6211\u5408\u4F5C\u7684\u662F SRM \u7CFB\u7EDF\u7684\u5EFA\u8BBE\u65B9, \u4ED6\u4EEC\u662F\u56FD\u5185\u6700\u5927\u7684\u7532\u9AA8\u6587\u4EE3\u7406\u5546, \u627F\u88AD\u4E86\u4E00\u5957\u5148\u8FDB\u7684\u9879\u76EE\u7BA1\u7406\u65B9\u6CD5\u8BBA.</p><p>\u4E8E\u662F\u6211\u975E\u5E38\u5E78\u8FD0\u7684, \u5728\u4E00\u4E2A\u771F\u5B9E\u7684\u5927\u578B\u9879\u76EE\u4E2D, \u7528\u4E00\u5957\u6B63\u89C4\u7684\u65B9\u6CD5\u8BBA, \u88AB\u7532\u65B9\u7684\u9879\u76EE\u7ECF\u7406\u8C03\u6559\u51FA\u6765\u4E86.</p><p>\u6211\u8FDB\u5165\u9879\u76EE\u5927\u6982\u56DB\u4E2A\u6708\u4E4B\u540E, \u9879\u76EE\u5C31\u6B63\u5F0F\u4E0A\u7EBF\u4E86. \u9879\u76EE\u4E0A\u7EBF\u540E, \u516C\u53F8\u7684\u6280\u672F\u4EBA\u5458\u88AB\u8C03\u56DE\u516C\u53F8, \u800C\u6211\u88AB\u7559\u5728\u6D77\u53E3\u5BF9\u63A5\u540E\u7EED\u7684\u4E8C\u671F\u9700\u6C42.</p><p>\u90A3\u6BB5\u65F6\u95F4, \u6211\u4E00\u4E2A\u4EBA\u7559\u5728\u6D77\u53E3, \u6536\u96C6\u9700\u6C42, \u5199\u4F5C\u6587\u6863, \u4E0E\u7532\u65B9\u5F00\u4F1A, \u987A\u4FBF\u89E3\u51B3\u4E00\u4E9B\u4E00\u671F\u9879\u76EE\u9057\u7559\u4E0B\u6765\u7684\u95EE\u9898. \u800C\u56E0\u4E3A\u6CA1\u6709\u6280\u672F\u4EBA\u5458\u5728\u65C1\u534F\u52A9, \u7ADF\u903C\u7740\u6211\u53C8\u5B66\u4E86\u4E0D\u5C11\u6280\u672F\u77E5\u8BC6.</p><p>\u800C\u5C31\u5728\u6211\u5B64\u72EC\u7684\u5728\u6D77\u53E3\u6536\u96C6\u4E8C\u671F\u9700\u6C42\u7684\u65F6\u5019, \u65E0\u9521\u7684\u516C\u53F8\u7ADF\u7136\u53D1\u751F\u4E86\u4E00\u6B21\u5927\u5206\u88C2.</p><p>\u516C\u53F8\u7684\u6280\u672F\u90E8\u95E8\u4E0E\u4E1A\u52A1\u90E8\u95E8\u53D1\u751F\u5DE8\u5927\u7684\u5206\u6B67. \u4E1A\u52A1\u90E8\u95E8\u8D1F\u8D23\u4EBA, \u516C\u53F8\u7684 ceo D \u603B\u501F\u52A9\u96C6\u56E2\u5185\u7684\u53E6\u4E00\u4E2A\u5317\u4EAC\u7684\u5B50\u516C\u53F8, \u91CD\u5851\u4E86\u7EC4\u7EC7\u67B6\u6784, \u5E76\u56DE\u5934\u89E3\u6563\u6280\u672F\u90E8\u95E8.</p><p>\u636E\u8BF4, \u516C\u53F8\u7684\u5185\u6597\u7ED3\u675F\u540E, \u6211\u5728\u5BA2\u670D\u90E8\u95E8\u7684\u9886\u5BFC, \u63D0\u9192 D \u603B\u6211\u4EEC\u8FD8\u6709\u4E00\u4E2A\u4EA7\u54C1\u7ECF\u7406\u88AB\u5916\u6D3E\u5230\u6D77\u53E3\u4E86, \u516C\u53F8\u624D\u60F3\u8D77\u6765, \u8FD8\u6709\u6211\u8FD9\u53F7\u4EBA.</p><hr><p>\u6211\u4E00\u4E2A\u4EBA\u5728\u6D77\u53E3\u5B64\u72EC\u7684\u6536\u96C6\u7684\u91C7\u8D2D\u652F\u4ED8\u4E8C\u671F\u9700\u6C42, \u5E76\u6CA1\u6709\u8FDB\u5165\u5B9E\u65BD\u9636\u6BB5. \u56E0\u4E3A\u6D77\u822A\u5185\u90E8\u53D1\u751F\u4E00\u4E2A\u91CD\u5927\u7684\u53D8\u5316.</p><p>\u6D77\u822A\u96C6\u56E2\u8981\u6C42\u6240\u6709\u90E8\u95E8\u5FC5\u987B\u76C8\u5229, \u5305\u62EC\u91C7\u8D2D\u90E8. \u56E0\u6B64\u91C7\u8D2D\u652F\u4ED8\u7CFB\u7EDF\u5FC5\u987B\u5411\u4EA7\u751F\u6536\u76CA\u7684\u65B9\u5411\u53D1\u5C55.</p><p>\u7ECF\u8FC7\u8BA8\u8BBA\u6700\u7EC8\u786E\u5B9A\u4E86\u4E24\u4E2A\u65B9\u5411: \u4E00\u4E2A\u662F\u4F9D\u6258\u91C7\u8D2D\u652F\u4ED8\u7684\u4F18\u52BF\u548C\u4EBA\u529B\u8D44\u6E90\u90E8\u95E8\u4E00\u8D77\u505A\u6D77\u822A\u5185\u90E8\u7684\u5F39\u6027\u798F\u5229\u5236\u5EA6; \u53E6\u4E00\u4E2A\u662F\u5229\u7528\u6D77\u822A\u5728\u91C7\u8D2D\u73AF\u8282\u4E2D\u7684\u6838\u5FC3\u4F01\u4E1A\u7684\u5730\u4F4D\u505A\u4F9B\u5E94\u94FE\u91D1\u878D.</p><p>\u6211\u4EEC\u5B8C\u6210\u4E86\u5F39\u6027\u798F\u5229\u5E73\u53F0\u7684\u5EFA\u8BBE, \u5E76\u4E14\u62FF\u4E0B\u4E86\u798F\u5229\u5E73\u53F0\u7684\u4EE3\u8FD0\u8425\u4E1A\u52A1.</p><p>\u540C\u65F6\u6211\u4EEC\u4E5F\u5BF9\u6D77\u822A\u4F9B\u5E94\u94FE\u91D1\u878D\u5E73\u53F0\u7684\u8FDB\u884C\u4E86\u89C4\u5219\u548C\u8BBE\u8BA1, \u4F46\u662F\u8FD8\u6CA1\u6765\u5F97\u53CA\u5B9E\u65BD, \u516C\u53F8\u7ADF\u7136\u53D1\u751F\u7B2C\u4E8C\u6B21\u5206\u88C2.</p><p>\u5BFC\u81F4\u8FD9\u6B21\u516C\u53F8\u5206\u88C2\u7684\u539F\u56E0\u662F, \u5317\u4EAC\u516C\u53F8\u6269\u5F20\u592A\u5FEB, \u7ADF\u7136\u5728\u4E0D\u5230\u4E00\u5E74\u7684\u65F6\u95F4\u91CC\u628A\u94B1\u5168\u90E8\u70E7\u5B8C\u4E86.</p><p>\u7B2C\u4E00\u6B21\u516C\u53F8\u5206\u88C2\u4E4B\u540E, \u5317\u4EAC\u516C\u53F8\u8FC5\u901F\u6269\u5F20\u8D77\u6765, \u5F88\u5FEB\u7531\u539F\u6765\u5341\u51E0\u4E2A\u4EBA\u7684\u5C0F\u56E2\u961F\u81A8\u80C0\u6210\u4E00\u4E2A\u4E0A\u767E\u53F7\u4EBA\u7684\u56E2\u961F.</p><p>\u516C\u53F8\u9664\u4E86\u6D77\u822A\u9879\u76EE\u7684\u5F00\u53D1\u56E2\u961F\u5916, \u8FD8\u6709\u5176\u5B83\u7684\u4EA7\u54C1\u548C\u76F8\u5E94\u7684\u5F00\u53D1\u56E2\u961F; \u800C\u62FF\u4E0B\u798F\u5229\u5E73\u53F0\u8FD0\u8425, \u4E5F\u610F\u5473\u7740\u516C\u53F8\u5FC5\u987B\u517B\u4E00\u4E2A\u5E9E\u5927\u7684\u8FD0\u8425\u56E2\u961F.</p><p>\u800C\u65E2\u7136\u6709\u4E86\u5982\u6B64\u5927\u7684\u4E1A\u52A1\u56E2\u961F, \u90A3\u914D\u5957\u7684\u884C\u653F, \u4EBA\u4E8B, \u8D26\u52A1, \u98CE\u63A7\u7B49\u90E8\u95E8\u4E5F\u8981\u6709\u76F8\u5E94\u7684\u6269\u5F20.</p><p>\u800C\u8FD9\u4E9B\u7BA1\u7406\u90E8\u95E8\u4E00\u65E6\u81A8\u80C0\u8D77\u6765, \u5404\u79CD\u5236\u5EA6\u4E5F\u5C31\u63A5\u8E35\u800C\u81F3. \u5236\u5EA6\u5206\u5272\u4E86\u8D23\u4EFB\u548C\u6743\u5229, \u4E0E\u662F\u5EFA\u7ACB\u5728\u6743\u8D23\u4E4B\u4E0A\u7684\u90E8\u95E8\u5229\u76CA\u4E5F\u5F00\u59CB\u51FA\u73B0. \u4E8E\u662F\u4E00\u4E2A\u521D\u521B\u4E24\u5E74\u4E0D\u5230\u7684\u516C\u53F8, \u53D8\u6210\u4E86\u4E00\u4E2A\u81C3\u80BF\u7684\u5DE8\u7269. \u800C\u8FD9\u4E2A\u5DE8\u7269\u7EC8\u4E8E\u62D6\u8DE8\u4E86\u81EA\u8EAB.</p><p>\u5728\u5317\u4EAC\u516C\u53F8\u8DE8\u6389\u540E, \u96C6\u56E2\u672C\u6765\u60F3\u628A\u516C\u53F8\u91CD\u5EFA\u8D77\u6765. \u4F46\u8FD9\u65F6 D \u603B\u5374\u4E8E\u96C6\u56E2\u53D1\u751F\u4E86\u5206\u6B67: \u96C6\u56E2\u4E3B\u5F20\u901A\u8FC7\u533A\u5757\u94FE\u53D1\u884C\u865A\u62DF\u5E01, \u800C D \u603B\u575A\u6301\u8BA4\u4E3A\u53D1\u884C\u865A\u62DF\u5E01\u662F\u975E\u5E38\u5371\u9669\u7684, \u5E94\u8BE5\u575A\u6301\u5229\u7528\u533A\u5757\u94FE\u4F5C\u5B9E\u9645\u5E94\u7528.</p><p>\u6700\u540E\u7684\u7ED3\u679C\u662F, D \u603B\u4EE5\u5BF9\u8D4C\u534F\u8BAE\u4E3A\u4EE3\u4EF7\u62C9\u6765\u4E00\u7B14\u6295\u8D44, \u5E76\u5229\u7528\u8FD9\u7B14\u6295\u8D44\u5728\u6DF1\u5733\u53E6\u7ACB\u95E8\u6237.</p><p>\u8FD9\u65F6\u6211\u9762\u4E34\u4E00\u4E2A\u9009\u62E9, \u662F\u7559\u5728\u96C6\u56E2\u91CD\u7EC4\u7684\u516C\u53F8\u4E2D, \u8FD8\u662F\u540C D \u603B\u53BB\u4ED6\u6210\u7ACB\u7684\u65B0\u516C\u53F8.</p><p>\u6211\u9009\u62E9\u8DDF\u968F D \u603B, \u7406\u7531\u4E5F\u5F88\u7B80\u5355, \u6211\u81EA\u8BA4\u4E3A\u5BF9\u4E8E\u5404\u79CD\u8DEF\u7EBF\u6CA1\u6709\u5224\u65AD\u7684\u80FD\u529B, \u4F46\u6BD4\u8D77\u4E00\u4E2A\u62BD\u8C61\u7684\u96C6\u56E2, \u6211\u66F4\u76F8\u4FE1\u4E00\u4E2A\u6D3B\u751F\u751F\u7684\u4EBA\u7684\u5224\u65AD.</p><hr><p>\u4E0E\u6211\u4E00\u8D77\u8DDF\u968F ceo \u6765\u5230\u65B0\u516C\u53F8\u7684, \u6709\u98CE\u63A7\u4EBA\u5458, \u884C\u653F\u4EBA\u5458, \u8D22\u52A1\u4EBA\u5458, \u5546\u52A1\u4EBA\u5458, \u72EC\u72EC\u6CA1\u6709\u6280\u672F\u4EBA\u5458.</p><p>\u800C\u5F53\u65F6\u6211\u4EEC\u9762\u5BF9\u7684\u662F\u6D77\u822A\u7684\u65B0\u9700\u6C42: \u6D77\u822A\u5E0C\u671B\u57FA\u4E8E\u798F\u5229\u5E73\u53F0\u7684\u79EF\u5206, \u505A\u79EF\u5206\u4EA4\u6362, \u6253\u901A\u6D77\u822A\u65D7\u4E0B\u4E0D\u540C\u4E1A\u52A1\u95F4\u7684\u4E0D\u540C\u5E94\u7528.</p><p>\u627E\u5916\u5305\u662F\u5F53\u65F6\u552F\u4E00\u7684\u9009\u62E9. \u4E8E\u662F\u6211\u5F00\u59CB\u5E26\u7740\u5916\u5305\u56E2\u961F\u505A\u79EF\u5206.</p><p>\u5927\u6982\u7ECF\u5386\u4E86\u4E24\u4E2A\u8FED\u4EE3, \u56DB\u4E2A\u6708\u7684\u65F6\u95F4, \u6211\u4EEC\u505A\u51FA\u4E00\u7B2C\u4E00\u4E2A\u7A33\u5B9A\u7248\u672C. \u800C\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D, \u6211\u4EEC\u4E5F\u5EFA\u7ACB\u4E86\u81EA\u5DF1\u7684\u6280\u672F\u56E2\u961F. \u4E00\u5207\u4F3C\u4E4E\u90FD\u5728\u8D70\u4E0A\u6B63\u8F68.</p><p>\u4F46\u6CA1\u6709\u60F3\u5230\u7684\u662F, \u79EF\u5206\u4EA4\u6362\u8FD9\u4E2A\u4EA7\u54C1\u6700\u7EC8\u4E5F\u53EA\u8D70\u5230\u4E86\u8FD9\u4E00\u6B65.</p><p>\u8FD9\u4E2A\u7A33\u5B9A\u7248\u672C\u7684\u79EF\u5206\u4EA4\u6362\u5E73\u53F0, \u628A\u8FD9\u4E2A\u5F53\u521D\u62CD\u677F\u7528\u533A\u5757\u94FE\u505A\u91C7\u8D2D\u652F\u4ED8\u7684\u91C7\u8D2D\u90E8\u9886\u5BFC\u4ECE\u91C7\u8D2D\u90E8\u9001\u5230\u4E86\u6D77\u822A\u91D1\u878D\u7248\u5757.</p><p>\u5E76\u4E14\u6D77\u822A\u96C6\u56E2\u51B3\u5B9A\u6210\u7ACB\u4E00\u4E2A\u516C\u53F8\u6765\u4E13\u95E8\u8FD0\u8425\u8FD9\u4E2A\u79EF\u5206\u4EA4\u6362\u5E73\u53F0.</p><p>\u4F46\u8FD9\u5E76\u6CA1\u6709\u4F7F\u79EF\u5206\u4EA4\u6362\u4EA7\u54C1\u8FDB\u5165\u65B0\u7684\u4EA7\u54C1\u9636\u6BB5. \u6D77\u822A\u7684\u9886\u5BFC\u8981\u6C42\u6211\u4EEC\u91CD\u6784\u4E86\u79EF\u5206\u4EA4\u6362\u5E73\u53F0. \u529F\u80FD\u6CA1\u6709\u4EFB\u4F55\u6539\u53D8, \u53EA\u662F\u6362\u76AE.</p><p>\u800C\u6362\u76AE\u7684\u76EE\u7684\u4E5F\u53EA\u662F\u4E3A\u4E86\u7ED9\u66F4\u9AD8\u7684\u9886\u5BFC\u6F14\u793A\u8D77\u6765\u597D\u770B\u4E9B.</p><p>\u800C\u6B64\u65F6, \u6B63\u662F 2017 \u5E74\u7684\u4E0A\u534A\u5E74. \u4F3C\u4E4E\u662F\u4E00\u591C\u95F4, \u533A\u5757\u94FE\u6280\u672F\u88AB\u7092\u4E0A\u4E86\u795E\u575B. \u4F46\u88AB\u7092\u4E0A\u795E\u575B\u7684\u4E0D\u662F\u533A\u5757\u94FE\u6280\u672F\u7684\u5E94\u7528, \u800C\u662F\u865A\u62DF\u5E01\u672C\u8EAB.</p><p>\u8FD9\u79CD\u6700\u521D\u8BBE\u8BA1\u6765\u5BF9\u6297\u73B0\u4EE3\u8D27\u5E01\u4F53\u7CFB\u7684\u6280\u672F, \u7ADF\u53D8\u6210\u4E86\u8D44\u672C\u6536\u5272\u97ED\u83DC\u7684\u9570\u5200.</p><p>\u6211\u4E0D\u77E5\u9053\u6211\u4EEC\u7684\u6295\u8D44\u4EBA\u662F\u5426\u7ED9\u516C\u53F8\u65BD\u52A0\u8FC7\u538B\u529B, \u8BA9\u6211\u4EEC\u53D1\u884C\u865A\u62DF\u5E01; \u6211\u4E5F\u4E0D\u77E5\u9053 D \u603B\u5728\u591C\u6DF1\u4EBA\u9759\u7684\u65F6\u5019, \u6709\u6CA1\u6709\u626A\u5FC3\u81EA\u95EE, \u9009\u62E9\u8FD9\u6837\u4E00\u6761\u53D1\u5C55\u8DEF\u5F84\u662F\u5426\u6B63\u786E.</p><p>\u4F46\u6211\u80FD\u611F\u89C9\u5230\u7684\u662F, \u6574\u4E2A\u516C\u53F8\u7684\u6C1B\u56F4\u53D8\u5F97\u5FAE\u5999\u8D77\u6765.</p><p>\u5F53\u65F6\u6211\u4EA7\u751F\u4E86\u4E00\u4E2A\u5224\u65AD, \u533A\u5757\u94FE\u884C\u4E1A\u8981\u5B8C:</p><ul><li>\u9996\u5148, \u7167\u7740\u8FD9\u5E2E\u4ECE\u4E1A\u8005\u73A9\u4E0B\u53BB, \u533A\u5757\u94FE\u884C\u4E1A\u88AB\u6C61\u540D\u5316\u662F\u8FDF\u65E9\u7684\u4E8B;</li><li>\u5176\u6B21, \u5982\u679C\u5199\u5199\u767D\u76AE\u4E66, \u53D1\u53D1\u865A\u62DF\u5E01\u5C31\u53EF\u4EE5\u8D5A\u7684\u76C6\u6EE1\u94B5, \u6709\u8C01\u8FD8\u4F1A\u6C89\u4E0B\u5FC3\u6765\u505A\u6280\u672F\u7814\u53D1\u548C\u6280\u672F\u5E94\u7528.</li><li>\u800C\u4E00\u4E2A\u6CA1\u4EBA\u505A\u7814\u53D1\u548C\u5E94\u7528\u7684\u6280\u672F, \u8FDF\u65E9\u662F\u8981\u7834\u4EA7\u7684.</li></ul><p>\u8FDB\u800C, \u4E00\u4E2A\u4E25\u91CD\u7126\u8651\u722C\u4E0A\u6211\u7684\u5FC3\u5934: \u6211\u81EA\u5165\u884C\u4EE5\u6765\u4E00\u76F4\u4ECE\u4E8B\u533A\u5757\u94FE\u884C\u4E1A, \u6211\u6240\u79EF\u7D2F\u7684\u7ECF\u9A8C\u662F\u5426\u5728\u4E92\u8054\u7F51\u884C\u4E1A\u80FD\u591F\u901A\u7528. \u5982\u679C\u4E0D\u80FD\u901A\u7684\u8BDD, \u7B49\u5230\u8FD9\u4E2A\u884C\u4E1A\u5B8C\u86CB\u7684\u90A3\u5929, \u6211\u4E0D\u662F\u88AB\u903C\u4E0A\u7EDD\u8DEF\u4E86?</p><p>\u4E8E\u662F\u6211\u51B3\u5B9A\u5728\u533A\u5757\u94FE\u8FD8\u6CA1\u6709\u88AB\u6C61\u540D\u5316\u65F6, \u5FEB\u901F\u79BB\u5F00\u8FD9\u4E2A\u884C\u4E1A. \u8FD9\u5728\u8FD9\u4E2A\u65F6\u95F4\u8282\u70B9\u79BB\u5F00\u8FD9\u4E2A\u884C\u4E1A, \u533A\u5757\u94FE\u884C\u4E1A\u7684\u7ECF\u9A8C\u8FD8\u663E\u5F97\u6709\u70B9\u4F18\u52BF.</p><h2 id="_2018-\u4F9B\u5E94\u94FE\u91D1\u878D\u4EE5\u53CA\u4EE5\u540E" tabindex="-1"><a class="header-anchor" href="#_2018-\u4F9B\u5E94\u94FE\u91D1\u878D\u4EE5\u53CA\u4EE5\u540E" aria-hidden="true">#</a> 2018 \u4F9B\u5E94\u94FE\u91D1\u878D\u4EE5\u53CA\u4EE5\u540E</h2><p>2018 \u5E74, \u6211\u4ECE\u533A\u5757\u94FE\u884C\u4E1A\u51FA\u6765\u540E, \u6211\u8BD5\u7740\u6765\u4E0A\u6D77\u53D1\u5C55.</p><p>\u4E00\u8F6E\u9762\u8BD5\u540E, \u6709\u4E24\u4E2A\u786E\u5B9A\u7684\u673A\u4F1A\u6446\u5230\u4E86\u6211\u9762\u524D. \u4E00\u4E2A\u662F\u505A\u667A\u6167\u5DE5\u5730\u7684, \u4E00\u4E2A\u662F\u505A\u4F9B\u5E94\u94FE\u91D1\u878D\u7684.</p><p>\u6211\u603B\u89C9\u5F97\u6240\u8C13\u7684\u667A\u6167\u5DE5\u5730\u53EA\u662F\u505A\u4FE1\u606F\u5316\u5EFA\u8BBE, \u800C\u4F9B\u5E94\u94FE\u91D1\u878D\u624D\u662F\u771F\u7684\u4E92\u8054\u7F51\u884C\u4E1A. \u4E8E\u662F\u6211\u9009\u62E9\u4E86\u4F9B\u5E94\u94FE\u91D1\u878D.</p><p>\u8FD9\u5176\u5B9E\u662F\u975E\u5E38\u9519\u8BEF\u7684\u51B3\u5B9A.</p><p>\u8FD9\u5BB6\u516C\u53F8\u7684\u6BCD\u516C\u53F8\u662F\u4E00\u4E2A\u6709\u56FD\u8D44\u80CC\u666F\u7684\u4F9B\u5E94\u94FE\u516C\u53F8. \u5728 p2p \u98CE\u884C\u7684\u5E74\u4EE3, \u4ED6\u4EEC\u505A\u4E86\u4E00\u5BB6 p2p \u516C\u53F8, \u5E76\u4E14\u8D5A\u5230\u4E86\u94B1.</p><p>\u5C1D\u5230\u4E92\u8054\u7F51\u91D1\u878D\u751C\u5934\u540E, \u516C\u53F8\u51B3\u5B9A\u5C06\u4E92\u8054\u7F51\u91D1\u878D\u4E0E\u81EA\u8EAB\u7684\u4E1A\u52A1\u7ED3\u5408\u8D77\u6765. \u4E8E\u662F\u51B3\u5B9A\u505A\u4E00\u4E2A\u4F9B\u5E94\u94FE\u91D1\u878D\u7CFB\u7EDF.</p><p>\u6211\u8FDB\u5165\u516C\u53F8\u4E4B\u540E, \u516C\u53F8\u5DF2\u7ECF\u7528\u4ED6\u4EEC\u539F\u6765\u7684 p2p \u7CFB\u7EDF\u6253\u4E86\u4E2A\u5E95\u5B50. \u4F46 p2p \u7684\u4E1A\u52A1\u548C\u4F9B\u5E94\u94FE\u91D1\u878D\u7684\u4E1A\u52A1\u5DEE\u8DDD\u8FD8\u662F\u5F88\u5927\u7684.</p><p>\u597D\u5728\u6211\u7ED9\u6D77\u822A\u89C4\u5212\u8FC7\u4F9B\u5E94\u94FE\u91D1\u878D\u9879\u76EE, \u5BF9\u4E8E\u4EA7\u54C1\u8BBE\u8BA1\u7684\u601D\u8DEF\u975E\u5E38\u6E05\u6670, \u4E5F\u83B7\u5F97\u4E86\u76F4\u5C5E\u9886\u5BFC\u548C\u603B\u516C\u53F8\u7684\u8BA4\u53EF.</p><p>\u4F3C\u4E4E\u524D\u9014\u4E00\u7247\u5927\u597D.</p><p>\u7136\u800C\u6709\u4E00\u5929, \u5F53\u6211\u62FF\u7740\u8BBE\u8BA1\u7684\u539F\u578B\u53BB\u8DDF\u4EA7\u54C1\u603B\u76D1\u8BA8\u8BBA\u7684\u65F6\u5019. \u60CA\u95FB\u96C6\u56E2\u7684 p2p \u516C\u53F8\u66B4\u96F7\u4E86: p2p \u516C\u53F8\u7684\u9AD8\u5C42\u5377\u4E86\u6C89\u6DC0\u8D44\u91D1\u8DD1\u8DEF\u53BB\u4E86.</p><p>\u6211\u4EEC\u7684\u6709\u56FD\u8D44\u80CC\u666F\u7684\u6BCD\u516C\u53F8, \u4E3A\u4E86\u505A\u5207\u5272, \u51B3\u5B9A\u628A\u6240\u6709\u4E92\u8054\u7F51\u5B50\u516C\u53F8\u5168\u5173\u4E86.</p><p>\u7136\u800C\u8BA9\u4EBA\u6CA1\u6709\u60F3\u5230\u7684\u662F, p2p \u66B4\u96F7\u53EA\u662F\u4E00\u4E2A\u5F00\u7AEF, \u7D27\u63A5\u7740, \u4E92\u8054\u7F51\u4E2D\u5404\u4E2A\u884C\u4E1A\u4F20\u6765\u574F\u6D88\u606F.</p><p>\u90A3\u4E9B\u53F7\u79F0\u8981\u6539\u53D8\u4E16\u754C\u7684\u516C\u53F8\u7684\u60C5\u6000\u4E00\u4E00\u7834\u4EA7.</p><p>\u5148\u662F\u6EF4\u6EF4\u63A5\u8FDE\u51FA\u4E8B, \u800C\u540E\u5171\u4EAB\u5355\u8F66\u4E5F\u51C9\u4E86, \u5230\u5E74\u5E95\u4E92\u8054\u7F51\u6574\u4F53\u8FDB\u5165\u4E86\u5BD2\u51AC.</p>',249),a=[e];function h(q,s){return t(),o("div",null,a)}var n=p(r,[["render",h],["__file","\u5DE5\u4F5C\u5341\u5E74.html.vue"]]);export{n as default};
