import{_ as t,o,c as e,e as a}from"./app.6bf529a6.js";const u={},p=a('<h2 id="\u8749\u7684\u6545\u4E8B" tabindex="-1"><a class="header-anchor" href="#\u8749\u7684\u6545\u4E8B" aria-hidden="true">#</a> \u8749\u7684\u6545\u4E8B</h2><p>\u67CF\u62C9\u56FE\u7684\u300A\u6590\u5FB7\u82E5\u7BC7\u300B\u8BB2\u4E86\u8FD9\u6837\u4E00\u4E2A\u6545\u4E8B:</p><p>\u4ECE\u5415\u897F\u4E9A\u65AF\u4F4F\u5904\u51FA\u6765\u7684\u6590\u5FB7\u82E5\u5728\u57CE\u5916\u6563\u6B65\u78B0\u4E0A\u4E86\u82CF\u683C\u62C9\u5E95. \u4ED6\u9080\u8BF7\u82CF\u683C\u62C9\u5E95\u4E00\u8D77\u5728\u57CE\u5916\u6563\u6B65, \u4E00\u76F4\u6765\u5230\u57CE\u5916\u6CB3\u8FB9\u4E00\u68F5\u68A7\u6850\u6811\u4E0B. \u6B64\u5904\u6070\u662F\u6C34\u4ED9\u4E0E\u6CB3\u795E\u7684\u795E\u9F9B\u6240\u5728. \u6590\u5FB7\u82E5\u4E3A\u82CF\u683C\u62C9\u5E95\u5FF5\u4E86\u5415\u897F\u4E9A\u65AF\u6700\u65B0\u7684\u6587\u7AE0. \u8FD9\u7BC7\u6587\u7AE0\u662F\u529D\u4E00\u4E2A\u5E74\u9752\u4EBA\u63A5\u53D7\u6CA1\u6709\u7231\u6B32\u8005\u7684\u8BB2\u8F9E. \u6590\u5FB7\u82E5\u4E3A\u5415\u897F\u4E9A\u65AF\u8FD9\u7BC7\u6587\u7AE0\u503E\u5012, \u5E76\u8BA4\u4E3A\u4E0D\u4F1A\u6709\u6BD4\u8FD9\u7BC7\u6587\u7AE0\u66F4\u597D\u7684\u6587\u7AE0\u4E86. \u82CF\u683C\u62C9\u5E95\u5374\u8BA4\u4E3A\u8FD9\u7BC7\u6587\u7AE0\u867D\u597D, \u4F46\u5E76\u975E\u5B8C\u7F8E. \u6590\u5FB7\u82E5\u8BA9\u82CF\u683C\u62C9\u5E95\u4E5F\u4F5C\u4E00\u7BC7\u6587\u7AE0\u548C\u5415\u897F\u65AF\u6BD4\u8D5B. \u82CF\u683C\u62C9\u5E95\u53D7\u5F53\u5730\u6C34\u4ED9\u6CB3\u795E\u7684\u795E\u7075\u611F\u53D1\u53E3\u5360\u4E86\u4E00\u7BC7\u4E3B\u4E3B\u9898\u4E00\u6837\u4E3A\u5426\u5B9A\u7231\u6B32\u7684\u8BB2\u8F9E. \u5F53\u82CF\u683C\u62C9\u5E95\u8BB2\u5B8C\u540E, \u7A81\u7136\u89C9\u5F97\u81EA\u5DF1\u505A\u6240\u5F97\u7F6A\u4E86\u7231\u795E, \u56E0\u6B64\u8981\u518D\u505A\u4E00\u7BC7\u8BB2\u8F9E\u6765\u8D5E\u7F8E\u7231\u795E\u4EE5\u505A\u8D4E\u7F6A. \u4E8E\u662F\u4ED6\u505A\u4E86\u7B2C\u4E8C\u7BC7\u8BB2\u8F9E. \u8FD9\u7BC7\u8BB2\u8F9E\u4E2D, \u82CF\u683C\u62C9\u5E95\u8D5E\u7F8E\u4E86\u4F5C\u4E3A\u795E\u5723\u7684\u8FF7\u72C2\u7684\u7231\u6B32. \u6590\u5FB7\u82E5\u542C\u4E86\u7B2C\u4E8C\u7BC7\u8BB2\u8F9E\u540E, \u975E\u5E38\u94A6\u4F69, \u60F3\u8BF7\u5415\u897F\u4E9A\u65AF\u518D\u505A\u4E00\u7BC7\u8BB2\u8F9E\u4E0E\u82CF\u683C\u62C9\u5E95\u6BD4\u8D5B. \u4F46\u4ED6\u53C8\u62C5\u5FC3\u5415\u897F\u4E9A\u65AF\u56E0\u4E3A\u88AB\u522B\u4EBA\u653B\u51FB\u4E3A &quot;\u5199\u6F14\u8BB2\u7A3F\u7684\u4EBA&quot;, \u800C\u4E0D\u613F\u5199\u4F5C. \u82CF\u683C\u62C9\u5E95\u8868\u793A\u4E0D\u5FC5\u62C5\u5FC3, \u8FDB\u800C\u8BBA\u8BC1\u4E86\u5199\u4F5C\u4E0D\u662F\u53EF\u803B\u7684\u4E8B, \u53EA\u8981\u5199\u7684\u597D. \u4E0E\u4E24\u4EBA\u5F00\u59CB\u8BA8\u8BBA\u4EC0\u4E48\u6837\u7684\u5199\u4F5C\u624D\u662F\u5199\u7684\u597D. \u4ED6\u4EEC\u8BA8\u8BBA\u4E86\u8FA9\u8BC1\u6CD5\u4E0E\u4FEE\u8F9E\u5B66\u7684\u5173\u7CFB, \u53C8\u8BA8\u8BBA\u4E86\u5199\u4F5C\u6D3B\u52A8\u6F5C\u85CF\u7684\u5371\u9669, \u6700\u540E\u5F97\u51FA\u7ED3\u8BBA: \u5982\u679C\u4ED6\u4EEC\u7684\u4F5C\u5415\u662F\u4F9D\u636E\u771F\u7406\u5199\u6210\u7684, \u4E14\u80FD\u4E3A\u81EA\u5DF1\u8FA9\u62A4, \u90A3\u4ED6\u4EEC\u7684\u5C31\u80FD\u79F0\u4E3A\u54F2\u5B66\u5BB6.</p><p>\u8FD9\u7BC7\u5BF9\u8BDD\u5F55\u524D\u534A\u90E8\u5206\u901A\u8FC7\u5415\u897F\u4E9A\u65AF\u548C\u82CF\u683C\u62C9\u5E95\u76F8\u4E92\u6BD4\u8D5B\u7684\u4E09\u7BC7\u8BB2\u8F9E\u8BA8\u8BBA\u4E86\u7231\u6B32\u662F\u597D\u662F\u574F\u7684\u95EE\u9898, \u540E\u534A\u90E8\u5206\u8BDD\u9898\u8F6C\u5411\u4E86\u8FA9\u8BC1\u6CD5, \u4FEE\u8F9E\u672F\u548C\u5199\u4F5C. \u5728\u8FD9\u4E24\u90E8\u5206\u7684\u6B63\u4E2D\u95F4, \u4E5F\u5C31\u662F\u82CF\u683C\u62C9\u5E95\u8BBA\u8BC1\u4E86\u5199\u4F5C\u53EA\u8981\u5199\u7684\u597D\u5C31\u5E76\u4E0D\u53EF\u803B\u540E, \u51C6\u5907\u5F00\u59CB\u8BA8\u8BBA\u4EC0\u4E48\u6837\u5199\u4F5C\u662F\u597D\u7684\u5199\u4F5C\u524D, \u82CF\u683C\u62C9\u5E95\u7A81\u7136\u63D2\u4E86\u4E00\u6BB5\u5173\u4E8E\u8749\u7684\u6545\u4E8B:</p><blockquote><p>\u82CF\u683C\u62C9\u5E95: \u90A3\u4E48\u4F5C\u54C1\u7684\u597D\u4E0E\u574F\u5177\u6709\u4EC0\u4E48\u6837\u7684\u6027\u8D28\u5462? \u6590\u5FB7\u82E5, \u6839\u636E\u4F5C\u54C1\u7684\u597D\u6765\u8003\u5BDF\u5415\u897F\u4E9A\u65AF, \u5BF9\u6211\u4EEC\u6765\u8BF4\u662F\u4E49\u4E0D\u5BB9\u8F9E\u7684, \u5BF9\u5417? \u5BF9\u5176\u4ED6\u6240\u6709\u5DF2\u6709\u4F5C\u54C1\u95EE\u4E16\u6216\u60F3\u8981\u5199\u6587\u7AE0\u7684\u4EBA\u6765\u8BF4\u90FD\u662F\u8FD9\u6837, \u65E0\u8BBA\u4F5C\u54C1\u6D89\u53CA\u653F\u6CBB\u9886\u57DF\u8FD8\u662F\u79C1\u4EBA\u4E8B\u52A1, \u65E0\u8BBA\u4ED6\u7684\u4F5C\u54C1\u662F\u97F5\u6587\u6765\u662F\u6563\u6587.</p><p>\u6590\u5FB7\u82E5: \u8FD9\u6837\u505A\u5F53\u7136\u4E49\u4E0D\u8F9E! \u8FD9\u662F\u4E00\u4EF6\u4E50\u4E8B, \u6CA1\u6709\u8FD9\u6837\u7684\u5FEB\u4E50\u751F\u6D3B\u5C31\u6CA1\u6709\u4EC0\u4E48\u4EF7\u503C -- \u6211\u8BF4\u7684\u80AF\u5B9A\u4E0D\u662F\u90A3\u4E9B\u5148\u6709\u75DB\u82E6, \u7136\u540E\u518D\u4EA7\u751F\u7684\u5FEB\u4E50. \u800C\u4E0E\u8EAB\u4F53\u76F8\u5173\u7684\u5FEB\u4E50\u51E0\u4E4E\u90FD\u4F1A\u5148\u6709\u67D0\u79CD\u75DB\u82E6, \u7531\u4E8E\u8FD9\u4E2A\u539F\u56E0, \u628A\u8089\u4F53\u7684\u5FEB\u4E50\u79F0\u4E3A\u5974\u6027\u7684\u5FEB\u4E50\u662F\u6B63\u5F53\u7684.</p><p>\u82CF\u683C\u62C9\u5E95: \u597D\u5427, \u6211\u4EEC\u597D\u50CF\u8FD8\u6709\u65F6\u95F4. \u800C\u4E14\u6211\u8FD8\u6709\u4E00\u4E2A\u5FF5\u5934, \u6211\u4EEC\u5934\u9876\u7684\u90A3\u4E9B\u8749\u5728\u708E\u70ED\u7684\u6B63\u5348\u4EA4\u8C08\u4E4B\u540E\u4ECD\u7136\u5728\u5531\u6B4C. \u90A3\u4E48\u5B83\u4EEC\u4E5F\u4E0D\u4F1A\u4E0D\u671D\u6211\u4EEC\u770B. \u5982\u679C\u4ED6\u4EEC\u770B\u89C1\u6211\u4EEC\u4E24\u4E2A\u50CF\u666E\u901A\u4EBA\u4E00\u6837, \u5728\u6B63\u5348\u65F6\u5C31\u4E22\u4E0B\u8BDD\u4E0D\u8C08, \u800C\u662F\u61D2\u6D0B\u6D0B\u7684\u5782\u4E0B\u5934\u8BA9\u4ED6\u4EEC\u7684\u53EB\u58F0\u50AC\u7720, \u53EA\u7BA1\u6253\u778C\u7761, \u90A3\u4E48\u5B83\u4EEC\u6709\u7406\u7531\u5632\u7B11\u6211\u4EEC, \u628A\u6211\u4EEC\u5F53\u505A\u4E24\u4E2A\u5077\u61D2\u7684\u5974\u96B6. \u50CF\u7EF5\u7F8A\u4E00\u6837\u8EB2\u5230\u8FD9\u8FB9\u7761\u5348\u89C9. \u4F46\u82E5\u5B83\u4EEC\u770B\u5230\u6211\u4EEC\u5728\u4E13\u5FC3\u8C08\u8BDD, \u6211\u4EEC\u7684\u822A\u8239\u9A76\u8FC7\u585E\u58EC\u7684\u5C0F\u5C9B\u4E5F\u4E0D\u66FE\u88AB\u5979\u4EEC\u6E05\u6F88\u7684\u6B4C\u58F0\u6240\u8BF1\u60D1, \u90A3\u4E48\u5B83\u4EEC\u4E5F\u8BB8\u4F1A\u4F69\u670D\u6211\u4EEC, \u5E76\u628A\u4E0A\u82CD\u5141\u8BB8\u5B83\u4EEC\u8D60\u7ED9\u51E1\u4EBA\u7684\u6CD5\u5B9D\u9001\u7ED9\u6211\u4EEC.</p><p>\u6590\u5FB7\u82E5: \u4EC0\u4E48\u6CD5\u5B9D? \u6211\u4ECE\u6765\u6CA1\u6709\u542C\u8FC7.</p><p>\u82CF\u683C\u62C9\u5E95: \u90A3\u5012\u602A\u4E86, \u4E00\u4F4D\u7F2A\u65AF\u7684\u4FE1\u5F92\u8FDE\u8FD9\u6837\u7684\u4E8B\u60C5\u90FD\u6CA1\u542C\u8BF4\u8FC7! \u8FD9\u4E2A\u6545\u4E8B\u662F\u8FD9\u6837\u7684: \u4ECE\u524D\u6709\u4E00\u4E2A\u65F6\u4EE3\u8749\u90FD\u662F\u4EBA, \u8FD9\u4E2A\u65F6\u4EE3\u65E9\u4E8E\u7F2A\u65AF\u964D\u751F, \u540E\u6765\u7F2A\u65AF\u964D\u751F\u4E86, \u51FA\u73B0\u4E86\u97F3\u4E50, \u90A3\u4E2A\u65F6\u4EE3\u7684\u67D0\u4E9B\u4EBA\u5C31\u6B22\u559C\u7684\u8981\u547D, \u53EA\u7BA1\u5531\u6B4C, \u5FD8\u8BB0\u4E86\u996E\u98DF, \u4E00\u76F4\u5230\u6B7B\u4E3A\u6B62. \u7ECF\u8FC7\u65E2\u5B9A\u7684\u8FC7\u7A0B, \u8FD9\u4E9B\u4EBA\u53D8\u6210\u4E86\u8749. \u5B83\u4EEC\u4ECE\u7F2A\u65AF\u90A3\u91CC\u5F97\u5230\u4E00\u4E2A\u6CD5\u5B9D, \u4E00\u51FA\u751F\u5C31\u65E0\u9700\u8425\u517B, \u5E72\u7740\u5589\u5499\u7A7A\u7740\u809A\u76AE\u9A6C\u4E0A\u5C31\u80FD\u5531\u6B4C\u4E00\u76F4\u5230\u6B7B, \u6839\u672C\u4E0D\u9700\u8981\u5403\u4EFB\u4F55\u4E1C\u897F. \u5B83\u4EEC\u6B7B\u540E\u5C31\u53BB\u89C1\u7F2A\u65AF, \u5411\u5979\u4EEC\u62A5\u544A\u5728\u4EBA\u4E16\u95F4\u5F97\u5230\u54EA\u4E9B\u5C0A\u8363, \u54EA\u4E9B\u4EBA\u5D07\u62DC\u5979\u4EEC\u4E2D\u7684\u54EA\u4E00\u4F4D. \u5B83\u4EEC\u5411\u5FD2\u8033\u897F\u79D1\u745E\u62A5\u544A\u7528\u821E\u8E48\u5D07\u62DC\u5979\u7684\u4EBA, \u4F7F\u4ED6\u4EEC\u66F4\u52A0\u5F97\u5230\u5979\u7684\u5BA0\u7231; \u5B83\u4EEC\u5411\u57C3\u62C9\u6258\u62A5\u544A\u90A3\u4E9B\u53C2\u52A0\u5D07\u62DC\u7231\u795E\u7684\u796D\u4EEA\u7684\u4EBA, \u4F7F\u4ED6\u4EEC\u5F97\u5230\u8FD9\u4F4D\u7F2A\u65AF\u7684\u9752\u7750; \u5176\u4F59\u7C7B\u63A8, \u5411\u6BCF\u4E00\u4E2A\u7F2A\u65AF\u62A5\u544A\u5979\u4E3B\u7BA1\u7684\u90A3\u4E00\u884C\u4E2D\u5D07\u62DC\u5979\u7684\u60C5\u5F62. \u5B83\u4EEC\u5411\u4E5D\u4F4D\u7F2A\u65AF\u4E2D\u6700\u5E74\u957F\u7684\u5361\u5229\u4FC4\u73C0\u548C\u5E74\u7EAA\u6700\u5C0F\u7684\u4E4C\u62C9\u5C3C\u4E9A\u62A5\u544A\u90A3\u4E9B\u7EC8\u8EAB\u4ECE\u4E8B\u54F2\u5B66\u5E76\u7528\u54F2\u5B66\u8FD9\u79CD\u97F3\u4E50\u5D07\u62DC\u5979\u4EEC\u7684\u4EBA, \u56E0\u4E3A\u8FD9\u4E24\u4F4D\u7F2A\u65AF\u4E3B\u7BA1\u7684\u662F\u5929\u6587\u4EE5\u53CA\u795E\u548C\u4EBA\u6240\u6709\u7684\u5386\u53F2, \u6240\u4EE5\u8FD9\u79CD\u6B4C\u58F0\u662F\u6700\u9AD8\u5C1A\u7684. \u56E0\u6B64, \u6211\u4EEC\u6709\u7406\u7531\u4E0D\u7761\u5348\u89C9, \u8BA9\u6211\u4EEC\u7EE7\u7EED\u8C08\u4E0B\u53BB\u5427.</p></blockquote><h2 id="\u5FEB\u4E50\u4E0E\u81EA\u7531" tabindex="-1"><a class="header-anchor" href="#\u5FEB\u4E50\u4E0E\u81EA\u7531" aria-hidden="true">#</a> \u5FEB\u4E50\u4E0E\u81EA\u7531</h2><p>\u4ECE\u300A\u9AD8\u5C14\u5409\u4E9A\u7BC7\u300B\u4E2D\u7684\u5361\u5229\u514B\u52D2\u65AF\u53E3\u4E2D, \u6211\u4EEC\u53EF\u4EE5\u77E5\u9053\u5F53\u65F6\u96C5\u5178\u9752\u5E74\u4E2D\u6D41\u884C\u4E00\u79CD &quot;\u5FEB\u4E50\u4E3B\u4E49&quot;: \u4EBA\u5982\u679C\u6CA1\u6709\u5FEB\u4E50, \u5C31\u4E0E\u5C38\u4F53\u65E0\u5F02. \u867D\u7136\u6590\u5FB7\u82E5\u8BF4 &quot;\u6CA1\u6709\u8FD9\u6837\u7684\u5FEB\u4E50\u751F\u6D3B\u5C31\u6CA1\u6709\u4EC0\u4E48\u4EF7\u503C&quot;, \u4F46\u6211\u4EEC\u5176\u5B9E\u5F88\u96BE\u8BF4\u4ED6\u662F\u5361\u5229\u514B\u52D2\u65AF\u4E00\u6837\u7684\u5FEB\u4E50\u4E3B\u4E49. \u4ED6\u81F3\u5C11\u533A\u5206\u4E86\u8089\u4F53\u7684\u5FEB\u4E50\u548C\u975E\u8089\u4F53\u7684\u5FEB\u4E50.</p><p>\u6590\u5FB7\u82E5\u8BA4\u4E3A\u5FEB\u4E50\u6709\u7740\u975E\u5E38\u91CD\u8981\u4EF7\u503C: &quot;\u6CA1\u6709\u8FD9\u6837\u7684\u5FEB\u4E50\u751F\u6D3B\u5C31\u6CA1\u6709\u4EC0\u4E48\u4EF7\u503C&quot;. \u4ECE\u6545\u4E8B\u7684\u5F00\u7BC7\u770B\u6765, \u4ED6\u8BA4\u4E3A\u597D\u7684\u6587\u7AE0\u6B63\u662F\u6765\u6E90\u4E0E\u5FEB\u4E50\u7684\u95F2\u6687, \u4ED6\u8D5E\u7F8E\u5415\u897F\u4E9A\u65AF\u7684\u90A3\u7BC7\u6587\u7AE0\u662F &quot;\u82B1\u8D39\u4E86\u51E0\u5468\u95F2\u6687\u7684\u65F6\u5019&quot; \u505A\u51FA\u6765\u7684. \u4F46\u4ED6\u4E5F\u5F3A\u8C03\u4ED6\u8BF4\u7684\u5FEB\u4E50\u4E0D\u662F\u4E00\u79CD\u8089\u4F53\u7684\u5FEB\u4E50, \u8089\u4F53\u7684\u5FEB\u4E50\u603B\u662F\u4F34\u968F\u7740\u75DB\u82E6, \u56E0\u6B64\u4ED6\u79F0\u8089\u4F53\u7684\u5FEB\u4E50\u79F0\u4E3A &quot;\u5974\u6027\u7684\u5FEB\u4E50&quot;.</p><p>\u6590\u5FB7\u82E5\u6240\u8BF4\u7684 &quot;\u8089\u4F53\u7684\u5FEB\u4E50&quot; \u662F\u300A\u9AD8\u5C14\u5409\u4E9A\u7BC7\u300B\u4E2D\u7684\u5361\u5229\u514B\u52D2\u65AF\u7684\u5FEB\u4E50. \u4F5C\u4E3A\u96C5\u5178\u8868\u5E74\u4EE3\u8868\u7684\u5361\u5229\u514B\u52D2\u63D0\u51FA\u4E00\u79CD &quot;\u6B63\u4E49\u89C2&quot;: \u6309\u7167\u81EA\u6CD5\u7684\u6CD5\u5219, \u5F3A\u8005\u5E94\u8BE5\u7EDF\u6CBB\u5F31\u8005; \u800C\u5F31\u8005\u5BB3\u6015\u5F3A\u8005\u7EDF\u6CBB\u81EA\u5DF1\u624D\u53D1\u660E\u4E86\u5E73\u7B49\u7684\u4E60\u4FD7, \u7528\u6765\u7EA6\u675F\u5F3A\u8005. \u56E0\u6B64\u6B63\u4E49\u5C31\u662F\u5F3A\u8005\u901A\u8FC7\u66B4\u529B\u83B7\u53D6\u5229\u76CA\u7EDF\u6CBB\u5F31\u8005. \u4E8E\u662F\u82CF\u683C\u62C9\u5E95\u5411\u5361\u5229\u514B\u52D2\u53D1\u95EE, \u5373\u662F\u5982\u6B64, \u8C01\u624D\u662F\u5F3A\u8005\u5462? \u82E5\u8BBA\u5F3A\u58EE, \u5974\u96B6\u53EF\u80FD\u5F3A\u8FC7\u81EA\u7531\u6C11; \u82E5\u8BBA\u667A\u6167, \u533B\u751F\u53EF\u7B97\u5F3A\u8005, \u4F46\u4ED6\u5374\u5E76\u4E0D\u5E0C\u671B\u83B7\u5F97\u8D85\u8FC7\u9650\u5EA6\u7684\u98DF\u7269; \u82E5\u8BBA\u6280\u827A\u7CBE\u719F, \u978B\u5320\u519C\u592B\u4E2D\u5177\u6709\u4F7C\u4F7C\u8005. \u5361\u5229\u514B\u52D2\u65E0\u6CD5\u627F\u8BA4\u8FD9\u4E9B\u4EBA\u8005\u662F &quot;\u5F3A\u8005&quot;</p>',9),r=[p];function _(c,q){return o(),e("div",null,r)}var d=t(u,[["render",_],["__file","\u5FEB\u4E50\u4E0E\u8749\u7684\u6545\u4E8B.html.vue"]]);export{d as default};
