import{_ as t,o as p,c as o,e as u}from"./app.efeddd24.js";const q={},e=u('<h1 id="_03-13-22-21-36" tabindex="-1"><a class="header-anchor" href="#_03-13-22-21-36" aria-hidden="true">#</a> 03-13-22:21:36</h1><p>\u521A\u624D\u5728\u8BD5\u56FE\u5199\u4ECE\u300A\u4ECE\u611F\u89C9\u5F00\u59CB\u300B\u7684\u8BFB\u4E66\u7B14\u8BB0.</p><p>\u7136\u540E\u53D1\u73B0. \u60F3\u8981\u5199\u8BFB\u4E66\u7B14\u8BB0, \u771F\u4E0D\u662F\u4E00\u4EF6\u5BB9\u6613\u7684\u4E8B\u60C5.</p><p>\u4E0D\u72EC\u662F\u8BFB\u7B14\u8BB0\u5982\u6B64, \u5C31\u662F\u6709\u89C9\u5F97\u6709\u4E00\u4E2A\u610F\u601D, \u60F3\u8981\u660E\u8FF0\u51FA\u6765, \u5BF9\u6211\u6765\u8BF4\u5C31\u5F88\u96BE.</p><p>\u5728\u6211\u60F3\u8981\u5C06\u6211\u5728\u5199\u8BFB\u4E66\u7B14\u8BB0\u65F6\u611F\u53D7\u5230\u7684\u56F0\u96BE\u548C\u7591\u60D1\u8868\u8FBE\u51FA\u6765, \u90FD\u6709\u63D0\u7B14\u5FD8\u8A00\u7684\u611F\u89C9.</p><p>\u6240\u4EE5, \u6211\u4EEC\u4E0D\u8981\u53BB\u8BB2\u4EC0\u4E48\u8BFB\u4E66\u7B14\u8BB0\u6280\u5DE7\u7684\u95EE\u9898. \u800C\u662F\u6709\u4E00\u4E2A\u66F4\u5BBD\u6CDB\u548C\u6709\u610F\u4E49\u7684\u95EE\u9898:</p><p>\u6211\u4EEC\u5982\u4F55\u53EF\u4EE5\u786E\u5B9A\u6211\u4EEC\u628A\u63E1\u4E86\u4E00\u4E2A\u610F\u4E49?</p><p>\u6BD4\u5982\u5F53\u6211\u4EEC\u8BFB\u4E66\u7684\u65F6\u5019, \u6211\u4EEC\u5982\u4F55\u786E\u5B9A\u6211\u4EEC\u662F\u5728\u8FDB\u884C\u4E00\u4E2A\u6709\u610F\u4E49\u7684\u6D3B\u52A8 \u800C\u4E0D\u662F\u65E0\u6548\u7684\u8111\u529B\u52B3\u52A8?</p><p>\u6BD4\u5982\u5F53\u6211\u4EEC\u611F\u89C9\u6211\u4EEC\u6709\u4E00\u4E2A\u610F\u601D\u7684\u65F6\u5019, \u6211\u4EEC\u5982\u4F55\u786E\u5B9A\u6211\u4EEC\u771F\u7684\u662F\u6709\u4E00\u4E2A\u610F\u601D, \u800C\u4E0D\u662F\u4EA7\u751F\u4E00\u79CD\u865A\u5984\u7684\u5E7B\u89C9?</p><p>\u6211\u539F\u4EE5\u4E3A\u9648\u5609\u6620\u6240\u8BF4\u7684 \u300A\u771F\u7406\u638C\u63E1\u6211\u4EEC\u300B \u53EF\u80FD\u4F1A\u5BF9\u8FD9\u4E2A\u95EE\u9898\u6709\u6240\u56DE\u7B54.</p><p>\u56E0\u4E3A\u4ECE\u8FD9\u4E2A\u95EE\u9898\u7684\u8868\u8FF0\u4E0A\u770B, \u8FD9\u4E2A\u95EE\u9898\u662F: \u6211\u4EEC\u5982\u4F55\u5F97\u77E5\u6211\u4EEC\u638C\u63E1\u4E86\u4E00\u4E2A\u610F\u4E49. \u4F46\u5982\u679C &quot;\u6211\u4EEC\u638C\u63E1\u610F\u4E49&quot; \u8FD9\u4E2A\u60F3\u6CD5\u672C\u6765\u5C31\u4E0D\u5BF9, \u800C\u662F &quot;\u610F\u4E49\u638C\u63E1\u6211\u4EEC&quot;, \u90A3\u4E48\u6211\u7684\u95EE\u9898, \u4E0D\u5C31\u6210\u4E3A\u4E86\u4E00\u4E2A\u4F2A\u95EE\u9898\u4E86? \u6216\u8005\u6211\u4EEC\u53EF\u4EE5\u627E\u5230\u53E6\u4E00\u4E2A\u89D2\u5EA6\u6765\u5C1D\u8BD5\u89E3\u51B3\u8FD9\u95EE\u9898.</p><p>\u4F46\u597D\u8C61\u6211\u7684\u8FD9\u4E2A\u60F3\u6CD5\u843D\u7A7A\u4E86. \u300A\u771F\u7406\u638C\u63E1\u6211\u4EEC\u300B\u4E0D\u4EC5\u4EC5\u662F &quot;\u6211\u4EEC\u638C\u63E1\u771F\u7406&quot; \u7684\u5012\u88C5, \u5E76\u4E0D\u662F\u6211\u60F3\u5F53\u7136\u7684\u90A3\u4E2A\u610F\u601D.</p><hr><p>\u9648\u5609\u6620\u7684\u300A\u771F\u7406\u638C\u63E1\u6211\u4EEC\u300B\u4ECE\u4E24\u4E2A\u4EBA\u8BA8\u8BBA\u8BB2\u8D77, \u4E24\u4E2A\u4EBA\u5F00\u59CB\u90FD\u89C9\u5F97 &quot;\u6211\u662F\u5BF9\u7684&quot;, \u800C\u7EC8\u4E8E\u4E00\u4EBA\u8BA4\u8BA4\u4E3A\u81EA\u5DF1\u5176\u5B9E\u9519\u4E86. \u6BD4\u8D77\u8BA4\u4E3A\u4E00\u4E2A\u4EBA\u7684\u9053\u7406\u6218\u80DC\u4E86\u53E6\u4E00\u4E2A\u4EBA, \u5BF9\u4E8E\u8FD9\u4E2A\u4E8B\u66F4\u597D\u7684\u7406\u89E3\u662F, &quot;\u771F\u7406\u5728\u4E24\u4EBA\u8BDA\u6073\u7684\u8BA8\u8BBA\u4E2D\u5C55\u73B0\u51FA\u6765&quot;.</p><p>\u8FD9\u5C31\u662F\u6240\u8C13\u7684\u771F\u7406\u638C\u63E1\u6211\u4EEC. \u5E76\u4E0D\u662F\u638C\u63E1\u771F\u7406\u7684\u4EBA\u6218\u80DC\u4E86\u6CA1\u6709\u638C\u63E1\u771F\u7406\u7684\u4EBA, \u800C\u662F\u771F\u7406\u662F\u5728\u8BDA\u6073\u8BA8\u8BBA, \u5728\u514B\u670D\u6210\u89C1\u4E2D\u663E\u73B0\u51FA\u6765. \u6240\u4EE5\u5E76\u4E0D\u662F\u8C01\u8D62\u4E86\u8C01, \u800C\u662F\u771F\u7406\u8D62\u4E86\u6211\u4EEC.</p><p>\u8FD9\u91CC\u5F15\u4E86\u6D77\u5FB7\u683C\u5C14\u7684\u8BDD: \u4EBA\u4E4B\u4E3A\u4EBA, \u5FC5\u9700\u5C06\u771F\u7406\u8BBE\u4E3A\u524D\u63D0, \u8FD9\u5E76\u4E0D\u662F\u6211\u4E00\u5F00\u59CB\u5C31\u8BA4\u8BC6\u4E86\u771F\u7406, \u800C\u662F\u8BF4, \u5C3D\u7BA1\u6211\u8BA4\u4E3A\u6211\u662F\u5BF9\u7684, \u4F46\u6211\u627F\u8BA4\u6211\u53EF\u80FD\u662F\u9519\u7684\u6216\u9700\u8981\u4FEE\u6B63\u7684.</p><p>\u4E5F\u5C31\u662F\u5F53\u6211\u4EEC\u8BF4 &quot;\u6211\u662F\u5BF9\u7684&quot; \u7684\u65F6\u5019, \u6838\u5FC3\u662F &quot;\u5BF9&quot;, &quot;\u6211&quot; \u662F\u4ECE\u5C5E.</p><p>\u8FDB\u800C\u5F15\u7528 &quot;\u5927\u6211&quot; &quot;\u5C0F\u6211&quot; \u7684\u6BD4\u55BB. \u4EE5 &quot;\u5927\u6211&quot; \u6BD4\u55BB\u771F\u7406. \u800C\u5F53\u8BF4 &quot;\u5927\u6211&quot; \u7684\u65F6\u5019, \u7528 &quot;\u6211&quot; \u5B57\u8868\u8FBE\u4E86\u771F\u7406\u6839\u690D\u4E8E\u5173\u5207. \u771F\u7406\u4E0E\u6210\u89C1\u540C\u6839, \u8FD9\u4E2A\u6839\u5C31\u662F\u5173\u5207.</p><p>\u56E0\u4E3A\u5173\u5207, \u624D\u6709\u6210\u89C1, \u800C\u771F\u7406\u662F\u5BF9\u4E8E\u6210\u89C1\u7684\u514B\u670D.</p><p>\u90A3\u95EE\u9898\u5728\u4E8E, \u6211\u4EEC\u5E94\u8BE5\u5982\u4F55\u5224\u65AD\u4EC0\u4E48\u662F\u771F\u7406, \u4EC0\u4E48\u662F\u6210\u89C1? \u7136\u800C\u5E76\u4E0D\u80FD\u8FD9\u6837\u95EE, \u56E0\u4E3A, \u771F\u7406\u4E0D\u662F\u4E2A\u73B0\u6210\u7684\u5BA2\u89C2\u73A9\u610F, \u53EF\u4EE5\u4ECE\u5176\u5B83\u4E1C\u897F\u4E2D\u533A\u5206\u51FA\u6765. \u771F\u7406\u4E0D\u662F\u6C38\u6853\u4E0D\u53D8\u7684\u4E1C\u897F, \u800C\u662F\u6211\u4EEC\u8089\u773C\u770B\u5230\u6700\u597D\u7684.</p><p>\u4ECA\u5929\u4E4B\u771F\u7406, \u4FBF\u662F\u5728\u4E09\u767E\u5E74\u540E\u4E0D\u6210\u4E3A\u7406\u771F\u7406, \u5728\u4ECA\u5929\u4ECD\u662F\u771F\u7406.</p><p>\u6700\u540E\u8BB2\u4E86, \u8BB2\u9053\u7406\u7684\u8BDD\u8BED\u6743\u7684\u5173\u7CFB. \u6211\u4EEC\u8BF4, &quot;\u5BF9&quot; \u8981\u4F18\u5148\u4E8E &quot;\u6211&quot;, \u5E76\u4E0D\u662F\u8BF4, \u51E1\u4E8B\u8981\u7528\u8BB2\u9053\u7406\u6765\u5B9A\u593A. \u56E0\u4E3A &quot;\u8BB2\u9053\u7406&quot; \u672C\u6765\u5C31\u662F\u5E26\u6709\u6743\u529B\u5173\u7CFB\u7684.</p><hr><p>\u603B\u4E4B, \u5982\u4E0A, \u771F\u7406\u638C\u63E1\u6211\u4EEC, \u5E76\u4E0D\u89E3\u7B54\u6211\u4EEC\u5982\u4F55\u628A\u63E1\u4E00\u4E2A\u610F\u4E49\u7684\u95EE\u9898.</p><p>\u5B83\u66F4\u591A\u662F\u4E00\u79CD\u771F\u7406\u89C2\u548C\u6C42\u77E5\u6001\u5EA6.</p><hr><p>\u6216\u8BB8\u6211\u4EEC\u5E94\u8BE5\u8FDB\u4E00\u6B65\u8FFD\u95EE, \u6211\u4EEC\u4E3A\u4EC0\u4E48\u8981\u628A\u63E1\u4E00\u4E2A\u610F\u4E49? \u6216\u8005\u8BF4, \u610F\u4E49\u5BF9\u4E8E\u6211\u4EEC\u610F\u5473\u7740\u4EC0\u4E48.</p><p>\u96BE\u9053\u610F\u4E49\u5BF9\u4E8E\u6211\u4EEC\u4E0D\u662F\u610F\u5473\u7740\u4E00\u79CD\u770B\u5F85\u4E16\u754C\u7684\u89D2\u5EA6\u4E48.</p><p>\u7406\u8BBA\u662F\u4E0D\u6307\u5BFC\u5B9E\u8DF5\u7684. \u7406\u8BBA\u53EA\u662F\u5BF9\u4E8E\u6211\u4EEC\u5DF2\u77E5\u7684\u4E8B\u7269\u7684\u660E\u8FF0. \u8FD9\u79CD\u660E\u8FF0\u7528\u7A77\u7406\u7684\u65B9\u5F0F, \u5BF9\u7279\u5F02\u8FDB\u884C\u514B\u670D. \u800C\u8FD9\u79CD\u514B\u670D\u4F7F\u6211\u4EEC\u4EA7\u751F\u4E00\u79CD\u770B\u5F85\u4E16\u754C\u7684\u65B0\u7684\u65B9\u5F0F.</p><p>\u7406\u8BBA\u662F\u4E00\u79CD &quot;\u770B\u6CD5&quot;. \u5728\u8FD9\u79CD\u770B\u6CD5\u4E0B, \u4E16\u754C\u5728\u6211\u4EEC\u773C\u4E2D\u5448\u73B0\u4E0D\u540C\u7684\u6837\u5F0F.</p><p>\u6240\u4EE5\u6216\u8BB8, \u786E\u8BA4\u6211\u4EEC\u662F\u5426\u628A\u63E1\u610F\u4E49, \u4E0D\u662F\u5982\u4F55\u91CD\u8FF0\u5728\u8BFB\u4E66, \u601D\u8003\u65F6\u7684\u5239\u90A3\u6D1E\u89C1, \u800C\u662F\u7528\u8FD9\u79CD\u610F\u4E49\u53BB &quot;\u770B&quot;. \u5F53\u6211\u4EEC\u771F\u7684 &quot;\u770B\u51FA&quot; \u610F\u4E49\u4E0B\u7684\u4E16\u754C\u7684\u6837\u5F0F, \u610F\u4E49\u624D\u5728\u6211\u4EEC\u6240\u89C1\u4E2D\u663E\u73B0\u51FA\u6765.</p><p>\u6216\u8BB8\u5728\u8FD9\u79CD\u610F\u4E49\u4E0B, \u6211\u4E5F\u53EF\u4EE5\u8BF4, &quot;\u4E0D\u662F\u6211\u4EEC\u638C\u63E1\u610F\u4E49, \u800C\u662F\u610F\u4E49\u638C\u63E1\u6211\u4EEC&quot;. \u610F\u4E49\u8BA9\u6211\u4EEC &quot;\u770B\u89C1&quot;.</p>',32),_=[e];function r(a,c){return p(),o("div",null,_)}var s=t(q,[["render",r],["__file","\u6211\u4EEC\u5982\u4F55\u628A\u63E1\u610F\u4E49.html.vue"]]);export{s as default};
