import{_ as e,o as p,c as t,e as o}from"./app.6bf529a6.js";const a={},r=o('<h1 id="\u6211\u60F3\u548C\u4F60\u804A\u804A\u7F2A\u53EF\u99A8\u7684\u4E8B\u60C5" tabindex="-1"><a class="header-anchor" href="#\u6211\u60F3\u548C\u4F60\u804A\u804A\u7F2A\u53EF\u99A8\u7684\u4E8B\u60C5" aria-hidden="true">#</a> \u6211\u60F3\u548C\u4F60\u804A\u804A\u7F2A\u53EF\u99A8\u7684\u4E8B\u60C5</h1><p>\u770B\u5230\u7F2A\u53EF\u99A8\u5C0F\u670B\u53CB\u7684\u4E8B\u60C5, \u5927\u6982\u5DF2\u6709\u4E24\u5468\u65F6\u95F4. \u867D\u7136\u6211\u975E\u5E38\u5173\u5FC3\u8FD9\u4EF6\u4E8B, \u4F46\u5230\u73B0\u5728\u4E3A\u6B62, \u6211\u4F9D\u7136\u6CA1\u6709\u770B\u5B8C\u6574\u4E2A\u4E8B\u4EF6\u7684\u62A5\u5BFC, \u51E0\u4E4E\u6BCF\u6B21\u770B\u5230\u4E00\u534A\u5C31\u5FCD\u4E0D\u4F4F\u628A\u65B0\u95FB\u5173\u6389.</p><p>\u6BCF\u5927\u6982\u56E0\u4E3A\u6211\u662F\u4E24\u5B69\u7684\u7236\u4EB2\u5427, \u6BCF\u6B21\u60F3\u8D77\u8FD9\u4E8B, \u5404\u79CD\u5FF5\u5934\u4FBF\u7EB7\u81F3\u6C93\u6765:</p><p><em>\u4E3A\u4EC0\u4E48\u7F2A\u53EF\u99A8\u5C0F\u670B\u53CB\u5728\u5B66\u6821\u53D7\u5230\u59D4\u66F2\u540E\u4E0D\u53BB\u7ED9\u81EA\u5DF1\u7684\u7236\u6BCD\u8BF4, \u800C\u8981\u91C7\u7528\u8FD9\u6837\u6781\u7AEF\u7684\u65B9\u5F0F\u5F03\u7EDD\u8FD9\u4E2A\u4E16\u754C?</em></p><p><em>\u4E3A\u4EC0\u4E48\u5B66\u6821\u548C\u8001\u5E08\u5728\u8981\u4E8B\u53D1\u4E4B\u540E\u8868\u73B0\u51FA\u8FD9\u6837\u7684\u51B7\u6F20?</em></p><p><em>\u4E3A\u4EC0\u4E48\u7F2A\u53EF\u99A8\u5C0F\u670B\u53CB\u7684\u73ED\u7EA7\u7FA4\u91CC\u5BB6\u957F\u4EEC\u5C3D\u7136\u4F1A\u6BEB\u65E0\u540C\u7406\u5FC3\u7684\u53D1\u8D77\u70B9\u8D5E?</em></p><p>\u5C24\u5176\u662F, \u5173\u4E8E <strong>\u6211\u4EEC\u8FD8\u80FD\u505A\u4EC0\u4E48\u7684\u95EE\u9898</strong> :</p><p><em><strong>\u4F5C\u4E3A\u5BB6\u957F, \u6211\u4EEC\u5982\u4F55\u907F\u514D\u8FD9\u6837\u7684\u4E8B\u60C5\u53D1\u751F\u5728\u6211\u4EEC\u8EAB\u4E0A?</strong></em></p><p><em><strong>\u6211\u4EEC\u771F\u7684\u5E94\u8BE5\u628A\u6240\u6709\u8D23\u4EFB\u90FD\u7B97\u5230\u5B66\u6821\u548C\u8001\u5E08\u7684\u5934\u4E0A\u4E48?</strong></em></p><p><em><strong>\u6211\u4EEC\u8981\u5982\u4F55\u6559\u5BFC\u5B69\u5B50, \u6765\u9762\u5BF9\u8FD9\u4E2A\u4E16\u754C?</strong></em></p><p>\u6211\u611F\u5230\u81EA\u5DF1\u65E0\u6CD5\u7406\u6E05\u8FD9\u4EF6\u4E8B, \u4E8E\u662F\u9080\u8BF7\u5173\u6CE8\u6B64\u4E8B\u7684\u670B\u53CB\u6765\u4E00\u8D77\u8BA8\u8BBA\u8FD9\u4EF6\u4E8B. \u5E76\u628A\u6211\u4EEC\u8BA8\u8BBA\u7684\u8FC7\u7A0B\u5F55\u6210\u4E86\u8282\u76EE.</p><ul><li>\u7B2C\u4E00\u671F: \u5F20\u542F\u9707 &amp; Stella | \u4E0E\u54C0\u54ED\u7684\u4EBA\u8981\u540C\u54ED</li><li>\u7B2C\u4E8C\u671F: \u5F20\u542F\u9707 &amp; \u8F66 | \u5979\u6709\u4E00\u79CD\u5B64\u72EC\u7684\u771F\u8BDA</li><li>\u7B2C\u4E09\u671F: \u5F20\u542F\u9707 &amp; Simon | \u8981\u771F\u5B9E\u7684\u611F\u53D7\u81EA\u5DF1\u5B58\u5728</li></ul><p>\u5728\u8BA8\u8BBA\u4E2D, \u6211\u4EEC\u5E76\u4E0D\u60F3\u628A\u53BB\u6DF1\u7A76\u5230\u5E95\u8C01\u8BE5\u4E3A\u6B64\u4E8B\u8D1F\u8D23, \u4E5F\u5E76\u4E0D\u60F3\u628A\u76EE\u5149\u653E\u5230 &quot;\u6B63\u80FD\u91CF&quot; \u7EC8\u4E8E\u6740\u4EBA\u4E86\u4E4B\u4E0A, \u800C\u662F\u66F4\u591A\u60F3\u8BA8\u8BBA, \u8FD9\u4EF6\u4E8B\u80CC\u540E\u7684\u7ED3\u6784\u6027\u95EE\u9898, \u4EE5\u53CA\u5728\u8FD9\u6837\u7684\u7ED3\u6784\u6027\u95EE\u9898\u4E0B, \u6211\u4EEC\u8FD8\u53EF\u4EE5\u505A\u4E9B\u4EC0\u4E48.</p><p>\u5982\u679C\u4F60\u6709\u8DB3\u591F\u7684\u79D1\u5B66\u77E5\u8BC6\u7528\u4E8E\u4E0A\u7F51, \u4F60\u53EF\u4EE5\u901A\u8FC7\u5728 spotify | \u5C0F\u5B87\u5B99 | Pocket Casts | \u7B49\u6CDB\u7528\u6027\u64AD\u5BA2\u5E73\u53F0\u4E0A\u641C\u7D22 &quot;\u767D\u663C\u7535\u53F0&quot; \u6765\u6536\u542C\u4E0A\u8FF0\u8282\u76EE.</p><p>\u6216\u8005\u4F60\u53EF\u4EE5\u5728\u5FAE\u4FE1\u5C0F\u7A0B\u5E8F \u6F2B\u5348\u76EE\u7684 \u4E0A\u627E\u5230\u767D\u663C\u7535\u53F0\u4E13\u680F, \u6765\u6536\u542C\u4E0A\u8FF0\u8282\u76EE.</p><p>\u540C\u65F6\u5982\u679C\u4F60\u5BF9\u6B64\u4E8B\u6709\u7279\u6B8A\u7684\u5173\u5207, \u6211\u4EEC\u8FD8\u8054\u5408\u7FFB\u8F6C\u7535\u53F0\u548C\u6F2B\u5348\u76EE\u7684\u9080\u8BF7\u4F60\u4E00\u8D77\u6765\u8FDB\u884C\u8BA8\u8BBA.</p><p>\u5F81\u6587\u6295\u7A3F: flipradio.research@gmail.com \u58F0\u97F3\u5F81\u96C6\u6295\u7A3F: island1013@126.com</p><p>\u8BA9\u6211\u4EEC\u76F8\u4FE1\u7406\u6027\u8BA8\u8BBA\u7684\u529B\u91CF, \u4E0D\u8981\u9057\u5FD8\u90A3\u4E9B\u6DF1\u6DF1\u523A\u75DB\u6211\u4EEC\u7684\u4E8B\u60C5.</p>',18),s=[r];function _(m,n){return p(),t("div",null,s)}var l=e(a,[["render",_],["__file","\u6211\u60F3\u548C\u4F60\u804A\u804A\u7F2A\u53EF\u99A8\u7684\u4E8B\u60C5.html.vue"]]);export{l as default};
