import{_ as t,r as p,o as a,c as r,a as n,b as h,w as i,e as c,d as e}from"./app.6bf529a6.js";const d={},u=c('<h1 id="\u6709\u610F\u8BC6\u6F14\u5316" tabindex="-1"><a class="header-anchor" href="#\u6709\u610F\u8BC6\u6F14\u5316" aria-hidden="true">#</a> \u6709\u610F\u8BC6\u6F14\u5316</h1><blockquote><p>\u54F2\u4EBA\u5E94\u5F53\u65BD\u4E3A, \u4E0D\u5E94\u5F53\u88AB\u65BD\u4E3A, \u4ED6\u4E0D\u5E94\u542C\u4ECE\u4ED6\u4EBA, \u667A\u6167\u5C11\u7684\u4EBA\u5E94\u8BE5\u542C\u4ECE\u4ED6\u7684.</p></blockquote><blockquote><p>---- \u300A\u5F62\u800C\u4E0A\u5B66 \xB7 \u5377\u4E00 \xB7 \u7B2C\u4E8C\u7AE0\u300B</p></blockquote><h2 id="\u6E90\u8D77" tabindex="-1"><a class="header-anchor" href="#\u6E90\u8D77" aria-hidden="true">#</a> \u6E90\u8D77</h2><p>\u6700\u521D\u542C\u5230 &quot;\u6709\u610F\u8BC6\u6F14\u5316&quot; \u662F\u5728\u7FFB\u7535\u590F\u4EE4\u8425\u7684\u8282\u76EE\u91CC. \u4F46\u7FFB\u7535\u91CC\u63D0\u5230\u6709\u610F\u53EA\u6F14\u5316\u5E94\u8BE5\u66F4\u65E9 ( \u4F46\u6211\u4E00\u65F6\u60F3\u4E0D\u60F3\u6765\u662F\u90A3\u4E00\u671F\u4E86 ).</p><p>\u8BA9\u6211\u5BF9\u8FD9\u4E2A\u6982\u5FF5\u5F15\u8D77\u6CE8\u610F, \u662F\u5728\u4E0A\u6D77\u7FFB\u7535\u670B\u53CB\u7684\u805A\u4F1A\u4E0A. \u5728\u805A\u4F1A\u4E0A, \u5C0F\u674E\u8001\u5E08\u591A\u6B21\u63D0\u5230\u4ED6\u89C9\u5F97\u6709\u610F\u8BC6\u6F14\u5316\u5F88\u91CD\u8981.</p><p>\u771F\u6B63\u8BA9\u6211\u89C9\u5F97\u5BF9\u8FD9\u4E2A\u6982\u5FF5\u6709\u70B9\u628A\u63E1\u7684, \u662F\u5728\u8BFB\u300Aunix \u7F16\u7A0B\u827A\u672F\u300B\u65F6. Unix \u54F2\u5B66\u662F\u5B8C\u5168\u4F9D\u5EFA\u57FA\u5728\u793E\u533A\u6F14\u5316\u4E4B\u4E0A\u7684. \u5728\u8FD9\u4E2A\u65F6\u5019, \u6211\u624D\u5BF9\u6F14\u5316\u7406\u6027\u6709\u70B9\u611F\u89C9.</p><p>\u800C\u8BA9\u6211\u89C9\u5F97\u5BF9\u4E8E\u6709\u610F\u8BC6\u6F14\u5316\u53EF\u4EE5\u8BF4\u70B9\u4EC0\u4E48\u7684, \u662F\u5BF9\u4E8E\u667A\u80FD\u624B\u673A\u7684\u601D\u8003.</p><h2 id="\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u6982\u5FF5" aria-hidden="true">#</a> \u6982\u5FF5</h2><p>\u54C8\u8036\u514B\u5BF9\u4E8E\u7406\u6027\u6709\u8FD9\u6837\u7684\u5206\u7C7B: \u5EFA\u6784\u7406\u6027\u548C\u6F14\u5316\u7406\u6027.</p><p>\u5EFA\u6784\u7406\u6027\u662F\u4E00\u79CD\u4E8B\u5148\u7CBE\u5BC6\u8BA1\u7B97, \u8BD5\u56FE\u5BF9\u79E9\u5E8F\u8FDB\u884C &quot;\u8BBE\u8BA1&quot; \u7684\u7406\u6027. \u56E0\u4E3A\u4E2A\u4EBA\u77E5\u8BC6\u7684\u4E0D\u5B8C\u6574\u6027, \u5C40\u90E8\u6027, \u5206\u6563\u6027\u548C\u5F02\u8D28\u6027, \u4F7F\u4EBA\u7C7B\u65E0\u6CD5\u5904\u7406\u6574\u4F53\u6027\u77E5\u8BC6, \u56E0\u800C\u8FD9\u79CD\u5EFA\u6784\u7406\u6027\u65E0\u6CD5 &quot;\u8BBE\u8BA1&quot; \u793E\u4F1A\u6574\u4F53\u79E9\u5E8F.</p><p>\u6F14\u5316\u7406\u6027\u8BA4\u4E3A\u4E2A\u4EBA\u7406\u6027\u662F\u6709\u9650\u7684, \u793E\u4F1A\u79E9\u5E8F\u4E0D\u662F\u88AB\u7406\u6027\u6240\u5EFA\u6784\u51FA\u6765, \u800C\u662F\u7531\u4EBA\u7C7B\u884C\u4E3A\u957F\u671F\u6F14\u5316\u6765\u7684. \u6F14\u5316\u7406\u6027\u4E0D\u540C\u4E8E\u5EFA\u6784\u7406\u6027, \u662F\u4E8B\u5148\u7684\u548C\u8BBE\u8BA1\u7684, \u800C\u662F\u4E8B\u540E\u7684\u548C\u89E3\u91CA\u7684.</p><p>\u800C\u6F14\u5316\u6709\u4E24\u79CD\u65B9\u5F0F: \u4E00\u79CD\u662F\u6709\u65E0\u610F\u8BC6\u6F14\u5316; \u4E00\u79CD\u662F\u6709\u610F\u8BC6\u6F14\u5316.</p><p>\u65E0\u610F\u8BC6\u6F14\u5316, \u6307\u7684\u662F\u5728\u6F14\u5316\u8FC7\u7A0B\u4E2D, \u5B8C\u5168\u6309\u7167\u6F14\u5316\u673A\u5236\u8FDB\u884C\u53D1\u5C55, \u4E0D\u52A0\u8BF8\u4EBA\u7684\u610F\u5FD7. \u6240\u4EE5\u8FD9\u79CD\u6F14\u5316\u5C31\u662F\u6CA1\u6709\u65B9\u5411\u7684, \u6F14\u5316\u51FA\u5565\u5C31\u662F\u5565.</p><p>\u6709\u610F\u8BC6\u6F14\u5316, \u6307\u7684\u662F\u5728\u6F14\u5316\u8FC7\u7A0B\u4E2D, \u6F14\u5316\u7684\u4E3B\u4F53, \u5BF9\u4E8E\u6F14\u5316\u7684\u65B9\u5411\u6709\u4E00\u4E2A\u76EE\u7684, \u4ED6\u901A\u8FC7\u5BF9\u4E8E\u6F14\u5316\u8FC7\u7A0B\u7684\u5E72\u5B9E\u73B0\u4E86\u5BF9\u4E8E\u6F14\u5316\u65B9\u5411\u7684\u5E72\u6D89.</p><h2 id="\u76F8\u5BF9\u8BBA\u4E0E-iphone" tabindex="-1"><a class="header-anchor" href="#\u76F8\u5BF9\u8BBA\u4E0E-iphone" aria-hidden="true">#</a> \u76F8\u5BF9\u8BBA\u4E0E iphone</h2><p>\u636E\u8BF4\u7231\u56E0\u65AF\u5766\u8BF4\u8FC7\u8FD9\u6837\u4E00\u7FFB\u8BDD: \u5982\u679C\u4ED6\u4E0D\u63D0\u51FA\u72ED\u4E49\u76F8\u5BF9\u8BBA, \u90A3\u4E48\u8FC7\u51E0\u5E74\u4E5F\u4F1A\u6709\u4EBA\u63D0\u51FA\u72ED\u4E49\u76F8\u5BF9\u8BBA\u6765, \u4F46\u662F\u5E7F\u4E49\u76F8\u5BF9\u8BBA, \u5982\u679C\u4E0D\u662F\u4ED6, \u53EF\u80FD\u6C38\u8FDC\u4E0D\u4F1A\u6709\u4EBA\u53EF\u4EE5\u63D0\u51FA\u6765.</p><p>\u5982\u679C\u4ED6\u771F\u8BF4\u8FC7\u8FD9\u53E5\u8BDD, \u90A3\u4E48\u5728\u6F14\u5316\u7406\u6027\u7684\u903B\u8F91\u4E0B, \u4ED6\u662F\u5728\u8BF4, \u72ED\u4E49\u76F8\u5BF9\u8BBA\u662F\u53EF\u4EE5\u901A\u8FC7\u65E0\u610F\u8BC6\u6F14\u5316\u53D1\u5C55\u51FA\u6765\u7684, \u56E0\u4E3A\u72ED\u4E49\u76F8\u5BF9\u8BBA\u7684\u903B\u8F91\u5728\u5F53\u65F6\u7269\u7406\u5B66\u6F14\u5316\u53D1\u7684\u8DEF\u5F84\u4E0A, \u8FDF\u65E9\u4F1A\u88AB\u63D0\u51FA\u6765\u7684. \u4F46\u662F\u5E7F\u4E49\u76F8\u5BF9\u8BBA\u7684\u903B\u8F91\u662F\u4E0D\u5728\u5F53\u662F\u7269\u7406\u5B66\u6F14\u5316\u53D1\u5C55\u7684\u8DEF\u5F84\u4E0A\u7684. \u56E0\u6B64\u5E7F\u4E49\u76F8\u5BF9\u8BBA\u53EF\u4EE5\u8BF4\u662F\u88AB\u4ED6\u521B\u9020\u51FA\u6765\u7684.</p><p>\u540C\u6837\u7684\u9053\u7406\u53EF\u4EE5\u8BF4\u660E ipone \u4E3A\u4EC0\u4E48\u662F\u4E00\u4E2A\u4F1F\u5927\u7684\u4EA7\u54C1 ( \u867D\u7136\u4F5C\u4E3A\u4EA7\u54C1\u5DE5\u4F5C\u8005, \u975E\u5E38\u53CD\u611F\u8BF4\u8FD9\u79CD\u5439\u66B4\u4E54\u5E03\u65AF\u7684\u8BDD ).</p><p>\u6211\u4EEC\u770B\u770B iphone \u4E4B\u524D\u7684\u624B\u673A, \u6211\u4EEC\u518D\u770B\u770B iphone, \u5B8C\u5168\u662F\u4E24\u79CD\u4E1C\u897F.</p><p>\u5982\u679C\u6309\u7167\u4E4B\u524D\u624B\u673A\u7684\u4EA7\u54C1\u601D\u8DEF, \u518D\u53D1\u5C55\u4E00\u4E07\u5E74\u4E5F\u4E0D\u4F1A\u53D1\u5C55\u51FA iphone. \u56E0\u4E3A iphone \u7684\u4EA7\u54C1\u903B\u8F91\u5B8C\u5168\u4E0D\u5728\u4E4B\u524D\u624B\u673A\u4E2D.</p><p>\u5C31\u5982\u540C, \u7167\u7740\u725B\u987F\u529B\u5B66\u7684\u903B\u8F91, \u53D1\u5C55\u4E00\u4E07\u5E74\u4E5F\u53D1\u5C55\u4E0D\u51FA\u76F8\u5BF9\u8BBA\u4E00\u6837. \u76F8\u5BF9\u8BBA\u4E0E\u725B\u987F\u529B\u5B66\u5728\u5E95\u5C42\u7684\u903B\u8F91\u4E0A\u5C31\u662F\u4E0D\u4E00\u6837\u7684.</p><p>\u5728\u8FD9\u4E2A\u610F\u4E49\u4E0A, \u4E54\u5E03\u65AF\u662F\u771F\u6B63\u7684\u521B\u4F5C\u8005, \u800C\u4E4B\u540E\u7684\u6240\u6709\u667A\u80FD\u624B\u673A\u7684\u8BBE\u8BA1\u90FD\u53EA\u80FD\u662F\u8FD9\u79CD\u8DEF\u5F84\u4E0A\u7684\u5236\u4F5C\u8005.</p><h2 id="\u53EF\u4EE5\u7F72\u540D\u7684\u521B\u4F5C\u8005" tabindex="-1"><a class="header-anchor" href="#\u53EF\u4EE5\u7F72\u540D\u7684\u521B\u4F5C\u8005" aria-hidden="true">#</a> \u53EF\u4EE5\u7F72\u540D\u7684\u521B\u4F5C\u8005</h2><p>\u5728\u673A\u6838\u5934\u53F7\u73A9\u5BB6\u7684\u8282\u76EE\u4E2D, \u9F99\u9A6C\u5BF9\u4E16\u754C\u4E0A\u7B2C\u4E00\u4E2A\u5F69\u86CB\u8FDB\u884C\u89E3\u8BFB.</p><blockquote><p>\u300A\u5192\u9669\u300B\uFF08\u82F1\u8BED\uFF1AAdventure\uFF09\u662F\u4E00\u4E2A\u57281979\u5E74\u53D1\u884C, \u5728\u96C5\u8FBE\u52292600\u4E0A\u8FD0\u884C\u7684\u7535\u5B50\u6E38\u620F, \u88AB\u8BA4\u4E3A\u662F\u7535\u5B50\u6E38\u620F\u5386\u53F2\u4E0A\u7B2C\u4E00\u4E2A\u52A8\u4F5C\u5192\u9669\u6E38\u620F. \u5B83\u7684\u7F16\u5199\u5458Warren Robinett\u5728\u6E38\u620F\u4E2D\u5F15\u5165\u4E86\u7535\u5B50\u6E38\u620F\u4E16\u754C\u4E2D\u7B2C\u4E00\u4E2A\u5E7F\u4E3A\u4EBA\u77E5\u7684\u5F69\u86CB.</p></blockquote><blockquote><p>----- \u7EF4\u57FA\u767E\u79D1 \xB7 \u5192\u9669 ( 1979 \u5E74\u6E38\u620F );</p></blockquote><blockquote><p>\u5546\u4E1A\u8BA1\u7B97\u673A\u6E38\u620F\u4E2D\u7684\u7B2C\u4E00\u4E2A\u5F69\u86CB\u51FA\u73B0\u5728\u96C5\u8FBE\u5229 2600 \u7684\u4E00\u5F20\u65E9\u671F\u5361\u5E26\u4E2D, \u540D\u5B57\u5F88\u7B80\u5355\u5C31\u53EB\u505A\u300AAdventure\uFF08\u5192\u9669\uFF09\u300B, \u901A\u8FC7\u4E00\u7CFB\u5217\u4E0D\u5BFB\u5E38\u7684\u52A8\u4F5C\u548C\u9690\u6666\u7684\u64CD\u4F5C, \u73A9\u5BB6\u53EF\u4EE5\u53D1\u73B0\u4E00\u4E2A\u9690\u85CF\u7684\u623F\u95F4\u95EA\u70C1\u7740\u201CCreated by Warren Robinet\uFF08\u7531 Warren Robinet \u521B\u4F5C\uFF09\u201D\u5B57\u6837.</p></blockquote><blockquote><p>---- \u300A\u8BD7\u7BC7\u56DB\u5341\u516D\u7684\u5BC6\u79D8\u300B</p></blockquote><p>\u9F99\u9A6C\u8BA4\u4E3A\u8FD9\u4E2A\u5F69\u86CB\u7684\u610F\u4E49\u5728\u4E8E, \u4E4B\u524D\u6E38\u620F\u884C\u4E1A\u4E0D\u8BA4\u4E3A\u6E38\u620F\u5F00\u53D1\u8005\u662F\u8FDB\u884C\u4E00\u4E2A\u521B\u4F5C, \u6240\u4EE5\u4E0D\u5E94\u8BE5\u5728\u6E38\u620F\u4E2D\u8FDB\u884C\u7F72\u540D, \u5C31\u5982\u540C\u7C89\u5237\u5DE5\u628A\u5899\u5237\u7684\u5927\u767D, \u4E0D\u4F1A\u56E0\u6B64\u5728\u5899\u4E0A\u7F72\u540D\u4E00\u6837. \u800C\u8FD9\u4E2A\u5F69\u86CB\u4E14\u662F\u5728\u901A\u8FC7\u5F69\u86CB\u7684\u65B9\u5F0F\u5728\u6E38\u620F\u4E2D\u7F72\u540D, \u4EE5\u6B64\u8868\u8FBE\u6E38\u620F\u5236\u8005\u60F3\u8981\u6210\u4E3A\u4E00\u4E2A\u521B\u4F5C\u8005, \u800C\u4E0D\u662F\u4E00\u4E2A\u5236\u4F5C\u8005.</p><p>\u4F5C\u4E3A\u4E00\u4E2A\u4EA7\u54C1\u5DE5\u4F5C, \u591A\u591A\u5C11\u5C11\u4F1A\u601D\u8003\u8FD9\u6837\u7684\u60F3\u6CD5, \u4EA7\u54C1\u8BBE\u8BA1\u8FD9\u4E2A\u5DE5\u4F5C\u5230\u5E95\u5728\u4EC0\u4E48\u610F\u4E49\u4E0A\u53EF\u4EE5\u88AB\u79F0\u4E4B\u4E3A\u662F\u4E00\u4E2A\u521B\u4F5C.</p><p>\u5173\u4E8E\u8FD9\u70B9, \u6216\u8BB8\u6709\u610F\u8BC6\u6F14\u5316\u53EF\u4EE5\u6709\u6240\u56DE\u7B54\u7F62.</p><hr>',33),s=e("\u6216\u8BB8\u6B63\u662F\u5728\u8FD9\u4E2A\u610F\u4E49\u4E0A, \u4E9A\u91CC\u58EB\u591A\u5FB7\u624D\u8BF4, \u54F2\u5B66\u5E94\u8BE5\u65BD\u4E3A, \u800C\u4E0D\u662F\u88AB\u65BD\u4E3A; \u54F2\u4EBA\u4E0D\u5E94\u8BE5\u542C\u4ECE\u522B\u4EBA\u7684, \u522B\u4EBA\u5E94\u8BE5\u542C\u4ECE\u54F2\u4EBA\u7684. "),l=e("\u6C42\u77E5\u9636\u68AF");function _(b,q){const o=p("RouterLink");return a(),r("div",null,[u,n("p",null,[s,h(o,{to:"/write/%E6%B1%82%E7%9F%A5%E7%9A%84%E9%98%B6%E6%A2%AF.html"},{default:i(()=>[l]),_:1})])])}var f=t(d,[["render",_],["__file","\u6709\u610F\u8BC6\u6F14\u5316.html.vue"]]);export{f as default};
