import{_ as t,o as e,c as o,e as a}from"./app.6bf529a6.js";const r={},_=a('<h1 id="\u6885\u82B1\u6613\u4E0E\u672C\u4F53\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u6885\u82B1\u6613\u4E0E\u672C\u4F53\u8BBA" aria-hidden="true">#</a> \u6885\u82B1\u6613\u4E0E\u672C\u4F53\u8BBA</h1><p>\u8FD9\u4E2A\u4E3B\u9898\u662F\u5728\u5199\u300A\u6781\u7B80\u53E4\u5E0C\u814A\u54F2\u5B66\u53F2\u300B\u7684\u65F6\u5019\u4EA7\u751F\u7684. \u6216\u8005\u8BF4, \u6BCF\u5F53\u6211\u5FF5\u53CA\u5DF4\u95E8\u5C3C\u5FB7\u54F2\u5B66\u5BF9\u4E8E\u53E4\u5E0C\u814A\u54F2\u5B66\u53CA\u81F3\u897F\u65B9\u6587\u5316\u7684\u5F71\u54CD\u65F6, \u6211\u90FD\u4F1A\u60F3\u5230\u8FD9\u4E2A\u95EE\u9898: \u4E2D\u56FD\u5B66\u672F\u4E0E\u897F\u65B9\u54F2\u5B66\u7684\u4E3B\u8981\u5DEE\u5F02, \u662F\u4E0D\u662F\u5C31\u662F\u897F\u65B9\u54F2\u5B66\u4EE5\u903B\u8F91\u5B66\u4E3A\u57FA\u7840, \u800C\u4E2D\u56FD\u5B66\u672F\u4E2D\u57FA\u672C\u6CA1\u4EC0\u4E48\u903B\u8F91\u5B66\u7684\u5185\u5BB9? \u662F\u4E0D\u662F\u6B63\u662F\u8FD9\u4E2A\u5DEE\u5F02\u4F7F\u5F97\u5728\u897F\u65B9\u54F2\u5B66\u7684\u57FA\u7840\u4E0A\u4EA7\u751F\u4E86\u73B0\u4EE3\u79D1\u5B66, \u800C\u4E2D\u56FD\u5B66\u672F\u5219\u6CA1\u6709\u53D1\u5C55\u51FA\u7C7B\u4F3C\u7684\u73B0\u4EE3\u79D1\u5B66.</p><p>\u4EA7\u751F\u8FD9\u4E2A\u60F3\u6CD5\u662F\u975E\u5E38\u81EA\u7136\u7684\u4E8B\u60C5, \u56E0\u4E3A\u81F3\u76F4\u4ECA\u65E5, \u4E2D\u56FD\u4EBA\u90FD\u4E0D\u4E60\u60EF\u4EE5\u903B\u8F91\u8FDB\u884C\u601D\u7EF4.</p><p>\u4E0A\u5B66\u7684\u65F6\u5019, \u6559\u6CD5\u5F8B\u903B\u8F91\u7684\u8001\u5E08\u8BF4, \u56FD\u5185\u6559\u80B2\u754C\u4E00\u5EA6\u6709\u4E2D\u56FD\u6559\u80B2\u7F3A\u5C11\u903B\u8F91\u6559\u80B2\u7684\u58F0\u97F3, \u4E0E\u662F\u505A\u4E86\u4E00\u4E2A\u8C03\u67E5, \u5C31\u662F\u95EE &quot;\u6211\u662F\u4EBA&quot; \u548C &quot;\u4EBA\u662F\u6211&quot; \u8FD9\u4E24\u4E2A\u547D\u9898\u662F\u4E0D\u662F\u4E00\u6837. \u7ED3\u679C\u51B3\u5927\u591A\u6570\u7684\u88AB\u8C03\u67E5\u8005\u90FD\u89C9\u5F97\u4E24\u4E2A\u547D\u9898\u662F\u4E00\u6837\u7684. \u5F53\u65F6\u6211\u4EEC\u5B66\u6821\u7684\u8FA9\u8BBA\u961F\u5728\u5E02\u91CC\u7684\u6210\u7EE9, \u6211\u53C8\u6070\u597D\u5728\u8FA9\u8BBA\u961F\u91CC, \u6240\u4EE5\u6709\u673A\u4F1A\u4E8E\u5176\u4ED6\u9AD8\u6821\u7684\u8FA9\u8BBA\u961F\u505A\u4EA4\u6D41. \u5728\u8FDB\u884C\u4EA4\u6D41\u65F6, \u6211\u5C31\u95EE\u8FC7\u5BF9\u65B9\u8FD9\u4E2A\u95EE\u9898, \u6309\u8BF4\u8FA9\u8BBA\u961F\u91CC\u7684\u8FA9\u624B\u5E94\u8BE5\u6709\u57FA\u672C\u7684\u903B\u8F91\u5B66\u7D20\u517B, \u4F46\u7ED3\u679C\u662F\u5BF9\u65B9\u4F9D\u7136\u65E0\u6CD5\u533A\u5206\u4E24\u4E2A\u547D\u9898. \u751A\u81F3\u4E8E\u5230\u6700\u540E, \u6211\u90FD\u6CA1\u6709\u80FD\u7ED9\u5BF9\u65B9\u8BF4\u660E, &quot;\u4EBA\u662F\u6211&quot; \u8FD9\u4E2A\u547D\u9898\u5728\u903B\u8F91\u4E0A\u662F\u4E0D\u6210\u7ACB\u7684.</p><p>\u56E0\u6B64\u6211\u4E00\u5EA6\u8BA4\u4E3A, \u4E2D\u56FD\u5B66\u672F\u6CA1\u6709\u53D1\u5C55\u51FA\u73B0\u4EE3\u79D1\u5B66\u7684\u4E3B\u8981\u539F\u56E0\u5C31\u662F\u56E0\u4E3A\u4E2D\u56FD\u5B66\u672F\u4E0D\u91CD\u89C6\u903B\u8F91\u5B66.</p><p>\u4F46\u4ED4\u7EC6\u60F3\u60F3, \u8FD9\u4E2A\u60F3\u6CD5\u672C\u8EAB\u5728\u903B\u8F91\u4E0A\u5C31\u4E0D\u592A\u4E25\u8C28: \u4ECE&quot;\u897F\u65B9\u53D1\u5C55\u51FA\u79D1\u5B66, \u897F\u65B9\u91CD\u903B\u8F91, \u800C\u4E2D\u56FD\u6CA1\u53D1\u5C55\u51FA\u79D1\u5B66, \u4E2D\u56FD\u4E0D\u91CD\u903B\u8F91&quot;, \u63A8\u51FA <strong>\u897F\u65B9\u4E4B\u6240\u4EE5\u53D1\u5C55\u51FA\u79D1\u5B66\u662F\u56E0\u4E3A\u897F\u65B9\u4EBA\u91CD\u903B\u8F91</strong>, \u5F88\u6709\u53EF\u80FD\u72AF\u4E86\u5012\u679C\u4E3A\u56E0\u7684\u9519\u8BEF. \u56E0\u4E3A\u5F88\u6709\u53EF\u80FD <strong>\u6B63\u662F\u56E0\u4E3A\u897F\u65B9\u53D1\u5C55\u51FA\u4E86\u79D1\u5B66, \u6240\u4EE5\u897F\u65B9\u624D\u91CD\u903B\u8F91; \u4E2D\u56FD\u4E0D\u91CD\u903B\u8F91, \u53EF\u80FD\u4E0D\u662F\u4E2D\u56FD\u6CA1\u6709\u53D1\u5C55\u51FA\u6765\u79D1\u5B66\u7684\u539F\u56E0, \u53CD\u800C\u662F\u4E2D\u56FD\u6CA1\u6709\u53D1\u5C55\u51FA\u79D1\u5B66\u7684\u7ED3\u679C</strong>.</p><p>\u6240\u4EE5\u5982\u679C\u6211\u4EEC\u771F\u8981\u8BA8\u8BBA\u4E2D\u56FD\u5B66\u672F\u6CA1\u6709\u53D1\u5C55\u51FA\u73B0\u4EE3\u79D1\u5B66\u662F\u4E0D\u662F\u56E0\u4E3A\u4E2D\u56FD\u5B66\u672F\u4E0D\u91CD\u903B\u8F91, \u6211\u4EEC\u8FD8\u662F\u8981\u56DE\u5230\u5177\u4F53\u7684\u54F2\u5B66\u53F2\u4E2D.</p><h1 id="\u540D\u5BB6" tabindex="-1"><a class="header-anchor" href="#\u540D\u5BB6" aria-hidden="true">#</a> \u540D\u5BB6</h1><p>\u4E2D\u56FD\u54F2\u5B66\u53D1\u7AEF\u7684\u8BF8\u5B50\u767E\u5BB6\u4E2D, \u5176\u5B9E\u662F\u6709\u4E00\u5BB6\u4E13\u95E8\u7814\u8BA8\u903B\u8F91\u5B66\u7684. \u8FD9\u5C31\u662F\u540D\u5BB6.</p>',9),n=[_];function s(c,p){return e(),o("div",null,n)}var h=t(r,[["render",s],["__file","\u6885\u82B1\u6613\u4E0E\u672C\u4F53\u8BBA.html.vue"]]);export{h as default};
