import{_ as e,o as p,c as _,e as t}from"./app.6bf529a6.js";const i={},l=t('<h1 id="\u300A\u6B27\u7EEA\u5F17\u6D1B\u7BC7\u300B\u8BF4\u4E86\u5565" tabindex="-1"><a class="header-anchor" href="#\u300A\u6B27\u7EEA\u5F17\u6D1B\u7BC7\u300B\u8BF4\u4E86\u5565" aria-hidden="true">#</a> \u300A\u6B27\u7EEA\u5F17\u6D1B\u7BC7\u300B\u8BF4\u4E86\u5565</h1><p>\u6B27\u7EEA\u5F17\u6D1B\u5728\u738B\u5BAB\u6CD5\u5EAD\u524D\u9047\u89C1\u4E86\u82CF\u683C\u62C9\u5E95. \u4ED6\u4EEC\u4E92\u8BC9\u4E86\u6765\u6B64\u7684\u539F\u56E0. \u82CF\u683C\u62C9\u5E95\u6765\u6B64\u662F\u56E0\u7F8E\u52D2\u6258\u5411\u6CD5\u5EAD\u63A7\u544A\u4ED6\u4EE5\u521B\u7ACB\u65B0\u795E, \u4E0D\u4FE1\u8001\u795E\u7684\u65B9\u5F0F\u8150\u8680\u9752\u5E74. \u800C\u6B27\u7ED3\u5F17\u6D1B\u6765\u6B64\u5219\u662F\u4E3A\u4E86\u63A7\u544A\u4ED6\u7684\u7236\u4EB2\u6740\u4E86\u4EBA.</p><p>\u6B27\u7EEA\u5F17\u6D1B\u7684\u4E00\u4E2A\u96C7\u5DE5\u56E0\u9189\u9152\u540E\u4E89\u6267\u6740\u4E86\u4E00\u540D\u5BB6\u5974, \u4E8E\u662F\u4ED6\u7684\u7236\u4EB2\u4FBF\u628A\u8FD9\u96C7\u5DE5\u6346\u8D77\u6765\u6254\u5728\u6C9F\u6E20\u4E2D, \u7136\u540E\u6D3E\u4EBA\u53BB\u96C5\u5178\u95EE\u5DEB\u5E08\u8FD9\u4E8B\u8BE5\u5982\u4F55\u5904\u7406. \u4F46\u6D3E\u53BB\u7684\u4EBA\u8FD8\u672A\u56DE\u6765, \u8FD9\u4EBA\u5C31\u6B7B\u4E86. \u4E8E\u662F\u6B27\u7EEA\u5F17\u6D1B\u6765\u6B64\u63A7\u544A\u4ED6\u7684\u7236\u4EB2\u6740\u4E86\u4EBA.</p><p>\u6B27\u7EEA\u5F17\u6D1B\u7684\u7236\u4EB2\u548C\u4EB2\u5C5E\u90FD\u89C9\u5F97\u4ED6\u8FD9\u6837\u505A\u4E0D\u8654\u656C, \u4F46\u4ED6\u5374\u81EA\u89C6\u7CBE\u901A\u795E\u5723\u4E8B\u7269\u7684\u7CBE\u786E\u77E5\u8BC6, \u5E76\u8BA4\u4E3A\u81EA\u5DF1\u6240\u4E3A\u4E0D\u4F1A\u6709\u4EFB\u4F55\u7684\u4E0D\u8654\u656C.</p><p>\u82CF\u683C\u62C9\u5E95\u8981\u6C42\u6210\u4E3A\u6B27\u7EEA\u5F17\u6D1B\u7684\u5B66\u751F, \u5411\u4ED6\u5B66\u4E60\u5173\u4E8E\u8654\u656C\u7684\u77E5\u8BC6, \u597D\u5728\u6CD5\u5EAD\u4E0A\u5E94\u5BF9\u63A7\u544A. \u6B27\u7EEA\u5F17\u6D1B\u6B23\u7136\u540C\u610F. \u4E8E\u662F\u82CF\u683C\u62C9\u5E95\u8981\u6C42\u6B27\u7EEA\u5F17\u6D1B\u5BF9\u8654\u656C\u4E0B\u4E00\u4E2A\u5B9A\u4E49</p><p>\u6B27\u7EEA\u5F17\u6D1B\u5BF9\u8654\u656C\u7684\u7B2C\u4E00\u4E2A\u5B9A\u4E49\u662F: \u8654\u656C\u662F\u63A7\u544A\u4E0D\u8654\u656C\u7684\u4EBA, \u5982\u540C\u4ED6\u63A7\u544A\u81EA\u5DF1\u7684\u7236\u4EB2. \u6709\u5982\u5B99\u65AF\u4E00\u5BB6\u4E09\u4EE3\u90FD\u56E0\u81EA\u5DF1\u7684\u7236\u4EB2\u7684\u4E0D\u4E49\u800C\u505A\u51FA\u5411\u81EA\u5DF1\u7236\u4EB2\u590D\u4EC7\u7684\u4E8B\u60C5.</p><p>\u82CF\u683C\u62C9\u5E95\u63D0\u51FA\u6B27\u7EEA\u5F17\u6D1B\u6240\u8BF4\u7684, \u53EA\u662F\u4ECE\u8654\u656C\u4E4B\u4E8B\u51FA\u4E3E\u51FA\u4E00\u4E8C\u6837\u6765, \u4F46\u4ED6\u60F3\u95EE\u9898\u5374\u662F &quot;\u4F7F\u4E00\u5207\u8654\u656C\u884C\u4E3A\u6210\u4E3A\u8654\u656C\u7684\u57FA\u672C\u7684\u578B&quot;.</p><p>\u6B27\u7EEA\u5F17\u6D1B\u63D0\u51FA\u5BF9\u8654\u656C\u7684\u7B2C\u4E8C\u4E2A\u5B9A\u4E49: \u8654\u656C\u4E4B\u4E8B\u662F\u795E\u559C\u60A6\u4E4B\u4E8B, \u4E0D\u8654\u656C\u4E4B\u4E8B\u662F\u4E0D\u4E3A\u795E\u6240\u559C\u60A6\u4E4B\u4E8B.</p><p>\u82CF\u683C\u62C9\u5E95\u6839\u636E\u8FD9\u4E2A\u5B9A\u4E49\u63D0\u51FA\u95EE\u9898:</p><ol><li>\u795E\u4E4B\u95F4\u6709\u5206\u4E89</li><li>\u9AD8\u4F4E, \u957F\u77ED, \u8F7B\u91CD\u4E4B\u7C7B\u53EF\u91CF\u4E4B\u4E8B\u4E0D\u4F1A\u6709\u5206\u4E89, \u6709\u5206\u4E89\u7684\u90FD\u662F\u9AD8\u5C1A\u4E0E\u5351\u9119, \u5584\u4E0E\u6076\u8FD9\u6837\u7684\u4E8B</li><li>\u795E\u5BF9\u4E0A\u8FF0\u4E8B\u9879\u6709\u5206\u4E89, \u4E5F\u5C31\u610F\u5473\u7740\u5BF9\u540C\u4E00\u4EF6\u4E8B\u795E\u6709\u4E0D\u540C\u7684\u770B\u6CD5</li><li>\u6240\u4EE5\u540C\u6837\u7684\u4E8B\u7269\u53EF\u4EE5\u65E2\u662F\u795E\u559C\u7231, \u53C8\u662F\u795E\u6240\u4EC7\u6839\u7684</li><li>\u4E5F\u5C31\u662F\u8BF4, \u4E00\u4EF6\u4E8B\u53EF\u4EE5\u5373\u8654\u656C\u53C8\u4E0D\u8654\u656C.</li></ol><p>\u6B27\u7EEA\u5F17\u6D1B\u63D0\u51FA, \u65E0\u8BBA\u5982\u4F55, \u65E0\u8BBA\u5982\u4F55, \u8BF8\u795E\u5BF9\u9519\u6740\u4EBA\u5C31\u5F97\u62B5\u7F6A\u8FD9\u4E00\u70B9\u4E0A\u662F\u4E00\u81F3\u7684.</p><p>\u82CF\u683C\u62C9\u5E95\u8BF4, \u90A3\u6015\u662F\u4EBA, \u4E5F\u4E0D\u4F1A\u8BF4\u505A\u9519\u4E8B\u4E0D\u9700\u62B5\u7F6A. \u6240\u4EE5\u65E0\u8BBA\u795E\u8FD8\u662F\u4EBA, \u90FD\u53EA\u662F\u5BF9\u54EA\u4E9B\u4E8B\u662F\u9519\u7684\u6709\u5206\u6B67, \u800C\u4E0D\u662F\u505A\u9519\u5E94\u53D7\u7F5A\u6709\u5206\u6B67. \u6240\u4EE5\u5BF9\u4E8E\u6B27\u7EEA\u5F17\u6D1B\u6765\u8BF4, \u95EE\u9898\u5E76\u4E0D\u51FA\u5728\u9519\u6740\u4EBA\u5E94\u4E0D\u5E94\u8BE5\u88AB\u63A7\u544A\u4E0A, \u800C\u5728\u4ED6\u5728\u8FD9\u6837\u4E00\u4E2A\u5177\u4F53\u60C5\u51B5\u4E0B, \u505A\u8FD9\u6837\u4E00\u4E2A\u5177\u4F53\u7684\u4E8B\u60C5\u662F\u4E0D\u662F\u795E\u8BA4\u4E3A\u6B63\u786E\u7684. \u4E8E\u662F\u82CF\u683C\u62C9\u5E95\u4FEE\u6B63\u4E86\u4ED6\u4EEC\u7684\u5B9A\u4E49: \u51E1\u6240\u6709\u8BF8\u795E\u6240\u75DB\u6068\u7684\u5C31\u662F\u4E0D\u8654\u656C, \u51E1\u6240\u6709\u8BF8\u795E\u5168\u90E8\u559C\u7231\u7684\u5C31\u662F\u8654\u656C\u7684.</p>',12),a=[l];function o(c,r){return p(),_("div",null,a)}var s=e(i,[["render",o],["__file","\u6B27\u7EEA\u5F17\u6D1B\u7BC7\u8BF4\u4E86\u5565.html.vue"]]);export{s as default};
