import{_ as a,r as u,o as r,c as q,a as n,b as s,w as h,e as t,d as o}from"./app.efeddd24.js";const p={},d=t('<h1 id="\u6BD5\u8FBE\u54E5\u62C9\u65AF\u7684\u65E0\u7406\u6570" tabindex="-1"><a class="header-anchor" href="#\u6BD5\u8FBE\u54E5\u62C9\u65AF\u7684\u65E0\u7406\u6570" aria-hidden="true">#</a> \u6BD5\u8FBE\u54E5\u62C9\u65AF\u7684\u65E0\u7406\u6570</h1><h2 id="\u5173\u4E8E\u4E00\u4E2A\u6545\u4E8B\u7684\u8BEF\u89E3" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u4E00\u4E2A\u6545\u4E8B\u7684\u8BEF\u89E3" aria-hidden="true">#</a> \u5173\u4E8E\u4E00\u4E2A\u6545\u4E8B\u7684\u8BEF\u89E3</h2><p>\u5173\u4E8E\u6BD5\u8FBE\u54E5\u62C9\u65AF\u6709\u4E00\u4E2A\u6545\u4E8B, \u8BF4\u7684\u662F, \u6BD5\u8FBE\u54E5\u62C9\u65AF\u8BA4\u4E3A\u6570\u662F\u5B9E\u4F53, \u662F\u4E16\u754C\u7684\u57FA\u672C\u539F\u7D20, \u56E0\u6B64\u65E0\u7406\u6570\u662F\u4E0D\u5B58\u5728\u7684. \u4F46\u662F\u4ED6\u7684\u4E00\u4E2A\u5B66\u751F\u95EE\u4ED6, \u5982\u679C\u65E0\u7406\u6570\u662F\u4E0D\u5B58\u5728\u7684, \u90A3\u4E48\u6B63\u65B9\u5F62\u7684\u5BF9\u89D2\u7EBF\u662F\u591A\u957F. \u6BD5\u8FBE\u54E5\u62C9\u65AF\u56E0\u6B64\u628A\u8FD9\u4E2A\u5B66\u751F\u6254\u8FDB\u6D77\u91CC\u53BB\u4E86.</p><p>\u6211\u4E00\u76F4\u7528\u8FD9\u4E2A\u6545\u4E8B\u6765\u8BF4\u660E\u6BD5\u8FBE\u54E5\u62C9\u65AF\u5B66\u6D3E\u7684\u8FC7\u5EA6\u7406\u60F3\u4E3B\u4E49.</p><p>\u4F46\u540E\u6765\u4ECE\u7FFB\u7535\u4E2D\u542C\u5C0F\u674E\u8001\u5E08\u8BF4, \u5176\u5B9E\u6BD5\u8FBE\u54E5\u62C9\u65AF\u628A\u4ED6\u7684\u5B66\u751F\u6254\u6D77\u91CC\u5E76\u4E0D\u662F\u56E0\u4E3A\u4ED6\u4E0D\u627F\u8BA4\u65E0\u7406\u6570\u7684\u5B58\u5728, \u800C\u662F\u56E0\u4E3A\u4ED6\u8BA4\u4E3A\u7814\u7A76\u65E0\u7406\u6570 &quot;\u6E0E\u795E&quot;.</p><p>\u5F00\u59CB\u4E5F\u5E76\u6CA1\u6709\u89C9\u5F97\u8FD9\u4E2A\u8BBA\u70B9\u6709\u4EC0\u4E48\u7279\u6B8A,\u76F4\u5230\u770B\u4E86\u4E9A\u91CC\u58EB\u591A\u5FB7\u7684\u300A\u5F62\u800C\u4E0A\u5B66\u300B\u624D\u771F\u6B63\u7406\u89E3\u5230\u4E3A\u4EC0\u4E48\u7814\u7A76\u65E0\u7406\u6570\u662F &quot;\u6E0E\u795E&quot; \u7684.</p><h2 id="\u6545\u4E8B\u672C\u8EAB" tabindex="-1"><a class="header-anchor" href="#\u6545\u4E8B\u672C\u8EAB" aria-hidden="true">#</a> \u6545\u4E8B\u672C\u8EAB</h2><blockquote><p>\u5E0C\u5E15\u7D22\u65AF, \u751F\u6D3B\u4E8E\u5927\u7EA6\u516C\u65E0\u524D 500 \u5E74, \u751F\u5352\u5E74\u4E0D\u8BE6, \u5C5E\u4E8E\u6BD5\u8FBE\u54E5\u62C9\u65AF\u5B66\u6D3E\u95E8\u751F.</p></blockquote><blockquote><p>\u516C\u5143\u524D 5 \u4E16\u7EAA\u5DE6\u53F3, \u6BD5\u8FBE\u54E5\u62C9\u65AF\u5B66\u8BA4\u4E3A, &quot;\u4E07\u7269\u7686\u6570&quot;, \u4E16\u754C\u4E0A\u53EA\u6709\u6574\u6570\u548C\u5206\u6570 ( \u6709\u7406\u6570 ). \u800C\u5E0C\u5E15\u7D22\u65AF\u5374\u53D1\u73B0\u4E86\u4EE4\u4EBA\u9707\u60CA\u7684 &quot;\u65E0\u9650\u5FAA\u73AF\u5C0F\u6570&quot;, \u5373\u65E0\u7406\u6570. \u4EE4\u8BE5\u5B66\u6D3E\u611F\u5230\u6050\u614C, \u5E76\u5F15\u53D1\u4E86\u7B2C\u4E00\u6B21\u6570\u5B66\u5371\u673A. \u6709\u4F20\u8A00\u8BF4\u5E0C\u5E15\u65AF\u88AB\u81EA\u5DF1\u7684\u8001\u5E08\u6BD5\u8FBE\u54E5\u62C9\u65AF\u5224\u51B3\u6DF9\u6B7B. \u4E5F\u6709\u8BF4\u6CD5\u662F\u88AB\u5B66\u6D3E\u95E8\u4EBA\u4E22\u5230\u6D77\u91CC\u6DF9\u6B7B.</p></blockquote><blockquote><p>---- \u7EF4\u57FA\u767E\u79D1 \xB7 \u5E0C\u5E15\u7D22\u65AF</p></blockquote><h2 id="\u4E9A\u91CC\u58EB\u591A\u5FB7\u5728\u300A\u5F62\u800C\u4E0A\u5B66\u300B\u4E2D\u5BF9\u4E8E\u65E0\u7406\u6570\u8BA8\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u4E9A\u91CC\u58EB\u591A\u5FB7\u5728\u300A\u5F62\u800C\u4E0A\u5B66\u300B\u4E2D\u5BF9\u4E8E\u65E0\u7406\u6570\u8BA8\u8BBA" aria-hidden="true">#</a> \u4E9A\u91CC\u58EB\u591A\u5FB7\u5728\u300A\u5F62\u800C\u4E0A\u5B66\u300B\u4E2D\u5BF9\u4E8E\u65E0\u7406\u6570\u8BA8\u8BBA</h2><blockquote><p>\u53EF\u662F, \u5728\u67D0\u4E00\u542B\u4E49\u4E0A, \u4FEE\u4E60\u8FD9\u4E00\u95E8\u5B66\u672F\u7684\u7ED3\u679C\u6070\u4E0E\u6211\u4EEC\u4E0A\u8FF0\u63A2\u7D22\u76F8\u53CD. \u6240\u6709\u7684\u4EBA\u90FD\u4ECE\u5BF9\u4E07\u8C61\u7684\u60CA\u5F02\u4E3A\u5F00\u7AEF, \u5982\u5080\u5121\u81EA\u884C, \u5982\u51AC\u81F3\u4E8E\u590F\u81F3, \u5982 &quot;\u6B63\u65B9\u5F62\u7684\u5BF9\u89D2\u7EBF\u4E0D\u80FD\u7528\u8FB9\u6765\u8BA1\u91CF.&quot; \u7B49; \u8BF4\u662F\u4E16\u4E0A\u6709\u4E00\u4E8B\u7269, \u5373\u4FBF\u5F15\u7528\u6700\u5C0F\u7684\u5355\u4F4D\u8FD8\u662F\u4E0D\u80FD\u52A0\u4EE5\u8BA1\u91CF, \u8FD9\u5BF9\u4E8E\u6240\u6709\u672A\u660E\u5176\u6545\u7684\u4EBA\u6B63\u662F\u53EF\u60CA\u5F02\u7684. \u7136\u800C\u5B9E\u9645\u6070\u6B63\u76F8\u53CD, \u8FD9\u5BF9\u4E8E\u6240\u6709 &quot;\u518D\u601D\u800C\u5F97&quot;, \u4EBA\u80FD\u660E\u4E8B\u7269\u4E4B\u6545, \u800C\u540E\u4E0D\u4E3A\u4E8B\u7269\u6240\u60D1; \u5BF9\u4E8E\u4E00\u4E2A\u51E0\u4F55\u5B66\u8005, \u5982\u679C\u5BF9\u89D2\u7EBF\u6210\u4E3A\u53EF\u8BA1\u91CF\u7684, \u90A3\u624D\u662F\u4E16\u95F4\u602A\u4E8B.</p></blockquote><blockquote><p>---- \u4E9A\u91CC\u58EB\u591A\u5FB7 \u300A\u5F62\u800C\u4E0A\u5B66 \xB7 \u7B2C\u4E00\u5377 \xB7 \u7AE0\u4E8C\u300B</p></blockquote>',13),i=o("\u6C42\u77E5\u7684\u9636\u68AF"),c=o(";"),l=t('<h2 id="\u4ECE-\u5E73\u65B9-\u6765\u8BF4\u4E3A\u4EC0\u4E48-\u4E07\u7269\u7686\u6570" tabindex="-1"><a class="header-anchor" href="#\u4ECE-\u5E73\u65B9-\u6765\u8BF4\u4E3A\u4EC0\u4E48-\u4E07\u7269\u7686\u6570" aria-hidden="true">#</a> \u4ECE &quot;\u5E73\u65B9&quot; \u6765\u8BF4\u4E3A\u4EC0\u4E48 &quot;\u4E07\u7269\u7686\u6570&quot;</h2><p>2 \u4E58\u4EE5 2 \u79F0\u4E3A 2 \u7684\u5E73\u65B9, 3 \u4E58\u4EE5 3 \u79F0\u4E3A 3 \u7684\u5E73\u65B9. <strong>\u90A3\u5E73\u65B9\u4E3A\u4EC0\u4E48\u53EB\u505A &quot;\u5E73\u65B9&quot; \u5462?</strong></p><p>\u5E73\u65B9\u7684\u610F\u601D\u4E3A, \u5047\u5982\u4E00\u4E2A\u6570\u4E3A\u4E00\u4E2A\u70B9\u7684\u8BDD, \u90A3\u4E48 4 \u6216 9 \u5728\u4E00\u4E2A\u5E73\u9762\u4E0A\u6B63\u597D\u53EF\u4EE5\u6784\u6210\u4E00\u4E2A\u6B63\u5F62. \u5728\u6BD5\u8FBE\u54E5\u62C9\u65AF\u5B66\u6D3E\u4E2D, <strong>\u8FD9\u79CD\u53EF\u4EE5\u6784\u6210\u4E00\u4E2A\u6B63\u65B9\u5F62\u7684\u6570\u90FD\u53EB\u505A &quot;\u65B9\u6570&quot;.</strong></p><p>\u540C\u7406, \u53EF\u4EE5\u6784\u6210\u4E00\u4E2A\u4E09\u89D2\u5F62\u7684\u6570, <strong>\u4ED6\u4EEC\u79F0\u4E3A &quot;\u89D2\u6570&quot;, \u5982 3.</strong></p><p>\u4E5F\u5373\u662F\u8BF4, \u6BD5\u8FBE\u54E5\u62C9\u65AF\u5B66\u6D3E\u8BA4\u4E3A, <strong>\u6570\u4E0D\u662F\u4E00\u4E2A\u62BD\u8C61\u7684\u6982\u5FF5, \u800C\u662F\u5B9E\u5B58\u7684\u4E1C\u897F</strong>, \u800C\u8FD9\u4E16\u754C\u5C31\u662F\u7531\u8FD9\u6837\u5B9E\u5B58\u7684\u6570\u6784\u6210: \u4E5F\u65E2\u4E16\u754C\u662F <strong>&quot;\u70B9\u79EF&quot;</strong> \u7684, \u5176\u4E2D\u7684 &quot;\u70B9&quot; \u5C31\u662F &quot;\u6570&quot;.</p><h2 id="\u65E0\u7406\u6570\u4F55\u4EE5\u65E0\u7406" tabindex="-1"><a class="header-anchor" href="#\u65E0\u7406\u6570\u4F55\u4EE5\u65E0\u7406" aria-hidden="true">#</a> \u65E0\u7406\u6570\u4F55\u4EE5\u65E0\u7406</h2><p>\u65E0\u7406\u6570\u7684 &quot;\u65E0\u7406&quot;, \u5E76\u4E0D\u662F\u8FD9\u4E2A\u6570\u662F &quot;\u6CA1\u6709\u9053\u7406\u7684&quot;, <strong>\u800C\u662F\u8FD9\u4E2A\u6570\u662F &quot;\u65E0\u6CD5\u7406\u89E3\u7684&quot;</strong>.</p><p>\u8BA9\u6211\u4EEC\u56DE\u5230 &quot;\u6B63\u65B9\u5F62\u7684\u5BF9\u89D2\u7EBF\u4E0D\u80FD\u7528\u8FB9\u6765\u8BA1\u91CF&quot; \u4E0A. \u5982\u679C\u6B63\u65B9\u5F62\u7684\u8FB9\u662F\u7531 &quot;\u70B9\u79EF\u7D2F\u800C\u6210\u7684&quot;, \u90A3\u4E48\u6211\u4EEC\u65E0\u8BBA\u5982\u4F55\u5207\u5206\u6784\u6210\u6B63\u65B9\u5F62\u7684\u8FB9, \u4EE5\u5F97\u5230\u6784\u6210\u5176\u7684\u70B9, \u90FD\u65E0\u6CD5\u4F7F\u8FD9\u4E9B\u70B9\u6B63\u597D\u6784\u6210\u6B63\u65B9\u5F0F\u7684\u5BF9\u89D2\u7EBF.</p><p>\u5728\u8FD9\u4E2A\u610F\u4E49\u4E0A, \u6B63\u65B9\u5F62\u7684\u5BF9\u89D2\u7EBF\u662F\u65E0\u6CD5\u7406\u89E3\u7684: <strong>\u6211\u4EEC\u65E0\u6CD5\u7528\u5DF1\u77E5\u7684\u4E1C\u897F\u53BB\u8BA1\u91CF\u5B83.</strong></p><h2 id="\u795E\u7684\u5B66\u672F" tabindex="-1"><a class="header-anchor" href="#\u795E\u7684\u5B66\u672F" aria-hidden="true">#</a> \u795E\u7684\u5B66\u672F</h2><p>\u4E9A\u91CC\u58EB\u591A\u5FB7\u4E4B\u6240\u4EE5\u8981\u8BF4\u65E0\u7406\u6570\u7684\u4E8B, \u662F\u56E0\u4E3A\u4ED6\u8981\u505A\u4EE5\u4E0B\u8BBA\u8BC1:</p><ul><li>\u54F2\u5B66\u662F\u6700\u9AD8\u7684\u5B66\u95EE;</li><li>\u54F2\u5B66\u4E4B\u6240\u4EE5\u662F\u6700\u9AD8\u7684\u5B66\u95EE, \u662F\u56E0\u4E3A\u54F2\u5B66\u7814\u7A76\u7684\u77E5\u8BC6\u662F\u795E\u7684\u77E5\u8BC6;</li><li>\u6240\u8C13 &quot;\u795E\u7684\u77E5\u8BC6&quot; \u662F\u6307 &quot;\u8FD9\u95E8\u5B66\u672F\u662F\u795E\u6240\u72EC\u6709; \u6216\u795E\u80FD\u8D85\u4E4E\u4EBA\u7C7B\u800C\u6240\u77E5\u72EC\u591A&quot; \u7684\u77E5\u8BC6;</li><li>\u6BD4\u5982\u6B63\u65B9\u5F62\u5BF9\u89D2\u7EBF\u65E2\u662F\u5982\u6B64.</li></ul><p>\u65E0\u7406\u6570\u4E4B\u65E0\u7406, \u53EA\u662F\u5BF9\u4E8E\u6211\u4EEC\u65E0\u7406\u800C\u5DF1, <strong>\u4F46\u5BF9\u4E8E\u795E\u6765\u8BF4, \u8FD9\u4E2A\u6570\u4E00\u5B9A\u662F\u53EF\u4EE5\u7406\u89E3\u7684</strong>. \u56E0\u4E3A\u6B63\u65B9\u5F62\u7684\u5BF9\u89D2\u7EBF\u4E0E\u5176\u8FB9\u4E00\u5B9A\u662F\u6709\u4E00\u4E2A\u56FA\u5B9A\u5173\u7CFB\u7684, \u53EA\u662F\u6211\u4EEC\u4E0D\u77E5\u9053\u8BE5\u600E\u4E48\u8BA1\u91CF\u5B83, \u800C\u8FD9\u4E2A\u77E5\u8BC6\u795E\u4E00\u5B9A\u77E5\u9053, \u56E0\u4E3A\u795E\u80FD\u521B\u9020\u6B63\u65B9\u5F62\u4E00\u5B9A\u77E5\u9053\u6B63\u65B9\u5F62\u7684\u4E00\u5207\u77E5\u8BC6.</p><p>\u8FD9\u5C31\u662F\u6240\u8C13\u7684 &quot;\u8FD9\u95E8\u5B66\u672F\u662F\u795E\u6240\u72EC\u6709; \u6216\u795E\u80FD\u8D85\u4E4E\u4EBA\u4E3A\u800C\u6240\u77E5\u72EC\u591A&quot;.</p><h2 id="\u7814\u7A76\u65E0\u7406\u6570\u4E3A\u4EC0\u4E48-\u6E0E\u795E" tabindex="-1"><a class="header-anchor" href="#\u7814\u7A76\u65E0\u7406\u6570\u4E3A\u4EC0\u4E48-\u6E0E\u795E" aria-hidden="true">#</a> \u7814\u7A76\u65E0\u7406\u6570\u4E3A\u4EC0\u4E48 &quot;\u6E0E\u795E&quot;</h2><p>\u4E9A\u91CC\u58EB\u591A\u5FB7\u8BF4, \u4EBA\u4E4B\u6240\u4EE5\u8981\u7814\u7A76\u54F2\u5B66, \u662F\u56E0\u4E3A\u60CA\u5F02. &quot;\u4E00\u4E2A\u6709\u6240\u8FF7\u60D1\u4E0E\u60CA\u5F02\u7684\u4EBA, \u6BCF\u81EA\u6127\u611A\u8822... \u4ED6\u4EEC\u63A2\u7D22\u54F2\u7406\u662F\u4E3A\u4E86\u8131\u51FA\u611A\u8822&quot; .</p><p>\u7D27\u63A5\u7740\u4ED6\u8BF4: &quot;\u8981\u83B7\u5F97\u8FD9\u6837\u7684\u77E5\u8BC6\u4E5F\u8BB8\u8D85\u4E4E\u4EBA\u7C7B\u7684\u80FD\u529B.&quot; \u56E0\u4E3A &quot;\u8FD9\u6837\u4E00\u95E8\u5B66\u672F\u6216\u5219\u662F\u795E\u72EC\u6709, \u6216\u5219\u662F\u795E\u80FD\u8D85\u4E4E\u4EBA\u7C7B\u800C\u6240\u77E5\u72EC\u591A&quot;. \u6BD4\u5982\u6B63\u65B9\u5F62\u7684\u5BF9\u89D2\u7EBF.</p><p>\u56E0\u6B64, &quot;\u5728\u67D0\u4E00\u542B\u4E49\u4E0A, \u4FEE\u4E60\u8FD9\u4E00\u95E8\u5B66\u672F\u7684\u7ED3\u679C\u6070\u4E0E\u6211\u4EEC\u4E0A\u8FF0\u63A2\u7D22\u76F8\u53CD.&quot; \u6C42\u77E5\u662F\u4E3A\u4E86\u901A\u8FC7\u601D\u8003\u60CA\u5F02\u4E4B\u4E8B\u800C\u77E5\u5176\u539F\u56E0, \u4ECE\u800C &quot;\u4E0D\u4E3A\u4E8B\u7269\u6240\u60D1&quot;. \u800C\u5F53\u4E00\u4E2A\u4E8B\u7269, \u6211\u4EEC\u5F15\u7528\u6700\u5C0F\u7684\u5355\u4EEC\u5B66\u662F\u4E0D\u80FD\u52A0\u4EE5\u8BA1\u91CF, <strong>\u8FD9\u79CD\u786E\u51FF\u7684\u60CA\u5F02, \u6B63\u662F\u6570\u5B66\u7684\u795E\u6027</strong>.</p><p><strong>\u795E\u6027, \u5728\u4EBA\u7406\u6027\u7684\u8FB9\u754C\u663E\u73B0\u51FA\u6765.</strong></p><p>\u800C\u5F53\u6211\u4EEC\u4E00\u5B9A\u8981\u628A\u65E0\u7406\u6570\u62FF\u51FA\u6765\u7814\u7A76, \u8BA9\u5176\u53EF\u4EE5\u7406\u89E3, \u5176\u5B9E\u662F\u5C06\u6570\u5B66\u7684\u795E\u6027\u62FF\u6389. \u5C31\u5982, \u81F3\u4ECA\u6211\u4EEC\u4E5F\u6CA1\u6709\u80FD\u7A77\u5C3D\u6839\u53F7 2 \u662F\u591A\u5C11, \u6211\u4EEC\u4EC5\u4EC5\u662F\u4E0D\u4EE5\u4E16\u95F4\u7ADF\u7136\u6709\u4E00\u4E2A\u6570\u662F\u5982\u6839\u53F7 2 \u4E00\u6837\u65E0\u6CD5\u7A77\u5C3D\u800C\u60CA\u5F02\u7F62\u4E86.</p><p>\u5728\u8FD9\u4E2A\u610F\u4E49\u4E0A, \u7814\u7A76\u65E0\u7406\u6570\u662F &quot;\u6E0E\u795E&quot; \u7684.</p>',21);function _(g,b){const e=u("RouterLink");return r(),q("div",null,[d,n("p",null,[s(e,{to:"/write/%E6%B1%82%E7%9F%A5%E7%9A%84%E9%98%B6%E6%A2%AF.html"},{default:h(()=>[i]),_:1}),c]),l])}var k=a(p,[["render",_],["__file","\u6BD5\u8FBE\u54E5\u62C9\u65AF\u7684\u65E0\u7406\u6570.html.vue"]]);export{k as default};
