import{_ as a,r as i,o as l,c as o,a as e,b as r,w as u,e as p,d as h}from"./app.efeddd24.js";const d={},n=p('<h1 id="\u6C42\u77E5\u7684\u9636\u68AF" tabindex="-1"><a class="header-anchor" href="#\u6C42\u77E5\u7684\u9636\u68AF" aria-hidden="true">#</a> \u6C42\u77E5\u7684\u9636\u68AF</h1><h2 id="\u4EC0\u4E48\u662F\u6C42\u77E5\u7684\u9636\u68AF" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u6C42\u77E5\u7684\u9636\u68AF" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u6C42\u77E5\u7684\u9636\u68AF</h2><p>\u4E9A\u91CC\u58EB\u591A\u5FB7\u300A\u5F62\u800C\u4E0A\u5B66\u300B\u5F00\u7BC7\u7B2C\u4E00\u53E5\u8BDD\u662F &quot;\u6C42\u77E5\u662F\u4EBA\u7684\u672C\u6027&quot;.</p><p>\u5728\u5176\u5377\u4E00\u7684\u7B2C\u4E00\u7AE0\u548C\u7B2C\u4E8C\u7AE0, \u4E9A\u91CC\u58EB\u591A\u5FB7\u8BA8\u8BBA\u4E86\u4EBA\u7684\u6C42\u77E5\u662F\u4ECE\u611F\u89C9\u53D1\u5C55\u5230\u54F2\u5B66\u7684\u8FC7\u7A0B.</p><p>\u4E9A\u91CC\u58EB\u591A\u5FB7\u8BA4\u4E3A, \u8FD9\u4E2A\u8FC7\u7A0B\u662F\u9010\u6E10\u4E0A\u5347\u7684: \u611F\u89C9\u6700\u4F4E\u800C\u54F2\u5B66\u6700\u9AD8, \u5C42\u5C42\u53D1\u5C55\u5982\u9636\u68AF\u4E00\u822C.</p><h2 id="\u4ECE\u611F\u89C9\u5230\u6280\u672F" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u611F\u89C9\u5230\u6280\u672F" aria-hidden="true">#</a> \u4ECE\u611F\u89C9\u5230\u6280\u672F</h2><p>\u4E9A\u91CC\u58EB\u591A\u5FB7\u8BA4\u4E3A &quot;\u6C42\u77E5\u662F\u4EBA\u7684\u672C\u6027&quot; \u5462, \u56E0\u4E3A\u4ED6\u53D1\u73B0\u4EBA\u65E2\u4F7F\u4E0D\u4E3A\u4EFB\u4F55\u76EE\u7684\u4E5F\u4E50\u4E8E\u611F\u89C9, \u5C24\u5176\u770B\u89C6\u89C9.</p><p>\u7531\u611F\u89C9\u4EA7\u751F\u8BB0\u5FC6. \u6709\u7684\u52A8\u7269\u6709\u8BB0\u5FC6, \u5982\u679C\u4ED6\u4EEC\u8FD8\u5177\u6709\u542C\u89C9, \u5B83\u4EEC\u5C31\u8981\u53EF\u4EE5\u8BAD\u5316.</p><p>\u52A8\u7269\u51ED\u73B0\u8C61\u548C\u8BB0\u5FC6\u800C\u6D3B, \u800C\u4EBA\u6709\u7ECF\u9A8C. \u7ECF\u9A8C\u7531\u5C61\u6B21\u8BB0\u5FC6\u800C\u540E\u4EA7\u751F.</p><p>\u7ECF\u7531\u7ECF\u9A8C\u800C\u5BF9\u67D0\u4E00\u4E8B\u7269\u4EA7\u751F\u666E\u904D\u6027\u7684\u5224\u65AD, \u5C31\u662F\u6280\u672F. \u5982:</p><ul><li>\u52A0\u91CC\u4E9A\u56E0\u751F\u75C5\u800C\u53D7\u76CA (\u7ECF\u9A8C);</li><li>\u5982\u52A0\u91CC\u4E9A\u822C\u4F53\u8D28\u7684\u4F1A\u56E0\u751F\u75C5\u767E\u53D7\u76CA ( \u7531\u7ECF\u9A8C\u800C\u4EA7\u751F\u666E\u904D\u6027\u7684\u5224\u65AD );</li><li>\u82CF\u683C\u62C9\u5E95\u5982\u52A0\u91CC\u4E9A\u822C\u4F53\u8D28, \u56E0\u6B64\u82CF\u683C\u62C9\u5E95\u4F1A\u56E0\u751F\u75C5\u800C\u53D7\u76CA ( \u6280\u672F\u4E4B\u5E94\u7528 );</li></ul><h2 id="\u6280\u672F\u4F55\u4EE5\u662F\u667A\u6167" tabindex="-1"><a class="header-anchor" href="#\u6280\u672F\u4F55\u4EE5\u662F\u667A\u6167" aria-hidden="true">#</a> \u6280\u672F\u4F55\u4EE5\u662F\u667A\u6167</h2><p>\u4ECE\u5B9E\u8DF5\u89D2\u5EA6\u7ECF\u9A8C\u66F4\u6709\u7528, \u56E0\u4E3A\u6240\u6709\u7684\u533B\u751F\u90FD\u533B\u6CBB\u7684\u662F\u5177\u4F53\u7684\u4EBA, \u800C\u975E\u62BD\u8C61\u7684 &quot;\u4EBA&quot; \u7C7B. \u90A3\u6280\u672F\u4F55\u4EE5\u9AD8\u4E8E\u7ECF\u9A8C\u5462?</p><p>\u56E0\u4E3A\u6280\u672F\u53EF\u4EE5\u8BA9\u4EBA\u77E5\u5176\u7136\u800C\u77E5\u5176\u6240\u4EE5\u7136, \u4E5F\u65E2 &quot;\u5177\u6709\u7406\u8BBA, \u61C2\u5F97\u539F\u56E0&quot;. \u800C\u77E5\u5176\u6240\u4EE5\u7136\u8005\u80FD\u6559\u6388\u4ED6\u4EBA. \u56E0\u6B64\u6280\u672F\u624D\u662F\u771F\u77E5\u53EA, \u56E0\u4E3A\u6280\u672F\u5BB6\u80FD\u6559\u4EBA\u800C\u53EA\u51ED\u7ECF\u9A8C\u5219\u4E0D\u80FD.</p><p>\u6211\u4EEC\u4E0D\u4EE5\u611F\u89C9\u4E3A\u667A\u6167, \u4EA6\u662F\u56E0\u4E3A\u611F\u89C9\u4E0D\u77E5\u5176\u6240\u4EE5\u7136.</p><p>\u90A3\u4F55\u79CD\u6280\u672F\u6700\u6709\u667A\u6167\u5462?</p><ul><li>\u6709\u5B9E\u7528\u4EF7\u503C\u7684\u6280\u672F\u6700\u4F4E;</li><li>\u5A31\u4E50\u4EBA\u7C7B\u7684\u6280\u672F\u6B21\u4E4B</li><li>\u4E0D\u5B9E\u7528\u4E5F\u4E0D\u4E3A\u4E86\u4EBA\u7C7B\u5FEB\u4E50\u7684\u6280\u672F\u6700\u6709\u667A\u6167.</li></ul><p>\u4E0D\u5B9E\u7528\u4E5F\u4E0D\u4E3A\u4E86\u4EBA\u7C7B\u5FEB\u4E50\u7684\u6280\u672F\u8D77\u6E90\u4E8E\u95F2\u7771. \u5982\u6570\u5B66\u8D77\u6E90\u4E8E\u57C3\u53CA\u50E7\u5415\u4E4B\u95F2\u7771.</p><p>\u800C\u6700\u667A\u6167\u7684\u662F &quot;\u5927\u5BB6\u7528\u6765\u9610\u91CA\u4E8B\u7269\u7684\u539F\u56E0\u4E0E\u539F\u7406&quot; \u7684\u77E5\u8BC6.</p><h2 id="\u54F2\u4EBA" tabindex="-1"><a class="header-anchor" href="#\u54F2\u4EBA" aria-hidden="true">#</a> \u54F2\u4EBA</h2><p>\u54F2\u4EBA\u5373\u4E3A\u7231\u667A\u8005, ( \u53E4\u5E0C\u814A\u8BED\u4E2D\u54F2\u5B66\u4E00\u8BCD\u7684\u610F\u601D\u662F\u7231\u597D\u667A\u6167 ) \u6211\u4EEC\u901A\u5E38\u8BA4\u4E3A\u4ED6\u4EEC:</p><ul><li>\u77E5\u9053\u4E00\u5207\u53EF\u77E5\u7684\u4E8B\u7269 ( \u4E0D\u5FC5\u7EC6\u8282 ), \u56E0\u6B64\u4ED6\u4EEC\u8FFD\u6C42\u6700\u666E\u904D\u7684\u77E5\u8BC6</li><li>\u77E5\u9053\u4F17\u4EBA\u96BE\u77E5\u7684\u4E8B\u7269, \u56E0\u6B64\u4ED6\u4EEC\u8FFD\u6C42\u79BB\u611F\u89C9\u6700\u8FDC\u7684\u77E5\u8BC6;</li><li>\u66F4\u771F\u5207\u7684\u6559\u6388\u5404\u95E8\u77E5\u8BC6\u7684\u539F\u56E0, \u56E0\u6B64\u4ED6\u4EEC\u8FFD\u6C42\u7CBE\u786E\u7684\u77E5\u8BC6, \u800C\u5305\u6DB5\u539F\u7406\u6108\u5C11\u7684\u77E5\u8BC6\u6108\u7CBE\u786E ( \u5982\u7B97\u672F );</li><li>\u4E3A\u5B66\u672F\u672C\u8EAB\u800C\u63A2\u6C42\u7684\u77E5\u8BC6, \u800C\u975E\u4E3A\u4E86\u5B9E\u7528\u63A2\u6C42\u77E5\u8BC6, \u56E0\u6B64\u4ED6\u4EEC\u8FFD\u6C42\u6700\u53EF\u77E5\u7684\u77E5\u8BC6: \u539F\u56E0\u548C\u539F\u7406\u6700\u53EF\u77E5\u7684, \u800C\u77E5\u9053\u539F\u56E0\u548C\u539F\u7406 &quot;\u5176\u5B83\u4E00\u5207\u53EF\u5F97\u660E\u767D&quot;;</li><li>\u63A2\u6C42\u9AD8\u7EA7\u5B66\u672F, \u56E0\u6B64\u4ED6\u4EEC\u8FFD\u6C42\u539F\u56E0\u7684\u539F\u56E0, \u4EE5\u81F3\u4E8E\u7EC8\u7EA7\u76EE\u7684, \u8FD9\u4E2A\u7EC8\u6781\u76EE\u7684\u5728\u4E2A\u522B\u4E8B\u7269\u53EF\u79F0\u4E3A &quot;\u672C\u5584&quot;, \u5728\u5168\u4F53\u53EF\u79F0\u4E3A &quot;\u81F3\u5584&quot;;</li><li>\u6700\u540E, \u54F2\u4EBA\u5E94\u8BE5\u65BD\u4E3A, \u4E0D\u5E94\u88AB\u65BD\u4E3A, \u4ED6\u4EBA\u5E94\u5F53\u542C\u4ECE\u54F2\u4EBA, \u800C\u54F2\u4EBA\u4E0D\u5E94\u542C\u4ECE\u4ED6\u4EBA</li></ul><p>\u6240\u4EE5\u54F2\u4EBA\u7814\u7A76\u7684\u8BFE\u9898\u5F53: \u539F\u7406\u548C\u539F\u56E0; &quot;\u5584&quot; \u4E0E &quot;\u7EC8\u6781&quot;.</p><h2 id="\u60CA\u5F02\u4E0E\u81EA\u7531" tabindex="-1"><a class="header-anchor" href="#\u60CA\u5F02\u4E0E\u81EA\u7531" aria-hidden="true">#</a> \u60CA\u5F02\u4E0E\u81EA\u7531</h2><p>\u7231\u597D\u667A\u6167 ( \u54F2\u5B66 ) \u8D77\u4E8E\u60CA\u5F02, \u4EBA\u56E0\u60CA\u5F02\u800C\u81EA\u6127\u611A\u8822, \u4E3A\u8131\u51FA\u611A\u8822\u800C\u8FFD\u6C42\u54F2\u7406.</p><p>&quot;\u8FD9\u7C7B\u5B66\u672F\u7684\u7814\u7A76\u5FC5\u7136\u5F00\u59CB, \u90FD\u5728\u4EBA\u751F\u7684\u5FC5\u9700\u54C1\u4EE5\u53CA\u4F7F\u4EBA\u5FEB\u4E50\u5B89\u9002\u7684\u79CD\u79CD\u4E8B\u7269\u51E0\u4E4E\u83B7\u5F97\u540E&quot;.</p><p>&quot;\u56E0\u4EBA\u672C\u81EA\u7531, \u4E3A\u81EA\u5DF1\u751F\u5B58\u800C\u751F\u5B58. \u6240\u4EE5\u6211\u4EEC\u8BA4\u53D6\u54F2\u5B66\u662F\u552F\u4E00\u7684\u81EA\u7531\u5B66\u672F\u800C\u6DF1\u52A0\u63A2\u7D22, \u8FD9\u6B63\u662F\u5B66\u672F\u81EA\u8EAB\u800C\u6210\u7ACB\u7684\u552F\u4E00\u5B66\u672F.&quot;</p><p>\u6240\u4EE5\u4E4B\u6240\u4EE5\u5373\u4E0D\u4E3A\u5B9E\u7528\u4E5F\u4E0D\u4E3A\u5A31\u4E50\u7684\u6280\u672F\u624D\u662F\u6700\u667A\u6167\u7684, \u662F\u56E0\u4E3A\u53EA\u6709\u4E3A\u4E86\u81EA\u5DF1\u7684\u5B66\u672F\u624D\u662F\u81EA\u7531\u7684\u5B66\u672F.</p><h2 id="\u795E\u4E4B\u5B66\u672F" tabindex="-1"><a class="header-anchor" href="#\u795E\u4E4B\u5B66\u672F" aria-hidden="true">#</a> \u795E\u4E4B\u5B66\u672F</h2><p>\u54F2\u5B66\u662F\u795E\u5723\u7684\u5B66\u672F: \u4EFB\u4F55\u8BA8\u8BBA\u795E\u5723\u4E8B\u7269\u7684\u5B66\u672F\u4E5F\u5FC5\u662F\u795E\u5723\u7684</p><ul><li><ol><li>\u795E\u88AB\u8BA4\u4E3A\u662F\u4E07\u7269\u7684\u539F\u56E0: \u54F2\u5B66\u7814\u7A76\u7B2C\u4E00\u539F\u7406;</li></ol></li><li><ol start="2"><li>\u8FD9\u95E8\u5B66\u672F\u662F\u795E\u6240\u72EC\u6709; \u6216\u795E\u80FD\u8D85\u4E4E\u4EBA\u7C7B\u800C\u6240\u77E5\u72EC\u591A.</li></ol></li></ul><p>\u4F55\u8C13\u795E\u80FD\u8D85\u4E4E\u4EBA\u7C7B\u800C\u6240\u77E5\u72EC\u591A? \u5982\u4E07\u8C61\u5982\u5080\u5121\u81EA\u884C; \u5982 &quot;\u6B63\u65B9\u5F62\u7684\u5BF9\u89D2\u7EBF\u4E0D\u80FD\u7528\u8FB9\u6765\u8BA1\u91CF&quot;.</p><p>\u5C24\u5176\u7B2C\u4E8C\u70B9: &quot;\u8BF4\u662F\u4E16\u4E0A\u6709\u4E00\u4E8B\u7269, \u5373\u4F7F\u5F15\u7528\u6700\u5C0F\u7684\u5355\u4F4D\u5B66\u662F\u4E0D\u80FD\u52A0\u4EE5\u8BA1\u91CF. \u8FD9\u5BF9\u4E8E\u6240\u6709\u793A\u660E\u5176\u6545\u7684\u4EBA\u6B63\u662F\u53EF\u60CA\u5F02\u7684.&quot;</p><p>\u4EBA\u56E0\u77E5\u5176\u6545\u800C\u4E0D\u4E3A\u4E8B\u7269\u6240\u60D1, \u800C\u5BF9\u4E8E\u4E00\u4E2A\u51E0\u4F55\u5B66\u8005\u6765\u8BF4, \u5982\u679C\u5BF9\u89D2\u7EBF\u6210\u4E3A\u53EF\u8BA1\u91CF\u7684, \u90A3\u624D\u662F\u4E16\u95F4\u602A\u4E8B.</p>',34),s=h("\u6BD5\u8FBE\u54E5\u62C9\u65AF\u7684\u65E0\u7406\u6570"),c=e("hr",null,null,-1),q=e("p",null,"\u4EE5\u4E0A\u4FBF\u662F\u4E9A\u91CC\u58EB\u591A\u5FB7\u6C42\u77E5\u7684\u9636\u68AF: \u7531\u611F\u89C9\u800C\u81F3\u4E8E\u795E\u5723.",-1);function _(f,x){const t=i("RouterLink");return l(),o("div",null,[n,e("p",null,[r(t,{to:"/write/%E6%AF%95%E8%BE%BE%E5%93%A5%E6%8B%89%E6%96%AF%E7%9A%84%E6%97%A0%E7%90%86%E6%95%B0.html"},{default:u(()=>[s]),_:1})]),c,q])}var m=a(d,[["render",_],["__file","\u6C42\u77E5\u7684\u9636\u68AF.html.vue"]]);export{m as default};
