import{_ as p,o as n,c as r,e as t}from"./app.efeddd24.js";const o={},s=t(`<h1 id="\u6CA1\u6709\u94F6\u5F39-\u8F6F\u4EF6\u5DE5\u7A0B\u4E2D\u7684\u6839\u672C\u548C\u6B21\u8981\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u6CA1\u6709\u94F6\u5F39-\u8F6F\u4EF6\u5DE5\u7A0B\u4E2D\u7684\u6839\u672C\u548C\u6B21\u8981\u95EE\u9898" aria-hidden="true">#</a> \u6CA1\u6709\u94F6\u5F39-\u8F6F\u4EF6\u5DE5\u7A0B\u4E2D\u7684\u6839\u672C\u548C\u6B21\u8981\u95EE\u9898</h1><p>(No Silver Bullet \u2013 Essence and Accident in Software Engineering)</p><p>\u6CA1\u6709\u4EFB\u4F55\u6280\u672F\u6216\u7BA1\u7406\u4E0A\u7684\u8FDB\u5C55,\u80FD\u591F\u72EC\u7ACB\u5730\u8BB8\u8BFA\u5341\u5E74\u5185\u4F7F\u751F\u4EA7\u7387\u3001\u53EF\u9760\u6027\u6216\u7B80\u6D01\u6027\u83B7\u5F97\u6570\u91CF\u7EA7\u4E0A\u7684\u8FDB\u6B65\u3002</p><p>There is no single development, in either technology or management technique, which by itself promises even one order-of-magnitude improvement within a decade in productivity, in reliability, in simplicity.</p><p>\u6458\u8981 1</p><p>\u6240\u6709\u8F6F\u4EF6\u6D3B\u52A8\u5305\u62EC\u6839\u672C\u4EFB\u52A1\u2014\u2014\u6253\u9020\u7531\u62BD\u8C61\u8F6F\u4EF6\u5B9E\u4F53\u6784\u6210\u7684\u590D\u6742\u6982\u5FF5\u7ED3\u6784,\u6B21\u8981\u4EFB\u52A1\u2014\u2014\u4F7F\u7528\u7F16\u7A0B\u8BED\u8A00\u8868\u8FBE\u8FD9\u4E9B\u62BD\u8C61\u5B9E\u4F53,\u5728\u7A7A\u95F4\u548C\u65F6\u95F4\u9650\u5236\u5185\u5C06\u5B83\u4EEC\u6620\u5C04\u6210\u673A\u5668\u8BED\u8A00\u3002\u8F6F\u4EF6\u751F\u4EA7\u7387\u5728\u8FD1\u5E74\u5185\u53D6\u5F97\u7684\u5DE8\u5927\u8FDB\u6B65\u6765\u81EA\u5BF9\u540E\u5929\u969C\u788D\u7684\u7A81\u7834,\u4F8B\u5982\u786C\u4EF6\u7684\u9650\u5236\u3001\u7B28\u62D9\u7684\u7F16\u7A0B\u8BED\u8A00\u3001\u673A\u5668\u65F6\u95F4\u7684\u7F3A\u4E4F\u7B49\u7B49\u3002\u8FD9\u4E9B\u969C\u788D\u4F7F\u6B21\u8981\u4EFB\u52A1\u5B9E\u65BD\u8D77\u6765\u5F02\u5E38\u8270\u96BE,\u76F8\u5BF9\u5FC5\u8981\u4EFB\u52A1\u800C\u8A00,\u8F6F\u4EF6\u5DE5\u7A0B\u5E08\u5728\u6B21\u8981\u4EFB\u52A1\u4E0A\u82B1\u8D39\u4E86\u591A\u5C11\u65F6\u95F4\u548C\u7CBE\u529B?\u9664\u975E\u5B83\u5360\u4E86\u6240\u6709\u5DE5\u4F5C\u7684 9/10,\u5426\u5219\u5373\u4F7F\u5168\u90E8\u6B21\u8981\u4EFB\u52A1\u7684\u65F6\u95F4\u7F29\u51CF\u5230\u96F6,\u4E5F\u4E0D\u4F1A\u7ED9\u751F\u4EA7\u7387\u5E26\u6765\u6570\u91CF\u7EA7\u4E0A\u7684\u63D0\u9AD8\u3002</p><p>\u56E0\u6B64,\u73B0\u5728\u662F\u5173\u6CE8\u8F6F\u4EF6\u4EFB\u52A1\u4E2D\u7684\u5FC5\u8981\u6D3B\u52A8\u7684\u65F6\u5019\u4E86,\u4E5F\u5C31\u662F\u90A3\u4E9B\u548C\u6784\u9020\u5F02\u5E38\u590D\u6742\u7684\u62BD\u8C61\u6982\u5FF5\u7ED3\u6784\u6709\u5173\u7684\u90E8\u5206\u3002\u6211\u5EFA\u8BAE:</p><p>*\u4ED4\u7EC6\u5730\u8FDB\u884C\u5E02\u573A\u8C03\u7814,\u907F\u514D\u5F00\u53D1\u5DF2\u4E0A\u5E02\u7684\u4EA7\u54C1\u3002 *\u5728\u83B7\u53D6\u548C\u5236\u8BA2\u8F6F\u4EF6\u9700\u6C42\u65F6,\u5C06\u5FEB\u901F\u539F\u578B\u5F00\u53D1\u4F5C\u4E3A\u8FED\u4EE3\u8BA1\u5212\u7684\u4E00\u90E8\u5206\u3002 *\u6709\u673A\u5730\u66F4\u65B0\u8F6F\u4EF6,\u968F\u7740\u7CFB\u7EDF\u7684\u8FD0\u884C\u3001\u4F7F\u7528\u548C\u6D4B\u8BD5,\u9010\u6E10\u6DFB\u52A0\u8D8A\u6765\u8D8A\u591A\u7684\u529F\u80FD\u3002 *\u4E0D\u65AD\u6311\u9009\u548C\u57F9\u517B\u6770\u51FA\u7684\u6982\u5FF5\u8BBE\u8BA1\u4EBA\u5458\u3002</p><h2 id="\u4ECB\u7ECD" tabindex="-1"><a class="header-anchor" href="#\u4ECB\u7ECD" aria-hidden="true">#</a> \u4ECB\u7ECD</h2><p>\u5728\u6240\u6709\u6050\u6016\u6C11\u95F4\u4F20\u8BF4\u7684\u5996\u602A\u4E2D,\u6700\u53EF\u6015\u7684\u662F\u4EBA\u72FC,\u56E0\u4E3A\u5B83\u4EEC\u53EF\u4EE5\u5B8C\u5168\u51FA\u4E4E\u610F\u6599\u5730\u4ECE\u719F\u6089\u7684\u9762\u5B54\u53D8\u6210\u53EF\u6015\u7684\u602A\u7269\u3002\u4E3A\u4E86\u5BF9\u4ED8\u4EBA\u72FC,\u6211\u4EEC\u5728\u5BFB\u627E\u53EF\u4EE5\u6D88\u706D\u5B83\u4EEC\u7684\u94F6\u5F39\u3002</p><p>\u5927\u5BB6\u719F\u6089\u7684\u8F6F\u4EF6\u9879\u76EE\u5177\u6709\u4E00\u4E9B\u4EBA\u72FC\u7684\u7279\u6027(\u81F3\u5C11\u5728\u975E\u6280\u672F\u7ECF\u7406\u770B\u6765),\u5E38\u5E38\u770B\u4F3C\u7B80\u5355\u660E\u4E86\u7684\u4E1C\u897F,\u5374\u6709\u53EF\u80FD\u53D8\u6210\u4E00\u4E2A\u843D\u540E\u8FDB\u5EA6\u3001\u8D85\u51FA\u9884\u7B97\u3001\u5B58\u5728\u5927\u91CF\u7F3A\u9677\u7684\u602A\u7269\u3002\u56E0\u6B64,\u6211\u4EEC\u542C\u5230\u4E86\u8FD1\u4E4E\u7EDD\u671B\u7684\u5BFB\u6C42\u94F6\u5F39\u7684\u547C\u5524,\u5BFB\u6C42\u4E00\u79CD\u53EF\u4EE5\u4F7F\u8F6F\u4EF6\u6210\u672C\u50CF\u8BA1\u7B97\u673A\u786C\u4EF6\u6210\u672C\u4E00\u6837\u964D\u4F4E\u7684\u5C1A\u65B9\u5B9D\u5251\u3002</p><p>\u4F46\u662F,\u6211\u4EEC\u770B\u770B\u8FD1\u5341\u5E74\u6765\u7684\u60C5\u51B5,\u6CA1\u6709\u94F6\u5F39\u7684\u8E2A\u8FF9\u3002\u6CA1\u6709\u4EFB\u4F55\u6280\u672F\u6216\u7BA1\u7406\u4E0A\u7684\u8FDB\u5C55,\u80FD\u591F\u72EC\u7ACB\u5730\u8BB8\u8BFA\u5728\u751F\u4EA7\u7387\u3001\u53EF\u9760\u6027\u6216\u7B80\u6D01\u6027\u4E0A\u53D6\u5F97\u6570\u91CF\u7EA7\u7684\u63D0\u9AD8\u3002\u672C\u7AE0\u4E2D,\u6211\u4EEC\u8BD5\u56FE\u901A\u8FC7\u5206\u6790\u8F6F\u4EF6\u95EE\u9898\u7684\u672C\u8D28\u548C\u5F88\u591A\u5019\u9009\u94F6\u5F39\u7684\u7279\u5F81,\u6765\u63A2\u7D22\u5176\u539F\u56E0\u3002</p><p>\u4E0D\u8FC7,\u6000\u7591\u8BBA\u8005\u5E76\u4E0D\u662F\u60B2\u89C2\u4E3B\u4E49\u8005\u3002\u5C3D\u7BA1\u6211\u4EEC\u6CA1\u6709\u770B\u89C1\u4EE4\u4EBA\u60CA\u5F02\u7684\u7A81\u7834,\u5E76\u8BA4\u4E3A\u8FD9\u79CD\u94F6\u5F39\u5B9E\u9645\u4E0A\u662F\u4E0E\u8F6F\u4EF6\u7684\u5185\u5728\u7279\u6027\u76F8\u6096,\u4E0D\u8FC7\u8FD8\u662F\u51FA\u73B0\u4E86\u4E00\u4E9B\u4EE4\u4EBA\u632F\u594B\u7684\u9769\u65B0\u3002\u8FD9\u4E9B\u65B9\u6CD5\u7684\u89C4\u8303\u5316\u3001\u6301\u7EED\u5730\u5F00\u62D3\u3001\u53D1\u5C55\u548C\u4F20\u64AD\u786E\u5B9E\u662F\u53EF\u4EE5\u5728\u5C06\u6765\u4F7F\u751F\u4EA7\u7387\u4EA7\u751F\u6570\u91CF\u7EA7\u4E0A\u7684\u63D0\u9AD8\u3002\u867D\u7136\u6CA1\u6709\u901A\u5929\u5927\u9053,\u4F46\u662F\u8DEF\u5C31\u5728\u811A\u4E0B\u3002</p><p>\u89E3\u51B3\u7BA1\u7406\u707E\u96BE\u7684\u7B2C\u4E00\u6B65\u662F\u5C06\u5927\u5757\u7684\u201C\u5DE8\u65E0\u9738\u7406\u8BBA\u201D\u66FF\u6362\u6210\u201C\u5FAE\u751F\u7269\u7406\u8BBA\u201D,\u5B83\u7684\u6BCF\u4E00\u6B65\u2014\u2014\u5E0C\u671B\u7684\u8BDE\u751F,\u672C\u8EAB\u5C31\u662F\u5BF9\u4E00\u8E74\u800C\u5C31\u578B\u89E3\u51B3\u65B9\u6848\u7684\u51B2\u51FB\u3002\u5B83\u544A\u8BC9\u5DE5\u4F5C\u8005\u8FDB\u6B65\u662F\u9010\u6B65\u53D6\u5F97\u7684,\u4F34\u968F\u7740\u8F9B\u52E4\u7684\u52B3\u52A8,\u5BF9\u89C4\u8303\u5316\u8FC7\u7A0B\u5E94\u8FDB\u884C\u6301\u7EED\u4E0D\u61C8\u7684\u52AA\u529B\u3002\u7531\u6B64,\u8BDE\u751F\u4E86\u73B0\u5728\u7684\u8F6F\u4EF6\u5DE5\u7A0B\u3002</p><h2 id="\u662F\u5426\u4E00\u5B9A\u90A3\u4E48\u56F0\u96BE\u5462-\u2014\u2014\u6839\u672C\u56F0\u96BE" tabindex="-1"><a class="header-anchor" href="#\u662F\u5426\u4E00\u5B9A\u90A3\u4E48\u56F0\u96BE\u5462-\u2014\u2014\u6839\u672C\u56F0\u96BE" aria-hidden="true">#</a> \u662F\u5426\u4E00\u5B9A\u90A3\u4E48\u56F0\u96BE\u5462?\u2014\u2014\u6839\u672C\u56F0\u96BE</h2><p>\u4E0D\u4EC5\u4EC5\u662F\u5728\u76EE\u529B\u6240\u53CA\u7684\u8303\u56F4\u5185,\u6CA1\u6709\u53D1\u73B0\u94F6\u5F39,\u800C\u4E14\u8F6F\u4EF6\u7684\u7279\u6027\u672C\u8EAB\u4E5F\u5BFC\u81F4\u4E86\u4E0D\u5927\u53EF\u80FD\u6709\u4EFB\u4F55\u7684\u53D1\u660E\u521B\u65B0\u2014\u2014\u80FD\u591F\u50CF\u8BA1\u7B97\u673A\u786C\u4EF6\u5DE5\u4E1A\u4E2D\u7684\u5FAE\u7535\u5B50\u5668\u4EF6\u3001\u6676\u4F53\u7BA1\u3001\u5927\u89C4\u6A21\u96C6\u6210\u4E00\u6837\u2014\u2014\u63D0\u9AD8\u8F6F\u4EF6\u7684\u751F\u4EA7\u7387\u3001\u53EF\u9760\u6027\u548C\u7B80\u6D01\u7A0B\u5EA6\u3002\u6211\u4EEC\u751A\u81F3\u4E0D\u80FD\u671F\u671B\u6BCF\u4E24\u5E74\u6709\u4E00\u500D\u7684\u589E\u957F\u3002</p><p>\u9996\u5148,\u6211\u4EEC\u5FC5\u987B\u770B\u5230\u8FD9\u6837\u7684\u7578\u5F62\u5E76\u4E0D\u662F\u7531\u4E8E\u8F6F\u4EF6\u53D1\u5C55\u5F97\u592A\u6162,\u800C\u662F\u56E0\u4E3A\u8BA1\u7B97\u673A\u786C\u4EF6\u53D1\u5C55\u5F97\u592A\u5FEB\u3002\u4ECE\u4EBA\u7C7B\u6587\u660E\u5F00\u59CB,\u6CA1\u6709\u4EFB\u4F55\u5176\u4ED6\u4EA7\u4E1A\u6280\u672F\u7684\u6027\u4EF7\u6BD4,\u80FD\u5728 30 \u5E74\u4E4B\u5185\u53D6\u5F97 6 \u4E2A\u6570\u91CF\u7EA7\u7684\u63D0\u9AD8,\u4E5F\u6CA1\u6709\u4EFB\u4F55\u4E00\u4E2A\u4EA7\u4E1A\u53EF\u4EE5\u5728\u6027\u80FD\u63D0\u9AD8\u6216\u8005\u964D\u4F4E\u6210\u672C\u65B9\u9762\u53D6\u5F97\u5982\u6B64\u7684\u8FDB\u6B65\u3002\u8FD9\u4E9B\u8FDB\u6B65\u6765\u81EA\u8BA1\u7B97\u673A\u5236\u9020\u4EA7\u4E1A\u7684\u8F6C\u53D8,\u4ECE\u88C5\u914D\u5DE5\u4E1A\u8F6C\u53D8\u6210\u6D41\u6C34\u7EBF\u5DE5\u4E1A\u3002</p><p>\u5176\u6B21,\u8BA9\u6211\u4EEC\u901A\u8FC7\u89C2\u5BDF\u9884\u671F\u7684\u8F6F\u4EF6\u6280\u672F\u4EA7\u4E1A\u53D1\u5C55\u901F\u5EA6,\u6765\u4E86\u89E3\u4E2D\u95F4\u7684\u56F0\u96BE\u3002\u6548\u4EFF\u4E9A\u91CC\u58EB\u591A\u5FB7,\u6211\u5C06\u5B83\u4EEC\u5206\u6210 \u6839\u672C\u7684 \u2014\u2014\u8F6F\u4EF6\u7279\u6027\u4E2D\u56FA\u6709\u7684\u56F0\u96BE, \u6B21\u8981\u7684 \u2014\u2014\u51FA\u73B0\u5728\u76EE\u524D\u751F\u4EA7\u4E0A\u7684,\u4F46\u5E76\u975E\u90A3\u4E9B\u4E0E\u751F\u4FF1\u6765\u7684\u56F0\u96BE\u3002</p><p>\u6211\u4EEC\u5728\u4E0B\u4E00\u7AE0\u4E2D\u8BA8\u8BBA\u6B21\u8981\u95EE\u9898\u3002\u9996\u5148,\u6765\u5173\u6CE8\u5185\u5728\u3001\u5FC5\u8981\u7684\u95EE\u9898\u3002\u4E00\u4E2A\u76F8\u4E92\u7275\u5236\u5173\u8054\u7684\u6982\u5FF5\u7ED3\u6784,\u662F\u8F6F\u4EF6\u5B9E\u4F53\u5FC5\u4E0D\u53EF\u5C11\u7684\u90E8\u5206,\u5B83\u5305\u62EC:\u6570\u636E\u96C6\u5408\u3001\u6570\u636E\u6761\u76EE\u4E4B\u95F4\u7684\u5173\u7CFB\u3001\u7B97\u6CD5\u3001\u529F\u80FD\u8C03\u7528\u7B49\u7B49\u3002\u8FD9\u4E9B\u8981\u7D20\u672C\u8EAB\u662F\u62BD\u8C61\u7684,\u4F53\u73B0\u5728\u76F8\u540C\u7684\u6982\u5FF5\u6784\u67B6\u4E2D,\u53EF\u4EE5\u5B58\u5728\u4E0D\u540C\u7684\u8868\u73B0\u5F62\u5F0F\u3002\u5C3D\u7BA1\u5982\u6B64,\u5B83\u4ECD\u7136\u662F\u5185\u5BB9\u4E30\u5BCC\u548C\u9AD8\u5EA6\u7CBE\u786E\u7684\u3002</p><p>\u6211\u8BA4\u4E3A\u8F6F\u4EF6\u5F00\u53D1\u4E2D\u56F0\u96BE\u7684\u90E8\u5206\u662F\u89C4\u683C\u5316\u3001\u8BBE\u8BA1\u548C\u6D4B\u8BD5\u8FD9\u4E9B\u6982\u5FF5\u4E0A\u7684\u7ED3\u6784,\u800C\u4E0D\u662F\u5BF9\u6982\u5FF5\u8FDB\u884C\u8868\u8FBE\u548C\u5BF9\u5B9E\u73B0\u903C\u771F\u7A0B\u5EA6\u8FDB\u884C\u9A8C\u8BC1\u3002 \u5F53\u7136,\u6211\u4EEC\u8FD8\u662F\u4F1A\u72AF\u4E00\u4E9B\u8BED\u6CD5\u9519\u8BEF,\u4F46\u662F\u548C\u7EDD\u5927\u591A\u6570\u7CFB\u7EDF\u4E2D\u7684\u6982\u5FF5\u9519\u8BEF\u76F8\u6BD4,\u5B83\u4EEC\u662F\u5FAE\u4E0D\u8DB3\u9053\u7684\u3002</p><p><strong>\u5982\u679C\u8FD9\u662F\u4E8B\u5B9E,\u90A3\u4E48\u8F6F\u4EF6\u5F00\u53D1\u603B\u662F\u975E\u5E38\u56F0\u96BE\u7684\u3002\u5929\u751F\u5C31\u6CA1\u6709\u94F6\u5F39\u3002</strong></p><p>\u8BA9\u6211\u4EEC\u6765\u8003\u8651\u73B0\u4EE3\u8F6F\u4EF6\u7CFB\u7EDF\u4E2D\u8FD9\u4E9B\u65E0\u6CD5\u89C4\u907F\u7684\u5185\u5728\u7279\u6027: <strong>\u590D\u6742\u5EA6\u3001\u4E00\u81F4\u6027\u3001\u53EF\u53D8\u6027\u548C\u4E0D\u53EF\u89C1\u6027</strong> \u3002</p><p><strong>\u590D\u6742\u5EA6</strong> \u89C4\u6A21\u4E0A,\u8F6F\u4EF6\u5B9E\u4F53\u53EF\u80FD\u6BD4\u4EFB\u4F55\u7531\u4EBA\u7C7B\u521B\u9020\u7684\u5176\u4ED6\u5B9E\u4F53\u8981\u590D\u6742,\u56E0\u4E3A\u6CA1\u6709\u4EFB\u4F55\u4E24\u4E2A\u8F6F\u4EF6\u90E8\u5206\u662F\u76F8\u540C\u7684(\u81F3\u5C11\u662F\u5728\u8BED\u53E5\u7684\u7EA7\u522B)\u3002\u5982\u679C\u6709\u76F8\u540C\u7684\u60C5\u51B5,\u6211\u4EEC\u4F1A\u628A\u5B83\u4EEC\u5408\u5E76\u6210\u4F9B\u8C03\u7528\u7684\u5B50\u51FD\u6570\u3002\u5728\u8FD9\u4E2A\u65B9\u9762,\u8F6F\u4EF6\u7CFB\u7EDF\u4E0E\u8BA1\u7B97\u673A\u3001\u5EFA\u7B51\u6216\u8005\u6C7D\u8F66\u5927\u4E0D\u76F8\u540C,\u540E\u8005\u5F80\u5F80\u5B58\u5728\u7740\u5927\u91CF\u91CD\u590D\u7684\u90E8\u5206\u3002</p><p><strong>\u6570\u5B57\u8BA1\u7B97\u673A\u672C\u8EAB\u5C31\u6BD4\u4EBA\u7C7B\u5EFA\u9020\u7684\u5927\u591A\u6570\u4E1C\u897F\u590D\u6742\u3002</strong> \u8BA1\u7B97\u673A\u62E5\u6709\u5927\u91CF\u7684\u72B6\u6001,\u8FD9\u4F7F\u5F97\u6784\u601D\u3001\u63CF\u8FF0\u548C\u6D4B\u8BD5\u90FD\u975E\u5E38\u56F0\u96BE\u3002\u8F6F\u4EF6\u7CFB\u7EDF\u7684\u72B6\u6001\u53C8\u6BD4\u8BA1\u7B97\u673A\u7CFB\u7EDF\u72B6\u6001\u591A\u82E5\u5E72\u4E2A\u6570\u91CF\u7EA7\u3002</p><p>\u540C\u6837,\u8F6F\u4EF6\u5B9E\u4F53\u7684\u6269\u5C55\u4E5F\u4E0D\u4EC5\u4EC5\u662F\u76F8\u540C\u5143\u7D20\u91CD\u590D\u6DFB\u52A0,\u800C\u5FC5\u987B\u662F\u4E0D\u540C\u5143\u7D20\u5B9E\u4F53\u7684\u6DFB\u52A0\u3002\u5927\u591A\u6570\u60C5\u51B5\u4E0B,\u8FD9\u4E9B\u5143\u7D20\u4EE5\u975E\u7EBF\u6027\u9012\u589E\u7684\u65B9\u5F0F\u4EA4\u4E92,\u56E0\u6B64\u6574\u4E2A\u8F6F\u4EF6\u7684\u590D\u6742\u5EA6\u4EE5\u66F4\u5927\u7684 <strong>\u975E\u7EBF\u6027\u7EA7\u6570\u589E\u957F</strong>\u3002</p><p>\u8F6F\u4EF6\u7684\u590D\u6742\u5EA6\u662F <strong>\u5FC5\u8981\u5C5E\u6027</strong>,\u4E0D\u662F\u6B21\u8981\u56E0\u7D20\u3002\u56E0\u6B64,\u62BD\u6389\u590D\u6742\u5EA6\u7684\u8F6F\u4EF6\u5B9E\u4F53\u63CF\u8FF0\u5E38\u5E38\u4E5F\u53BB\u6389\u4E86\u4E00\u4E9B\u672C\u8D28\u5C5E\u6027\u3002\u6570\u5B66\u548C\u7269\u7406\u5B66\u5728\u8FC7\u53BB\u4E09\u4E2A\u4E16\u7EAA\u53D6\u5F97\u4E86\u5DE8\u5927\u7684\u8FDB\u6B65,\u6570\u5B66\u5BB6\u548C\u7269\u7406\u5B66\u5BB6\u4EEC\u5EFA\u7ACB\u6A21\u578B\u4EE5\u7B80\u5316\u590D\u6742\u7684\u73B0\u8C61,\u4ECE\u6A21\u578B\u4E2D\u62BD\u53D6\u51FA\u5404\u79CD\u7279\u6027,\u5E76\u901A\u8FC7\u8BD5\u9A8C\u6765\u9A8C\u8BC1\u8FD9\u4E9B\u7279\u6027\u3002\u8FD9\u4E9B\u65B9\u6CD5\u4E4B\u6240\u4EE5\u53EF\u884C\u2014\u2014\u662F\u56E0\u4E3A\u6A21\u578B\u4E2D\u5FFD\u7565\u7684\u590D\u6742\u5EA6\u4E0D\u662F\u88AB\u7814\u7A76\u73B0\u8C61\u7684\u5FC5\u8981\u5C5E\u6027\u3002\u5F53\u590D\u6742\u5EA6\u662F\u672C\u8D28\u7279\u6027\u65F6,\u8FD9\u4E9B\u65B9\u6CD5\u5C31\u884C\u4E0D\u901A\u4E86\u3002</p><p>\u4E0A\u8FF0\u8F6F\u4EF6\u7279\u6709\u7684\u590D\u6742\u5EA6\u95EE\u9898\u9020\u6210\u4E86\u5F88\u591A\u7ECF\u5178\u7684\u8F6F\u4EF6\u4EA7\u54C1\u5F00\u53D1\u95EE\u9898\u3002\u7531\u4E8E\u590D\u6742\u5EA6,\u56E2\u961F\u6210\u5458\u4E4B\u95F4\u7684\u6C9F\u901A\u975E\u5E38\u56F0\u96BE,\u5BFC\u81F4\u4E86\u4EA7\u54C1\u7455\u75B5\u3001\u6210\u672C\u8D85\u652F\u548C\u8FDB\u5EA6\u5EF6\u8FDF;\u7531\u4E8E\u590D\u6742\u5EA6,\u5217\u4E3E\u548C\u7406\u89E3\u6240\u6709\u53EF\u80FD\u7684\u72B6\u6001\u5341\u5206\u56F0\u96BE,\u5F71\u54CD\u4E86\u4EA7\u54C1\u7684\u53EF\u9760\u6027;\u7531\u4E8E\u51FD\u6570\u7684\u590D\u6742\u5EA6,\u51FD\u6570\u8C03\u7528\u53D8\u5F97\u56F0\u96BE,\u5BFC\u81F4\u7A0B\u5E8F\u96BE\u4EE5\u4F7F\u7528;\u7531\u4E8E\u7ED3\u6784\u6027\u590D\u6742\u5EA6,\u7A0B\u5E8F\u96BE\u4EE5\u5728\u4E0D\u4EA7\u751F\u526F\u4F5C\u7528\u7684\u60C5\u51B5\u4E0B\u7528\u65B0\u51FD\u6570\u6269\u5145;\u7531\u4E8E\u7ED3\u6784\u6027\u590D\u6742\u5EA6,\u9020\u6210\u5F88\u591A\u5B89\u5168\u673A\u5236\u72B6\u6001\u4E0A\u7684\u4E0D\u53EF\u89C1\u6027\u3002</p><p>\u590D\u6742\u5EA6\u4E0D\u4EC5\u4EC5\u5BFC\u81F4\u6280\u672F\u4E0A\u7684\u56F0\u96BE,\u8FD8\u5F15\u53D1\u4E86\u5F88\u591A\u7BA1\u7406\u4E0A\u7684\u95EE\u9898\u3002\u5B83\u4F7F\u5168\u9762\u7406\u89E3\u95EE\u9898\u53D8\u5F97\u56F0\u96BE,\u4ECE\u800C\u59A8\u788D\u4E86\u6982\u5FF5\u4E0A\u7684\u5B8C\u6574\u6027;\u5B83\u4F7F\u6240\u6709\u79BB\u6563\u51FA\u53E3\u96BE\u4EE5\u5BFB\u627E\u548C\u63A7\u5236; <strong>\u5B83\u5F15\u8D77\u4E86\u5927\u91CF\u5B66\u4E60\u548C\u7406\u89E3\u4E0A\u7684\u8D1F\u62C5,\u4F7F\u5F00\u53D1\u6162\u6162\u6F14\u53D8\u6210\u4E86\u4E00\u573A\u707E\u96BE</strong> \u3002</p><p><strong>\u4E00\u81F4\u6027</strong> \u5E76\u4E0D\u662F\u53EA\u6709\u8F6F\u4EF6\u5DE5\u7A0B\u5E08\u624D\u9762\u5BF9\u590D\u6742\u95EE\u9898\u3002\u7269\u7406\u5B66\u5BB6\u751A\u81F3\u5728\u975E\u5E38\u201C\u57FA\u7840\u201D\u7684\u7EA7\u522B\u4E0A,\u9762\u5BF9\u5F02\u5E38\u590D\u6742\u7684\u4E8B\u7269\u3002\u4E0D\u8FC7,\u7269\u7406\u5B66\u5BB6\u575A\u4FE1\u5FC5\u5B9A\u5B58\u5728\u7740\u67D0\u79CD\u901A\u7528\u539F\u7406,\u6216\u8005\u5728\u5938\u514B\u4E2D,\u6216\u8005\u5728\u7EDF\u4E00\u573A\u8BBA\u4E2D\u3002\u7231\u56E0\u65AF\u5766\u66FE\u4E0D\u65AD\u5730\u91CD\u7533\u81EA\u7136\u754C\u4E00\u5B9A\u5B58\u5728\u7740\u7B80\u5316\u7684\u89E3\u91CA,\u56E0\u4E3A\u4E0A\u5E1D\u4E0D\u662F\u4E13\u6A2A\u6B66\u65AD\u6216\u53CD\u590D\u65E0\u5E38\u7684\u3002</p><p>\u8F6F\u4EF6\u5DE5\u7A0B\u5E08\u5374\u65E0\u6CD5\u4ECE\u7C7B\u4F3C\u7684\u4FE1\u5FF5\u4E2D\u83B7\u5F97\u5B89\u6170,\u4ED6\u5FC5\u987B\u63A7\u5236\u7684\u5F88\u591A\u590D\u6742\u5EA6\u662F <strong>\u968F\u5FC3\u6240\u6B32\u3001\u6BEB\u65E0\u89C4\u5219</strong> \u53EF\u8A00\u7684,\u6765\u81EA\u82E5\u5E72\u5FC5\u987B\u9075\u5FAA\u7684 <strong>\u4EBA\u4E3A\u60EF\u4F8B\u548C\u7CFB\u7EDF</strong>\u3002\u5B83\u4EEC\u968F\u63A5\u53E3\u7684\u4E0D\u540C\u800C\u6539\u53D8,\u968F\u65F6\u95F4\u7684\u63A8\u79FB\u800C\u53D8\u5316,\u800C\u4E14,\u8FD9\u4E9B\u53D8\u5316\u4E0D\u662F\u5FC5\u9700\u7684,\u4EC5\u4EC5\u7531\u4E8E\u5B83\u4EEC\u662F\u4E0D\u540C\u7684\u4EBA\u2014\u2014\u800C\u975E\u4E0A\u5E1D\u2014\u2014\u8BBE\u8BA1\u7684\u7ED3\u679C\u3002</p><p>\u67D0\u4E9B\u60C5\u51B5\u4E0B,\u56E0\u4E3A\u662F\u5F00\u53D1\u6700\u65B0\u7684\u8F6F\u4EF6,\u6240\u4EE5\u5B83\u5FC5\u987B\u9075\u5FAA\u5404\u79CD\u63A5\u53E3\u3002\u53E6\u4E00\u4E9B\u60C5\u51B5\u4E0B,\u8F6F\u4EF6\u7684\u5F00\u53D1\u76EE\u6807\u5C31\u662F\u517C\u5BB9\u6027\u3002\u5728\u4E0A\u8FF0\u7684\u6240\u6709\u60C5\u51B5\u4E2D,\u5F88\u591A\u590D\u6742\u6027\u6765\u81EA\u4FDD\u6301\u4E0E\u5176\u4ED6\u63A5\u53E3\u7684\u4E00\u81F4,\u5BF9\u8F6F\u4EF6\u7684\u4EFB\u4F55\u518D\u8BBE\u8BA1,\u90FD\u65E0\u6CD5\u7B80\u5316\u8FD9\u4E9B\u590D\u6742\u7279\u6027\u3002</p><p><strong>\u53EF\u53D8\u6027</strong> \u8F6F\u4EF6\u5B9E\u4F53\u7ECF\u5E38\u4F1A\u906D\u53D7\u5230\u6301\u7EED\u7684\u53D8\u66F4\u538B\u529B\u3002\u5F53\u7136,\u5EFA\u7B51\u3001\u6C7D\u8F66\u3001\u8BA1\u7B97\u673A\u4E5F\u662F\u5982\u6B64\u3002\u4E0D\u8FC7,\u5DE5\u4E1A\u5236\u9020\u7684\u4EA7\u54C1\u5728\u51FA\u5382\u4E4B\u540E\u4E0D\u4F1A\u7ECF\u5E38\u5730\u53D1\u751F\u4FEE\u6539,\u5B83\u4EEC\u4F1A\u88AB\u540E\u7EED\u6A21\u578B\u6240\u53D6\u4EE3,\u6216\u8005\u5FC5\u8981\u66F4\u6539\u4F1A\u88AB\u6574\u5408\u5230\u5177\u6709\u76F8\u540C\u57FA\u672C\u8BBE\u8BA1\u7684\u540E\u7EED\u4EA7\u54C1\u7CFB\u5217\u3002\u6C7D\u8F66\u7684\u66F4\u6539\u5341\u5206\u7F55\u89C1,\u8BA1\u7B97\u673A\u7684</p><p>\u73B0\u573A\u8C03\u6574\u65F6\u6709\u53D1\u751F\u3002\u7136\u800C,\u5B83\u4EEC\u548C\u8F6F\u4EF6\u7684\u73B0\u573A\u4FEE\u6539\u6BD4\u8D77\u6765,\u90FD\u8981\u5C11\u5F88\u591A\u3002</p><p>\u5176\u4E2D\u90E8\u5206\u7684\u539F\u56E0\u662F\u56E0\u4E3A\u7CFB\u7EDF\u4E2D\u7684\u8F6F\u4EF6\u5305\u542B\u4E86\u5F88\u591A\u529F\u80FD,\u800C\u529F\u80FD\u662F\u6700\u5BB9\u6613\u611F\u53D7\u53D8\u66F4\u538B\u529B\u7684\u90E8\u5206\u3002\u53E6\u5916\u7684\u539F\u56E0\u662F\u56E0\u4E3A\u8F6F\u4EF6\u53EF\u4EE5\u5F88\u5BB9\u6613\u5730\u8FDB\u884C\u4FEE\u6539\u2014\u2014\u5B83\u662F\u7EAF\u7CB9\u601D\u7EF4\u6D3B\u52A8\u7684\u4EA7\u7269,\u53EF\u4EE5\u65E0\u9650\u6269\u5C55\u3002\u65E5\u5E38\u751F\u6D3B\u4E2D,\u5EFA\u7B51\u6709\u53EF\u80FD\u53D1\u751F\u53D8\u5316,\u4F46\u4F17\u6240\u5468\u77E5, <strong>\u5EFA\u7B51\u4FEE\u6539\u7684\u6210\u672C\u5F88\u9AD8,\u4ECE\u800C\u6253\u6D88\u4E86\u90A3\u4E9B\u60F3\u63D0\u51FA\u4FEE\u6539\u7684\u4EBA\u7684\u5FF5\u5934</strong> \u3002</p><p><strong>\u6240\u6709\u6210\u529F\u7684\u8F6F\u4EF6\u90FD\u4F1A\u53D1\u751F\u53D8\u66F4</strong>\u3002\u73B0\u5B9E\u5DE5\u4F5C\u4E2D,\u7ECF\u5E38\u53D1\u751F\u4E24\u79CD\u60C5\u51B5\u3002\u5F53\u4EBA\u4EEC\u53D1\u73B0\u8F6F\u4EF6\u5F88\u6709\u7528\u65F6,\u4F1A\u5728\u539F\u6709\u5E94\u7528\u8303\u56F4\u7684\u8FB9\u754C,\u6216\u8005\u5728\u8D85\u8D8A\u8FB9\u754C\u7684\u60C5\u51B5\u4E0B\u4F7F\u7528\u8F6F\u4EF6\u3002\u529F\u80FD\u6269\u5C55\u7684\u538B\u529B\u4E3B\u8981\u6765\u81EA\u90A3\u4E9B\u559C\u6B22\u57FA\u672C\u529F\u80FD,\u53C8\u5BF9\u8F6F\u4EF6\u63D0\u51FA\u4E86\u5F88\u591A\u65B0\u7528\u6CD5\u7684\u7528\u6237\u4EEC\u3002</p><p>\u5176\u6B21,\u8F6F\u4EF6\u4E00\u5B9A\u662F\u5728\u67D0\u79CD\u8BA1\u7B97\u673A\u786C\u4EF6\u5E73\u53F0\u4E0A\u5F00\u53D1,\u6210\u529F\u8F6F\u4EF6\u7684\u751F\u547D\u671F\u901A\u5E38\u6BD4\u5F53\u521D\u7684\u8BA1\u7B97\u673A\u786C\u4EF6\u5E73\u53F0\u8981\u957F\u3002\u5373\u4F7F\u4E0D\u662F\u66F4\u6362\u8BA1\u7B97\u673A,\u5219\u6709\u53EF\u80FD\u662F\u6362\u65B0\u578B\u53F7\u7684\u78C1\u76D8\u3001\u663E\u793A\u5668\u6216\u8005\u6253\u5370\u673A\u3002\u8F6F\u4EF6\u5FC5\u987B\u4E0E\u5404\u79CD\u65B0\u751F\u4E8B\u7269\u4FDD\u6301\u4E00\u81F4\u3002</p><p>\u7B80\u8A00\u4E4B, <strong>\u8F6F\u4EF6\u4EA7\u54C1\u624E\u6839\u4E8E\u6587\u5316\u7684\u6BCD\u4F53\u4E2D</strong> ,\u5982\u5404\u79CD\u5E94\u7528\u3001\u7528\u6237\u3001\u81EA\u7136\u53CA\u793E\u4F1A\u89C4\u5F8B\u3001\u8BA1\u7B97\u673A\u786C\u4EF6\u7B49\u7B49\u3002\u540E\u8005\u6301\u7EED\u4E0D\u65AD\u5730\u53D8\u5316\u7740,\u8FD9\u4E9B\u53D8\u5316\u65E0\u60C5\u5730\u5F3A\u8FEB\u7740\u8F6F\u4EF6\u968F\u4E4B\u53D8\u5316\u3002</p><p><strong>\u4E0D\u53EF\u89C1\u6027</strong> \u8F6F\u4EF6\u662F\u4E0D\u53EF\u89C1\u7684\u548C\u65E0\u6CD5\u53EF\u89C6\u5316\u7684\u3002\u4F8B\u5982,\u51E0\u4F55\u62BD\u8C61\u662F\u5F3A\u5927\u7684\u5DE5\u5177\u3002\u5EFA\u7B51\u5E73\u9762\u56FE\u80FD\u5E2E\u52A9\u5EFA\u7B51\u5E08\u548C\u5BA2\u6237\u4E00\u8D77\u8BC4\u4F30\u7A7A\u95F4\u5E03\u5C40\u3001\u8FDB\u51FA\u7684\u8FD0\u8F93\u6D41\u91CF\u548C\u5404\u4E2A\u89D2\u5EA6\u7684\u89C6\u89C9\u6548\u679C\u3002\u8FD9\u6837,\u77DB\u76FE\u53D8\u5F97\u7A81\u51FA,\u5FFD\u7565\u7684\u5730\u65B9\u53D8\u5F97\u660E\u663E\u3002\u540C\u6837,\u673A\u68B0\u5236\u56FE\u3001\u5316\u5B66\u5206\u5B50\u6A21\u578B\u5C3D\u7BA1\u662F\u62BD\u8C61\u6A21\u578B,\u4F46\u90FD\u8D77\u4E86\u76F8\u540C\u7684\u4F5C\u7528\u3002\u603B\u4E4B,\u90FD\u53EF\u4EE5\u901A\u8FC7\u51E0\u4F55\u62BD\u8C61\u6765\u6355\u83B7\u7269\u7406\u5B58\u5728\u7684\u51E0\u4F55\u7279\u6027\u3002</p><p><strong>\u8F6F\u4EF6\u7684\u5BA2\u89C2\u5B58\u5728\u4E0D\u5177\u6709\u7A7A\u95F4\u7684\u5F62\u4F53\u7279\u5F81</strong> \u3002\u56E0\u6B64,\u6CA1\u6709\u5DF2\u6709\u7684\u8868\u8FBE\u65B9\u5F0F,\u5C31\u50CF\u9646\u5730\u6D77\u6D0B\u6709\u5730\u56FE\u3001\u7845\u7247\u6709\u819C\u7247\u56FE\u3001\u8BA1\u7B97\u673A\u6709\u7535\u8DEF\u56FE\u4E00\u6837\u3002\u5F53\u6211\u4EEC\u8BD5\u56FE\u7528\u56FE\u5F62\u6765\u63CF\u8FF0\u8F6F\u4EF6\u7ED3\u6784\u65F6,\u6211\u4EEC\u53D1\u73B0\u5B83\u4E0D\u4EC5\u4EC5\u5305\u542B\u4E00\u4E2A,\u800C\u662F\u5F88\u591A\u76F8\u4E92\u5173\u8054\u3001\u91CD\u53E0\u5728\u4E00\u8D77\u7684\u56FE\u5F62\u3002\u8FD9\u4E9B\u56FE\u5F62\u53EF\u80FD\u63CF\u7ED8\u63A7\u5236\u6D41\u7A0B\u3001\u6570\u636E\u6D41\u3001\u4F9D\u8D56\u5173\u7CFB\u3001\u65F6\u95F4\u5E8F\u5217\u3001\u540D\u5B57\u7A7A\u95F4\u7684\u76F8\u4E92\u5173\u7CFB\u7B49\u7B49\u3002\u5B83\u4EEC\u901A\u5E38\u4E0D\u662F\u6709\u8F83\u5C11\u5C42\u6B21\u7684\u6241\u5E73\u7ED3\u6784\u3002\u5B9E\u9645\u4E0A,\u5728\u4E0A\u8FF0\u7ED3\u6784\u4E0A\u5EFA\u7ACB\u6982\u5FF5\u63A7\u5236\u7684\u4E00\u79CD\u65B9\u6CD5\u662F\u5F3A\u5236\u5C06\u5173\u8054\u5206\u5272,\u76F4\u5230\u53EF\u4EE5\u5C42\u6B21\u5316\u4E00\u4E2A\u6216\u591A\u4E2A\u56FE\u5F62 \u3002</p><p>\u9664\u53BB\u8F6F\u4EF6\u7ED3\u6784\u4E0A\u7684\u9650\u5236\u548C\u7B80\u5316\u65B9\u9762\u7684\u8FDB\u5C55,\u8F6F\u4EF6\u4ECD\u7136\u4FDD\u6301\u7740\u65E0\u6CD5\u53EF\u89C6\u5316\u7684\u56FA\u6709\u7279\u6027,\u4ECE\u800C\u5265\u593A\u4E86\u4E00\u4E9B\u5177\u6709\u5F3A\u5927\u529F\u80FD\u7684\u6982\u5FF5\u5DE5\u5177\u7684\u6784\u9020\u601D\u8DEF\u3002\u8FD9\u79CD\u7F3A\u61BE\u4E0D\u4EC5\u9650\u5236\u4E86\u4E2A\u4EBA\u7684\u8BBE\u8BA1\u8FC7\u7A0B,\u4E5F\u4E25\u91CD\u5730\u963B\u788D\u4E86\u76F8\u4E92\u4E4B\u95F4\u7684\u4EA4\u6D41\u3002</p><h2 id="\u4EE5\u5F80\u89E3\u51B3\u6B21\u8981\u56F0\u96BE\u7684\u4E00\u4E9B\u7A81\u7834" tabindex="-1"><a class="header-anchor" href="#\u4EE5\u5F80\u89E3\u51B3\u6B21\u8981\u56F0\u96BE\u7684\u4E00\u4E9B\u7A81\u7834" aria-hidden="true">#</a> \u4EE5\u5F80\u89E3\u51B3\u6B21\u8981\u56F0\u96BE\u7684\u4E00\u4E9B\u7A81\u7834</h2><p>\u5982\u679C\u56DE\u987E\u4E00\u4E0B\u8F6F\u4EF6\u9886\u57DF\u4E2D\u53D6\u5F97\u7684\u6700\u5BCC\u6709\u6210\u6548\u7684\u4E09\u6B21\u8FDB\u6B65,\u6211\u4EEC\u4F1A\u53D1\u73B0\u6BCF\u4E00\u6B21\u90FD\u662F\u89E3\u51B3\u4E86\u8F6F\u4EF6\u6784\u5EFA\u4E0A\u7684\u5DE8\u5927\u56F0\u96BE, <strong>\u4F46\u662F\u8FD9\u4E9B\u56F0\u96BE\u4E0D\u662F\u672C\u8D28\u5C5E\u6027,\u4E5F\u4E0D\u662F\u4E3B\u8981\u56F0\u96BE</strong> \u3002\u540C\u6837,\u6211\u4EEC\u53EF\u4EE5\u5BF9\u6BCF\u4E00\u6B21\u8FDB\u6B65\u8FDB\u884C\u5916\u63A8,\u6765\u4E86\u89E3\u5B83\u4EEC\u7684\u56FA\u6709\u9650\u5236\u3002</p><p><strong>\u9AD8\u7EA7\u8BED\u8A00</strong>\u3002\u52FF\u5EB8\u7F6E\u7591,\u8F6F\u4EF6\u751F\u4EA7\u7387\u3001\u53EF\u9760\u6027\u548C\u7B80\u6D01\u6027\u4E0A\u6700\u6709\u529B\u7684\u7A81\u7834\u662F\u4F7F\u7528\u9AD8\u7EA7\u8BED\u8A00\u7F16\u7A0B\u3002\u5927\u591A\u6570\u89C2\u5BDF\u8005\u76F8\u4FE1\u5F00\u53D1\u751F\u4EA7\u7387\u81F3\u5C11\u63D0\u9AD8\u4E86\u4E94\u500D,\u540C\u65F6\u53EF\u9760\u6027\u3001\u7B80\u6D01\u6027\u548C\u7406\u89E3\u7A0B\u5EA6\u4E5F\u5927\u4E3A\u63D0\u9AD8\u3002</p><p>\u90A3\u4E48,\u9AD8\u7EA7\u8BED\u8A00\u53D6\u5F97\u4E86\u54EA\u4E9B\u8FDB\u5C55\u5462?\u9996\u5148,\u5B83\u51CF\u8F7B\u4E86\u4E00\u4E9B\u6B21\u8981\u7684\u8F6F\u4EF6\u590D\u6742\u5EA6\u3002\u62BD\u8C61\u7A0B\u5E8F\u5305\u542B\u4E86\u5F88\u591A\u6982\u5FF5\u4E0A\u7684\u8981\u7D20:\u64CD\u4F5C\u3001\u6570\u636E\u7C7B\u578B\u3001\u6D41\u7A0B\u548C\u76F8\u4E92\u901A\u8BAF,\u800C\u5177\u4F53\u7684\u673A\u5668\u8BED\u8A00\u7A0B\u5E8F\u5219\u5173\u5FC3\u4F4D\u3001\u5BC4\u5B58\u5668\u3001\u6761\u4EF6\u3001\u5206\u652F\u3001\u901A\u9053\u3001\u78C1\u76D8\u7B49\u7B49\u3002\u9AD8\u7EA7\u8BED\u8A00\u6240\u8FBE\u5230\u7684\u62BD\u8C61\u7A0B\u5EA6\u5305\u542B\u4E86(\u62BD\u8C61)\u7A0B\u5E8F\u6240\u9700\u8981\u7684\u8981\u7D20,\u907F\u514D\u4E86\u66F4\u4F4E\u7EA7\u7684\u5143\u7D20,\u5B83\u6D88\u9664\u4E86\u5E76\u4E0D\u662F\u7A0B\u5E8F\u6240\u56FA\u6709\u7684\u6574\u4E2A\u7EA7\u522B\u7684\u590D\u6742\u5EA6\u3002</p><p>\u9AD8\u7EA7\u8BED\u8A00\u6700\u53EF\u80FD\u5B9E\u73B0\u7684\u662F\u63D0\u4F9B\u6240\u6709\u7F16\u7A0B\u4EBA\u5458\u5728\u62BD\u8C61\u7A0B\u5E8F\u4E2D\u80FD\u60F3\u5230\u7684\u8981\u7D20\u3002\u53EF\u4EE5\u80AF\u5B9A\u7684\u662F,\u6211\u4EEC\u601D\u8003\u6570\u636E\u7ED3\u6784\u3001\u6570\u636E\u7C7B\u578B\u548C\u64CD\u4F5C\u7684\u901F\u5EA6\u7A33\u56FA\u63D0\u9AD8,\u4E0D\u8FC7\u662F\u4EE5\u975E\u5E38\u7F13\u6162\u7684\u901F\u5EA6\u3002\u53E6\u5916,\u7A0B\u5E8F\u5F00\u53D1\u65B9\u6CD5\u8D8A\u6765\u8D8A\u63A5\u8FD1\u7528\u6237\u7684\u590D\u6742\u5EA6\u3002</p><p>\u7136\u800C,\u5BF9\u4E8E\u8F83\u5C11\u4F7F\u7528\u90A3\u4E9B\u590D\u6742\u6DF1\u5965\u8BED\u8A00\u8981\u7D20\u7684\u7528\u6237,\u9AD8\u7EA7\u8BED\u8A00\u5728\u67D0\u79CD\u7A0B\u5EA6\u4E0A\u589E\u52A0\u800C\u4E0D\u662F\u51CF\u5C11\u4E86\u8111\u529B\u52B3\u52A8\u4E0A\u7684\u8D1F\u62C5\u3002</p><p><strong>\u5206\u65F6</strong> \u3002\u5927\u591A\u6570\u89C2\u5BDF\u8005\u76F8\u4FE1\u5206\u65F6\u63D0\u9AD8\u4E86\u7A0B\u5E8F\u5458\u7684\u751F\u4EA7\u7387\u548C\u4EA7\u54C1\u7684\u8D28\u91CF,\u5C3D\u7BA1\u5B83\u5E26\u6765\u7684\u8FDB\u6B65\u4E0D\u5982\u9AD8\u7EA7\u8BED\u8A00\u3002</p><p>\u5206\u65F6\u89E3\u51B3\u4E86\u5B8C\u5168\u4E0D\u540C\u7684\u56F0\u96BE\u3002\u5206\u65F6\u4FDD\u8BC1\u4E86\u53CA\u65F6\u6027,\u4ECE\u800C\u4F7F\u6211\u4EEC\u80FD\u7EF4\u6301\u5BF9\u590D\u6742\u7A0B\u5EA6\u7684\u4E00\u4E2A\u603B\u4F53\u628A\u63E1\u3002\u6279\u5904\u7406\u7F16\u7A0B\u7684\u8F83\u957F\u5468\u8F6C\u65F6\u95F4\u610F\u5473\u7740\u4E0D\u53EF\u907F\u514D\u4F1A\u9057\u5FD8\u4E00\u4E9B\u7EC6\u679D\u672B\u8282,\u5982\u679C\u6211\u4EEC\u505C\u4E0B\u7F16\u7A0B,\u8C03\u7528\u7F16\u8BD1\u7A0B\u5E8F\u6216\u8005\u6267\u884C\u7A0B\u5E8F,\u601D\u7EF4\u4E0A\u7684\u4E2D\u65AD\u4F7F\u6211\u4EEC\u4E0D\u5F97\u4E0D\u91CD\u65B0\u8FDB\u884C\u601D\u8003,\u5B83\u5728\u65F6\u95F4\u4E0A\u7684\u4EE3\u4EF7\u975E\u5E38\u9AD8\u6602\u3002\u6700\u4E25\u91CD\u7684\u7ED3\u679C\u53EF\u80FD\u662F\u5931\u53BB\u5BF9\u590D\u6742\u7CFB\u7EDF\u7684\u638C\u63E1\u3002</p><p>\u8F83\u957F\u7684\u5468\u8F6C\u65F6\u95F4\u548C\u673A\u5668\u8BED\u8A00\u7684\u590D\u6742\u5EA6\u4E00\u6837,\u662F\u8F6F\u4EF6\u5F00\u53D1\u8FC7\u7A0B\u7684\u6B21\u8981\u56F0\u96BE,\u800C\u4E0D\u662F\u672C\u8D28\u56F0\u96BE\u3002\u5206\u65F6\u6240\u8D77\u4F5C\u7528\u4E5F\u975E\u5E38\u6709\u9650\u3002\u4E3B\u8981\u6548\u679C\u662F\u7F29\u77ED\u4E86\u7CFB\u7EDF\u7684\u54CD\u5E94\u65F6\u95F4\u3002\u968F\u7740\u5B83\u63A5\u8FD1\u4E8E\u96F6,\u5230\u8FBE\u4EBA\u7C7B\u53EF\u4EE5\u8FA8\u8BC6\u7684\u57FA\u672C\u80FD\u529B\u2014\u2014\u5927\u6982 100 \u6BEB\u79D2\u65F6,\u6240\u83B7\u5F97\u7684\u597D\u5904\u5C31\u63A5\u8FD1\u4E8E\u65E0\u4E86\u3002</p><p><strong>\u7EDF\u4E00\u7F16\u7A0B\u73AF\u5883</strong> \u7B2C\u4E00\u4E2A\u96C6\u6210\u5F00\u53D1\u73AF\u5883\u2014\u2014Unix \u548C Interlisp \u73B0\u5728\u5DF2\u7ECF\u5F97\u5230\u4E86\u5E7F\u6CDB\u5E94\u7528,\u5E76\u4E14\u4F7F\u751F\u4EA7\u7387\u63D0\u9AD8\u4E86 5 \u500D\u3002\u4E3A\u4EC0\u4E48?</p><p>\u5B83\u4EEC\u4E3B\u8981\u901A\u8FC7\u63D0\u4F9B\u96C6\u6210\u5E93\u3001\u7EDF\u4E00\u6587\u4EF6\u683C\u5F0F\u3001\u7BA1\u9053\u548C\u8FC7\u6EE4\u5668,\u89E3\u51B3\u4E86\u5171\u540C\u4F7F\u7528\u7A0B\u5E8F\u7684\u6B21\u8981\u56F0\u96BE\u3002\u8FD9\u6837,\u6982\u5FF5\u6027\u7ED3\u6784\u7406\u8BBA\u4E0A\u7684\u76F8\u4E92\u8C03\u7528\u3001\u63D0\u4F9B\u8F93\u5165\u548C\u4E92\u76F8\u4F7F\u7528,\u5728\u73B0\u5B9E\u4E2D\u53EF\u4EE5\u975E\u5E38\u5BB9\u6613\u5730\u5B9E\u73B0\u3002\u56E0\u4E3A\u6BCF\u4E2A\u65B0\u5DE5\u5177\u53EF\u4EE5\u901A\u8FC7\u6807\u51C6\u683C\u5F0F\u5728\u4EFB\u4F55\u4E00\u4E2A\u7A0B\u5E8F\u4E2D\u5E94\u7528,\u8FD9\u79CD\u7A81\u7834\u63A5\u7740\u53C8\u6FC0\u53D1\u6574\u4E2A\u5DE5\u5177\u5E93\u7684\u5F00\u53D1\u3002</p><p>\u7531\u4E8E\u8FD9\u4E9B\u6210\u529F,\u73AF\u5883\u5F00\u53D1\u662F\u5F53\u4ECA\u8F6F\u4EF6\u5DE5\u7A0B\u7814\u7A76\u7684\u4E3B\u8981\u9898\u76EE\u3002\u6211\u4EEC\u5C06\u5728\u4E0B\u7AE0\u4E2D\u8BA8\u8BBA\u671F\u671B\u8FBE\u5230\u7684\u76EE\u6807\u548C\u9650\u5236\u3002</p><h2 id="\u94F6\u5F39\u7684\u5E0C\u671B" tabindex="-1"><a class="header-anchor" href="#\u94F6\u5F39\u7684\u5E0C\u671B" aria-hidden="true">#</a> \u94F6\u5F39\u7684\u5E0C\u671B</h2><p>\u73B0\u5728,\u8BA9\u6211\u4EEC\u6765\u8BA8\u8BBA\u4E00\u4E0B\u5F53\u4ECA\u53EF\u80FD\u4F5C\u4E3A\u6F5C\u5728\u94F6\u5F39\u7684\u6700\u5148\u8FDB\u7684\u6280\u672F\u8FDB\u6B65\u3002\u5B83\u4EEC\u5404\u81EA\u9488\u5BF9\u4EC0\u4E48\u6837\u7684\u95EE\u9898?\u5B83\u4EEC\u662F\u5C5E\u4E8E\u5FC5\u8981\u95EE\u9898,\u6216\u8005\u4F9D\u7136\u662F\u89E3\u51B3\u6211\u4EEC\u5269\u4E0B\u7684\u6B21\u8981\u56F0\u96BE?\u5B83\u4EEC\u662F\u63D0\u4F9B\u4E86\u521B\u65B0,\u8FD8\u662F\u4EC5\u4EC5\u662F\u589E\u91CF\u6539\u8FDB?</p><p><strong>Ada \u548C\u5176\u4ED6\u9AD8\u7EA7\u7F16\u7A0B\u8BED\u8A00</strong> \u8FD1\u6765,\u6700\u88AB\u5439\u6367\u7684\u5F00\u53D1\u8FDB\u5C55\u4E4B\u4E00\u662F\u7F16\u7A0B\u8BED\u8A00 Ada,\u4E00\u79CD 80 \u5E74\u4EE3\u7684\u9AD8\u7EA7\u8BED\u8A00\u3002Ada \u5B9E\u9645\u4E0A\u4E0D\u4EC5\u4EC5\u53CD\u6620\u4E86\u8BED\u8A00\u6982\u5FF5\u4E0A\u7684\u7A81\u7834\u6027\u8FDB\u5C55,\u800C\u4E14\u8574\u6DB5\u4E86\u9F13\u52B1\u73B0\u4EE3\u8BBE\u8BA1\u548C\u6A21\u5757\u5316\u6982\u5FF5\u8FD0\u7528\u7684\u91CD\u8981\u7279\u6027\u3002\u7531\u4E8E Ada \u91C7\u7528\u7684\u662F\u62BD\u8C61\u6570\u636E\u7C7B\u578B\u3001\u5C42\u6B21\u7ED3\u6784\u7684\u6A21\u5757\u5316\u7406\u5FF5,\u6240\u6709 Ada \u7406\u5FF5\u53EF\u80FD\u6BD4\u8BED\u8A00\u672C\u8EAB\u66F4\u52A0\u5148\u8FDB\u3002Ada \u4F7F\u7528\u8BBE\u8BA1\u6765\u627F\u8F7D\u9700\u6C42,\u4F5C\u4E3A\u8FD9\u4E00\u8FC7\u7A0B\u7684\u81EA\u7136\u4EA7\u7269,\u5B83\u53EF\u80FD\u8FC7\u4E8E\u4E30\u5BCC\u4E86\u3002\u4E0D\u8FC7,\u8FD9\u5E76\u4E0D\u662F\u81F4\u547D\u7684,\u56E0\u4E3A\u5B83\u7684\u8BCD\u6C47\u5B50\u96C6\u53EF\u4EE5\u89E3\u51B3\u5B66\u4E60\u95EE\u9898,\u786C\u4EF6\u7684\u8FDB\u5C55\u80FD\u63D0\u4F9B\u66F4\u9AD8\u7684 MIPS(\u6BCF\u79D2\u767E\u4E07\u6307\u4EE4\u96C6),\u51CF\u5C11\u7F16\u8BD1\u7684\u6210\u672C\u3002\u8F6F\u4EF6\u7CFB\u7EDF\u7ED3\u6784\u5316\u7684\u5148\u8FDB\u7406\u5FF5\u5B9E\u9645\u4E0A\u975E\u5E38\u597D\u5730\u5229\u7528\u4E86 MIPS \u4E0A\u7684\u8FDB\u5C55\u300260 \u5E74\u4EE3,\u66FE\u5728\u5185\u5B58\u548C\u901F\u5EA6\u6210\u672C\u4E0A\u5E7F\u53D7\u8C34\u8D23\u7684\u64CD\u4F5C\u7CFB\u7EDF,\u5982\u4ECA\u5DF2\u88AB\u8BC1\u660E\u662F\u4E00\u79CD\u80FD\u4F7F\u7528\u67D0\u4E9B MIPS \u548C\u5EC9\u4EF7\u5185\u5B58\u7684\u975E\u5E38\u4F18\u79C0\u7684\u7CFB\u7EDF\u3002</p><p>\u7136\u800C,Ada \u4ECD\u7136\u4E0D\u662F\u6D88\u706D\u8F6F\u4EF6\u751F\u4EA7\u7387\u602A\u517D\u7684\u94F6\u5F39\u3002\u6BD5\u7ADF,\u5B83\u53EA\u662F\u53E6\u4E00\u79CD\u9AD8\u7EA7\u8BED\u8A00,\u8FD9\u7C7B\u8BED\u8A00\u51FA\u73B0\u6700\u5927\u7684\u56DE\u62A5\u6765\u81EA\u51FA\u73B0\u65F6\u7684\u51B2\u51FB,\u5B83\u901A\u8FC7\u4F7F\u7528\u66F4\u52A0\u62BD\u8C61\u7684\u8BED\u53E5\u6765\u5F00\u53D1,\u964D\u4F4E\u4E86\u673A\u5668\u7684\u6B21\u8981\u590D\u6742\u5EA6\u3002\u4E00\u65E6\u8FD9\u4E9B\u96BE\u9898\u88AB\u89E3\u51B3,\u5C31\u53EA\u5269\u4E0B\u975E\u5E38\u5C11\u7684\u95EE\u9898,\u89E3\u51B3\u5269\u4F59\u90E8\u5206\u7684\u83B7\u76CA\u5FC5\u7136\u4E5F\u8981\u5C11\u4E00\u4E9B\u3002</p><p>\u6211\u9884\u8A00\u5728\u4EE5\u540E\u7684\u5341\u5E74\u4E2D,\u5F53 Ada \u7684\u6548\u7387\u88AB\u5927\u5BB6\u8BC4\u4F30\u8BA4\u53EF\u65F6,\u5B83\u4F1A\u5E26\u6765\u76F8\u5F53\u5927\u7684\u53D8\u5316,\u8FD9\u5E76\u4E0D\u662F\u56E0\u4E3A\u4EFB\u4F55\u7279\u522B\u7684\u8BED\u8A00\u7279\u6027,\u4E0D\u662F\u7531\u4E8E\u8FD9\u4E9B\u8BED\u8A00\u7279\u6027\u88AB\u5408\u5E76\u5728\u4E00\u8D77,\u4E5F\u4E0D\u662F\u56E0\u4E3A Ada \u5F00\u53D1\u73AF\u5883\u4F1A\u4E0D\u65AD\u53D1\u5C55\u8FDB\u6B65\u3002Ada \u7684\u6700\u5927\u8D21\u732E\u5728\u4E8E\u7F16\u7A0B\u4EBA\u5458\u57F9\u8BAD\u65B9\u5F0F\u7684\u8F6C\u53D8,\u5373\u5BF9\u5F00\u53D1\u4EBA\u5458\u9700\u8981\u8FDB\u884C\u73B0\u4EE3\u8F6F\u4EF6\u8BBE\u8BA1\u6280\u672F\u57F9\u8BAD\u3002</p><p><strong>\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B</strong> \u8F6F\u4EF6\u4E13\u4E1A\u7684\u4E00\u4E9B\u5B66\u751F\u575A\u6301\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u662F\u5F53\u4ECA\u82E5\u5E72\u65B0\u6F6E\u6280\u672F\u4E2D\u6700\u5BCC\u6709\u5E0C\u671B\u7684 \u3002\u6211\u4E5F\u662F\u5176\u4E2D\u4E4B\u4E00\u3002\u8FBE\u7279\u8305\u65AF\u7684 Mark Sherman \u63D0\u51FA,\u5FC5\u987B\u4ED4\u7EC6\u5730\u533A\u522B\u4E24\u4E2A\u4E0D\u540C\u7684\u6982\u5FF5:\u62BD\u8C61\u6570\u636E\u7C7B\u578B\u548C\u5C42\u6B21\u5316\u7C7B\u578B,\u540E\u8005\u4E5F\u88AB\u79F0\u4E3A \u7C7B(class) \u3002\u62BD\u8C61\u6570\u636E\u7C7B\u578B\u7684\u6982\u5FF5\u662F\u6307\u5BF9\u8C61\u7C7B\u578B\u5E94\u8BE5\u901A\u8FC7\u4E00\u4E2A\u540D\u79F0\u3001\u4E00\u7CFB\u5217\u5408\u9002\u7684\u503C\u548C\u64CD\u4F5C\u6765\u5B9A\u4E49,\u800C\u4E0D\u662F\u7406\u5E94\u88AB\u9690\u85CF\u7684\u5B58\u50A8\u7ED3\u6784\u3002\u62BD\u8C61\u6570\u636E\u7C7B\u578B\u7684\u4F8B\u5B50\u662F Ada \u5305(\u4EE5\u53CA\u79C1\u6709\u7C7B\u578B)\u548C Modula \u7684\u6A21\u5757\u3002</p><p>\u5C42\u6B21\u5316\u7C7B\u578B,\u5982 Simula-67 \u7684\u7C7B,\u662F\u5141\u8BB8\u5B9A\u4E49\u53EF\u4EE5\u88AB\u540E\u7EED\u5B50\u7C7B\u578B\u7CBE\u5316\u7684\u901A\u7528\u63A5\u53E3\u3002\u8FD9\u4E24\u4E2A\u6982\u5FF5\u662F\u4E92\u4E0D\u76F8\u5E72\u7684\u2014\u2014\u53EF\u4EE5\u53EA\u6709\u5C42\u6B21\u5316,\u6CA1\u6709\u6570\u636E\u9690\u85CF;\u4E5F\u53EF\u80FD\u662F\u53EA\u6709\u6570\u636E\u9690\u85CF,\u800C\u6CA1\u6709\u5C42\u6B21\u5316\u3002\u4E24\u79CD\u6982\u5FF5\u90FD\u4F53\u73B0\u4E86\u8F6F\u4EF6\u5F00\u53D1\u9886\u57DF\u7684\u8FDB\u6B65\u3002</p><p>\u5B83\u4EEC\u7684\u51FA\u73B0\u90FD\u6D88\u9664\u4E86\u5F00\u53D1\u8FC7\u7A0B\u4E2D\u7684\u975E\u672C\u8D28\u56F0\u96BE,\u5141\u8BB8\u8BBE\u8BA1\u4EBA\u5458\u8868\u8FBE\u81EA\u5DF1\u8BBE\u8BA1\u7684\u5185\u5728\u7279\u6027,\u800C\u4E0D\u9700\u8981\u8868\u8FBE\u5927\u91CF\u53E5\u6CD5\u4E0A\u7684\u5185\u5BB9,\u8FD9\u4E9B\u5185\u5BB9\u5E76\u6CA1\u6709\u6DFB\u52A0\u4EC0\u4E48\u65B0\u7684\u4FE1\u606F\u3002\u5BF9\u4E8E\u62BD\u8C61\u6570\u636E\u7C7B\u578B\u548C\u5C42\u6B21\u5316\u7C7B\u578B,\u5B83\u4EEC\u90FD\u662F\u89E3\u51B3\u4E86\u9AD8\u7EA7\u522B\u7684\u6B21\u8981\u56F0\u96BE\u548C\u5141\u8BB8\u91C7\u7528\u8F83\u9AD8\u5C42\u6B21\u7684\u8868\u73B0\u5F62\u5F0F\u6765\u8868\u8FBE\u8BBE\u8BA1\u3002</p><p>\u4E0D\u8FC7,\u8FD9\u4E9B\u63D0\u9AD8\u4EC5\u4EC5\u80FD\u6D88\u9664\u6240\u6709\u8BBE\u8BA1\u8868\u8FBE\u4E0A\u7684\u6B21\u8981\u56F0\u96BE\u3002\u8F6F\u4EF6\u7684\u5185\u5728\u95EE\u9898\u662F\u8BBE\u8BA1\u7684\u590D\u6742\u5EA6,\u4E0A\u8FF0\u65B9\u6CD5\u5E76\u6CA1\u6709\u5BF9\u5B83\u6709\u4EFB\u4F55\u7684\u4FC3\u8FDB\u3002\u9664\u975E\u6211\u4EEC\u73B0\u5728\u7684\u7F16\u7A0B\u8BED\u8A00\u4E2D,\u4E0D\u5FC5\u8981\u7684\u4F4E\u5C42\u6B21\u7C7B\u578B\u8BF4\u660E\u5360\u636E\u4E86\u8F6F\u4EF6\u4EA7\u54C1\u8BBE\u8BA1 90%,\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u624D\u80FD\u5E26\u6765\u6570\u91CF\u7EA7\u4E0A\u7684\u63D0\u9AD8\u3002\u5BF9\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u8FD9\u9897\u201C\u94F6\u5F39\u201D,\u6211\u6DF1\u8868\u6000\u7591\u3002</p><p><strong>\u4EBA\u5DE5\u667A\u80FD</strong> \u5F88\u591A\u4EBA\u671F\u671B\u4EBA\u5DE5\u667A\u80FD\u4E0A\u7684\u8FDB\u5C55\u53EF\u4EE5\u7ED9\u8F6F\u4EF6\u751F\u4EA7\u7387\u548C\u8D28\u91CF\u5E26\u6765\u6570\u91CF\u7EA7\u4E0A\u7684\u589E\u957F ,\u4F46\u6211\u4E0D\u8FD9\u6837\u8BA4\u4E3A\u3002\u8FFD\u7A76\u5176\u539F\u56E0,\u6211\u4EEC\u5FC5\u987B\u5256\u6790\u201C\u4EBA\u5DE5\u667A\u80FD\u201D\u610F\u5473\u7740\u4EC0\u4E48,\u4EE5\u53CA\u5B83\u5982\u4F55\u5E94\u7528\u3002</p><p>Parnas \u6F84\u6E05\u4E86\u672F\u8BED\u4E0A\u7684\u6DF7\u4E71:</p><p>\u73B0\u5728\u6709\u4E24\u79CD\u5DEE\u5F02\u975E\u5E38\u5927\u7684 AI \u5B9A\u4E49\u88AB\u5E7F\u6CDB\u4F7F\u7528\u3002AI-1:\u4F7F\u7528\u8BA1\u7B97\u673A\u6765\u89E3\u51B3\u4EE5\u524D\u53EA\u80FD\u901A\u8FC7\u4EBA\u7C7B\u667A\u6167\u89E3\u51B3\u7684\u95EE\u9898\u3002AI-2:\u4F7F\u7528\u542F\u53D1\u5F0F\u548C\u57FA\u4E8E\u89C4\u5219\u7684\u7279\u5B9A\u7F16\u7A0B\u6280\u672F\u3002\u5728\u8FD9\u79CD\u65B9\u6CD5\u4E2D,\u5BF9\u4EBA\u7C7B\u4E13\u5BB6\u8FDB\u884C\u7814\u7A76,\u5224\u65AD\u4ED6\u4EEC\u89E3\u51B3\u65B9\u6CD5\u7684\u542F\u53D1\u6027\u601D\u7EF4\u6216\u8005\u7ECF\u9A8C\u6CD5\u5219......\u3002\u7A0B\u5E8F\u88AB\u8BBE\u8BA1\u6210\u4EE5\u4EBA\u7C7B\u89E3\u51B3\u95EE\u9898\u7684\u65B9\u5F0F\u6765\u8FD0\u4F5C\u3002</p><p>\u7B2C\u4E00\u79CD\u5B9A\u4E49\u7684\u610F\u4E49\u5BB9\u6613 \u53D1\u751F \u53D8\u5316......\u4ECA\u5929\u53EF\u80FD\u9002\u5408 AI-1 \u5B9A\u4E49\u7684\u7A0B\u5E8F,\u4E00\u65E6\u6211\u4EEC\u4E86\u89E3\u4E86\u5B83\u7684\u8FD0\u884C\u65B9\u5F0F,\u7406\u89E3\u4E86\u95EE\u9898,\u5C31\u4E0D\u518D\u8BA4\u4E3A\u5B83\u662F\u4EBA\u5DE5\u667A\u80FD......\u4E0D\u5E78\u7684\u662F,\u6211\u65E0\u6CD5\u8BC6\u522B\u8FD9\u4E2A\u9886\u57DF\u7684\u7279\u5B9A\u77E5\u8BC6\u4F53\u7CFB......\u7EDD\u5927\u591A\u6570\u5DE5\u4F5C\u662F\u9488\u5BF9\u95EE\u9898\u57DF\u7684,\u6211\u4EEC\u9700\u8981\u4E00\u4E9B\u62BD\u8C61\u6216\u8005\u521B\u9020\u6027\u6765\u89E3\u51B3\u4E0A\u8FF0\u95EE\u9898\u3002</p><p>\u6211\u5B8C\u5168\u540C\u610F\u8FD9\u79CD\u6279\u8BC4\u610F\u89C1\u3002\u8BED\u97F3\u8BC6\u522B\u6280\u672F\u4E0E\u56FE\u8C61\u8BC6\u522B\u6280\u672F\u7684\u5171\u540C\u70B9\u975E\u5E38\u5C11,\u5B83\u4EEC\u4E0E\u4E13\u5BB6\u7CFB\u7EDF\u4E2D\u5E94\u7528\u7684\u6280\u672F\u4E0D\u540C\u3002\u4F8B\u5982,\u6211\u89C9\u5F97\u5F88\u96BE\u53BB\u53D1\u73B0\u56FE\u8C61\u8BC6\u522B\u6280\u672F\u80FD\u7ED9\u7F16\u7A0B\u5F00\u53D1\u5B9E\u8DF5\u5E26\u6765\u4EC0\u4E48\u6837\u7684\u5DEE\u5F02\u3002\u540C\u6837,\u8BED\u97F3\u8BC6\u522B\u4E5F\u5DEE\u4E0D\u591A\u2014\u2014\u8F6F\u4EF6\u5F00\u53D1\u4E0A\u7684\u56F0\u96BE\u662F\u51B3\u5B9A\u8BF4\u4EC0\u4E48,\u800C\u4E0D\u662F\u5982\u4F55\u8BF4\u3002</p><p>\u8868\u8FBE\u7684\u7B80\u5316\u4EC5\u4EC5\u80FD\u63D0\u4F9B\u5C11\u91CF\u7684\u4FC3\u8FDB\u4F5C\u7528\u3002\u81F3\u4E8E AI-2 \u4E13\u5BB6\u7CFB\u7EDF\u6280\u672F,\u5E94\u8BE5\u7528\u4E13\u95E8\u7684\u7AE0\u8282\u6765\u8BA8\u8BBA\u3002</p><p><strong>\u4E13\u5BB6\u7CFB\u7EDF</strong> \u4EBA\u5DE5\u667A\u80FD\u9886\u57DF\u6700\u5148\u8FDB\u7684\u3001\u88AB\u6700\u5927\u8303\u56F4\u4F7F\u7528\u7684\u90E8\u5206,\u662F\u5F00\u53D1\u4E13\u5BB6\u7CFB\u7EDF\u7684\u6280\u672F\u3002</p><p>\u5F88\u591A\u8F6F\u4EF6\u79D1\u5B66\u5BB6\u6B63\u975E\u5E38\u52AA\u529B\u5730\u5DE5\u4F5C\u7740,\u60F3\u628A\u8FD9\u79CD\u6280\u672F\u5E94\u7528\u5728\u8F6F\u4EF6\u7684\u5F00\u53D1\u73AF\u5883\u4E2D \u3002\u90A3\u4E48\u5B83\u7684\u6982\u5FF5\u662F\u4EC0\u4E48,\u524D\u666F\u5982\u4F55?</p><p>\u4E13\u5BB6\u7CFB\u7EDF\u662F\u5305\u542B\u5F52\u7EB3\u63A8\u8BBA\u5F15\u64CE\u548C\u89C4\u5219\u57FA\u7840\u7684\u7A0B\u5E8F,\u5B83\u63A5\u6536\u8F93\u5165\u6570\u636E\u548C\u5047\u8BBE\u6761\u4EF6,\u901A\u8FC7\u4ECE\u57FA\u7840\u89C4\u5219\u63A8\u5BFC\u903B\u8F91\u7ED3\u679C,\u63D0\u51FA\u7ED3\u8BBA\u548C\u5EFA\u8BAE,\u5411\u7528\u6237\u5C55\u793A\u524D\u56E0\u540E\u679C,\u5E76\u89E3\u91CA\u6700\u7EC8\u7684\u7ED3\u679C\u3002\u63A8\u8BBA\u5F15\u64CE\u9664\u4E86\u5904\u7406\u63A8\u7406\u903B\u8F91\u4EE5\u5916,\u901A\u5E38\u8FD8\u5305\u62EC\u590D\u6742\u903B\u8F91\u6216\u8005\u6982\u7387\u6570\u636E\u548C\u89C4\u5219\u3002</p><p>\u5BF9\u4E8E\u89E3\u51B3\u76F8\u540C\u7684\u95EE\u9898,\u8FD9\u79CD\u7CFB\u7EDF\u660E\u663E\u6BD4\u4F20\u7EDF\u7684\u7A0B\u5E8F\u7B97\u6CD5\u8981\u5148\u8FDB\u5F88\u591A\u3002</p><ul><li>\u63A8\u5BFC\u5F15\u64CE\u6280\u672F\u7684\u5F00\u53D1\u72EC\u7ACB\u4E8E\u5E94\u7528\u7A0B\u5E8F,\u56E0\u6B64\u53EF\u4EE5\u7528\u4E8E\u591A\u4E2A\u7528\u6237\u3002\u5728\u8BE5\u5F15\u64CE\u4E0A\u82B1\u8D39\u8F83\u5927\u7684\u5DE5\u4F5C\u662F\u5F88\u5408\u7406\u7684\u3002\u5B9E\u9645\u4E0A,\u8FD9\u79CD\u5F15\u64CE\u6280\u672F\u975E\u5E38\u5148\u8FDB\u3002</li><li>\u57FA\u4E8E\u5E94\u7528\u7684\u3001\u53EF\u53D8\u66F4\u7684\u90E8\u5206,\u5728\u57FA\u7840\u89C4\u5219\u4E2D\u4EE5\u4E00\u79CD\u7EDF\u4E00\u7684\u98CE\u683C\u7F16\u7801,\u5E76\u4E14\u4E3A\u89C4\u5219\u57FA\u7840\u7684\u5F00\u53D1\u3001\u66F4\u6539\u3001\u6D4B\u8BD5\u548C\u6587\u6863\u5316\u63D0\u4F9B\u4E86\u82E5\u5E72\u5DE5\u5177\u3002\u8FD9\u5B9E\u9645\u4E0A\u5BF9\u4E00\u4E9B\u5E94\u7528\u7A0B\u5E8F\u672C\u8EAB\u7684\u590D\u6742\u5EA6\u8FDB\u884C\u4E86\u7CFB\u7EDF\u5316\u3002</li></ul><p>Edward Feigenbaum \u6307\u51FA\u8FD9\u79CD\u7CFB\u7EDF\u7684\u80FD\u529B\u4E0D\u662F\u6765\u81EA\u67D0\u79CD\u524D\u6240\u672A\u6709\u7684\u63A8\u5BFC\u673A\u5236,\u800C\u662F\u6765\u81EA\u975E\u5E38\u4E30\u5BCC\u7684\u77E5\u8BC6\u79EF\u7D2F\u57FA\u7840,\u6240\u4EE5\u66F4\u52A0\u7CBE\u786E\u5730\u53CD\u6620\u4E86\u73B0\u5B9E\u4E16\u754C\u3002\u6211\u8BA4\u4E3A\u8FD9\u79CD\u6280\u672F\u63D0\u4F9B\u7684\u6700\u91CD\u8981\u8FDB\u6B65\u662F\u5177\u4F53\u5E94\u7528\u7684\u590D\u6742\u6027\u4E0E\u7A0B\u5E8F\u672C\u8EAB\u76F8\u5206\u79BB\u3002</p><p>\u5982\u4F55\u628A\u5B83\u5E94\u7528\u5728\u8F6F\u4EF6\u5F00\u53D1\u5DE5\u4F5C\u4E2D?\u53EF\u4EE5\u901A\u8FC7\u5F88\u591A\u9014\u5F84:\u5EFA\u8BAE\u63A5\u53E3\u89C4\u5219\u3001\u5236\u8BA2\u6D4B\u8BD5\u7B56\u7565\u3001\u8BB0\u5F55\u5404\u79CD bug \u4EA7\u751F\u7684\u9891\u7387\u3001\u63D0\u4F9B\u4F18\u5316\u5EFA\u8BAE\u7B49\u7B49\u3002</p><p>\u4F8B\u5982,\u8003\u8651\u4E00\u4E2A\u865A\u6784\u7684\u6D4B\u8BD5\u987E\u95EE\u7CFB\u7EDF\u3002\u5728\u6700\u6839\u672C\u7684\u7EA7\u522B,\u8BCA\u65AD\u4E13\u5BB6\u7CFB\u7EDF\u548C\u98DE\u884C\u5458\u7684\u68C0\u67E5\u5217\u8868\u5F88\u76F8\u4F3C,\u5BF9\u53EF\u80FD\u96BE\u4EE5\u5BFB\u627E\u7684\u539F\u56E0\u63D0\u4F9B\u57FA\u672C\u7684\u5EFA\u8BAE\u3002\u5EFA\u7ACB\u57FA\u7840\u89C4\u5219\u65F6,\u53EF\u4EE5\u4F9D\u636E\u66F4\u591A\u7684\u590D\u6742\u95EE\u9898\u5F81\u5146\u62A5\u544A,\u4ECE\u800C\u4F7F\u8FD9\u4E9B\u5EFA\u8BAE\u66F4\u52A0\u7CBE\u786E\u3002\u53EF\u4EE5\u60F3\u8C61,\u8BE5\u8C03\u8BD5\u8F85\u52A9\u7A0B\u5E8F\u8D77\u521D\u63D0\u4F9B\u7684\u662F\u4E00\u822C\u5316\u5EFA\u8BAE,\u968F\u7740\u57FA\u7840\u89C4\u5219\u5305\u62EC\u8D8A\u6765\u8D8A\u591A\u7684\u7CFB\u7EDF\u7ED3\u6784\u4FE1\u606F\u65F6,\u5B83\u4EA7\u751F\u7684\u63A8\u6D4B\u548C\u63A8\u8350\u7684\u6D4B\u8BD5\u4E5F\u8D8A\u6765\u8D8A\u51C6\u786E\u3002\u8BE5\u7C7B\u578B\u7684\u4E13\u5BB6\u7CFB\u7EDF\u53EF\u80FD\u4E0E\u4F20\u7EDF\u7CFB\u7EDF\u5F7B\u5E95\u5206\u79BB,\u7CFB\u7EDF\u4E2D\u7684\u89C4\u5219\u57FA\u7840\u53EF\u80FD\u4E0E\u76F8\u5E94\u7684\u8F6F\u4EF6\u4EA7\u54C1\u5177\u6709\u76F8\u540C\u7684\u5C42\u6B21\u6A21\u5757\u5316\u7ED3\u6784,\u56E0\u6B64\u5F53\u4EA7\u54C1\u6A21\u5757\u5316\u4FEE\u6539\u65F6,\u8BCA\u65AD\u89C4\u5219\u4E5F\u80FD\u76F8\u5E94\u5730\u8FDB\u884C\u6A21\u5757\u5316\u4FEE\u6539\u3002</p><p>\u4EA7\u751F\u8BCA\u65AD\u89C4\u5219\u4E5F\u662F\u5728\u4E3A\u6A21\u5757\u548C\u7CFB\u7EDF\u7F16\u5236\u6D4B\u8BD5\u7528\u4F8B\u96C6\u65F6\u5FC5\u987B\u5B8C\u6210\u7684\u4EFB\u52A1\u3002\u5982\u679C\u5B83\u4EE5\u4E00\u79CD\u9002\u5F53\u901A\u7528\u7684\u65B9\u5F0F\u6765\u5B8C\u6210,\u5BF9\u89C4\u5219\u91C7\u7528\u4E00\u81F4\u7684\u7ED3\u6784,\u62E5\u6709\u4E00\u4E2A\u826F\u597D\u53EF\u7528\u7684\u63A8\u6D4B\u5F15\u64CE,\u90A3\u4E48\u4E8B\u5B9E\u4E0A\u5B83\u5C31\u53EF\u4EE5\u51CF\u5C11\u6D4B\u8BD5\u7528\u4F8B\u8BBE\u8BA1\u7684\u603B\u4F53\u5DE5\u4F5C\u91CF,\u4EE5\u53CA\u5E2E\u52A9\u6574\u4E2A\u8F6F\u4EF6\u751F\u547D\u5468\u671F\u7684\u7EF4\u62A4\u3001\u4FEE\u6539\u548C\u6D4B\u8BD5\u3002\u540C\u6837,\u6211\u4EEC\u53EF\u4EE5\u63A8\u6D4B\u5176\u4ED6\u7684\u987E\u95EE\u4E13\u5BB6\u7CFB\u7EDF\u2014\u2014\u53EF\u80FD\u662F\u5B83\u4EEC\u4E2D\u7684\u67D0\u4E00\u4E9B,\u6216\u8005\u662F\u8F83\u7B80\u5355\u7684\u7CFB\u7EDF\u2014\u2014\u80FD\u591F\u7528\u5728\u8F6F\u4EF6\u5F00\u53D1\u7684\u5176\u4ED6\u90E8\u5206\u3002</p><p>\u5728\u8F83\u65E9\u5B9E\u73B0\u7684\u7528\u4E8E\u8F6F\u4EF6\u5F00\u53D1\u7684\u4E13\u5BB6\u987E\u95EE\u7CFB\u7EDF\u4E2D,\u5B58\u5728\u7740\u5F88\u591A\u56F0\u96BE\u3002\u5728\u6211\u4EEC\u5047\u8BBE\u7684\u4F8B\u5B50\u4E2D,\u4E00\u4E2A\u5173\u952E\u7684\u95EE\u9898\u662F\u5BFB\u627E\u4E00\u79CD\u65B9\u6CD5,\u80FD\u4ECE\u8F6F\u4EF6\u7ED3\u6784\u7684\u6280\u672F\u8BF4\u660E\u4E2D,\u81EA\u52A8\u6216\u8005\u534A\u81EA\u52A8\u5730\u4EA7\u751F\u8BCA\u65AD\u89C4\u5219\u3002\u53E6\u5916,\u66F4\u52A0\u91CD\u8981\u4E5F\u662F\u66F4\u52A0\u56F0\u96BE\u7684\u4EFB\u52A1\u662F:\u5BFB\u89C5\u80FD\u591F\u6E05\u6670\u8868\u8FBE\u3001\u6DF1\u523B\u7406\u89E3\u4E3A\u4EC0\u4E48\u7684\u5206\u6790\u4E13\u5BB6;\u5F00\u53D1\u6709\u6548\u7684\u6280\u672F\u2014\u2014\u62BD\u53D6\u4E13\u5BB6\u4EEC\u6240\u4E86\u89E3\u7684\u77E5\u8BC6,\u628A\u5B83\u4EEC\u7CBE\u70BC\u6210\u57FA\u7840\u89C4\u5219\u3002\u8FD9\u9879\u5DE5\u4F5C\u7684\u5DE5\u4F5C\u91CF\u662F\u77E5\u8BC6\u83B7\u53D6\u5DE5\u4F5C\u91CF\u7684\u4E24\u500D\u3002\u6784\u5EFA\u4E13\u5BB6\u7CFB\u7EDF\u7684\u5FC5\u8981\u524D\u63D0\u6761\u4EF6\u662F\u62E5\u6709\u4E13\u5BB6\u3002</p><p>\u4E13\u5BB6\u7CFB\u7EDF\u6700\u5F3A\u6709\u529B\u7684\u8D21\u732E\u662F\u7ED9\u7F3A\u4E4F\u7ECF\u9A8C\u7684\u5F00\u53D1\u4EBA\u5458\u63D0\u4F9B\u670D\u52A1,\u7528\u6700\u4F18\u79C0\u5F00\u53D1\u8005\u7684\u7ECF\u9A8C\u548C\u77E5\u8BC6\u79EF\u7D2F\u4E3A\u4ED6\u4EEC\u63D0\u4F9B\u4E86\u6307\u5BFC\u3002\u8FD9\u662F\u975E\u5E38\u5927\u7684\u8D21\u732E\u3002\u6700\u4F18\u79C0\u548C\u4E00\u822C\u7684\u8F6F\u4EF6\u5DE5\u7A0B\u5B9E\u8DF5\u4E4B\u95F4\u7684\u5DEE\u8DDD\u662F\u975E\u5E38\u5927\u7684,\u53EF\u80FD\u6BD4\u5176\u4ED6\u5DE5\u7A0B\u9886\u57DF\u4E2D\u7684\u5DEE\u8DDD\u90FD\u8981\u5927,\u4E00\u79CD\u4F20\u64AD\u4F18\u79C0\u5B9E\u8DF5\u7684\u5DE5\u5177\u7279\u522B\u91CD\u8981\u3002</p><p><strong>\u201C\u81EA\u52A8\u201D\u7F16\u7A0B</strong> \u8FD1\u56DB\u5341\u5E74\u4E2D,\u4EBA\u4EEC\u4E00\u76F4\u5728\u9884\u8A00\u548C\u7F16\u5199\u6709\u5173\u201C\u81EA\u52A8\u7F16\u7A0B\u201D\u7684\u6587\u5B57,\u4ECE\u95EE\u9898\u7684\u4E00\u6BB5\u9648\u8FF0\u8BF4\u660E\u81EA\u52A8\u4EA7\u751F\u89E3\u51B3\u95EE\u9898\u7684\u7A0B\u5E8F\u3002\u73B0\u5728,\u4ECD\u6709\u4E00\u4E9B\u4EBA\u671F\u671B\u8FD9\u6837\u7684\u6280\u672F\u80FD\u591F\u6210\u4E3A\u4E0B\u4E00\u4E2A\u7A81\u7834\u70B9 \u3002</p><p>Parnas \u6697\u793A\u8FD9\u662F\u4E00\u4E2A\u7528\u4E8E\u9B54\u5492\u7684\u672F\u8BED,\u58F0\u79F0\u5B83\u672C\u8EAB\u662F\u8BED\u4E49\u4E0D\u5B8C\u6574\u7684\u3002</p><p>\u4E00\u53E5\u8BDD,\u81EA\u52A8\u7F16\u7A0B\u603B\u662F\u6210\u4E3A\u4E00\u79CD\u70ED\u60C5,\u4F7F\u7528\u73B0\u5728\u5E76\u4E0D\u53EF\u7528\u7684\u66F4\u9AD8\u7EA7\u8BED\u8A00\u7F16\u7A0B\u7684\u70ED\u60C5\u3002\u4ED6\u6307\u51FA,\u5927\u591A\u6570\u60C5\u51B5\u4E0B\u6240\u7ED9\u51FA\u7684\u6280\u672F\u8BF4\u660E\u672C\u8D28\u4E0A\u662F\u95EE\u9898\u7684\u89E3\u51B3\u65B9\u6CD5,\u800C\u4E0D\u662F\u95EE\u9898\u81EA\u8EAB\u3002\u53EF\u4EE5\u627E\u5230\u4E00\u4E9B\u4F8B\u5916\u60C5\u51B5\u3002\u4F8B\u5982,\u6570\u636E\u53D1\u751F\u5668\u7684\u5F00\u53D1\u6280\u672F\u5C31\u975E\u5E38\u5B9E\u7528,\u5E76\u7ECF\u5E38\u5730\u7528\u4E8E\u6392\u5E8F\u7A0B\u5E8F\u4E2D\u3002\u7CFB\u7EDF\u8BC4\u4F30\u82E5\u5E72\u53C2\u6570,\u4ECE\u95EE\u9898\u89E3\u51B3\u65B9\u6848\u5E93\u4E2D\u8FDB\u884C\u9009\u62E9,\u751F\u6210\u5408\u9002\u7684\u7A0B\u5E8F\u3002</p><p>\u8FD9\u6837\u7684\u5E94\u7528\u5177\u6709\u975E\u5E38\u826F\u597D\u7684\u7279\u6027:</p><ul><li>\u95EE\u9898\u901A\u8FC7\u76F8\u5BF9\u8F83\u5C11\u7684\u53C2\u6570\u8FC5\u901F\u5730\u63CF\u8FF0\u51FA\u7279\u5F81\u3002</li><li>\u5B58\u5728\u5F88\u591A\u5DF2\u77E5\u7684\u89E3\u51B3\u65B9\u6848,\u63D0\u4F9B\u4E86\u53EF\u4F9B\u9009\u62E9\u7684\u5E93\u3002</li><li>\u5728\u7ED9\u5B9A\u95EE\u9898\u53C2\u6570\u7684\u524D\u63D0\u4E0B,\u5927\u91CF\u5E7F\u6CDB\u7684\u5206\u6790\u4E3A\u9009\u62E9\u5177\u4F53\u7684\u89E3\u51B3\u6280\u672F\u63D0\u4F9B\u4E86\u6E05\u6670\u7684\u89C4\u5219\u3002</li></ul><p>\u5177\u6709\u4E0A\u8FF0\u7B80\u6D01\u5C5E\u6027\u7684\u7CFB\u7EDF\u662F\u4E00\u4E2A\u4F8B\u5916,\u5F88\u96BE\u770B\u5230\u8BE5\u65B9\u6CD5\u80FD\u666E\u53CA\u5230\u66F4\u5E7F\u6CDB\u7684\u5BFB\u5E38\u8F6F\u4EF6\u7CFB\u7EDF,\u751A\u81F3\u96BE\u4EE5\u60F3\u8C61\u8FD9\u79CD\u7A81\u7834\u5982\u4F55\u80FD\u591F\u8FDB\u884C\u63A8\u5E7F\u3002</p><p><strong>\u56FE\u5F62\u5316\u7F16\u7A0B</strong> \u5728\u8F6F\u4EF6\u5DE5\u7A0B\u7684\u535A\u58EB\u8BBA\u6587\u4E2D,\u4E00\u4E2A\u5F88\u53D7\u6B22\u8FCE\u7684\u4E3B\u9898\u662F\u56FE\u5F62\u5316\u548C\u53EF\u89C6\u5316\u7F16\u7A0B,\u8BA1\u7B97\u673A\u56FE\u5F62\u5728\u8F6F\u4EF6\u8BBE\u8BA1\u4E0A\u7684\u5E94\u7528 \u3002\u8FD9\u79CD\u65B9\u6CD5\u7684\u63A8\u6D4B\u90E8\u5206\u6765\u81EA VLSI \u82AF\u7247\u8BBE\u8BA1\u7684\u7C7B\u6BD4,\u8BA1\u7B97\u673A\u56FE\u5F62\u5316\u5728\u8BBE\u8BA1\u4E2D\u626E\u6F14\u4E86\u9AD8\u751F\u4EA7\u529B\u7684\u89D2\u8272\u3002\u90E8\u5206\u6E90\u4E8E\u2014\u2014\u4EBA\u4EEC\u5C06\u6D41\u7A0B\u56FE\u4F5C\u4E3A\u4E00\u79CD\u7406\u60F3\u7684\u8BBE\u8BA1\u4ECB\u8D28,\u5E76\u4E3A\u7ED8\u5236\u5B83\u4EEC\u63D0\u4F9B\u4E86\u5F88\u591A\u529F\u80FD\u5F3A\u5927\u7684\u5B9E\u7528\u7A0B\u5E8F\u2014\u2014\u8FD9\u8BC1\u5B9E\u4E86\u56FE\u5F62\u5316\u7684\u53EF\u884C\u6027\u3002</p><p>\u4E0D\u8FC7,\u4E0A\u8FF0\u65B9\u6CD5\u4E2D\u81F3\u4ECA\u8FD8\u6CA1\u6709\u51FA\u73B0\u4EFB\u4F55\u4EE4\u4EBA\u4FE1\u670D\u548C\u6FC0\u52A8\u7684\u8FDB\u6B65\u3002\u6211\u786E\u4FE1\u5C06\u6765\u4E5F\u4E0D\u4F1A\u51FA\u73B0\u3002</p><p>\u9996\u5148,\u5982\u540C\u6211\u5148\u524D\u6240\u63D0\u51FA\u7684,\u6D41\u7A0B\u56FE\u662F\u4E00\u79CD\u975E\u5E38\u5DEE\u52B2\u8F6F\u4EF6\u7ED3\u6784\u8868\u8FBE\u65B9\u6CD5 \u3002\u5B9E\u9645\u4E0A,\u5B83\u6700\u597D\u88AB\u89C6\u4E3A\u662F\u51AF.\u8BFA\u4F9D\u66FC\u3001\u6208\u5C14\u5FB7\u65AF\u5EF7\u548C\u52C3\u514B\u65AF\u8BD5\u56FE\u4E3A\u4ED6\u4EEC\u6240\u8BBE\u8BA1\u7684\u8BA1\u7B97\u673A\u63D0\u4F9B\u7684\u4E00\u79CD\u5F53\u65F6\u8FEB\u5207\u9700\u8981\u7684\u9AD8\u7EA7\u63A7\u5236\u8BED\u8A00\u3002\u5982\u4ECA\u7684\u6D41\u7A0B\u56FE\u5DF2\u7ECF\u53D8\u5F97\u590D\u6742,\u4E00\u5F20\u56FE\u6709\u82E5\u5E72\u9875,\u6709\u5F88\u591A\u8FDE\u63A5\u7ED3\u70B9\u3002</p><p>\u8FD9\u79CD\u8868\u73B0\u5F62\u5F0F\u5B9E\u5728\u4EE4\u4EBA\u540C\u60C5\u3002\u6D41\u7A0B\u56FE\u5DF2\u7ECF\u6210\u4E3A\u5B8C\u5168\u4E0D\u5FC5\u8981\u7684\u8BBE\u8BA1\u5DE5\u5177\u2014\u2014\u7A0B\u5E8F\u5458\u5728\u5F00\u53D1\u4E4B\u540E,\u800C\u4E0D\u662F\u4E4B\u524D\u7ED8\u5236\u63CF\u8FF0\u7A0B\u5E8F\u7684\u6D41\u7A0B\u56FE\u3002</p><p>\u5176\u6B21,\u73B0\u5728\u7684\u5C4F\u5E55\u975E\u5E38\u5C0F,\u50CF\u7D20\u7EA7\u522B,\u65E0\u6CD5\u540C\u65F6\u8868\u73B0\u8F6F\u4EF6\u56FE\u5F62\u7684\u6240\u6709\u6B63\u5F0F\u3001\u8BE6\u7EC6\u7684\u8303\u56F4\u548C\u7EC6\u8282\u3002\u73B0\u5728\u6240\u8C13 \u201C\u7C7B\u4F3C\u684C\u9762\u201D\u7684\u5DE5\u4F5C\u7AD9\u5B9E\u9645\u4E0A\u50CF\u662F\u201C\u98DE\u673A\u5750\u8231\u5EA7\u6905\u201D\u3002\u98DE\u673A\u4E0A,\u4EFB\u4F55\u5750\u5728\u4E24\u4E2A\u80A5\u80D6\u4E58\u5BA2\u4E4B\u95F4,\u53CD\u590D\u632A\u52A8\u4E00\u5927\u515C\u6587\u4EF6\u7684\u4EBA\u4F1A\u610F\u8BC6\u5230\u8FD9\u4E2D\u95F4\u7684\u5DEE\u522B\u2014\u2014\u6BCF\u6B21\u53EA\u80FD\u770B\u5230\u5F88\u5C11\u7684\u5185\u5BB9\u3002\u771F\u6B63\u7684\u684C\u9762\u63D0\u4F9B\u4E86\u5F88\u591A\u6587\u4EF6\u7684\u603B\u89C8,\u8BA9\u5927\u5BB6\u53EF\u4EE5\u968F\u610F\u5730\u4F7F\u7528\u5B83\u4EEC\u3002\u6B64\u5916,\u5F53\u4EBA\u4EEC\u7684\u521B\u9020\u529B\u4E00\u9635\u9635\u5730\u6D8C\u73B0\u65F6,\u5F00\u53D1\u4EBA\u5458\u5927\u591A\u6570\u90FD\u4F1A\u820D\u5F03\u5DE5\u4F5C\u53F0,\u4F7F\u7528\u7A7A\u95F4\u66F4\u4E3A\u5E7F\u9614\u7684\u5730\u677F\u3002\u8981\u4F7F\u6211\u4EEC\u9762\u5BF9\u7684\u5DE5\u4F5C\u7A7A\u95F4\u6EE1\u8DB3\u8F6F\u4EF6\u5F00\u53D1\u5DE5\u4F5C\u7684\u9700\u8981,\u786C\u4EF6\u6280\u672F\u5FC5\u987B\u8FDB\u4E00\u6B65\u53D1\u5C55\u3002</p><p>\u66F4\u52A0\u57FA\u672C\u7684\u662F,\u5982\u540C\u6211\u4EEC\u4E0A\u9762\u6240\u4E89\u8BBA\u7684,\u8F6F\u4EF6\u975E\u5E38\u96BE\u4EE5\u53EF\u89C6\u5316\u3002\u5373\u4F7F\u7528\u56FE\u5F62\u8868\u8FBE\u51FA\u4E86\u6D41\u7A0B\u56FE\u3001\u53D8\u91CF\u8303\u56F4\u5D4C\u5957\u60C5\u51B5\u3001\u53D8\u91CF\u4EA4\u53C9\u5F15\u7528\u3001\u6570\u636E\u6D41\u3001\u5C42\u6B21\u5316\u6570\u636E\u7ED3\u6784\u7B49\u7B49,\u4E5F\u53EA\u662F\u8868\u8FBE\u4E86\u67D0\u4E2A\u65B9\u9762,\u5C31\u50CF\u76F2\u4EBA\u6478\u8C61\u4E00\u6837\u3002\u5982\u679C\u6211\u4EEC\u628A\u5F88\u591A\u76F8\u5173\u7684\u89C6\u56FE\u53E0\u52A0\u5728\u6240\u4EA7\u751F\u7684\u56FE\u5F62\u4E0A,\u90A3\u4E48\u5F88\u96BE\u518D\u62BD\u53D6\u51FA\u5168\u5C40\u7684\u603B\u4F53\u89C6\u56FE\u3002\u5BF9 VLSI \u82AF\u7247\u8BBE\u8BA1\u65B9\u6CD5\u7684\u7C7B\u63A8\u662F\u4E00\u79CD\u8BEF\u5BFC\u2014\u2014\u82AF\u7247\u8BBE\u8BA1\u662F\u5BF9\u4E24\u7EF4\u5BF9\u8C61\u7684\u5C42\u6B21\u8BBE\u8BA1,\u5B83\u7684\u51E0\u4F55\u7279\u6027\u53CD\u6620\u4E86\u5B83\u7684\u672C\u8D28\u7279\u6027,\u800C\u8F6F\u4EF6\u7CFB\u7EDF\u4E0D\u662F\u8FD9\u6837\u3002</p><p><strong>\u7A0B\u5E8F\u9A8C\u8BC1</strong> \u73B0\u4EE3\u7F16\u7A0B\u7684\u8BB8\u591A\u5DE5\u4F5C\u662F\u6D4B\u8BD5\u548C\u4FEE\u590D bug\u3002\u662F\u5426\u6709\u53EF\u80FD\u51FA\u73B0\u94F6\u5F39,\u80FD\u591F\u5728\u7CFB\u7EDF\u8BBE\u8BA1\u7EA7\u522B\u3001\u6E90\u4EE3\u7801\u7EA7\u522B\u6D88\u9664 bug \u5462?\u662F\u5426\u53EF\u4EE5\u5728\u5927\u91CF\u5DE5\u4F5C\u88AB\u6295\u5165\u5230\u5B9E\u73B0\u548C\u6D4B\u8BD5\u4E4B\u524D,\u901A\u8FC7\u91C7\u7528\u8BC1\u5B9E\u8BBE\u8BA1\u6B63\u786E\u6027\u7684\u201C\u6DF1\u5965\u201D\u7B56\u7565,\u5F7B\u5E95\u63D0\u9AD8\u8F6F\u4EF6\u7684\u751F\u4EA7\u7387\u548C\u4EA7\u54C1\u7684\u53EF\u9760\u6027?</p><p>\u6211\u5E76\u4E0D\u8BA4\u4E3A\u5728\u8FD9\u91CC\u80FD\u627E\u5230\u9B54\u6CD5\u3002\u7A0B\u5E8F\u9A8C\u8BC1\u7684\u786E\u662F\u5F88\u5148\u8FDB\u7684\u6982\u5FF5,\u5B83\u5BF9\u5B89\u5168\u64CD\u4F5C\u7CFB\u7EDF\u5185\u6838\u7B49\u8FD9\u7C7B\u5E94\u7528\u662F\u975E\u5E38\u91CD\u8981\u7684\u3002\u4E0D\u8FC7,\u8FD9\u9879\u6280\u672F\u5E76\u4E0D\u80FD\u4FDD\u8BC1\u8282\u7EA6\u52B3\u52A8\u529B\u3002\u9A8C\u8BC1\u8981\u6C42\u5982\u6B64\u591A\u7684\u5DE5\u4F5C\u91CF,\u4EE5\u81F4\u4E8E\u53EA\u6709\u5C11\u91CF\u7684\u7A0B\u5E8F\u80FD\u591F\u5F97\u5230\u9A8C\u8BC1\u3002</p><p>\u7A0B\u5E8F\u9A8C\u8BC1\u4E0D\u610F\u5473\u7740\u96F6\u7F3A\u9677\u7684\u7A0B\u5E8F\u3002\u8FD9\u91CC\u5E76\u6CA1\u6709\u4EC0\u4E48\u9B54\u672F,\u6570\u5B66\u9A8C\u8BC1\u4ECD\u7136\u53EF\u80FD\u662F\u6709\u9519\u8BEF\u7684\u3002\u56E0\u6B64,\u5C3D\u7BA1\u9A8C\u8BC1\u53EF\u80FD\u51CF\u5C11\u7A0B\u5E8F\u6D4B\u8BD5\u7684\u5DE5\u4F5C\u91CF,\u4F46\u5374\u4E0D\u80FD\u7701\u7565\u7A0B\u5E8F\u6D4B\u8BD5\u3002</p><p>\u66F4\u4E25\u8083\u5730\u8BF4,\u5B8C\u7F8E\u7684\u7A0B\u5E8F\u9A8C\u8BC1\u53EA\u80FD\u5EFA\u7ACB\u6EE1\u8DB3\u6280\u672F\u8BF4\u660E\u7684\u7A0B\u5E8F\u3002\u8FD9\u65F6,\u8F6F\u4EF6\u5DE5\u4F5C\u4E2D\u6700\u56F0\u96BE\u7684\u90E8\u5206\u5DF2\u7ECF\u63A5\u8FD1\u5B8C\u6210,\u5F62\u6210\u4E86\u5B8C\u6574\u548C\u4E00\u81F4\u7684\u8BF4\u660E\u3002\u5F00\u53D1\u7A0B\u5E8F\u7684\u4E00\u4E9B\u5FC5\u8981\u5DE5\u4F5C\u5B9E\u9645\u4E0A\u5DF2\u7ECF\u53D8\u6210\u5BF9\u6280\u672F\u89C4\u683C\u8BF4\u660E\u8FDB\u884C\u6D4B\u8BD5\u3002</p><p><strong>\u73AF\u5883\u548C\u5DE5\u5177</strong> \u5411\u66F4\u597D\u7684\u7F16\u7A0B\u5F00\u53D1\u73AF\u5883\u5F00\u53D1\u4E2D\u6295\u5165,\u6211\u4EEC\u53EF\u4EE5\u671F\u5F85\u5F97\u5230\u591A\u5C11\u56DE\u62A5\u5462?\u4EBA\u4EEC\u7684\u672C\u80FD\u53CD\u5E94\u662F\u9996\u5148\u7740\u624B\u89E3\u51B3\u9AD8\u56DE\u62A5\u7684\u95EE\u9898:\u5C42\u6B21\u5316\u6587\u4EF6\u7CFB\u7EDF,\u7EDF\u4E00\u6587\u4EF6\u683C\u5F0F\u4EE5\u83B7\u5F97\u4E00\u81F4\u7684\u7F16\u7A0B\u63A5\u53E3\u548C\u901A\u7528\u5DE5\u5177\u7B49\u3002\u7279\u5B9A\u8BED\u8A00\u7684\u667A\u80FD\u5316\u7F16\u8F91\u5668\u5728\u73B0\u5B9E\u4E2D\u8FD8\u6CA1\u6709\u5F97\u5230\u5E7F\u6CDB\u5E94\u7528,\u4E0D\u8FC7\u5B83\u4EEC\u6700\u6709\u5E0C\u671B\u5B9E\u73B0\u7684\u662F\u6D88\u9664\u8BED\u6CD5\u9519\u8BEF\u548C\u7B80\u5355\u7684\u8BED\u4E49\u9519\u8BEF\u3002</p><p>\u5F00\u53D1\u73AF\u5883\u4E0A,\u73B0\u5728\u5DF2\u7ECF\u5B9E\u73B0\u7684\u6700\u5927\u6210\u679C\u53EF\u80FD\u662F\u96C6\u6210\u6570\u636E\u5E93\u7684\u4F7F\u7528,\u7528\u6765\u8DDF\u8E2A\u5927\u91CF\u7684\u5F00\u53D1\u7EC6\u8282,\u4F9B\u6BCF\u4E2A\u7A0B\u5E8F\u5458\u7CBE\u786E\u5730\u67E5\u9605\u4FE1\u606F,\u4EE5\u53CA\u5728\u6574\u4E2A\u56E2\u961F\u534F\u4F5C\u5F00\u53D1\u4E2D\u4FDD\u6301\u6700\u65B0\u7684\u72B6\u6001\u3002</p><p>\u663E\u7136,\u8FD9\u6837\u7684\u5DE5\u4F5C\u662F\u975E\u5E38\u6709\u4EF7\u503C\u7684,\u5B83\u80FD\u5E26\u6765\u8F6F\u4EF6\u751F\u4EA7\u7387\u548C\u53EF\u9760\u6027\u4E0A\u7684\u4E00\u4E9B\u63D0\u9AD8\u3002\u4F46\u662F,\u7531\u4E8E\u5B83\u81EA\u8EAB\u7684\u7279\u6027,\u76EE\u524D\u5B83\u7684\u56DE\u62A5\u5F88\u6709\u9650\u3002</p><p><strong>\u5DE5\u4F5C\u7AD9</strong> \u968F\u7740\u5DE5\u4F5C\u7AD9\u7684\u5904\u7406\u80FD\u529B\u548C\u5185\u5B58\u5BB9\u91CF\u7684\u7A33\u56FA\u548C\u5FEB\u901F\u63D0\u9AD8,\u6211\u4EEC\u80FD\u671F\u671B\u5728\u8F6F\u4EF6\u9886\u57DF\u53D6\u5F97\u591A\u5927\u7684\u6536\u83B7\u5462?\u73B0\u5728\u7684\u8FD0\u7B97\u901F\u5EA6\u5DF2\u7ECF\u53EF\u4EE5\u5B8C\u5168\u6EE1\u8DB3\u7A0B\u5E8F\u7F16\u5236\u548C\u6587\u6863\u4E66\u5199\u7684\u9700\u8981\u3002\u7F16\u8BD1\u8FD8\u9700\u8981\u4E00\u4E9B\u63D0\u9AD8,\u4E0D\u8FC7\u4E00\u65E6\u673A\u5668\u8FD0\u7B97\u901F\u5EA6\u63D0\u9AD8\u5341\u500D,\u90A3\u4E48\u7A0B\u5E8F\u5F00\u53D1\u4EBA\u5458\u7684\u601D\u8003\u6D3B\u52A8\u5C06\u6210\u4E3A\u65E5\u5E38\u5DE5\u4F5C\u7684\u4E3B\u8981\u6D3B\u52A8\u3002\u5B9E\u9645\u4E0A,\u8FD9\u5DF2\u7ECF\u662F\u73B0\u5728\u7684\u60C5\u51B5\u3002</p><p>\u6211\u4EEC\u5F53\u7136\u6B22\u8FCE\u66F4\u52A0\u5F3A\u5927\u7684\u5DE5\u4F5C\u7AD9,\u4F46\u662F\u4E0D\u80FD\u671F\u671B\u6709\u9B54\u672F\u822C\u7684\u63D0\u9AD8\u3002</p><h2 id="\u9488\u5BF9\u6982\u5FF5\u4E0A\u6839\u672C\u95EE\u9898\u7684\u9887\u5177\u524D\u9014\u7684\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u9488\u5BF9\u6982\u5FF5\u4E0A\u6839\u672C\u95EE\u9898\u7684\u9887\u5177\u524D\u9014\u7684\u65B9\u6CD5" aria-hidden="true">#</a> \u9488\u5BF9\u6982\u5FF5\u4E0A\u6839\u672C\u95EE\u9898\u7684\u9887\u5177\u524D\u9014\u7684\u65B9\u6CD5</h2><p>\u867D\u7136\u73B0\u5728\u8F6F\u4EF6\u4E0A\u6CA1\u6709\u6280\u672F\u4E0A\u7684\u7A81\u7834\u80FD\u591F\u9884\u793A\u6211\u4EEC\u53EF\u4EE5\u53D6\u5F97\u50CF\u5728\u786C\u4EF6\u9886\u57DF\u4E0A\u4E00\u6837\u7684\u8FDB\u5C55,\u4F46\u5728\u73B0\u5B9E\u7684\u8F6F\u4EF6\u9886\u57DF\u4E2D,\u65E2\u6709\u5927\u91CF\u4F18\u79C0\u7684\u5DE5\u4F5C,\u4E5F\u6709\u4E0D\u5F15\u4EBA\u6CE8\u610F\u7684\u5E73\u7A33\u8FDB\u6B65\u3002</p><p>\u6240\u6709\u9488\u5BF9\u8F6F\u4EF6\u5F00\u53D1\u8FC7\u7A0B\u4E2D\u6B21\u8981\u56F0\u96BE\u7684\u6280\u672F\u5DE5\u4F5C\u57FA\u672C\u4E0A\u80FD\u8868\u8FBE\u6210\u4EE5\u4E0B\u7684\u751F\u4EA7\u7387\u516C\u5F0F:</p><pre><code>  \u4EFB\u52A1\u65F6\u95F4 = \u2211(\u9891\u7387) i \xD7(\u65F6\u95F4) i
</code></pre><p>\u5982\u679C\u548C\u6211\u6240\u8BA4\u4E3A\u7684\u4E00\u6837,\u5DE5\u4F5C\u7684\u521B\u9020\u6027\u90E8\u5206\u5360\u636E\u4E86\u5927\u90E8\u5206\u65F6\u95F4,\u90A3\u4E48\u90A3\u4E9B\u4EC5\u4EC5\u662F\u8868\u8FBE\u6982\u5FF5\u7684\u6D3B\u52A8\u5E76\u4E0D\u80FD\u5728\u5F88\u5927\u7A0B\u5EA6\u4E0A\u5F71\u54CD\u751F\u4EA7\u7387\u3002</p><p>\u56E0\u6B64,\u6211\u4EEC\u5FC5\u987B\u8003\u8651\u90A3\u4E9B\u89E3\u51B3\u8F6F\u4EF6\u4E0A\u5FC5\u8981\u56F0\u96BE\u7684\u6D3B\u52A8\u2014\u2014\u5373,\u51C6\u786E\u5730\u8868\u8FBE\u590D\u6742\u6982\u5FF5\u7ED3\u6784\u3002\u5E78\u8FD0\u7684\u662F,\u5176\u4E2D\u7684\u4E00\u4E9B\u975E\u5E38\u6709\u5E0C\u671B\u3002</p><p><strong>\u8D2D\u4E70\u548C\u81EA\u884C\u5F00\u53D1</strong> \u6784\u5EFA\u8F6F\u4EF6\u6700\u53EF\u80FD\u7684\u5F7B\u5E95\u89E3\u51B3\u65B9\u6848\u662F\u4E0D\u5F00\u53D1\u4EFB\u4F55\u8F6F\u4EF6\u3002</p><p>\u60C5\u51B5\u6BCF\u4E00\u5929\u90FD\u6709\u4E9B\u597D\u8F6C,\u8D8A\u6765\u8D8A\u591A\u7684\u8F6F\u4EF6\u63D0\u4F9B\u5546,\u4E3A\u5404\u79CD\u773C\u82B1\u7F2D\u4E71\u7684\u5E94\u7528\u7A0B\u5E8F\u63D0\u4F9B\u4E86\u8D28\u91CF\u66F4\u597D\u3001\u6570\u91CF\u66F4\u591A\u7684\u8F6F\u4EF6\u4EA7\u54C1\u3002\u5F53\u6211\u4EEC\u7684\u8F6F\u4EF6\u5DE5\u7A0B\u5E08\u6B63\u5FD9\u4E8E\u751F\u4EA7\u65B9\u6CD5\u5B66\u65F6,\u4E2A\u4EBA\u8BA1\u7B97\u673A\u7684\u60CA\u5929\u52A8\u5730\u7684\u53D8\u5316\u4E3A\u8F6F\u4EF6\u521B\u9020\u4E86\u5E7F\u9614\u7684\u5E02\u573A\u3002\u6BCF\u4E2A\u62A5\u644A\u4E0A\u90FD\u6709\u5927\u91CF\u7684\u6708\u520A,\u6839\u636E\u673A\u5668\u7684\u7C7B\u578B,\u520A\u767B\u7740\u4ECE\u51E0\u7F8E\u5143\u5230\u51E0\u767E\u7F8E\u5143\u7684\u5404\u79CD\u4EA7\u54C1\u7684\u5E7F\u544A\u548C\u8BC4\u8BBA\u3002\u66F4\u591A\u4E13\u4E1A\u5382\u5546\u4E3A\u5DE5\u4F5C\u7AD9\u548C UNIX \u5E02\u573A\u63D0\u4F9B\u4E86\u5F88\u591A\u975E\u5E38\u6709\u7ADE\u4E89\u529B\u7684\u4EA7\u54C1,\u751A\u81F3\u5F88\u591A\u5DE5\u5177\u8F6F\u4EF6\u548C\u5F00\u53D1\u73AF\u5883\u8F6F\u4EF6\u90FD\u53EF\u4EE5\u968F\u65F6\u8D2D\u4E70\u4F7F\u7528\u3002</p><p>\u5BF9\u4E8E\u72EC\u7ACB\u7684\u8F6F\u4EF6\u6A21\u5757\u5E02\u573A,\u6211\u5DF2\u5728\u5176\u4ED6\u7684\u5730\u65B9\u63D0\u51FA\u4E00\u4E9B\u5EFA\u8BAE\u3002</p><p>\u4EE5\u4E0A\u63D0\u5230\u7684\u4EFB\u4F55\u8F6F\u4EF6,\u8D2D\u4E70\u90FD\u8981\u6BD4\u91CD\u65B0\u5F00\u53D1\u8981\u4F4E\u5EC9\u4E00\u4E9B\u3002\u5373\u4F7F\u652F\u4ED8 100,000 \u7F8E\u5143,\u8D2D\u4E70\u7684\u8F6F\u4EF6\u4E5F\u4EC5\u4EC5\u662F\u4E00\u4E2A\u4EBA\u5E74\u7684\u6210\u672C\u3002\u800C\u4E14\u8F6F\u4EF6\u662F\u7ACB\u5373\u53EF\u7528\u7684!\u81F3\u5C11\u5BF9\u4E8E\u73B0\u6709\u7684\u4EA7\u54C1\u3001\u5BF9\u4E8E\u90A3\u4E9B\u4E13\u6CE8\u4E8E\u8BE5\u9886\u57DF\u5F00\u53D1\u8005\u7684\u6210\u679C\u800C\u8A00,\u5B83\u4EEC\u662F\u53EF\u4EE5\u7ACB\u523B\u6295\u5165\u4F7F\u7528\u7684\u3002\u5E76\u4E14,\u5B83\u4EEC\u5F80\u5F80\u914D\u5907\u4E86\u4E66\u5199\u826F\u597D\u7684\u6587\u6863,\u5728\u67D0\u79CD\u7A0B\u5EA6\u4E0A\u6BD4\u81EA\u884C\u5F00\u53D1\u7684\u8F6F\u4EF6\u7EF4\u62A4\u5F97\u66F4\u52A0\u5B8C\u5907\u3002</p><p>\u6211\u76F8\u4FE1,\u8FD9\u4E2A\u5927\u4F17\u5E02\u573A\u5C06\u662F\u8F6F\u4EF6\u5DE5\u7A0B\u9886\u57DF\u610F\u4E49\u6700\u6DF1\u8FDC\u7684\u5F00\u53D1\u65B9\u5411\u3002\u8F6F\u4EF6\u6210\u672C\u4E00\u76F4\u662F\u5F00\u53D1\u7684\u6210\u672C,\u800C\u4E0D\u662F\u590D\u5236\u7684\u6210\u672C\u3002\u6240\u4EE5,\u5373\u4F7F\u53EA\u5728\u5C11\u6570\u4F7F\u7528\u8005\u4E4B\u95F4\u5B9E\u73B0\u5171\u4EAB,\u4E5F\u80FD\u5728\u5F88\u5927\u7A0B\u5EA6\u4E0A\u51CF\u5C11\u6210\u672C\u3002\u53E6\u4E00\u79CD\u770B\u6CD5\u662F\u4F7F\u7528\u8F6F\u4EF6\u7CFB\u7EDF\u7684 n \u4E2A\u62F7\u8D1D,\u5C06\u4F1A\u4F7F\u5F00\u53D1\u4EBA\u5458\u7684\u751F\u4EA7\u7387\u6709\u6548\u5730\u63D0\u9AD8 n \u500D\u3002\u8FD9\u662F\u4E00\u4E2A\u9886\u57DF\u548C\u884C\u4E1A\u8303\u56F4\u7684\u63D0\u9AD8\u3002</p><p>\u5F53\u7136,\u5173\u952E\u7684\u95EE\u9898\u8FD8\u662F\u53EF\u7528\u6027\u3002\u662F\u5426\u53EF\u4EE5\u5728\u81EA\u5DF1\u7684\u5F00\u53D1\u5DE5\u4F5C\u4E2D\u4F7F\u7528\u5546\u7528\u7684\u8F6F\u4EF6\u5305?\u8FD9\u91CC,\u6709\u4E00\u4E2A\u4EE4\u4EBA\u5403\u60CA\u7684\u95EE\u9898\u3002\u5728 1950~1960 \u5E74\u671F\u95F4,\u4E00\u4E2A\u63A5\u4E00\u4E2A\u7684\u7814\u7A76\u663E\u793A,\u7528\u6237\u4E0D\u4F1A\u5728\u5DE5\u8D44\u7CFB\u7EDF\u3001\u7269\u6D41\u63A7\u5236\u3001\u5E10\u52A1\u5904\u7406\u7B49\u7CFB\u7EDF\u4E2D\u4F7F\u7528\u5546\u7528\u8F6F\u4EF6\u5305\u3002\u9700\u6C42\u5F80\u5F80\u8FC7\u4E8E\u4E13\u4E1A,\u4E0D\u540C\u60C5\u51B5\u4E4B\u95F4\u7684\u5DEE\u522B\u592A\u5927\u3002\u5728 80 \u5E74\u4EE3,\u6211\u4EEC\u53D1\u73B0\u8FD9\u4E9B\u8F6F\u4EF6\u5305\u7684\u9700\u6C42\u5927\u4E3A\u589E\u52A0,\u5E76\u5F97\u5230\u4E86\u5927\u89C4\u6A21\u7684\u4F7F\u7528\u3002</p><p>\u4EC0\u4E48\u5BFC\u81F4\u4E86\u8FD9\u6837\u7684\u53D8\u5316?</p><p>\u5E76\u4E0D\u662F\u8F6F\u4EF6\u5305\u53D1\u751F\u4E86\u53D8\u5316\u3002\u5B83\u4EEC\u53EF\u80FD\u6BD4\u4EE5\u524D\u66F4\u52A0\u901A\u7528\u548C\u66F4\u52A0\u5BA2\u6237\u5316\u4E00\u4E9B,\u4F46\u5E76\u4E0D\u592A\u591A\u3002</p><p>\u540C\u6837,\u4E5F\u4E0D\u662F\u5E94\u7528\u53D1\u751F\u4E86\u53D8\u5316\u3002\u5373\u4F7F\u6709,\u4ECA\u5929\u7684\u5546\u4E1A\u548C\u5B66\u672F\u4E0A\u7684\u9700\u8981\u4E5F\u6BD4 20 \u5E74\u4EE5\u524D\u66F4\u52A0\u4E0D\u540C\u548C\u590D\u6742\u3002</p><p>\u91CD\u5927\u7684\u53D8\u5316\u5728\u4E8E\u8BA1\u7B97\u673A\u786C\u4EF6/\u8F6F\u4EF6\u6210\u672C\u6BD4\u7387\u3002\u5728 1960 \u5E74,2 \u767E\u4E07\u7F8E\u5143\u673A\u5668\u7684\u8D2D\u4E70\u8005\u89C9\u5F97\u4ED6\u53EF\u4EE5\u4E3A\u5B9A\u5236\u7684\u85AA\u8D44\u7CFB\u7EDF\u652F\u4ED8 250,000 \u7F8E\u5143\u3002\u73B0\u5728,\u5BF9 50,000 \u7F8E\u5143\u7684\u529E\u516C\u5BA4\u673A\u5668\u8D2D\u4E70\u8005\u800C\u8A00,\u5F88\u96BE\u60F3\u8C61\u80FD\u4E3A\u5B9A\u5236\u85AA\u8D44\u7CFB\u7EDF\u518D\u652F\u4ED8\u8D39\u7528\u3002\u56E0\u6B64,\u4ED6\u4EEC\u628A\u4E0A\u8FF0\u7CFB\u7EDF\u7684\u6A21\u5757\u8FDB\u884C\u8C03\u6574,\u6DFB\u52A0\u5230\u53EF\u7528\u7684\u8F6F\u4EF6\u5305\u4E2D\u3002\u8BA1\u7B97\u673A\u73B0\u5728\u5982\u6B64\u7684\u666E\u904D,\u4E0A\u8FF0\u7684\u6539\u7F16\u548C\u8C03\u6574\u662F\u53D1\u5C55\u7684\u5FC5\u7136\u7ED3\u679C\u3002</p><p>\u6211\u7684\u4E0A\u8FF0\u89C2\u70B9\u4E5F\u5B58\u5728\u4E00\u4E9B\u620F\u5267\u6027\u7684\u4F8B\u5916\u2014\u2014\u8F6F\u4EF6\u5305\u7684\u901A\u7528\u5316\u65B9\u9762\u5E76\u6CA1\u6709\u53D1\u751F\u4EC0\u4E48\u53D8\u5316,\u9664\u4E86\u7535\u5B50\u8868\u683C\u548C\u7B80\u5355\u7684\u6570\u636E\u5E93\u7CFB\u7EDF\u3002\u8FD9\u4E9B\u5F3A\u5927\u7684\u5DE5\u5177,\u51FA\u73B0\u5F97\u5982\u6B64\u4E4B\u665A\u548C\u5982\u6B64\u9192\u76EE,\u5BFC\u81F4\u65E0\u6570\u5E94\u7528\u4E2D\u7684\u4E00\u4E9B\u5E76\u4E0D\u5341\u5206\u89C4\u8303\u3002\u5927\u91CF\u7684\u6587\u7AE0\u3001\u751A\u81F3\u4E66\u7C4D\u8BB2\u8FF0\u4E86\u5982\u4F55\u4F7F\u7528\u7535\u5B50\u8868\u683C\u5E94\u4ED8\u5F88\u591A\u610F\u60F3\u4E0D\u5230\u7684\u95EE\u9898\u3002\u539F\u5148\u4F5C\u4E3A\u5BA2\u6237\u7A0B\u5E8F,\u4F7F\u7528 Cobol \u6216\u8005\u62A5\u8868\u751F\u6210\u7A0B\u5E8F\u7F16\u5199\u7684\u5927\u91CF\u5E94\u7528,\u5982\u4ECA\u5DF2\u7ECF\u88AB\u8FD9\u4E9B\u5DE5\u5177\u6240\u53D6\u4EE3\u3002</p><p>\u73B0\u5728\u5F88\u591A\u7528\u6237\u5929\u5929\u64CD\u4F5C\u8BA1\u7B97\u673A,\u4F7F\u7528\u7740\u5404\u79CD\u5404\u6837\u7684\u5E94\u7528\u7A0B\u5E8F,\u4F46\u5E76\u4E0D\u7F16\u5199\u4EE3\u7801\u3002\u4E8B\u5B9E\u4E0A,\u4ED6\u4EEC\u4E2D\u95F4\u5F88\u591A\u4EBA\u65E0\u6CD5\u4E3A\u81EA\u5DF1\u7684\u8BA1\u7B97\u673A\u7F16\u5199\u4EFB\u4F55\u7A0B\u5E8F,\u4E0D\u8FC7\u4ED6\u4EEC\u975E\u5E38\u719F\u7EC3\u5730\u4F7F\u7528\u8BA1\u7B97\u673A\u6765\u89E3\u51B3\u65B0\u95EE\u9898\u3002</p><p>\u6211\u8BA4\u4E3A,\u5BF9\u4E8E\u73B0\u5728\u7684\u5F88\u591A\u7EC4\u7EC7\u673A\u6784\u6765\u8BF4,\u6700\u6709\u6548\u7684\u8F6F\u4EF6\u751F\u4EA7\u7387\u7B56\u7565\u662F\u5728\u751F\u4EA7\u4E00\u7EBF\u914D\u5907\u5F88\u591A\u4E2A\u4EBA\u8BA1\u7B97\u673A,\u5B89\u88C5\u597D\u901A\u7528\u7684\u4E66\u5199\u3001\u4F5C\u56FE\u3001\u6587\u4EF6\u7BA1\u7406\u548C\u7535\u5B50\u8868\u683C\u7A0B\u5E8F,\u4EE5\u53CA\u914D\u5907\u80FD\u719F\u7EC3\u4F7F\u7528\u5B83\u4EEC\u7684\u4EBA\u5458,\u5E76\u4E14\u628A\u8FD9\u4E9B\u4EBA\u5458\u6563\u5E03\u5230\u5404\u4E2A\u5C97\u4F4D\u3002\u7C7B\u4F3C\u7684\u7B56\u7565\u2014\u2014\u901A\u7528\u7684\u6570\u5B66\u548C\u7EDF\u8BA1\u8F6F\u4EF6\u5305,\u4EE5\u53CA\u4E00\u4E9B\u7B80\u5355\u7684\u7F16\u7A0B\u80FD\u529B,\u540C\u6837\u5730\u9002\u7528\u4E8E\u5F88\u591A\u5B9E\u9A8C\u5BA4\u7684\u79D1\u5B66\u5DE5\u4F5C\u8005\u3002</p><p><strong>\u9700\u6C42\u7CBE\u70BC\u548C\u5FEB\u901F\u539F\u578B</strong> \u5F00\u53D1\u8F6F\u4EF6\u7CFB\u7EDF\u7684\u8FC7\u7A0B\u4E2D,\u6700\u56F0\u96BE\u7684\u90E8\u5206\u662F\u786E\u5207\u5730\u51B3\u5B9A\u642D\u5EFA\u4EC0\u4E48\u6837\u7684\u7CFB\u7EDF\u3002\u6982\u5FF5\u6027\u5DE5\u4F5C\u4E2D,\u6CA1\u6709\u5176\u4ED6\u4EFB\u4F55\u4E00\u4E2A\u90E8\u5206\u6BD4\u786E\u5B9A\u8BE6\u7EC6\u7684\u6280\u672F\u9700\u6C42\u66F4\u52A0\u56F0\u96BE,\u8BE6\u7EC6\u7684\u9700\u6C42\u5305\u62EC\u4E86\u6240\u6709\u7684\u4EBA\u673A\u754C\u9762\u3001\u4E0E\u673A\u5668\u548C\u5176\u4ED6\u8F6F\u4EF6\u7CFB\u7EDF\u7684\u63A5\u53E3\u3002\u9700\u6C42\u5DE5\u4F5C\u5BF9\u7CFB\u7EDF\u7684\u5F71\u54CD\u6BD4\u5176\u4ED6\u4EFB\u4F55\u4E00\u4E2A\u90E8\u5206\u7684\u5931\u8BEF\u90FD\u5927,\u5F53\u7136\u7EA0\u6B63\u9700\u6C42\u7684\u56F0\u96BE\u4E5F\u6BD4\u5176\u4ED6\u4EFB\u4F55\u4E00\u4E2A\u90E8\u5206\u8981\u5927\u3002</p><p>\u56E0\u6B64,\u8F6F\u4EF6\u5F00\u53D1\u4EBA\u5458\u4E3A\u5BA2\u6237\u6240\u627F\u62C5\u7684\u6700\u91CD\u8981\u7684\u804C\u80FD\u662F\u4E0D\u65AD\u91CD\u590D\u5730\u62BD\u53D6\u548C\u7EC6\u5316\u4EA7\u54C1\u7684\u9700\u6C42\u3002\u4E8B\u5B9E\u4E0A,\u5BA2\u6237\u4E0D\u77E5\u9053\u4ED6\u4EEC\u81EA\u5DF1\u9700\u8981\u4EC0\u4E48\u3002\u4ED6\u4EEC\u901A\u5E38\u4E0D\u77E5\u9053\u54EA\u4E9B\u95EE\u9898\u662F\u5FC5\u987B\u56DE\u7B54\u7684\u3002\u5E76\u4E14,\u8FDE\u5FC5\u987B\u786E\u5B9A\u7684\u95EE\u9898\u7EC6\u8282\u5E38\u5E38\u6839\u672C\u4E0D\u4E88\u8003\u8651,\u751A\u81F3\u53EA\u662F\u7B80\u5355\u5730\u56DE\u7B54\u2014\u2014\u201C\u5F00\u53D1\u4E00\u4E2A\u7C7B\u4F3C\u4E8E\u6211\u4EEC\u5DF2\u6709\u7684\u624B\u5DE5\u5904\u7406\u8FC7\u7A0B\u7684\u65B0\u8F6F\u4EF6\u7CFB\u7EDF\u201D\u2014\u2014\u5B9E\u9645\u4E0A\u90FD\u8FC7\u4E8E\u7B80\u5355\u3002\u5BA2\u6237\u51B3\u4E0D\u4F1A\u4EC5\u4EC5\u8981\u6C42\u8FD9\u4E9B\u3002\u590D\u6742\u7684\u8F6F\u4EF6\u7CFB\u7EDF\u5F80\u5F80\u662F\u6D3B\u52A8\u7684\u3001\u53D8\u5316\u7684\u7CFB\u7EDF\u3002\u6D3B\u52A8\u7684\u52A8\u6001\u90E8\u5206\u662F\u5F88\u96BE\u60F3\u8C61\u7684\u3002\u6240\u4EE5,\u5728\u8BA1\u5212\u4EFB\u4F55\u8F6F\u4EF6\u6D3B\u52A8\u65F6,\u8981\u8BA9\u5BA2\u6237\u548C\u8BBE\u8BA1\u4EBA\u5458\u4E4B\u95F4\u8FDB\u884C\u591A\u6B21\u5E7F\u6CDB\u7684\u4EA4\u6D41\u6C9F\u901A,\u5E76\u5C06\u5176\u4F5C\u4E3A\u7CFB\u7EDF\u5B9A\u4E49\u7684\u4E00\u90E8\u5206\u3002\u8FD9\u662F\u975E\u5E38\u5FC5\u8981\u7684\u3002</p><p>\u8FD9\u91CC,\u6211\u5C06\u5411\u524D\u591A\u8D70\u4E00\u6B65,\u4E0B\u4E00\u4E2A\u5B9A\u8BBA\u3002\u5728\u5C1D\u8BD5\u548C\u5F00\u53D1\u4E00\u4E9B\u5BA2\u6237\u5B9A\u5236\u7684\u7CFB\u7EDF\u4E4B\u524D,\u5373\u4F7F\u4ED6\u4EEC\u548C\u8F6F\u4EF6\u5DE5\u7A0B\u5E08\u4E00\u8D77\u5DE5\u4F5C,\u60F3\u8981\u5B8C\u6574\u3001\u7CBE\u786E\u3001\u6B63\u786E\u5730\u62BD\u53D6\u73B0\u4EE3\u8F6F\u4EF6\u4EA7\u54C1\u7684\u9700\u6C42\u2014\u2014\u8FD9,\u5B9E\u9645\u4E0A\u4E5F\u662F\u4E0D\u53EF\u80FD\u7684\u3002</p><p>\u56E0\u6B64,\u73B0\u5728\u7684\u6280\u672F\u4E2D\u6700\u6709\u5E0C\u671B\u7684,\u5E76\u4E14\u89E3\u51B3\u4E86\u8F6F\u4EF6\u7684\u6839\u672C\u800C\u975E\u6B21\u8981\u95EE\u9898\u7684\u6280\u672F,\u662F\u5F00\u53D1\u4F5C\u4E3A\u8FED\u4EE3\u9700\u6C42\u8FC7\u7A0B\u7684\u4E00\u90E8\u5206\u2014\u2014\u5FEB\u901F\u539F\u578B\u5316\u7CFB\u7EDF\u7684\u65B9\u6CD5\u548C\u5DE5\u5177\u3002</p><p>\u8F6F\u4EF6\u7CFB\u7EDF\u7684\u5FEB\u901F\u539F\u578B\u5BF9\u91CD\u8981\u7684\u7CFB\u7EDF\u754C\u9762\u8FDB\u884C\u6A21\u62DF,\u5E76\u6F14\u793A\u5F85\u5F00\u53D1\u7CFB\u7EDF\u7684\u4E3B\u8981\u529F\u80FD\u3002\u539F\u578B\u4E0D\u5FC5\u53D7\u5230\u76F8\u540C\u786C\u4EF6\u901F\u5EA6\u3001\u89C4\u6A21\u6216\u8005\u6210\u672C\u7EA6\u675F\u7684\u9650\u5236\u3002\u539F\u578B\u901A\u5E38\u5C55\u793A\u4E86\u5E94\u7528\u7A0B\u5E8F\u7684\u529F\u80FD\u4E3B\u7EBF,\u4F46\u4E0D\u5904\u7406\u4EFB\u4F55\u5982\u65E0\u6548\u8F93\u5165\u3001\u9000\u51FA\u6E05\u9664\u7B49\u5F02\u5E38\u60C5\u51B5\u3002\u539F\u578B\u7684\u76EE\u7684\u662F\u660E\u786E\u5B9E\u9645\u7684\u6982\u5FF5\u7ED3\u6784,\u4ECE\u800C\u5BA2\u6237\u53EF\u4EE5\u6D4B\u8BD5\u4E00\u81F4\u6027\u548C\u53EF\u7528\u6027\u3002</p><p>\u73B0\u5728\u7684\u8F6F\u4EF6\u5F00\u53D1\u6D41\u7A0B\u57FA\u4E8E\u5982\u4E0B\u7684\u5047\u8BBE\u2014\u2014\u4E8B\u5148\u660E\u786E\u5730\u9610\u8FF0\u7CFB\u7EDF,\u4E3A\u7CFB\u7EDF\u5F00\u53D1\u7ADE\u6807,\u5B9E\u9645\u8FDB\u884C\u5F00\u53D1,\u6700\u540E\u5B89\u88C5\u3002\u6211\u8BA4\u4E3A\u8FD9\u79CD\u5047\u8BBE\u6839\u672C\u4E0A\u5C31\u662F\u4E0D\u6B63\u786E\u7684,\u5F88\u591A\u8F6F\u4EF6\u95EE\u9898\u5C31\u6765\u81EA\u8FD9\u79CD\u8C2C\u8BEF\u3002\u56E0\u6B64,\u5982\u679C\u4E0D\u8FDB\u884C\u5F7B\u5E95\u5730\u8C03\u6574,\u5C31\u65E0\u6CD5\u6D88\u9664\u90A3\u4E9B\u8F6F\u4EF6\u95EE\u9898\u3002\u5176\u4E2D,\u4E00\u79CD\u6539\u8FDB\u662F\u5BF9\u4EA7\u54C1\u548C\u539F\u578B\u4E0D\u65AD\u5F80\u590D\u5730\u5F00\u53D1\u548C\u89C4\u683C\u5316\u3002</p><p><strong>\u589E\u91CF\u5F00\u53D1\u2014\u2014\u589E\u957F,\u800C\u975E\u642D\u5EFA\u7CFB\u7EDF</strong> \u6211\u73B0\u5728\u8FD8\u8BB0\u5F97\u5728 1958 \u5E74,\u5F53\u542C\u5230\u4E00\u4E2A\u670B\u53CB\u63D0\u53CA\u642D\u5EFA(building), \u800C\u4E0D\u662F \u7F16\u5199(writing) \u7CFB\u7EDF\u65F6,\u6211\u6240\u611F\u53D7\u5230\u7684\u9707\u52A8\u3002\u4E00\u77AC\u95F4,\u6211\u7684\u6574\u4E2A\u8F6F\u4EF6\u5F00\u53D1\u6D41\u7A0B\u7684\u89C6\u91CE\u5F00\u9614\u4E86\u3002\u8FD9\u79CD\u6697\u55BB\u662F\u975E\u5E38\u6709\u529B\u548C\u7CBE\u786E\u7684\u3002\u73B0\u5728,\u6211\u4EEC\u5DF2\u7ECF\u7406\u89E3\u8F6F\u4EF6\u5F00\u53D1\u662F\u5982\u4F55\u7C7B\u4F3C\u4E8E\u5176\u4ED6\u7684\u5EFA\u9020\u8FC7\u7A0B,\u5E76\u5F00\u59CB\u968F\u610F\u5730\u4F7F\u7528\u5176\u4ED6\u7684\u6697\u55BB,\u5982 \u89C4\u683C\u8BF4\u660E\u3001\u6784\u4EF6\u88C5\u5907\u3001\u811A\u624B\u67B6(\u6D4B\u8BD5\u5E73\u53F0)(specifications, assembly of components, and scaffolding)\u3002</p><p>\u6697\u55BB\u201C\u642D\u5EFA\u7CFB\u7EDF\u201D\u7684\u4F7F\u7528\u5DF2\u7ECF\u6709\u4E9B\u8D85\u51FA\u4E86\u5B83\u7684\u6709\u6548\u671F\u9650,\u662F\u91CD\u65B0\u6362\u4E00\u79CD\u8868\u8FBE\u65B9\u5F0F\u7684\u65F6\u5019\u4E86\u3002\u5982\u679C\u73B0\u5728\u7684\u5F00\u53D1\u60C5\u51B5\u548C\u6211\u8003\u8651\u7684\u4E00\u6837,\u90A3\u4E9B\u6982\u5FF5\u6027\u7684\u7ED3\u6784\u975E\u5E38\u590D\u6742,\u4EE5\u81F4\u4E8E\u96BE\u4EE5\u4E8B\u5148\u7CBE\u786E\u5730\u8BF4\u660E\u548C\u96F6\u7F3A\u9677\u5730\u5F00\u53D1,\u90A3\u4E48\u6211\u4EEC\u5FC5\u987B\u91C7\u7528\u5F7B\u5E95\u4E0D\u540C\u7684\u65B9\u6CD5\u3002</p><p>\u8BA9\u6211\u4EEC\u8F6C\u5411\u81EA\u7136\u754C,\u7814\u7A76\u4E00\u4E0B\u751F\u7269\u7684\u590D\u6742\u6027,\u800C\u4E0D\u662F\u4EBA\u4EEC\u7684\u50F5\u786C\u5DE5\u4F5C\u3002\u6211\u4EEC\u4F1A\u53D1\u73B0\u5B83\u4EEC\u7684\u590D\u6742\u7A0B\u5EA6\u4EE4\u6211\u4EEC\u656C\u754F\u3002\u5149\u662F\u5927\u8111\u672C\u8EAB,\u5C31\u6BD4\u4EFB\u4F55\u5BF9\u5B83\u7684\u63CF\u8FF0\u90FD\u8981\u590D\u6742,\u6BD4\u4EFB\u4F55\u7684\u6A21\u62DF\u4EFF\u771F\u90FD\u8981\u5F3A\u5927,\u5B83\u7684\u591A\u6837\u6027\u3001\u81EA\u6211\u4FDD\u62A4\u548C\u81EA\u6211\u66F4\u65B0\u80FD\u529B\u5F02\u5E38\u4E30\u5BCC\u548C\u6709\u529B\u3002\u5176\u4E2D\u7684\u79D8\u5BC6\u5C31\u662F\u9010\u6B65\u53D1\u80B2\u6210\u957F,\u800C\u4E0D\u662F\u4E00\u6B21\u6027\u642D\u5EFA\u3002</p><p>\u6240\u4EE5,\u6211\u4EEC\u7684\u8F6F\u4EF6\u7CFB\u7EDF\u4E5F\u5FC5\u987B\u5982\u6B64\u3002\u5F88\u591A\u5E74\u524D,Harlan Mill \u5EFA\u8BAE\u6240\u6709\u8F6F\u4EF6\u7CFB\u7EDF\u90FD\u5E94\u8BE5\u4EE5\u589E\u91CF\u7684\u65B9\u5F0F\u5F00\u53D1 \u3002\u5373,\u9996\u5148\u7CFB\u7EDF\u5E94\u8BE5\u80FD\u591F\u8FD0\u884C,\u5373\u4F7F\u672A\u5B8C\u6210\u4EFB\u4F55\u6709\u7528\u529F\u80FD,\u53EA\u80FD\u6B63\u786E\u8C03\u7528\u4E00\u7CFB\u5217\u4F2A\u5B50\u7CFB\u7EDF\u3002\u63A5\u7740,\u7CFB\u7EDF\u4E00\u70B9\u4E00\u70B9\u88AB\u5145\u5B9E,\u5B50\u7CFB\u7EDF\u8F6E\u6D41\u88AB\u5F00\u53D1,\u6216\u8005\u662F\u5728\u66F4\u4F4E\u7684\u5C42\u6B21\u8C03\u7528\u7A0B\u5E8F\u3001\u6A21\u5757\u3001\u5B50\u7CFB\u7EDF\u7684\u5360\u4F4D\u7B26(\u4F2A\u7A0B\u5E8F)\u7B49\u3002</p><p>\u4ECE\u6211\u5728\u8F6F\u4EF6\u5DE5\u7A0B\u8BD5\u9A8C\u73ED\u4E0A\u5F00\u59CB\u63A8\u52A8\u8FD9\u79CD\u65B9\u6CD5\u8D77,\u5176\u6548\u679C\u4E0D\u53EF\u601D\u8BAE\u3002\u5728\u8FC7\u53BB\u51E0\u5341\u5E74\u4E2D,\u6CA1\u6709\u4EFB\u4F55\u65B9\u6CD5\u548C\u6280\u672F\u80FD\u5982\u6B64\u5F7B\u5E95\u5730\u6539\u53D8\u6211\u81EA\u5DF1\u7684\u5B9E\u8DF5\u3002\u8FD9\u79CD\u65B9\u6CD5\u8FEB\u5207\u5730\u8981\u6C42\u81EA\u9876\u5411\u4E0B\u8BBE\u8BA1,\u56E0\u4E3A\u5B83\u672C\u8EAB\u662F\u4E00\u79CD\u81EA\u9876\u5411\u4E0B\u589E\u957F\u7684\u8F6F\u4EF6\u3002\u589E\u91CF\u5316\u5F00\u53D1\u4F7F\u9006\u5411\u8DDF\u8E2A\u5F88\u65B9\u4FBF,\u5E76\u975E\u5E38\u5BB9\u6613\u8FDB\u884C\u539F\u578B\u5F00\u53D1\u3002\u6BCF\u4E00\u9879\u65B0\u589E\u529F\u80FD,\u4EE5\u53CA\u9488\u5BF9\u66F4\u52A0\u590D\u6742\u6570\u636E\u6216\u60C5\u51B5\u7684\u65B0\u6A21\u5757,\u4ECE\u5DF2\u7ECF\u89C4\u5212\u7684\u7CFB\u7EDF\u4E2D\u6709\u673A\u5730\u589E\u957F\u3002</p><p>\u8FD9\u79CD\u5F00\u53D1\u6A21\u5F0F\u5BF9\u58EB\u6C14\u7684\u63A8\u52A8\u662F\u4EE4\u4EBA\u9707\u60CA\u7684\u3002\u5F53\u4E00\u4E2A\u53EF\u8FD0\u884C\u7CFB\u7EDF\u2014\u2014\u5373\u4F7F\u662F\u975E\u5E38\u7B80\u5355\u7684\u7CFB\u7EDF\u51FA\u73B0\u65F6,\u5F00\u53D1\u4EBA\u5458\u7684\u70ED\u60C5\u5C31\u8FF8\u53D1\u4E86\u51FA\u6765\u3002\u5F53\u4E00\u4E2A\u65B0\u56FE\u5F62\u8F6F\u4EF6\u7CFB\u7EDF\u7684\u7B2C\u4E00\u526F\u56FE\u6848\u51FA\u73B0\u5728\u5C4F\u5E55\u4E0A\u65F6,\u5373\u4F7F\u662F\u4E00\u4E2A\u7B80\u5355\u7684\u957F\u65B9\u5F62,\u5DE5\u4F5C\u7684\u52A8\u529B\u4E5F\u4F1A\u6210\u500D\u5730\u589E\u957F\u3002\u5728\u5F00\u53D1\u8FC7\u7A0B\u4E2D\u7684\u6BCF\u4E2A\u9636\u6BB5,\u603B\u6709\u53EF\u8FD0\u884C\u7684\u7CFB\u7EDF\u3002\u6211\u53D1\u73B0\u5F00\u53D1\u56E2\u961F\u53EF\u4EE5\u5728\u56DB\u4E2A\u6708\u5185, \u57F9\u80B2(grow) \u51FA\u6BD4 \u642D\u5EFA(building)\u590D\u6742\u5F97\u591A\u7684\u7CFB\u7EDF\u3002</p><p>\u5927\u578B\u9879\u76EE\u540C\u6837\u53EF\u4EE5\u5F97\u5230\u4E0E\u6211\u6240\u53C2\u4E0E\u7684\u5C0F\u578B\u9879\u76EE\u76F8\u540C\u7684\u597D\u5904 \u3002</p><p>\u5353\u8D8A\u7684\u8BBE\u8BA1\u4EBA\u5458\u3002\u5173\u952E\u7684\u95EE\u9898\u662F\u5982\u4F55\u63D0\u9AD8\u8F6F\u4EF6\u884C\u4E1A\u7684\u6838\u5FC3,\u4E00\u5982\u65E2\u5F80\u7684\u662F\u2014\u2014\u4EBA\u5458\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u9075\u5FAA\u4F18\u79C0\u800C\u4E0D\u662F\u62D9\u52A3\u7684\u5B9E\u8DF5,\u6765\u5F97\u5230\u826F\u597D\u7684\u8BBE\u8BA1\u3002\u4F18\u79C0\u7684\u8BBE\u8BA1\u662F\u53EF\u4EE5\u4F20\u6388\u7684\u3002\u7A0B\u5E8F\u5458\u7684\u5468\u56F4\u5F80\u5F80\u662F\u6700\u51FA\u8272\u7684\u4EBA\u5458,\u56E0\u6B64\u4ED6\u4EEC\u53EF\u4EE5\u5B66\u4E60\u5230\u826F\u597D\u7684\u5B9E\u8DF5\u3002\u56E0\u6B64,\u7F8E\u56FD\u7684\u91CD\u5927\u7B56\u7565\u662F\u9881\u5E03\u5404\u79CD\u4F18\u79C0\u7684\u73B0\u4EE3\u5B9E\u8DF5\u3002\u65B0\u8BFE\u7A0B\u3001\u65B0\u6587\u732E\u3002\u8C61\u8F6F\u4EF6\u5DE5\u7A0B\u7814\u7A76\u6240 SEI \u7B49\u65B0\u673A\u6784\u7684\u51FA\u73B0\u90FD\u662F\u4E3A\u4E86\u628A\u6211\u4EEC\u7684\u5B9E\u8DF5\u4ECE\u4E0D\u8DB3\u63D0\u5347\u5230\u66F4\u9AD8\u7684\u6C34\u5E73\u3002\u5176\u6B63\u786E\u6027\u662F\u52FF\u5EB8\u7F6E\u7591\u7684\u3002</p><p>\u4E0D\u8FC7,\u6211\u4E0D\u8BA4\u4E3A\u6211\u4EEC\u53EF\u4EE5\u7528\u76F8\u540C\u7684\u65B9\u5F0F\u53D6\u5F97\u4E0B\u4E00\u6B21\u8FDB\u6B65\u3002\u4F4E\u52A3\u8BBE\u8BA1\u548C\u826F\u597D\u8BBE\u8BA1\u4E4B\u95F4\u7684\u533A\u522B\u53EF\u80FD\u5728\u4E8E\u8BBE\u8BA1\u65B9\u6CD5\u4E2D\u7684\u5B8C\u5584\u6027,\u800C\u826F\u597D\u8BBE\u8BA1\u548C\u5353\u8D8A\u8BBE\u8BA1\u4E4B\u95F4\u7684\u533A\u522B\u80AF\u5B9A\u4E0D\u662F\u5982\u6B64\u3002\u5353\u8D8A\u8BBE\u8BA1\u6765\u81EA\u5353\u8D8A\u7684\u8BBE\u8BA1\u4EBA\u5458\u3002\u8F6F\u4EF6\u5F00\u53D1\u662F\u4E00\u4E2A \u521B\u9020\u6027 \u7684\u8FC7\u7A0B\u3002\u5B8C\u5907\u7684\u65B9\u6CD5\u5B66\u53EF\u4EE5\u57F9\u517B\u548C\u91CA\u653E\u521B\u9020\u6027\u7684\u601D\u7EF4,\u4F46\u5B83\u65E0\u6CD5\u5B55\u80B2\u6216\u6FC0\u53D1\u521B\u9020\u6027\u7684\u8FC7\u7A0B\u3002</p><p>\u5176\u4E2D\u7684\u5DEE\u5F02\u5E76\u4E0D\u5C0F\u2014\u2014\u5C31\u8C61\u8428\u5217\u91CC\u548C\u83AB\u624E\u7279\u3002\u4E00\u4E2A\u63A5\u4E00\u4E2A\u7684\u7814\u7A76\u663E\u793A,\u975E\u5E38\u5353\u8D8A\u7684\u8BBE\u8BA1\u8005\u4EA7\u751F\u7684\u6210\u679C\u66F4\u5FEB\u3001\u66F4\u5C0F\u3001\u66F4\u7B80\u5355\u3001\u66F4\u4F18\u96C5,\u5B9E\u73B0\u7684\u4EE3\u4EF7\u66F4\u5C11\u3002\u5353\u8D8A\u548C\u4E00\u822C\u4E4B\u95F4\u7684\u5DEE\u5F02\u63A5\u8FD1\u4E8E\u4E00\u4E2A\u6570\u91CF\u7EA7\u3002</p><p>\u7B80\u5355\u5730\u56DE\u987E\u4E00\u4E0B,\u5C3D\u7BA1\u5F88\u591A\u6770\u51FA\u3001\u5B9E\u7528\u7684\u8F6F\u4EF6\u7CFB\u7EDF\u662F\u7531\u5F88\u591A\u4EBA\u5171\u540C\u8BBE\u8BA1\u5F00\u53D1,\u4F46\u662F\u90A3\u4E9B\u6FC0\u52A8\u4EBA\u5FC3\u3001\u62E5\u6709\u5E7F\u5927\u70ED\u60C5\u7231\u597D\u8005\u7684\u4EA7\u54C1\u5F80\u5F80\u662F\u4E00\u4E2A\u6216\u8005\u5C11\u6570\u4F1F\u5927\u8BBE\u8BA1\u5E08\u4EEC\u7684\u601D\u60F3\u3002\u8003\u8651\u4E00\u4E0B Unix\u3001 APL\u3001 Pascal\u3001 Modula\u3001 Smalltalk \u7684\u754C\u9762\u3001\u751A\u81F3 Fortran;\u4E0E\u4E4B\u5BF9\u5E94\u7684\u4EA7\u54C1\u662F Cobol\u3001PL/I\u3001Algol\u3001MVS/370 \u548C MS/DOS\u3002</p><p>\u56E0\u6B64,\u5C3D\u7BA1\u6211\u5F3A\u70C8\u5730\u652F\u6301\u73B0\u5728\u7684\u6280\u672F\u8F6C\u79FB\u548C\u5F00\u53D1\u6280\u80FD\u7684\u4F20\u6388,\u4F46\u6211\u8BA4\u4E3A\u6211\u4EEC\u53EF\u4EE5\u7740\u624B\u7684\u6700\u91CD\u8981\u5DE5\u4F5C\u662F\u5BFB\u6C42\u57F9\u517B\u5353\u8D8A\u8BBE\u8BA1\u4EBA\u5458\u7684\u9014\u5F84\u3002</p><p>\u6CA1\u6709\u4EFB\u4F55\u8F6F\u4EF6\u673A\u6784\u53EF\u4EE5\u5FFD\u89C6\u8FD9\u9879\u6311\u6218\u3002\u5C3D\u7BA1\u516C\u53F8\u53EF\u80FD\u7F3A\u5C11\u826F\u597D\u7684\u7BA1\u7406\u4EBA\u5458,\u4F46\u51B3\u4E0D\u4F1A\u6BD4\u826F\u597D\u8BBE\u8BA1\u4EBA\u5458\u7684\u9700\u6C42\u66F4\u52A0\u8FEB\u5207,\u800C\u5353\u8D8A\u7684\u7BA1\u7406\u4EBA\u5458\u548C\u8BBE\u8BA1\u4EBA\u5458\u90FD\u662F\u975E\u5E38\u7F3A\u4E4F\u7684\u3002\u5927\u591A\u6570\u673A\u6784\u82B1\u8D39\u4E86\u5927\u91CF\u7684\u65F6\u95F4\u548C\u7CBE\u529B\u6765\u5BFB\u627E\u548C\u57F9\u517B\u7BA1\u7406\u4EBA\u5458,\u4F46\u636E\u6211\u6240\u77E5,\u5B83\u4EEC\u4E2D\u95F4\u6CA1\u6709\u4EFB\u4F55\u7684\u4E00\u5BB6\u5728\u5BFB\u6C42\u548C\u57F9\u80B2\u6770\u51FA\u7684\u8BBE\u8BA1\u4EBA\u5458\u4E0A\u6295\u5165\u76F8\u540C\u7684\u8D44\u6E90,\u800C\u4EA7\u54C1\u7684\u6280\u672F\u7279\u8272\u6700\u7EC8\u4F9D\u8D56\u4E8E\u8FD9\u4E9B\u8BBE\u8BA1\u4EBA\u5458\u3002</p><p>\u6211\u7684\u7B2C\u4E00\u9879\u5EFA\u8BAE\u662F\u6BCF\u4E2A\u8F6F\u4EF6\u673A\u6784\u5FC5\u987B\u51B3\u5B9A\u548C\u8868\u660E,\u6770\u51FA\u7684\u8BBE\u8BA1\u4EBA\u5458\u548C\u5353\u8D8A\u7684\u7BA1\u7406\u4EBA\u5458\u4E00\u6837\u91CD\u8981,\u4ED6\u4EEC\u5E94\u8BE5\u5F97\u5230\u76F8\u540C\u7684\u57F9\u517B\u548C\u56DE\u62A5\u3002\u4E0D\u4EC5\u4EC5\u662F\u85AA\u8D44,\u8FD8\u5305\u62EC\u5404\u4E2A\u65B9\u9762\u7684\u8BA4\u53EF\u2014\u2014\u529E\u516C\u5BA4\u89C4\u6A21\u3001\u5B89\u6392\u3001\u4E2A\u4EBA\u7684\u8BBE\u5907\u3001\u5DEE\u65C5\u8D39\u7528\u3001\u4EBA\u5458\u652F\u6301\u7B49\u2014\u2014\u5FC5\u987B\u5B8C\u5168\u4E00\u81F4\u3002</p><p>\u5982\u4F55\u57F9\u517B\u6770\u51FA\u7684\u8BBE\u8BA1\u4EBA\u5458?\u9650\u4E8E\u7BC7\u5E45,\u4E0D\u5141\u8BB8\u8FDB\u884C\u8F83\u957F\u7684\u4ECB\u7ECD,\u4F46\u6709\u4E9B\u6B65\u9AA4\u662F\u663E\u800C\u6613\u89C1\u7684\u3002</p><ul><li>\u5C3D\u53EF\u80FD\u65E9\u5730\u3001\u6709\u7CFB\u7EDF\u5730\u8BC6\u522B\u9876\u7EA7\u7684\u8BBE\u8BA1\u4EBA\u5458\u3002\u6700\u597D\u7684\u901A\u5E38\u4E0D\u662F\u90A3\u4E9B\u6700\u6709\u7ECF\u9A8C\u7684\u4EBA\u5458\u3002</li><li>\u4E3A\u8BBE\u8BA1\u4EBA\u5458\u6307\u6D3E\u4E00\u4F4D\u804C\u4E1A\u5BFC\u5E08,\u8D1F\u8D23\u4ED6\u4EEC\u6280\u672F\u65B9\u9762\u7684\u6210\u957F,\u4ED4\u7EC6\u5730\u4E3A\u4ED6\u4EEC\u89C4\u5212\u804C\u4E1A\u751F\u6DAF\u3002</li><li>\u4E3A\u6BCF\u4E2A\u65B9\u9762\u5236\u8BA2\u548C\u7EF4\u62A4\u4E00\u4EFD\u804C\u4E1A\u8BA1\u5212,\u5305\u62EC\u4E0E\u8BBE\u8BA1\u5927\u5E08\u7684\u3001\u7ECF\u8FC7\u4ED4\u7EC6\u6311\u9009\u7684\u5B66\u4E60\u8FC7\u7A0B\u3001\u6B63\u5F0F\u7684\u9AD8\u7EA7\u6559\u80B2\u548C\u4EE5\u53CA\u77ED\u671F\u7684\u8BFE\u7A0B\u2014\u2014\u6240\u6709\u8FD9\u4E9B\u90FD\u7A7F\u63D2\u5728\u8BBE\u8BA1\u548C\u6280\u672F\u9886\u5BFC\u80FD\u529B\u7684\u57F9\u517B\u5B89\u6392\u4E2D\u3002</li><li>\u4E3A\u6210\u957F\u4E2D\u7684\u8BBE\u8BA1\u4EBA\u5458\u63D0\u4F9B\u76F8\u4E92\u4EA4\u6D41\u548C\u5B66\u4E60\u7684\u673A\u4F1A</li></ul>`,141),a=[s];function e(i,g){return n(),r("div",null,a)}var d=p(o,[["render",e],["__file","\u6CA1\u6709\u94F6\u5F39.html.vue"]]);export{d as default};
