import{_ as e,o,c,e as t}from"./app.6bf529a6.js";const d={},_=t('<h1 id="\u6CE2\u65AF\u4EBA\u7684\u653F\u6CBB\u9009\u62E9" tabindex="-1"><a class="header-anchor" href="#\u6CE2\u65AF\u4EBA\u7684\u653F\u6CBB\u9009\u62E9" aria-hidden="true">#</a> \u6CE2\u65AF\u4EBA\u7684\u653F\u6CBB\u9009\u62E9</h1><blockquote><p>\u5728\u300A\u7F8E\u6D85\u514B\u585E\u52AA\u7BC7\u300B\u7684\u8BA8\u8BBA\u4E2D, \u6211\u4EEC\u63D0\u5230\u4E86\u6CE2\u65AF\u4EBA\u66FE\u7ECF\u6709\u4E00\u6B21\u9009\u62E9\u653F\u4F53\u7684\u673A\u4F1A, \u5F53\u65F6\u6709\u4E09\u79CD\u610F\u89C1: \u6C11\u4E3B\u5236, \u5BE1\u5934\u5236\u548C\u72EC\u88C1\u5236. \u4E09\u79CD\u653F\u4F53\u7684\u4EE3\u8868\u4EBA\u5206\u522B\u9648\u8FF0\u4E86\u5404\u81EA\u653F\u4F53\u7684\u597D\u5904. \u6700\u540E\u6CE2\u65AF\u4EBA\u9009\u62E9\u4E86\u72EC\u88C1\u5236. \u4E0B\u9762\u5C31\u662F\u5E0C\u7F57\u591A\u5FB7\u7684\u300A\u5386\u53F2\u300B\u91CC\u5BF9\u8FD9\u6B21\u8BA8\u8BBA\u7684\u8BB0\u8F7D.</p></blockquote><p>\u5F53\u6CE2\u65AF\u4EBA\u542C\u5230\u8FD9\u4E03\u4E2A\u4EBA\u6240\u505A\u7684\u4E00\u5207\u4EE5\u53CA\u739B\u54E5\u65AF\u50E7\u4EBA\u5982\u4F55\u6B3A\u9A97\u4E86\u4ED6\u4EEC\u7684\u65F6\u5019, \u4FBF\u51B3\u5B9A\u8FFD\u968F\u4ED6\u4EEC\u7684\u699C\u6837, \u4E5F\u638F\u51FA\u5315\u9996\u628A\u4ED6\u4EEC\u6240\u80FD\u5BFC\u627E\u5230\u7684\u5168\u90E8\u739B\u54E5\u65AF\u50E7\u90FD\u7ED9\u6740\u6B7B\u4E86. \u800C\u5982\u679C\u4E0D\u662F\u591C\u5E55\u964D\u4E34\u800C\u4F7F\u4ED6\u4EEC\u4E0D\u5F97\u4E0D\u505C\u624B\u7684\u8BDD, \u4ED6\u4EEC\u6050\u6015\u662F\u4E0D\u4F1A\u53EB\u4EFB\u4F55\u4E00\u4E2A\u739B\u54E5\u65AF\u50E7\u5F97\u5230\u6D3B\u547D\u7684. \u8FD9\u4E00\u5929\u662F\u4E00\u5207\u6CE2\u65AF\u4EBA\u540C\u6837\u90FD\u4E3E\u884C\u7684\u6700\u76DB\u5927\u7684\u795E\u5723\u7684\u65E5\u5B50; \u4ED6\u4EEC\u4E3A\u8FD9\u4EF6\u4E8B\u4E3E\u884C\u4E86\u76DB\u5927\u7684\u8282\u65E5, \u5E76\u79F0\u4E4B\u4E3A\u739B\u54E5\u65AF\u50E7\u5C60\u6740\u8282; \u5728\u8282\u65E5\u671F\u95F4, \u739B\u54E5\u65AF\u50E7\u4E0D\u8BB8\u5230\u8857\u4E0A\u6765, \u4ED6\u4EEC\u8981\u6574\u5929\u7559\u5728\u81EA\u5DF1\u7684\u5BB6\u91CC. \u5F53\u4E94\u5929\u4EE5\u540E\u6DF7\u4E71\u7684\u60C5\u51B5\u597D\u8F6C\u7684\u65F6\u5019, \u90A3\u4E9B\u8D77\u6765\u53CD\u6297\u739B\u54E5\u65AF\u4EEC\u7684\u4EBA\u4EEC\u4FBF\u96C6\u4F1A\u8BA8\u8BBA\u5168\u90E8\u5C40\u52BF, \u5728\u4F1A\u4E0A\u53D1\u8868\u7684\u610F\u89C1, \u5728\u67D0\u4E9B\u5E0C\u814A\u4EBA\u770B\u8D77\u6765\u662F\u4E0D\u53EF\u4FE1\u7684; \u4F46\u6BEB\u65E0\u7591\u95EE\u8FD9\u4E9B\u610F\u89C1\u662F\u53D1\u8868\u4E86\u7684. <code>\u6B27\u5854\u6D85\u65AF</code> \u7684\u610F\u89C1\u662F\u4E3B\u5F20 <strong>\u4F7F\u5168\u4F53\u6CE2\u65AF\u4EBA\u53C2\u52A0\u7BA1\u7406\u56FD\u5BB6</strong>. \u4ED6\u8BF4: &quot;\u6211\u4EE5\u4E3A\u6211\u4EEC\u5FC5\u987B\u505C\u6B62\u4F7F\u4E00\u4E2A\u4EBA\u8FDB\u884C\u72EC\u88C1\u7684\u7EDF\u6CBB, \u56E0\u4E3A\u8FD9\u5373\u4E0D\u662F\u4E00\u4EF6\u5FEB\u6D3B\u4E8B, \u53C8\u4E0D\u662F\u4E00\u4EF6\u597D\u4E8B. \u4F60\u4EEC\u5DF2\u7ECF\u770B\u5230\u521A\u6BD4\u897F\u65AF\u9A84\u50B2\u81EA\u6EE1\u5230\u4EC0\u4E48\u7A0B\u5EA6, \u800C\u4F60\u4EEC\u4E5F\u5C1D\u5230\u8FC7\u739B\u54E5\u65AF\u4EBA\u7684\u90A3\u79CD\u65C1\u82E5\u65E0\u4EBA\u7684\u6ECB\u5473. \u5F53\u4E00\u4E2A\u4EBA\u613F\u610F\u600E\u6837\u505A\u4FBF\u600E\u6837\u505A\u800C\u81EA\u5DF1\u5BF9\u6240\u505A\u7684\u4E8B\u53C8\u53EF\u4EE5\u6BEB\u4E0D\u8D1F\u8D23\u7684\u65F6\u5019, \u90A3\u672A\u8FD9\u79CD\u72EC\u88C1\u7684\u7EDF\u6CBB\u53C8\u6709\u4EC0\u4E48\u597D\u5904\u5462? \u628A\u8FD9\u79CD\u6743\u529B\u7ED9\u4E16\u754C\u4E0A\u6700\u4F18\u79C0\u7684\u4EBA, \u4ED6\u4E5F\u4F1A\u8131\u79BB\u4ED6\u7684\u6B63\u5E38\u5FC3\u60C5\u7684. \u4ED6\u5177\u6709\u7684\u7279\u6743\u4EA7\u751F\u4E86\u9A84\u50B2, \u800C\u4EBA\u4EEC\u7684\u5AC9\u5992\u5FC3\u53C8\u662F\u4E00\u4EF6\u5F88\u81EA\u7136\u7684\u4E8B\u60C5. \u8FD9\u53CC\u91CD\u7684\u539F\u56E0\u4FBF\u662F\u5728\u4ED6\u8EAB\u4E0A\u4EA7\u751F\u4E00\u5207\u6076\u4E8B\u7684\u6839\u6E90; \u4ED6\u4E4B\u6240\u4EE5\u505A\u51FA\u8BB8\u591A\u6076\u4E8B\u6765, \u6709\u4E9B\u662F\u7531\u4E8E\u9A84\u50B2\u81EA\u6EE1, \u6709\u4E9B\u5219\u662F\u7531\u4E8E\u5AC9\u5992. \u672C\u6765\u4E00\u4E2A\u5177\u6709\u72EC\u88C1\u6743\u529B\u7684\u541B\u4E3B, \u65E2\u7136\u8981\u4EE5\u968F\u5FC3\u6240\u6B32\u5730\u5F97\u5230\u4E00\u5207\u4E1C\u897F, \u90A3\u4ED6\u5E94\u5F53\u662F\u4E0D\u4F1A\u5AC9\u5992\u4EFB\u4F55\u4EBA\u7684\u4E86; \u4F46\u662F\u5728\u4ED6\u548C\u56FD\u4EBA\u6253\u4EA4\u9053\u65F6, \u60C5\u51B5\u5374\u6070\u6070\u76F8\u53CD. \u4ED6\u5AC9\u5992\u4ED6\u7684\u81E3\u6C11\u4E2D\u6700\u6709\u9053\u5FB7\u7684\u4EBA\u4EEC, \u5E0C\u671B\u4ED6\u4EEC\u5FEB\u6B7B, \u5374\u6B22\u8FCE\u90A3\u4E9B\u6700\u4E0B\u8D31\u5351\u52A3\u7684\u4EBA\u4EEC, \u5E76\u4E14\u6BD4\u4EFB\u4F55\u4EBA\u90FD\u66F4\u613F\u610F\u542C\u4FE1\u8C17\u8A00. \u6B64\u5916, \u4E00\u4E2A\u56FD\u738B\u53C8\u662F\u4E00\u4E2A\u6700\u96BE\u5BF9\u4ED8\u7684\u4EBA. \u5982\u679C\u4F60\u53EA\u662F\u9002\u5F53\u5730\u5C0A\u656C\u4ED6, \u4ED6\u5C31\u662F\u4E0D\u4F1A\u9AD8\u5174, \u8BF4\u4F60\u5F85\u5949\u4ED6\u4E0D\u591F\u5C3D\u5FC3\u7AED\u529B; \u5982\u679C\u4F60\u771F\u5FC3\u7AED\u529B\u7684\u8BDD, \u4ED6\u53C8\u8981\u9A82\u4F60\u5DE7\u8A00\u4EE4\u8272. \u7136\u800C\u6211\u8BF4\u4ED6\u6700\u5927\u7684\u5BB3\u5904\u8FD8\u4E0D\u662F\u5728\u8FD9\u91CC; \u4ED6\u628A\u7236\u7956\u76F8\u4F20\u7684\u5927\u6CD5\u4EFB\u610F\u6539\u53D8, \u4ED6\u5F3A\u5978\u5987\u5973, \u4ED6\u53EF\u4EE5\u628A\u4EBA\u6C11\u4E0D\u52A0\u5BA1\u5224\u800C\u4EFB\u610F\u8BDB\u6740. \u4E0D\u8FC7, \u76F8\u53CD\u7684, \u4EBA\u6C11\u7684\u7EDF\u6CBB\u7684\u4F18\u70B9\u9996\u5148\u5728\u4E8E\u5B83\u7684\u6700\u7F8E\u597D\u7684\u58F0\u540D \u90A3\u5C31\u662F, \u5728\u6CD5\u5F8B\u9762\u524D\u4EBA\u4EBA\u5E73\u7B49. \u5176\u6B21, \u90A3\u6837\u4E5F\u4FBF\u4E0D\u4F1A\u4EA7\u751F\u4E00\u4E2A\u56FD\u738B\u6240\u6613\u72AF\u7684\u4EFB\u4F55\u9519\u8BEF. \u4E00\u5207\u804C\u4F4D\u90FD\u7531\u62BD\u7B7E\u51B3\u5B9A, \u4EFB\u4F55\u7684\u4EBA\u5BF9\u4ED6\u4EEC\u4EFB\u4E0A\u6240\u505A\u7684\u4E00\u5207\u8D1F\u8D23, \u800C\u4E00\u5207\u610F\u89C1\u5747\u4EA4\u7531\u4EBA\u6C11\u5927\u4F17\u52A0\u4EE5\u88C1\u51B3. \u56E0\u6B64\u6211\u7684\u610F\u89C1\u662F, \u6211\u4EEC\u5E9F\u6389\u72EC\u88C1\u653F\u6CBB\u5E76\u589E\u52A0\u4EBA\u6C11\u7684\u6743\u529B, \u56E0\u4E3A\u4E00\u5207\u4E8B\u60C5\u662F\u5FC5\u987B\u53D6\u51B3\u4E8E\u516C\u4F17\u7684.&quot;</p><p><code>\u6B27\u5854\u6D85\u65AF</code> \u53D1\u8868\u7684\u610F\u89C1\u5C31\u662F\u8FD9\u6837. \u4F46\u662F <code>\u7F8E\u4F3D\u6BD4\u4F50\u65AF</code> \u7684\u610F\u89C1\u662F\u4E3B\u5F20\u7EC4\u6210\u4E00\u4E2A\u7EDF\u6CBB\u7684\u5BE1\u5934. \u4ED6\u8BF4: &quot;\u6211\u540C\u610F <code>\u6B27\u5854\u6D85\u65AF</code> \u6240\u8BF4\u7684\u5168\u90E8\u53CD\u5BF9\u4E00\u4E2A\u4EBA\u7684\u7EDF\u6CBB\u7684\u610F\u89C1. \u4F46\u662F\u5F53\u4ED6\u4E3B\u5F20\u8981\u628A\u4F60\u7684\u6743\u529B\u7ED9\u4E88\u6C11\u4F17\u7684\u65F6\u5019, \u4ED6\u7684\u89C1\u89E3\u4FBF\u4E0D\u662F\u6700\u597D\u7684\u89C1\u89E3\u4E86. \u6CA1\u6709\u6BD4\u4E0D\u597D\u5BF9\u4ED8\u7684\u7FA4\u4F17\u66F4\u52A0\u611A\u8822\u548C\u6A2A\u66B4\u65E0\u793C\u7684\u4E86. \u628A\u6211\u4EEC\u81EA\u5DF1\u4ECE\u4E00\u4E2A\u66B4\u541B\u7684\u6A2A\u66B4\u65E0\u793C\u7684\u7EDF\u6CBB\u4E4B\u4E0B\u62EF\u6551\u51FA\u6765, \u5374\u53C8\u7528\u5B83\u6765\u6362\u53D6\u90A3\u8086\u65E0\u5FCC\u60EE\u7684\u4EBA\u6C11\u5927\u4F17\u7684\u4E13\u64C5, \u90A3\u662F\u4E0D\u80FD\u5BB9\u5FCD\u7684\u4E8B\u60C5. \u4E0D\u7BA1\u66B4\u541B\u505A\u4EC0\u4E48\u4E8B\u60C5, \u4ED6\u8FD8\u662F\u660E\u660E\u77E5\u9053\u8FD9\u4EF6\u4E8B\u624D\u505A\u7684; \u4F46\u662F\u4EBA\u6C11\u5927\u4F17\u8FDE\u8FD9\u4E00\u70B9\u90FD\u505A\u4E0D\u5230\u800C\u5B8C\u5168\u662F\u76F2\u76EE\u7684; \u4F60\u60F3\u6C11\u4F17\u65E2\u7136\u4E0D\u77E5\u9053, \u4ED6\u4EEC\u81EA\u5DF1\u4E5F\u4E0D\u80FD\u770B\u5230\u4EC0\u4E48\u662F\u6700\u597D\u7684\u6700\u59A5\u5F53\u7684; \u4F60\u60F3\u6C11\u4F17\u65E2\u7136\u4E0D\u77E5\u9053, \u4ED6\u4EEC\u81EA\u5DF1\u4E5F\u4E0D\u80FD\u770B\u5230\u4EC0\u4E48\u662F\u6700\u597D\u7684\u6700\u59A5\u5F53\u7684, \u800C\u662F\u76F4\u5411\u524D\u51B2, \u50CF\u4E00\u6761\u6CDB\u6EE5\u7684\u6CB3\u90A3\u6837\u5730\u76F2\u76EE\u5411\u524D\u5954\u6D41, \u90A3\u4ED6\u4EEC\u600E\u4E48\u80FD\u61C2\u5F97\u4ED6\u4EEC\u6240\u505A\u7684\u662F\u4EC0\u4E48\u5462? \u53EA\u6709\u5E0C\u671B\u6CE2\u65AF\u4F1A\u53D8\u574F\u7684\u4EBA\u624D\u62E5\u62A4\u6C11\u6CBB; \u8FD8\u8BA9\u6211\u4EEC\u9009\u4E00\u6279\u6700\u4F18\u79C0\u7684\u4EBA\u7269, \u628A\u653F\u6743\u4EA4\u7ED9\u4ED6\u4EEC\u7F62. \u6211\u4EEC\u81EA\u5DF1\u4E5F\u53EF\u4EE5\u53C2\u52A0\u8FD9\u4E00\u6279\u4EBA\u7269; \u800C\u65E2\u7136\u6211\u4EEC\u6709\u4E00\u6279\u6700\u4F18\u79C0\u7684\u4EBA\u7269, \u90A3\u6211\u4EEC\u5C31\u53EF\u4EE5\u4F5C\u51FA\u9AD8\u660E\u7684\u51B3\u5B9A\u4E86.&quot;</p><p>\u4EE5\u4E0A\u4FBF\u662F <code>\u7F8E\u4F3D\u6BD4\u4F50\u65AF</code> \u7684\u770B\u6CD5\u4E86. <code>\u5927\u6D41\u58EB</code> \u662F\u7B2C\u4E09\u4E2A\u53D1\u8868\u610F\u89C1\u7684\u4EBA. \u4ED6\u662F\u8FD9\u6837\u8BF4\u7684: &quot;\u6211\u4EE5\u4E3A\u6211\u4EEC\u5728\u8C08\u5230\u6C11\u6CBB\u7684\u65F6\u5019, <code>\u7F8E\u4F3D\u6BD4\u4F50\u65AF</code> \u7684\u8BDD\u662F\u6709\u9053\u7406\u7684, \u4F46\u662F\u5728\u8C08\u5230\u5BE1\u5934\u4E4B\u6CBB\u7684\u65F6\u5019, \u4ED6\u7684\u8BDD\u4FBF\u4E0D\u80FD\u8FD9\u6837\u770B\u4E86. \u73B0\u5728\u7684\u9009\u62E9\u65E2\u7136\u662F\u5728\u8FD9\u4E09\u8005\u4E4B\u95F4, \u800C\u8FD9\u4E09\u8005, \u5373\u6C11\u6CBB, \u5BE1\u5934\u4E4B\u6CBB\u548C\u72EC\u88C1\u4E4B\u6CBB\u4E4B\u4E2D\u7684\u6BCF\u4E00\u79CD\u65E2\u7136\u53C8\u90FD\u7740\u5B83\u6700\u597D\u7684\u4E00\u79CD\u800C\u8A00, \u5219\u6211\u7684\u610F\u89C1, \u662F\u8BA4\u4E3A\u72EC\u88C1\u4E4B\u6CBB\u8981\u6BD4\u5176\u4ED6\u4E24\u79CD\u597D\u5F97\u591A. \u6CA1\u6709\u4EC0\u4E48\u80FD\u591F\u6BD4\u4E00\u4E2A\u6700\u4F18\u79C0\u7684\u4EBA\u7269\u7684\u7EDF\u6CBB\u66F4\u597D\u4E86. \u4ED6\u65E2\u7136\u6709\u4E0E\u4ED6\u672C\u4EBA\u76F8\u9002\u5E94\u7684\u5224\u65AD\u529B. \u56E0\u6B64\u4ED6\u80FD\u5B8C\u7F8E\u65E0\u7F3A\u5730\u7EDF\u6CBB\u4EBA\u6C11, \u540C\u65F6\u4E3A\u5BF9\u4ED8\u654C\u4EBA\u800C\u62DF\u8BA2\u7684\u8BA1\u5212\u4E5F\u53EF\u4EE5\u9690\u85CF\u5F97\u6700\u4E25\u5BC6. \u7136\u800C\u82E5\u5B9E\u65BD\u5BE1\u5934\u4E4B\u6CBB, \u5219\u8BB8\u591A\u4EBA\u867D\u7136\u90FD\u613F\u610F\u7ED9\u56FD\u5BB6\u505A\u597D\u4E8B\u60C5, \u4F46\u8FD9\u79CD\u613F\u671B\u5374\u5E38\u5E38\u5728\u4ED6\u4EEC\u4E4B\u95F4\u4EA7\u751F\u6FC0\u70C8\u7684\u654C\u5BF9\u60C5\u7EEA, \u56E0\u4E3A\u6BCF\u4E00\u4E2A\u4EBA\u90FD\u76F8\u5728\u6240\u6709\u4EBA\u5F53\u4E2D\u4E3A\u9996\u9886, \u90FD\u60F3\u4F7F\u81EA\u5DF1\u7684\u610F\u89C1\u5360\u4E0A\u98CE, \u8FD9\u7ED3\u679C\u4FBF\u5F15\u8D77\u6FC0\u70C8\u7684\u503E\u8F67, \u76F8\u4E92\u4E4B\u95F4\u7684\u503E\u8F67\u4EA7\u751F\u6D3E\u7CFB, \u6D3E\u7CFB\u4EA7\u751F\u6D41\u8840\u4E8B\u4F2F, \u800C\u6D41\u8840\u4E8B\u4EF6\u7684\u7ED3\u679C\u4ECD\u662F\u72EC\u88C1\u4E4B\u6CBB; \u56E0\u6B64\u53EF\u4EE5\u770B\u51FA, \u8FD9\u79CD\u7EDF\u6CBB\u65B9\u5F0F\u4E43\u662F\u6700\u597D\u7684\u7EDF\u6CBB\u65B9\u5F0F. \u518D\u8005, \u6C11\u4F17\u7684\u7EDF\u6CBB\u5FC5\u5B9A\u4F1A\u4EA7\u751F\u6076\u610F, \u800C\u5F53\u7740\u5728\u516C\u5171\u7684\u4E8B\u52A1\u4E2D\u4EA7\u751F\u6076\u610F\u7684\u65F6\u5019, \u574F\u4EBA\u4EEC\u4FBF\u4E0D\u4F1A\u56E0\u654C\u5BF9\u800C\u5206\u88C2, \u800C\u662F\u56E0\u5DE9\u56FA\u7684\u53CB\u8C0A\u800C\u56E2\u7ED3\u8D77\u6765; \u56E0\u4E3A\u90A3\u4E9B\u5BF9\u5927\u4F17\u505A\u574F\u4E8B\u7684\u4EBA\u662F\u4F1A\u72FC\u72C8\u4E3A\u5978\u5730\u884C\u52A8\u7684. \u8FD9\u79CD\u60C5\u51B5\u4F1A\u7EE7\u7EED\u4E0B\u53BB, \u76F4\u5230\u5728\u67D0\u4E2A\u4EBA\u4E3A\u6C11\u4F17\u7684\u5229\u76CA\u8D77\u6765\u8FDB\u884C\u534A\u4E89\u5E76\u5236\u6B62\u4E86\u8FD9\u6837\u7684\u574F\u4E8B. \u4E8E\u662F\u4ED6\u4FBF\u6210\u4E86\u4EBA\u6C11\u5D07\u62DC\u7684\u5076\u50CF, \u4E5F\u4FBF\u6210\u4E86\u4ED6\u4EEC\u72EC\u88C1\u7684\u541B\u4E3B; \u5728\u8FD9\u6837\u7684\u60C5\u51B5\u4E0B\u4E5F\u53EF\u8BC1\u660E\u72EC\u88C1\u4E4B\u6CBB\u662F\u6700\u597D\u7684\u7EDF\u6CBB\u65B9\u6CD5. \u4F46\u662F, \u603B\u800C\u8A00\u4E4B, \u8BF7\u544A\u8BC9\u6211, \u6211\u4EEC\u7684\u81EA\u7531\u662F\u4ECE\u4EC0\u4E48\u5730\u65B9\u6765\u7684, \u662F\u8C01\u8D50\u4E0E\u7684 -- \u662F\u6C11\u4F17, \u662F\u5BE1\u5934, \u8FD8\u662F\u4E00\u4E2A\u5355\u72EC\u7684\u7EDF\u6CBB\u8005? \u56E0\u4E3A\u6211\u8BA4\u4E3A, \u5373\u7136\u4E00\u4E2A\u4EBA\u7684\u7EDF\u6CBB\u80FD\u7ED9\u6211\u4EEC\u81EA\u7531, \u90A3\u672B\u6211\u4EEC\u4FBF\u5E94\u5F53\u4FDD\u7559\u8FD9\u79CD\u7EDF\u6CBB\u65B9\u6CD5; \u518D\u8BF4, \u6211\u4EEC\u4E5F\u4E0D\u5E94\u5F53\u5E9F\u5F03\u6211\u4EEC\u7236\u7956\u7684\u4F18\u826F\u6CD5\u5236; \u90A3\u6837\u505A\u662F\u4E0D\u597D\u7684.&quot;</p><p>\u5728\u5224\u65AD\u4E0A\u8FF0\u4E09\u79CD\u610F\u89C1\u65F6, \u4E03\u4E2A\u4EBA\u91CC\u6709\u56DB\u4E2A\u4EBA\u8D5E\u6210\u6700\u540E\u7684\u90A3\u79CD\u770B\u6CD5. \u8FD9\u6837\u4E00\u6765, \u60F3\u4F7F\u6BCF\u4E2A\u6CE2\u65AF\u4EBA\u5177\u6709\u5E73\u7B49\u6743\u5229\u7684\u6B27\u5854\u6D85\u65AF\u7684\u610F\u89C1\u5C31\u5931\u8D25\u4E86.</p>',6),a=[_];function r(n,s){return o(),c("div",null,a)}var u=e(d,[["render",r],["__file","\u6CE2\u65AF\u4EBA\u7684\u653F\u6CBB\u9009\u62E9.html.vue"]]);export{u as default};
