import{_ as t,o,c as u,e as q}from"./app.6bf529a6.js";const p={},e=q('<p>\u5468\u516D\u665A\u4E0A, \u67CF\u62C9\u56FE\u5BF9\u8BDD\u5F55\u5B66\u4E60\u5C0F\u7EC4\u8BA8\u8BBA\u4E86\u300A\u9AD8\u5C14\u5409\u4E9A\u7BC7\u300B\u7684\u7B2C\u4E8C\u90E8\u5206. \u5728\u8BA8\u8BBA\u7684\u8FC7\u7A0B\u4E2D, \u6211\u4EEC\u7B80\u77ED\u7684\u804A\u5230\u4E86\u67CF\u62C9\u56FE\u7684\u54F2\u4EBA\u738B, \u4EE5\u53CA\u54F2\u4EBA\u738B\u4E3A\u4EC0\u4E48\u4E0D\u80FD\u5728\u73B0\u5B9E\u653F\u6CBB\u4E2D\u5B9E\u73B0\u7684\u95EE\u9898. \u5728\u67CF\u62C9\u56FE\u5BF9\u8BDD\u5F55\u5B66\u4E60\u5C0F\u7EC4\u8BA8\u8BBA\u5B8C\u540E, \u6211\u53C8\u5F00\u59CB\u8FDB\u884C\u8BBA\u8BC1\u5171\u5B66\u5199\u4F5C. \u5F53\u5929\u6240\u5199\u5185\u5BB9, \u6070\u597D\u53C8\u662F &quot;\u5B50\u66F0: \u9053\u5343\u4E58\u4E4B\u56FD&quot; \u4E00\u7AE0, \u4E5F\u662F\u5728\u8BB2\u5112\u5BB6\u7684\u653F\u6CBB\u4E3B\u5F20, \u4E0E\u662F\u6211\u53C8\u60F3\u5230\u4E86\u5112\u5BB6\u4F5C\u4E3A\u77E5\u8BC6\u5206\u5B50\u7FA4\u4F53\u5728\u4E2D\u56FD\u5386\u53F2\u4E2D\u7684\u5883\u9047\u95EE\u9898. \u800C\u5468\u4E94\u7684\u65F6\u5019, \u6211\u5728\u300A\u76F4\u4E0E\u771F\u6211\u300B\u4E2D\u8BF4\u5230\u6211\u5BF9\u5112\u5BB6\u7684\u8BA4\u8BC6\u5927\u591A\u6765\u81EA\u51AF\u53CB\u5170. \u800C\u51AF\u53CB\u5170\u7684\u4E00\u751F, \u6B63\u662F\u4E00\u4E2A\u5112\u5BB6\u77E5\u8BC6\u5206\u5B50\u9762\u5BF9\u6743\u529B\u7684\u4E00\u751F. \u4E0E\u662F\u6211\u5C31\u60F3\u8D77\u4E86\u8FD9\u7BC7\u6587\u7AE0.</p><p>\u8FD9\u7BC7\u6587\u7AE0\u662F\u4E13\u6CBB\u53E4\u5E0C\u814A\u54F2\u5B66\u7684\u6C6A\u5B50\u5D69\u6240\u5199\u7684\u51AF\u53CB\u5170\u7684\u5C0F\u4F20.</p><h1 id="\u6D77\u9614\u5929\u7A7A\u6211\u81EA\u98DE" tabindex="-1"><a class="header-anchor" href="#\u6D77\u9614\u5929\u7A7A\u6211\u81EA\u98DE" aria-hidden="true">#</a> \u6D77\u9614\u5929\u7A7A\u6211\u81EA\u98DE</h1><h2 id="\u8BFB\u51AF\u53CB\u5170\u300A\u4E2D\u56FD\u73B0\u4EE3\u54F2\u5B66\u53F2\u300B" tabindex="-1"><a class="header-anchor" href="#\u8BFB\u51AF\u53CB\u5170\u300A\u4E2D\u56FD\u73B0\u4EE3\u54F2\u5B66\u53F2\u300B" aria-hidden="true">#</a> -- \u8BFB\u51AF\u53CB\u5170\u300A\u4E2D\u56FD\u73B0\u4EE3\u54F2\u5B66\u53F2\u300B</h2><p>\u4E00\u4F4D\u597D\u5FC3\u7684\u670B\u53CB\u4ECE\u9999\u6E2F\u4E70\u4E86\u4E00\u672C\u51AF\u53CB\u5170\u5148\u751F\u7684\u300A\u4E2D\u56FD\u73B0\u4EE3\u54F2\u5B66\u53F2\u300B( \u5373\u300A\u4E2D\u56FD\u54F2\u5B66\u53F2\u65B0\u7F16\u300B\u7B2C\u4E03\u518C ) \u9001\u6211, \u9996\u5148\u770B\u5230\u51AF\u5148\u751F\u7684 &quot;\u81EA\u5E8F&quot;:</p><blockquote><p>\u300A\u4E2D\u56FD\u54F2\u5B66\u53F2\u65B0\u7F16\u300B\u7B2C\u4E00, \u4E8C\u518C\u4E8E\u4E00\u4E5D\u516D\u56DB\u5E74\u516D\u6708\u5370\u884C. \u5F88\u5FEB\u6211\u5C31\u611F\u5230\u4E0D\u6EE1\u610F, \u9042\u53C8\u4ECE\u5934\u64B0\u5199, \u5C06\u5DF2\u51FA\u7684\u4E8C\u518C\u4F5C\u4E3A\u300A\u8BD5\u7A3F\u300B. \u7ECF\u8FC7\u4E8C\u5341\u591A\u5E74\u7684\u52AA\u529B, \u300A\u65B0\u7F16\u300B\u7B2C\u4E03\u518C\u7EC8\u4E8E\u5B8C\u6210. \u56DE\u987E\u4E8C\u5341\u591A\u5E74\u7684\u5DE5\u4F5C\u8FC7\u7A0B, \u4E0D\u7981\u611F\u6168\u7CFB\u4E4B\u77E3.</p><p>\u6211\u7684\u8001\u59BB\u4EFB\u8F7D\u5764\u5728\u4E00\u4E5D\u4E03\u4E03\u5E74\u53BB\u4E16\u7684\u65F6\u5019, \u6211\u4E5F\u4E86\u4E00\u526F\u633D\u8054: &quot;\u540C\u8363\u8FB1, \u5171\u5B89\u5371, \u51FA\u5165\u76F8\u6276\u6301, \u78A7\u843D\u9EC4\u6CC9\u541B\u5148\u53BB; \u65A9\u540D\u5173, \u7834\u5229\u7D22, \u4FEF\u4EF0\u65E0\u6127\u4F5C, \u6D77\u9614\u5929\u7A7A\u6211\u81EA\u98DE.&quot; \u5728\u90A3\u4E2A\u65F6\u5019, \u6211\u5F00\u59CB\u8BA4\u8BC6\u5230\u540D, \u5229\u4E4B\u6240\u4EE5\u4E3A\u675F\u7F1A, &quot;\u6211\u81EA\u98DE&quot; \u4E4B\u6240\u4EE5\u4E3A\u81EA\u7531. \u5728\u5199\u8FD9\u672C\u518C\u7B2C\u516B\u5341\u4E00\u7AE0\u7684\u65F6\u5019, \u6211\u771F\u611F\u89C9\u5230 &quot;\u6D77\u9614\u5929\u7A7A\u6211\u81EA\u98DE&quot; \u4E86.</p></blockquote><p>\u8FD9\u7BC7\u81EA\u5E8F\u5199\u4E8E 1990 \u5E74 7 \u6708 11 \u65E5, \u56DB\u4E2A\u6708\u540E\u51AF\u5148\u751F\u81EA\u5DF1\u4E5F\u53BB\u4E16\u4E86. \u51AF\u5148\u751F\u76F4\u5230\u665A\u5E74\u624D\u611F\u5230\u771F\u6B63\u7684\u81EA\u7531, \u54F2\u5B66\u5BB6\u6700\u7EC8\u53D1\u73B0\u4E86\u81EA\u6211.</p><p>\u4ECE\u51AF\u5148\u751F\u7684\u4E00\u751F, \u6211\u60F3\u5230\u4F5C\u4E3A\u77E5\u8BC6\u5206\u5B50\u7684\u54F2\u5B66\u5BB6\u547D\u8FD0.</p><p>\u6211\u60F3, \u6709\u4E24\u7C7B\u54F2\u5B66\u5BB6: \u4E00\u7C7B\u54F2\u5B66\u5BB6\u70ED\u8877\u5173\u5FC3\u4EBA\u7C7B\u7684\u524D\u9014, \u4ED6\u7684\u54F2\u5B66\u662F\u60F3\u7528\u6765\u6D4E\u4E16\u5B89\u6C11\u7684; \u4E00\u7C7B\u54F2\u5B66\u5BB6\u53EA\u662F\u5BF9\u54F2\u5B66\u95EE\u9898\u53D1\u751F\u4E86\u5174\u8DA3, \u4E3A\u5B66\u672F\u800C\u5B66\u672F, \u6CA1\u6709\u5176\u4ED6\u7684\u76EE\u7684. \u660E\u663E\u7684\u4F8B\u5B50\u662F\u53E4\u4EE3\u5E0C\u814A\u7684\u67CF\u62C9\u56FE\u548C\u4E9A\u91CC\u58EB\u591A\u5FB7. \u67CF\u62C9\u56FE\u7684\u7406\u60F3\u662F\u8BA9\u54F2\u5B66\u4E3A\u738B, \u4EE5\u4ED6\u7684\u54F2\u5B66\u521B\u9020\u51FA\u4E00\u4E2A\u5B9E\u73B0\u6B63\u4E49\u7684\u7406\u60F3\u56FD\u5BB6. \u4E3A\u6B64\u76EE\u7684\u4ED6\u4E09\u6B21\u8FDC\u8BBF\u672C\u672C\u91CC\u7684\u53D9\u62C9\u53E4\u57CE\u90A6, \u60F3\u627E\u5230\u4E00\u4E2A\u80FD\u8D2F\u5F7B\u4ED6\u601D\u60F3\u7684\u541B\u738B; \u4E0D\u4F46\u6CA1\u6709\u8FBE\u5230\u76EE\u7684, \u53CD\u88AB\u5F53\u5974\u96B6\u51FA\u5356, \u8FD8\u51E0\u4E4E\u4E27\u4E86\u547D. \u4E0D\u5F97\u5DF2\u8457\u4E66\u7ACB\u8BF4, \u5728\u6700\u540E\u8457\u4F5C\u300A\u6CD5\u5F8B\u7BC7\u300B\u4E2D\u8FD8\u5728\u4E3A\u7406\u60F3\u57CE\u90A6\u5236\u5B9A\u4E00\u6761\u6761\u5177\u4F53\u6CD5\u5F8B. \u4E9A\u91CC\u58EB\u591A\u5FB7\u662F\u4ED6\u7684\u5B66\u751F, \u5374\u6CA1\u6709\u4ED6\u8001\u5E08\u90A3\u6837\u7684\u96C4\u5FC3. \u5F53\u65F6\u8FDC\u5F81\u6B27, \u4E9A, \u975E\u4E09\u6D32, \u5EFA\u7ACB\u897F\u65B9\u5386\u53F2\u4E0A\u5927\u5E1D\u56FD\u7684\u9A6C\u5176\u987F\u738B\u4E9A\u91CC\u5C71\u5927\u662F\u4E9A\u91CC\u58EB\u591A\u5FB7\u7684\u5B66\u751F, \u4ED6\u5374\u6CA1\u6709\u60F3\u4F9D\u9760\u8FD9\u4F4D\u5B66\u751F\u6765\u5B9E\u73B0\u4ED6\u7684\u54F2\u5B66. \u4ED6\u8BA4\u4E3A\u54F2\u5B66\u4EA7\u751F\u4E8E\u60CA\u5947, \u53EA\u662F\u7531\u4E8E\u4EBA\u5BF9\u5468\u56F4\u53D1\u751F\u8BB8\u591A\u95EE\u9898, \u8981\u60F3\u5BFB\u6839\u7A76\u5E95\u5730\u53BB\u89E3\u51B3\u5B83\u4EEC, \u624D\u4EA7\u751F\u54F2\u5B66\u548C\u5404\u79CD\u79D1\u5B66\u601D\u60F3. \u56E0\u6B64\u4ED6\u4EE5\u4E3A\u53EA\u6709\u8FD9\u79CD\u601D\u60F3\u672C\u8EAB\u624D\u662F\u76EE\u7684, \u5982\u679C\u4E3A\u4E86\u5176\u4ED6\u5B9E\u7528\u76EE\u7684, \u5728\u4ED6\u770B\u6765\u662F\u4F4E\u4E00\u7B49\u7684. \u867D\u7136\u4ED6\u4E5F\u7814\u7A76\u653F\u6CBB\u5B66\u548C\u4F26\u7406\u5B66, \u5374\u53EA\u662F\u5BA2\u89C2\u5730\u7814\u7A76\u5404\u79CD\u653F\u5236\u548C\u5B66\u8BF4\u7684\u662F\u975E\u5F97\u5931, \u5C06\u4ED6\u81EA\u5DF1\u7F6E\u8EAB\u4E8B\u5916.</p><p>\u5728\u4E2D\u56FD\u54F2\u5B66\u5BB6\u5F53\u4E2D, \u6211\u5F88\u5BB9\u6613\u5730\u60F3\u5230\u51AF\u5148\u751F\u548C\u91D1\u5CB3\u9716\u5148\u751F. \u4ED6\u4EEC\u4E24\u4F4D\u90FD\u662F\u6211\u7684\u8001\u5E08, \u4E0A\u5927\u5B66\u65F6\u542C\u8FC7\u4ED6\u4EEC\u7684\u8BFE, \u4F46\u56E0\u4E3A\u6211\u81EA\u5DF1\u7684\u5174\u8DA3\u5728\u897F\u65B9\u54F2\u5B66\u53F2, \u5BF9\u4ED6\u4EEC\u7684\u54F2\u5B66\u601D\u60F3\u7F3A\u4E4F\u540C\u60C5\u7684\u4E86\u89E3. 1952 \u5E74\u9662\u7CFB\u8C03\u6574\u540E\u5012\u662F\u6709\u673A\u4F1A\u548C\u4ED6\u4EEC\u4E00\u8D77\u5DE5\u4F5C, \u4F46\u63A5\u89E6\u662F\u504F\u91CD\u653F\u6CBB\u6027\u7684.</p><p>\u51AF\u5148\u751F\u4E66\u4E2D\u6709\u4E00\u7AE0\u662F\u4E13\u95E8\u8BB2\u91D1\u5148\u751F\u7684\u54F2\u5B66\u7684, \u6700\u540E\u4E00\u6BB5\u8BF4: &quot;\u56E0\u4E3A\u5BF9\u4E00\u4EF6\u4E00\u4EF6\u7684\u4E8B\u5B9E\u6BEB\u65E0\u8868\u793A, \u4EBA\u4EEC\u4ECE\u300A\u8BBA\u9053\u300B\u5F97\u4E0D\u5230\u65B0\u7684\u77E5\u8BC6, \u4EE5\u4E3A\u300A\u8BBA\u9053\u300B\u53EA\u662F\u5206\u6790\u4E00\u4E9B\u6982\u5FF5, \u8FD9\u6837\u7684\u5206\u6790\u5BF9\u4E8E\u5B9E\u9645\u751F\u6D3B\u6CA1\u6709\u4EC0\u4E48\u7528\u5904. \u542C\u8BF4\u91D1\u5CB3\u9716\u5148\u751F\u5728\u82F1\u56FD\u5251\u6865\u5927\u5B66\u66FE\u6709\u4E00\u4E2A\u53D1\u8A00\u8BF4, \u54F2\u5B66\u662F\u6982\u5FF5\u7684\u6E38\u620F. \u4ED6\u8BA4\u4E3A, \u54F2\u5B66\u672C\u6765\u8BF4\u662F\u8FD9\u6837\u7684\u4E00\u95EE\u5B66\u95EE. \u4EBA\u4EEC\u4E0D\u7981\u8981\u95EE, \u8FD9\u6837\u7684\u5B66\u95EE\u5BF9\u4E8E\u4EBA\u751F\u6709\u4EC0\u4E48\u7528\u5904, \u8FD9\u5C31\u7275\u6D89\u5230\u54F2\u5B66\u7684\u6027\u8D28\u53CA\u5176\u4F5C\u7528\u7684\u95EE\u9898. \u5BF9\u4E8E\u8FD9\u4E2A\u95EE\u9898, \u300A\u65B0\u7F16\u300B\u5728\u7B2C\u516B\u5341\u4E00\u7AE0\u4E2D\u5C06\u4F5C\u8BE6\u7EC6\u7684\u8BA8\u8BBA.&quot;</p><p>\u51AF\u5148\u751F\u5BF9\u91D1\u5148\u751F\u7684\u54F2\u5B66\u601D\u60F3\u7684\u89E3\u91CA\u5F53\u7136\u662F\u6B63\u786E\u7684, \u4F46\u4ED6\u5728\u6700\u540E\u4E00\u7AE0\u603B\u7ED3\u4E2D\u8BF4\u660E\u7684\u54F2\u5B66\u7684\u6027\u8D28\u548C\u4F5C\u7528, \u6211\u4EE5\u4E3A\u5E76\u4E0D\u662F\u91D1\u5148\u751F\u6240\u60F3\u7684\u54F2\u5B66. \u91D1\u5148\u751F\u5728\u300A\u8BBA\u9053\u300B\u7B49\u8457\u4F5C\u4E2D\u505A\u4E86\u7EC6\u81F4\u7684\u6982\u5FF5\u5206\u6790, \u786E\u5B9E\u7C7B\u4E4E\u6982\u5FF5\u6E38\u620F, \u5BF9\u4EBA\u751F\u5E76\u6CA1\u6709\u4EC0\u4E48\u7528\u5904; \u4F46\u662F\u5BF9\u54F2\u5B66\u6709\u7528\u5904, \u7528\u5904\u5728\u4E8E\u63D0\u4F9B\u4E00\u79CD\u65B0\u7684\u54F2\u5B66\u7406\u8BBA, \u4E3A\u540E\u6765\u7684\u63A2\u5F00\u8F9F\u65B0\u7684\u9014\u5F84. \u6211\u60F3\u91D1\u5148\u751F\u5199\u90A3\u4E9B\u4E66\u7684\u65F6\u5019, \u51B3\u6CA1\u6709\u60F3\u5230\u8981\u50CF\u51AF\u5148\u751F\u6240\u8BF4\u7684\u90A3\u6837 &quot;\u4E3A\u751F\u6C11\u7ACB\u547D&quot;, &quot;\u4E3A\u4E07\u4E16\u5F00\u592A\u5E73&quot;.</p><p>\u6211\u5E76\u4E0D\u662F\u8BF4\u91D1\u5148\u751F\u6CA1\u6709\u653F\u6CBB\u70ED\u60C5, \u4ED6\u7684\u786E\u662F\u4E00\u4F4D\u70ED\u5217\u7684\u7231\u56FD\u8005. \u4E94\u5341\u5E74\u4EE3\u521D\u671F\u4ED6\u662F\u5317\u5927\u54F2\u5B66\u7CFB\u4E3B, \u6211\u662F\u7CFB\u79D8\u4E66, \u4EB2\u773C\u770B\u5230\u4ED6\u4E3A\u5F53\u65F6\u6211\u4EEC\u56FD\u5BB6\u53D1\u751F\u7684\u5DE8\u5927\u53D8\u5316\u800C\u53D8\u5174\u6FC0\u52A8\u7684\u8BB8\u591A\u60C5\u666F, \u81F3\u4ECA\u8FD8\u8BB0\u5F97 1953 \u5E74\u542C\u5230\u65AF\u5927\u6797\u901D\u4E16\u7684\u6D88\u606F\u540E, \u4ED6\u542B\u7740\u6CEA\u548C\u6211\u8C08\u8BBA\u4E16\u754C\u9769\u547D\u4F1A\u4E0D\u4F1A\u56E0\u6B64\u7231\u5230\u5F71\u54CD\u7684\u62C5\u5FC3.</p><p>\u53EF\u662F\u5728\u54F2\u5B66\u601D\u60F3\u65B9\u9762, \u89E3\u653E\u540E\u91D1\u5148\u751F\u7A76\u7ADF\u63A5\u53D7\u4E86\u591A\u5C11\u5F71\u54CD? \u8001\u5B9E\u8BF4, \u6211\u89C9\u5F97\u662F\u5F88\u96BE\u8BF4\u7684. \u6BCF\u6B21\u5B66\u672F\u6279\u5224\u4F1A\u4E0A\u4ED6\u662F\u7CFB\u4E3B\u4EFB, \u603B\u5E26\u5934\u53D1\u8A00, \u6211\u8BB0\u5F97\u662F\u5F88\u7B80\u5355\u7684 &quot;\u8868\u6001&quot;.1955 \u5E74\u4ED6\u548C\u6211\u4EEC\u5408\u5199\u7684\u6279\u5224\u5B9E\u7528\u4E3B\u4E49\u7684 &quot;\u7ECF\u9A8C&quot; \u7684\u6587\u7AE0, \u662F\u6211\u4EEC\u63A8\u52A8\u7684, \u90A3\u79CD\u7B80\u5355\u5316\u7684\u6279\u5224\u548C\u91D1\u5148\u751F\u7684\u7EC6\u81F4\u5206\u6790\u7684\u98CE\u683C\u6BEB\u4E0D\u76F8\u5E72. \u5BF9\u7F57\u7D20\u7684\u6279\u5224\u4E5F\u662F\u6211\u9F13\u52A8\u4ED6\u5199\u7684, \u4ECE\u4E8C\u5341\u4E16\u7EAA\u4E94\u5341\u5E74\u4EE3\u4E2D\u671F\u8D77\u4E00\u76F4\u5230\u516D\u5341\u5E74\u4EE3\u521D\u624D\u5199\u5B8C, \u53EF\u89C1\u4ED6\u4E0B\u7B14\u4E4B\u4E0D\u6613. \u6240\u4EE5\u5728\u4ED6\u53BB\u4E16\u540E\u9009\u7F16\u4ED6\u7684\u6587\u96C6\u8005\u5C06\u8FD9\u4E9B\u6587\u7AE0\u4E5F\u9009\u7F16\u5728\u5185, \u6211\u603B\u89C9\u5F97\u4E0D\u59A5. \u770B\u5230\u5F20\u4E2D\u884C\u5148\u751F\u5728\u300A\u8BFB\u4E66\u300B\u4E0A\u5199\u7684\u300A\u4E3A\u9B4F\u516C\u8BB3\u300B, \u5BF9\u6B64\u63D0\u51FA\u610F\u89C1. \u6211\u4F5C\u4E3A\u91D1\u5148\u751F\u7684\u5B66\u751F, \u5E76\u4E14\u5BF9\u8FD9\u4E9B\u6279\u5224\u6587\u7AE0\u8D1F\u6709\u8D23\u4EFB, \u662F\u5F88\u611F\u8C22\u5F20\u5148\u751F\u7684.</p><p>\u51AF\u5148\u751F\u5BF9\u54F2\u5B66\u7684\u770B\u6CD5\u548C\u91D1\u5148\u751F\u5B8C\u5168\u4E0D\u540C. \u51AF\u5148\u751F\u7684\u770B\u6CD5\u96C6\u4E2D\u8868\u8FF0\u4E8E\u5168\u4E66\u6700\u540E\u4E00\u7AE0 &quot;\u603B\u7ED3&quot; \u4E2D. \u5728\u8FD9\u4E00\u7AE0\u7684\u7B2C\u4E00\u90E8\u5206\u8BB2 &quot;\u4ECE\u4E2D\u56FD\u54F2\u5B66\u53F2\u7684\u4F20\u7EDF\u770B\u54F2\u5B66\u7684\u6027\u8D28\u53CA\u5176\u4F5C\u7528&quot;. \u4ED6\u9996\u5148\u6307\u51FA\u54F2\u5B66\u4E0D\u662F\u79D1\u5B66, \u56E0\u4E3A\u79D1\u5B66\u662F\u5BF9\u5B9E\u9645\u6709\u6240\u80AF\u5B9A, \u800C\u54F2\u5B66\u662F\u5BF9\u4E8E\u5B9E\u9645\u65E0\u6240\u80AF\u5B9A\u7684. \u4F46\u4ED6\u8BA4\u4E3A\u54F2\u5B66\u662F\u5BF9\u4EBA\u751F\u4F5C\u6709\u7CFB\u7EDF\u7684\u53CD\u601D. \u4ED6\u8BF4, &quot;\u5C31\u4EBA\u7684\u5B9E\u9645\u751F\u6D3B\u8BF4, \u4E00\u7EC4\u4E00\u7EC4\u5BF9\u4E8E\u5B9E\u9645\u65E0\u6240\u80AF\u5B9A\u7684\u77E5\u8BC6, \u4F46\u80FD\u63D0\u9AD8\u4EBA\u7684\u5883\u754C&quot;. \u4ED6\u4EE5\u4E3A\u8981\u8FBE\u5230\u4ED6\u6240\u8BF4\u7684\u6700\u9AD8\u5883\u754C -- \u5929\u5730\u5883\u754C, &quot;\u975E\u7ECF\u8FC7\u54F2\u5B66\u8FD9\u6761\u8DEF\u4E0D\u53EF&quot;; \u56E0\u4E3A\u54F2\u5B66\u80FD\u8BA9\u4F60 &quot;\u81EA\u540C\u4E8E\u5927\u5168&quot;, \u4E0D\u5C06 &quot;\u6211&quot; \u548C\u5929\u5730\u4E07\u7269\u5BF9\u7ACB\u8D77\u6765. \u4ED6\u8FD8\u7279\u522B\u6307\u51FA: &quot;\u54F2\u5B66\u5BB6\u4E0D\u540C\u4E8E\u54F2\u5B66\u6559\u6388. \u54F2\u5B66\u6559\u6388\u662F\u4ECE\u6587\u5B57\u4E0A\u4E86\u89E3\u54F2\u5B66\u6982\u5FF5; \u54F2\u5B66\u5BB6\u4E0D\u540C, \u4ED6\u5BF9\u4E8E\u54F2\u5B66\u4E0A\u7684\u6982\u5FF5\u4E0D\u53EA\u4F5C\u6587\u5B57\u4E0A\u7684\u4E86\u89E3, \u800C\u662F\u4F5C\u66F4\u6DF1\u5165\u7684\u7406\u89E3, \u5E76\u628A\u8FD9\u6837\u7684\u7406\u89E3\u878D\u5408\u4E8E\u4ED6\u7684\u751F\u6D3B\u4E2D. \u8FD9\u5728\u4E2D\u56FD\u54F2\u5B66\u4F20\u7EDF\u7684\u8BDD\u4E2D\u53EB\u4F5C &#39;\u8EAB\u4F53\u529B\u884C&#39;&quot;. \u51AF\u5148\u751F\u8BF4\u7684\u786E\u5B9E\u662F\u4E2D\u56FD\u54F2\u5B66\u7684\u4F20\u7EDF, \u897F\u65B9\u54F2\u5B66\u5F88\u5C11\u8FD9\u6837\u8BB2.</p><p>\u51AF\u5148\u751F\u8BF4, &quot;&#39;\u4EC1&#39; \u662F\u5112\u5BB6\u6240\u8BF4\u7684\u4EBA\u7684\u6700\u9AD8\u7684\u7CBE\u795E\u5883\u754C, \u4E5F\u662F\u4EBA\u4E4B\u6240\u4EE5\u4E3A\u4EBA\u7684\u6700\u9AD8\u6807\u51C6. &quot; \u8FD9\u4E2A\u6700\u9AD8\u7684\u7CBE\u795E\u5883\u754C\u548C\u6700\u9AD8\u7684\u6807\u51C6\u7A76\u7ADF\u6709\u4EC0\u4E48\u5185\u5BB9? \u51AF\u5148\u751F\u6700\u559C\u6B22\u5F15\u7528\u5F20\u8F7D\u7684\u56DB\u53E5\u8BDD: &quot;\u4E3A\u5929\u5730\u7ACB\u5FC3, \u4E3A\u751F\u6C11\u7ACB\u547D, \u4E3A\u5F80\u5723\u7EE7\u7EDD\u5B66, \u4E3A\u4E07\u4E16\u5F00\u592A\u5E73&quot;. \u4ED6\u79F0\u4E4B\u4E3A &quot;\u6A2A\u6E20\u56DB\u53E5&quot;. \u5728\u5BF9\u8FD9\u56DB\u53E5\u8BDD\u4F5C\u4E86\u89E3\u91CA\u540E, \u4ED6\u603B\u7ED3\u8BF4:</p><blockquote><p>\u6700\u5408\u4E8E &quot;\u4EBA\u4E4B\u6240\u4EE5\u4E3A\u4EBA&quot; \u6807\u51C6\u7684\u4EBA, \u5112\u5BB6\u79F0\u4E3A &quot;\u5723\u4EBA&quot;. \u5112\u5BB6\u8BA4\u4E3A, \u5723\u4EBA\u6700\u5B9C\u4E8E\u505A\u793E\u4F1A\u6700\u9AD8\u7EDF\u6CBB\u8005, \u56E0\u4E3A\u4ED6\u662F\u5ED3\u7136\u5927\u516C. \u67CF\u62C9\u56FE\u8BA4\u4E3A, \u5728\u4ED6\u7684\u7406\u60F3\u793E\u4F1A\u4E2D, \u6700\u9002\u5408\u7684\u7EDF\u6CBB\u8005\u662F\u54F2\u5B66\u5BB6, \u5373\u628A\u54F2\u5B66\u4E0E\u653F\u6CBB\u5B9E\u8DF5\u7ED3\u5408\u8D77\u6765\u7684\u6240\u8C13 &quot;\u54F2\u5B66\u738B&quot;. \u5112\u5BB6\u4E5F\u8BA4\u4E3A, \u6709\u5723\u4EBA\u4E4B\u5FB7\u8005, \u624D\u5B9C\u4E8E\u5C45\u6700\u9AD8\u7EDF\u6CBB\u8005\u4E4B\u4F4D, \u8FD9\u5C31\u662F\u6240\u8C13 &quot;\u5723\u738B&quot;. \u300A\u5E84\u5B50.\u5929\u4E0B\u7BC7\u300B\u8BA4\u4E3A, \u6700\u9AD8\u7684\u5B66\u95EE\u662F &quot;\u5185\u5723\u5916\u738B\u4E4B\u9053&quot;\uFF0C\u7528\u6211\u4EEC\u73B0\u5728\u7684\u8BDD\u8BF4, \u5C31\u662F\u54F2\u5B66.</p></blockquote><p>\u51AF\u5148\u751F\u5728\u81EA\u5E8F\u4E2D\u8BF4\u5728\u5199\u8FD9\u4E00\u7AE0\u65F6\u4ED6\u771F\u611F\u89C9\u5230\u81EA\u7531\u4E86, \u4ED6\u771F\u662F &quot;\u6D77\u9614\u5929\u7A7A\u6211\u81EA\u98DE&quot; \u5730\u8BF4\u51FA\u4ED6\u7684\u7406\u60F3 -- \u8981\u8BA9\u54F2\u5B66\u5BB6\u5145\u5F53\u793E\u4F1A\u7684\u6700\u9AD8\u7EDF\u6CBB\u8005.</p><p>\u8FD9\u79CD\u7406\u60F3\u6709\u5B9E\u73B0\u7684\u53EF\u80FD\u4E48?</p><p>\u5B9E\u9645\u4E0A, \u793E\u4F1A\u548C\u56FD\u5BB6\u7684\u6700\u9AD8\u7EDF\u6CBB\u8005, \u9664\u4E86\u7531\u6C11\u4E3B\u9009\u4E3E\u4EA7\u751F\u4EE5\u5916, \u4E0D\u662F\u7531\u6B66\u529B\u593A\u53D6\u7684, \u4FBF\u662F\u901A\u5B88\u6B8B\u9177\u7684\u653F\u6CBB\u6597\u4E89\u83B7\u5F97\u7684, \u4ED6\u4EEC\u7684\u540E\u4EE3\u5219\u662F\u4E16\u88AD\u7684. \u6240\u4EE5\u54F2\u5B66\u5BB6\u662F\u5F53\u4E0D\u4E86\u7EDF\u6CBB\u8005\u7684. \u5373\u4F7F\u5B54\u5B50\u81EA\u5DF1\u751F\u524D\u4E5F\u53EA\u80FD\u5E26\u7740\u4E00\u7FA4\u5F1F\u5B50, \u5F80\u6765\u5954\u8D70, \u5468\u6E38\u5217\u56FD, \u60F3\u6C42\u5F97\u4E00\u5B98\u534A\u804C, \u8BA9\u4ED6\u6709\u65BD\u5C55\u624D\u80FD\u7684\u673A\u4F1A, \u672A\u80FD\u5982\u613F. \u53EA\u662F\u5230\u4ED6\u6B7B\u540E\u51E0\u767E\u5E74\u624D\u88AB\u4EBA\u53D1\u73B0, \u539F\u6765\u4ED6\u7684\u5B66\u8BF4\u4E2D\u4E5F\u6709\u53EF\u4EE5\u4E3A\u7EDF\u6CBB\u8005\u6240\u7528\u7684, \u624D\u88AB\u63A8\u5D07\u8D77\u6765, \u800C\u4E14\u4E00\u518D\u62E8\u9AD8, \u6210\u4E3A &quot;\u81F3\u5723\u5148\u5E08&quot;, \u7687\u5E1D\u4E5F\u8981\u4EB2\u81EA\u796D\u7940\u4ED6. \u5B54\u5B50\u5076\u5C14\u4E5F\u4F1A\u88AB\u6253\u5012, \u4FBF\u4E0D\u4E45\u4ECD\u4F1A\u88AB\u5C0A\u5D07, \u5386\u4E45\u4E0D\u8870.</p><p>\u81F3\u4E8E\u51AF\u5148\u751F\u6240\u8BF4\u7684\u6709\u5FB7\u624D\u7684\u54F2\u5B66\u5BB6\u6216\u6709\u5B89\u90A6\u6CBB\u56FD\u624D\u80FD\u7684\u77E5\u8BC6\u5206\u5B50, \u662F\u53EA\u80FD\u505A\u6BDB\u6CFD\u4E1C\u79F0\u4E4B\u4E3A &quot;\u7CB1\u4E0A\u541B\u5B50\u7684&quot;, \u53EA\u80FD\u4F9D\u9644\u4E8E\u67D0\u4E2A\u7EDF\u6CBB\u8005\u6216\u7EDF\u6CBB\u96C6\u56E2, \u56E0\u6B64\u4ED6\u4EEC\u7684\u547D\u8FD0\u603B\u662F\u638C\u63E1\u5728\u7EDF\u6CBB\u8005\u624B\u4E0A. \u4E00\u4E2A\u6709\u624D\u80FD\u7684\u4EBA\u5982\u679C\u4E0D\u613F\u610F\u505A\u552F\u552F\u8BFA\u8BFA\u7684\u5E94\u58F0\u866B, \u4FBF\u4E0D\u5BB9\u4E8E\u5F53\u671D, \u4E0D\u88AB\u8BA4\u4E3A\u662F &quot;\u5FE4\u9006&quot;, \u4E5F\u8981\u88AB\u6000\u7591\u4E3A\u6709\u7BE1\u4F4D\u7684 &quot;\u91CE\u5FC3&quot;, \u603B\u4E4B\u662F\u843D\u5F97\u6CA1\u6709\u597D\u4E0B\u573A. \u8303\u8821\u548C\u5F20\u826F\u662F\u804C\u660E\u4EBA, \u529F\u6210\u4E4B\u540E\u8D76\u5FEB\u6025\u6D41\u52C7\u9000, \u624D\u4FDD\u5168\u4E86\u6027\u547D, \u6CA1\u6709\u906D\u5230\u6587\u79CD\u548C\u97E9\u4FE1\u90A3\u6837\u7684\u7ED3\u5C40. \u8FD1\u6765\u5E7F\u4E3A\u6D41\u4F20\u7684\u5386\u53F2\u5C0F\u8BF4\u300A\u66FE\u56FD\u85E9\u300B, \u4F5C\u8005\u6839\u636E\u65E5\u8BB0\u548C\u4E66\u4FE1\u4E86\u89E3\u4ED6\u7684\u5185\u5FC3\u4E16\u754C, \u5C06\u66FE\u56FD\u85E9\u653B\u7834\u5929\u4EAC\u7ACB\u4E0B\u76D6\u4E16\u529F\u7EE9\u4EE5\u540E, \u5374\u9047\u5230\u6765\u81EA\u4E0A\u4E0B\u5404\u65B9\u7684\u79CD\u79CD\u731C\u5FCC, \u523B\u753B\u5F97\u5165\u6728\u4E09\u5206. \u8FD9\u7C7B\u4EBA\u4E2D\u547D\u8FD0\u6700\u597D\u7684\u4F3C\u4E4E\u8981\u6570\u8BF8\u845B\u4EAE, \u53EF\u662F\u4E5F\u522B\u770B\u5728\u5C0F\u8BF4\u4E2D, \u5728\u821E\u53F0\u548C\u7535\u89C6\u5C4F\u4E0A\u4ED6\u90A3\u6837\u7FBD\u6247\u7EB6\u5DFE, \u701F\u6D12\u81EA\u5982, \u5B9E\u9645\u4E0A\u6211\u770B\u4ED6\u4E5F\u6709\u8BF4\u4E0D\u51FA\u7684\u82E6\u5904: \u5355\u8BF4\u767D\u5E1D\u57CE\u6258\u5B64\u4E00\u5E55, \u4E0E\u5176\u8BF4\u5218\u5907\u771F\u662F\u90A3\u4E48\u4EC1\u6148\u5FE0\u539A, \u80AF\u4E8E &quot;\u8BA9\u8D24&quot;, \u4E0D\u5982\u8BF4\u4ED6\u662F\u62C5\u5FC3\u5BB3\u6015, \u800D\u6743\u672F\u903C\u7740\u5B54\u660E\u5148\u751F\u4F5C\u51FA\u627F\u8BFA, \u5373\u4F7F\u662F\u5BF9\u90A3 &quot;\u6276\u4E0D\u8D77\u7684\u963F\u6597&quot;, \u4ED6\u4E5F\u53EA\u80FD\u662F &quot;\u97A0\u8EAC\u5C3D\u7601, \u6B7B\u800C\u540E\u5DF2&quot;, \u660E\u77E5\u4E0D\u53EF\u4E3A\u800C\u4E3A\u4E4B. \u5BF9\u4E8E\u67D0\u4E9B\u653F\u6CBB\u5BB6\u7684\u8FD9\u79CD\u59D4\u66F2\u6C42\u5168, \u5FCD\u8FB1\u8D1F\u91CD\u7684\u884C\u4E3A, \u540E\u4EBA\u53C8\u4F55\u5FC5\u8981\u6C42\u82DB\u6C42\u8D23\u5907\u5462?</p><p>\u5728\u4E2D\u56FD\u5386\u53F2\u4E0A, \u4ECE\u53E4\u81F3\u4ECA\u8FD9\u7C7B\u4E8B\u60C5\u53EF\u8BF4\u7684\u5B9E\u5728\u592A\u591A, \u51AF\u5148\u751F\u81EA\u5DF1\u7684\u7ECF\u5386\u53C8\u5982\u4F55\u5462? \u4ED6\u5728\u665A\u5E74, 1984 \u5E74\u51FA\u7248\u7684\u300A\u4E09\u677E\u5802\u81EA\u5E8F\u300B\u4E2D\u5BF9\u4ED6\u4E00\u751F\u7684\u7ECF\u5386\u548C\u5B66\u672F\u601D\u60F3\u4F5C\u4E86\u6BD4\u8F83\u8BE6\u5C3D\u7684\u8BB0\u8FF0. \u4ED6\u5728&quot;\u300A\u81EA\u5E8F\u300B\u4E4B\u81EA\u5E8F&quot; \u4E2D\u8BF4: &quot;\u540D\u66F0\u300A\u81EA\u5E8F\u300B, \u975E\u4E00\u4E66\u4E4B\u5E8F, \u4E43\u4F59\u4EE5\u524D\u8457\u4F5C\u4E4B\u603B\u5E8F\u4E5F. \u4E16\u4E4B\u77E5\u4EBA\u8BBA\u4E16, \u77E5\u6211\u7F6A\u6211\u8005, \u4EE5\u89C2\u89C8\u7109&quot;. \u6B63\u56E0\u4E3A\u4ED6\u662F\u62B1\u7740\u8981\u5C06\u81EA\u5DF1\u7684\u4E00\u751F\u7559\u7ED9\u4E16\u4EBA\u8BC4\u8BF4\u7684\u76EE\u7684, \u6240\u4EE5\u5373\u4F7F\u4E00\u4E9B\u9C9C\u4E3A\u4EBA\u77E5\u7684\u4E8B\u60C5, \u4E5F\u76F4\u8A00\u4E0D\u8BB3\u5730\u5199\u4E86\u51FA\u6765. \u597D\u50CF\u51AF\u5148\u751F\u7684\u8FD9\u4EFD\u82E6\u5FC3\u8FD8\u6CA1\u6709\u5F15\u8D77\u4EBA\u4EEC\u7684\u6CE8\u610F, \u6211\u73B0\u5728\u4E3B\u6839\u636E\u4E66\u4E2D\u7684\u539F\u8BDD, \u5C06\u5176\u4E2D\u75D5\u8FF9\u4F5C\u70B9\u7B80\u5355\u52FE\u753B.</p><p>1923 \u5E74\u51AF\u5148\u751F\u5728\u7F8E\u56FD\u5F97\u5230\u535A\u58EB\u5B66\u4F4D\u56DE\u56FD, \u5728\u6545\u4E61\u6CB3\u5357\u4E2D\u5DDE\u5927\u5B66\u4EFB\u6559\u6388\u548C\u6587\u79D1\u4E3B\u4EFB. \u4ED6\u5F00\u59CB\u9762\u4E34\u524D\u9014\u9009\u62E9: \u662F\u4E8B\u529F\u5462\u8FD8\u662F\u4ECE\u4E8B\u5B66\u672F? \u5728\u4E8B\u529F\u65B9\u9762\u4ED6\u7684 &quot;\u62B1\u8D1F\u4E0D\u5927, \u53EA\u60F3\u529E\u597D\u4E00\u4E2A\u5927\u5B66&quot;, \u56E0\u6B64\u4ED6\u5411\u6821\u957F\u63D0\u51FA\u8981\u6C42, \u8BA9\u4ED6\u5F53\u6821\u52A1\u4E3B\u4EFB\u7BA1\u7406\u8FD9\u6240\u5927\u5B66. \u6821\u957F\u6CA1\u6709\u540C\u610F, \u4ED6\u5C31\u79BB\u5F00\u4E86. \u51AF\u5148\u751F\u7684\u5CB3\u5BB6\u662F\u6709\u9769\u547D\u4F20\u7EDF\u7684, 1925 \u5E74\u4ED6\u6765\u5230\u5E7F\u5DDE, \u672C\u6765\u662F\u60F3\u53BB\u770B\u770B\u5F53\u65F6\u8FD9\u4E2A\u9769\u547D\u6839\u636E\u5730\u7684\uFF0C\u53EF\u662F\u4ED6\u770B\u5230\u548C\u542C\u5230\u7684\u5C3D\u662F\u56FD\u5171\u7684\u5206\u88C2\u548C\u6597\u4E89, \u4ED6\u53EA\u5446\u4E86\u534A\u5E74\u5C31\u79BB\u5F00\u4E86. \u6765\u5230\u5317\u4EAC\u540E\u5728\u6E05\u534E\u5927\u5BB6\u505A\u5230\u6821\u52A1\u4F1A\u8BAE\u4E3B\u5E2D, \u4ED6\u8BF4\u662F\u627E\u5230\u4E86 &quot;\u5B89\u8EAB\u7ACB\u547D\u4E4B\u5730&quot;. 1934 \u5E74\u4ED6\u5E94\u9080\u53BB\u82F1\u56FD\u8BB2\u4E2D\u56FD\u6587\u5316, \u7B2C\u4E8C\u5E74\u5728\u6B27\u6D32\u6E38\u5386\u6CD5\u56FD, \u745E\u58EB, \u5FB7\u56FD\u4EE5\u540E\u53C8\u53BB\u4E86\u524D\u82CF\u8054, \u662F\u60F3\u4E86\u89E3\u9769\u547D\u540E\u7684\u60C5\u51B5\u7684. \u4ED6\u5728\u524D\u82CF\u8054\u770B\u5230\u90A3\u91CC\u5E76\u4E0D\u50CF\u8D44\u672C\u4E3B\u4E49\u56FD\u5BB6\u5BA3\u4F20\u7684\u90A3\u6837 &quot;\u6CA1\u6709\u5B97\u6559&quot;, &quot;\u4E0D\u8981\u5BB6\u5EAD&quot; \u4E86, \u800C\u4E14\u5927\u4E8B\u5BA3\u4F20\u52B3\u52A8\u6A21\u8303\u5148\u8FDB\u4EBA\u7269, \u7B26\u5408\u51AF\u5148\u751F\u7684 &quot;\u5C1A\u8D24&quot; \u7406\u60F3, \u6240\u4EE5 &quot;\u5BF9\u793E\u4F1A\u4E3B\u4E49\u53D1\u751F\u4E86\u597D\u611F&quot;. \u5F52\u56FD\u4EE5\u540E\u4ED6\u4F5C\u4E86\u4E24\u6B21\u6F14\u8BB2, \u8BB2\u4ED6\u6240\u89C1\u7684\u524D\u82CF\u8054\u60C5\u51B5\u548C\u4ED6\u6240\u4E86\u89E3\u7684\u552F\u7269\u53F2\u89C2. \u7FA4\u4F17\u7EB7\u7EB7\u8BAE\u8BBA &quot;\u51AF\u751F\u5148\u53D8\u4E86&quot;. \u5374\u7ED9\u4ED6\u5F15\u6765\u4E00\u573A\u707E\u96BE: \u4ED6\u88AB\u56FD\u6C11\u515A\u8B66\u5BDF\u5E26\u4E0A\u624B\u94D0, \u62BC\u9001\u5230\u4FDD\u5B9A\u848B\u4ECB\u77F3\u884C\u8425\u4E2D\u5173\u4E86\u4E00\u591C. \u7B2C\u4E8C\u5929\u662F\u4F55\u5E94\u94A6\u53D1\u6765\u7535\u62A5, \u8BF4\u4ED6 &quot;\u65E0\u91CD\u5927\u5ACC\u7591, \u7740\u5373\u91CA\u653E&quot;. \u51AF\u5148\u751F\u8BF4: &quot;\u6211\u5728\u8FD9\u4E2A\u65F6\u5019\u597D\u50CF\u8D70\u5230\u4E00\u4E2A\u5341\u5B57\u8DEF\u53E3. \u6211\u53EF\u4EE5\u4E58\u6B64\u673A\u4F1A\u548C\u5357\u4EAC\u653F\u5E9C\u51B3\u88C2, \u5927\u95F9\u4E00\u573A, \u52A0\u5165\u5171\u4EA7\u515A\u9886\u5BFC\u7684\u9769\u547D\u961F\u4F0D\u7684\u884C\u5217. \u6216\u8005\u7EE7\u7EED\u6211\u90A3\u4E2A\u6837\u5B50, \u66F4\u52A0\u8C28\u5C0F\u614E\u5FAE, \u4EE5\u907F\u514D\u7279\u52A1\u7684\u6CE8\u610F.&quot; \u5728\u8FD9\u4E2A\u7B2C\u4E8C\u6B21\u4E5F\u662F\u66F4\u91CD\u8981\u7684\u9009\u62E9\u65F6, \u4ED6\u9009\u62E9\u4E86\u540E\u8005, &quot;\u51AF\u5148\u751F\u6CA1\u6709\u53D8\u8FC7\u6765&quot;.</p><p>\u6B64\u8DEF\u4E0D\u901A\u4FBF\u53EA\u80FD\u8D70\u53E6\u4E00\u6761\u8DEF. \u6E05\u534E\u6821\u957F\u6885\u8D3B\u7426\u8981\u4ED6\u540C\u53BB\u5357\u4EAC, &quot;\u53EB\u4ED6\u4EEC\u770B\u770B\u4F60\u8FD9\u4E2A &#39;\u5171\u4EA7\u515A&#39; \u662F\u4EC0\u4E48\u6837\u5B50.&quot; \u4ED6\u4E5F\u5C31\u53BB\u4E86. \u6297\u6218\u5F00\u59CB, \u6E05\u534E\u548C\u5317\u5927, \u5357\u5F00\u5728\u6606\u660E\u6210\u7ACB\u897F\u5357\u8054\u5927, \u51AF\u5148\u751F\u4EFB\u6587\u5B66\u9662\u957F. \u5F53\u65F6\u91CD\u5E86\u6559\u80B2\u90E8\u89C4\u5B9A\u5927\u5BB6\u9662\u957F\u4EE5\u4E0A\u7684\u4EBA\u5FC5\u987B\u662F\u56FD\u6C11\u515A\u515A\u5458\uFF0C \u51AF\u5148\u751F\u8003\u8651\u5230 &quot;\u5DF2\u7ECF\u6709\u8FC7\u88AB\u902E\u6355\u90A3\u6BB5\u4E8B\u60C5, \u518D\u4E0D\u52A0\u5165, \u6015\u88AB\u8BF4\u6210\u4E0D\u5408\u4F5C, \u53EA\u597D\u9ED8\u8BA4&quot;. \u4EE5\u540E\u848B\u4ECB\u77F3\u529E\u7684\u4E2D\u592E\u8BAD\u7EC3\u56E2\u548C\u56FD\u6C11\u515A\u7684\u4E2D\u592E\u653F\u6CBB\u6821\u8BB2\u5B66, \u63A5\u53D7\u848B\u4ECB\u77F3\u7684\u5BB4\u8BF7, \u5E76\u5728\u848B\u4ECB\u77F3\u5F85\u4ECE\u5BA4\u51FA\u94B1\u529E\u7684\u4E2D\u56FD\u54F2\u5B66\u7814\u7A76\u4F1A\u62C5\u4EFB\u4E3B\u4EFB\u59D4\u5458. 1945 \u5E74\u6625\u5929\u56FD\u6C11\u515A\u53EC\u5F00\u7B2C\u4E94\u6B21\u5168\u56FD\u4EE3\u8868\u5927\u4F1A, \u51AF\u5148\u751F\u88AB\u6CB3\u5357\u7701\u9009\u4E3A\u51FA\u5E2D\u4EE3\u8868. \u4ED6\u4EE5\u7B2C\u4E00\u6D41\u5B66\u8005\u8EAB\u4EFD\u53BB\u53C2\u52A0\u5927\u4F1A, \u88AB\u9009\u5165\u4E3B\u5E2D\u56E2\u5E76\u5185\u5B9A\u8981\u4ED6\u62C5\u4EFB\u56FD\u6C11\u515A\u4E2D\u592E\u59D4\u5458\uFF0C \u5F53\u848B\u4ECB\u77F3\u4EB2\u81EA\u548C\u4ED6\u8C08\u8BDD\u65F6, \u4ED6\u8BF4: &quot;\u6211\u8981\u662F\u5F53\u4E86\u4E2D\u592E\u59D4\u5458, \u518D\u5BF9\u9752\u5E74\u8BB2\u8BDD\u5C31\u4E0D\u65B9\u4FBF\u4E86&quot;, \u8FD9\u624D\u4F5C\u7F62.</p><p>\u6297\u6218\u7ED3\u675F\u540E, \u51AF\u5148\u751F\u5E94\u9080\u53BB\u7F8E\u56FD\u8BB2\u5B66, \u56DE\u56FD\u65F6\u56FD\u6C11\u515A\u7684\u8D25\u5C40\u5DF2\u5B9A, \u4ED6\u6709\u4F4D\u53C2\u52A0\u9769\u547D\u7684\u4EB2\u621A\u5EFA\u8BAE\u4ED6\u4EEC\u5168\u5BB6\u53EF\u4EE5\u4E58\u98DE\u673A\u53BB\u5EF6\u5B89. \u51AF\u5148\u751F\u4F5C\u4E86\u51B3\u5B9A: &quot;\u53CD\u6B63\u6211\u4EEC\u662F\u4E0D\u8D70\u7684, \u89E3\u653E\u519B\u4E5F\u5FEB\u6253\u5230\u5317\u4EAC\u4E86, \u6211\u4EEC\u5728\u8FD9\u91CC\u7B49\u7740\u4ED6\u4EEC\u5427. \u5F53\u65F6\u6211\u7684\u6001\u5EA6\u662F: \u65E0\u8BBA\u4EC0\u4E48\u515A\u6D3E\u5F53\u6743, \u53EA\u8981\u4ED6\u80FD\u628A\u4E2D\u56FD\u6CBB\u7406\u597D, \u6211\u90FD\u62E5\u62A4.&quot; \u89E3\u653E\u524D\u5915, \u5357\u4EAC\u6D3E\u98DE\u673A\u6765\u5317\u5E73\u63A5\u6559\u6388\u4EEC\u5357\u4E0B, \u51AF\u5148\u751F\u548C\u8BB8\u591A\u6559\u6388\u4E00\u6837, \u90FD\u6CA1\u6709\u53BB. \u6885\u8D3B\u7426\u8D70\u4E86\u4EE5\u540E, \u6E05\u534E\u6821\u52A1\u4F1A\u8BAE\u6210\u5458\u81EA\u52A8\u96C6\u5408\uFF0C \u5546\u91CF\u5584\u540E\u4E8B\u5B9C, \u63A8\u51AF\u5148\u751F\u518D\u4E00\u6B21\u5F53\u6821\u52A1\u4F1A\u8BAE\u4E3B\u5E2D. \u89E3\u653E\u519B\u8FDB\u9A7B\u6E05\u534E, \u6587\u7BA1\u4F1A\u53D1\u5E03\u547D\u4EE4, \u6210\u7ACB\u6E05\u534E\u6821\u52A1\u59D4\u5458\u4F1A, \u4EE5\u539F\u6821\u52A1\u4F1A\u8BAE\u6210\u5458\u4E3A\u59D4\u5458, \u4EFB\u547D\u51AF\u5148\u751F\u4E3A\u4E3B\u4EFB\u59D4\u5458. \u51AF\u5148\u751F\u968F\u5373\u53EC\u5F00\u5168\u6821\u5E08\u751F\u5927\u4F1A, \u5BA3\u544A &quot;\u6E05\u534E\u73B0\u5728\u662F\u4EBA\u6C11\u7684\u6E05\u534E\u4E86&quot;.</p><p>\u4F46\u662F\u4E0D\u4E45\u4EE5\u540E, \u519B\u7BA1\u4F1A\u6D3E\u6765\u519B\u4EE3\u8868, \u5B9E\u9645\u4E3B\u6301\u6821\u52A1, \u51AF\u5148\u751F\u88AB\u6401\u5728\u4E00\u8FB9. \u4E94\u6708\u5BA3\u5E03\u6821\u52A1\u59D4\u5458\u4F1A\u6539\u7EC4, \u4E0D\u518D\u662F\u51AF\u5148\u751F\u62C5\u4EFB\u4E3B\u4EFB\u59D4\u5458. \u4ED6\u5C31\u5199\u4E86\u4E00\u5C01\u4FE1, \u8981\u6C42\u4E0D\u518D\u62C5\u4EFB\u6821\u52A1\u59D4\u5458\u548C\u6587\u5B66\u9662\u957F, \u7406\u7531\u662F &quot;\u80FD\u529B\u4E0D\u80DC&quot;. \u6709\u4EBA\u544A\u8BC9\u4ED6\u4E0D\u80FD\u5199\u80FD\u529B\u4E0D\u80DC, \u5E94\u8BE5\u5199\u653F\u6CBB\u4E0A\u7684\u7406\u7531; \u4ED6\u5C31\u53E6\u5199\u4E86\u4E00\u4FE1\u8BF4 &quot;\u53C2\u52A0\u8FC7\u56FD\u6C11\u515A, \u884C\u52A8\u8A00\u8BBA\u9519\u8BEF\u5B9E\u591A, \u8BF7\u8F9E\u53BB\u4E00\u5207\u517C\u804C.&quot; \u5F97\u5230\u6279\u51C6, &quot;\u771F\u662F &#39;\u65E0\u5B98\u4E00\u8EAB\u8F7B&#39;&quot;. \u51AF\u5148\u751F\u53C8\u9762\u4E34\u8981\u4F5C\u7B2C\u4E09\u6B21\u9009\u62E9\u4E86. \u5F53\u5E74\u5341\u6708\u5411\u6BDB\u6CFD\u4E1C\u5199\u4FE1\u8868\u6001, \u5927\u610F\u662F &quot;\u6211\u5728\u8FC7\u53BB\u8BB2\u5C01\u5EFA\u54F2\u5B66, \u5E2E\u4E86\u56FD\u6C11\u515A\u7684\u5FD9, \u73B0\u5728\u6211\u51B3\u5FC3\u6539\u9020\u601D\u60F3, \u5B66\u4E60\u9A6C\u514B\u601D\u4E3B\u4E49, \u51C6\u5907\u4E8E\u4E94\u5E74\u4E4B\u5185\u7528\u9A6C\u514B\u601D\u4E3B\u4E49\u7684\u7ACB\u573A\u89C2\u70B9\u65B9\u6CD5, \u91CD\u65B0\u5199\u4E00\u90E8\u4E2D\u56FD\u54F2\u5B66\u53F2.&quot; \u5F88\u5FEB\u5F97\u5230\u6BDB\u6CFD\u4E1C\u4EB2\u7B14\u56DE\u4FE1: &quot;\u50CF\u4F60\u8FD9\u6837\u7684\u4EBA, \u8FC7\u53BB\u72AF\u8FC7\u9519\u8BEF, \u73B0\u5728\u51C6\u5907\u6539\u6B63\u9519\u8BEF, \u5982\u679C\u80FD\u5B9E\u8DF5, \u90A3\u662F\u597D\u7684. \u4E5F\u4E0D\u8981\u6025\u4E8E\u6C42\u6548, \u53EF\u4EE5\u6162\u6162\u5730\u6539, \u603B\u4EE5\u91C7\u53D6\u8001\u5B9E\u6001\u5EA6\u4E3A\u5B9C.&quot;</p><p>\u51AF\u5148\u751F\u6240\u4F5C\u7684\u8FD9\u6B21\u9009\u62E9, \u4E3A\u4ED6\u7740\u60F3, \u5927\u6982\u662F\u4ED6\u5F53\u65F6\u80FD\u4F5C\u7684\u552F\u4E00\u9009\u62E9, \u540E\u6765\u8BC1\u660E\u4E5F\u662F\u5BF9\u4EBA\u6700\u6709\u5229\u7684\u9009\u62E9. \u81F3\u5C11\u5728\u4EE5\u540E\u51E0\u6B21\u91CD\u5927\u7684\u653F\u6CBB\u8FD0\u52A8\u548C\u601D\u60F3\u6279\u5224\u4E2D, \u51AF\u5148\u751F\u90FD\u51FA\u4EBA\u610F\u6599\u5730\u5E78\u514D\u4E8E\u96BE. \u4ED6\u4EE5\u4E3B\u52A8\u63A5\u53D7\u6539\u9020\u7684\u65B9\u6CD5\u907F\u514D\u4E86\u88AB\u5F3A\u8FEB\u6539\u9020. 1964 \u5E74\u300A\u4E2D\u56FD\u54F2\u5B66\u53F2\u65B0\u7F16\u300B\u7B2C\u4E00\u518C ( \u5373\u51AF\u5148\u751F\u5728\u81EA\u5E8F\u4E2D\u8BF4\u4ED6 &quot;\u5F88\u5FEB\u5C31\u611F\u5230\u4E0D\u6EE1\u610F&quot;, \u53EA\u80FD\u4F5C\u4E3A &quot;\u8BD5\u7A3F&quot; \u7684 ) \u51FA\u7248\u65F6, \u51AF\u5148\u751F\u4EB2\u7B14\u5728\u5377\u9996\u5199\u4E86\u4E00\u9996 &quot;\u9898\u8BCD&quot;: &quot;\u671B\u9053\u4FBF\u60CA\u5929\u5730\u5BBD, \u5357\u9488\u5EFF\u8F7D\u6EAF\u5EF6\u5B89; \u5C0F\u8A00\u4EA6\u53EF\u6DA6\u6D2A\u4E1A, \u65B0\u4F5C\u5E94\u9700\u4EE3\u65E7\u520A. \u59CB\u609F\u9854\u56DE\u53F9\u5B54\u6C0F, \u4E0D\u4E3A\u4F59\u5B50\u5B66\u90AF\u90F8. \u6B64\u5173\u6362\u9AA8\u8131\u80CE\u4E8B, \u83AB\u5F53\u5BFB\u5E38\u8457\u8FF0\u770B.&quot; \u8FD9\u9996\u8BD7\u5F88\u53D7\u6B23\u8D4F, \u636E\u8BF4\u5EB7\u751F\u89C1\u5230\u51AF\u5148\u751F\u65F6\u7ACB\u5373\u5C06\u5B83\u80CC\u8BF5\u51FA\u6765. \u5927\u7EA6\u6B63\u662F\u56E0\u6B64, &quot;\u6587\u9769&quot; \u4E2D\u51AF\u5148\u751F\u867D\u7136\u4E5F\u548C\u522B\u4EBA\u4E00\u6837\u906D\u6279\u6597, \u8FDB\u725B\u68DA, \u4F46\u4ED6\u6240\u53D7\u7684\u5F85\u9047, \u4EE5\u4ED6\u7684\u540D\u58F0\u548C\u5730\u4F4D\u8BF4, \u5B9E\u5728\u7B97\u662F\u5F88\u5BA2\u6C14\u7684. \u4EE5\u81F4\u5230 &quot;\u6587\u9769&quot; \u540E\u671F\u7ADF\u6F14\u51FA\u4E86\u90A3\u4E00\u5E55, \u4E3A\u51AF\u5148\u751F\u4E00\u751F\u7559\u4E0B\u6C61\u70B9, \u4E00\u65F6\u7684\u865A\u8363\u6210\u4E3A\u7EC8\u8EAB\u7684\u803B\u8FB1.</p><p>\u8BB8\u591A\u6587\u7AE0\u90FD\u5C06\u51AF\u53CB\u5170\u548C\u6881\u6F31\u6E9F\u4F5C\u5BF9\u6BD4, \u4E00\u8D2C\u4E00\u8912\u662F\u5F88\u6E05\u695A\u7684. \u6211\u60F3, \u4ED6\u4EEC\u7684\u4F5C\u4E3A\u4E0D\u540C\u6709\u4E2A\u4EBA\u6027\u683C\u65B9\u9762\u7684\u539F\u56E0: \u6881\u5148\u751F\u8BA4\u4E3A\u53EA\u6709\u4ED6\u624D\u638C\u63E1\u4E86\u771F\u7406, \u6240\u4EE5\u4ED6 &quot;\u72C2&quot;, \u53EF\u4EE5\u4E0D\u5411\u4EFB\u4F55\u538B\u529B\u4F4E\u5934. \u51AF\u5148\u751F\u81EA\u79F0\u8C28\u5C0F\u614E\u5FAE, \u5728\u653F\u6CBB\u98CE\u6D6A\u4E2D\u53EA\u80FD\u968F\u6CE2\u9010\u6D41; \u7279\u522B\u662F\u540E\u6765, \u4ED6\u8BF4\u51FA\u7684\u8BDD\u53EA\u8981\u6709\u4EBA\u8868\u793A\u4E0D\u540C\u610F, \u4ED6\u4FBF\u8D76\u5FEB\u4FEE\u6B63\u68C0\u8BA8, \u7ADF\u6709\u4EBA\u8BA4\u4E3A\u4ED6\u662F &quot;\u4ECE\u5584\u5982\u6D41&quot;. \u4F46\u8FD8\u6709\u66F4\u91CD\u8981\u7684\u539F\u56E0: \u6881\u5148\u751F\u5728\u6297\u6218\u521D\u671F\u5C31\u8BBF\u95EE\u8FC7\u5EF6\u5B89, \u4E00\u76F4\u4F5C\u4E3A\u6C11\u4E3B\u4EBA\u58EB\u8C03\u89E3\u56FD\u5171\u548C\u8C08, \u540E\u6765\u53D1\u8D77\u7EC4\u7EC7\u6C11\u4E3B\u540C\u76DF\u53CD\u5BF9\u56FD\u6C11\u515A\u7684\u72EC\u88C1\u4E13\u5236. \u6881\u5148\u751F\u6709\u7684\u8FD9\u4E9B &quot;\u653F\u6CBB\u8D44\u672C&quot;, \u51AF\u5148\u751F\u4E0D\u4F46\u6CA1\u6709, \u800C\u4E14\u6B63\u597D\u76F8\u53CD. \u6211\u770B\u5230\u5343\u5BB6\u9A79\u5148\u751F\u5199\u7684\u300A\u4ECE\u6881\u6F31\u6E9F, \u51AF\u53CB\u5170\u8C08\u5230\u4E2D\u56FD\u77E5\u8BC6\u5206\u5B50\u7684\u5384\u8FD0\u300B, \u6587\u7AE0\u4E3B\u8981\u662F\u8BB0\u8FF0\u6881\u5148\u751F\u7684\u906D\u9047\u7684, \u5343\u5148\u751F\u5BF9\u51AF\u5148\u751F\u4E0D\u5927\u4E86\u89E3, \u4F46\u662F\u4ED6\u5728\u6700\u540E\u6284\u5F15 1988 \u5E74\u6881\u5148\u751F\u53BB\u4E16\u65F6\u51AF\u5148\u751F\u9001\u7684\u633D\u8054: &quot;\u94A9\u7384\u51B3\u7591, \u767E\u5E74\u5C3D\u7601, \u4EE5\u53D1\u626C\u5112\u5B66\u4E3A\u5DF1\u4EFB; \u5EF7\u4E89\u9762\u6298, \u4E00\u4EE3\u76F4\u58F0, \u4E3A\u540C\u60C5\u519C\u592B\u800C\u6267\u8A00.&quot; \u51AF\u5148\u751F\u7684\u60BC\u6587\u5199\u5F97\u66F4\u660E\u767D: &quot;\u5728\u4E2D\u56FD\u5C01\u5EFA\u793E\u4F1A\u4E2D, \u77E5\u8BC6\u5206\u5B50\u5BF9\u4E8E\u7687\u5E1D\u6562\u4E8E\u72AF\u9854\u76F4\u8C0F, \u4E0D\u6015\u8D2C\u5B98, \u4E0D\u6015\u5145\u519B, \u4E5F\u4E0D\u6015\u5EF7\u6756, \u8BA4\u4E3A\u8BE5\u600E\u4E48\u8BF4\u5C31\u600E\u4E48\u8BF4, \u8FD9\u662F\u5C01\u5EFA\u793E\u4F1A\u4E2D\u77E5\u8BC6\u5206\u5B50\u7684\u7F8E\u5FB7, \u6881\u5148\u751F\u7EE7\u627F\u4E86\u8FD9\u79CD\u7F8E\u5FB7.&quot; \u5343\u5148\u751F\u6587\u7AE0\u8FD8\u63D0\u5230: \u51AF\u5148\u751F\u5728 &quot;\u6587\u9769&quot; \u540E\u867D\u7136\u8FD8\u4FDD\u7559\u7740\u653F\u534F\u59D4\u5458\u548C\u5E38\u59D4\u7684\u8EAB\u4EFD, \u4F46\u4ED6\u4E00\u6B21\u4E5F\u6CA1\u6709\u51FA\u5E2D\u4F1A\u8BAE &quot;\u53EF\u4EE5\u51AF\u8001\u7684\u60C5\u7EEA\u4E4B\u6076\u52A3\u548C\u5185\u759A\u4E4B\u6DF1\u4E86.&quot;</p><p>\u6211\u60F3, \u51AF\u5148\u751F\u665A\u5E74\u786E\u5B9E\u5BF9\u81EA\u5DF1\u7684\u4E00\u751F\u4F5C\u4E86\u53CD\u601D. \u5728\u300A\u4E09\u677E\u5802\u81EA\u5E8F\u300B\u4E2D\u5C06\u81EA\u5DF1\u7684\u4E00\u751F\u7ECF\u5386\u90FD\u5199\u4E86\u51FA\u6765, \u77E5\u6211\u7F6A\u6211, \u4EFB\u4EBA\u8BC4\u8BF4, \u5C06 &quot;\u5B89\u5371&quot;, &quot;\u8363\u8FB1&quot; \u7F6E\u4E8E\u5EA6\u5916. \u6240\u4EE5\u4ED6\u8BF4\u7684 &quot;\u65A9\u540D\u5173, \u7834\u5229\u7D22&quot;, \u5E76\u4E0D\u662F\u4E00\u822C\u4EBA\u8BF4\u7684\u540D\u7F30\u5229\u7D22, \u800C\u662F\u5C06\u4ED6\u539F\u6765\u7684\u79CD\u79CD\u987E\u8651\u62C5\u5FE7\u7EDF\u7EDF\u629B\u5230\u4E00\u8FB9, \u6211\u5C31\u662F\u6211, \u6211\u8981\u8BF4\u4EC0\u4E48\u5C31\u8BF4\u4EC0\u4E48, \u6062\u590D\u4E86\u81EA\u6211, \u4E0D\u5FC5\u518D\u8BF4\u90A3\u4E9B\u8FDD\u5FC3\u7684\u8BDD, \u53EF\u4EE5\u5802\u5802\u6B63\u6B63\u7684\u505A\u4E00\u4E2A\u4EBA. &quot;\u4FEF\u4EF0\u65E0\u6127\u4F5C&quot;, \u53C8\u4F55\u5FC5\u518D\u53D7\u90A3\u6837\u7684\u901F\u7F1A\u548C\u9650\u5236\u5462? \u8FD9\u65F6\u5019, \u51AF\u5148\u751F\u624D\u771F\u6B63\u8BA4\u8BC6\u5230 &quot;&#39;\u6211\u81EA\u98DE&#39; \u4E4B\u6240\u4EE5\u81EA\u7531&quot;.</p><p>\u5728\u4ED6\u8BA4\u4E3A &quot;\u6211\u771F\u611F\u89C9\u5230 &#39;\u6D77\u9614\u5929\u7A7A\u6211\u81EA\u98DE&#39; \u7684\u81EA\u7531&quot; \u7684\u90A3\u6700\u540E\u4E00\u7AE0\u603B\u7ED3\u4E2D, \u9664\u4E86\u9648\u8FF0\u4E0A\u9762\u8BF4\u7684\u4ED6\u5BF9\u5730\u54F2\u5B66\u7684\u4F5C\u7528\u548C\u770B\u6CD5\u5916, \u5728\u54F2\u5B66\u7406\u8BBA\u4E0A\u4ED6\u53EA\u7B80\u5355\u8BF4\u4E86\u4E24\u70B9: \u7B2C\u4E00, \u4ED6\u5BF9 &quot;\u5185\u5723\u5916\u738B&quot; \u4F5C\u4E86\u89E3\u91CA, \u5F15\u7528\u5B5F\u5B50\u8BF4\u7684\u738B\u8005 &quot;\u4EE5\u5FB7\u670D\u4EBA&quot;, \u9738\u8005 &quot;\u4EE5\u529B\u670D\u4EBA&quot;\uFF0C \u8BF4 &quot;\u4E2D\u56FD\u5386\u4EE3\u738B\u671D\u90FD\u662F\u7528\u6B66\u529B\u5F81\u670D\u6765\u5EFA\u7ACB\u548C\u7EF4\u6301\u5176\u7EDF\u6CBB, \u8FD9\u90FD\u662F\u9738. \u81F3\u4E8E\u4EE5\u5FB7\u670D\u4EBA\u7684, \u5219\u8FD8\u6CA1\u6709&quot;. \u7B2C\u4E8C, \u5728\u8FA9\u8BC1\u6CD5\u7684\u7EDF\u4E00\u548C\u6597\u4E89\u4E24\u4E2A\u65B9\u9762, \u4ED6\u8BF4\u9A6C\u514B\u601D\u4E3B\u4E49\u5C06\u77DB\u76FE\u6597\u4E89\u653E\u5728\u7B2C\u4E00\u4F4D, \u800C\u4E2D\u56FD\u53E4\u5178\u54F2\u5B66\u662F\u5C06\u7EDF\u4E00\u653E\u5728\u7B2C\u4E00\u4F4D\u7684. \u51AF\u5148\u751F\u4E3B\u5F20\u7528\u5F20\u8F7D\u6240\u8BF4\u7684 &quot;\u4EC7\u5FC5\u548C\u800C\u89E3&quot;, \u53CD\u5BF9 &quot;\u4EC7\u5FC5\u4EC7\u5230\u5E95&quot;. \u4E5F\u6709\u670B\u53CB\u4EBA\u8BA4\u4E3A\u51AF\u5148\u751F\u8BF4\u7684\u8FD9\u4E24\u70B9\u90FD\u662F\u8001\u751F\u5E38\u8C08. \u4F46\u6211\u4EE5\u4E3A, \u5982\u679C\u5C06\u8FD9\u4E24\u70B9\u610F\u601D\u548C\u51AF\u5148\u751F\u4E00\u751F\u906D\u9047, \u8FD8\u6709\u4E2D\u56FD\u77E5\u8BC6\u5206\u5B50\u7684\u547D\u8FD0, \u4EE5\u53CA\u51E0\u5341\u5E74\u6765\u6211\u4EEC\u56FD\u5BB6\u793E\u4F1A\u7684\u5B9E\u9645\u60C5\u51B5, \u52A0\u4E0A\u5F53\u4ECA\u4E16\u754C\u4E0A\u5145\u6EE1\u4E86\u5404\u5F0F\u5404\u6837\u7684\u6C11\u65CF\u77DB\u76FE, \u5B97\u6559\u77DB\u76FE, \u9886\u571F\u4E3B\u6743\u77DB\u76FE\u4EE5\u53CA\u610F\u8BC6\u5F62\u6001\u77DB\u76FE\u7B49\u7B49\u60C5\u51B5\u8054\u7CFB\u8D77\u6765, \u5C31\u53EF\u4EE5\u770B\u5230: \u8FD9\u70B9\u7B80\u70B9\u7684\u9053\u7406\u5374\u662F\u8FD9\u4F4D\u54F2\u5B66\u5BB6\u4E3A\u4E86\u5B9E\u8DF5\u4ED6\u7684\u5185\u5723\u5916\u738B\u4E4B\u9053, \u6700\u540E\u7559\u7ED9\u540E\u4EBA\u7C7B\u7684\u667A\u6167 -- \u54F2\u5B66. \u5BF9\u4E8E\u8FD9\u6837\u4E00\u4F4D\u9971\u7ECF\u4EBA\u95F4\u75BE\u82E6, \u5DF2\u903E\u4E5D\u5341\u9AD8\u9F84\u7684\u54F2\u5B66\u5BB6, \u6211\u4EEC\u8FD8\u5962\u6C42\u4EC0\u4E48\u5462?</p><p>\u4E0D\u8FC7, \u51AF\u5148\u751F\u867D\u7136\u53D6\u5F97\u4E86\u4E3B\u89C2\u4E0A\u7684\u81EA\u7531, \u5374\u8FD8\u662F\u4E0D\u80FD\u6446\u8131\u5BA2\u89C2\u4E0A\u7684\u4E0D\u81EA\u7531. \u4ED6\u5728\u8FD9\u7BC7 &quot;\u81EA\u5E8F&quot; \u4E2D\u50CF\u662F\u5DF2\u7ECF\u6709\u4E86\u9884\u611F, \u6240\u4EE5\u5199\u9053: &quot;\u5728\u5199\u7B2C\u516B\u5341\u4E00\u7AE0\u7684\u65F6\u5019, \u6211\u786E\u662F\u7167\u6211\u6240\u89C1\u7684\u5199\u7684. \u5E76\u4E14\u5BF9\u670B\u53CB\u8BF4\uFF0C &#39;\u5982\u679C\u6709\u4EBA\u4E0D\u4EE5\u4E3A\u7136, \u56E0\u4E4B\u4E0D\u80FD\u51FA\u7248, \u543E\u5176\u4E3A\u738B\u8239\u5C71\u77E3&#39;. \u8239\u5C71\u5728\u6DF1\u5C71\u8457\u4E66\u8FBE\u6570\u767E\u5377, \u6CA1\u6709\u4EBA\u4E3A\u4ED6\u51FA\u7248; \u51E0\u767E\u5E74\u4EE5\u540E, \u7EC8\u4E8E\u51FA\u7248\u4E86, \u5F15\u6240\u8C13 &#39;\u6587\u7AE0\u81EA\u6709\u547D, \u4E0D\u4ED7\u53F2\u7B14\u5782&#39;.&quot; \u4E0D\u5E78, \u51AF\u5148\u751F\u9884\u6599\u7684\u4E8B\u60C5\u8FD8\u662F\u51FA\u73B0\u4E86, \u8FD9\u672C\u300A\u4E2D\u56FD\u54F2\u5B66\u53F2\u65B0\u7F16\u300B\u7B2C\u4E03\u518C, \u4E0D\u80FD\u548C\u4ED6\u524D\u9762\u516D\u518C\u4E00\u6837\u5728\u540C\u4E00\u4E2A\u51FA\u7248\u793E\u51FA\u7248. \u53EF\u662F\u73B0\u5728\u7EC8\u7A76\u4E0D\u662F\u738B\u8239\u5C71\u90A3\u4E2A\u65F6\u4EE3, \u6240\u4EE5\u4E0D\u4E45\u4EE5\u540E\u5B83\u5728\u9999\u6E2F\u51FA\u7248\u4E86, \u53EA\u662F\u5C06\u4E66\u540D\u6539\u4E3A\u300A\u4E2D\u56FD\u73B0\u4EE3\u54F2\u5B66\u53F2\u300B.</p><p>\u8FD9\u6837\u5C31\u6709\u4E00\u4E9B\u670B\u53CB -- \u8BB8\u591A\u662F\u51AF\u5148\u751F\u7684\u5B66\u751F, \u8BF4\u4ED6\u4EEC\u81F3\u4ECA\u8FD8\u6CA1\u6709\u770B\u5230\u8FD9\u672C\u4E66, \u56E0\u6B64\u6211\u4F5C\u8FD9\u70B9\u4ECB\u7ECD, \u540C\u65F6\u4E5F\u8C08\u6211\u73B0\u5728\u5BF9\u51AF\u5148\u7684\u8FD9\u70B9\u770B\u6CD5. \u56E0\u4E3A\u5728\u548C\u51AF\u5148\u751F\u63A5\u89E6\u7684\u90A3\u51E0\u5E74\u91CC, \u7531\u4E8E\u6211\u7684 &quot;\u5DE6&quot;, \u5BF9\u4ED6\u6709\u8BB8\u591A\u4E0D\u656C\u4E4B\u5904, \u8C08\u6211\u73B0\u5728\u7684\u8BA4\u8BC6, \u4EE5\u8868\u793A\u6211\u5BF9\u8001\u5E08\u7684\u8D1F\u759A\u4E4B\u60C5, \u4E5F\u4EE5\u6B64\u7EAA\u5FF5\u51AF\u5148\u751F\u767E\u5C81\u5BFF\u8FB0.</p>',32),a=[e];function c(r,_){return o(),u("div",null,a)}var d=t(p,[["render",c],["__file","\u6D77\u9614\u5929\u7A7A\u6211\u81EA\u98DE.html.vue"]]);export{d as default};
