import{_ as u,o as a,c as t,e}from"./app.6bf529a6.js";const o={},h=e('<h1 id="\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E00\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E00\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E00\u7AE0</h1><h2 id="\u9ED8\u897F\u4E9A\u8363\u8FDB\u8036\u8DEF\u6492\u51B7" tabindex="-1"><a class="header-anchor" href="#\u9ED8\u897F\u4E9A\u8363\u8FDB\u8036\u8DEF\u6492\u51B7" aria-hidden="true">#</a> \u9ED8\u897F\u4E9A\u8363\u8FDB\u8036\u8DEF\u6492\u51B7</h2><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>1</sup> \u5F53\u4ED6\u4EEC\u4E34\u8FD1\u8036\u8DEF\u6492\u51B7, \u6765\u5230\u9760\u8FD1\u6A44\u6984\u5C71\u7684\u8D1D\u7279\u6CD5\u9769\u65F6, \u8036\u7A23\u5C31\u6253\u53D1\u4E24\u4E2A\u95E8\u5F92, <sup>2</sup> \u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u4F60\u4EEC\u5F80\u5BF9\u9762\u7684\u6751\u5E84\u91CC\u53BB, \u7ACB\u65F6\u4F1A\u770B\u89C1\u4E00\u5339\u62F4\u7740\u7684\u6BCD\u9A74, \u548C\u8DDF\u5B83\u5728\u4E00\u8D77\u7684\u9A74\u9A79. \u89E3\u5F00, \u7ED9\u6211\u7275\u6765! <sup>3</sup> \u5982\u679C\u6709\u4EBA\u5BF9\u4F60\u4EEC\u8BF4\u4EC0\u4E48, \u4F60\u4EEC\u5C31\u8BF4: \u4E3B\u8981\u7528\u5B83\u4EEC. \u4ED6\u5C31\u4F1A\u7ACB\u523B\u653E\u5B83\u4EEC\u6765.&quot; <sup>4</sup> \u8FD9\u4E8B\u53D1\u751F, \u662F\u4E3A\u5E94\u9A8C\u5148\u77E5\u6240\u8BF4\u7684: <sup>5</sup>&quot;\u4F60\u4EEC\u5E94\u5411\u7199\u96CD\u5973\u5B50\u8BF4: \u770B, \u4F60\u7684\u541B\u738B\u6765\u5230\u4F60\u8FD9\u91CC, \u6E29\u548C\u7684\u9A91\u5728\u4E00\u5339\u9A74\u4E0A, \u4E00\u5339\u6BCD\u9A74\u7684\u5C0F\u9A74\u9A79\u4E0A.&quot; <sup>6</sup> \u95E8\u5F92\u5C31\u53BB, \u7167\u8036\u7A23\u5429\u5490\u4ED6\u4EEC\u7684\u4F5C\u4E86. \u4ED6\u4EEC\u7275\u4E86\u6BCD\u9A74\u548C\u9A74\u9A79\u6765, \u628A\u5916\u8863\u642D\u5728\u5B83\u4EEC\u7684\u8EAB\u4E0A, \u6276\u8036\u7A23\u5750\u5728\u4E0A\u9762. <sup>8</sup> \u5F88\u591A\u7FA4\u4F17, \u628A\u81EA\u5DF1\u7684\u5916\u8863\u94FA\u5728\u8DEF\u4E0A, \u8FD8\u6709\u4E9B\u4EBA\u4ECE\u6811\u4E0A\u780D\u4E0B\u6811\u679D\u6765, \u6492\u5728\u8DEF\u4E0A. <sup>9</sup> \u524D\u884C\u540E\u968F\u7684\u7FA4\u4F17\u558A\u8BF4: &quot;\u8D3A\u4E09\u7EB3\u4E8E\u8FBE\u5473\u4E4B\u5B50! \u56E0\u4E0A\u4E3B\u8FD9\u540D\u800C\u6765\u7684, \u5F53\u7136\u8D5E\u9882! \u8D3A\u4E09\u7EB3\u4E8E\u81F3\u9AD8\u4E4B\u5929!&quot; <sup>10</sup> \u5F53\u8036\u7A23\u8FDB\u5165\u8036\u8DEF\u6492\u51B7\u7684\u65F6\u5019, \u5168\u57CE\u54C4\u52A8, \u8BF4: &quot;\u8FD9\u4EBA\u662F\u8C01?&quot; \u7FA4\u4F17\u8BF4: &quot;\u8FD9\u662F\u52A0\u91CC\u808B\u4E9A\u7EB3\u531D\u808B\u7684\u5148\u77E5\u8036\u7A23.&quot;</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u4E00\u53CD\u5E73\u65E5\u884C\u4E3A\u7684\u60EF\u4F8B, \u4ED6\u73B0\u5728\u7EC8\u4E8E\u51B3\u5B9A\u628A\u4ED6\u7684\u6551\u4E16\u4E3B\u4E4B\u5C0A\u4E25\u663E\u9732\u7ED9\u5723\u57CE. \u8FD9\u4E2A\u50CF\u5F81\u6027\u7684\u4E3E\u52A8, \u9884\u73B0\u4E86\u7942\u8981\u8363\u8000\u7684\u8FDB\u5165\u7942\u7684\u738B\u56FD. \u7ED9\u5E0C\u4F2F\u6765\u4E66\u7684\u4F5C\u8005\u7F6E\u5907\u4E86\u4E2D\u5FC3\u4E3B\u65E8. \u6211\u4EEC\u4ED4\u7EC6\u8003\u67E5\u4E86\u56DB\u90E8\u798F\u97F3\u4E66\u91CC\u5BF9\u8FD9\u4E00\u4E8B\u4EF6\u7684\u63CF\u8FF0. \u53EF\u4EE5\u770B\u51FA\u6BCF\u4F4D\u4F5C\u8005\u90FD\u7528\u4E0D\u540C\u7684\u624B\u6CD5, \u4F7F\u5B83\u7684\u91CD\u8981\u6027\u8FBE\u4E8E\u9AD8\u6F6E. \u739B\u7AA6\u628A\u52A8\u6570\u7684\u6570\u76EE\u53D8\u6210\u4E24\u500D. \u56E0\u4E3A\u4ED6\u60F3\u8981\u5F3A\u8C03\u8036\u7A23\u5B9E\u73B0\u4E86\u65E7\u7EA6\u7684\u9884\u8A00. ( \u531D\u4E5D 9 \u7684\u5E0C\u814A\u6587\u662F\u7248\u662F\u8BF4\u4E24\u5339, \u800C\u5E0C\u4F2F\u6765\u6587\u7248\u662F\u8BF4, \u4E00\u5339\u6BCD\u9A74, \u5373\u662F\u9A74\u9A79. ), \u8BF4 &quot;\u4E00\u5339\u6BCD\u9A74\u548C\u4E00\u5339\u9A74\u9A79\u51FA\u73B0&quot;. \u7FA4\u4F17\u7684\u6B22\u547C\u4EE4\u4EBA\u8054\u60F3\u8D77\u5E10\u7BF7\u8282\u7684\u548F\u5531\u7684\u5723\u548F. \u8036\u8DEF\u6492\u51B7\u7684\u53CD\u5E94\u4E0E\u5F53\u521D\u4E09\u8D24\u58EB\u7684\u5BA3\u544A\u6240\u5F15\u8D77\u7684\u53CD\u5E94\u76F8\u4F3C.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u524D\u5F80\u8036\u8DEF\u6492\u51B7\u7684\u65C5\u7A0B, \u4ECE\u739B\u5341\u516D 21 \u5F00\u59CB. \u5728\u6B64, \u56E0\u6709\u5927\u6279\u7FA4\u4F17\u70ED\u70C8\u6B22\u8FCE\u8036\u7A23\u8FDB\u57CE ( 1 ~ 11 \u8282 ), \u968F\u540E\u8036\u7A23\u53C8\u5728\u5723\u6BBF\u505A\u51FA\u4E86\u4E86\u60CA\u4EBA\u7684\u4E3E\u52A8 ( 12 ~ 17 \u8282 ), \u56E0\u800C\u4F7F\u6B64\u65C5\u7A0B\u8FBE\u5230\u4E86\u9AD8\u5CF0. \u8FD9\u4E24\u4E2A\u573A\u9762\u7684\u63CF\u5199, \u90FD\u542B\u6709\u672B\u4E16\u6027\u7684\u8BED\u8C03. \u739B\u7AA6\u5723\u53F2\u5927\u91CF\u5F15\u7528\u65E7\u7EA6\u7ECF\u4E66\u5148\u77E5\u7684\u9884\u8A00, \u5E76\u6307\u51FA\u5B83\u5982\u4F55\u5F97\u4EE5\u6EE1\u5168, \u5176\u610F\u4E49\u662F\u76F8\u5F53\u660E\u663E\u7684.</p><p>\u8036\u7A23\u662F\u4ECE\u8036\u8DEF\u6492\u51B7\u4E1C\u9762\u8FDB\u57CE\u7684. \u6839\u636E\u300A\u531D\u52A0\u5229\u4E9A\u5148\u77E5\u4E66\u300B, \u6A44\u6984\u5C71\u662F\u672B\u4E16\u7684\u6700\u540E\u4EA4\u6218\u53D1\u751F\u4E4B\u5730: &quot;\u5728\u90A3\u4E00\u5929, \u4ED6\u7684\u811A\u8981\u7AD9\u5728\u8036\u8DEF\u6492\u51B7\u5BF9\u9762, \u5373\u4E1C\u8FB9\u7684\u6A44\u6984\u5C71\u4E0A; \u6A44\u6984\u5C71\u5FC5\u7531\u4E2D\u95F4\u88C2\u5F00, \u7531\u4E1C\u81F3\u897F\u5F62\u6210\u4E00\u6761\u5F88\u5927\u7684\u5C71\u8C37, \u5176\u4E2D\u4E00\u534A\u5C71\u5411\u5317\u632A\u79FB, \u53E6\u4E00\u534A\u5373\u5411\u5357\u79FB\u52A8&quot; ( \u531D\u5341\u56DB 4 ). \u739B\u7AA6\u5723\u53F2\u5728\u63CF\u8FF0\u9ED8\u897F\u4E9A\u8363\u8FDB\u8036\u8DEF\u6492\u51B7\u5723\u4EAC\u8FD9\u4E00\u573A\u666F ( 5 \u8282\u4E2D\u6240\u5F15\u7528\u7684 ) \u65F6, \u5F15\u7528 \u300A\u531D\u52A0\u5229\u4E9A\u5148\u77E5\u4E66\u300B\u4E00\u6BB5\u5E0C\u4F2F\u6765\u5BF9\u4ED7\u8BD7\u53E5 ( \u531D\u4E5D 9 ). \u739B\u7AA6\u8BB2\u8FF0\u7684, \u4F3C\u4E4E\u6709\u4E24\u5339\u9A74\u5B50 ( \u6BCD\u9A74\u548C\u9A74\u9A79 ); \u4F46\u5B9E\u9645\u4E0A, \u300A\u531D\u52A0\u5229\u4E9A\u5148\u77E5\u4E66\u300B\u6240\u63CF\u8FF0\u7684\u53EA\u662F\u4E00\u5339\u9A74\u9A79, \u9ED8\u897F\u4E9A\u541B\u738B\u662F &quot;\u9A91\u5728\u9A74\u4E0A, \u9A91\u5728\u9A74\u9A79\u4E0A&quot; ( \u531D\u4E5D 9 ). \u8036\u7A23\u7684\u884C\u52A8\u662F\u4EFF\u6548\u5F81\u670D\u8005\u8FDB\u5165\u51FB\u8D25\u7684\u57CE\u5E02.</p><p>&quot;\u8D3A\u4E09\u7EB3&quot; ( hosianna ) \u662F\u4E00\u53E5\u5E0C\u4F2F\u6765\u7684\u547C\u5524\u53E3\u53F7, \u539F\u610F\u662F &quot;\u6C42\u4F60\u6551\u6211\u554A!&quot; \u4F46\u5728\u8FD9\u91CC, &quot;\u8D3A\u4E09\u7EB3&quot; \u547C\u58F0\u5374\u4E0D\u5B8C\u5168\u662F &quot;\u6C42\u6551&quot; \u7684\u54C0\u6073, \u800C\u66F4\u591A\u662F\u4E00\u79CD\u8D5E\u7F8E\u548C\u79F0\u9882\u7684\u6B22\u547C. \u7FA4\u4F17\u7684\u6B22\u547C\u58F0 ( 9 \u8282\u56DE\u5E94\u7740: \u548F\u4E00\u4E00\u516B 26 ), \u4E0E\u72B9\u592A\u9886\u8896\u5BF9\u8036\u7A23\u65E5\u76CA\u9AD8\u6DA8\u7684\u654C\u610F\u5F62\u6210\u5BF9\u6BD4. \u739B\u7AA6\u5723\u53F2\u4EE5 &quot;\u5168\u57CE\u5F00\u52A8&quot; ( 10 \u8282 ) \u6765\u63CF\u8FF0\u7FA4\u4F17\u7684\u53CD\u5E94, \u4E5F\u4E3A\u8036\u7A23\u53D7\u96BE\u7684\u7ED3\u5C40 &quot;\u5927\u5730\u9707\u52A8&quot; ( \u739B\u4E8C\u5341\u4E03 51 ) \u57CB\u4E0B\u4E86\u4F0F\u7B14.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u5728\u9884\u544A\u4E86\u95E8\u5F92\u4EEC\u7684\u547D\u8FD0, \u5E76\u5631\u5490\u4ED6\u4EEC\u8DDF\u968F\u7684\u6761\u4EF6\u4E4B\u540E ( 1 6 :21 - 20:34) , \u8D70\u8FDB\u4E86\u8036\u8DEF\u6492\u51B7. \u7942\u548C\u72B9\u592A\u5F53\u5C40\u7684\u51B2\u7A81\u4E5F\u65E5\u76CA\u6FC0\u5316. \u5982\u6B64, \u8036\u7A23\u5728\u4E16 \u4E0A \u7684\u6700\u540E\u4E00\u7A0B\u4FBF\u5F00\u59CB\u4E86.</p><p>\u5728\u5E86\u795D\u903E\u8D8A\u8282 ( \u7EAA\u5FF5\u51FA\u79BB\u57C3\u53CA\u5E76\u83B7\u5F97\u81EA\u7531 ) \u7684\u65F6\u5019, \u5927\u6279\u72B9\u592A\u4EBA\u8D76\u5F80\u8036\u8DEF\u6492\u51B7. \u5728\u8FD9\u79CD\u573A\u5408, \u4EBA\u4EEC\u5BF9\u4E8E\u9ED8\u897F\u4E9A\u7684\u671F\u5F85\u4E5F\u53D8\u5F97\u66F4\u52A0\u5F3A\u70C8. \u5BF9\u4E8E\u5929\u56FD\u5373\u5C06\u6765\u4E34\u7684\u671F\u5F85 , \u4F7F\u4F34\u968F\u8036\u7A23\u7684\u8FD9\u7FA4\u4EBA, \u5F00\u59CB\u5BA3\u626C\u8036\u7A23\u5C31\u662F\u90A3\u4F4D\u9ED8\u897F\u4E9A &quot;\u8FBE\u5473\u4E4B\u5B50&quot; ( 9 ) . \u8036\u7A23\u9A91\u7740\u9A74\u5B50\u8FDB\u4EAC, \u8FD9\u4E2A\u8C26\u865A\u7684\u884C\u4E3A\u8868\u8FBE\u4E86\u888D\u7684\u9ED8\u897F\u4E9A\u601D\u60F3\u6392\u9664\u4E00\u5207\u8C0B\u6C42\u6743\u529B\u548C\u5149\u8363\u7684\u8BA1\u5212\u3002</p><p>\u90A3\u65F6, \u72B9\u592A\u5F53\u5C40\u548C\u7EAF\u6734\u7684\u7FA4\u4F17\u90FD\u4E86\u89E3\u8036\u7A23\u8FD9\u4E00\u4E3E\u52A8\u7684\u610F\u4E49, \u4F46\u4ED6\u4EEC\u7684\u53CD\u5E94\u5404\u4E0D\u76F8\u540C: \u4EBA\u4EEC\u8BA4\u51FA\u7942\u662F\u4E00\u4F4D\u5148\u77E5, \u800C\u8036\u8DEF\u6492\u51B7\u5F53\u5C40\u5374\u5BF9\u6B64\u5145\u6EE1\u6000\u7591, \u5E76\u63D0\u9AD8\u8B66\u60D5, \u95EE &quot; \u8FD9\u4E2A\u4EBA\u662F\u8C01? &quot; \u5E76\u4E0D\u610F\u5473\u7740\u4ED6\u4EEC\u6E34\u671B\u4E86\u89E3, \u800C\u662F\u8868\u660E\u5BF9\u8036\u7A23\u7684\u76F4\u63A5\u6392\u65A5\u3002</p><h2 id="\u9A71\u9010\u6BBF\u5185\u5546\u4EBA" tabindex="-1"><a class="header-anchor" href="#\u9A71\u9010\u6BBF\u5185\u5546\u4EBA" aria-hidden="true">#</a> \u9A71\u9010\u6BBF\u5185\u5546\u4EBA</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>12</sup> \u8036\u7A23\u8FDB\u4E86\u5723\u6BBF, \u628A\u4E00\u5207\u5728\u5723\u6BBF\u5185\u7684\u5546\u4EBA\u987E\u5BA2\u8D76\u51FA\u53BB, \u628A\u94B1\u5E84\u7684\u684C\u5B50\u548C\u5356\u9E3D\u5B50\u7684\u51F3\u5B50\u63A8\u7FFB, <sup>13</sup> \u5411\u4ED6\u4EEC\u8BF4: &quot;\u7ECF\u4E0A\u8BB0\u8F7D: &#39;\u6211\u7684\u6BBF\u5B87, \u5E94\u79F0\u4E3A\u7948\u7977\u4E4B\u6240.&#39; \u4F60\u4EEC\u7ADF\u628A\u5B83\u505A\u6210\u4E86\u8D3C\u7A9D.&quot;</p><p><sup>14</sup> \u5728\u5723\u6BBF\u5185\u7684\u778E\u5B50\u548C\u7638\u5B50\u6765\u5230\u4ED6\u8DDF\u524D, \u4ED6\u90FD\u6CBB\u597D\u4E86\u4ED6\u4EEC. <sup>15</sup> \u53F8\u796D\u957F\u53CA\u7ECF\u5E08\u89C1\u4E86\u4ED6\u6240\u884C\u7684\u5947\u4E8B, \u53C8\u542C\u89C1\u4E86\u5B69\u5B50\u4EEC\u5728\u5723\u6BBF\u5185\u558A\u8BF4: &quot;\u8D3A\u4E09\u7EB3\u4E8E\u8FBE\u5473\u4E4B\u5B50!&quot; \u5C31\u5927\u53D1\u5FFF\u6012, <sup>16</sup> \u5BF9\u8036\u7A23\u8BF4: &quot;\u4F60\u4EEC\u542C\u89C1\u4ED6\u6240\u8BF4\u7684\u5417?&quot; \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u662F\u7684, \u4F60\u4EEC\u4ECE\u672A\u8BFB\u8FC7: &#39;\u4F60\u7531\u5A74\u513F\u548C\u5403\u5976\u8005\u7684\u53E3\u4E2D, \u5907\u53D7\u8D5E\u7F8E&#39; \u8FD9\u53E5\u8BDD\u5417?&quot; <sup>17</sup> \u4E8E\u662F\u4FBF\u79BB\u5F00\u4ED6\u4EEC\u8D70\u51FA\u57CE\u5916, \u5230\u4F2F\u8FBE\u5C3C\u53BB, \u5728\u90A3\u91CC\u8FC7\u591C.</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u628A\u8036\u7A23\u8FD9\u4E00\u9ED8\u897F\u4E9A\u7684\u884C\u5F84\u548C\u7942\u8363\u8FDB\u4EAC\u90FD\u8054\u7CFB\u5728\u4E00\u8D77. ( \u9A6C\u5C14\u8C37\u798F\u97F3\u91CC, \u5B83\u5728\u5F80\u540E\u4E00\u5929\u624D\u53D1\u751F. ) \u739B\u7AA6\u5F15\u7528\u65E7\u7EA6\u91CC\u7684\u4E24\u53E5\u8BDD\u6765\u8BC1\u5B9E\u8036\u7A23\u6240\u505A\u7684\u4E8B\u662F\u5408\u6CD5\u7684. \u81F3\u4E8E\u8036\u7A23\u7684\u6CBB\u75C5, \u53F8\u796D\u7684\u6307\u8D23, \u8036\u7A23\u5F15\u548F\u516B 3 \u56DE\u590D\u4ED6\u4EEC\u90FD\u662F\u739B\u7AA6\u7279\u6709\u7684.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u739B\u7AA6\u5723\u53F2\u7684\u8BB0\u8F7D\u91CC, \u8036\u7A23\u7684\u8363\u8FDB\u8036\u8DEF\u6492\u51B7, \u56E0\u7942\u5728\u5723\u6BBF\u7684\u4E3E\u52A8\u8FBE\u5230\u9AD8\u5CF0 ( \u53C2\u9605: \u8C37\u5341\u4E00 15 ~ 19, \u8036\u7A23\u53EA\u5728\u5723\u6BBF\u5468\u56F4\u5BDF\u770B, \u5E76\u672A\u505A\u4EFB\u4F55\u4E3E\u52A8, \u56E0\u4E3A\u65F6\u8FB0\u5DF2\u665A, \u7942\u548C\u95E8\u5F92\u5C31\u51FA\u57CE, \u5F80\u4F2F\u8FBE\u5C3C\u53BB\u4E86 ). \u5723\u7ECF\u5B66\u8005\u4EEC\u81F3\u4ECA\u4ECD\u5728\u63A8\u6D4B: \u8036\u7A23\u5230\u5E95\u4EE5\u4EC0\u4E48\u7406\u7531, \u6297\u8BAE\u8BF4\u5723\u6BBF\u88AB\u6EE5\u7528\u4E86. \u5BF9\u8036\u7A23\u6297\u8BAE\u7684\u89E3\u91CA, \u6BCF\u4E2A\u5723\u53F2\u7684\u8BB0\u8FF0\u90FD\u7A0D\u6709\u4E0D\u540C. \u5728\u739B\u7AA6\u5723\u53F2\u7684\u8BB0\u8F7D\u91CC, \u8036\u7A23\u4E2D\u65AD\u4E86\u5728\u5723\u6BBF\u5185\u7684\u5546\u4E1A\u884C\u4E3A ( 12 \u8282 ). \u5728\u90A3\u91CC\u4EA4\u6613\u725B\u7F8A, \u662F\u5723\u6BBF\u732E\u796D\u6240\u5FC5\u9700\u7684, \u96CF\u9E3D\u662F\u8D2B\u7A77\u5987\u4EBA\u4EA7\u751F\u7684\u732E\u796D\u7269 ( \u808B\u5341\u4E8C 6 ~ 8: \u8DEF\u4E8C 24 ). \u5E0C\u814A\u548C\u7F57\u9A6C\u94F6\u5E01\u5FC5\u987B\u9009\u6362\u6210\u5723\u6BBF\u4E13\u7528\u7684 &quot;\u65AF\u5854\u7279&quot; \u94F6\u5E01, \u4E0D\u662F\u56E0\u4E3A &quot;\u65AF\u5854\u7279&quot; \u94F6\u5E01\u4E0A\u6CA1\u6709\u4EE4\u4EBA\u4E0D\u60A6\u7684\u56FE\u50CF ( \u51EF\u6492\u6216\u5176\u5B83\u89C6\u7947\u7684\u50CF, \u6709\u6E0E\u5723\u6240, \u4E0D\u80FD\u5728\u5723\u6BBF\u5185\u4F7F\u7528 ), \u800C\u662F\u56E0\u4E3A &quot;\u5723\u6BBF\u4E13\u7528\u7684\u94F6\u5E01&quot; \u5177\u6709\u6700\u9AD8\u7684\u542B\u94F6\u91CF.</p><p>\u739B\u7AA6\u5723\u53F2\u5C06\u300A\u4F9D\u6492\u610F\u4E9A\u5148\u77E5\u4E66\u300B\u548C\u300A\u8036\u808B\u7C73\u4E9A\u5148\u77E5\u4E66\u300B\u7684\u7ECF\u6587\u7EFC\u5408\u6210 &quot;\u6211\u7684\u6BBF\u5B87\u5E94\u79F0\u4E3A\u7948\u7977\u4E4B\u6240, \u4F60\u4EEC\u7ADF\u628A\u5B83\u505A\u6210\u4E86\u8D3C\u7A9D&quot; ( 13 \u8282, \u8BF7\u53C2\u9605: \u4F9D\u4E94\u516D 7; \u8036\u4E03 11 ), \u7528\u4EE5\u89E3\u91CA\u8036\u7A23\u5728\u5723\u6BBF\u7684\u4E3E\u52A8. \u4F9D\u6492\u610F\u4E9A\u5148\u77E5\u8BF4\u7684\u662F: \u7531\u9ED8\u897F\u4E9A\u7684\u7406\u5FF5\u6765\u8BF4, \u5723\u6BBF\u662F\u5B8C\u7F8E\u7684 &quot;\u4E07\u6C11\u7948\u7977\u6240&quot; ( \u739B\u7AA6\u5723\u53F2\u628A &quot;\u4E07\u6C11&quot; \u7701\u6389\u4E86; \u53C2\u9605: \u8C37\u5341\u4E00 16 ). \u8036\u808B\u7C73\u4E9A\u5148\u77E5\u662F\u5411\u72B9\u592A\u5730\u533A\u7684\u4EBA\u6C11\u8B66\u544A, \u56E0\u4E3A\u4ED6\u4EEC\u4E00\u76F4\u76F8\u4FE1\u5728\u5723\u6BBF\u8B66\u62DC\u662F\u6709\u6548\u529B\u7684, \u53EF\u662F\u4ED6\u4EEC\u5F7C\u6B64\u95F4\u7684\u884C\u4E3A\u5374\u7C97\u66B4, \u4E0D\u516C\u4E49, \u56E0\u6B64\u8036\u808B\u7C73\u4E9A\u5148\u77E5\u53D1\u51FA\u8B66\u544A\u8BF4, \u4ED6\u4EEC\u7684\u5815\u843D\u8A00\u884C\u4EB5\u6E0E\u4E86\u5723\u6BBF, \u4F7F\u5723\u6BBF\u6210\u4E86 &quot;\u8D3C\u7A9D&quot;, \u5E76\u9884\u8A00\u5723\u6BBF\u5373\u5C06\u88AB\u6BC1\u706D. \u5728\u6B64, \u739B\u7AA6\u5723\u53F2\u662F\u8981\u628A\u8036\u7A23\u63CF\u8FF0\u6210\u4E00\u4F4D\u60C5\u7DD2\u6FC0\u6602, \u60F3\u8981\u6E05\u9664\u5723\u6BBF\u7684\u5148\u77E5 ( 12 ~ 13 \u8282 ).</p><p>\u63A5\u7740, \u739B\u7AA6\u5723\u53F2\u628A\u8036\u7A23\u63CF\u8FF0\u6210\u4E00\u4F4D\u5BCC\u4E8E\u6190\u60AF\u5FC3\u7684\u6CBB\u7652\u8005, \u662F\u5723\u6BBF\u4E2D\u6700\u4E0D\u53D7\u4EBA\u6B22\u8FCE\u7684\u6CBB\u7652\u8005 ( 14 ~ 17 \u8282\u53C2\u9605: \u808B\u4E8C\u5341\u4E00 18, \u778E\u5B50\u548C\u7638\u5B50\u662F\u88AB\u7981\u6B62\u5949\u732E\u796D\u54C1\u7684 ), \u8FD9\u4E00\u6BB5\u662F\u300A\u739B\u7AA6\u798F\u97F3\u300B\u6240\u72EC\u6709\u7684.</p><p>\u8036\u7A23\u6EE1\u5168\u4E86\u300A\u4F9D\u6492\u610F\u4E9A\u5148\u77E5\u4E66\u300B\u6709\u5173\u300A\u9ED8\u897F\u4E9A\u300B\u7684\u9884\u8A00: \u5305\u62EC\u88AB\u6CBB\u7652\u7684\u778E\u5B50\u548C\u7638\u5B50, \u90FD\u5174\u9AD8\u91C7\u70C8\u5730\u8FDB\u5165\u8036\u8DEF\u6492\u51B7 ( \u4F9D\u4E09\u5341\u4E94 5 ~ 6 ). \u603B\u62EC\u6765\u8BF4, \u4EBA\u4EEC\u5BF9\u8036\u7A23\u6E05\u9664\u5723\u6BBF\u7684\u53CD\u5E94\u4E0D\u4E00. \u5F53\u5B97\u6559\u9886\u8896\u542C\u5230\u5B69\u5B50\u5728\u558A &quot;\u8D3A\u4E09\u7EB3\u4E8E\u8FBE\u5473\u4E4B\u5B50&quot; ( 17 \u8282: \u53C2\u9605: \u739B\u5341\u516B 1 ~ 4; \u5341\u4E5D 13 ~ 15 ) \u65F6, \u4ED6\u4EEC\u53D8\u5F97\u66F4\u4E3A\u5FFF\u6012 ( \u53C2\u9605: \u739B\u4E5D 27; \u5341\u4E8C 23; \u5341\u4E94 22; \u4E8C\u5341 30, 31 \u7B49\u5916\u5728\u6CBB\u7652\u4E8B\u4EF6\u4E2D, \u4F7F\u7528\u7684\u662F &quot;\u8FBE\u5473\u4E4B\u5B50&quot; \u8FD9\u4E2A\u540D\u53F7 ). \u4E8E\u662F\u8036\u7A23\u7528\u4E86\u300A\u5723\u548F\u300B\u4E2D &quot;\u4F60\u7531\u5A74\u513F\u548C\u5403\u5976\u8005\u7684\u53E3\u4E2D, \u5907\u53D7\u8D5E\u7F8E&quot; ( \u8A60\u516B 3 ) \u8FD9\u53E5\u8BDD, \u6765\u56DE\u5E94\u5B97\u6559\u9886\u8896.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u8FDB\u5165\u8036\u8DEF\u6492\u51B7\u6240\u505A\u7684\u7B2C\u4E00\u4EF6\u4E8B, \u5C31\u662F\u6E05\u7406\u5723\u6BBF. \u8FD9\u662F\u6807\u8BB0\u6027\u7684\u884C\u52A8, \u4E5F\u662F\u6743\u5A01\u7684\u8C61\u5F81. \u8FD9\u79CD\u6743\u5A01, \u5BF9\u5F53\u65F6\u7684\u5B98\u65B9\u6743\u5A01\u548C\u5B97\u6559\u6743\u5A01\u662F\u4E00\u79CD\u4FAE\u8FB1. \u8FD9\u4E9B\u6743\u529B\u673A\u6784, \u5141\u8BB8\u5546\u8D29\u5728\u5723\u6BBF\u7684\u5927\u9662\u5185\u4E70\u5356\u7272\u755C\u3001\u5151\u6362\u91D1\u94B1 (\u4EBA\u4EEC\u9700\u8981\u4E70\u7272\u755C\u4F5C\u796D\u732E, \u9700\u8981\u5151\u6362\u5723\u6BBF\u8BA4\u53EF\u7684\u94B1\u5E01\u6350\u6B3E, \u56E0\u4E3A\u5723\u6BBF\u4E0D\u63A5\u53D7\u5370\u6709\u7687\u5E1D\u5934\u50CF\u7684\u7F57\u9A6C\u94B1\u5E01). \u8036\u7A23\u5F15\u7528 \u300A \u65E7\u7EA6 \u300B \u4E2D\u7684\u4E24\u6BB5\u7ECF\u6587, \u8BC1\u5B9E\u81EA\u5DF1\u884C\u4E3A\u7684\u5408\u6CD5\u6027 (\u4F9D 56: 7; \u8036 7: 11), \u8FD9\u662F\u8036\u7A23\u7684\u91CD\u8981\u4E3E\u52A8\u4E4B\u4E00.</p><p>\u8036\u7A23\u6240\u505A\u7684\u4E00\u5207, \u90FD\u8981\u6C42\u6DF1\u5EA6\u7684\u996D\u4F9D, \u56DE\u5F52\u5230\u5929\u4E3B\u7684\u7231\u4E2D. \u8FD9\u4E2A\u4E3E\u52A8\u5F88\u5371\u9669. \u56E0\u4E3A\u5B83\u76F4\u63A5\u6311\u6218\u4E86\u6839\u57FA\u575A\u56FA\u7684\u5B97\u6559\u4F53\u7CFB. \u8036\u7A23\u501F\u6B64\u6240\u8BC1\u5B9E\u7684\u9053\u7406, \u5DF2\u7ECF\u6FC0\u6012\u4E86\u72B9\u592A\u9886\u5BFC\u9636\u5C42. \u5BF9\u4F20\u7EDF\u7684\u5929\u4E3B\u5F62\u8C61\u3001\u5723\u6BBF\u3001\u8036\u8DEF\u6492\u51B7\u4EE5\u53CA\u6CD5\u5F8B\u5B58\u6709\u7684\u8D28\u7591, \u662F\u5BF9\u795E\u7684\u4E0D\u656C, \u7834\u574F\u8FD9\u4E9B\u9886\u5BFC\u4EBA\u7684 &quot;\u597D\u540D\u58F0&quot; \u5C31\u662F\u4EB5\u6E0E\u4FE1\u4EF0. \u8036\u7A23\u6700\u7EC8\u8981\u5BF9\u6297\u5F53\u65F6\u7684\u6743\u5A01\u673A\u6784(\u72B9\u592A\u516C\u8BAE\u4F1A\u548C\u5723\u6BBF) \u4EE5\u53CA\u673A\u6784\u5185\u7684\u5404\u7EA7\u4EBA\u5458. \u4ED6\u4EEC\u540E\u6765\u5C31\u6307\u63A7\u8036\u7A23\u4EB5\u6E0E\u4E86\u5929\u4E3B.</p><p>\u6D01\u51C0\u5723\u6BBF\u7684\u60C5\u8282\u4F34\u6709\u6CBB\u6108\u75C5\u4EBA\u548C\u513F\u7AE5\u4EEC\u7684\u559D\u5F69, \u8FD9\u4E9B\u663E\u793A\u51FA\u8036\u7A23\u9ED8\u897F\u4E9A\u7684\u8EAB\u4EFD. \u4E0D\u8FC7, \u5723\u6BBF\u7684\u9886\u8896\u4EEC\u5374\u5BF9\u6B64\u4EA7\u751F\u53CD\u611F. \u4ED6\u4EEC\u4E0D\u660E\u767D, \u539F\u6765\u968F\u7740\u8036\u7A23\u7684\u5230\u6765, \u771F\u6B63\u7684\u671D\u62DC\u5DF2\u7ECF\u5F00\u59CB\u4E86.</p><h2 id="\u5492\u8BC5\u65E0\u82B1\u679C\u6811" tabindex="-1"><a class="header-anchor" href="#\u5492\u8BC5\u65E0\u82B1\u679C\u6811" aria-hidden="true">#</a> \u5492\u8BC5\u65E0\u82B1\u679C\u6811</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>18</sup> \u65E9\u6668, \u4ED6\u56DE\u57CE\u65F6, \u997F\u4E86, <sup>19</sup> \u89C1\u8DEF\u65C1\u6709\u68F5\u65E0\u82B1\u679C\u6811, \u5C31\u8D70\u5230\u8DDF\u524D, \u4F46\u5728\u6811\u4E0A\u9664\u4E86\u53F6\u5B50\u5916, \u4EC0\u4E48\u4E5F\u6CA1\u627E\u7740, \u5C31\u5BF9\u5B83\u8BF4: &quot;\u4F60\u6C38\u8FDC\u4E0D\u518D\u7ED3\u679C\u5B50\u4E86!&quot; \u90A3\u65E0\u82B1\u679C\u6811\u7ACB\u5373\u67AF\u5E72\u4E86. <sup>20</sup> \u95E8\u5F92\u4E00\u89C1, \u5C31\u60CA\u5F02\u8BF4: &quot;\u600E\u4E48\u8FD9\u65E0\u82B1\u679C\u6811\u7ACB\u5373\u67AF\u5E72\u4E86?&quot; <sup>21</sup> \u8036\u7A23\u56DE\u7B54\u4ED6\u4EEC\u8BF4: &quot;\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC: \u4F60\u4EEC\u5982\u679C\u6709\u4FE1\u5FB7, \u4E0D\u7591\u60D1, \u4E0D\u4F46\u80FD\u5BF9\u65E0\u82B1\u679C\u6811\u505A\u8FD9\u4EF6\u4E8B, \u5373\u4FBF\u4F60\u4EEC\u5BF9\u8FD9\u5EA7\u5C71\u8BF4: \u8D77\u6765, \u6295\u5230\u6D77\u4E2D! \u4E5F\u5FC5\u8981\u5B9E\u73B0. <sup>22</sup> \u4E0D\u8BBA\u4F60\u4EEC\u5728\u7948\u7977\u65F6\u6073\u6C42\u4EC0\u4E48, \u53EA\u8981\u4FE1, \u5C31\u5FC5\u83B7\u5F97.&quot;</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u518D\u5EA6\u7F29\u77ED\u4E86\u65F6\u9650, \u8BF4\u90A3\u682A\u6811 &quot;\u90A3\u65E0\u82B1\u679C\u6811\u7ACB\u5373\u67AF\u5E72\u4E86&quot;. \u4ED6\u5BF9\u8FD9\u4EF6\u4E8B\u60C5\u7684\u5174\u8DA3, \u5B8C\u5168\u96C6\u4E2D\u4E8E\u6709\u5173\u4E8E\u4FE1\u5FB7\u7684\u5FC5\u987B\u6027\u7684\u6559\u8BAD\u4E0A.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u4E2A\u6709\u4E9B\u5947\u602A\u7684\u6545\u4E8B, \u4E5F\u8BB8\u662F\u4ECE\u300A\u8DEF\u52A0\u798F\u97F3\u300B\u4E2D\u7684 &quot;\u65E0\u82B1\u679C\u6BD4\u55BB&quot; (\u8DEF\u5341\u4E09 6 ~ 9) \u6F14\u53D8\u800C\u6765\u7684. \u7ED3\u679C\u5B9E\u7684\u65E0\u82B1\u679C\u6811, \u8DDF\u8461\u8404\u6811\u4E00\u6837, \u662F\u548C\u5E73, \u7E41\u8363\u7684\u8C61\u5F81 ( \u5217\u4E0A\u4E94 5 ), \u739B\u7AA6\u5723\u53F2\u5E38\u5E38\u4F7F\u7528 &quot;\u7ED3\u597D\u679C\u5B50&quot; \u7684\u9690\u55BB, \u6765\u8C08\u8BBA\u516C\u4E49\u7684\u884C\u4E3A ( \u53C2\u9605: \u739B\u4E09 8; \u4E03 15 ~ 20; \u5341\u4E8C 33 ~ 37; \u5341\u4E0923; \u4E8C\u5341\u4E00 19\uFF0C 33 ~ 43 ). \u739B\u7AA6\u5723\u53F2\u7684\u5E03\u5C40, \u81EA\u739B\u4E8C\u5341\u4E00\u7AE0\u5F00\u59CB, \u4E4B\u540E\u7684\u5185\u5BB9\u90FD\u5E26\u6709\u5F88\u5F3A\u7684\u672B\u4E16\u6027\u8BBA\u8C03. \u5FC5\u987B\u5448\u73B0 &quot;\u597D\u679C\u5B50&quot; \u7684\u65F6\u95F4\u5230\u4E86, \u6216\u662F\u5723\u6BBF\u88AB\u6BC1\u7684\u65F6\u95F4\u5230\u4E86, \u90A3\u4E9B\u628A\u4EBA\u9886\u5165\u6B67\u9014\u7684\u8981\u88AB\u5B9A\u7F6A\u7684\u65F6\u95F4\u5230\u4E86 ( \u4E5F\u53C2\u9605: \u8036\u516B 13; \u6B27\u4E5D 10, 16 ).</p><p>\u6700\u540E\u7684\u4E24\u8282, \u8F6C\u79FB\u4E86\u6545\u4E8B\u7684\u91CD\u70B9, \u8FD9\u6837\u5C31\u4F7F\u4E3B\u9898\u6210\u4E3A &quot;\u4FE1\u5FB7\u7684\u529B\u91CF&quot; ( \u540C\u6837\u7684\u4E3B\u9898\u89C1: \u739B\u4E03 7 ~ 11; \u5E76\u53C2\u9605: \u739B\u516D 30; \u516B 25 ~ 26; \u5341\u56DB 30 ~ 31; \u5341\u516D 8; \u5341\u4E03 20, \u5176\u4E2D\u8036\u7A23\u9063\u8D23\u95E8\u5F92\u7F3A\u4E4F\u4FE1\u5FB7 ). \u8036\u7A23\u5E76\u6CA1\u6709\u627F\u8BFA\u6BCF\u4E00\u4EF6\u7948\u6C42\u7684\u4E8B\u7269\u90FD\u4F1A\u5F97\u5230\u5E94\u5141; \u76F8\u53CD\u7684, \u4ED6\u5411\u5165\u5F92\u4FDD\u8BC1, \u5F53\u4ED6\u4EEC\u6000\u7740\u4FE1\u5FB7\u5728\u5929\u4E3B\u6069\u5BA0\u53CA\u7F8E\u5584\u5185\u7948\u6C42\u65F6, \u5929\u4E3B\u5C31\u6C38\u8FDC\u4E0E\u4ED6\u4EEC\u540C\u5728 ( \u739B\u4E00 23; \u4E8C\u5341\u516B 20 ). \u5929\u4E3B\u7684\u6743\u80FD\u5728\u4FE1\u5F92\u7684\u8EAB\u4E0A\u663E\u626C, \u5373\u4F7F\u4ED6\u4EEC\u9762\u5BF9\u7684\u662F\u4E0D\u53EF\u8D85\u8D8A\u7684\u56F0\u96BE.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u4E4D\u770B\u8D77\u6765, \u8FD9\u6BB5\u573A\u666F\u5F88\u4E0D\u534F\u8C03. \u5728\u9A6C\u5C14\u8C37\u7684\u8BB0\u8F7D\u4E2D, \u8FD9\u6BB5\u53D9\u8FF0\u51FA\u73B0\u5728\u6D01\u51C0\u5723\u6BBF\u4E4B\u524D (\u8C37 11: 12- 14\u3001 15- 17). \u8FD9\u68F5\u4E0D\u7ED3\u679C\u5B9E\u7684\u65E0\u82B1\u679C\u6811, \u8C61\u5F81\u4E86\u4E0D\u5FE0\u4E8E\u81EA\u5DF1\u4F7F\u547D\u7684\u4EE5\u8272\u5217\u6C11: \u8036\u7A23\u7684\u65A5\u8D23\u5BA3\u544A\u4E86\u5929\u4E3B\u5BF9\u8FD9\u4E2A\u6C11\u65CF\u7684\u5F03\u7EDD(\u53C2\u9605\u8DEF 13:6 -9 ). \u800C\u739B\u7AA6\u7684\u63CF\u8FF0\u662F\u8981\u663E\u793A\u8036\u7A23\u7684\u80FD\u529B, \u7942\u672C\u6765\u662F\u4E3A\u4E86\u5728\u5723\u57CE\u5BFB\u627E\u6B63\u4E49\u7684\u679C\u5B9E, \u5374\u6CA1\u6709\u627E\u5230: \u6700\u540E, \u5979\u8C61\u5F81\u6027\u5730\u5BA3\u544A\u8036\u8DEF\u6492\u51B7\u7684\u9677\u843D\u548C\u5723\u6BBF\u7684\u6BC1\u706D. \u4E3A\u5723\u53F2\u800C\u8A00, \u8FD9\u4E00\u4E8B\u5B9E\u662F\u6559\u80B2\u56E2\u4F53\u6709\u5173\u4FE1\u5FB7\u7684\u529B\u91CF\u548C\u5176\u91CD\u8981\u6027\u7684\u4E00\u6B21\u673A\u4F1A. \u6211\u4EEC\u5728 \u300A\u739B\u7AA6\u798F\u97F3 \u300B \u7B2C\u516B\u7AE0\u548C\u7B2C\u4E5D\u7AE0\u4E2D\u770B\u5230, \u4FE1\u5FB7\u662F\u8036\u7A23\u65BD\u884C\u795E\u8FF9\u7684\u5FC5\u8981\u6761\u4EF6.</p><h2 id="\u8036\u7A23\u6743\u67C4\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u8036\u7A23\u6743\u67C4\u7684\u7531\u6765" aria-hidden="true">#</a> \u8036\u7A23\u6743\u67C4\u7684\u7531\u6765</h2><h3 id="\u7ECF\u6587-3" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-3" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>25</sup> \u4ED6\u8FDB\u4E86\u5723\u6BBF, \u6B63\u6559\u8BAD\u4EBA\u65F6, \u53F8\u796D\u957F\u548C\u6C11\u95F4\u7684\u957F\u8001\u6765\u5230\u4ED6\u8DDF\u524D\u8BF4: &quot;\u4F60\u51ED\u4EC0\u4E48\u6743\u67C4\u4F5C\u8FD9\u4E9B\u4E8B? \u8C01\u7ED9\u4E86\u4F60\u8FD9\u79CD\u6743\u67C4?&quot; <sup>24</sup> \u8036\u7A23\u56DE\u7B54\u8BF4: &quot;\u6211\u4E5F\u95EE\u4F60\u4EEC\u4E00\u53E5\u8BDD, \u4F60\u4EEC\u82E5\u7B54\u590D\u6211, \u6211\u5C31\u544A\u8BC9\u4F60\u4EEC: \u6211\u51ED\u4EC0\u4E48\u6743\u67C4\u4F5C\u8FD9\u4E8B. <sup>25</sup> \u82E5\u7FF0\u7684\u6D17\u793C, \u662F\u4ECE\u54EA\u91CC\u6765\u7684? \u662F\u4ECE\u5929\u4E0A\u6765\u7684? \u8FD8\u662F\u4ECE\u4EBA\u6765\u7684?&quot; \u4ED6\u4EEC\u5FC3\u4E2D\u601D\u91CF\u8BF4: \u5982\u679C\u6211\u4EEC\u8BF4: \u662F\u4ECE\u5929\u4E0A\u6765\u7684, \u4ED6\u5FC5\u5BF9\u6211\u4EEC\u8BF4: \u4F60\u4EEC\u4E3A\u4EC0\u4E48\u4E0D\u4FE1\u4ED6? <sup>26</sup> \u5982\u679C\u6211\u4EEC\u8BF4: \u662F\u4ECE\u4EBA\u6765\u7684, \u6211\u4EEC\u5BB3\u6015\u6C11\u4F17, \u56E0\u4E3A\u4F17\u4EBA\u90FD\u4EE5\u82E5\u7FF0\u4E3A\u4E00\u4F4D\u5148\u77E5. <sup>27</sup> \u4ED6\u4EEC\u4FBF\u56DE\u7B54\u8036\u7A23\u8BF4: &quot;\u6211\u4EEC\u4E0D\u77E5\u9053.&quot; \u8036\u7A23\u4E5F\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u6211\u4E5F\u4E0D\u544A\u8BC9\u4F60\u4EEC: \u6211\u51ED\u4EC0\u4E48\u6743\u67C4\u4F5C\u8FD9\u4E9B\u4E8B.&quot;</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5F53\u72B9\u592A\u5B97\u6559\u9886\u8896\u8BE2\u95EE\u8036\u7A23\u7684\u6743\u67C4\u65F6, \u8036\u7A23\u7684\u53CD\u95EE\u53CD\u4F7F\u4ED6\u4EEC\u9677\u5165\u5DE6\u53F3\u4E3A\u96BE\u7684\u72B6\u51B5. \u82E5\u7FF0\u7684\u6D17\u793C\u6765\u81EA\u5929\u4E0A\u6291\u4EBA\u95F4? \u8FD9\u4E9B\u5BFB\u8845\u8005\u7684\u4F2A\u88C5\u4E0D\u77E5\u66F4\u66DD\u9732\u4E86\u4ED6\u4EEC\u7684\u4F2A\u88C5; \u8036\u7A23\u4E5F\u62D2\u7EDD\u7ED9\u4ED6\u4EEC\u7B54\u6848.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u6574\u672C\u300A\u739B\u7AA6\u798F\u97F3\u300B\u4E2D, \u8036\u7A23\u88AB\u523B\u753B\u6210\u4E00\u4F4D\u7FA4\u4F17\u8DDF\u968F, \u4F46\u5B97\u6559\u9886\u8896\u53CD\u5BF9\u7684\u6743\u5A01\u5BFC\u5E08. \u73B0\u5728, \u8036\u7A23\u548C\u5B97\u6559\u6743\u5A01\u4EBA\u58EB\u4EA7\u751F\u4E86\u516C\u5F00\u51B2\u7A81. \u5728\u8036\u7A23\u53D7\u96BE\u7684\u53D9\u8FF0\u4E2D, \u53F8\u796D\u957F\u548C\u6C11\u95F4\u957F\u8001\u6210\u4E86\u4E3B\u8981\u7684\u654C\u5BF9\u8005 ( 23 \u8282; \u739B\u4E8C\u5341\u4E09\u4EE5\u540E\u6CD5\u5229\u585E\u4EBA\u5C31\u4E0D\u518D\u51FA\u73B0\u4E86 ). \u4ED6\u4EEC\u4E3A\u8036\u7A23\u8BBE\u7684\u9677\u9631, \u5374\u4EA7\u751F\u4E86\u9002\u5F97\u5176\u53CD\u7684\u7ED3\u679C, \u4F7F\u5F97\u81EA\u5DF1\u53CD\u800C\u88AB\u8036\u7A23\u7684\u95EE\u9898\u95EE\u5012. \u968F\u540E\u7684\u4E09\u4E2A\u6BD4\u55BB\u4E2D, \u524D\u4E24\u4E2A\u6BD4\u55BB\u95F4\u63A5\u5730\u56DE\u7B54\u4E86\u8036\u6743\u67C4\u7684\u7531\u6765\u8FD9\u4E2A\u95EE\u9898.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u6240\u884C\u7684\u6743\u67C4\u6FC0\u6012\u4E86\u7942\u7684\u5BF9\u5934\u4EEC. \u6211\u4EEC\u5728\u8FD9\u91CC\u770B\u5230\u4E86\u8036\u7A23\u4E0E\u90A3\u4E9B\u53F8\u796D\u957F\u53CA\u6C11\u95F4\u957F\u8001\u7B2C\u4E00\u6B21\u8FA9\u8BBA. \u4ED6\u4EEC\u8D28\u7591\u8036\u7A23\u7684\u6743\u67C4 (23 ), \u5373\u7942\u51ED\u4EC0\u4E48\u5728\u7FA4\u4F17\u7684\u6B22\u547C\u58F0\u4E2D\u8FDB\u5165\u8036\u8DEF\u6492\u51B7\u3001 \u6E05\u6D01\u5723\u6BBF\u3001\u6CBB\u6108\u75C5\u60A3\u5E76\u5BA3\u8BB2\u65BD\u6559. \u8036\u7A23\u4EE5\u95EE\u4F5C\u7B54 ( 24 ), \u5982\u679C\u4ED6\u4EEC\u80FD\u591F\u516C\u5F00\u56DE\u7B54\u6D17\u8005\u82E5\u7FF0\u662F\u6765\u81EA\u5929\u4E3B, \u8FD8\u662F\u6765\u81EA\u4EBA, \u7942\u5C31\u56DE\u7B54\u4ED6\u4EEC. \u6307\u63A7\u888D\u7684\u4EBA\u53D1\u73B0\u81EA\u5DF1\u65E0\u8A00\u4EE5\u5BF9 (\u53EA\u80FD\u8BF4&quot;\u6211\u4EEC\u4E0D\u77E5\u9053&quot; ), \u8FD9\u6837\u4FBF\u4F7F\u4ED6\u4EEC\u5728\u7FA4\u4F17\u9762\u524D\u53C8\u4E00\u6B21\u53D7\u8FB1, \u800C\u8036\u7A23\u5219\u53C8\u4E00\u6B21\u80AF\u5B9A\u4E86\u81EA\u5DF1\u7684\u6743\u5A01 ( 27 ). \u739B\u7AA6\u798F\u97F3\u7684\u8BFB\u8005\u5F88\u660E\u767D, \u65E0\u8BBA\u662F\u82E5\u7FF0\u7684\u884C\u52A8, \u8FD8\u662F\u8036\u7A23\u7684\u884C\u52A8, \u90FD\u6765\u81EA\u4E8E\u5929\u4E3B.</p><h2 id="\u4E8C\u5B50\u7684\u6BD4\u55BB" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u5B50\u7684\u6BD4\u55BB" aria-hidden="true">#</a> \u4E8C\u5B50\u7684\u6BD4\u55BB</h2><h3 id="\u7ECF\u6587-4" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-4" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>28</sup> \u4F60\u4EEC\u4EE5\u4E3A\u600E\u6837? \u4ECE\u524D\u6709\u4E00\u4E2A\u4EBA, \u6709\u4E24\u4E2A\u513F\u5B50, \u4ED6\u5BF9\u7B2C\u4E00\u4E2A\u8BF4: \u5B69\u5B50! \u4F60\u4ECA\u5929\u5230\u8461\u8404\u56ED\u91CC\u53BB\u5DE5\u4F5C\u5427! <sup>29</sup> \u4ED6\u56DE\u7B54\u8BF4: \u4E3B, \u6211\u53BB. \u4F46\u4ED6\u5374\u6CA1\u6709\u53BB. <sup>30</sup> \u4ED6\u5BF9\u7B2C\u4E8C\u4E2A\u4E5F\u8BF4\u4E86\u540C\u6837\u7684\u8BDD, \u7B2C\u4E8C\u4E2A\u5374\u7B54\u5E94\u8BF4: \u6211\u4E0D\u613F\u610F. \u4F46\u540E\u6765\u6094\u609F\u8FC7\u6765, \u800C\u53C8\u53BB\u4E86. <sup>31</sup> \u4E8C\u4EBA\u4E2D\u54EA\u4E00\u4E2A\u5C65\u884C\u4E86\u7236\u4EB2\u7684\u610F\u613F?\u4ED6\u4EEC\u8BF4: &quot;\u540E\u4E00\u4E2A&quot; \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC: \u7A0E\u540F\u548C\u5A3C\u5993\u8981\u5728\u4F60\u4EEC\u4EE5\u5148\u8FDB\u5165\u5929\u56FD. <sup>32</sup> \u56E0\u4E3A\u82E5\u7FF0\u6765\u5230\u4F60\u4EEC\u8FD9\u91CC\u5C65\u884C\u4E86\u6B63\u4E49, \u4F60\u4EEC\u4ECD\u4E0D\u76F8\u4FE1\u4ED6, \u7A0E\u540F\u548C\u5A3C\u5993\u5012\u76F8\u4FE1\u4E86; \u81F3\u4E8E\u4F60\u4EEC, \u89C1\u4E86\u540E, \u4ECD\u4E0D\u6094\u609F\u53BB\u76F8\u4FE1\u4ED6.&quot;</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-4" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-4" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u7B2C\u4E8C\u4E2A\u513F\u5B50\u7684\u53CD\u5E94\u662F\u8FD9\u4E2A\u6BD4\u55BB\u7684\u5173\u952E: &quot;\u4F46\u540E\u6765\u6094\u609F\u8FC7\u6765&quot;, \u5982\u6B64\u4FBF\u8BF4\u51FA\u4E86\u8FDB\u5165\u5929\u56FD\u5FC5\u987B\u7684\u5FC3\u5883, \u800C\u8FD9\u6B63\u662F\u5927\u90E8\u5206\u72B9\u592A\u4EBA\u6240\u7F3A\u4E4F\u7684. \u8036\u7A23\u4E5F\u628A\u8FD9\u4E2A\u6545\u4E8B\u548C\u82E5\u7FF0\u6D17\u8005\u7684\u4EFB\u52A1\u76F8\u5173\u8054.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-4" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-4" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u5BA3\u8BB2\u6240\u7528\u7684\u65B9\u6CD5, \u548C\u7EB3\u5802\u5148\u77E5\u7C7B\u4F3C ( \u6492\u4E0B\u5341\u4E8C 1 ~ 12 ), \u5148\u8BE2\u95EE\u542C\u9053\u8005\u7684\u770B\u6CD5\u548C\u610F\u89C1, \u7136\u540E\u4EE5\u4ED6\u4EEC\u5BF9\u81EA\u5DF1\u7684\u8BC4\u65AD\u4F5C\u4E3A\u7ED3\u675F. \u4E00\u5F00\u59CB\u770B\u8D77\u6765\uFF0C\u8FD9\u662F\u4E2A\u7B80\u5355\u6613\u61C2\u7684\u6BD4\u55BB. \u6BD4\u55BB\u4E2D\u7684\u4E24\u4E2A &quot;\u5B69\u5B50&quot; ( 28 \u8282\u7528\u7684\u5B57\u662F reknon &quot;\u5B69\u5B50&quot;, \u800C\u4E0D\u662F huios &quot;\u513F\u5B50&quot; ) \u90FD\u4E0D\u662F\u7406\u60F3\u4E2D\u7684\u4EBA\u7269. \u770B\u4E0A\u53BB\u4F3C\u4E4E\u613F\u610F\u5C65\u884C\u7236\u4EB2\u610F\u613F\u7684\u662F\u7B2C\u4E00\u4E2A\u5B69\u5B50.</p><p>\u7136\u800C, \u5728\u4E00\u79CD\u9AD8\u5EA6\u91CD\u89C6\u8363\u8A89\u7684\u6587\u5316\u91CC, \u7B2C\u4E00\u4E2A\u5B69\u5B50\u7684\u56DE\u7B54\u5C31\u663E\u5F97\u4E0D\u591F\u6E05\u695A\u660E\u767D ( \u8FD9\u6837\u7684\u7B54\u8986\u4E0D\u662F\u90A3\u4E48\u6E05\u695A ). \u6839\u636E\u8FD9\u4E2A\u6BD4\u55BB\u7684\u4E00\u4E9B\u76F8\u5173\u624B\u6284\u672C, \u7ED9\u4E88\u6E05\u695A\u660E\u767D\u56DE\u7B54\u7684, \u662F\u7B2C\u4E8C\u4E2A\u5B69\u5B50. \u7B2C\u4E00\u4E2A\u5B69\u5B50\u4EE4\u7236\u4EB2\u5728\u4F17\u4EBA\u524D\u611F\u5230\u7F9E\u6127, \u8FD9\u662F\u4E00\u4E2A\u6BD4\u98DF\u8A00\u800C\u80A5\u66F4\u4E3A\u4E25\u91CD\u7684\u8FC7\u72AF. \u8036\u7A23\u4E4B\u524D\u66FE\u5BF9\u5176\u95E8\u5F92\u575A\u79F0: &quot;\u8BF4&quot; \u548C &quot;\u505A&quot; \u90FD\u662F\u5FC5\u9700\u7684 ( \u739B\u4E03 21 ~ 27 ); \u73B0\u5728, \u8036\u7A23\u628A\u8BAD\u793A\u7684\u5BF9\u8C61\u6307\u5411\u4E86\u53EA\u8BF4\u4E0D\u505A\u7684\u5B97\u6559\u9886\u8896, \u4E0E\u6700\u4E0D\u53EF\u80FD\u6210\u4E3A\u4E49\u5FB7\u699C\u6837\u7684\u4EBA\u8FDB\u884C\u5BF9\u6BD4 ( 31 ~ 32 \u8282 ). \u4F46\u5B97\u6559\u9886\u8896\u4EEC\u4ECD\u7136\u8FD8\u6709\u65F6\u95F4\u6094\u6539; \u90A3\u4E9B\u5728\u8D77\u5148\u62D2\u7EDD\u8DDF\u968F\u8036\u7A23, \u4F46\u6309\u7167\u5929\u4E3B\u65E8\u610F\u53BB\u5B9E\u884C\u7684\u4EBA, \u4ECD\u6709\u53EF\u80FD\u6539\u53D8\u4ED6\u4EEC\u7684\u5FC3\u610F.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-4" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-4" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u5728\u6BD4\u55BB\u7684\u5F00\u59CB\u63D0\u51FA\u4E00\u4E2A\u95EE\u9898: &quot;\u4F60\u4EEC\u6709\u600E\u6837 \u7684 \u60F3\u6CD5 &quot; ( 28 ). \u6309\u7167\u5F53\u65F6\u7684\u4E60\u4FD7, \u5927\u5BB6\u90FD\u4F1A\u8BA4\u4E3A\u90A3\u4E2A\u7B54\u5E94 &quot;\u6211\u53BB&quot; \u7684\u513F\u5B50\u6BD4\u8F83\u597D, \u56E0\u4E3A\u4ED6\u79EF\u6781\u5730\u56DE\u7B54\u4E86\u7236\u4EB2: \u800C\u53E6\u5916\u4E00\u4E2A\u513F\u5B50\u8BF4 &quot;\u6211\u4E0D\u53BB&quot; \u8868\u793A\u4ED6\u6CA1\u6709\u5C0A\u91CD\u7236\u4EB2\u7684\u540D\u8A89. \u4ED6\u7684\u4E0D\u670D\u4ECE\u5C31\u8868\u660E\u4ED6\u7F3A\u5C11\u5BF9\u7236\u4EB2\u7684\u5C0A\u91CD.</p><p>\u4F46\u662F\u8036\u7A23\u968F\u5373\u6539\u53D8\u4E86\u95EE\u9898 &quot;\u8FD9\u4E24\u4E2A\u513F\u5B50, \u8C01\u9075\u884C\u4E86\u7236\u4EB2\u7684\u610F\u613F?&quot; (31). \u4E00\u4E2A\u4EBA\u7684\u5916\u8868\u5E76\u4E0D\u91CD\u8981, \u5173\u952E\u5728\u4E8E\u5176\u5185\u5FC3. \u771F\u6B63\u5149\u8363\u5929\u4E3B\u7684\u4EBA, \u4E0D\u662F\u53EA\u9075\u5B88\u5916\u5728\u7684\u793C\u8282\u548C\u4EEA\u5F0F, \u800C\u662F\u6309\u7167\u888D\u7684\u65E8\u610F\u884C\u4E8B. \u6B63\u7EDF\u6027\u5E76\u4E0D\u80FD\u6EE1\u5168\u7231, \u6295\u8EAB\u4E8E\u5B9E\u8DF5\u624D\u80FD\u6EE1\u5168. \u5728\u8036\u7A23\u65F6\u4EE3, \u90A3\u4E9B\u6CD5\u5F8B\u4E3B\u4E49\u8005\u60EF\u5E38\u5C06&quot;\u6B63\u7EDF\u6027&quot;\u4F5C\u4E3A\u652F\u6491, \u56E0\u6B64, \u4FBF\u5F62\u6210\u4E86\u770B\u91CD\u5916\u8868\u7684\u4E60\u60EF. \u8036\u7A23\u6DF1\u6DF1\u61C2\u5F97, \u5728\u90A3\u4F4D\u900F\u89C6\u4EBA\u5FC3\u7684\u5929\u4E3B\u9762\u524D, \u771F\u6B63\u7684\u513F\u5B50\u662F\u4EE5\u884C\u52A8\u8DF5\u884C\u6B63\u4E49\u7684\u4EBA. \u8FD9\u4E2A\u6BD4\u55BB\u6E05\u695A\u5730\u8BF4\u660E, \u4EBA\u4E0E\u5929\u4E3B\u4E4B\u95F4\u7684\u771F\u6B63\u5173\u7CFB\u5EFA\u7ACB\u5728\u6295\u8EAB\u5B9E\u8DF5\u7684\u57FA\u7840\u4E4B\u4E0A. \u90A3\u79CD\u8868\u9762\u7684\u670D\u4ECE(\u53EA\u662F\u8A00\u8BED\u4E0A\u7684)\u5E76\u4E0D\u80FD\u9020\u5C31\u771F\u6B63\u7684\u5173\u7CFB.</p><h2 id="\u56ED\u6237\u7684\u6BD4\u55BB" tabindex="-1"><a class="header-anchor" href="#\u56ED\u6237\u7684\u6BD4\u55BB" aria-hidden="true">#</a> \u56ED\u6237\u7684\u6BD4\u55BB</h2><blockquote><p><sup>33</sup> \u4F60\u4EEC\u518D\u542C\u4E00\u4E2A\u6BD4\u55BB\u5427! \u4ECE\u524D\u6709\u4E00\u4E2A\u5BB6\u4E3B, \u57F9\u690D\u4E86\u4E00\u4E2A\u8461\u8404\u56ED, \u5468\u56F4\u56F4\u4E0A\u7BF1\u7B06, \u56ED\u5185\u6398\u4E86\u4E00\u4E2A\u69A8\u9152\u6C60, \u7B51\u4E86\u4E00\u4E2A\u5B88\u671B\u53F0, \u628A\u5B83\u79DF\u7ED9\u56ED\u6237, \u5C31\u79BB\u5F00\u4E86\u672C\u56FD. <sup>34</sup> \u5FEB\u5230\u6536\u679C\u5B50\u7684\u65F6\u8282, \u4ED6\u6253\u53D1\u4EC6\u4EBA\u5230\u56ED\u6237\u90A3\u91CC\u53BB\u6536\u679C\u5B50. <sup>35</sup> \u56ED\u6237\u62FF\u4F4F\u4E86\u4EC6\u4EBA, \u5C06\u4E00\u4E2A\u97AD\u6253\u4E86, \u5C06\u4E00\u4E2A\u6740\u4E86, \u5C06\u53E6\u4E00\u4E2A\u7528\u77F3\u5934\u7838\u6B7B\u4E86. <sup>36</sup> \u4ED6\u518D\u6253\u53D1\u4E00\u4E9B\u4EC6\u4EBA\u53BB, \u4EBA\u6570\u6BD4\u4EE5\u524D\u8FD8\u591A; \u56ED\u6237\u4E5F\u4E00\u6837\u5BF9\u5F85\u4E86\u4ED6\u4EEC, <sup>37</sup> \u6700\u540E\u4ED6\u6253\u53D1\u81EA\u5DF1\u7684\u513F\u5B50\u5230\u4ED6\u4EEC\u90A3\u91CC\u53BB, \u8BF4: \u4ED6\u4EEC\u4F1A\u656C\u91CD\u6211\u7684\u513F\u5B50. <sup>38</sup> \u4F46\u56ED\u6237\u4E00\u770B\u89C1\u662F\u513F\u5B50, \u5C31\u5F7C\u6B64\u8BF4: \u8FD9\u662F\u7EE7\u627F\u4EBA; \u6765! \u6211\u4EEC\u6740\u6389\u4ED6, \u6211\u4EEC\u5C31\u80FD\u5F97\u5230\u4ED6\u7684\u4EA7\u4E1A. <sup>39</sup> \u4E8E\u662F\u4ED6\u4EEC\u62FF\u4F4F\u4ED6, \u628A\u4ED6\u63A8\u5230\u56ED\u5916\u6740\u4E86. <sup>40</sup> \u90A3\u4E48, \u5F53\u8461\u8404\u56ED\u7684\u4E3B\u4EBA\u56DE\u6765\u65F6, \u4ED6\u8981\u600E\u6837\u5904\u7F6E\u90A3\u4E9B\u56ED\u6237\u5462? <sup>41</sup> \u4ED6\u4EEC\u56DE\u7B54\u8BF4: &quot;\u8981\u51F6\u6076\u5730\u6D88\u706D\u90A3\u4E9B\u51F6\u6076\u7684\u4EBA, \u628A\u8461\u8404\u56ED\u53E6\u79DF\u7ED9\u6309\u65F6\u7ED9\u4ED6\u7F34\u7EB3\u51FA\u4EA7\u7684\u56ED\u6237.&quot; <sup>42</sup> \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4: &quot;&#39;\u5320\u4EBA\u5F03\u800C\u4E0D\u7528\u7684\u77F3\u5934, \u53CD\u800C\u6210\u4E86\u5C4B\u89D2\u7684\u57FA\u77F3; \u90A3\u662F\u4E0A\u4E3B\u7684\u6240\u884C\u6240\u4E3A, \u5728\u6211\u4EEC\u773C\u4E2D, \u795E\u5999\u83AB\u6D4B&#39;\u7684\u8FD9\u53E5\u7ECF\u6587, \u4F60\u4EEC\u6CA1\u6709\u8BFB\u8FC7\u5417? <sup>43</sup> \u4E3A\u6B64, \u6211\u5BF9\u4F60\u4EEC\u8BF4: \u5929\u4E3B\u7684\u56FD, \u5FC5\u7531\u4F60\u4EEC\u593A\u53BB, \u800C\u4EA4\u7ED9\u7ED3\u679C\u5B50\u7684\u5916\u90A6\u4EBA. \u8DCC\u5728\u8FD9\u77F3\u5934\u4E0A\u7684, \u5FC5\u88AB\u649E\u788E; \u8FD9\u77F3\u5934\u843D\u5728\u8C01\u8EAB\u4E0A, \u5FC5\u8981\u628A\u4ED6\u538B\u788E.&quot; \u53F8\u796D\u957F\u548C\u6CD5\u5229\u585E\u4EBA\u542C\u4E86\u4ED6\u7684\u8FD9\u4E9B\u6BD4\u55BB, \u89C9\u51FA\u4ED6\u662F\u6307\u4ED6\u4EEC\u8BF4\u7684, <sup>46</sup> \u5C31\u60F3\u902E\u4F4F\u4ED6; \u4F46\u5BB3\u6015\u6C11\u4F17, \u56E0\u4E3A\u4ED6\u4EEC\u4EE5\u8036\u7A23\u4E3A\u4E00\u4F4D\u5148\u77E5.</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-5" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-5" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u662F\u4EE5\u55BB\u8BBD\u7684\u5F62\u5F0F\u5199\u51FA\u7684\u6BD4\u55BB, \u662F\u8036\u7A23\u4ECE\u4F9D\u6492\u610F\u4E9A\u5148\u77E5\u4E66\u53D6\u7528\u6765\u7684. \u65E7\u7EA6\u7684\u5148\u77E5\u662F\u4E3B\u4EBA\u6D3E\u9063\u7684\u7B2C\u4E00\u6279\u4EC6\u4EBA, \u800C\u8036\u7A23\u663E\u7136\u7684\u5C31\u662F\u6BD4\u55BB\u4E2D\u6240\u6307\u7684\u513F\u5B50, \u5728\u56ED\u5916\u88AB\u4EBA\u6740\u5BB3 ( \u8036\u7A23\u6B7B\u5728\u57CE\u95E8\u4E4B\u5916. ) \u8036\u7A23\u4E3A\u5929\u4E3B\u7684\u9009\u6C11\u6240\u62D2\u7EDD, \u65E9\u5728\u65E7\u7EA6\u4E2D\u5C61\u88AB\u9884\u8A00 ...... \u8FD9\u4E9B\u7AE0\u8282\u4E5F\u5E38\u5E38\u88AB\u798F\u97F3\u4F5C\u8005\u6240\u5F15\u7528. \u53EA\u6709\u739B\u7AA6\u798F\u97F3\u91CC\u5173\u4E8E\u5929\u56FD\u7684\u8F6C\u79FB ( \u5929\u4E3B\u7684\u4E5F\u5FC5\u7531\u4F60\u4EEC\u4E2D\u593A\u53BB ) \u7684\u8BB0\u5F55. &quot; \u90A3\u7ED3\u679C\u5B50\u7684\u5916\u90A6\u4EBA&quot;, \u662F\u6307\u6559\u4F1A, \u5176\u4E0D\u4F1A\u673D\u574F\u6027\u5728\u8FBE\u5C3C\u5C14\u5148\u77E5\u4E66\u91CC\u88AB\u9884\u8A00. \u739B\u7AA6\u8FD8\u662F\u63D0\u9192\u8BFB\u8005\u6CE8\u610F\u53CD\u5BF9\u8036\u7A23\u7684\u9634\u8C0B\u7684\u6ECB\u957F.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-5" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-5" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u5723\u53F2\u5728\u6B64\u91CD\u7EC4\u4E86\u300A\u9A6C\u5C14\u8C37\u798F\u97F3\u300B\u8FD9\u4E2A\u6BD4\u55BB\u7684\u8BF4\u6CD5 ( \u8C37\u5341\u4E8C 1 ~ 12 ), \u4F7F\u8FD9\u4E2A\u6BD4\u55BB\u66F4\u5177\u5BD3\u610F, \u66F4\u7A81\u663E\u57FA\u7763\u7CBE\u795E. \u8FD9\u662F\u4E00\u4E2A\u5927\u5BB6\u719F\u6089\u7684\u6545\u4E8B, \u5B83\u547C\u5E94\u7740\u300A\u4F9D\u6492\u610F\u4E9A\u5148\u77E5\u4E66\u300B\u91CC\u4E00\u9996 &quot;\u8461\u8404\u56ED\u5BD3\u8A00\u8BD7&quot; ( \u4F9D\u4E94 ), \u4F46\u7ED9\u4E86\u4E00\u4E2A\u65B0\u7684\u7ED3\u5C40. \u5728\u8FD9\u9996 &quot;\u8461\u8404\u56ED\u5BD3\u8A00\u8BD7&quot; \u4E2D, &quot;\u96C5\u5A01&quot; ( \u4E0A\u4E3B ) \u7CBE\u5FC3\u683D\u79CD\u4E86\u4EE5\u8272\u5217\u8FD9\u682A\u8461\u8404\u6811, \u4F46\u662F\u5BF9\u4EE5\u8272\u5217\u751F\u51FA\u7684\u91CE\u8461\u8404\u611F\u5230\u5931\u671B, \u4E8E\u662F\u51B3\u5B9A\u6467\u6BC1\u8FD9\u4E2A\u8461\u8404\u56ED. \u4E0D\u8FC7, \u5728\u8036\u7A23\u7684\u6BD4\u55BB\u4E2D, \u56ED\u6237\u88AB\u6D88\u706D\u4E86; \u8461\u8404\u56ED\u4FDD\u7559\u4E86\u4E0B\u6765\u53E6\u884C\u79DF\u7ED9\u5176\u4ED6\u56ED\u6237.</p><p>\u672B\u4E16\u65F6\u671F ( kairos, 34 \u8282 ) \u8981\u6C42\u73B0\u5728\u5C31\u6709 &quot;\u7ED3\u679C\u5B50&quot; \u7684\u8BC1\u636E ( \u53C2\u9605: \u739B\u4E8C\u5341\u4E00 18 ~ 22 ). \u5BB6\u4E3B\u518D\u4E09\u6253\u53D1\u4EC6\u4EBA ( 34 ~ 39 \u8282 ), \u5C31\u50CF\u5929\u4E3B\u4E0D\u65AD\u6D3E\u9063\u5148\u77E5\u5230\u4EE5\u8272\u5217\u53BB\u90A3\u6837. \u5148\u77E5\u88AB\u79F0\u4E3A\u5929\u4E3B\u7684 &quot;\u4EC6\u4EBA&quot; ( \u8036\u4E03 25; \u4E8C\u5341\u4E94 4; \u4E9A\u4E09 7; \u531D\u4E00 6 ), \u4ED6\u4EEC\u7684\u547D\u8FD0\u5374\u548C\u6BD4\u55BB\u4E2D\u7684 &quot;\u4EC6\u4EBA&quot; \u4E00\u6837 ( \u53C2\u9605: \u8036\u4E8C\u5341 2; \u4E8C\u5341\u516D 20 ~ 23; \u7F16\u4E0B\u4E8C\u5341\u56DB 21 ).</p><p>\u4E8B\u4EF6\u53D1\u751F\u7684\u987A\u5E8F ( 39 \u8282 ), \u6B63\u5BF9\u5E94\u4E86\u8036\u7A23\u53D7\u96BE\u7684\u7EC6\u8282\u8FC7\u7A0B. \u8036\u7A23\u4E5F\u662F\u53D7\u5230\u6349\u62FF ( \u739B\u4E8C\u5341\u516D 50 ), \u5E76\u5E26\u5F80\u57CE\u5916 ( \u739B\u4E8C\u5341\u4E03 31 ~ 32 ), \u7136\u540E\u88AB\u6740\u5BB3 ( \u739B\u4E8C\u5341\u4E03 35 ), \u8461\u8404\u56ED\u6237\u7684\u8C0B\u6740\u8BA1\u753B, \u4E0E\u53F8\u796D\u957F\u548C\u6CD5\u5229\u585E\u4EBA\u5BF9\u8036\u7A23\u7684\u610F\u56FE\u76F8\u5BF9\u5E94 ( \u739B\u4E8C\u5341\u4E00 46: \u4E8C\u5341\u4E8C 15 ). \u53F8\u796D\u957F\u548C\u957F\u8001\u5BA3\u544A\u7684 ( 41 \u8282 ) \u662F\u5BF9\u4ED6\u4EEC\u81EA\u5DF1\u7684\u9063\u8D23, \u4F46\u8FD9\u53E5\u8BDD\u7528\u4E86\u672A\u6765\u7684\u52A8\u8BCD, \u8868\u793A\u53EF\u80FD\u6027\u8FD8\u672A\u51B3\u5B9A: \u72B9\u592A\u7684\u9886\u8896\u4EEC\u4ECD\u6709\u53EF\u80FD\u6539\u53D8\u4ED6\u4EEC\u7684\u5FC3\u610F ( \u5982\u4E8C\u5341\u4E00 29, 32 ), \u4ED6\u4EEC\u4ECD\u53EF\u5728\u4E0E &quot;\u5176\u4ED6\u56ED\u6237&quot; \u4E2D, \u4E5F\u5C31\u662F\u628A\u8461\u8404\u56ED\u53E6\u79DF\u7ED9\u7684\u90A3\u4E9B &quot;\u5176\u4ED6\u56ED\u6237&quot;.</p><p>\u8036\u7A23\u7528 42 \u8282\u8FD9\u53E5\u51FA\u81EA\u300A\u5723\u548F\u300B\u7684\u8BDD\u6240\u63D0\u51FA\u7684\u95EE\u9898 ( \u548F\u4E00\u4E00\u516B 42 ), \u7A81\u663E\u51FA\u8036\u7A23\u4E0E\u654C\u5BF9\u7684\u5B97\u6559\u9886\u8896\u5F7C\u6B64\u4E4B\u95F4, \u5BF9\u4E8E\u5723\u7ECF\u7ECF\u6587\u8BE0\u91CA\u7684\u4E0D\u540C, \u56E0\u800C\u4EA7\u751F\u4E86\u51B2\u7A81 ( \u4E5F\u53C2\u9605: \u739B\u5341\u4E8C 3, 5; \u5341\u4E5D 4; \u4E8C\u5341\u4E00 16 ). \u8FD9\u53E5\u8BDD\u4F7F\u4EBA\u60F3\u5230\u5929\u4E3B\u62E3\u9009\u8FBE\u5473\u4E3A\u541B\u738B, \u8FD9\u6837\u7684\u62E3\u9009\u65B9\u5F0F, \u5BF9\u4EBA\u6765\u8BF4\u4E0D\u53EF\u80FD ( \u4E5F\u4EBA\u6240\u9884\u6599 ), \u5374\u6B63\u662F\u5929\u4E3B\u62E3\u9ED8\u897F\u4E9A\u7684\u65B9\u5F0F, \u5E76\u4E14\u5B83\u8FD8\u662F\u6307\u4ECE\u90A3\u4E9B\u4E0D\u53D7\u91CD\u89C6, \u5E76\u53D7\u5230\u62D2\u7EDD\u7684\u4EBA\u4E2D, \u6240\u4EA7\u751F\u7684\u65B0\u4EE5\u8272\u5217\u5B50\u6C11\u7684\u9886\u5BFC\u8005. \u739B\u7AA6\u5723\u53F2\u5728\u8FD9\u4E00\u6BB5\u7684\u7ED3\u675F\u8BED\u4E2D\u660E\u663E\u8868\u793A, \u53F8\u796D\u957F\u548C\u6CD5\u5229\u585E\u4EBA\u6E05\u695A\u61C2\u5F97\u8036\u7A23\u8FD9\u4E2A\u6BD4\u55BB\u7684\u5BD3\u610F ( 45 ~ 46 \u8282; \u53C2\u9605: \u739B\u5341\u4E09 51 ), \u4F46\u4ED6\u4EEC\u4E0D\u987E\u5BF9\u8036\u7A23\u56DE\u5E94, \u6240\u4EE5\u5E76\u6CA1\u6709\u7559\u610F\u6216\u8003\u8651\u8036\u7A23\u7684\u9080\u8BF7, \u5374\u662F\u5BC6\u8C0B\u6349\u62FF\u7942.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-5" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-5" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u4E2A\u6BD4\u55BB\u8868\u660E\u4E86\u8036\u7A23\u548C\u6C11\u4F17\u9886\u8896\u4E4B\u95F4\u6FC0\u70C8\u7684\u51B2\u7A81. \u8036\u7A23\u65E9\u5DF2\u9884\u89C1\u81EA\u5DF1\u7684\u6B7B\u671F\u5373\u5C06\u6765\u5230, \u4E5F\u6DF1\u77E5\u90A3\u4E9B\u5F53\u6743\u8005\u8981\u4EE5\u66B4\u529B\u5C06\u888D\u63A8\u5411\u6B7B\u5211\u67B6. \u8FD9\u4E9B\u5F53\u6743\u8005\u5C31\u50CF\u8C0B\u6740\u8005, \u5BF9\u7942\u7684\u6B7B\u4EA1\u8D1F\u6709\u4E3B\u8981\u8D23\u4EFB. \u8036\u7A23\u6240\u63D0\u5021\u7684\u4EE5\u5E73\u7B49\u548C\u5144\u5F1F\u95F4\u7684\u53CB\u7231\u8054\u5408\u5728\u4E00\u8D77\u7684\u7406\u60F3\u793E\u4F1A, \u4ECE\u6839\u672C\u4E0A\u76F8\u53CD\u5F53\u65F6\u7684\u4F53\u5236\u7684\u5229\u76CA. \u6BD4\u55BB\u5E26\u6709\u5C16\u523B\u7684\u8BBD\u523A\u610F\u5473, \u603B\u7ED3\u4E86\u4EE5\u8272\u5217\u7684\u6574\u4E2A\u5386\u53F2: \u4EE5\u4E00\u4E2A\u8C26\u900A\u7684\u4F63\u5DE5\u5F62\u8C61\u5F00\u59CB, \u5374\u4EE5\u4E00\u4E2A\u6076\u610F\u6740\u5BB3\u8036\u7A23\u7684\u7F6A\u72AF\u5F62\u8C61\u7EC8\u7ED3.</p><p>\u4EE5\u8272\u5217\u7684\u9886\u5BFC\u4EEC\u5E76\u6CA1\u6709\u597D\u597D\u5730\u5F00\u57A6\u8461\u8404\u56ED, \u51C6\u5907\u9ED8\u897F\u4E9A\u7684\u6765\u4E34. \u4ED6\u4EEC\u5360\u6709\u4EBA\u6C11\u7684\u8D22\u4EA7, \u5E76\u4EE5\u4ED6\u4EEC\u7684\u540D\u4E49\u51B3\u5B9A\u5904\u6B7B\u8036\u7A23, \u56E0\u4E3A\u8036\u7A23\u593A\u53D6\u4E86\u4ED6\u4EEC\u63A7\u5236\u7EAF\u6734\u4EBA\u6C11\u7684\u6743\u529B. \u90A3\u4E2A\u7EE7\u627F\u4EBA\u88AB\u6740\u4E8E\u8461\u8404\u56ED\u4E4B\u5916, \u8036\u7A23\u4E5F\u540C\u6837\u6B7B\u4E8E\u8036\u8DEF\u6492\u51B7\u4E4B\u5916. \u4ECE\u6B64, \u8FD9\u4E9B\u72B9\u592A\u9886\u5BFC\u4EBA\u5BF9\u4E8E\u65B0\u7684\u5929\u4E3B\u5B50\u6C11\u518D\u65E0\u4EFB\u4F55\u6743\u529B, \u56E0\u4E3A\u8FD9\u4E2A\u6743\u529B\u5DF2\u7ECF\u8F6C\u5230\u4E86\u8036\u7A23\u95E8\u5F92\u4EEC\u7684\u624B\u4E2D. \u4ECE\u516C\u5143 70 \u5E74\u8036\u8DEF\u6492\u51B7\u88AB\u6BC1\u7684\u65F6\u5019\u8D77, \u8FD9\u4E2A\u6BD4\u55BB\u6709\u4E86\u4E00\u5C42\u7279\u6B8A\u7684\u6DB5\u4E49.</p><p>\u65E9\u671F\u7684\u6559\u4F1A\u548C\u6211\u4EEC\u4ECA\u5929\u7684\u6559\u4F1A\u90FD\u77E5\u9053, \u6559\u4F1A\u7684\u5B58\u5728\u4EE5\u53CA\u5176\u5B58\u5728\u7684\u7406\u7531\u662F\u57FA\u4E8E\u5BF9\u8036\u7A23\u4F7F\u547D\u7684\u5FE0\u4FE1, \u5C31\u662F\u4F7F\u5168\u65B0\u7684\u5929\u4E3B\u7684\u738B\u6743\u4E34\u4E8E\u4ECA\u65E5\u4E16\u754C. \u8036\u7A23\u5DF2\u7ECF\u89E3\u91CA\u4E86\u8FD9\u79CD\u738B\u6743, \u4E0D\u5728\u4E8E\u58A8\u5B88\u6210\u89C4\u7684\u6B63\u7EDF\u6027, \u800C\u5728\u4E8E\u770B\u987E\u4E16\u4E0A\u8D2B\u7A77\u548C\u4E00\u65E0\u6240\u6709\u7684\u4EBA.</p>',71),p=[h];function r(s,d){return a(),t("div",null,p)}var i=u(o,[["render",r],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E00\u7AE0.html.vue"]]);export{i as default};
