import{_ as u,o,c as t,e as p}from"./app.6bf529a6.js";const e={},a=p('<h1 id="\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E09\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E09\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E09\u7AE0</h1><h2 id="\u75DB\u65A5\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u4EBA" tabindex="-1"><a class="header-anchor" href="#\u75DB\u65A5\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u4EBA" aria-hidden="true">#</a> \u75DB\u65A5\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u4EBA</h2><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>1</sup> \u90A3\u65F6\uFF0C\u8036\u7A23\u5BF9\u6C11\u4F17\u548C\u4ED6\u7684\u95E8\u5F92\u8BB2\u8BBA\u8BF4\uFF1A<sup>2</sup> \u201C\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u4EBA\u5750\u5728\u6885\u745F\u7684\u8BB2\u5EA7\u4E0A\uFF1A<sup>3</sup> \u51E1\u4ED6\u4EEC\u5BF9\u4F60\u4EEC\u6240\u8BF4\u7684\uFF0C\u4F60\u4EEC\u8981\u884C\u8981\u5B88\uFF1B\u4F46\u4E0D\u8981\u7167\u4ED6\u4EEC\u7684\u884C\u4E3A\u53BB\u505A\uFF0C\u56E0\u4E3A\u4ED6\u4EEC\u53EA\u8BF4\u4E0D\u505A\u3002 <sup>4</sup> \u4ED6\u4EEC\u628A\u6C89\u91CD\u800C\u96BE\u4EE5\u8D1F\u8377\u7684\u62C5\u5B50\u6346\u597D\uFF0C\u653E\u5728\u4EBA\u7684\u80A9\u4E0A\uFF0C\u81EA\u5DF1\u5374\u4E0D\u80AF\u7528\u4E00\u4E2A\u6307\u5934\u52A8\u4E00\u4E0B\u3002<sup>5</sup> \u4ED6\u4EEC\u6240\u505A\u7684\u4E00\u5207\u5DE5\u4F5C\u90FD\u662F\u4E3A\u53EB\u4EBA\u770B\uFF1B\u4E3A\u6B64\uFF0C\u4ED6\u4EEC\u628A\u7ECF\u5323\u653E\u5BBD\uFF0C\u8863\u7A57\u52A0\u957F\uFF1B<sup>6</sup> \u4ED6\u4EEC\u53C8\u559C\u7231\u7B75\u5E2D\u4E0A\u7684\u9996\u4F4D\uFF0C\u4F1A\u5802\u4E2D\u7684\u4E0A\u5EA7\uFF1B<sup>7</sup> \u559C\u7231\u4EBA\u5728\u8857\u5E02\u4E0A\u5411\u4ED6\u4EEC\u81F4\u656C\uFF0C\u79F0\u4ED6\u4EEC\u4E3A\u201C\u8FA3\u5F7C\u201D\u3002 <sup>8</sup> \u81F3\u4E8E\u4F60\u4EEC\uFF0C\u5374\u4E0D\u8981\u88AB\u79F0\u4E3A\u201C\u8FA3\u5F7C\u201D\uFF0C\u56E0\u4E3A\u4F60\u4EEC\u7684\u5E08\u5085\u53EA\u6709\u4E00\u4F4D\uFF0C\u4F60\u4EEC\u4F17\u4EBA\u90FD\u662F\u5144\u5F1F\uFF1B<sup>9</sup> \u4E5F\u4E0D\u8981\u5728\u5730\u4E0A\u79F0\u4EBA\u4E3A\u4F60\u4EEC\u7684\u7236\uFF0C\u56E0\u4E3A\u4F60\u4EEC\u7684\u7236\u53EA\u6709\u4E00\u4F4D\uFF0C\u5C31\u662F\u5929\u4E0A\u7684\u7236\u3002<sup>10</sup> \u4F60\u4EEC\u4E5F\u4E0D\u8981\u88AB\u79F0\u4E3A\u5BFC\u5E08\uFF0C\u56E0\u4E3A\u4F60\u4EEC\u7684\u5BFC\u5E08\u53EA\u6709\u4E00\u4F4D\uFF0C\u5C31\u662F\u9ED8\u897F\u4E9A\u3002<sup>11</sup> \u4F60\u4EEC\u4E2D\u90A3\u6700\u5927\u7684\uFF0C\u8BE5\u4F5C\u4F60\u4EEC\u7684\u4EC6\u5F79\u3002<sup>12</sup> \u51E1\u9AD8\u4E3E\u81EA\u5DF1\u7684\uFF0C\u5FC5\u88AB\u8D2C\u6291\uFF1B\u51E1\u8D2C\u6291\u81EA\u5DF1\u7684\uFF0C\u5FC5\u88AB\u9AD8\u4E3E\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6574\u4E2A\u739B\u4E8C\u5341\u4E09, \u90FD\u662F\u8BB0\u8FF0\u8036\u7A23\u5C16\u9510\u5730\u6307\u8D23\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u4EBA; \u739B\u7AA6\u5723\u53F2\u5728\u672C\u798F\u97F3\u4E2D, \u628A\u4ED6\u4EEC\u5851\u9020\u6210\u8036\u7A23\u7684\u654C\u5BF9\u8005. \u5728\u6B64, \u739B\u7AA6\u6536\u96C6\u4E86\u300A\u739B\u5C14\u8C37\u798F\u97F3\u300B\u4E2D\u5BF9\u7ECF\u5E08\u7684\u7B80\u8981\u6279\u5224 ( \u8C37\u5341\u4E8C 38 ~ 40 ), \u540C\u65F6\u52A0\u63D2\u4E86\u4E00\u4E9B\u6765\u81EA &quot;Q \u6E90\u6D41&quot; ( \u9664\u4E86\u9A6C\u5C14\u8C37\u5916, \u739B\u7AA6\u548C\u8DEF\u52A0\u5E94\u7528\u4E86\u53E6\u5916\u4E00\u4E2A\u5DF2\u5931\u4F20\u7684\u6E90\u6D41, \u8FD9\u5047\u5B9A\u7684\u6E90\u6D41\u88AB\u79F0\u4E3A Q, \u53C8\u56F0\u8FD9\u4E2A\u6E90\u6D41\u7684\u5185\u5BB9\u51E0\u4E4E\u5168\u662F\u8036\u7A23\u7684\u8BF4\u8BDD, \u6545\u53C8\u79F0\u4E3A &quot;\u8BED\u5F55\u6E90\u6D41&quot; ) \u53CA \u300A\u8DEF\u52A0\u798F\u97F3\u300B \u7684\u8D44\u6599 ( \u8DEF\u5341\u4E00 37 ~ 52 ), \u4EE5\u81EA\u5DF1\u7684\u65B9\u5F0F\u91CD\u65B0\u7F16\u5199\u51FA\u300A\u739B\u7AA6\u798F\u97F3\u300B\u4E2D\u7684\u4E00\u8FD9\u4E00\u7247\u6BB5.</p><p>\u5728\u65B0\u7EA6\u4F5C\u54C1\u4E2D, \u7ECF\u5E08\u662F\u7CBE\u901A\u300A\u6885\u745F\u4E94\u4E66\u300B\u7684\u5B97\u6559\u9886\u8896. \u6CD5\u5229\u585E\u4EBA\u662F\u5E73\u4FE1\u5F92\u7684\u5B97\u6559\u9886\u5BFC\u8005, \u4ED6\u4EEC\u4E0E\u6492\u675C\u585E\u4EBA\u4E4B\u95F4, \u4E0D\u53EA\u5BF9\u6B7B\u540E\u590D\u6D3B\u7684\u4FE1\u5FF5\u4E0D\u540C ( \u53C2\u9605: \u739B\u4E8C\u5341\u4E8C 23 ~ 33 ), \u5BF9\u5176\u4ED6\u4E00\u4E9B\u53E3\u4F20\u6CD5\u5F8B\u7684\u8BA4\u77E5\u4E5F\u4E0D\u76F8\u540C. \u739B\u7AA6\u5723\u53F2\u5728\u6B64\u63CF\u8FF0\u8036\u7A23\u8C34\u8D23\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u4EBA\u7684\u53E3\u6C14\u975E\u5E38\u4E25\u5389, \u8FD9\u70B9\u53CD\u6620\u51FA\u739B\u7AA6\u6240\u5904\u7684\u56E2\u4F53, \u662F\u72B9\u592A\u4EBA\u5360\u5927\u591A\u6570, \u800C\u4E14\u662F\u7531\u72B9\u592A\u57FA\u7763\u5F92\u6240\u4E3B\u5BFC, \u56E0\u6B64, \u4ED6\u4EEC\u4E0E\u6709\u4F20\u7EDF\u72B9\u592A\u7ECF\u5E08\u7406\u5FF5\u7684\u72B9\u592A\u4EBA\u4E4B\u95F4, \u53D1\u751F\u4E86\u5F88\u6FC0\u70C8\u7684\u51B2\u7A81.</p><p>\u56E0\u6B64, \u8036\u7A23\u62C5\u5F53\u4E86\u4E00\u4E2A\u9009\u77E5\u7684\u89D2\u8272, \u72B9\u5982\u4E9A\u6BDB\u65AF ( \u4E9A\u4E94 18 ~ 20; \u516D 1 ~ 7 ), \u4F9D\u6492\u610F\u4E9A ( \u4F9D\u4E94 8 ~ 10, 11 ~ 14 ) \u7B49\u4EBA\u4E00\u6837, \u7528 &quot;\u7978\u54C9&quot; \u8FD9\u7ECF\u5178\u5F62\u5F0F\u6765\u65A5\u8D23\u7942\u5B50\u6C11\u7684\u8FC7\u72AF, \u60F3\u8981\u4ECE\u7F6A\u6076\u4E2D\u4F7F\u5B50\u6C11\u6539\u53D8, \u4ECE\u800C\u4E0E\u5929\u4E3B\u5EFA\u7ACB\u8D77\u826F\u597D\u7684\u5173\u7CFB. \u8036\u7A23\u7684\u8BDD, \u662F\u8B66\u544A\u7FA4\u4F17\u548C\u95E8\u5F92\u4E0D\u8981\u6548\u6CD5\u8FD9\u4E9B\u5B97\u6559\u9886\u8896\u53EA\u8BF4\u4E0D\u505A\u7684\u4F2A\u5584\u884C\u4E3A.</p><p>\u4E0E\u8036\u7A23\u67D4\u548C\u7684\u8F6D ( \u739B\u5341\u4E00 30 ) \u4E0D\u540C, \u6CD5\u5229\u585E\u4EBA &quot;\u628A\u6C89\u91CD\u800C\u96BE\u4EE5\u8D1F\u8377\u7684\u62C5\u5B50\u6346\u653E\u5728\u4EBA\u7684\u80A9\u4E0A&quot;. \u6CD5\u5229\u585E\u4EBA\u6539\u88C5\u7ECF\u5323\u662F\u7528\u76AE\u505A\u6210\u7684\u76D2\u5B50, \u91CC\u9762\u653E\u6709\u5199\u4E86\u5723\u7ECF\u53E5\u5B50\u7684\u7F8A\u76AE\u7EB8, \u4EE5\u8272\u5217\u5B50\u6C11\u5728\u505A\u6668\u7977\u65F6, \u5C31\u628A\u7ECF\u5323\u7CFB\u5728\u524D\u989D\u548C\u624B\u8155\u4E0A, \u7ECF\u5323\u5185\u7F8A\u76AE\u7EB8\u4E0A\u6240\u5199\u7684\u7ECF\u53E5, \u5305\u62EC\u300A\u51FA\u8C37\u8BB0\u300B, \u300A\u7533\u547D\u7EAA\u300B\u7B49 ( \u8B6C\u5982: \u51FA\u5341\u4E09 1 ~ 16; \u7533\u516D 4 ~ 9; \u5341\u4E00 13 ~ 22 ). \u72B9\u592A\u4EBA\u4F69\u6234 &quot;\u8863\u7E78&quot;, \u6216\u5728\u5916\u8863\u8FB9\u89D2\u7F1D\u4E0A &quot;\u7E78\u5934&quot;, \u662F\u4E3A\u4E86\u53EB\u4ED6\u4EEC &quot;\u4E00\u770B\u89C1, \u5C31\u60F3\u8D77\u4E0A\u4E3B\u7684\u8BEB\u547D, \u4F9D\u7167\u9075\u884C&quot; ( \u6237\u5341\u4E94 38 ~ 39; \u7533\u4E8C\u5341\u4E8C 12; \u739B\u4E5D 20: \u5341\u56DB 36 ).</p><p>\u8036\u7A23\u6279\u8BC4\u5B97\u6559\u9886\u8896\u8D2A\u6155\u5C0A\u8D35\u7684\u5730\u4F4D\u53CA\u540D\u8854 ( 6 ~ 10 \u8282 ), \u800C\u8FD9\u4E9B\u540D\u8854\u53EA\u6709\u7942\u548C\u5929\u4E3B\u624D\u914D\u62E5\u6709. \u5728\u521D\u671F\u57FA\u7763\u6559\u4F1A\u5185, \u4E5F\u50CF\u8BB8\u591A\u63A8\u52A8\u6539\u9769\u8FD0\u52A8\u7684\u65F6\u4EE3\u4E00\u6837, \u66FE\u6709\u4E00\u6CE2\u63A8\u52A8 &quot;\u5E73\u7B49\u8FD0\u52A8&quot; \u53CA &quot;\u5730\u4F4D\u9006\u8F6C&quot; \u7684\u98CE\u6F6E ( 11 ~ 12 \u8282: \u53C2\u9605: \u739B\u5341\u516B 1 ~ 4; \u5341\u4E5D 13 ~ 15; \u4E8C\u5341\u4E8C 20 ~ 28 ).</p><h2 id="\u4E03\u7978\u54C9" tabindex="-1"><a class="header-anchor" href="#\u4E03\u7978\u54C9" aria-hidden="true">#</a> \u4E03\u7978\u54C9</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>13</sup> \u201C\u7978\u54C9\uFF0C\u4F60\u4EEC\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u5047\u5584\u4EBA\uFF01\u56E0\u4E3A\u4F60\u4EEC\u7ED9\u4EBA\u5C01\u95ED\u4E86\u5929\u56FD\uFF1A\u4F60\u4EEC\u4E0D\u8FDB\u53BB\uFF0C\u4E5F\u4E0D\u8BA9\u613F\u610F\u8FDB\u53BB\u7684\u4EBA\u8FDB\u53BB\u3002<sup>14</sup> \u3010\u7978\u54C9\uFF0C\u4F60\u4EEC\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u5047\u5584\u4EBA\uFF01\u56E0\u4E3A\u4F60\u4EEC\u541E\u6CA1\u4E86\u5BE1\u5987\u7684\u5BB6\u4EA7\uFF0C\u800C\u4EE5\u957F\u4E45\u7684\u7948\u7977\u4F5C\u63A9\u9970\uFF0C\u4E3A\u6B64\uFF0C\u4F60\u4EEC\u5FC5\u8981\u906D\u53D7\u66F4\u91CD\u7684\u5904\u7F5A\u3002\u3011<sup>15</sup> \u7978\u54C9\uFF0C\u4F60\u4EEC\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u5047\u5584\u4EBA\uFF01\u56E0\u4E3A\u4F60\u4EEC\u8D70\u904D\u4E86\u6D77\u6D0B\u548C\u9646\u5730\uFF0C\u4E3A\u4F7F\u4E00\u4E2A\u4EBA\u6210\u4E3A\u5F52\u4F9D\u8005\uFF1B\u51E0\u65F6\u4ED6\u6210\u4E86\uFF0C\u4F60\u4EEC\u53CD\u800C\u4F7F\u4ED6\u6210\u4E3A\u4E00\u4E2A\u6BD4\u4F60\u4EEC\u52A0\u500D\u574F\u7684\u2018\u5730\u72F1\u4E4B\u5B50\u2019\u3002</p></blockquote><blockquote><p><sup>16</sup> \u7978\u54C9\uFF0C\u4F60\u4EEC\u778E\u773C\u7684\u5411\u5BFC\uFF01\u4F60\u4EEC\u8BF4\uFF1A\u8C01\u82E5\u6307\u7740\u5723\u6240\u8D77\u8A93\uFF0C\u4E0D\u7B97\u4EC0\u4E48\uFF1B\u4F46\u662F\u8C01\u82E5\u6307\u7740\u5723\u6240\u7684\u91D1\u5B50\u8D77\u8A93\uFF0C\u5C31\u8BE5\u8FD8\u613F\u3002<sup>17</sup> \u53C8\u660F\u53C8\u778E\u7684\u4EBA\u54EA\uFF01\u7A76\u7ADF\u4EC0\u4E48\u66F4\u8D35\u91CD\uFF1A\u662F\u91D1\u5B50\u6216\u662F\u90A3\u4F7F\u91D1\u5B50\u6210\u5723\u7684\u5723\u6240\uFF1F<sup>18</sup> \u8FD8\u6709\uFF1A\u8C01\u82E5\u6307\u7740\u5168\u71D4\u796D\u575B\u8D77\u8A93\uFF0C\u4E0D\u7B97\u4EC0\u4E48\uFF1B\u4F46\u662F\u8C01\u82E5\u6307\u7740\u90A3\u4E0A\u9762\u7684\u4F9B\u7269\u8D77\u8A93\uFF0C\u5C31\u8BE5\u8FD8\u613F\u3002<sup>19</sup> \u778E\u773C\u7684\u4EBA\u54EA\uFF01\u7A76\u7ADF\u4EC0\u4E48\u66F4\u8D35\u91CD\uFF1A\u662F\u4F9B\u7269\u6216\u662F\u90A3\u4F7F\u4F9B\u7269\u6210\u5723\u7684\u5168\u71D4\u796D\u575B\uFF1F<sup>20</sup> \u6240\u4EE5\uFF0C\u90A3\u6307\u7740\u5168\u71D4\u796D\u575B\u8D77\u8A93\u7684\uFF0C\u662F\u6307\u7740\u5B83\u548C\u90A3\u4E0A\u9762\u7684\u4E00\u5207\u8D77\u8A93\uFF0C<sup>21</sup> \u90A3\u6307\u7740\u5723\u6240\u8D77\u8A93\u7684\uFF0C\u662F\u6307\u7740\u5B83\u548C\u90A3\u4F4F\u5728\u5B83\u5185\u7684\u800C\u8D77\u8A93\uFF1B<sup>22</sup> \u90A3\u6307\u7740\u5929\u8D77\u8A93\u7684\uFF0C\u662F\u6307\u7740\u5929\u4E3B\u7684\u5B9D\u5EA7\u548C\u5750\u5728\u4E0A\u9762\u7684\u90A3\u4F4D\u8D77\u8A93\u3002</p></blockquote><blockquote><p><sup>23</sup> \u7978\u54C9\uFF0C\u4F60\u4EEC\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u5047\u5584\u4EBA\uFF01\u56E0\u4E3A\u4F60\u4EEC\u6350\u732E\u5341\u5206\u4E4B\u4E00\u7684\u8584\u8377\u3001\u8334\u9999\u548C\u83B3\u841D\uFF0C\u5374\u653E\u8FC7\u4E86\u6CD5\u5F8B\u4E0A\u6700\u91CD\u8981\u7684\u516C\u4E49\u3001\u4EC1\u7231\u4E0E\u4FE1\u4E49\uFF1B\u8FD9\u4E9B\u56FA\u7136\u8BE5\u4F5C\uFF0C\u90A3\u4E9B\u4E5F\u4E0D\u53EF\u653E\u8FC7\u3002<sup>24</sup> \u778E\u773C\u7684\u5411\u5BFC\uFF01\u4F60\u4EEC\u6EE4\u51FA\u868A\u868B\uFF0C\u5374\u541E\u4E0B\u4E86\u9A86\u9A7C\u3002</p></blockquote><blockquote><p><sup>25</sup> \u7978\u54C9\uFF0C\u4F60\u4EEC\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u5047\u5584\u4EBA\uFF01\u56E0\u4E3A\u4F60\u4EEC\u6D17\u64E6\u676F\u76D8\u7684\u5916\u9762\uFF0C\u91CC\u9762\u5374\u6EE1\u662F\u52AB\u593A\u4E0E\u8D2A\u6B32\u3002<sup>26</sup> \u778E\u773C\u7684\u6CD5\u5229\u585E\u4EBA\uFF01\u4F60\u5148\u5E94\u6E05\u6D01\u676F\u7684\u91CC\u9762\uFF0C\u597D\u53EB\u5B83\u5916\u9762\u4E5F\u6210\u4E3A\u6E05\u6D01\u7684\u3002</p></blockquote><blockquote><p><sup>27</sup> \u7978\u54C9\uFF0C\u4F60\u4EEC\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u5047\u5584\u4EBA\uFF01\u56E0\u4E3A\u4F60\u4EEC\u597D\u50CF\u7528\u77F3\u7070\u5237\u767D\u7684\u575F\u5893\uFF1A\u5916\u9762\u770B\u6765\u5012\u534E\u4E3D\uFF0C\u91CC\u9762\u5374\u6EE1\u662F\u6B7B\u8005\u7684\u9AA8\u9AB8\u548C\u5404\u6837\u7684\u6C61\u79FD\u3002<sup>28</sup> \u540C\u6837\uFF0C\u4F60\u4EEC\u5916\u9762\u53EB\u4EBA\u770B\u6765\u5012\u50CF\u4E49\u4EBA\uFF0C\u4F60\u4EEC\u91CC\u9762\u5374\u6EE1\u662F\u865A\u4F2A\u548C\u4E0D\u6CD5\u3002</p></blockquote><blockquote><p><sup>29</sup> \u7978\u54C9\uFF0C\u4F60\u4EEC\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u5047\u5584\u4EBA\uFF01\u56E0\u4E3A\u4F60\u4EEC\u4FEE\u5EFA\u5148\u77E5\u7684\u575F\uFF0C\u88C5\u9970\u4E49\u4EBA\u7684\u5893\uFF0C<sup>30</sup> \u4E14\u8BF4\uFF1A\u5047\u4F7F\u6211\u4EEC\u751F\u5728\u6211\u4EEC\u7956\u5148\u7684\u65F6\u65E5\uFF0C\u6211\u4EEC\u51B3\u4E0D\u662F\u4ED6\u4EEC\u6D41\u5148\u77E5\u8840\u7684\u4F19\u4F34\uFF1B<sup>31</sup> \u8FD9\u6837\u4F60\u4EEC\u81EA\u5DF1\u4F5C\u8BC1\uFF0C\u4F60\u4EEC\u662F\u90A3\u4E9B\u6B8B\u6740\u5148\u77E5\u8005\u7684\u5B50\u5B59\u4E86\u3002<sup>32</sup> \u4F60\u4EEC\u5C31\u6765\u8865\u8DB3\u4F60\u4EEC\u7956\u5148\u4F5C\u6076\u7684\u5C3A\u5EA6\u5427\uFF01<sup>33</sup> \u86C7\u554A\uFF01\u6BD2\u86C7\u7684\u79CD\u7C7B\uFF0C\u4F60\u4EEC\u600E\u80FD\u9003\u907F\u5730\u72F1\u7684\u5904\u7F5A\uFF1F<sup>34</sup> \u4E3A\u6B64\uFF0C\u770B\uFF01\u6211\u6253\u53D1\u5148\u77E5\u3001\u8D24\u54F2\u548C\u7ECF\u5E08\u5230\u4F60\u4EEC\u8FD9\u91CC\u6765\uFF0C\u5176\u4E2D\u6709\u7684\u4F60\u4EEC\u8981\u6740\u6B7B\uFF0C\u6709\u7684\u4F60\u4EEC\u8981\u9489\u6B7B\uFF0C\u6709\u7684\u4F60\u4EEC\u8981\u5728\u4F60\u4EEC\u7684\u4F1A\u5802\u4E2D\u97AD\u6253\uFF0C\u6709\u7684\u4F60\u4EEC\u8981\u7531\u4E00\u57CE\u5E02\u8FFD\u903C\u5230\u53E6\u4E00\u57CE\u5E02\uFF0C<sup>35</sup> \u597D\u53EB\u6D41\u5728\u5730\u4E0A\u7684\u4E00\u5207\u4E49\u4EBA\u7684\u8840\uFF0C\u81EA\u4E49\u4EBA\u4E9A\u4F2F\u5C14\u7684\u8840\uFF0C\u76F4\u5230\u4F60\u4EEC\u66FE\u5728\u5723\u6240\u4E0E\u5168\u71D4\u796D\u575B\u95F4\uFF0C\u6240\u6740\u7684\u8D1D\u52D2\u57FA\u96C5\u7684\u513F\u5B50\u5219\u52A0\u9ECE\u96C5\u7684\u8840\uFF0C\u90FD\u5F52\u5230\u4F60\u4EEC\u8EAB\u4E0A\u3002<sup>36</sup> \u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u8FD9\u4E00\u5207\u90FD\u8981\u5F52\u5230\u8FD9\u4E00\u4EE3\u4EBA\u8EAB\u4E0A\u3002\u201D</p></blockquote><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5230\u6B64, \u57FA\u7763\u5F92\u56E2\u4F53\u548C\u72B9\u592A\u5B97\u6559\u6743\u5A01\u4E4B\u95F4\u7684\u4E89\u8BBA\u5DF2\u7ECF\u8FBE\u5230\u9AD8\u5CF0. \u8FD9\u4E00\u6BB5\u53CD\u6620\u5F53\u65F6\u7684\u57FA\u7763\u5F92\u56E2\u4F53, \u53EF\u80FD\u5DF2\u7ECF\u4ECE\u72B9\u592A\u56E2\u4F53\u4E2D\u88AB\u9010\u51FA. \u8FD9\u6BB5\u8FD0\u7528\u8FA9\u8BBA\u6027\u7684\u6587\u5B66\u4F53\u88C1, \u8BF4\u660E\u5728\u5BF9\u654C\u4EBA\u7684\u63CF\u8FF0\u65F6\u6709\u6240\u5938\u5927\u6216\u7B80\u5316, \u5176\u4E2D\u67D0\u4E9B\u63CF\u8FF0\u7684\u7279\u5F81\u6BD4\u6F2B\u753B\u66F4\u5177\u8BBD\u523A\u610F\u5473. \u540C\u65F6\u671F\u7684\u54F2\u5B66\u8FA9\u8BBA\u6587\u732E\u4E5F\u6709\u7C7B\u4F3C\u7684\u4E89\u8BBA. \u6240\u4EE5, \u8FD9\u6BB5\u6587\u5B57\u672C\u8EAB\u53D7\u5230\u4E86\u6587\u5B66\u7C7B\u578B\u548C\u73AF\u5883\u7684\u9650\u5236, \u6211\u4EEC\u4E5F\u9700\u8981\u4ECE\u8FD9\u4E24\u65B9\u9762\u6765\u7406\u89E3.</p><p>\u8FD9\u91CC\u5173\u4E8E\u6CD5\u5229\u585E\u5165\u548C\u6CD5\u5B66\u58EB\u7684\u63CF\u8FF0\u4E0E\u8BC4\u4EF7, \u8DDF\u6211\u4EEC\u4ECE\u5176\u4ED6\u8D44\u6599\u770B\u5230\u7684\u5E76\u4E0D\u5B8C\u5168\u7B26\u5408. \u53E6\u5916, \u6709\u5173\u7ECF\u6587\u91CC\u5178\u578B\u4EBA\u7269\u7684\u8BB2\u8FF0, \u5F88\u53EF\u80FD, \u4E5F\u5F88\u5BB9\u6613\u6765\u6E90\u4E8E\u5176\u4ED6\u5B97\u6559\u56E2\u4F53\u7684\u8D44\u6599, \u5F53\u7136\u8FD9\u4E5F\u5305\u62EC\u4ED6\u4EEC\u81EA\u5DF1\u7684\u56E2\u4F53. \u8FD9\u6BB5\u6559\u8BAD\u9488\u5BF9\u7684\u662F\u5F53\u65F6\u7684\u56E2\u4F53 &quot;\u548C\u886A\u7684\u95E8\u5F92\u4EEC&quot;</p><p>\u6700\u7EC8, \u8036\u7A23\u7684\u8BDD\u53EF\u4EE5\u88AB\u5F53\u4F5C\u5BF9\u5404\u65F6\u4EE3\u7684\u95E8\u5F92\u7684\u8B66\u544A, \u56E0\u4E3A\u6BCF\u4E2A\u65F6\u4EE3\u90FD\u6709\u53EF\u80FD\u9677\u5165\u8FD9\u91CC\u6240\u8C34\u8D23\u7684\u7F6A\u6076: \u4E13\u6A2A\u9738\u9053\u3001\u5F3A\u4EBA\u6240\u96BE\u3001\u4E3A\u5B88\u6CD5\u5F8B\u800C\u53D8\u5F97\u865A\u4F2A\u987D\u56FA\u3001\u4E0D\u61C2\u5F97\u95EE\u9898\u7684\u8F7B\u91CD\u7F13\u6025, \u6700\u91CD\u8981\u7684\u662F\u8A00\u884C\u4E0D\u4E00\u81F4\u3001 \u5F92\u6709\u865A\u540D. \u8FD9\u79CD\u5E38\u89C1\u7684\u4EBA\u7684\u4F2A\u5584, \u5728\u8FD9\u91CC\u88AB\u5F7B\u5E95\u5730\u63ED\u9732\u51FA\u6765.</p><p>\u5B97\u6559\u662F\u5173\u4E8E\u4EBA\u5FC3\u7684\u4E8B\u60C5, \u4E0D\u7BA1\u662F\u4ECE\u7EB5\u5411 ( \u5373\u4EBA\u4E0E\u5929\u4E3B\u4E4B\u95F4\u7684\u5173\u7CFB ) \u6765\u8BB2, \u8FD8\u662F\u4ECE\u6A2A\u5411 ( \u5373\u6D89\u53CA\u4EBA\u4E0E\u4EBA\u4E4B\u95F4\u7684\u5173\u7CFB ) \u800C\u8A00. \u5982\u679C\u6CA1\u6709\u8FD9\u4E9B\u56E0\u7D20\u5728\u5185, \u5B97\u6559\u5C31\u4F1A\u53D8\u6210\u8352\u8BDE\u7684\u7B11\u8BDD, \u53D8\u6210\u5974\u5F79\u4EBA\u3001\u675F\u7F1A\u4EBA\u3001\u4EE4\u4EBA\u7A92\u606F\u7684\u4E1C\u897F.</p><p>\u8036\u7A23\u4E0D\u4EC5\u5C0A\u91CD\u6CD5\u5F8B, \u886A\u6765\u66F4\u662F\u4E3A\u4F7F\u6CD5\u5F8B\u5F97\u4EE5\u5706\u6EE1, \u4F7F\u5176\u66F4\u6709\u610F\u4E49. \u886A\u595A\u843D\u6CD5\u5229\u585E\u5F0F\u7684\u4F2A\u5584\u7406\u89E3\u548C\u8BE0\u91CA. \u8036\u7A23\u5BF9\u4E8E\u6CD5\u5F8B\u7684\u6279\u8BC4, \u5E76\u4E0D\u662F\u76F4\u63A5\u9488\u5BF9\u6CD5\u5F8B\u672C\u8EAB, \u800C\u662F\u9488\u5BF9\u90A3\u4E9B\u7528\u6CD5\u5F8B\u4F5C\u501F\u53E3, \u63A9\u9970\u81EA\u5DF1\u4E0D\u53EF\u544A\u4EBA\u7684\u6B32\u671B\u7684\u4EBA. \u4EBA\u7684\u5185\u5FC3\u6700\u91CD\u8981, \u5176\u6B21\u624D\u662F\u7531\u5185\u800C\u751F\u7684\u5916\u5728\u884C\u4E3A. \u4EBA\u7684\u5FC3\u7075\u5FC5\u987B\u7ECF\u7531\u5929\u4E3B\u5723\u8A00\u51C0\u5316 ( \u82E5 15 :3 ), \u5E76\u4E14\u5BF9\u6B64\u5723\u8A00\u4F5C\u51FA\u56DE\u5E94, \u65E0\u8BBA\u8FD9\u79CD\u56DE\u5E94\u662F\u51FA\u81EA\u4FE1\u4EF0, \u8FD8\u662F\u51FA\u81EA\u5BF9\u4E8E\u4FE1\u4EF0\u7684\u670D\u4ECE (\u7F57 1 :5 ) .</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u5728\u8FD9 &quot;\u4E03\u7978\u54C9&quot; \u7684\u8A00\u8BBA\u4E2D, \u4E00\u518D\u79F0\u5B97\u6559\u9886\u8896\u4E3A &quot;\u5047\u5584\u4EBA&quot;. &quot;\u5047\u5584\u4EBA&quot; \u8BD1\u81EA <em>hypocrites</em> \u8FD9\u4E00\u540D\u8BCD, \u539F\u672C\u7684\u610F\u601D\u662F\u6307\u6234\u4E0A\u9762\u5177\u88C5\u626E\u6210\u4ED6\u4EBA\u7684\u6F14\u5458. \u5728\u7B2C\u4E00\u4E2A\u7978\u54C9\u8A00\u8BBA\u4E2D, \u8036\u7A23\u65A5\u8D23\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u4EBA, \u56E0\u4E3A\u4E0D\u4EC5\u4ED6\u4EEC\u81EA\u5DF1\u4E0D\u80FD\u8FDB\u5165\u5929\u56FD, \u66F4\u7CDF\u7684\u662F, \u4ED6\u4EEC\u8FD8\u963B\u6321\u4E86\u4ED6\u4EBA\u8FDB\u5929\u56FD\u4E4B\u8DEF ( 13 ~ 14 \u8282 ). \u5C01\u95ED\u548C\u5F00\u542F\u901A\u5F80\u5929\u56FD\u9053\u8DEF\u7684\u56FE\u50CF, \u4EE4\u4EBA\u60F3\u5230\u524D\u8FF0\u8036\u7A23\u628A\u5929\u56FD\u7684\u94A5\u5319\u4EA4\u7ED9\u4F2F\u591A\u7984\u7684\u53D9\u8FF0 ( \u739B\u5341\u516D 19 ). \u56E0\u4E3A\u5728\u739B\u7AA6\u7684\u6559\u4F1A\u56E2\u4F53\u4E2D, \u4F2F\u591A\u7984\u548C\u6B63\u5728\u5D1B\u8D77\u7684\u7684\u57FA\u7763\u5F92\u9886\u8896\u4EEC, \u624D\u662F\u53D7\u5230\u4EBA\u4EEC\u6CE8\u610F\u7684\u6743\u5A01, \u800C\u4E0D\u662F\u90A3\u4E9B\u72B9\u592A\u4F1A\u5802\u7684\u4EBA.</p><p>\u7B2C\u4E8C\u4E2A\u7978\u54C9\u8A00\u8BBA, \u662F\u4E00\u4E2A\u6307\u63A7: \u5982\u679C\u5916\u90A6\u4EBA\u7688\u4F9D\u6210\u4E86 &quot;\u6CD5\u5229\u585E\u4E3B\u4E49\u8005&quot;, \u90A3\u4F1A\u6BD4\u4ED6\u4EEC\u7684\u8001\u5E08\u52A0\u500D\u70ED\u8877, \u52A0\u500D\u8BEF\u5BFC, \u6240\u4EE5\u8036\u7A23\u8B66\u544A: \u5230\u5934\u6765, \u4ED6\u4EEC\u4F1A\u53D8\u6210 &quot;\u5730\u72F1\u4E4B\u5B50&quot; ( 15 \u8282 ), \u800C\u4E0D\u662F &quot;\u5929\u4E3B\u7684\u5B50\u5973&quot; ( \u739B\u4E94 9, \u53C2\u9605: \u739B\u4E94 45 ). \u8FD9\u91CC\u7684 &quot;\u5730\u72F1\u4E4B\u5B50&quot;, \u5E0C\u814A\u539F\u6587\u662F\u8BF4 &quot;\u9769\u5384\u7EB3\u5C71\u8C37\u4E4B\u5B50&quot;. &quot;\u9769\u5384\u7EB3\u5C71\u8C37&quot; ( Gehenna ), \u6740\u4F2F\u6765\u8BED\u4E43\u79F0 &quot;\u672C\u5E0C\u519C\u5C71\u8C37&quot;, \u662F\u8036\u8DEF\u6492\u51B7\u57CE\u4E1C\u5357\u7684\u4E00\u5904\u5C71\u8C37, \u662F\u8C61\u5F81\u53D7\u706B\u72F1\u4E4B\u7F5A\u7684\u6240\u5728\u5730, \u90A3\u91CC\u66FE\u662F\u4EE5\u8272\u5217\u7684\u4E24\u4F4D\u6076\u541B\u5C06\u81EA\u5DF1\u7684\u5B50\u5973\u727A\u7272, \u5411\u90AA\u795E\u6469\u808B\u5BA2 ( Molech ) \u4E3E\u884C\u5927\u796D\u7684\u5C71\u8C37, \u540E\u6765\u6210\u4E86\u711A\u70E7\u5783\u573E\u548C\u5C38\u4F53\u7684\u573A\u6240.</p><p>\u8036\u7A23\u5728\u7B2C\u4E09\u4E2A\u7978\u54C9\u8A00\u8BBA\u4E2D, \u6279\u5224\u6CD5\u5229\u585E\u4EBA\u81EA\u521B &quot;\u6307\u7740\u5723\u6240\u7684\u91D1\u5B50\u8D77\u8A93, \u5C31\u8BE5\u8FD8\u613F&quot; \u7684\u8BF4\u6CD5\u6BEB\u65E0\u610F\u4E49 ( 16 ~ 22 \u8282 ). \u5728\u8036\u7A23\u7684\u65F6\u4EE3, \u4E49\u52A1\u7684\u5C65\u884C\u4E0D\u53D7\u5408\u7EA6\u7EA6\u675F, \u800C\u662F\u901A\u8FC7\u516C\u5F00\u8D77\u8A93\u7684\u65B9\u5DE5, \u4EE5\u4EBA\u53E3\u91CC\u7684\u8BDD\u4F5C\u4E3A\u51ED\u8BC1, \u5BF9\u4E8E\u6781\u5176\u91CD\u5927\u7684\u534F\u8BAE, \u5C31\u4F1A\u547C\u6C42\u5929\u4E3B\u7684\u5723\u540D. \u7136\u800C, \u8654\u8BDA\u7684\u72B9\u592A\u4EBA\u53CD\u5BF9\u5927\u58F0\u547C\u5929\u4E3B\u7684\u5723\u540D, \u4E8E\u662F, \u6CD5\u5229\u585E\u4EBA\u5C31\u8C61\u739B\u7AA6\u5723\u53F2\u4EE5 &quot;\u5929\u56FD&quot; \u66FF\u4EE3 &quot;\u5929\u4E3B\u7684\u56FD&quot; ( \u739B\u4E09 2 ) \u4E00\u6837\u5730, \u4EE5\u6307\u8457\u5723\u6240\u7684\u91D1\u5B50, \u6216\u4F9B\u7269, \u6216\u4E0E\u5929\u4E3B\u6709\u5173\u8FDE\u7684\u7269\u4EF6\u8D77\u8A93, \u4F5C\u4E3A\u4E00\u79CD\u907F\u514D\u547C\u53F7\u5929\u4E3B\u5723\u540D\u7684\u65B9\u6CD5. \u8036\u7A23\u8BF4, \u8FD9\u6837\u505A\u4E0E\u76F4\u547C\u5929\u4E3B\u5723\u540D\u6240\u4EA7\u751F\u7684\u7EC6\u5FAE\u5DEE\u522B, \u662F\u65E0\u610F\u4E49\u7684; \u5176\u5192\u72AF\u5929\u4E3B\u7684\u7ED3\u679C\u662F\u76F8\u540C\u7684. \u8036\u7A23\u4E3B\u5F20: &quot;\u603B\u4E0D\u53EF\u53D1\u8A93&quot; ( \u739B\u4E94 33 ~ 37 ).</p><p>\u8036\u7A23\u5728\u7B2C\u56DB\u4E2A\u7978\u54C9\u7684\u8A00\u8BBA\u4E2D, \u8C34\u8D23\u9886\u8896\u4EEC\u5206\u4E0D\u6E05\u695A\u4EC0\u4E48\u662F\u91CD\u8981\u7684\u4E8B, \u4EC0\u4E48\u662F\u4E0D\u91CD\u8981\u7684\u4E8B ( 23 ~ 24 \u8282 ). \u4EC0\u4E00\u5949\u732E\u7684\u7ECF\u6587\u89C4\u5B9A: \u4EBA\u5E94\u5F53\u628A\u519C\u4EA7\u7269, \u725B\u6216\u7F8A\u7FA4, \u9152, \u4E94\u8C37\u548C\u6CB9\u7684\u5341\u5206\u4E4B\u4E00\u5949\u732E\u51FA\u6765, \u7528\u6765\u8D44\u52A9\u5723\u6BBF, \u8D61\u517B\u808B\u672B\u4EBA\u548C\u5E2E\u52A9\u7A77\u4EBA ( \u53C2\u9605: \u808B\u4E8C\u5341\u4E03 30 ~ 33; \u6237\u5341\u516B 21 ~ 32; \u7533\u5341\u56DB 22 ~ 19 ). \u4EC0\u4E00\u5949\u732E\u7684\u7ECF\u6587\u5E76\u6CA1\u6709\u63D0\u53CA &quot;\u8584\u8377, \u8334\u9999\u548C\u8494\u863F&quot; \u7B49\u8349\u672C\u690D\u7269. \u8036\u7A23\u6559\u5BFC\u95E8\u5F92, \u5BF9\u6CD5\u5F8B\u7684\u9075\u5B88\u5FC5\u987B\u8981\u8D85\u8D8A\u7ECF\u4E0A\u6240\u8BB0\u8F7D\u7684 ( \u739B\u4E94 21 ~ 48 ), \u4F46\u662F\u8FD9\u6837\u505A, \u4E3A\u7684\u662F\u8981\u4F7F\u5929\u4E3B\u521B\u9020\u7684\u6240\u6709\u53D7\u9020\u7269\u4E0E\u5929\u4E3B\u672C\u8EAB\u8FBE\u5230\u66F4\u5B8C\u7F8E\u7684\u548C\u8C10\u5883\u754C ( \u739B\u4E94 20, 48 ). \u76F8\u5BF9\u7684, \u6CD5\u5229\u585E\u4EBA\u5374\u8BA9\u4EBA\u6781\u7AEF\u8FC7\u5EA6\u5730\u9075\u884C\u6CD5\u5F8B\u6559\u6761, \u8FD9\u6837\u53CD\u800C\u4F7F\u4ED6\u4EEC\u7684\u884C\u4E3A\u8FDC\u79BB\u4E86 &quot;\u516C\u4E49, \u4EC1\u7231\u548C\u4FE1\u4E49&quot;. \u4E5F\u56E0\u6B64, \u4ED6\u4EEC\u6210\u4E86 &quot;\u778E\u773C\u7684\u5411\u5BFC&quot;. \u81EA\u5DF1\u770B\u4E0D\u6E05\u695A\u9053\u8DEF, \u8FD8\u8981\u5F15\u5BFC\u4ED6\u4EBA\u8D70\u4E0A\u6BC1\u706D\u4E4B\u8DEF. &quot;\u541E\u4E0B\u4E86\u9A86\u9A7C&quot;, \u6B63\u662F\u5BF9\u6CD5\u5229\u585E\u4EBA\u4EE4\u4EBA\u4E0D\u53EF\u5BB9\u5FCD, \u8FC7\u5EA6\u5949\u884C\u6CD5\u5F8B\u7684\u884C\u4E3A, \u6240\u505A\u7684\u5938\u5F20\u63CF\u8FF0.</p><p>\u8036\u7A23\u8BF4\u7684\u7B2C\u4E94\u4E2A\u7978\u54C9\u8A00\u8BBA, \u5C06\u5916\u5728\u7684\u884C\u4E3A\u4E0E\u5185\u5728\u7684\u5B97\u6559\u60C5\u64CD\u4F5C\u4E86\u6BD4\u8F83 ( 25 ~ 26 \u8282 ). \u5728\u6B64, \u8036\u7A23\u4F7F\u7528\u7684\u662F <em>harpages</em> ( \u601D\u9AD8\u672C\u8BD1\u4F5C &quot;\u52AB\u593A&quot;, \u548C\u5408\u672C\u8BD1\u4F5C &quot;\u52D2\u7D22&quot;, \u610F\u601D\u5C31\u662F &quot;\u62A2\u52AB&quot; ) \u8FD9\u4E2A\u8BED\u6C14\u5F3A\u70C8\u7684\u5B57, \u6765\u5F62\u5BB9\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u4EBA\u8150\u8D25\u7684\u5185\u5FC3\u72B6\u6001. \u8036\u7A23\u4E5F\u7528\u4E86 <em>akrasia</em> ( \u601D\u9AD8\u672C\u8BD1\u4F5C &quot;\u8D2A\u6B32&quot;, \u548C\u5408\u672C\u8BD1\u4F5C &quot;\u653E\u8361&quot;, \u539F\u610F\u4E3A &quot;\u610F\u5FD7\u529B\u8584\u5F31&quot;, &quot;\u7F3A\u4E4F\u81EA\u5236\u529B&quot; \u6216 &quot;\u8D2A\u604B\u6743\u529B&quot; ) \u8FD9\u4E2A\u5B57, \u6765\u8C34\u8D23\u7ECF\u5E08\u548C\u6CD5\u5229\u585E\u4EBA ( \u53C2\u9605: 25 \u8282 ), \u6697\u6307\u4ED6\u4EEC\u662F\u4E00\u4E9B\u5728\u6027\u884C\u4E3A\u6216\u4E00\u822C\u884C\u4E3A\u4E0A, \u6F2B\u65E0\u8282\u5236\u5730\u8FC7\u5EA6\u653E\u7EB5\u7684\u4EBA. \u76F8\u5BF9\u5730, \u8036\u7A23\u6559\u5BFC\u95E8\u5F92\u8981\u6709\u5185\u5728\u5B97\u6559\u60C5\u64CD, \u4FDD\u6301 &quot;\u5FC3\u91CC\u6D01\u6D44&quot; ( \u739B\u4E94 8 ), \u6709\u80FD\u529B &quot;\u4ECE\u5FC3\u91CC\u5BBD\u6055\u4EBA&quot; ( \u739B\u5341\u516B 35 ), \u5E76 &quot;\u5168\u5FC3, \u5168\u7075, \u5168\u610F\u7231\u5929\u4E3B&quot; ( \u739B\u4E8C\u5341\u4E8C 37 ).</p><p>\u8036\u7A23\u8BF4\u7684\u7B2C\u516D\u4E2A\u7978\u54C9\u8A00\u8BBA, \u662F\u5728\u7EE7\u7EED\u53D1\u6325\u7B2C\u4E94\u7978\u54C9\u8A00\u8BBA\u7684\u8BAE\u9898 ( 27 ~ 28 \u8282 ). \u6CD5\u5229\u585E\u4EBA\u548C\u7ECF\u5E08\u90FD\u6709\u7740\u8654\u8BDA\u7684\u5916\u8868, \u4F3C\u4E4E\u4E0E\u5929\u4E3B\u548C\u4ED6\u4EBA\u6709\u7740\u826F\u597D\u7684\u5173\u7CFB, \u7136\u800C\u4ED6\u4EEC\u7684\u5185\u5FC3\u5374\u5DF2\u7ECF\u88AB\u4F2A\u5584\u53CA\u6076\u884C\u8150\u8680\u4E86, \u5C31\u8C61\u88AB\u767D\u8272\u7C89\u5237\u8FC7\u7684\u575F\u5893, \u63A9\u85CF\u7740\u5185\u5728\u7684\u8150\u8D25\u673D\u70C2. \u628A\u575F\u5893\u7C89\u5237\u6210\u767D\u8272, \u662F\u4E3A\u4F7F\u4EBA\u6613\u4E8E\u8FA9\u8BA4, \u8FD9\u6837, \u72B9\u592A\u4EBA\u5C31\u53EF\u907F\u514D\u78B0\u89E6\u5230\u5B83\u4EEC, \u4ECE\u800C\u4FDD\u6301\u81EA\u8EAB\u7684\u6D01\u51C0 ( \u53C2\u9605: \u808B\u4E8C\u5341\u4E001, 11 ).</p><p>\u8036\u7A23\u5728\u7B2C\u4E03\u4E2A, \u4E5F\u662F\u6700\u540E\u4E00\u4E2A\u7978\u54C9\u8A00\u8BBA\u4E2D, \u8C34\u8D23\u6CD5\u5229\u585E\u4EBA\u548C\u7ECF\u5E08\u5229\u7528\u88C5\u9970\u5148\u77E5\u548C\u4E49\u4EBA\u7684\u575F\u5893, \u6765\u4F2A\u88C5\u81EA\u5DF1\u662F\u5728\u5149\u8363\u5148\u77E5\u548C\u5148\u8D24\u4E49\u4EBA, \u5E76\u4E14\u65AD\u8A00\u5982\u679C\u81EA\u5DF1\u51FA\u751F\u5728\u53E4\u4EE3, \u7EDD\u4E0D\u4F1A\u505A\u51FA\u7956\u5148\u4EEC\u5BF9\u5148\u77E5\u53CA\u4E49\u4EBA\u6240\u505A\u7684\u4E8B ( 29 ~ 36 \u8282 ). \u8036\u7A23\u5B9E\u5728\u5730\u8BF4, \u6CD5\u5229\u585E\u4EBA\u548C\u7ECF\u5E08\u4E0E\u4ED6\u4EEC\u7684\u7956\u5148\u5E76\u6CA1\u6709\u5206\u522B, \u5C06\u8981\u6740\u5BB3\u5148\u77E5\u8036\u7A23, \u5C31\u50CF\u4ED6\u4EEC\u7684\u7956\u5148\u6307\u79F0\u5148\u77E5\u4E0D\u4E49, \u800C\u9664\u6389\u5148\u77E5\u4EEC\u4E00\u6837.</p><p>\u6CD5\u5229\u585E\u4EBA\u548C\u7ECF\u5E08\u7684\u4F5C\u4E3A\u663E\u793A\u51FA: \u4ED6\u4EEC\u4E0D\u662F &quot;\u5929\u4E3B\u7684\u5B50\u5973&quot;, \u5374\u662F &quot;\u5730\u72F1\u4E4B\u5B50&quot; ( 15 \u8282 ), &quot;\u6B8B\u6740\u5148\u77E5\u8005\u7684\u5B50\u5B59&quot; ( 31 \u8282 ), &quot;\u81EA\u4E49\u4EBA\u4E9A\u4F2F\u5C14\u7684\u8840, \u76F4\u5230\u4F60\u4EEC\u66FE\u5728\u5723\u6240\u4E0E\u5168\u71D4\u796D\u575B\u95F4, \u6240\u6740\u7684\u8D1D\u52D2\u57FA\u96C5\u7684\u513F\u5B50\u5219\u52A0\u9ECE\u96C5\u7684\u8840, \u90FD\u5F52\u5230\u4F60\u4EEC\u8EAB\u4E0A&quot; ( 35 \u8282 ). \u6362\u53E5\u8BDD\u8BF4, \u4ECE\u65E7\u7EA6\u7ECF\u4E66\u4E2D\u7B2C\u4E00\u4F4D\u88AB\u6740\u7684\u4E49\u4EBA ( \u4E9A\u4F2F\u5C14, \u521B\u56DB 8 ), \u5230\u6700\u540E\u4E00\u4F4D\u6B89\u96BE\u8005, \u6240\u6709 &quot;\u5148\u77E5, \u8D24\u54F2\u548C\u7ECF\u5E08&quot; \u6240\u6D41\u7684\u65E0\u8F9C\u9C9C\u8840, \u8981\u5F52\u5728\u6CD5\u5229\u585E\u4EBA\u548C\u7ECF\u5E08\u8EAB\u4E0A. \u5173\u4E8E\u5219\u52A0\u9ECE\u96C5\u5148\u77E5\u7684\u8EAB\u5206, \u5728\u8FD9\u91CC\u6709\u4E9B\u6DF7\u6DC6\u4E0D\u6E05. \u65E7\u7EA6\u7ECF\u4E66\u4E2D\u6240\u8BB0\u8FF0\u7684\u8D1D\u52D2\u57FA\u96C5\u7684\u513F\u5B50, \u5C31\u662F\u531D\u52A0\u5229\u4E9A\u5148\u77E5 ( \u531D\u4E00 1 ), \u53EF\u662F\u636E\u6211\u4EEC\u4E86\u89E3, \u531D\u52A0\u5229\u4E9A\u5148\u77E5\u4E0D\u662F\u88AB\u6740\u5728 &quot;\u5723\u6240\u4E0E\u5168\u71D4\u796D\u575B\u95F4&quot; ( 35 \u8282 ), \u800C\u662F\u7EA6\u96C5\u8FBE\u7684\u513F\u5B50\u5219\u52A0\u9ECE\u96C5\u5148\u77E5 ( \u7F16\u4E0B\u4E8C\u5341\u56DB 20 ~ 22 ).</p><p>\u5728 \u300A\u739B\u7AA6\u798F\u97F3\u300B \u8036\u7A23\u53D7\u96BE\u53F2\u7684\u8BB0\u8FF0\u4E2D, \u8C01\u8BE5\u4E3A\u65E0\u8F9C\u7684\u9C9C\u8840\u8D1F\u8D23, \u786E\u5B9E\u662F\u4E00\u4E2A\u5F88\u91CD\u8981\u7684\u4E3B\u9898: \u72B9\u8FBE\u65AF\u8BD5\u56FE\u628A\u51FA\u5356\u8036\u7A23\u7684\u8840\u94B1, \u8FD8\u7ED9\u53F8\u796D\u548C\u957F\u8001 ( \u739B\u4E8C\u5341\u4E03 3 ~ 4 ); \u6BD4\u62C9\u591A\u8BBE\u6CD5\u4E3A\u81EA\u5DF1\u6D17\u6E05\u9489\u6B7B\u8036\u7A23\u7684\u7F6A\u884C ( \u739B\u4E8C\u5341\u4E03 24 ); \u5168\u4F53\u767E\u59D3\u5BF9\u6BD4\u62C9\u591A\u8BF4: &quot;\u4ED6\u7684\u8840\u5F52\u5728\u6211\u4EEC\u548C\u6211\u4EEC\u7684\u5B50\u5B59\u8EAB\u4E0A&quot; ( \u739B\u4E8C\u5341\u4E03 25 ). \u5728\u6700\u540E\u7684\u665A\u9910\u4E2D, \u8036\u7A23\u4EE5\u81EA\u5DF1\u7684\u8840\u7ACB\u5B9A\u4E86\u65B0\u7EA6, \u4ED6\u62FF\u8D77\u676F\u6765, \u795D\u8C22\u4E86, \u9012\u7ED9\u95E8\u5F92\u8BF4: &quot;\u8FD9\u662F\u6211\u7684\u8840, \u65B0\u7EA6\u7684\u8840, \u4E3A\u5927\u4F17\u503E\u6D41, \u4EE5\u8D66\u514D\u7F6A\u8FC7.&quot; ( \u739B\u4E8C\u5341\u516D 28 ).</p><h2 id="\u54C0\u8036\u8DEF\u6492\u51B7" tabindex="-1"><a class="header-anchor" href="#\u54C0\u8036\u8DEF\u6492\u51B7" aria-hidden="true">#</a> \u54C0\u8036\u8DEF\u6492\u51B7</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>37</sup> \u201C\u8036\u8DEF\u6492\u51B7\uFF01\u8036\u8DEF\u6492\u51B7\uFF01\u4F60\u5E38\u6B8B\u6740\u5148\u77E5\uFF0C\u7528\u77F3\u5934\u7838\u6B7B\u90A3\u4E9B\u6D3E\u9063\u5230\u4F60\u8FD9\u91CC\u6765\u7684\u4EBA\u3002\u6211\u591A\u5C11\u6B21\u613F\u610F\u805A\u96C6\u4F60\u7684\u5B50\u5973\uFF0C\u6709\u5982\u6BCD\u9E21\u628A\u81EA\u5DF1\u7684\u5E7C\u96CF\u805A\u96C6\u5728\u7FC5\u8180\u5E95\u4E0B\uFF0C\u4F46\u4F60\u5374\u4E0D\u613F\u610F\uFF01<sup>38</sup> \u4F60\u770B\u5427\uFF01\u4F60\u4EEC\u7684\u623F\u5C4B\u5FC5\u7ED9\u4F60\u4EEC\u7559\u4E0B\u4E00\u7247\u8352\u51C9\u3002<sup>39</sup> \u56E0\u4E3A\u6211\u544A\u8BC9\u4F60\u4EEC\uFF1A\u81EA\u4ECA\u4EE5\u540E\uFF0C\u4F60\u4EEC\u65AD\u4E0D\u80FD\u518D\u770B\u89C1\u6211\uFF0C\u76F4\u5230\u4F60\u4EEC\u8BF4\uFF1A\u56E0\u4E0A\u4E3B\u4E4B\u540D\u800C\u6765\u7684\uFF0C\u5F53\u53D7\u8D5E\u9882\uFF01\u201D</p></blockquote><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6240\u6709\u53D1\u751F\u5728\u8036\u8DEF\u6492\u51B7\u7684\u4E8B\u4EF6, \u501F\u7740\u8036\u7A23\u5BF9\u6B64\u57CE\u7684\u54C0\u60BC\u800C\u544A\u4E00\u6BB5\u843D. \u8036\u7A23\u6700\u540E\u7684\u8FD9\u4E9B\u8BDD\u7279\u522B\u9488\u5BF9\u5723\u57CE\u4E2D\u7684\u5B97\u6559\u9886\u8896\u4EEC. \u4ED6\u4EEC\u5BF9\u8036\u7A23\u59CB\u7EC8\u62B1\u6709\u6000\u7591\u7684\u6001\u5EA6, \u4E0D\u76F8\u4FE1\u886A. \u4E5F\u662F\u56E0\u4E3A\u8FD9\u4E00\u70B9, \u4ED6\u4EEC\u6CA1\u6709\u4ECE\u5929\u4E3B\u90A3\u91CC\u63A5\u53D7\u6551\u6069. \u8FD9\u91CC\u5217\u4E3E\u4E86\u4E24\u9879\u6307\u63A7: \u4ED6\u4EEC\u66FE\u7ECF\u6740\u5BB3\u4E86\u5148\u77E5, \u7EDD\u4E86\u8036\u7A23\u7684\u9080\u8BF7 ( 37 ) . \u6700\u7EC8\u7684\u7ED3\u679C\u662F, \u5982\u4ECA\u4E5F\u62D2\u7EDD\u5929\u4E3B\u5C06\u8981\u79BB\u5F00\u4ED6\u4EEC\u7684\u5723\u6BBF ( 38 ) , \u8036\u7A23\u8FD9\u4F4D\u9ED8\u897F\u4E9A\u4E5F\u8981\u5F03\u4ED6\u4EEC\u800C\u53BB, \u76F4\u5230\u6700\u7EC8\u5929\u56FD\u6765\u5230, \u886A\u4EE5\u5BA1\u5224\u8005\u7684\u8EAB\u4EFD\u6765\u4E34. \u8036\u7A23\u79BB\u5F03\u4E86\u8036\u8DEF\u6492\u51B7, \u968F\u5176\u81EA\u751F\u81EA\u706D: \u7136\u800C\u4ED6\u4EEC\u5E76\u4E0D\u662F\u6CA1\u6709\u5E0C\u671B, \u800C\u662F\u5728\u9ED1\u6697\u4E2D\u671F\u5F85\u66D9\u5149\u7684\u51FA\u73B0, \u56E0\u4E3A\u603B\u6709\u4E00\u5929, \u4ED6\u4EEC\u4F1A\u8BF4 &quot;\u90A3\u4F4D\u5949\u4E0A\u4E3B\u540D\u800C\u6765\u7684\u5F53\u53D7\u8D5E\u7F8E&quot; ( 39; \u53C2\u9605\u548F 118 : 26 ). \u8FD9\u4E2A\u8BB8\u8BFA\u4E0E\u4FDD\u7984\u5728\u300A\u7F57\u9A6C\u4E66\u300B\u4E2D\u6240\u5BA3\u5E03\u7684\u4E0D\u8C0B\u800C\u5408 ( \u7F57 11 :26 ) \u3002</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u81EA 37 \u8282\u8D77, \u8BED\u6C14\u7531\u5BF9\u9886\u8896\u4EEC\u7684\u6FC0\u70C8\u8C34\u8D23, \u8F6C\u5230\u4E86\u5BF9\u8036\u8DEF\u6492\u51B7\u57CE\u6DF1\u6DF1\u7684\u60B2\u75DB. \u8FD9\u57CE\u5E02\u6BC1\u4E86\u9012\u9001\u8BAF\u606F\u7684\u4EBA, \u5728\u6B64\u4EE5\u540E, \u5728\u739B\u7AA6\u5199\u4F5C\u7684\u65F6\u671F, \u6B64\u57CE\u5DF2\u6CA6\u843D\u6210\u4E86\u4E00\u7247\u5E9F\u589F. \u4E00\u5E45\u5185\u5FC3\u7EDE\u75DB\u7684\u6BCD\u96BE\u7684\u56FE\u50CF, \u6E34\u671B\u7740\u60F3\u8981\u805A\u96C6\u5979\u7684\u53DB\u9006\u5C0F\u9E21, \u60F3\u8981\u6536\u7559\u5728\u5979\u7684\u7FBD\u7FFC\u4E0B, \u662F\u4E00\u4E2A\u6781\u666E\u901A\u7684\u56FE\u50CF, \u5728\u7ECF\u6587\u4E2D\u662F\u4EE5\u8FD9\u4E2A\u9690\u55BB\u7684\u7B14\u6CD5, \u6765\u5F62\u5BB9\u5929\u4E3B\u7684\u5173\u7231 ( \u7533\u4E09\u5341\u4E8C 11; \u5362\u4E8C 12; \u548F\u5341\u4E03 8; \u4E09\u5341\u516D 8; \u4E94\u4E03 2; \u516D\u4E00 5; \u8DEF\u5341\u4E09 34 ~ 35 ).</p><p>\u7136\u800C, \u8036\u7A23\u5C31\u50CF\u4E00\u4F4D\u6BCD\u4EB2\u5BF9\u6700\u4EFB\u6027\u7684\u5B69\u5B50\u4ECE\u4E0D\u653E\u5F03, \u8036\u7A23\u5F15\u7528\u300A\u5723\u548F\u300B&quot;\u56E0\u4E0A\u4E3B\u4E4B\u540D\u800C\u6765\u7684, \u5F53\u53D7\u8D5E\u9882&quot; ( \u548F\u4E00\u4E00\u516B 26 ) \u8FD9\u53E5\u9882\u8BCD, \u5411\u7942\u7684\u5B50\u6C11\u627F\u8BFA, \u5F53\u4ED6\u4EEC\u80FD\u591F\u50CF\u95E8\u5F92\u6B22\u8FCE\u8036\u7A23\u7B2C\u4E00\u6B21\u8363\u8FDB\u8036\u8DEF\u6492\u51B7 ( \u739B\u4E8C\u5341\u4E00 9 ) \u90A3\u6837\u63A5\u53D7\u7942\u65F6, \u4ED6\u4EEC\u5C06\u4F1A\u518D\u6B21\u770B\u5230\u8036\u7A23.</p><p>\u8BFB\u672C\u7AE0\u65A5\u8D23\u72B9\u592A\u4EBA\u548C\u4E03\u7978\u54C9\u7684\u8A00\u8BBA\u65F6, \u5FC5\u987B\u8981\u5E38\u5E38\u8003\u8651\u739B\u7AA6\u5723\u53F2\u5199\u4F5C\u7684\u65F6\u671F, \u72B9\u592A\u57FA\u7763\u5F92\u548C\u8FD8\u672A\u52A0\u5165\u57FA\u7763\u56E2\u4F53\u7684\u72B9\u592A\u4EBA\u4E4B\u95F4, \u6709\u7740\u4E25\u9177\u7684\u6C11\u65CF\u5185\u90E8\u51B2\u7A81. \u8036\u7A23\u4F5C\u4E3A\u4E00\u4F4D\u5148\u77E5, \u886A\u529D\u8BEB\u81EA\u5DF1\u6C11\u65CF\u7684\u5B97\u6559\u9886\u8896, \u5E76\u9080\u8BF7\u4ED6\u4EEC\u6539\u53D8\u5FC3\u610F. \u886A\u6240\u8BF4\u7684\u8BDD, \u8FD8\u662F\u5BF9\u6240\u6709\u5B97\u6559\u9886\u8896\u7684\u4F2A\u5584\u53D1\u51FA\u8B66\u544A.</p><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u8036\u7A23\u51E0\u5EA6\u4E0E\u6CD5\u5229\u585E\u4EBA\u7684\u51B2\u7A81\u91CC, \u8BFB\u8005\u6240\u80FD\u611F\u89C9\u5230\u7684\u4E49\u6012, \u5230\u73B0\u5728\u5728\u8036\u7A23\u75DB\u8D23\u4ED6\u4EEC\u6B6A\u66F2\u4EE5\u8272\u5217\u5B97\u6559\u7CBE\u795E\u7684\u65F6\u5019, \u5E76\u53D1\u6210\u4E00\u756A\u60CA\u5929\u52A8\u5730\u7684\u75DB\u9A82, \u4ED6\u8B66\u544A\u4ED6\u7684\u95E8\u5F92\u548C\u7FA4\u4F17\u4EEC, \u5F53\u4ED6\u4EEC\u7684\u5B97\u6559\u9886\u8896\u786E\u662F\u6885\u745F\u4E0E\u4F20\u7EDF\u7684\u6B63\u5F0F\u89C1\u8BC1\u4EBA\u7684\u65F6\u5019, \u5E94\u8BE5\u987A\u4ECE\u4ED6\u4EEC, \u4F46\u662F\u5374\u4E0D\u8BE5\u6548\u6CD5\u4ED6\u4EEC\u7684\u4F2A\u5584, \u7F3A\u4E4F\u7231\u5FB7, \u559C\u597D\u865A\u9970\u4E0E\u81EA\u5927. \u7136\u540E, \u8036\u7A23\u7981\u6B62\u4ED6\u7684\u95E8\u5F92\u4F7F\u7528\u90A3\u4E9B\u5C0A\u8D35\u7684\u5934\u8854: \u8FA3\u5F7C ( \u8036\u7A23\u662F\u552F\u4E00\u7684\u5E08\u5085 ), \u7236 ( \u8FD9\u53EA\u914D\u7528\u4E8E\u5929\u4E3B ), \u5BFC\u5E08 ( \u8FD9\u4E00\u79F0\u547C\u5BF9\u9ED8\u897F\u4E9A\u7684\u610F\u4E49\u8FD8\u4E0D\u786E\u5B9A ), \u56E0\u4E3A\u8FD9\u4E9B\u540D\u53F7\u5728\u5F53\u65F6\u7684\u5DF4\u52D2\u65AF\u5766\u662F\u7528\u4E8E\u5929\u4E3B\u7684\u5C0A\u5A01\u7684\u8BB0\u53F7. \u57FA\u7763\u5F92\u4E2D\u5360\u9AD8\u4EEC\u7684\u4EBA, \u5E94\u5F53\u6548\u6CD5\u57FA\u7763, \u505A\u4E3A\u5927\u4F17\u7684\u4EC6\u5F79. \u73B0\u5728, \u8036\u7A23\u5BF9\u72B9\u592A\u7684\u9886\u8896\u4EEC\u5BA3\u5E03\u4E86\u4ED6\u4EEC\u7684 &quot;\u4E03\u5927\u7F6A\u72B6&quot; ( \u662F\u771F\u798F\u516B\u7AEF\u7684\u53CD\u4E3B\u9898 ). \u4ED6\u4EEC\u4E0D\u613F\u8FDB\u5165\u5929\u56FD, \u4E5F\u4E0D\u8BA9\u522B\u4EBA\u8FDB\u53BB, \u5F53\u65F6\u4ED6\u4EEC\u8F9B\u52E4\u529D\u4EBA\u7688\u4F9D ( \u800C\u786E\u6709\u76F8\u5F53\u7684\u6210\u679C ), \u7ED3\u679C\u4F7F\u4EBA\u66F4\u574F, \u800C\u4E0D\u662F\u4F7F\u4EBA\u5411\u5584. \u4ECE\u5BF9\u5929\u4E3B\u53D1\u8A93\u7684\u8FA9\u767D\u53EF\u77E5, \u6CD5\u5229\u585E\u4EBA\u7684\u66F2\u89E3\u6B63\u4E25\u91CD\u5371\u5BB3\u4E86\u65E7\u7EA6\u5B97\u6559\u7CBE\u795E\u7684\u5B9E\u8D28. \u4ED6\u4EEC\u628A\u5916\u5728\u7684\u5B97\u6559\u4EEA\u5F0F\u548C\u6885\u745F\u6CD5\u5F8B\u7CBE\u795E ( \u516C\u4E49, \u4EC1\u7231, \u4FE1\u5FC3 ) \u6DF7\u4E3A\u4E00\u8C08. \u5916\u5728\u7684\u5F62\u5F0F\u4E3B\u4E49\u627C\u6740\u4E86\u5185\u542B\u7684\u5B97\u6559\u7CBE\u795E. \u4F2A\u5584\u7684\u5916\u8863\u906E\u76D6\u4E86\u7F8E\u5FB7\u7684\u8D2B\u4E4F, \u867D\u7136\u4ED6\u4EEC\u7528\u5B8F\u4F1F\u7684\u575F\u5893\u6765\u5149\u8363\u5148\u77E5, \u4ED6\u4EEC\u7684\u884C\u4E3A\u5374\u4E5F\u627F\u8BA4, \u8BC1\u660E\u4E86\u4ED6\u4EEC\u662F\u6B8B\u6740\u5148\u77E5\u8005\u7684\u5B50\u5B59. \u5F53\u8036\u7A23\u9884\u8A00\u8FD9\u4E9B\u6885\u745F\u6CD5\u5F8B\u7684\u6BC1\u574F\u8005\u6050\u6016\u7684\u547D\u8FD0\u65F6, \u4ED6\u7684\u8BED\u8C03\u9010\u6E10\u589E\u5F3A, \u5728\u56DE\u8FF0\u65E7\u7EA6\u65F6\u7684\u88AB\u8C0B\u6740\u8005\u7684\u65F6\u5019, \u8036\u7A23\u5E76\u4E0D\u5B8C\u5168\u4F9D\u7167\u65F6\u4EE3\u7684\u5148\u540E, \u800C\u662F\u6309\u7167\u65E7\u7EA6\u6587\u732E\u6392\u5217\u7684\u79E9\u5E8F, ( \u8FD9\u662F\u6307\u72B9\u592A\u4EBA\u6240\u627F\u8BA4\u7ECF\u4E66\u7684\u6392\u5217\u79E9\u5E8F ): \u7B2C\u4E00\u4E2A\u6B89\u96BE\u8005 ( \u4E9A\u4F2F\u5C14 ) \u662F\u5728\u521B\u4E16\u7EAA\u8BB0\u8F7D\u7684; \u6700\u540E\u4E00\u4E2A ( \u5219\u52A0\u9ECE\u4E9A ) \u5728\u7F16\u4E0B. \u8036\u7A23\u6700\u540E\u53D1\u51FA\u4E86\u4E00\u9635\u75DB\u82E6\u7684\u547C\u558A, \u5176\u4E2D\u542B\u6709\u4ED6\u5BF9\u8036\u8DEF\u6492\u51B7\u7684\u70ED\u7231, \u4F46\u540C\u65F6\u4E5F\u4E0D\u5BF9\u5B83\u5B58\u6709\u4EFB\u4F55\u5E7B\u60F3, \u4E0A\u4E3B\u5C06\u8981\u79BB\u5F00 ( \u4F60\u4EEC\u7684\u623F\u5C4B ) ( \u5373\u6307\u5723\u6BBF ). \u4ED6\u53EF\u80FD\u7528\u4E00\u79CD\u542B\u6DF7\u7684\u65B9\u5F0F\u6697\u6307\u4ED6\u7684\u672B\u65E5, \u90A3\u65F6\u7684\u72B9\u592A\u4EBA, \u7688\u4F9D\u4E86\u8036\u7A23, \u5C06\u8981 &quot;\u770B\u89C1&quot; \u4ED6, \u4EE5\u9ED8\u897F\u4E9A\u4E4B\u5C0A, &quot;\u56E0\u4E3B\u540D\u800C\u6765&quot;. \u8DEF\u52A0\u628A\u8FD9\u4E00\u6BB5\u8BDD\u548C\u8036\u7A23\u9ED8\u897F\u4E9A\u5F0F\u7684\u8FDB\u5165\u8036\u8DEF\u6492\u51B7\u76F8\u8054, \u4F46\u739B\u7AA6\u8BA4\u4E3A\u8FD9\u4E24\u4EF6\u4E8B\u5E76\u6CA1\u6709\u90A3\u4E48\u5BC6\u5207\u7684\u5173\u8054.</p>',45),q=[a];function s(h,r){return o(),t("div",null,q)}var i=u(e,[["render",s],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E09\u7AE0.html.vue"]]);export{i as default};
