import{_ as u,o as t,c as o,e as a}from"./app.6bf529a6.js";const e={},s=a('<h1 id="\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E8C\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E8C\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E8C\u7AE0</h1><h2 id="\u5A5A\u5BB4\u7684\u6BD4\u55BB" tabindex="-1"><a class="header-anchor" href="#\u5A5A\u5BB4\u7684\u6BD4\u55BB" aria-hidden="true">#</a> \u5A5A\u5BB4\u7684\u6BD4\u55BB</h2><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>1</sup> \u8036\u7A23\u53C8\u5F00\u53E3\u7528\u6BD4\u55BB\u5BF9\u4ED6\u4EEC\u8BF4\uFF1A<sup>2</sup> \u201C\u5929\u56FD\u597D\u6BD4\u4E00\u4E2A\u56FD\u738B\uFF0C\u4E3A\u81EA\u5DF1\u7684\u513F\u5B50\u529E\u5A5A\u5BB4\u3002<sup>3</sup> \u4ED6\u6253\u53D1\u4EC6\u4EBA\u53BB\u53EC\u88AB\u8BF7\u7684\u4EBA\u6765\u8D74\u5A5A\u5BB4\uFF0C\u4ED6\u4EEC\u5374\u4E0D\u613F\u610F\u6765\u3002<sup>4</sup> \u53C8\u6D3E\u5176\u4ED6\u7684\u4EC6\u4EBA\u53BB\uFF0C\u8BF4\uFF1A\u4F60\u4EEC\u5BF9\u88AB\u8BF7\u7684\u4EBA\u8BF4\uFF1A\u770B\uFF0C\u6211\u5DF2\u7ECF\u9884\u5907\u597D\u4E86\u6211\u7684\u76DB\u5BB4\uFF0C\u6211\u7684\u516C\u725B\u548C\u80A5\u755C\u90FD\u5BB0\u6740\u4E86\uFF0C\u4E00\u5207\u90FD\u9F50\u5907\u4E86\uFF0C\u4F60\u4EEC\u6765\u8D74\u5A5A\u5BB4\u5427\uFF01<sup>5</sup> \u4ED6\u4EEC\u5374\u4E0D\u7406\uFF1A\u6709\u7684\u5F80\u81EA\u5DF1\u7684\u7530\u91CC\u53BB\u4E86\uFF0C\u6709\u7684\u4F5C\u81EA\u5DF1\u7684\u751F\u610F\u53BB\u4E86\uFF1B<sup>6</sup> \u5176\u4F59\u7684\u7ADF\u62FF\u4F4F\u4ED6\u7684\u4EC6\u4EBA\uFF0C\u51CC\u8FB1\u540E\u6740\u6B7B\u4E86\u3002<sup>7</sup> \u56FD\u738B\u4E8E\u662F\u52A8\u4E86\u6012\uFF0C\u6D3E\u81EA\u5DF1\u7684\u519B\u961F\u6D88\u706D\u4E86\u90A3\u4E9B\u6740\u4EBA\u7684\u51F6\u624B\uFF0C\u711A\u6BC1\u4E86\u4ED6\u4EEC\u7684\u57CE\u5E02\u3002<sup>8</sup> \u7136\u540E\u5BF9\u4EC6\u4EBA\u8BF4\uFF1A\u5A5A\u5BB4\u5DF2\u7ECF\u9F50\u5907\u4E86\uFF0C\u4F46\u662F\u88AB\u8BF7\u7684\u4EBA\u90FD\u4E0D\u914D\u3002<sup>9</sup> \u5982\u4ECA\u4F60\u4EEC\u5230\u5404\u8DEF\u53E3\u53BB\uFF0C\u51E1\u662F\u4F60\u4EEC\u6240\u9047\u5230\u7684\uFF0C\u90FD\u8BF7\u6765\u8D74\u5A5A\u5BB4\u3002<sup>10</sup> \u90A3\u4E9B\u4EC6\u4EBA\u5C31\u51FA\u53BB\u5230\u5927\u8DEF\u4E0A\uFF0C\u51E1\u9047\u5230\u7684\uFF0C\u65E0\u8BBA\u574F\u4EBA\u597D\u4EBA\uFF0C\u90FD\u53EC\u96C6\u4E86\u6765\uFF0C\u5A5A\u5BB4\u4E0A\u5C31\u6EE1\u4E86\u5750\u5E2D\u7684\u4EBA\u3002<sup>11</sup> \u56FD\u738B\u8FDB\u6765\u5DE1\u89C6\u5750\u5E2D\u7684\u5BA2\u4EBA\uFF0C\u770B\u89C1\u5728\u90A3\u91CC\u6709\u4E00\u4E2A\u6CA1\u6709\u7A7F\u5A5A\u5BB4\u793C\u670D\u7684\u4EBA\uFF0C<sup>12</sup> \u4FBF\u5BF9\u4ED6\u8BF4\uFF1A\u670B\u53CB\uFF0C\u4F60\u600E\u4E48\u5230\u8FD9\u91CC\u6765\uFF0C\u4E0D\u7A7F\u5A5A\u5BB4\u793C\u670D\uFF1F\u90A3\u4EBA\u9ED8\u7136\u65E0\u8BED\u3002<sup>13</sup> \u56FD\u738B\u9042\u5BF9\u4EC6\u5F79\u8BF4\uFF1A\u4F60\u4EEC\u6346\u8D77\u4ED6\u7684\u811A\u548C\u624B\u6765\uFF0C\u628A\u4ED6\u4E22\u5728\u5916\u9762\u7684\u9ED1\u6697\u4E2D\uFF1A\u5728\u90A3\u91CC\u8981\u6709\u54C0\u53F7\u548C\u5207\u9F7F\u3002<sup>14</sup> \u56E0\u4E3A\u88AB\u53EC\u7684\u4EBA\u591A\uFF0C\u88AB\u9009\u7684\u4EBA\u5C11\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u7684\u6BD4\u55BB\u548C\u8DEF\u52A0\u798F\u97F3\u7684\u6709\u51E0\u70B9\u660E\u663E\u7684\u4E0D\u540C, (1) \u739B\u7AA6\u798F\u97F3\u91CC\u7684\u4E3B\u4EBA\u662F\u56FD\u738B; \u90A3\u665A\u9910, \u662F\u738B\u5B50\u7684\u5A5A\u5BB4 ( \u8FD9\u4E9B\u76DB\u5BB4\u7684\u8C61\u5F81\u662F\u6307\u5929\u4E3B\u7236, \u57FA\u7763, \u53CA\u6559\u4F1A. ) (2) \u739B\u7AA6\u798F\u97F3\u6709\u4E24\u7FA4\u4EC6\u4EBA\u53BB\u53EC\u8BF7\u5BA2\u4EBA: \u7B2C\u4E00\u7FA4\u6240\u6307\u7684\u662F\u65E7\u7EA6\u7684\u5148\u77E5; \u7B2C\u4E8C\u7FA4\u662F\u5B97\u5F92\u65F6\u671F\u7684\u4F20\u9053\u58EB, \u4ED6\u4EEC\u7684\u6B89\u9053\u7531\u5929\u4E3B\u5728\u516C\u5143\u4E03\u5341\u5E74\u8024\u8036\u8DEF\u6492\u51B7\u88AB\u711A\u800C\u5F97\u62A5\u590D. \u90A3\u53E5 &quot;\u4ED6\u4EEC\u7684\u57CE\u5E02&quot; \u6307\u51FA\u90A3\u4E9B\u5BBE\u5BA2\u4F4F\u5F97\u6BD4\u8F83\u8FDC ( \u8FD9\u4E00\u7EC6\u8282\u5728\u4E00\u4E2A &quot;\u4E00\u5207\u90FD\u9F50\u5907\u4E86&quot; \u7684\u5A5A\u5BB4\u4E0A, \u663E\u5F97\u4E0D\u81EA\u7136 ), \u8FD9\u53C8\u663E\u793A\u8FD9\u4E2A\u6BD4\u55BB\u662F\u5728\u8036\u8DEF\u6492\u51B7\u7684\u6BC1\u706D\u4E4B\u540E\u7F16\u7E82\u7684. (3) \u739B\u7AA6\u798F\u97F3\u7684\u5F71\u540E\u4E00\u666F, \u5B9E\u8D28\u4E0A\u662F\u9644\u5C5E\u4E8E\u7B2C\u4E00\u4E2A\u7684\u53E6\u4E00\u4E2A\u6BD4\u55BB. \u6839\u636E\u4E1C\u65B9\u7684\u4E60\u4FD7, \u53EA\u6709\u5728\u591A\u5BBE\u5BA2\u5F00\u59CB\u71D5\u996E\u4E4B\u540E, \u56FD\u738B\u624D\u4F1A\u8FDB\u6765\u5DE1\u89C6. \u90A3\u4E2A &quot;\u6CA1\u6709\u7A7F\u5A5A\u5BB4\u793C\u670D&quot; \u7684\u4EBA, \u53EF\u80FD\u662F\u7ED9\u90A3\u4E9B\u539F\u5C5E\u4E8E\u7570\u6559\u7684\u57FA\u7763\u5F92\u4EEC\u7684\u4E00\u4E2A\u8C61\u5F81\u6027\u7684\u8B66\u544A, \u4E0D\u8981\u4EE5\u4E3A\u4ED6\u4EEC\u53D6\u4EE3\u4E86\u90A3\u4E9B\u88AB\u62D2\u7EDD\u7684\u72B9\u592A\u4EBA, \u5C31\u4EE5\u4E3A\u4ED6\u4EEC\u88AB\u53EC\u8FDB\u5165\u6559\u4F1A\u7684\u6069\u5BA0\u672C\u6765\u5C31\u8BE5\u662F\u4ED6\u4EEC\u7684.</p><p>\u739B\u7AA6\u6700\u540E\u4E00\u53E5\u8BDD\u5E38\u88AB\u8BEF\u89E3. \u4ED6\u7528\u65E7\u7EA6\u7684 &quot;\u9057\u6C11&quot; \u7684\u6559\u4E49\u6765\u8BF4\u660E\u4E00\u822C\u4EBA\u7684\u88AB\u53EC\u6210\u4E3A\u57FA\u7763\u5F92 ( \u4E0E &quot;\u4EBA\u5C11&quot; \u76F8\u5BF9\u7684 &quot;\u4EBA\u591A&quot; \u5728\u95EA\u65CF\u6587\u5B57\u91CC, \u5C31\u662F &quot;\u5168\u90E8&quot; \u7684\u610F\u601D ). \u6CE8\u610F, \u8FD9\u91CC\u4E1D\u6BEB\u4E0D\u6D89\u53CA &quot;\u9884\u9009\u8BBA&quot; \u7684\u95EE\u9898.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u662F\u8036\u7A23\u5728\u8036\u8DEF\u6492\u51B7\u5411\u90A3\u5BF9\u4ED6\u6743\u67C4\u6311\u6218\u7684\u5B97\u6559\u9886\u8896 ( \u739B\u4E8C\u5341\u4E00 23 ~ 27 ), \u8BB2\u7684\u4E09\u4E2A\u6BD4\u55BB\u4E2D\u7684\u7B2C\u4E09\u4E2A\u3002 \u8FD9\u4E2A\u6BD4\u55BB\u6781\u5BCC\u5BD3\u610F, \u540C\u65F6\u5B83\u4E5F\u6709\u8BB8\u591A\u4E0E\u73B0\u5B9E\u4E0D\u76F8\u7B26\u5408\u7684\u7EC6\u8282\u63CF\u5199. \u5A5A\u5BB4\u7684\u56FE\u50CF\u4EE4\u4EBA\u60F3\u8D77: \u8036\u7A23\u66FE\u88AB\u6BD4\u4F5C\u65B0\uF92C, \u65B0\uF92C\u7684\u4E34\u5728\u9700\u8981\u7684\u662F\u76DB\u5BB4, \u800C\u4E0D\u662F\u8981\u7981\u98DF ( \u739B\u4E5D 15 ). \u76DB\u5BB4\u8FD9\u4E2A\u9690\u55BB\u5728\u7ECF\u4E66\u4E2D, \u5E38\u88AB\u7528\u6765\u8868\u793A\u73B0\u4E16\u53CA\u672B\u4E16\u65F6\u5929\u4E3B\u65E0\u6BD4\u4E30\u5BCC\u7684\u7167\u987E ( \u4F9D\u4E8C\u5341\u4E94 6 ~ 10; \u4E94\u4E94 1 ~ 3 ). \u8FD9\u4E2A\u6BD4\u55BB\u4E2D, \u4E0D\u65AD\u53D1\u51FA\u7684\u9080\u60C5, \u4EE4\u4EBA\u5FC6\u8D77\u524D\u4E00\u4E2A\u6BD4\u55BB\u4E2D\u7684\u8461\u8404\u56ED\u56ED\u4E3B\u4E0D\u65AD\u6253\u53D1\u4EC6\u4EBA\u51FA\u53BB\u6536\u679C\u5B50 ( \u739B\u4E8C\u5341\u4E00 33 ~ 46 ); \u540C\u65F6, \u8FD9\u4E2A\u6BD4\u55BB\u4E5F\u662F\u77E5\u6167\u9080\u8BF7\u4EBA\u4EEC\u8D74\u5BB4 ( \u7BB4\u4E5D 5 ) \u7684\u56DE\u54CD. \u4E24\u6B21\u9080\u8BF7\u5BA2\u4EBA\u8D74\u5BB4\u7684\u4E60\u4FD7 ( \u53C2\u9605: \u827E\u4E94 8; \u516D 14 ), \u53EF\u5BB9\u8BB8\u88AB\u9080\u8BF7\u8005\u77E5\u9053\u5176\u4ED6\u5BA2\u4EBA\u662F\u8C01, \u540C\u65F6\u4E5F\u53EF\u8BA9\u88AB\u9080\u8BF7\u8005\u77E5\u9053\u4EBA\u4EEC\u662F\u5426\u5DF2\u5B83\u6392\u59A5\u5F53. \u8FD9\u4E2A\u4E60\u4FD7\u8FD8\u53EF\u7ED9\u5BA2\u4EBA\u65F6\u95F4, \u51B3\u5B9A\u4ED6\u4EEC\u662F\u5426\u6709\u80FD\u529B\u56DE\u8C22\u9080\u60C5. \u4E24\u6B21\u9080\u8BF7\u4E2D\u95F4\u76F8\u9694\u7684\u65F6\u95F4, \u8FD8\u53EF\u5BB9\u8BB8\u4E3B\u4EBA\u6709\u65F6\u95F4\u786E\u5B9A\u6240\u9700\u98DF\u7279\u7684\u6570\u91CF.</p><p>\u739B\u7AA6\u5723\u53F2\u5728\u6B64\u8BF4\u7684\u6545\u4E8B, \u4E0E\u300A\u8DEF\u52A0\u798F\u97F3\u300B\u6240\u8BF4\u7684\u65B9\u5F0F ( \u8DEF\u5341\u56DB 15 ~ 24 ) \u6709\u6240\u4E0D\u540C, \u739B\u7AA6\u5723\u53F2\u53EA\u8BF4\u4ED6\u4EEC &quot;\u4E0D\u987E\u610F\u6765&quot;, \u5E76\u6CA1\u6709\u63D0\u51FA\u5177\u4F53\u4E0D\u8D74\u5BB4\u4F1A\u7684\u85C9\u53E3. \u88AB\u9080\u8BF7\u7684\u4EBA\u628A\u56FD\u738B\u4EC6\u4EBA\u52A0\u4EE5\u8650\u5F85, \u5E76 &quot;\u51CC\u8FB1\u540E\u6740\u6B7B&quot; ( 5 ~ 6 ), \u4E8E\u662F\u56FD\u738B\u9707\u6012, &quot;\u6D3E\u81EA\u5DF1\u7684\u519B\u961F\u6D88\u706D\u4E86\u90A3\u4E9B\u6740\u4EBA\u7684\u51F6\u624B, \u711A\u6BC1\u4E86\u4ED6\u4EEC\u7684\u57CE\u5E02&quot; ( 7\u8282 ), \u8FD9\u60C5\u8282\u662F\u5728\u9690\u55BB\u63CF\u5199\u82E5\u7FF0\u6D17\u8005\u548C\u5148\u77E5\u7684\u88AB\u6740, \u4EE5\u53CA\u4E3B\u5386 70 \u8036\u8DEF\u6492\u51B7\u5723\u6BBF\u7684\u88AB\u6BC1.</p><p>\u56FD\u738B\u4EE5\u62A5\u590D\u884C\u52A8\u6765\u505A\u56DE\u5E94, \u5728\u5F53\u65F6 &quot;\u4F60\u7ED9\u6211\u9762\u5B50, \u6211\u4E5F\u5C31\u7ED9\u4F60\u9762\u5B50; \u4F60\u4E0D\u7ED9\u6211\u9762\u5B50, \u6211\u5C31\u8BA9\u4F60\u53D7\u8FB1&quot; \u7684\u6587\u5316\u4F53\u7CFB\u91CC, \u662F\u5728\u9884\u6599\u4E4B\u4E2D\u7684. \u7136\u800C, \u56FD\u738B\u5728\u6B64\u6240\u505A\u51FA\u7684\u7B2C\u4E8C\u4E2A\u53CD\u5E94 ( 8 ~ 10 \u8282 ) \u5374\u4EE4\u4EBA\u9707\u60CA. \u5728\u4E3B\u5386\u7B2C\u4E00\u4E16\u7EAA\u7684\u5730\u4E2D\u6D77\u4E16\u754C\u4E2D, \u4EBA\u987E\u610F\u4E0E\u540C\u4E00\u7C7B\u4EBA\u4E00\u8D77\u5403\u996D, \u5728\u4E00\u8D77\u5403\u996D\u8868\u793A\u5F7C\u6B64\u4EAB\u6709\u76F8\u540C\u7684\u4EF7\u503C\u89C2\u548C\u793E\u4F1A\u5730\u4F4D. \u56FD\u738B\u6253\u53D1\u4EC6\u4EBA\u51FA\u53BB, \u7A7F\u8FC7\u57CE\u5E02\u7684\u8857\u9053, \u76F4\u8FBE\u57CE\u4E61\u8FB9\u754C\u4E4B\u5916\u7684 &quot;\u5927\u8DEF\u4E0A&quot; \u53EC\u96C6\u4EBA\u6765\u8D74\u5BB4 ( 10 \u8282 ). &quot;\u5927\u8DEF&quot; \u662F\u5728\u7A77\u4EBA\u5C45\u4F4F\u4E61\u95F4\u5730\u65B9, \u4E0A\u6D41\u9636\u5C42\u7684\u7CBE\u82F1 ( \u4EBA\u53E3\u4E2D\u7ECF\u6D4E\u6536\u5165\u5728\u524D 5% ~ 10% \u7684\u4EBA ) \u5219\u5C45\u4F4F\u5728\u5E02\u4E2D\u5FC3. \u5C31\u8C61\u739B\u5341\u4E09 24 ~ 30, 47 ~ 50 \u4E2D\u7684\u6BD4\u55BB\u90A3\u6837, &quot;\u597D\u7684\u9EA6\u5B50&quot; \u548C &quot;\u574F\u7684\u83A0\u5B50&quot; \u5148\u6DF7\u805A\u5728\u4E00\u8D77, \u7136\u540E\u624D\u88AB\u62E3\u9009\u5206\u5F00.</p><p>\u8FD9\u4E2A\u6BD4\u55BB\u7684\u6700\u540E\u4E00\u5E55 ( 11 ~ 14 \u8282 ) \u5B8C\u5168\u4E0E\u73B0\u5B9E\u4E0D\u76F8\u7B26\u5408, \u4F46\u5B83\u5F3A\u8C03\u4E86\u739B\u7AA6\u5723\u53F2\u5BF9\u4F26\u7406\u4E0A\u7684\u5173\u6CE8: \u4EBA\u5E94\u7A7F\u6234\u6574\u9F50, \u4E5F\u5C31\u662F\u5E94\u7A7F\u4E0A\u597D\u884C\u4E3A, \u968F\u65F6\u51C6\u5907\u597D\u80FD\u53BB\u8D74\u672B\u65E5\u7684\u76DB\u5BB4 ( \u53C2\u9605: \u7F57\u5341\u4E09 14; \u8FE6\u4E09 27; \u54E5\u4E09 12 ). \u5BF9\u95E8\u5F92\u7684\u8981\u6C42, \u6BD4\u6700\u521D\u63A5\u53D7\u9080\u8BF7\u6210\u4E3A &quot;\u5929\u4E3B\u7684\u670B\u53CB\u548C\u5148\u77E5&quot; \u7684\u4EBA ( \u667A\u4E03 27 ) \u8981\u66F4\u9AD8. \u5728\u8461\u8404\u56ED\u4E3B\u4EBA\u53D1\u5DE5\u8D44\u7684\u6BD4\u55BB\u4E2D, \u56ED\u4E3B\u79F0\u90A3\u4F4D\u53D1\u6028\u8A00\u8005\u4E3A &quot;\u670B\u53CB&quot; ( \u739B\u4E8C\u5341 13 ); \u540C\u6837\u7684, \u5728\u72B9\u8FBE\u65AF\u5356\u8036\u7A23\u65F6, \u8036\u7A23\u4E5F\u79F0\u4ED6\u4E3A &quot;\u670B\u53CB&quot; ( \u739B\u4E8C\u5341\u516D 50 ). \u739B\u7AA6\u5723\u53F2\u662F\u7528\u8FD9\u4E2A\u6BD4\u55BB, \u6765\u5411\u4E0D\u65AD\u53D7\u5230\u8036\u7A23\u9080\u8BF7\u7684\u5B97\u6559\u9886\u8896\u4EEC\u63D0\u51FA\u8B66\u544A.</p><p>\u739B\u7AA6\u5723\u53F2\u7528\u751F\u52A8\u7684\u9690\u55BB, \u63CF\u7ED8\u51FA\u5B97\u6559\u9886\u8896\u4EEC\u62D2\u7EDD\u8036\u7A23\u7684\u4E25\u91CD\u540E\u679C: \u4ED6\u4EEC\u5C06\u88AB\u4E22\u5728\u5916\u9762\u7684\u9ED1\u6697\u4E2D ( \u5982: \u739B\u516B 12; \u4E8C\u5341\u4E94 30 ), \u5728\u90A3\u91CC\u8981\u6709 &quot;\u54C0\u53F7\u548C\u5207\u9F7F&quot; ( \u739B\u516B 12; \u5341\u4E09 42, 50; \u4E8C\u5341\u56DB 51; \u4E8C\u5341\u4E94 30 ). &quot;\u88AB\u53EC\u7684\u4EBA\u591A, \u88AB\u9009\u7684\u4EBA\u5C11&quot; \u8FD9\u53E5\u8C1A\u8BED\u5F0F\u7684\u8BAD\u8A00 ( 14 \u8282 ), \u5E76\u4E0D\u80FD\u5B8C\u5168\u8868\u8FBE\u8FD9\u4E2A\u6BD4\u55BB\u7684\u610F\u4E49. \u8FD9\u6BD4\u55BB\u7740\u91CD\u4E8E: \u5148\u88AB\u8BA4\u4E3A\u5E94\u8BE5\u4F1A\u63A5\u53D7\u9080\u8BF7\u7684\u90A3\u4E9B\u4EBA ( \u5B97\u6559\u9886\u8896 ) \u662F\u5982\u4F55\u62D2\u7EDD\u8D74\u5BB4, \u800C\u5E76\u672A\u9884\u5907\u9080\u8BF7\u7684\u5BF9\u8C61 ( \u793E\u4F1A\u5730\u4F4D\u4F4E\u5FAE\u7684\u4EBA ) \u5374\u63A5\u53D7\u4E86\u9080\u8BF7.</p><h2 id="\u7EB3\u7A0E\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u7EB3\u7A0E\u7684\u95EE\u9898" aria-hidden="true">#</a> \u7EB3\u7A0E\u7684\u95EE\u9898</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>15</sup> \u90A3\u65F6\uFF0C\u6CD5\u5229\u585E\u4EBA\u53BB\u5546\u8BA8\u600E\u6837\u5728\u8A00\u8C08\u4E0A\u53EB\u8036\u7A23\u5165\u5708\u5957\u3002<sup>16</sup> \u4ED6\u4EEC\u9042\u6D3E\u81EA\u5DF1\u7684\u95E8\u5F92\u548C\u9ED1\u843D\u5FB7\u515A\u4EBA\u5230\u4ED6\u8DDF\u524D\u8BF4\uFF1A\u201C\u5E08\u5085\uFF0C\u6211\u4EEC\u77E5\u9053\u4F60\u662F\u771F\u8BDA\u7684\uFF0C\u6309\u771F\u7406\u6559\u6388\u5929\u4E3B\u7684\u9053\u8DEF\uFF0C\u4E0D\u987E\u5FCC\u4EFB\u4F55\u4EBA\uFF0C\u56E0\u4E3A\u4F60\u4E0D\u770B\u4EBA\u7684\u60C5\u9762\u3002<sup>17</sup> \u5982\u4ECA\u8BF7\u4F60\u544A\u8BC9\u6211\u4EEC\uFF1A\u4F60\u4EE5\u4E3A\u5982\u4F55\uFF1F\u7ED9\u51EF\u6492\u7EB3\u7A0E\uFF0C\u53EF\u4EE5\u4E0D\u53EF\u4EE5\uFF1F\u201D <sup>18</sup> \u8036\u7A23\u770B\u7834\u4ED6\u4EEC\u7684\u6076\u610F\uFF0C\u5C31\u8BF4\uFF1A\u201C\u5047\u5584\u4EBA\uFF0C\u4F60\u4EEC\u4E3A\u4EC0\u4E48\u8981\u8BD5\u63A2\u6211\uFF1F<sup>19</sup> \u62FF\u4E00\u4E2A\u7A0E\u5E01\u7ED9\u6211\u770B\u770B\uFF01\u201D\u4ED6\u4EEC\u4FBF\u9012\u7ED9\u4ED6\u4E00\u5757\u201C\u5FB7\u7EB3\u201D\u3002<sup>20</sup> \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u8FD9\u8096\u50CF\u548C\u540D\u53F7\u662F\u8C01\u7684\uFF1F\u201D <sup>21</sup> \u4ED6\u4EEC\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u51EF\u6492\u7684\u3002\u201D\u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u90A3\u4E48\uFF0C\u51EF\u6492\u7684\uFF0C\u5C31\u5E94\u5F52\u8FD8\u51EF\u6492\uFF1B\u5929\u4E3B\u7684\uFF0C\u5C31\u5E94\u5F52\u8FD8\u5929\u4E3B\u3002\u201D <sup>22</sup> \u4ED6\u4EEC\u542C\u4E86\u5927\u4E3A\u60CA\u5F02\uFF0C\u9042\u79BB\u5F00\u4ED6\u8D70\u4E86\u3002</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6CD5\u5229\u585E\u4EBA\u8BE2\u95EE\u8036\u7A23\u53EF\u4E0D\u53EF\u4EE5\u7ED9\u7F57\u9A6C\u4EBA\u7EB3\u7A0E, \u8036\u7A23\u90A3\u51FA\u4EBA\u610F\u5916, \u8BA4\u4ED6\u4EEC\u5F20\u614C\u5931\u63AA\u7684\u56DE\u7B54, \u907F\u514D\u4E86\u4ED6\u4EEC\u6240\u8BBE\u7684\u5708\u5957.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u662F\u8036\u7A23\u548C\u5B97\u6559\u9886\u8896\u4E4B\u95F4, \u53E6\u5916\u56DB\u4E2A\u4E89\u8FA9\u4E2D\u7684\u4E00\u4E2A. \u867D\u7136\u5B97\u6559\u9886\u8896\u4EEC\u8BF4\u7684\u5949\u627F\u8BDD ( 16 \u8282 ) \u8BB2\u7684\u90FD\u662F\u4E8B\u5B9E, \u4F46\u5374\u6BEB\u65E0\u8BDA\u610F, \u56E0\u4E3A\u4ED6\u4EEC\u65E9\u6709\u9884\u8C0B, \u8BBE\u4E86\u9677\u9631 ( 15 \u8282 ). \u5B97\u6559\u9886\u8896\u6240\u63D0\u51FA\u7684, \u662F\u4E00\u4E2A\u76F8\u5F53\u5FAE\u5999\u7684\u95EE\u9898. \u81EA\u4ECE\u4E3B\u524D 63 \u5E74\u7F57\u9A6C\u4EBA\u5360\u9886\u5DF4\u52D2\u65AF\u5766\u4E4B\u540E, \u72B9\u592A\u4EBA\u88AB\u8FEB\u7EB3\u7A0E, \u6216\u79F0 &quot;\u4EBA\u4E01\u7A0E&quot;. \u7537\u5B50, \u5987\u5973\u548C\u5974\u96B6\u90FD\u8981\u4EE5\u7F57\u9A6C\u786C\u5E01\u7EB3\u7A0E. \u5982\u679C\u8036\u7A23\u8BF4 &quot;\u4E0D\u53EF\u7EB3\u7A0E&quot;, \u7942\u5C31\u662F\u53CD\u5BF9\u7F57\u9A6C\u653F\u5E9C; \u5982\u679C\u8036\u7A23\u4E3B\u5F20\u7EB3\u7A0E, \u5219\u4F1A\u88AB\u8BA4\u4E3A\u662F\u7518\u613F\u53D7\u7F57\u9A6C\u5E1D\u56FD\u7684\u7EDF\u6CBB.</p><p>\u8036\u7A23\u8BC6\u7834\u4E86\u53D1\u95EE\u8005\u8BBE\u7684\u8FD9\u4E2A\u5708\u5957 ( 18 \u8282 ), \u770B\u6E05\u4E86\u4ED6\u4EEC\u7684\u6076\u610F\u548C\u4F2A\u5584. \u8036\u7A23\u667A\u6167\u5730\u4EE5 &quot;\u51EF\u6492\u7684, \u5C31\u5E94\u5F52\u8FD8\u51EF\u6492; \u5929\u4E3B\u7684, \u5C31\u5E94\u5F52\u8FD8\u5929\u4E3B&quot; ( 21 \u8282 ) \u6765\u7B54\u590D, \u53EF\u4EE5\u6709\u4E09\u79CD\u7406\u89E3\u65B9\u5F0F: (1) \u56E0\u4E3A\u4E00\u5207\u90FD\u5C5E\u4E8E\u5929\u4E3B ( \u808B\u4E8C\u5341\u4E94 23 ), \u6240\u4EE5\u4E0D\u5E94\u7ED9\u51EF\u6492\u7A0E; (2) \u5E94\u7ED9\u541B\u738B\u7EB3\u7A0E, \u56E0\u4E3A\u4ED6\u662F\u5929\u4E3B\u7684\u4EE3\u8868 ( \u5982: \u7F57\u5341\u4E09 1 ~ 7; \u4F2F\u524D\u4E8C 13 ~ 17 ); (3) \u53EF\u4EE5\u7EB3\u7A0E\u7ED9\u51EF\u6492, \u4F46\u8981\u8BA4\u8BC6\u5230\u51EF\u6492\u7684\u6743\u529B\u662F\u76F8\u5BF9\u7684, \u5E94\u4EE5\u5FE0\u5B9E\u4E8E\u5929\u4E3B\u4E3A\u4F18\u5148. \u6700\u540E\u7684\u8FD9\u79CD\u65B9\u5F0F\u662F\u6700\u5408\u9002\u7684\u89E3\u91CA. \u6B63\u5982\u739B\u5341\u4E03 24 ~ 27 \u90A3\u6837. \u8036\u7A23\u5EFA\u8BAE\u7F34\u7EB3\u6BBF\u7A0E, \u4F46\u8FD9\u5E76\u4E0D\u662F\u5360\u9886\u56FD\u7684\u652F\u6301\u7968. \u6CD5\u5229\u585E\u4EBA\u7684\u95E8\u5F92\u95E8\u542C\u4E86\u8036\u7A23\u7684\u8BDD, &quot;\u5927\u4E3A\u60CA\u5F02&quot; ( 22 \u8282, \u4E5F\u8BF7\u53C2\u9605: \u739B\u516B 27; \u4E5D 33; \u5341\u4E94 31; \u4E8C\u5341\u4E00 20; ), \u8FD9\u6837\u7684\u53CD\u5E94\u7A81\u663E\u73B0\u8036\u7A23\u7684\u667A\u6167\u80DC\u8FC7\u654C\u5BF9\u8005\u7684\u80FD\u529B.</p><h2 id="\u590D\u6D3B\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u590D\u6D3B\u7684\u95EE\u9898" aria-hidden="true">#</a> \u590D\u6D3B\u7684\u95EE\u9898</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>23</sup> \u5728\u90A3\u4E00\u5929\uFF0C\u5426\u8BA4\u590D\u6D3B\u7684\u6492\u675C\u585E\u4EBA\uFF0C\u6765\u5230\u4ED6\u8DDF\u524D\uFF0C\u95EE\u4ED6\u8BF4\uFF1A<sup>24</sup> \u201C\u5E08\u5085\uFF0C\u6885\u745F\u8BF4\uFF1A\u8C01\u82E5\u6B7B\u4E86\u6CA1\u6709\u513F\u5B50\uFF0C\u4ED6\u7684\u5F1F\u5F1F\u5C31\u5E94\u5A36\u4ED6\u7684\u5973\u4EBA\u4E3A\u59BB\uFF0C\u7ED9\u4ED6\u54E5\u54E5\u7ACB\u55E3\u3002<sup>25</sup> \u5728\u6211\u4EEC\u4E2D\u66FE\u6709\u5144\u5F1F\u4E03\u4EBA\uFF1A\u7B2C\u4E00\u4E2A\u5A36\u4E86\u59BB\u6CA1\u6709\u5B50\u55E3\u5C31\u6B7B\u4E86\uFF0C\u9057\u4E0B\u4E86\u59BB\u5B50\u7ED9\u4ED6\u7684\u5F1F\u5F1F\uFF1B<sup>26</sup> \u8FDE\u7B2C\u4E8C\u4E2A\u4E0E\u7B2C\u4E09\u4E2A\uFF0C\u76F4\u5230\u7B2C\u4E03\u4E2A\u90FD\u662F\u8FD9\u6837\u3002<sup>27</sup> \u6700\u540E\uFF0C\u90A3\u5987\u4EBA\u4E5F\u6B7B\u4E86\u3002<sup>28</sup> \u90A3\u4E48\uFF0C\u5728\u590D\u6D3B\u7684\u65F6\u5019\uFF0C\u5979\u662F\u4E03\u4EBA\u4E2D\u90A3\u4E00\u4E2A\u7684\u59BB\u5B50\uFF1F\u56E0\u4E3A\u90FD\u66FE\u5A36\u8FC7\u5979\u3002\u201D <sup>29</sup> \u8036\u7A23\u56DE\u7B54\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u4F60\u4EEC\u9519\u4E86\uFF0C\u4E0D\u660E\u4E86\u7ECF\u4E66\uFF0C\u4E5F\u4E0D\u660E\u4E86\u5929\u4E3B\u7684\u80FD\u529B\uFF0C<sup>30</sup> \u56E0\u4E3A\u590D\u6D3B\u7684\u65F6\u5019\uFF0C\u4E5F\u4E0D\u5A36\u4E5F\u4E0D\u5AC1\uFF0C\u597D\u50CF\u5728\u5929\u4E0A\u7684\u5929\u4F7F\u4E00\u6837\u3002<sup>31</sup> \u5173\u4E8E\u6B7B\u4EBA\u590D\u6D3B\uFF0C\u4F60\u4EEC\u4E0D\u66FE\u5FF5\u8FC7\u5929\u4E3B\u5BF9\u4F60\u4EEC\u6240\u8BF4 <sup>32</sup> \u2018\u6211\u662F\u4E9A\u5DF4\u90CE\u7684\u5929\u4E3B\uFF0C\u4F9D\u6492\u683C\u7684\u5929\u4E3B\u53CA\u96C5\u5404\u4F2F\u7684\u5929\u4E3B\u2019\u7684\u8BDD\u5417\uFF1F\u4ED6\u4E0D\u662F\u6B7B\u4EBA\u7684\uFF0C\u800C\u662F\u6D3B\u4EBA\u7684\u5929\u4E3B\u3002\u201D <sup>33</sup> \u6C11\u4F17\u542C\u4E86\uFF0C\u5C31\u90FD\u60CA\u8BB6\u4ED6\u7684\u9053\u7406\u3002</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6492\u675C\u585E\u4EBA\u5728\u795E\u5B66\u7406\u8BBA\u4E0A\u5F88\u4FDD\u5B88, \u53CD\u5BF9\u8F83\u524D\u8FDB\u7684\u6CD5\u5229\u8D5B\u4EBA\u6240\u6301\u76F8\u4FE1\u8089\u8EAB\u590D\u6D3B\u6BD4\u8F83\u65B0\u7684\u72B9\u592A\u6559\u4E49 ( \u5B83\u5728\u672B\u671F\u7684\u8BAD\u8BF2\u6587\u5B66\u91CC\u624D\u9996\u6B21\u51FA\u73B0. ) \u6709\u4E9B\u6492\u675C\u585E\u4EBA\u63D0\u51FA\u4E86\u4E00\u4E2A\u4ED6\u4EEC\u8BA4\u4E3A\u65E0\u6CD5\u89E3\u51B3\u7684\u95EE\u9898; \u8036\u7A23\u544A\u8BC9\u4ED6\u4EEC, \u4ED6\u4EEC\u8BEF\u89E3\u4E86 &quot;\u7ECF\u4E66\u548C\u5929\u4E3B\u7684\u80FD\u529B&quot;. \u56E0\u4E3A\u65E7\u7EA6\u663E\u793A\u7684\u5929\u4E3B\u662F &quot;\u6D3B\u4EBA\u7684\u5929\u4E3B&quot;, \u6240\u4EE5\u6309\u5929\u4E3B\u672C\u6027\u5FC5\u7136\u4F7F\u4EBA\u590D\u6D3B, \u5728\u6B64\u4E4B\u540E\u5C06\u4E0D\u518D\u6709\u5AC1\u5A36\u4E4B\u4E8B. \u6CE8\u610F, \u8036\u7A23\u5728\u6B64\u5904\u5F15\u7528\u6885\u745F\u4E66 -- \u662F\u6492\u793E\u8D5B\u4EBA\u6240\u627F\u8BA4\u7684\u552F\u4E00\u7ECF\u4E66.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u7B2C\u4E8C\u4E2A\u8FA9\u8BBA\u4E2D, \u6492\u675C\u585E\u4EBA\u63D0\u51FA\u4E86\u4E00\u4E2A\u5632\u7B11\u76F8\u4FE1\u590D\u6D3B\u7684\u95EE\u9898. \u8036\u7A23\u65F6\u4EE3, \u4EBA\u4EEC\u5BF9\u6B7B\u540E\u751F\u547D\u6709\u8BB8\u591A\u4E0D\u540C\u770B\u6CD5. \u6B7B\u540E\u590D\u6D3B\u7684\u601D\u60F3\u6700\u5148\u51FA\u73B0\u4E8E\u4E3B\u524D\u4E8C\u4E16\u7EAA\u7684\u300A\u8FBE\u5C3C\u5C14\u5148\u77E5\u4E66\u300B( \u8FBE\u5341\u4E8C 2 ), \u8BE5\u601D\u60F3\u4E3A\u6CD5\u5229\u585E\u4EBA\u63A5\u53D7, \u5374\u4E0D\u88AB\u6492\u675C\u585E\u4EBA\u63A5\u53D7 ( \u89C1: \u5B97\u4E8C\u5341\u4E09 6 ). \u6492\u675C\u585E\u4EBA\u5F15\u7528\u300A\u7533\u547D\u7EAA\u300B\u6240\u8BF4 &quot;\u5144\u6B7B\u5F1F\u5A36\u5AC2&quot; \u7684\u8FD9\u79CD &quot;\u4EE3\u5144\u5F1F\u7ACB\u55E3&quot; \u6CD5\u5F8B\u89C4\u5B9A\u53CA\u4F8B\u5B50 ( \u7533\u4E8C\u5341\u4E94 5 ~ 10; \u5E76\u53C2\u9605: \u591A\u4E09 8; \u516D 14; \u5728\u6B64, \u6492\u8FA3, \u8FA3\u53E4\u8033\u7684\u5973\u513F, \u6BD4\u5979\u4E03\u4E2A\u4E08\u592B\u90FD\u6D3B\u5F97\u957F ), \u6765\u8BF4\u660E\u4ED6\u4EEC\u8BA4\u4E3A\u590D\u6D3B\u7684\u4FE1\u4EF0\u662F\u8352\u8C2C\u7684. \u6B63\u5982\u524D\u9762\u7EB3\u7A0E\u7684\u95EE\u9898\u90A3\u6837\uFF0C \u8FD9\u4E2A\u95EE\u9898\u4E5F\u662F\u4E8B\u5148\u9884\u8C0B\u597D, \u60F3\u8BA9\u8036\u7A23\u56DE\u7B54\u4E0E\u7942\u7684\u6559\u5BFC, \u6216\u4E0E\u7ECF\u4E66\u7684\u5185\u5BB9\u76F8\u4E92\u62B5\u89E6. \u8036\u7A23\u8EAB\u4E3A\u6743\u5A01\u7684\u8001\u5E08, \u9677\u5165\u4E86\u8FD9\u573A\u7ECF\u4E66\u8BE0\u91CA\u4E4B\u4E89.</p><p>\u8036\u7A23\u4EE5\u4ED6\u4EEC\u4E0D\u4E86\u89E3\u7ECF\u4E66, \u4E5F\u4E0D\u660E\u767D\u5929\u4E3B\u7684\u80FD\u529B, \u6765\u53CD\u51FB\u654C\u5BF9\u8005. \u8036\u7A23\u5F15\u7528 \u300A\u51FA\u8C37\u7EAA\u300B \u4E2D\u5929\u4E3B\u81EA\u79F0\u662F &quot;\u4E9A\u5DF4\uF92C\u7684\u5929\u4E3B, \u4F9D\u6492\u683C\u7684\u5929\u4E3B, \u96C5\u5404\u4F2F\u7684\u5929\u4E3B&quot; ( \u51FA\u4E09 6, 15 ~ 16 ) \u6765\u53CD\u9A73: \u5728\u5929\u4E3B\u4E0E\u6885\u745F\u8BF4\u8BDD\u7684\u65F6\u5019, \u4EE5\u8272\u5217\u5148\u7956\u8089\u4F53\u5DF2\u6B7B, \u4F46\u4ED6\u4EEC\u7EE7\u7EED\u4E0E\u5929\u4E3B\u4FDD\u6301\u7740\u5173\u7CFB, \u6240\u4EE5\u67D0\u79CD\u610F\u4E49\u4E0A\u8BF4, \u4EE5\u8272\u5217\u7684\u5148\u7956\u4ECD\u7136\u4E0E\u6D3B\u7740\u7684\u4EBA\u5728\u4E00\u8D77. \u8036\u7A23\u660E\u786E\u8868\u793A\u6492\u675C\u585E\u4EBA\u4E0D\u4E86\u89E3\u590D\u6D3B\u7684\u6027\u8D28. \u85C9\u7740\u5929\u4E3B\u7684\u6743\u80FD\u6240\u521B\u9020\u7684\u65B0\u751F\u547D, \u4EE5\u67D0\u79CD\u65B9\u5F0F\u6210\u4E3A\u6211\u4EEC\u73B0\u6709\u751F\u5408\u7684\u5EF6\u7EED, \u800C\u4E14, \u5B83\u5C06\u4EE5\u6211\u4EEC\u4E0D\u4E86\u89E3\u7684\u65B9\u5F0F\u8D8B\u5411\u5B8C\u6EE1.</p><h2 id="\u6700\u5927\u8BEB\u547D\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u6700\u5927\u8BEB\u547D\u7684\u95EE\u9898" aria-hidden="true">#</a> \u6700\u5927\u8BEB\u547D\u7684\u95EE\u9898</h2><h3 id="\u7ECF\u6587-3" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-3" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>34</sup> \u6CD5\u5229\u585E\u4EBA\u542C\u8BF4\u8036\u7A23\u4F7F\u6492\u675C\u585E\u4EBA\u95ED\u53E3\u65E0\u8A00\uFF0C\u5C31\u805A\u96C6\u5728\u4E00\u8D77\uFF1B<sup>35</sup> \u4ED6\u4EEC\u4E2D\u6709\u4E00\u4E2A\u6CD5\u5B66\u58EB\u8BD5\u63A2\u4ED6\uFF0C\u53D1\u95EE\u8BF4\uFF1A<sup>36</sup> \u201C\u5E08\u5085\uFF0C\u6CD5\u5F8B\u4E2D\u90A3\u6761\u8BEB\u547D\u662F\u6700\u5927\u7684\uFF1F\u201D <sup>37</sup> \u8036\u7A23\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u2018\u4F60\u5E94\u5168\u5FC3\uFF0C\u5168\u7075\uFF0C\u5168\u610F\uFF0C\u7231\u4E0A\u4E3B\u4F60\u7684\u5929\u4E3B\u3002\u2019 <sup>38</sup> \u8FD9\u662F\u6700\u5927\u4E5F\u662F\u7B2C\u4E00\u6761\u8BEB\u547D\u3002<sup>39</sup> \u7B2C\u4E8C\u6761\u4E0E\u6B64\u76F8\u4F3C\uFF1A\u4F60\u5E94\u5F53\u7231\u8FD1\u4EBA\uFF0C\u5982\u4F60\u81EA\u5DF1\u3002<sup>40</sup> \u5168\u90E8\u6CD5\u5F8B\u548C\u5148\u77E5\uFF0C\u90FD\u7CFB\u4E8E\u8FD9\u4E24\u6761\u8BEB\u547D\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6CD5\u5229\u8D5B\u4EBA\u518D\u5EA6\u8BD5\u56FE\u8BF1\u9677\u8036\u7A23, \u8BE2\u95EE\u4ED6\u6885\u745F\u6CD5\u5F8B\u4E2D\u90A3\u4E00\u6761\u6700\u5927. \u8036\u7A23\u5F15\u8FF0\u4E86\u65E7\u7EA6\u91CC\u7684\u4E24\u6BB5\u8BDD, \u8FDE\u7CFB\u4E86\u7231\u5929\u4E3B\u548C\u7231\u8FD1\u4EBA\u4E24\u6761\u8BEB\u547D, \u8FD8\u66F4\u8BF4\u660E\u4E86\u8FD9\u5C31\u662F\u6574\u4E2A\u65E7\u7EA6\u5B97\u6559\u7684\u7CBE\u9AD3.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u300A\u9A6C\u5C14\u8C37\u798F\u97F3\u300B\u7684\u8BB0\u8F7D\u4E2D, \u8FD9\u4E2A\u6CD5\u5B66\u58EB\u7684\u95EE\u9898\u662F\u51FA\u4E8E\u771F\u8BDA ( \u8C37\u5341\u4E8C 28 ~ 34; \u53C2\u9605: \u8DEF\u5341 25 ), \u4F46\u5728\u300A\u739B\u7AA6\u798F\u97F3\u300B\u4E2D, \u6CD5\u5B66\u58EB\u7684\u8FD9\u95EE\u9898\u5374\u5F15\u8D77\u4E86\u53E6\u4E00\u573A\u4E89\u8BBA. \u6CD5\u5229\u585E\u4EBA\u805A\u5728\u4E00\u8D77, \u915D\u917F\u53CD\u5BF9\u8036\u7A23\u7684\u9634\u8C0B ( 34 \u8282, \u53C2\u9605: \u739B\u4E8C4\uFF1B \u4E8C\u5341\u4E8C 41; \u4E8C\u5341\u516D 3; \u4E8C\u5341\u4E03 17, 27; \u4E8C\u5341\u516B 12; \u4E5F\u53EF\u80FD\u662F\u6697\u6307: \u548F\u4E8C 2 ). \u4ED6\u4EEC\u63D0\u51FA\u8FD9\u4E2A\u95EE\u9898\u6709\u610F\u8981\u8BD5\u63A2\u8036\u7A23 ( \u53C2\u9605: \u739B\u4E8C\u5341\u4E8C 15 ). \u6240\u6709\u7684\u8BEB\u547D\u90FD\u662F\u91CD\u8981\u7684, \u4E5F\u90FD\u5FC5\u987B\u9075\u5B88. \u6CD5\u5B66\u58EB\u6240\u8D28\u8BE2\u7684, \u4E0D\u662F\u6709\u5173\u662F\u5426\u53EF\u4EE5\u5BF9\u6709\u4E9B\u6CD5\u5F8B\u4E0D\u4E88\u91CD\u89C6, \u800C\u662F\u8036\u7A23\u662F\u5426\u50CF\u6709\u4E9B\u8001\u5E08\u90A3\u6837, \u7528\u4E00\u4E2A\u7B80\u5355\u7684\u9648\u8FF0\u6765\u603B\u7ED3\u300A\u6885\u745F\u4E94\u4E66\u300B, \u5C31\u50CF\u72B9\u592A\u6559\u8FA3\u6BD4\u5E0C\u808B\u8033\u90A3\u6837: &quot;\u5DF1\u6240\u4E0D\u6B32, \u52FF\u65BD\u4E8E\u4EBA&quot;.</p><p>\u8036\u7A23\u628A\u6574\u4E2A\u6CD5\u5F8B\u6982\u7565\u5F52\u7EB3\u4E3A\u4E24\u6761\u8BEB\u547D ( \u4E5F\u53C2\u9605: \u739B\u4E03 12 ); \u7B2C\u4E00\u6761\u8BEB\u547D, \u5C31\u662F\u72B9\u592A\u4EBA\u6BCF\u65E5\u80CC\u8BF5\u4E24\u6B21 &quot;\u820D\u739B&quot; ( Shema: &quot;\u4EE5\u8272\u5217! \u4F60\u8981\u542C&quot; ) \u91CC\u5429\u5490\u4EBA\u8981\u505A\u5230\u7684 &quot;\u4F60\u5E94\u5168\u5FC3, \u5168\u7075, \u5168\u529B, \u7231\u4E0A\u4E3B\u4F60\u7684\u5929\u4E3B&quot; ( \u53C2\u9605: \u7533\u516D 4 ~ 6 ). \u5176\u4E2D\u7684 &quot;\u5FC3&quot; ( kardia ) \u88AB\u8BA4\u4E3A\u662F\u6240\u6709\u60C5\u611F\u4E4B\u6240\u5728; &quot;\u7075&quot; ( psyche ) \u4E3A\u751F\u547D\u529B\u548C\u601D\u60F3\u7684\u4E2D\u5FC3\uFF1B &quot;\u529B&quot; ( ischys ) \u8868\u793A\u529B\u91CF\u6216\u80FD\u529B. \u7B2C\u4E8C\u6761\u8BEB\u547D, &quot;\u7231\u8FD1\u4EBA\u5982\u4F60\u81EA\u5DF1&quot;, \u6765\u81EA\u795E\u5723\u7684\u6CD5\u89C4 ( \u808B\u5341\u4E5D 18 ), \u5B83\u58F0\u79F0\u5BF9\u5929\u4E3B\u7684\u7231, \u5728\u5BF9\u90BB\u4EBA\u7684\u7231\u4E2D\u5F70\u6700\u51FA\u6765. \u73B0\u4EE3\u897F\u65B9\u56FD\u5BB6\u5FC5\u987B\u7231\u81EA\u5DF1\u7684\u6982\u5FF5, \u5C06\u4E0D\u9002\u5408\u5723\u7ECF\u4E16\u754C\u4E2D\u7684\u4EBA. \u5723\u7ECF\u4E16\u754C\u4E2D\u7684\u4EBA\u4E0D\u4F1A\u7406\u89E3\u4E2A\u4EBA\u81EA\u6211\u4E3B\u4E49\u8FD9\u79CD\u8BF4\u6CD5. \u4ED6\u4EEC\u66F4\u613F\u610F\u53C2\u4E0E\u67D0\u4E00\u5BB6\u5EAD, \u5BB6\u65CF\u6216\u5B97\u6559\u56E2\u4F53. \u4ED6\u4EEC\u4F9D\u9760\u4ED6\u4EBA\u6765\u80AF\u5B9A\u81EA\u5DF1, \u7231\u81EA\u5DF1\u548C\u7231\u522B\u4EBA\u662F\u4E0D\u53EF\u5206\u5272\u7684.</p><h2 id="\u9ED8\u897F\u4E9A\u4E3A\u8FBE\u5473\u4E4B\u5B50" tabindex="-1"><a class="header-anchor" href="#\u9ED8\u897F\u4E9A\u4E3A\u8FBE\u5473\u4E4B\u5B50" aria-hidden="true">#</a> \u9ED8\u897F\u4E9A\u4E3A\u8FBE\u5473\u4E4B\u5B50</h2><h3 id="\u7ECF\u6587-4" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-4" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>41</sup> \u6CD5\u5229\u585E\u4EBA\u805A\u96C6\u5728\u4E00\u8D77\u65F6\uFF0C\u8036\u7A23\u95EE\u4ED6\u4EEC\u8BF4\uFF1A<sup>42</sup> \u201C\u5173\u4E8E\u9ED8\u897F\u4E9A\uFF0C\u4F60\u4EEC\u4EE5\u4E3A\u5982\u4F55\uFF1F\u4ED6\u662F\u8C01\u7684\u513F\u5B50\uFF1F\u201D\u4ED6\u4EEC\u56DE\u7B54\u8BF4\uFF1A\u201C\u8FBE\u5473\u7684\u3002\u201D <sup>43</sup> \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u600E\u4E48\u8FBE\u5473\u56E0\u5723\u795E\u7684\u611F\u52A8\uFF0C\u79F0\u4ED6\u4E3A\u4E3B\u8BF4\uFF1A<sup>44</sup> \u2018\u4E0A\u4E3B\u5BF9\u6211\u4E3B\u8BF4\uFF1A\u4F60\u5750\u5728\u6211\u53F3\u8FB9\uFF0C\u7B49\u6211\u628A\u4F60\u7684\u4EC7\u654C\u653E\u5728\u4F60\u7684\u811A\u4E0B\uFF1F\u2019 <sup>45</sup> \u90A3\u4E48\uFF0C\u5982\u679C\u8FBE\u5473\u79F0\u4ED6\u4E3A\u4E3B\uFF0C\u4ED6\u600E\u4F1A\u662F\u8FBE\u5473\u7684\u513F\u5B50\uFF1F\u201D <sup>46</sup> \u6CA1\u6709\u4EBA\u80FD\u56DE\u7B54\u4ED6\u4E00\u53E5\u8BDD\u3002\u4ECE\u90A3\u5929\u4EE5\u540E\uFF0C\u6CA1\u6709\u8C01\u518D\u6562\u95EE\u4ED6\u3002</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-4" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-4" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u53CD\u8FC7\u6765\u7ED9\u6CD5\u5229\u8D5B\u4EBA\u4E00\u4E2A\u96BE\u9898: \u5982\u679C\u4ED6\u4EEC\u6240\u77E5\u9053\u7684, \u57FA\u7763\u662F\u8FBE\u5473\u4E4B\u5B50\u55E3, \u8FBE\u5473\u600E\u4E48\u80FD\u79F0\u4ED6\u4E3A &quot;\u4E3B&quot; \u5462? \u4F5C\u8005\u8FD8\u52A0\u4E86\u4E00\u53E5, &quot;\u6CA1\u6709\u8C01\u518D\u6562\u95EE\u4ED6&quot;.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-4" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-4" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u8FD9\u573A\u7B2C\u56DB\u4E2A, \u4E5F\u662F\u6700\u540E\u7684\u8FA9\u8BBA\u4E2D, \u8036\u7A23\u6210\u4E3A\u53D1\u95EE\u8005. \u8FA9\u8BBA\u518D\u4E00\u6B21\u56F4\u7ED5\u7740\u5BF9\u7ECF\u4E66\u7684\u6B63\u786E\u8BE0\u91CA\u4E0A ( \u4E09\u90E8\u5BF9\u89C2\u798F\u97F3\u90FD\u6709\u8FD9\u4E00\u6BB5\u8BB0\u8FF0 ). \u8FA9\u8BBA\u7684\u7ECF\u53E5, \u662F\u4E00\u9996 &quot;\u4E3A\u65B0\u541B\u738B ( \u9ED8\u897F\u4E9A ) \u52A0\u5195&quot; \u7684\u5723\u548F: &quot;\u4E0A\u4E3B\u5BF9\u543E\u4E3B\u8BF4: \u4F60\u5750\u5728\u6211\u53F3\u8FB9, \u7B49\u6211\u4F7F\u4F60\u7684\u4EC7\u4E71\u53D8\u4F5C\u4F60\u811A\u4E0B\u7684\u8E0F\u677F&quot; ( 42 ~ 43 \u8282: \u548F\u4E00\u4E00\u96F61: \u8FD9\u91CC\u662F\u5F15\u7528 \u300A\u4E03\u5341\u8D24\u58EB\u5E0C\u814A\u8BD1\u672C\u7684\u5B57\u53E5 ) \u8FD9\u7BC7\u5723\u548F\u7684\u4F5C\u8005\u4EE5 &quot;\u4E0A\u4E3B&quot; \u6765\u6307 &quot;\u96C5\u5A01&quot;; \u4EE5 &quot;\u4E3B&quot; \u6765\u6307\u5373\u5C06 &quot;\u88AB\u96C5\u5A01\u5B89\u7F6E\u5728\u5B9D\u5EA7\u4E0A\u7684\u541B\u738B&quot;. \u5728\u65B0\u7EA6\u5E0C\u814A\u6587\u672C\u7684\u8BE0\u91CA\u4E2D, &quot;\u4E0A\u4E3B&quot; \u4ECD\u662F\u6307 &quot;\u96C5\u5A01&quot;; \u4F46\u8FD9\u7BC7\u5723\u548F\u91CC, \u8BF4\u8BDD\u7684\u662F\u8FBE\u5473\u738B, \u6240\u4EE5\u4E45\u800C\u4E45\u4E4B, &quot;\u4E3B&quot; \u4E5F\u5C31\u4EE3\u8868\u4E86\u4E00\u4E2A\u6BD4\u8FBE\u5473\u8FD8\u8981\u4F1F\u5927\u7684\u9ED8\u897F\u4E9A\u5F0F\u4EBA\u7269: &quot;\u65B0\u541B\u738B&quot;. \u8036\u7A23\u5728\u8FD9\u51E0\u53E5\u7ECF\u6587\u91CC\u6240\u7528\u7684 &quot;\u4E3B&quot;, \u6307\u7684 &quot;\u65B0\u541B\u738B&quot; \u662F\u7942\u81EA\u5DF1, \u6240\u4EE5\u7942\u624D\u4F1A\u8BF4: &quot;\u8FBE\u5473\u65E2\u79F0\u7942 ( \u9ED8\u897F\u4E9A ) \u4E3A\u4E3B, \u7942\u600E\u4E48\u53C8\u662F\u8FBE\u5473\u4E4B\u5B50\u5462?&quot;</p><p>\u8036\u7A23\u95EE\u9053, \u5982\u679C\u8FBE\u5473\u56E0\u5723\u795E\u611F\u52A8\u79F0 &quot;\u65B0\u541B\u738B&quot; ( &quot;\u9ED8\u897F\u4E9A&quot; ) \u4E3A &quot;\u4E3B&quot;, \u8FD9\u6837 &quot;\u65B0\u541B\u738B&quot; \u5C31\u8FDC\u8FDC\u4E0D\u53EA\u662F\u8FBE\u5473\u7684\u5B50\u5B59\u8FD9\u4E48\u7B80\u5355. \u8036\u7A23\u7684\u8FD9\u4E2A\u95EE\u9898\u628A\u654C\u5BF9\u8005\u95EE\u5012\u4E86. &quot;\u9ED8\u897F\u4E9A&quot; \u5C06\u662F &quot;\u8FBE\u5473\u4E4B\u5B50&quot; \u7684\u601D\u60F3, \u53EF\u5728\u5148\u77E5\u4E66\u4E2D\u627E\u5230 ( \u53C2\u9605: \u4F9D\u5341\u4E00 1, 10; \u8036\u7A23\u4E8C\u5341\u4E09 5 ). \u4EC5\u7BA1 &quot;\u8FBE\u5473\u4E4B\u5B50&quot; \u662F\u739B\u7AA6\u5723\u53F2\u559C\u7231\u7ED9\u8036\u7A23\u51A0\u4E0A\u7684\u540D\u53F7 ( \u739B\u4E00 1; \u4E5D 7; \u5341\u4E8C 23; \u5341\u4E94 22; \u4E8C\u5341 30, 31; \u4E8C\u5341\u4E00 9, 15 ), \u4F46\u662F &quot;\u8FBE\u5473\u4E4B\u5B50&quot; \u5E76\u4E0D\u8DB3\u4EE5\u63CF\u8FF0\u8036\u7A23\u7684\u5168\u90E8. \u8FD9\u4E00\u5C0F\u6BB5\u7ECF\u6587\u7ECF\u5408\u4E86\u6570\u4E2A\u91CD\u8981\u7684\u795E\u5B66\u540D\u53F7, \u8868\u793A\u8036\u7A23\u662F &quot;\u9ED8\u897F\u4E9A&quot;, &quot;\u5929\u4E3B\u5B50&quot; \u4EE5\u53CA &quot;\u5929\u4E3B&quot;. \u654C\u5BF9\u8005\u5BF9\u8036\u7A23\u7684\u53D1\u95EE\u65E0\u8A00\u4EE5\u5BF9, \u8C61\u5F81\u7740\u8036\u7A23\u7684\u80DC\u5229. \u4ECE\u73B0\u5728\u5F00\u59CB, \u4E00\u76F4\u5230\u8036\u7A23\u53D7\u96BE\u4E3A\u6B62, \u518D\u6CA1\u6709\u8036\u7A23\u4E0E\u5B97\u6559\u9886\u8896\u63A5\u89E6\u7684\u8FDB\u4E00\u6B65\u8BB0\u8F7D, \u56E0\u4E3A\u8036\u7A23\u73B0\u5728\u53EA\u5411\u7FA4\u4F17\u548C\u95E8\u5F92\u8BB2\u9053\u4E86.</p>',45),p=[s];function q(h,r){return t(),o("div",null,p)}var i=u(e,[["render",q],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E8C\u7AE0.html.vue"]]);export{i as default};
